yoruba_ant = {'Gen$1:4': ('okunkun', 'imole', 'Gen$1:4 "Ọlọ́run rí i pé ìmọ́lẹ̀ náà dára, ó sì ya ìmọ́lẹ̀ náà sọ́tọ̀ kúrò lára òkùnkùn."'), 'Gen$1:5': ('okunkun', 'imole', 'Gen$1:5 Ọlọ́run sì pe ìmọ́lẹ̀ náà ní “Ọ̀sán” àti òkùnkùn ní “Òru.” Àsálẹ́ àti òwúrọ̀ sì jẹ́ ọjọ́ kìn-ín-ní.'), 'Gen$1:14': ('ọjọ', None, 'Gen$1:14 "Ọlọ́run sì wí pé “Jẹ́ kí ìmọ́lẹ̀ kí ó wà ní ojú ọ̀run, làti pààlà sí àárin ọ̀sán àti òru, kí wọn ó sì máa wà fún àmì láti mọ àwọn àsìkò, àti àwọn ọjọ́ àti àwọn ọdún,"'), 'Gen$1:16': (None, None, 'Gen$1:16 "Ọlọ́run dá ìmọ́lẹ̀ ńlá-ńlá méjì, ìmọ́lẹ̀ tí ó tóbi láti ṣe àkóso ọ̀sán àti ìmọ́lẹ̀ tí ó kéré láti ṣe àkóso òru. Ó sì dá àwọn ìràwọ̀ pẹ̀lú."'), 'Gen$1:17': ('ọjọ', None, 'Gen$1:17 "Ọlọ́run sì ṣe wọ́n lọ́jọ̀ sí ojú ọ̀run láti máa tan ìmọ́lẹ̀ si orí ilẹ̀,"'), 'Gen$1:27': (None, None, 'Gen$1:27 " Nítorí náà, Ọlọ́run dá ènìyàn ní àwòrán ara rẹ̀, ní àwòrán Ọlọ́run ni ó dá a; akọ àti abo ni ó dá wọn."'), 'Gen$2:9': (None, 'dara', 'Gen$2:9 " Ọlọ́run sì mú kí onírúurú igi hù jáde láti inú ilẹ̀: àwọn igi tí ó dùn-ún wò lójú, tí ó sì dára fún oúnjẹ. Ní àárin ọgbà náà ni igi ìyè àti igi tí ń mú ènìyàn mọ rere àti búburú wà."'), 'Gen$2:16': (None, None, 'Gen$2:16 "OLÚWA Ọlọ́run sì pàṣẹ fún ọkùnrin náà pé, “ìwọ́ lè jẹ lára èyíkéyìí èṣo àwọn igi inú ọgbà;"'), 'Gen$2:21': (None, None, 'Gen$2:21 "Nígbà náà ni OLÚWA Ọlọ́run mú kí ọkùnrin náà sùn fọnfọn; nígbà tí ó sì ń sùn, Ọlọ́run yọ egungun ìhà rẹ̀ kan, ó sì fi ẹran ara bò ó padà."'), 'Gen$2:24': ('baba', 'iya', 'Gen$2:24 " Nítorí ìdí èyí, ọkùnrin yóò fi baba àti ìyá rẹ̀ sílẹ̀, yóò sì dàpọ̀ mọ́ aya rẹ̀, wọn yóò sì di ara kan."'), 'Gen$3:4': (None, None, 'Gen$3:4 " Ejò wí fún obìnrin náà pé, “Ẹ̀yin kì yóò kú ikúkíkú kan.”"'), 'Gen$3:12': (None, None, 'Gen$3:12 "Ọkùnrin náà wí pé, “Obìnrin tí ìwọ fi fún mi, ni ó fún mi nínú èṣo igi náà, mo sì jẹ ẹ́.”"'), 'Gen$3:22': (None, None, 'Gen$3:22 " OLÚWA Ọlọ́run sì wí pé, “Ọkùnrin náà ti dàbí ọ̀kan lára wa, ó mọ rere àti búburú. A kò gbọdọ̀ jẹ́ kí ó na ọwọ́ rẹ̀ kí ó mú lára èṣo igi ìyè kí ó sì jẹ, kí ó sì wà láàyè títí láéláé.”"'), 'Gen$5:3': (None, 'ọmọ', 'Gen$5:3 "Nígbà tí Ádámù di ẹni Àádóje ọdún (130), ó bí ọmọkùnrin kan tí ó jọ ọ́, tí ó jẹ́ àwòrán ara rẹ̀, ó sì pe orúkọ rẹ̀ ní Ṣétì."'), 'Gen$6:19': (None, None, 'Gen$6:19 Ìwọ yóò mú gbogbo ohun alààyè takọ tabo wá sí inú ọkọ̀ kí wọn le wà láàyè pẹ̀lú rẹ.'), 'Gen$7:2': (None, None, 'Gen$7:2 "Mú méjeméje nínú àwọn ẹran tí ó mọ́, akọ àti abo, mú méjìméjì takọ tabo nínú àwọn ẹran aláìmọ́."'), 'Gen$7:3': (None, None, 'Gen$7:3 "Sì mú méjeméje pẹ̀lú nínú onírúurú ẹyẹ takọ tabo ni kí o mú wọn, wọlé sínú ọkọ̀ pẹ̀lú rẹ, kí á baà lè pa wọ́n mọ́ láàyè ní gbogbo ayé."'), 'Gen$7:8': (None, None, 'Gen$7:8 "Méjìméjì ni àwọn ẹran tí ó jẹ́ mímọ́ àti aláìmọ́, ti ẹyẹ àti ti gbogbo àwọn ẹ̀dá afàyàfà,"'), 'Gen$7:16': (None, None, 'Gen$7:16 "Gbogbo wọn wọlé ní takọ tabo bí Ọlọ́run ti pàṣẹ fún Nóà, OLÚWA sì tì wọn mọ́ inú ọkọ̀."'), 'Gen$8:22': ('ọjọ', None, 'Gen$8:22 "“Níwọ̀n ìgbà tí ayé bá sì wà, ìgbà ọ̀gbìn àti ìgbà ìkórè ìgbà òtútù àti ìgbà ooru, igbà ẹ̀ẹ̀rùn àti ìgbà òjò, ìgbà ọ̀ṣán àti ìgbà òru, yóò wà títí láé.”"'), 'Gen$11:31': (None, 'ọmọ', 'Gen$11:31 "Tẹ́rà sì mú ọmọ rẹ̀ Ábúrámù àti Lọ́tì ọmọ Áránì, ọmọ-ọmọ rẹ̀, ó sì mú Sáráì tí i ṣe aya ọmọ rẹ̀. Ábúrámù pẹ̀lú, gbogbo wọn sì jáde kúrò ní Úrì ti Kálídéà láti lọ sí ilẹ̀ Kénánì. Ṣùgbọ́n nígbà tí wọn dé Áránì wọ́n tẹ̀dó síbẹ̀."'), 'Gen$12:1': (None, None, 'Gen$12:1 " OLÚWA sì wí fún Ábúrámù, “Jáde kúrò ní ile rẹ àti lọ́dọ̀ àwọn ènìyàn rẹ àti kúrò ní ìdílé baba rẹ, lọ sí ilẹ̀ kan tí èmi ó fi hàn ọ́."'), 'Gen$12:5': (None, 'lọ', 'Gen$12:5 "Ábúrámù sì mú Ṣáráì aya rẹ̀, àti Lọ́tì ọmọ arákùnrin rẹ̀: gbogbo ohun ìní tí wọ́n ti kó jọ àti àwọn ènìyàn tí wọ́n ní ní Háránì. Wọ́n sì jáde lọ sí ilẹ̀ Kénánì. Wọ́n sì gúnlẹ̀ ṣíbẹ̀."'), 'Gen$14:21': (None, None, 'Gen$14:21 "Ọba Ṣódómù sì wí fún Ábúrámù pé, “Kó àwọn ènìyàn mi fún mi, ṣùgbọ́n mú àwọn ẹrù fún ara rẹ.”"'), 'Gen$15:9': (None, None, 'Gen$15:9 "Nítorí náà, OLÚWA wí fún-un pé, “Mú abo màlúù, ewúrẹ́ àti àgbò ọlọ́dún mẹ́tamẹ́ta wá, àti àdàbà kan àti ọmọ ẹyẹlé kan.”"'), 'Gen$16:3': (None, 'ọkọ', 'Gen$16:3 "Lẹ́yín ọdún mẹ́wàá tí Ábúrámù ti ń gbé ni Kénánì ni Ṣáráì mú ọmọbìnrin, ọmọ ọ̀dọ̀ rẹ Hágárì, tí í ṣe ará Éjíbítì fún Ábúrámù ọkọ rẹ̀ láti fi ṣe aya."'), 'Gen$18:20': (None, None, 'Gen$18:20 "OLÚWA sì wí pé, igbe Ṣódómù àti Gòmórà pọ̀ púpọ̀ àti pé ẹ̀ṣẹ̀ wọn burú jọjọ."'), 'Gen$18:27': ('ni', None, 'Gen$18:27 "Ábúráhámù sì tún tẹ̀ṣíwájú pé, “Wò ó nísinsin yìí, níwọ̀n bí èmi ti ní ìgboyà láti bẹ̀rẹ̀ sí sọ̀rọ̀ níwájú sí OLÚWA, èmi ẹni tí í ṣe erùpẹ̀ àti eérú,"'), 'Gen$19:1': ('wá', None, 'Gen$19:1 "Ní àsáálẹ́, àwọn ańgẹ́lì méjì sì wá sí ìlú Ṣódómù, Lọ́tì sì jókòó ní ẹnu ibodè ìlú. Bí ó sì ti rí wọn, ó sì dide láti pàdé wọn, ó kí wọn, ó sí foríbalẹ̀ fún wọn."'), 'Gen$19:4': (None, 'odo', 'Gen$19:4 "Ṣùgbọ́n kí ó tó di pé wọ́n lọ sùn, àwọn ọkùnrin ìlú Ṣódómù tọmọdé tàgbà yí ilé náà ká."'), 'Gen$19:26': ('wò', None, 'Gen$19:26 " Ṣùgbọ́n aya Lọ́tì bojú wo ẹ̀yìn, ó sì di òpó (ọ̀wọ́n) iyọ̀."'), 'Gen$19:31': ('aburo', None, 'Gen$19:31 "Ní ọjọ́ kan, èyí ẹ̀gbọ́n sọ fún àbúrò rẹ̀ pé, “Baba wa ti dàgbà, kò sì sí ọkùnrin kankan ní agbégbé yìí tí ì bá bá wa lò pọ̀, bí ìṣe gbogbo ayé."'), 'Gen$19:34': ('wá', None, 'Gen$19:34 "Ní ọjọ́ kejì èyí ẹ̀gbọ́n wí fún àbúrò rẹ̀ pé, “Ní àná mo sùn ti bàbá mi. Jẹ́ kí a tún wọlé tọ̀ ọ́, kí a lè ní irú ọmọ láti ọ̀dọ̀ baba wa.”"'), 'Gen$20:3': (None, 'obinrin', 'Gen$20:3 "Ṣùgbọ́n Ọlọ́run tọ Ábímélékì wá ní ojú àlá lọ́jọ́ kan, ó sì wí fún un pé, “Kíyèsi, kò sí ohun tí o fi sàn ju òkú lọ, nítorí obìnrin tí ìwọ mú sọ́dọ̀, aya aláya ni.”"'), 'Gen$20:12': ('baba', 'iya', 'Gen$20:12 "Yàtọ̀ fún ìyẹn, òtítọ́ ni pé arábìnrin mi ni. Ọmọ baba kan ni wá, bí ó tilẹ̀ jẹ́ pé, a kì í ṣe ọmọ ìyá kan. Mo sì fẹ́ ẹ ní aya."'), 'Gen$22:5': ('wá', 'lọ', 'Gen$22:5 "Ó sì wí fún àwọn ìránṣẹ́ rẹ̀ pé, “Ẹ̀yin, ẹ dúró níhìn-ín pẹ̀lú kẹ́tẹ́kẹ́tẹ́, èmi àti ọmọ yìí yóò lọ sí ọ̀hún-un-nì láti sin OLÚWA, a ó sì tún pada wá bá a yín.”"'), 'Gen$23:13': ('gba', 'fun', 'Gen$23:13 "Ó sì wí fún Éfúrónì lójú gbogbo wọn pé, “Bí ìwọ bá fẹ́, gbọ́ tèmi, èmi yóò san owó ilẹ̀ náà, gbà á lọ́wọ́ mi kí n lè sin òkú mi ṣíbẹ̀.”"'), 'Gen$24:2': (None, None, 'Gen$24:2 "Ó wí fún olórí àwọn ìránṣẹ́ ilẹ̀ rẹ̀, tí ó jẹ́ alábojútó ohun gbogbo tí ó ní pé, “Fi ọwọ́ rẹ sí abẹ́ itan mi."'), 'Gen$24:7': ('fun', None, 'Gen$24:7 "“OLÚWA, Ọlọ́run ọ̀run tí ó mú mi jáde láti ilẹ̀ baba mi àti ní ilẹ̀ tí a bí mi, tí ó bá mi sọ̀rọ̀ tí ó sì búra fún mi pé, ‘Irú ọmọ rẹ ni n ó fi ilẹ̀ yìí fún’, yóò rán ańgẹ́lì rẹ̀ ṣáájú rẹ, kí ìwọ kí ó lè rí aya fẹ́ wá fún ọmọ mi láti ibẹ̀."'), 'Gen$24:16': (None, None, 'Gen$24:16 "Ọmọbìnrin náà rẹwà, wúndíá ni, kò sì tíì mọ ọkùnrin, ó lọ sí ibi ìsun omi náà, ó sì pọn omi sínú ìkòkò omi, ó sì gbé e, ó ń gòkè bọ̀ kúrò níbi omi."'), 'Gen$24:24': (None, 'ọmọ', 'Gen$24:24 "Ó dáhùn pé, “Bétúélì ọmọ tí Mílíkà bí fún Náhórì ni baba mi”"'), 'Gen$24:37': ('ọmọbinrin', 'ọmọ', 'Gen$24:37 "OLÚWA mi sì ti mú mi búra wí pé, ‘Ìwọ kò gbọdọ̀ fẹ́ aya fún ọmọ mi, láàrin àwọn ọmọbìnrin Kénánì, ní ilẹ̀ ibi tí èmi ń gbé,"'), 'Gen$24:40': (None, 'fun', 'Gen$24:40 "“Ó sì dáhùn wí pé, ‘OLÚWA, níwájú ẹni tí èmi ń rìn yóò rán Ańgẹ́lì rẹ̀ ṣáájú rẹ yóò sì jẹ́ kí o ṣe aṣeyọrí ní ìrìn-àjò rẹ, kí ìwọ kí ó ba le fẹ́ aya fún ọmọ mi, láàrin àwọn ìbátan mi, àti láàrin àwọn ìdílé baba mi."'), 'Gen$24:42': ('lọ', 'wá', 'Gen$24:42 "“Nígbà tí mo dé ibi ìsun omi lónìí, mo wí pé, ‘OLÚWA Ọlọ́run Ábúráhámù olúwa mi, bí ìwọ bá fẹ́, jọ̀wọ́ jẹ́ kí n ṣe àṣeyọ́rí lórí ohun tí mo bá wá yìí,"'), 'Gen$24:48': (None, 'ọmọ', 'Gen$24:48 "Mo sì wólẹ̀ mo sì sin OLÚWA, Ọlọ́run Ábúráhámù olúwa à mi, ẹni tí ó ṣe amọ̀nà mi láti rí ọmọ ọmọ arákùnrin olúwa mi, mú wá fún ọmọ rẹ̀ (Ísáákì)."'), 'Gen$24:49': (None, None, 'Gen$24:49 "Ǹjẹ́ nísin yìí bí ẹ̀yin yóò bá fi inú rere àti òtítọ́ bá olúwa mi lò, ẹ sọ fún mi, bí bẹ́ẹ̀ sì kọ́, ẹ sọ fún mi, kí ó le è mọ ọ̀nà tí èmi yóò yà sí.”"'), 'Gen$24:50': ('buburu', None, 'Gen$24:50 "Lábánì àti Bétúélì sì dáhùn pé, “Lọ́dọ̀ OLÚWA ni èyi ti wá, nítorí náà àwa kò le sọ rere tàbí búburú fún ọ."'), 'Gen$25:19': ('ọmọ', None, 'Gen$25:19 Wọ̀nyí ni ìtàn ìran Ísáákì ọmọ Ábúráhámù. Ábúráhámù bí Ísáákì.'), 'Gen$27:11': (None, 'iya', 'Gen$27:11 "Jákọ́bù sì wí fún Rèbékà ìyá rẹ̀ pé, “Ṣùgbọ́n Ísọ̀ ẹ̀gbọ́n mi jẹ́ ènìyàn onírun lára bẹ́ẹ̀ alára ọ̀bọ̀rọ́ ni mi,"'), 'Gen$27:14': (None, 'iya', 'Gen$27:14 "Jákọ́bù sì ṣe ohun gbogbo tí ìyá rẹ̀ wí fun un, Rèbékà sì se oúnjẹ àdídùn náà, irú èyí tí Ísáákì fẹ́ràn."'), 'Gen$28:2': ('baba', 'iya', 'Gen$28:2 "Dípò bẹ́ẹ̀ lọ sí Padani-Árámù, sí ilé Bétúélì, baba ìyá rẹ, kí ìwọ kí ó sì fẹ́ aya fún ara rẹ nínú àwọn ọmọbìnrin Lábánì arákùnrin ìyá rẹ."'), 'Gen$28:6': ('ọmọbinrin', 'ọmọ', 'Gen$28:6 "Nígbà tí Ísọ̀ gbọ pé, Ísáákì ti ṣúre fún Jákọ́bù, ó sì ti ran Jákọ́bù lọ sí Padani-Árámù láti fẹ́ aya níbẹ̀ àti pé nígbà tí ó ṣúre fún un, ó kìlọ̀ fun un pé, kò gbọdọ̀ fẹ́ nínú àwọn ọmọbìnrin Kénánì"'), 'Gen$28:12': (None, None, 'Gen$28:12 " Ó sì lá àlá pé, a gbé àkàsọ̀ kan dúró ti ó fi ìdí lélẹ̀, orí rẹ̀ sì kan ọ̀run, àwọn ańgẹ́lì Ọlọ́run sì ń gòkè, wọ́n sì ń sọ̀kalẹ̀ lórí rẹ̀."'), 'Gen$28:18': ('dide', None, 'Gen$28:18 "Jákọ́bù sì dìde ní kùtùkùtù òwúrọ̀, ó sì gbé òkúta tí ó fi ṣe ìrọ̀rí lélẹ̀ bí òpó, ó sì da òróró si lórí."'), 'Gen$28:20': ('lọ', 'wá', 'Gen$28:20 "Jákọ́bù sì jẹ́ ẹ̀jẹ́ níbẹ̀ pé, “Bí Ọlọ́run yóò bá wà pẹ̀lú mi, tí yóò sì pa mí mọ́ ní ìrìnàjò mi tí mo ń lọ, tí yóò sì fún mi ní oúnjẹ láti jẹ àti aṣọ láti wọ̀,"'), 'Gen$30:16': ('lọ', 'wá', 'Gen$30:16 "Nítorí náà, nígbà tí Jákọ́bù ti oko dé ní ìrọ̀lẹ́ ọjọ́ náà, Líà jáde lọ pàde rẹ̀, ó sì wí pé, “O ní láti sun ọ̀dọ̀ mi ní alẹ́ yìí nítorí mo ti fi mánídárákì tí ọmọ mi wá bẹ̀ ọ́ lọ́wẹ̀.” Nítorí náà ni Jákọ́bù sùn tì í ní alẹ́ ọjọ́ náà."'), 'Gen$30:25': ('wá', 'lọ', 'Gen$30:25 "Lẹ́yìn tí Rákélì ti bí Jóṣẹ́fù, Jákọ́bù wí fún Lábánì pé, “Jẹ́ kí èmi máa lọ sí ilẹ̀ mi tí mo ti wá."'), 'Gen$31:17': (None, None, 'Gen$31:17 Nígbà náà ni Jákọ́bù gbé àwọn ọmọ àti aya rẹ̀ gun ràkunmí'), 'Gen$31:21': (None, None, 'Gen$31:21 "Ó sì sá lọ pẹ̀lú ohun gbogbo tí ó ni, ó sì la odò kọjá (Yúfúrátè), ó sì kọrí sí àwọn ilẹ̀ olókè ti Gílíádì."'), 'Gen$31:24': ('buburu', None, 'Gen$31:24 "Ọlọ́run sì yọ sí Lábánì ará Arámáínì lójú àlá ní òru, ó sì wí fun un pé, “Ṣọ́ra, má ṣe sọ ohunkóhun fún Jákọ́bù, ìbá à ṣe rere tàbí búburú.”"'), 'Gen$31:29': ('buburu', None, 'Gen$31:29 "Mo ní agbára láti ṣe ọ ni ibi, ṣùgbọ́n ní òru àná, Ọlọ́run baba rẹ sọ fún mi pé, kí èmi ṣọ́ra, kí èmi má ṣe sọ ohun kan fún ọ, ìbáà ṣe rere tàbí búburú."'), 'Gen$31:39': (None, None, 'Gen$31:39 "Èmi kò mú ọ̀kankan wá fún ọ rí nínú èyí tí ẹranko búburú fà ya, èmi ni ó fara mọ́ irú àdánù bẹ́ẹ̀. Ẹrankẹ́ran tí wọ́n bá sì jí lọ, lọ́sàn án tàbí lóru, ìwọ ń gba owó rẹ̀ lọ́wọ́ mi"'), 'Gen$31:40': (None, None, 'Gen$31:40 "Báyìí ni mo wà; oòrùn ń pa mi lọ́sàn-án, òtútù ń pa mi lóru, mo sì ń ṣe àìsùn."'), 'Gen$34:5': (None, 'ọmọbinrin', 'Gen$34:5 "Nígbà tí Jákọ́bù gbọ́ ohun tí ó sẹlẹ̀ pé a fi ipá bá Dínà ọmọbìnrin òun lò pọ̀, ṣùgbọ́n àwọn ọmọ rẹ̀ wà nínú pápá níbi tí wọ́n ti ń daran nítorí náà ó mú sùúrù títí tí wọ́n fi dé."'), 'Gen$34:7': (None, None, 'Gen$34:7 "Àwọn ọmọ Jákọ́bù sì ti oko dé, wọ́n sì gbọ́ ohun tí ó ṣelẹ̀ inú wọn sì bàjẹ́, ó sì ń bí wọn nínú gidigidi, nítorí pé, ohun àbùkù ńlá ni ó jẹ́ pé Ṣékémù bá ọmọ Jákọ́bù lò pọ̀-irú ohun tí kò yẹ kí ó ṣẹlẹ̀ rárá."'), 'Gen$34:8': ('ọmọ', None, 'Gen$34:8 "Hámórì sì bá wọn sọ̀rọ̀ pé, “Ọkàn ọmọ mi Ṣékémù fà sí ọmọ rẹ. Jọ̀wọ́ fi fún un gẹ́gẹ́ bí aya."'), 'Gen$34:9': (None, None, 'Gen$34:9 "Ẹ jẹ́ kí a máa ṣe ìgbéyàwó láàrin ara wa, kí àwọn ọmọ yín kó máa fẹ́ àwọn ọmọ wa."'), 'Gen$34:13': (None, None, 'Gen$34:13 Àwọn arákùnrin Dínà sì fi ẹ̀tàn dá Ṣékémù àti Ámórì bàbá rẹ̀ lóhùn nítorí tí ó ti ba ògo Dínà arábìnrin wọn jẹ́.'), 'Gen$34:20': (None, None, 'Gen$34:20 Ámórì àti Ṣékémù ọmọ rẹ̀ sì wá sí ẹnu ibodè ìlú náà wọn sì bá àwọn ará ìlú náà sọ̀rọ̀.'), 'Gen$36:14': (None, 'ọmọbinrin', 'Gen$36:14 "Wọ̀nyí ni àwọn ọmọ Óhólíbámà ọmọbìnrin Ánà ọmọ-ọmọ Ṣíbéónì: tí ó bí fún Ísọ̀: Jéúsì, Jálámì àti Kórà."'), 'Gen$36:18': (None, None, 'Gen$36:18 "Àwọn ọmọ Óhólíbámà aya Ísọ̀: Jéúsì, Jálámì, àti Kórà, àwọn wọ̀nyí ló jẹ́ olorí ìdílé tí ó ti ọ̀dọ̀ Óhólíbámà ọmọ Ánà, ìyàwó Ísọ̀ wá."'), 'Gen$36:39': ('ọmọ', 'ọmọbinrin', 'Gen$36:39 "Nígbà tí Baali-Hánánì ọmọ Ákíbórì kú, Ádádì ni ó jọba lẹ́yìn rẹ̀. Orúkọ ìlú rẹ̀ ni Páù, Méhétà-bélì ọmọbìnrin Mátírédì àti ọmọ-ọmọ Mé-ṣáhábù ni ìyàwó rẹ̀."'), 'Gen$37:35': (None, 'ọmọ', 'Gen$37:35 "Gbogbo àwọn ọmọ rẹ̀ lọ́kùnrin, lóbìnrin wá láti tù ú nínú, ṣùgbọ́n kò gbà. Ó wí pé, “Rárá, nínú ọ̀fọ̀ yìí ni èmi yóò lọ sí iṣà òkú lọ́dọ̀ ọmọ mi.” Baba Jósẹ́fù sì sunkún fún un."'), 'Gen$38:11': (None, 'ọmọ', 'Gen$38:11 "Nígbà náà ni Júdà wí fún Támárì, ìyàwó ọmọ rẹ̀ pé, “Lọ máa gbé bí opó ní ilé baba rẹ, títí tí Ṣélà yóò fi dàgbà ó lérò pé òun náà lè kú bí àwọn arákùnrin rẹ̀ tó kù.” Nígbà náà ni Támárì ń lọ gbé ilé baba rẹ̀."'), 'Gen$41:4': (None, None, 'Gen$41:4 "Àwọn màlúù tí ó rù, tí kò sì lẹ́wà sì gbé àwọn tí ó lẹ́wà tí ó sanra jẹ. Nígbà náà ni Fáráò jí."'), 'Gen$41:7': (None, None, 'Gen$41:7 "Àwọn sìírì ọkà méje tí kò yó\'mọ (ọmọ rẹ̀ kò tóbi) wọ̀nyí sì gbé àwọn tí ó yó\'mọ (ọmọ rẹ̀ tóbi) mì. Nígbà náà ni Fáráò jí lójú oorun, ó sì rí i pé àlá ni."'), 'Gen$41:9': (None, None, 'Gen$41:9 "Nígbà náà ni olórí agbọ́tí wí fún Fáráò pé, “Lónìí ni mo rántí àìṣedéédé mi."'), 'Gen$41:17': (None, None, 'Gen$41:17 "Nígbà náà ni Fáráò wí fún Jóṣẹ́fu pé, “Ní inú àlá mi, mo dúró ni etí bèbè odò Náílì,"'), 'Gen$41:22': (None, None, 'Gen$41:22 "“Ní ojú àlá mi, mo tún rí ṣiiri ọkà méje tí ó yó ọmọ tí ó sì dára, wọ́n jáde láti ara igi ọkà kan."'), 'Gen$41:35': (None, None, 'Gen$41:35 "Kí wọn kó gbogbo oúnjẹ ilẹ̀ náà ni àwọn ọdún méje ọ̀pọ̀ yìí, kí wọn sì kó àwọn ọkà tí wọn jẹ sẹ́kù pamọ́ lábẹ́ aṣẹ Fáráò. Kí a kó wọn pamọ́ ni àwọn ìlú fún jíjẹ."'), 'Gen$41:50': (None, None, 'Gen$41:50 "Kí ó tó di pé ọdún ìyàn dé, Áṣénátì ọmọ Pótífẹ́rà alábojútó Ónì bí ọmọkùnrin méjì fún Jósẹ́fù."'), 'Gen$42:14': (None, None, 'Gen$42:14 "Jósẹ́fù wí fún wọn pé, “Bí mo ti wí fún un yín náà ni: Ayọ́lẹ̀wò ni yín!"'), 'Gen$42:16': ('pa', None, 'Gen$42:16 "Ẹ rán ọ̀kan nínú yín lọ láti mú arákùnrin yín wá, àwa yóò fi ẹ̀yin tókù pamọ́ sínú àtìmọ́lé, kí àwa ba à lè mọ bóyá òtítọ́ ni ẹ̀yin ń wí. Ṣùgbọ́n bí ó bá jẹ́ pé irọ́ ni ẹ̀yin ń pa, ní òtítọ́ bí Fáráò ti ń bẹ láàyè ayọ́lẹ̀wò ni yín!”"'), 'Gen$43:11': ('fun', 'gba', 'Gen$43:11 "Nígbà náà ni Ísírẹ́lì baba wọn wí fún wọn, “Bí ó bá rí bẹ́ẹ̀, èyí ni kí ẹ ṣe: Ẹ mú àwọn ohun dáradára ilẹ̀ yìí lọ́wọ́ lọ bí ẹ̀bùn fún ọkùnrin náà-ìpara díẹ̀, oyin díẹ̀, tùràrí àti òjíá, èso Písítakíò àti eso álímíndì"'), 'Gen$44:4': (None, None, 'Gen$44:4 "Wọn kò tí ì rìn jìnnà sí ìlú náà tí Jósẹ́fù fi wí fún ìránṣẹ́ rẹ̀ pé, “Lépa àwọn ọkùnrin náà, nígbà tí o bá sì bá wọn, kí o wí pé, ‘Èéṣe ti ẹ fi búburú san rere.?"'), 'Gen$44:20': ('baba', 'iya', 'Gen$44:20 "A sì dáhùn pé, ‘A ni baba tí ó ti darúgbó, ọmọkùnrin kan sì wà pẹ̀lú tí a bí fún un ní ọjọ́ ogbó rẹ̀. Ẹ̀gbọ́n rẹ̀ ti kú, òun nìkan sì ni ó kù nínú àwọn ọmọ ìyá rẹ̀, baba rẹ̀ sì fẹ́ràn an rẹ̀.’"'), 'Gen$45:9': ('wá', 'lọ', 'Gen$45:9 " Nísinsìn yìí, ẹ yára padà sọ́dọ̀ baba mi kí ẹ sì wí fun un pé, èyí ni ohun tí Jósẹ́fù ọmọ rẹ wí, Ọlọ́run ti fi mí ṣe àkóso fún gbogbo ilẹ̀ Éjíbítì, ẹ sọ̀kalẹ̀ wá láì jáfara."'), 'Gen$45:17': (None, None, 'Gen$45:17 "Fáráò wí fún Jósẹ́fù pé, “Wí fún àwọn arákùnrin rẹ pé, ‘Èyí ni kí ẹ ṣe: Ẹ di ẹrù lé ẹranko yín kí ẹ sì padà sí ilẹ̀ Kénánì,"'), 'Gen$46:15': ('awọn ọmọkunrin', 'ọmọbinrin', 'Gen$46:15 Wọ̀nyí ni àwọn ọmọkùnrin Líà tí ó bí fún Jákọ́bù ní Padani-Árámù yàtọ̀ fún Dínà ọmọbìnrin rẹ̀. Àwọn ọmọkùnrin rẹ̀ àti ọmọbìnrin rẹ̀ wọ̀nyí jẹ́ mẹ́talélọ́gbọ̀n. (35) lápapọ̀.'), 'Gen$46:18': (None, 'ọmọbinrin', 'Gen$46:18 "Wọ̀nyí ni àwọn ọmọ tí Jákọ́bù bí nípaṣẹ̀ Ṣílípà, ẹni tí Lábánì fi fún Líà ọmọbìnrin rẹ̀. Gbogbo wọn jẹ́ mẹ́rìndínlógún (16) lápapọ̀."'), 'Gen$46:25': (None, None, 'Gen$46:25 Wọ̀nyí ni àwọn ọmọ tí Bílíhà ẹni tí Lábánì fi fún Rákélì ọmọ rẹ̀ bí fún Jákọ́bù. Wọ́n jẹ́ méje lápapọ̀.'), 'Gen$46:31': ('wá', 'lọ', 'Gen$46:31 "Nígbà náà ni Jósẹ́fù wí fún àwọn arákùnrin rẹ̀ àti fún àwọn ará ilé baba rẹ̀ pé, “Èmi yóò gòkè lọ, èmi yóò sì bá Fáráò sọ̀rọ̀, èmi yóò sì wí fún-un pé, Àwọn arákùnrin mi àti ìdílé baba mi tí ń gbé ní Kénánì ti tọ̀ mí wá."'), 'Gen$48:13': ('osi', 'ọtun', 'Gen$48:13 "Jósẹ́fù sì mú àwọn méjèèjì, Éfúráímù ni o fi sí ọwọ́ ọ̀tún òun tìkararẹ̀, èyí tí í ṣe ọwọ́ òsì fún Ísírẹ́lì, ó sì fi Mánásè sí ọwọ́ òsì ara rẹ̀, èyí tí ó bọ́ sí ọwọ́ ọ̀tún Ísírẹ́lì."'), 'Gen$48:14': ('osi', 'ọtun', 'Gen$48:14 "Ísírẹ́lì sì na ọwọ́ ọ̀tún rẹ̀ jáde, ó sì gbe lé Éfúráímù lórí, bí ó tilẹ̀ jẹ́ pé òun ni àbúrò, ó sì fi ọwọ́ rẹ̀ tàṣé ara wọn, ó sì na ọwọ́ òsì lé Mánásè lórí, bí ó tilẹ̀ jẹ́ pé Mánásè ni àkọ́bí."'), 'Gen$49:9': (None, None, 'Gen$49:9 " Ọmọ kìnnìún ni ọ́, ìwọ Júdà, o darí láti ìgbẹ́ ọdẹ, ọmọ mi. Bí i kìnnìún, o ba mọ́lẹ̀, o sì sùn sílẹ̀ bí i abo kìnnìún, Ta ni ó tó bẹ́ẹ̀, kí o lé e dìde?"'), 'Gen$49:10': (None, 'wá', 'Gen$49:10 "Ọ̀pá oyè kì yóò kúrò ní Júdà bẹ́ẹ̀ ni ọ̀pá-ìsàkóso kì yóò kúrò láàrin ẹṣẹ̀ rẹ̀, títí tí ẹni tí ó ni í yóò fi dé, tí gbogbo àwọn orílẹ̀ èdè yóò máa wárí fún un."'), 'Gen$50:20': (None, 'ibi', 'Gen$50:20 "Bí ó tilẹ̀ jẹ́ pé ẹ gbérò láti ṣe mi ní ibi, ṣùgbọ́n Ọlọ́run gbérò láti fi ṣe rere tí ń ṣe lọ́wọ́ yìí; ni gbígba ẹ̀mi ọ̀pọ̀lọpọ̀ ènìyàn là."'), 'Exod$1:15': (None, None, 'Exod$1:15 Ọba Éjíbítì sọ fún àwọn agbẹ̀bí Hébérù ti orúkọ wọn ń jẹ́ Ṣífúrà àti Púà pé:'), 'Exod$1:22': ('ọmọ', 'ọmọbinrin', 'Exod$1:22 " Nígbà náà ni Fáráò pàṣẹ yìí fún gbogbo àwọn ènìyàn rẹ̀ pé; “Gbogbo ọmọkùnrin tí a bí ni kí ẹ gbé jù sínú odò Náílì, ṣùgbọ́n gbogbo àwọn ọmọbìnrin ni kí ẹ jẹ́ kí wọn ó wà láàyè.”"'), 'Exod$2:9': (None, 'fun', 'Exod$2:9 "Ọmọbìnrin Fáráò sì wí fún un pé, “Gba ọmọ yìí kí o sì tọ́jú rẹ̀ fún mi, èmi yóò san owó iṣẹ́ ẹ̀ rẹ fún ọ” Ọmọbìnrin náà sì gbé ọmọ náà lọ, ó sì tọ́jú rẹ̀."'), 'Exod$2:10': ('ọmọbinrin', 'ọmọ', 'Exod$2:10 "Nígbà tí ọmọ náà sì dàgbà, ó mú un tọ ọmọbìnrin Fáráò wá, ó sì di ọmọ rẹ̀. Ó sì pe orúkọ rẹ̀ ní Mósè, ó wí pé “Nítorí tí mo fà á jáde nínú omi.”"'), 'Exod$3:18': ('lọ', 'wá', 'Exod$3:18 "“Àwọn àgbààgbà Ísírẹ́lì yóò fetí sílẹ̀ sí ohùn rẹ. Nígbà náà ni ìwọ, àti àwọn àgbààgbà yóò jọ tọ ọba Éjíbítì lọ, ẹ ó sì wí fún un pé, ‘OLÚWA, Ọlọ́run àwọn ará Hébérù ti pàdé wa. Jẹ́ kí a lọ ni ìrìn ọjọ́ mẹ́ta sí inú ihà láti lọ rú ẹbọ sí OLÚWA Ọlọ́run.’"'), 'Exod$3:21': (None, 'lọ', 'Exod$3:21 "“Èmi yóò sì jẹ́ kí ẹ bá ojúrere àwọn ará Éjíbítì pàdé. Tí yóò fi jẹ́ pé ti ẹ̀yin bá ń lọ, ẹ kò ní lọ ní ọwọ́ òfo."'), 'Exod$6:25': ('ọmọbinrin', 'ọmọ', 'Exod$6:25 "Élíásárì ọmọ Árónì fẹ́ ọ̀kan lára àwọn ọmọbìnrin Pútíẹ́lì ní ìyàwó, ó sì bí Fínéhásì fún un. Ìwọ̀nyí ni olórí àwọn Léfì ni ìdílé ìdílé."'), 'Exod$8:2': (None, 'lọ', 'Exod$8:2 "Bí ìwọ bá kọ̀ láti jẹ́ kí wọn ó lọ, èmi yóò fi ọ̀pọ̀lọpọ̀ àwọn ọ̀pọ̀lọ́ kọ lu gbogbo orílẹ̀ èdè rẹ."'), 'Exod$10:4': ('ni', 'kọ', 'Exod$10:4 "Bí ìwọ bá kọ̀ láti jẹ́ kí wọn lọ, èmi yóò mú eṣú wa sí orílẹ̀-èdè rẹ ní ọ̀la."'), 'Exod$10:9': (None, 'odo', 'Exod$10:9 "Mósè dáhùn ó wí pé, “A ó lọ pẹ̀lú àwọn ọmọdé àti àgbà wa, pẹ̀lú àwọn ọmọ wa ọkùnrin àti ọmọ wa obìnrin pẹ̀lú agbo àgùntàn wa àti agbo màlúù wa nítorí pé a gbọdọ̀ ṣe ajọ fún OLÚWA.”"'), 'Exod$10:13': ('ọjọ', None, 'Exod$10:13 "Nígbà náà ni Mósè na ọ̀pá ọwọ́ rẹ̀ sórí ilẹ̀ Éjíbítì, OLÚWA sì mú kí afẹ́fẹ́ láti ìlà oòrùn fẹ́ kọjá lórí ilẹ̀ náà ni gbogbo ọ̀sán àti ni gbogbo òru ni ọjọ́ náà. Ní òwúrọ̀ afẹ́fẹ́ náà ti gbá ọ̀pọ̀lọpọ̀ Esú wá;"'), 'Exod$10:22': ('okunkun', 'imole', 'Exod$10:22 " Nígbà náà ni Mósè gbé ọwọ́ rẹ sókè sí ojú ọ̀run, òkùnkùn biribiri sì bo gbogbo ilẹ̀ Éjíbítì fún ọjọ́ mẹ́ta. Ṣùgbọ́n àwọn ọmọ Ísírẹ́lì ní ìmọ́lẹ̀ ní ibi tí wọn ń gbé."'), 'Exod$10:26': ('lọ', 'wá', 'Exod$10:26 "Àwọn ohun ọ̀sìn wa gbọdọ̀ lọ pẹ̀lú wa, a kì yóò fi pátákò ẹṣẹ̀ ẹran sílẹ̀. A ní láti lò lára wọn fún sínsìn OLÚWA Ọlọ́run wa, ìgbà tí a bá sì dé ibẹ̀ ni a ó to mọ̀ ohun ti a ó lò láti fi sìn OLÚWA.”"'), 'Exod$11:2': ('eniyan', 'obinrin', 'Exod$11:2 Sọ fún àwọn ènìyàn náà pé kí tọkùnrin tobìnrin wọn béèrè fún ohun èlò fàdákà àti wúrà lọ́wọ́ aládùúgbò rẹ̀.'), 'Exod$11:8': ('wá', None, 'Exod$11:8 "Gbogbo àwọn ìjòyè rẹ̀ wọ̀nyí yóò tọ̀ mí wá, wọn yóò wólẹ̀ ni iwájú, mi wọn yóò sì máa wí pé, ‘Lọ àti àwọn ènìyàn tí ó tẹ̀ lé ọ!’ Lẹ́yìn náà èmi yóò jáde.” Nígbà náà ni Mósè fi ìbínú jáde kúrò ní iwájú Fáráò"'), 'Exod$12:15': (None, None, 'Exod$12:15 "Fún ọjọ́ méje ni ẹ̀yin yóò fi jẹ àkàrà tí kò ní ìwúkàrà. Ní ọjọ́ kìn-ín-ní, ẹ ó gbé ìwúkàrà kúrò ni ilé yín, nítorí ẹnikẹ́ni tí ó bá jẹ ohunkóhun tí ó ni ìwúkàrà nínú láti ọjọ́ kìn-ín-ní di ọjọ́ keje ni kí a yọ kúrò ni Ísírẹ́lì."'), 'Exod$12:20': (None, None, 'Exod$12:20 "Ẹ kò gbọdọ̀ jẹ ohunkóhun tí a fi ìwúkàrà ṣe. Ní ibikíbi tí ẹ̀yin bá ń gbé, kí ẹ̀yin kí ó jẹ àkàrà tí kò ní ìwúkàrà.”"'), 'Exod$12:39': (None, None, 'Exod$12:39 Pẹ̀lú ìyẹ̀fun tí kò ní ìwúkàrà nínú tí wọ́n gbé jáde láti Éjíbítì wá ní wọ́n fi ṣe àkàrà aláìwú. Ìyẹ̀fun náà kò ni ìwúkàrà nínú nítorí a lé wọn jáde láti ilẹ̀ Éjíbítì wọn kò si rí ààyè láti tọ́jú oúnjẹ fún ara wọn.'), 'Exod$12:48': (None, None, 'Exod$12:48 "“Àjòjì ti ó bá ń gbé ní àárin yín ti ó bá fẹ́ kópa nínú àjọ ìrékọjá OLÚWA, ni o gbọdọ̀ kọ gbogbo àwọn ọmọkùnrin ilé rẹ̀ ní ìlà; ní ìgbà náà ni ó lè kó ipa gẹ́gẹ́ bí àwọn ọmọ tí a bí ní ilẹ̀ náà. Gbogbo àwọn ọmọkùnrin tí a kò bá kọ ní ìlà, wọ́n kò ni jẹ ni ara rẹ̀."'), 'Exod$13:17': (None, 'lọ', 'Exod$13:17 "Ní ìgbà tí Fáráò jẹ́ kí àwọn ènìyàn ó lọ, Ọlọ́run kò mú wọn tọ ojú ọ̀nà ti ó la orílẹ̀ èdè àwọn Fílístínì kọjá, bí o tilẹ̀ jẹ́ pé ọ̀nà náà kúrú. Nítorí Ọlọ́run sọ pé, “Bí wọ́n bá dojú kọ ogun, wọ́n lè yí ọkàn wọn padà kí wọ́n padà sí ilẹ̀ Éjíbítì.”"'), 'Exod$13:21': (None, None, 'Exod$13:21 "OLÚWA sì ń lọ níwájú wọn, nínú òpó ìkùùkuu ní ọ̀san láti máa ṣe amọ̀nà wọn àti ní òru nínú òpó iná láti máa tan ìmọ́lẹ̀ fún wọn, kí wọn lè máa lọ nínú ìrìnàjò wọn ní tọ̀sán tòru."'), 'Exod$14:19': (None, None, 'Exod$14:19 "Nígbà náà ni ańgẹ́lì Ọlọ́run tó ti ń ṣááju ogun Ísírẹ́lì lọ, padà o sì rìn ni ẹ̀yìn wọn; Òpó ìkùùkuu tí ń rìn ni iwájú wọn sì padà sí í rìn ni ẹ̀yìn wọn."'), 'Exod$14:22': ('ọtun', 'osi', 'Exod$14:22 àwọn ọmọ Ísírẹ́lì si la ìyàngbẹ ilẹ̀ kọjá nínú òkun pẹ̀lú ògiri omi ní ọ̀tún àti òsì.'), 'Exod$14:29': ('ọtun', 'osi', 'Exod$14:29 Ṣùgbọ́n àwọn ará Ísírẹ́lì la òkun kọjá lórí ìyàngbẹ ilẹ̀ pẹ̀lú ògiri omi ni ọ̀tún àti òsì wọn.'), 'Exod$16:17': (None, None, 'Exod$16:17 "Àwọn ọmọ Ísírẹ́lì sì ṣe bí a ti sọ fún wọn; àwọn kan kó púpọ̀, àwọn kan kó kékeré."'), 'Exod$16:18': ('pọ', None, 'Exod$16:18 " Nígbà ti wọn fi òṣùwọ̀n ómérì wọ̀n-ọ́n-nì, ẹni ti ó kó púpọ̀ kò ní púpọ̀ jù, bẹ́ẹ̀ ni ẹni tí ó kó ìwọ̀nba kò ní kéré jù. Ẹnìkọ̀ọ̀kan kó ìwọ̀nba ti ó tó fún un."'), 'Exod$19:2': (None, 'wá', 'Exod$19:2 "Lẹ́yìn ìgbà tí wọ́n gbéra kúrò ní Réfídímù, wọ́n wọ ijù Ṣínáì, wọ́n sì tẹ̀dó ṣíbẹ̀ ní iwájú òkè Ṣínáì."'), 'Exod$19:12': ('lọ', None, 'Exod$19:12 " Kí ìwọ kí ó se ààlà fún àwọn ènìyàn, ibi tí wọn lè dé dúró, kí ìwọ kí ó sì wí fún wọn pé, ‘Ẹ ṣọ́ra! Ẹ má ṣe gun orí òkè lọ, kí ẹ máa tilẹ̀ fi ọwọ́ kan etí ààlà rẹ̀; ẹnikẹ́ni tí ó bá fi ọwọ́ kan òkè náà yóò kú:"'), 'Exod$19:20': (None, None, 'Exod$19:20 "OLÚWA sọ̀kalẹ̀ sí orí òkè Ṣínáì, o sì pe Mósè wá sí orí òkè náà. Mósè sì gun orí òkè."'), 'Exod$20:5': (None, None, 'Exod$20:5 " Ìwọ kò gbọdọ̀ tẹ orí ara rẹ ba fún wọn, bẹ́ẹ̀ ni ìwọ kò gbọdọ̀ sìn wọ́n; nítorí Èmi OLÚWA Ọlọ́run rẹ, Ọlọ́run owú ni mí, tí ń bẹ ẹ̀sẹ̀ baba wò lára ọmọ láti ìran kìn-ín-ní títí dé ẹ̀kẹrin nínú àwọn tí ó kórìíra mi."'), 'Exod$20:9': (None, None, 'Exod$20:9 "Ọjọ́ mẹ́fà ni ìwọ yóò fi ṣiṣẹ́, kí ìwọ kí ó ṣe gbogbo iṣẹ́ rẹ,"'), 'Exod$20:12': ('baba', 'iya', 'Exod$20:12 " “Bọ̀wọ̀ fún baba àti ìyá rẹ, kí ọjọ́ rẹ bá a le pẹ́ ní orí ilẹ̀ tí OLÚWA Ọlọ́run rẹ fi fún ọ."'), 'Exod$21:3': ('wá', 'lọ', 'Exod$21:3 "Bí ó bá wá ní òun nìkan òun nìkan náà ni yóò padà lọ ní òmìnira; ṣùgbọ́n tí ó bá mú ìyàwó wá, ó ní láti mú ìyàwó rẹ̀ padà lọ."'), 'Exod$21:9': (None, 'ọmọ', 'Exod$21:9 "Bí ó bá fẹ́ ẹ fún ọmọkùnrin rẹ̀, kò ní máa lò ó bí ìwẹ̀fà mọ́, ṣùgbọ́n yóò máa ṣe sí i gẹ́gẹ́ bí ọmọ rẹ̀ obìnrin."'), 'Exod$21:15': ('baba', 'iya', 'Exod$21:15 "“Ẹnikẹ́ni tí ó bá pa baba tàbí ìyá rẹ̀, pípa ni a ó pa á."'), 'Exod$21:17': ('baba', 'iya', 'Exod$21:17 " “Ẹnikẹ́ni tí ó bá ṣépè fún baba tàbí ìyá rẹ̀, pípa ni a ó pa á."'), 'Exod$21:22': (None, None, 'Exod$21:22 "“Bí àwọn ènìyàn ti ń jà bá pa aboyún lára, tí aboyún náà bá bimọ láìpé ọjọ́, ṣùgbọ́n ti kò sí aburú mìíràn mọ́ lẹ́yìn rẹ̀, ẹni ti ó fa ìpalára yìí yóò san iyekíye ti ọkọ aboyún náà bá béèrè fún, bí ilé ẹjọ́ bá se gbà láàyè gẹ́gẹ́ bí owó ìtanràn."'), 'Exod$21:28': (None, 'obinrin', 'Exod$21:28 "“Bí akọ màlúù bá kan ọkùnrin tàbí obìnrin kan pa, a ó sọ akọ màlúù náà ní òkúta pa, a kò sì gbọdọ̀ jẹ ẹran rẹ̀. Ṣùgbọ́n a kò ní dá ẹni tí ó ni akọ màlúù náà lẹ́bi, ọrùn rẹ̀ yóò sì mọ́."'), 'Exod$21:29': ('eniyan', 'obinrin', 'Exod$21:29 "Ṣùgbọ́n tí ó bá ti di ìgbà gbogbo ti akọ màlúù náà ti máa ń kan ènìyàn, tí a sì ti ń kìlọ̀ fún olówó rẹ̀, ti olówó rẹ̀ kò sì mú un so, tí ó bá kan ọkùnrin tàbí obìnrin pa, a ó sọ akọ màlúù náà ní òkúta pa, a ó sì pa olówó rẹ̀ pẹ̀lú."'), 'Exod$21:31': ('ọmọbinrin', 'ọmọ', 'Exod$21:31 "Bí akọ màlúù bá kan ọmọkùnrin tàbí ọmọbìnrin pa, a ó se gẹ́gẹ́ bí ofin yìí ti là á kalẹ̀."'), 'Exod$21:35': (None, 'kú', 'Exod$21:35 "“Bí akọ màlúù ọkùnrin kan bá pa akọ màlúù ẹlòmíràn lára tí ó sì kú, wọn yóò ta akọ màlúù tó wà láàyè, wọn yóò sì pín owo èyí ti wọn tà àti ẹran èyí tí ó kú dọ́gbadọ́gba."'), 'Exod$22:6': ('ṣe', None, 'Exod$22:6 "“Bí iná bá ṣẹ́ tí ó kán lu igbó tí ó sì jó àká ọkà tàbí gbogbo oko náà, ẹni tí iná ṣẹ́ lọ́wọ́ rẹ̀ yóò san ohun tí ìná ti ó ṣẹ́ jó padà."'), 'Exod$22:7': ('tọju', None, 'Exod$22:7 " “Bí ọkùnrin kan bá fún aládùúgbò rẹ̀ ní fàdákà tàbí ohun èlò fún ìtọ́jú, ti wọ́n sì jí gbé lọ́dọ̀ aládùúgbò rẹ̀, tí a bá mú irú olè bẹ́ẹ̀, yóò san án padà ní ìlọ́po méjì."'), 'Exod$24:2': ('wá', None, 'Exod$24:2 Ṣùgbọ́n Mósè nìkan ṣoṣo ni yóò súnmọ́ OLÚWA; àwọn tókù kò gbọdọ̀ súnmọ́ tòsí ibẹ̀. Bẹ́ẹ̀ ni àwọn ènìyàn kò gbọdọ̀ gòkè wá pẹ̀lú rẹ̀.”'), 'Exod$26:28': ('opin', None, 'Exod$26:28 Ọ̀pá ìdábùú àárin ni agbede-méjì gbọdọ̀ tàn láti òpin dé òpin pákó náà.'), 'Exod$26:35': ('ariwa', None, 'Exod$26:35 "Gbé tábìlì náà sí ìta aṣọ títa náà sí ìhà gúsù àgọ́ náà, kí o sì gbé ọ̀pá fitílà sí ọ̀kánkán rẹ̀ ní ìhà àríwá."'), 'Exod$30:15': (None, 'talaka', 'Exod$30:15 "Àwọn olówó kì yóò san ju ìdajì sékélì lọ, àwọn talákà kò sì gbọdọ̀ dín ní ìdajì sékélì nígbà tí ẹ̀yin bá mú ọrẹ wá fún OLÚWA láti se ètùtù fún ọkàn yín."'), 'Exod$30:19': (None, 'wá', 'Exod$30:19 Árónì àti àwọn ọmọ rẹ̀ yóò máa wẹ ọwọ́ àti ẹṣẹ̀ wọn pẹ̀lú omi tí ó wà nínú rẹ̀.'), 'Exod$32:1': ('wá', None, 'Exod$32:1 " Nígbà tí àwọn ènìyàn rí i pé Mósè pẹ́ kí ó tó sọ̀kalẹ̀ láti orí òkè wá, wọn péjọ yí Árónì ká, wọ́n sì wí pé, “Wá, dá òrìṣà tí yóò máa ṣáájú wa fún wa. Bí ó ṣe ti Mósè tí ó mú wa jáde láti Éjíbítì wá, àwa kò mọ́ ohun tí ó ti sẹlẹ̀ sí i.”"'), 'Exod$32:6': (None, None, 'Exod$32:6 " Bẹ́ẹ̀ ni àwọn ènìyàn dìde ní kùtùkùtù ọjọ́ kejì, wọ́n sì rúbọ síṣun, wọ́n sì mú ẹbọ àlàáfíà wá. Lẹ́yìn náà wọ́n jókòó láti jẹ àti láti mu, wọ́n sì dìde láti ṣeré."'), 'Exod$32:30': (None, 'lọ', 'Exod$32:30 "Ní ọjọ́ kejì Mósè sọ fún àwọn ènìyàn náà pé, “Ẹ̀yin ti dá ẹ̀ṣẹ̀ tí ó pọ̀. Ṣùgbọ́n nísinsin yìí èmi yóò gòkè lọ bá OLÚWA; bóyá èmi lè se ètùtù fún ẹ̀ṣẹ̀ yín.”"'), 'Exod$34:12': (None, None, 'Exod$34:12 "Má a sọ́ra kí o má ba à bá àwọn ti ó n gbé ilẹ náà tí ìwọ ń lọ dá májẹ̀mú, nítorí wọn yóò jẹ́ ìdíwọ́ láàrin rẹ."'), 'Exod$35:22': ('obinrin', None, 'Exod$35:22 "Gbogbo àwọn tí ó fẹ́, ọkùnrin àti obìnrin, wọ́n wá wọ́n sì mú onírúurú ìlẹ̀kẹ wúrà: òrùka etí, òrùka àti ọ̀sọ́. Gbogbo wọn mú wúrà gẹ́gẹ́ bí ọrẹ wá fún OLÚWA."'), 'Exod$35:29': ('eniyan', 'obinrin', 'Exod$35:29 Gbogbo àwọn ènìyàn Isirẹli ọkùnrin àti obìnrin ẹni tí ó fẹ́ mú ọrẹ àtinúwá fún OLÚWA fún gbogbo iṣẹ́ tí OLÚWA ti pa láṣẹ fún wọn láti se nípaṣẹ̀ Mósè.'), 'Exod$36:6': ('eniyan', 'obinrin', 'Exod$36:6 "Mósè sì pàṣẹ, wọ́n sì rán iṣẹ́ yìí sí gbogbo ibùdó: “Kí ọkùnrin tàbí obìnrin má ṣe ṣe ohun kankan bí ọrẹ fún ibi mímọ́ náà mọ́.” Bẹ́ẹ̀ ni a dá àwọn ènìyàn lẹ́kun láti mú un wá sí i,"'), 'Exod$36:33': (None, None, 'Exod$36:33 Wọ́n sì ṣe ọ̀pá àárin tí yóò fi jáde láti ìkangun dé ìkangun ní àárin àwọn pákó náà.'), 'Exod$37:17': (None, None, 'Exod$37:17 " Ó sì se ọ̀pá fìtílà náà ní kìkì wúrà ó sì lù ú jáde, ọ̀pá rẹ̀, ìtànná ìfẹ́ rẹ̀, irudi rẹ àti ago rẹ̀, wọ́n jẹ́ òkan náà."'), 'Exod$40:17': ('keji', None, 'Exod$40:17 Bẹ́ẹ̀ ni wọ́n gbé Àgọ́ náà ró ní ọjọ́ kìn-ín-ní oṣù kìn-ìn-ní ní ọdún kejì.'), 'Exod$40:38': (None, None, 'Exod$40:38 "Nítorí náà àwọ̀ọsánmọ̀ OLÚWA wà lórí Àgọ́ ní ọ̀sán, iná sì wà nínú àwọ̀ọsánmọ̀ ní òru, ní ojú gbogbo ilé Ísírẹ́lì ní gbogbo ìrìnàjò wọn."'), 'Lev$3:1': (None, None, 'Lev$3:1 " “ ‘Bí ọrẹ ẹnìkan bá jẹ́ ọrẹ àlàáfíà, tí ó sì mú ẹran wá láti inú agbo màlúù yálà akọ tàbí abo, kí ó mú èyí tí kò ní àbùkù wá ṣíwájú OLÚWA."'), 'Lev$3:6': (None, None, 'Lev$3:6 “ ‘Bí ó bá ṣe inú agbo ewúrẹ́ àti àgùntàn ni ó ti mú ọrẹ àlàáfíà wá fún OLÚWA kí ó mú akọ tàbí abo tí kò ní àbùkù.'), 'Lev$5:4': ('dara', None, 'Lev$5:4 "“ ‘Tàbí bí ẹni kan bá búra láti ṣe ohun kan láì ronú lé e lórí, yálà ohun tó dára tàbí ohun búburú, nínú ọ̀rọ̀ yòówù tó ti búra láì kíyèsí ara, bí ó tilẹ̀ jẹ́ pé, kò mọ̀ nípa rẹ̀ tẹ́lẹ̀, bí ó ti wù kó rí, nígbà tó bá mọ̀ yóò jẹ̀bi."'), 'Lev$6:1': (None, 'fun', 'Lev$6:1 OLÚWA sọ fún Mósè pé:'), 'Lev$6:13': (None, None, 'Lev$6:13 "Iná gbọdọ̀ máa jó lórí pẹpẹ títí, kò gbọdọ̀ kú."'), 'Lev$6:19': (None, None, 'Lev$6:19 "OLÚWA sọ fún Mósè pé,"'), 'Lev$7:19': (None, 'mimọ', 'Lev$7:19 "“ ‘Ẹ kò gbọdọ̀ jẹ ẹran tí ó bá ti kan ohun tí a kà sí àìmọ́, sísun ni kí ẹ sun ún. Ẹnikẹ́ni tí a ti kà sí mímọ́ le è jẹ nínú ẹran tí ó kù."'), 'Lev$8:35': ('ọjọ', None, 'Lev$8:35 "Ẹ gbọdọ̀ wà ní ẹnu ọ̀nà àgọ́ ìpàdé lọ́sàn án àti lóru fún ọjọ́ méje kí ẹ sì ṣe ohun tí OLÚWA fẹ́, kí ẹ má ba à kú, nítorí ohun tí OLÚWA pa láṣẹ fún mi ni èyí.”"'), 'Lev$10:8': (None, None, 'Lev$10:8 OLÚWA sì sọ fún Árónì pé.'), 'Lev$11:36': ('alaimọ', None, 'Lev$11:36 "Bí òkú wọn bá bọ́ sínú omi tàbí kànga tó ní omi nínú, omi náà kò di aláìmọ́ ṣùgbọ́n ẹnikẹ́ni tí ó bá yọ òkú wọn jáde tí ó fi ọwọ́ kàn-án yóò di aláìmọ́."'), 'Lev$11:46': (None, None, 'Lev$11:46 " “ ‘Àwọn wọ̀nyí ni ìlànà fún ẹranko, ẹyẹ, gbogbo ẹ̀dá alààyè tí ń gbé inú omi àti àwọn ẹ̀dá tí ó ń rìn lórí ilẹ̀."'), 'Lev$12:1': (None, None, 'Lev$12:1 " OLÚWA sọ fún Mósè pé,"'), 'Lev$12:6': ('ọmọ', 'ọmọbinrin', 'Lev$12:6 “ ‘Nígbà tí ọjọ́ ìwẹ̀nùmọ́ rẹ̀ fún ọmọkùnrin tàbí ọmọbìnrin bá kọjá kí ó mú ọ̀dọ́ àgùntàn ọlọ́dún kan wá fún àlùfáà ní ẹnu ọ̀nà àgọ́ ìpàdé fún ẹbọ sísun àti ọmọ ẹyẹlé tàbí àdàbà kan fún ẹbọ ẹ̀ṣẹ̀.'), 'Lev$13:12': ('ẹsẹ', 'ori', 'Lev$13:12 "“Bí àrùn náà bá ràn yíká gbogbo awọ ara rẹ̀ tí àlùfáà sì yẹ̀ ẹ́ wò, tí ó rí i pé àrùn náà ti gba gbogbo ara rẹ̀ láti orí títí dé ẹṣẹ̀,"'), 'Lev$13:29': (None, 'obinrin', 'Lev$13:29 “Bí ọkùnrin tàbí obìnrin kan bá ní egbò lórí tàbí ní àgbọ̀n.'), 'Lev$13:38': (None, 'obinrin', 'Lev$13:38 “Bí ọkùnrin tàbí obìnrin kan bá ní ojú àpá funfun lára àwọ̀ ara rẹ̀.'), 'Lev$13:59': (None, 'mimọ', 'Lev$13:59 "Ìlànà wọ̀nyí wà fún àwọn ohun tí ẹ̀tẹ̀ bàjẹ́ níbi, aṣọ irun àgùtàn aṣọ funfun, aṣọ títa, aṣọ híhun tàbí ohun èlò awọ, láti fi hàn bóyá wọ́n wà ní mímọ́ tàbí àìmọ́."'), 'Lev$14:11': (None, None, 'Lev$14:11 Àlùfáà tí ó pè é ní mímọ́: yóò mú ẹni náà tí a ó sọ di mímọ́ àti ọrẹ rẹ̀ wá ṣíwájú OLÚWA ní ẹnu ọ̀nà àgọ́ ìpàdé.'), 'Lev$14:24': (None, None, 'Lev$14:24 Àlùfáà náà yóò sì mú ọ̀dọ́ àgùntàn ẹbọ ẹ̀bi pẹ̀lú òróró yóò sì fì wọ́n níwájú OLÚWA bí ẹbọ fífì.'), 'Lev$14:33': (None, None, 'Lev$14:33 OLÚWA sọ fún Mósè àti Árónì pé.'), 'Lev$14:54': (None, None, 'Lev$14:54 "Àwọn wọ̀nyí ni ìlànà fún èyíkéyí àrùn àwọ̀ ara tí ó le è ràn ká (ẹ̀tẹ̀), fún làpálàpá,"'), 'Lev$15:8': (None, None, 'Lev$15:8 "“ ‘Bí ẹni tí ó ní ìṣunjáde bá tu itọ́ sí ẹni tí ó mọ́ lára, ẹni tí ó mọ́ náà gbọdọ̀ fọ aṣọ rẹ̀ kí ó sì wẹ̀: yóò sì wà nípò àìmọ́ títí di ìrọ̀lẹ́."'), 'Lev$15:14': ('fun', None, 'Lev$15:14 "Ní ọjọ́ kẹjọ kí ó mú àdàbà méjì tàbí ọmọ ẹyẹlé méjì, kí ó sì wá sí ẹnu ọ̀nà àgọ́ ìpàdé níwájú OLÚWA, kí ó sì kó wọn fún àlùfáà."'), 'Lev$15:18': (None, 'obinrin', 'Lev$15:18 Bí ọkùnrin kan bá bá obìnrin lòpọ̀ tí nǹkan ọkùnrin sì tú jáde lára rẹ̀. Àwọn méjèèjì gbọdọ̀ fi omi wẹ̀: kí wọ́n sì wà ní ipò àìmọ́ títí di ìrọ̀lẹ́.'), 'Lev$15:32': (None, None, 'Lev$15:32 Ìwọ̀nyí ni àwọn òfin fún ọkùnrin tí ó ni ìṣunjáde fún ẹnikẹ́ni tí a sọ di àìmọ́ nípa ìṣunjáde nǹkan ọkùnrin rẹ̀.'), 'Lev$16:23': ('lọ', 'wá', 'Lev$16:23 Árónì yóò sì padà wá sí ibi àgọ́ ìpàdé yóò sì bọ́ aṣọ funfun gbòò tí ó wọ̀ nígbà tí ó lọ sí ibi mímọ́ jùlọ yóò sì fi wọ́n sílẹ̀ níbẹ̀'), 'Lev$16:26': (None, 'wá', 'Lev$16:26 Ẹni tí ó tú ewúrẹ́ ìpààrọ̀ náà sílẹ̀ yóò sì fọ aṣọ rẹ̀: yóò sì wẹ ara rẹ̀ nínú omi: lẹ́yìn èyí ó lè wá sí ibùdó.'), 'Lev$17:15': (None, 'mimọ', 'Lev$17:15 “ ‘Ẹnikẹ́ni tí ó bá jẹ ohun tí ó kú sílẹ̀ fúnrarẹ̀: tàbí èyí tí ẹranko búburú pa kálẹ̀ yálà onílé tàbí àlejò: ó ní láti fọ aṣọ rẹ̀. Kí ó sì fi omi wẹ ara rẹ̀: yóò sì wà ní ipò àìmọ́ títí di àṣálẹ́ lẹ́yìn èyí ni yóò tó di mímọ́.'), 'Lev$18:7': ('baba', 'iya', 'Lev$18:7 " “ ‘Ìwọ kò gbọdọ̀ tàbùkù u bàbá rẹ nípa bíbá ìyá rẹ lòpọ̀, ìyá rẹ̀ ni, ìwọ kò gbọdọ̀ bá a lòpọ̀."'), 'Lev$18:9': (None, 'iya', 'Lev$18:9 " “ ‘Ìwọ kò gbọdọ̀ bá arábìnrin rẹ tí ó jẹ́ ọmọ ìyá rẹ lòpọ̀, yálà a bí i nílé yín tàbí lóde."'), 'Lev$18:10': ('ọmọbinrin', 'ọmọ', 'Lev$18:10 "“ ‘Ìwọ kò gbọdọ̀ bá ọmọbìnrin, ọmọ rẹ ọkùnrin lòpọ̀ tàbí ọmọbìnrin ọmọ rẹ obìnrin lòpọ̀ nítorí pé ìhòòhò wọn, ìhòòhò ìwọ fúnrarẹ ni."'), 'Lev$18:15': (None, 'ọmọ', 'Lev$18:15 “ ‘Ìwọ kò gbọdọ̀ bá ọmọ àna obìnrin rẹ lòpọ̀ nítorí pé aya ọmọ rẹ ni. Má ṣe bá a lòpọ̀.'), 'Lev$18:17': ('ọmọbinrin', 'ọmọ', 'Lev$18:17 " “ ‘Má ṣe bá ìyá àti ọmọ rẹ obìnrin lòpọ̀, bẹ́ẹ̀ ni ìwọ kò gbọdọ̀ bá ọmọbìnrin ọmọkùnrin rẹ tàbí ọmọbìnrin ọmọbìnrin rẹ lòpọ̀ nítorí pé ìbátan rẹ tímọ́tímọ́ ni: nítorí àbùkù ni."'), 'Lev$18:23': (None, None, 'Lev$18:23 " “ ‘Ìwọ kò gbọdọ̀ bá ẹranko lòpọ̀ kí ìwọ má ba à ba ara rẹ jẹ́. Obìnrin kò sì gbọdọ̀ jọ̀wọ́ ara rẹ̀ sílẹ̀ fún ẹranko láti bá a lòpọ̀, ohun tó lòdì ni."'), 'Lev$19:3': ('baba', 'iya', 'Lev$19:3 " “ ‘Ẹnìkọ̀ọ̀kan yín gbọdọ̀ bọ̀wọ̀ fún ìyá àti bàbá rẹ̀, kí ẹ sì ya ọjọ́ ìsinmi mi sí mímọ́. Èmi ni OLÚWA Ọlọ́run yín."'), 'Lev$19:15': (None, 'ododo', 'Lev$19:15 " “ ‘Ìwọ kò gbọdọ̀ yí ìdájọ́ po, má ṣe ojúṣàájú sí ẹjọ́ talákà: bẹ́ẹ̀ ni o kò gbọdọ̀ gbé ti ọlọ́lá lẹ́yìn: ṣùgbọ́n fi òdodo ṣe ìdájọ́, àwọn aládùúgbò rẹ."'), 'Lev$19:16': (None, None, 'Lev$19:16 “ ‘Ìwọ kò gbọdọ̀ máa ṣèyíṣọ̀hún bí olófofó láàrin àwọn ènìyàn rẹ. “ ‘Ìwọ kò gbọdọ̀ ṣe ohunkóhun tí yóò fi ẹ̀mi aládúgbò rẹ wéwu: Èmi ni OLÚWA.'), 'Lev$20:9': ('baba', 'iya', 'Lev$20:9 " “ ‘Ẹnikẹ́ni tí ó bá ṣépè lé bàbá tàbí ìyá rẹ̀ ni kí ẹ pa, ẹ̀jẹ̀ rẹ̀ wà lórí ara rẹ̀ torí pé ó ti ṣépè lé bàbá àti ìyá rẹ̀."'), 'Lev$20:13': (None, 'obinrin', 'Lev$20:13 " “ ‘Bí ọkùnrin kan bá bá ọkùnrin mìíràn lòpọ̀ bí wọ́n í ti bá obìnrin lòpọ̀: àwọn méjèèjì ti ṣe ohun ìríra: pípa ni kí ẹ pa wọ́n, ẹ̀jẹ̀ wọn yóò wà lórí wọn."'), 'Lev$20:17': ('baba', 'iya', 'Lev$20:17 " “ ‘Bí ọkùnrin kan bá fẹ́ arábìnrin rẹ̀ yálà ọmọbìnrin bàbá rẹ̀ tàbí ọmọbìnrin ìyá rẹ̀ tí ó sì bá a lòpọ̀, ohun ìtìjú ni èyí: Ẹ ó gé wọn kúrò lójú àwọn ènìyàn wọn: ó ti tàbùkù arábìnrin rẹ̀. Ẹ̀bi rẹ̀ yóò wá sórí rẹ̀."'), 'Lev$20:18': ('eniyan', 'obinrin', 'Lev$20:18 " “ ‘Bí ọkùnrin kan bá sún mọ́ obìnrin, ní àkókò nǹkan oṣù rẹ̀ tí ó sì bá a lòpọ̀. Ó ti tú orísun ẹ̀jẹ̀ rẹ̀. Òun náà sì gbà á láàyè. Àwọn méjèèjì ni a ó gé kúrò láàrin àwọn ènìyàn wọn."'), 'Lev$20:19': ('baba', 'iya', 'Lev$20:19 " “ ‘Má ṣe bá arábìnrin bàbá tàbí ti ìyá rẹ lòpọ̀, nítorí o ti tú ìhòòhò ìbátan rẹ: Ẹ̀yin méjèèjì ni yóò ru ẹ̀bi yín."'), 'Lev$20:25': (None, None, 'Lev$20:25 “ ‘Ẹ gbọdọ̀ pààlà sáàrin ẹran tí ó mọ́ àti àwọn tí kò mọ́. Láàrin ẹyẹ tí ó mọ́ àti àwọn tí kò mọ́. Ẹ má ṣe sọ ara yín di àìmọ́ nípasẹ̀ àwọn ẹranko tàbí ẹyẹ tàbí ohunkohun tí ń rìn lórí ilẹ̀: èyí tí mo yà sọ́tọ̀ fún yín gẹ́gẹ́ bí ohun àìmọ́.'), 'Lev$20:27': (None, 'obinrin', 'Lev$20:27 " “ ‘Ọkùnrin tàbí obìnrin tí ó jẹ́ abókúsọ̀rọ̀ tàbí oṣó láàrin yín ni kí ẹ pa. Ẹ sọ wọ́n ní òkúta, ẹ̀jẹ̀ wọn yóò wà lórí wọn.’ ”"'), 'Lev$21:1': (None, 'iya', 'Lev$21:1 " OLÚWA sọ fún Mósè wí pe, “Bá àwọn àlùfáà tí wọ́n jẹ́ ọmọ Árónì sọ̀rọ̀, kí o sì sọ fún wọn pé: ‘Ẹnikẹ́ni nínú wọn kò gbọdọ̀ sọ ara rẹ̀ di aláìmọ́ nítorí àwọn ènìyàn wọn tí ó kù."'), 'Lev$21:7': (None, 'ọkọ', 'Lev$21:7 “ ‘Àwọn àlùfáà kò gbọdọ̀ fẹ́ aṣẹ́wó tàbí obìnrin tí ó ti fi ọkùnrin ba ara rẹ̀ jẹ́ tàbí obìnrin tí ọkọ rẹ̀ ti kọ̀ sílẹ̀ nítorí pé àwọn àlùfáà jẹ́ mímọ́ sí Ọlọ́run wọn.'), 'Lev$21:10': (None, None, 'Lev$21:10 "“ ‘Lẹ́yìn tí a ti fi òróró yan olórí àlùfáà láàrin àwọn arákùnrin rẹ̀ tí a sì ti fi amì òróró yàn láti máa wọ aṣọ àlùfáà, gbọdọ̀ tọ́jú irún orí rẹ̀, bẹ́ẹ̀ ni kò gbọdọ̀ fa aṣọ rẹ̀ ya láti fihàn pé òun wà ní ipò ọ̀fọ̀."'), 'Lev$21:23': ('lọ', None, 'Lev$21:23 "Nípa ṣíṣe bẹ́ẹ̀ yóò ba ibi mímọ́ mi jẹ́ torí pé, Èmi OLÚWA ló ti sọ wọ́n di mímọ́.’ ”"'), 'Lev$22:22': (None, 'ṣe', 'Lev$22:22 "Ẹ má ṣe fi ẹranko tí ó fọ́jú, tí ó farapa, tí ó yarọ, tí ó ní koko, tí ó ní èkúkú, èépá àti egbò kíkẹ̀ rúbọ sí OLÚWA. Ẹ kò gbọdọ̀ gbé èyíkeyi nínú wọn sórí pẹpẹ gẹ́gẹ́ bi ọrẹ tí a fi iná ṣe sí OLÚWA."'), 'Lev$22:24': (None, 'ṣe', 'Lev$22:24 "Ẹ má ṣe fi ẹranko tí kóró ẹpọ̀n rẹ̀ fọ́ tàbí tí a tẹ̀, tàbí tí a yà tàbí tí a là rúbọ sí OLÚWA. Ẹ kò gbọdọ̀ ṣe èyí ní ilẹ̀ yín."'), 'Lev$24:10': (None, None, 'Lev$24:10 "Ọmọkùnrin kan wà tí ìyá rẹ̀ jẹ́ ọmọ Ísírẹ́lì baba rẹ̀ sì jẹ́ ará Éjíbítì. Ó jáde lọ láàrin àwọn ọmọ Ísírẹ́lì, ìjà sì ṣẹlẹ̀ nínú àgọ́ láàrin òun àti ọmọ Ísírẹ́lì kan."'), 'Lev$25:14': ('ta', 'ra', 'Lev$25:14 "“ ‘Bí ẹ bá ta ilẹ̀ fún ọ̀kan nínú àwọn ènìyàn yín tàbí bí ẹ bá ra èyíkéyí láti ọ̀dọ̀ rẹ̀, ẹ má ṣe rẹ́ ara yín jẹ."'), 'Lev$25:47': (None, 'talaka', 'Lev$25:47 “ ‘Bí àlejò kan láàrin yín tàbí ẹni tí ń gbé àárin yín fún ìgbà díẹ̀ bá lọ́rọ̀ tí ọmọ Ísírẹ́lì sì talákà débi pé ó ta ara rẹ̀ lẹ́rú fún àlejò tàbí ìdílé àlejò náà.'), 'Lev$26:10': (None, 'tuntun', 'Lev$26:10 "Àwọn èrè oko yín yóò pọ̀ tóbẹ́ẹ̀ tí ẹ̀yin ó máa jẹ èrè ọdún tí ó kọjá, ẹ̀yin ó sì kó wọn jáde: kí ẹ̀yin lè rí àyè kó túntún sí."'), 'Lev$26:19': (None, 'fọ', 'Lev$26:19 Èmi yóò fọ́ agbára ìgbéraga yín: ojú ọ̀run yóò le koko bí irin: ilẹ̀ yóò sì le bí idẹ (òjò kò ní rọ̀: ilẹ̀ yín yóò sì le).'), 'Lev$27:5': ('obinrin', 'okunrin', 'Lev$27:5 "Bí ẹni náà bá jẹ́ ọmọ ọdún márùn-ún sí ogún ọdún, kí iye owó rẹ̀ jẹ́ ogún ṣékélì fún ọkùnrin àti òṣùwọ̀n ṣékélì mẹ́wàá fún obìnrin."'), 'Lev$27:6': ('okunrin', 'obinrin', 'Lev$27:6 "Bí ẹni náà bá jẹ́ ọmọ oṣù kan sí ọdún márùn-ún, kí iye owó rẹ̀ jẹ́ òṣùwọ̀n ṣékélì fàdákà márùn-ún fún ọkùnrin àti òṣùwọ̀n ṣékélì fàdákà mẹ́ta fún obìnrin."'), 'Lev$27:7': ('okunrin', 'obinrin', 'Lev$27:7 "Bí ẹni náà bá jẹ́ ọmọ ọgọ́ta ọdún àti jù bẹ́ẹ̀ lọ, kí iye owó rẹ̀ jẹ́ òṣùwọ̀n ṣékélì mẹ́ẹ̀dógún fún ọkunrin àti òṣùwọ̀n ṣékélì mẹ́wàá fún obìnrin."'), 'Lev$27:10': (None, 'dara', 'Lev$27:10 "Ẹni tí ó jẹ́ ẹ̀jẹ́ náà kò gbọdọ̀ pàrọ̀ ẹran mìíràn, yálà kí ó pàrọ̀ èyí tí ó dára sí èyí tí kò dára tàbí èyí tí kò dára sí èyí tí ó dára. Bí ó bá se bẹ́ẹ́ ti OLÚWA ni ẹranko méjèèjì."'), 'Lev$27:11': (None, None, 'Lev$27:11 Ṣùgbọ́n bí ó bá jẹ́ ẹ̀jẹ́ níti ẹranko àìmọ́ tí a fi tọrẹ: èyí tí kò jẹ́ ìtẹ́wọ́gbà bí i ọrẹ fún OLÚWA. Kí ọkùnrin náà mú ọrẹ náà lọ fún àlùfáà.'), 'Lev$27:14': (None, 'dara', 'Lev$27:14 “ ‘Bí ẹnikẹ́ni bá ya ilẹ̀ rẹ̀ sí mímọ́ fún OLÚWA: kí àlùfáà kó sọ iye tí yóò san gẹ́gẹ́ bí ilẹ̀ náà ti dára tàbí bàjẹ́ sí: iye owó náà ni kí ó jẹ́.'), 'Lev$27:33': (None, 'dara', 'Lev$27:33 "Ènìyàn kò gbọdọ̀ wádìí bóyá ó dára tàbí kò dára, kò sì gbọdọ̀ pààrọ̀ rẹ̀. Bí ó bá pààrọ̀ rẹ̀: àti èyí tí ó pààrọ̀ àti èyí tí ó fi pààrọ̀ ni kí ó jẹ́ mímọ́. Wọn kò gbọdọ̀ rà á padà.’ ”"'), 'Num$1:17': (None, None, 'Num$1:17 Mósè àti Árónì mú àwọn ènìyàn tí a dárúkọ wọ̀nyí'), 'Num$3:46': (None, 'gba', 'Num$3:46 "Nísinsin yìí, láti lè ra ọ̀rìnlúgba dín méje (273) àkọ́bí àwọn Ísírẹ́lì tó ju iye àwọn ọmọ Léfì lọ,"'), 'Num$5:1': (None, None, 'Num$5:1 OLÚWA sọ fún Mósè pé:'), 'Num$5:5': (None, None, 'Num$5:5 OLÚWA sọ fún Mósè pé:'), 'Num$5:11': (None, None, 'Num$5:11 "OLÚWA sọ fún Mósè wí pé,"'), 'Num$5:16': (None, None, 'Num$5:16 “ ‘Àlùfáà yóò sì mú-un wá ṣíwájú OLÚWA.'), 'Num$5:29': (None, 'ọkọ', 'Num$5:29 "“ ‘Èyí ni òfin owú jíjẹ nígbà tí obìnrin tó wà lábẹ́ ọkọ bá ṣe aṣemá ṣe, tí ó bá ba ara rẹ̀ jẹ́"'), 'Num$5:31': (None, 'obinrin', 'Num$5:31 Ara ọkọ rẹ̀ mọ́ nínú ẹ̀ṣẹ̀ yìí ṣùgbọ́n obìnrin náà yóò ru ẹ̀bi ẹ̀ṣẹ̀ rẹ̀.’ ”'), 'Num$6:1': (None, None, 'Num$6:1 OLÚWA sọ fún Mósè pé:'), 'Num$6:7': ('baba', 'iya', 'Num$6:7 "Ìbá à ṣe òkú baba, àti ìyá rẹ̀, ẹ̀gbọ́n rẹ̀ ọkùnrin tàbí obìnrin tàbí àbúrò rẹ̀, kò gbọdọ̀ sọ ara rẹ̀ di aláìmọ́ nítorí wọn, nítorí pé àmì ìyàsọ́tọ̀ rẹ̀ sí Ọlọ́run wà ní orí rẹ̀."'), 'Num$6:10': (None, None, 'Num$6:10 "Ní ọjọ́ kẹjọ, yóò mú àdàbà méjì àti ọmọ ẹyẹlé méjì wá sọ́dọ̀ àlùfáà ní ẹnu ọ̀nà àgọ́ ìpàdé."'), 'Num$7:18': (None, 'keji', 'Num$7:18 Ní ọjọ́ kéjì ni Nétaníẹ́lì ọmọ Súárì olórí àwọn ọmọ Ísákárì mú ọrẹ tirẹ̀ wá.'), 'Num$9:1': ('keji', None, 'Num$9:1 OLÚWA sọ fún Mósè nínú ihà Ṣínáì ní oṣù kìn-ín-ní ọdún kejì lẹ́yìn tí wọ́n kúrò ní ilẹ̀ Éjíbítì wí pé;'), 'Num$9:12': (None, None, 'Num$9:12 " Wọn kò gbọdọ̀ ṣẹ́ ọ̀kankan kù di àárọ̀, wọn kò sì gbọdọ̀ ṣẹ́ eegun rẹ̀. Wọ́n gbọdọ̀ tẹ̀lé gbogbo ìlànà fún ṣíṣẹ àjọ Ìrékọjá."'), 'Num$9:16': (None, 'alẹ', 'Num$9:16 "Bẹ́ẹ̀ ló sì ṣe títí, ìkúùkù yóò bò ó lọ́sán, ìrísí rẹ̀ sì dàbí iná lálẹ́."'), 'Num$9:21': (None, None, 'Num$9:21 "Ìgbà mìíràn ìkúùkù lè dúró láti ìrọ̀lẹ́ di àárọ̀, nígbà tó bá sì kúrò ní àárọ̀, wọn ó gbéra. Ìbá à ṣe ní ọ̀sán tàbí òru, ìgbàkigbà tí ìkúùkù bá tó kúrò náà ni wọn ó tó gbéra."'), 'Num$11:32': ('ọjọ', None, 'Num$11:32 "Ní gbogbo ọjọ́ náà àti òru, títí dé ọjọ́ kejì ni àwọn ènìyàn fi ń kó àparò yìí: Ẹni tó kó kéré jù lọ kó ìwọ̀n hómérì mẹ́wáà, wọ́n sì sà wọ́n sílẹ̀ fún ara wọn yí gbogbo ibùdó."'), 'Num$13:17': (None, None, 'Num$13:17 "Nígbà tí Mósè rán wọn lọ láti yẹ ilẹ̀ náà wò, ó sọ fún wọn pé, “Ẹ gba ọ̀nà Gúúsù lọ títi dé àwọn ìlú olókè"'), 'Num$13:21': ('lọ', None, 'Num$13:21 "Bẹ́ẹ̀ ni wọ́n gòkè lọ láti yẹ ilẹ̀ náà wò, wọ́n lọ láti Aginjù Ṣínì títí dé Réhóbù lọ́nà Lébò Hámátì."'), 'Num$14:13': (None, None, 'Num$14:13 "Ṣùgbọ́n Mósè sọ fún OLÚWA pé, “Nígbà náà ni àwọn ará Éjíbítì yóò gbọ́; Nítorí pé nípa agbára Írẹ ni ìwọ fi mú àwọn ènìyàn wọ̀nyí jáde kúrò láàrin wọn."'), 'Num$16:48': (None, 'kú', 'Num$16:48 "Ó dúró láàrin àwọn alààyè àti òkú, àjàkálẹ̀-àrùn náà sì dúró."'), 'Num$18:25': (None, None, 'Num$18:25 "OLÚWA sọ fún Mósè pé,"'), 'Num$19:1': ('fun', None, 'Num$19:1 OLÚWA sọ fún Mósè àti Árónì pé:'), 'Num$19:17': ('alaimọ', None, 'Num$19:17 "“Fún aláìmọ́ ènìyàn, mú eérú díẹ̀ lára eérú ọrẹ ìwẹ̀nùmọ́ sínú ìgò, kí o sì da omi tó ń sàn lée lórí."'), 'Num$20:14': (None, None, 'Num$20:14 "Mósè sì ránṣẹ́ láti Kádésì sí ọba Édómù, wí pé: “Èyí ni nǹkan tí arákùnrin rẹ Ísírẹ́lì sọ: Ìwọ ti mọ̀ nípa gbogbo ìnira, tí ó wá sí orí wa."'), 'Num$22:5': (None, None, 'Num$22:5 "rán oníṣẹ́ pé Bálámù ọmọ Béórì, tí ó wà ní Pétórì, ní ẹ̀bá odò ti ilẹ̀ àwọn ènìyàn rẹ. Bálákì sọ pé: “Àwọn ènìyàn kan jáde wá láti Éjíbítì; wọ́n bo gbogbo ilẹ̀ ayé wọ́n sì pa ibùdó súnmọ́ ọ̀dọ̀ mi."'), 'Num$22:12': (None, 'egún', 'Num$22:12 "Ṣùgbọ́n Ọlọ́run sọ fún Bálámù pé, “Má ṣe bá wọn lọ. Ìwọ kò gbọdọ̀ gé ègún lé àwọn ènìyàn náà lórí, nítorí a ti bùkún wọn.”"'), 'Num$22:14': ('lọ', 'wá', 'Num$22:14 "Nígbà náà àwọn ìjòyè Móábù sì padà tọ Bálákì lọ wọ́n sì wí pé, “Bálámù kọ̀ láti tẹ̀lé wa.”"'), 'Num$22:18': (None, None, 'Num$22:18 "Ṣùgbọ́n Bálámù dá wọn lóhùn pé, “Kó dà tí Bálákì bá fún mi ní ààfin rẹ̀ tí ó kún fún fàdákà àti wúrà, n kò ṣe ohunkóhun tí ó tóbi tàbí kéré tí o kọjá òfin OLÚWA Ọlọ́run mi."'), 'Num$22:20': ('lọ', 'wá', 'Num$22:20 "Ní alẹ́ ọjọ́ náà Ọlọ́run sì tọ Bálámù wá ó sì sọ wí pé, “Nígbà tí ọkùnrin yìí ti wá pè ọ́, lọ pẹ̀lú wọn, ṣùgbọ́n ẹnu nǹkan tí mo sọ fún ọ ni kí o ṣe.”"'), 'Num$22:26': ('ọtun', 'osi', 'Num$22:26 "Nígbà náà ángẹ́lì OLÚWA súnmọ́ wájú ó sì dúró níbi ọ̀nà tóóró níbi tí kò ti sí ọ̀nà láti yípadà, bóyá ní ọ̀tún tàbí òsì."'), 'Num$22:36': ('lọ', 'wá', 'Num$22:36 "Nígbà tí Bálákì gbọ́ pé Bálámù ń bọ̀ wá, ó jáde lọ láti lọ pàdé rẹ̀ ní ìlú Móábù tí ó wà ní agbégbé Ánónì, ní ẹ̀gbẹ́ ilẹ̀ rẹ̀."'), 'Num$23:3': (None, 'lọ', 'Num$23:3 "Nígbà náà Bálámù sọ fún Bálákì pé, “Dúró ní ẹ̀gbẹ́ ẹbọ rẹ kí èmi sì lọ sí ẹ̀gbẹ́ kan. Bóyá OLÚWA yóò farahàn mí. Ohunkóhun tí ó bá fihàn, mí màá sọ fún ọ.” Nígbà náà ó sì lọ sí ibi gíga."'), 'Num$23:25': ('bukun', None, 'Num$23:25 "Nígbà náà ni Bálákì sọ fún Bálámù pé, “O ko fi wọ́n bú tàbí bùkún wọn rárá!”"'), 'Num$24:10': (None, None, 'Num$24:10 "Nígbà náà ni ìbínú Bálákì sì dé sí Bálámù. Ó sì fi ọwọ́ lu ọwọ́ ó wí pé, “Mo pè ọ́ láti bú àwọn ọ̀ta mi ṣùgbọ́n o tún bùkún fún wọn nígbà mẹ́ta yìí."'), 'Num$24:12': (None, None, 'Num$24:12 "Bálámù dá Bálákì lóhùn, “Ǹjẹ́ èmi kò sọ fún àwọn ìránṣẹ́ tí o rán sí mi wí pé,"'), 'Num$24:14': ('lọ', 'wá', 'Num$24:14 "Nísinsin yìí mò ń padà lọ sí ọ̀dọ̀ àwọn ènìyàn mi, ṣùgbọ́n jẹ́ kí èmi kí ó kìlọ̀ fún ọ nítorí nǹkan tí àwọn ènìyàn wọ̀nyí yóò ṣe sí àwọn ènìyàn rẹ ní ọjọ́ iwájú.”"'), 'Num$24:15': (None, None, 'Num$24:15 "Nígbà náà ni ó bẹ̀rẹ̀ òwe: “Òwe Bálámù ọmọ Béórì, òwe ẹnì tí ojú rẹ̀ ríran kedere,"'), 'Num$25:7': (None, None, 'Num$25:7 "Nígbà tí Fínéhásì, ọmọ Élíásárì, ọmọ Árónì, àlùfáà, rí èyí, ó fi ìpéjọ sílẹ̀, pẹ̀lú ọ̀kọ̀ ní ọwọ́ rẹ̀."'), 'Num$26:1': (None, None, 'Num$26:1 " Lẹ́yìn àjàkálẹ̀-àrùn OLÚWA sọ fún Mósè àti Élíásárì ọmọ Árónì, àlùfáà pé"'), 'Num$26:33': ('ọmọbinrin', 'ọmọ', 'Num$26:33 "(Selofehádì ọmọ Héférì kò sì ni ọmọkùnrin, bí kò ṣe ọmọbìnrin; orúkọ àwọn ọmọbìnrin Selofehádì ni Málà, Nóà, àti Hógílà, Mílíkà àti Táṣà)."'), 'Num$26:54': (None, None, 'Num$26:54 "Fún àwọn ọ̀pọ̀ ni kí ìwọ ó fún ní ogún ìlú púpọ̀ àti fún àwọn tí ó kéré ni kí ìwọ ó fi kékeré fún, ẹnìkọ̀ọ̀kan gbọdọ̀ gba ogún ìbí tirẹ̀ gẹ́gẹ́ bí ènìyàn tí a kọ."'), 'Num$26:56': (None, None, 'Num$26:56 Olúkúlùkù ogún ìní ni a gbọdọ̀ fi ìbò pín gẹ́gẹ́ bí wọ́n ti pọ̀ tó láàrin ńlá àti kékeré.”'), 'Num$27:1': ('ọmọbinrin', 'ọmọ', 'Num$27:1 " Ọmọbìnrin Ṣélóféhátì ọmọ Héférì, ọmọ Gílíádì, ọmọ Mákírì ọmọ Mánásè tó jẹ́ ìdílé Mánásè, ọmọ Jósẹ́fù wá. Orúkọ àwọn ọmọbìnrin náà ni Málà, Nóà, Hógílà, Mílíkà àti Tásà."'), 'Num$27:8': ('ọmọ', 'ọmọbinrin', 'Num$27:8 "“Sọ fún àwọn ọmọ Ísírẹ́lì, ‘Tí ọkùnrin kan bá kú tí kò sì fi ọmọkùnrin sáyé, ẹ fún àwọn ọmọbìnrin rẹ̀ ní ogún ìní rẹ̀."'), 'Num$27:21': ('wá', 'lọ', 'Num$27:21 "Kí ó dúró níwájú Élíásárì àlùfáà, tí yóò gba ìpinnu fún láti béèrè Úrímù níwájú OLÚWA. Gẹ́gẹ́ bí òfin yìí ni òun pẹ̀lú gbogbo ìjọ ènìyàn Ísírẹ́lì yóò jáde lọ, pẹ̀lú òfin rẹ̀ sì ni wọn ó wọlé.”"'), 'Num$29:17': (None, 'keji', 'Num$29:17 "“ ‘Àti ní ijọ́ kejì ni kí ẹ̀yin kí ó fi ẹgbọ̀rọ̀ akọ màlúù méjìlá, àgbò méjì àti ọ̀dọ́ àgùntàn mẹ́rìnlá ọlọ́dún kan aláìlábùkù rúbọ."'), 'Num$31:17': (None, 'obinrin', 'Num$31:17 "Nísinsìnyìí, pa gbogbo ọ̀dọ́mọkùnrin àti àwọn obìnrin tí ó ti súnmọ́ ọkùnrin,"'), 'Num$31:18': ('obinrin', None, 'Num$31:18 Ṣùgbọ́n kí o dá obìnrin tí kò bá tí ì sún mọ́ ọkùnrin sí fún ara yín.'), 'Num$31:25': (None, 'fun', 'Num$31:25 "OLÚWA sọ fún Mósè pé,"'), 'Num$31:30': (None, 'fun', 'Num$31:30 "Lára ààbọ̀ ti Ísírẹ́lì, yan ọ̀kan kúrò nínú àádọ́ta, (50) yálà ènìyàn, ẹran ọ̀sìn, kẹ́tẹ́kẹ́tẹ́, àgùntàn, ewúrẹ́ tàbí ẹ̀yà ẹranko mìíràn. Kó wọn fún àwọn Léfì, tí ó dúró fún olùtọ́jú àgọ́ OLÚWA.”"'), 'Num$31:32': (None, None, 'Num$31:32 "Èrè tí ó kù lára ìkógun tí àwọn ọmọ ogun kó jẹ́, ọ̀kẹ́ mẹ́rìnlélọ́gbọ̀n (675,000) ó dín ẹgbẹ̀ẹ́dọ́gbọ̀n àgùntàn."'), 'Num$32:16': ('wá', None, 'Num$32:16 "Nígbà náà wọ́n wá sí òkè ní ọ̀dọ̀ rẹ̀, “Àwa yóò fẹ́ láti kọ́ ilé ẹran níhínyìí fún ohun ọ̀sìn wa, àti ìlú fún àwọn obìnrin àti ọmọ wẹ́wẹ́ wa."'), 'Num$33:38': (None, 'wá', 'Num$33:38 " Nípa àsẹ OLÚWA, Árónì àlùfáà gùn orí òkè Hórì, níbẹ̀ ni ó kú. Ní ọjọ́ kìn-ín-ní oṣù kárun, ọdún ogójì, lẹ́yìn tí àwọn ọmọ Ísírẹ́lì ti ilẹ̀ Éjíbítì jáde wá."'), 'Num$33:54': ('diẹ', None, 'Num$33:54 " Ẹ̀yin pín ilẹ̀ náà pẹ̀lú kèké, gẹ́gẹ́ bí ìdílé yín. Fún ọ̀pọ̀ ni kí ẹ̀yin ó fi ilẹ̀ ìní púpọ̀ fún, àti fún díẹ̀, ni kí ẹ̀yin kí ó fi ilẹ̀ ìní díẹ̀ fún. Ohunkóhun tí ó bá bọ́ sí ọ̀dọ̀ wọn nípa kèké yóò jẹ́ ti wọn. Pín wọn gẹ́gẹ́ bí ẹ̀yà ìran wọn."'), 'Num$34:1': (None, None, 'Num$34:1 "OLÚWA sọ fún Mósè pé,"'), 'Num$35:5': (None, None, 'Num$35:5 "Lẹ́yin náà, wọn ẹgbẹ̀ẹ́dogun (3000) ẹṣẹ̀ bàtà lápá ibi ìhà ìlà oòrùn ẹgbẹ̀ẹ́dógún (3000) ẹṣẹ̀ bàtà ní ìhà gúsù, ẹgbẹ̀ẹ́dógún (3000) ní ìhà ìwọ̀ oòrùn, àríwá. Pẹ̀lú ìlú ní àárin. Wọn yóò ní agbégbé yìí gẹ́gẹ́ bí ilẹ̀ pápá oko tútù fún ìlú náà."'), 'Num$35:8': (None, 'diẹ', 'Num$35:8 "Ìlú tí ẹ fún àwọn ọmọ Léfì láti ara ilẹ̀ tí àwọn ọmọ Ísírẹ́lì jogún, olúkúlùkù kí ó fi nínú ìlú rẹ̀ fún àwọn ọmọ Léfì gẹ́gẹ́ bí ilẹ̀ ìní rẹ̀ tí ó ní. Gba púpọ̀ lọ́wọ́ ẹ̀yà tí ó ní púpọ̀ àti díẹ̀ lọ́wọ́ ẹ̀yà tí ó ní díẹ̀.”"'), 'Num$36:1': (None, 'ọmọ', 'Num$36:1 "Àwọn olórí ìdílé Gílíádì, ọmọkùnrin Mákírì ọmọ Mánásè tí ó wá láti ara ìdílé ìrán Jósẹ́fù wá, wọ́n sì sọ̀rọ̀ níwájú Mósè àti àwọn olórí, àwọn ìdílé Ísírẹ́lì."'), 'Num$36:10': ('ọmọbinrin', 'ọmọ', 'Num$36:10 "Nígbà náà, àwọn ọmọbìnrin Ṣẹ́lófẹ́hádì ṣe gẹ́gẹ́ bí OLÚWA ti pa á láṣẹ fún Mósè."'), 'Deut$1:22': ('wá', None, 'Deut$1:22 " Nígbà náà ni gbogbo yín tọ̀ mí wá wí pé, “Jẹ́ kí a yan àwọn ayọ́lẹ̀wò láti yọ́ ilẹ̀ náà wò fún wa, kí wọn sì fún wa ní ìròyìn àwọn ọ̀nà tí a ó gbà àti àwọn ìlú tí a ó bá pàdé.”"'), 'Deut$1:25': ('mu', 'fun', 'Deut$1:25 "Wọ́n sì mú lára èso ilẹ̀ náà wá fún wa pé, “Ilẹ̀ tí ó dára ni OLÚWA Ọlọ́run wa fún wa.”"'), 'Deut$1:32': (None, None, 'Deut$1:32 "Pẹ̀lú gbogbo èyí tí mo wí, ẹ kò gbẹ́kẹ̀lé OLÚWA Ọlọ́run yín,"'), 'Deut$1:34': (None, None, 'Deut$1:34 "Nígbà tí OLÚWA gbọ́ ohun tí ẹ sọ, inú bí i, ó sì búra wí pé:"'), 'Deut$1:39': (None, None, 'Deut$1:39 Àwọn èwe yín tí ẹ ṣọ pé wọn yóò kó lẹ́rú. Àwọn ọmọ yín tí ko tí ì mọ rere yàtọ̀ sí búburú ni yóò wọ ilẹ̀ náà.'), 'Deut$2:4': (None, None, 'Deut$2:4 " Fún àwọn ènìyàn náà ní àwọn òfin wọ̀nyí: ‘Ẹ ti fẹ́ la ilẹ̀ àwọn arákùnrin yín kọjá, àwọn ọmọ Ísọ̀; àwọn ará Édómù tí ń gbé ní Séírì. Ẹ̀rù yín yóò bà wọ́n, ṣùgbọ́n ẹ ṣọ́ra gidigidi."'), 'Deut$2:26': (None, None, 'Deut$2:26 "Mo rán àwọn ìránṣẹ́ sí Síhónì láti ihà Kédémótì pẹ̀lú ọ̀rọ̀ àlàáfíà wí pé,"'), 'Deut$3:19': (None, 'pọ', 'Deut$3:19 "Àwọn ẹ̀yà a yín, àwọn ọmọ yín àti àwọn ohun ọ̀sìn in yín (Mo mọ̀ pé ẹ ti ní ohun ọ̀sìn púpọ̀) lè dúró ní àwọn ìlú tí mo fi fún un yín,"'), 'Deut$4:15': (None, None, 'Deut$4:15 "Ẹ kíyèsára gidigidi, torí pé, ẹ kò rí ìrísí ohunkóhun ní ọjọ́ tí OLÚWA bá yín sọ̀rọ̀ ní Hórébù láti àárin iná wá. Torí náà ẹ sọ́ra yín gidigidi,"'), 'Deut$5:8': (None, None, 'Deut$5:8 “Ẹ̀yin kò gbọdọ̀ ṣe ère fún ara yín ní ìrí ohunkóhun lọ́run tàbí ní ilẹ̀ ní ìṣàlẹ̀ níhìn-ín tàbí nínú omi ní ìṣàlẹ̀.'), 'Deut$5:13': (None, None, 'Deut$5:13 " Ọjọ́ mẹ́fà ni kí ẹ fi ṣiṣẹ́, kí ẹ ṣe iṣẹ́ ẹ yín pátapáta"'), 'Deut$5:16': ('baba', 'iya', 'Deut$5:16 " “Bọ̀wọ̀ fún baba àti ìyáà rẹ, bí OLÚWA Ọlọ́run yín ti pàṣẹ fún un yín, kí ẹ ba à le è pẹ́ láyé, àti kí ó bá à lè dára fún un yín ní ilẹ̀ tí OLÚWA Ọlọ́run yín yóò fún un yín."'), 'Deut$5:32': ('ọtun', 'osi', 'Deut$5:32 Torí èyí ẹ sọ́ra láti máa ṣe ohun tí OLÚWA Ọlọ́run yín tí pa ní àṣẹ fún un yín; ẹ má ṣe yà sọ́tùn-ún tàbí sósì.'), 'Deut$7:1': (None, 'gba', 'Deut$7:1 " Nígbà tí OLÚWA Ọlọ́run yín bá mú u yín dé ilẹ̀ náà, tí ẹ ó wọ̀ lọ láti gbà, tí ó sì lé ọ̀pọ̀lọpọ̀ orílẹ̀ èdè kúrò níwájú u yín: Àwọn ará Hítì, Gígásì, Ámórì, Kénánì, Pérésì, Hífì àti àwọn ará Jébúsì: Àwọn orílẹ̀ èdè méje tí wọ́n lágbára tí wọ́n sì pọ̀ jù yín lọ,"'), 'Deut$7:9': ('ife', None, 'Deut$7:9 "Nítorí náà ẹ mọ̀ dájúdájú pé OLÚWA Ọlọ́run yín, Òun ni Ọlọ́run, Ọlọ́run olótìítọ́ ni, tí ń pa májẹ̀mú ìfẹ́ rẹ̀ mọ́ fún ẹgbẹẹgbẹ̀rún ìran, àwọn tí ó fẹ́ ẹ tí ó sì ń pa àṣẹ rẹ̀ mọ́."'), 'Deut$7:14': ('okunrin', 'obinrin', 'Deut$7:14 "A ó bùkún un yín ju gbogbo ènìyàn lọ, kò sí ẹni tí yóò yàgàn nínú ọkùnrin tàbí obìnrin yín, bẹ́ẹ̀ ni kò sí ọ̀kan nínú àwọn ohun ọ̀sìn in yín tí yóò wà láìlọ́mọ."'), 'Deut$8:11': (None, None, 'Deut$8:11 " Ẹ ṣọ́ra kí ẹ má gbàgbé OLÚWA Ọlọ́run yín, nípa kíkùnà láti pa àṣẹ rẹ̀, àwọn òfin rẹ̀, àti àwọn ìlànà rẹ̀ tí mo ń fún un yín lónìí mọ́."'), 'Deut$9:9': ('oru', None, 'Deut$9:9 "Nígbà tí mo gòkè lọ láti lọ gba sílétì òkúta, sílétì májẹ̀mú ti OLÚWA ti bá a yín dá. Ogójì ọ̀sán àti òru ni mo fi wà lórí òkè náà, èmi kò fẹnu kan oúnjẹ bẹ́ẹ̀ ni ń kò mumi."'), 'Deut$10:11': ('gba', 'fun', 'Deut$10:11 "OLÚWA sọ fún mi pé, “Lọ darí àwọn ènìyàn wọ̀nyí ní ọ̀nà wọn, kí wọn báà lè wọ ilẹ̀ náà, kí wọn sì gba ilẹ̀ tí mo ti búra fún àwọn baba wọn láti fún wọn.”"'), 'Deut$11:10': (None, None, 'Deut$11:10 "Ilẹ̀ tí ẹ̀ ń wọ̀ lọ láti gbà kò rí bí ilẹ̀ Éjíbítì, níbi tí ẹ ti wá, níbi tó jẹ́ wí pé ẹṣẹ̀ yín ni ẹ fi ń rìn lọ bominrin irúgbìn tí ẹ gbìn bí oko ẹ̀fọ́."'), 'Deut$11:25': (None, None, 'Deut$11:25 "Kò sí ẹni náà tí yóò lè kò yín lójú ìjà, OLÚWA Ọlọ́run yín yóò fi ẹ̀rù àti ìwárìrì i yín sórí gbogbo ilẹ̀ náà bí ó ti ṣèlérí fún un yín ní gbogbo ibi tí ẹ bá lọ."'), 'Deut$12:15': ('alaimọ', 'mimọ', 'Deut$12:15 "Ṣíbẹ̀ṣíbẹ̀, ẹ lọ pa àwọn ẹran yín ní èyíkéyìí nínú àwọn ìlú u yín, kí ẹ sì jẹ ẹran náà tó bí ẹ ti fẹ́, bí ẹ ti ń ṣe sí àgbọ̀nrín àti èṣúó, gẹ́gẹ́ bí ìbùkún OLÚWA Ọlọ́run yín tí ó fi fún un yín, àti ẹni mímọ́ àti aláìmọ́ ni ó lè jẹ nínú àwọn ẹran náà."'), 'Deut$12:17': (None, None, 'Deut$12:17 "Ẹ kò gbọdọ̀ jẹ ìdámẹ́wàá oúnjẹ yín, wáìnì tuntun, òróró, àkọ́bí àwọn màlúù yín, tàbí ti ewúrẹ́ ẹ yín, ohunkóhun tí ó jẹ́ ẹ̀jẹ́, ọrẹ àtinúwá, tàbí ẹ̀bùn pàtàkì ní ìlú ẹ̀yin tìkárayín."'), 'Deut$12:22': ('alaimọ', 'mimọ', 'Deut$12:22 "Ẹ jẹ wọ́n bí ẹ ó ti jẹ èṣúó tàbí àgbọ̀nrín, ẹni tí a kà sí mímọ́ àti aláìmọ́ ni ó lè jẹ ẹ́."'), 'Deut$13:1': (None, None, 'Deut$13:1 "Bí wòlíì tàbí aláṣọtẹ́lẹ̀ nípa àlá lílá bá farahàn láàrin yín, tí ó sì ń kéde iṣẹ́ àmì tàbí iṣẹ́ ìyanu,"'), 'Deut$13:6': ('ọmọ', None, 'Deut$13:6 "Bí arákùnrin tìrẹ gan-an ọmọ ìyá rẹ tàbí ọmọ rẹ ọkùnrin tàbí obìnrin, aya tí o fẹ́ràn jù, tàbí ọ̀rẹ́ tímọ́tímọ́ bá tàn ọ́ jẹ níkọ̀kọ̀ wí pé jẹ́ kí a lọ sin ọlọ́run mìíràn, (ọlọ́run tí ìwọ tàbí baba rẹ kò mọ̀"'), 'Deut$15:6': (None, 'ya', 'Deut$15:6 "Nítorí pé OLÚWA Ọlọ́run yín yóò bùkún un yín bí ó ti ṣèlérí, ẹ̀yin yóò sì máa yá ọ̀pọ̀lọpọ̀ orílẹ̀ èdè ẹ kì yóò sì tọrọ. Ẹ ó ṣàkóso ọ̀pọ̀lọpọ̀ orílẹ̀ èdè, ṣùgbọ́n kò sí orílẹ̀ èdè tí yóò lè jọba lé e yín lórí."'), 'Deut$15:7': (None, 'sé', 'Deut$15:7 " Bí talákà kan bá wà láàrin àwọn arákùnrin yín ní èyíkèyí àwọn ìlú, ilẹ̀ náà tí OLÚWA yín yóò fún un yín. Ẹ má ṣe se àìláànú bẹ́ẹ̀ ni kí ẹ má ṣe háwọ́ sí arákùnrin yín tí ó jẹ́ tálákà."'), 'Deut$15:12': (None, 'obinrin', 'Deut$15:12 " Bí Hébérù ara yín kan, yálà ọkùnrin tàbí obìnrin bá ta ara rẹ̀ fún ọ, tí ó sì sìn ọ́ fún ọdún mẹ́fà. Ní ọdún kéje, jẹ́ kí ó di òmìnira."'), 'Deut$15:22': ('alaimọ', 'mimọ', 'Deut$15:22 Ẹ jẹ ẹ́ ní ìlú u yín. Àti ẹni tí a kà sí mímọ́ àti aláìmọ́ ni ó lè jẹ ẹ́ bí ìgbà tí a ń jẹ èsúó tàbí àgbọ̀nrín.'), 'Deut$16:3': (None, None, 'Deut$16:3 "Ẹ má ṣe jẹ ẹ́ pẹ̀lú u àkàrà tí a fi ìwúkàrà ṣe, ṣùgbọ́n ọjọ́ méje ni kí ẹ fi jẹ àkàrà tí kò ní ìwúkàrà, àkàrà ìpọ́njú, nítorí pé pẹ̀lú ìkánjú ni ẹ kúrò ní Éjíbítì: kí ẹ báà lè rántí ìgbà tí ẹ kúrò ní Éjíbítì ní gbogbo ọjọ́ ayé e yín."'), 'Deut$16:4': ('ọjọ', None, 'Deut$16:4 Kí a má ṣe rí ìwúkàrà ní ọ̀dọ̀ ọ yín nínú ilẹ̀ ẹ yín fún ọjọ́ méje. Ẹ má ṣe jẹ́ kí nǹkan kan sẹ́kù nínú ẹran tí ẹ ó fi rúbọ ní ìrọ̀lẹ́ ọjọ́ kìn-ín-ní di àárọ̀ ọjọ́ kéjì.'), 'Deut$16:10': (None, None, 'Deut$16:10 "Nígbà náà ni kí ẹ se àjọ̀dún ọ̀ṣẹ̀ sí OLÚWA Ọlọ́run yín, nípa pípèṣè ọrẹ àtinúwá, gẹ́gẹ́ bí ìbùkún tí OLÚWA Ọlọ́run yín fún un yín."'), 'Deut$16:13': (None, None, 'Deut$16:13 Ẹ ṣe ayẹyẹ àgọ́ ìpàdé fún ọjọ́ méje lẹ́yìn ìgbà tí ẹ bá ti kórè oko yín tí ẹ ti pakà tí ẹ sì ti fún ọtí tán.'), 'Deut$17:2': (None, 'obinrin', 'Deut$17:2 " Bí ọkùnrin tàbí obìnrin kan tó ń gbé láàrin yín ní èyíkéyìí ìlú wọ̀n-ọn-nì, tí OLÚWA fún un yín bá ń ṣe ibi ní ojú OLÚWA Ọlọ́run yín, ní ríré májẹ̀mú rẹ̀ kọjá,"'), 'Deut$17:8': (None, None, 'Deut$17:8 "Bí ẹjọ́ bá wá sí ilé ẹjọ́ ọ yín tí ó nira jù láti dá: yálà ìtàjẹ̀sílẹ̀ ni, ọ̀ràn dídá tàbí ìkanni-lábùkù: Ẹ mú wọn lọ sí ibi tí OLÚWA Ọlọ́run yín yóò yàn."'), 'Deut$17:18': (None, None, 'Deut$17:18 "Nígbà tí ó bá dé orí oyè ilé ọba rẹ̀ kí ó ṣe àdàkọ àwọn òfin wọ̀nyí sí inú ìwé fún ara rẹ̀, láti inú un ti àlùfáà tí wọ́n jẹ́ ọmọ Léfì."'), 'Deut$18:10': ('ọmọ', None, 'Deut$18:10 " Má se jẹ́ kí a rí ẹnikẹ́ni láàrin yín tí ó fi ọmọ rẹ̀ ọkùnrin tàbí ọmọ rẹ̀ obìnrin la iná já (fi ọmọ rẹ rúbọ) kí ẹnikẹ́ni má ṣe lọ ṣe àyẹ̀wò, tàbí ṣẹ́ oṣó, tàbí túmọ̀ àwọn àmì nǹkan tí ń bọ̀, tàbí àjẹ́,"'), 'Deut$20:12': ('àlàáfíà', 'ogun', 'Deut$20:12 "Tí wọ́n bá kọ̀ láti bá ọ ṣe àlàáfíà ṣùgbọ́n tí wọ́n bá gbógun tì ọ́, nígbà náà ni kí ìwọ gbà á."'), 'Deut$21:10': (None, None, 'Deut$21:10 "Nígbà tí o bá lọ sí ogun sí àwọn ọ̀ta rẹ tí OLÚWA Ọlọ́run rẹ sì fi wọ́n lé ọ lọ́wọ́ tí o sì mú àwọn ìgbékùn,"'), 'Deut$21:18': ('baba', 'iya', 'Deut$21:18 " Bí ọkùnrin kan bá ní aláìgbọ́ràn tàbí ọlọ̀tẹ̀ ọmọ tí kò gbọ́rọ̀ sí baba àti ìyá rẹ̀ tí kò sì ní í gbà tí wọ́n bá ń bá a wí,"'), 'Deut$21:22': (None, None, 'Deut$21:22 " Bí ọkùnrin kan tí ó jẹ̀bi ẹ̀sùn bá ní láti kú tí ó sì kú, tí a sì gbé òkú rẹ̀ kọ́ sára igi,"'), 'Deut$22:3': ('sọnu', 'ri', 'Deut$22:3 Ṣe bákan náà tí o bá rí kẹ́tẹ́kẹ́tẹ́ arákùnrin rẹ tàbí aṣọ ìlekè tàbí ohunkóhun rẹ̀ tí ó sọnù. Má ṣe ṣe àìkíyèsí i rẹ̀.'), 'Deut$22:4': (None, None, 'Deut$22:4 "Bí o bá rí kẹ́tẹ́kẹ́tẹ́ tàbí màlúù arákùnrin rẹ tí o dùbúlẹ̀ sójú ọ̀nà, má ṣe ṣàìfiyèsí i rẹ̀, ràn án lọ́wọ́ láti dìde dúró lórí ẹṣẹ̀ rẹ̀."'), 'Deut$22:5': (None, 'obinrin', 'Deut$22:5 "Obìnrin kò gbọdọ̀ wọ aṣọ ọkùnrin, tàbí kí ọkùnrin wọ aṣọ obìnrin, nítorí OLÚWA Ọlọ́run rẹ kórìíra ẹnikẹ́ni tí ó ṣe èyí."'), 'Deut$22:13': (None, None, 'Deut$22:13 "Bí ọkùnrin kan bá mú ìyàwó àti, lẹ́yìn ìgbà tí ó ti dùbúlẹ̀ pẹ̀lú u rẹ̀, kórìíra rẹ̀,"'), 'Deut$22:18': (None, 'gba', 'Deut$22:18 "àwọn àgbààgbà yóò sì mú ọkùnrin náà, wọn yóò sì jẹ́ ẹ́ níyà."'), 'Deut$22:22': (None, None, 'Deut$22:22 "Bí o bá rí ọkùnrin kan tí ó bá ìyàwó ọkùnrin mìíràn lòpọ̀, àwọn méjèèjì ní láti kú. Ẹ ní láti pọ ohun búburú kúrò láàrin Ísírẹ́lì."'), 'Deut$22:23': (None, None, 'Deut$22:23 "Bí ó bá ṣẹlẹ̀ wí pé ọkùnrin kan pàde wúndíá tí a ti fi fún ni láti fẹ́ láàrin ìlú tí ó sì lòpọ̀ pẹ̀lú u rẹ̀,"'), 'Deut$23:10': (None, None, 'Deut$23:10 "Bí ọ̀kan nínú àwọn ọkùnrin rẹ bá jẹ́ aláìmọ́ nítorí ìtújáde tí ó ní, o ní láti jáde kúrò nínú àgọ́, kí o má ṣe wọ inú àgọ́."'), 'Deut$24:1': ('ya', 'fun', 'Deut$24:1 " Bí ọkùnrin kan bá fẹ́ obìnrin kan ní ìyàwó tí ó wá padà bà á nínú jẹ́ nítorí ó rí ohun àìtọ́ kan nípa rẹ̀, tí ó sì kọ ìwé ìkọ̀sílẹ̀ si i, fi fún un kí ó sì lọ jáde kúrò ní ilé è rẹ,"'), 'Deut$24:3': (None, None, 'Deut$24:3 "àti tí ọkọ rẹ̀ kejì kò bá fẹ́ràn rẹ̀ tí ó sì kọ ìwé ẹ̀rí ìkọ̀sílẹ̀ sí i, kí ó fi fún un kí ó sì lé e kúrò nílé e rẹ̀, tàbí tí ọkọ rẹ̀ kejì bá kú,"'), 'Deut$25:5': (None, 'ọkọ', 'Deut$25:5 " Bí àwọn arákùnrin rẹ bá jọ ń gbé pọ̀, tí ọ̀kan wọn sì kú láìní ọmọkùnrin, opó rẹ̀ kò gbọdọ̀ fẹ́ ọkọ mìíràn tayọ ẹbí ọkọ rẹ̀. Arákùnrin ọkọ rẹ̀ ni yóò mú u ṣe aya, òun ni yóò sì ṣe ojúṣe ọkọ fún un."'), 'Deut$25:7': (None, 'ọkọ', 'Deut$25:7 "Ṣùgbọ́n bí ọkùnrin náà kò bá fẹ́ fi aya arákùnrin rẹ̀ ṣe aya rẹ̀, obìnrin náà yóò lọ sí ọ̀dọ̀ àwọn àgbààgbà ní ẹnu bodè ìlú láti sọ pé, “Arákùnrin ọkọ ọ̀ mi kọ̀ láti gbé orúkọ arákùnrin rẹ̀ ró ní Ísírẹ́lì. Kò ní ṣe ojúṣe rẹ̀ bí arákùnrin ọkọ mi sí mi.”"'), 'Deut$25:11': ('ọkọ', 'iyawo', 'Deut$25:11 "Bí ọkùnrin méjì bá ń jà, tí ìyàwó ọ̀kan nínú wọn bá sì wá láti gbéjà ọkọ rẹ̀ kúró lọ́wọ́ ọkùnrin tí ń lù ú, tí ó sì nawọ́ di nǹkan ọkùnrin abẹ́ ẹ rẹ̀ mú."'), 'Deut$26:1': (None, 'lọ', 'Deut$26:1 " Nígbà tí o bá wọ ilẹ̀ tí OLÚWA Ọlọ́run rẹ ń fi fún ọ gẹ́gẹ́ bí ohun ìní tí ìwọ sì ti jogún, tí o sì ti ń gbé níbẹ̀,"'), 'Deut$26:3': ('lọ', 'wá', 'Deut$26:3 "Kí o sì sọ fun àlùfáà tí ó wà ní ibi iṣẹ́ ní àsìkò náà, pé “Mo sọ ọ́ di mímọ̀ fún OLÚWA Ọlọ́run rẹ pé mo ti wá sí ilẹ̀ tí OLÚWA búra fún àwọn baba wa.”"'), 'Deut$27:11': (None, None, 'Deut$27:11 Ní ọjọ́ kan náà Móṣè pàṣẹ fún àwọn ènìyàn pé.'), 'Deut$27:16': ('baba', 'iya', 'Deut$27:16 " “Ègún ni fún ọkùnrin náà tí ó dójú ti baba tàbí ìyá rẹ̀” Nígbà náà ni gbogbo ènìyàn wọ̀nyí yóò wí pé, “Àmín!”"'), 'Deut$27:22': ('baba', 'iya', 'Deut$27:22 "“Ègún ni fún ọkùnrin náà tí ó dùbúlẹ̀ pẹ̀lú arábìnrin rẹ̀, ọmọbìnrin baba rẹ̀ tàbí ọmọbìnrin ìyá a rẹ̀.” Nígbà náà ni gbogbo ènìyàn wọ̀nyí yóò wí pé, “Àmín!”"'), 'Deut$28:12': (None, 'ya', 'Deut$28:12 "OLÚWA yóò ṣí ọ̀run sílẹ̀, ìṣúra rere rẹ̀ fún ọ, láti rọ òjò sórí ilẹ̀ rẹ ní àsìkò rẹ àti láti bùkún gbogbo iṣẹ́ ọwọ́ ọ̀ rẹ. Ìwọ yóò yá ọ̀pọ̀lọpọ̀ orílẹ̀ èdè ṣùgbọ́n ìwọ kò ní í yá lọ́wọ́ ẹnìkankan."'), 'Deut$28:13': ('iru', 'ori', 'Deut$28:13 "OLÚWA yóò fi ọ́ ṣe orí, ìwọ kì yóò sì jẹ́ ìrù. Bí o bá fi etí sílẹ̀ sí àṣẹ OLÚWA Ọlọ́run rẹ tí mo fi fún ọ kí o sì máa rọra tẹ̀lé wọn, ìwọ yóò máa wà lókè, bẹ́ẹ̀ ni ìwọ kì yóò wà ní ìsàlẹ̀ láé."'), 'Deut$28:35': ('ori', 'ẹsẹ', 'Deut$28:35 OLÚWA yóò lu eékún àti ẹṣẹ̀ rẹ pẹ̀lú oówo dídùn tí kò le san tán láti àtẹ́lẹṣẹ̀ méjèèjì rẹ lọ sí òkè orí rẹ.'), 'Deut$28:38': (None, 'pọ', 'Deut$28:38 "Ìwọ yóò gbin irúgbìn púpọ̀ ṣùgbọ́n ìwọ yóò kórè kékeré, nítorí eṣú yóò jẹ̀ ẹ́ run."'), 'Deut$28:43': (None, 'giga', 'Deut$28:43 "Àlejò tí ń gbé láàrin rẹ yóò gbé sókè gíga jù ọ́ lọ, ṣùgbọ́n ìwọ yóò máa di ìrẹ̀sílẹ̀."'), 'Deut$28:44': ('ori', 'iru', 'Deut$28:44 "Yóò yà ọ́, ṣùgbọ́n o kì yóò yà á, òun ni yóò jẹ́ orí, ìwọ yóò jẹ́ ìrù."'), 'Deut$28:49': (None, None, 'Deut$28:49 " OLÚWA yóò mú àwọn orílẹ̀ èdè wá sí ọ láti ọ̀nà jínjìn, bí òpin ayé, bí idì ti ń bẹ́ láti orí òkè sílẹ̀, orílẹ̀ èdè tí ìwọ kò gbọ́ èdè wọn."'), 'Deut$28:56': ('ọmọ', None, 'Deut$28:56 "Obìnrin tí àwọ̀ rẹ̀ tutù nínú u yín, tí ó si ṣe ẹlẹgẹ́, tí kò jẹ́ dáṣà láti fi àtẹ́lẹṣẹ̀ ẹ rẹ̀ kan ilẹ̀ nítorí ìkẹ́ra àti ìwà ẹlẹgẹ́, ojú u rẹ̀ yóò korò sí ọkọ oókan àyà a rẹ̀, àti sí ọmọ rẹ̀ ọkùnrin, àti ọmọ rẹ̀ obìnrin"'), 'Deut$28:65': (None, None, 'Deut$28:65 "Ìwọ kì yóò sinmi láàrin àwọn orílẹ̀ èdè náà, kò sí ibi ìsinmi fún àtẹ́lẹ́ṣẹ̀ rẹ. Níbẹ̀ ni OLÚWA yóò ti fún ọ ní ọkàn ìnàngà, àárẹ̀ ojú, àti àìnírètí àyà."'), 'Deut$28:68': ('ta', 'ra', 'Deut$28:68 "OLÚWA yóò rán ọ padà nínú ọkọ̀ sí Éjíbítì sí ìrìnàjò tí mo ní ìwọ kì yóò lọ mọ́. Níbẹ̀ ni ìwọ yóò tún fi ara rẹ fún àwọn ọ̀ta à rẹ gẹ́gẹ́ bí ẹrúkùnrin àti ẹrúbìnrin, ṣùgbọ́n kò sí ẹni tí yóò rà ọ́."'), 'Deut$29:14': (None, None, 'Deut$29:14 "Èmi ń ṣe májẹ̀mú yìí, pẹ̀lú ìbúra rẹ̀, kì í ṣe fún ìwọ nìkan"'), 'Deut$30:15': (None, None, 'Deut$30:15 "Wòó mo gbé kalẹ̀ síwájú u yín lónìí, ìyè àti àlàáfíà, ikú àti ìparun."'), 'Deut$31:2': (None, None, 'Deut$31:2 "“Mo jẹ́ ọmọ ọgọ́fà ọdún báyìí àti pé èmi kò ni lè darí i yín mọ́. OLÚWA ti sọ fún mi pé, ‘O kò ní kọjá Jọ́dánì.’"'), 'Deut$31:16': ('ṣe', None, 'Deut$31:16 "OLÚWA sì sọ fún Móṣè pé: “Ìwọ ń lọ sinmi pẹ̀lú àwọn baba à rẹ, àwọn wọ̀nyí yóò sì ṣàgbèrè ara wọn sí ọlọ́run àjèjì ilẹ̀ tí wọn ń wọ̀ lọ láìpẹ́. Wọn yóò kọ̀ mí sílẹ̀ wọn yóò sì da májẹ̀mú tí mo bá wọn dá."'), 'Deut$31:21': ('lọ', 'wá', 'Deut$31:21 "Àti nígbà tí ọ̀pọ̀lọpọ̀ ìpọ́njú àti ìṣòro bá wá sórí i wọn, orin yìí yóò jẹ́ ẹ̀rí sí wọn, nítorí kò ní di ìgbàgbé fún àwọn ọmọ wọn. Mo mọ̀ ohun tí wọ́n ní inú dídùn sí láti ṣe, pàápàá kí èmi tó mú wọn wá sí ilẹ̀ tí mo ṣe ìlérí fún wọn lórí ìbúra.”"'), 'Deut$33:8': (None, None, 'Deut$33:8 "Ní ti Léfì ó wí pé: “Jẹ́ kí Túmímù àti Úrímù rẹ kí ó wà pẹ̀lú ẹni mímọ́ rẹ. Ẹni tí ó dánwò ní Másà, ìwọ bá jà ní omi Méríbà."'), 'Josh$1:7': ('ọtun', 'osi', 'Josh$1:7 "Jẹ́ alágbára, kí o sì mú àyà le gidigidi. Kí o sì sọ́ra láti pa gbogbo òfin tí Móṣè ìránṣẹ́ mi fún ọ mọ́, Má ṣe yà kúrò nínú u rẹ̀ sí ọ̀tún tàbí sí òsì, kí ìwọ kí ó lè ṣe rere níbikíbi tí ìwọ bá ń lọ."'), 'Josh$1:8': (None, None, 'Josh$1:8 "Má ṣe jẹ́ kí Ìwé Ofin yìí kúrò ní ẹnu ù rẹ, máa ṣe àṣàrò nínú u rẹ̀ ní ọ̀ṣán àti ní òru, kí ìwọ kí ó lè ṣọ́ra láti ṣe gbogbo nǹkan tí a kọ sí inú u rẹ̀. Nígbà náà ni yóò dára fún ọ, ìwọ yóò sì ní àṣeyọrí."'), 'Josh$2:4': ('awọn ọkunrin', 'obinrin', 'Josh$2:4 "Ṣùgbọ́n obìnrin náà ti mú àwọn ọkùnrin méjì náà, o sì fi wọ́n pamọ́. Ó sì wí pé, “lóótọ́ ní àwọn ọkùnrin náà wá sí ọ̀dọ̀ mi, ṣùgbọ́n èmi kò mọ ibi tí wọ́n ti wá."'), 'Josh$2:8': (None, None, 'Josh$2:8 "Kí àwọn ayọ́lẹ̀wò náà tó sùn ní alẹ́, ó gòkè tọ̀ wọ́n lọ lókè àjà."'), 'Josh$2:12': (None, None, 'Josh$2:12 "Ǹjẹ́ nísinsìnyìí, ẹ jọ̀wọ́, ẹ búra fún mi ní ti OLÚWA pé ẹ̀yin yóò ṣe àánú fún ìdílé mi, nítorí pé mo ti ṣe yín ní oore. Ẹ fún mi ní àmì tó dájú:"'), 'Josh$2:18': ('baba', 'iya', 'Josh$2:18 "Nígbà tí àwa bá wọ ilé è rẹ, ìwọ yóò ṣo okùn aláwọ̀ aró yìí sí ojú fèrèsé èyí tí ìwọ fi sọ̀ wá kalẹ̀, kí ìwọ kí ó mú baba rẹ, ìyá rẹ, àwọn arákùnrin in rẹ àti gbogbo ìdílé è rẹ kí wọ́n wá sí inú ilé è rẹ."'), 'Josh$3:2': (None, 'lọ', 'Josh$3:2 Lẹ́yìn ọjọ́ kẹ́ta àwọn olórí la àárin ibùdó já.'), 'Josh$3:4': (None, 'wá', 'Josh$3:4 "Ẹyin yóò lè mọ ọ̀nà tí ẹ ó gbà, torí pé ẹ̀yin kò gba ọ̀nà yìí tẹ́lẹ̀ rí. Ṣùgbọ́n àlàfo gbọdọ̀ wà ní àárin yín àti àpótí náà, tó bí ìwọ̀n ẹgbẹ̀rún un mítà (1000).”"'), 'Josh$3:14': (None, 'kọjá', 'Josh$3:14 "Nígbà tí àwọn ènìyàn gbéra láti ibùdó láti kọjá nínú odò Jọ́dánì, àwọn àlùfáà tí ó ru àpótí ẹ̀rí náà ń lọ níwájú wọn."'), 'Josh$4:1': (None, None, 'Josh$4:1 "Nígbà tí gbogbo orílẹ̀-èdè náà kọjá nínú odò Jọ́dánì tan, OLÚWA ṣọ fún Jóṣúà pé,"'), 'Josh$6:5': (None, None, 'Josh$6:5 "Nígbà tí ẹ bá gbọ́ ohùn un fèrè náà, kí àwọn ènìyàn hó-yèè ní igbe ńlá, nígbà náà ni odi ìlú náà yóò sì wó lulẹ̀, àwọn ènìyàn yóò sì lọ sí òkè, olúkúlùkù yóò sì wọ inú rẹ̀ lọ tààrà.”"'), 'Josh$6:20': (None, None, 'Josh$6:20 "Nígbà tí àwọn àlùfáà fọn ìpè, àwọn ènìyàn náà sì hó Ó sì ṣe, bí àwọn ènìyàn ti gbọ́ ìró ìpè, tí àwọn ènìyàn sì hó ìhó-ńlá, odi ìlú náà wó lulẹ̀ bẹẹrẹ; bẹ́ẹ̀ ni olúkúlùkù ya wọ inú ìlú náà lọ tààrà, wọ́n sì kó ìlú náà."'), 'Josh$6:21': (None, 'obinrin', 'Josh$6:21 "Wọ́n ya ìlú náà sọ́tọ̀ fún OLÚWA àti fún ìparun, wọ́n sì fi idà run gbogbo ohun alààyè ní ìlú náà—ọkùnrin àti obìnrin, ọ́mọdé, àti àgbà, màlúù, àgùntàn àti kẹ́tẹ́kẹ́tẹ́."'), 'Josh$6:22': ('awọn ọkunrin', None, 'Josh$6:22 "Jóṣúà sì sọ fún àwọn ọkùnrin méjì tí wọ́n ti wá ṣe ayọlẹ̀ náà wò pé, “Ẹ lọ sí ilé aṣẹ́wó nì, kí ẹ sì mu jáde àti gbogbo ohun tí í ṣe tirẹ̀ gẹ́gẹ́ bí ẹ tí búra fún un.”"'), 'Josh$6:23': ('baba', 'iya', 'Josh$6:23 "Bẹ́ẹ̀ ní àwọn ọ̀dọ́mọkùnrin tí ó lọ ṣe ayọ́lẹ̀wò wọlé lọ, wọ́n sì mú Ráhábù jáde, baba rẹ̀, ìyá rẹ̀, àwọn arákùnrin rẹ̀ àti ohun gbogbo tí ó ní. Wọ́n sì mú gbogbo ìdílé rẹ̀ jáde, wọ́n sì fi wọ́n sí ibìkan ní ìta ibùdó àwọn ará Ísírẹ́lì."'), 'Josh$7:24': (None, 'ọmọ', 'Josh$7:24 "Nígbà náà ni Jóṣúà pẹ̀lú gbogbo Ísírẹ́lì, mú Ákánì ọmọ Sérà, fàdákà, ẹ̀wù àti wúrà tí a dà, àwọn ọmọ rẹ̀ ọkùnrin àti obìnrin, màlúù rẹ̀, kẹ́tẹ́kẹ́tẹ́ àti àgùntàn àgọ́ rẹ̀ àti gbogbo ohun tí ó ni, wọ́n sì kó wọn lọ sí ibi àfonífojì Ákórì."'), 'Josh$8:1': (None, 'gba', 'Josh$8:1 "Nígbà náà ni OLÚWA sọ fún Jóṣúà pé, “Má ṣe bẹ̀rù, kí àyà kí ó má ṣe fò ọ́. Kó gbogbo àwọn ọmọ ogun pẹ̀lúù rẹ, kí ẹ gòkè lọ gbógun ti Áì. Nítorí mo ti fi ọba Áì, àwọn ènìyàn an rẹ̀, ìlú u rẹ̀ àti ilẹ̀ ẹ rẹ̀ lé ọ lọ́wọ́."'), 'Josh$8:4': ('wá', None, 'Josh$8:4 Pẹ̀lú àwọn àṣẹ wọ̀nyí: “Ẹ fi etí sílẹ̀ dáadáa. Ẹ ba sí ẹ̀yìn ìlú náà. Ẹ má ṣe jìnnà sí i púpọ̀. Kí gbogbo yín wà ní ìmúra-sílẹ̀.'), 'Josh$8:20': ('wò', 'pada', 'Josh$8:20 "Àwọn ọkùnrin Áì bojú wo ẹ̀yìn, wọ́n rí èéfín ìlú náà ń gòkè lọ sí ọ̀run, ṣùgbọ́n wọn kò rí, àyè láti sá àsálà lọ sí ibìkan kan, nítorí tí àwọn ará Ísírẹ́lì tí wọ́n tí ń sálọ sí ihà ti yípadà sí àwọn tí ń lépa wọn."'), 'Josh$9:6': ('lọ', 'wá', 'Josh$9:6 "Wọ́n sì tọ Jóṣúà lọ ní ibùdó ní Gílígálì, wọ́n sì sọ fún òun àti àwọn ọkùnrin Ísírẹ́lì pé, “Ìlú òkèrè ní àwọn ti wá, ẹ ṣe àdéhùn àlàáfíà pẹ̀lú wa.”"'), 'Josh$9:12': (None, None, 'Josh$9:12 Gbígbóná ní a mú oúnjẹ ní ojí nílé ní oji tí à ń bọ̀ wá sọ́dọ̀ yín. Ṣùgbọ́n ẹ wò ó bí ó ṣe gbẹ àti bí ó sì ṣe bu nísinsinyí.'), 'Josh$14:11': ('wá', 'lọ', 'Josh$14:11 "Síbẹ̀ èmi ní agbára lónìí, bí ìgbà tí Mósè rán mi jáde. Èmi sì ní agbára láti jáde fún ogun nísinsìnyí gẹ́gẹ́ bí mo ti wà ní ígbà náà."'), 'Josh$15:17': ('ọmọbinrin', 'ọmọ', 'Josh$15:17 "Ótíniẹ́lì ọmọ Kénásì, arákùnrin Kélẹ́bù, sì gbà á, báyìí ni Kélẹ́bù sì fi ọmọbìnrin rẹ̀ Ákísà fún un ní ìyàwó."'), 'Josh$17:3': ('ọmọbinrin', 'ọmọ', 'Josh$17:3 " Nísinsìn yìí Sẹloféádì ọmọ Héférì, ọmọ Gílíádì, ọmọ Mákírì, ọmọ Mánásè, kò ní ọmọkùnrin, bí kò se àwọn ọmọbìnrin, tí orúkọ wọn jẹ́: Málà, Nóà, Hógílà, Mílíkà àti Tásà."'), 'Josh$18:4': ('wá', 'lọ', 'Josh$18:4 "Ẹ yan àwọn ọkùnrin mẹ́ta nínú ẹ̀yà kọ̀ọ̀kan. Èmi yóò rán wọn jáde láti lọ bojúwo ilẹ̀ náà káàkiri, wọn yóò sì ṣe àpèjúwe rẹ̀, gẹ́gẹ́ bí ìní ẹni kọ̀ọ̀kan. Nígbà náà ni wọn yóò padà tọ̀ mí wá."'), 'Josh$18:8': ('lọ', 'wá', 'Josh$18:8 "Bí àwọn ọkùnrin náà ti bẹ̀rẹ̀ ìrìnàjò wọn láti fojú díwọ̀n ilẹ̀ náà, Jóṣúà pàṣẹ fún wọn pé, “Ẹ lọ kí ẹ sì fojú díwọ̀n ilẹ̀ náà kákiri, kí ẹ sì kọ àpèjúwe rẹ̀. Lẹ́yìn náà kí ẹ padà tọ̀ mí wá, èmi yóò sì fún yin ní ibí ní Sílòi ní iwájú OLÚWA.”"'), 'Josh$18:15': (None, None, 'Josh$18:15 "Ìhà gúsù bẹ̀rẹ̀ ní ìpẹ̀kun Kiriati-Jéárímù ní ìwọ̀-oòrùn, ààlà náà wà ní ibi ìsun omi Nẹ́fítóà."'), 'Josh$20:4': ('gba', 'fun', 'Josh$20:4 "“Nígbà tí ó bá sálọ sí ọ̀kan nínú àwọn ìlú wọ̀nyí, yóò sì dúró ní ẹnu ọ̀nà àtiwọ ibodè ìlú, kí ó sì ṣe àlàyé ara rẹ̀ ní iwájú àwọn àgbààgbà ìlú náà. Nígbà náà ni wọn yóò gbà á sí ìlú wọn, wọn yóò sì fun ní ibùgbé láàárin wọn."'), 'Josh$21:45': ('kuna', None, 'Josh$21:45 Kò sí ọ̀kan nínú ìlérí rere tí ó se fún ilé Ísírẹ́lì tí ó kùnà. Gbogbo rẹ̀ ni ó ṣe.'), 'Josh$22:15': ('idaji', None, 'Josh$22:15 "Nígbà tí wọ́n lọ sí Gílíádì-sí Rúbẹ́nì, Gádì àti ìdajì ẹ̀yà Mánásè wọ́n sì sọ fún wọn pé,"'), 'Josh$23:6': ('ọtun', 'osi', 'Josh$23:6 "“Ẹ jẹ́ alágbára gidigidi, kí ẹ sì sọ́ra láti ṣe ìgbọ́ran sí ohun tí a kọ sí inú Ìwé Ofin Mósè, láìyí padà sí ọ̀tún tàbí sí òsì."'), 'Josh$23:14': ('kuna', None, 'Josh$23:14 "“Nísinsin yìí èmi ti fẹ́ máa lọ sí ibi tí àwọn àgbà ń rè. Ẹ mọ̀ pẹ̀lú gbogbo ọkàn yín àti àyà yín pé, kò sí ohun kan tí ó kùnà nínú àwọn ìlérí rere gbogbo tí OLÚWA Ọlọ́run yín fi fún un yín. Gbogbo ìlérí tó ti se kò sí ọ̀kan tí ó kùnà."'), 'Josh$23:15': ('ibi', 'dara', 'Josh$23:15 "Ṣùgbọ́n gẹ́gẹ́ bí ìlérí dáradára gbogbo ti OLÚWA Ọlọ́run yín ti wá sí ìmúsẹ, bẹ́ẹ̀ ni OLÚWA yóò mú ibi gbogbo tí ó ti kìlọ̀ wá sí orí yín, títí yóò fi pa yín run kúrò ní ilẹ̀ dáradára tí ó ti fi fún un yín."'), 'Josh$24:9': (None, None, 'Josh$24:9 "Nígbà tí Bálákì ọmọ Sípórì, ọba Móábù, múra láti bá Ísírẹ́lì jà, ó ránṣẹ́ sí Bálámù ọmọ Béóríù láti fi yín bú."'), 'Judg$1:13': ('ọmọ', None, 'Judg$1:13 "Ótíníẹ́lì ọmọ Kénásì àbúrò Kélẹ́bù ṣíwájú, wọ́n sì kọ lu ìlú náà, ó sì fún un ní Ákíṣà gẹ́gẹ́ bí aya."'), 'Judg$2:1': ('ṣe', None, 'Judg$2:1 "Ní ọjọ́ kan ańgẹ́lì OLÚWA dìde kúrò láti Gígálì lọ sí Bókímù ó sì wí pé, “Èmi mú un yín gòkè láti ilẹ̀ Éjíbítì wá, èmi sì ṣíwájú yín wá sí ilẹ̀ tí mo ti ṣèlérí láti fi fún àwọn baba ńlá yín. Èmi sì wí pé, ‘Èmi kì yóò yẹ májẹ̀mú mi pẹ̀lú yín."'), 'Judg$2:20': (None, None, 'Judg$2:20 "Ìbínú OLÚWA yóò sì tún ru sí Ísírẹ́lì a sì wí pé, “Nítorí tí orílẹ̀-èdè yìí ti yẹ májẹ̀mu tí mo fi lélẹ̀ fún àwọn baba ńlá wọn, tí wọn kò sì fetí sí mi."'), 'Judg$3:21': ('osi', 'ọtun', 'Judg$3:21 Éhúdù fi ọwọ́ òsì rẹ̀ yọ idà láti ibi itan ọ̀tún rẹ̀ ó sì fi gún ọba nínú ikùn rẹ̀.'), 'Judg$3:27': ('lọ', None, 'Judg$3:27 "Nígbà tí ó dé ibẹ̀ ó fọn fèrè ní orí òkè Éfúráímù, fèrè ìpè ogun, ó sì kó ogun jọ lábẹ́ ara rẹ̀ bí olórí ogun."'), 'Judg$6:25': (None, None, 'Judg$6:25 "Ní òru ọjọ́ náà OLÚWA wí fún un pé, mú akọ màlúù bàbá rẹ kejì láti inú agbo, akọ màlúù ọlọ́dún méje. Wó pẹpẹ Báálì baba rẹ lulẹ̀, kí o sì fọ́ ọ̀pá Áṣírà tí ó wà ní ẹ̀gbẹ́ rẹ̀."'), 'Judg$6:27': (None, None, 'Judg$6:27 "Gídíónì mú mẹ́wàá nínú àwọn ìránṣẹ́ rẹ̀, ó sì ṣe bí OLÚWA ti pàṣẹ fún un ṣùgbọ́n, nítorí ó bẹ̀rù àwọn ará ilé bàbá rẹ̀ àti àwọn ènìyàn ìlú náà kò ṣe é ní ọ̀sán, òru ni ó ṣe."'), 'Judg$7:19': (None, None, 'Judg$7:19 "Gídíónì àti àwọn ọgọ́rùn-ún ọkùnrin tí ó wà pẹ̀lú rẹ̀ dé òpin ibùdó àwọn ará Mídíánì ní nǹkan bí agogo méjìlá padà. Wọ́n fun fèrè wọn, wọ́n sì tún fọ́ àwọn ìkòkò tí ó wà ní ọwọ́ mọ́lẹ̀."'), 'Judg$7:20': ('osi', 'ọtun', 'Judg$7:20 "Àwọn ẹgbẹ́ mẹ́tẹ̀ẹ̀ta fun fèrè wọn, wọ́n tún fọ́ àwọn ìkòkò wọn mọ́lẹ̀. Wọ́n mú àwọn àtùpà iná wọn ní ọwọ́ òsì wọn àti fèrè tí wọ́n ń fun ní ọwọ́ ọ̀tún wọn. Wọ́n pariwo hè è pé, “Idà kan fún OLÚWA àti fún Gídíónì!”"'), 'Judg$9:1': (None, 'iya', 'Judg$9:1 "Ní ọjọ́ kan Ábímélékì ọmọ Jérúbù-Báálì lọ sí ọ̀dọ̀ àwọn arákùnrin ìyá rẹ̀ ní Ṣékémù ó sì bá wọn sọ̀rọ̀ àti gbogbo àwọn ìbátan ìyá rẹ̀ wí pé,"'), 'Judg$9:32': (None, None, 'Judg$9:32 Wá ní òru kí ìwọ àti àwọn ogun rẹ sá pamọ́ dè wọ́n nínú igbó.'), 'Judg$9:49': ('obinrin', 'eniyan', 'Judg$9:49 Báyìí gbogbo àwọn ọkùnrin tí ó wà lọ́dọ̀ rẹ̀ gé àwọn ẹ̀ka igi wọn tẹ̀lé Ábímélékì. Wọ́n kó wọn ti ilé ìṣọ́ agbára níbi tí àwọn ènìyàn sá pamọ́ sí wọ́n sì fi iná síi pẹ̀lú gbogbo àwọn ènìyàn tí ó wà níbẹ̀. Gbogbo àwọn ènìyàn náà tí ó tó ẹgbẹ̀rún ènìyàn lọ́kùnrin àti lóbìnrin sì kú.'), 'Judg$9:54': (None, None, 'Judg$9:54 "Ní ojú kan náà ni ó pe ẹni tí ó ru àpáta rẹ̀ pé, “Yára yọ idà rẹ kí o sì pa mí, kí wọn má ba à sọ pé, ‘Obìnrin ni ó pa á.’ ” Ọ̀dọ́mọkùnrin náà sì fi ọ̀kọ̀ gún-un ó sì kú."'), 'Judg$11:34': ('ọmọbinrin', 'ọmọ', 'Judg$11:34 "Nígbà tí Jẹ́fítà padà sí ilé rẹ̀ ní Mísípà, wò ó, ọmọ rẹ̀ obìnrin ń jáde bọ̀ wá pàdé rẹ̀ pẹ̀lú taboríìnì àti ijó. Òun ni ọmọ kan ṣoṣo tí ó ní: kò ní ọmọkùnrin àti ọmọbìnrin mìíràn yàtọ̀ sí òun nìkan."'), 'Judg$13:6': ('eniyan', 'obinrin', 'Judg$13:6 "Nígbà náà ni obìnrin náà tọ ọkọ rẹ̀ lọ, o sì sọ fún un wí pé, “Èniyàn Ọlọ́run kan tọ̀ mí wá. Ó jọ ańgẹ́lì Ọlọ́run, ó bà ènìyàn lẹ́rù gidigidi. Èmi kò béèrè ibi tí ó ti wá, òun náà kò sì sọ orúkọ rẹ̀ fún mi."'), 'Judg$13:10': (None, 'obinrin', 'Judg$13:10 "Nítorí náà ni obìnrin náà ṣe yára lọ sọ fún ọkọ rẹ̀ pé, “Ọkùnrin tí ó fara hàn mí ní ọjọ́sí ti tún padà wá.”"'), 'Judg$13:11': (None, 'obinrin', 'Judg$13:11 "Mánóà yára dìde, ó sì tẹ̀lé aya rẹ̀, nígbà tí ó dé ọ̀dọ̀ ọkùnrin náà ó ní, “Ǹjẹ́ ìwọ ni o bá obìnrin yìí sọ̀rọ̀?” Ọkùnrin náà dáhùn pé “Èmi ni.”"'), 'Judg$14:2': ('baba', 'iya', 'Judg$14:2 "Nígbà tí ó darí (padà sílé) dé, ó sọ fún àwọn òbí rẹ̀ (baba àti ìyá rẹ̀) pé, “Mo rí obìnrin Fílístínì kan ní Tímínà: báyìí ẹ fẹ́ ẹ fún mi bí aya mi”"'), 'Judg$14:3': ('baba', 'iya', 'Judg$14:3 "Baba àti ìyá rẹ̀ dáhùn pé, ṣé kò sí obìnrin tí ó dára tí ó sì tẹ́ ọ lọ́rùn ní àárin àwọn ìbátan rẹ, tàbí láàárin gbogbo àwọn ènìyàn wa? Ṣé ó di túlàsì fún ọ láti lọ sí àárin àwọn Fílístínì aláìkọlà kí o tó fẹ́ ìyàwó? Ṣùgbọ́n Sámúsónì wí fún baba rẹ̀ pé, “Fẹ́ ẹ fún mi torí pé inú mi yọ́ síi púpọ̀ púpọ̀.”"'), 'Judg$14:4': (None, None, 'Judg$14:4 "(Àwọn òbí rẹ̀ kò mọ̀ pé ọ̀dọ̀ OLÚWA ni nǹkan yìí ti wá, ẹni tí ń wá ọ̀nà àti bá Fílístínì jà; nítorí àwọn ni ń ṣe àkóso Ísírẹ́lì ní àkókò náà.)"'), 'Judg$14:5': ('baba', 'iya', 'Judg$14:5 "Sámúsónì sọ̀kalẹ̀ lọ sí Tímínà òun àti bàbá àti ìyá rẹ̀. Bí wọ́n ṣe ń súnmọ́ àwọn ọgbà àjàrà tí ó wà ní Tímínà, láìrò tẹ́lẹ̀, ọ̀dọ́ kìnnìún kan jáde síta tí ń ké ramúramù bọ̀ wá sí ọ̀dọ̀ rẹ̀."'), 'Judg$14:6': ('baba', 'iya', 'Judg$14:6 "Ẹ̀mí OLÚWA sì bà lé e pẹ̀lú agbára tó bẹ́ẹ̀ tí ó fi fa kìnnìún náà ya pẹ̀lú ọwọ́ rẹ̀ lásán bí ẹní ya ọmọ ewúrẹ́, ṣùgbọ́n òun kò sọ ohun tí ó ṣe fún baba tàbí ìyá rẹ̀."'), 'Judg$14:9': (None, None, 'Judg$14:9 "ó fi ọwọ́ ha jáde, ó sì ń jẹ ẹ́ bí ó ti ń lọ. Nígbà tí ó dé ọ̀dọ̀ àwọn òbí rẹ̀, ó fún wọn ní díẹ̀, àwọn náà sì jẹ, ṣùgbọ́n kò sọ fún wọn pé ara òkú kìnnìún ni òun ti rí oyin náà."'), 'Judg$14:10': (None, 'obinrin', 'Judg$14:10 Baba rẹ̀ sì sọ̀kalẹ̀ lọ láti kí obìnrin náà. Sámúsónì sì ṣe àṣè gẹ́gẹ́ bí àṣà ọkọ ìyàwó ní àkókò náà.'), 'Judg$14:15': ('ọkọ', 'iyawo', 'Judg$14:15 "Ní ọjọ́ kẹrin, wọ́n wí fún ìyàwó Sámúsónì, pé, “Tan ọkọ rẹ kí ó lè ṣe àlàyé àlọ́ náà fún wa, bí bẹ́ẹ̀ kọ́ àwa yóò sun ìwọ àti ìdílé baba rẹ̀ ní iná. Tàbí ṣe o pè wá sí ibi àsè yìí láti sọ wá di òtòsì tàbí kó wa lẹ́rú ni?”"'), 'Judg$15:1': (None, 'lọ', 'Judg$15:1 "Lẹ́yìn ìgbà díẹ̀, ní àkókò ìkórè jéró Sámúsónì mú ọ̀dọ́ ewúrẹ́ kan láti bẹ ìyàwó rẹ̀ wò. Ó ní, “Èmi yóò wọ yàrá ìyàwó mi lọ.” Ṣùgbọ́n baba rẹ̀ kò gbà á láàyè láti wọlé."'), 'Judg$16:16': (None, None, 'Judg$16:16 Ó sì ṣe nígbà tí ó fi ọ̀rọ̀ rẹ̀ rọ̀ ọ́ ní ojoojúmọ́ pẹ̀lú ọ̀rọ̀ rẹ̀ títí dé bi pé ó ṣú u dé òpin ẹ̀mí rẹ̀.'), 'Judg$16:29': ('ọtun', 'osi', 'Judg$16:29 "Sámúsónì sì na ọwọ́ mú àwọn òpó méjèèjì tí ó wà láàárin gbùngbùn, orí àwọn tí tẹ́ḿpìlì náà dúró lé, ó fi ọwọ́ ọ̀tún mú ọ̀kan àti ọwọ́ òsì mú èkejì, ó fi ara tì wọ́n,"'), 'Judg$18:10': (None, 'lọ', 'Judg$18:10 "Nígbà tí ẹ̀yin bá dé ibẹ̀, ẹ ó ò rí àwọn ènìyàn tí ọkàn wọn balẹ̀ àti ilẹ̀ tí ó tẹ́jú tí Ọlọ́run ti fi fún yín, ilẹ̀ tí kò ṣe aláìní nǹkan kan.”"'), 'Judg$19:5': (None, 'lọ', 'Judg$19:5 "Ní ọjọ́ kẹrin, wọ́n dìde ní òwúrọ̀ kùtùkùtù òun sì múra láti padà lọ, ṣùgbọ́n baba ọmọbìnrin náà wí fún àna rẹ̀ pé, “Fi ohun jíjẹ díẹ̀ gbé inú ró nígbà náà kí ìwọ máa lọ.”"'), 'Judg$19:9': ('gba', None, 'Judg$19:9 "Nígbà tí ọkùnrin náà, pẹ̀lú àlè àti ìránṣẹ́ rẹ̀, dìde láti máa lọ, àna rẹ̀, baba ọmọbìnrin náà ní, “Wò ó ilẹ̀ ti ń ṣú lọ, dúró níbí, ọjọ́ ti lọ. Dúró kí o sì gbádùn ara rẹ. Ìwọ lè jí ní àárọ̀ kùtùkùtù ọ̀la kí ìwọ sì máa lọlé.”"'), 'Judg$19:23': ('lọ', 'wá', 'Judg$19:23 "Ọkùnrin, baálé ilé náà sì jáde tọ̀ wọ́n lọ, ó sì wí fún un pé, “Bẹ́ẹ̀ kọ́ ẹyin arákùnrin mi, èmi bẹ̀ yín, ẹ má ṣe hùwà búburú; nítorí tí ọkùnrin yìí ti wọ ilé mi, ẹ má ṣe hùwà òmùgọ̀ yìí."'), 'Judg$19:25': (None, 'alẹ', 'Judg$19:25 "Ṣùgbọ́n àwọn ọkùnrin náà kò fetí sí tirẹ̀. Torí náà ọkùnrin náà mú àlè rẹ̀ ó sì tari rẹ̀ jáde sí wọn, wọ́n sì bá a fi ipá lòpọ̀, wọ́n sì fi gbogbo òru náà bá a lòpọ̀, nígbà tí ó di àfẹ̀mọ́júmọ́ wọ́n jọ̀wọ́ rẹ̀ lọ́."'), 'Judg$19:26': (None, 'obinrin', 'Judg$19:26 "Nígbà tí ojúmọ́ bẹ̀rẹ̀ sí là obìnrin náà padà lọ sí ilé tí ọ̀gá rẹ̀ wà, ó ṣubú lulẹ̀ lọ́nà, ó sì wà níbẹ̀ títí ó fi di òwúrọ̀."'), 'Judg$20:9': ('lọ', 'wá', 'Judg$20:9 "Ṣùgbọ́n nísinsin yìí, ohun tí àwa yóò se sí Gíbíà ní yìí: Àwa yóò lọ kọlù ú bí ìbò bá ṣe darí wa."'), 'Judg$21:11': (None, 'obinrin', 'Judg$21:11 "Wọ́n ní, “Èyí ni ẹ̀yin yóò ṣe, ẹ pa gbogbo ọkùnrin àti gbogbo obìnrin tí kì í ṣe wúndíá.”"'), 'Judg$21:19': ('ariwa', None, 'Judg$21:19 "Wọ́n sì wí pé, “Kíyèsí i ayẹyẹ ọlọ́dọọdún fún OLÚWA wà ní Ṣílò ní ìhà àríwá Bẹ́tẹ́lì àti ìhà ìlà oòrùn òpópó náà tí ó gba Bẹ́tẹ́lì kọjá sí Ṣékémù, àti sí ìhà gúṣù Lébónà.”"'), 'Judg$21:20': (None, 'lọ', 'Judg$21:20 "Wọ́n sì fi àṣẹ fún àwọn ará Bẹ́ńjámínì pé, “Ẹ lọ kí ẹ sì fara pamọ́ nínú àwọn ọgbà àjàrà"'), 'Ruth$1:21': (None, 'kun', 'Ruth$1:21 "Mo jáde ní kíkún, ṣùgbọ́n OLÚWA mú mi padà ní òfo. Nítorí náà kíń ló dé tí ẹ fi ń pè mí ní Náómì, nígbà tí Olódùmarè ti kọ̀ mí sílẹ̀, tí ó sì mú ìdààmú bá mi?”"'), 'Ruth$2:11': ('baba', 'iya', 'Ruth$2:11 "Bóásì sì fèsì wí pé, “Èmi ti gbọ́ gbogbo bí o ti ń ṣe sí ìyá ọkọ ọ̀ rẹ láti ìgbà tí ọkọ rẹ ti kú àti bí o ti ṣe fi baba àti ìyá rẹ àti ilẹ̀ rẹ sílẹ̀, tí o sì wá láti gbé láàárin àwọn ènìyàn tí ìwọ kò mọ̀ rí tẹ́lẹ̀."'), 'Ruth$2:15': (None, None, 'Ruth$2:15 "Nígbà tí ó sì dìde láti máa ṣa ọkà, Bóásì pàṣẹ fún àwọn òṣìṣẹ́ rẹ̀ wí pé, “Bí ó tilẹ̀ ń ṣà láàárin oko ọkà pàápàá, ẹ má ṣe dí i lọ́wọ́."'), 'Ruth$2:20': (None, 'kú', 'Ruth$2:20 "Náómì sì wí fún-un pé, “Kí OLÚWA, kí ó bùkún fún ọkùnrin náà. Ọlọ́run kò dáwọ́ oore àti àánú ṣíṣe sí àwọn alààyè àti òkú dúró.” Náómì sì sọ ṣíwájú sí i wí pé, “Ìbátan tí ó súnmọ́ wa pẹ́kípẹ́kí ni ọkùnrin náà ń ṣe, ó sì jẹ́ ọ̀kan nínú àwọn tí ó ní ẹ̀tó láti ra ohun-ìní ìdílé padà.”"'), 'Ruth$3:8': (None, 'obinrin', 'Ruth$3:8 "Ó sì ṣe nígbà tí ọkùnrin náà tají ní àárin òru, ẹ̀rú bàá, ó sì yí ara padà, ó sì ṣàkíyèsí obìnrin kan tí ó sùn sí ibi ẹsẹ̀ rẹ̀."'), 'Ruth$3:10': (None, None, 'Ruth$3:10 "Bóásì sì wí fún-un pé, “Kí OLÚWA bùkún fún ọ, ọmọbìnrin mi. Ìfẹ́ tí o fi hàn yí ti pọ̀ ju ti àtẹ̀yìnwá lọ, bí ó ti jẹ́ wí pé ìwọ kò lọ láti wá àwọn ọ̀dọ́mọkùnrin bóyá ọlọ́rọ̀ tàbí talákà."'), 'Ruth$4:15': (None, None, 'Ruth$4:15 "Yóò tún ayé rẹ ṣe, yóò sì dáàbò bò ọ́ ní ọjọ́ ogbó rẹ. Nítorí pé ìyàwó ọmọ rẹ, èyí tí ó sàn fún ọ ju ọmọkùnrin méje lọ, tí ó sì fẹ́ràn rẹ ni ó bí ọmọ yìí fún.”"'), '1Sam$1:11': ('gbagbe', 'ranti', '1Sam$1:11 "Ó sì jẹ́jẹ̀ẹ́ wí pé, “OLÚWA alágbára jùlọ tí ìwọ bá le bojú wo ìránṣẹ́-bìnrin rẹ kí ìwọ sì rántí rẹ̀, tí ìwọ kò sì gbàgbé ìránṣẹ́ rẹ ṣùgbọ́n tí iwọ yóò fún un ní ọmọkùnrin, nígbà náà èmi yóò sì fi fún OLÚWA ní gbogbo ọjọ́ ayé rẹ̀ a kì yóò sì fi abẹ kàn án ní orí.”"'), '1Sam$2:7': ('ọlọrọ', 'talaka', '1Sam$2:7 "OLÚWA sọ di talákà; ó sì sọ di ọlọ́rọ̀; ó rẹ̀ sílẹ̀, ó sì gbé sókè."'), '1Sam$2:16': ('fun', 'gba', '1Sam$2:16 "Bí ọkùnrin náà bá sì wí fún un pé, “Jẹ́ kí wọn ó sun ọ̀rá náà nísinsin yìí, kí o sì mú iyekíye tí ọkàn rẹ̀ bá fẹ́;” nígbà náà ni yóò dá a lóhùn pé, “Bẹ́ẹ̀ kọ́, ṣùgbọ́n kí ìwọ ó fi í fún mi nísinsin yìí, bí kó ṣe bẹ́ẹ̀, èmi ó fi agbára gbà á.”"'), '1Sam$3:12': ('berè', 'opin', '1Sam$3:12 Ní ìgbà náà ni èmi yóò mú ohun gbogbo tí mo ti sọ sí ilé Élì ṣẹ láti ìbẹ̀rẹ̀ dé òpin.'), '1Sam$4:19': ('iyawo', 'ọkọ', '1Sam$4:19 "Aya ọmọ rẹ̀, ìyàwó Fínéhásì, ó lóyún ó súnmọ́ àkókò àti bí. Nígbà tí ó gbọ́ ìròyìn náà wí pé wọ́n ti gba àpótí ẹ̀rí OLÚWA lọ àti wí pé baba ọkọ rẹ̀ àti ọkọ rẹ̀ ti kú, ó rọbí ó sì bímọ, ó sì borí ìrora ìrọbí náà."'), '1Sam$6:7': ('wá', 'lọ', '1Sam$6:7 "“Nísinsìnyìí ẹ pèse kẹ̀kẹ́ ẹrù tuntun sílẹ̀, pẹ̀lú màlúù méjì tí ó ti fí ọmú fun ọmọ, èyí tí a kò tí ì fi ru ẹrù rí, kí ẹ sì so ó mọ́ kẹ̀kẹ́ náà, kí ẹ sì mú ọmọ wọn kúrò lọ́dọ̀ wọn wá sí ilé láti so."'), '1Sam$6:12': ('ọtun', 'osi', '1Sam$6:12 "Nígbà náà; àwọn màlúù lọ tààrà sí ìhà Béti-Ṣémésì, wọ́n ń lọ tààrà, wọ́n sì ń dún bí wọ́n ti ń lọ. Wọn kò yà sí ọ̀tún tàbí yà sí òsì. Àwọn aláṣẹ Fílístínì tẹ̀lé wọn títí dé ibodè Bẹti-Sémésì."'), '1Sam$8:14': ('gba', 'fun', '1Sam$8:14 "Yóò mú èyí tí ó dára jù nínú oko yín, àti nínú ọgbà àjàrà yín àti nínú igi ólífì yín, yóò sì fi wọ́n fún àwọn ọmọ ọ̀dọ̀ rẹ̀."'), '1Sam$8:15': (None, 'fun', '1Sam$8:15 "Yóò mú ìdásímẹ́wàá hóró ọkà yín àti àká èso àjàrà yín, yóò sì fi fún àwọn oníṣẹ́ àti ọmọ ọ̀dọ̀ rẹ̀."'), '1Sam$9:5': ('wá', None, '1Sam$9:5 "Nígbà tí wọ́n dé agbégbé Ṣúfù, Ṣọ́ọ̀lù sọ fún ìránṣẹ́ tí ó wà lọ́dọ̀ rẹ̀ pé, “Wá, jẹ́ kí á padà sẹ́yìn, bí bẹ́ẹ̀ kọ́, baba mi yóò dá ìrònú rẹ̀ dúró nípa àwọn kẹ́tẹ́kẹ́tẹ́ yóò sì bẹ̀rẹ̀ àníyàn nípa wa.”"'), '1Sam$9:12': ('wá', 'lọ', '1Sam$9:12 "“Ó wà,” wọ́n dáhùn. “Ó ń bẹ níwájú u yín, múra nísinsìn yìí, ó ṣẹ̀ ṣẹ̀ wá sí ọ̀dọ̀ wa lónìí ni, nítorí àwọn ènìyàn ní ẹbọ rírú ni ibi gíga."'), '1Sam$9:14': ('lọ', 'wá', '1Sam$9:14 "Wọ́n gòkè lọ sí ìlú náà, bí wọ́n ti ń wọlé, níbẹ̀ ni Sámúẹ́lì, ń bọ̀ wá sí ọ̀dọ̀ wọn bí ó ti ń lọ ibi gíga náà."'), '1Sam$10:3': ('wá', 'lọ', '1Sam$10:3 "“Nígbà náà, ìwọ yóò lọ láti ibẹ̀ títí yóò fi dé ibi igi Tábórì ńlá. Ọkùnrin mẹ́ta tí wọ́n ń gòkè lọ sọ́dọ̀ Ọlọ́run ní Bẹ́tẹ́lì yóò pàdé rẹ níbẹ̀. Ọ̀kan yóò mú ọmọ ewúrẹ́ mẹ́ta lọ́wọ́, èkejì, iṣù àkàrà mẹ́ta àti ẹ̀kẹtà yóò mú ìgò wáìnì."'), '1Sam$10:8': (None, 'wá', '1Sam$10:8 "“Lọ ṣáájú mi sí Gílgálì. Èmi yóò sọ̀kalẹ̀ tọ̀ ọ́ wá láti rú ẹbọ sísun àti láti rú ẹbọ ìrẹ́pọ̀, ṣùgbọ́n ìwọ gbọdọ̀ dúró fún ọjọ́ méje títí èmi yóò fi wá sí ọ̀dọ̀ rẹ láti sọ fún ọ ohun tí ó yẹ tí ìwọ yóò ṣe.”"'), '1Sam$10:9': ('wá', 'lọ', '1Sam$10:9 "Bí Ṣọ́ọ̀lù ti yípadà láti fi Sámúẹ́lì sílẹ̀, Ọlọ́run yí ọkàn Ṣọ́ọ̀lù padà àti pé gbogbo àmì wọ̀nyí sì wá sí ìmúṣẹ ní ọjọ́ náà."'), '1Sam$14:1': ('wá', 'lọ', '1Sam$14:1 "Ní ọjọ́ kan, Jónátanì ọmọ Ṣọ́ọ̀lù wí fún ọ̀dọ́mọkùnrin tí ó ń ru ìhámọ́ra rẹ̀ pé, “Wá, jẹ́ kí a rékọjá lọ sí ìlú olódi àwọn Fílístínì tí ó wà ní ìhà kejì.” Ṣùgbọ́n kò sọ fún baba rẹ̀."'), '1Sam$14:6': (None, 'diẹ', '1Sam$14:6 "Jónátanì sì wí fún ọ̀dọ́mọkùnrin tí ó ń ru ìhámọ́ra rẹ̀ pé, “Wá, jẹ́ kí a lọ sí ìlu olódi àwọn aláìkọlà yìí. Bóyá OLÚWA yóò jà fún wa, kò sí ohun tó lè di OLÚWA lọ́wọ́ láti gbàlà, yálà nípaṣẹ̀ púpọ̀ tàbí nípaṣẹ̀ díẹ̀.”"'), '1Sam$14:9': ('lọ', 'wá', '1Sam$14:9 "Bí wọ́n bá sọ fún wa pé, ‘Ẹ dúró títí àwa yóò fi tọ̀ yín wá,’ àwa yóò dúró síbi tí a wà, àwa kì yóò sì gòkè tọ̀ wọ́n lọ."'), '1Sam$14:49': ('ọmọbinrin', 'ọmọ', '1Sam$14:49 " Àwọn ọmọ Sọ́ọ̀lù sì ni Jónátánì, Ísúì àti Málíkísúà. Orúkọ ọmọbìnrin rẹ̀ àgbà sì ni Mérábù àti orúkọ ọmọbìnrin rẹ̀ kékeré ni Míkálì."'), '1Sam$15:3': (None, 'obinrin', '1Sam$15:3 "Lọ nísinsìn yìí, kí o sì kọlu Ámálékì, kí o sì pa gbogbo ohun tí ó bá jẹ́ ti wọn ní àparun. Má ṣe dá wọn sí, pa ọkùnrin àti obìnrin wọn, ọmọ kékeré àti ọmọ ọmú, màlúù àti àgùntàn, ìbákasẹ àti kẹ́tẹ́kẹ́tẹ́ wọn.”"'), '1Sam$15:12': ('dide', None, '1Sam$15:12 "Ní òwúrọ̀ kùtùkùtù Sámúẹ́lì sì dìde láti lọ pàdé Ṣọ́ọ̀lù, ṣùgbọ́n wọ́n sọ fún un pé, “Ṣọ́ọ̀lù ti wá sí Kámẹ́lì. Ó ti kọ́ ibìkan fún ara rẹ̀ níbẹ̀, ó sì ti yípadà, ó sì ti sọ̀kalẹ̀ lọ sí Gílígálì.”"'), '1Sam$17:46': (None, 'fun', '1Sam$17:46 "Lónìí yìí ni OLÚWA yóò fi ọ́ lé mi lọ́wọ́, èmi yóò sì pa ọ́, èmi yóò sì gé orí ì rẹ. Lónìí èmi yóò fi òkú ogun Fílístínì fún ẹyẹ ojú ọ̀run àti fún ẹranko ìgbó, gbogbo ayé yóò sì mọ̀ pé Ọlọ́run wà ní Ísírẹ́lì."'), '1Sam$19:24': ('ọjọ', None, '1Sam$19:24 "Ó sì bọ́ aṣọ rẹ̀, òun pẹ̀lú sì ń ṣọtẹ́lẹ̀ níwájú Sámúẹ́lì. Ó sì dùbúlẹ̀ bẹ́ẹ̀ ní gbogbo ọjọ́ náà àti ní òru. Ìdí nìyìí tí àwọn ènìyàn fi wí pé, “Ṣé Ṣọ́ọ̀lù náà wà lára àwọn wòlíì ni?”"'), '1Sam$20:12': (None, None, '1Sam$20:12 "Nígbà náà ni Jónátanì sọ fún Dáfídì: “Pẹ̀lú OLÚWA Ọlọ́run àwọn Ísírẹ́lì, èmi yóò rí i dájú pé mo gbọ́ ti ẹnu baba mi ní ìwòyí ọ̀la tàbí ní ọ̀túnla tí ó bá sì ní ojú rere ní inú dídùn sí ọ, tí èmi kò bá ránṣẹ́ sí ọ, kí èmi sì jẹ́ kí o mọ̀?"'), '1Sam$20:19': (None, 'lọ', '1Sam$20:19 "Ní ọ̀túnla lọ́wọ́ alẹ́ lọ sí bi tí o sá pamọ́ sí nígbà tí ìṣòro yìí bẹ̀rẹ̀, kí o sì dúró níbi òkúta Ésélù."'), '1Sam$20:28': (None, None, '1Sam$20:28 "Jónátanì sì dáhùn pé, “Dáfídì bẹ̀ mí tọ̀wọ̀tọ̀wọ̀ fún ààyè láti lọ sí Bẹ́tílẹ́hẹ́mù."'), '1Sam$21:8': (None, None, '1Sam$21:8 "Dáfídì sì tún wí fún Áhímélékì pé, “Kò sí ọ̀kọ̀ tàbí idà lọ́wọ́ rẹ níhìn? Nítorí tí èmi kò mú idà mi, bẹ́ẹ̀ ni èmi kò mú nǹkan ìjà mi lọ́wọ́, nítorí pé iṣẹ́ ọba náà jẹ́ iṣẹ́ ìkánjú.”"'), '1Sam$22:3': ('baba', 'iya', '1Sam$22:3 "Dáfídì sì ti ibẹ̀ náà lọ sí Mísípa tí Móábù: ó sì wí fún ọba Móábù pé, “Jẹ́ kí baba àti ìyá mi, èmi bẹ̀ ọ́ wá bá ọ gbé, títí èmi yóò fi mọ ohun ti Ọlọ́run yóò ṣe fún mi.”"'), '1Sam$22:6': ('duro', None, '1Sam$22:6 "Ṣọ́ọ̀lù si gbọ́ pé a rí Dáfídì àti àwọn ọkùnrin tí ó wà lọ́dọ̀ rẹ̀; Ṣọ́ọ̀lù sì ń bẹ ní Gíbéà lábẹ́ igi kan ní Rámà; ọ̀kọ̀ rẹ̀ sì ń bẹ lọ́wọ́ rẹ̀, àti gbogbo àwọn ìránṣẹ́ rẹ̀ sì dúró tì í."'), '1Sam$23:23': ('wá', 'lọ', '1Sam$23:23 "Ẹ sì wo, kí ẹ sì mọ ibi ìsápamọ́ tí ó máa ń sápamọ́ sí, kí ẹ sì tún padà tọ̀ mí wá, kí èmi lè mọ̀ dájú; èmi ó sì bá yín lọ: yóò sì ṣe bí ó bá wà ní ilẹ̀ Ísírẹ́lì, èmi ó sì wá a ní àwárí nínú gbogbo ẹgbẹ̀rún Júdà!”"'), '1Sam$24:17': ('ibi', None, '1Sam$24:17 "Ó sì wí fún Dáfídì pé, “Ìwọ ṣe olódodo jù mí lọ; nítorí pé ìwọ ti fi ire san án fún mi, èmi fi ibi san án fún ọ."'), '1Sam$25:3': ('eniyan', 'obinrin', '1Sam$25:3 "Orúkọ ọkùnrin náà sì ń jẹ́ Nábálì, orúkọ aya rẹ̀ ń jẹ́ Ábígáílì; òun sì jẹ́ olóye obìnrin, àti arẹwà ènìyàn; ṣùgbọ́n òǹrorò àti oníwà búburú ni ọkùnrin; ẹni ìdílé Kálẹ́bù ni òun jẹ́."'), '1Sam$25:7': (None, 'ni', '1Sam$25:7 "“ ‘Ǹjẹ́ mo gbọ́ pé, àwọn olùrẹ̀run ń bẹ lọ́dọ̀ rẹ Wò ó, àwọn olùsọ́ àgùntàn rẹ ti wà lọ́dọ̀ wa àwa kò sì pà wọ́n lára, wọn kò sì sọ ohunkóhun nù ní gbogbo ọjọ́ tí wọ́n wà ní Kámẹ́lì."'), '1Sam$25:11': (None, 'fun', '1Sam$25:11 "Ǹjẹ́ ki èmi o mú òunjẹ́ mi, àti omi mi, àti ẹran mi ti mo pá fún àwọn olùrẹ́run mi, ki èmi si fi fún àwọn ọkùnrin ti èmi kò mọ̀ ibi wọ́n ti wá?”"'), '1Sam$25:15': ('ọjọ', None, '1Sam$25:15 "Ṣùgbọ́n àwọn ọkùnrin náà ṣe oore fún wa gidigidi, wọn kò ṣe wá ní ibi kan, ohunkóhun kò nù ni ọwọ́ wa ni gbogbo ọjọ́ ti àwa bá wọn rìn nígbà ti àwa ń bẹ lóko."'), '1Sam$25:21': (None, 'ni', '1Sam$25:21 "Dáfídì sì ti wí pé, “Ǹjẹ́ lásán ni èmi ti pa gbogbo èyi tí i ṣe ti eléyìí ní ihà, ti ohunkóhun kò sì nù nínú gbogbo èyí ti í ṣe tirẹ̀: òun ni o sì fi ibi san ire fún mi yìí."'), '1Sam$25:36': (None, None, '1Sam$25:36 "Ábígáílì sì tọ Nábálì wá, sì wò ó, òun sì ṣe àṣè ni ilé rẹ̀ gẹ́gẹ́ ni àsè ọba: inú Nábálì sì dùn nítorí pé, ó ti mú ọtí ni àmupara; òun kò si sọ nǹkan fún un, díẹ̀ tàbí púpọ̀: títí di ìmọ́lẹ̀ òwúrọ̀"'), '1Sam$25:44': (None, 'ọmọ', '1Sam$25:44 "Ṣùgbọ́n Ṣọ́ọ̀lù ti fi Míkálì ọmọ rẹ̀ obìnrin aya Dáfídì, fún Fátíélì ọmọ Láìsi tí i ṣe ara Gálímù."'), '1Sam$26:11': ('jẹ ki', None, '1Sam$26:11 "OLÚWA má jẹ́ kí èmi na ọwọ́ mi sí ẹni àmì òróró OLÚWA: ǹjẹ́ èmi bẹ̀ ọ́, mú ọ̀kọ̀ náà tí ń bẹ níbi tìmùtìmù rẹ̀, àti ìgò omi kí a sì máa lọ.”"'), '1Sam$27:9': (None, 'obinrin', '1Sam$27:9 "Dáfídì sì kọlu ilẹ̀ náà kò sì fi ọkùnrin tàbí obìnrin sílẹ̀ láàyè, ó sì kó àgùntàn, àti màlúù, àti kẹ́tẹ́kẹ́tẹ́, àti ràkúnmí, àti aṣọ, ó sì yípadà ó tọ Ákíṣì wá."'), '1Sam$27:11': (None, 'obinrin', '1Sam$27:11 "Dáfídì kò sì dá ọkùnrin tàbí obìnrin sí láàyè, láti mú ìròyìn wá sí Gátì, wí pé, “Ki wọn máa bá à sọ ọ̀rọ̀ wa nibẹ̀, pé, ‘Báyìí ni Dáfídì ṣe’ ” Àti bẹ́ẹ̀ ni iṣe rẹ̀ yóò sì rí ni gbogbo ọjọ́ ti yóò fí jókòó ni ìlú àwọn Fílístínì."'), '1Sam$28:8': (None, 'obinrin', '1Sam$28:8 "Ṣọ́ọ̀lù sì pa ara dà, ó sì mú aṣọ mìíràn wọ̀, ó sì lọ, àwọn ọmọkùnrin méjì sì pẹ̀lú rẹ̀, wọ́n sì wá sí ọ̀dọ̀ obìnrin náà lóru: òun sì wí pé, “Èmi bẹ̀ ọ́, fi ẹ̀mí abókúsọ̀rọ̀ wo nǹkan fún mi, kí o sì mú ẹni tí èmí ó dárúkọ rẹ̀ fún ọ wá sókè fún mi.”"'), '1Sam$28:20': ('ọjọ', None, '1Sam$28:20 "Lojúkan náà ni Ṣọ́ọ̀lù ṣubú lulẹ̀ gbalaja ní bí ó ṣe gùn tó, ẹ̀rù sì bà á gidigidi nítorí ọ̀rọ̀ Sámúẹ́lì; agbára kò sí fún un; nítorí pé kò jẹun ní ọjọ́ náà t\'ọ̀sán t\'òru."'), '1Sam$29:6': (None, None, '1Sam$29:6 "Ákíṣì sì pe Dáfídì, ó sì wí fún un pé, “Bí OLÚWA ti ń bẹ láàyè, ìwọ jẹ́ ólóòótọ́ àti ẹni ìwà rere lójú mi, ní àlọ rẹ àti ààbọ̀ rẹ pẹ̀lú mi ní ogun: nítorí pé èmi ko tìí ri búburú kan lọ́wọ́ rẹ́ láti ọjọ́ ti ìwọ ti tọ̀ mí wá, títí o fi dì òní yìí ṣùgbọ́n lójú àwọn ìjòyè, ìwọ kò ṣe ẹni tí ó tọ́."'), '1Sam$30:1': (None, None, '1Sam$30:1 "Ó sì ṣe nigbà ti Dáfídì àti àwọn ọmọkùnrin rẹ̀ sì bọ̀ sí Síkílágì ní ọjọ́ kẹta, àwọn ará Ámálékì sì ti kọlu Néséfà, àti Síkílágì, wọ́n sì ti kùn ún ní iná."'), '2Sam$2:1': ('wá', 'lọ', '2Sam$2:1 "Lẹ́yìn àkókò yìí, Dáfídì wádìí lọ́wọ́ OLÚWA. Ó sì béèrè pé, “Ṣé èmi lè gòkè lọ sí ọ̀kan lára àwọn ìlú Júdà?” OLÚWA sì wí pé, “Gòkè lọ.” Dáfídì sì béèrè pé, “Ní ibo ni kí èmi kí ó lọ?” OLÚWA sì dáa lóhùn pé, “Sí Hébúrónì.”"'), '2Sam$2:19': ('ọtun', 'osi', '2Sam$2:19 "Ó ń lé Ábínérì, kò sì yípadà sí ọ̀tún tàbí sí òsì bí ó ti ń lé e."'), '2Sam$2:21': ('ọtun', 'osi', '2Sam$2:21 "Nígbà náà, Ábínérì sọ fún un pé, “Yípadà sí ọ̀tún tàbí òsì; mú ọ̀kan lára àwọn ọ̀dọ́mọkùnrin kí o sì bọ́ ìhámọ́ra rẹ.” Ṣùgbọ́n Ásáhélì kọ̀ láti dẹ́kun lílépa rẹ̀."'), '2Sam$2:24': ('lọ', 'wá', '2Sam$2:24 "Jóábù àti Ábíṣáì sì lépa Ábínérì: òòrùn sì wọ̀, wọ́n sì dé òkè ti Ámímà tí o wà níwájú Gíà lọ́nà ijù Gíbíónì."'), '2Sam$3:2': ('ọmọ', None, '2Sam$3:2 Dáfídì sì bí ọmọkùnrin ní Hébírónì: Ámónì ni àkọ́bí rẹ̀ tí Áhínóámù ará Jésírẹ́lì bí fún un.'), '2Sam$3:23': ('ti lọ', 'wá', '2Sam$3:23 "Nígbà tí Jóábù àti gbogbo ogun tí ó pẹ̀lú rẹ̀ sì dé, wọ́n sì sọ fún Jóábù pé Ábínérì, ọmọ Nérì ti tọ ọba wá, òun sì ti rán an lọ, ó sì ti lọ ní àlàáfíà."'), '2Sam$5:6': ('lọ', 'wá', '2Sam$5:6 " Àti ọba àti àwọn ọmọkùnrin rẹ̀ sì lọ sí Jérúsálẹ́mù sọ́dọ̀ àwọn ará Jébúsì, àwọn ènìyàn ilẹ̀ náà; àwọn tí ó sì ti wí fún Dáfídì pé, “Bí kò ṣe pé ìwọ bá mú àwọn afọ́jú àti àwọn arọ́ kúrò, ìwọ kì yóò wọ ìhín wá” wọ́n sì wí pé, “Dáfídì kì yóò lè wá síhìn ín.”"'), '2Sam$5:17': (None, None, '2Sam$5:17 "Ṣùgbọ́n nígbà tí àwọn Fílístínì gbọ́ pé, wọ́n ti fi Dáfídì jọba lórí Ísírẹ́lì, gbogbo àwọn Fílístínì sì gòkè wá láti wá Dáfídì; Dáfídì sì gbọ́, ó sì sọ̀kalẹ̀ lọ sí ìlú olódì."'), '2Sam$5:23': ('lọ', 'wá', '2Sam$5:23 "Dáfídì sì bèèrè lọ́dọ̀ OLÚWA, òun sì wí pé, “Má ṣe gòkè lọ; ṣùgbọ́n bù wọ́n lẹ́yìn, kí o sì kọlù wọ́n níwájú àwọn igi Bákà."'), '2Sam$7:10': ('gbe', None, '2Sam$7:10 "Èmi ó sì yan ibìkan fún àwọn ènìyàn mi, àní Ísírẹ́lì, èmi ó sì gbìn wọ́n, wọn ó sì má gbé bí ibùjókòó ti wọn, wọn kì yóò sì ṣípò padà mọ́; àwọn ọmọ ènìyàn búburú kì yóò sì pọ́n wọn lójú mọ́, bí ìgbà àtijọ́."'), '2Sam$11:1': (None, None, '2Sam$11:1 " Lẹ́yìn ìgbà tí ọdún yípo, ní àkókò ìgbà tí àwọn ọba náà ń jáde ogun, Dáfídì sì rán Jóábù, àti àwọn ìránṣẹ́ rẹ̀ pẹ̀lú rẹ̀, àti gbogbo Ísírẹ́lì; wọ́n sì pa àwọn ọmọ Ámónì, wọ́n sì dó ti Rábà. Dáfídì sì jókòó ní Jérúsálẹ́mù."'), '2Sam$11:26': (None, 'ọkọ', '2Sam$11:26 "Nígbà tí aya Ùráyà sì gbọ́ pé Ùráyà ọkọ rẹ̀ kú, ó sì ṣọ̀fọ̀ nítorí ọkọ rẹ̀."'), '2Sam$12:2': ('ọlọrọ', None, '2Sam$12:2 Ọkùnrin ọlọ́rọ̀ náà sì ní àgùntàn àti màlúù lọ́pọ̀lọpọ̀.'), '2Sam$12:4': ('ọlọrọ', 'talaka', '2Sam$12:4 "“Àlejò kan sì tọ ọkùnrin ọlọ́rọ̀ náà wá, òun kò sì fẹ́ mú nínú àgùntàn rẹ̀, àti nínú màlúù rẹ̀: láti fi ṣe àlejò fún ẹni tí ó tọ̀ ọ́ wá: o sì mú àgùntàn ọkùnrin talákà náà fi ṣe àlejò fún ọkùnrin tí ó tọ̀ ọ́ wá.”"'), '2Sam$12:11': ('gba', 'fun', '2Sam$12:11 "“Bàyìí ni OLÚWA wí, Kíyèsí i, ‘Èmi ó jẹ́ kí ibi kí ó dìde sí ọ láti inú ilé rẹ wá, èmi ó sì gba àwọn obìnrin rẹ lójú rẹ, èmi ó sì fi wọ́n fún aládúgbò rẹ, òun ó sì bá àwọn obìnrin rẹ sùn níwájú òòrun yìí."'), '2Sam$13:15': ('korira', 'ife', '2Sam$13:15 "Ámúnónì sì kóríra rẹ̀ gidigidi, ìríra náà sì wá ju ìfẹ́ tí òun ti ní sí i rí lọ. Ámúnónì sì wí fún un pé, “Dìde, kí o sì máa lọ!”"'), '2Sam$13:22': (None, None, '2Sam$13:22 "Ábúsálómù kò sì bá Ámíúnónì sọ nǹkan rere, tàbí búburú: nítorí pé Ábúsálómù kóríra Ámúnónì nítorí èyí tí ó ṣe, àní tí ó fi agbára mú Támárì àbúrò rẹ̀."'), '2Sam$13:24': ('wá', 'lọ', '2Sam$13:24 "Ábúsálómù sì tọ ọba wá, ó sì wí pé, “Wò ó, jọ̀wọ́, ìránṣẹ́ rẹ ní olùrẹ́run àgùntàn, èmi bẹ̀ ọ́, jẹ́ kí ọba, àti àwọn ìránṣẹ́ rẹ̀ bá ìránṣẹ́ rẹ lọ.”"'), '2Sam$13:32': ('arakunrin', None, '2Sam$13:32 "Jónádábù ọmọ Ṣíméà arakùnrin Dáfídì sì dáhùn ó sì wí pé, “Kí OLÚWA mi ọba má ṣe rò pé wọ́n ti pa gbogbo àwọn ọ̀dọ́mọdé-kùnrin àwọn ọmọ ọba; nítorí pé Ámúnónì nìkan ṣoṣo ni ó kú: nítorí láti ẹnu Ábúsálómù wá ni a ti pinnu rẹ̀ láti ọjọ́ tí ó ti fi agbára mú Támárì àbúrò rẹ̀."'), '2Sam$14:17': ('buburu', None, '2Sam$14:17 "“Ìránṣẹ́bìnrin rẹ̀ sì wí pé, ‘Ǹjẹ́ ọ̀rọ̀ ọba OLÚWA mi yóò sì jásí ìtùnú; nítorí bí ańgẹ́lì Ọlọ́run bẹ́ẹ̀ ni OLÚWA mi ọba láti mọ rere àti búburú: OLÚWA Ọlọ́run rẹ yóò sì wà pẹ̀lú rẹ.’ ”"'), '2Sam$14:25': (None, 'ẹsẹ', '2Sam$14:25 Kó sì sí arẹ́wà kan ní gbogbo Ísírẹ́lì tí à bá yìn bí Ábúsálómù: láti àtẹ́lẹsẹ̀ rẹ̀ títí dé àtàrí rẹ̀ kò sí àbùkù kan lára rẹ̀.'), '2Sam$14:27': (None, 'ọmọbinrin', '2Sam$14:27 A sì bí ọmọkùnrin mẹ́ta fún Ábúsálómù àti ọmọbìnrin kan ti orúkọ rẹ̀ ń jẹ́ Támárì: òun sì jẹ́ obìnrin tí ó lẹ́wà lójú.'), '2Sam$15:7': (None, 'lọ', '2Sam$15:7 "Ó sì ṣe lẹ́yìn ogójì ọdún, Ábúsálómù sì wí fún ọba pé, “Èmi bẹ́ ọ́, jẹ́ kí èmi ó lọ, kí èmi sì san ìlérí mi tí èmi ti ṣe fún OLÚWA, ní Hébírónì."'), '2Sam$15:20': (None, None, '2Sam$15:20 "Lánàá yìí ni ìwọ dé, èmi ó ha sì mú kí ìwọ máa bá wá lọ kákiri lónìí bí? Èmi ń lọ sí ibikíbi tí mo bá rí: padà, kí o sì mú àwọn arakùnrin rẹ padà, kí àánú àti òtítọ́ OLÚWA wà pẹ̀lú rẹ.”"'), '2Sam$16:6': ('ọtun', 'osi', '2Sam$16:6 "Ó sì sọ òkúta sí Dáfídì, àti sí gbogbo àwọn ènìyàn, gbogbo àwọn alágbára ọkùnrin sì wà lọ́wọ́ ọ̀tún rẹ̀ àti lọ́wọ́ òsì rẹ̀."'), '2Sam$17:17': ('lọ', None, '2Sam$17:17 "Jónátanì àti Áhímásì sì dúró ní Énrógélì ọ̀dọ́mọdébìnrin kan sì lọ, ó sì sọ fún wọn, wọ́n sì lọ wọ́n sọ fún Dáfídì ọba nítorí pé kí a má báà rí wọn pé wọ́n wọ ìlú."'), '2Sam$17:25': ('ọmọ', 'ọmọbinrin', '2Sam$17:25 "Ábúsálómù sì fi Ámásà ṣe olórí ogun ní ipò Jóábù: Ámásà ẹni tí í ṣe ọmọ ẹnìkan, orúkọ ẹni tí a ń pè ní Itírà, ará Ísírẹ́lì, tí ó wọlé tọ Ábígáílì ọmọbìnrin Náhásì, arabìnrin Sérúíà, ìyá Jóábù."'), '2Sam$17:27': (None, None, '2Sam$17:27 "Nígbà tí Dáfídì sì wá sí Mahánáímù, Ṣóbì ọmọ Nahásì ti Rábà tí àwọn ọmọ Ámónì, àti Mákírì ọmọ Ámíélì ti Lodebárì, àti Básíláì ará Gílíádì ti Rógélímù."'), '2Sam$19:20': ('wá', 'lọ', '2Sam$19:20 "Nítorí pé ìránṣẹ́ rẹ mọ̀ pé èmi ti ṣẹ̀; sì wòó, mo wá lónìí yìí, ẹni àkọ́kọ́ ní gbogbo ìdílé Jósẹ́fù tí yóò sọ̀kalẹ̀ wá pàdé OLÚWA mi ọba.”"'), '2Sam$19:35': (None, None, '2Sam$19:35 "Ẹni ogbó ọgọ́rin ọdún sáà ni èmi lónìí: ǹjẹ́ èmi mọ ìyàtọ̀ nínú rere àti búburú? Ǹjẹ́ ìránṣẹ́ rẹ ló mọ adùn ohun tí òun ń jẹ tàbí ohun ti òun ń mu bí? Èmi tún lè mọ adùn ohùn àwọn ọkùnrin tí ń kọrin àti àwọn obìnrin tí ń kọ́rin bí, ǹjẹ́ nítorí kínni ìránṣẹ́ rẹ yóò ṣe jẹ́ ìyọnu síbẹ̀ fún OLÚWA mi ọba."'), '2Sam$19:37': ('baba', 'iya', '2Sam$19:37 "Èmi bẹ̀ ọ́, jẹ́ kí ìránṣẹ́ rẹ padà, èmi ó sì kú ní ìlú mi, a ó sì sin mí ní ibojì baba àti ìyá mi. Sì wo Kimhámù ìránṣẹ́ rẹ, yóò bá OLÚWA mi ọba gòkè; ìwọ ó sì ṣe ohun tí ó bá tọ́ lójú rẹ fún un.”"'), '2Sam$21:8': ('awọn ọmọkunrin', 'ọmọbinrin', '2Sam$21:8 "Ọba sì mú àwọn ọmọkùnrin méjèèjì tí Rísípà ọmọbìnrin Áíyà bí fún Ṣọ́ọ̀lù, àní Ámónì àti Méfíbóṣétì àwọn ọmọkùnrin máràrùn ti Mérábù, ọmọbìnrin Ṣọ́ọ̀lù, àwọn tí ó bí fún Ádíríélì ọmọ Básílíà ará Méhólátì."'), '2Sam$21:10': (None, None, '2Sam$21:10 "Rísípà ọmọbìnrin Áíyà sì mú aṣọ ọ̀fọ̀ kan, ó sì tẹ́ fún ará rẹ̀ lórí àpáta ní ìbẹ̀rẹ̀ ìkórè, títí omi fi dà sí wọn lára láti ọ̀run wá, kò sì jẹ́ kí àwọn ẹyẹ ojú ọ̀run bà lé wọn lọ́sán-an, tàbí àwọn ẹranko ìgbẹ́ lóru."'), '1Kgs$2:5': ('àlàáfíà', 'ogun', '1Kgs$2:5 "“Ìwọ pẹ̀lú sì mọ ohun tí Jóábù ọmọ Ṣérúyà ṣe sí mi àti ohun tí ó ṣe sí balógun méjì nínú àwọn ológun Ísírẹ́lì, sí Ábínérì ọmọ Nérì àti sí Ámásà ọmọ Jétérì. Ó sì pa wọ́n, ó sì ta ẹ̀jẹ̀ wọn sílẹ̀ ní ìgbà àlàáfíà bí í ti ojú ogun ó sì fi ẹ̀jẹ̀ náà sí ara àmùrè rẹ̀ tí ń bẹ ní ẹ̀gbẹ́ rẹ̀, àti sí ara Sálúbàtà rẹ̀ tí ń bẹ ní ẹṣẹ̀ rẹ̀."'), '1Kgs$2:41': ('ti lọ', None, '1Kgs$2:41 "Nígbà tí a sì sọ fún Sólómónì pé Síméì ti lọ láti Jérúsálẹ́mù sí Gátì, ó sì ti padà,"'), '1Kgs$3:7': ('lọ', None, '1Kgs$3:7 "“Nísinsìnyìí, OLÚWA Ọlọ́run mi, o ti mú ìránṣẹ́ rẹ jọba ní ipò Dáfídì baba mi. Ṣùgbọ́n ọmọ kékeré ni mí, èmi kò sì mọ̀ bí mo ṣe lè ṣe àwọn ojúṣe mi."'), '1Kgs$3:9': ('buburu', None, '1Kgs$3:9 Nítorí náà fi ọkàn ìmòye fún ìránṣẹ́ rẹ láti le ṣàkóso àwọn ènìyàn rẹ àti láti mọ ìyàtọ̀ láàrin rere àti búburú. Nítorí ta ni ó lè ṣe àkóso àwọn ènìyàn ńlá rẹ yìí?”'), '1Kgs$3:22': (None, 'kú', '1Kgs$3:22 "Obìnrin kejì sì wí pé, “Bẹ́ẹ̀ kọ́! Èyí alààyè ni ọmọ mi, èyí òkú ni ọmọ tirẹ̀.” Èyí òkú ni tirẹ̀; èyí alààyè ni tèmi. Ṣùgbọ́n èyí àkọ́kọ́ tún wí pé, “Bẹ́ẹ̀ kọ́! Èyí òkú ni tìrẹ; èyí alààyè ni tèmi.” Báyìí ni wọ́n sì ń jiyàn níwájú ọba."'), '1Kgs$3:23': (None, 'kú', '1Kgs$3:23 "Ọba sì wí pé, “Ẹni yìí wí pé, ‘Ọmọ mi ni ó wà láàyè, ọmọ tirẹ̀ ni ó kú,’ nígbà tí ẹni èkejì náà ń wí pé, ‘Bẹ́ẹ̀ kọ́! Ọmọ tirẹ̀ ni ó kú, ọmọ tèmi ni ó wà láàyè.’ ”"'), '1Kgs$4:8': (None, None, '1Kgs$4:8 Orúkọ wọn ni wọ̀nyí: Bénhúrì ní ìlú olókè Éfúráímù.'), '1Kgs$7:21': (None, None, '1Kgs$7:21 "Ó sì gbé àwọn ọ̀wọn náà ró ní ìloro tẹ́ḿpìlì, ó sì pe orúkọ ọ̀wọ̀n tí ó wà ní gúṣù ní Járánì àti èyí tí ó wà ní àríwá ní Bóásì."'), '1Kgs$7:39': ('osi', 'ọtun', '1Kgs$7:39 "Ó sì fi ẹsẹ̀ márùn ún sí apá ọ̀tún ìhà gúṣù ilé náà àti márùn ún sí apá òsì ìhà àríwá. Ó sì gbé agbádá ńlá ka apá ọ̀tún, ní apá ìlà oòrùn sí ìdojúkọ gúṣù ilé náà."'), '1Kgs$7:48': (None, None, '1Kgs$7:48 Sólómónì sì tún ṣe gbogbo ohun ọ̀ṣọ́ tí ń ṣe ti ilé OLÚWA pẹ̀lú: pẹpẹ wúrà; tabílì wúrà lórí èyí tí àkàrà ìfihàn gbé wà;'), '1Kgs$8:28': (None, None, '1Kgs$8:28 "Síbẹ̀ ṣe ìfetísílẹ̀ àdúrà ìránṣẹ́ rẹ àti ẹ̀bẹ̀ rẹ fún àánú, OLÚWA Ọlọ́run mi. Gbọ́ ẹkún àti àdúrà tí ìránṣẹ́ rẹ̀ ń gbà níwájú rẹ lónìí."'), '1Kgs$8:57': (None, None, '1Kgs$8:57 Kí OLÚWA Ọlọ́run wa kí ó wà pẹ̀lú wa bí ó ti wà pẹ̀lú àwọn baba wa; kí ó má sì ṣe fi wá sílẹ̀ tàbí kọ̀ wá sílẹ̀.'), '1Kgs$8:59': ('ọjọ', None, '1Kgs$8:59 "Àti kí ó sì jẹ́ kí àwọn ọ̀rọ̀ mi wọ̀nyí, tí mo ti gbà ní àdúrà níwájú OLÚWA, kí ó wà nítòsí OLÚWA Ọlọ́run wa ní ọ̀sán àti ní òru, kí ó lè mú ọ̀rọ̀ ìránṣẹ́ rẹ̀ dúró àti ọ̀rọ̀ Ísírẹ́lì ènìyàn rẹ̀, gẹ́gẹ́ bí àìní wa ojoojúmọ́,"'), '1Kgs$10:10': ('ayaba', 'ọba', '1Kgs$10:10 "Ó sì fún ọba ní ọgọ́fà (120) talẹ́ńtì wúrà, tùràrí olóòórún dídùn lọ́pọ̀lọpọ̀, àti òkúta iyebíye. Kò sí irú ọ̀pọ̀lọpọ̀ tùràrí tí a mú wá tí ó dà bí irú èyí tí ayaba Ṣébà fi fún Sólómónì ọba."'), '1Kgs$10:13': ('ayaba', 'ọba', '1Kgs$10:13 "Sólómónì ọba sì fún ayaba Ṣébà ní gbogbo ìfẹ́ rẹ̀ àti ohun tí ó béèrè, yàtọ̀ sí èyí tí a fi fún un láti ọwọ́ Sólómónì ọba wa. Nígbà náà ni ó yípadà, ó sì lọ sí ìlú rẹ̀, pẹ̀lú àwọn ìránṣẹ́ rẹ̀."'), '1Kgs$11:1': (None, 'lọ', '1Kgs$11:1 "Sólómónì ọba fẹ́ ọ̀pọ̀ àjèjì obìnrin yàtọ̀ sí ọmọbìnrin Fáráò, àwọn ọmọbìnrin Móábù, àti ti Ámónì, ti Édómù, ti Sídónì àti ti àwọn ọmọ Hítì."'), '1Kgs$11:15': ('wá', 'lọ', '1Kgs$11:15 "Ó sì ṣe, nígbà tí Dáfídì wà ní Édómù, Jóábù olórí ogun sì gòkè lọ láti sìn àwọn ọmọ ogun Ísírẹ́lì ti a pa lójú ogun, ó sì pa gbogbo ọkùnrin Édómù."'), '1Kgs$11:34': (None, 'gba', '1Kgs$11:34 "“ ‘Ṣùgbọ́n èmi kì yóò gba gbogbo ìjọba náà lọ́wọ́ Sólómónì; èmi ti mú un jẹ́ olórí ní gbogbo ọjọ́ ayé rẹ̀ nítorí ti Dáfídì ìránṣẹ́ mi, ẹni tí mo yàn, tí ó sì ti pa òfin mi àti àṣẹ mi mọ́."'), '1Kgs$12:1': ('lọ', None, '1Kgs$12:1 " Réhóbóámù sì lọ sí Ṣékémù, nítorí gbogbo Ísírẹ́lì ti lọ síbẹ̀ láti fi í jẹ ọba."'), '1Kgs$12:8': (None, None, '1Kgs$12:8 "Ṣùgbọ́n Réhóbóámù kọ ìmọ̀ràn tí àwọn àgbààgbà fún un, ó sì fi ọ̀rọ̀ náà lọ àwọn ọmọdé tí wọ́n dàgbà pẹ̀lú rẹ̀, tí wọ́n sì ń sìn ín."'), '1Kgs$12:10': (None, 'wuwo', '1Kgs$12:10 "Àwọn ọmọdé tí ó dàgbà pẹ̀lú rẹ̀ dá a lóhùn pé, “Sọ fún àwọn ènìyàn tí wọ́n wí fún ọ pé, ‘Baba rẹ̀ mú kí àjàgà wa wúwo ṣùgbọ́n ìwọ mú kí ó fúyẹ́ díẹ̀ fún wa’; sọ fún wọn pé, ìka ọwọ́ mi kékeré nípọn ju ẹ̀gbẹ́ baba mi lọ."'), '1Kgs$12:13': (None, None, '1Kgs$12:13 "Ọba sì fi ohùn líle dá àwọn ènìyàn lóhùn, ó sì kọ ìmọ̀ràn tí àwọn àgbààgbà fún un,"'), '1Kgs$12:22': ('wá', 'lọ', '1Kgs$12:22 Ṣùgbọ́n ọ̀rọ̀ Ọlọ́run tọ Sémáíàh ènìyàn Ọlọ́run wá wí pé:'), '1Kgs$12:31': (None, 'giga', '1Kgs$12:31 "Jéróbóámù sì kọ́ ojúbọ sórí ibi gíga, ó sì yan àwọn àlùfáà láti inú àwọn ènìyàn bí ó tilẹ̀ jẹ́ pé wọn kì í ṣe inú àwọn ọmọ Léfì."'), '1Kgs$13:33': (None, 'giga', '1Kgs$13:33 "Lẹ́yìn nǹkan yìí, Jéróbóámù kò padà kúrò nínú ọ̀nà búburú rẹ̀, ṣùgbọ́n ó tún yan àwọn àlùfáà sí i fún àwọn ibi gíga nínú gbogbo àwọn ènìyàn. Ẹnikẹ́ni tí ó bá fẹ́ di àlùfáà, a yà á sọ́tọ̀ fún ibi gíga wọ̀nyí."'), '1Kgs$14:7': (None, None, '1Kgs$14:7 "Lọ, sọ fún Jéróbóámù pé báyìí ni OLÚWA Ọlọ́run Ísírẹ́lì wí; Èmi sì gbé ọ ga láti inú àwọn ènìyàn, mo sì fi ọ́ jẹ olórì lórí Ísírẹ́lì ènìyàn mi."'), '1Kgs$15:17': (None, 'lọ', '1Kgs$15:17 "Bááṣà, ọba Ísírẹ́lì sì gòkè lọ sí Júdà, ó sì kọ́ Rámà láti má jẹ́ kí ẹnikẹ́ni jáde tàbí wọlé tọ Áṣà ọba lọ."'), '1Kgs$15:18': ('lọ', None, '1Kgs$15:18 "Nígbà náà ni Áṣà mú gbogbo fàdákà àti wúrà tí ó kù nínú ìṣúra ilé OLÚWA àti ìṣúra ilé ọba. Ó sì fi lé àwọn ìránṣẹ́ rẹ̀ lọ́wọ́, ó sì rán wọn lọ sí ọ̀dọ̀ Bẹni-Hádádì ọmọ Tábírímónì, ọmọ Hésíónì ọba Síríà tí ó ń gbé ní Dámásíkù."'), '1Kgs$16:31': ('ọmọ', 'ọmọbinrin', '1Kgs$16:31 "Ó sì ṣe bí ẹni pé ó ṣe ohun kékeré fún un láti máa rìn nínú ẹ̀ṣẹ̀ Jéróbóámù ọmọ Nébátì, ó sì mú Jésébélì, ọmọbìnrin Étíbáálì, ọba àwọn ará Sídónì ní aya, ó sì bẹ̀rẹ̀ sí ń sin Báálì, ó sì bọ ọ́."'), '1Kgs$17:24': ('eniyan', None, '1Kgs$17:24 "Obìnrin náà sì wí fún Èlíjà pé, “Nísinsìnyìí mo mọ̀ pé ènìyàn Ọlọ́run ni ìwọ ń ṣe, àti pé òtítọ́ ni ọ̀rọ̀ Ọlọ́run ní ẹnu rẹ.”"'), '1Kgs$18:12': (None, 'wá', '1Kgs$18:12 "Èmi kò sì mọ ibi tí ẹ̀mí OLÚWA yóò gbé ọ lọ nígbà tí mo bá fi ọ́ sílẹ̀. Bí mo bá lọ, tí mo sì sọ fún Áhábù, tí kò sì rí ọ, òun a sì pa mí. Ṣíbẹ̀ èmi ìránṣẹ́ rẹ bẹ̀rù OLÚWA láti ìgbà èwe mi wá."'), '1Kgs$19:9': (None, None, '1Kgs$19:9 "Níbẹ̀, ó lọ sí ibi ihò òkúta, ó sì wọ̀ níbẹ̀. Ọ̀rọ̀ OLÚWA sì tọ Èlíjà wá wí pé, “Kí ni ìwọ ń ṣe níhìn-ín yìí, Èlíjà?”"'), '1Kgs$19:20': ('baba', 'iya', '1Kgs$19:20 "Nígbà náà ni Èlíṣà sì fi àwọn màlúù sílẹ̀, ó sì ṣáré tọ Èlíjà lẹ́yìn. Ó wí pé, “Èmi bẹ̀ ọ́, jẹ́ kí èmi lọ fi ẹnu ko baba àti ìyá mi ní ẹnu. Nígbà náà ni èmi yóò tọ̀ ọ́ lẹ́yìn.” Èlíjà sì dáhùn wí pé, “Padà ṣẹ́yìn, kí ni mo fi ṣe ọ́?”"'), '1Kgs$20:17': ('lọ', 'wá', '1Kgs$20:17 "Àwọn ìjòyè kékèké ìgbéríko tètè kọ́ jáde lọ. Bẹni-Hádádì sì ránṣẹ́ jáde, wọ́n sì sọ fún un wí pé, “Àwọn ọkùnrin ń ti Samáríà jáde wá.”"'), '1Kgs$20:18': ('àlàáfíà', 'ogun', '1Kgs$20:18 "Ó sì wí pé, “Bí wọ́n bá bá ti àlàáfíà jáde wá, ẹ mú wọn láàyè; bí ti ogun ni wọ́n bá bá jáde, ẹ mú wọn láàyè.”"'), '1Kgs$20:40': (None, None, '1Kgs$20:40 "Nígbà tí ìránṣẹ́ rẹ̀ sì ní ìṣe níhìn ín àti lọ́hùn ún, a fẹ́ ẹ kù.” Ọba Ísírẹ́lì sì wí fún un pé, “Bẹ́ẹ̀ ni ìdájọ́ rẹ yóò rí, ìwọ fúnra rẹ ti dá a.”"'), '1Kgs$21:15': ('fun', 'gba', '1Kgs$21:15 "Bí Jésébélì sì ti gbọ́ pé, a ti sọ Nábótì ní Òkúta pa, ó sì wí fún Áhábù pé, “Dìde! Kí o sì jogún ọgbà àjàrà Nábótì, ará Jésérẹ́lì, tí ó kọ̀ láti tà fún ọ kò sí láàyè mọ́, ó ti kú.”"'), '1Kgs$21:16': (None, 'lọ', '1Kgs$21:16 "Nígbà tí Áhábù sì gbọ́ pé Nábótì ti kú, ó sì dìde, ó sì sọ̀kalẹ̀ lọ láti jogún ọgbà àjàrà Nábótì."'), '1Kgs$21:17': ('wá', None, '1Kgs$21:17 Nígbà náà ni ọ̀rọ̀ OLÚWA sì tọ Èlíjà ará Tíṣíbì wá wí pé;'), '1Kgs$22:7': (None, None, '1Kgs$22:7 "Ṣùgbọ́n Jèhóṣáfátì béèrè pé, “Ǹjẹ́ wòlíì OLÚWA kan kò sí níhìn ín, tí àwa ìbá béèrè lọ́wọ́ rẹ̀?”"'), '1Kgs$22:8': (None, 'ibi', '1Kgs$22:8 "Ọba Ísírẹ́lì dá Jèhóṣáfátì lóhùn pé, “Ọkùnrin kan sì wà, lọ́dọ̀ ẹni tí àwa lè béèrè lọ́wọ́ OLÚWA, ṣùgbọ́n mo kórìíra rẹ̀ nítorí kì í sọ àsọtẹ́lẹ̀ rere kan nípa mi, bí kò ṣe ibi. Míkáyà ọmọ Ímílà ni.” Jéhósáfátì sì wí pé, “Kí ọba má ṣe sọ bẹ́ẹ̀.”"'), '1Kgs$22:18': (None, 'ibi', '1Kgs$22:18 "Ọba Ísírẹ́lì sì wí fún Jèhósáfátì pé, “Ǹjẹ́ èmi kò sọ fún ọ pé kò fọ ire kan sí mi rí bí kò ṣe ibi?”"'), '1Kgs$22:19': ('ọtun', 'osi', '1Kgs$22:19 "Míkáyà sì tún wí pé, “Nítorí náà gbọ́ ọ̀rọ̀ OLÚWA: Mo rí OLÚWA jókòó lórí ìtẹ́ rẹ̀ pẹ̀lú gbogbo ogun ọ̀run dúró ní apá ọ̀tún àti ní apá òsì rẹ̀."'), '1Kgs$22:52': ('baba', 'iya', '1Kgs$22:52 "Ó sì ṣe búburú níwájú OLÚWA, nítorí tí ó rìn ní ọ̀nà baba rẹ̀, àti ní ọ̀nà ìyá rẹ̀, àti ní ọ̀nà Jéróbóámù ọmọ Nébátì, tí ó mú Ísírẹ́lì dẹ́ṣẹ̀."'), '2Kgs$1:4': (None, None, '2Kgs$1:4 Nítorí náà ohun tí OLÚWA sọ ni èyí: ‘Ìwọ kò ní kúrò lórí ìbùsùn tí o dúbúlẹ̀ lé. Dájúdájú ìwọ yóò kú!’ ” Bẹ́ẹ̀ ni Èlíjà lọ.'), '2Kgs$2:23': ('wá', 'lọ', '2Kgs$2:23 "Láti ibẹ̀ Èlíṣà lọ sókè ní Bétélì gẹ́gẹ́ bí ó ti ń rìn lọ lójú ọ̀nà, àwọn ọ̀dọ́ kan jáde wá láti ìlú náà wọ́n sì fi ṣe ẹlẹ́yà. “Má a lọ sókè ìwọ apárí!” Wọ́n wí pé. “Má a lọ sókè ìwọ apárí!”"'), '2Kgs$2:24': ('wò', 'pada', '2Kgs$2:24 "Ó sì yípadà, ó sì wò wọ́n ó sì fi wọ́n bú ní orúkọ OLÚWA. Nígbà náà béárì méjì sì jáde wá láti inú igbó ó sì lu méjìlélógójì (42) lára àwọn ọ̀dọ́ náà."'), '2Kgs$3:2': ('baba', 'iya', '2Kgs$3:2 "Ó sì ṣe búburú níwájú OLÚWA, ṣùgbọ́n kì í ṣe bí ti ìyá àti bàbá rẹ̀ ti ṣe. Ó gbé òkúta ère ti Báálì tí baba rẹ̀ ti ṣe."'), '2Kgs$3:11': (None, None, '2Kgs$3:11 "Ṣùgbọ́n Jèhóṣáfátì sì wí pé, “Ṣé kò sí wòlíì OLÚWA níbí, tí àwa ìbá ti ọ̀dọ̀ rẹ̀ béèrè lọ́wọ́ rẹ̀?” Ọ̀kan lára àwọn ìránṣẹ́ ọba Ísírẹ́lì dáhùn pé, “Èlíṣà ọmọ Ṣáfátì wà níbí. Ó máa ṣábà bu omi sí ọwọ́ Èlíjàh.”"'), '2Kgs$4:1': (None, 'ọkọ', '2Kgs$4:1 "Ìyàwó ọkùnrin kan láti ara ẹgbẹ́ wòlíì sunkún tọ Èlíṣà wá, “Ìránṣẹ́ rẹ ọkọ mi ti kú, ó sì mọ̀ wí pé ó bu ọlá fún OLÚWA. Ṣùgbọ́n Nísinsìn yìí onígbèsè rẹ̀ ti ń bọ̀ láti wá kó ọmọ ọkùnrin mi gẹ́gẹ́ bí ẹrú rẹ̀.”"'), '2Kgs$5:11': ('lọ', 'wá', '2Kgs$5:11 "Ṣùgbọ́n Námánì lọ pẹ̀lú ìbúnú ó sì wí pé, “Mo lérò pé yóò sì dìde jáde wá nítòótọ́ sí mi, yóò sì pe orúkọ OLÚWA Ọlọ́run, fi ọwọ́ọ rẹ̀ lórí ibẹ̀ kí ó sì wo ẹ̀tẹ̀ mi sàn."'), '2Kgs$5:24': ('wá', 'lọ', '2Kgs$5:24 "Nígbà tí Géhásì wá sí ilẹ̀ gíga, ó sì mú nǹkan náà lọ́dọ̀ ìránṣẹ́ ó sì gbé wọn kúrò ní ilé, ó sì rán ọkùnrin náà jáde ó sì lọ."'), '2Kgs$7:4': (None, None, '2Kgs$7:4 "Tí àwa bá wí pé, ‘Àwa lọ sí ìlú, ìyàn wà níbẹ̀,’ àwa yóò sì kú. Tí àwa bá dúró níbí, a máa kú, ǹjẹ́ Nísinsìnyí ẹ jẹ́ kí a lọ sí ibùdó ti àwọn ará Síríà kí àwa kí ó sì tẹríba. Bí wọ́n bá dá wa sí, àwa yóò yè, tí wọ́n bá sì pa wá, bẹ́ẹ̀ ni àwa yóò kú.”"'), '2Kgs$7:5': ('lọ', None, '2Kgs$7:5 "Ní àfẹ̀mójúmọ́ wọ́n dìde wọ́n sì lọ sí ibùdó àwọn ará Síríà. Nígbà tí wọ́n dé ẹ̀gbẹ́ ibùdó náà, kò sí ọkùnrin kan níbẹ̀,"'), '2Kgs$7:9': ('wá', 'lọ', '2Kgs$7:9 "Nígbà náà wọ́n wí fún ara wọn pé, “Àwa kò ṣe ohun rere. Oni yìí jẹ́ ọjọ́ ìròyìn rere àwa si paamọ́ ara wa. Tí àwa bá dúró títí di àfẹ̀mọ́júmọ́, ìjayà yóò jẹ́ ti wa. Ẹ jẹ́ kí a lọ ní ẹ̀ẹ̀kan kí a lọ ròyìn èyí fún àwọn ilé ọba.”"'), '2Kgs$8:1': (None, 'lọ', '2Kgs$8:1 "Nísin yìí, Èlíṣà wí fún obìnrin náà tí ọmọ rẹ̀ ọkùnrin jí pàdà sáyé pé, “jáde lọ pẹ̀lú ìdílé rẹ kí o sì lọ dúró fún ìgbà díẹ̀ ní ibíkíbi tí o bá le dúró sí, nítorí OLÚWA ti pàṣẹ ìyàn ní ìlú tí yóò lò tó ọdún méje.”"'), '2Kgs$8:2': ('eniyan', None, '2Kgs$8:2 "Obìnrin náà tẹ̀ ṣíwájú gẹ́gẹ́ bí ọ̀rọ̀ ènìyàn Ọlọ́run ti sọ. Òun àti ìdílé rẹ̀ sá lọ, wọ́n sì dúró ní ilé àwọn ará Fílístínì fún ọdún méje."'), '2Kgs$10:18': ('diẹ', 'pọ', '2Kgs$10:18 "Nígbà náà, Jéhù kó gbogbo àwọn ènìyàn jọ, ó sì wí fún wọn pé, “Áhábù sin Báálì díẹ̀; ṣùgbọ́n Jéhù yóò sìn ín púpọ̀."'), '2Kgs$11:2': ('ọmọ', 'ọmọbinrin', '2Kgs$11:2 "Ṣùgbọ́n Jéhóṣébà ọmọbìnrin ọba Jéhórámù àti arábìnrin Áhásáyà, mú Jóásì ọmọ Áhásáyà, ó sì jí i lọ kúrò láàárin àwọn ọmọ aládé bìnrin ti ọba náà, tí ó kù díẹ̀ kí a pa. Ó gbé òhun àti olùtọ́jú rẹ̀ sínú ìyẹ̀wù láti fi pamọ́ kúrò fún Ataláyà; Bẹ́ẹ̀ ni a kò pa á."'), '2Kgs$11:5': (None, None, '2Kgs$11:5 "Ó pàṣẹ fún wọn, wí pé, “Ìwọ tí ó wà nínú àwọn ẹgbẹ́ mẹ́tẹ̀ta tí ó ń lọ fún iṣẹ́ ìsìn ní ọjọ́ ìsinmi—ìdámẹ́ta yín fún síṣọ́ ààfin ọba."'), '2Kgs$11:9': ('wá', 'lọ', '2Kgs$11:9 Olórí àwọn ọrọọrún ṣe gẹ́gẹ́ bí Jéhóíádà àlùfáà ti paà láṣẹ́. Olúkúlùkù mú àwọn ọkùnrin rẹ̀ àwọn tí ó ń lọ sí iṣẹ́ ìsìn ní ọjọ́ ìsinmi àti àwọn tí wọ́n wá sí ọ̀dọ̀ Jéhóíádà àlùfáà.'), '2Kgs$11:11': (None, None, '2Kgs$11:11 "Àwọn olùṣọ́, olúkúlùkù pẹ̀lú ohun ìjà rẹ̀ ní ọwọ́ rẹ̀, wọ́n sì dúró ṣinṣin yìí ọba ká—lẹ́báá pẹpẹ àti ilé ìhà gúsù sí ìhà àríwá ilé tí a kọ́ fun OLÚWA náà."'), '2Kgs$14:9': ('ọmọbinrin', 'ọmọ', '2Kgs$14:9 "Ṣùgbọ́n Jéhóásì ọba Ísírẹ́lì fèsì sí Ámásáyà ọba Júdà: “Òṣùṣù kan ní Lébánónì rán iṣẹ́ sí Kédárì ní Lébánónì, ‘Fi ọmọbìnrin rẹ fún ọmọkùnrin mi ní ìgbéyàwó.’ Nígbà náà ẹranko ẹlẹ́sẹ̀ mẹ́rin tinú ìgbẹ́ ní Lébánónì wá pẹ̀lú ó sì fi ẹsẹ̀ tẹ òṣùṣú náà mọ́lẹ̀."'), '2Kgs$14:10': (None, 'ṣubu', '2Kgs$14:10 "Ìwọ ti ṣẹ́gun Édómù pẹ̀lú, ṣùgbọ́n, nísinsìn yìí ìwọ ṣe ìgbéraga. Ògo nínú ìṣẹ́gun rẹ, ṣùgbọ́n dúró nílé! Kí ni ó dé tí o fi ń wá wàhálà tí o sì fa ìṣubú rẹ àti ti Júdà pẹ̀lú?”"'), '2Kgs$17:32': (None, 'giga', '2Kgs$17:32 "Wọ́n sin OLÚWA, ṣùgbọ́n wọ́n sì tún yan gbogbo ẹgbẹ́ tí ènìyàn wọn láti ṣe iṣẹ́ oyè fún wọn gẹ́gẹ́ bí àlùfáà ní ibi gíga."'), '2Kgs$19:30': (None, None, '2Kgs$19:30 "Lẹ́ẹ̀kan sí i ìyókù ti ilé Júdà yóò sì tún hu gbòǹgbò lábẹ́, yóò sì so èṣo lókè."'), '2Kgs$20:4': ('wá', None, '2Kgs$20:4 "Kí ó tó di wí pé Àìṣáyà jáde kúrò ní àárin àgbàlá ààfin, ọ̀rọ̀ OLÚWA wá sí ọ̀dọ̀ rẹ̀ pé."'), '2Kgs$20:9': ('siwaju', 'pada', '2Kgs$20:9 "Àìṣáyà dáhùn pé, “Èyí ni àmìn tí OLÚWA fún ọ wí pé OLÚWA yóò ṣe ohun tí ó ti ṣe ìlérí: kí òjìji lọ ṣíwájú ìgbésẹ̀ mẹ́wàá, tàbí kí ó padà lọ ní ìgbésẹ̀ mẹ́wàá?”"'), '2Kgs$22:2': ('ọtun', 'osi', '2Kgs$22:2 "Ó sì ṣe èyí tí ó dára lójú OLÚWA, ó sì rìn ní ọ̀nà Dáfídì bàbá a rẹ̀, kò sì yípadà sí apá ọ̀tún tàbí sí apá òsì."'), '2Kgs$23:10': ('ọmọ', None, '2Kgs$23:10 "Ó sì ba ohun mímọ́ Tófẹ́tì jẹ́, tí ó wà ní pẹ̀tẹ́lẹ̀ Beni-Hínómì, bẹ́ẹ̀ ni kò sí ẹnìkan tí ó lè lò ó fún ẹbọ rírú fún ọmọ rẹ̀ ọkùnrin tàbí ọmọ rẹ̀ obìnrin nínú iná sí Mólékì."'), '1Chr$2:3': (None, 'ọmọbinrin', '1Chr$2:3 " Àwọn ọmọ Júdà: Érì, Ónánì àti Ṣélà, àwọn mẹ́tẹ̀ta wọ̀nyí ni wọ́n bí fún un láti ọ̀dọ̀ arábìnrin Kénánì, ọmọbìnrin Súà Érì àkọ́bí Júdà, ó sì burú ní ojú OLÚWA; Bẹ́ẹ̀ ni OLÚWA sì pa á."'), '1Chr$3:1': ('ọmọ', None, '1Chr$3:1 Àwọn wọ̀nyí sì ni ọmọ Dáfídì tí a bí fún un ní Hébírónì: Àkọ́bí sì ni Ámínónì ọmọ Áhínóámù ti Jésírẹ́lì; èkejì sì ni Dáníẹ́lì ọmọ Ábígáílì ará Kárímélì;'), '1Chr$9:24': ('ariwa', None, '1Chr$9:24 "Àwọn olùtọ́ju ẹnu ọ̀nà wà ní igun mẹ́rẹ̀ẹ̀rin: ìlà oòrùn, ìwọ̀ oòrùn àríwá àti gúsù."'), '1Chr$9:33': (None, None, '1Chr$9:33 "Àwọn tí wọ́n jẹ́ ọkùnrin, olórí àwọn ìdílé Léfì dúró nínú àgọ́ ilé OLÚWA, wọn kò sì se lára isẹ́ ìsìn yòókù nítorí wọ́n ń se isẹ́ náà lọ́sán, lóru."'), '1Chr$12:2': ('ọtun', 'osi', '1Chr$12:2 "Wọ́n sì mú wọn pẹ̀lú ìtẹríba wọ́n sì lágbára láti ta ọfà tàbí láti ta kànnàkànnà òkúta ní ọwọ́ òsì tàbí ní ọwọ́ ọ̀tún, wọ́n sì jẹ́ ìbátan ọkùnrin Ṣọ́ọ̀lù láti ẹ̀yà Bẹ́ńjámínì):"'), '1Chr$12:8': (None, None, '1Chr$12:8 "Díẹ̀ lára àwọn ará Gádì yà sọ́dọ̀ Dáfídì ní ibi gíga ní ihà. Wọ́n sì jẹ́ onígboyà ológun tí ó múra fún ogun tí ó sì lágbára láti di àṣà àti ẹsin mú. Ojú wọn sì dàbí ojú kìnìún, wọ́n sì yára bí àgbọ̀nrín lórí àwọn òkè."'), '1Chr$12:17': ('lọ', 'wá', '1Chr$12:17 "Dáfídì sì lọ láti lọ pàdé wọn ó sì wí fún wọn pé, “Tí ẹ bá wá sọ́dọ̀ mi lálàáfíà, láti ràn mí lọ́wọ́ mo sẹtan láti mú un yín wà ní ìṣọ̀kan pẹ̀lú mi ṣùgbọ́n tí ẹ bá wá láti dá mi sí fún àwọn ọ̀ta mi nígbà tí ọwọ́ mi bá mọ́ kúrò nínú ìwà agbára, kí Ọlọ́run àwọn baba wa kí ó sì ri kí ó sì ṣe ìdájọ́.”"'), '1Chr$16:3': (None, 'obinrin', '1Chr$16:3 "Nígbà náà ó fún olúkúlùkù ọkùnrin àti obìnrin ọmọ Ísírẹ́lí ní ìṣù àkàrà kan, àkàrà dídùn ti àkókò kan àti àkàrà dídùn ti èṣo àjàrà kan."'), '1Chr$17:3': ('ọjọ', 'alẹ', '1Chr$17:3 "Ní àsálẹ́ ọjọ́ náà, ọ̀rọ̀ Ọlọ́run tọ Nátanì wá, wí pé:"'), '1Chr$17:11': (None, 'lọ', '1Chr$17:11 "Nígbà ti ọjọ́ rẹ bá kọjá, tí ìwọ bá lọ láti lọ bá àwọn baba à rẹ, Èmi yóò gbé àwọn ọmọ rẹ sókè láti rọ́pò rẹ, ọ̀kan lára àwọn ọmọ tìrẹ, Èmi yóò sì fìdí ìjọba Rẹ̀ kalẹ̀."'), '1Chr$20:1': (None, 'lọ', '1Chr$20:1 " Ní àkókò àkọ́rọ̀ òjò, ni ìgbà tí àwọn ọba lọ sí ogun, Jóábù ṣamọ̀nà àwọn ọ̀wọ́ ọmọ ogun. Ó fi ilẹ̀ àwọn ará Ámónì ṣòfò. Ó lọ sí ọ̀dọ̀ Rábà. Ó sì fi ogun dúró tì í. Ṣùgbọ́n Dáfídì dúró sí Jérúsálẹ́mù Jóábù kọlu Rábà, ó sì fi sílẹ̀ ní ìparun."'), '1Chr$21:23': (None, 'fun', '1Chr$21:23 "Órínánì ni ó sọ fún Dáfídì pé, “Gbà á! Jẹ́ kí OLÚWA ọba mi kí ó ṣe ohun tí ó dára lójú Rẹ̀. Woó, èmí yóò fún ọ ní àwọn màlúù fún ẹbọ ọrẹ ṣiṣun, àti ohun èlò ìpakà fún igi, àti ọkà fún ọrẹ oúnjẹ. Èmi yóò fun ọ ní gbogbo èyí.”"'), '1Chr$23:19': ('keji', 'akoko', '1Chr$23:19 "Àwọn ọmọ Hérónì: Jéríyà sì ni ẹni àkọ́kọ́, Ámáríyà sì ni ẹni ẹ̀ẹ̀kejì, Jáhásíélì sì ni ẹ̀ẹ̀kẹta àti Jékáméámù ẹ̀ẹ̀kẹrin. "'), '1Chr$23:20': ('akoko', 'keji', '1Chr$23:20 Àwọn ọmọ Húsíélì: Míkà ni àkọ́kọ́ àti Íṣíà ẹ̀ẹ̀kejì.'), '1Chr$23:27': (None, None, '1Chr$23:27 "Gẹ́gẹ́ bí ọ̀rọ̀ ìkẹyìn Dáfídì sí, àwọn ọmọ Léfì, wọ́n sì kà wọ́n, bẹ̀rẹ̀ láti orí àwọn ọmọ ogún ọdún tàbí jù bẹ́ẹ̀ lọ."'), '1Chr$24:7': (None, 'keji', '1Chr$24:7 "Ìpín èkíní jáde sí Jéhóíáríbù, èkejì sí Jédáià, "'), '1Chr$24:23': ('keji', 'akoko', '1Chr$24:23 "Àwọn ọmọ Hebúrónì: Jéríyà alákọ́kọ́, Ámáríyà elẹ́kẹjì, Jáhásélì ẹlẹ́kẹta àti Jékáméámù ẹlẹ́kẹrin. "'), '1Chr$25:9': (None, 'keji', '1Chr$25:9 "Isẹ́kèké èkíni èyí tí ó jẹ́ ti Ásáfù, jáde sí Joṣéfù, àwọn ọmọ Rẹ̀ àti ìdílé Rẹ̀ jẹ́, — méjìlá ekejì sì ni Gédálíà, òun àti àwọn ìdílé Rẹ̀ àti àwọn ọmọ Rẹ̀, — méjìlá"'), '1Chr$29:29': (None, None, '1Chr$29:29 "Ní ti ohun tí ó ṣẹlẹ̀ nígbà ìjọba ọba Dáfídì, láti ìbẹ̀rẹ̀ dé òpin, a kọ wọ́n sínú ìwe ìrántí ti Sámúẹ́lì aríran, ìwé ìrántí ti Nátanì wòlíì àti ìwé ìrántí ti Gádì aríran,"'), '2Chr$1:10': (None, None, '2Chr$1:10 "Fún mi ní ọgbọ́n àti ìmọ̀, kí èmi kí ó lè tọ́ àwọn ènìyàn wọ̀nyí sọ́nà, nítorí ta ni ó le è ṣàkóso àwọn ènìyàn rẹ tí ó pọ̀ yanturu wọ̀nyí?”"'), '2Chr$2:13': (None, None, '2Chr$2:13 "“Èmi ń rán Húrámì-Abi, sí ọ ọkùnrin tí ó kún fún ìmọ̀ ńlá,"'), '2Chr$3:17': (None, None, '2Chr$3:17 "Ó gbe òpó náà dúró níwájú ilé OLÚWA, ọ̀kan sí gúsù, pẹ̀lú ọ̀kan sí àríwá. Èyí ti gúsù, ó ṣe ní Jákínì àti èyí ti àríwá, ó ṣe ní Bóásì."'), '2Chr$4:6': (None, None, '2Chr$4:6 " Ó ṣe pọnunpọ́n mẹ́wàá fún fífọ nǹkan, ó sì gbé márùn-ún ka ìhà gúṣù àti márùn ún ní àríwá. Nínú wọn ni ó ti ń fọ àwọn nǹkan tí wọ́n ń lò fún ọrẹ sísun ṣùgbọ́n, àwọn àlùfáà ní ó ń lo òkun fún fífọ nǹkan."'), '2Chr$4:7': (None, None, '2Chr$4:7 Ó ṣe ìgbe fìtílà dúró mẹ́wàá ti wúrà gẹ́gẹ́ bí èyí tí a yàn fún wọn. A sì gbé wọn kalẹ̀ sínú ilé OLÚWA márùn un ní ìhà gúsù àti márùn ún ní ìhà àríwá.'), '2Chr$4:8': (None, None, '2Chr$4:8 "Ó ṣé tábìlì mẹ́wàá, ó sì gbé wọn sí inú ilé OLÚWA, márùn un ní gúṣù àti márùn ún ní ìhà àríwá. Ó ṣé ọgọ́rún ọpọ́n ìbùwọ̀n wúrà."'), '2Chr$6:10': (None, None, '2Chr$6:10 "“OLÚWA sì ti mú ìléri rẹ̀ ṣẹ. Èmi ti dìde ní ipò Dáfídì baba mi, a sì gbé mi ka ìtẹ́ Ísírẹ́lì, gẹ́gẹ́ bí OLÚWA ti ṣe ìlérí, èmi sì ti kọ́ tẹ́ḿpìlì fún orúkọ OLÚWA, Ọlọ́run Ísírẹ́lì."'), '2Chr$6:19': (None, None, '2Chr$6:19 "Síbẹ̀ ṣe àfiyèsí àdúrà ìránṣẹ́ rẹ àti ẹ̀bẹ̀ rẹ̀ fún àánú, OLÚWA Ọlọ́run mi, gbọ́ ẹkún àti àdúrà tí ìránṣẹ́ rẹ̀ ń gbà níwájú rẹ."'), '2Chr$9:9': ('ayaba', 'ọba', '2Chr$9:9 "Nígbà náà ni ó sì fún ọba ní ọgọ́fà talẹ́ntì wúrà (120). Ọ̀pọ̀lọpọ̀ iye tùràrí, àti òkúta iyebíye. Kò sì tíì sí irú tùràrí yí gẹ́gẹ́ bí èyí tí ayaba Ṣébà fifún ọba Sólómónì."'), '2Chr$9:12': ('ayaba', None, '2Chr$9:12 Ọba Sólómónì fún ayaba Ṣébà ní gbogbo ohun tí ó bèèrè fún àti ohun tí ó wù ú; ó sì fi fún un ju èyí tí ó mú wá fún un lọ. Nígbà náà ni ó lọ ó sì padà pẹ̀lú àwọn ọmọ-ẹ̀yìn rẹ̀ sí ìlú rẹ̀.'), '2Chr$9:29': (None, None, '2Chr$9:29 "Àti gẹ́gẹ́ bí ìyókù iṣẹ́ ìjọba Sólómónì, láti ìbẹ̀rẹ̀ títí dé òpin, ṣé a kò kọ wọ́n sínú ìwé ìtàn Nátanì wòlíì, àti nínú ìsọtẹ́lẹ̀ Áhíjà ará Sílónì àti nínú ìran Ídò, wòlíì tí o kan Jéróbámù ọmọ Nébátì?"'), '2Chr$10:1': ('lọ', None, '2Chr$10:1 Réhóbóámù sì lọ sí Ṣékémù nítorí gbogbo àwọn ọmọ Ísírẹ́lì ti lọ síbẹ̀ láti fi jẹ ọba.'), '2Chr$10:6': ('mu', None, '2Chr$10:6 "Nígbà náà ni ọba Réhóbóámù fi ọ̀ràn lọ̀ àwọn àgbààgbà tí ó ti ń sin Baba rẹ̀ Sólómónì nígbà ayé rẹ̀, ó sì bi wọ́n léèrè pe “Báwo ni ẹ̀yin yóò ṣe gbà mí ní ìmọ̀ràn láti dá àwọn ènìyàn wọ̀nyí lóhùn?”."'), '2Chr$10:8': (None, None, '2Chr$10:8 "Ṣùgbọ́n Réhóbóámù kọ ìmọ̀ràn náà tí àwọn àgbààgbà fi fún un, ó sì fi ọ̀rọ̀ lọ àwọn ọmọ ọkùnrin tí ó ti dàgbà sókè pẹ̀lú rẹ̀ tí wọ́n sì ń dúró níwájú rẹ̀."'), '2Chr$10:10': (None, 'wuwo', '2Chr$10:10 "Àwọn ọ̀dọ́mọdé tí ó ti dàgbà pẹ̀lú rẹ dá a lóhùn pé, “sọ fún àwọn ènìyàn tí ó wí fún ọ wí pé, ‘Baba gbé àjàgà wúwo lórí wa, ṣùgbọ́n mú àjàgà wa fúyẹ́, wí fún wọn, pé ìka ọwọ́ mi kékeré, ó nípọn ju ìbàdí baba mi lọ."'), '2Chr$10:13': (None, None, '2Chr$10:13 "Ọba sì dá wọn lóhùn ní ọ̀nà líle. Ó kọ̀ ìmọ̀ràn àwọn àgbààgbà,"'), '2Chr$11:2': ('wá', 'lọ', '2Chr$11:2 Ṣùgbọ́n ọ̀rọ̀ Ọlọ́run yí wá sí ọ̀dọ̀ Ṣémáíà ènìyàn Ọlọ́run:'), '2Chr$11:18': ('ọmọ', 'ọmọbinrin', '2Chr$11:18 "Réhóbóámù fẹ́ Máhálátì tí ó jẹ́ ọmọbìnrin ọmọkùnrin Dáfídì Jérímótì àti ti Ábíháílì, ọmọbìnrin ọmọkùnrin ti Jésè Élíábì."'), '2Chr$11:21': (None, 'ọmọbinrin', '2Chr$11:21 "Réhóbóámù fẹ́ràn Mákà ọmọbìnrin Ábúsálómù ju èyí kejì nínú àwọn ìyàwó rẹ̀ àti àwọn àlè rẹ̀ lọ. Ní gbogbo rẹ̀, ó ní ìyàwó méjìdínlógún àti ọgọ́ta àlè ọmọkùnrin méjìdínlọ́gbọ̀n àti ọgọ́ta ọmọbìnrin."'), '2Chr$12:15': (None, None, '2Chr$12:15 "Fún tí iṣẹ́ ìjọba Réhóbóámù láti ìbẹ̀rẹ̀ dé ìparí, ṣé a kò kọ wọ́n sínú ìwé ìrantí ti Ṣémáíà wòlíì àti ti Idò, aríran tí ó ní ṣe pẹ̀lú ìtàn ìdílé láti ọ̀dọ̀ àwọn baba ńlá? Ìtẹ̀ṣíwájú ogun jíjà sì wà láàárin Réhóbóámù àti Jéróbóámù."'), '2Chr$13:14': ('wò', None, '2Chr$13:14 "Nígbà tí Júdà sì bojúwo ẹ̀yìn, sì kíyèsi i, ogun ń bẹ níwáju àti lẹ́yìn, wọn sì ké pe OLÚWA, àwọn àlùfáà sì fun ìpè"'), '2Chr$15:7': (None, 'lagbara', '2Chr$15:7 "Ṣùgbọ́n fun ìwọ, jẹ́ alágbára, kí ó má sì se sú ọ. Nítorí tí a ó fi èrè sí iṣẹ́ ẹ̀ rẹ”"'), '2Chr$15:12': (None, None, '2Chr$15:12 Wọ́n sì tún dá májẹ̀mú láti wá OLÚWA Ọlọ́run àwọn baba wọn tinútinú wọn àti tọkàntọkàn wọn.'), '2Chr$15:16': ('ọba', None, '2Chr$15:16 " Ọba Ásà rọ ìyá ńlá rẹ̀ Mákà lóyè láti ipò rẹ̀ gẹ́gẹ́ bí ìyá ọba nítorí tí ó ti ṣe òpó Áṣérà tí ń lé ni sá. Ásà gé òpó náà lulẹ̀, wòó, ó sì jóo ní pẹ̀tẹ́lẹ̀ Kídírónì."'), '2Chr$16:1': ('wá', 'lọ', '2Chr$16:1 "Ní ọdún kẹrìndínlógójì ìjọba Ásà, Básà ọba Ísírẹ́lì gòkè wá sí Júdà ó sì kọlu Rámà, láti ma bàá jẹ́ kí ẹnikẹ́ni lè jáde tàbí wọlé tọ Ásà ọba Júdà lọ."'), '2Chr$16:11': (None, None, '2Chr$16:11 "Àwọn iṣẹ́ ìjọba Ásà, láti ìbẹ̀rẹ̀ títí dé òpin, ni akọ sínú ìwé ọba Júdà àti Ísírẹ́lì."'), '2Chr$18:6': (None, None, '2Chr$18:6 "Ṣùgbọ́n Jehósáfátì béèrè pé, “Ṣé kò ha sí wòlíì OLÚWA níbí ẹni tí àwa ìbá béèrè lọ́wọ́ rẹ̀?”"'), '2Chr$18:7': (None, None, '2Chr$18:7 "Ọba Ìsírélì dá Jehóṣáfatì lóhùn pé, “Ọkùnrin kan wà síbẹ̀ lọ́dọ̀ ẹni tí àwa ì bá tún béèrè lọ́wọ́ OLÚWA, ṣùgbọ́n èmi kórìíra rẹ̀ nítorì kò jẹ́ sọ àsọtẹ́lẹ̀ ohun rere kan nípa mi ṣùgbọ́n bí kò ṣe ohun búburú, ní gbogbo ìgbà òun náà ni Míkáyà ọmọ Ímílà.” “Ọba kò gbọdọ̀ sọ bẹ́ẹ̀,” Jehóṣáfátì sì dá lóhùn."'), '2Chr$18:14': ('lọ', 'wá', '2Chr$18:14 "Nígbà tí ó dé, ọba béèrè lọ́wọ́ rẹ̀, “Míkáyà, se kí àwa ki ó lọ sí ogun ti Rámótì Gílíádì, tàbí kí àwa kí ó fàsẹ́yìn?” “Ẹ dojú kọ wọ́n kí ẹ sì ṣẹ́gun.” Ó dahùn, “nítorí a ó fi wọ́n lé yín lọ́wọ́.”"'), '2Chr$18:17': (None, None, '2Chr$18:17 "Ọba Ísírẹ́lì wí fún Jéhóṣáfátì pé, “Ṣe èmi kò sọ fún ọ wí pé òun kò sọ àsọtẹ́lẹ̀ rere kankan nípa mi rí, ṣùgbọ́n búburú nìkan?”"'), '2Chr$18:18': ('ọtun', 'osi', '2Chr$18:18 "Mikáyà tẹ̀ṣíwájú pé, “Nítorí náà, gbọ́ ọ̀rọ̀ OLÚWA: Mo rí OLÚWA jòkó lórí ìtẹ́ rẹ̀ gbogbo ogun ọ̀run sì dúró lápá ọ̀tún àti lápá òsì."'), '2Chr$19:2': (None, None, '2Chr$19:2 "Jéhù aríran, ọmọ Hánánì jáde lọ láti lọ pàdé rẹ̀, ó sì wí fún ọba pé, “Sé ìwọ yóò máa ran ènìyàn búburú lọ́wọ́, kí o sì fẹ́ràn àwọn tí ó kórìíra OLÚWA? Nítorí èyí, ìbínú OLÚWA wà lórí wa."'), '2Chr$20:16': ('lọ', 'wá', '2Chr$20:16 "Ní ọla, ẹ sọ̀kalẹ̀ tọ̀ wọ́n lọ, wọn yóò gòkè pẹ̀lú ibi ṣíṣe wa, ẹ̀yin yóò sì rí wọn ní ìpẹ̀kun odò náà, níwájú ihà Jérúẹ́lì."'), '2Chr$20:34': (None, None, '2Chr$20:34 "Ìyòókù iṣẹ́ ìjọba Jéhóṣáfátì, láti ìbẹ̀rẹ̀ títí dé òpin, ni a kọ sí inú ìwé ìtàn ti Jéhù ọmọ Hánánì, tí a kọ sí inú ìwé àwọn ọba Ísírẹ́lì."'), '2Chr$21:12': (None, 'lọ', '2Chr$21:12 "Jóhórámì gba ìwé láti ọwọ́ Èlíjà wòlíì, tí ó wí pé: “Èyí ní ohun tí OLÚWA, Ọlọ́run baba à rẹ Dáfídì wí: ‘Ìwọ kò tí ì rìn ní ọ̀nà àwọn baba à rẹ Jèhóṣáfátì tàbí Ásà ọba Júdà."'), '2Chr$22:7': ('lọ', 'wá', '2Chr$22:7 " Ní gbogbo ìgbà ìbẹ̀wò Áhásáyà sí Jórámí, Ọlọ́run mú ìṣubú Áhásáyà wá. Nígbà tí Áhásáyà dé, ó jáde lọ pẹ̀lú Jórámì láti lọ bá Jéhù ọmọ Nímísì, ẹni tí OLÚWA ti fi àmì òróró yàn láti pa ìdílé Áhábù run."'), '2Chr$22:11': ('ọmọ', 'ọmọbinrin', '2Chr$22:11 "Ṣùgbọ́n Jehóṣẹ́bà ọmọbìnrin ọba Jéhórámù mú Jóásì, ọmọkùnrin Áhásáyà ó sì jíi gbé lọ kúrò láàrin àwọn ọmọ-obìrin ọba, àwọn tí ó kù díẹ̀ kí wọn pa. Wọn gbé òun àti olùtọ́jú rẹ̀ sínú ìyẹ̀wù. Nítorí Jèhóṣebà ọmọbìnrin ọba Jehórámì àti ìyàwó àlùfáà Jéhóiádà jẹ́ arábìnrin Áhásáyà. Ó fi ọmọ naà pamọ́ kúrò fún Ataláyà, kí ó má ba à pa á."'), '2Chr$23:6': (None, None, '2Chr$23:6 "Kò sí ẹnìkan tí ó gbọdọ̀ wọ inú ilé OLÚWA yàtọ̀ sí àwọn àlùfáà àti àwọn ará Léfì tí a rán ní iṣẹ́ ìsìn. Wọ́n lè wọlé nítori tí a ti yà wọ́n sọ́tọ̀, ṣùgbọ́n gbogbo ọkùnrin mìíràn ni kí wọn ó ṣọ ohun tí OLÚWA ti yàn fún wọn."'), '2Chr$23:8': (None, 'lọ', '2Chr$23:8 "Àwọn ará Léfì àti gbogbo ọkùnrin Júdà ṣe gẹ́gẹ́ bí Jehóádà Àlùfáà ti palásẹ. Olukúlùkù mú àwọn ọkùnrin rẹ̀, àwọn tí wọ́n lọ fún iṣẹ́ ìsìn ní ọjọ́ ìsinmi àti àwọn tí wọ́n ń kúrò ní ibi iṣẹ́. Nítorí Jéhóiádà àlùfáà kò ì tí tú ìpín kankan sílẹ̀."'), '2Chr$23:10': (None, None, '2Chr$23:10 "Ó mú gbogbo àwọn ọkùnrin wà ní ipò ìdúró pẹ̀lú ohun ìjà rẹ̀ ní ọwọ́ rẹ̀, yí ọba ká ní ẹ̀bá pẹpẹ àti ilé OLÚWA láti ìhà gúsù sí ìhà àríwa ilé OLÚWA."'), '2Chr$25:7': ('wá', 'lọ', '2Chr$25:7 "Ṣùgbọ́n ènìyàn Ọlọ́run kan tọ̀ ọ́ wá ó sì wí pé, “Ọba, àwọn ọ̀wọ́-ogun láti Ísírẹ́lì kò gbọdọ̀ yan pẹ̀lú rẹ, nítorí tí OLÚWA kò wà pẹ̀lú Ísírẹ́lì kì í ṣe pẹ̀lú ẹnìkankan láti Éfíráímù."'), '2Chr$25:10': ('lọ', 'wá', '2Chr$25:10 "Bẹ́ẹ̀ ni Ámásíà, tú àwọn ọwọ́ ogun tí ó ti wá sí ọ̀dọ̀ rẹ̀ láti Éfíráimù ká. Ó sì rán wọn lọ lé. Wọ́n kún fún ìbínú pẹ̀lú Júdà, wọ́n sì padà lọlé pẹ̀lu ìbínú ńlá."'), '2Chr$25:18': ('ọmọbinrin', 'ọmọ', '2Chr$25:18 "Ṣùgbọ́n Jéhóáṣì ọba Ísírẹ́lì fèsì padà sí Ámásíà ọba Júdà pé, “Òṣùṣù kan ní Lẹ́bánónì rán isẹ́ sí òpépé (igi) ní Lẹ́bánónì, fi ọmọbìnrin rẹ fún ọmọkùnrin mi ní ìgbéyàwo. Nígbà náà, ẹhànnà ẹranko ènìyàn ni Lébánónì wá, ó sì tẹ òṣùṣù náà lábẹ́ ẹsẹ̀."'), '2Chr$25:26': (None, None, '2Chr$25:26 "Fún ti iṣẹ́ mìíràn ti ìjọba Ámásíà láti ìbẹ̀rẹ̀ dé ìparí, ṣé a kò kọ wọ́n sínú ìwé àwọn ọba Júdà àti Ísírẹ́lì?"'), '2Chr$26:22': (None, None, '2Chr$26:22 "Ìyòókù iṣẹ́ ìjọba Ùsía láti ìbẹ̀rẹ̀ títí dé òpin, ni a kọ láti ọwọ́ wòlíì Ìsàíà ọmọ Ámósì."'), '2Chr$28:26': (None, None, '2Chr$28:26 "Ìyòókù iṣẹ́ ìjọba rẹ̀ àti gbogbo ọ̀nà rẹ̀, láti ìbẹ̀rẹ̀ títí dé òpin, ni a kọ sínú ìwé àwọn ọba Júdà àti ti Ísírẹ́lì."'), '2Chr$29:17': ('bẹrẹ', None, '2Chr$29:17 "Wọ́n sì bẹ̀rẹ̀ ìyà sí mímọ́ ní ọjọ́ kìn-ín-ní osù kìn-ín-ní, àti ní ọjọ́ kẹjọ oṣù náà wọ́n sì dé ìloro OLÚWA. Fún ọjọ́ mẹ́jọ mìíràn síi, wọ́n sì ya ilé OLÚWA sí mímọ́ fún rarẹ̀. Wọ́n sì parí ní ọjọ́ kẹ́rìndínlógún oṣù kìn-ín-ní."'), '2Chr$34:2': ('ọtun', 'osi', '2Chr$34:2 "Ó sì ṣe ohun tí ó dára ní ojú OLÚWA, ó sì rìn ọ̀nà baba rẹ̀ Dáfídì, kò sì yà sí ọwọ́ ọ̀tún tàbí òsì."'), '2Chr$35:26': (None, None, '2Chr$35:26 "Ìyòókù iṣẹ́ ìjọba Jósíà àti ìwà rere rẹ̀, gẹ́gẹ́ bí èyí tí a ti kọ sínu ìwé òfin OLÚWA."'), '2Chr$36:17': (None, 'odo', '2Chr$36:17 "Ó sì mú wá sórí wọn ọba àwọn ará Bábílónì tí wọ́n bá àwọn ọ̀dọ́mọkùnrin wọn jà pẹ̀lú idà ní ilẹ̀ ibi mímọ́, kò sì ní ìyọ́nú sí àgbà ọkùnrin tàbí ọ̀dọ́mọdé bìnrin, wúndíá, tàbí arúgbó. Ọlọ́run sì fi gbogbo wọn lé Nebukadinésárì lọ́wọ́."'), 'Ezra$7:9': ('wá', 'lọ', 'Ezra$7:9 "Ó bẹ̀rẹ̀ ìrìnàjò rẹ̀ láti Bábílónì ní ọjọ́ kìn-ín-ní oṣù kìn-ín-ní, ó sì dé Jérúsálẹ́mù ní ọjọ́ kìn-ín-ní oṣù karùn-ún nítorí ọwọ́ àánú Ọlọ́run rẹ̀ wà ní ara rẹ̀."'), 'Ezra$9:12': ('gba', 'fun', 'Ezra$9:12 "Nítorí náà, ẹ má ṣe fi àwọn ọmọbìnrin yín fún àwọn ọmọkùnrin wọn ní ìyàwó tàbí kí ẹ fẹ́ àwọn ọmọbìnrin wọn fún àwọn ọmọkùnrin yín ní ìyàwó. Ẹ má ṣe dá májẹ̀mú àlàáfíà pẹ̀lú wọn nígbà-kí-gbà kí ẹ̀yin kí ó sì le lágbára, kí ẹ sì jẹ ohun dáradára ilẹ̀ náà, kí ẹ sì fi sílẹ̀ fún àwọn ọmọ yín gẹ́gẹ́ bí ogún ayé rayé;"'), 'Neh$1:4': ('ọjọ', None, 'Neh$1:4 "Nígbà tí mo gbọ́ àwọn nǹkan wọ̀nyí, mo jókòó mo sì ṣunkún. Mo ṣọ̀fọ̀, mo gbààwẹ̀, mo sì gbàdúrà fún ọjọ́ díẹ̀ níwájú Ọlọ́run ọ̀run."'), 'Neh$2:6': (None, 'ọba', 'Neh$2:6 "Nígbà náà ni ọba, pẹ̀lú ìyàwó rẹ̀ tí ó jókòó lẹ́gbẹ̀ẹ́ rẹ̀, bi mí pé, “Báwo ni ìrìnàjò náà yóò ṣe pẹ́ ọ tó, nígbà wo sì ni ìwọ yóò padà?” Ó dùn mọ́ ọba láti rán mi lọ, bẹ́ẹ̀ ni mo dá àkókò kan."'), 'Neh$3:1': (None, None, 'Neh$3:1 "Élíṣíbù olórí àlùfáà àti àwọn àlùfáà arákùnrin rẹ̀ lọ ṣiṣẹ́, wọ́n sì tún ibodè Àgùntàn mọ. Wọ́n yà á sí mímọ́, wọ́n sì gbé ìlẹ̀kùn dúró sí ibi tí ó yẹ, wọ́n mọ ọ́n títí dé ilé ìṣọ́ Ọgọ́rùn ún, èyí tí wọ́n yà sí mímọ́ títí dé ilé ìṣọ́ gíga Hánálélì"'), 'Neh$3:12': ('ọmọbinrin', 'ọmọ', 'Neh$3:12 "Ṣálúmù ọmọ Hálọ́ésì, alákóṣo ìdajì agbégbé Jérúsálẹ́mù tún ti ẹ̀gbẹ́ rẹ̀ mọ pẹ̀lú ìrànlọ́wọ́ àwọn ọmọbìnrin rẹ̀"'), 'Neh$4:9': (None, 'alẹ', 'Neh$4:9 "Ṣùgbọ́n àwa gbàdúrà sí Ọlọ́run wa, a sì yan olùṣọ́ ọ̀sán àti ti òru láti kojú ìhàlẹ̀ yìí."'), 'Neh$4:22': (None, 'alẹ', 'Neh$4:22 "Ní ìgbà náà mo tún sọ fún àwọn ènìyàn pé, “Jẹ́ kí olúkúlùkù ènìyàn àti olùrànlọ́wọ́ rẹ̀ dúró ní Jérúsálẹ́mù ní òru, nítorí kí wọn le jẹ́ ẹ̀ṣọ́ fún wa ní òru, kí wọn sì le ṣe iṣẹ́ ní ọ̀sán.”"'), 'Neh$6:3': ('wá', None, 'Neh$6:3 "Bẹ́ẹ̀ ni mo rán oníṣẹ́ padà pẹ̀lú èsì yìí pé; “Èmi ń ṣe iṣẹ́ ńlá kan, èmi kò le è sọ̀kalẹ̀ wá. Èéṣe tí iṣẹ́ náà yóò fi dúró, nígbà tí mo bá fi í sílẹ̀ tí mo sì sọ̀kalẹ̀ tọ̀ yín wá?”"'), 'Neh$6:18': ('ọmọ', 'ọmọbinrin', 'Neh$6:18 "Nítorí ọ̀pọ̀lọpọ̀ àwọn Júdà ti mulẹ̀ pẹ̀lú u rẹ̀, nítorí tí ó jẹ́ àna Ṣekenáyà ọmọ Árà (Ṣáńbálátì fẹ́ ọmọ Ṣekenáyà) ọmọ rẹ̀ Jéhóhánánì sì tún fẹ́ ọmọbìnrin Méṣúlámì ọmọ Bérékíyà"'), 'Neh$8:4': ('osi', 'ọtun', 'Neh$8:4 "Akọ̀wé Ẹ́sírà dìde dúró lórí i pẹpẹ ìdúrólé tí a fi igi kàn fún ètò yìí. Ní ẹ̀gbẹ́ẹ rẹ̀ ọ̀tún ni Mátítayà, Ṣémà, Ánáyà, Úráyà, Hílíkáyà àti Máṣéíyà gbé dúró sí, ní ẹ̀gbẹ́ òsìi rẹ̀ ní Pédáíyà, Míṣíhẹ́lì, Málíkíjà, Hásúmù, Háṣábádánà, Ṣekaráyà àti Mésúlámù dúró sí."'), 'Neh$8:18': (None, None, 'Neh$8:18 "Ẹ́sírà kà nínú ìwé òfin Ọlọ́run, bí ọjọ́ ṣe ń gorí ọjọ́, láti ọjọ́ kìn-ín-ní dé ọjọ́ ìkẹyìn. Wọ́n ṣe àjọyọ̀ àjọ náà fún ọjọ́ méje, ní ọjọ́ kẹjọ wọ́n ní àpèjọ, ní ìbámu pẹ̀lú òfin."'), 'Neh$9:12': (None, None, 'Neh$9:12 Ní ọ̀sán ìwọ daríi wọn pẹ̀lú òpó ìkúùkú àti ní òru ni ìwọ daríi wọn pẹ̀lú òpó iná láti fún wọn ní ìmọ́lẹ̀ ní ọ̀nà tí wọn yóò gbà.'), 'Neh$9:18': (None, None, 'Neh$9:18 "Kódà nígbà tí wọ́n yá ère dídá (ère ọmọ màlúù) fún ara wọn, tí wọ́n sì wí pé, Èyí ni Ọlọ́run rẹ tí ó mú ọ gòkè láti Éjíbítì wá; tàbí nígbà tí wọ́n sọ ọ̀rọ̀-òdì tí ó burú jàì."'), 'Neh$10:28': (None, None, 'Neh$10:28 "“Àwọn ènìyàn tó kù—àwọn àlùfáà, àwọn ọmọ Léfì, àwọn aṣọ́nà, àwọn ìránṣẹ́ tẹ́ḿpìlì àti gbogbo àwọn tí wọ́n ya ara wọn sọ́tọ̀ kúrò lára àwọn ènìyàn àjèjì nítorí òfin Ọlọ́run, papọ̀ pẹ̀lú ìyàwóo wọn, gbogbo ọmọkùnrin àti àwọn ọmọbìnrin wọn, tí òye yé"'), 'Neh$13:6': (None, None, 'Neh$13:6 "Ṣùgbọ́n nígbà tí gbogbo èyí ń lọ lọ́wọ́, èmi kò sí ní Jérúsálẹ́mù, nítorí pé ní ọdún kejìlélọ́gbọ̀n Aritaṣéṣéṣì ọba Bábílónì ni mo padà tọ ọba lọ. Ṣùgbọ́n nígbà tí ó ṣe díẹ̀, mo gba ààyè lọ́dọ̀ọ rẹ̀."'), 'Neh$13:25': (None, 'fun', 'Neh$13:25 "Mo bá wọn wí mo sì gégùn-ún lé wọn lórí. Mo lu àwọn ènìyàn díẹ̀ nínú un wọn mo sì fa irun oríi wọn tu. Mo mú kí wọn búra ní orúkọ Ọlọ́run, kí wọn wí pé, “Ẹ̀yin kì yóò fi àwọn ọmọbìnrin wọn fún àwọn ọmọkùnrin yín ní ìyàwó tàbí fún ẹ̀yin tìkáara yín."'), 'Esth$1:5': (None, None, 'Esth$1:5 "Nígbà tí ọjọ́ wọ̀nyí kọjá, ọba se àsè fún ọjọ́ méje, nínú ọgbà tí ó wà nínú àgbàlá ààfin ọba, gbogbo ènìyàn láti orí ẹni tí ó kéré dé orí ẹni tí ó lọ́lá jùlọ, tí wọ́n wà ní ilé ìṣọ́ ti Súsà."'), 'Esth$1:9': (None, 'ọba', 'Esth$1:9 "Ayaba Fásítì náà ṣe àṣè fún àwọn obìnrin ní ààfin ọba Ṣérísésì,"'), 'Esth$1:10': (None, 'ọba', 'Esth$1:10 "Ní ọjọ́ keje, nígbà tí wáìnì mú inú ọba dùn, ó pàṣẹ fún Méhúmínì, Bísítà, Hábónà, Bígítà àti Ábágítà, Ṣétarì àti Kákásì, àwọn ìwẹ̀fà méje tí ń jíṣẹ́ fún un."'), 'Esth$1:12': ('ọba', 'ayaba', 'Esth$1:12 "Ṣùgbọ́n nígbà tí àwọn ìránṣẹ́ jẹ́ iṣẹ́ ọba, ayaba Fásítì kọ̀ láti wá. Nígbà náà ni ọba bínú gidgidi, ìbínú náà sì pọ̀ jọjọ."'), 'Esth$1:13': (None, 'ọba', 'Esth$1:13 "Gẹ́gẹ́ bí ìṣe rẹ̀ ní ìgbà gbogbo, ọba máa ń béèrè ọ̀rọ̀ lọ́wọ́ àwọn tí ó ní ìmọ̀ òfin àti ìdájọ́, ó sọ ọ́ fún àwọn amòye tí wọ́n mòye àkókò,"'), 'Esth$1:16': ('ọba', None, 'Esth$1:16 "Mémúkánì sì dáhùn níwájú ọba àti àwọn ọlọ́lá pé, “Ayaba Fásítì ti ṣe búburú, kì í ṣe sí ọba nìkan Ṣùgbọ́n sí gbogbo àwọn ọlọ́lá àti àwọn ènìyàn tí ó wà ní gbogbo agbégbé ilẹ̀ ọba Ṣérísésì."'), 'Esth$1:17': ('ayaba', 'ọba', 'Esth$1:17 "Nítorí ìwà ayaba yìí yóò tàn dé ọ̀dọ̀ gbogbo àwọn obínrin, tó bẹ́ẹ̀ tí ọkọ wọn yóò di gígàn lójúu wọn, wọn yóò sì sọ pé, ọba Ṣérísésì pàṣẹ̀ pé kí á mú ayaba Fásítì wá ṣíwájú òun, ṣùgbọ́n ó kọ̀ láti wá."'), 'Esth$1:18': ('ayaba', 'ọba', 'Esth$1:18 Ní ọjọ́ yìí gan-an ni àwọn ọlọ́lá obinrin Páṣíà àti ti Médíánì tí wọ́n ti gbọ́ nípa ìwà ayaba wọn yóò ṣe bẹ́ẹ̀ sí gbogbo àwọn ìjòyè ọba bákan náà. Àfojúdi àti àìnírẹ́pọ̀ tí kò lópin yóò wà.'), 'Esth$1:19': ('lọ', 'wá', 'Esth$1:19 "“Nítorí náà, bí ó bá tọ́ lójú ọba, jẹ́ kí ó gbé àṣẹ ọba jáde, kí ó sì jẹ́ kí ó wà ní àkọsílẹ̀ pẹ̀lú òfin Pásíà àti Médíánì, èyí tí kò le é parẹ́, pé kí Fásítì kí ó má ṣe wá ṣíwájú ọba Ṣérísésì. Kí ọba sì fi oyè ayaba rẹ̀ fún ẹlòmíràn tí ó ṣàn jù ú lọ."'), 'Esth$2:2': ('ọba', None, 'Esth$2:2 "Nígbà naà ni ìránṣẹ́ ọba tí ó wà ní ọ̀dọ̀ ọ rẹ̀ wí pé, “jẹ́ kí a wá ọmọbìnrin arẹwà tí kòì mọ ọkùnrin rí fún ọba."'), 'Esth$2:7': ('baba', 'iya', 'Esth$2:7 "Módékáì ní arákùnrin kan ẹni tí orúkọ rẹ̀ ń jẹ́ Hádásà, ẹni tí ó tọ́ dàgbà nítorí tí kò ní bàbá bẹ́ẹ̀ sì ni kò ní ìyá. Obìnrin yìí, ẹni tí a tún mọ̀ sí Ẹ́sítà, ó dára ó sì lẹ́wà, Módékáì mú u gẹ́gẹ́ bí ọmọ rẹ̀ nígbà tí bàbá àti ìyá rẹ̀ ti kú."'), 'Esth$2:12': ('wá', 'lọ', 'Esth$2:12 "Kí àkókò tó tó fún obìnrin kọ̀ọ̀kan láti lọ sí ọ̀dọ̀ ọba Ṣérísésì, ó ní láti lo ohun èlò tí ń mú ara lẹ́wà tí a yàn fún àwọn obìnrin fún oṣù méjìlá, yóò lo òróró òjía fún oṣù mẹ́fà, yóò sì lo ohun èlò olóòrùn dídùn tùràrí àti ìpara fún oṣù mẹ́fà pẹ̀lú."'), 'Esth$2:15': ('lọ', None, 'Esth$2:15 "Nígbà tí ó kan Ẹ́sítà (ọmọbìnrin tí Módékáì gbà ṣe ọmọ, ọmọbìnrin arákùnrin rẹ̀ tí ó ń jẹ́ Ábíháílì) láti lọ sí ọ̀dọ̀ ọba, wọn kò bèrè fún ohunkóhun ju èyí tí Hégáì, ìwẹ̀fà ọba ẹni tí ó jẹ́ olùtọ́jú ilé àwọn obìnrin sọ pé kí ó ṣe lọ. Ẹ́sítà sì rí ojú rere lọ́dọ̀ gbogbo àwọn tí ó rí i."'), 'Esth$2:17': ('ọba', 'ayaba', 'Esth$2:17 "Ésítà sì wu ọba ju àwọn obìnrin tó kù lọ, Ó sì rí ojú rere àti oore ọ̀fẹ́ gbà ju ti àwọn wúndíá tó kù lọ. Nítorí náà ó fi adé ọba dé e ní orí ó sì fi ṣe ayaba dípò Fásítì."'), 'Esth$2:22': ('ayaba', 'ọba', 'Esth$2:22 "Ṣùgbọ́n Módékáì sì mọ̀ nípa ọ̀tẹ̀ náà, ó sọ̀ fún ayaba Ẹ́sítà, Ẹ́sítà sì sọ fún ọba, wọ́n sì fi ọlá fún Módékáì."'), 'Esth$3:13': (None, None, 'Esth$3:13 "A sì fi ìwé náà ránṣẹ́ sí gbogbo àwọn ìgbèríko pẹ̀lú àṣẹ láti parun, láti pa gbogbo àwọn Júù èwe àti àgbà, obìnrin àti àwọn ọmọ wẹ́ẹ́wẹ́—ní ọjọ́ kan kí wọn sì parẹ́, ní ọjọ́ kẹtàlá oṣù kejìlá, oṣù Ádárì kí a sì kó àwọn ohun ìní in wọn."'), 'Esth$4:10': (None, None, 'Esth$4:10 "Nígbà náà ni Ésítà pàṣẹ fún un pé kí ó sọ fún Módékáì,"'), 'Esth$4:15': (None, None, 'Esth$4:15 Nígbà náà ni Ẹ́sítà rán iṣẹ́ yìí sí Módékáì:'), 'Esth$5:2': (None, 'ayaba', 'Esth$5:2 "Nígbà tí ó rí ayaba Ẹ́sítà tí ó dúró nínú àgbàlá, inú un rẹ̀ yọ́ síi, ó sì na ọ̀pá aládé wúrà ọwọ́ọ rẹ̀ sí i. Bẹ́ẹ̀ ni Ẹ́sítà ṣe ṣún mọ́ ọn ó sì fi ọwọ́ kan orí ọ̀pá náà."'), 'Esth$5:3': ('ọba', 'ayaba', 'Esth$5:3 " Nígbà náà ni ọba béèrè pé, “kí ni ó dé, ayaba Ẹ́sítà? Kí ni o fẹ́? Bí ó tilẹ̀ ṣe títí dé ìdajìn mi ìjọba àní, a ó fi fún ọ.”"'), 'Esth$5:12': ('ayaba', 'ọba', 'Esth$5:12 "Hámánì tún fi kún-un pé, “Kìí ṣe èyí nìkan. Èmi nìkan ni ayaba Ẹ́sítà pè láti sin ọba wá sí ibi àsè tí ó sè. Bákan náà, ó sì tún ti pè mí pẹ̀lú ọba ní ọ̀la."'), 'Esth$7:1': (None, 'ayaba', 'Esth$7:1 "Ọba àti Hámánì sì lọ sí àpèjẹ pẹ̀lú Ẹ́sítà ayaba,"'), 'Esth$7:2': ('ọba', 'ayaba', 'Esth$7:2 " Bí wọ́n sì ṣe ń mu wáìnì ní ọjọ́ kejì yìí, ọba sì tún béèrè pé, “Ẹ́sítà ayaba, kí ni ẹ̀bẹ̀ rẹ? A ó sì fi fún ọ. Kí ni ìbéèrè rẹ? Bí ó ti lẹ̀ jẹ́ ìdajì ìjọbaà mi, n ó fi fún ọ.”"'), 'Esth$7:3': ('ayaba', 'ọba', 'Esth$7:3 "Nígbà náà ni ayaba Ẹ́sítà dáhùn, “Bí èmi bá rí ojú reree rẹ, ìwọ ọba, bí ó bá sì tẹ́ ọláà ńlá à rẹ lọ́rùn, fún mi ní ẹ̀míì mi-èyí ni ẹ̀bẹ̀ mi. Kí o sì pa àwọn ènìyàn mi mọ́-èyí ni ìbéèrè mi."'), 'Esth$7:5': ('ayaba', None, 'Esth$7:5 "Ọba Ṣéríṣésì sì bi Ésítà ayaba léèrè pé, “ta a ni ẹni náà? Níbo ni ẹni náà wà tí kò bẹ̀rù láti ṣe irú nǹkan bẹ́ẹ̀?”"'), 'Esth$7:7': ('ayaba', 'ọba', 'Esth$7:7 "Ọba sì dìde pẹ̀lú ìbínú, ó fi wáìnì sílẹ̀ ó sì jáde lọ sí àgbàlá ààfin. Ṣùgbọ́n nígbà tí Hámánì, ti ríi dájú pé ọba ti pinnu láti pa òun, ó dúró lẹ́yìn láti bẹ Ẹ́sítà ayaba nítorí ẹ̀míi rẹ̀."'), 'Esth$8:1': ('ayaba', 'ọba', 'Esth$8:1 "Ní ọjọ́ kan náà ni ọba Ṣérísésì fún Ésítà ayaba ní ilée Hámánì, ọ̀ta àwọn Júù. Módékáì sì wá síwájú ọba, nítorí Ẹ́sítà ti sọ bí ó ṣe jẹ́ sí ọba."'), 'Esth$8:7': ('ayaba', None, 'Esth$8:7 "Ọba Ṣéríṣésì dá Ẹ́sítà ayaba àti Módékáì aráa Júù náà lóhùn pé, “Nítorí Hámánì kọ lu àwọn aráa Júù, èmi ti fi ilée rẹ̀ fún Ẹ́sítà, wọ́n sì ti ṣo ó kọ́ sórí igi."'), 'Esth$9:12': ('ayaba', 'ọba', 'Esth$9:12 "Ọba sì sọ fún Ẹ́sítà ayaba pé, “Àwọn Júù ti pa ẹ̀ẹ́dẹ̀gbẹ̀ta (500) ọkùnrin àti àwọn ọmọkùnrin mẹ́wẹ̀ẹ̀wá tí i ṣe ọmọ Hámánì ní ilé ìṣọ́ Ṣúṣà run. Kí ni wọ́n ṣe ní gbogbo ìgbèríko ọba tó kù? Báyìí kí ni ẹ̀bẹ̀ rẹ? A ó fi fún ọ. Kí ni ìbéèrè rẹ? A ó sì tún fi fún ọ.”"'), 'Esth$9:16': (None, None, 'Esth$9:16 "Lákókò yìí, àwọn tó kù nínú àwọn Júù tí wọ́n wà ní agbégbé ọba náà tún kó ara wọn jọ láti dáàbò bo ara wọn kí wọn sì sinmi lọ́wọ́ àwọn ọ̀tá a wọn. Wọ́n sì pa ẹgbàá mẹ́tadínlógójì ó lé ẹgbẹ̀rin (75,000) àwọn tí ó kóòríra wọn ṣùgbọ́n wọn kò fi ọwọ́ọ wọn lé ìkógún un wọn."'), 'Esth$9:20': (None, None, 'Esth$9:20 "Módékáì ṣe àkọsílẹ̀ àwọn ìṣẹ̀lẹ̀ wọ̀nyí, ó sì kọ ìwé ránṣẹ́ sí gbogbo àwọn Júù jákèjádò àgbáyé ọba Ṣéríṣésì, tí ó wà ní tòòsí àti àwọn tí ó wà ní jìnnà réré,"'), 'Job$3:2': (None, None, 'Job$3:2 "Jóòbù sọ, ó sì wí pé:"'), 'Job$3:6': ('ọjọ', None, 'Job$3:6 "Kí òkùnkùn kí ó ṣú bo òru náà biribiri, kí ó má ṣe yọ pẹ̀lú ọjọ́ ọdún náà: kí ó má ṣe kà a mọ́ iye ọjọ́ oṣù."'), 'Job$3:9': ('ṣokunkun', 'imole', 'Job$3:9 "Kí ìràwọ̀ òféfé ọjọ́ rẹ̀ kí ó ṣókùnkùn; kí ó má wá ìmọ́lẹ̀, ṣùgbọ́n kí ó máa mọ́ síi, bẹ́ẹ̀ ni kí ó má ṣe rí àfẹ̀rẹ̀mọ́jú mọ́"'), 'Job$5:8': (None, None, 'Job$5:8 "“Sọ́dọ̀ Ọlọ́run ní èmi lè máa ṣe àwárí, ní ọwọ́ Ọlọ́run ní èmi lè máa fi ọ̀rọ̀ mi lé."'), 'Job$5:14': (None, 'alẹ', 'Job$5:14 Wọ́n sáre wọ inú òkùnkùn ní ọ̀sán wọ́n sì fọwọ́ tálẹ̀ ní ọ̀sán gangan bí ẹni pé ní òru.'), 'Job$7:4': (None, 'dide', 'Job$7:4 "Nígbà ti mo dùbúlẹ̀, ‘Èmi wí pé nígbà wo ni èmi ó dìde?’ Tí òru yóò sì kọjá? Ó sì tó fún mi láti yí síhìn-ín yí sọ́hùn-ún Títí yóò fi di ògúlùtu erùpẹ̀ ti a fi wọ̀ mí ní aṣọ, àwọ̀ mi bù, ó sì di sísun ni."'), 'Job$8:7': (None, None, 'Job$8:7 "Ìpilẹsẹ̀ rẹ ìbá tilẹ̀ kéré si í, bẹ́ẹ̀ ìgbẹ̀yin rẹ ìbá pọ̀ sí i gidigidi. "'), 'Job$11:15': (None, None, 'Job$11:15 "Nígbà náà ni ìwọ ó gbé ojú rẹ sókè láì ní àbàwọ́n, àní ìwọ yóò dúró ṣinṣin, ìwọ kì yóò sì bẹ̀rù,"'), 'Job$12:22': ('okunkun', 'imole', 'Job$12:22 "Ó hú ìdí ohun ìjìnlẹ̀ jáde láti inú òkùnkùn wá, Ó sì mú òjìji ikú wá sínú ìmọ́lẹ̀."'), 'Job$12:25': ('imole', None, 'Job$12:25 "Wọn a máa fi ọwọ́ ta ilẹ̀ nínú òkùnkùn níbi tí kò sí ìmọ́lẹ̀, Òun a sì máa mú wọn tàsé ìrìn bí ọ̀mùtí."'), 'Job$14:4': ('mimọ', None, 'Job$14:4 Ta ni ó lè mú ohun mímọ́ láti inú àìmọ́ jáde wá? Kò sí ẹnìkan!'), 'Job$16:22': ('lọ', None, 'Job$16:22 "“Nítorí nígbà tí iye ọdun díẹ̀ rékọjá tán, nígbà náà ni èmi ó lọ sí ibi tí èmi kì yóò padà bọ̀."'), 'Job$17:12': (None, None, 'Job$17:12 "Àwọn ènìyàn wọn yí ìsọ́ òru di ọ̀sán; wọ́n ní, ìmọ́lẹ̀ súnmọ́ ibi tí òkùnkùn dé."'), 'Job$17:14': ('baba', 'iya', 'Job$17:14 "Èmi ti wí fún ìdibàjẹ́ pé, ìwọ ni baba mi, àti fún kòkòrò pé, ìwọ ni ìyá mi àti arábìnrin mi,"'), 'Job$18:6': (None, None, 'Job$18:6 "Ìmọ́lẹ̀ yóò di òkùnkùn nínú àgọ́ rẹ̀, fìtílà ẹ̀gbẹ́ rẹ̀ ni a ó sì pa pẹ̀lú."'), 'Job$18:18': ('okunkun', 'imole', 'Job$18:18 "A ó sì lée láti inú ìmọ́lẹ̀ sí inú òkùnkùn, a ó sì lé e kúrò ní ayé."'), 'Job$18:20': (None, 'wá', 'Job$18:20 "Ẹnu yóò ya àwọn ìran ti ìwọ̀ oòrùn sí ìgbà ọjọ́ rẹ̀, gẹ́gẹ́ bí ẹ̀rù ìwárìrì ti í bá àwọn ìran ti ìlà oòrùn."'), 'Job$23:8': ('lọ', 'wá', 'Job$23:8 "“Sì wòó, bí èmi bá lọ sí iwájú, òun kò sí níbẹ̀, àti sí ẹ̀yìn, èmi kò sì rí òye rẹ̀:"'), 'Job$24:16': ('imole', None, 'Job$24:16 "Ní òkùnkùn wọn á rúnlẹ̀ wọlé, tí wọ́n ti fi ojú sọ fún ara wọn ní ọ̀sán, wọn kò mọ̀ ìmọ́lẹ̀."'), 'Job$24:21': (None, None, 'Job$24:21 Ẹni tí o hù ìwà búburú sí àgàn tí kò ṣe rere sí opó.'), 'Job$26:10': (None, None, 'Job$26:10 "Ó fi idẹ yí omi òkun ká, títí dé ààlà ìmọ́lẹ̀ àti òkùnkùn."'), 'Job$29:1': (None, None, 'Job$29:1 "Pẹ̀lúpẹ̀lù, Jóòbù sì tún tẹ̀ ṣíwájú nínú ọ̀rọ̀ rẹ̀, ó sì wí pé:"'), 'Job$30:26': ('okunkun', 'imole', 'Job$30:26 "Nígbà tí mo fojú sọ́nà fún àlàáfíà, ibi sì dé; nígbà tí mo dúró de ìmọ́lẹ̀, òkùnkùn sì dé."'), 'Job$31:16': (None, None, 'Job$31:16 "“Bí mo bá fa ọwọ́ sẹ́yìn fún ìfẹ́ inú talákà, tàbí bí mo bá sì mú kí ojú opó di aláìríran,"'), 'Job$32:6': (None, None, 'Job$32:6 "Élíhù, ọmọ Bárákélì, ará Búsì, dáhùn ó sì wí pé: ọmọdé ní èmi àgbà sì ní ẹ̀yin; Ǹjẹ́ nítorí náà ní mo dúró mo sì ń bẹ̀rù láti fi ìmọ̀ mi hàn yin."'), 'Job$32:22': (None, None, 'Job$32:22 "Nítorí pé èmí kò mọ̀ ọ̀rọ̀ ìpọ́nni í sọ; ní ṣíṣe bẹ́ẹ̀, Ẹlẹ́dàá mi yóò mú mi kúrò lọ́gán."'), 'Job$34:19': ('ọlọrọ', 'talaka', 'Job$34:19 Ańbọ̀tórí fún ẹni tí kì í ṣójúṣàájú àwọn ọmọ-aládé tàbí tí kò kà ọlọ́rọ̀ sí talákà lọ. Nítorí pé iṣẹ́ ọwọ́ rẹ̀ ni gbogbo wọn í ṣe?'), 'Job$37:9': (None, None, 'Job$37:9 "Láti ìhà gúsù ni ìjì àjàyíká tí jáde wá, àti òtútù láti inú afẹ́fẹ́ ti tú àwọ̀sánmọ̀ ká."'), 'Job$41:22': ('ayo', 'ibanujẹ', 'Job$41:22 "Ní ọrún rẹ̀ ní agbára kù sí, àti ìbànújẹ́ àyà sì padà di ayọ̀ níwájú rẹ̀."'), 'Job$42:12': (None, None, 'Job$42:12 "Bẹ́ẹ̀ ni OLÚWA bùkún ìgbẹ̀yìn Jóòbù ju ìsáá jú rẹ̀ lọ; ẹgbàá-méje àgùntàn, ẹgbàá-mẹ́ta ràkùnmí, àti ẹgbẹ̀rún àjàgà ọdá-màlúù, àti ẹgbẹ̀rún abo kẹ́tẹ́kẹ́tẹ́."'), 'Job$42:14': ('keji', None, 'Job$42:14 "Ó sì sọ orúkọ àkọ̀bí ní Jẹ́mímà, àti orúkọ èkejì ni Késíà àti orúkọ ẹ̀kẹta ní Kérén-hápúkì."'), 'Ps$1:2': (None, None, 'Ps$1:2 Ṣùgbọ́n ayọ̀ inú Rẹ̀ wà nínú òfin OLÚWA àti nínú òfin Rẹ̀ ni ó ń ṣe àṣàrò ní ọ̀sán àti òru.'), 'Ps$18:28': ('okunkun', 'imole', 'Ps$18:28 "Ìwọ, OLÚWA, jẹ́ kí fìtílà mi kí ó máa tàn; Ọlọ́run mi, yí òkùnkùn mi padà sí ìmọ́lẹ̀. "'), 'Ps$18:38': ('ṣubu', 'dide', 'Ps$18:38 Èmi ṣá wọn ní ọgbẹ́ tí wọn ko fi le è dìde; Wọ́n subú ní abẹ́ ẹsẹ̀ mi.'), 'Ps$19:2': ('ọjọ', 'alẹ', 'Ps$19:2 Wọ́n ń fọhùn ní ọjọ́ dé ọjọ́; wọ́n ń fi ìmọ̀ hàn ní alẹ́ dé alẹ́.'), 'Ps$22:2': (None, None, 'Ps$22:2 "Ọlọ́run mi, ní ọ̀sán gangan ni mo kígbe, ṣùgbọ́n ìwọ kò dá mi ní ohùn: àti ní òru èmi kò dákẹ́. "'), 'Ps$25:20': (None, 'jẹ ki', 'Ps$25:20 "Pa ọkàn mi mọ́, kí o sì gbà mí sílẹ̀; Má ṣe jẹ́ kí a fi mí sínú ìtìjú, nítorí pé ìwọ ni ibi ìsádi mi."'), 'Ps$27:10': ('baba', 'iya', 'Ps$27:10 "Bí ìyá àti bàbá bá kọ̀ mí sílẹ̀, OLÚWA yóò tẹ́wọ́ gbà mí."'), 'Ps$32:4': (None, None, 'Ps$32:4 Nítorí pé ni ọ̀sán àti ní òru ọwọ́ Rẹ̀ wúwo sími lára; agbára mi gbẹ tán gẹ́gẹ́ bí ooru ẹ̀ẹ̀rùn Sela'), 'Ps$34:14': ('ibi', None, 'Ps$34:14 "Yà kúrò nínú ibi kí o sì ṣe rere; wá àlàáfíà, kí o sì lépa Rẹ̀. "'), 'Ps$35:12': (None, None, 'Ps$35:12 Wọ́n fi búburú san ire fún mi; láti sọ ọkàn mi di òfo.'), 'Ps$36:4': ('ibi', 'dara', 'Ps$36:4 Wọ́n gbìmọ̀ ìwà ìkà nígbà tí wọ́n wà lórí ibùsùn wọn: wọ́n gba ọ̀nà tí kò dára: wọn kò sì kọ ọ̀nà ibi sílẹ̀.'), 'Ps$36:12': ('ṣubu', 'dide', 'Ps$36:12 "Níbẹ̀ ni àwọn oníṣẹ́ ẹ̀ṣẹ̀ gbé subú sí: a Rẹ̀ wọ́n sílẹ̀, wọn kì yóò le è dìde!"'), 'Ps$37:25': (None, 'odo', 'Ps$37:25 "Èmi ti wà ni èwe, báyìí èmí sì dàgbà; ṣíbẹ̀ èmi kò ì tíi rí ki a kọ olódodo ṣílẹ̀, tàbí kí irú ọmọ Rẹ̀ máa ṣagbe oúnjẹ."'), 'Ps$37:27': ('ibi', None, 'Ps$37:27 "Lọ kúrò nínú ibi, kí o sì máa ṣe rere; nígbà náà ni ìwọ gbé ní ilẹ̀ náà títí láéláé."'), 'Ps$38:20': ('ibi', None, 'Ps$38:20 Àwọn tí wọn ń fi ibi san rere fún mi àwọn ni ọ̀ta mi nítorí pé mò ń tọ ire lẹ́yìn.'), 'Ps$42:3': ('ọjọ', None, 'Ps$42:3 "Oúnjẹ mi ní omijé mi ní ọ̀sán àti ní òru, nígbà tí àwọn ènìyàn ń wí fún mi ní gbogbo ọjọ́, “Ọlọ́run Rẹ̀ dà?”"'), 'Ps$42:8': (None, 'alẹ', 'Ps$42:8 "Ní ọ̀sán ní OLÚWA ran ìfẹ́ Rẹ̀, àti ni àṣálẹ́ ni orin Rẹ̀ wà pẹ̀lú mi àdúrà sí Ọlọ́run ayé mi."'), 'Ps$49:1': (None, None, 'Ps$49:1 "Ẹ gbọ́ èyí, gbogbo ènìyàn! Ẹ fi etí sí i, gbogbo ẹ̀yin aráyé"'), 'Ps$52:3': (None, 'ibi', 'Ps$52:3 "Ìwọ fẹ́ràn ibi ju ire lọ, àti èké ju kí ó sọ òtítọ́ lọ."'), 'Ps$54:3': (None, None, 'Ps$54:3 Àwọn àlejò ń dìde sí mí. Àwọn aláìláàánú ènìyàn ń wá ayé mi àwọn ẹni tí kò ka Ọlọ́run sí ní ojú wọn.'), 'Ps$59:10': ('jẹ ki', None, 'Ps$59:10 Ọlọ́run àánú mi ni yóò ṣáájú mi. Ọlọ́run yóò sì jẹ́ kí èmi rí ìfẹ́ mi lára àwọn ọ̀tá mi. Yóò sì jẹ́ kí ń yọ ayọ̀ ìṣẹ́gún lórí àwọn ọ̀tá ìfẹ́ àwọn ọ̀ta mi.'), 'Ps$62:4': (None, 'egún', 'Ps$62:4 "Kìkì èró wọn ni láti bì ṣubú kúrò nínú ọlá Rẹ̀; inú wọn dùn sí irọ́. Wọn ń fi ẹnu wọn súre, ṣùgbọ́n wọ́n ń gégùn nínú ọkàn wọn. Sela "'), 'Ps$62:9': (None, None, 'Ps$62:9 "Ní tòótọ́, asán ní àwọn ọmọ ènìyàn, èké si ni àwọn olóyè, wọn gòkè nínú ìwọ̀n bákan náà ni wọn fẹ́rẹ̀ jù asán lọ lápapọ̀ wọn jẹ èémí."'), 'Ps$74:16': (None, None, 'Ps$74:16 "Tìrẹ ni ọ̀sán, tìrẹ ni òru pẹ̀lú; ìwọ yà oòrùn àti òsùpá."'), 'Ps$77:2': (None, None, 'Ps$77:2 "Nígbà tí mo wà nínú ìpọ́njú, mo wá OLÚWA; ní òru ni mo na ọwọ́ mi jáde ní àìkáárẹ̀ ọkàn mí sì kọ láti tùú nínú. "'), 'Ps$78:14': ('ọsan', None, 'Ps$78:14 "Ní ọ̀ṣán ó fi ìkúùku àwọsánmọ̀ ṣe amọ̀nà wọn àti ní gbogbo òru pẹ̀lú, ìmọ̀lẹ̀ ìná."'), 'Ps$79:8': ('jẹ ki', None, 'Ps$79:8 "Má ṣe ka ẹ̀ṣẹ̀ àwọn baba wa sí wa lọ́rùn jẹ́ kí àánú Rẹ yà kánkán láti bá wa, nítorí ti a Rẹ̀ wá sílẹ̀ gidigidi. "'), 'Ps$86:14': (None, None, 'Ps$86:14 "Àwọn onígbéraga ń dojú kọ mí, Ọlọ́run; àti ìjọ̀ àwọn alágbára ń wá ọkàn mi kiri, wọn kò sì fi ọ pè."'), 'Ps$88:1': (None, None, 'Ps$88:1 "OLÚWA, Ọlọ́run tí o gba mí là, ní ọ̀sán àti ní òru ni mo kígbe sókè sí Ọ."'), 'Ps$89:12': (None, None, 'Ps$89:12 Gúṣù àti Àríwá ìwọ ní ó dá wọn; Taborí àti Hámónì ń fi ayọ̀ yìn orúkọ Rẹ.'), 'Ps$89:30': (None, None, 'Ps$89:30 “Bí àwọn ọmọ Rẹ̀ bá kọ òfin mi sílẹ̀ tí wọ́n kò sì rìn gẹ́gẹ́ bí ìlànà mi.'), 'Ps$91:5': (None, None, 'Ps$91:5 "Ìwọ kì yóò bẹ̀rù nítorí ẹ̀rù òru, tàbí fún ọfà tí ń fò ní ọ̀sán,"'), 'Ps$97:10': (None, None, 'Ps$97:10 "Jẹ kí gbogbo àwọn tí ó fẹ́ OLÚWA, kórira ibi, ó pa ọkan àwọn ènìyàn mímọ́ Rẹ̀ mọ́ ó gbà wọ́n ní ọwọ́ àwọn ènìyàn búburú."'), 'Prov$1:8': ('baba', None, 'Prov$1:8 "Tẹ́tí, ìwọ ọmọ mi sí ẹ̀kọ́ baba rẹ má ṣe kọ ẹ̀kọ́ màmá ọ̀ rẹ sílẹ̀"'), 'Prov$2:10': (None, None, 'Prov$2:10 Nítorí ọgbọ́n yóò wọ inú ọkàn rẹ ìmọ̀ yóò sì jẹ́ ìtura fún ọkàn rẹ'), 'Prov$3:1': (None, None, 'Prov$3:1 "Ọmọ mi, má ṣe gbàgbé ẹ̀kọ́ mi. Ṣùgbọ́n pa òfin mi mọ́ sí ọkàn rẹ."'), 'Prov$3:16': ('osi', 'ọtun', 'Prov$3:16 Ẹ̀mí gígùn ń bẹ ní ọwọ́ ọ̀tún rẹ̀; ní ọwọ́ òsì rẹ̀ sì ni ọrọ̀ àti ọlá.'), 'Prov$3:21': (None, 'jẹ ki', 'Prov$3:21 "Ọmọ mi pa ìdájọ́ tí ó yè kooro mọ́ àti ìmọ̀yàtọ̀, má jẹ́ kí wọn lọ kúrò ní ibi tí ojú rẹ ti le tó wọn"'), 'Prov$4:3': ('baba', 'iya', 'Prov$4:3 "Nígbà tí mo jẹ́ ọ̀dọ́mọkùnrin ní ilé baba à mí, mo jẹ́ èwe, tí mo sì jẹ́ ọ̀kanṣoṣo lọ́wọ́ ìyá mi"'), 'Prov$4:13': ('tọju', 'jẹ ki', 'Prov$4:13 "Di ẹ̀kọ́ mú, má ṣe jẹ́ kí ó lọ; tọ́jú u rẹ̀ dáradára Nítorí òun ni ìyè rẹ."'), 'Prov$4:27': ('osi', 'ọtun', 'Prov$4:27 má ṣe yà sọ́tùn-ún tàbí ṣósì pa ẹṣẹ̀ rẹ mọ́ kúrò nínú ibi.'), 'Prov$5:8': (None, 'jinna', 'Prov$5:8 Rìn ní ọ̀nà tí ó jìnnà síi dáadáa má ṣe súnmọ́ ẹnu ọ̀nà ilé rẹ̀'), 'Prov$6:1': (None, None, 'Prov$6:1 "Ọmọ mi, bí ìwọ bá ṣe oníduúró fún aládùúgbò rẹ bí ìwọ bá ti bọwọ́ ní ìlérí májẹ̀mu pẹ̀lú ẹlòmíràn"'), 'Prov$6:3': (None, 'lọ', 'Prov$6:3 "Nígbà náà, ṣe èyí, ìwọ ọmọ mi, láti gba ara rẹ níwọ̀n bí o ti kó sọ́wọ́ aládùúgbò rẹ: lọ kí o sì rẹ ara rẹ sílẹ̀; bẹ aládùúgbò rẹ dáadáa"'), 'Prov$6:20': ('baba', 'iya', 'Prov$6:20 "Ọmọ mi, pa àṣẹ baba rẹ mọ́ má sì ṣe kọ ẹ̀kọ́ ìyá rẹ sílẹ̀."'), 'Prov$6:26': (None, None, 'Prov$6:26 nítorí Aṣẹ́wó yóò sọ ọ́ di àkàrà lásánlàsàn ẹ̀mí rẹ gan an sì ni alágbérè ń dọdẹ.'), 'Prov$7:6': (None, None, 'Prov$7:6 Ní ojú fèrèsé ilé è mi mo wo ìta láti ojú ù fèrèsé.'), 'Prov$7:19': ('ti lọ', None, 'Prov$7:19 Ọkọ ọ̀ mi ò sí nílé; ó ti lọ sí ìrìnàjò jínjìn.'), 'Prov$8:36': (None, None, 'Prov$8:36 Ṣùgbọ́n ẹnikẹ́ni tí ó bá kọ̀ láti rí ń pa ara rẹ̀ lára gbogbo ẹni tí ó kórira mi fẹ́ ikú.”'), 'Prov$9:8': (None, None, 'Prov$9:8 "Má ṣe bá ẹlẹ́gàn wí, àìṣebẹ́ẹ̀ yóò kórìíra rẹ. Bá Ọlọgbọ́n wí yóò sì fẹ́ràn rẹ;"'), 'Prov$10:4': ('ọlọrọ', 'talaka', 'Prov$10:4 "Ọwọ́ tí ó lẹ máa ń sọ ènìyàn di talákà, ṣùgbọ́n ọwọ́ tí ó múra síṣẹ́ a máa sọni di ọlọ́rọ̀. "'), 'Prov$10:15': (None, None, 'Prov$10:15 "Ọrọ̀ àwọn Olódodo ni ìlú olódi wọn, ṣùgbọ́n òsì ni ìparun aláìní. "'), 'Prov$11:16': (None, None, 'Prov$11:16 Obìnrin oníwà rere gba ìyìn ṣùgbọ́n alágbára aláìbìkítà ènìyàn gba ọrọ̀ nìkan.'), 'Prov$11:26': ('ibukun', None, 'Prov$11:26 Àwọn ènìyàn a ṣépè lé ènìyàn tí ń kó oúnjẹ pamọ́ ṣùgbọ́n ìbùkún a máa wá sórí ẹni tí ó ṣetán láti tà.'), 'Prov$13:7': (None, 'talaka', 'Prov$13:7 "Ènìyàn kan díbọ́n bí ẹni tí ó ní ọrọ̀ síbẹ̀ kò ní nǹkankan ẹlòmííràn díbọ́n bí i talákà, síbẹ̀ ó ní ọrọ̀ púpọ̀. "'), 'Prov$13:8': (None, 'talaka', 'Prov$13:8 Ọrọ̀ ènìyàn le è ra ẹ̀mí rẹ̀ ṣùgbọ́n talákà kì í gbọ́ ìdẹ́rùbà.'), 'Prov$13:11': (None, None, 'Prov$13:11 "Owó tí a fi ọ̀nà èrú kó jọ yóò sí lọ, ṣùgbọ́n ẹni tí ń kó owó jọ díẹ̀ díẹ̀ yóò pọ̀ síi. "'), 'Prov$14:1': (None, None, 'Prov$14:1 "Ọlọ́gbọ́n obìnrin kọ́ ilé e rẹ̀, ṣùgbọ́n aláìgbọ́n obìnrin fi ọwọ́ ara rẹ̀ wó o. "'), 'Prov$14:3': ('aṣiwère', 'ọlọgbọn', 'Prov$14:3 "Ọ̀rọ̀ aṣiwèrè a máa ṣokùnfà pàsán fún ẹ̀yìn rẹ̀, ṣùgbọ́n ọ̀rọ̀ ètè ọlọ́gbọ́n a máa dáàbò bò ó. "'), 'Prov$14:8': (None, 'wère', 'Prov$14:8 Ọgbọ́n olóye ni láti ronú jinlẹ̀ nípa ọ̀nà an wọn ṣùgbọ́n ìwà òmùgọ̀ asìwèrè ni ìtànjẹ.'), 'Prov$14:19': ('ibi', None, 'Prov$14:19 Ènìyàn ìkà yóò tẹríba níwájú àwọn ènìyàn rere àti ènìyàn búburú níbi ìlẹ̀kùn àwọn olódodo.'), 'Prov$14:20': (None, 'talaka', 'Prov$14:20 "Kódà àwọn aládùúgbò o talákà kò fẹ́ràn rẹ̀ ṣùgbọ́n, Ọlọ́rọ̀ ní ọ̀rẹ́ púpọ̀. "'), 'Prov$15:3': ('ibi', None, 'Prov$15:3 "Ojú OLÚWA wà níbi gbogbo, Ó ń wo àwọn ẹni búburú àti àwọn ẹni rere. "'), 'Prov$15:7': (None, None, 'Prov$15:7 Ètè olódodo ń tan ìmọ̀ kalẹ̀; ṣùgbọ́n kò rí bẹ́ẹ̀ fún ọkàn aláìgbọ́n.'), 'Prov$15:20': ('baba', None, 'Prov$15:20 "Ọlọgbọ́n ọmọ mú inú baba rẹ̀ dùn, ṣùgbọ́n aláìgbọ́n ọmọ kẹ́gàn baba rẹ̀. "'), 'Prov$16:19': ('onirẹlẹ', None, 'Prov$16:19 ó sàn láti jẹ́ onírẹ̀lẹ̀ ọkàn láàrin àwọn olùpọ́njú jù láti máa pín ìpín pẹ̀lú àwọn agbéraga.'), 'Prov$17:13': ('ibi', None, 'Prov$17:13 "Bí ènìyàn kan bá fi ibi san ire, ibi kì yóò kúrò nílé rẹ̀ láéláé. "'), 'Prov$17:21': ('ayo', 'ibanujẹ', 'Prov$17:21 Láti bí aláìgbọ́n lọmọ a máa fa ìbànújẹ́ ọkàn kò sí ayọ̀ fún baba ọmọ tí kò gbọ́n.'), 'Prov$18:23': ('ọlọrọ', None, 'Prov$18:23 "Tálákà ń bẹ̀bẹ̀ fún àánú, Ṣùgbọ́n ọlọ́rọ̀ a dáhùn pẹ̀lú ìkanra. "'), 'Prov$19:26': ('baba', 'iya', 'Prov$19:26 "Ẹni tí ó já baba rẹ̀ lulẹ̀, tí ó sì lé ìyá rẹ̀ jáde ó jẹ́ adójútini ọmọ. "'), 'Prov$20:20': ('baba', 'iya', 'Prov$20:20 "Bí ènìyàn kan bá ṣépè fún baba tàbí ìyá rẹ̀, ìmọ́lẹ̀ rẹ̀ ni a ó pa kú nínú òkùnkùn biribiri. "'), 'Prov$20:21': (None, 'ibere', 'Prov$20:21 Ogún tí a kó jọ kíákíá ní ìbẹ̀rẹ̀ kì yóò ní ìbùkún ní ìgbẹ̀yìn gbẹ́yín.'), 'Prov$20:29': (None, 'odo', 'Prov$20:29 Ògo ọ̀dọ́mọkùnrin ni agbára wọn jẹ́ ewú orí ni iyì arúgbó.'), 'Prov$21:17': (None, 'talaka', 'Prov$21:17 "Ẹni tí ó bá fẹ́ afẹ́, yóò di talákà: ẹni tí ó fẹ́ ọtí-wáìnì pẹ̀lú òróró kò le lọ́rọ̀. "'), 'Prov$21:20': ('aṣiwère', 'ọlọgbọn', 'Prov$21:20 Ìṣúra iyebíye àti òróró wà ní ibùgbé ọlọ́gbọ́n; ṣùgbọ́n ènìyàn aṣiwèrè (òmùgọ̀) jẹ ẹ́ run.'), 'Prov$22:2': (None, 'talaka', 'Prov$22:2 Ọlọ́rọ̀ àti tálákà péjọ pọ̀: OLÚWA ni ẹlẹ́dàá gbogbo wọn.'), 'Prov$22:7': (None, None, 'Prov$22:7 "Ọlọ́rọ̀ ṣe olórí olùpọ́njú, ajigbésè sì ṣe ìránṣẹ́ fún ẹni tí a jẹ ní gbèsè. "'), 'Prov$22:16': ('ọrọ', 'talaka', 'Prov$22:16 "Ẹni tó ń ni talákà lára láti ní ọrọ̀, tí ó sì ń ta ọlọ́rọ̀ lọ́rẹ, yóò di aláìní bí ó ti wù kó rí. Gbọ́ ọ̀rọ̀ ọlọ́gbọ́n ènìyàn."'), 'Prov$23:22': ('baba', 'iya', 'Prov$23:22 "Fetí sí ti baba rẹ tí ó bí ọ, má sì ṣe gan ìyá rẹ, nígbà tí o bá gbó"'), 'Prov$23:25': ('baba', 'iya', 'Prov$23:25 "Jẹ́ kí bàbá rẹ àti ìyá rẹ ó yọ̀, sì jẹ́ kí inú ẹni tí ó bí ọ dùn. "'), 'Prov$25:21': (None, None, 'Prov$25:21 " Bí ebi bá ń pa ọ̀tá rẹ, fún un ní oúnjẹ jẹ; bí òrùngbẹ bá ń gbẹ ẹ́, fún un ní omi mú."'), 'Prov$27:6': ('ọtá', 'ọrẹ', 'Prov$27:6 Ọgbẹ́ láti ọ̀dọ̀ ọ̀rẹ́ ṣe é gbẹ́kẹ̀lé ṣùgbọ́n ọ̀tá máa ń fẹnu-koni-lẹ́nu púpọ̀.'), 'Prov$28:6': ('ọlọrọ', 'talaka', 'Prov$28:6 Ó sàn láti jẹ́ talákà tí ìrìn rẹ̀ jẹ́ aláìlábùkù ju ọlọ́rọ̀ tí ọ̀nà rẹ̀ rí pálapàla.'), 'Prov$28:10': (None, None, 'Prov$28:10 Ẹni tí ó mú olódodo rìn ọ̀nà búburú yóò bọ́ sínú pàkúté ara rẹ̀ ṣùgbọ́n aláìlẹ́gàn yóò gba ogún rere.'), 'Prov$28:11': (None, 'talaka', 'Prov$28:11 "Ọlọ́rọ̀ ènìyàn le è gbọ́n lójú ara rẹ̀ ṣùgbọ́n talákà tí ó ní òye rí ìdí, rẹ̀. "'), 'Prov$28:24': ('baba', 'iya', 'Prov$28:24 Ẹni tí ó ja baba tàbí ìyá rẹ̀ lólè tí ó sì wí pé “Kì í ṣe ẹ̀ṣẹ̀” irú kan ni òun àti ẹni tí ń panírun.'), 'Prov$28:27': ('ni', 'aini', 'Prov$28:27 Ẹni tí ó ń fifún talákà kì yóò ṣe aláìní ohunkóhun ṣùgbọ́n ẹni tí ó di ojú rẹ̀ sí wọn gba ọ̀pọ̀lọpọ̀ ègún.'), 'Prov$29:9': (None, 'ọlọgbọn', 'Prov$29:9 "Bí ọlọ́gbọ́n ènìyàn bá lọ sí ilé ẹjọ́ pẹ̀lú aláìgbọ́n aláìgbọ́n a máa bínú a sì máa jà, kò sì ní sí àlàáfíà. "'), 'Prov$29:27': (None, None, 'Prov$29:27 Olódodo kórìíra àwọn aláìsòótọ́: ènìyàn búburú kórìíra olódodo.'), 'Prov$30:11': ('baba', 'iya', 'Prov$30:11 “Àwọn kan wá ṣépè fún àwọn baba wọn tí wọn kò sì ṣúre fún àwọn ìyá wọn:'), 'Prov$30:17': ('baba', 'iya', 'Prov$30:17 "“Ojú tí ń fi baba ṣẹ̀fẹ̀, tí ó kẹ́gàn ìgbọràn sí ìyá ẹyẹ àkálá ẹ̀bá odò ni yóò yọ ọ́, igún yóò mú un jẹ. "'), 'Prov$31:7': ('ranti', 'gbagbe', 'Prov$31:7 Jẹ́ kí wọn mu ọtí kí wọn sì gbàgbé òsì wọn kí wọn má sì rántí òsì wọn mọ́.'), 'Eccl$1:15': (None, None, 'Eccl$1:15 "Ohun tí ó ti wọ́ kò le è ṣe é tọ́ mọ́, ohun tí kò sí kò le è ṣe é kà."'), 'Eccl$1:17': ('ọgbọn', None, 'Eccl$1:17 "Nígbà náà ni mo fi ara jìn láti ní ìmọ̀ nípa ọgbọ́n, àti pàápàá àìgbọ́n àti àgọ̀, ṣùgbọ́n mo rí, wí pé èyí pẹ̀lú bí ẹni ń gbìyànjú àti mú afẹ́fẹ́ ni."'), 'Eccl$2:3': ('ọgbọn', None, 'Eccl$2:3 "Mo tiraka láti dun ara mi nínú pẹ̀lú ọtí wáìnì, àti láti fi ọwọ́ lé òmùgọ̀,—ọkàn mi sì ń tọ́ mi pẹ̀lú ọgbọ́n. Mo fẹ́ wo ohun tí ó yẹ ní ṣíṣe fún ènìyàn ní abẹ́ ọ̀run ní ìwọ̀nba ọjọ́ ayé rẹ̀."'), 'Eccl$2:12': ('ọgbọn', None, 'Eccl$2:12 "Nígbà náà ni mo tún bẹ̀rẹ̀ sí ọgbọ́n, àti ìsínwín àti àìgbọ́n kí ni ọba tí ó jẹ lẹ́yìn tí ọba kan kú le è ṣe ju èyí tí ọba ìṣáájú ti ṣe lọ."'), 'Eccl$2:13': ('okunkun', 'imole', 'Eccl$2:13 Mo sì ri wí pé ọgbọ́n dára ju òmùgọ̀ gẹ́gẹ́ bí ìmọ́lẹ̀ ti dára ju òkùnkùn lọ.'), 'Eccl$2:23': ('ọjọ', 'alẹ', 'Eccl$2:23 "Gbogbo ọjọ́ rẹ, iṣẹ́ rẹ kún fún ìrora, àti ìbànújẹ́, kódà ọkàn rẹ̀ kì í ní ìṣinmi ní alẹ́. Aṣán ni eléyìí pẹ̀lú."'), 'Eccl$3:1': (None, None, 'Eccl$3:1 Àsìkò wà fún ohun gbogbo àti ìgbà fún gbogbo nǹkan ní abẹ́ ọ̀run.'), 'Eccl$3:11': ('opin', 'ibere', 'Eccl$3:11 "Ó ti ṣe ohun gbogbo dáradára ní àsìkò tirẹ̀, ó sì ti fi èrò nípa ayérayé sí ọkàn ọmọ ènìyàn, ṣíbẹ̀, wọn kò le ṣe àwárí ìdí ohun tí Ọlọ́run ti ṣe láti ìbẹ̀rẹ̀ títí dé òpin."'), 'Eccl$3:21': ('si oke', None, 'Eccl$3:21 Ta ni ó mọ̀ bóyá ẹ̀mí ènìyàn ń lọ sí òkè tí ẹ̀mí ẹranko sì ń lọ sí ìsàlẹ̀ nínú ilẹ̀ ní?”'), 'Eccl$4:2': ('laaye', 'kú', 'Eccl$4:2 "Mo jowú àwọn tí wọ́n ti kú tí wọ́n sì ti lọ, ó ṣàn fún wọn ju àwọn tí wọ́n sì wà láàyè lọ."'), 'Eccl$4:3': (None, None, 'Eccl$4:3 "Nítòótọ́, ẹni tí kò tí ì sí sàn ju àwọn méjèèjì lọ: ẹni tí kò tí ì rí iṣẹ́ búburú tí ó ń lọ ní abẹ́ oòrun."'), 'Eccl$4:10': (None, 'ṣubu', 'Eccl$4:10 "Tí ọ̀kan bá ṣubú lulẹ̀, ọ̀rẹ́ rẹ̀ le è ràn án lọ́wọ́ kí ó fàá sókè, ṣùgbọ́n ìyọnu ni fún ọkùnrin náà tí ó ṣubú tí kò sì ní ẹni tí ó le è ràn-án lọ́wọ́!"'), 'Eccl$4:13': ('aṣiwère', 'ọlọgbọn', 'Eccl$4:13 "Òtòsì ìpẹ́ẹ̀rẹ̀ tí ó ṣe ọlọ́gbọ́n, ó ṣàn ju arúgbó àti aṣiwèrè ọba lọ tí kò mọ bí yóò ti ṣe gba ìmọ̀ràn,"'), 'Eccl$5:12': ('pọ', None, 'Eccl$5:12 "Oorun alágbàṣe a máa dùn, yálà ó jẹun kékeré ni tàbí ó jẹun púpọ̀, ṣùgbọ́n ọ̀pọ̀ ọ̀rọ̀ ọlọ́rọ̀ kì í jẹ́ kí ó ṣùn rárá."'), 'Eccl$5:15': ('wá', None, 'Eccl$5:15 "Ìhòòhò ni ènìyàn wá láti inú ìyá rẹ̀, bí ó sì ṣe wá, bẹ́ẹ̀ ni yóò kúrò kò sí ohunkóhun nínú iṣẹ́ rẹ̀ tí ó le mú ní ọwọ́ rẹ̀."'), 'Eccl$5:16': ('wá', 'lọ', 'Eccl$5:16 "Ohun búburú gbáà ni eléyìí pàápàá: Bí ènìyàn ṣe wá, ni yóò lọ kí wá ni èrè tí ó jẹ nígbà tí ó ṣe wàhálà fún afẹ́fẹ́?"'), 'Eccl$5:19': (None, 'gba', 'Eccl$5:19 "Síwájú sí, nígbà tí Ọlọ́run fún ẹnikẹ́ni ní ọrọ̀ àti ohun ìní, tí ó sì fún-un lágbára láti gbádùn wọn, láti gba ìpín rẹ̀ kí inú rẹ̀ sì dùn sí iṣẹ́ rẹ̀—ẹ̀bùn Ọlọ́run ni èyí."'), 'Eccl$7:1': (None, 'iku', 'Eccl$7:1 Orúkọ rere ṣàn ju ìpara olóòórùn dídùn lọ ọjọ́ ikú sì dára ju ọjọ́ tí a bí ènìyàn lọ'), 'Eccl$7:8': (None, 'ibere', 'Eccl$7:8 "Òpin ọ̀rọ̀ dára ju ìbẹ̀rẹ̀ rẹ̀ lọ, ṣùúrù sì dára ju ìgbéraga lọ."'), 'Eccl$7:25': ('ọgbọn', None, 'Eccl$7:25 "Mó wá ròó nínú ọkàn mi láti mọ̀, láti wá àti láti ṣàwárí ọgbọ́n àti ìdí ohun gbogbo, àti láti mọ àgọ́ ìwà búburú àti ti ìsínwín tàbí òmùgọ̀. "'), 'Eccl$7:27': (None, None, 'Eccl$7:27 "Oníwàásù wí pé, “Wò ó” eléyìí ni ohun tí mo ti ṣàwárí: “Mímú ohun kan pọ̀ mọ́ òmíràn láti ṣàwárí ìdí ohun gbogbo."'), 'Eccl$8:10': (None, 'wá', 'Eccl$8:10 Nígbà náà ni mo tún rí ìsìnkú òsìkà—àwọn tí wọ́n máa ń wá tí wọ́n sì ń lọ láti ibi mímọ́ kí wọn sì gba ìyìn ní ìlú tàbí tí wọ́n ti ṣe èyí. Eléyìí pẹ̀lú kò ní ìtumọ̀.'), 'Eccl$8:16': (None, None, 'Eccl$8:16 Nígbà tí mo lo ọkàn mi láti mọ ọgbọ́n àti láti wo wàhálà ènìyàn ní ayé.'), 'Eccl$9:2': (None, 'mimọ', 'Eccl$9:2 "Àyànmọ́ kan náà ni gbogbo wọn ń pín—olótìítọ́ àti òsìkà, rere àti ibi, mímọ́ àti àìmọ́, àwọn tí ó ń rúbọ àti àwọn tí kò rúbọ. Bí ó ti wà pẹ̀lú ọkùnrin rere bẹ́ẹ̀ náà ni ó wà pẹ̀lú ẹlẹ́ṣẹ̀ bí ó ti wà pẹ̀lú àwọn tí ó ń ṣe ìbúra bẹ́ẹ̀ náà ni ó wà pẹ̀lú àwọn tí ó ń bẹ̀rù láti ṣe ìbúra."'), 'Eccl$9:3': ('kú', None, 'Eccl$9:3 "Ohun búburú ni èyí jẹ́ nínú gbogbo ohun tí ó ń ṣẹlẹ̀ lábẹ́ oòrùn. Ìpín kan ṣoṣo ni ó ń dúró de gbogbo wọn, ọkàn ènìyàn pẹ̀lú kún fún ibi, ìṣínwín sì wà ní ọkàn wọn nígbà tí wọ́n wà láàyè àti nígbà tí wọ́n bá darapọ̀ mọ́ òkú."'), 'Eccl$9:4': (None, 'kú', 'Eccl$9:4 Ẹnikẹ́ni tí ó wà láàrin alààyè ní ìrètí—kódà ààyè ajá ṣàn dáadáa ju òkú ẹkùn lọ!'), 'Eccl$9:5': (None, 'kú', 'Eccl$9:5 "Nítorí pé ẹni tí ó wà láàyè mọ̀ wí pé àwọn yóò kú ṣùgbọ́n òkú kò mọ ohun kan wọn kò ní èrè kankan mọ́, àti pé kódà ìrántí wọn tí di ohun ìgbàgbé."'), 'Eccl$10:1': ('ọgbọn', None, 'Eccl$10:1 "Gẹ́gẹ́ bí òkú eṣinṣin tí ń fún ìpara ní òórùn burúkú, bẹ́ẹ̀ náà ni òmùgọ̀ díẹ̀ ṣe ń bo ọgbọ́n àti ọlá mọ́lẹ̀."'), 'Eccl$10:2': (None, None, 'Eccl$10:2 "Ọkàn ọlọgbọ́n a máa sí sí ohun tí ó tọ̀nà, ṣùgbọ́n ọkàn òmùgọ̀ sí ohun tí kò dára."'), 'Eccl$10:13': (None, None, 'Eccl$10:13 Ìbẹ̀rẹ̀ ọ̀rọ̀ rẹ̀ jẹ́ òmùgọ̀; ìparí rẹ̀ sì jẹ́ ìṣínwín búburú.'), 'Eccl$11:3': ('kun', None, 'Eccl$11:3 "Bí àwọ̀ṣánmọ̀ bá kún fún omi, ayé ni wọ́n ń rọ òjò sí Bí igi wó sí ì hà Gúṣù tàbí sí ìhà àríwá níbi tí ó wó sí náà, ni yóò dùbúlẹ̀ sí."'), 'Eccl$11:7': (None, None, 'Eccl$11:7 Ìmọ́lẹ̀ dùn Ó sì dára fún ojú láti rí oòrùn.'), 'Eccl$12:1': (None, None, 'Eccl$12:1 "Rántí Ẹlẹ́dàá rẹ ní ọjọ́ èwe rẹ, nígbà tí ọjọ́ ibi kò tíì dé àti tí ọdún kò tíì ní ṣun mọ́ etílé, nígbà tí ìwọ yóò wí pé, “Èmi kò ní ìdùnnú nínú wọn”"'), 'Eccl$12:14': (None, None, 'Eccl$12:14 "Nítorí Ọlọ́run yóò ṣe ìdájọ́ olúkúlùkù iṣẹ́ àti ohun ìkọ̀kọ̀, kì bá à ṣe rere kì bá à ṣe búburú."'), 'Song$2:6': ('osi', 'ọtun', 'Song$2:6 Ọwọ́ òsì rẹ ń bẹ lábẹ́ orí mi Ọwọ́ ọ̀tún rẹ sì ń gbà mí mọ́ra'), 'Song$2:11': ('ti lọ', None, 'Song$2:11 "Wò ó! Ìgbà otútù ti kọjá; Òjò ti rọ̀ dawọ́, ó sì ti lọ."'), 'Song$4:16': (None, 'ariwa', 'Song$4:16 "Jí ìwọ afẹ́fẹ́ àríwá kí o sì wá, afẹ́fẹ́ gúṣù! Fẹ́ lórí ọgbà mi, kí àwọn òórùn dídùn inú rẹ lè rùn jáde. Jẹ́ kí olùfẹ́ mi wá sínú ọgbà a rẹ̀ kí ó sì jẹ àṣàyàn èso rẹ̀."'), 'Song$7:13': (None, None, 'Song$7:13 "Àwọn èṣo mánídárákì mú òórùn wọn jáde ní ẹnu ọ̀nà wa ni onírúurú àṣàyàn èso, èso ọ̀tún àti àkúgbó tí mo ti kó pamọ́ fún ọ, olùfẹ́ mi."'), 'Song$8:3': ('osi', 'ọtun', 'Song$8:3 "Ọwọ́ òsì rẹ ìbá wà ní abẹ́ orí mi, ọwọ́ ọ̀tún rẹ ìbá sì gbá mí mọ́ra."'), 'Isa$1:6': (None, 'ẹsẹ', 'Isa$1:6 "Láti àtẹ́lẹṣẹ̀ yín dé àtàrí yín kò sí àlàáfíà rárá, àyàfi ọgbẹ́ òun ìfarapa àti ojú egbò, tí a kò nùnù tàbí kí á dì tàbí kí a kùn ún ní òróró. "'), 'Isa$2:12': (None, 'giga', 'Isa$2:12 "OLÚWA àwọn ọmọ-ogun ní ọjọ́ kan nípamọ́ fún gbogbo agbéraga àti ọlọ́kàn gíga nítorí gbogbo àwọn tí a gbéga (ni a ó rẹ̀sílẹ̀),"'), 'Isa$3:16': ('ẹsẹ', None, 'Isa$3:16 "OLÚWA wí pé, “Àwọn obìnrin Ṣíónì jẹ́ agbéraga, wọn ń rìn lọ pẹ̀lú ọrùn tí ó nà tàntàn, tí wọn ń fojú pe ọkùnrin, tí wọn ń sọ̀dí bí wọn ti ń yan lọ pẹ̀lú ohun ọ̀ṣọ́ tí ń mì wọnjan wọnjan lọ́rùn ẹṣẹ̀ wọn."'), 'Isa$4:1': ('obinrin', None, 'Isa$4:1 "Ní ọjọ́ náà, obìnrin méje yóò dì mọ́ ọkunrin kan yóò sì wí pé, “Àwa ó má a jẹ oúnjẹ ara wa a ó sì pèsè aṣọ ara wa; sáà jẹ́ kí a má a fi orúkọ rẹ̀ pè wá. Mú ẹgan wá kúrò!”"'), 'Isa$4:5': (None, None, 'Isa$4:5 "Lẹ́yìn náà, OLÚWA yóò dá sorí òkè Síhónì àti sóríi gbogbo àwọn tí ó péjọ pọ̀ ṣíbẹ̀, kúrúkúrú èéfín ní ọ̀ṣán àti ìtànsán ọ̀wọ́ iná ní òru, lórí gbogbo ògo yìí ni ààbò yóò wà."'), 'Isa$5:5': (None, None, 'Isa$5:5 "Ní ìsinsìn yìí, èmi yóò sọ fún ọ ohun tí n ó ṣe sí ọgbà-àjàrà mi: Èmi yóò gé igi inú un rẹ̀ kúrò, a ó sì pa á run, Èmi yóò wó ògiri rẹ̀ lulẹ̀ yóò sì di ìtẹ̀mọ́lẹ̀."'), 'Isa$5:20': ('okunkun', 'imole', 'Isa$5:20 "Ègbé ni fún àwọn tí ń pe ibi ní rere, àti rere ní ibi, tí ń fi òkùnkùn ṣe ìmọ́lẹ̀ àti ìmọ́lẹ̀ ṣe òkunkun, tí ń fi ìkorò ṣe adùn àti adùn ṣe ìkorò. "'), 'Isa$5:30': ('okunkun', 'imole', 'Isa$5:30 "Ní ọjọ́ náà, wọn yóò hó lé e lórí gẹ́gẹ́ bí i rírú omi òkun. Bí ènìyàn bá sì wo ilẹ̀, yóò rí òkùnkùn àti ìbànújẹ́; pẹ̀lúpẹ̀lú ìmọ́lẹ̀ yóò di òkùnkùn pẹ̀lú kùrukùru rẹ̀."'), 'Isa$7:5': (None, None, 'Isa$7:5 "Árámù, Éfáímù àti Rèmálíà ti dìtẹ̀ ìparun rẹ, wọ́n wí pé,"'), 'Isa$7:15': (None, None, 'Isa$7:15 Òun yóò jẹ wàrà àti oyin nígbà tí ó bá ní ìmọ̀ tó láti kọ ẹ̀bi àti láti yan àre.'), 'Isa$7:16': (None, None, 'Isa$7:16 "Ṣùgbọ́n kí ọ̀dọ́mọkùnrin náà tó mọ bí a ti ń kọ ẹ̀bi àti láti yan àre, ilẹ̀ àwọn ọba méjèèjì tí ń bà ọ́ lẹ́rù wọ̀nyí yóò ti di ahoro."'), 'Isa$7:17': ('lọ', 'wá', 'Isa$7:17 "OLÚWA yóò mú àsìkò mìíràn wá fún ọ àti àwọn ènìyàn rẹ àti lórí ilé baba rẹ irú èyí tí kò sí láti ìgbà tí Éfáímù ti yà kúrò ní Júdà, yóò sì mú ọba Áṣíríà wá.”"'), 'Isa$7:20': ('ẹsẹ', 'ori', 'Isa$7:20 "Ní ọjọ́ náà, OLÚWA yóò lo abẹfẹ́lẹ́ tí a yá láti ìkọjá odò, ọba Ásíríà, láti fá irun orí àti ti àwọn ẹsẹ̀ẹ yín, àti láti mú irùngbọ̀n yín kúrò pẹ̀lú."'), 'Isa$8:4': (None, None, 'Isa$8:4 Kí ọmọkùnrin náà tó mọ bí a ti ń sọ pé ‘Baba mi’ tàbí ‘Ìyá mi’ gbogbo ọrọ̀ Dámásíkù àti ìkógun ti Ṣamáríà ni ọba àwọn Áṣíríà yóò ti kó lọ.”'), 'Isa$8:5': (None, None, 'Isa$8:5 OLÚWA sì tún sọ fún mi pé:'), 'Isa$9:2': ('okunkun', 'imole', 'Isa$9:2 Àwọn ènìyàn tí ń rìn nínú òkùnkùn ti rí ìmọ́lẹ̀ ńlá; Lórí àwọn tí ń gbé ní ilẹ̀ òjìji ikú ni ìmọ́lẹ̀ ti ràn.'), 'Isa$9:3': (None, 'pin', 'Isa$9:3 "Ìwọ ti sọ orílẹ̀-èdè di ńlá; wọ́n sì yọ̀ níwájúu rẹ gẹ́gẹ́ bí àwọn ènìyàn ti í yọ ayọ̀ ìkóórè, gẹ́gẹ́ bí ènìyàn ti í yọ̀ nígbà tí à ń pín ìkógun."'), 'Isa$9:14': ('ori', 'iru', 'Isa$9:14 "Nítorí náà ni OLÚWA yóò ṣe ké àti orí àti ìrù kúrò ní Ísírẹ́lì, àti Imọ̀-ọ̀pẹ àti koríko-odò ní ọjọ́ kanṣoṣo,"'), 'Isa$9:15': ('ori', 'iru', 'Isa$9:15 "Àwọn alàgbà, àti àwọn gbajúmọ̀ ni orí, àwọn wòlíì tí ń kọ́ni ní irọ́ ni ìrù."'), 'Isa$9:20': ('osi', 'ọtun', 'Isa$9:20 "Ní apá ọ̀tún wọn yóò jẹrun, síbẹ̀ ebi yóò sì máa pa wọ́n, ní apá òsì, wọn yóò jẹ ṣùgbọ́n, kò ní tẹ́wọn lọ́rùn. Ẹnìkọ̀ọ̀kan yóò sì máa jẹ ẹran-ara ọmọ rẹ̀."'), 'Isa$10:6': ('gba', None, 'Isa$10:6 "Mo rán an sí orílẹ̀ èdè aláìní Ọlọ́run Mo dojúu rẹ̀ kọ àwọn ènìyàn tí ó múmi bínú láti já ẹrù gbà, àti láti kó ìkógun láti tẹ̀mọ́lẹ̀ bí amọ̀ ní ojú òpópó."'), 'Isa$10:28': (None, None, 'Isa$10:28 "Wọ́n wọ Áíyátì, Wọ́n gba Mígírónì kọjá Wọ́n kó nǹkan pamọ́ sí Mísímásì."'), 'Isa$11:14': (None, None, 'Isa$11:14 "Wọn yóò dìgbòlu àwọn gẹ̀rẹ́gẹ̀rẹ́ Fílísítíà ni apá ìwọ̀ oòrùn, wọn yóò pawọ́pọ̀ kọlu àwọn ènìyàn apá ìlà oòrùn. Wọn yóò gbé ọwọ́ọ wọn lé Édómù àti Móábù, àwọn ará Ámónì yóò sì di ìwẹ̀fàa wọn."'), 'Isa$13:7': (None, None, 'Isa$13:7 "Nítorí èyí, gbogbo ọwọ́ ni yóò rọ, ọkàn ẹnìkọ̀ọ̀kan yóò rẹ̀wẹ̀sì."'), 'Isa$14:3': ('fun', None, 'Isa$14:3 "Ní ọjọ́ tí OLÚWA yóò fi ìtùra fún un yín kúrò nínú ìpọ́njú àti ìyà àti ìdè ìkà,"'), 'Isa$16:8': (None, None, 'Isa$16:8 "Gbogbo pápá-oko Héṣíbónì ti gbẹ, bákan náà ni àjàrà Ṣíbínà rí. Àwọn aláṣẹ àwọn orílẹ̀ èdè wọ́n tẹ àwọn àyànfẹ́ àjàrà mọ́lẹ̀, èyí tí ó ti fà dé Jáṣérì ó sì ti tàn dé agbègbè aṣálẹ̀. Àwọn èhu rẹ̀ fọ́n jáde ó sì lọ títí ó fi dé òkun."'), 'Isa$19:10': (None, 'ṣe', 'Isa$19:10 "Àwọn tí ń ṣiṣẹ́ aṣọ yóò rẹ̀wẹ̀sì, gbogbo àwọn oníṣẹ́ oṣù ni àìṣàn ọkàn yóò ṣe. "'), 'Isa$19:15': ('ori', 'iru', 'Isa$19:15 "Kò sí ohun tí Éjíbítì le ṣe— orí tàbí ìrù, ọ̀wá ọ̀pẹ tàbí koríko odò."'), 'Isa$20:3': (None, None, 'Isa$20:3 "Lẹ́yìn náà ni OLÚWA wí pé, “Gẹ́gẹ́ bí Ìránṣẹ́ mi Àìsáyà ti lọ káàkiri ní ìhòòhò àti láì wọ bàtà fún ọdún mẹ́ta, gẹ́gẹ́ bí àmì àti àpẹẹrẹ sí Éjíbítì àti Kúṣì,"'), 'Isa$22:22': (None, None, 'Isa$22:22 " Èmi yóò sì fi kọ́kọ́rọ́ ilée Dáfídì lé e ní èjìká; ohunkóhun tí ó bá sí, ẹnikẹ́ni kì yóò lè ti, ohunkóhun tí ó bá sì tì, ẹnikẹ́ni kì yóò lè ẹnikẹ́ni kì yóò le è sí."'), 'Isa$24:2': (None, 'ayanilowo', 'Isa$24:2 "bákan náà ni yóò sì rí fún àlùfáà àti àwọn ènìyàn, fún ọ̀gá àti ọmọ ọ̀dọ̀, fún ìya-ilé àti ọmọbìnrin, fún olùtà àti olùrà, fún ayáni àti atọrọ fún ayánilówó àti onígbésè."'), 'Isa$24:20': ('ṣubu', 'dide', 'Isa$24:20 "Ilẹ̀ ayé yí gbiri bí ọ̀mùtí, ó bì síwá ṣẹ́yìn bí ahéré nínú afẹ́fẹ́; Ẹ̀bi ìṣọ̀tẹ̀ rẹ̀ ń pa á lẹ́rù tó bẹ́ẹ̀ tí ó fi ṣubú láìní lè dìde mọ́."'), 'Isa$25:12': (None, 'giga', 'Isa$25:12 "Òun yóò sì bi gbogbo ògiri gíga alágbára yín lulẹ̀ wọn yóò sì wà nílẹ̀ Òun yóò sì mú wọn wá si ilẹ̀, àní sí erùpẹ̀ lásán."'), 'Isa$26:4': (None, None, 'Isa$26:4 "Gbẹ́kẹ̀lé OLÚWA títí láé, nítorí OLÚWA, OLÚWA ni àpáta ayérayé náà."'), 'Isa$26:14': ('kú', None, 'Isa$26:14 "Wọ́n ti kú báyìí, wọn ò sí láàyè mọ́; gbogbo ẹ̀mí tí ó ti kọjá lọ wọ̀nyí kò le dìde mọ́. Ìwọ jẹ́ wọ́n ní ìyà o sì sọ wọ́n di asán, Ìwọ pa gbogbo ìrántíi wọn rẹ́ pátapáta."'), 'Isa$26:19': ('kú', 'gbe', 'Isa$26:19 "Ṣùgbọ́n àwọn òkúu yín yóò wà láàyè ara wọn yóò dìde. Ìwọ tí o wà nínú erùpẹ̀, dìde nílẹ̀ kí o sì ké igbe ayọ̀. Ìrì rẹ dàbí ìrì òwúrọ̀, ayé yóò bí àwọn òkúu rẹ̀ lọ́mọ. "'), 'Isa$27:3': (None, None, 'Isa$27:3 "ÈMI OLÚWA ń bojú tó o, Mo ń bomirin ín láti ìgbàdégbà. Mò ń sọ́ ọ tọ̀sán tòru kí ẹnikẹ́ni má ba à pa á lára."'), 'Isa$28:3': ('ẹsẹ', None, 'Isa$28:3 "Òdòdó-ẹ̀yẹ náà, tí í ṣe ìgbéraga àwọn ọ̀mùtí Éfáímù, òun ni a ó tẹ̀ mọ́lẹ̀ lábẹ́ àtẹ́lẹṣẹ̀."'), 'Isa$28:19': (None, None, 'Isa$28:19 "Nígbàkúùgbà tí ó bá ti wá ni yóò máa gbé ọ lọ, ni àràárọ̀, ní ọ̀sán àti ní òru, ni yóò máa fẹ́ kọjá lọ.” Ìmòye ọ̀rọ̀ ìmọ̀ yìí yóò máa mú ìpayà ńlá wá."'), 'Isa$29:8': (None, None, 'Isa$29:8 "àti bí ìgbà tí ẹni tí ebi ń pa ń lá àlá pé òun ń jẹun, ṣùgbọ́n ó jí, ebi rẹ̀ sì wà pẹ̀lúu rẹ̀; àti bí ẹni tí òùngbẹ ń gbẹ lá àlá pé òun ń mumi, ṣùgbọ́n nígbà tí ó yajú pẹ́ ẹ́, pẹ̀lú òùngbẹ tí kò dáwọ́ dúró ni. Bẹ́ẹ̀ ni yóò sì rí fún ọ̀pọ̀lọpọ̀ orílẹ̀ èdè tí ń bá òkè Ṣíhónì jà. "'), 'Isa$30:6': (None, None, 'Isa$30:6 "Ọ̀rọ̀-ìmọ̀ sí àwọn ẹranko tí ó wà ní Gúúsù: Láàrin ilẹ̀ ìnira àti ìpọ́njú, ti kìnnìún àti abo kìnnìún ti pamọ́lẹ̀ àti ejò olóró, àwọn ikọ̀ náà kó ẹrù àti ọrọ̀ wọn lẹ́yìn àwọn kẹ́tẹ́kẹ́tẹ́, àwọn ohun ìní wọn ní orí iké àwọn ràkunmí, sí orílẹ̀ èdè aláìlérè,"'), 'Isa$30:8': (None, None, 'Isa$30:8 "Lọ nísinsìn yìí, kí o sì kọ ọ́ sí ara pátakó fún wọn, tẹ̀ ẹ́ sí ara ìwé kíká, pé fún àwọn ọjọ́ tí ó ń bọ̀ kí ó sì lè jẹ́ ẹ̀rí ayérayé."'), 'Isa$30:20': (None, None, 'Isa$30:20 "Bí ó tilẹ̀ jẹ́ pé OLÚWA fún ọ ní àkàrà ìyà àti omi ìpọ́njú, àwọn olùkọ́ rẹ kì yóò farasin mọ́; pẹ̀lú ojú rẹ ni ìwọ ó rí wọn."'), 'Isa$34:10': (None, None, 'Isa$34:10 "A ki yóò paá ní òru tàbí ní ọ̀sán, èéfín rẹ̀ yóò gòkè láéláé: yóò dahoro láti ìran dé ìran, kò sí ẹnìkan tí yóò là á kọjá láé àti láéláé."'), 'Isa$35:9': (None, None, 'Isa$35:9 "Kì yóò sí kìnnìún níbẹ̀, tàbí kí ẹranko búburú kí ó dìde lóríi rẹ̀; a kì yóò rí wọn níbẹ̀. Ṣùgbọ́n àwọn ẹni ìràpadà nìkan ni yóò rìn níbẹ̀,"'), 'Isa$37:21': ('ọmọ', None, 'Isa$37:21 "Lẹ́yìn náà Àìṣáyà ọmọ Ámósì rán iṣẹ́ kan sí Heṣekáyà: “Èyí ni ohun tí OLÚWA, Ọlọ́run Ísírẹ́lì sọ pé: Nítorí pé ìwọ ti gbàdúrà sí mi nípa Ṣenakérúbù ọba Áṣíríà,"'), 'Isa$37:31': (None, None, 'Isa$37:31 "Lẹ́ẹ̀kan sí i, àṣẹ́ku láti ilé Júdà yóò ta gbòǹgbò níṣàlẹ̀ yóò sì ṣo èṣo lókè."'), 'Isa$38:12': (None, None, 'Isa$38:12 "Gẹ́gẹ́ bí àgọ́ olùṣọ́-àgùntàn, ilé mi ni a ti wó lulẹ̀ tí a sì gbà kúrò lọ́wọ́ọ̀ mi. Gẹ́gẹ́ bí ahunsọ mo ti ká ayé mi nílẹ̀, bẹ́ẹ̀ ni òun sì ti ké mi kúrò lára àṣà; ọ̀ṣán àti òru ni ìwọ ṣe òpin mi."'), 'Isa$38:13': (None, None, 'Isa$38:13 "Èmi fi ṣùúrù dúró títí di àfẹ̀mọ́jú, ṣùgbọ́n gẹ́gẹ́ bí kìnnìún ó ti fọ́ gbogbo egungun mi; ọ̀ṣán àti òru ni ìwọ fi ṣe òpin mi."'), 'Isa$40:4': (None, None, 'Isa$40:4 " Gbogbo àfonífojì ni a ó gbé ṣókè, gbogbo òkè ńlá àti òkè kékeré ni yóò di pẹ̀tẹ́lẹ̀; wíwọ́ ni a ó sọ di títọ́ àti ọ̀nà pálapàla ni a yóò sọ di títẹ́jú pẹrẹṣẹ,"'), 'Isa$41:22': (None, None, 'Isa$41:22 "“Mú àwọn ère-òrìṣà rẹ wọlé láti sọ fún wa ohun tí yóò ṣẹlẹ̀. Sọ fún wa ohun tí àwọn nǹkan àtijọ́ jẹ́, kí àwa lè ṣe àgbéyẹ̀wò wọn kí àwa sì mọ àbájáde wọn níparí. Tàbí kí o sọ fún wa ohun tí ó ń bọ̀ wá,"'), 'Isa$41:23': (None, None, 'Isa$41:23 "ẹ sọ fún wa ohun ti ọjọ́ iwájú mú dání kí àwa kí ó lè mọ̀ pé Ọlọ́run niyín. Ẹ ṣe nǹkankan, ìbáà ṣe rere tàbí búburú, tó bẹ́ẹ̀ tí àyà yóò fi fò wá tí ẹ̀rù yóò sì fi kún inú wa."'), 'Isa$42:5': (None, None, 'Isa$42:5 " Èyí ni ohun tí OLÚWA Ọlọ́run wí Ẹni tí ó dá àwọn ọ̀run tí ó sì tẹ́ wọ́n ṣóde, tí ó tẹ́ ilẹ̀ ayé àti ohun gbogbo tí ó jáde nínú un wọn, Ẹni tí ó fún àwọn ènìyàn rẹ̀ ní èémí àti ẹ̀mí fún gbogbo àwọn tí ń rìn nínú un rẹ̀:"'), 'Isa$44:6': (None, 'akoko', 'Isa$44:6 " “Ohun tí OLÚWA wí nìyìí ọba Ísírẹ́lì àti Olùdáǹdè, àní OLÚWA àwọn ọmọ-ogun: Èmi ni ẹni àkọ́kọ́ àti ẹni ìgbẹ̀yìn, lẹ́yin mi kò sí Ọlọ́run kan."'), 'Isa$45:1': (None, None, 'Isa$45:1 "Èyí ni ohun tí OLÚWA sọ fún ẹni òróró rẹ̀, sí Kírúsì, ọwọ́ ọ̀tún ẹni tí mo dì mú láti dojú àwọn orílẹ̀ èdè bolẹ̀ níwájú rẹ̀ àti láti gba ohun ìjà àwọn ọba lọ́wọ́ wọn, láti sí àwọn ìlẹ̀kùn níwájú rẹ̀ tí ó fi jẹ́ pé a kì yóò ti àwọn ẹnu-ọ̀nà."'), 'Isa$45:7': ('okunkun', 'imole', 'Isa$45:7 "Mo dá ìmọ́lẹ̀, mo sì dá òkùnkùn mo mú àlàáfíà wá, mo sì dá àjálù; Èmi OLÚWA ni ó ṣe nǹkan wọ̀nyí. "'), 'Isa$46:9': (None, None, 'Isa$46:9 "Rántí àwọn nǹkan àtẹ̀yìnwá, àwọn ti àtijọ́tijọ́; Èmi ni Ọlọ́run, kò sì sí ẹlọ̀mìíràn; Èmi ni Ọlọ́run, kò sí ẹlòmìíràn bí ì mi."'), 'Isa$46:12': (None, 'jinna', 'Isa$46:12 "Tẹ́tí sí mi, ìwọ ọlọ́kàn-dídi, ìwọ tí ó jìnnà sí òdodo."'), 'Isa$48:12': (None, 'akoko', 'Isa$48:12 " “Tẹ́tí sí mi, Ìwọ Jákọ́bù Ísírẹ́lì ẹni tí mo pè: Èmi ni ẹni náà; Èmi ni ẹni àkọ́kọ́ àti ẹni ìgbẹ̀yìn."'), 'Isa$50:10': ('okunkun', 'imole', 'Isa$50:10 "Ta ni nínú yín tí ó bẹ̀rù OLÚWA tí ó sì ń gbọ́ràn sí ìràńṣẹ́ rẹ̀ lẹ́nu? Jẹ́ kí ẹni tí ń rìn ní òkùnkùn tí kò ní ìmọ́lẹ̀, kí ó gbẹ́kẹ̀lé orúkọ OLÚWA kí o sì gbẹ́kẹ̀lé Ọlọ́run rẹ̀."'), 'Isa$51:11': ('ayo', 'ibanujẹ', 'Isa$51:11 Àwọn ẹni ìràpadà OLÚWA yóò padà wá. Wọn yóò wọ Ṣíhónì wá pẹ̀lú orin kíkọ; ayọ̀ ayérayé ni yóò sì bo oríi wọn. Ayọ̀ àti inú-dídùn yóò sì bà lé wọn ìbànújẹ́ àti ìtìjú yóò sì sá kúrò.'), 'Isa$52:2': (None, 'dide', 'Isa$52:2 "Gbọn eruku rẹ kúrò; dìde ṣókè, kí o sì gúnwà, Ìwọ Jérúsálẹ́mù. Bọ́ ẹ̀wọ̀n tí ń bẹ lọ́rùn rẹ kúrò, Ìwọ ọ̀dọ́mọbìnrin ìgbékùn Ṣíhónì."'), 'Isa$52:11': (None, 'mimọ', 'Isa$52:11 " Ẹ túká, ẹ túká, ẹ jáde kúrò níhìnínyìí! Ẹ má fọwọ́ kan ohun àìmọ́ kan! Ẹ jáde kúrò nínú rẹ̀ kí ẹ sì di mímọ́, ẹ̀yin tí ẹ̀ ń gbé ohun èlò OLÚWA."'), 'Isa$54:2': ('ṣe', None, 'Isa$54:2 "Fẹ ibi àgọ́ rẹ lójú sí i, fẹ aṣọ àgọ́ rẹ kí ó gbòòrò sí i, má ṣe dá a dúró; sọ okùn rẹ di gígùn, mú òpo rẹ lágbára sí i."'), 'Isa$54:4': ('gbagbe', 'ranti', 'Isa$54:4 "“Má ṣe bẹ̀rù, ìtìjú kò ní ṣubú lù ọ́. Má ṣe bẹ̀rù ìdójútì, a kì yóò kàn ọ́ lábùkù. Ìwọ yóò gbàgbé ìtìjú ìgbà èwee rẹ Ìwọ kì yóò sì rántí ẹ̀gàn ìwà-rópó rẹ mọ́."'), 'Isa$56:4': (None, None, 'Isa$56:4 "Nítorí pé ohun tí OLÚWA wí nìyìí: “Sí àwọn ìwẹ̀fà yìí tí ó pa ọjọ́ ìsinmi mi mọ́, tí wọ́n yan ohun tí ó dùn mọ́ mi tí wọ́n sì di májẹ̀mú mi mú ṣinṣin"'), 'Isa$59:9': ('okunkun', 'imole', 'Isa$59:9 "Nítorí èyí ni ẹ̀tọ́ fi jìnnà sí wa, àti tí òdodo kò fi tẹ̀wá lọ́wọ́. A ń wá ìmọ́lẹ̀, ṣùgbọ́n gbogbo rẹ jẹ́ òkùnkùn; fún ìmọ́lẹ̀, ṣùgbọ́n à ń rìn nínú òjìji."'), 'Isa$59:10': (None, 'alẹ', 'Isa$59:10 Gẹ́gẹ́ bí afọ́jú à ń táràrà lọ lẹ́gbẹ̀ẹ́ ògiri tí a ń wá ọ̀nà wa gẹ́gẹ́ bí ènìyàn tí kò ní ojú. Ní ọ̀sán gangan ni à ń kọsẹ̀ bí ẹni pé alẹ́ ni; láàrin alágbára àwa dàbí òkú.'), 'Isa$60:11': (None, None, 'Isa$60:11 " Gbogbo ẹnu bodè rẹ ni yóò wà ní sísí sílẹ̀, a kì yóò tì wọ́n ní ọ̀sán àti ní òru, tó bẹ́ẹ̀ tí àwọn ọkùnrin yóò le è kó ọrọ̀ àwọn orílẹ̀ èdè wá tí àwọn ọba wọn yóò ṣáájú ní ọ̀wọ̀ọ̀wọ́ ìṣẹ́gun."'), 'Isa$61:8': (None, None, 'Isa$61:8 "“Nítorí Èmi, OLÚWA fẹ́ràn ìdájọ́ òdodo; mo kórìíra olè jíjà àti ẹ̀ṣẹ̀ Ní òtítọ́ mi èmi yóò ṣẹ̀san fún wọn èmi yóò sì dá májẹ̀mú ayérayé pẹ̀lúu wọn."'), 'Isa$62:6': (None, 'alẹ', 'Isa$62:6 "Èmi ti fi olùṣọ́ ránṣẹ́ sórí odi rẹ ìwọ Jérúsálẹ́mù; wọn kì yóò lè dákẹ́ tọ̀sán tòru. Ẹ̀yin tí ń ké pe OLÚWA, ẹ má ṣe fún ra yín ní ìsinmi,"'), 'Isa$65:13': (None, None, 'Isa$65:13 "Nítorí náà ohun tí OLÚWA àwọn ọmọ-ogun wí nìyìí: “Àwọn ìránṣẹ́ mi yóò jẹun; ṣùgbọ́n ebi yóò máa pa ẹ̀yin, àwọn ìránṣẹ́ mi yóò mu, ṣùgbọ́n òrùngbẹ yóò máa gbẹ ẹ̀yin; àwọn ìránṣẹ́ mi yóò ṣe àjọyọ̀, ṣùgbọ́n a ó dójú ti ẹ̀yin."'), 'Isa$65:15': (None, 'egún', 'Isa$65:15 "Ẹ̀yin yóò fi orúkọ yín sílẹ̀ fún àwọn àyànfẹ́ mi gẹ́gẹ́ bí ègún; OLÚWA àwọn ọmọ-ogun yóò sì pa yín, ṣùgbọ́n fún àwọn ìránṣẹ́ rẹ̀ ni òun yóò fún ní orúkọ mìíràn"'), 'Jer$3:14': ('gba', None, 'Jer$3:14 "“Padà, ẹ̀yin ènìyàn aláìnígbàgbọ́,” ni OLÚWA wí, “nítorí èmi ni ọkọ rẹ. Èmi ó yàn ọ́, ọ̀kan láti ìlú àti méjì láti ẹ̀yà. Èmi ó sì mú ọ wá sí Síónì."'), 'Jer$3:20': (None, 'ọkọ', 'Jer$3:20 "Ṣùgbọ́n gẹ́gẹ́ bí aláìgbàgbọ́ obìnrin sí ọkọ rẹ̀, bẹ́ẹ̀ gẹ́gẹ́ o jẹ́ aláìgbàgbọ́ sí mi. Ìwọ ilé Ísírẹ́lì” ni OLÚWA wí. "'), 'Jer$4:7': (None, None, 'Jer$4:7 "Kìnnìún ti sá jáde láti inú ibùgbé rẹ̀, apanirun orílẹ̀ èdè sì ti jáde. Ó ti fi àyè rẹ̀ sílẹ̀ láti ba ilẹ̀ rẹ̀ jẹ́. Ìlú rẹ yóò di ahoro láìsí olùgbé."'), 'Jer$4:22': (None, None, 'Jer$4:22 “Aṣiwèrè ni àwọn ènìyàn mi; wọn kò mọ̀ mí. Wọ́n jẹ́ aláìgbọ́n ọmọ; wọ́n sì jẹ́ aláìlóye. Wọ́n mọ ibi ṣíṣe; wọn kò mọ bí a ti í ṣe rere.”'), 'Jer$5:24': (None, None, 'Jer$5:24 "Wọn kò sọ fún ara wọn pé, ‘Ẹ jẹ́ ká a bẹ̀rù OLÚWA Ọlọ́run wa, ẹni tí ó fún wa ní òjò àkọ́rọ̀ àti àrọ̀kúrò òjò ní ìgbà rẹ̀, tí ó fi wá lọ́kàn balẹ̀ nípa ìkórè ọ̀sẹ̀ déédéé.’"'), 'Jer$6:11': ('ọkọ', None, 'Jer$6:11 "Èmi kún fún ìbínú OLÚWA, èmi kò sì le è pa á mọ́ra. “Tú u sí orí àwọn ọmọ ńigboro, àti sórí àwọn ọmọkùnrin tí wọn kóra wọn jọ pọ̀, àti ọkọ àti aya ni a ò mú sínú rẹ̀, àti àwọn arúgbó tí ó ní ọjọ́ kíkún lórí."'), 'Jer$6:26': (None, 'ọmọ', 'Jer$6:26 "Ẹ̀yin ènìyàn mi, ẹ gbé asọ ọ̀fọ̀ wọ̀, kí ẹ sì sùn nínú eérú, ẹ sọ̀fọ̀ pẹ̀lú ìpohùnréré ẹkún gẹ́gẹ́ bí i lórí ọmọkùnrin yín, kanṣoṣo nítorí pé lójijì ni àwọn apanirun yóò kọlù wá. "'), 'Jer$7:24': (None, 'siwaju', 'Jer$7:24 "Ṣùgbọ́n wọn kò gbọ́, wọn kò sì fetí sílẹ̀ dípò èyí wọ́n ń tẹ̀ sí ọ̀nà agídí ọkàn wọn. Dípò kí wọn tẹ̀ṣíwájú wọ́n ń rẹ̀yìn."'), 'Jer$7:31': ('ọmọbinrin', 'ọmọ', 'Jer$7:31 Wọ́n ti kọ́ àwọn ibi gíga ti Tófẹ́tì ní àfonífojì Beni Hínómí láti sun àwọn ọmọkùnrin àti ọmọbìnrin wọn nínú iná èyí tí èmi kò paláṣẹ tí kò sì wá sí ọkàn mi.'), 'Jer$8:1': ('jọ', None, 'Jer$8:1 "“ ‘Ní ìgbà náà ni OLÚWA wí pé, wọn yóò mú egungun àwọn ọba Júdà àti egungun àwọn ijoye, egungun àwọn àlùfáà àti egungun àwọn wòlíì àti egungun àwọn olùgbé Jérúsálẹ́mù kúrò nínú ibojì."'), 'Jer$8:4': ('dide', 'ṣubu', 'Jer$8:4 "“Wí fún wọn pé, ‘Èyí ni ohun tí OLÚWA wí: “ ‘Bí ènìyàn bá ṣubú lulẹ̀ wọn kì í padà dìde bí? Nígbà tí ènìyàn bá yà kúrò ní ọ̀nà rẹ, kì í yí padà bí?"'), 'Jer$9:1': (None, None, 'Jer$9:1 Áà! Orí ìbá jẹ́ orísun omi kí ojú mi sì jẹ́ orísun omijé Èmi yóò sì sunkún tọ̀sán tòru nítorí pípa àwọn ènìyàn mi.'), 'Jer$13:3': ('wá', None, 'Jer$13:3 Nígbà náà ni ọ̀rọ̀ OLÚWA tọ̀ mí wá nígbà kejì:'), 'Jer$13:6': (None, 'lọ', 'Jer$13:6 "Lẹ́yìn ọjọ́ díẹ̀, OLÚWA sọ fún mi: Lọ sí Pérátì kí o lọ mú àmùrè tí mo ní kí o pamọ́ síbẹ̀."'), 'Jer$13:10': (None, None, 'Jer$13:10 "Àwọn ènìyàn búburú tí ó kùnà láti gbọ́ ọ̀rọ̀ mi, tí wọ́n ń lo agídí ọkàn wọn, tí ó sì ń rìn tọ àwọn òrìṣà láti sìn wọ́n, àti láti foríbalẹ̀ fún wọn, yóò sì dàbí àmùrè yìí tí kò wúlò fún ohunkóhun."'), 'Jer$13:16': ('okunkun', 'imole', 'Jer$13:16 "Ẹ fi ògo fún OLÚWA Ọlọ́run yín, kí ó tó mú òkùnkùn wá, àti kí ó tó mú ẹsẹ̀ yín tàsé lórí òkè tí ó ṣókùnkùn, Nígbà tí ẹ̀yin sì ń retí ìmọ́lẹ̀, òun yóò sọ ọ́ di òjìji yóò si ṣe bi òkùnkùn biribiri."'), 'Jer$13:18': ('ayaba', None, 'Jer$13:18 "Sọ fún Ọba àti ayaba pé, “Ẹ rẹ ara yín sílẹ̀, ẹ sọ̀kalẹ̀ láti orí ìtẹ́ yín, adé ògo yín bọ́ sí ilẹ̀ láti orí yín.”"'), 'Jer$13:19': (None, None, 'Jer$13:19 "Àwọn orílẹ̀ èdè tí ó wà ní Négéfì ni à ó tì pa, kò sì ní sí ẹnikẹ́ni láti sí wọn. Gbogbo Júdà ni a ó kó lọ sí ilẹ̀ ìgbèkùn, gbogbo wọn ni a ó kó lọ pátapáta. "'), 'Jer$14:17': (None, None, 'Jer$14:17 “Sọ ọ̀rọ̀ yìí fún wọn báyìí pé: “ ‘Jẹ́ kí omijé dà lójú mi lọ́sàn-án àti lóru láìdúró. Fún wúndíá mi-àwọn ènìyàn mi tí a dá lọ́gbẹ́ àti lílù bolẹ̀.'), 'Jer$15:2': (None, 'lọ', 'Jer$15:2 " Tí wọ́n bá sì bi ọ́ pé, ‘Níbo ni kí a lọ?’ Sọ fún wọn pé, ‘Báyìí ni OLÚWA wí: “ ‘Àwọn tí a kọ ikú mọ́, sí ikú; àwọn tí a kọ idà mọ́, sí idà; àwọn tí a kọ ìyàn mọ́, sí ìyàn; àwọn tí a kọ ìgbékùn mọ́ sí ìgbékùn.’"'), 'Jer$16:6': (None, None, 'Jer$16:6 "“Àti ẹni ńlá àti kékeré ni yóò ṣègbé ní ilẹ̀ yìí, wọn kò ní sin wọ́n tàbí sọ̀fọ̀ wọn, bẹ́ẹ̀ ni kò sí ẹni tí yóò fá irun orí wọn nítorí wọn."'), 'Jer$16:11': (None, None, 'Jer$16:11 "Nígbà náà ni ìwọ yóò sọ fún wọn wí pé, ‘Nítorí tí àwọn baba yín ti kọ̀ mí sílẹ̀,’ ni OLÚWA wí, ‘tí wọ́n sì tẹ̀lé àwọn òrìṣà mìíràn tí wọ́n ń bọ, tí wọ́n ń sìn, wọ́n kọ̀ mí sílẹ̀ wọn kò sì tẹ̀lé òfin mi mọ́."'), 'Jer$17:19': (None, None, 'Jer$17:19 Èyí ni ohun tí OLÚWA wí fún mi: “Lọ dúró ní ẹnu ọ̀nà àwọn ènìyàn níbi tí àwọn Ọba Júdà ń gbà wọlé tí wọ́n ń gbà jáde àti ní gbogbo ẹnu bodè Jérúsálẹ́mù.'), 'Jer$18:1': ('wá', None, 'Jer$18:1 Èyí ni ọ̀rọ̀ OLÚWA tí ó tọ Jeremáyà wá wí pe:'), 'Jer$18:9': (None, None, 'Jer$18:9 Ní ìgbà mìíràn tí èmi bá tún kéde láti tẹ̀dó tàbí gbin orílẹ̀ èdè kan tàbí ìjọba kan.'), 'Jer$18:11': ('ibi', None, 'Jer$18:11 "“Ǹjẹ́ nísìnsìnyìí, sọ fún àwọn ènìyàn Júdà àti àwọn olúgbé Jérúsálẹ́mù wí pé, ‘Báyìí ní OLÚWA wí: Wò ó! Èmi ń gbérò ibi sí yin, èmi sì ń ṣe ìpinnu kan lórí yín. Nítorí náà ẹ yípadà kúrò lọ́nà búburú yín kí olúkúlúkù yín sì tún ọ̀nà àti ìṣe rè ṣe.’"'), 'Jer$18:20': (None, 'ibi', 'Jer$18:20 "Ṣe kí a fi rere san búburú? Síbẹ̀ wọ́n ti gbẹ́ kòtò fún mi, rántí pé mo dúró níwájú rẹ, mo sì sọ̀rọ̀ ní torí wọn, láti yí ìbínú rẹ kúrò lọ́dọ̀ wọn."'), 'Jer$19:9': (None, None, 'Jer$19:9 "Èmi yóò mú kí wọn jẹ ẹran ara ọmọ wọn ọkùnrin àti obìnrin, wọn yóò sì jẹ ẹran ara wọn lásìkò ìparun wọn láti ọwọ́ ọ̀tá wọn, tí ó ń wá ẹ̀mí wọn.’"'), 'Jer$19:10': (None, 'fọ', 'Jer$19:10 “Nígbà náà ni ìwọ yóò fọ́ ìkòkò náà ní oju àwọn tí ó bá ọ lọ.'), 'Jer$20:5': (None, 'fun', 'Jer$20:5 Èmi yóò jọ̀wọ́ gbogbo ọrọ̀ inú ìlú yìí fún ọ̀ta wọn. Gbogbo ìní wọ́n gbogbo ọlá Ọba Júdà. Wọn yóò sì ru ìkógun lọ sí Bábílónì.'), 'Jer$20:6': ('lọ', None, 'Jer$20:6 "Àti ìwọ Páṣúrì, gbogbo ènìyàn inú ilé rẹ yóò sì lọ ṣe àtìpó ní Bábílónì. Níbẹ̀ ni ìwọ yóò sì kú sí, tí wọn yóò sì sin ọ́ síbẹ̀, ìwọ àti gbogbo àwọn ọ̀rẹ́ rẹ tí ìwọ ti sọ àṣọtẹ́lẹ̀ irọ́ fún.”"'), 'Jer$21:1': ('wá', None, 'Jer$21:1 Ọ̀rọ̀ OLÚWA sì tọ Jeremáyà wá nígbà tí Ọba Sedekáyà rán Páṣúrì ọmọ Málíkíjà àti àlùfáà Sedekáyà ọmọ Mánásè síi; wọ́n wí pé:'), 'Jer$21:10': (None, None, 'Jer$21:10 "Mo ti pinnu láti jẹ ìlú yìí níyà, ni OLÚWA wí. A ó sì gbé e fún Ọba Bábílónì, yóò sì run ún pẹ̀lú iná.’"'), 'Jer$24:2': (None, 'dara', 'Jer$24:2 Agbọ̀n kan ni èṣo ọ̀pọ̀tọ́ tí ó dára bí èyí tí ó tètè pọ́n; èkejì sì ní èṣo ọ̀pọ̀tọ́ tí ó burú rékọjá tí kò sì le è ṣe é jẹ'), 'Jer$25:27': ('dide', 'ṣubu', 'Jer$25:27 "“Nígbà náà, sọ fún wọn: Èyí ni ohun tí OLÚWA Ọlọ́run alágbára Ísírẹ́lì wí: Mu, kí o sì mu amuyó kí o sì bì, bẹ́ẹ̀ ni kí o sì ṣubú láì dìde mọ́ nítorí idà tí n ó rán sí àárin yín."'), 'Jer$25:28': ('gba', 'kọ', 'Jer$25:28 "Ṣùgbọ́n bí wọ́n ba kọ̀ láti gba aago náà ní ọwọ́ rẹ kí wọ́n sì mu ún, sọ fún wọn pé, ‘Èyí ni ohun tí Ọlọ́run alágbára sọ: Ẹyin gbọdọ̀ mu ún!"'), 'Jer$26:4': (None, None, 'Jer$26:4 "Sọ fún wọn wí pé, ‘Èyí ni ohun tí OLÚWA wí: Tí ẹ kò bá fi etí sí ọ̀rọ̀ mi, tí ẹ kò sì gbọ́ òfin mi, ti mo gbé síwájú yín."'), 'Jer$26:10': (None, None, 'Jer$26:10 "Nígbà tí àwọn aláṣẹ Júdà gbọ́ nípa nǹkan wọ̀nyí, wọ́n lọ láti ààfin sí ilé OLÚWA, wọ́n sì mú àyè wọn, wọ́n jòkòó ní ẹnu ọ̀nà tuntun ilé OLÚWA."'), 'Jer$28:12': (None, None, 'Jer$28:12 Láìpẹ́ ni ọ̀rọ̀ OLÚWA tọ Jeremáyà wòlíì wá lẹ́yìn ìgbà tí wòlíì Hananáyà ti gbé àjàgà kúrò ní ọrùn wòlíì Jeremáyà wí pé:'), 'Jer$29:1': (None, None, 'Jer$29:1 "Èyí ni ọ̀rọ̀ inú lẹ́tà tí wòlíì Jeremáyà fi ránṣẹ́ láti Jérúsálẹ́mù sí àwọn tí ó yè nínú àwọn aṣàtìpó àti àwọn àlùfáà, àwọn wòlíì àti gbogbo àwọn ènìyàn Nebukadinésárì tí wọ́n ṣe àtìpó láti Jérúsálẹ́mù ní Bábílónì."'), 'Jer$31:13': (None, None, 'Jer$31:13 "Àwọn wúndíá yóò jó, wọn ó sì kún fún ayọ̀, bẹ́ẹ̀ gẹ́gẹ́ ni ọkùnrin àti obìnrin. Èmi yóò sọ ọ̀fọ̀ wọn di ayọ̀, dípò ìkorò èmi yóò tù wọ́n nínú. Èmi ó sì fún wọn ní ayọ̀."'), 'Jer$31:35': (None, None, 'Jer$31:35 "Èyí ni ohun tí OLÚWA wí: ẹni tí ó mú oòrùn tan ìmọ́lẹ̀ ní ọ̀sán, tí ó mú òṣùpá àti ìràwọ̀ ràn ní òru; tí ó rú omi òkun sókè tó bẹ́ẹ̀ tí Ìjì rẹ̀ fi ń hó OLÚWA àwọn ọmọ ogun ni orúkọ rẹ̀."'), 'Jer$32:3': ('fun', 'gba', 'Jer$32:3 "Nítorí Sedekáyà Ọba Júdà ti há a mọ́lé síbẹ̀; pé, “Kí ló dé tí ìwọ fi ń sọ àsọtẹ́lẹ̀ báyẹn? Tí o sì wí pé, ‘Báyìí ni OLÚWA wí: Èmi ń bọ̀ wá fi ìlú yìí fún Ọba Bábílónì, tí yóò sì gbà á."'), 'Jer$32:25': (None, None, 'Jer$32:25 "Ṣíbẹ̀ à ò fi ìlú náà fún àwọn ará Kálídéà. Ìwọ OLÚWA Ọba sọ fún mi pé, ‘Ra pápá náà pẹ̀lú owó fàdákà, kí o sì pe ẹlẹ́rìí.’ ”"'), 'Jer$32:28': ('fun', None, 'Jer$32:28 "Nítorí náà, báyìí ni OLÚWA wí: Èmi ṣetan láti fi ìlú yìí fún àwọn ará Kálídéà àti fún Nebukadinésárì Ọba Bábílónì ẹni tí yóò kó o."'), 'Jer$32:35': (None, 'ọmọ', 'Jer$32:35 "Wọ́n kọ́ ibi gíga fún Báálì ní àfonífojì Hínómù láti fi ọmọ wọn ọkùnrin àti obìnrin rúbọ sí Mólékì. Èmi kò páláṣẹ, fún wọn, bẹ́ẹ̀ ni kò wá sí ọkàn mi pé kí wọ́n ṣe ohun ìríra wọ̀nyí tí ó sì mú Júdà ṣe."'), 'Jer$32:42': ('ibi', None, 'Jer$32:42 "“Nítorí bayìí ni OLÚWA wí: Gẹ́gẹ́ bí èmi ti mú gbogbo ibi ńlá yìí wá sórí àwọn ènìyàn yìí, bẹ́ẹ̀ ni èmi ó mú gbogbo rere tí èmi ti sọ nípa ti wọn wá sórí wọn."'), 'Jer$33:19': (None, None, 'Jer$33:19 Ọ̀rọ̀ OLÚWA sì tọ Jeremáyà wá wí pé:'), 'Jer$33:25': (None, None, 'Jer$33:25 Báyìí ni OLÚWA wí: ‘Bí èmi kò bá ti da májẹ̀mú mi pẹ̀lú ọ̀sán àti òru àti pẹ̀lú òfin ọ̀run àti ayé tí kò lè yẹ̀.'), 'Jer$34:1': ('wá', 'lọ', 'Jer$34:1 " Nígbà tí Nebukadinésárì Ọba Bábílónì àti àwọn ọmọ ogun rẹ̀, pẹ̀lú gbogbo ìjọba àti àwọn ènìyàn ní ilẹ̀ Ọba tí ó jọba lé lórí ń bá Jérúsálẹ́mù jà, àti gbogbo àwọn ìlú tí ó yí ká, ọ̀rọ̀ yìí tọ Jeremáyà wá láti ọ̀dọ̀ OLÚWA pé:"'), 'Jer$34:8': ('wá', 'lọ', 'Jer$34:8 Ọ̀rọ̀ OLÚWA sì tọ Jeremáyà wá lẹ́yìn ìgbà tí Ọba Sedekáyà ti dá májẹ̀mú pẹ̀lú gbogbo àwọn ènìyàn ní Jérúsálẹ́mù láti polongo ìtúsílẹ̀ fún àwọn ẹrú.'), 'Jer$34:12': ('wá', None, 'Jer$34:12 Nígbà náà ni ọ̀rọ̀ OLÚWA tọ Jeremáyà wá wí pé:'), 'Jer$35:8': ('ọmọbinrin', 'ọmọ', 'Jer$35:8 "Báyìí ni àwa gba ohùn Jónádábù ọmọ Rékábù bàbá wa gbọ́ nínú gbogbo èyí tí ó palásẹ fún wa, kí a má mu ọtí wáìnì ní gbogbo ọjọ́ ayé wa; àwa, àwọn aya wa, àwọn ọmọkùnrin wa, àti àwọn ọmọbìnrin wa."'), 'Jer$35:11': ('wá', 'lọ', 'Jer$35:11 "Ó sì se, nígbà tí Nebukadinésárì Ọba Bábílónì gòkè wá sí ilẹ̀ náà; àwa wí pé, ‘Ẹ wá, ẹ jẹ́ kí a lọ sí Jérúsálẹ́mù, nítorí ìbẹ̀rù ogun àwọn ara Síríà.’ Bẹ́ẹ̀ ni àwa sì ń gbé Jérúsálẹ́mù.”"'), 'Jer$36:5': ('lọ', None, 'Jer$36:5 "Nígbà náà ni Jeremáyà wí fún Bárúkì pé, “A ṣé mi mọ́! Èmi kò lè lọ sí ilé OLÚWA."'), 'Jer$36:30': (None, None, 'Jer$36:30 "Nítorí náà, báyìí ni OLÚWA wí níti Jéhóíákímù Ọba Júdà pé: Òun kì yóò ní ẹni tí yóò jókòó lórí ìtẹ́ Dáfídì, à ó sì sọ òkú rẹ̀ nù fún ooru ní ọ̀sán àti fún òtútù ní òru."'), 'Jer$37:5': ('lọ', None, 'Jer$37:5 "Àwọn ọmọ ogun Fáráò ti jáde kúrò nílẹ̀ Éjíbítì àti nígbà tí àwọn ará Bábílónì tó ń ṣàtìpó ní Jérúsálẹ́mù gbọ́ ìròyìn nípa wọn, wọ́n kúrò ní Jérúsálẹ́mù."'), 'Jer$37:11': (None, 'lọ', 'Jer$37:11 Lẹ́yìn tí àwọn ọmọ ogun Bábílónì ti kúrò ní Jérúsálẹ́mù nítorí àwọn ọmọ ogun Fáráò.'), 'Jer$38:3': (None, None, 'Jer$38:3 Àti pé èyí ni ohun tí OLÚWA wí: ‘Ìdánilójú wà wí pé a ó fi ìlú náà lé ọwọ́ àwọn ọmọ ogun Bábílónì; tí yóò sì kó wa nígbékùn.’ ”'), 'Jer$38:21': ('ni', 'kọ', 'Jer$38:21 "Ṣùgbọ́n bí ìwọ bá kọ̀ láti jọ̀wọ́ ara rẹ, èyí ni ohun tí OLÚWA ti fi hàn mí:"'), 'Jer$39:15': (None, None, 'Jer$39:15 "Nígbà tí Jeremáyà wà nínú túbú, ọ̀rọ̀ OLÚWA tọ̀ ọ́ wá wí pé:"'), 'Jer$40:1': ('wá', 'lọ', 'Jer$40:1 Ọ̀rọ̀ náà sì tọ Jeremáyà wá láti ọ̀dọ̀ OLÚWA lẹ́yìn tí Nebukadinésárì balógun àwọn ẹ̀ṣọ́ ti tú u sílẹ̀ ní Rámà. Ó rí Jeremáyà tí a fi ẹ̀wọ̀n dè láàrin gbogbo àwọn tí wọ́n mú ní Jérúsálẹ́mù àti Júdà. Wọ́n kó lọ sí ilẹ̀ àjèjì Bábílónì.'), 'Jer$41:10': ('ọmọbinrin', 'ọmọ', 'Jer$41:10 "Ísímáẹ́lì sì kó gbogbo àwọn ènìyàn tí ó wà ní Mísípà nígbèkùn, ọmọbìnrin Ọba àti gbogbo àwọn ènìyàn tó kù síbẹ̀ lórí àwọn tí Nebusarádánì balógun àwọn ẹ̀sọ́ ti fi yan Gédáláyà ọmọkùnrin Álíkámù ṣe olórí. Isímáẹ́lì ọmọkùnrin Nétanáyà kó wọn ní ìgbèkùn, ó sì jáde rékọjá sí ọ̀dọ̀ àwọn Ámónì."'), 'Jer$42:1': (None, None, 'Jer$42:1 Nígbà náà ní gbogbo àwọn ọmọ ogun àti Jóhánánì ọmọkùnrin Káréà àti Jésánáyà omọkùnrin Hòsáyà àti kékeré títí dé orí ẹni ńlá wá.'), 'Jer$42:6': (None, 'dara', 'Jer$42:6 "Ìbá à ṣe rere, ìbá à ṣe búburú, àwa yóò gbọ́ràn sí OLÚWA Ọlọ́run wa; èyí tí àwa ń rán ọ sí; Kí ó ba à lè dára fún wa. Nítorí pé àwa yóò gbọ́ràn sí OLÚWA Ọlọ́run wa.”"'), 'Jer$42:13': ('wá', 'lọ', 'Jer$42:13 "“Àmọ́ ṣá, bí ẹ̀yin bá wí pé, ‘Àwa kò ní gbé ilẹ̀ yìí,’ ẹ̀yin ti ṣe àìgbọ́ràn sí OLÚWA Ọlọ́run yín."'), 'Jer$43:5': (None, 'ọmọ', 'Jer$43:5 "Dípò bẹ́ẹ̀, Jóhánánì ọmọ Káréà àti àwọn ọ̀gágun sì ko àwọn àjẹkù Júdà tí wọ́n wá láti gbé ilẹ̀ Júdà láti orílẹ̀-èdè gbogbo tí wọ́n ti tú wọn ká."'), 'Jer$44:7': (None, 'obinrin', 'Jer$44:7 "“Báyìí tún ni OLÚWA Ọlọ́run àwọn ọmọ ogun Ísírẹ́lì wí: Kí ló dé tí ẹ fi ń mu ibi ńlá yìí wá sí orí ara yín nípa yíyapa kúrò lára Júdà ọkùnrin àti obìnrin, ọmọdé àti èwe, tí ẹ kò sì ku ọ̀kankan?"'), 'Jer$44:17': (None, 'ayaba', 'Jer$44:17 "Dájúdájú; à ó ṣe gbogbo nǹkan tí a sọ pé à ò ṣe: A ó sun tùràrí sí ayaba ọ̀run; à ó sì da ohun mímu sílẹ̀ gẹ́gẹ́ bí ẹbọ sí àwa àti àwọn baba wa, àwọn Ọba àti àwọn aláṣẹ ti ṣe ní àwọn ìlú Júdà àti ni àwọn ìgboro Jérúsálẹ́mù. Nígbà naà àwa ní ounjẹ púpọ̀ a sì ṣe rere a kò sì rí ibi"'), 'Jer$44:27': ('ibi', None, 'Jer$44:27 "Nítorí náà, mò ń wò wọ́n bí i fún ìparun, kì í ṣe fún rere. Àwọn Júù tí ó wà ní Éjíbítì yóò parun pẹ̀lú idà àti ìyàn títí tí gbogbo wọn yóò fi parun."'), 'Jer$46:22': ('wá', None, 'Jer$46:22 "Éjíbítì yóò pòṣé bí ejò tí ń sá bí ọmọ ogun ṣe ń tọ̀ ọ́ wá pẹ̀lú agbára. Wọn ó tọ̀ ọ́ wá pẹ̀lú àáké, gẹ́gẹ́ bí i ti agégi."'), 'Jer$47:3': ('wo', None, 'Jer$47:3 Nígbà tí wọ́n bá gbọ́ ìró títẹlẹ̀ pátakò ẹsẹ̀ ẹṣin alágbára nígbà tí wọ́n bá gbọ́ ariwo kẹ̀kẹ́ ogun ọ̀tá ńlá àti iye kẹ̀kẹ́ wọn. Àwọn baba kò ní lè ran àwọn ọmọ lọ́wọ́; ọwọ́ wọn yóò kákò.'), 'Jer$48:41': (None, 'obinrin', 'Jer$48:41 Koríko kan yóò di kíkó àti àwọn ìlú olódì. Ní ọjọ́ náà ọkàn akọni Móábù yóò dàbí ọkàn obìnrin tí ó ń rọbí.'), 'Jer$49:9': ('wá', None, 'Jer$49:9 Tí àwọn tí ń ṣa èso bá tọ̀ ọ́ wá; ǹjẹ́ wọn kò ní fi èso díẹ̀ sílẹ̀? Tí olè bá wá ní òru; ǹjẹ́ wọn kò ní kó gbogbo ohun tí wọ́n bá fẹ́?'), 'Jer$49:22': (None, 'obinrin', 'Jer$49:22 "Wò ó! Ẹyẹ idì yóò gòkè fò wálẹ̀, yóò tẹ ìyẹ́ rẹ̀ lórí Bóráṣà. Ní ọjọ́ náà ọkàn àwọn ajagun Édómù yóò dàbí ọkan obìnrin tí ń rọbí."'), 'Jer$50:4': ('wá', 'lọ', 'Jer$50:4 "“Ní ọjọ́ wọ̀nyí àti ní àkókò náà,” ni OLÚWA wí, “Àwọn ọmọ Ísírẹ́lì yóò jùmọ̀ wá, àwọn, àti àwọn ọmọ Júdà, wọn yóò lọ pẹ̀lú ẹkún láti ṣàfẹ́ri OLÚWA Ọlọ́run wọn"'), 'Jer$50:15': (None, 'gba', 'Jer$50:15 "Kígbe mọ-ọn ní gbogbo ọ̀nà! Ó tẹríba, òpó rẹ̀ sì yẹ̀, níwọ̀n ìgbà tí ó ti jẹ́ wí pé èyí ni ìgbẹ̀san OLÚWA, gbẹ̀san lára rẹ̀. Ṣe síi gẹ́gẹ́ bí ó ti ṣe sí àwọn ẹlòmìíràn."'), 'Jer$50:17': (None, 'akoko', 'Jer$50:17 "“A ti fọ́n Ísírẹ́lì ká, Kìnnìún sì ti lé e lọ. Ẹni àkọ́kọ́ tí ó kọ́kọ́ jẹ ẹ́ ni Ọba Ásíríà, ẹni tí ó sì jẹ eegun rẹ̀ kẹ́yìn ni Nebukadinésárì Ọba Bábílónì.”"'), 'Jer$51:20': (None, None, 'Jer$51:20 "“Ìwọ ni kùmọ̀ ohun èlò ogun mi, ohun èlò ìjà mi, pẹ̀lú rẹ èmi ó fọ́ orílẹ̀ èdè túútúú, èmi ó bà àwọn ilé Ọba jẹ́."'), 'Jer$51:34': ('kun', None, 'Jer$51:34 "“Nebukadinésárì Ọba Bábílónì tó jẹ wá run, ó ti mú kí ìdààmú bá wa, ó ti sọ wá di àgbá òfìfo. Gẹ́gẹ́ bí ejò, ó ti gbé wa mì. Ó fi oúnjẹ àdídùn wa kún inú rẹ̀, lẹ́yìn náà ni “Ó pọ̀ wá jáde."'), 'Jer$51:39': ('ji', 'sun', 'Jer$51:39 "Ṣùgbọ́n nígbà tí ọkàn wọn bá ru sókè, èmi yóò se àsè fún wọn, èmi yóò jẹ́ kí wọn mutí yó débi tí wọn yóò máa kọ ẹ̀rín lẹ́yìn náà, wọn yóò sun oorun àsùnjí,” ni OLÚWA wí."'), 'Jer$51:50': ('wá', 'lọ', 'Jer$51:50 "Ẹ̀yin tí ó ti bọ́ lọ́wọ́ idà, ẹ lọ, ẹ má dúró: Ẹ rántí OLÚWA ní òkèrè, ẹ sì jẹ́ kí Jérúsálẹ́mù wá sí ọkàn yín.” "'), 'Jer$51:57': ('ji', 'sun', 'Jer$51:57 "Èmi ó sì mú kí àwọn ìjòyè rẹ̀ yó bí ọ̀mùtí, àti àwọn ọlọ́gbọ́n rẹ̀ àti àwọn alákòóṣo rẹ̀, àti àwọn akọni rẹ̀, wọn ó sì sun oorun láéláé, wọn kì ó sì jí mọ́,” ní Ọba wí, ẹni tí orúkọ rẹ̀ ń jẹ́ OLÚWA ọmọ ogun."'), 'Lam$1:14': (None, None, 'Lam$1:14 “Ẹ̀ṣẹ̀ mi ti di síṣopọ̀ sí àjàgà; ọwọ́ ọ rẹ̀ ni a fi hun ún papọ̀. Wọ́n ti yí ọrùn mi ká OLÚWA sì ti dín agbára mi kù. Ó sì ti fi mí lé àwọn tí n kò le dojúkọ lọ́wọ́.'), 'Lam$2:18': (None, None, 'Lam$2:18 "Ọkàn àwọn ènìyàn kígbe jáde sí OLÚWA. Odi ọmọbìnrin Síónì, jẹ́ kí ẹkún rẹ̀ ṣàn bí odò ní ọ̀sán àti òru; má ṣe fi ara rẹ fún ìtura, ojú rẹ fún ìsinmi. "'), 'Lam$2:21': (None, 'odo', 'Lam$2:21 “Ọmọdé àti àgbà ń sùn papọ̀ sínú eruku àwọn òpópó; àwọn ọ̀dọ́mọkùnrin àti ọ̀dọ́mọbìnrin mi ti ṣègbé nípa idà. Ìwọ pa wọ́n run ní ọjọ́ ìbínú rẹ; Ìwọ pa wọ́n láìní àánú.'), 'Lam$3:2': ('okunkun', 'imole', 'Lam$3:2 Ó ti lémi jáde ó sì mú mi rìn nínú òkùnkùn ju ti ìmọ́lẹ̀;'), 'Lam$3:38': (None, 'dara', 'Lam$3:38 Ǹjẹ́ kì í ṣe láti ẹnu ẹni ńlá ni dídára àti búburú tí ń wá?'), 'Lam$4:18': (None, 'wá', 'Lam$4:18 "Wọ́n ń ṣọ́ wa kiri, àwa kò sì le rìn ní òpópó wa mọ́. Òpin wa ti súnmọ́, ọjọ́ wa sì níye nítorí òpin wa ti dé. "'), 'Ezek$1:10': ('osi', 'ọtun', 'Ezek$1:10 " Báyìí ni ìrísí ojú àwa ẹ̀dá alààyè yìí: ọ̀kọ̀ọ̀kan wọn ní ojú ènìyàn, ní apa ọ̀tún wọn, wọ́n ní ojú kìnnìún ní ìhà ọ̀tún, wọ́n ní ojú màlúù ní ìhà òsì, ọ̀kọ̀ọ̀kan wọ́n sì tún ní ojú ẹyẹ idì."'), 'Ezek$1:13': ('soke', None, 'Ezek$1:13 "Ìrísí àwọn ẹ̀dá alààyè yìí dàbí ẹyin iná tí ń jó tàbí bí i iná tọ́ọ̀sì. Iná tó ń jó ń lọ sókè lọ sódò láàrin àwọn ẹ̀dá alààyè; iná yìí mọ́lẹ̀ rokoṣo, ó sì ń bù yẹ̀rì yẹ̀rì jáde lára rẹ̀."'), 'Ezek$1:27': (None, None, 'Ezek$1:27 "Láti ibi ìbàdí ènìyàn náà dé òkè, o dàbí irin tí ń kọ yànrànyànràn. O dàbí pe kìkì iná ni, láti ibi ìbàdí rẹ dé ìsàlẹ̀ dàbí iná tó mọ́lẹ̀ yí i ká."'), 'Ezek$7:10': (None, None, 'Ezek$7:10 "“Ọjọ́ náà ti dé! O ti dé: Ìparun ti bú jáde, ọ̀pá ti tan ná, ìgbéraga ti sọ jáde!"'), 'Ezek$8:2': (None, 'sisale', 'Ezek$8:2 "Nígbà tí mo wò, mo rí ohun kan tí o jọ ènìyàn. Láti ibi ìbàdí rẹ lọ sísàlẹ̀ dàbí iná, láti ibi ìbàdí yìí sókè sì mọ́lẹ̀ bí idẹ tó ń kọ mọ̀nà."'), 'Ezek$9:5': (None, None, 'Ezek$9:5 "Bí mo ṣe ń fetí sí èyí, O tún sọ fún àwọn yòókù pé, “Tẹ̀lé ọkùnrin náà lọ sáàrin ìlú láti pa láì dásí àti láì ṣàánú rárá."'), 'Ezek$10:14': ('keji', None, 'Ezek$10:14 " Ọ̀kọ̀ọ̀kan àwọn kérúbù yìí ni ojú mẹ́rin: ojú èkínní jẹ́ ojú kérúbù, ojú kejì jẹ́ ti ènìyàn, ojú kẹta jẹ́ ti kìnnìún nígbà tí ojú kẹrin jẹ́ ti ẹyẹ idì."'), 'Ezek$11:19': (None, 'fun', 'Ezek$11:19 " N ó fún wọn ní ọ̀kan kan; èmi yóò sì fi ẹ̀mí tuntun sínú wọn; N ó mú ọkàn òkúta kúrò lára wọn, n ó sì fún wọn ní ọkàn tó rọ̀ bí ara ẹran."'), 'Ezek$12:16': (None, None, 'Ezek$12:16 "Ṣùgbọ́n n ó ṣẹ́ díẹ̀ kù lára wọn, lọ́wọ́ idà, ìyàn àti àjàkálẹ̀-àrùn, kí wọn le jẹ́wọ́ gbogbo ìṣe ìríra wọn láàrin àwọn orílẹ̀ èdè yìí. Nígbà náà ni wọn ó mọ̀ pé Èmi ni OLÚWA.”"'), 'Ezek$13:17': ('ọmọbinrin', 'ọmọ', 'Ezek$13:17 "“Nísinsin yìí, ọmọ ènìyàn, dojú kọ àwọn ọmọbìnrin ènìyàn rẹ tí wọ́n ń sọ tẹ́lẹ̀ láti inú èrò ọkàn wọn, sọtẹ́lẹ̀ sí wọn"'), 'Ezek$14:19': (None, None, 'Ezek$14:19 "“Tàbí bí mo rán àjàkálẹ̀-àrùn si ilẹ̀ náà, ti mo si da ìrunu mi lé e lórí pẹ̀lú ẹ̀jẹ̀, láti gé ènìyàn àti ẹranko kúrò nínú rẹ̀,"'), 'Ezek$16:1': (None, None, 'Ezek$16:1 Ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$16:25': (None, None, 'Ezek$16:25 "Ní gbogbo òpin ojú pópó lo kọ ojúbọ gíga sí tó o sì fi ẹwà rẹ̀ wọlé, o sọ àgbèrè rẹ di púpọ̀ nípa fífi ara rẹ sílẹ̀ fún gbogbo ẹni tó ń kọjá lọ."'), 'Ezek$16:30': (None, None, 'Ezek$16:30 "“OLÚWA Ọlọ́run wí pé, ‘Báwo ni ọkàn rẹ ṣe jẹ aláìlera tó tí o n ṣe gbogbo nǹkan wọ̀nyí, gẹ́gẹ́ bí iṣẹ́ àwọn agídí alágbérè!"'), 'Ezek$16:35': (None, None, 'Ezek$16:35 "“ ‘Nítorí náà, ìwọ alágbèrè, gbọ́ ọ̀rọ̀ OLÚWA!"'), 'Ezek$16:39': (None, 'gba', 'Ezek$16:39 "Nígbà náà ni èmi yóò fà ọ lé àwọn olólùfẹ́ rẹ lọ́wọ́, wọn yóò sì wó gbogbo òkìtì rẹ pẹ̀lú àwọn ojúbọ rẹ palẹ̀. Wọn yóò tú aṣọ kúrò lára rẹ̀, gbogbo ọ̀ṣọ́ rẹ ni wọn yóò gbà, wọn yóò sì fi ọ sílẹ̀ ní ìhòòhò àti ààbò."'), 'Ezek$16:45': ('baba', 'iya', 'Ezek$16:45 "Ọmọ ìyà rẹ ni ọ lóòtọ́, to korìíra ọkọ rẹ àti àwọn ọmọ rẹ, bẹ́ẹ̀ ni ara ilé àwọn arábìnrin rẹ ni ọ nitootọ, àwọn to n korìíra ọkọ wọn sílẹ̀, tó tún ń korìíra àwọn ọmọ. Ará Híítì ni ìyá rẹ, baba rẹ si jẹ́ ara Ámórì."'), 'Ezek$16:46': (None, None, 'Ezek$16:46 "Ẹ̀gbọ́n rẹ obìnrin ni Samaríà, òun àti àwọn ọmọbìnrin rẹ̀ n gbé ni apá aríwá rẹ, Sódómù sì ni àbúrò rẹ, obìnrin tó ń gbé pẹ̀lú àwọn ọmọbìnrin rẹ ni ìhà gúúsù rẹ."'), 'Ezek$17:17': (None, 'ṣe', 'Ezek$17:17 "Nígbà tí wọ́n bá pa bùdó ogun tì í, tí wọ́n sì mọ odi láti pa ọ̀pọ̀ ènìyàn, Fáráò pẹ̀lú àwọn ọmọ ogun àti àjọ ńlá rẹ̀ kò ní lè ṣe ìrànwọ́ fún-un lójú ogun."'), 'Ezek$17:24': (None, 'giga', 'Ezek$17:24 "Gbogbo igi inú oko yóò mọ̀ pé, Èmi OLÚWA ni ó mú igi gíga walẹ̀, tí mo sì mú kúkurú ga sókè, tí mo mu igi tútù gbẹ, tí mo sì mú igi gbígbẹ rúwé. “ ‘Èmi OLÚWA ni ó sọ bẹ́ẹ̀, Èmi yóò sì se e.’ ”"'), 'Ezek$18:5': (None, None, 'Ezek$18:5 "“Bí ọkùnrin Olódodo kan bá wà, tó ń ṣe ohun tó tọ́, tó sì yẹ"'), 'Ezek$19:6': (None, None, 'Ezek$19:6 "Ó sì ń rìn káàkiri láàrin àwọn kìnnìún nítorí pé ó ti lágbára, o kọ ọdẹ ṣíṣe, ó sì pa àwọn ènìyàn."'), 'Ezek$20:45': (None, None, 'Ezek$20:45 Ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$21:16': ('ọtun', 'osi', 'Ezek$21:16 "Ìwọ idà, jà sí ọ̀tún kí o sì jà sí òsì lọ ibikíbi tí ẹnu rẹ bá dojúkọ"'), 'Ezek$21:25': (None, None, 'Ezek$21:25 "“ ‘Ìwọ aláìmọ́ àti ẹni búburú ọmọ aládé Ísírẹ́lì, ẹni ti ọjọ́ rẹ̀ ti dé, ẹni tí àsìkò ìjìyà rẹ̀ ti dé góngó,"'), 'Ezek$22:7': ('baba', 'iya', 'Ezek$22:7 "Nínú rẹ wọn ti hùwà sí baba àti ìyá pẹ̀lú ìfojú tínrín; nínú rẹ wọn ti ni àwọn àlejò lára, wọn sì hùwà-kúwà sí aláìní baba àti opó."'), 'Ezek$22:26': (None, 'mimọ', 'Ezek$22:26 "Àwọn àlùfáà rẹ̀ ti rú ofin mi, wọn si ti sọ ohun mímọ́ mi di àìlọ́wọ̀: wọn kò fi ìyàtọ̀ sáàrin ohun mímọ́ àti àìlọ́wọ̀, bẹ́ẹ̀ ni wọn kò fi ìyàtọ̀ hàn láàrin ohun àìmọ́, àti mímọ́, wọn sì ti fi ojú wọn pamọ́ kúrò ní ọjọ ìsinmi mi, mó sì dí ẹni àìlọ́wọ̀ láàrin wọn."'), 'Ezek$23:1': (None, None, 'Ezek$23:1 Ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$24:14': (None, 'lọ', 'Ezek$24:14 "“ ‘Èmi OLÚWA ni ó sọ ọ́, yóò sì ṣe, èmi yóò sì ṣe é. Èmi kì yóò pada sẹ́yìn; bẹ́ẹ̀ ni èmi kì yóò dá sí i, bẹ́ẹ̀ ni èmi kì yóò yí ọkàn mi padà; gẹ́gẹ́ bí ọ̀nà rẹ, àti gẹ́gẹ́ bí iṣẹ́ rẹ, ni wọn yóò dá ọ lẹ́jọ́, ni OLÚWA Ọlọ́run wí.’ ”"'), 'Ezek$24:17': ('ẹsẹ', 'ori', 'Ezek$24:17 "Má ṣe sunkún, má ṣe gbààwẹ̀ fún òkú. Wé ọ̀já sí orí rẹ, sì bọ bàtà sí ẹsẹ̀ rẹ: má ṣe bo ètè ìsàlẹ̀ rẹ, ma ṣe jẹ oúnjẹ tí àwọn tí ń sọ̀fọ̀ ń jẹ gẹ́gẹ́ bí àṣà.”"'), 'Ezek$24:25': (None, 'ọmọ', 'Ezek$24:25 "“Pẹ̀lúpẹ̀lù ìwọ, Ọmọ ènìyàn, kì yóò ha jẹ́ pé, ní ọjọ́ náà, nígbà ti mo bá gba agbára wọn, ayọ̀ wọn àti ògo wọn, ìfẹ́ ojú wọn, ohun tí wọn gbé ọkàn wọn lé, àti àwọn ọmọ ọkùnrin wọn pẹ̀lú àwọn ọmọ obìnrin wọn kúrò lọ́dọ̀ wọn,"'), 'Ezek$26:4': (None, None, 'Ezek$26:4 "Wọn yóò wó odi Tírè lulẹ̀, wọn yóò sì wo ilé ìṣọ́ rẹ̀ lulẹ̀; Èmi yóò sì ha ẹrùpẹ̀ rẹ̀ kúrò, Èmi yóò sì sọ ọ́ di orí àpáta."'), 'Ezek$26:12': ('ṣe', None, 'Ezek$26:12 "Wọn yóò kó ọrọ̀ rẹ, wọn yóò sì fi òwò rẹ ṣe ìjẹ ogun; wọn yóò sì wó odi rẹ lulẹ̀, wọn yóò sì ba àwọn ilé rẹ dídára jẹ́, wọn yóò sì kó àwọn òkúta rẹ̀, àti igi ìtì ìkọ́lé rẹ̀, àti erùpẹ̀ rẹ̀, dà sí inú òkun."'), 'Ezek$26:16': (None, 'joko', 'Ezek$26:16 " Nígbà náà ni gbogbo àwọn ọmọ aládé etíkun yóò sọ̀ kalẹ̀ láti orí ìtẹ́ wọn, wọn yóò sì pa àwọn aṣọ ìgúnwà wọn tì wọn yóò sì bọ́ àwọn ẹ̀wù wọn tí a ṣiṣẹ́ ọ̀nà sí lára kúrò. Ẹ̀rù yóò bò wọ́n, wọn yóò sì jòkòó lórí ilẹ̀ pẹ̀pẹ̀, wọn yóò sì máa wárìrì ní gbogbo ìgbà, ẹnu yóò sì yà wọ́n sí ọ́."'), 'Ezek$28:14': (None, None, 'Ezek$28:14 "A fi àmì òróró yàn ọ́ gẹ́gẹ́ bí olùtọ́jú kérúbù, torí èyí ni mo fi yàn ọ́. Ìwọ wà lórí òkè mímọ́ Ọlọ́run; ìwọ rìn ni àárin òkúta amúbína,"'), 'Ezek$29:17': (None, None, 'Ezek$29:17 "Ó sì ṣe ní ọjọ́ kìn-ín-ní, oṣù kìn-ín-ní, ọdún kẹtàdínlọ́gbọ̀n, ọ̀rọ̀ OLÚWA tọ̀ mí wá wí pé:"'), 'Ezek$30:9': ('wá', 'lọ', 'Ezek$30:9 "“ ‘Ní ọjọ́ náà oníṣẹ́ yóò ti ọ̀dọ̀ mi jáde nínú ọkọ̀ ojú omi láti dẹ́rùbà Kúsì kúrò nínú ìtẹ́lọ́rùn rẹ̀. Ìrora yóò wá sórí wọn gẹ́gẹ́ bí ti ọjọ́ ìparun Éjíbítì: Kíyèsí i, ó dé."'), 'Ezek$30:20': (None, None, 'Ezek$30:20 Ní ọjọ́ keje osù kìn-ín-ní ọdún kọkànlá ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$32:7': ('ṣokunkun', 'imole', 'Ezek$32:7 "Nígbà tí mo bá fọ́n ọ jáde, èmi yóò pa ọ̀run dé àwọn ìràwọ̀ wọn yóò sì ṣókùnkùn; èmi yóò sì fi ìkùukùu bo oòrùn òṣùpá kì yóò sì tan ìmọ́lẹ̀ rẹ̀"'), 'Ezek$32:8': (None, 'ṣokunkun', 'Ezek$32:8 "Gbogbo ìmọ́lẹ̀ títàn ní ojú ọ̀run ni èmi yóò mú ṣókùnkùn lórí rẹ; èmi yóò mú òkùnkùn wá sórí ilẹ rẹ, ni OLÚWA àwọn ọmọ-ogun wí"'), 'Ezek$32:17': ('wá', None, 'Ezek$32:17 "Ní ọjọ́ kẹẹdógún oṣù, ọdún kejìlá, ọ̀rọ̀ OLÚWA tọ mí wá:"'), 'Ezek$34:20': (None, None, 'Ezek$34:20 "“ ‘Nítorí náà, èyí yìí ni OLÚWA àwọn ọmọ-ogun wí fún wọn: Wòó, èmi fúnra mi yóò ṣe ìdájọ́ láàárin àgùntàn tí ó sanra àti èyí tí ó rù."'), 'Ezek$36:16': (None, None, 'Ezek$36:16 Síwájú síi ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$36:26': (None, 'fun', 'Ezek$36:26 "Èmi yóò fún yín ni ọkàn tuntun, èmi yóò sì fi ẹmi tuntun sínú yín; Èmi yóò sì mú ọkàn òkúta kúrò nínú yín, èmi yóò sì fi ọkàn ẹran fún yín."'), 'Ezek$36:31': (None, None, 'Ezek$36:31 " Nígbà náà ni ẹ̀yin yóò rántí àwọn ọ̀nà búburú àti àwọn ìwà ìkà yín, ẹ̀yin yóò sì korìíra ara yín fún ẹ̀ṣẹ̀ yín, ìwà tí kò bójúmu."'), 'Ezek$38:10': (None, None, 'Ezek$38:10 "“ ‘Èyí ni OLÚWA àwọn ọmọ-ogun wí: Ní ọjọ́ náà èrò kan yóò wá ṣọ́kan rẹ, ìwọ yóò sì pète ìlànà búbúru."'), 'Ezek$39:1': (None, None, 'Ezek$39:1 "“Ọmọ ènìyàn, ṣọtẹ́lẹ̀ sí Gógù, kí ó sì wí pé: ‘Èyí yìí ni ohun tí OLÚWA àwọn ọmọ-ogun wí: Èmi lòdì sí ọ, Ìwọ Gógì, olórí ọmọ-aládé ti Mésékì àti Túbálì."'), 'Ezek$40:44': (None, None, 'Ezek$40:44 "Lẹ́yìn náà ó wọn àgbàlá: Ó jẹ́ ọgọ́rùn-ún ìgbọ̀nwọ́ ní gígùn, ó sì jẹ́ ọgọ́rùn-ún ìgbọ̀nwọ́ ní fífẹ. Pẹpẹ sì wà ní iwájú tẹ́ḿpìlì."'), 'Ezek$42:13': ('ariwa', None, 'Ezek$42:13 "Ó sì wí fún mi pé, “Àwọn yàrá àríwá àti àwọn yàrá gúsù, tí ó wà níwájú ibi tí a yà sọ́tọ̀ àwọn ni yàrá mímọ́, níbi tí àwọn àlùfáà ti ń súnmọ́ OLÚWA yó máa jẹ́ ohun mímọ́ jùlọ; níbẹ̀ ní wọn o máa gbé ohun mímọ́ jùlọ kà, àti ọrẹ ẹbọ jíjẹ, àti ọrẹ ẹbọ ẹ̀ṣẹ̀, àti ọrẹ ẹbọ ìrékọjá; nítorí ibẹ̀ jẹ́ mímọ́"'), 'Ezek$43:14': (None, None, 'Ezek$43:14 "Láti ìsàlẹ̀ rẹ̀ ni ilẹ̀ títí dé ìsàlẹ̀ igun rẹ̀, ó jẹ́ ìgbọ̀nwọ́ méjì ni gíga o sì jẹ́ ìgbọ̀nwọ́ kan ní fífẹ̀:"'), 'Ezek$44:12': (None, 'ṣubu', 'Ezek$44:12 "Ṣùgbọ́n nítorí pé wọn ṣe ìránṣẹ́ fún wọn níwájú àwọn ère wọn, tí wọn sì mu ilé Ísírẹ́lì subú sínú ẹ̀ṣẹ̀, nítorí náà, Èmi tí búra nípa nína ọwọ́ sókè pé, wọn gbọdọ̀ jèrè ẹ̀ṣẹ̀ wọn, ní OLÚWA Ọba sọ."'), 'Ezek$44:15': ('lọ', 'wá', 'Ezek$44:15 "“ ‘Ṣùgbọ́n àwọn àlùfáà, tí ó jẹ́ Léfì àti àwọn ìran Sádómù tí fi tọkàntọkàn ṣe iṣẹ́ ibi mímọ́ mi nígbà tí àwọn Ísírẹ́lì ṣáko lọ kúrò lọ́dọ̀ mi, wọn gbọdọ̀ súnmọ́ iwájú láti ṣe ìránṣẹ́ ní iwájú mi, láti rú ẹbọ ọ̀rá àti ẹ̀jẹ̀, ni OLÚWA Ọba sọ."'), 'Ezek$44:23': ('alaimọ', 'mimọ', 'Ezek$44:23 "Wọn ní láti fi ìyàtọ̀ tí ó wà nínú mímọ́ àti aláìmọ́ kọ́ àwọn ènìyàn mi, kí wọn sì fi bi wọn yóò ṣe dá aláìmọ́ mọ̀ yàtọ̀ sí mímọ́ hàn wọ́n."'), 'Ezek$44:25': ('baba', 'iya', 'Ezek$44:25 "“ ‘Àlùfáà kò gbọdọ̀ sọ ara rẹ̀ di aláìmọ́ nípa sísún mọ́ òkú ènìyàn; ṣùgbọ́n ti òkú ènìyàn yẹn bá jẹ́ bàbá tàbí ìyá rẹ̀, àbúrò rẹ̀ ọkùnrin tàbí àbúrò rẹ̀ obìnrin ti kò i ti lọ́kọ, nígbà náà o le sọ ara rẹ̀ di aláìmọ́."'), 'Ezek$45:2': (None, None, 'Ezek$45:2 "Lára èyí, apákan ti ẹ̀gbẹ́ rẹ̀ kọ̀ọ̀kan tí o jẹ́ ẹ̀ẹ́dégbẹ̀ta ìgbọ̀nwọ́ ní ó gbọdọ̀ wà fún ibi ìyàsímúmọ́ fún Ọlọ́run, pẹ̀lú ibẹ̀ tí ó jẹ́ àádọ́ta ìgbọ̀nwọ́ fún ilẹ̀ tí ó yí ibi mímọ́ náà ká. Ilẹ̀ tí ó yí i ká yìí yóò wà bẹ́ẹ̀ láì lò ó."'), 'Ezek$45:7': (None, None, 'Ezek$45:7 "“ ‘Àwọn ọmọ aládé ni yóò bá ibi mímọ́ pààlà ní ẹ̀gbẹ́ kọ̀ọ̀kan agbégbé tí ó jẹ ibi mímọ́ àti ohun tí ó jẹ́ ti ìlú. Yóò fẹ̀ sẹ́yìn sí ìhà ìwọ̀ oòrùn láti apá ìwọ̀ oòrùn àti si ìlà oòrùn, láti apá ìlà oòrùn, gígùn rẹ̀ yóò jẹ́ láti ìwọ̀ oòrùn sí ààlà ìlà oòrùn, ti ìṣe déédéé rẹ̀ yóò jẹ́ ti ọ̀kan lára àwọn ìpín ẹ̀yà."'), 'Ezek$46:9': ('wá', None, 'Ezek$46:9 "“ ‘Nígbà tí àwọn ènìyàn ilẹ̀ náà bá wá ṣíwájú OLÚWA ni àwọn àjọ tí a yàn, ẹnikẹ́ni tí ó bá gba ti ẹnu ọ̀nà ìhà àríwá wọlé láti jọ́sìn gbọdọ̀ gba ti ẹnu ọ̀nà ìhà gúsù jáde; ẹnikẹ́ni tí ó bá sì gba tí ẹnu ọ̀nà ìhà àríwá jáde. Kò sí ẹni tí ó gbọdọ̀ gba ibi tí ó bá wọlé padà, ṣùgbọ́n kí olúkúlùkù gba òdìkejì ẹnu ọ̀nà tí ó gbà wọlé jáde."'), 'Ezek$46:12': ('sé', None, 'Ezek$46:12 "Nígbà tí ọmọ aládé bá pèsè ọrẹ àtinúwá fún OLÚWA yálà ọrẹ ẹbọ sísun tàbí ọrẹ ẹbọ ìdàpọ̀ ojú ọ̀nà tí ó dojúkọ ìhà ìlà oòrùn ni kí ó wà ni sísí sílẹ̀ fún-un Òun yóò rú ọrẹ ẹbọ sísun rẹ̀ tàbí ọrẹ ẹbọ ìdàpọ̀ gẹ́gẹ́ bí òun ti ṣe ní ọjọ́ ìsinmi. Lẹ́yìn náà, oun yóò jáde, lẹ́yìn ìgbà tí ó bá jáde tán wọn yóò ti ẹnu ọ̀nà."'), 'Ezek$46:18': (None, 'fun', 'Ezek$46:18 "Ọmọ aládé kò gbọdọ̀ mú ìkankan lára ogún ìní àwọn ènìyàn, tàbí mú wọn kúrò níbi ohun ìní wọn. Ó ní láti fi ogún ìní rẹ̀ láti inú ìní rẹ̀ fún àwọn ọmọ rẹ̀, kí a ma baà ya ìkankan kúrò lára ìní rẹ̀’ ”"'), 'Ezek$47:14': (None, None, 'Ezek$47:14 "Ìwọ yóò pín ilẹ̀ náà déédéé ní àárin wọn. Nítorí pé mo búra nípa nína ọwọ́ sókè láti fi fún àwọn baba ńlá yin, ilẹ̀ yìí yóò di ogún ìní yín."'), 'Ezek$48:2': (None, None, 'Ezek$48:2 “Àsárì yóò ní ìpín kan; yóò jẹ́ ààlà agbégbé dánì láti ìlà oòrùn lọ sí ìwọ oòrùn.'), 'Ezek$48:3': (None, None, 'Ezek$48:3 “Náfítalì yóò ní ìpín kan; èyí yóò jẹ́ ààlà agbégbé Ásárì láti ìlà oòrùn lọ sí ìwọ̀ oòrùn.'), 'Ezek$48:4': (None, None, 'Ezek$48:4 “Mánásè yóò ní ìpín; èyí yóò jẹ́ ààlà ti agbégbé Náfátalì láti ìlà oòrùn sí ìwọ̀ oòrùn.'), 'Ezek$48:5': (None, None, 'Ezek$48:5 “Éfúráímù yóò ní ìpín kan; èyí yóò jẹ́ ààlà agbégbé Mánásè láti ìlà oòrùn lọ sí ìwọ oòrùn.'), 'Ezek$48:6': (None, None, 'Ezek$48:6 “Rúbẹ́nì yóò ní ìpín kan; èyí yóò jẹ́ ààlà agbégbé Éfúráímù láti ìlà oòrùn títí dé ìwọ̀ oòrùn.'), 'Ezek$48:7': (None, None, 'Ezek$48:7 “Júdà yóò ní ìpín kan; èyí yóò jẹ́ ààlà agbégbé Rúbẹ́nì láti ìlà oòrùn lọ sí ìwọ̀ oòrùn.'), 'Ezek$48:8': (None, None, 'Ezek$48:8 "“Ààlà agbégbé Júdà láti ìlà oòrùn dé ìwọ̀ oòrùn yóò jẹ́ ìpín tí ìwọ yóò gbé kalẹ̀ gẹ́gẹ́ bí ẹ̀bùn pàtàkì. Yóò jẹ́ ẹgbẹ̀rún mẹ́ẹ̀dọ́gbọ̀n ìgbọ̀nwọ́ (25,000) ní fífẹ̀, gígùn rẹ̀ láti ìlà oòrùn lọ sí ìwọ̀ oòrùn yóò jẹ́ bákan náà pẹ̀lú àwọn ìpín ẹ̀yà kọ̀ọ̀kan; ilé mímọ́ yóò wà ní àárin gbùngbùn rẹ̀."'), 'Ezek$48:10': (None, 'ariwa', 'Ezek$48:10 "Èyí yóò jẹ́ ìpín ibi mímọ́ fún àwọn àlùfáà. Yóò jẹ́ ẹgbẹ̀rún mẹ́ẹ̀dọ́gbọ̀n (25,000) ìgbọ̀nwọ́ ni gígùn ní ìhà àríwá, ẹgbẹ̀rún mẹ́wàá (10,000) ìgbọ̀nwọ́ ni ìhà ìlà oòrùn, ó sì jẹ́ ẹgbẹ̀rún mẹ́ẹ̀dọ́gbọ̀n (25,000) ìgbọ̀nwọ́ ní gígùn ní ìhà gúsù. Ní àárin gbùngbùn rẹ̀ ní ilé fún OLÚWA yóò wà."'), 'Ezek$48:16': (None, None, 'Ezek$48:16 " Ìwọ̀nyí sì ní bi a ṣe wọ̀n ọ́n: ní ìhà àríwá ẹgbẹ̀rún mẹ́rin ó lé ẹ̀ẹ́dẹ́gbẹ̀ta (4,500) ìgbọ̀nwọ́, ni ìhà gúsù ẹgbẹ̀rún mẹ́rin ó lé ẹ̀ẹ́dẹ́gbẹ̀ta (4,500) ìgbọ̀nwọ́, ní ìhà ìlà oòrùn ẹgbẹ̀rún mẹ́rin ó lé ẹ̀ẹ́dẹ́gbẹ̀ta (4,500) ìgbọ̀nwọ́ àti ní ìhà ìwọ̀ oòrùn ẹgbẹ̀rún mẹ́rin ó lé ẹ̀ẹ́dẹ́gbẹ̀ta (4,500) ìgbọ̀nwọ́."'), 'Ezek$48:17': (None, None, 'Ezek$48:17 "Ilẹ̀ ìjẹ fún àwọn ẹran ọ̀sìn fún ìlú ńlá náà yóò jẹ igba àti àádọ́ta (250) ìgbọ̀nwọ́ ní ìhà gúsù, ìgbà àti ìgbà pẹ̀lú àádọ́ta (250) ìgbọ̀nwọ́ ní ìhà ìwọ̀ oòrùn."'), 'Ezek$48:21': (None, None, 'Ezek$48:21 "“Èyí tí ó sẹ́kù ni ẹ̀gbẹ́ méjèèjì agbégbé tí ó jẹ́ ìpín ibi mímọ́ àti ohun ìní ìlú ńlá náà yóò jẹ́ ti àwọn ọmọ aládé. Yóò lọ títí dé ìhà ìlà oòrùn láti ẹgbẹ̀rún mẹ́ẹ̀dọ́gbọ̀n (25,000) ìgbọ̀nwọ́ ti ìpín ibi mímọ́ títí dé ààlà ìlà oòrùn. Agbégbé méjèèjì wọ̀nyí ni gígùn àwọn ìpín ẹlẹ́yàmẹ́yà yóò jẹ́ ti àwọn ọmọ aládé, àti ìpín ibi mímọ́ pẹ̀lú tẹ́ḿpìlì yóò wa ní àárin wọn."'), 'Ezek$48:23': (None, None, 'Ezek$48:23 “Níti àwọn ẹ̀yà tí ó kù: Bẹ́ńjámínì yóò ní ìpín kan; Yóò wà láti ìhà ìlà oòrùn títí dé ìhà ìwọ̀ oòrùn.'), 'Ezek$48:24': (None, None, 'Ezek$48:24 “Símónì yóò ní ìpín kan: Yóò jẹ́ ààlà agbégbé Bẹ́ńjámínì láti ìlà oòrùn lọ sí ìwọ̀ oòrùn.'), 'Ezek$48:25': (None, None, 'Ezek$48:25 “Ísàkárì yóò ní ìpín kan: yóò jẹ́ ààlà agbégbé Símónì láti ìlà oòrùn sí ìwọ̀ oòrùn.'), 'Ezek$48:26': (None, None, 'Ezek$48:26 “Sébúlónì yóò ní ìpín kan; yóò jẹ́ ààlà agbégbé Isàkárì láti ìlà oòrùn sí ìwọ̀ oòrùn.'), 'Ezek$48:27': (None, None, 'Ezek$48:27 “Gádì yóò ní ìpín; yóò jẹ́ ààlà agbégbé Sébúlónì láti ìlà oòrùn sí ìwọ̀ oòrùn.'), 'Dan$2:20': (None, None, 'Dan$2:20 Dáníẹ́lì wí pé: “Ọpẹ́ ni fún orúkọ OLÚWA láé àti láéláé; tirẹ̀ ni ọgbọ́n àti agbára'), 'Dan$2:32': (None, None, 'Dan$2:32 "Orí ère náà jẹ́ kìkì wúrà, àyà rẹ̀ àti ọwọ́ ọ rẹ̀ jẹ́ fàdákà, inú àti ẹ̀gbẹ́ itan rẹ̀ jẹ́ idẹ,"'), 'Dan$2:44': (None, None, 'Dan$2:44 "“Ní àsìkò àwọn ọba náà, Ọlọ́run ọ̀run yóò gbé ìjọba èyí tí kò le è bàjẹ́ kalẹ̀, èyí tí a kò ní fi lé ẹlòmíràn lọ́wọ́. Yóò sì run gbogbo ìjọba, yóò sì mú wọn wá sí òpin, ṣùgbọ́n ìjọba yìí yóò dúró láéláé.”"'), 'Dan$4:13': ('wá', None, 'Dan$4:13 "“Lórí ibùsùn mi, mo rí ìran náà, olùṣọ́ kan dúró ṣíwájú u mi, àní ẹni mímọ́ kan, ó ń bọ̀ wá láti ọ̀run"'), 'Dan$4:23': ('bọ', None, 'Dan$4:23 "“Ìwọ ọba, rí ìránṣẹ́ ẹni mímọ́ kan, tí ó ń bọ̀ láti ọ̀run ó sì sọ pé, ‘Gé igi náà kí o sì run ún, ṣùgbọ́n fi kùkùté rẹ tí a dè pẹ̀lú irin àti idẹ sílẹ̀ nínú koríko igbó, nígbà tí gbòǹgbò rẹ̀ sì wà nínú ilẹ̀ kí o sì jẹ́ kí ìrì ọ̀run ṣẹ̀ sórí i rẹ̀, jẹ́ kí ìpín in rẹ̀ wà láàrin ẹranko búburú títí ìgbà méje yóò fi kọjá lórí i rẹ̀.’"'), 'Dan$5:10': ('ayaba', 'ọba', 'Dan$5:10 "Nígbà tí ayaba gbọ́ ohùn ọba àti àwọn ọlọ́lá rẹ̀, ó wá ilé àsè wá. Ó wí pé, “Kí ọba kí ó pẹ́! Má ṣe jẹ́ kí inú un rẹ bàjẹ́, má sì ṣe jẹ́ kí ojú u rẹ fàro."'), 'Dan$7:13': (None, None, 'Dan$7:13 " “Nínú ìran mi ní òru mo wò, mo rí ẹnìkan tí ó dúró sí iwájú u mi, ó rí bí ọmọ ènìyàn, ó ń bọ̀ wá nínú àwọ̀ọsánmọ̀ ọ̀run, ó ń bọ̀ wá sí ọ̀dọ̀ ẹni ìgbàanì, a sì mú u wá sí iwájú rẹ̀."'), 'Dan$7:19': ('ẹsẹ', None, 'Dan$7:19 "“Nígbà náà, ni mo fẹ́ mọ ìtúmọ̀ òtítọ̀ ẹranko kẹrin, tí ó yàtọ̀ sí àwọn yóòkù, èyí tí ó dẹ́rù ba ni gidigidi, tí ó ní eyín irin àti èékánná idẹ, ẹranko tí ó ń run tí ó sì ń pajẹ, tí ó sì ń fi ẹsẹ̀ tẹ èyí tó kù mọ́lẹ̀."'), 'Dan$11:6': (None, 'ariwa', 'Dan$11:6 "Lẹ́yìn ọdún púpọ̀ wọn yóò dá májẹ̀mú àlàáfíà, ọmọbìnrin ọba gúṣù yóò lọ sí ọ̀dọ̀ ọba àríwá láti bá a dá májẹ̀mú àlàáfíà, ṣùgbọ́n òun kì yóò lè di agbára apá a rẹ̀ mú, bẹ́ẹ̀ ni òun kì yóò lè dá dúró, ṣùgbọ́n a ó tẹrí i rẹ̀ ba àti àwọn tí ó mú un wá, àti ọmọ tí ó bí, àti ẹni tí ó ń mu lọ́kàn le ní gbogbo àkókò wọ̀nyí."'), 'Dan$11:11': (None, 'ariwa', 'Dan$11:11 "“Nígbà náà ni ọba Gúṣù yóò jáde pẹ̀lú ìbínú, yóò sì bá ọba àríwá jà, ẹni tí yóò kó ọmọ ogun púpọ̀ jọ, ṣùgbọ́n ọba Gúṣù yóò borí i wọn."'), 'Dan$11:15': ('ariwa', None, 'Dan$11:15 "Nígbà náà ni ọba àríwá yóò wá yóò sì gbé ogun tìí, yóò sì kó ìlú olódi. Ogun ọba Gúṣù kò ní ní agbára láti kọjú ìjà sí i; Bẹ́ẹ̀ ni àwọn ọ̀wọ́ ogun tó dára jù kò ní ní agbára láti dúró."'), 'Dan$11:29': (None, None, 'Dan$11:29 "“Ní àsìkò tí a yàn, yóò gbógun sí Gúṣù lẹ́ẹ̀ kan sí i, ṣùgbọ́n ní ìgbà yí àyọrísí yóò yàtọ̀ sí ti ìṣáájú."'), 'Dan$11:40': ('ariwa', None, 'Dan$11:40 "“Ní ìgbà ìkẹyìn, ọba Gúṣù yóò gbé ogun de sí i, ọba àríwá yóò sì jáde bí ìjì láti kọ lù ú pẹ̀lú u kẹ̀kẹ́ ogun àti ẹlẹ́ṣin àti ọ̀pọ̀lọpọ̀ ọkọ̀ ojú omi. Yóò gbógun wọ orílẹ̀ èdè púpọ̀, yóò sì gbá wọn mọ́lẹ̀ bí ìkún omi."'), 'Dan$12:7': ('osi', 'ọtun', 'Dan$12:7 " Ọkùnrin tí ó wọ aṣọ àlà, ẹni tí ó wà lórí omi odò, gbé ọwọ́ ọ̀tún rẹ̀ àti ọwọ́ òsì rẹ̀, mo gbọ́ tí ó fi ẹni tí ó wà títí láé búra, ó sọ wí pé, “Yóò ṣe ní àkókò kan, àkókò méjì àti ààbọ̀. Nígbà tí agbára àwọn ẹni mímọ́ yóò ti fọ́ tán pátapáta, gbogbo nǹkan wọ̀nyí yóò sì parí.”"'), 'Hos$1:3': ('ọmọbinrin', 'ọmọ', 'Hos$1:3 "Nígbà náà ni ó sì lọ, ó sì fẹ́ Gómérì ọmọbìnrin Díbíláémù, ọmọbìnrin náà sì lóyún, ó sì bí ọmọkùnrin kan fún un."'), 'Hos$2:2': ('ọkọ', 'iyawo', 'Hos$2:2 "“Ẹ bá ìyáa yín wí, ẹ bá a wí, nítorí pé kì í ṣe ìyàwó mi, Èmi náà kì í sì í ṣe ọkọ rẹ̀. Jẹ́ kí ó yọ àgbèrè rẹ̀ kúrò lójú rẹ̀ àti àìsòótọ́ kúrò ní àyà rẹ̀."'), 'Hos$2:9': (None, 'gba', 'Hos$2:9 "“Nítorí náà, èmi yóò mú ọkà mi kúrò nígbà tó bá pọ́n, èmi yóò sì mú wáìnì mi kúrò ní àsìkò rẹ̀. Èmi yóò sì gba ìrun àgùntàn irun àgùtàn àti ọ̀gbọ̀ mi padà mo ti fifun un láti bo ìhòòho rẹ̀."'), 'Hos$2:18': ('ṣe', None, 'Hos$2:18 "Ní ọjọ́ náà, èmi yóò ṣe májẹ̀mú fún wọn àti àwọn ẹranko ìgbẹ́ àti ẹyẹ ojú ọ̀run àti àwọn ẹ̀dá tí ń rìn lórí ilẹ̀. Kí wọn má bà á bẹ̀rù ara wọn mọ́ Ọrun, idà àti ogun jíjà ni èmi ó parun ní ilẹ̀ náà kí gbogbo ènìyàn bá a lè wà ní àìléwu."'), 'Hos$4:5': (None, None, 'Hos$4:5 Ẹ ń ṣubú lọ́sàn-án àti lóru àwọn wòlíì yín sì ń ṣubú pẹ̀lúu yín Èmi ó pa ìyá rẹ run'), 'Hos$4:15': ('wá', 'lọ', 'Hos$4:15 "“Bí ó tilẹ̀ jẹ́ pé o ṣe àgbèrè ìwọ Ísírẹ́lì Ìdájọ́ yìí wà fún un yín Ẹ má ṣe jẹ́ kí Júdà di ẹlẹ́bi. “Ẹ má ṣe lọ sí Gílígálì. Ẹ má sì ṣe gòkè lọ sí Bẹti-Áfénì Ẹ má sì búra pé, ‘Bí OLÚWA ti wà láàyè nítòótọ́!’"'), 'Hos$6:3': (None, None, 'Hos$6:3 "Ẹ jẹ́ kí a mọ OLÚWA Ẹ jẹ́ kí a tẹ̀ṣíwájú láti mọ̀ ọ́. Gẹ́gẹ́ bí oòrùn ṣe ń yọ, Yóò jáde; Yóò tọ̀ wá wá bí òjò bí òjò àkọ́rọ̀ ti ń bomirin ilẹ̀.”"'), 'Hos$7:9': (None, None, 'Hos$7:9 "Àwọn àlejò ti jẹ agbára rẹ̀ run ṣùgbọ́n kò sì mọ̀, Ewú ti wà ní orí rẹ̀ káàkiri bẹ́ẹ̀ ni kò kíyèsíi"'), 'Hos$9:15': (None, 'ife', 'Hos$9:15 "“Nítorí gbogbo ìwà búburú tí wọ́n hù ní Gílígálì Mo kórìírà wọn níbẹ̀, Nítorí ìwà ẹ̀ṣẹ̀ wọn. Èmi yóò lé wọn jáde ní ilé mi Èmi kò ní ní ìfẹ́ wọn mọ́ ọlọ̀tẹ̀ ni gbogbo olórí wọn."'), 'Hos$10:11': (None, 'fọ', 'Hos$10:11 "Éfúráímù jẹ́ ọmọ màlúù tí a tí kọ́, to si fẹ́ràn láti máa pa ọkà lórí ọrun rẹ̀ tó lẹ́wà ni èmi ó dí ẹru wúwo lé Èmi yóò mú kí a gun Éfúráímù bí ẹṣin Júdà yóò tú ilẹ̀, Jákọ́bù yóò sì fọ́ ogúlùtu rẹ̀"'), 'Joel$2:23': (None, None, 'Joel$2:23 "Ǹjẹ́ jẹ́ kí inú yín dùn, ẹ̀yin ọmọ Ṣíónì, ẹ sì yọ̀ nínú OLÚWA Ọlọ́run yín, nítorí ó ti fi àkọ́rọ̀ òjò fún yín bí ó ti tọ́, Òun ti mú kí òjò rọ̀ sílẹ̀ fún un yín, àkọ́rọ̀ àti àrọ̀kúrò òjò ní oṣù kìn-ín-ní."'), 'Joel$2:28': (None, 'odo', 'Joel$2:28 " “Yóò sì ṣe, èmi yóò tú ẹ̀mi mí sí ara ènìyàn gbogbo; àti àwọn ọmọ yín ọkùnrin, àti àwọn ọmọ yín obìnrin yóò máa ṣọtẹ́lẹ̀, àwọn arúgbó yín yóò máa lá àlá, àwọn ọ̀dọ́mọ́kunrìn yín yóò máa ríran."'), 'Joel$3:3': (None, None, 'Joel$3:3 "Wọ́n si ti di ibò fún àwọn ènìyàn mi; wọ́n sì ti fi ọmọdé-kùnrin kan fún panṣágà obìnrin kan, wọ́n sì ta ọmọdé bìnrin kan fún ọtí wáìnì, kí wọ́n kí ó lè mu."'), 'Joel$3:9': (None, 'lagbara', 'Joel$3:9 "Ẹ kéde èyí ní àárin àwọn aláìkọlà; Ẹ dira ogun, ẹ jí àwọn alágbára, Jẹ kí awọn ajagun kí wọn bẹ̀rẹ̀ ogun,"'), 'Amos$5:2': ('ṣubu', 'dide', 'Amos$5:2 “Wúndíá Ísírẹ́lì ṣubú láì kò sì le padà dìde ó di ẹni ìkọ̀tì ní ilẹ̀ rẹ̀ kò sí ẹni tí yóò gbé e nàró.”'), 'Amos$5:4': (None, None, 'Amos$5:4 Èyí ni ohun tí OLÚWA sọ fún ilé Ísírẹ́lì: “Wá mi kí o sì yè;'), 'Amos$5:7': (None, None, 'Amos$5:7 Ẹ̀yin tí ẹ̀ sọ òdodo di ìkorò Tí ẹ sì gbé olódodo ṣánlẹ̀'), 'Amos$5:14': (None, None, 'Amos$5:14 "Wá rere, má ṣe wá búburú kí ìwọ ba à le yè Nígbà náà ni OLÚWA alágbára yóò wà pẹ̀lú ù rẹ. Òun yóò sì wà pẹ̀lú ù rẹ bí ìwọ ṣe wí"'), 'Amos$5:15': (None, None, 'Amos$5:15 Kòrìírà búburú kí o sì fẹ́ rere Dúró ní orí òtítọ́ ní ilé ẹjọ́ Bóyá OLÚWA Ọlọ́run alágbára yóò síjú àánú wo ọmọ Jósẹ́fù tó ṣẹ́kù'), 'Amos$8:6': ('ta', 'ra', 'Amos$8:6 Kí àwa lè fi fàdákà ra àwọn talákà kí a sì fi bàtà ẹsẹ̀ méjèèjì ra aláìní kí a sì ta jéró tí a kó mọ́ ilẹ̀'), 'Amos$8:9': (None, 'lọ', 'Amos$8:9 "“Yóò sì ṣe ní ọjọ́ náà,” ni OLÚWA Ọlọ́run wí, “Èmi yóò mú òòrùn wọ̀ ní ọ̀sán, Èmi yóò mú kí ayé ṣókùnkùn ní ọ̀ṣán gangan."'), 'Amos$8:14': ('ṣubu', 'dide', 'Amos$8:14 "Àwọn tí ó fi òrìṣà Samaríà búra tí wọ́n wí pé, ‘tí wọ́n sì wí pé, ìwọ Dánì, ọlọ́run rẹ ń bẹ láàyè,’ àti bi ó ti dájú pé ọlọ́run Báṣébà ti ń bẹ láàyè àní, wọn yóò ṣubú, wọn kì yóò sì tún dìde mọ́.’ láàyè ìwọ Dánì.’ Bí ó ti dájú pè ọlọrun rẹ ń bẹ láàyè ìwọ Báṣébà Wọ́n yóò ṣubú, Wọn kì yóò si tún díde mọ.”"'), 'Amos$9:2': (None, None, 'Amos$9:2 "Bí wọ́n tilẹ wa ilẹ lọ sí ìpò òkú Láti ibẹ̀ ni ọwọ́ mi yóò ti tẹ̀ wọ́n Bí wọ́n tilẹ̀ gun òkè ọ̀run lọ, láti ibẹ̀ ni èmi yóò ti mú wọn sọ̀kalẹ̀."'), 'Jonah$3:1': ('wá', None, 'Jonah$3:1 Ọ̀rọ̀ OLÚWA sì tọ Jónà wá nigbà kejì wí pé:'), 'Jonah$4:10': (None, None, 'Jonah$4:10 "Nígbà náà ni OLÚWA wí pé, “Ìwọ kẹ́dùn ìtàkùn náà, nítorí èyí tí ìwọ kò ṣiṣẹ́, tí ìwọ kò mu dàgbà; tí ó hù jáde ní òru kan tí ó sì kú ni òru kan."'), 'Mic$1:12': (None, 'ibi', 'Mic$1:12 "Nítorí àwọn tí ó ń gbé ni Márátì ń retí ire, Ṣùgbọ́n ibi sọ̀kalẹ̀ ti ọ̀dọ̀ OLÚWA wá sí ẹnu bodè Jérúsálẹ́mù."'), 'Mic$2:13': (None, 'wá', 'Mic$2:13 "Ẹni tí ó ń fọ ọ̀nà yóò lọ sókè níwájú wọn; Wọn yóò fọ láti ẹnu-bodè, wọn yóò sì jáde lọ. Àwọn ọba wọn yóò sì kọjá lọ níwájú wọn, OLÚWA ni yóò sì ṣe olórí wọn.”"'), 'Mic$3:2': (None, None, 'Mic$3:2 "Ẹ̀yin tí ó kórìíra ire, tí ẹ sì fẹ́ràn ibi; Ẹ̀yin tí ẹ fa awọ-ẹran ara àwọn ènìyàn mi ya kúrò lára wọn àti ẹran-ara wọn kúrò ní egungun wọn;"'), 'Mic$3:6': ('ọjọ', None, 'Mic$3:6 "Nítorí náà òru yóò wá sórí yín, tí ẹ̀yin kì yóò sì rí ìran kankan, òkùnkùn yóò sì kùn fún yín, tí ẹ̀yin kì yóò sì lè ṣọ tẹ́lẹ̀ Oòrùn yóò sì yọ lórí àwọn wòlíì ọjọ́ yóò sì ṣókùnkùn lórí wọn."'), 'Mic$4:2': ('wá', 'lọ', 'Mic$4:2 "Ọ̀pọ̀lọpọ̀ orílẹ̀-èdè yóò wá, wọn yóò sì wí pé, “Ẹ wá, ẹ jẹ́ kí á gòkè ńlá OLÚWA, àti sí ilé Ọlọ́run Jákọ́bù Òun yóò sì kọ́ wa ní ọ̀nà rẹ̀, àwa ó sì máa rìn ní ipa ọ̀nà rẹ̀.” Nítorí láti Síónì ni òfin yóò ti jáde wá, àti ọ̀rọ̀ Ọlọ́run láti Jérúsálẹ́mù."'), 'Mic$6:14': (None, 'fun', 'Mic$6:14 "Ìwọ yóò jẹun ṣùgbọ́n kì yóò yó; ìyàn yóò wà láàrin rẹ; Ìwọ yóò kó pamọ́, ṣùgbọ́n ìwọ kì yóò lò làìléwu, nítorí ohun tí ìwọ kò pamọ́ ni èmi yóò fi fún idà."'), 'Mic$7:6': ('baba', 'iya', 'Mic$7:6 " Nítorí tí ọmọkùnrin kò bọ̀wọ̀ fún baba rẹ̀ ọmọbìnrin sì dìde sí ìyá rẹ̀, aya ọmọ sí ìyá ọkọ rẹ̀, ọ̀tá olúkúlùkù ni àwọn ará ilé rẹ̀."'), 'Mic$7:8': ('okunkun', 'imole', 'Mic$7:8 "Má ṣe yọ̀ mí, ìwọ ọ̀tá mi; Bí mó bá ṣubú, èmi yóò dìde. Nígbà tí mo bá jókòó sínú òkùnkùn OLÚWA yóò jẹ́ ìmọ́lẹ̀ fún mi."'), 'Nah$2:11': (None, None, 'Nah$2:11 "Níbo ni ihò àwọn kìnnìún wà àti ibi ìjẹun àwọn ọmọ kìnnìún, níbi tí kìnnìún, àní abo kìnnìún ti ń rìn, àti ọmọ kìnnìún, láìsí ohun ìbẹ̀rù"'), 'Hab$2:20': (None, 'jẹ ki', 'Hab$2:20 Ṣùgbọ́n OLÚWA wà nínú tẹ́ḿpílì mímọ́ rẹ̀; Ẹ jẹ́ kí gbogbo ayé pa rọ́rọ́ níwájú rẹ.”'), 'Zeph$1:12': (None, None, 'Zeph$1:12 "Ní àkókò wọ̀n-ọn-nì, èmi yóò wá Jérúsálẹ́mù kiri pẹ̀lú fìtílà, èmi ó sì fi ìyà jẹ àwọn tí kò ní ìtẹ́lọ́rùn, tí wọn sì dàbí àwọn ènìyàn tí ó sinmi sínú gẹ̀dẹ̀gẹ́dẹ̀ wọn, àwọn tí wọn sì ń wí ní ọkàn wọn pé, ‘OLÚWA kì yóò ṣe nǹkan kan tí ó jẹ́ rere tàbí tí ó jẹ́ búburú.’"'), 'Zeph$3:5': (None, None, 'Zeph$3:5 "OLÚWA ni àárin rẹ̀ jẹ́ olódodo; kì yóò ṣe ohun tí kò tọ̀nà. Àràárọ̀ ni ó máa ń mú ìdájọ́ rẹ̀ wá sí ìmọ́lẹ̀, kì í sì kùnà ní gbogbo ọjọ́ tuntun, ṣíbẹ̀ àwọn aláìsòótọ́ kò mọ ìtìjú."'), 'Hag$1:6': ('diẹ', 'pọ', 'Hag$1:6 "Ẹ̀yin ti gbìn ohun ti ó pọ̀. Ṣùgbọ́n ẹ̀yin kórè díẹ̀ níbẹ̀; Ẹ̀yin jẹun, Ṣùgbọ́n ẹ̀yin kò yó. Ẹ̀yin mu ṣùgbọ́n kò tẹ́ ẹ yín lọ́run; ẹ̀yin wọ aṣọ, ṣùgbọ́n kò mú òtútù yin lọ; Ẹ̀yin gba owó iṣẹ́ ṣùgbọ́n ẹ ń gbà á sínú ajádìí àpò.”"'), 'Hag$1:9': ('pọ', 'diẹ', 'Hag$1:9 "“Ẹyin ti ń retí ọ̀pọ̀, ṣùgbọ́n kíyèsíi,, o yípadà sí díẹ̀. Ohun tí ẹ̀yin mú wa ilé, èmi sì fẹ́ ẹ dànù. Nítorí kí ni?” ni OLÚWA àwọn ọmọ-ogun wí. “Nítorí ilé mi tí ó dáhoro; tí olúkúlùkù yín sì ń sáré fún ilé ara rẹ̀."'), 'Hag$2:9': (None, None, 'Hag$2:9 "‘Ògo ìkẹyìn àwọn ọmọ-ogun ilé yìí yóò pọ̀ ju ti ìṣáájú lọ;’ ni OLÚWA àwọn ọmọ ogun wí: ‘Àti ní ìhín yìí ni Èmi ó sì fi àlàáfíà fún ni’, ni OLÚWA àwọn ọmọ ogun wí.”"'), 'Zech$1:7': ('ọjọ', None, 'Zech$1:7 "Ní ọjọ́ kẹrìnlélógún oṣù kọkànlá, tí ó jẹ́, oṣù Sébátì, ní ọdún kejì Dáríúsì, ni ọ̀rọ̀ OLÚWA tọ Sekaráyà, ọmọ Berekáyà ọmọ Idò wòlíì wá, pé,"'), 'Zech$4:1': ('ji', None, 'Zech$4:1 "Ańgẹ́lì tí ó ń bá mi sọ̀rọ̀ sì tún dé, ó sì jí mi, bí ọkùnrin tí a jí lójú oorun rẹ̀,"'), 'Zech$4:11': ('osi', 'ọtun', 'Zech$4:11 "Mo sì béèrè, mo sì sọ fún un pé, “Kí ni àwọn igi ólífì méjì wọ̀nyí jásí, tí ó wà ní apá ọ̀tún fìtílà àti ní apá òsì rẹ̀?”"'), 'Zech$6:2': (None, 'dudu', 'Zech$6:2 Àwọn ẹṣin pupa wà ní kẹ̀kẹ́ èkínní; àti àwọn ẹṣin dúdú ní kẹ̀kẹ́ èkejì.'), 'Zech$6:6': (None, 'ariwa', 'Zech$6:6 Àwọn ẹṣin dúdú tí ó wà nínú rẹ̀ jáde lọ sí ilẹ̀ àríwá; àwọn funfun sì jáde tẹ̀lé wọn; àwọn adíkálà sì jáde lọ sí ìhà ilẹ̀ gúsù.”'), 'Zech$6:9': ('wá', None, 'Zech$6:9 "Ọ̀rọ̀ OLÚWA sì tọ̀ mí wá, wí pé:"'), 'Zech$8:4': ('obinrin', None, 'Zech$8:4 "Báyìí ni OLÚWA àwọn ọmọ-ogun wí: “Arúgbó ọkùnrin, àti arúgbó obìnrin, yóò gbé ìgboro Jérúsálẹ́mù, àti olúkúlùkù pẹ̀lú ọ̀pá ni ọwọ́ rẹ̀ fún ogbó."'), 'Zech$8:7': (None, None, 'Zech$8:7 "Báyìí ní OLÚWA àwọn ọmọ-ogun wí pé: “Kiyesi i, èmi ó gba àwọn ènìyàn mi kúrò ni ilẹ̀ ìlà-oòrùn, àti kúrò ni ilẹ̀ ìwọ̀-oòrùn."'), 'Zech$8:16': (None, None, 'Zech$8:16 " Wọ̀nyí ni nǹkan tí ẹ̀yin ó ṣe: Ẹ ṣọ̀rọ̀ òtítọ́, olúkúlúkù sí ẹnikejì rẹ̀; ṣe ìdájọ́ tòótọ́ àti àlàáfíà ní àwọn ibodè yín."'), 'Zech$8:20': ('wá', None, 'Zech$8:20 "Báyìí ni OLÚWA àwọn ọmọ-ogun wí pé: “Àwọn ènìyàn yóò ṣa tún wa, àti ẹni tí yóò gbe ìlú-ńlá púpọ̀."'), 'Zech$8:23': ('wá', 'lọ', 'Zech$8:23 "Báyìí ni OLÚWA àwọn ọmọ-ogun wí pé: “Ní àwọn ọjọ́ wọ̀nyí ni ọkùnrin mẹ́wàá láti inú gbogbo èdè àti orílẹ̀-èdè yóò dì í mú, àní yóò di etí aṣọ ẹni tí i ṣe Júù mú, wí pé, ‘Àwa yóò ba ọ lọ, nítorí àwa tí gbọ́ pé, Ọlọ́run wà pẹ̀lú rẹ.’ ”"'), 'Zech$11:10': ('ṣe', None, 'Zech$11:10 "Mo sì mu ọ̀pá mi, ti a ń pè ní Oore-ọ̀fẹ́, mo ṣẹ́ ẹ méjì, ki èmi bá lè da májẹ̀mu mi tí mo tí bá gbogbo àwọn ènìyàn náà dá."'), 'Zech$12:6': ('ọtun', 'osi', 'Zech$12:6 "“Ní ọjọ́ náà, ni èmi yóò se àwọn baálẹ̀ Júdà bí ààrò iná kan láàrin igi, àti bi ẹfúùfù iná láàrin ìtí; wọn yóò sì jẹ gbogbo àwọn ènìyàn run yíká lápá ọ̀tún àti lápá òsì: a ó sì tún máa gbé inú Jérúsálẹ́mù ní ipò rẹ̀."'), 'Zech$13:3': ('baba', 'iya', 'Zech$13:3 "Yóò sì ṣe, nígbà tí ẹnìkan yóò ṣọtẹ́lẹ̀ ṣíbẹ̀, ni baba rẹ̀ àti ìyá rẹ̀ tí ó bí í yóò wí fún un pé, ‘Ìwọ ki yóò yè: nítorí ìwọ ń sọ ọ̀rọ̀ èké ni orúkọ OLÚWA.’ Àti baba rẹ̀ àti ìyá rẹ̀ tí o bí í yóò gun un ni àgúnpa nígbà tí ó bá ṣọtẹ́lẹ̀."'), 'Zech$14:6': ('ọjọ', None, 'Zech$14:6 "Yóò sì ṣe ni ọjọ́ náà, ìmọ́lẹ̀ kì yóò mọ́, bẹ́ẹ̀ ni kì yóò ṣókùnkùn."'), 'Zech$14:16': (None, 'lọ', 'Zech$14:16 "Yóò sì ṣe, olúkúlùkù ẹni tí o kù nínú gbogbo àwọn orílẹ̀-èdè tí ó dìde sí Jérúsálẹ́mù yóò máa gòkè lọ lọ́dọọdún láti sìn Ọba, OLÚWA àwọn ọmọ-ogun, àti láti ṣe àjọyọ̀ àṣè àgọ́ náà."'), 'Zech$14:18': ('lọ', 'wá', 'Zech$14:18 " Bí ìdílé Éjíbítì kò bá sì gòkè lọ, tí wọn kò sì wá, fi ara wọn hàn tí wọn kò ní òjò; àrùn náà yóò wà, tí OLÚWA yóò fi kọlù àwọn aláìkọlà tí kò gòkè wá láti se àjọyọ̀ àsè àgọ́ náà"'), 'Matt$1:2': (None, None, 'Matt$1:2 "Ábúráhámù ni baba Ísáákì; Ísáákì ni baba Jákọ́bù; Jákọ́bù ni baba Júdà àti àwọn arákùnrin rẹ̀, "'), 'Matt$2:22': (None, 'lọ', 'Matt$2:22 "Ṣùgbọ́n nígbà tí ó gbọ́ pé Ákílọ́sì ni ó ń jọba ní Jùdíà ní ipò Hẹ́rọ́dù baba rẹ̀, ó bẹ̀rù láti lọ sí ì bẹ̀. Nítorí tí Ọlọ́run ti kìlọ̀ fún un ní ojú àlá, ó yí padà, ó sì gba ẹkùn Gálílì lọ,"'), 'Matt$4:12': (None, None, 'Matt$4:12 Nígbà tí Jésù gbọ wí pé a ti fi Jòhánù sínu túbú ó padà sí Gálílì.'), 'Matt$5:23': ('wá', None, 'Matt$5:23 "“Nítorí náà, nígbà tí ìwọ bá ń mú ẹ̀bùn rẹ wá ṣíwájú pẹpẹ, bí ìwọ bá sì rántí níbẹ̀ pé arákùnrin rẹ̀ ni ohùn kan nínú sí ọ."'), 'Matt$5:36': ('dudu', 'funfun', 'Matt$5:36 "Má ṣe fi orí rẹ búra, nítorí ìwọ kò lè sọ irun ẹyọ kan di funfun tàbí di dúdú."'), 'Matt$5:37': (None, None, 'Matt$5:37 "Ẹ jẹ́ kí bẹ́ẹ̀ ni yín jẹ́ bẹ́ẹ̀ ni àti bẹ́ẹ̀ kọ́ yín jẹ́ bẹ́ẹ̀ kọ́, ohunkóhun tí ó ba ju ìwọ̀nyí lọ, wá láti ọ̀dọ̀ ẹni ibi."'), 'Matt$5:43': (None, None, 'Matt$5:43 " “Ẹ̀yin ti gbọ́ bí òfin ti wí pé, ‘Fẹ́ràn aládúgbo rẹ, kí ìwọ sì kórírà ọ̀ta rẹ̀.’"'), 'Matt$5:44': ('ibi', None, 'Matt$5:44 "Ṣùgbọ́n èmi wí fún yín pé, ẹ fẹ́ràn àwọn ọ̀ta yín kí ẹ sì gbàdúrà fún àwọn tí ń ṣe inúnibíni sí yín,"'), 'Matt$6:3': ('ọtun', 'osi', 'Matt$6:3 "Ṣùgbọ́n bí ìwọ bá ńfi fún aláìní, má ṣe jẹ́ kí ọwọ́ òsì rẹ mọ ohun tí ọwọ́ ọ̀tún rẹ ń ṣe,"'), 'Matt$6:24': (None, None, 'Matt$6:24 " “Kò sìí ẹnìkan tí ó lè sin ọ̀ga méjì. Òun yóò yà kórìíra ọ̀kan tí yóò fẹ́ràn èkejì, tàbí kí ó fara mọ́ ọ̀kan kí ó sì yan èkejì ní ìpọ̀sì. Ẹ̀yin kò lè sin Ọlọ́run àti owó papọ̀."'), 'Matt$6:30': (None, None, 'Matt$6:30 " Ǹjẹ́ bí Ọlọ́run bá wọ koríko igbó ní aṣọ bẹ́ẹ̀, èyí tí ó wà níhìn-ín lónìí ti a sì gbà sínú iná lọ́la, kò ha ṣe ni ṣe yín lọ́ṣọ̀ọ́ tó bẹ́ẹ̀ àti jù bẹ́ẹ̀ lọ, ẹ̀yin tí ìgbàgbọ́ yín kéré?"'), 'Matt$7:11': (None, None, 'Matt$7:11 "Ǹjẹ́ bí ẹ̀yin tí í ṣe ènìyàn búburú bá mọ̀ bí a ti í fi ẹ̀bùn rere fún àwọn ọmọ yín, mélòómélòó ni Baba yín tí ń bẹ ní ọ̀run yóò fi ohun rere fún àwọn tí ó béèrè lọ́wọ́ rẹ̀?"'), 'Matt$7:13': ('gbooro', None, 'Matt$7:13 " “Ẹ máa ba ẹnu-ọ̀nà tóótó wọlé; gbòòrò ni ẹnu-ọ̀nà náà, àti oníbùú ni ojú ọ̀nà náà tí ó lọ sí ibi ìparun, ọ̀pọ̀lọpọ̀ ni àwọn ẹni tí ń bá ibẹ̀ wọlé."'), 'Matt$7:17': (None, None, 'Matt$7:17 Bẹ́ẹ̀ ni gbogbo igi rere a máa so èso rere ṣùgbọ́n igi búburú a máa so èso búburú.'), 'Matt$7:18': (None, None, 'Matt$7:18 "Igi rere kò le so èso búburú, bẹ́ẹ̀ ni igi búburú kò lè so èso rere."'), 'Matt$9:13': ('lọ', 'wá', 'Matt$9:13 "Ṣùgbọ́n ẹ lọ kọ́ bí a ti mọ̀ èyí sí: ‘Àánú ni èmi ń fẹ́, kì í ṣe ẹbọ;’ nítorí èmi kò wá láti pe àwọn olódodo, bí kò ṣe àwọn ẹlẹ́sẹ̀.”"'), 'Matt$9:16': (None, None, 'Matt$9:16 "“Kò sí ẹni tí í fi ìrépé aṣọ tutun lẹ ògbólògbó ẹ̀wù; nítorí èyí tí a fi lẹ̀ ẹ́ yóò ya ní ojú lílẹ̀, aṣọ náà yóò sì ya púpọ̀ sì i ju ti ìṣáájú lọ."'), 'Matt$9:17': (None, 'tuntun', 'Matt$9:17 "Bẹ́ẹ̀ ni kò sí ẹni tí fi ọtí titun sínú ògbólògbó ìgò-awọ; bí a bá ṣe bẹ́ẹ̀, ìgò-àwọ yóò bẹ́, ọtí á sì tú jáde, ìgò-àwọ á sì ṣègbé. Ṣùgbọ́n ọtí titun ni wọn í fi sínú ìgò-àwọ tuntun àwọn méjèèjì a ṣì ṣe dédé.”"'), 'Matt$9:18': ('kú', None, 'Matt$9:18 "Bí ó ti ń sọ nǹkan wọ̀nyí fún wọn, kíyèsí i, ìjòyè kan tọ̀ ọ́ wá, ó sì wólẹ̀ níwájú rẹ̀ wí pé, “Ọmọbìnrin mi kú nísinsìn yìí, ṣùgbọ́n wá fi ọwọ́ rẹ̀ lé e, òun yóò sì yè.”"'), 'Matt$10:19': (None, 'gba', 'Matt$10:19 "Nígbà tí wọ́n bá mú yín, ẹ má ṣe ṣàníyàn ohun tí ẹ ó sọ tàbí bí ẹ ó ṣe sọ ọ́. Ní ojú kan náà ni a ó fi ohun tí ẹ̀yin yóò sọ fún yín,"'), 'Matt$10:21': ('obi', 'ọmọ', 'Matt$10:21 " “Ọmọ-ìyá méjì yóò ṣe ikú pa ara wọ́n. Baba yóò fi àwọn ọmọ rẹ̀ fún ikú pa. Àwọn ọmọ yóò sọ̀tẹ̀ sí òbí wọn, wọn yóò sì mú kí a pa wọ́n.”"'), 'Matt$10:23': (None, None, 'Matt$10:23 " Nígbà tí wọn bá ṣe inúnibíni sì i yín ní ìlú kan, ẹ sá lọ sì ìlú kejì. Lóòótọ́ ni mo wí fún yín, ẹ̀yin kò tì í le la gbogbo ìlú Ísírẹ́lì já tán kí Ọmọ Ènìyàn tó dé."'), 'Matt$10:27': ('okunkun', 'imole', 'Matt$10:27 "Ohun tí mo bá wí fún yín ní òkùnkùn, òun ni kí ẹ sọ ní ìmọ́lẹ̀. Èyí tí mo sọ kẹ́lẹ́lẹ́kẹ́lẹ́ sì etí yín ni kí ẹ kéde rẹ́ lórí òrùlé."'), 'Matt$10:35': ('baba', 'iya', 'Matt$10:35 "Nítorí èmi wá láti “ ‘ọmọkùnrin ní ipa sí bàbá rẹ̀, ọmọbìnrin ní ipa sí ìyá rẹ̀, àti aya ọmọ sí ìyakọ rẹ̀…"'), 'Matt$10:37': ('ọmọ', None, 'Matt$10:37 " “Ẹnikẹ́ni tí ó bá fẹ́ràn bàbá rẹ̀ tàbí ìyá rẹ̀ jù mí lọ kò yẹ ní tèmi, ẹnikẹ́ni tí ó ba fẹ́ràn ọmọ rẹ̀ ọkùnrin tàbí ọmọ rẹ̀ obìnrin jù mí lọ kò yẹ ní tèmi;"'), 'Matt$10:39': (None, None, 'Matt$10:39 "Ẹni tí ó bá rí ẹ̀mí rẹ̀ yóò sọ ọ́ nù, ṣùgbọ́n ẹni tí ó bá sọ ẹ̀mí rẹ̀ nú nítorí tèmi ni yóò rí i."'), 'Matt$12:11': (None, None, 'Matt$12:11 " Ó dá wọn lóhùn pé, “Bí ó bá jẹ́ wí pé bí ẹnikẹ́ni nínú yín bá ní àgùntàn kan ṣoṣo, tí ó sì bọ́ sínú kòtò ní ọjọ́ ìsinmi, tí kì yóò dì í mú, kí ó sì fà á jáde."'), 'Matt$12:34': (None, None, 'Matt$12:34 "Ẹ̀yin ọmọ paramọ́lẹ̀, báwo ni ẹ̀yin tí ẹ jẹ búburú ṣe lè sọ̀rọ̀ rere? Ọkàn ènìyàn ni ó ń darí irú ọ̀rọ̀ tí ó lè jáde lẹ́nu rẹ̀."'), 'Matt$12:35': (None, None, 'Matt$12:35 Ẹni rere láti inú ìsúra rere ọkàn rẹ̀ ní i mú ohun rere jáde wá: àti ẹni búburu láti inú ìsúra búburú ni; mu ohun búburú jáde wá.'), 'Matt$12:45': (None, None, 'Matt$12:45 "Nígbà náà ni ẹ̀mí ẹ̀ṣù náà yóò wá ẹ̀mí méje mìíràn tí ó burú ju òun lọ. Gbogbo wọn yóò sì wá sí inú rẹ̀, wọn yóò máa gbé ibẹ̀. Ìgbẹ̀yìn ọkùnrin náà a sì burú ju ti ìṣáájú lọ; Bẹ́ẹ̀ gẹ́gẹ́ ni yóò rí fún ìran búburú yìí pẹ̀lú.”"'), 'Matt$12:50': ('arabinrin', 'arakunrin', 'Matt$12:50 " Nítorí náà, “Ẹnikẹ́ni tí ó bá ṣe ìfẹ́ Baba mi tí ń bẹ ní ọ̀run, ni arákùnrin mi àti arábìnrin mi àti ìyá mi.”"'), 'Matt$13:47': (None, None, 'Matt$13:47 " “Bákan náà, a sì tún lè fi ìjọba ọ̀run wé àwọ̀n kan tí a jù sínú odò, ó sì kó onírúurú ẹja."'), 'Matt$13:52': (None, 'tuntun', 'Matt$13:52 "Ó wí fún wọn pé, “Nítorí náà ni olúkúlùkù olùkọ́ òfin tí a ti kọ́ nípa ìjọba ọ̀run ṣe dàbí ọkùnrin kan tí í ṣe baálé ilé, tí ó mú ìṣúra tuntun àti èyí tí ó ti gbó jáde láti inú yàrá ìṣúra rẹ̀.”"'), 'Matt$14:15': ('wá', 'lọ', 'Matt$14:15 "Nígbà ti ilẹ̀ ń sú lọ, àwọn ọmọ-ẹ̀yìn rẹ̀ tọ̀ ọ́ wá, wọ́n sì wí pé, “Ibi yìí jìnnà sí ìlú ilẹ̀ sì ń ṣú lọ. Rán àwọn ènìyàn lọ sí àwọn ìletò kí wọ́n lè ra oúnjẹ jẹ.”"'), 'Matt$14:23': ('lọ', 'wá', 'Matt$14:23 "Lẹ́yìn tí ó tú ìjọ ènìyàn ká tán, ó gun orí òkè lọ fúnra rẹ̀ láti lọ gba àdúrà. Nígbà tí ilẹ̀ sú, óun wà ni nìkan níbẹ̀,"'), 'Matt$15:4': ('baba', 'iya', 'Matt$15:4 " Nítorí Ọlọ́run wí pé, ‘Bọ̀wọ̀ fún Baba òun ìyá rẹ,’ àtipé, ẹnikẹ́ni tí ó bá sọ ọ̀rọ̀ òdì sí baba tàbí ìyá rẹ̀, ní láti kú."'), 'Matt$15:5': ('baba', 'iya', 'Matt$15:5 "Ṣùgbọ́n ẹ̀yin wí pé, ‘Ẹnikẹ́ni tí ó bá wí fún baba tàbí ìyá rẹ̀ pé, “Ẹ̀bun fún Ọlọ́run i ohunkóhun tí ìwọ ìbá fi jèrè lára mi;”"'), 'Matt$15:7': (None, None, 'Matt$15:7 "Ẹ̀yin àgàbàgebè, ní òtítọ́ ni Wòlíì Àìsáyà sọ tẹ́lẹ̀ nípa yín wí pé:"'), 'Matt$15:22': ('ọmọ', 'ọmọbinrin', 'Matt$15:22 "Obìnrin kan láti Kénánì, tí ó ń gbé ibẹ̀ wá sí ọ̀dọ̀ rẹ̀. Ó ń bẹ̀bẹ̀, ó sì kígbe pé, “Olúwa, ọmọ Dáfídì, ṣàánú fún mi; ọmọbìnrin mi ní ẹ̀mí èṣù ti ń dá a lóró gidigidi.”"'), 'Matt$15:29': (None, None, 'Matt$15:29 " Jésù ti ibẹ̀ lọ sí òkun Gálílì. Ó gun orí òkè, o sì jókòó níbẹ̀."'), 'Matt$16:25': (None, None, 'Matt$16:25 "Nítorí ẹnikẹ́ni tí ó bá fẹ́ gba ẹ̀mí rẹ̀ là, yóò sọ ẹ̀mí rẹ̀ nù ṣùgbọ́n ẹnikẹ́ni tí ó bá sọ ẹ̀mí rẹ̀ nítorí mi, yóò rí i."'), 'Matt$16:26': ('ere', None, 'Matt$16:26 "Èrè kí ni ó jẹ́ fún ènìyàn bí ó bá jèrè gbogbo ayé yìí, tí ó sì sọ ẹ̀mí rẹ̀ nù? Tàbí kí ni ènìyàn yóò fi dípò ẹ̀mí rẹ̀?"'), 'Matt$17:27': ('fun', None, 'Matt$17:27 " ‘Ṣíbẹ̀ṣíbẹ̀, àwa kò fẹ́ mú wọn bínú’ Nítorí náà, ẹ lọ sí etí òkun, kí ẹ sì sọ ìwọ̀ sí omi. Ẹ mú ẹja àkọ́kọ́ tí ẹ kọ́ fà sókè, ẹ ya ẹnu rẹ̀, ẹ̀yin yóò sì rí owó idẹ kan níbẹ̀, ki ẹ fi fún wọn fún owó-orí tèmi àti tirẹ̀.”"'), 'Matt$19:4': (None, None, 'Matt$19:4 "Ó dáhùn pé, “A ti rí i kà pé ‘ní ìpilẹ̀sẹ, Ọlọ́run dá wọn ni ọkùnrin àti obìnrin.’"'), 'Matt$19:20': ('ni', None, 'Matt$19:20 "Ọmọdékùnrin náà tún wí pé, “Gbogbo òfin wọ̀nyí ni èmi ti ń pamọ́, kí ni nǹkan mìíràn tí èmi mo ní láti ṣe?”"'), 'Matt$19:21': ('lọ', 'wá', 'Matt$19:21 " Jésù wí fún un pé, “Bí ìwọ bá fẹ́ di ẹni pípé, lọ ta ohun gbogbo tí ìwọ ní, kí o sì fi owó rẹ̀ tọrẹ fún àwọn aláìní. Ìwọ yóò ní ọrọ̀ ńlá ní ọ̀run. Lẹ́yìn náà, wá láti máa tọ̀ mi lẹ́yìn.”"'), 'Matt$19:26': (None, None, 'Matt$19:26 " Jésù sì tẹjú mọ́ wọn, ó wí pé, “Bí o bá jẹ́ ti ènìyàn ni, eléyìí sòro. Ṣùgbọ́n bí ó bá ṣe ti Ọlọ́run ni, ohun gbogbo ni ṣíṣe.”"'), 'Matt$19:29': ('iya', 'baba', 'Matt$19:29 "Àti pé ẹnikẹ́ni tí ó bá fi ilé tàbí arákùnrin tàbí arábìnrin tàbí baba tàbí ìyá tàbí àwọn ọmọ, tàbí ohun ìní rẹ̀ sílẹ̀ nítorí orúkọ mí, yóò gba ọgọọgọ́rùn-ún èrè rẹ̀ láyé, wọn ó sì tún jogún ìyè àìnípẹ̀kun."'), 'Matt$19:30': (None, None, 'Matt$19:30 " Ṣùgbọ́n ọ̀pọ̀ àwọn tí ó ṣíwájú nísinsìn yìí ni yóò kẹ́yìn, àwọn tí ó kẹ́yìn ni yóò sì ṣíwájú.’"'), 'Matt$20:8': (None, None, 'Matt$20:8 " “Nígbà tí ó di àṣálẹ́, ẹni tó ni ọgbà àjàrà sọ fún aṣojú rẹ̀ pé, ‘Pe àwọn òsiṣẹ́ náà, kí ó san owó iṣẹ́ wọn fún wọn, bẹ̀rẹ̀ láti ẹni ìkẹyìn lọ sí ti ìṣáájú.’"'), 'Matt$20:16': (None, None, 'Matt$20:16 " “Bẹ́ẹ̀ ni àwọn ẹni ìkẹyìn yóò di ẹni ìṣáájú, ẹni ìṣáájú yóò sì di ẹni ìkẹyìn.”"'), 'Matt$20:23': ('ọtun', 'osi', 'Matt$20:23 " Jésù sì wí fún wọn pé, “Dájúdájú, ẹ̀yin yóò mu nínú aago mi, ṣùgbọ́n èmi kò ní àṣẹ láti sọ irú ènìyàn tí yóò jòkòó ní apá ọ̀tún tàbí apá òsì. Baba mi ti pèsè àyè wọ́n-ọn-nì sílẹ̀ fún kìkì àwọn tí ó yàn.”"'), 'Matt$21:1': (None, None, 'Matt$21:1 " Bí wọ́n ti sún mọ́ Jérúsalẹmu, tí wọ́n dé ìtòòsí ìlú Bẹ́tífágè ní orí òkè Ólífì, Jésù sì rán àwọn ọmọ-ẹ̀yìn méjì,"'), 'Matt$22:10': (None, 'dara', 'Matt$22:10 " Nítorí náà, àwọn ọmọ-ọ̀dọ̀ náà sì jáde lọ sí òpópónà. Wọ́n sì mú oríṣìíríṣìí ènìyàn tí wọ́n lè rí wá, àwọn tí ò dára àti àwọn tí kò dára, ilé àṣè ìyàwó sì kún fún àlejò."'), 'Matt$22:14': (None, 'diẹ', 'Matt$22:14 “Nítorí ọ̀pọ̀ ni a pè ṣùgbọ́n díẹ̀ ni a yàn.”'), 'Matt$22:25': (None, 'keji', 'Matt$22:25 "Bí ó bá jẹ́ bẹ́ẹ̀, láàrin wa níhìn-in yìí, ìdílé kan wà tí ó ní arákùnrin méje. Èyí èkínní nínú wọn gbéyàwó, lẹ́yìn náà ó kú láìlọ́mọ. Nítorí náà ìyàwó rẹ̀ di ìyàwó arákùnrin kejì."'), 'Matt$22:46': (None, 'idahun', 'Matt$22:46 " Kò sí ẹnì kan tí ó lè sọ ọ̀rọ̀ kan ni ìdáhùn, kò tún sí ẹni tí ó tún bí i léèrè ohun kan mọ́ láti ọjọ́ náà mọ́."'), 'Matt$24:23': (None, None, 'Matt$24:23 "Nígbà náà, bí ẹnikẹ́ni bá sọ fún yín pé, ‘Wo Kírísítì náà,’ tàbí pé ó ti farahàn níhìn-ín tàbí lọ́hùn-ún, ẹ má ṣe gbà á gbọ́."'), 'Matt$25:2': (None, 'ọlọgbọn', 'Matt$25:2 " Márùn-ún nínú wọn jẹ́ ọlọgbọ́n, márùn-ún nínú wọn ni wọ́n jẹ́ aláìgbọ́n."'), 'Matt$25:3': (None, None, 'Matt$25:3 Àwọn tí wọ́n jẹ́ aláìgbọ́n gbé àtùpà wọn ṣùgbọ́n wọn kò mú epo kankan lọ́wọ́.'), 'Matt$25:9': ('ta', 'ra', 'Matt$25:9 "“Ṣùgbọ́n àwọn ọlọ́gbọ́n márùn-ún dáhùn pé, bẹ́ẹ̀ kọ́; kí ó má baà ṣe aláìtó fún àwa àti ẹ̀yin, ẹ kúkú tọ àwọn tí ń tà á lọ, kí ẹ sì rà fún ara yín."'), 'Matt$25:16': (None, None, 'Matt$25:16 "Ọkùnrin tí ó gba tálẹ́ǹtì márùn-ún bẹ̀rẹ̀ lẹ́sẹ̀kẹsẹ̀ láti fi owó náà ṣòwò. Láìpẹ́, ó sì jèrè márùn-ún mìíràn."'), 'Matt$25:21': (None, 'diẹ', 'Matt$25:21 " “Olúwa rẹ̀ wí fún un pé, ‘O ṣeun ìwọ ọmọ-ọ̀dọ̀ rere àti olóòótọ́: ìwọ ṣe olóòótọ́ nínú ohun díẹ̀, èmi yóò fi ọ́ ṣe olórí ohun púpọ̀, bọ́ sínú ayọ̀ olúwa rẹ.’"'), 'Matt$25:23': (None, 'diẹ', 'Matt$25:23 "“Olúwa rẹ̀ sì wí fún un pé ‘O ṣeun, ìwọ ọmọ-ọ̀dọ̀ rere àti olóòótọ́. Ìwọ ti jẹ́ olóòótọ́ nínú ohun díẹ̀, èmi yóò fi ọ ṣe olórí ohun púpọ̀. Ìwọ bọ́ sínú ayọ̀ olúwa rẹ.’"'), 'Matt$25:31': (None, None, 'Matt$25:31 “Ṣùgbọ́n nígbà ti Ọmọ-Ènìyàn yóò wá nínú ògo rẹ̀ pẹ̀lú gbogbo àwọn ańgẹ́lì rẹ̀ nígbà náà ni yóò jókòó lórí ìtẹ́ ògo ní ọ̀run.'), 'Matt$27:38': ('ọtun', 'osi', 'Matt$27:38 "Wọ́n kan àwọn olè méjì pẹ̀lú rẹ̀ ní òwúrọ̀ náà. Ọ̀kan ní ọwọ́ ọ̀tún, àti èkejì ní ọwọ́ òsì rẹ̀."'), 'Matt$27:51': ('isalẹ', 'oke', 'Matt$27:51 " Lójú kan náà aṣọ ìkélé tẹ̀ḿpìlì fàya, láti òkè dé ìsàlẹ̀. Ilẹ̀ sì mì tìtì. Àwọn àpáta sì sán."'), 'Matt$27:57': ('lọ', None, 'Matt$27:57 " Nígbà tí alẹ́ sì lẹ́, ọkùnrin ọlọ́rọ̀ kan láti Arimatíyà, tí à ń pè ní Jósẹ́fù, ọ̀kan nínú àwọn tí ó jẹ́ ọmọ-ẹ̀yìn Jésù,"'), 'Matt$27:64': (None, 'akoko', 'Matt$27:64 "Nítorí náà, pàṣẹ kí a ti ibojì rẹ̀ gbọn-ingbọn-in títí ọjọ́ kẹta, kí àwọn ọmọ-ẹ̀yìn Rẹ̀ má ṣe wá jí i gbé lọ, wọn a sì bẹ̀rẹ̀ sí í sọ fún gbogbo ènìyàn pé, ‘Òun ti jíǹde,’ Bí èyí bá ní láti ṣẹlẹ̀, yóò burú fún wa púpọ̀ ju ti àkọ́kọ́ lọ.”"'), 'Matt$28:1': (None, None, 'Matt$28:1 " Ní kùtùkùtù òwúrọ̀ ọjọ́ ọ̀ṣẹ̀, bí ilẹ̀ ti ń mọ́, Máríà Magidalénì àti Màríà kejì lọ sí ibojì."'), 'Mark$1:38': ('wá', 'lọ', 'Mark$1:38 "Jésù sì dáhùn wí pé, “Ẹ jẹ́ kí a lọ sí àwọn ìlú mìíràn, kí ń lè wàásù níbẹ̀ pẹ̀lú. Nítorí èyí ni èmi sá à ṣe wá.”"'), 'Mark$2:21': (None, 'tuntun', 'Mark$2:21 "“Kò si ẹni tí ń fi ìrépé aṣọ túntún lẹ ògbólógbo ẹ̀wù, bí ó ba se bẹ́ẹ̀, èyí túntún ti a fi lẹ̀ ẹ́ yóò fà ya kúrò lára ògbólógbòó, yíyọ́ rẹ̀ yóò sí burú púpọ̀ jù."'), 'Mark$2:22': (None, 'tuntun', 'Mark$2:22 "Kò sì sí ẹni tí ń fi ọtí wáìnì túntún sínú ìgò wáìni ògbólógbó. Bí a bá ṣe bẹ́ẹ̀, ọtí wáìnì náà yóò fa ìgò náà ya, ọtí wáìnì a si dàànu, bákan náà ni ìgò náà, ṣùgbọ́n ọtí wáìnì tuntun ni a n fi sínú ìgò wáìnì tuntun.”"'), 'Mark$3:4': (None, None, 'Mark$3:4 "Nígbà náà ni Jésù bèèrè lọ́wọ́ wọ́n pé, “Ǹjẹ́ ó bá òfin mu ní ọjọ́ ìsinmi bóyá láti ṣe rere, tàbí láti ṣe búburú! Láti gbà ẹ̀mí là, tàbí pa á run?” Ṣùgbọ́n wọ́n dákẹ́ láìfèsì."'), 'Mark$3:35': ('arabinrin', 'arakunrin', 'Mark$3:35 "Nítorí ẹnikẹ́ni tí ó bá ń ṣe ìfẹ́ Ọlọ́run, òun ni arákùnrin mi àti arábìnrin mi àti ìyá mi.”"'), 'Mark$4:26': (None, None, 'Mark$4:26 " Ó sì tún ọ èyí pé, “Èyí ni a lè fí ìjọba Ọlọ́run wé. Ọkùnrin kan ń fúnrúgbìn sí ilẹ̀."'), 'Mark$4:35': (None, None, 'Mark$4:35 " Nígbà tí alẹ́ lẹ́, Jésù wí fún àwọn ọmọ-ẹ̀yìn rẹ̀ pé, “Ẹ jẹ́ kí a rékọjá sí apákejì.”"'), 'Mark$5:5': (None, None, 'Mark$5:5 Tọ̀sán-tòru láàrin àwọn ibojì àti ní àwọn òkè ni ó máa ń kígbe rara tí ó sì ń fi òkúta ya ara rẹ̀.'), 'Mark$6:7': ('fun', None, 'Mark$6:7 " Ó sì pe àwọn méjìlá náà sọ́dọ̀ rẹ̀, ó bẹ̀rẹ̀ sí rán wọn lọ ní méjì-méjì, Ó sì fi àṣẹ fún wọn lórí ẹ̀mí àìmọ́."'), 'Mark$6:35': ('wá', 'lọ', 'Mark$6:35 "Nígbà tí ọjọ́ sì ti bu lọ tán, àwọn ọmọ-ẹ̀yìn rẹ̀ tọ ọ́ wa, wọ́n wí fún un pé, ibi aṣálẹ̀ ni ìbí yìí, ọjọ́ sì bù lọ tán."'), 'Mark$7:10': ('baba', 'iya', 'Mark$7:10 " Mósè fún un yín ní òfin yìí láti ọ̀dọ̀ Ọlọ́run pé, ‘Bọ̀wọ̀ fún baba àti ìyá rẹ.’ Ó tún sọ pé, ‘Ẹnikẹ́ni tí ó bá sọ̀rọ̀ aburú sí baba tàbí ìyá rẹ̀ ní láti kú ni?’"'), 'Mark$7:12': ('baba', 'iya', 'Mark$7:12 Bẹ́ẹ̀ ni ẹ̀yin kò si jẹ́ kí ó ṣe ohunkóhun fún baba tàbí iyá rẹ̀ mọ́.'), 'Mark$7:30': (None, None, 'Mark$7:30 "Nígbà tí ó náà padà dé ilé, ó bá ọmọbìnrin rẹ̀ ní ìdùbúlẹ̀ jẹ́jẹ́ lórí ibùsùn, ẹ̀mí àìmọ́ náà ti fi í sílẹ̀."'), 'Mark$8:36': ('ere', None, 'Mark$8:36 "Nítorí èrè kí ni ó jẹ fún ẹni tí ó jèrè gbogbo ayé, tí ó sọ ẹ̀mí rẹ̀ nù.?"'), 'Mark$9:35': (None, None, 'Mark$9:35 " Ó jókòó, ó sì pè àwọn méjìlá náà, ó sọ fún wọn pé, “Bí ẹnikẹ́ni bá fẹ́ ṣe ẹni ìṣáájú, òun ni yóò ṣe ẹni ìkẹyìn gbogbo wọn. Ó ní láti jẹ́ ìránṣẹ́ gbogbo ènìyàn.”"'), 'Mark$10:6': (None, None, 'Mark$10:6 " Ṣùgbọ́n láti ìgbà tí ayé ti ṣẹ̀, Ọlọ́run dá wọn ní akọ àti abo."'), 'Mark$10:7': ('baba', 'iya', 'Mark$10:7 " Nítorí ìdí èyí, ọkùnrin yóò fi baba àti ìyá rẹ̀ sílẹ̀ yóò sì faramọ́ aya rẹ̀."'), 'Mark$10:14': (None, None, 'Mark$10:14 "Ṣùgbọ́n nígbà tí Jésù rí ohun tí ó ń ṣẹlẹ̀, inú rẹ̀ kò dùn sí àwọn ọmọ-ẹ̀yìn rẹ̀. Nítorí náà, ó sọ fún wọn pé, “Ẹ jẹ́ kí àwọn ọmọdé kékeré wá sọ́dọ̀ mi. Ẹ má ṣe dá wọn lẹ́kun nítorí pé irú wọ́n ni ìjọba Ọlọ́run."'), 'Mark$10:19': ('baba', 'iya', 'Mark$10:19 " Ìwọ mọ àwọn òfin bí i: Má ṣe pànìyàn, má ṣe panṣágà, má ṣe jalè, má ṣe purọ́, má ṣe rẹ́ ọmọnìkejì jẹ, bọ̀wọ̀ fún baba àti ìyá rẹ.”"'), 'Mark$10:21': ('fun', None, 'Mark$10:21 " Jésù wò ó tìfẹ́tìfẹ́. Ó wí fún un pé, “Bí ó bá rí bẹ́ẹ̀, ohun kan ló kù fún ọ láti ṣe, lọ nísinsìn yìí, ta gbogbo nǹkan tí o ní, kí o sì pín owó náà fún àwọn aláìníìwọ yóò sì ni ìsura ní ọ̀run, sì wá, gbe àgbélébú, kí ó sì máa tọ̀ mí lẹ́yìn.”"'), 'Mark$10:27': (None, None, 'Mark$10:27 "Jésù wò wọ́n? ó sì wí fún wọn pé, “Ènìyàn ní èyí kò se é ṣe fún ṣùgbọ́n kì í ṣe fún Ọlọ́run, nítorí ohun gbogbo ni síṣe fún Ọlọ́run.”"'), 'Mark$10:29': (None, 'baba', 'Mark$10:29 "Jésù dáhùn pé, “Lóòótọ́ ni mo wí fún un yín pé, kò sí ẹnikẹ́ni tí ó fi ohúnkóhun sílẹ̀ bí: ilé, tàbí àwọn arákùnrin, tàbí àwọn arábìnrin, tàbí ìyá, tàbí baba, tàbí àwọn ọmọ tàbí ohun ìní,"'), 'Mark$10:31': (None, None, 'Mark$10:31 " Ṣùgbọ́n ọ̀pọ̀ àwọn tí ó síwájú ni yóò kẹ́yìn, àwọn tí ó sì kẹ́yìn yóò síwájú.”"'), 'Mark$10:37': ('osi', 'ọtun', 'Mark$10:37 " Wọ́n bẹ̀bẹ̀ pé, “Jẹ́ kí ọ̀kan nínú wa jókòó ní ọwọ́ ọ̀tún rẹ àti ẹnikejì ní ọwọ́ òsì nínú ògo rẹ!”"'), 'Mark$11:11': ('lọ', None, 'Mark$11:11 " Jésù wọ Jerúsálémù ó sì lọ sí inú tẹ́ḿpílì. Ó wo ohun gbogbo yíká fínnífínní. Ó sì kúrò níbẹ̀, nítorí pé ilẹ̀ ti sú. Ó padà lọ sí Bẹ́tanì pẹ̀lú àwọn ọmọ-ẹ̀yìn rẹ̀ méjìlá."'), 'Mark$11:15': (None, None, 'Mark$11:15 " Nígbà ti wọ́n padà sí Jerúsálémù, ó wọ inú tẹ́ḿpílì. Ó bẹ̀rẹ̀ sí ní lé àwọn oníṣòwò àti àwọn oníbárà wọn síta. Ó ti tábìlì àwọn tí ń pààrọ̀ owó nínú tẹ́ḿpílì ṣubú. Bákan náà ni ó ti ìjókòó àwọn tí ń ta ẹyẹlé lulẹ̀."'), 'Mark$11:19': (None, None, 'Mark$11:19 " Ní ìrọ̀lẹ́ ọjọ́ náà, bí ìṣe wọn, Jésù àti àwọn ọmọ-ẹ̀yìn rẹ̀ jáde kúrò ní ìlú Jerúsálémù."'), 'Mark$11:29': ('ibeere', None, 'Mark$11:29 "Jésù dá wọn lóhùn pé, “Èmi yóò sọ fún un yín bí ẹ bá lè dáhùn ìbéèrè mi yìí.”"'), 'Mark$12:18': ('wá', None, 'Mark$12:18 " Àwọn Ṣadusí tún wá sọ́dọ̀ rẹ̀, àwọn wọ̀nyí kò gbàgbọ́ pé àjíǹde ń bẹ. Ìbéèrè wọn ni pé,"'), 'Mark$12:27': (None, 'kú', 'Mark$12:27 Òun kì í ṣe Ọlọ́run àwọn òkú bí kò ṣe Ọlọ́run àwọn alààyè: nítorí ẹ̀yin ṣe àṣìṣe gidigidi.”'), 'Mark$13:11': (None, 'gba', 'Mark$13:11 " Ṣùgbọ́n nígbà tí a bá mú yín, tí ẹ sì dúró níwájú adájọ́, ẹ má ṣe dààmú nípa ohun tí ẹ ó wí fún ààbò. Ẹ ṣáà sọ ohun tí Ọlọ́run bá fi sí yín lọ́kàn. Ẹ̀yin kọ́ ní yóò sọ̀rọ̀, bí kò ṣe Ẹ̀mí Mímọ́."'), 'Mark$13:21': (None, None, 'Mark$13:21 "Nígbà náà, bí ẹnikẹ́ni bá sọ fún un yín pé, ‘Ẹ wò ó, Kírísítì nìyìí!’ tàbí, sọ fún un yín pé, ‘Ẹ wò ó, ó wà lọ́hùn-ún nì!’ Ẹ má ṣe gbà wọ́n gbọ́."'), 'Mark$14:30': (None, None, 'Mark$14:30 " Jésù wá wí fún un pé, “Jẹ́ kí ń sọ òótọ́ fún ọ. Kí àkùkọ tóó kọ lẹ́ẹ̀kejì ní òwúrọ̀ ọ̀la, ìwọ, pàápàá yóò ti sọ lẹ́ẹ̀mẹ̀ta wí pe ìwọ kò mọ̀ mí rí.”"'), 'Mark$14:44': (None, None, 'Mark$14:44 "Júdásì tí fí àmì fún wọn wí pe, “Ẹni tí mo bá fi ẹnu kò lẹ́nu nínú wọn, òun ní Jésù, Ẹ mú un.”"'), 'Mark$15:27': ('ọtun', 'osi', 'Mark$15:27 "Wọ́n sì kan àwọn olè méjì mọ́ àgbélébùú pẹ̀lú rẹ̀, ọ̀kan lọ́wọ́ ọ̀tún rẹ̀, èkejì lọ́wọ́ òsì rẹ̀."'), 'Mark$15:36': (None, 'gba', 'Mark$15:36 " Nígbà náà ni ẹnìkan wá sáré lọ ki kàn-ìn-kàn-ìn bọ inú ọtí kíkan, ó fi lé orí ọ̀pá, ó sì nà án sí Jésù kí ó lè mu ún. Ó wí pé, “Ẹ jẹ́ kí a máa wò ó, bóyá Èlíjà yóò wá sọ̀ ọ́ kalẹ̀.”"'), 'Mark$15:38': ('isalẹ', 'oke', 'Mark$15:38 Aṣọ ìkélé tẹ́ḿpìlì sì fàya sí méjì láti òkè dé ìsàlẹ̀.'), 'Mark$15:42': (None, None, 'Mark$15:42 " Ọjọ́ yìí jẹ́ ọjọ́ ìpalẹ̀mọ́ (èyí tí se, ọjọ́ tó sáájú ọjọ́ ìsinmi). Nígbà tí ilẹ̀ ọjọ́ náà sì ṣú,"'), 'Luke$1:53': ('kun', None, 'Luke$1:53 Ó ti fi ohun tí ó dára kún àwọn tí ebí ń pa ó sì rán àwọn ọlọ́rọ̀ padà ní òfo.'), 'Luke$1:76': (None, None, 'Luke$1:76 " “Àti ìwọ, ọmọ mi, wòlíì Ọ̀gá-ògo jùlọ ni a ó máa pè ọ́: Nítorí ìwọ ni yóò ṣáájú Olúwa láti tún ọ̀nà rẹ̀ ṣe;"'), 'Luke$2:15': ('lọ', 'wá', 'Luke$2:15 "Ó sì ṣe, nígbà tí àwọn ańgẹ́lì náà padà kúrò lọ́dọ̀ wọn lọ sí ọ̀run, àwọn Olùṣọ́-àgùntàn náà bá ara wọn sọ pé, “Ẹ jẹ́ kí á lọ tàrà sí Bẹ́tílẹ́hẹ́mù, kí á lè rí ohun tí ó ṣẹ̀ jẹ, tí Olúwa ti sọ nípa rẹ̀ fún wa.”"'), 'Luke$2:27': ('obi', 'ọmọ', 'Luke$2:27 "Ó sì ti ipa Ẹ̀mí wá sínú tẹ́ḿpìlì: nígbà tí àwọn òbí rẹ̀ sì gbé ọmọ náà Jésù wá, láti ṣe fún un bí ìṣe òfin,"'), 'Luke$2:34': (None, None, 'Luke$2:34 "Síméónì sì súre fún wọn, ó sì wí fún Màríà ìyá rẹ̀ pé: “Kíyèsí i, a gbé ọmọ yìí kalẹ̀ fún ìṣubú àti ìdìde ọ̀pọ̀ ènìyàn ní Ísírẹ́lì; àti fún àmì tí a ń sọ̀rọ̀ òdì sí;"'), 'Luke$2:36': ('ọjọ', None, 'Luke$2:36 " Ẹnìkan sì ń bẹ, Ánà wòlíì, ọmọbìnrin Fánúénì, ní ẹ̀yà Ásérì: ọjọ́ ogbó rẹ̀ pọ̀, ó ti bá ọkọ gbé ní ọdún méje láti ìgbà wúndíá rẹ̀ wá;"'), 'Luke$3:5': (None, None, 'Luke$3:5 "Gbogbo ọ̀gbun ni a óò kún, Gbogbo òkè ńlá àti òkè kékeré ni a ó tẹ̀bẹ̀rẹ̀; Wíwọ́ ni a ó ṣe ní títọ́, àti ọ̀nà gbọ́ngun-gbọ̀ngun ni a o sọ di dídán."'), 'Luke$4:42': ('duro', 'lọ', 'Luke$4:42 " Nígbà tí ilẹ̀ sì mọ́, Jésù sì jáde lọ ó sì ya ara rẹ̀ sọ́tọ̀. Ìjọ ènìyàn sì ń wá a kiri, wọ́n sì tọ̀ ọ́ wá, wọ́n sì dá a dúró, nítorí kí ó má baà lọ kúrò lọ́dọ̀ wọn."'), 'Luke$5:36': (None, 'tuntun', 'Luke$5:36 "Ó sì pa òwe yìí fún wọn wí pé: “Kò sí ẹni tó lè ya asọ tuntun kí ó sì rán mọ́, èyí tó ti gbó. Tí ó ba ṣe èyí, yóò ba asọ tuntun jẹ́, èyí tí ó tuntun náà kì yóò ṣe dógba pẹ̀lú èyí tí ó ti gbó."'), 'Luke$5:37': (None, 'tuntun', 'Luke$5:37 "Àti wí pé, kò sí ẹni tí ó lè dá otí wáìnì sínú ògbólógbò awọ, tí ó bá ṣe èyí, wáìnì tuntun yóò fa awọ náà ya, wáìnì náà yóò dànù awo náà a sí."'), 'Luke$5:39': (None, 'tuntun', 'Luke$5:39 "Kò sì ẹni tí yóò fẹ́ láti mu wáìnì tuntun lẹ́yìn tí ó bá ti mu ogbólógbò tán, nítorí yóò wí pé ‘èyí tí ó jẹ́ ògbólógbò dára jù.’ ”"'), 'Luke$6:1': (None, None, 'Luke$6:1 " Ní ọjọ́ ìsinmi kan, Jésù ń kọjá láàrin oko ọkà; àwọn ọmọ-ẹ̀yìn rẹ̀ sì ń ya ìpẹ́ ọkà, wọ́n sì ń jẹ ẹ́."'), 'Luke$6:3': (None, 'gba', 'Luke$6:3 " Jésù sì dá wọn lóhùn pé, “Ẹ̀yin kò kà nípa ohun tí Dáfídì ṣe, nígbà tí ebi ń pa òun tìkárarẹ̀ àti àwọn tí ó wà lọ́dọ̀ rẹ̀;"'), 'Luke$6:9': (None, None, 'Luke$6:9 "Nígbà náà ni Jésù wí fún wọn pé, “Èmi bi yín léèrè, Ó ha tọ́ láti máa ṣe rere ni ọjọ́ ìsinmi, tàbí láti máa ṣe búburú? Láti gba ọkàn là, tàbí láti pa á run?”"'), 'Luke$6:12': ('ọjọ', None, 'Luke$6:12 " Ọ̀kan nínú àwọn ọjọ́ wọnnì, Jésù lọ sí orí òkè lọ gbàdúrà, ó sì fi gbogbo òru náà gbàdúrà sí Ọlọ́run."'), 'Luke$6:35': (None, None, 'Luke$6:35 " Ṣùgbọ́n kí ẹ̀yin fẹ́ àwọn ọ̀ta yín kí ẹ̀yin sì ṣoore, kí ẹ̀yin sì yá, kí ẹ̀yin má ṣe retí láti rí nǹkan gbà padà; èrè yín yóò sì pọ̀, àwọn ọmọ ọ̀gá ògo ni a ó sì máa pè yín: nítorí tí ó ṣeun fún aláìmoore àti fún ẹni búburú."'), 'Luke$6:45': (None, None, 'Luke$6:45 Ènìyàn rere láti inú ìsúra rere ọkàn rẹ̀ ní mú ohun rere jáde wá; àti ènìyàn búburú láti inú ìṣúra búburú ọkàn rẹ̀ ní í mú ohun búburú jáde wá: nítorí ohun tí ó wà nínú ọkàn ni ẹnu rẹ̀ ń sọ.'), 'Luke$7:37': ('ẹsẹ', None, 'Luke$7:37 "Sì kíyèsí i, obìnrin kan wà ní ìlú náà, ẹni tí i ṣe ẹlẹ́sẹ̀, nígbà tí ó mọ̀ pé Jésù jókòó, ó ń jẹun ní ilé Farisí, ó mú ṣágo kekeré alabásítà òróró ìkunra wá,"'), 'Luke$7:39': (None, 'obinrin', 'Luke$7:39 " Nígbà tí Farisí tí ó pè é sì rí i, ó wí nínú ara rẹ̀ pé, “Ọkùnrin yìí ìbá ṣe wòlíì, ìbá mọ ẹni àti irú ẹni tí obìnrin yìí jẹ́ tí ń fi ọwọ́ kàn an, nítorí ẹlẹ́ṣẹ̀ ni.”"'), 'Luke$7:41': (None, None, 'Luke$7:41 "“Ayánilówó kan wà tí ó ní ajigbésè méjì: ọ̀kan jẹ ẹ́ ní ẹ̀ẹ́dẹ́gbẹ̀ta owó idẹ, èkejì sì jẹ ẹ́ ní àádọ́ta."'), 'Luke$7:46': ('ẹsẹ', 'ori', 'Luke$7:46 "Ìwọ kò fi òróró pa mí lórí, ṣùgbọ́n òun ti fi òróró pa mí lẹ́ṣẹ̀."'), 'Luke$7:47': ('pọ', 'diẹ', 'Luke$7:47 "Ǹjẹ́ mo wí fún ọ, A dárí ẹ̀ṣẹ̀ rẹ̀ tí ó pọ̀ jì í: nítorí tí ó ní ìfẹ́ púpọ̀: ẹni tí a sì dárí díẹ̀ jì, òun náà ni ó ní ìfẹ́ díẹ̀.”"'), 'Luke$8:4': (None, 'wá', 'Luke$8:4 " Nígbà tí ọ̀pọ̀ ìjọ ènìyàn pé jọ pọ̀, àwọn ènìyàn láti ìlú gbogbo sì tọ̀ ọ́ wá, ó fi òwe bá wọn sọ̀rọ̀ pé:"'), 'Luke$8:22': (None, 'lọ', 'Luke$8:22 " Ní ijọ́ kan, ó sì wọ ọkọ̀ ojú-omi kan lọ òun pẹ̀lú àwọn ọmọ-ẹ̀yìn rẹ̀: ó sì wí fún wọn pé, “Ẹ jẹ́ kí a rékọjá lọ sí ìhà kejì adágún.” Wọ́n sì ṣíkọ̀ lọ."'), 'Luke$8:51': ('baba', 'iya', 'Luke$8:51 "Ṣùgbọ́n nígbà tí Jésù sì wọ ilé, kò jẹ́ kí ẹnikẹ́ni wọlé, bí kò ṣe Pétérù, àti Jákọ́bù, àti Jòhánù, àti baba àti ìyá ọmọbìnrin náà."'), 'Luke$9:12': ('wá', 'lọ', 'Luke$9:12 "Nígbà tí ọjọ́ bẹ̀rẹ̀ sí rẹlẹ̀, àwọn méjìlá wá, wọ́n sì wí fún un pé, “Tú ìjọ ènìyàn ká, kí wọn lè lọ sí ìletò àti sí ìlú yíká, kí wọn lè wò, àti kí wọn lè wá oúnjẹ: níbi ijù ni àwa sá wà níhín-ín.”"'), 'Luke$9:25': ('ere', None, 'Luke$9:25 "Nítorí pé èrè kínni fún ènìyàn, bí ó jèrè gbogbo ayé, tí ó sì sọ ara rẹ̀ nù tàbí kí ó ṣòfò."'), 'Luke$9:51': ('lọ', None, 'Luke$9:51 " Nígbà tí ọjọ́ àti gbà á sókè ń súnmọ́, ó fi ojú lé ọ̀nà láti lọ sí Jerúsálémù."'), 'Luke$9:62': (None, None, 'Luke$9:62 "Jésù sì wí fún un pé, “Kò sí ẹni, tí ó fi ọwọ́ rẹ̀ lé ohun èlò ìtùlẹ̀, tí ó sì wo ẹ̀yìn, tí ó yẹ fún ìjọba Ọlọ́run.”"'), 'Luke$10:10': (None, None, 'Luke$10:10 "Ṣùgbọ́n ní ìlúkílùú tí ẹ̀yin bá sì wọ̀, tí wọn kò bá sì gbà yín, nígbà tí ẹ̀yin bá sì jáde sí ìgboro ìlú náà, kí ẹ̀yin sì wí pé,"'), 'Luke$10:35': ('lọ', None, 'Luke$10:35 "Nígbà tí ó sì lọ ní ijọ́ kejì, ó fi owó idẹ méjì fún olórí ilé èrò ó sì wí fún un pé, ‘Má a tọ́jú rẹ̀; ohunkóhun tí ìwọ bá sì ná kún un, nígbà tí mo bá padà dé, èmi ó san án fún ọ.’"'), 'Luke$11:5': ('lọ', None, 'Luke$11:5 " Ó sì wí fún wọn pé, “Ta ni nínú yín tí yóò ní ọ̀rẹ́ kan, tí yóò sì tọ̀ ọ́ lọ láàrin ọ̀gànjọ́, tí yóò sì wí fún un pé, ‘Ọ̀rẹ́, yá mi ní ìṣù àkàrà mẹ́ta."'), 'Luke$11:13': (None, 'dara', 'Luke$11:13 Ǹjẹ́ bí ẹ̀yin tí ó jẹ́ ènìyàn búburú bá mọ bí a ti í fi ẹ̀bùn dáradára fún àwọn ọmọ yín: mélóméló ha ni Baba yín tí ń bẹ ní ọ̀run yóò fi Ẹ̀mí-Mímọ́ rẹ̀ fún àwọn tí ó bèèrè lọ́dọ̀ rẹ̀!”'), 'Luke$11:26': (None, None, 'Luke$11:26 "Nígbà náà ni yóò lọ, yóò sì mú ẹ̀mí méje mìíràn tí ó burú ju òun tìkárarẹ̀ lọ wá: wọn yóò wọlé, wọn yóò sì máa gbé níbẹ̀: ìgbẹ̀hìn ọkùnrin náà yóò sì burú ju ìṣáájú rẹ̀ lọ.”"'), 'Luke$11:34': ('okunkun', 'imole', 'Luke$11:34 " Ojú ni ìmọ́lẹ̀ ara: bí ojú rẹ bá mọ́, gbogbo ara rẹ a mọ́lẹ̀; ṣùgbọ́n bí ojú rẹ bá burú, ara rẹ pẹ̀lú a kún fún òkùnkùn."'), 'Luke$11:36': ('imole', 'ṣokunkun', 'Luke$11:36 "Ǹjẹ́ bí gbogbo ara rẹ bá kún fún ìmọ́lẹ̀, tí kò ní apákan tí ó ṣókùnkùn, gbogbo rẹ̀ ni yóò ní ìmọ́lẹ̀, gẹ́gẹ́ bí ìgbà tí fìtílà bá fi ìtànsán rẹ̀ fún ọ ní ìmọ́lẹ̀.”"'), 'Luke$12:3': ('okunkun', None, 'Luke$12:3 "Nítorí náà ohunkóhun tí ẹ̀yin sọ ní òkùnkùn, ní gbangba ni a ó ti gbọ́ ọ; àti ohun tí ẹ̀yin bá sọ sí etí ní ìkọ̀kọ̀, lórí òrùlé ni a ó ti kéde rẹ̀."'), 'Luke$12:28': (None, None, 'Luke$12:28 "Ǹjẹ́ bí Ọlọ́run bá wọ koríko ìgbẹ́ ní aṣọ tó bẹ́ẹ̀ èyí tí ó wà lónì-ín, tí a sì gbá a sínú iná lọ́la: mélòómélòó ni yóò wọ̀ yín láṣọ, ẹ̀yin oní kékeré ìgbàgbọ́."'), 'Luke$12:53': ('baba', 'iya', 'Luke$12:53 "A ó ya baba nípa sí ọmọ rẹ̀ ọkùnrin, àti ọmọ ọkùnrin sí baba; ìyá sí ọmọbìnrin, àti ọmọbìnrin sí ìyá rẹ̀, ìyá ọkọ sí ìyàwó ọmọ, àti ìyàwó ọmọ sí ìyá ọkọ rẹ̀.”"'), 'Luke$13:25': (None, None, 'Luke$13:25 " Nígbà tí baálé ilé bá dìde lẹ́ẹ̀kan fù ú, tí ó bá sí ìlẹ̀kùn ẹ̀yin ó sì bẹ̀rẹ̀ sí í dúró lóde, tí ẹ ó máa kan ìlẹ̀kùn, wí pé, ‘Olúwa, Olúwa, ṣí i fún wa!’ “Òun ó sì dáhùn wí fún yín pé, ‘Èmi kò mọ̀ ibi tí ẹ̀yin ti wá.’"'), 'Luke$13:30': (None, None, 'Luke$13:30 " Sì wò ó, àwọn ẹni ẹ̀yìn ń bẹ tí yóò di ẹni iwájú, àwọn ẹni ìwájú ń bẹ tí yóò di ẹni ẹ̀yìn.”"'), 'Luke$14:8': (None, 'gba', 'Luke$14:8 " “Nígbà tí ẹnìkan bá pè ọ́ wá sí ibi ìyàwó, má ṣe jókòó ní ipò ọlá; kí ó má ba à jẹ́ pé, a ó pe ẹni tí ó lọ́lá jù ọ́ lọ."'), 'Luke$14:23': (None, 'wá', 'Luke$14:23 "“Olúwa náà sì wí fún ọmọ ọ̀dọ̀ náà pé, ‘Jáde lọ sí òpópó, àti sí ọ̀nà ọgbà, kí o sì rọ̀ wọ́n láti wọlé wá, kí ilé mi lè kún."'), 'Luke$14:25': ('lọ', None, 'Luke$14:25 "Àwọn ọ̀pọ̀ ìjọ ènìyàn ń bá a lọ; ó sì yípadà, ó sì wí fún wọn pé,"'), 'Luke$14:29': ('pari', 'berè', 'Luke$14:29 "Kí ó má baà jẹ́ pé nígbà tí ó bá fi ìpìlẹ̀ ilé sọlẹ̀ tan, tí kò lè parí rẹ̀ mọ́, gbogbo àwọn tí ó rí i a bẹ̀rẹ̀ sí í fi í ṣe ẹlẹ́yà."'), 'Luke$15:3': (None, None, 'Luke$15:3 "Ó sì pa òwe yìí fún wọn, pé,"'), 'Luke$15:8': (None, 'wa', 'Luke$15:8 "“Tàbí obìnrin wo ni ó ní fàdákà mẹ́wàá bí ó bá sọ ọ̀kan nù, tí kì yóò tan fìtílà, kí ó fi gbá ilé, kí ó sì wá a gidigidi títí yóò fi rí i?"'), 'Luke$15:22': (None, None, 'Luke$15:22 " “Ṣùgbọ́n Baba náà wí fún àwọn ọmọ-ọ̀dọ̀ rẹ̀ pé, Ẹ mú ààyò aṣọ wá kánkán, kí ẹ sì fi wọ̀ ọ́; ẹ sì fi òrùka bọ̀ ọ́ lọ́wọ́, àti bàtà sí ẹsẹ̀ rẹ̀:"'), 'Luke$15:28': ('wá', None, 'Luke$15:28 "“Ó sì bínú, ó sì kọ̀ láti wọlé; bàbá rẹ̀ sì jáde, ó sì wá í sìpẹ̀ fún un."'), 'Luke$15:32': (None, 'kú', 'Luke$15:32 "Ó yẹ kí a ṣe àríyá kí a sì yọ̀: nítorí àbúrò rẹ yìí ti kú, ó sì tún yè; ó ti nù, a sì rí i.’ ”"'), 'Luke$16:13': (None, None, 'Luke$16:13 " “Kò sí ìránṣẹ́ kan tí ó le sin olúwa méjì: àyàfi kí ó kórìíra ọ̀kan, yóò sì fẹ́ èkejì; tàbí yóò fi ara mọ́ ọ̀kan yóò sì yan èkejì ní ìpọ̀sì. Ẹ̀yin kò lè sin Ọlọ́run pẹ̀lú mámónì.”"'), 'Luke$16:17': (None, 'kọjá', 'Luke$16:17 " Ṣùgbọ́n ó rọrùn fún ọ̀run òun ayé láti kọjá lọ, ju kí sóńsó kan ti òfin kí ó yẹ̀."'), 'Luke$16:18': ('iyawo', 'ọkọ', 'Luke$16:18 " “Ẹnikẹ́ni tí ó bá kọ aya rẹ̀ sílẹ̀, tí ó sì gbé òmíràn ní ìyàwó, ó ṣe panṣágà: ẹnikẹ́ni tí ó bá sì gbé, ẹni tí ọkọ rẹ̀ kọ̀ sílẹ̀ ní ìyàwó, ó ṣe panṣágà."'), 'Luke$16:25': (None, None, 'Luke$16:25 " “Ṣùgbọ́n Ábúráhámù wí pé, ‘Ọmọ, rántí pé, nígbà ayé rẹ, ìwọ ti gba ohun rere tìrẹ, àti Lásárù ohun búburú: ṣùgbọ́n nísinsin yìí ara rọ̀ ọ́, ìwọ sì ń joró."'), 'Luke$17:23': (None, None, 'Luke$17:23 " Wọ́n sì wí fún yín pé, ‘Wò ó níhìnín;’ tàbí ‘Wò ó lọ́hùn ún!’ Ẹ máa lọ, ẹ má se tẹ̀lé wọn."'), 'Luke$18:7': (None, None, 'Luke$18:7 " Ọlọ́run kì yóò ha sì gbẹ̀san àwọn àyànfẹ́ rẹ̀, tí ń fi ọ̀sán àti òru kígbe pè é, tí ó sì mú sùúrù fún wọn?"'), 'Luke$18:20': ('baba', 'iya', 'Luke$18:20 " Ìwọ mọ̀ àwọn òfin, ‘Má ṣe ṣe panṣágà, má ṣe pànìyàn, má ṣe jalè, má ṣe jẹ́rìí èké, bọ̀wọ̀ fún bàbá àti ìyá rẹ.’ ”"'), 'Luke$18:27': (None, None, 'Luke$18:27 " Ó sì wí pé, “Ohun tí ó ṣòro lọ́dọ̀ ènìyàn kò ṣòro lọ́dọ̀ Ọlọ́run.”"'), 'Luke$19:8': (None, 'fun', 'Luke$19:8 " Sákéù sì dìde, ó sì wí fún Olúwa pé, “Wòó, Olúwa, ààbọ̀ ohun ìní mi ni mo fifún talákà; bí mo bá sì fi èké gba ohun kan lọ́wọ́ ẹnikẹ́ni, èmi san án padà ní ìlọ́po mẹ́rin!”"'), 'Luke$19:24': (None, 'fun', 'Luke$19:24 "“Ó sì wí fún àwọn tí ó dúró létí ibẹ̀ pé, ‘Ẹ gba owó mínà náà lọ́wọ́ rẹ̀, kí ẹ sì fi í fún ẹni tí ó ní owó mínà mẹ́wàá.’"'), 'Luke$19:43': (None, None, 'Luke$19:43 " Nítorí ọjọ́ ń bọ̀ fún ọ, tí àwọn ọ̀tá rẹ yóò mọ odi bèbè yí ọ ká, àní tí wọn yóò sì yí ọ ká, wọn ó sì ká ọ mọ́ níhà gbogbo."'), 'Luke$20:38': (None, 'kú', 'Luke$20:38 "Bẹ́ẹ̀ ni òun kì í ṣe Ọlọ́run àwọn òkú, bí kò ṣe ti àwọn alààyè: nítorí gbogbo wọn wà láàyè fún un.”"'), 'Luke$21:8': ('wá', 'lọ', 'Luke$21:8 " Ó sì wí pé, “Ẹ máa kíyèsára, kí a má báà mú yín sìnà: nítorí ọ̀pọ̀lọpọ̀ yóò wá ní orúkọ mi, tí yóò máa wí pé, ‘Èmi ní Kírísítì náà àkókò náà sì kù sí dẹ̀dẹ̀.’ Ẹ má ṣe tọ̀ wọ́n lẹ́yìn."'), 'Luke$21:37': (None, None, 'Luke$21:37 " Lọ́sàn-án, a sì máa kọ́ni ní tẹ́ḿpílì: lóru, a sì máa jáde lọ wọ̀ lórí òkè tí à ń pè ní òkè Ólífì."'), 'Luke$22:36': ('ta', 'ra', 'Luke$22:36 " Nígbà náà ni ó wí fún wọn pé, “Ṣùgbọ́n nísinsin yìí, ẹni tí ó bá ní àpò owó kí ó mú un, àti àpò pẹ̀lú; ẹni tí kò bá sì ní idà, kí ó ta aṣọ rẹ̀, kí ó sì fi ra ìkan."'), 'Luke$23:33': ('ọtun', 'osi', 'Luke$23:33 " Nígbà tí wọ́n sì dé ibi tí a ń pè ní Agbárí, níbẹ̀ ni wọ́n gbé kàn án mọ́ àgbélèbú, àti àwọn arúfin náà, ọ̀kan ní ọwọ́ ọ̀tún, àti ọ̀kan ní ọwọ́ òsì."'), 'Luke$23:40': (None, None, 'Luke$23:40 "Ṣùgbọ́n èyí èkejì dáhùn, ó ń bá a wí pé, “Ìwọ kò bẹ̀rù Ọlọ́run, tí ìwọ wà nínú ẹ̀bi kan náà?"'), 'Luke$24:4': (None, 'kú', 'Luke$24:4 "Bí wọ́n ti dààmú kiri níhà ibẹ̀, kíyèsí i, àwọn ọkùnrin méjì aláṣọ dídán dúró tì wọ́n:"'), 'Luke$24:12': ('lọ', None, 'Luke$24:12 "Nígbà náà ni Pétérù dìde, ó súré lọ sí ibojì; nígbà tí ó sì bẹ̀rẹ̀, ó rí aṣọ àlà lọ́tọ̀ fúnrarawọn, ó sì padà lọ sí ilé rẹ̀, ẹnu yà á sí ohun tí ó ṣe."'), 'John$1:5': ('okunkun', None, 'John$1:5 " Ìmọ́lẹ̀ náà sì ń mọ́lẹ̀ nínú òkùnkùn, òkùnkùn kò sì borí rẹ̀."'), 'John$2:9': (None, None, 'John$2:9 "Olórí àsè náà tọ́ omi tí a sọ di wáìnì wò. Òun kò sì mọ ibi tí ó ti wá, ṣùgbọ́n àwọn ìránṣẹ́ tí ó bu omi náà wá mọ̀. Olórí àsè sì pe ọkọ ìyàwó sí apákan,"'), 'John$3:12': ('ayé', 'ọrun', 'John$3:12 "Bí mo bá sọ ohun ti ayé yìí fún yín, tí ẹ̀yin kò sì gbàgbọ́, ẹ̀yin ó ti ṣe gbàgbọ́ bí mo bá sọ ohun ti ọ̀run fún yín?"'), 'John$3:13': (None, None, 'John$3:13 " Kò sì sí ẹni tí ó gòkè re ọ̀run bí kò ṣe ẹni tí ó ti ọ̀run sọ̀kalẹ̀ wá, Ọmọ Ènìyàn tí ń bẹ ní ọ̀run."'), 'John$3:19': ('okunkun', 'imole', 'John$3:19 " Èyí ni ìdájọ́ náà pé, ìmọ́lẹ̀ wá sí ayé, àwọn ènìyàn sì fẹ́ òkùnkùn ju ìmọ́lẹ̀ lọ, nítorí tí iṣẹ́ wọn burú."'), 'John$3:30': (None, None, 'John$3:30 "Òun kò lè ṣàì máa pọ̀ sí i, ṣùgbọ́n èmi kò lè ṣàì má relẹ̀."'), 'John$4:27': (None, 'obinrin', 'John$4:27 "Lórí èyí ni àwọn ọmọ-ẹ̀yìn rẹ̀ dé, ẹnu sì yà wọ́n pé ó ń bá obìnrin sọ̀rọ̀: ṣùgbọ́n kò sí ẹnì kan tí ó wí pé, “Kíni ìwọ ń wá?” tàbí “Èéṣe tí ìwọ fi ń bá a sọ̀rọ̀?”"'), 'John$4:28': ('eniyan', 'obinrin', 'John$4:28 "Nígbà náà ni obìnrin náà fi ládugbó rẹ̀ sílẹ̀, ó sì mú ọ̀nà rẹ̀ pọ̀n lọ sí ìlú, ó sì wí fún àwọn ènìyàn pé,"'), 'John$4:45': ('lọ', None, 'John$4:45 "Nítorí náà nígbà tí ó dé Gálílì, àwọn ará Gálílì gbà á, nítorí ti wọ́n ti rí ohun gbogbo tí ó ṣe ní Jérúsálẹ́mù nígbà àjọ; nítorí àwọn tìkára wọn lọ sí àjọ pẹ̀lú."'), 'John$4:47': (None, 'wá', 'John$4:47 "Nígbà tí ó gbọ́ pé Jésù ti Jùdéà wá sí Gálílì, ó tọ̀ ọ́ wá, ó sì ń bẹ̀ ẹ́, kí ó lè sọ̀kalẹ̀ wá kí ó mú ọmọ òun láradá: nítorí tí ó wà ní ojú ikú."'), 'John$5:25': ('kú', None, 'John$5:25 " Lóòótọ́, lóòótọ́ ni mo wí fún yín, wákàtí náà ń bọ̀, ó sì dé tán nísinsìn yìí, nígbà tí àwọn òkú yóò gbọ́ ohùn ọmọ Ọlọ́run: àwọn tí ó bá gbọ́ yóò sì yè."'), 'John$5:28': (None, None, 'John$5:28 "“Kí èyí má ṣe yà yín lẹ́nu; nítorí pé wákàtí ń bọ̀, nínú èyí tí gbogbo àwọn tí ó wà ní ibojì yóò gbọ́ ohun rẹ̀."'), 'John$6:15': ('lọ', 'wá', 'John$6:15 " Nígbà tí Jésù sì wòye pé wọ́n ń fẹ́ wá fi agbára mú òun láti lọ fi jọba, ó tún padà lọ sórí òkè, òun nìkan."'), 'John$6:16': ('lọ', None, 'John$6:16 " Nígbà tí alẹ́ sì lẹ́, àwọn ọmọ-ẹ̀yìn rẹ̀ sọ̀kalẹ̀ lọ sí òkun."'), 'John$6:42': ('baba', 'iya', 'John$6:42 " Wọ́n sì wí pé, “Jésù ha kọ́ èyí, ọmọ Jósẹ́fù, baba àti ìyá ẹni tí àwa mọ̀? Báwo ni ó ṣe wí pé, ‘Èmi ti ọ̀run sọ̀kalẹ̀ wá’?”"'), 'John$6:58': ('kú', None, 'John$6:58 " Èyí sì ni oúnjẹ náà tí ó sọ̀kalẹ̀ láti ọ̀run wá: kì í ṣe bí àwọn baba yín ti jẹ mánà, tí wọ́n sì kú: ẹni tí ó bá jẹ́ oúnjẹ yìí yóò yè láéláé.”"'), 'John$7:8': ('lọ', None, 'John$7:8 Ẹ̀yin ẹ gòkè lọ sí àjọ yìí: èmi kì yóò tí ì gòkè lọ sí àjọ yìí; nítorí tí àkókò mi kò ì tí ì dé.”'), 'John$8:12': ('okunkun', 'imole', 'John$8:12 " Jésù sì tún sọ fún wọn pé, “Èmi ni ìmọ́lẹ̀ ayé, ẹni tí ó bá tọ̀ mí lẹ́yìn kì yóò rìn nínú òkùnkùn, ṣùgbọ́n yóò ní ìmọ́lẹ̀ ìyè.”"'), 'John$8:14': ('lọ', 'wá', 'John$8:14 "Jésù dáhùn ó sì wí fún wọn pé, “Bí mo tilẹ̀ ń jẹ́rí fún ara mi, òtítọ́ ni ẹ̀rí mi: nítorí tí mo mọ ibi tí mo ti wá, mo sì mọ ibi tí mo ń lọ; ṣùgbọ́n ẹ̀yin kò lè mọ ibi tí mo ti wá, àti ibi tí mo ń lọ."'), 'John$8:21': ('wá', 'lọ', 'John$8:21 " Nítorí náà ó tún wí fún wọn pé, “Èmi ń lọ, ẹ̀yin yóò sì wá mi, ẹ ó sì kú nínú ẹ̀ṣẹ̀ yín: ibi tí èmi gbé ńlọ, ẹ̀yin kì yóò lè wá.”"'), 'John$9:4': (None, None, 'John$9:4 " Àwa ní láti ṣe iṣẹ́ ẹni tí ó rán mi, níwọ̀n ìgbà tí ó ba ti jẹ́ ọ̀sán: òru ń bọ̀ wá nígbà tí ẹnìkan kì yóò lè ṣe iṣẹ́."'), 'John$10:1': (None, None, 'John$10:1 "“Lóòótọ́, lóòótọ́ ni mo wí fún yín, Ẹni tí kò bá gba ẹnu ọ̀nà wọ inú agbo àgùntàn, ṣùgbọ́n tí ó bá gba ibòmíràn gun òkè, Òun náà ni olè àti ọlọ́sà."'), 'John$11:25': ('kú', None, 'John$11:25 " Jésù wí fún un pé, “Èmi ni àjíǹde àti ìyè: ẹni tí ó bá gbà mí gbọ́, bí ó tilẹ̀ kú, yóò yè:"'), 'John$11:28': ('lọ', None, 'John$11:28 "Nígbà tí ó sì ti wí èyí tan, ó lọ, ó sì pe Màríà arábìnrin rẹ̀ sẹ́yìn wí pé, “Olùkọ́ dé, ó sì ń pè ọ́.”"'), 'John$11:57': (None, None, 'John$11:57 "Ǹjẹ́ àwọn olórí àlùfáà àti àwọn Farisí ti pàṣẹ pé bí ẹnìkan bá mọ ibi tí ó gbé wà, kí ó fi í hàn, kí wọn baà lè mú un."'), 'John$12:12': (None, 'wá', 'John$12:12 " Ní ijọ kejì nígbà tí ọ̀pọ̀ ènìyàn tí ó wá sí àjọ gbọ́ pé, Jésù ń bọ̀ wá sí Jérúsálẹ́mù."'), 'John$12:25': (None, None, 'John$12:25 Ẹni tí ó bá fẹ́ ẹ̀mí rẹ̀ yóò sọ ọ́ nù; ẹni tí ó bá sì kórìíra ẹ̀mí rẹ̀ láyé yìí ni yóò sì pa á mọ́ títí ó fi di ìyè àìnípẹ̀kun.'), 'John$12:46': ('okunkun', 'imole', 'John$12:46 " Èmi ni ìmọ́lẹ̀ tí ó wá sí ayé, kí ẹnikẹ́ni tí ó bá gbà mí gbọ́ má ṣe wà nínú òkùnkùn."'), 'John$13:1': ('lọ', 'wá', 'John$13:1 " Ǹjẹ́ kí àjọ ìrékọjá tó dé, nígbà tí Jésù mọ̀ pé, wákàtí rẹ̀ dé tan, tí òun ó ti ayé yìí kúrò lọ sọ́dọ̀ Baba, fífẹ́ tí ó fẹ́ àwọn tirẹ̀ tí ó wà ní ayé, ó fẹ́ wọn títí dé òpin."'), 'John$13:2': (None, None, 'John$13:2 " Bí wọ́n sì ti ń jẹ oúnjẹ alẹ́, tí èṣù ti fi í sí ọkàn Júdásì Isíkáríótù ọmọ Símónì láti fi í hàn;"'), 'John$13:9': ('ẹsẹ', 'ori', 'John$13:9 "Símónì Pétérù wí fún ún pé, “Olúwa, kì í ṣe ẹṣẹ̀ mi nìkan, ṣùgbọ́n àti ọwọ́ àti orí mi pẹ̀lú.”"'), 'John$14:3': ('wá', 'lọ', 'John$14:3 "Bí mo bá sì lọ láti pèṣè àyè sílẹ̀ fún un yín, èmi ó tún padà wá, èmi ó sì mú yín lọ sọ́dọ̀ èmi tìkáara mi; pé níbi tí èmi gbé wà, kí ẹ̀yin lè wà níbẹ̀ pẹ̀lú."'), 'John$14:18': ('wá', None, 'John$14:18 "Èmi kò ní fi yín sílẹ̀ láìní Òlùtùnú, kí ẹ wá dàbí ọmọ tí kò ní òbí. Rárá o! Mo tún ń tọ̀ yín bọ̀."'), 'John$14:28': ('wá', 'lọ', 'John$14:28 "“Ẹ̀yin sá ti gbọ́ bí mo ti wí fún yín pé, ‘Èmi ń lọ, èmi ó sì tọ̀ yín wá.’ Ìbáṣe pé ẹ̀yin fẹ́ràn mi, ẹ̀yin ìbá yọ̀ nítorí èmi ń lọ sọ́dọ̀ Baba; nítorí Baba mi tóbi jù mí lọ."'), 'John$16:7': ('wá', 'lọ', 'John$16:7 " Ṣùgbọ́n òtítọ́ ni èmi ńsọ fún yín; àǹfàní ni yóò jẹ́ fún yín bí èmi bá lọ: nítorí bí èmi kò bá lọ, Olùtùnú kì yóò tọ̀ yín wá; ṣùgbọ́n bí mo bá lọ, èmi ó rán an sí yín."'), 'John$16:8': (None, None, 'John$16:8 "Nígbà tí òun bá sì dé, yóò fi òye yé aráíyé níti ẹ̀ṣẹ̀, àti ní ti òdodo, àti níti ìdájọ́:"'), 'John$16:20': ('ayo', None, 'John$16:20 " Lóòótọ́, lóòótọ́ ni mo wí fún yín pé, ẹ̀yin ó máa sọkún ẹ ó sì máa pohùnréré ẹkún, ṣùgbọ́n àwọn aráyé yóò máa yọ̀: inú yín yóò sì di ayọ̀."'), 'John$16:21': ('eniyan', 'obinrin', 'John$16:21 " Nígbà tí obìnrin bá ń rọbí, a ní ìbìnújẹ́, nítorí tí wákàtí rẹ̀ dé: ṣùgbọ́n nígbà tí ó bá ti rí ọmọ náà tán, òun kì í sì í rántí ìrora náà mọ́, fún ayọ̀ nítorí a bí ènìyàn sí ayé."'), 'John$16:22': ('ayo', 'ibanujẹ', 'John$16:22 "Nítorí náà ẹ̀yin ní ìbànújẹ́ nísinsìn yìí: ṣùgbọ́n èmi ó tún rí yín, ọkàn yín yóò sì yọ̀, kò sì sí ẹni tí yóò gba ayọ̀ yín lọ́wọ́ yín."'), 'John$16:28': ('lọ', 'wá', 'John$16:28 "Mo ti ọ̀dọ̀ Baba jáde wá, mo sì wá sí ayé: ẹ̀wẹ̀, mo fi ayé sílẹ̀, mo sì lọ sọ́dọ̀ Baba.”"'), 'John$16:32': ('wá', None, 'John$16:32 " Kíyèsí i, wákàtí ń bọ̀, àní ó dé tan nísinsin yìí, tí a ó fọ́n yín ká kiri, olúkúlùkù sí ilé rẹ̀; ẹ ó sì fi èmi nìkan sílẹ̀: ṣùgbọ́n kì yóò sì ṣe èmi nìkan, nítorí tí Baba ń bẹ pẹ̀lú mi."'), 'John$18:4': ('lọ', 'wá', 'John$18:4 " Nítorí náà bí Jésù ti mọ ohun gbogbo tí ń bọ̀ wá bá òun, ó jáde lọ, ó sì wí fún wọn pé, “Ta ni ẹ ń wá?”"'), 'John$19:38': ('fun', 'gba', 'John$19:38 " Lẹ́yìn nǹkan wọ̀nyí ní Jóṣéfù ará Arimatíyà, ẹni tí ó jẹ́ ọmọ-ẹ̀yìn Jésù, ṣùgbọ́n ní ìkọ̀kọ̀, nítorí ìbẹ̀rù àwọn Júù, o bẹ Pílátù kí òun lè gbé òkú Jésù kúrò: Pílátù sì fún un ní àṣẹ. Nígbà náà ni ó wá, ó sì gbé òkú Jésù lọ."'), 'John$20:11': (None, None, 'John$20:11 "Ṣùgbọ́n Màríà dúró létí ibojì lóde, ó ń sọkún: bí ó ti ń ṣọkún, bẹ́ẹ̀ ni ó bẹ̀rẹ̀, ó sì wo inú ibojì."'), 'John$21:18': (None, 'odo', 'John$21:18 "Lóòótọ́, lóòótọ́ ni mo wí fún ọ, nígbà tí ìwọ wà ní ọ̀dọ́mọdé, ìwọ a máa di ara rẹ ní àmùrè, ìwọ a sì máa rìn lọ sí ibi tí ìwọ bá fẹ́: ṣùgbọ́n nígbà tí ìwọ bá di arúgbó, ìwọ ó na ọwọ́ rẹ jáde, ẹlòmíràn yóò sì dì ọ́ ní àmùrè, yóò mú ọ lọ sí ibi tí ìwọ kò fẹ́."'), 'John$21:23': (None, None, 'John$21:23 "Ọ̀rọ̀ yìí sì tàn ká láàrin àwọn arákùnrin pé, ọmọ-ẹ̀yìn náà kì yóò kú: ṣùgbọ́n Jésù kò wí fún un pé, Òun kì yóò kú; ṣùgbọ́n, “Bí èmi bá fẹ́ kí ó dúró títí èmi ó fi dé, kínni èyí jẹ́ sí ọ?”"'), 'Acts$1:13': ('lọ', 'wá', 'Acts$1:13 " Nígbà tí wọn sì wọlé, wọn lọ sí ìyàrá òkè, níbi tí wọ́n ń gbé. Àwọn tó wà níbẹ̀ ni Pétérù, àti Jákọ́bù àti Jòhánù, àti Áńdérù, àti Fílípì, àti Tọ́másì, Bátóléméù, àti Mátíù, Jákọ́bù ọmọ Álíféù, àti Símónì Sélótì, àti Júdà arakùnrin Jákọ́bù."'), 'Acts$2:16': (None, None, 'Acts$2:16 "Bẹ́ẹ̀ kọ́, èyí ni ọ̀rọ̀ ti a ti sọ láti ẹnu wòlíì Joeli:"'), 'Acts$2:25': (None, None, 'Acts$2:25 " Dáfídì tí wí nípa tirẹ̀ pé: “ ‘Mo rí Olúwa nígbà gbogbo niwájú mí, nítorí tí ó ń bẹ lọ́wọ́ ọ̀tún mi, A kì ó ṣí mi ní ipò."'), 'Acts$7:20': (None, None, 'Acts$7:20 " “Ní àkókò náà ni a bí Mósè, ẹni tí ó lẹ́wà púpọ̀, tí wọn sí bọ́ lóṣù mẹ́ta ni ilé baba rẹ̀."'), 'Acts$8:27': ('lọ', 'wá', 'Acts$8:27 "Nígbà tí ó sì dìde, ó lọ; sí kíyèsí, ọkùnrin kan ará Etiópíà, ìwẹ̀fà ọlọ́lá púpọ̀ lọdọ̀ Káńdákè ọba-bìnrin àwọn ara Etiópíà, ẹni tí í se olórí ìsúra rẹ̀, tí ó sì ti wá sí Jerúsálémù láti jọ́sìn,"'), 'Acts$8:39': ('lọ', 'wá', 'Acts$8:39 "Nígbà tí wọ́n sí jáde kúrò nínú omi Ẹ̀mí Olúwa gbé Fílípì lọ, ìwẹ̀fà kò sì rí i mọ́; nítorí tí ó ń bá ọ̀nà rẹ̀ lọ, ó ń yọ̀."'), 'Acts$9:11': (None, 'lọ', 'Acts$9:11 "Olúwa sì wí fún un pé, “Dìde kí ó sì lọ sí ilé Júdà ní Òpópónà Tààrà, kí ó sì bèèrè ẹni tí a ń pè ni Ṣọ́ọ̀lù, ara Tásọ́sì, sá wò ó, ó ń gbàdúrà."'), 'Acts$10:21': (None, 'wá', 'Acts$10:21 "Nígbà náà ni Pétérù sọ̀kalẹ̀ tọ àwọn ọkùnrin náà tí a rán, ó sì wí pé, “Wò ó, èmi ni ẹni tí ẹ̀yin ń wá: kín ni ìdí tẹ̀ ti ẹ fi wá?”"'), 'Acts$10:27': (None, None, 'Acts$10:27 "Bí ó sì ti ń bá a sọ̀rọ̀, ó wọlé, ó sì rí àwọn ènìyàn púpọ̀ tí wọ́n péjọ."'), 'Acts$11:22': ('wá', 'lọ', 'Acts$11:22 Ìròyìn nípa wọn sì dé etí ìjọ ti ó wà ni Jerúsálémù; wọ́n sì rán Bánábà lọ títí dé Áńtíókù;'), 'Acts$12:10': ('keji', None, 'Acts$12:10 "Nígbà tí wọ́n kọjá ìṣọ́ ìkínní àti ìkejì, wọ́n dé ẹnu-ọ̀nà ìlẹ̀kùn irin tí ó lọ sí ìlú. Ó sí tikararẹ̀ sí sílẹ̀ fún wọn: wọ́n sì jáde, wọ́n ń gba ọ̀nà ìgboro kan lọ; lójúkan náà ańgẹ́lì náà sì fi í sílẹ̀ lọ."'), 'Acts$16:6': (None, None, 'Acts$16:6 "Wọ́n sì la agbégbé Fírígíà já, àti Gálátíà, nítorí tí Ẹ̀mí Mímọ́ kọ̀ fún wọn láti ṣọ ọ̀rọ̀ náà ni Éṣíà."'), 'Acts$17:12': (None, 'diẹ', 'Acts$17:12 "Nítorí náà púpọ̀ nínú wọn gbàgbọ́; àti nínú àwọn obìnrin Gíríkì ọlọ́lá, àti nínú àwọn ọkùnrin, kì í ṣe díẹ̀."'), 'Acts$17:15': ('lọ', 'wá', 'Acts$17:15 "Àwọn tí ó sin Pọ́ọ̀lù wá sì mú un lọ títí dé Átẹ́nì; nígbà tí wọ́n sì gba àṣẹ lọ́dọ̀ rẹ̀ wá fún Sílà àti Tímótíù pé, ki wọn ó yára tọ òun wá, wọ́n lọ."'), 'Acts$17:34': ('awọn ọkunrin', 'obinrin', 'Acts$17:34 "Ṣùgbọ́n àwọn ọkùnrin kan fi ara mọ́ ọn, wọ́n sì gbàgbọ́: nínú àwọn ẹni tí Díónísíù ara Aréópágù wà, àti obìnrin kan tí a ń pè ni Dámárì, àti àwọn mìíràn pẹ̀lú wọn."'), 'Acts$18:1': ('lọ', None, 'Acts$18:1 "Lẹ́yin nǹkan wọ̀nyí, Pọ́ọ̀lù jáde kúrò ni Aténì, lọ sí Kọ́ríńtì;"'), 'Acts$18:22': (None, None, 'Acts$18:22 "Nígbà tí ó sì tí gúnlẹ̀ ni Keṣaríà; tí ó gòkè, tí ó sì kí ijọ, ó sọ̀kalẹ̀ lọ sì Áńtíókù."'), 'Acts$20:11': ('lọ', 'wá', 'Acts$20:11 "Nígbà tí ó sì tún gòkè lọ, ó sì jẹ oúnjẹ́ Olúwa, ó sì sọ̀rọ̀ pẹ́ títí ó fi di àfẹ̀mọ́júmọ́, nígbà náà ni ó sì lọ."'), 'Acts$20:31': (None, None, 'Acts$20:31 "Nítorí náà ẹ máa sọ́ra, ki ẹ sì máa rántí pé, fún ọdún mẹ́ta, èmi kò dẹ́kun láti máa fi omijé kìlọ̀ fún olúkúlùkù ní ọ̀sán àti ní òru."'), 'Acts$23:32': ('wá', 'lọ', 'Acts$23:32 "Ní ọjọ́ kejì wọ́n sì fi àwọn ẹlẹ́ṣin sílẹ̀ láti máa bá a lọ, àwọn sì padà wá sínú àgọ́ àwọn ológun."'), 'Acts$24:14': (None, None, 'Acts$24:14 "Ṣùgbọ́n, èmí jẹ́wọ́ fún ọ pé, èmi ń sin Ọlọ́run àwọn baba wa gẹ́gẹ́ bí èmi ti ń tẹ̀le ọ̀nà tí wọ́n ń pè ni ìyapa-ìsìn. Èmi ń gbá gbogbo nǹkan ti a kọ sínú ìwé òfin gbọ́, àti tí a kọ sínú ìwé àwọn wòlíì,"'), 'Acts$24:23': (None, None, 'Acts$24:23 "Ó sì pàṣẹ fún balógun ọ̀run kan kí ó fi Pọ́ọ̀lù sí a bẹ́ ìsọ́, ṣùgbọ́n kí ó fún un ní ààyè, àti pé kí ó má ṣe dá àwọn ọ̀rẹ́ rẹ̀ lẹ́kùn láti máa ṣe ìránṣẹ́ fún un."'), 'Acts$25:11': ('ni', 'kọ', 'Acts$25:11 "Ǹjẹ́ bí mo bá ṣẹ̀, tí mo sí ṣe ohun kan tí ó yẹ fún ikú, èmi kò kọ̀ láti kú: ṣùgbọn bí kò bá sí otítọ́ kan nínú ẹ̀sùn tí àwọn Júù yìí fi mi sùn sí, ẹnìkan kò lẹ́tọ̀ọ́ láti fà mí lé wọn lọ́wọ́. Mo fí ọ̀ràn mi lọ Késárì.”"'), 'Acts$25:18': (None, None, 'Acts$25:18 "Nígbà tí àwọn olùfiṣùn náa dìde, wọn kò ka ọ̀ràn búburú irú èyí tí mo rò sí i lọ́rùn."'), 'Acts$26:6': (None, None, 'Acts$26:6 Nísinsìn yìí nítorí ìrètí ìlérí tí Ọlọ́run tí ṣe fún àwọn baba wa ni mo ṣe dúró níhìn-ín fún ìdájọ́.'), 'Acts$26:16': (None, None, 'Acts$26:16 " Ṣùgbọ́n dìde, kí o sí fi ẹṣẹ̀ rẹ tẹlẹ̀: nítorí èyí ni mo ṣe farahàn ọ́ láti yàn ọ́ ní ìránṣẹ́ àti ẹlẹ́rìí, fún ohun wọ̀nyí tí ìwọ tí rí nípa mi, àti àwọn ohun tí èmi yóò fí ara hàn fún ọ:"'), 'Acts$27:12': (None, None, 'Acts$27:12 "Àti nítorí pé èbúté náà kò rọrùn láti lo àkókò òtútù níbẹ̀, àwọn púpọ̀ sí dámọ̀ràn pé, kí a lọ kúrò níbẹ̀, bóyá wọn ó lè làkàkà dé Fóníkè, tí i ṣe èbúté Kírétè ti ó kọjú sí òsì ìwọ̀-oorùn, àti ọ̀tún ìwọ̀-oorùn, láti lo àkókò òtútù níbẹ̀."'), 'Acts$27:27': (None, 'wá', 'Acts$27:27 "Ṣùgbọ́n nígbà tí ó di òru ọjọ́ kẹrìnlá, bí a ti ń gbé wa kọjá lọ láàrin òkun Ádíríà, láàrin ọ̀gànjọ́ àwọn atukọ̀ fura pé, àwọn súnmọ́ etí ilẹ̀ kan:"'), 'Rom$1:26': (None, 'obinrin', 'Rom$1:26 Nítorí èyí yìí ni Ọlọ́run ṣe fi wọ́n fún ìfẹ́ ìwàkíwà: nítorí àwọn obìnrin wọn tilẹ̀ yí ìlò àdánidá padà sí èyí tí ó lòdì sí ti àdánidá:'), 'Rom$2:5': ('ibi', None, 'Rom$2:5 "Ṣùgbọ́n gẹ́gẹ́ bí líle àti àìronúpìwàdà ọkàn rẹ̀, ìwọ ń to ìbínú jọ fún ara rẹ de ọjọ́ ìbínú àti ìfihàn ìdájọ́ òdodo Ọlọ́run."'), 'Rom$2:17': (None, None, 'Rom$2:17 "Ṣùgbọ́n bí a bá ń pe ìwọ ní Júù, tí o sì sinmi lé òfin, tí o sì ń ṣògo nínú ìbálò rẹ̀ sí Ọlọ́run,"'), 'Rom$2:28': (None, None, 'Rom$2:28 " Kì í ṣe èyí tí ó farahàn ní òde ni Júù, bẹ́ẹ̀ ni kì í ṣe èyí tí ó farahàn ní ara ni ìkọlà:"'), 'Rom$3:4': (None, None, 'Rom$3:4 " Kí a má rí i: kí Ọlọ́run jẹ́ olóòtọ́, àti olúkúlùkù ènìyàn jẹ́ èké; gẹ́gẹ́ bí a ti kọ ọ́ pé: “Kí a lè dá ọ láre nínú ọ̀rọ̀ rẹ, ṣùgbọ́n kí ìwọ lè borí nígbà tí ìwọ bá wá sí ìdájọ́.”"'), 'Rom$3:5': ('ododo', 'aiṣododo', 'Rom$3:5 " Ṣùgbọ́n bí àìṣòdodo wa bá fi òdodo Ọlọ́run hàn, kín ni àwa ó wí? Ọlọ́run ha jẹ́ àìsòdodo bí? Nígbà tí ó bá ń fi ìbínú rẹ̀ hàn (Mo fi ṣe àkàwé bí ènìyàn)."'), 'Rom$3:8': (None, None, 'Rom$3:8 Èéṣe tí a kò fi ṣe búburú kí rere lè jáde wá? Bí àwọn kan tí ń fi ẹnu àtẹ́ sọ wí pé a ń sọ bẹ́ẹ̀; ti àwọn kan sì ń tẹnumọ́ ọn pé a sọ; àwọn ẹni tí ìdálẹ́bí wọn tọ́.'), 'Rom$4:19': (None, None, 'Rom$4:19 " Ẹni tí kò rẹ̀wẹ̀sì nínú ìgbàgbọ́, nígbà tí ó ro ti ara òun tìkarárẹ̀ tí ó ti kú tan, nítorí tí ó tó bí ẹni ìwọ̀n ọgọ́rún-ún ọdún, àti nígbà tí ó ro ti yíyàgàn inú Sárà:"'), 'Rom$5:19': ('igboran', 'aigboran', 'Rom$5:19 " Nítorí gẹ́gẹ́ bí nípa àìgbọ́ràn ọkùnrin kan, ènìyàn púpọ̀ di ẹlẹ́sẹ̀, bẹ́ẹ̀ ni nípa ìgbọ́ràn ẹnìkan, a ó sọ ènìyàn púpọ̀ di olódodo."'), 'Rom$6:8': ('kú', None, 'Rom$6:8 " Níwọ̀n ìgbà tí ògbólógbòó ara yín tó ń fẹ́ máa dẹ́sẹ̀ ti kú pẹ̀lú Kírísítì, àwa mọ̀ pé, ẹ̀yin yóò pín nínú ìyè titun rẹ̀."'), 'Rom$6:11': (None, 'kú', 'Rom$6:11 " Nítorí náà, ẹ máa wo ògbólógbòó ara ẹ̀ṣẹ̀ yín gẹ́gẹ́ bí òkú tí kò ní ohunkóhun í ṣe pẹ̀lú ẹ̀ṣẹ̀ mọ́. Dípò èyí, máa gbé ìgbé ayé yín fún Ọlọ́run nìkan ṣoṣo. Ẹ dúró gbọn-in gbọn-in fún un, nípasẹ̀ Jésù Kírísítì Olúwa wa."'), 'Rom$6:13': (None, None, 'Rom$6:13 " Ẹ má ṣe jẹ́ kí ẹ̀yà ara yín kan di ohun èlò ìkà, nípa ẹ̀ṣẹ̀ dídá. Ṣùgbọ́n ẹ yọ̀ǹda wọn fún Ọlọ́run pátápátá. Wọ́n ti di ààyè, ẹ jẹ́ kí wọn di ohun èlò ní ọwọ́ Ọlọ́run, kí ó lè lò wọ́n fún àwọn ìlànà rẹ̀ tí ó dára."'), 'Rom$7:6': ('tuntun', None, 'Rom$7:6 "Ṣùgbọ́n nísinsìn yìí, nípa kíkú láti ipasẹ̀ ohun tó so wápọ̀ tẹ́lẹ̀ rí, a ti tú wa sílẹ̀ kúrò nínú òfin, kí a lè sin ín ní ìlànà tuntun ti Ẹ̀mí, kì í se ní ìlànà ti àtijọ́ tí òfin gùnlé."'), 'Rom$7:19': (None, None, 'Rom$7:19 "Níorí ohun tí èmi se kì í se ohun rere tí èmi fẹ́ láti se; rárá, ṣùgbọ́n búburú tí èmi kò fẹ́, éyí nì ni èmi ń se."'), 'Rom$9:10': (None, None, 'Rom$9:10 " Kì sì íṣe kìkì èyí; Ṣùgbọ́n nígbà tí Rèbékà pẹ̀lú lóyún fún ẹnìkan, fún Ísákì baba wa;"'), 'Rom$12:21': (None, None, 'Rom$12:21 "Má ṣe jẹ́ kí búburú ṣẹ́gun rẹ, ṣùgbọ́n fi rere ṣẹ́gun búburú."'), 'Rom$13:3': (None, None, 'Rom$13:3 " Nítorí pé adájọ́ kò wá láti dẹ́rù ba àwọn ẹni tí ń se rere. Ṣùgbọ́n àwọn tó ń ṣe búburú yóò máa bẹ̀rù rẹ̀ nígbà gbogbo. Nítorí ìdí èyí, pa òfin mọ́ ìwọ kò sì ní gbé nínú ìbẹ̀rù."'), 'Rom$13:12': (None, None, 'Rom$13:12 " Òru bù kọjá tan, ilẹ̀ sì fẹ́rẹ mọ́: nítorí náà ẹ jẹ́ kí a bọ́ ara iṣẹ́ òkùnkùn sílẹ̀, kí a sì gbé ìhámọ́ra ìmọ́lẹ̀ wọ̀."'), 'Rom$14:9': (None, 'kú', 'Rom$14:9 "Nítorí ìdí èyí náà ni Kírísítì fi kú, tí ó sì tún yè, kí ó bá le jẹ́ Olúwa òkú àti alààye."'), 'Rom$14:16': (None, None, 'Rom$14:16 Má se gba kí a sọ̀rọ̀ ohun tí ó gbà sí rere ní buburu.'), 'Rom$15:1': ('alailera', 'lagbara', 'Rom$15:1 "Àwa tí a jẹ́ alágbára nínú ìgbàgbọ́ yẹ kí ó máa ru ẹrù àìlera àwọn aláìlera, kí a má si ṣe ohun tí ó wu ara wa."'), '1Cor$1:21': (None, 'ọgbọn', '1Cor$1:21 "Nítorí pé, nínú ọgbọ́n Ọlọ́run, ayé kò le mọ̀ òun nípa ọgbọ́n àti ìṣeféfé wọn. Ó sì gba gbogbo àwọn tí ó gbàgbọ́ là nípa ìwàásù tí àwọn aráyé pè ní òmùgọ̀ àti ọ̀rọ̀ ọ yẹ̀yẹ́."'), '1Cor$1:22': (None, 'ọgbọn', '1Cor$1:22 " Nítorí pé àwọn Júù ń bèrè àmì, àwọn Gíríkì sí ń ṣàfẹ́rí ọgbọ́n:"'), '1Cor$1:26': (None, 'ọlọgbọn', '1Cor$1:26 "Ará, ẹ kíyèsí ohun tí nígbà tí a pè yín. Bí ó ti ṣe pé, kì í ṣe ọ̀pọ̀ àwọn ọlọ́gbọ́n ènìyàn nípa ti ara, kí í ṣe ọ̀pọ̀ àwọn alàgbà, kì ì ṣe ọ̀pọ̀ àwọn ọlọ́lá ni a pè."'), '1Cor$4:5': (None, 'imole', '1Cor$4:5 " Nítorí náà, kí ẹ má ṣe se ìdájọ́ ohukóhun, kí Olúwa tó padà dé, ẹni tí yóò mú ohunkóhun tí ó farasin wá sí ìmọ́lẹ̀, tí yóò sì fi ìmọ̀ ọkàn ènìyàn hàn, nígbà náà ni olukúlukù yóò sì ní ìyìn tirẹ̀ lọ̀dọ́ Ọlọ́run."'), '1Cor$4:10': ('alailera', 'lagbara', '1Cor$4:10 " Àwa jẹ́ asiwèrè nítorí Kírísítì, ṣùgbọ́n ẹ̀yin jẹ́ ọlọ́gbọ́n nínú Kírísítì! Àwa jẹ́ aláìlera, ṣùgbọ́n ẹ̀yín jẹ́ alágbára! Ẹ̀yin jẹ́ ẹni àyẹ́sí, àwa jẹ ẹni ẹ̀gàn!"'), '1Cor$5:3': (None, 'ko si', '1Cor$5:3 " Lóòtọ́ èmi kò sí láàrin yín, ṣùgbọ́n mo wà lọ́dọ̀ yín nínú ẹ̀mí àti pé, ní orúkọ Olúwa Jésù Kírísítì, mo tí ṣe ìdájọ̀ lórí irú ẹni bẹ́ẹ̀, bí ẹni pé mo wá láàrin yín."'), '1Cor$5:7': ('atijọ', None, '1Cor$5:7 "Nítorí náà ẹ mú ìwúkàrà àtijọ́ kúrò nínú yín, kí ẹ̀yin kí ó lè jẹ ìyẹ̀fun titun, gẹ́gẹ́ bí ẹ̀yin tí jẹ àkàrà ti kò ní ìwúkàrà. Nítorí à ti fi ìrekọjá wa, àní Kírísítì ni a ti pa láti fi ṣe ẹbọ fún ẹ̀ṣẹ̀ wa."'), '1Cor$7:1': (None, None, '1Cor$7:1 "Ní báyìí, nítorí àwọn ohun tí ẹ ṣe kọ̀wé: Ó dára fún ọkùnrin kí ó máa gbéyàwó."'), '1Cor$7:2': (None, 'obinrin', '1Cor$7:2 Ṣùgbọ́n nítorí àgbérè pọ̀ tó bẹ́ẹ̀ gẹ́ẹ̀ kí ọkùnrin kọ̀ọ̀kan gbéyàwó tirẹ̀. Bẹ́ẹ̀ ni kí obìnrin kọ̀ọ̀kan ní ọkọ tirẹ̀.'), '1Cor$7:3': ('iyawo', 'ọkọ', '1Cor$7:3 "Kí ọkùnrin kí ó fi gbogbo ẹ̀tọ́ ìyàwó rẹ̀ fún ún, kí ìyàwó fi gbogbo ẹ̀tọ́ ọkọ fún ọkọ rẹ̀."'), '1Cor$7:4': ('iyawo', 'ọkọ', '1Cor$7:4 "Aya kò láṣẹ lórí ara rẹ̀ mọ́, ara rẹ̀ ti di ti ọkọ rẹ̀. Bákan náà ni ọkùnrin tí ó gbéyàwó kò ní àṣẹ lórí ara rẹ̀ mọ́, ara rẹ̀ ti di ti ìyàwó rẹ̀."'), '1Cor$7:10': (None, 'ọkọ', '1Cor$7:10 "Àwọn ti ó ti gbéyàwó àti àwọn tí ó lọ́kọ, ni mo fẹ́ pa á láṣẹ fún kì í ṣe láti ọ̀dọ̀ mi ṣùgbọ́n láti ọ̀dọ̀ Olúwa: “Obìnrin kò gbọdọ̀ fi ọkọ rẹ̀ sílẹ̀.”"'), '1Cor$7:14': ('iyawo', 'ọkọ', '1Cor$7:14 "Nítorí pé ó ṣe é ṣe kí a lè mú ọkọ tí kò gba Kírísítì gbọ́ súnmọ́ Ọlọ́run nípa aya tí í ṣe onígbàgbọ́, a sì le mú ìyàwó tí kò gba Kírísítì gbọ́ súnmọ́ Ọlọ́run nípa ọkọ tí í ṣe onígbàgbọ́. Bí kò bá ṣe bẹ́ẹ̀, àwọn ọmọ wọn yóò jẹ́ aláìmọ́. Ṣùgbọ́n bí wọn kò bá kọ ara wọn sílẹ̀, ó ṣe é ṣe kí àwọn ọmọ wọn di mímọ́."'), '1Cor$7:16': (None, 'ọkọ', '1Cor$7:16 " Báwo ni ẹ̀yin aya ṣe mọ̀ pé ẹ̀yin ni yóò gba ọkọ yín là? Bákan náà ni a lè wí nípa ọkọ tí í ṣe onígbàgbọ́ pé, kò sí ìdánilójú pé aya aláìgbàgbọ́ le yípadà láti di onígbàgbọ́ nípa dídúró ti ọkọ."'), '1Cor$7:39': (None, 'ọkọ', '1Cor$7:39 " A fi òfin dé obìnrin níwọ̀n ìgbà tí òun pẹ̀lú ọkọ rẹ̀ wà láàyè, bí ọkọ rẹ̀ bá kú, ó ní òmìnira láti ṣe ìgbéyàwó pẹ̀lú ọkùnrin mìíràn, tí ó bá wù ú ó sì gbọdọ̀ jẹ́ ti Olúwa."'), '1Cor$8:8': ('dara julọ', 'buru', '1Cor$8:8 " Ṣùgbọ́n oúnjẹ kò mú wa súnmọ́ Ọlọ́run, nítorí pé kì í ṣe bí àwa bá jẹun ni àwa buru julọ tàbí nígbà tí a kò jẹ ni àwa dára jùlọ."'), '1Cor$9:5': ('arakunrin', None, '1Cor$9:5 " Ṣé àwa kò ní ẹ̀tọ́ láti máa mú ìyàwó tí í se onígbàgbọ́ káàkiri gẹ́gẹ́ bí àwọn Àpósítélì mìíràn? Àti bí arákùnrin Olúwa, àti Kéfà."'), '1Cor$10:7': (None, None, '1Cor$10:7 " Kí ẹyin má ṣe sin ère òrìṣà gẹ́gẹ́ bí wọ́n ti ṣe; gẹ́gẹ́ bí a ti kọ ọ́ pé, “Àwọn ènìyàn náà jókòó láti jẹ àti láti mu, wọ́n sì dìde dúró láti jó.”"'), '1Cor$11:3': (None, 'obinrin', '1Cor$11:3 " Ṣùgbọ́n mo fẹ́ kí ẹ̀yin mọ̀ pé, Kírísítì ni orí olukúlúkù ọkùnrin, orí obìnrin sì ni ọkọ rẹ̀ àti orí Kírísítì sì ní Ọlọ́run."'), '1Cor$11:7': (None, 'obinrin', '1Cor$11:7 " Ṣugbọ́n ọkùnrin kò ní láti fi nǹkan bo orí rẹ́ nígbà tí ó bá ń sìn, nítorí àwọrán àti ògo Ọlọ́run ni òun í ṣe, ṣùgbọ́n ògo ọkùnrin ni obìnrin í ṣe."'), '1Cor$11:8': ('obinrin', None, '1Cor$11:8 " Ọkùnrin kò ti inú obìnrin wá, ṣùgbọ́n a yọ obìnrin jáde lára ọkùnrin,"'), '1Cor$11:9': (None, 'obinrin', '1Cor$11:9 bẹ́ẹ̀ ni a kò dá ọkùnrin fún àǹfààní obìnrin ṣùgbọ́n a da obìnrin fún ọkùnrin.'), '1Cor$11:11': ('obinrin', None, '1Cor$11:11 "Ẹ rántí pé, nínú ètò Ọlọ́run obìnrin kò lè wà láìsí ọkùnrin, bẹ́ẹ̀ ni ọkùnrin kò lè wà láàsí obìnrin."'), '1Cor$11:12': (None, 'obinrin', '1Cor$11:12 Lóòtọ́ láti ara ọkùnrin ni a ti yọ obìnrin jáde bẹ́ẹ̀ si ni ọkùnrin tipàsẹ obìnrin wa. Ṣùgbọ́n láti ọ̀dọ̀ Ọlọ́run ni ohun gbogbo ti wà.'), '1Cor$11:17': (None, 'buru', '1Cor$11:17 Nínú àwọn àlàkalẹ̀ èmi ko ni yìn yín nítorí pé bí ẹ bá pé jọ kì í ṣe fún rere bí ko ṣe fún búburú.'), '1Cor$12:21': ('ẹsẹ', 'ori', '1Cor$12:21 "Bí ó tí rí yìí, ojú kò lè sọ fún ọwọ́ pé, “Èmi kò nílò rẹ.” Bẹ́ẹ̀ ni orí kò le sọ fún ẹsẹ̀ pé, “Èmi kò nílò rẹ.”"'), '1Cor$12:22': (None, None, '1Cor$12:22 "Àwọn ẹ̀yà ara tí ó dàbí ẹni pé wọ́n ṣe aláìlágbára jùlọ, tí ó dàbí ẹni pé kò ṣe pàtàkì rárá, àwọn gan-an ni a ko le ṣe aláìnílò."'), '1Cor$12:24': ('ṣe alaini', 'ni', '1Cor$12:24 "Nítorí pé àwọn ibi tí ó ní ẹ̀yẹ ní ara wa kò nílò ìtọ́jú ọ̀tọ̀. Ṣùgbọ́n Ọlọ́run ti pa gbogbo ẹ̀yà ara pọ̀ sọ̀kan lọ́nà kan, ó sì ti fi ẹ̀yẹ tó ga jùlọ fún ibi tí ó ṣe aláìní."'), '1Cor$14:28': (None, 'jẹ ki', '1Cor$14:28 "Ṣùgbọ́n bí kò bá sí ògbufọ̀, kí ó pá ẹnu rẹ̀ mọ́ nínú ìjọ; sì jẹ́ kí ó máa bá ara rẹ̀ àti Ọlọ́run sọ̀rọ̀."'), '1Cor$15:33': (None, None, '1Cor$15:33 Kí a má tàn yín jẹ́: “Ẹgbẹ́ búburú bá ìwà rere jẹ́!?”'), '1Cor$15:45': (None, None, '1Cor$15:45 " Bẹ́ẹ̀ ní a si kọ ọ́ pé, “Ádámù ọkùnrin ìṣáàjú, alààyè ọkàn ni a dá a” Ádámù ìkẹ́yìn ẹ̀mí ti ń fún ní ní ìyè."'), '1Cor$15:47': ('keji', None, '1Cor$15:47 "Ọkùnrin ìṣáájú ti inú erùpẹ̀ wá, ẹni erùpẹ̀: ọkùnrin èkejì láti ọ̀run wá ni."'), '1Cor$16:17': (None, 'ni', '1Cor$16:17 Mo láyọ̀ fún wíwá Sítéfánà àti Fórítúnátù àti Ákáyákù: nítorí èyí tí ó kù nípá tí yín wọ́n ti fí kún un.'), '2Cor$1:19': (None, None, '2Cor$1:19 " Nítorí pé Jésù Kírísítì, Ọmọ Ọlọ́run, ẹni tí a tí wàásù rẹ̀ láàárin yín nípaṣẹ̀ èmí àti Silífánù àti Tímótíù, kì í ṣe bẹ́ẹ̀ ni bẹ́ẹ̀ kọ́, ṣùgbọ́n nínú rẹ̀ ni bẹ́ẹ̀ ní."'), '2Cor$2:3': ('ayo', 'ibanujẹ', '2Cor$2:3 "Èmi sì kọ̀wé bí mo tí kọ sí yín pé, nígbà tí mo bá sì de, kí èmi má ṣe ni ìbànújẹ́ lọ́dọ̀ àwọn ti ìbá mú mi ní ayọ̀: nítorí tí mo ní ìgbẹkẹlé nínú gbogbo yín, wí pé ẹ̀yin yóò jẹ́ alábápín ayọ̀ mi."'), '2Cor$2:12': ('wá', None, '2Cor$2:12 " Ṣùgbọ́n nígbá ti mo dé Tíróà láti wàásù iyinrere Kírísitì, tí mo sì ríi wí pé Olúwa ti sílẹ̀kùn fún mi,"'), '2Cor$4:6': ('okunkun', 'imole', '2Cor$4:6 " Nítorí Ọlọ́run ni ẹni tí ó wí pé, “Kí ìmọ́lẹ̀ ó mọ́lẹ̀ láti inú òkùnkùn jáde,” òun ní ó ti ń mọ́lẹ̀ lọ́kan wa, láti fún wa ní ìmọ́lẹ̀ ìmọ̀ ògo Ọlọ́run ní ojú Jésù Kírísítì."'), '2Cor$5:4': ('aṣọ', 'aṣọ', '2Cor$5:4 "Nítorí àwa tí ń bẹ nínú àgọ́ yìí ń kérora nítòótọ́, kì í ṣe nítorí tí àwa yóò jẹ́ aláìwọsọ, ṣùgbọ́n pé a ó wọ̀ wá ní aṣọ sí i, kí ìyè baà lè gbé ara kíkú mì."'), '2Cor$5:6': (None, 'ko si', '2Cor$5:6 "Nítorí náà àwa ní ìgboyà nígbà gbogbo, àwa sì mọ̀ pé, nígbà tí àwa ń bẹ ní ilé nínú ara, àwa kò sí lọ́dọ̀ Olúwa."'), '2Cor$5:9': (None, 'ko si', '2Cor$5:9 "Nítorí náà àwa ń dù ú, pé, bí àwa bá wà ní ilé tàbí bí a kò sí, kí àwa lè jẹ́ ẹni ìtẹ́wọ́gbà lọ́dọ̀ rẹ̀."'), '2Cor$5:10': ('buburu', None, '2Cor$5:10 Nítorí pé gbogbo wa ni ó gbọ́dọ̀ fí ara hán níwájú ìtẹ́ ìdájọ́ Kírísítì; kí olúkúlùkù lè gbà èyí tí ó ṣe gẹ́gẹ́ bí èyí tí ó tọ́ sí i ti ṣe nígbà tí ó wà nínú ara ìbáà ṣe rere tàbí búburú.'), '2Cor$5:14': (None, 'kú', '2Cor$5:14 " Nítorí ifẹ́ Kírísítì ń rọ̀ wá, nítorí àwa mọ̀ báyìí pé, bí ẹnìkan bá kú fún gbogbo ènìyàn, ǹjẹ́ nígbà náà, gbogbo wọ́n ni ó ti kú."'), '2Cor$5:17': ('atijọ', None, '2Cor$5:17 " Nítorí náà bí ẹnìkan bá wà nínú Kírísítì, ó di ẹ̀dá titun: ohun àtijọ́ ti kọjá lọ; kíyèsí i, ohun titun ti dé."'), '2Cor$6:14': ('aiṣododo', 'ododo', '2Cor$6:14 Ẹ má ṣe fí àìdọ́gba dàpọ̀ pẹ̀lú àwọn aláìgbàgbọ́: nítorí idápọ̀ kín ni òdodo ní pẹ̀lú àìṣòdodo? Ìdápọ̀ kín ni ìmọ́lẹ̀ sì ní pẹ̀lú òkùnkùn?'), '2Cor$8:9': ('ọlọrọ', 'talaka', '2Cor$8:9 " Nítorí ẹ̀yin mọ oore-ọ̀fẹ́ Olúwa wa Jésù Kírísítì, pé bí òun ti jẹ́ ọlọ́rọ̀ rí ṣùgbọ́n nítorí yín ó di talákà kí a lè sọ yín di ọlọ́rọ̀ nípa àìní rẹ̀."'), '2Cor$8:13': ('ní', None, '2Cor$8:13 "Nítorí èmi kò fẹ kí àwọn ẹlòmíràn wà ní ìrọ̀rùn, kí ó sì jẹ́ ìpọ́njú fún yín. Ṣùgbọ́n pé nípa mímú dọ́gba,"'), '2Cor$10:1': (None, 'ko si', '2Cor$10:1 " Ṣùgbọ́n èmi Pọ́ọ̀lù fúnra mi fi inú tútù àti ìwà pẹ̀lẹ́ Kírísítì bẹ̀ yín, èmi ẹni ìrẹ̀lẹ̀ lójú yín nígbà tí mo wà láàárin yín, ṣùgbọ́n nígbà tí èmi kò sí, mo di ẹni ìgboyà sí yín."'), '2Cor$10:3': (None, None, '2Cor$10:3 "Nítorí pé, bí àwa tilẹ̀ rìn nípa ti ara, ṣùgbọ́n àwa kò jagun nípa ti ara."'), '2Cor$10:11': (None, 'ko si', '2Cor$10:11 "Kí irú ènìyàn bẹ́ẹ̀ mọ̀ pé, irú ẹni tí àwa jẹ́ nínú ọ̀rọ̀ nípa ìwé kíkọ nígbà tí àwa kò sí, irú ẹni bẹ́ẹ̀ ni àwa sì jẹ́ nínú iṣẹ́ pẹ̀lú nígbà ti àwa bá wà."'), '2Cor$11:9': (None, 'ni', '2Cor$11:9 "Nígbà tí mo sì wà pẹ̀lú yín, tí mo sì ṣe aláìní, èmi kò dẹ́rù pa ẹnikẹ́ni: nítorí ohun tí mo ṣe aláìní ni àwọn ará tí ó ti Makedóníà wá ti mú wá. Bẹ́ẹ̀ ni nínú ohun gbogbo mo pa ara mi mọ́ láti má ṣe jẹ́ ẹrù fún yín, èmi yóò sì pa ara mi mọ́ bẹ́ẹ̀."'), '2Cor$11:25': (None, None, '2Cor$11:25 " Nígbà mẹ́ta ni a fi ọ̀gọ̀ lù mí, ẹ̀ẹ̀kanṣoṣo ni a sọ mí ní òkúta, ẹ̀ẹ̀mẹ́ta ni ọkọ̀ ojú omi mi rì, ọ̀sán kan àti òru kan ni mo wà nínú ibú."'), '2Cor$11:32': (None, None, '2Cor$11:32 " Ní Dámásíkù, baálẹ̀ tí ó wà lábẹ́ ọba Árétà fí ẹgbẹ́ ogun ká ìlú àwọn ara Dámásíkù mọ́, ó ń fẹ́ mi láti mú:"'), '2Cor$12:9': ('ailera', 'agbara', '2Cor$12:9 "Òun sì wí fún mi pé, “Oore ọ̀fẹ́ mi tó fún ọ: nítorí pé a sọ agbára mi di pípé nínú àìlera.” Nítorí náà tayọ̀tayọ̀ ni èmi ó kúkú máa fí ṣògo nínú àìlera mi, kí agbára Kírísítì lè máa gbé inú mi."'), '2Cor$12:10': ('alailera', 'lagbara', '2Cor$12:10 " Nítorí náà èmi ní inú dídùn nínú àìlera gbogbo, nínú ẹ̀gàn gbogbo, nínú àìní gbogbo, nínú inúníbíni gbogbo, nínú wàhálà gbogbo nítorí Kírísítì. Nítorí nígbà tí mo bá jẹ́ aláìlera, nígbà náà ni mo di alágbára."'), '2Cor$12:15': (None, None, '2Cor$12:15 "Èmi ó sì fi ayọ̀ ná ohun gbogbo tí mo bá ní, èmi ó sì ná ara mi fún ọkàn yín nítòótọ́; bí mo bá fẹ́ yín lọ́pọ̀lọpọ̀, ó há tọ́ kí ẹ̀yin kí ó fẹ́ràn mi díẹ̀ bí?"'), '2Cor$13:2': (None, None, '2Cor$13:2 "Mo ti sọ fún yín ṣáájú, mo sì ń sọ fún yín tẹ́lẹ̀, bí ẹni pé mo wà pẹ̀lú yín nígbà kejì, àti bí èmi kò ti sí lọ́dọ̀ yín ní ìsinsin yìí, mo kọ̀wé sí àwọn tí ó ti ṣẹ̀ náà, àti sí gbogbo àwọn ẹlòmíran, pé bí mo bá tún padà wá, èmi kì yóò dá wọn sí."'), '2Cor$13:9': ('alailera', 'lagbara', '2Cor$13:9 "Nítorí àwa ń yọ̀, nígbà ti àwa jẹ́ aláìlera, tí ẹ̀yín sì jẹ́ alágbára; èyí ni àwa sì ń gbàdúrà fún pẹ̀lú, àní pípé yín."'), '2Cor$13:10': (None, 'ko si', '2Cor$13:10 "Ìdí nìyí mo ṣe kọ̀wé àwọn nǹkan wọ̀nyí nígbà tí èmi kò sí lọ́dọ̀ yín, pé nígbà tí mo bá dé kí èmi má ba à lo ìkanra ní lílo àṣẹ, gẹ́gẹ́ bí àṣẹ tí Olúwa ti fifún mí, láti mú dàgbàsókè, kì í ṣe láti fà yín subú."'), 'Gal$2:19': ('kú', None, 'Gal$2:19 "Nítorí pé nípa òfin, mo tí di òkú sí òfin, kí èmi lè wà láàyè sí Ọlọ́run."'), 'Gal$3:28': ('obinrin', 'okunrin', 'Gal$3:28 " Kò le sí Júù tàbí Gíríkì, ẹrú tàbí òmìnira, ọkùnrin tàbí ọbìnrin nítorí pé ọ̀kan ni nínú Kírísítì Jésù."'), 'Eph$1:15': (None, 'ori', 'Eph$1:15 " Nítorí ìdí èyí, nígbà tí mo ti gbúro ìgbàgbọ́ ti ń bẹ láàárin yín nínú Jésù Olúwa, àti ìfẹ́ yín sí gbogbo àwọn ènìyàn mímọ́."'), 'Eph$2:11': (None, None, 'Eph$2:11 "Nítorí náà ẹ rántí pé, nígbà àtijọ́ rí, ẹ̀yin tí ẹ ti jẹ́ aláìkọlà nípa ti ìbí, tí àwọn tí a ń pè ní Akọlà ti a fi ọwọ́ ṣe ni ara ń pè ní Aláìkọlà."'), 'Eph$2:14': (None, 'ṣe', 'Eph$2:14 "Nítorí oun ni àlàáfíà wa, ẹni tí o ti ṣe méjèèjì ni ọ̀kan, tí ó sì ti wó ògiri ìkélé ti ìkórira èyí tí ń bẹ láàrin yín."'), 'Eph$3:1': (None, None, 'Eph$3:1 "Nítorí èyí náà ni èmi Pọ́ọ̀lù ṣe di òǹdè Jésù Kírísítì nítorí ẹ̀yin aláìkọlà,"'), 'Eph$4:20': (None, None, 'Eph$4:20 Ṣùgbọ́n a kò fi Kírísítì kọ́ yín bẹ́ẹ̀.'), 'Eph$5:23': (None, 'ọkọ', 'Eph$5:23 "Nítorí pé ọkọ ni ó ń ṣe orí aya, gẹ́gẹ́ bí Kírísítì tí i ṣe orí ìjọ, tí ó ń ṣe ara rẹ̀, oún si ni Olugbálá rẹ̀."'), 'Eph$5:31': (None, 'iya', 'Eph$5:31 " Nítorí èyí ní ọkùnrin yóò ṣe fí Baba àti ìyá rẹ̀ sílẹ̀, òun yóò sì dàpọ̀ mọ́ aya rẹ̀, àwọn méjèèjì á sí dí ara kán."'), 'Eph$5:33': (None, 'ọkọ', 'Eph$5:33 "Ṣùgbọ́n kí olúkúlùkù yín fẹ́ràn aya rẹ̀, bẹ́ẹ̀ gẹ́gẹ́ bí òun fúnra rẹ̀; kí aya kí ó sì bọ̀wọ̀ fún ọkọ rẹ̀."'), 'Eph$6:2': ('baba', 'iya', 'Eph$6:2 " “Bọ̀wọ̀ fún bábá àtí ìyá rẹ”: èyí tí í ṣe ofín kìn-ín-ní pẹ̀lú ilérí,"'), 'Eph$6:17': (None, 'gba', 'Eph$6:17 "Kí ẹ sì mú àṣíborí ìgbàlà, àti idà ẹ̀mí, tí í ṣe ọ̀rọ̀ Ọlọ́run."'), 'Phil$2:12': (None, None, 'Phil$2:12 "Nítorí náà ẹ̀yin ará mi, gẹ́gẹ́ bí ẹ̀yin ti ń gbọ́ràn nígbà gbogbo, kì í ṣe nígbà ti mo wà lọ́dọ̀ yín nìkan, ṣùgbọ́n pàápàá nísinsin yìí tí èmi kò sí ni àárin yín, ẹ máa ṣiṣẹ́ ìgbàlà yín yọrí pẹ̀lú ìbẹ̀rù àti ìwárìrì,"'), 'Phil$2:28': (None, None, 'Phil$2:28 "Nítorí náà ni mo ṣe ní ìtara láti rán an, pé nígbà tí ẹ̀yin bá sì tún rí i, kí ẹ̀yin lè yọ̀ àti kí ìkáyà sókè mi lè dínkù."'), 'Phil$3:7': (None, 'ere', 'Phil$3:7 "Ṣùgbọ́n ohunkóhun tí ó ti jásí èrè fún mi, àwọn ni mo ti kà sí òfo nítorí Kírísítì."'), 'Col$1:9': (None, None, 'Col$1:9 " Nítorí ìdí èyí, láti ọjọ́ tí a ti gbọ́ nípa yín, a kò yéé gbàdúrà fún yin, tí a sì ń gbàdúrà pé kí Ọlọ́run fi ìmọ̀ ìfẹ́ rẹ̀ kún yín nípaṣẹ̀ ọgbọ́n àti òye gbogbo tí ń ṣe ti Ẹ̀mí"'), 'Col$2:20': (None, 'kú', 'Col$2:20 " Bí ẹ̀yin bá ti kú pẹ̀lú Kírísítì kúrò nínú ìpilẹ̀ṣẹ̀ ayé, èé ha ti ṣe tí ẹ̀yin ń tẹ́ríba fún òfin bí ẹni pé ẹ̀yin wà nínú ayé,"'), 'Col$3:9': (None, None, 'Col$3:9 "Ẹ má ṣe purọ́ fún ara yín, níwọ̀n ìgbà tí ẹ ti bọ́ ògbólógbòó ayé yín pẹ̀lú ìṣe rẹ̀ sílẹ̀,"'), '1Thess$1:9': (None, 'kú', '1Thess$1:9 "Ṣe ni wọ́n bẹ̀rẹ̀ sí i ròyìn fún wa bí ẹ ti gbà wá lálejò. Wọ́n ròyìn fún wa pẹ̀lú pé, ẹ ti yípadà kúrò nínú ìbọ̀rìṣà àti wí pé Ọlọ́run alààyè nìkan ṣoṣo ni ẹ ń sìn."'), '1Thess$2:9': (None, None, '1Thess$2:9 "Nítòótọ́ ẹ rántí, ará, iṣẹ́ àti làálàá wa; lọ́sàn-án àti lóru ni àwa ń ṣiṣẹ́ kí ìnáwó wa má bà á di ìṣòro fún ẹnìkẹ́ni bí a ti ń wàásù ìyìnrere Ọlọ́run fún un yín."'), '1Thess$4:3': ('ni', None, '1Thess$4:3 " Nítorí ìfẹ́ Ọlọ́run ni pé kí ẹ jẹ́ mímọ, kí ẹ sì yàgò kúrò nínú ẹ̀ṣẹ̀ àgbérè,"'), '1Thess$4:10': ('ni', None, '1Thess$4:10 "Nitòótọ́, ẹ ní ìfẹ́ fún àwọn arákùnrin tí ó wà káàkiri Makidóníà. Ṣíbẹ̀ṣíbẹ̀ àwa ń bẹ̀ yín, ará, pé kí ẹ̀yin kí ó máa tẹ̀síwájú nínú rẹ̀."'), '1Thess$4:16': (None, 'kú', '1Thess$4:16 " Nítorí pé, Olúwa fúnraarẹ́ yóò sọ̀kalẹ̀ láti ọ̀run wá, pẹ̀lú ariwo àṣẹ ńlá àti ohùn àwọn ańgẹ́lì ti àwọn ańgẹ́lì ti àwọn ti ìpè Ọlọ́run, àwọn òkú nínú Kírísítì yóò sì kọ́kọ́ jíǹde."'), '1Thess$5:2': ('ọjọ', None, '1Thess$5:2 nítorí ẹ̀yin pàápàá mọ̀ wí pé ọjọ́ Olúwa yóò wàá bí olè lóru.'), '1Thess$5:5': (None, None, '1Thess$5:5 Nítorí gbogbo yín ni ọmọ ìmọ́lẹ̀ àti ọmọ ọ̀sán gangan. Ẹ kì í ṣe tí òru tàbí tí òkùnkùn mọ́.'), '1Thess$5:9': (None, None, '1Thess$5:9 " Nítorí pé, Ọlọ́run kò yàn wa láti da ìbínú rẹ̀ gbígbóná sí orí wa, ṣùgbọ́n ó yàn láti gbà wá là nípasẹ̀ Olúwa wa, Jésù Kírísítì."'), '1Thess$5:15': (None, None, '1Thess$5:15 " Ẹ rí i pé kò sí ẹni tí ó fi búburú san búburú, ṣùgbọ́n ẹ maa lépa èyí tíi ṣe rere láàrin ara yín àti sí ènìyàn gbogbo."'), '2Thess$3:7': (None, None, '2Thess$3:7 Nítorí ẹ̀yin náà mọ̀ bí ó ṣe yẹ kí ẹ máa farawé wa. Àwa kì í ṣe ìmẹ́lẹ́ nígbà tí a wà ní ọ̀dọ̀ yín.'), '1Tim$2:12': (None, 'obinrin', '1Tim$2:12 "Ṣùgbọ́n èmi kò fi àṣẹ fún obìnrin láti máa kọ́ni, tàbí láti pàṣẹ lórí ọkùnrin, bí kò ṣe kí ó dákẹ́jẹ́."'), '1Tim$3:2': ('ọkọ', None, '1Tim$3:2 "Ǹjẹ́ Bíṣọ́ọ̀bù yẹ kí ó jẹ́ aláìlẹ́gàn, ọkọ aya kan, olùsọ̀ràn, aláìrékọjá, oníwà yíyẹ, olùfẹ́ àlejò ṣíṣe, ẹni tí ó lè ṣe olùkọ́."'), '1Tim$3:12': (None, None, '1Tim$3:12 "Kí àwọn díákónì jẹ́ ọkọ obìnrin kan, kí wọn káwọ́ àwọn ọmọ àti ilé ara wọn dáradára."'), '1Tim$4:1': (None, None, '1Tim$4:1 "NÍṢÍNṢIN YÌÍ, èmi ń tẹnumọ́ ọ́ pé ní ìgbà ìkẹyìn àwọn mìíràn yóò kúrò nínú ìgbàgbọ́, wọn yóò máa fiyèsí àwọn ẹ̀mí tí ń tannijẹ, àti ẹ̀kọ́ àwọn ẹ̀mí èsù."'), '1Tim$5:1': (None, 'baba', '1Tim$5:1 "Má ṣe bá àgbàlagbà ọkùnrin wí, ṣùgbọ́n kí ó máa gbà á níyànjú bíi bàbá; àwọn ọ̀dọ́mọkùnrin bí arákùnrin."'), '1Tim$5:5': (None, None, '1Tim$5:5 "Ǹjẹ́ ẹni ti í ṣe opó nítòótọ́, ti ó ṣe òun nìkan, a máa gbẹ́kẹ̀lé Ọlọ́run, a sì máa dúró nínú ẹ̀bẹ̀ àti nínú àdúrà lọ́sàn àti lóru."'), '1Tim$5:11': ('ni', 'kọ', '1Tim$5:11 "Ṣùgbọ́n kọ̀ (láti kọ orúkọ) àwọn opó tí kò dàgbà; nítorí pé nígbà ti wọn bá ti ṣe ìfẹ́kúfẹ̀ẹ́ lòdì sí Kírísítì, wọn á tún fẹ́ láti gbéyàwó."'), '1Tim$5:16': (None, 'obinrin', '1Tim$5:16 "Bí ọkùnrin tàbí obìnrin kan tí ó gbàgbọ́ bá ní àwọn opó, kí ó máa ràn wọ́n lọ́wọ́, kí a má sì di ẹrù lé ìjọ, kí wọn lè máa ran àwọn ti í ṣe opó nítòótọ́ lọ́wọ́."'), '2Tim$1:3': (None, None, '2Tim$1:3 "Mo dúpẹ́ lọ́wọ́ Ọlọ́run, ẹni tí èmi ń sìn pẹ̀lú ẹ̀rí-ọkàn funfun gẹ́gẹ́ bí àwọn baba mi ti í ṣe, pé ni àìsinmi lọ́sàn-án àti lóru ni mo ń ṣe ìrántí rẹ nínú àdúrà mi."'), '2Tim$2:11': ('kú', None, '2Tim$2:11 "Òtítọ́ ni ọ̀rọ̀ náà: Bi àwa bá bá a kú, àwa yóò yè pẹ̀lú rẹ̀."'), '2Tim$2:24': ('ya', None, '2Tim$2:24 "Ìránsẹ́, Olúwa kò sì gbọdọ̀ jà; bí kò ṣe kí ó jẹ́ ẹni pẹ̀lẹ́ sí ènìyàn gbogbo ẹni tí ó lè kọ́ni, onísùúrù."'), '2Tim$4:9': (None, 'wá', '2Tim$4:9 Sa ipá rẹ láti tètè tọ̀ mí wá.'), 'Titus$1:5': (None, None, 'Titus$1:5 Ìdí tí mo fi fi ọ́ sílẹ̀ ní Kírétè ni pé kí o lè ṣe àsepé àwọn iṣẹ́ tó sẹ́kù. Mo sì ń rọ̀ ọ́ kí o yan àwọn alàgbà ní ìlú kọ̀ọ̀kan gẹ́gẹ́ bí mo ṣe darí rẹ̀.'), 'Titus$2:7': (None, None, 'Titus$2:7 "Nínú ohun gbogbo fi ara rẹ̀ hàn gẹ́gẹ́ bí alápẹẹrẹ ohun rere. Nínú ẹ̀kọ́ rẹ fi apẹẹrẹ ìwà pípé hàn, ẹni tó kún ojú òsùnwọ̀n"'), 'Titus$3:1': (None, None, 'Titus$3:1 "Rán àwọn ènìyàn náà létí láti máa tẹríba fún ìjọba àti àwọn aláṣẹ. Kí wọn ṣe ìgbọ́ràn nígbà gbogbo, kí wọn sì múra fún iṣẹ́ rere gbogbo."'), 'Titus$3:10': ('keji', None, 'Titus$3:10 "Bí ẹnikẹ́ni bá fẹ́ dá ìyapa sílẹ̀ láàrin yín, ẹ bá a wí lẹ́ẹ̀kínní àti lẹ́ẹ̀kejì. Lẹ́yìn náà, ẹ má ṣe ní ohunkóhun íṣe pẹ̀lú rẹ̀."'), 'Phlm$1:10': (None, None, 'Phlm$1:10 " Èmí bẹ̀ ọ́ nítorí ọmọ mi Ónísímu, ẹni tí ó di ọmọ nígbà tí mo wà nínú ìdè."'), 'Heb$3:14': ('opin', None, 'Heb$3:14 "Nítorí àwá di alábàápín pẹ̀lú Kírísítì, bí àwá bá di ìpilẹ̀ṣẹ̀ ìgbẹ́kẹ̀lé wa mú ṣinṣin títí dé òpin;"'), 'Heb$5:14': (None, None, 'Heb$5:14 "Ṣùgbọ́n oúnjẹ líle wà fún àwọn tí ó dàgbà, àwọn ẹni nípa ìrírí, tí wọn ń lo ọgbọ́n wọn láti fi ìyàtọ̀ sáàrin rere àti búburú."'), 'Heb$7:1': (None, None, 'Heb$7:1 " Nítorí Melikisédékì yìí, ọba Sálẹ́mù, àlùfáà Ọlọ́run ọ̀gá ògo, ẹni tí ó pàdé Ábúráhámù bí ó ti ń padà bọ̀ láti ibi pípa àwọn ọba, tí ó sì súre fún un."'), 'Heb$8:7': ('keji', None, 'Heb$8:7 "Nítorí ìbá ṣe pé májẹ̀mú ìṣaajú nì kò ní àbùkù, ǹjẹ́ a kì bá ti wá àyè fún èkejì."'), 'Heb$8:13': (None, None, 'Heb$8:13 "Ní èyí tí ó wí pé, Májẹ̀mu títún ó ti sọ ti ìṣáájú di ti láéláé. Ṣùgbọ́n èyí tí ó ń di i ti láéláé tí ó sì ń gbó, o múra àti di asán."'), 'Heb$9:7': (None, 'keji', 'Heb$9:7 " Ṣùgbọ́n sínú èkejì ni olórí àlùfáà nìkan máa ń lọ lẹ́kan ṣoṣo lọ́dún, fún ara rẹ̀, àti fún ìsìnà àwọn ènìyàn."'), 'Heb$9:13': (None, None, 'Heb$9:13 Nítorí bí ẹ̀jẹ̀ ewúrẹ́ àti ti akọ màlúù àti eerú ẹgbọrọ abo màlúù tí a fi wọ́n àwọn tí a ti sọ di aláìmọ́ ba ń sọni-di-mímọ́ fún ìwẹ̀nùmọ́ ara:'), 'Heb$10:11': (None, None, 'Heb$10:11 "Àti olukulùkù àlùfáà sì ń dúró lójoojúmọ́ láti ṣiṣẹ́ ìsìn, ó sì ń ṣe ẹbọ kan náà nígbàkúgbà, tí kò lè mú ẹ̀ṣẹ̀ kúrò láé:"'), 'Heb$11:23': ('obi', 'ọmọ', 'Heb$11:23 " Nípa ìgbàgbọ́ ní àwọn òbí Móṣè pa a mọ́ fún oṣù mẹ́ta nígbà tí a bí i, nítorí ti wọn rí i ní arẹwà ọmọ; wọn kò sì bẹ̀rù àṣẹ ọba."'), 'Heb$11:24': (None, None, 'Heb$11:24 " Nípa ìgbàgbọ́ ni Móṣè, nígbà tí o dàgbà, ó kọ̀ ki a máa pé òun ni ọmọ ọmọ-bìnrin Fáráò;"'), 'Jas$2:2': (None, None, 'Jas$2:2 "Nítorí bí ọkùnrin kan bá wá sí ìpéjọpọ̀ yín, pẹ̀lú òrùka wúrà àti aṣọ dáradára, tí talákà kan sì wá pẹ̀lú nínú aṣọ èérí;"'), 'Jas$2:5': ('ọlọrọ', 'talaka', 'Jas$2:5 "Ẹ fi etí sílẹ̀, ẹ̀yin ará mi olùfẹ́: Ọlọ́run kò ha ti yan àwọn talákà ayé yìí láti jẹ́ ọlọ́rọ̀ nínú ìgbàgbọ́, àti ajogún ìjọba náà, tí ó ti ṣe ìlérí fún àwọn tí ó fẹ́ ẹ?"'), 'Jas$2:10': (None, None, 'Jas$2:10 "Nítorí ẹnikẹ́ni tí ó bá pa gbogbo òfin mọ́, tí ó sì rú ọ̀kan, ó jẹ̀bi rírú gbogbo rẹ̀."'), 'Jas$2:15': ('arakunrin', 'arabinrin', 'Jas$2:15 "Bí arákùnrin tàbí arábìnrin kan bá wà ní àìní aṣọ, tí ó sì ṣe àìní oúnjẹ òòjọ́,"'), 'Jas$4:13': ('ta', None, 'Jas$4:13 "Ẹ wá nísinsìn yìí, ẹ̀yin tí ó ń wí pé, “Lónìí tàbí lọ́la àwa ó lọ sí ìlú báyìí, a ó sì ṣe ọdún kan níbẹ̀, a ó sì ṣòwò, a ó sì jèrè.”"'), 'Jas$5:12': (None, None, 'Jas$5:12 " Ṣùgbọ́n ju ohun gbogbo lọ, ará mi, ẹ má ṣe búra, ìbáà ṣe ìfi ọ̀run búra, tàbí ilẹ̀, tàbí ìbúra-kíbùúra mìíràn. Ṣùgbọ́n jẹ́ kí “Bẹ́ẹ̀ ni” yín jẹ́ bẹ́ẹ̀ ni; àti “Bẹ́ẹ̀ kọ́” yín jẹ́ bẹ́ẹ̀ kọ́; kí ẹ má baà bọ́ sínú ẹ̀bi."'), '1Pet$2:9': ('okunkun', 'imole', '1Pet$2:9 " Ṣùgbọ́n ẹ̀yin ni ìran tí a yàn Olu àlùfáà, Orílẹ̀ èdè mímọ́, ènìyàn ọ̀tọ̀ ki ẹ̀yin lè fi ọla ń lá ẹni tí ó pè yín jáde kúrò nínú òkùnkùn sínú ìmọ́lẹ̀ ìyanu rẹ̀ hàn:"'), '1Pet$2:21': (None, None, '1Pet$2:21 "Sí èyí ni a pè yín: nítorí Kírísítì pẹ̀lú jìyà fún-un yin, ó fi àpẹẹrẹ sílẹ̀ fún-un yín, kí ẹ̀yin lè máa tọ ipaṣẹ̀ rẹ̀:"'), '1Pet$3:7': (None, None, '1Pet$3:7 "Bẹ́ẹ̀ gẹ́gẹ́ ẹ̀yin ọkọ, ẹ máa fi Òye ba àwọn aya yín gbé, ẹ máa fi ọlá fún aya, bi ohun èlò ti kò lágbára, àti pẹ̀lú bí àjùmọ̀jogún oore-ọ̀fẹ́ ìyè; kí àdúrà yín má bà á ní ìdènà."'), '1Pet$3:10': ('ibi', None, '1Pet$3:10 " Nítorí, “Ẹni tí yóò bá fẹ ìyè, ti yóò sì rí ọjọ́ rere, kí o pa ahọ́n rẹ̀ mọ́ kúrò nínú ibi, àti ètè rẹ̀ mọ́ kúrò nínú sísọ ọ̀rọ̀ ẹ̀tàn:"'), '1Pet$3:14': (None, None, '1Pet$3:14 " Ṣùgbọ́n bí ẹ̀yin bá jìyà nítorí òdodo, àlàáfíà ni: ẹ má ṣe bẹ̀rù wọn, kí ẹ má sì ṣe kọminú;"'), '1Pet$4:6': ('kú', None, '1Pet$4:6 "Nítorí èyí ní a ṣá ṣe wàásù ìyìn rere fún àwọn òkú, kí a lè ṣe ìdájọ́ wọn gẹ́gẹ́ bí ènìyàn nípa ti ara, ṣùgbọ́n kí wọ́n lè wà láàyè si Ọlọ́run nípa tí Ẹ̀mí."'), '1Pet$4:17': ('berè', None, '1Pet$4:17 "Nítorí ti ìgbà náà dé, tí ìdájọ́ yóò bẹ̀rẹ̀ láti ilé Ọlọ́run wá: bí ó bá sì tètè tí ọ̀dọ̀ wa bẹ̀rẹ̀, ìgbẹ̀hìn àwọn tí kò gba ìyìn rere Ọlọ́run yó ha ti rí?"'), '1Pet$4:19': (None, 'jẹ ki', '1Pet$4:19 "Nítorí náà ẹ jẹ́ kí àwọn tí ń jìyà gẹ́gẹ́ bí ìfẹ́ Ọlọ́run máa ṣe rere, fi ọkàn wọn lè Ẹlẹ́dàá olóòtọ́ lọ́wọ́."'), '2Pet$1:19': ('imole', None, '2Pet$1:19 "Àwa sì ní ìfẹsẹ̀múlẹ̀ ọ̀rọ̀ àwọn wòlìí dunjúdunjú síi, èyí tí ó yẹ kí ẹ kíyèsí gẹ́gẹ́ bí ìmọ́lẹ̀ tó ń mọ́lẹ̀ níbi tí òkùnkùn gbé wà, títí ilẹ̀ yóò fi mọ́, tí ìràwọ́ òwúrọ̀ yóò sì yọ lọ́kàn yín."'), '2Pet$2:4': (None, None, '2Pet$2:4 "Nítorí pé bi Ọlọ́run kò bá dá àwọn ańgẹ́lì si nígbà tí wọn ṣẹ̀, ṣùgbọ́n ti ó sọ wọ́n sí ìsàlẹ̀ ọ̀gbun tí ó sì fi wọ́n sínú ọ̀gbun òkùnkùn biribiri nínú ìfipamọ́ títí dé ìdájọ́."'), '2Pet$2:20': (None, None, '2Pet$2:20 "Lẹ́yìn tí wọ́n bá ti yọ tán kúrò nínú èérí ayé nípa mímọ́ Olúwa àti Olúgbála wá Jésù Kírísítì, bí wọn bá sì tún fi ara kó o, tí a sì ṣẹ́gun wọn, ìgbẹ̀yìn wọn a buru jú ti ìṣáájú lọ."'), '2Pet$3:1': (None, None, '2Pet$3:1 "Olùfẹ́, èyí ni ìwé kejì tí mo ń kọ sí yín, nínú méjèèjì náà ni èmi ń ru èrò inú yín sókè nípa rírán yín létí;"'), '2Pet$3:10': ('ọjọ', None, '2Pet$3:10 "Ṣùgbọ́n ọjọ́ Olúwa ń bọ̀wá bí olè ní òru; nínú èyí tí àwọn ọ̀run yóò kọjá lọ pẹ̀lú ariwo ńlá, àti àwọn ìmọ́lẹ̀ ojú ọ̀run yóò sì ti inú ooru gbígbóná gidigidi di yíyọ́, ayé àti àwọn iṣẹ́ tí ó wà nínú rẹ̀ yóò sì jóná lúúlúú."'), '1John$1:5': ('okunkun', 'imole', '1John$1:5 Èyí sì ni iṣẹ́ tí àwa ti gbọ́ lẹ́nu rẹ tí àwa sì ń jẹ́ fún yín: pé ìmọ́lẹ̀ ni Ọlọ́run; ní ọ̀dọ̀ rẹ̀ kò sì òkùnkùn rárá.'), '1John$1:6': ('okunkun', None, '1John$1:6 " Bí àwa bá wí pé àwa ní ìdàpọ̀ pẹ̀lú rẹ̀, tí àwa sì ń rìn nínú òkùnkùn, àwa ń ṣèké, àwa kò sì gbé nínú òtítọ́."'), '1John$2:7': ('atijọ', 'tuntun', '1John$2:7 " Ẹ̀yin olùfẹ ọ̀wọ́n, kì í ṣe òfin tuntun ni mo ń kọ̀wé rẹ̀ sí yín ṣùgbọ́n òfin àtijọ́, èyí tí ẹ̀yin tí gbọ́ ní àtètèkọ́ṣe. Òfin àtijọ́ ni ọ̀rọ̀ náà tí ẹ̀yin tí gbọ́."'), '1John$2:8': ('okunkun', 'imole', '1John$2:8 " Pẹ̀lúpẹ̀lú, òfin tuntun ni mo ń kọ̀wé rẹ̀ sí yín; èyí tí í ṣe òtítọ́ nínú rẹ̀ àti nínú yin, nítorí òkùnkùn ń kọjá lọ, ìmọ́lẹ́ òtítọ́ sì tí ń tàn."'), '3John$1:11': ('ibi', None, '3John$1:11 "Olùfẹ́, má ṣe ṣe àfarawé ohun tí í ṣe ibi bí kò ṣe ohun tí ṣe rere. Ẹni tí ó bá ń ṣe rere ti Ọlọ́run ni; ẹni tí ó ba ń ṣe búburú kò rí Ọlọ́run."'), 'Rev$1:8': (None, None, 'Rev$1:8 " “Èmi ni Álfà àti Òmégà,” ni Olúwa Ọlọ́run wí, “ẹni tí ó ń bẹ, tí ó ti wà, tí ó sì ń bọ̀ wá, Olódùmarè.”"'), 'Rev$1:10': (None, None, 'Rev$1:10 "Mo wà nínú Ẹ̀mí ní ọjọ́ Olúwa, mo sì gbọ́ ohùn ńlá kan lẹ̀yìn mi, bí ìró ipè,"'), 'Rev$1:14': (None, None, 'Rev$1:14 "Orí rẹ̀ àti irun rẹ̀ funfun bí ẹ̀gbọ̀n òwú, ó funfun bí Sínóò; ojú rẹ̀ sì dàbí, ọwọ́ iná;"'), 'Rev$2:8': (None, 'kú', 'Rev$2:8 " “Àti sí Ańgẹ́lì Ìjọ ní Símírínà Kọ̀wé: Nǹkan wọ̀nyí ni ẹni tí í ṣe ẹni-ìṣáájú àti ẹni-ìkẹyìn wí, ẹni tí ó ti kú, tí ó sì tún yè:"'), 'Rev$2:18': (None, None, 'Rev$2:18 " “Àti sí Ańgẹ́lì ìjọ ní Tíátírà kọ̀wé: Nǹkan wọ̀nyí ni Ọmọ Ọlọ́run wí, ẹni tí ojú rẹ̀ dàbí ọ̀wọ́ iná, tí ẹṣẹ̀ rẹ̀ sì dàbí idẹ dáradára:"'), 'Rev$3:1': (None, 'kú', 'Rev$3:1 "“Àti sí Ańgẹ́lì ìjọ ni Sádísì kọ̀wé: Ọ̀rọ̀ wọ̀nyí ni ẹni tí ó ni Ẹ̀mí méje Ọlọ́run, àti ìràwọ̀ méje wí pé: Èmi mọ̀ iṣẹ́ rẹ̀, àti pé ìwọ ni orúkọ pé ìwọ ń bẹ láàyè, ṣùgbọ́n ìwọ ti kú."'), 'Rev$3:7': ('sé', None, 'Rev$3:7 " “Àti sí Ańgẹ́lì Ìjọ ni Filadéfíà Kọ̀wé: Ọ̀rọ̀ wọ̀nyí ni ẹni tí ó jẹ́ mímọ́ náà wí, ẹni tí ó ṣe olóòótọ́, ẹni tí ó ní kọ́kọ́rọ́ Dáfídì, ẹni tí ó sí, tí kò sí ẹni tí yóò tì; ẹni tí o sì tì, tí kò sì ẹni tí yóò ṣí i:"'), 'Rev$3:14': (None, None, 'Rev$3:14 " “Àti sí áńgẹ́lì ìjọ ní Láódékíá kọ̀wé: Ọ̀rọ̀ wọ̀nyí ni ẹni tí ń jẹ́ Àmín wí, ẹlẹ́rìí olódodo àti olóòótọ́, olórí ìṣẹ̀dá Ọlọ́run:"'), 'Rev$3:16': ('tutu', 'gbona', 'Rev$3:16 "Ǹjẹ nítorí tí ìwọ lọ wọ́ọ́rọ́, tí o kò si gbóná, bẹ́ẹ̀ ni tí o kò tutù, èmi yóò pọ̀ ọ́ jáde kúrò ni ẹnu mi."'), 'Rev$3:17': (None, 'talaka', 'Rev$3:17 " Nítorí tí ìwọ wí pé, Èmi ní ọrọ̀, èmi sì ń pọ̀ sí i ni ọrọ̀, èmi kò si ṣe aláìní ohunkóhun; tí ìwọ kò sì mọ̀ pé, òsì ni ìwọ, ẹni-ikáànù, tálákà, afọ́jú, àti ẹni-ìhòhò:"'), 'Rev$4:7': ('keji', None, 'Rev$4:7 " Ẹ̀dá kínní sì dàbí kìnnìún, ẹ̀dá kéjì si dàbí ọmọ màlúù, ẹ̀dá kẹta sì ni ojú bi ti ènìyàn, ẹ̀dá kẹ́rin sì dàbí ìdì tí ń fò."'), 'Rev$4:8': (None, None, 'Rev$4:8 " Àwọn ẹ̀dá alààyè mẹ́rin náà, tí olúkúlùkù wọn ni ìyẹ́ mẹ́fà, kún fún ojú yíká ara àti nínú; wọn kò sì sinmi lọ́sàn-ań àti lóru, láti wí pé: “Mímọ́, Mímọ́, Mímọ́, Olúwa Ọlọ́run Olódùmare, tí ó ti wà, tí ó sì ń bẹ, tí ó sì ń bọ̀ wá!”"'), 'Rev$6:4': (None, 'gba', 'Rev$6:4 "Ẹṣin mìíràn tí ó pupa sì jáde: a sì fi agbára fún ẹni tí ó jókòó lórí rẹ̀, láti gba àlàáfíà kúrò lórí ilẹ̀ ayé, àti pé kí wọn kí ó máa pa ara wọn: A sì fi idà ńlá kan lé e lọ́wọ́."'), 'Rev$7:15': (None, None, 'Rev$7:15 "Nítorí náà ni, “wọn ṣe ń bẹ níwájú ìtẹ́ Ọlọ́run, tí wọn sì ń sìn ín, lọ́sàn àti lóru nínú tẹḿpílì rẹ̀; ẹni tí o jókòó lórí ìtẹ́ náà yóò si ṣíji bò wọn."'), 'Rev$8:12': ('ọjọ', None, 'Rev$8:12 "Ańgẹ́lì kẹ́rin sì fún ìpè tirẹ̀, a sì kọlu ìdámẹ̀ta òòrùn, àti ìdámẹ̀ta òṣùpá, àti ìdámẹ̀ta àwọn ìràwọ̀, ki ìdámẹ̀ta wọn lè ṣóòkùn, kí ọjọ́ má ṣe mọ́lẹ̀ fún ìdámẹ̀ta rẹ̀, àti òru báka náà."'), 'Rev$9:12': (None, None, 'Rev$9:12 "Ègbé kan tí kọjá, kíyèsi i, ègbé méjì ń bọ̀ síbẹ̀ lẹ́yìn èyí."'), 'Rev$10:1': ('ẹsẹ', 'ori', 'Rev$10:1 "Mó sì rí ańgẹ́lì mìíràn alágbára ò ń ti ọ̀run sọ̀kalẹ̀ wá, a fi awọsànmà wọ̀ ọ́ ni aṣọ; òṣùmàrè sì ń bẹ ní orí rẹ̀, ojú rẹ̀ sì dàbí òòrùn, àti ẹṣẹ̀ rẹ̀ bí ọ̀wọ́n iná."'), 'Rev$10:5': (None, None, 'Rev$10:5 " Ańgẹ́lì náà tí mo rí tí ó dúró lórí òkun àti lórí ilẹ̀, sì gbé ọwọ́ rẹ̀ sí òkè ọ̀run:"'), 'Rev$11:16': (None, None, 'Rev$11:16 "Àwọn àgbà mẹ́rìnlélógún náà tí wọn jókòó níwájú Ọlọ́run lórí ìtẹ́ wọn, dojúbolẹ̀, wọn sì sin Ọlọ́run,"'), 'Rev$12:1': ('ẹsẹ', 'ori', 'Rev$12:1 "Àmì ńlá kan sì hàn ni ọ̀run; obìnrin kan tí a fi òòrùn wọ̀ ní aṣọ, òṣùpá sì ń bẹ lábẹ́ ẹsẹ̀ rẹ̀, adé oníràwọ̀ méjìlá sì ń bẹ lórí rẹ̀:"'), 'Rev$12:10': (None, None, 'Rev$12:10 " Mo sì gbọ́ ohùn rara ní ọ̀run, wí pè: “Nígbà yìí ni ìgbàlà dé, àti agbára, àti ìjọba Ọlọ́run wá, àti ọlá àti Kírísítì rẹ̀. Nítorí a tí le olùfisùn àwọn arakùnrin wa jáde, tí o ń fí wọ́n sùn níwájú Ọlọ́run wa lọ́sàn-án àti lóru."'), 'Rev$12:13': (None, 'obinrin', 'Rev$12:13 "Nígbà tí dírágónì náà rí pé a lé òun lọ sí ilẹ ayé, ó sè inúnibíni sì obìnrin tí ó bí ọmọkùnrin náà."'), 'Rev$13:16': ('ọlọrọ', 'talaka', 'Rev$13:16 "Ó sì mu gbogbo wọn, àti kékeré àti ńlá, ọlọ́rọ̀ àti talákà, olómìnira àti ẹrú, láti gba àmì kan sí ọwọ́ ọ̀tún wọn, tàbí ní iwájú orí wọn;"'), 'Rev$14:9': (None, 'alẹ', 'Rev$14:9 "Ańgẹ́lì mìíràn, ẹ̀kẹta, sì tẹ̀lé wọn, ó ń wí ni ohùn rara pé, “Bí ẹnikẹ́ni bá ń fóríbalẹ̀ fún ẹranko náà àti àwòrán rẹ̀, tí ó sì gba àmì sí iwájú orí rẹ̀ tàbí sí ọwọ́ rẹ̀."'), 'Rev$16:3': (None, 'kú', 'Rev$16:3 Èkejì sì tú ìgò sínú òkun; ó sì dàbí ẹ̀jẹ̀ òkú ènìyàn: gbogbo ọkàn alàyè sì kú nínú òkun.'), 'Rev$20:6': ('keji', None, 'Rev$20:6 "Olúkúlùkù àti mímọ́ ni ẹni tí ó ní ipa nínú àjíǹde èkíní náà: lórí àwọn wọ̀nyí ikú eekejì kò ní agbára, ṣùgbọ́n wọn ó jẹ́ àlùfáà Ọlọ́run àti ti Kírísítì, wọn ó sì máa jọba pẹ̀lú rẹ̀ ní ẹgbẹ̀rún ọdún."'), 'Rev$20:9': (None, None, 'Rev$20:9 " Wọ́n sì gòkè lọ la ibú ayé já, wọ́n sì yí ibùdó àwọn ènìyàn mímọ́ ká àti ìlú àyànfẹ́ náà: iná sì ti ọ̀run sọ̀kalẹ̀ wá, ó sì jó wọn run."'), 'Rev$20:10': (None, None, 'Rev$20:10 "A sì wọ́ Èsù tí ó tàn wọ́n jẹ lọ sínú adágún iná àti súfúrù, níbi tí ẹranko àti wòlíì èké nì gbé wà, a ó sì máa dá wọn lóró tọ̀sán tòru láé àti láéláé."'), 'Rev$21:10': (None, None, 'Rev$21:10 " Ó sì mu mi lọ nínú Ẹ̀mí si òkè ńlá kan tí o sì ga, ó sì fí ìlú náà hàn mi, Jerúsálémù mímọ́, tí ń ti ọ̀run sọ̀kalẹ̀ wá láti ọ̀dọ̀ Ọlọ́run,"'), 'Rev$21:25': (None, None, 'Rev$21:25 A kì yóò sì ṣé àwọn ẹnu-bodè rẹ̀ rárá ní ọ̀sán: nítorí ki yóò si òru níbẹ̀.'), 'Rev$22:13': ('opin', None, 'Rev$22:13 " Èmi ni Álífà àti Òmégà, ẹni ìṣáájú àti ẹni ìkẹyìn, ìpilẹ̀ṣẹ̀ àti òpin."')}
yoruba_syn = {'Gen$1:16': ('tobi', 'nla', 'Gen$1:16 "Ọlọ́run dá ìmọ́lẹ̀ ńlá-ńlá méjì, ìmọ́lẹ̀ tí ó tóbi láti ṣe àkóso ọ̀sán àti ìmọ́lẹ̀ tí ó kéré láti ṣe àkóso òru. Ó sì dá àwọn ìràwọ̀ pẹ̀lú."'), 'Gen$1:26': ('jẹ ki', 'ni', 'Gen$1:26 " Lẹ́yìn náà ni Ọlọ́run wí pé, “Ẹ jẹ́ kí a dá ènìyàn ní àwòrán ara wa, gẹ́gẹ́ bí àwa ti rí, kí wọn kí ó jọba lórí ẹja òkun, ẹyẹ ojú ọ̀run, ohun ọ̀sìn, gbogbo ilẹ̀ àti lórí ohun gbogbo tí ń rìn lórí ilẹ̀.”"'), 'Gen$1:29': (None, 'ni', 'Gen$1:29 "Nígbà náà ni Ọlọ́run wí pé, “Mo fi gbogbo ọgbìn tí ń so èso fún un yín bí oúnjẹ àti àwọn igi eléso pẹ̀lú. Gbogbo rẹ̀ yóò jẹ́ oúnjẹ fún un yín."'), 'Gen$1:30': (None, 'ni', 'Gen$1:30 Àti fún àwọn ẹranko inú igbó àti ẹyẹ ojú ọ̀run àti àwọn ohun afàyàfà: gbogbo ohun tó ní èémí ìyè nínú ni mo fi ewéko fún bí oúnjẹ.” Ó sì rí bẹ́ẹ̀.'), 'Gen$2:2': ('ṣe', 'iṣẹ', 'Gen$2:2 " Ní ọjọ́ keje Ọlọ́run parí iṣẹ́ rẹ̀ tí ó ń ṣe, ó sì sinmi ní ọjọ́ kéje."'), 'Gen$2:3': ('ṣe', 'iṣẹ', 'Gen$2:3 "Ọlọ́run sì súre fún ọjọ́ kéje, ó sì yà á sí mímọ́, nítorí pé ní ọjọ́ náà ni ó sinmi kúrò nínú iṣẹ́ dídá ayé tí ó ti ń ṣe."'), 'Gen$2:4': ('ayé', None, 'Gen$2:4 "Èyí ni ìtàn bí Ọlọ́run ṣe dá àwọn ọ̀run àti ayé nígbà tí ó dá wọn. Nígbà tí OLÚWA Ọlọ́run dá ayé àti àwọn ọ̀run,"'), 'Gen$2:6': (None, 'ilẹ', 'Gen$2:6 "Ṣùgbọ́n ìsun omi ń jáde láti ilẹ̀, ó sì ń bu omi rin gbogbo ilẹ̀."'), 'Gen$2:7': (None, None, 'Gen$2:7 " OLÚWA Ọlọ́run sì mọ ènìyàn láti ara erùpẹ̀ ilẹ̀, ó si mí èémí sí ihò imú rẹ̀, èémí ìyè, ènìyàn sì di ẹ̀dá alààyè."'), 'Gen$3:6': ('mu', None, 'Gen$3:6 "Nígbà tí obìnrin náà rí i wí pé èṣo igi náà dára fún oúnjẹ àti pé, ó sì dùn ún wò, àti pé ó ń mú ni ní ọgbọ́n, ó mú díẹ̀ níbẹ̀, ó sì jẹ ẹ́. Ó sì mú díẹ̀ fún ọkọ rẹ̀, ẹni tí ó wà pẹ̀lú rẹ̀, òun náà sì jẹ ẹ́."'), 'Gen$3:17': ('ya', 'mú', 'Gen$3:17 " Ọlọ́rún sì wí fún Ádámù pé, “Nítorí pé ìwọ fetí sí aya rẹ, ìwọ sì jẹ nínú èṣo igi tí mo pàṣẹ fún ọ pé ‘Ìwọ kò gbọdọ̀ jẹ nínú rẹ̀,’ “Ègún ni fún ilẹ̀ nítorí rẹ; nínú ọ̀pọ̀ làálàá ni ìwọ yóò jẹ nínú rẹ̀, ní gbogbo ọjọ́ ayé rẹ."'), 'Gen$3:22': ('mọ', None, 'Gen$3:22 " OLÚWA Ọlọ́run sì wí pé, “Ọkùnrin náà ti dàbí ọ̀kan lára wa, ó mọ rere àti búburú. A kò gbọdọ̀ jẹ́ kí ó na ọwọ́ rẹ̀ kí ó mú lára èṣo igi ìyè kí ó sì jẹ, kí ó sì wà láàyè títí láéláé.”"'), 'Gen$4:1': (None, 'ni', 'Gen$4:1 "Ádámù sì bá aya rẹ̀ Éfà lò pọ̀, ó sì lóyún, ó sì bí Káínì. Ó wí pé, “Pẹ̀lú ìrànlọ́wọ́ OLÚWA ni mo bí ọmọkùnrin.”"'), 'Gen$4:11': (None, 'ilẹ', 'Gen$4:11 "Láti ìsinsin yìí lọ, ìwọ ti wà lábẹ́ ègún, a sì ti lé ọ lórí ilẹ̀ tí ó ya ẹnu gba ẹ̀jẹ̀ arákùnrin rẹ lọ́wọ́ rẹ."'), 'Gen$5:3': ('ní', None, 'Gen$5:3 "Nígbà tí Ádámù di ẹni Àádóje ọdún (130), ó bí ọmọkùnrin kan tí ó jọ ọ́, tí ó jẹ́ àwòrán ara rẹ̀, ó sì pe orúkọ rẹ̀ ní Ṣétì."'), 'Gen$6:7': (None, 'ni', 'Gen$6:7 "Nítorí náà, OLÚWA wí pé “Èmi yóò pa ènìyàn tí mo ti dá run kúrò lórí ilẹ̀, ènìyàn àti ẹranko, àti ohun tí ń rákò, àti ẹyẹ ojú ọ̀run, nítorí inú mi bàjẹ́ pé mo ti dá wọn.”"'), 'Gen$6:12': ('baje', None, 'Gen$6:12 "Ọlọ́run sì rí bí ayé ti bàjẹ́ tó, nítorí àwọn ènìyàn ayé ti bá ara wọn jẹ́ ní gbogbo ọ̀nà wọn."'), 'Gen$6:19': (None, None, 'Gen$6:19 Ìwọ yóò mú gbogbo ohun alààyè takọ tabo wá sí inú ọkọ̀ kí wọn le wà láàyè pẹ̀lú rẹ.'), 'Gen$7:4': ('ni', None, 'Gen$7:4 "Nítorí ní ọjọ́ méje sí ìhín, èmi yóò rọ òjò sórí ilẹ̀ fún ogójì ọ̀sán àti ogójì òru, èmi yóò sì pa gbogbo ohun alààyè tí mo ti dá run, kúrò lórí ilẹ̀.”"'), 'Gen$7:13': (None, None, 'Gen$7:13 "Ní ọjọ́ tí ìkún omi yóò bẹ̀rẹ̀ gan-an ni Nóà àti Ṣémù, Ámù àti Jáfétì pẹ̀lú aya Nóà àti aya àwọn ọmọ rẹ̀ wọ inú ọkọ̀."'), 'Gen$7:21': ('ayé', 'ilẹ', 'Gen$7:21 "Gbogbo ohun alààyè tí ó wà lórí ilẹ̀ parun: ẹyẹ, ohun ọ̀sìn, ẹranko igbó, àwọn ẹ̀dá afàyàfà àti ènìyàn."'), 'Gen$7:23': ('ayé', 'ilẹ', 'Gen$7:23 "Gbogbo ohun alààyè tí ó wà lórí ilẹ̀ ayé ni a parẹ́: ènìyàn àti ẹranko, àwọn ohun tí ń rìn nílẹ̀, àwọn ẹyẹ ojú ọ̀run pátapáta ló ṣègbé. Nóà àti àwọn tí ó wà pẹ̀lú rẹ̀ nínú ọkọ̀ ni ó ṣẹ́kù."'), 'Gen$8:1': (None, None, 'Gen$8:1 "Ọlọ́run sì rántí Nóà àti ohun alààyè gbogbo tí ó wà pẹ̀lú rẹ̀, bí àwọn ẹranko igbó àti ohun ọ̀sìn, ó sì mú kí afẹ́fẹ́ fẹ́ sórí ilẹ̀, omi náà sì fà."'), 'Gen$8:6': (None, None, 'Gen$8:6 "Lẹ́yìn ogójì ọjọ́, Nóà sí fèrèsé tí ó ṣe sára ọkọ̀."'), 'Gen$8:8': ('ri', 'ilẹ', 'Gen$8:8 Ó sì rán àdàbà kan jáde láti wò ó bóyá omi ti gbẹ kúrò lórí ilẹ̀.'), 'Gen$8:13': (None, 'ilẹ', 'Gen$8:13 "Ní ọjọ́ kìn-ín-ní oṣù kìn-ín-ní, ní ọdún kọ́kànlélẹ́gbẹ̀ta (601) ni omi náà gbẹ kúrò lórí ilẹ̀; Nóà sì sí ọkọ̀, ó sì ri pé ilẹ̀ ti gbẹ."'), 'Gen$8:17': (None, None, 'Gen$8:17 "Kó gbogbo àwọn ẹ̀dá alààyè tí ó wà pẹ̀lú rẹ jáde: àwọn ẹyẹ, ẹranko àti gbogbo àwọn ẹ̀dá tí ń rìn nílẹ̀, kí wọn lè bí sí i, kí wọn sì pọ̀ sí i, kí wọn sì máa gbá yìn-ìn lórí ilẹ̀.”"'), 'Gen$9:3': (None, 'ni', 'Gen$9:3 "Gbogbo ohun tó wà láàyè tí ó sì ń rìn, ni yóò jẹ́ oúnjẹ fún-ún yín bí mo ṣe fi ewéko fún un yín, náà ni mo fi ohun gbogbo fún un yín."'), 'Gen$9:12': (None, None, 'Gen$9:12 "Ọlọ́run sì wí pé, “Èyí ni àmì májẹ̀mú tí mo ń dá yìí láàrin èmi àti ẹ̀yin àti ẹ̀dá alààyè tí ó wà pẹ̀lú yín, májẹ̀mú àtìrandíran tó ń bọ̀:"'), 'Gen$10:10': ('ilẹ', None, 'Gen$10:10 "Ìjọba rẹ̀ bẹ̀rẹ̀ ni Bábílónì, Érékì, Ákádì, Kálínéhì, gbogbo wọn wà ní ilẹ̀ Ṣínárì."'), 'Gen$11:1': (None, 'ede', 'Gen$11:1 "Lẹ́yìn náà, gbogbo àgbáyé sì ń sọ èdè kan ṣoṣo."'), 'Gen$11:4': (None, 'kọ', 'Gen$11:4 "Nígbà náà ni wọ́n wí pé, “Ẹ wá, ẹ jẹ́ kí a tẹ ìlú kan fún ara wa, kí a sì kọ́ ilé-ìṣọ́ kan tí yóò kan ọ̀run, kí á baà lè ní orúkọ (òkìkí) kí a má sì tú káàkiri sórí gbogbo ilẹ̀ ayé.”"'), 'Gen$11:7': (None, 'ede', 'Gen$11:7 "Ẹ̀ wá, ẹ jẹ́ kí a sọ̀kalẹ̀ lọ, kí a da èdè wọn rú kí èdè wọn má baà yé ara wọn mọ́.”"'), 'Gen$12:1': (None, None, 'Gen$12:1 " OLÚWA sì wí fún Ábúrámù, “Jáde kúrò ní ile rẹ àti lọ́dọ̀ àwọn ènìyàn rẹ àti kúrò ní ìdílé baba rẹ, lọ sí ilẹ̀ kan tí èmi ó fi hàn ọ́."'), 'Gen$12:5': (None, 'lọ', 'Gen$12:5 "Ábúrámù sì mú Ṣáráì aya rẹ̀, àti Lọ́tì ọmọ arákùnrin rẹ̀: gbogbo ohun ìní tí wọ́n ti kó jọ àti àwọn ènìyàn tí wọ́n ní ní Háránì. Wọ́n sì jáde lọ sí ilẹ̀ Kénánì. Wọ́n sì gúnlẹ̀ ṣíbẹ̀."'), 'Gen$12:11': ('wo', 'wo', 'Gen$12:11 "Bí ó ti ku díẹ̀ kí ó wọ Éjíbítì, ó wí fún Ṣáráì, aya rẹ̀ pé, “Mo mọ̀ pé arẹwà obìnrin ni ìwọ,"'), 'Gen$12:13': (None, None, 'Gen$12:13 "Nítorí náà, sọ pé, arábìnrin mi ni ìwọ, wọn yóò sì ṣe mí dáradára, a ó sì dá ẹ̀mí mi sí nítorí tìrẹ.”"'), 'Gen$13:3': (None, None, 'Gen$13:3 "Láti Gúúsù, ó ń lọ láti ibìkan sí ibòmíràn títí ó fi dé ilẹ̀ Bẹ́tẹ́lì, ní ibi tí àgọ́ rẹ̀ ti wà ní ìṣáájú ri lágbedeméjì Bẹ́tẹ́lì àti Áì."'), 'Gen$13:16': (None, None, 'Gen$13:16 Èmi yóò mú kí irú ọmọ rẹ kí ó pọ̀ bí erùpẹ̀ ilẹ̀. Àyàfi bí ẹnikẹ́ni bá le è ka erùpẹ̀ ilẹ̀ ni yóò tó lè ka irú ọmọ rẹ.'), 'Gen$14:22': (None, None, 'Gen$14:22 "Ṣùgbọ́n Ábúrámù dá ọba Ṣódómù lóhùn pé, “Mo ti búra fún OLÚWA, Ọlọ́run ọ̀gá ògo, tí ó dá ọ̀run àti ayé, mo sì ti gbọ́wọ́ sókè,"'), 'Gen$15:7': ('mu', 'ilẹ', 'Gen$15:7 "Ó sì tún wí fún un pé, “Èmi ni OLÚWA tí ó mú ọ jáde láti Úrì ti Kálídéà láti fún ọ ní ilẹ̀ yìí láti jogún.”"'), 'Gen$15:13': ('ilẹ', None, 'Gen$15:13 " Nígbà náà ni OLÚWA wí fún un pé, “Mọ èyí dájú pé, àwọn ìran rẹ yóò ṣe àtìpó ní orílẹ̀ èdè tí kì í ṣe ti wọn, a ó kó wọn lẹ́rú, a ó sì jẹ wọ́n ní ìyà fún irínwó ọdún (400)."'), 'Gen$15:17': (None, 'kọjá', 'Gen$15:17 "Nígbà tí oòrùn wọ̀ tí ilẹ̀ sì sú, ìkòkò iná tí ń sèéfín àti ọwọ́ iná sì ń kọjá láàrin ẹ̀là a ẹran náà."'), 'Gen$15:18': (None, 'ni', 'Gen$15:18 " Ní ọjọ́ náà gan-an ni OLÚWA dá májẹ̀mú pẹ̀lú Ábúrámù, ó sì wí pé, “Àwọn ìran rẹ ni èmi yóò fi ilẹ̀ yìí fún, láti odò Éjíbítì dé odò ńlá Yúfúrátè:"'), 'Gen$16:5': (None, 'ni', 'Gen$16:5 "Nígbà náà ni Ṣáráì wí fún Ábúrámù pé, “Ìwọ ni ó jẹ́ kí ẹ̀gbin yìí máa jẹmí, mo fa ọmọ ọ̀dọ̀ mi lé ọ lọ́wọ́, nígbà tí ó sì rí i pé òun lóyún, mo di ẹni ẹ̀gàn ní ojú rẹ̀. Kí OLÚWA kí ó ṣe ìdájọ́ láàrin èmi àti ìwọ.”"'), 'Gen$17:5': ('ṣe', 'orukọ', 'Gen$17:5 " A kì yóò pe orúkọ rẹ̀ ní Ábúrámù mọ́, bí kò ṣe Ábúráhámù, nítorí, mo ti sọ ọ́ di baba orílẹ̀ èdè púpọ̀."'), 'Gen$17:20': ('ni', None, 'Gen$17:20 "Ṣùgbọ́n ní ti Ísímáélì, mo gbọ́ ohun tí ìwọ wí, èmi yóò bùkún fún-un ní tòótọ́, èmi ó sì mú un bí sí i, yóò sì pọ̀ sí i, òun yóò sì jẹ́ baba fún àwọn ọmọ-ọba méjìlá, bẹ́ẹ̀ ni èmi yóò sì sọ ọ́ di orílẹ̀ èdè ńlá."'), 'Gen$18:1': ('ri', None, 'Gen$18:1 "OLÚWA sì farahan Ábúráhámù ní tòsí àwọn igi ńlá Mámúrè, bí ó ti jókòó ní ẹnu ọ̀nà àgọ́ rẹ̀, nígbà tí ọjọ́ kanrí tí oòrùn sì mú."'), 'Gen$18:6': ('ṣe', None, 'Gen$18:6 "Ábúráhámù sì yára tọ Ṣárà aya rẹ̀ lọ nínú àgọ́, ó wí pé, “Tètè mú òṣùwọn ìyẹ̀fun dáradára mẹ́ta kí o sì pò ó pọ̀, kí o sì ṣe oúnjẹ.”"'), 'Gen$18:8': (None, None, 'Gen$18:8 "Ó sì mú wàrà àti mílíìkì àti màlúù tí ó ti pèsè, ó sì gbé e ṣíwájú wọn. Ó sì dúró nítòòsí wọn lábẹ́ igi bí wọn ti ń jẹ ẹ́."'), 'Gen$18:20': ('ṣe', None, 'Gen$18:20 "OLÚWA sì wí pé, igbe Ṣódómù àti Gòmórà pọ̀ púpọ̀ àti pé ẹ̀ṣẹ̀ wọn burú jọjọ."'), 'Gen$18:27': ('ya', 'ni', 'Gen$18:27 "Ábúráhámù sì tún tẹ̀ṣíwájú pé, “Wò ó nísinsin yìí, níwọ̀n bí èmi ti ní ìgboyà láti bẹ̀rẹ̀ sí sọ̀rọ̀ níwájú sí OLÚWA, èmi ẹni tí í ṣe erùpẹ̀ àti eérú,"'), 'Gen$18:31': ('ya', 'ni', 'Gen$18:31 "Ábúráhámù wí pé, “Níwọ̀n bí mo ti ní ìgboyà láti bá OLÚWA sọ̀rọ̀ báyìí, jẹ́ kí n tẹ̀ṣíwájú. Bí o bá ṣe pé olódodo ogún péré ni ó wà nínú ìlú náà ńkọ́?” OLÚWA sì dáhùn pé, “Èmi kì yóò pa á run nítorí ogún ènìyàn náà.”"'), 'Gen$19:1': (None, 'pade', 'Gen$19:1 "Ní àsáálẹ́, àwọn ańgẹ́lì méjì sì wá sí ìlú Ṣódómù, Lọ́tì sì jókòó ní ẹnu ibodè ìlú. Bí ó sì ti rí wọn, ó sì dide láti pàdé wọn, ó kí wọn, ó sí foríbalẹ̀ fún wọn."'), 'Gen$19:8': ('jẹ ki', 'ni', 'Gen$19:8 "kíyèsí i, mo ní ọmọbìnrin méjì tí kò mọ ọkùnrin rí, ẹ jẹ́ kí n mú wọn tọ̀ yín wá kí ẹ sì ṣe ohun tí ẹ fẹ́ pẹ̀lú wọn. Ṣùgbọ́n ẹ má ṣe ṣe àwọn ọkùnrin wọ̀nyí ní ibi kan nítorí wọ́n wá wọ̀ lábẹ́ ààbò ní ilé mi.”"'), 'Gen$19:14': ('lọ', 'gba', 'Gen$19:14 "Nígbà náà ni Lọ́tì jáde, ó sì wí fún àwọn àna rẹ̀ ọkùnrin tí ó ti bá jẹ́jẹ̀ẹ́ láti fẹ́ àwọn ọmọ rẹ̀ pé, “Ẹ ṣe kánkán, kí ẹ jáde ní ìlú yìí nítorí OLÚWA fẹ́ pa ilu yìí run!” Ṣùgbọ́n àwọn ọkọ ọmọ rẹ̀ wọ̀nyí rò pé àwàdà ló ń ṣe."'), 'Gen$19:15': ('run', 'gba', 'Gen$19:15 "Ní àfẹ̀mọ́jú, àwọn ańgẹ́lì náà rọ Lọ́tì pé, “Ṣe wéré, mú aya rẹ àti àwọn ọmọbìnrin rẹ méjèèjì tí ó wà níhìnín, àìṣe bẹ́ẹ̀, ìwọ yóò parun pẹ̀lú ìlú yìí nígbà tí wọ́n bá ń jìyà ẹ̀ṣẹ̀ wọn.”"'), 'Gen$19:18': (None, None, 'Gen$19:18 "Ṣùgbọ́n Lọ́tì wí fún wọn pé, “Rárá, ẹ̀yin Olúwa mi, ẹ jọ̀wọ́!"'), 'Gen$19:21': ('gba', None, 'Gen$19:21 "Ó sì wí fún un pé, “Ó dára, mo gba ẹ̀bẹ̀ rẹ. Èmi kì yóò run ìlú náà tí ìwọ sọ̀rọ̀ rẹ̀."'), 'Gen$19:22': ('ṣe', None, 'Gen$19:22 "Tètè! Sá lọ ṣíbẹ̀, nítorí èmi kò le ṣe ohun kan àyàfi tí ó bá dé ibẹ̀,” (ìdí nìyí tí a fi ń pe ìlú náà ní Ṣóárì)."'), 'Gen$19:27': (None, None, 'Gen$19:27 "Ní kùtùkùtù òwúrọ̀ ọjọ́ kejì, Ábúráhámù sì dìde ó sì padà lọ sí ibi tí ó gbé dúró níwájú OLÚWA."'), 'Gen$19:31': (None, 'wá', 'Gen$19:31 "Ní ọjọ́ kan, èyí ẹ̀gbọ́n sọ fún àbúrò rẹ̀ pé, “Baba wa ti dàgbà, kò sì sí ọkùnrin kankan ní agbégbé yìí tí ì bá bá wa lò pọ̀, bí ìṣe gbogbo ayé."'), 'Gen$19:34': (None, None, 'Gen$19:34 "Ní ọjọ́ kejì èyí ẹ̀gbọ́n wí fún àbúrò rẹ̀ pé, “Ní àná mo sùn ti bàbá mi. Jẹ́ kí a tún wọlé tọ̀ ọ́, kí a lè ní irú ọmọ láti ọ̀dọ̀ baba wa.”"'), 'Gen$20:7': ('mọ', None, 'Gen$20:7 "Níṣinyìí, dá aya ọkùnrin náà padà, nítorí pé wòlíì ni, yóò sì gbàdúrà fún ọ, ìwọ yóò sì yè. Ṣùgbọ́n bí ìwọ kò bá dá a padà, mọ̀ dájú pé ìwọ àti gbogbo ẹni tíí ṣe tìrẹ yóò kú.”"'), 'Gen$20:16': (None, 'ni', 'Gen$20:16 "Ábímélékì sì wí fún Ṣárà pé, “Mo fi ẹgbẹ̀rún owó ẹyọ fàdákà fún arákùnrin rẹ. Éyí ni owó ìtanràn ẹ̀ṣẹ̀ mi sí ọ níwájú gbogbo ẹni tí ó wà pẹ̀lú rẹ àti pé, a dá ọ láre pátapáta.”"'), 'Gen$21:7': (None, 'ni', 'Gen$21:7 "Ó sì fi kún un pé, “Ta ni ó le sọ fún Ábúráhámù pé, Ṣárà yóò di ọlọ́mọ? Ṣíbẹ̀ṣíbẹ̀, mo sì tún bí ọmọ fún Ábúráhámù ní ìgbà ogbó rẹ.”"'), 'Gen$21:18': (None, None, 'Gen$21:18 "Dìde, gbé ọmọ náà sókè, kí o sì dì í mú (tùú nínú) nítorí èmi yóò ṣọ ọmọ náà di orilẹ̀ èdè ńlá.”"'), 'Gen$21:22': ('wá', 'ṣe', 'Gen$21:22 " Ní àkókò yìí ni ọba Ábímélékì àti Píkólì, olórí ogun rẹ̀ wí fún Ábúráhámù pé, “Ọlọ́run wà pẹ̀lú rẹ̀ nínú gbogbo ohun tí ó ń ṣe."'), 'Gen$21:30': ('ni', None, 'Gen$21:30 Ó da lóhùn pé “Gba àwọn abo ọ̀dọ́-àgùtàn méje wọ̀nyí lọ́wọ́ mi gẹ́gẹ́ bí ẹ̀rí pé èmi ni mo gbẹ́ kànga yìí.”'), 'Gen$22:9': ('so fun', None, 'Gen$22:9 " Nígbà tí wọn sì dé ibi tí Ọlọ́run sọ fún Ábúráhámù, ó mọ pẹpẹ kan, ó sì to igiọ lé e lórí, ó sì di Ísáákì ọmọ rẹ̀, ó sì da dùbúlẹ̀ lórí pẹpẹ náà."'), 'Gen$22:15': (None, None, 'Gen$22:15 Ańgẹ́lì OLÚWA sì tún pe Ábúráhámù láti ọ̀run lẹ́ẹ̀kejì.'), 'Gen$24:2': ('ṣe', None, 'Gen$24:2 "Ó wí fún olórí àwọn ìránṣẹ́ ilẹ̀ rẹ̀, tí ó jẹ́ alábojútó ohun gbogbo tí ó ní pé, “Fi ọwọ́ rẹ sí abẹ́ itan mi."'), 'Gen$24:5': (None, 'jẹ', 'Gen$24:5 "Ìránṣẹ́ náà bi í wí pé, “Ǹjẹ́ bí ọmọbìnrin náà bá kọ̀ láti bá mi wá sí ilẹ̀ yìí ń kọ́? Ǹjẹ́ mo lè mú ọmọ rẹ padà lọ sí orílẹ̀-èdè níbi tí ìwọ ti wá?”"'), 'Gen$24:8': ('tẹle', None, 'Gen$24:8 "Bí obìnrin náà kò bá fẹ́ tẹ̀lé ọ wá, nígbà náà ni a tú ọ sílẹ̀ kúrò nínú ìbúra yìí, ṣùgbọ́n pàtàkì ni pé ìwọ kò gbọdọ̀ mú ọmọ mi padà lọ sí ibẹ̀.”"'), 'Gen$24:11': (None, None, 'Gen$24:11 "Ó sì mú àwọn ràkunmí náà kúnlẹ̀ nítòòsí kànga lẹ́yìn ìlú, ó ti ń di ọwọ́ ìrọ̀lẹ̀ ní àkókò tí àwọn obìnrin máa ń lọ pọn omi."'), 'Gen$24:13': (None, None, 'Gen$24:13 "Kíyèsí i, mo dúró lẹ́gbẹ̀ẹ́ yìí, àwọn ọmọbìnrin ìlú yìí sì ń jáde wá pọn omi."'), 'Gen$24:18': ('fun', 'ọwọ', 'Gen$24:18 "Òun náà dáhùn pé, “Mu, olúwa mi,” ó sì yára sọ ìkòkò omi náà ka ọwọ́ rẹ̀, ó sì fún-un mu."'), 'Gen$24:19': (None, 'ni', 'Gen$24:19 "Lẹ́yìn tí ó ti fún un ni omi mu tán, ó wí pé, “Èmi ó bu omi fún àwọn ràkunmí rẹ pẹ̀lú títí wọn yóò fi mu àmutẹ́rùn.”"'), 'Gen$24:20': ('sáré', None, 'Gen$24:20 "Ó sì yára da omi inú ìkòkò omi rẹ̀ sínú ìbùmu, ó sáré padà lọ síbi omi láti pọn sí i wá fún àwọn ràkunmí, títí tí ó fi pọn fún gbogbo wọn."'), 'Gen$24:32': (None, None, 'Gen$24:32 "Ọkùnrin náà sì bá Lábánì lọ ilé, ó sì tú ẹrù orí ràkunmí rẹ̀ sílẹ̀, ó sì fún wọn ni koríko àti oúnjẹ. Ó sì bu omi fún ọkùnrin náà àti àwọn arákùnrin rẹ̀ láti wẹ ẹsẹ̀ wọn."'), 'Gen$24:37': (None, None, 'Gen$24:37 "OLÚWA mi sì ti mú mi búra wí pé, ‘Ìwọ kò gbọdọ̀ fẹ́ aya fún ọmọ mi, láàrin àwọn ọmọbìnrin Kénánì, ní ilẹ̀ ibi tí èmi ń gbé,"'), 'Gen$24:42': ('lọ', None, 'Gen$24:42 "“Nígbà tí mo dé ibi ìsun omi lónìí, mo wí pé, ‘OLÚWA Ọlọ́run Ábúráhámù olúwa mi, bí ìwọ bá fẹ́, jọ̀wọ́ jẹ́ kí n ṣe àṣeyọ́rí lórí ohun tí mo bá wá yìí,"'), 'Gen$24:46': (None, 'fun', 'Gen$24:46 "“Kíákíá ni ó sọ ìkòkò rẹ̀ kalẹ̀ láti èjìká rẹ̀, ó sì wí fún mi pé, ‘Mu ún, èmi yóò sì tún fún àwọn ràkúnmí rẹ mu pẹ̀lú.’ Mo sì mu, ó sì tún fún àwọn ràkúnmí mi mu pẹ̀lú."'), 'Gen$24:48': ('ní', None, 'Gen$24:48 "Mo sì wólẹ̀ mo sì sin OLÚWA, Ọlọ́run Ábúráhámù olúwa à mi, ẹni tí ó ṣe amọ̀nà mi láti rí ọmọ ọmọ arákùnrin olúwa mi, mú wá fún ọmọ rẹ̀ (Ísáákì)."'), 'Gen$24:49': (None, None, 'Gen$24:49 "Ǹjẹ́ nísin yìí bí ẹ̀yin yóò bá fi inú rere àti òtítọ́ bá olúwa mi lò, ẹ sọ fún mi, bí bẹ́ẹ̀ sì kọ́, ẹ sọ fún mi, kí ó le è mọ ọ̀nà tí èmi yóò yà sí.”"'), 'Gen$25:8': (None, None, 'Gen$25:8 "Ábúráhámù sì kú ní ọjọ́ ogbó rẹ̀. Ó dàgbà, ó darúgbó kí ó tó kú. A sì sin in sí ibojì àwọn ènìyàn rẹ̀."'), 'Gen$25:22': (None, None, 'Gen$25:22 "Àwọn ọmọ náà ń gbún ara wọn nínú rẹ̀, ó sì wí pé, “Èéṣe tí èyí ń ṣẹlẹ̀ sí mi,” ó sì lọ béèrè lọ́dọ̀ OLÚWA."'), 'Gen$25:26': ('mu', None, 'Gen$25:26 "Lẹ́yìn rẹ̀ ni èyí èkejì jáde, ó sì di arákùnrin rẹ̀ ni gìgíṣẹ̀ mú, nítorí náà ni wọn ṣe pe orúkọ rẹ ni Jákọ́bù. Ọmọ ọgọ́ta ọdún ni Ísáákì, nígbà tí Rèbékà bí ìbejì yìí fún un."'), 'Gen$25:27': (None, None, 'Gen$25:27 "Àwọn ọmọkùnrin náà sì dàgbà. Ísọ̀ sì di ọlọ́gbọ́n ọdẹ, ẹni tí ó fẹ́ràn àti máa dúró ní oko. Jákọ́bù sì jẹ́ ènìyàn jẹ́ẹ́jẹ́ẹ́ tí ó ń gbé láàrin ìlú."'), 'Gen$26:2': ('ṣe', 'fun', 'Gen$26:2 "OLÚWA sì fi ara han Ísáákì, ó sì wí pé, “Má ṣe sọ̀kalẹ̀ lọ sí Éjíbítì: Jókòó ní ilẹ̀ tí èmi sọ fún ọ."'), 'Gen$26:22': (None, 'orukọ', 'Gen$26:22 "Ó sì tún kúrò níbẹ̀, ó sì gbẹ́ kànga mìíràn, wọn kò sì já sí èyí rárá, ó sì pe orúkọ rẹ̀ ní Réhóbótì, ó wí pé, “Nísinsìnyìí, OLÚWA ti fi àyè gbà wá, a ó sí i gbilẹ̀ sì ni ilẹ̀ náà.”"'), 'Gen$26:28': ('ṣe', 'ni', 'Gen$26:28 "Wọ́n dáhùn pé, “A rí i dájú pé OLÚWA wà pẹ̀lú rẹ, nítorí náà ni a fi rò ó wí pé, ó yẹ kí májẹ̀mú kí o wà láàrin àwa àti ìwọ. Jẹ́ kí a ṣe àdéhùn"'), 'Gen$27:2': ('mọ', None, 'Gen$27:2 "Ísáákì sì wí pé, “Nísinsin yìí mo di arúgbó, èmi kò sì mọ ọjọ́ tí èmi yóò kú."'), 'Gen$27:9': ('mú', 'mú', 'Gen$27:9 "Lọ sínú agbo ẹran, kí o sì mú ọmọ ewúrẹ́ méjì, kí ó lè ṣe oúnjẹ́ àdídùn fún baba rẹ, irú èyí tí ó fẹ́ràn dáradára."'), 'Gen$27:15': (None, None, 'Gen$27:15 "Nígbà náà ni Rèbékà mú èyí tí ó dára jù nínú aṣọ Ísọ̀ ọmọ rẹ̀ àgbà tí ó wà nínú ilé Rèbékà, ó sì fi wọ Jákọ́bù ọmọ rẹ̀ kékeré."'), 'Gen$27:19': ('bi', 'ni', 'Gen$27:19 "Jákọ́bù sì fèsì pé, “Èmi ni Ísọ̀ àkọ́bí rẹ, mo ti ṣe gẹ́gẹ́ bí o ti wí, jọ̀wọ̀ dìde jókòó, kí o sì jẹ nínú ẹran-igbó mi tí mo ti sè, kí o ba à le súre fún mi pẹ̀lú gbogbo ọkàn rẹ.”"'), 'Gen$27:30': (None, None, 'Gen$27:30 Bí Ísáákì ti súre tan tí Jákọ́bù ṣẹ̀ṣẹ̀ jáde kúrò ní ọ̀dọ̀ baba rẹ̀ ni Ísọ̀ ti oko ọdẹ dé.'), 'Gen$27:37': (None, 'ni', 'Gen$27:37 "Ísáákì sì dá Ísọ̀ lóhùn pé, “Mo ti fi ṣe olórí rẹ àti àwọn ìbátan rẹ ni mo fi ṣe ìránṣẹ́ fún-un, àti oúnjẹ àti wáìnì ni mo ti fi lé e lọ́wọ́ lọ́pọ̀lọpọ̀, kí ni ó tún kù tí ǹ bá tún fún ọ báyìí ọmọ mi?”"'), 'Gen$27:39': (None, None, 'Gen$27:39 "Ísáákì bàbá rẹ̀ sì dá a lóhùn pé, “Ibùjòkòó rẹ yóò jìnnà sí ọ̀rá ilẹ̀, àti sí ìrì ọ̀run láti òkè wá."'), 'Gen$28:3': (None, 'fun', 'Gen$28:3 "Kí Ọlọ́run Olódùmarè kí ó bùkún fún ọ, kí ó sì mú ọ bí sí i, kí ó sì mú ọ pọ̀ sí i ní iye títí tí ìwọ yóò di àgbájọ àwọn ènìyàn."'), 'Gen$28:6': ('ní', 'gba', 'Gen$28:6 "Nígbà tí Ísọ̀ gbọ pé, Ísáákì ti ṣúre fún Jákọ́bù, ó sì ti ran Jákọ́bù lọ sí Padani-Árámù láti fẹ́ aya níbẹ̀ àti pé nígbà tí ó ṣúre fún un, ó kìlọ̀ fun un pé, kò gbọdọ̀ fẹ́ nínú àwọn ọmọbìnrin Kénánì"'), 'Gen$28:13': ('ilẹ', None, 'Gen$28:13 " OLÚWA sì dúró lókè rẹ̀, ó sì wí pé, “Èmi ni OLÚWA, Ọlọ́run Baba rẹ Ábúráhámù àti Ọlọ́run Ísáákì, ìwọ àti ìran rẹ ni èmi yóò fi ilẹ̀ tí ìwọ dùbúlẹ̀ sórí rẹ̀ yìí fún."'), 'Gen$28:15': ('mú', 'ilẹ', 'Gen$28:15 "Èmi wà pẹ̀lú rẹ, èmi yóò sì pa ọ mọ́ ní ibikíbi tí ìwọ bá lọ, èmi yóò sì mú ọ padà wá sí ilẹ̀ yìí ní àlàáfíà. Èmi kì yóò fi ọ sílẹ̀ ní ìgbà kan, títí tí èmi yóò fi mú gbogbo ìlérí mi ṣẹ.”"'), 'Gen$28:20': ('fun', 'ni', 'Gen$28:20 "Jákọ́bù sì jẹ́ ẹ̀jẹ́ níbẹ̀ pé, “Bí Ọlọ́run yóò bá wà pẹ̀lú mi, tí yóò sì pa mí mọ́ ní ìrìnàjò mi tí mo ń lọ, tí yóò sì fún mi ní oúnjẹ láti jẹ àti aṣọ láti wọ̀,"'), 'Gen$29:10': (None, None, 'Gen$29:10 "Nígbà tí Jákọ́bù rí Rákélì ọmọbìnrin Lábánì tí í ṣe ẹ̀gbọ́n ìyá rẹ̀, pẹ̀lú àgùntàn Lábánì, Jákọ́bù súnmọ́ kànga náà, ó sì yí òkúta kúrò lẹ́nu rẹ̀, ó sì fún àwọn ẹran arákùnrin ìyá rẹ̀ Lábánì ní omi."'), 'Gen$29:13': (None, None, 'Gen$29:13 "Ní keté tí Lábánì gbúròó Jákọ́bù ọmọ arábìnrin rẹ̀, ó jáde lọ pàdé Jákọ́bù, ó dì mọ́ ọn, ó fi ẹnu kò ó ní ẹnu, ó sì mú un lọ sí ilé. Nigbà náà ni Jákọ́bù ròyìn ohun gbogbo fún un."'), 'Gen$29:23': ('lọ', None, 'Gen$29:23 "Ṣùgbọ́n nígbà tí ó di òru, Lábánì mú Líà tọ Jákọ́bù lọ. Jákọ́bù sì bá a lòpọ̀."'), 'Gen$29:27': ('iṣẹ', 'sin', 'Gen$29:27 "Mú sùúrù parí ọ̀sẹ̀ ìgbéyàwó yìí ná, nígbà náà ni èmi yóò fi àbúrò rẹ̀ fún ọ pẹ̀lú, bí ìwọ ó bá ṣiṣẹ́ sìn mi fún ọdún méje mìíràn.”"'), 'Gen$29:34': ('bi', 'ni', 'Gen$29:34 "Ó sì tún lóyún, ó bí ọmọkùnrin kan, ó sì wí pé, “Nigbà yìí ni ọkọ mi yóò fara mọ́ mi, nítorí tí mo ti bí ọmọkùnrin mẹ́ta fún un,” nítorí náà ni ó ṣe pe orúkọ rẹ̀ ní Léfì."'), 'Gen$30:3': (None, 'ni', 'Gen$30:3 "Nígbà náà ni Rákélì wí pé, “Bílíhà ìránṣẹ́bìnrin mi nìyìí, bá a lòpọ̀, kí ó ba à le bí ọmọ fún mi, kí èmi si le è tipaṣẹ̀ rẹ̀ ní ọmọ.”"'), 'Gen$30:18': ('fi fun', 'ni', 'Gen$30:18 "Nígbà náà ni Líà wí pé, “Ọlọ́run ti ṣẹ̀san ọmọ ọ̀dọ̀ mi ti mo fi fún ọkọ mi fún mi,” ó sì pe orúkọ rẹ̀ ní Ísákárì."'), 'Gen$30:20': ('bi', 'ni', 'Gen$30:20 "Nígbà náà ni Líà tún wí pé “Ọlọ́run ti fún mi ní ẹ̀bùn iyebíye, nígbà yìí ni ọkọ mi yóò máa bu ọlá fún mi.” Nítorí náà ni ó ṣe pe orúkọ rẹ̀ ni Ṣébúlúnì."'), 'Gen$30:25': ('lọ', None, 'Gen$30:25 "Lẹ́yìn tí Rákélì ti bí Jóṣẹ́fù, Jákọ́bù wí fún Lábánì pé, “Jẹ́ kí èmi máa lọ sí ilẹ̀ mi tí mo ti wá."'), 'Gen$30:26': (None, 'ni', 'Gen$30:26 "Kó àwọn ọmọ àti ìyàwó mi fún mi, àwọn ẹni tí mo ti torí wọn sìn ọ́. Ki èmi lè máa bá ọ̀na mi lọ. O sá à mọ bí mo ti ṣiṣẹ́ sìn ọ́ tó”"'), 'Gen$30:27': ('ni', None, 'Gen$30:27 "Ṣùgbọ́n Lábánì wí fún un pé, “Bí o bá ṣe pé mo rí ojúrere rẹ, jọ̀wọ́ dúró, nítorí, mo ti ṣe àyẹ̀wò rẹ, mo sì rí i pé OLÚWA bùkún mi nítorí rẹ."'), 'Gen$30:35': ('fun', None, 'Gen$30:35 "Ní ọjọ́ náà gan-an ni Lábánì kó gbogbo ewúrẹ̀ tí ó lámì tàbí ilà (àti akọ àti abo, tí ó ní funfun díẹ̀ lára), pẹ̀lú gbogbo àgbò dúdú, ó sì fi wọ́n sí ìtọ́jú àwọn ọmọ rẹ̀."'), 'Gen$30:40': (None, None, 'Gen$30:40 "Nígbà náà ni ó ya àwọn abo ẹran kúrò nínú agbo ẹran Lábánì, ó sì yà wọ́n sọ́tọ̀ kúrò lára àgbò, ó sì mú kí wọn máa gùn pẹ̀lú àwọn àgbò Jákọ́bù dúdú nìkan, bẹ́ẹ̀ ni ó kó agbo-ẹran jọ fún ara rẹ̀ láti ara agbo ẹran Lábánì."'), 'Gen$31:1': (None, 'baba', 'Gen$31:1 "Jákọ́bù sì gbọ́ pé àwọn ọmọ Lábánì ń wí pé, “Jákọ́bù ti gba gbogbo ohun ìní baba wa, ó sì ti kó ọrọ̀ jọ fún ara rẹ̀ lára àwọn ohun tí í ṣe ti baba wa.”"'), 'Gen$31:7': (None, None, 'Gen$31:7 Ṣíbẹ̀ṣíbẹ̀ baba yín ti rẹ́ mi jẹ ní ẹ̀ẹ̀mẹ́wàá ọ̀tọ̀ọ̀tọ̀ ni ó sì ti yí owó iṣẹ́ mi padà. Ṣùgbọ́n Ọlọ́run kò jẹ́ kí ó le è pa mi lára.'), 'Gen$31:17': (None, None, 'Gen$31:17 Nígbà náà ni Jákọ́bù gbé àwọn ọmọ àti aya rẹ̀ gun ràkunmí'), 'Gen$31:21': (None, 'oke', 'Gen$31:21 "Ó sì sá lọ pẹ̀lú ohun gbogbo tí ó ni, ó sì la odò kọjá (Yúfúrátè), ó sì kọrí sí àwọn ilẹ̀ olókè ti Gílíádì."'), 'Gen$31:32': ('ni', None, 'Gen$31:32 "“Ṣùgbọ́n bí o bá ri ẹnikẹ́ni pẹ̀lú ère rẹ, kí ẹni náà di òkú.” Ó tún wí pé, “Níwájú gbogbo ìbátan wa báyìí, wò ó fúnra rẹ, bí o bá rí ohunkóhun tí í ṣe tìrẹ, mú un.” Jákọ́bù kò sì mọ̀ pé, Rákélì ni ó jí àwọn òrìṣà náà."'), 'Gen$31:43': ('bi', 'ni', 'Gen$31:43 "Lábánì sì dá Jákọ́bù lóhùn, “Tèmi ni àwọn obìnrin wọ̀nyí, ọmọ mi ni àwọn ọmọ wọ̀nyí pẹ̀lú, àwọn agbo ẹran yìí, tèmi ni wọ́n pẹ̀lú. Gbogbo ohun tí o rí wọ̀nyí, tèmi ni. Kín ni mo wá le ṣe sí àwọn ọmọbìnrin wọ̀nyí àti àwọn ọmọ wọn tí wọn bí?"'), 'Gen$31:50': ('wo', 'ẹlẹri', 'Gen$31:50 "Bí o bá fìyà jẹ àwọn ọmọbìnrin mi, tàbí tí o fẹ́ aya mìíràn yàtọ̀ sí wọn, rántí pé, Ọlọ́run ń bẹ láàrin wa bí ẹlẹ́rìí bí ẹnikẹ́ni kò tilẹ̀ sí.”"'), 'Gen$32:3': ('ilẹ', 'orilẹ-ede', 'Gen$32:3 Jákọ́bù sì rán àwọn oníṣẹ́ ṣáájú ara rẹ̀ sí Ísọ̀ arákùnrin rẹ̀ ni ilẹ̀ Ṣéírì ní orílẹ̀-èdè Édómù.'), 'Gen$32:4': (None, None, 'Gen$32:4 "Ó pàṣẹ fún wọn pé, “Èyí ni ẹ̀yin yóò wí fún Ísọ̀ olúwa mi, Jákọ́bù ìránṣẹ́ rẹ̀ wí pé, ‘Mo ti ṣe àtìpó lọ́dọ̀ Lábánì títí ó fi di àṣìkò yìí"'), 'Gen$33:10': ('ni', 'gba', 'Gen$33:10 "Jákọ́bù bẹ̀ ẹ́ wí pé, “Rárá bẹ́ẹ̀ kọ́, bí ó bá ṣe pé, mo rí ojú rere rẹ, jọ̀wọ́ gba wọ́n lọ́wọ́ mi. Bí mo ṣe rí ojú rẹ̀ yìí, ó dàbí wí pé mo rí ojú Ọlọ́run ni báyìí, tí inú un rẹ̀ ti dùn sí mi."'), 'Gen$33:14': (None, None, 'Gen$33:14 "Èmi bẹ̀ ọ́, máa lọ ṣíwájú ìránṣẹ́ rẹ, àwọn ó sì máa rọra bọ̀, títí èmi àti àwọn ọmọ yóò fi dé ọdọ̀ olúwa mi ní Ṣéírì.”"'), 'Gen$33:17': ('ṣe', None, 'Gen$33:17 "Jákọ́bù sì lọ sí Ṣúkótù, ó sì kọ́ ilé fún ara rẹ̀, ó sì ṣe ọgbà fún àwọn ẹran. Ìdí èyí ní a fi ń pe ibẹ̀ ní Ṣúkótù."'), 'Gen$34:4': (None, 'baba', 'Gen$34:4 "Ṣékémù sì wí fún Hámórì bàbá rẹ̀ pé, “Fẹ́ ọmọbìnrin yìí fún mi bí aya.”"'), 'Gen$34:9': (None, 'ṣe', 'Gen$34:9 "Ẹ jẹ́ kí a máa ṣe ìgbéyàwó láàrin ara wa, kí àwọn ọmọ yín kó máa fẹ́ àwọn ọmọ wa."'), 'Gen$34:12': (None, 'ebun', 'Gen$34:12 "Iyekíye tí owó orí rẹ̀ bá jẹ́ àti ẹ̀bùn gbogbo tí ẹ bá fẹ́, bí ó ti wù kí ó pọ̀ tó, èmi yóò san-án, kí ẹ sáà jẹ́ kí ń fi ọmọ náà ṣe aya.”"'), 'Gen$34:13': ('ní', None, 'Gen$34:13 Àwọn arákùnrin Dínà sì fi ẹ̀tàn dá Ṣékémù àti Ámórì bàbá rẹ̀ lóhùn nítorí tí ó ti ba ògo Dínà arábìnrin wọn jẹ́.'), 'Gen$34:30': ('ni', None, 'Gen$34:30 "Nígbà náà ni Jákọ́bù wí fún Símónì àti Léfì wí pé, “Ẹ̀yin ti kó ìyọnu bá mi nípa sísọ mí di olóòórùn láàrin ará Kénánì àti Pérésì, tí ó ń gbé ilẹ̀ yìí. Àwa kò pọ̀, bí wọn bá wá parapọ̀ sígun sí wa, gbogbo wa pátapáta ni wọn yóò parun.”"'), 'Gen$35:4': ('fun', 'ọwọ', 'Gen$35:4 "Bẹ́ẹ̀ ni wọ́n fún Jákọ́bù ní gbogbo àjòjì òrìsà tí ó wà lọ́wọ́ wọn, àti yẹtí etí wọn, Jákọ́bù sì bo gbogbo wọn mọ́lẹ̀ sabẹ́ igi Óákù ní Ṣékémù."'), 'Gen$35:11': (None, 'wá', 'Gen$35:11 " Ọlọ́run sì wí fún-un pé, “Èmi ni Ọlọ́run alágbára; máa bí sí i, kí o sì máa pọ̀ sí i. Orílẹ̀-èdè àti ọ̀pọ̀lọpọ̀ orílẹ̀-èdè ni yóò ti ọ̀dọ̀ rẹ̀ wá, àwọn ọba yóò sì jáde wá láti ara rẹ̀."'), 'Gen$35:22': ('lọ', None, 'Gen$35:22 " Nígbà tí Ísírẹ́lì sì ń gbé ní ibẹ̀, Rúbẹ́nì wọlé tọ Bílíhà, àlè (ìyàwó tí a kò fi owó fẹ́ lọ́wọ́ àwọn òbí rẹ̀) baba rẹ̀ lọ, ó sì bá a lò pọ̀, Ísírẹ́lì sì gbọ́ ọ̀rọ̀ náà. Jákọ́bù sì bí ọmọkunrin méjìlá:"'), 'Gen$36:6': ('ilẹ', None, 'Gen$36:6 "Ísọ̀ sì mú àwọn aya rẹ̀, àwọn ọmọkùnrin rẹ̀, àwọn ọmọbìnrin rẹ̀ àti gbogbo àwọn ará ilé rẹ̀, àwọn ohun ọ̀sìn rẹ̀ àti àwọn ẹran ọ̀sìn mìíràn àti gbogbo ohun ìní mìíràn tí ó ní, ni Kénánì, ó sì kó lọ sí ilẹ̀ mìíràn, jìnà sí ibi tí Jákọ́bù arakùnrin rẹ̀ wà."'), 'Gen$37:6': (None, None, 'Gen$37:6 O wí fún wọn pé “Ẹ fetí sí àlá tí mo lá:'), 'Gen$37:9': (None, None, 'Gen$37:9 "O sì tún lá àlá mìíràn, ó sì tún sọ ọ́ fún àwọn arákùnrin rẹ̀. Ó wí pé, ẹ tẹ́tí sí mi, “Mo tún lá àlá mìíràn, wò ó, oòrùn, òṣùpá àti ìràwọ̀ mọ́kànlá ń forí balẹ̀ fún mi.”"'), 'Gen$37:25': (None, None, 'Gen$37:25 "Bí wọ́n sì ti jókòó láti jẹun, wọ́n gbójú sókè, wọ́n sì rí àwọn oníṣòwò ará Íṣímáélì tí wọ́n ń wọ́ bọ̀ láti Gílíádì. Ràkunmí wọn sì ru tùràrí, ìkunra àti òjíá, wọ́n ń lọ sí Éjíbítì."'), 'Gen$37:31': ('mu', 'ni', 'Gen$37:31 "Nígbà náà ni wọ́n mú aṣọ Jósẹ́fù, wọ́n pa ewúrẹ́ kan, wọ́n sì tẹ aṣọ náà bọ inú ẹ̀jẹ̀ ewúrẹ́ náà."'), 'Gen$38:1': ('lọ', None, 'Gen$38:1 "Ní àkókò náà, Júdà lọ kúrò lọ́dọ̀ àwọn arákùnrin rẹ̀ láti lọ dúró lọ́dọ̀ ọkùnrin ará Ádúlámù kan tí ń jẹ́ Hírà."'), 'Gen$38:7': (None, 'pa', 'Gen$38:7 "Ṣùgbọ́n Érì àkọ́bí Júdà ṣe ènìyàn búburú níwájú OLÚWA, Ọlọ́run sì pa á."'), 'Gen$38:9': ('fun', None, 'Gen$38:9 "Ṣùgbọ́n Ónání mọ̀ pé àwọn ọmọ náà kì yóò jẹ́ ti òun, nítorí náà ni gbogbo ìgbà tí ó bá wọlé tọ aya arákùnrin rẹ̀ lọ, ilẹ̀ ni ó ń da nǹkan ọkùnrin rẹ̀ sí, kí ó má bá a bímọ fún arákùnrin rẹ̀."'), 'Gen$38:14': (None, None, 'Gen$38:14 "Ó bọ́ aṣọ opó rẹ̀, ó sì fi ìbòjú bo ojú ara rẹ̀ kí wọn má ba à mọ̀ ọ́. Ó sì jókòó sí ẹnu ibodè Énáímù, èyí tí ó wà ní ọ̀nà Tímínà. Nítorí ó rí i wí pé, bí ó tilẹ̀ jẹ́ pé Ṣélà ti dàgbà, ṣíbẹ̀, a kò fi òun fún un gẹ́gẹ́ bí aya."'), 'Gen$38:20': ('ri', 'gba', 'Gen$38:20 "Nígbà náà ni Júdà fi ọmọ ewúrẹ́ náà rán ọ̀rẹ́ rẹ̀ sí obìnrin náà, kí ó ba à lè rí àwọn nǹkan tí ó fi ṣe ìdúró gbà padà. Ṣùgbọ́n wọn kò bá obìnrin náà níbẹ̀."'), 'Gen$38:29': ('fa', None, 'Gen$38:29 "Ṣùgbọ́n nìgbá tí ó fa ọwọ́ rẹ̀ padà, èkejì rẹ̀ jáde. Támárì sì wí pé, “Níbo ni ìwọ ti wá?” Ó sì pe orúkọ rẹ̀ ní Pérésì."'), 'Gen$39:8': (None, None, 'Gen$39:8 "Ṣùgbọ́n Jósẹ́fù kọ̀, ó sì wí fún aya ọ̀gá rẹ̀ pé, “Kíyèsi, OLÚWA mi kò fi ohunkóhun dù mi nínú ilé yìí, gbogbo ohun ìní rẹ̀ ni ó fi lé mi lọ́wọ́."'), 'Gen$39:11': (None, 'ṣe', 'Gen$39:11 "Ní ọjọ́ kan, Jósẹ́fù lọ sínú ilé láti ṣe iṣẹ́, kò sì sí èyíkéyìí nínú àwọn ìránṣẹ́ ní tòsí."'), 'Gen$40:9': ('fun', None, 'Gen$40:9 "Olórí agbọ́tí sì ṣọ́ àlá rẹ̀ fún Jósẹ́fù, wí pé, “Ní ojú àlá mi, mo rí àjàrà kan (tí wọn ń fi èso rẹ̀ ṣe wáìnì) níwájú mi,"'), 'Gen$40:14': (None, 'ni', 'Gen$40:14 "Ṣùgbọ́n nígbà tí ohun gbogbo bá dára fún ọ, rántí mi kí o sì fi àánú hàn sí mi. Dárúkọ mi fún Fáráò, kí o sì mú mi jáde kúrò ní ìhín."'), 'Gen$40:16': ('ori', 'olori', 'Gen$40:16 "Nígbà tí olórí alásè rí i wí pé ìtúmọ̀ tí Jósẹ́fù fún àlá náà dára, ó wí fún Jósẹ́fù pé, “Èmi pẹ̀lú lá àlá: Mo ru agbọ̀n oúnjẹ mẹ́ta lórí,"'), 'Gen$40:20': ('ori', None, 'Gen$40:20 "Ọjọ́ kẹ́ta sì jẹ́ ọjọ́ ìbí Fáráò, ó sì ṣe àsè fún gbogbo àwọn ìjòyè e rẹ̀. Ó sì mú olórí agbọ́tí àti olórí aláṣè jáde kúrò nínú ẹ̀wọ̀n."'), 'Gen$40:21': (None, None, 'Gen$40:21 "Ó dá olórí agbọ́tí padà sí ipò tí ó wà tẹ́lẹ̀, kí ó ba à le máa ṣe iṣẹ́ rẹ̀ bí i ti àtẹ̀yìnwa,"'), 'Gen$41:15': (None, None, 'Gen$41:15 "Fáráò wí fún Jósẹ́fù, “Mo lá àlá kan, kò sì sí ẹni tí o le è túmọ̀ rẹ̀. Ṣùgbọ́n mo ti gbọ́ nípa rẹ pé bí o bá ti gbọ́ àlá, o le è túmọ̀ rẹ̀”"'), 'Gen$41:17': (None, None, 'Gen$41:17 "Nígbà náà ni Fáráò wí fún Jóṣẹ́fu pé, “Ní inú àlá mi, mo dúró ni etí bèbè odò Náílì,"'), 'Gen$41:22': (None, 'dara', 'Gen$41:22 "“Ní ojú àlá mi, mo tún rí ṣiiri ọkà méje tí ó yó ọmọ tí ó sì dára, wọ́n jáde láti ara igi ọkà kan."'), 'Gen$41:29': (None, 'jẹ', 'Gen$41:29 Ọdún méje tí oúnjẹ yóò pọ yanturu ń bọ̀ wà ní Éjíbítì.'), 'Gen$41:51': ('ṣe', 'orukọ', 'Gen$41:51 "Jósẹ́fù sọ orúkọ àkọ́bí rẹ̀ ni Mánásè, ó sì wí pé, “Nítorí tí Ọlọ́run ti mú mi gbàgbé gbogbo ìdààmú mi àti gbogbo ilé baba mi.”"'), 'Gen$41:56': (None, 'ilẹ', 'Gen$41:56 "Nígbà tí ìyàn sì ti tàn ká gbogbo ilẹ̀ náà, Jósẹ́fù sí inú àká, ó sì bẹ̀rẹ̀ sí ní ta ọkà fún àwọn ènìyàn, nítorí ìyàn náà mú gan-an ní gbogbo ilẹ̀ Éjíbítì."'), 'Gen$42:1': ('ri', 'wo', 'Gen$42:1 "Nígbà tí Jákọ́bù mọ̀ pé ọkà wà ní Éjíbítì, ó wí fún àwọn ọmọ rẹ̀ ọkùnrin pé, “Èéṣe tí ẹ ń wo ara yín lásán?”"'), 'Gen$42:2': ('ni', None, 'Gen$42:2 " “Mo tí gbọ́ pé ọkà wà ní ilẹ̀ Éjíbítì. Ẹ sọ̀kalẹ̀ lọ sí ibẹ̀ kí ẹ sì rà wá fún wa, kí a má ba à kú.”"'), 'Gen$42:6': (None, 'ilẹ', 'Gen$42:6 "Nisinsin yìí, Jósẹ́fù ni alábojútó fún gbogbo ilẹ̀ Éjíbítì, òun sì ni ó ń bojú tó ọkà títà fún gbogbo ènìyàn ìlú náà. Nítorí náà nígbà tí àwọn arákùnrin Jóṣẹ́fù dé, wọ́n tẹríba, wọ́n sì foríbalẹ̀ fún Jósẹ́fù."'), 'Gen$42:11': (None, 'eniyan', 'Gen$42:11 "Ọmọ baba ni wá, olóòótọ́ ènìyàn sì ni wá pẹ̀lú, àwa kì í ṣe ayọ́lẹ̀wò.”"'), 'Gen$42:18': (None, None, 'Gen$42:18 "Ní ọjọ́ kẹ́ta, Jósẹ́fù wí fún wọn pé, “Èyí ni ẹ̀yin yóò ṣe kí ẹ̀yin ba à le yè nítorí mo bẹ̀rù Ọlọ́run:"'), 'Gen$42:21': ('ri', 'gbo', 'Gen$42:21 "Wọ́n ń sọ ọ́ láàrin ara wọn wí pé, “Dájúdájú a ń jìyà nítorí ohun tí a ṣe sí Jósẹ́fù. A rí ìbànújẹ́ ọkàn rẹ̀ nígbà tí ó ń bẹ̀ wá nítorí ẹ̀mí rẹ̀, ṣùgbọ́n a kò gbẹ́bẹ̀, nítorí náà ni ìdààmú yìí fi dé bá wa.”"'), 'Gen$42:27': ('ṣí', 'fun', 'Gen$42:27 "Níbi tí wọ́n ti dúró láti sùn lóru ọjọ́ náà, ọ̀kan nínú wọn tú àpò rẹ̀ láti mú oúnjẹ fún kẹ́tẹ́kẹ́tẹ́ rẹ̀, ó sì rí owó rẹ̀ ní ẹnu àpò rẹ̀."'), 'Gen$42:29': (None, None, 'Gen$42:29 "Nígbà tí wọ́n padà dé ọ̀dọ̀ Jákọ́bù baba wọn, wọ́n ròyìn gbogbo ohun tí ó sẹlẹ̀ fún-un wí pé,"'), 'Gen$42:33': ('ilẹ', None, 'Gen$42:33 "“Nígbà náà ni ọkùnrin tí ó jẹ́ alábojútó ilẹ̀ náà wí fún wa pé, ‘Báyìí ni n ó ṣe mọ̀ bóyá olóòótọ́ ènìyàn ni yín; Ẹ fi ọ̀kan nínú àwọn arákùnrin yín sílẹ̀ níbí, pẹ̀lú mi, ẹ sì mú oúnjẹ lọ fún àwọn ará ilé yín tí ebi ń pa kú lọ, lọ́wọ́ ìyàn."'), 'Gen$42:36': ('ni', None, 'Gen$42:36 "Jákọ́bù bàbá wọn wí fún wọn pé, “Ẹ ti mú mi pàdánù àwọn ọmọ mi. N kò rí Jósẹ́fù mọ́, bẹ́ẹ̀ ni n ò kò sì rí Símónì náà mọ. Ẹ sì tún fẹ́ mú Bẹ́ńjámínì lọ! Èmi ni gbogbo ohun búburú yìí wá ń ṣẹlẹ̀ sí.”"'), 'Gen$43:8': ('lọ', None, 'Gen$43:8 "Júdà sì wí fún Ísírẹ́lì bàbá rẹ̀, “Jẹ́ kí ọmọkùnrin náà lọ pẹ̀lú mi, a ó sì lọ ní kíákíá, kí àwa àti ìwọ àti àwọn ọmọ wa le yè, kí a má sì kú."'), 'Gen$43:9': ('ní', 'jẹ ki', 'Gen$43:9 "Èmi fúnra mi yóò ṣe onídùúró fún un, èmi ni kí o gbà pé o fi lé lọ́wọ́. Bí n kò bá sì mu un padà tọ̀ ọ́ wá, jẹ́ kí ẹ̀bi rẹ̀ kí ó jẹ́ tèmi ní gbogbo ọjọ́ ayé mi níwájú rẹ."'), 'Gen$43:11': ('fun', 'ọwọ', 'Gen$43:11 "Nígbà náà ni Ísírẹ́lì baba wọn wí fún wọn, “Bí ó bá rí bẹ́ẹ̀, èyí ni kí ẹ ṣe: Ẹ mú àwọn ohun dáradára ilẹ̀ yìí lọ́wọ́ lọ bí ẹ̀bùn fún ọkùnrin náà-ìpara díẹ̀, oyin díẹ̀, tùràrí àti òjíá, èso Písítakíò àti eso álímíndì"'), 'Gen$43:16': ('ile', 'ile', 'Gen$43:16 "Nígbà tí Jóṣẹ́fù rí Bẹ́ńjámínì pẹ̀lú wọn, ó sọ fún ìránṣẹ́ ilé rẹ̀, “Mú àwọn ọkùnrin wọ̀nyí lọ sí ilé mi, pa ẹran kí o sì se àsè; wọn ó jẹun ọ̀sán pẹ̀lú mi.”"'), 'Gen$43:17': ('awọn ọkunrin', None, 'Gen$43:17 "Ọkùnrin náà sì ṣe bí Jósẹ́fù ti wí fún un, ó sì mú àwọn ọkùnrin náà lọ sí ilé Jósẹ́fù."'), 'Gen$43:18': (None, None, 'Gen$43:18 "Ẹ̀rù sì ba àwọn ọkùnrin náà nígbà tí wọ́n mú wọn lọ sí ilé Jósẹ́fù. Wọ́n rò ó pé, “A mú wa wá sí ìhín nítorí owó tí a fi sí inú àpò wa ní ìgbà àkọ́kọ́. Ó fẹ́ bá wa jà, kí ó mú wa lẹ́rú kí ó sì gba àwọn kẹ́tẹ́kẹ́tẹ́ wa.”"'), 'Gen$43:24': ('awọn ọkunrin', None, 'Gen$43:24 "Ìránṣẹ́ náà mú àwọn ọkùnrin náà lọ sí ilé Jósẹ́fù, ó fún wọn ní omi láti fi wẹ ẹsẹ̀ wọn nu, ó sì fún àwọn kẹ́tẹ́kẹ́tẹ́ wọn lóúnjẹ pẹ̀lú."'), 'Gen$43:25': ('ṣe', None, 'Gen$43:25 "Wọ́n pèṣè ẹ̀bùn wọn sílẹ̀ fún Jósẹ́fù di ọ̀sán nígbà tí yóò dé, nítorí wọ́n ti gbọ́ pé ibẹ̀ ni àwọn yóò ti jẹun ọ̀sán."'), 'Gen$43:26': ('ile', 'ile', 'Gen$43:26 "Nígbà tí Jósẹ́fù dé sí ilé, wọ́n kó àwọn ẹ̀bùn tí wọ́n ti mú wá fún-un, wọ́n sì wólẹ̀ níwájú rẹ̀."'), 'Gen$43:32': (None, None, 'Gen$43:32 "Wọ́n gbé oúnjẹ tirẹ̀ fun-un lọ́tọ̀, àti fún àwọn ará Éjíbítì tí ó wá ba jẹun náà lọ́tọ̀, nítorí ará Éjíbítì kò le bá ará Ébérù jẹun nítorí ìríra pátapáta ló jẹ́ fún àwọn Éjíbítì."'), 'Gen$44:1': ('awọn ọkunrin', None, 'Gen$44:1 "Nígbà náà ni Jósẹ́fù pàṣẹ fún ìránṣẹ́ rẹ̀ pé, “Di oúnjẹ kún inú àpò àwọn ọkùnrin náà tó ìwọ̀n èyí tí wọ́n le rù, kí o sì mú owó olúkúlukù àwọn ọkùnrin náà padà sí ẹnu àpò rẹ̀."'), 'Gen$44:4': (None, None, 'Gen$44:4 "Wọn kò tí ì rìn jìnnà sí ìlú náà tí Jósẹ́fù fi wí fún ìránṣẹ́ rẹ̀ pé, “Lépa àwọn ọkùnrin náà, nígbà tí o bá sì bá wọn, kí o wí pé, ‘Èéṣe ti ẹ fi búburú san rere.?"'), 'Gen$44:17': ('ri', None, 'Gen$44:17 "Ṣùgbọ́n Jósẹ́fù dáhùn pé, “Ká má rí i pé mo ṣe irú nǹkan bẹ́ẹ̀! Ẹni tí a bá kọ́ọ̀bù mi lọ́wọ́ rẹ̀ nìkan ni yóò di ẹrú mi, ẹ̀yin tí ó kù, ẹ máa lọ sọ́dọ̀ baba yín ní àlàáfíà.”"'), 'Gen$44:27': (None, None, 'Gen$44:27 "“Baba mi, ìránṣẹ́ rẹ wí fún wa pé, ‘Ẹ mọ̀ pé ìyàwó mi bí ọmọkùnrin méjì fún mi."'), 'Gen$45:3': (None, None, 'Gen$45:3 "Jósẹ́fù wí fún àwọn arákùnrin rẹ̀ pé, “Èmi ni Jósẹ́fù! Ṣe baba mi sì wà láàyè?” Ṣùgbọ́n àwọn arákùnrin rẹ̀ kò le è dá a lóhùn nítorí ẹ̀rù bà wọ́n gidigidi, ẹnu sì yà wọ́n níwájú rẹ̀."'), 'Gen$45:7': (None, None, 'Gen$45:7 Ṣùgbọ́n Ọlọ́run rán mi ṣáájú yín sí ìhín láti da irú ọmọ yín sí fún-un yín lórí ilẹ̀ ayé àti láti fi ìgbàlà ńlá gba ẹ̀mí yín là.'), 'Gen$45:17': (None, 'ṣe', 'Gen$45:17 "Fáráò wí fún Jósẹ́fù pé, “Wí fún àwọn arákùnrin rẹ pé, ‘Èyí ni kí ẹ ṣe: Ẹ di ẹrù lé ẹranko yín kí ẹ sì padà sí ilẹ̀ Kénánì,"'), 'Gen$45:19': (None, 'mú', 'Gen$45:19 "“A pàṣẹ fún ọ láti sọ fún wọn pé, ‘Ẹ ṣe èyí: Ẹ mú kẹ̀kẹ́ ẹrù láti ilẹ̀ Éjíbítì fún àwọn ọmọ yín àti àwọn aya yín. Kí ẹ sì mú baba yín tọ mí wá."'), 'Gen$45:22': (None, None, 'Gen$45:22 Ó fún ẹni kọ̀ọ̀kan wọn ní aṣọ tuntun. Ṣùgbọ́n Bẹ́ńjámínì ni ó fún ní ọ̀ọ́dúnrún ẹyọ owó (300) idẹ fàdákà àti ìpààrọ̀ aṣọ márùn ún.'), 'Gen$45:27': (None, None, 'Gen$45:27 "Ṣùgbọ́n nígbà tí wọ́n sọ ohun gbogbo tí Jósẹ́fù ti sọ fún wọn fún un tí ó sì rí kẹ̀kẹ́ ẹrù tí Jósẹ́fù fi ránṣẹ́ láti gbé e padà wá, iyè Jákọ́bù, baba wọn ṣọ."'), 'Gen$46:6': (None, 'ilẹ', 'Gen$46:6 " Wọ́n tún kó àwọn ohun ọ̀sìn wọn àti gbogbo ohun-ìní tí wọ́n ti ní láti ilẹ̀ Kénánì, Jákọ́bù àti gbogbo irú-ọmọ rẹ̀ sọ̀kalẹ̀ lọ sí Éjíbítì."'), 'Gen$46:29': ('ṣe', None, 'Gen$46:29 "Jósẹ́fù tọ́jú kẹ̀kẹ́-ẹsin rẹ̀ ó sì lọ sí Gósénì láti pàdé Ísírẹ́lì baba rẹ̀. Bí Jósẹ́fù ti dé iwájú baba rẹ̀, ó dì mọ́ baba rẹ̀ ó sì sunkún fún ìgbà pípẹ́."'), 'Gen$46:30': (None, 'ni', 'Gen$46:30 "Ísírẹ́lì wí fún Jósẹ́fù pé, “Wàyí o, mo le kú, níwọ̀n bí mo ti rí i fún ara a mi pé, o wà láàyè ṣíbẹ̀.”"'), 'Gen$46:31': (None, 'ilẹ', 'Gen$46:31 "Nígbà náà ni Jósẹ́fù wí fún àwọn arákùnrin rẹ̀ àti fún àwọn ará ilé baba rẹ̀ pé, “Èmi yóò gòkè lọ, èmi yóò sì bá Fáráò sọ̀rọ̀, èmi yóò sì wí fún-un pé, Àwọn arákùnrin mi àti ìdílé baba mi tí ń gbé ní Kénánì ti tọ̀ mí wá."'), 'Gen$47:4': ('jẹ ki', 'ni', 'Gen$47:4 "Wọ́n sì tún sọ fun un síwájú sí i pé, “A wá láti gbé ìhín yìí fún ìgbà díẹ̀ nítorí ìyàn náà mú púpọ̀ ní ilẹ̀ Kénánì, àwọn ohun-ọ̀sìn àwa ìránṣẹ́ rẹ kò sì rí ewéko jẹ. Nítorí náà jọ̀wọ́ má sàì jẹ́ kí àwọn ìránṣẹ́ rẹ tẹ̀dó sí ilẹ̀ Gósénì.”"'), 'Gen$47:14': ('mu', 'ilẹ', 'Gen$47:14 "Jósẹ́fù gba gbogbo owó tí ó wà ní ilẹ̀ Éjíbítì àti Kénánì ní ìpààrọ̀ fún ti ọkà tí wọn ń rà, ó sì mú owó náà wá sí ààfin Fáráò."'), 'Gen$47:15': (None, 'kú', 'Gen$47:15 "Nígbà tí owó wọn tán pátapáta ní Éjíbítì àti Kénánì, gbogbo Éjíbítì wá bá Jósẹ́fù, wọ́n wí pé, “Fún wa ní oúnjẹ, èéṣe tí a ó fi kú ní ojú rẹ? Gbogbo owó wa ni a ti ná tan.”"'), 'Gen$47:22': (None, 'ipin', 'Gen$47:22 "Ṣùgbọ́n sá, kò ra ilẹ̀ àwọn àlùfáà, nítorí wọ́n ń gba ìpín oúnjẹ lọ́wọ́ Fáráò, wọn sì ní oúnjẹ tí ó tó láti inú ìpín tí Fáráò ń fún wọn. Ìdí nìyí tí wọn kò fi ta ilẹ̀ wọn."'), 'Gen$47:24': (None, None, 'Gen$47:24 "Ṣùgbọ́n nígbà tí ire oko náà bá jáde, ẹ ó mú ìdá kan nínú ìdá márùn-ún rẹ̀ fún Fáráò. Ẹ le pa ìdá mẹ́rin tókù mọ́ fún ara yín àti ìdílé yín àti àwọn ọmọ yín.”"'), 'Gen$47:26': ('ṣe', 'ni', 'Gen$47:26 "Nítorí náà Jósẹ́fù sọ ọ́ di òfin nípa ọ̀rọ̀ ilẹ̀ ni Éjíbítì, ó sì wà bẹ́ẹ̀ di òni olónìí-pé, ìdá kan nínú ìdá márùn-ún irè oko jẹ́ ti Fáráò, ilẹ̀ àwọn àlùfáà nìkan ni kò di ti Fáráò."'), 'Gen$47:27': (None, None, 'Gen$47:27 "Àwọn ará Ísírẹ́lì sì tẹ̀dó sí Éjíbítì ní agbégbé Gósénì. Wọ́n ní ohun-ìní fún ara wọn, wọ́n sì bí sí i tó bẹ́ẹ̀ tí wọ́n pọ̀ si gidigidi ní iye."'), 'Gen$47:28': (None, 'ọdun', 'Gen$47:28 Jákọ́bù gbé ní Éjíbítì fún ọdún mẹ́tadínlógún (17) iye ọdún ọjọ́ ayé rẹ̀ sì jẹ́ ẹ̀tàdín ní àádọ́jọ (147).'), 'Gen$47:29': ('oju', None, 'Gen$47:29 " Nígbà tí àkókò ń súnmọ́ etílé fún Ísírẹ́lì láti kú, ó pe Jósẹ́fù, ọmọ rẹ̀, ó sì wí fún-un pé, “Bí mo bá rí ojú rere ni oju rẹ̀ fi ọwọ́ rẹ sí abẹ́ itan mi, kí o sì ṣe ìlérí pé ìwọ yóò fi àánú àti òtítọ́ hàn sí mi. Má ṣe sin òkú mi sí ilẹ̀ Éjíbítì,"'), 'Gen$48:3': ('fun', 'ṣe', 'Gen$48:3 " Jákọ́bù wí fún Jósẹ́fù pé, “Ọlọ́run Olódùmarè fara hàn mí ní Lúsì ní ilẹ̀ Kénánì, níbẹ̀ ni ó sì ti súre fún mi."'), 'Gen$48:19': (None, 'ọpọlọpọ', 'Gen$48:19 "Ṣùgbọ́n baba rẹ̀ kò gbà, ó wí pé, “Mo mọ̀, ọmọ mi, mo mọ̀. Òun náà yóò di orílẹ̀-èdè, òun náà yóò sì di ńlá. Ṣùgbọ́n àbúrò rẹ̀ yóò di ẹni ńlá jù ú lọ, irú ọmọ rẹ yóò sì di ọ̀pọ̀lọpọ̀ orílẹ̀-èdè.”"'), 'Gen$48:21': ('mú', 'ilẹ', 'Gen$48:21 "Nígbà náà ni Ísírẹ́lì wí fún Jósẹ́fù pé, “Ọjọ́ ikú mi súnmọ́ etílé, ṣùgbọ́n Ọlọ́run yóò wà pẹ̀lú yín, yóò sì mú un yín padà sí ilẹ̀ àwọn baba yín."'), 'Gen$48:22': (None, 'ọwọ', 'Gen$48:22 Pẹ̀lúpẹ̀lú èmi yóò fún ọ ní ìpín kan ju ti àwọn arákùnrin rẹ lọ. Ilẹ̀ tí mo fi idà àti ọ̀kọ̀ mi gbà lọ́wọ́ àwọn ará Ámórì.”'), 'Gen$49:7': ('ibinu', 'ibinu', 'Gen$49:7 "Ìfibú ni ìbínú wọn nítorí tí ó gbóná púpọ̀, àti fún ìrunú wọn nítorí tí ó kún fún ìkà. Èmi yóò tú wọn ká ní Jákọ́bù, èmi ó sì fọ́n wọn ká ní Ísírẹ́lì. "'), 'Gen$49:17': ('ọna', 'ona', 'Gen$49:17 "Dánì yóò jẹ́ ejo ni pópónà àti paramọ́lẹ̀ ní ẹ̀bá ọ̀nà, tí ó bu ẹsin jẹ ní ẹṣẹ̀, kí ẹni tí n gùn-un bá à le è subú sẹ́yìn. "'), 'Gen$49:22': ('ṣe', 'ni', 'Gen$49:22 "“Jóṣẹ́fù jẹ́ àjàrà eléṣo, àjàrà eléṣo ní etí odò, tí ẹ̀ka rẹ̀ gun orí odi."'), 'Gen$50:4': ('ṣe', None, 'Gen$50:4 "Nígbà tí ọjọ́ ìṣọ̀fọ̀ náà kọjá. Jósẹ́fù wí fún àwọn ará ilé Fáráò pé, “Bí mo bá bá ojúrere yín pàdé, ẹ bámi sọ fún Fáráò."'), 'Gen$50:10': ('ṣọfọ', 'ẹkún', 'Gen$50:10 "Nígbà tí wọ́n dé ilẹ̀ ìpakà Átadì, ní ẹ̀bá Jọ́dánì, wọn pohùn réré ẹkún; Níbẹ̀ ni Jósẹ́fù sì tún dúró ṣọ̀fọ̀ baba rẹ̀ fún ọjọ́ méje."'), 'Gen$50:24': ('mú', 'ilẹ', 'Gen$50:24 "Nígbà náà ni Jóṣẹ́fù wí fún àwọn arákùnrin rẹ̀ pé, “Mo ti fẹrẹ kú, ṣùgbọ́n dájúdájú Ọlọ́run yóò wá sí ìrànlọ́wọ́ yín, yóò sì mú-un yín jáde kúrò ní ilẹ̀ yìí lọ sí ilẹ̀ tí ó ti ṣèlérí ní ìbúra fún Ábúráhámù, Ísáákì àti Jákọ́bù.”"'), 'Gen$50:25': ('mu', None, 'Gen$50:25 "Jóṣẹ́fù sì mú kí àwọn ọmọ Ísírẹ́lì búra májẹ̀mu kan wí pé, “Dájúdájú Ọlọ́run yóò wá sí ìrànlọ́wọ́ yín, nígbà náà ni ẹ gbọdọ̀ kó egungun mi lọ́wọ́ kúrò ní ìhín.”"'), 'Exod$1:9': (None, 'gba', 'Exod$1:9 "Ó sọ fún àwọn ènìyàn rẹ̀ “Ẹ wò ó, àwọn ará Ísírẹ́lì ti pọ̀ ní iye àti agbára jù fún wa."'), 'Exod$1:11': ('ṣe', None, 'Exod$1:11 "Nítorí náà, wọ́n yan àwọn ọ̀gá akóniṣiṣẹ́ lé wọn lórí láti máa ni wọ́n lára pẹ̀lú iṣẹ́ àṣekára. Wọ́n sì kọ́ Pítómi àti Ráméṣéṣì gẹ́gẹ́ bí ìlú ìkó ìṣúra pamọ́ sí fún Fáráò."'), 'Exod$1:13': (None, 'iṣẹ', 'Exod$1:13 Wọ́n sì mú wọn ṣiṣẹ́ àṣekú láìkáánú wọn.'), 'Exod$1:15': ('jẹ', 'gbe', 'Exod$1:15 Ọba Éjíbítì sọ fún àwọn agbẹ̀bí Hébérù ti orúkọ wọn ń jẹ́ Ṣífúrà àti Púà pé:'), 'Exod$2:11': ('lọ', None, 'Exod$2:11 " Ní ọjọ́ kan, nígbà tí Mósè ti dàgbà, ó jáde lọ sí ibi ti àwọn ènìyàn rẹ̀ wà, ó ń wò wọ́n lẹ́nu iṣẹ́ líle wọn, O ri ará Éjíbítì tí ń lu ará Ébérù, ọ̀kan nínú àwọn ènìyàn rẹ̀."'), 'Exod$3:1': ('agbo', 'òkè', 'Exod$3:1 " Ní ọjọ́ kan nígbà tí Mósè ń sọ́ agbo ẹran Jẹ́tírò baba ìyàwó rẹ̀, àlùfáà Mídíánì. Ó da agbo ẹran náà lọ sí ọ̀nà jínjìn nínú ihà. Ó dé Hórébù, òkè Ọlọ́run."'), 'Exod$3:2': ('ina', 'ina', 'Exod$3:2 Níbẹ̀ ni ańgẹ́lì OLÚWA ti yọ sí i nínú ọ̀wọ́ iná ti ń jó láàrin igbó. Mósè rí i pé iná ń jó nínú igbó ṣùgbọ́n igbó kò run'), 'Exod$3:4': ('ri', 'wo', 'Exod$3:4 "Nígbà tí OLÚWA rí i pe Mósè ti lọ láti lọ wò ó, Ọlọ́run ké pè é láti àárin igbó náà, “Mósè! Mósè!!” Mósè sì dáhùn ó wí pé, “Èmi nì yìí.”"'), 'Exod$3:5': ('ibi', 'bata', 'Exod$3:5 " Ọlọ́run sì wí fún Mósè pé, “Má ṣe súnmọ́ ìhín yìí, bọ́ bàtà ẹsẹ̀ rẹ̀, nítorí ibi tí ìwọ dúró sí i nì ilẹ̀ mímọ́ ni.”"'), 'Exod$3:7': (None, 'ni', 'Exod$3:7 "Ọlọ́run si wí pé, “Èmí ti rí ìpọ́njú àwọn ènìyàn mi ní ilẹ̀ Éjíbítì, mo sì ti gbọ́ ohùn igbe wọn nítorí àwọn akóniṣiṣẹ́ wọn. Ìyà tí ń jẹ wọ́n sì kan mi lára."'), 'Exod$3:16': (None, 'ilẹ', 'Exod$3:16 "“Lọ, kó àwọn àgbààgbà Ísírẹ́lì jọ, kí o sí sọ fún wọn, ‘OLÚWA Ọlọ́run Jákọ́bù; yọ sí mi, ó sì wí pé: Lóòótọ́ èmi ti ń bojúwò yín, èmi sì ti rí ohun tí wọ́n ṣe sí i yín ni ilẹ̀ Íjíbítì."'), 'Exod$3:21': ('lọ', None, 'Exod$3:21 "“Èmi yóò sì jẹ́ kí ẹ bá ojúrere àwọn ará Éjíbítì pàdé. Tí yóò fi jẹ́ pé ti ẹ̀yin bá ń lọ, ẹ kò ní lọ ní ọwọ́ òfo."'), 'Exod$4:21': ('jẹ ki', 'ni', 'Exod$4:21 "OLÚWA sì sọ fún Mósè pé, “Ní ìgbà tí ìwọ bá padà sí Éjíbítì rí i pé ìwọ se iṣẹ́ ìyanu ni iwájú Fáráò. Èmi ti fún ọ lágbára láti ṣe é. Èmi yóò sì ṣé àyà rẹ̀ le, òun kì yóò jẹ́ kí àwọn ènìyàn náà kí ó lọ."'), 'Exod$5:6': ('fun', 'ṣe', 'Exod$5:6 Ní ọjọ́ yìí kan náà ni Fáráò pàṣẹ fún àwọn akóniṣiṣẹ́ àti àwọn ti ń ṣe alábojútó iṣẹ́ lórí àwọn ènìyàn.'), 'Exod$5:11': (None, 'wa', 'Exod$5:11 "Ẹ lọ wá koríko gbígbẹ ni ibi tí ẹ bá ti lè rí i, ṣùgbọ́n iṣẹ́ yín kí yóò dínkù.’ ”"'), 'Exod$5:12': ('koriko', 'koriko', 'Exod$5:12 Gbogbo wọn sí fọ́n káàkiri ni ilẹ̀ Éjíbítì láti sa ìdì koríko tí wọn yóò lò bí ì koríko gbígbẹ fún sísun bíríkì.'), 'Exod$5:14': ('sise', 'ni', 'Exod$5:14 "Àwọn alábojútó iṣẹ́ tí àwọn akóniṣiṣẹ́ yàn lára ọmọ Ísírẹ́lì ni wọn ń lù, tí wọn sì ń béèrè lọ́wọ́ wọn pé, “Èéṣe tí ẹ̀yin kò ṣe iye bíríkì ti ẹ̀yin ń ṣe ní àná ní òní bí i tí àtẹ̀yìnwá?”"'), 'Exod$5:20': ('ṣe', 'ni', 'Exod$5:20 Ní ìgbà tí wọ́n kúró ni ọ̀dọ̀ Fáráò wọ́n rí Mósè àti Árónì tí ó dúró láti pàdé wọn.'), 'Exod$6:4': ('ni', 'fun', 'Exod$6:4 "Èmí sì tún fi idi májẹ̀mu mi múlẹ̀ pẹ̀lú wọn láti fún wọn ní ilẹ̀ Kénánì, ilẹ̀ tí wọn gbé gẹ́gẹ́ bí àjòjì."'), 'Exod$6:6': ('gba', None, 'Exod$6:6 "“Sọ fún àwọn ará ilé Ísírẹ́lì: ‘Èmi ni OLÚWA, Èmi yóò sọ yín di òmìnira kúró nínú àjàgà àwọn ará Éjíbítì. Èmi yóò sì gbà yín sílẹ̀ kúró ní oko ẹrú wọn. Èmi yóò dá a yín sílẹ̀ pẹ̀lú apá nínà (àwọn iṣẹ́ ìyanu) àti pẹ̀lú ìdájọ́ ńlá."'), 'Exod$6:8': ('mú', 'ilẹ', 'Exod$6:8 "Èmi yóò mú un yín wá ilẹ̀ náà ti èmi ti búra pẹ̀lú ìgbọ́wọ́sókè láti fi fún Ábúráhámù. Ísáákì àti Jákọ́bù. Èmi yóò fi fún un yín bí ohun ìní, èmi ni OLÚWA.’ ”"'), 'Exod$6:13': (None, 'ilẹ', 'Exod$6:13 "OLÚWA bá ìran Mósè àti Árònì sọ̀rọ̀ nípa àwọn ará Ísírẹ́lì àti Fáráò ọba Íjibítì, ó pàṣẹ fún wọn pé kí wọn kó àwọn Ísírẹ́lì jáde kúrò ni ilẹ̀ Éjíbítì."'), 'Exod$7:1': ('ṣe', 'ni', 'Exod$7:1 "Nígbà náà ni OLÚWA sọ fún Mósè pé, “Wò ó, èmi ti ṣe ọ bí Ọlọ́run fún Fáráò, Árónì arákùnrin rẹ ni yóò jẹ́ wòlíì (agbẹnusọ) rẹ."'), 'Exod$7:4': ('mú', None, 'Exod$7:4 "ṣíbẹ̀ òun kì yóò fi etí sí ọ. Nígbà náà ni èmi yóò gbé ọwọ́ mi lé Éjíbítì, pẹ̀lú agbára ìdájọ́ ńlá mi ni èmi yóò mú àwọn ènìyàn mi Ísírẹ́lì jáde ní ọ̀wọ̀ọ̀wọ́."'), 'Exod$7:23': (None, None, 'Exod$7:23 "Dípò bẹ́ẹ̀, ó yíjú padà, ó sì lọ sí ààfin rẹ̀, kò sì jẹ́ kí àwọn nǹkan wọ̀nyí wà ni oókan àyà rẹ̀."'), 'Exod$8:7': ('mu', 'ilẹ', 'Exod$8:7 Ṣùgbọ́n àwọn onídan ilẹ̀ Éjíbítì ṣe bákan náà pẹ̀lú agbára òkùnkùn wọn. Àwọn náà mú kí ọ̀pọ̀lọ́ gun wá sí orí ilẹ̀ Éjíbítì.'), 'Exod$8:17': (None, 'ilẹ', 'Exod$8:17 "Wọ́n sì ṣe bẹ́ẹ̀, Nígbà tí Árónì na ọwọ́ rẹ́ jáde pẹ̀lú ọ̀pá ní ọwọ́ rẹ̀, tí ó sì lu eruku ilẹ̀, kòkòrò-kantíkantí sì wà lára àwọn ènìyàn àti àwọn ẹranko wọn. Gbogbo eruku jákè-jádò ilẹ̀ Éjíbítì ni ó di kòkòrò-kantíkantí."'), 'Exod$8:22': (None, 'ilẹ', 'Exod$8:22 "“ ‘Ṣùgbọ́n ni ọjọ́ náà, èmi yóò ya ilẹ̀ Góṣénì sọ́tọ̀, níbi tí àwọn ènìyàn mi ń gbé, ọ̀wọ́ eṣinṣin kankan ki yóò dé ibẹ̀. Kí ìwọ kí ó lè mọ̀ pé, èmi ni OLÚWA, mo wà ni ilẹ̀ yìí."'), 'Exod$8:29': ('lọ', 'lọ', 'Exod$8:29 "Mósè dáhùn ó wí pé, “Bí èmi bá ti kúrò ní ọ̀dọ̀ rẹ, èmi yóò gbàdúrà sí OLÚWA, awọn esinsin yóò fi Fáráò, àwọn ìjòyè rẹ, àti àwọn ènìyàn rẹ sílẹ̀ ní ọ̀la ṣùgbọ́n ǹjẹ́ ìdánílójú wa pé Fáráò kò tún ni lo ẹ̀tàn láti má jẹ́ kí àwọn ènìyàn kí ó lọ rú ẹbọ sí OLÚWA.”"'), 'Exod$9:5': ('ṣe', None, 'Exod$9:5 "OLÚWA sì dá àkókò kan wí pé, “Ní ọ̀la ni OLÚWA yóò ṣe èyí ni ilẹ̀ yìí.”"'), 'Exod$9:16': ('ni', None, 'Exod$9:16 " Ṣùgbọ́n nítorí èyí ni mo ṣe dá ọ sí, kí èmi kí ó lè fi agbára mi hàn ọ́, kí a bá à lè gbọ́ òkìkí orúkọ mi ní gbogbo ayé."'), 'Exod$9:19': ('wá', 'yinyin', 'Exod$9:19 "Pàṣẹ nísin yìí láti kó ẹran-ọ̀sìn yin àti ohun gbogbo ti ẹ ni ní pápá wá sí abẹ́ ààbò, nítorí òjò yìnyín yóò rọ̀ sí orí àwọn ènìyàn àti ẹran ti a kò kó wá sí abẹ́ ààbò tí wọ́n sì wà ni orí pápá, wọn yóò sì kú.’ ”"'), 'Exod$9:23': ('ilẹ', 'ilẹ', 'Exod$9:23 "Nígbà tí Mósè gbé ọ̀pá rẹ̀ sókè sí ojú ọ̀run, OLÚWA rán àrá àti yìnyín, mọ̀nàmọ́ná sì bù sí orí ilẹ̀. OLÚWA rọ òjò yìnyín sí orí ilẹ̀ Éjíbítì;"'), 'Exod$9:24': ('ilẹ', None, 'Exod$9:24 " Yìnyín rọ̀, mọ̀nàmọ́ná sì bẹ̀rẹ̀ sí bù sí orí ilẹ̀ èyí ni ó tí ì buru jù ti ó ṣẹlẹ̀ láti ìgbà ti Éjíbítì ti di orílẹ̀ èdè."'), 'Exod$9:35': (None, 'jẹ ki', 'Exod$9:35 "Ọkàn Fáráò sì yigbì, kò sì jẹ́ kí àwọn ọmọ Ísírẹ́lì ó lọ, bí OLÚWA ti sọ láti ẹnu Mósè."'), 'Exod$10:4': ('jẹ ki', 'ni', 'Exod$10:4 "Bí ìwọ bá kọ̀ láti jẹ́ kí wọn lọ, èmi yóò mú eṣú wa sí orílẹ̀-èdè rẹ ní ọ̀la."'), 'Exod$10:11': ('ṣe', 'sin', 'Exod$10:11 "Rárá! Ọkùnrin yín nìkan ni kí ó lọ, láti lọ sin OLÚWA, ní wọ̀n ìgbà tí ó jẹ́ pé èyí ni ẹ̀yín ń béèrè fún.” Wọ́n sì lé Mósè àti Árónì kúrò ní iwájú Fáráò."'), 'Exod$10:12': ('wá', 'yinyin', 'Exod$10:12 "Ní ìgbà náà ni Ọlọ́run sọ fún Mósè, “Gbé ọwọ́ rẹ sókè lórí Éjíbítì kí ọ̀pọ̀lọpọ̀ eṣú bo gbogbo ilẹ̀, kí ó sì ba gbogbo ohun ọ̀gbìn tí ó wà ni inú oko wọn jẹ́, àní gbogbo ohun tí òjò yìnyín kò bájẹ́ tan.”"'), 'Exod$10:13': ('mu', 'ilẹ', 'Exod$10:13 "Nígbà náà ni Mósè na ọ̀pá ọwọ́ rẹ̀ sórí ilẹ̀ Éjíbítì, OLÚWA sì mú kí afẹ́fẹ́ láti ìlà oòrùn fẹ́ kọjá lórí ilẹ̀ náà ni gbogbo ọ̀sán àti ni gbogbo òru ni ọjọ́ náà. Ní òwúrọ̀ afẹ́fẹ́ náà ti gbá ọ̀pọ̀lọpọ̀ Esú wá;"'), 'Exod$10:15': ('ilẹ', None, 'Exod$10:15 Wọ̀n bo gbogbo ilẹ̀ tí ilẹ̀ fi di dúdú. Wọ́n ba gbogbo ohun tó kù ní orí ilẹ̀ lẹ́yìn òjò yìnyín jẹ́; gbogbo ohun ọ̀gbìn tí ó wà ní inú oko àti gbogbo èso tí ó wà lórí igi. Kò sí ewe tí ó kù lórí igi tàbí lorí ohun ọ̀gbìn ní gbogbo ilẹ̀ Íjibítì.'), 'Exod$10:25': ('fun', None, 'Exod$10:25 "Ṣùgbọ́n Mósè sọ pé, “Ìwọ gbọdọ̀ fún wa láàyè láti rúbọ àti ọrẹ ẹbọ sísun ní iwájú OLÚWA Ọlọ́run wa."'), 'Exod$11:4': (None, None, 'Exod$11:4 "Nígbà náà ni Mósè wí pé, “Èyí ni ohun tí OLÚWA sọ, ‘Ní ọ̀gànjọ́ òru èmi yóò la ilẹ̀ Éjíbítì kọjá."'), 'Exod$11:8': ('wá', None, 'Exod$11:8 "Gbogbo àwọn ìjòyè rẹ̀ wọ̀nyí yóò tọ̀ mí wá, wọn yóò wólẹ̀ ni iwájú, mi wọn yóò sì máa wí pé, ‘Lọ àti àwọn ènìyàn tí ó tẹ̀ lé ọ!’ Lẹ́yìn náà èmi yóò jáde.” Nígbà náà ni Mósè fi ìbínú jáde kúrò ní iwájú Fáráò"'), 'Exod$12:1': (None, None, 'Exod$12:1 " OLÚWA sọ fún Mósè àti Árónì ni ilẹ̀ Éjíbítì pé,"'), 'Exod$12:3': (None, None, 'Exod$12:3 "Sọ fún gbogbo àwọn ènìyàn Ísírẹ́lì pé ni ọjọ́ kẹwàá oṣù yìí, ọkùnrin kọ̀ọ̀kan yóò mú ọ̀dọ́ àgùntàn kọ̀ọ̀kan fún ìdílé rẹ̀, ọ̀kan fún agbo ilé kọ̀ọ̀kan."'), 'Exod$12:7': (None, None, 'Exod$12:7 "Wọn yóò mú lára ẹ̀jẹ̀ ẹran náà, wọn yóò fi kan ẹ̀gbẹ́ méjèèjì àti òkè ẹnu ọ̀nà àbáwọlé níbi tí wọ́n bá ti jẹ ẹran ọ̀dọ́ àgùntàn náà."'), 'Exod$12:17': ('mu', None, 'Exod$12:17 "“Ẹ ṣe àpèjẹ àkàrà aláìwú, nítorí ọjọ́ yìí ni mo mú un yín jáde ni Éjíbítì. Ẹ ṣe àjọyọ̀ ọjọ́ yìí gẹ́gẹ́ bí ìlànà tí yóò wà títí ayé ní àwọn ìran tí ń bọ̀."'), 'Exod$12:22': (None, None, 'Exod$12:22 "Ẹ̀yin yóò sì mú ìdí-ewé hísópù, ẹ tì í bọ inú ẹ̀jẹ̀ tí ó wà nínú ọpọ́n kí ẹ sì fi kun òkè à bá wọ ẹnu ọ̀nà ilé yín àti ẹ̀gbẹ́ méjèèjì ìlẹ̀kùn yín. Ẹnikẹ́ni nínú yín kò gbọdọ̀ jáde sí òde títí di òwúrọ̀."'), 'Exod$12:25': ('fun', None, 'Exod$12:25 "Nígbà tí ẹ̀yin bá de ilẹ̀ tí OLÚWA yóò fi fún un yín bí òun ti ṣe ìlérí, ẹ máa kíyèsí àjọ yìí."'), 'Exod$12:31': ('lọ', None, 'Exod$12:31 "Fáráò sì ránṣẹ́ pe Mósè àti Árónì ní òru, ó sì wí pé, “Ẹ kúrò lọ́dọ̀ mi ẹ̀yin àti àwọn ènìyàn Ísírẹ́lì! Ẹ lọ láti sin OLÚWA gẹ́gẹ́ bí ẹ ti béèrè."'), 'Exod$12:32': (None, None, 'Exod$12:32 "Ẹ kó agbo àgùntàn yín àti agbo màlúù yín gẹ́gẹ́ bí ẹ̀yin ti sọ, kí ẹ sì máa lọ, kí ẹ sì súre fún mi.”"'), 'Exod$12:41': (None, None, 'Exod$12:41 Ní ọjọ́ ti irínwó ọdún ó le ọgbọ̀n (430) pé gan an ni gbogbo ènìyàn jáde kúrò ní ilẹ̀ Éjíbítì.'), 'Exod$12:42': ('mú', 'ilẹ', 'Exod$12:42 "Nítorí pé, OLÚWA ṣe àìsùn ni òru ọjọ́ náà láti mú wọn jáde kúrò ní ilẹ̀ Éjíbítì, ní òru yìí ni gbogbo Ísírẹ́lì ní láti máa se àìsùn láti fi bu ọlá fún OLÚWA títí di àwọn ìran tí ń bọ̀."'), 'Exod$12:51': ('mú', 'ilẹ', 'Exod$12:51 Àti pé ní ọjọ́ náà gan-an ni OLÚWA mú àwọn ọmọ Ísírẹ́lì jáde kúrò ní ilẹ̀ Éjíbítì gẹ́gẹ́ bí ìpín ìpín wọn.'), 'Exod$13:5': ('mú', 'ilẹ', 'Exod$13:5 "Ní ìgbà tí Ọlọ́run mú un yín jáde wá sí ilẹ̀ ẹ Kénánì, Hítì, Ámórì, Hífì àti ilẹ̀ àwọn Jébúsì; ilẹ̀ tí ó ti ṣe ìlérí láti fi fún àwọn baba ńlá yín, ilẹ̀ tí ń sàn fún wàrà àti oyin, ẹ ni láti pa ìsìn yìí mọ́ ní oṣù yìí."'), 'Exod$13:11': ('mú', 'ilẹ', 'Exod$13:11 "“Lẹ́yìn tí OLÚWA tí mú ọ wá sí ilẹ̀ àwọn ará Kénánì tí ó sì fi fún ọ gẹ́gẹ́ bí òun ti ṣe ìlérí pẹ̀lú ìbúra fún ọ àti fún àwọn baba ńlá rẹ̀,"'), 'Exod$13:17': ('lọ', 'kọjá', 'Exod$13:17 "Ní ìgbà tí Fáráò jẹ́ kí àwọn ènìyàn ó lọ, Ọlọ́run kò mú wọn tọ ojú ọ̀nà ti ó la orílẹ̀ èdè àwọn Fílístínì kọjá, bí o tilẹ̀ jẹ́ pé ọ̀nà náà kúrú. Nítorí Ọlọ́run sọ pé, “Bí wọ́n bá dojú kọ ogun, wọ́n lè yí ọkàn wọn padà kí wọ́n padà sí ilẹ̀ Éjíbítì.”"'), 'Exod$13:19': ('mu', None, 'Exod$13:19 " Mósè kó egungun Jósẹ́fù pẹ̀lú rẹ̀ nítorí Jósẹ́fù tí mú kí àwọn ọmọ Isírẹ́lì búra. Ó ti wí pé, “Dájúdájú Ọlọ́run yóò dìde fún ìrànlọ́wọ́ yín ẹ sì gbọdọ̀ kó egungun mi lọ pẹ̀lú yín kúrò níhìnín yìí.”"'), 'Exod$13:21': ('lọ', None, 'Exod$13:21 "OLÚWA sì ń lọ níwájú wọn, nínú òpó ìkùùkuu ní ọ̀san láti máa ṣe amọ̀nà wọn àti ní òru nínú òpó iná láti máa tan ìmọ́lẹ̀ fún wọn, kí wọn lè máa lọ nínú ìrìnàjò wọn ní tọ̀sán tòru."'), 'Exod$14:4': (None, None, 'Exod$14:4 "Èmi yóò sé ọkàn Fáráò le ti yóò fi lépa wọn. Ṣùgbọ́n èmi yóò gba ògo fún ará mi láti ìpaṣẹ̀ Fáráò àti ogun rẹ̀, àti pé àwọn ará Éjíbítì yóò mọ̀ pé èmi ni OLÚWA.” Àwọn Isírẹ́lì sì ṣe bẹ́ẹ̀."'), 'Exod$14:5': ('ni', 'jẹ ki', 'Exod$14:5 "Nígbà tí wọ́n sọ fún ọba Éjíbítì pé àwọn ènìyàn náà ti sá lọ, ọkàn Fáráò àti àwọn ìrànṣẹ́ rẹ̀ yí padà sí àwọn ènìyàn, wọ́n sì wí pé, “Kí ni ohun tí àwa ṣe yìí? Àwa ti jẹ́ kí àwọn ọmọ Ísírẹ́lì kí ó lọ, a sì ti pàdánù ìsìnrú wọn.”"'), 'Exod$14:6': (None, None, 'Exod$14:6 "Ó sì di kẹ̀kẹ́ ogun rẹ̀, ó sì kó àwọn ọmọ ogun rẹ̀ pẹ̀lú rẹ̀,"'), 'Exod$14:12': (None, 'sin', 'Exod$14:12 " Ǹjẹ́ èyí kọ́ ni àwa sọ fún ọ ni Éjíbítì, ‘Fi wá sílẹ̀, jẹ́ kí àwa máa sin ará Éjíbítì’? Nítorí kò bá sàn fún wa kí a sin àwọn ará Éjíbítì ju kí a kú sínú ihà yìí lọ!”"'), 'Exod$14:18': (None, 'ni', 'Exod$14:18 Àwọn ará Éjíbítì yóò mọ̀ pé èmi ni OLÚWA nígbà ti èmi bá gba ògo fún ara mi lórí Fáráò: lórí kẹ̀kẹ́-ogun rẹ̀ àti lórí àwọn ẹlẹ́sin rẹ̀.”'), 'Exod$14:24': ('wò', 'oju', 'Exod$14:24 "Ní ìsọ́ òwúrọ̀ (láàárin ago mẹ́ta sí mẹ́ta òwúrọ̀) OLÚWA bojúwo ogun àwọn ará Éjíbítì láàrin òpó iná àti ìkùùkuu, ó sì kó ìpayà bá àwọn ọmọ ogun Éjíbítì."'), 'Exod$15:10': (None, None, 'Exod$15:10 Ìwọ fẹ́ èèmí rẹ òkun ru bò wọ́n mọ́lẹ̀ wọ́n rì bí òjé ni àárin omi ńlá.'), 'Exod$15:14': (None, None, 'Exod$15:14 Orílẹ̀-èdè yóò gbọ́ wọn yóò wárìrì Ìkáàánú yóò mi àwọn ènìyàn palẹ́sítínì.'), 'Exod$15:15': (None, None, 'Exod$15:15 "Ìbẹ̀rù yóò mú àwọn ìjòyè Édómù, Àwọn olórí Móábù yóò wárìrì Àwọn ènìyàn Kénánì yóò sì yọ̀ danu;"'), 'Exod$15:16': ('ṣubu', 'kọjá', 'Exod$15:16 "Ìbẹ̀rù-bojo yóò subú lù wọ́n nítorí nína títóbi apá rẹ̀ wọn yóò dúró jẹ́ ẹ́ láì mira bí i òkúta Títí àwọn ènìyàn rẹ yóò fi ré kọjá, OLÚWA, Títí tí àwọn ènìyàn tí o ti rà yóò fi ré kọjá."'), 'Exod$15:17': ('ṣe', 'ni', 'Exod$15:17 "Ìwọ yóò mú wọn wá láti gbìn wọ́n ni ori òkè ti ìwọ jogún; Ní ibi ti ìwọ ṣe fún ara rẹ, OLÚWA. Láti máa gbé ibi mímọ́ ti ìwọ fi ọwọ́ rẹ gbékalẹ̀, OLÚWA."'), 'Exod$15:19': ('mu', 'ilẹ', 'Exod$15:19 "Nígbà ti ẹsin Fáráò, kẹ̀kẹ́ ogun àti àwọn ẹlẹ́sin rẹ̀ wọ inú òkun, OLÚWA mú kí omi òkun padà bò wọ́n mọ́lẹ̀, ṣùgbọ́n àwọn ọmọ Ísírẹ́lì rìn lórí ìyàngbẹ ilẹ̀ la òkun kọjá."'), 'Exod$15:25': (None, None, 'Exod$15:25 "Mósè sì ké pe OLÚWA, OLÚWA sì fi igi kan hàn án, ó sì sọ igi náà sínú omi, omi náà sì dùn. Níbẹ̀ ni OLÚWA ti gbé ìlànà àti òfin kalẹ̀ fún wọn, níbẹ̀ ni ó sì ti dán wọn wò."'), 'Exod$16:2': (None, None, 'Exod$16:2 Ní ijù náà ni ìjọ àwọn ènìyàn ti kùn sí Mósè àti Árónì.'), 'Exod$16:6': ('mu', None, 'Exod$16:6 "Mósè àti Árónì sọ fún gbogbo àwọn ọmọ Ísírẹ́lì pé, “Ní ìrọ̀lẹ́, ẹ̀yin yóò mọ̀ pé OLÚWA ni ó mú un yín jáde láti Éjíbítì wá."'), 'Exod$16:27': ('lọ', None, 'Exod$16:27 "Ṣíbẹ̀ṣíbẹ̀ àwọn ènìyàn kan jáde lọ ní ọjọ́ keje láti kó o, ṣùgbọ́n wọn kò ri nǹkan kan kó."'), 'Exod$16:31': ('ṣe', None, 'Exod$16:31 " Àwọn ènìyàn Ísírẹ́lì sì pe oúnjẹ náà ní Mánà. Ó funfun bí irúgbìn kóríáńdà, ó sì dùn bí burẹ́dì fẹlẹfẹlẹ ti a fi oyin ṣe."'), 'Exod$16:32': ('mu', 'ilẹ', 'Exod$16:32 "Mósè wí pé, “Èyí ni ohun tí OLÚWA pàṣẹ: ‘Ẹ mú òṣùnwọ̀n ómérì mánà kí ẹ sì pa á mọ́ de àwọn ìran ti ń bọ̀, kí wọn kí ó lè rí oúnjẹ ti èmi fi fún un yín jẹ ní ijù, nígbà tí mo mú un yín jáde ni ilẹ̀ Éjíbítì.’ ”"'), 'Exod$17:14': ('iranti', 'iranti', 'Exod$17:14 " OLÚWA sì sọ fun Mósè pé. “Kọ èyí sì inú ìwé fún ìrántí, kí o sì sọ fún Jóṣúà pẹ̀lú; nítorí èmi yóò pa ìràntí Ámélékì run pátapáta kúrò lábẹ́ ọ̀run.”"'), 'Exod$18:1': (None, 'ni', 'Exod$18:1 "Jẹ́tírò, àlùfáà Mídíánì, àna Mósè, gbọ́ gbogbo ohun tí Ọlọ́run ti ṣe fún Mósè àti fún Ísírẹ́lì àwọn ènìyàn rẹ̀, àti bí OLÚWA ti mú àwọn ènìyàn Ísírẹ́lì jáde láti Éjíbítì wá."'), 'Exod$18:8': ('ṣe', None, 'Exod$18:8 Mósè sọ fún àna rẹ̀ nípa ohun gbogbo ti OLÚWA tí se sí Fáráò àti àwọn ará Éjíbítì nítorí Ísírẹ́lì. Ó sọ nípa gbogbo ìṣòro tí wọn bá pàdé ní ọ̀nà wọn àti bí OLÚWA ti gbà wọ́n là.'), 'Exod$18:15': ('ni', 'ṣe', 'Exod$18:15 "Mósè dá a lóhùn pé, “Nítorí àwọn ènìyàn ń tọ̀ mí wá láti mọ́ ìfẹ́ Ọlọ́run."'), 'Exod$18:19': ('mú', None, 'Exod$18:19 "Nísinsìnyìí, fetísílẹ̀ sí mi, èmi yóò sì gbà ọ́ ni ìmọ̀ràn, Ọlọ́run yóò sì wà pẹ̀lú rẹ. Ìwọ gbọdọ̀ jẹ́ aṣojú àwọn ènìyàn wọ̀nyí níwájú Ọlọ́run, ìwọ yóò sì mú èdè àìyedè wá sí iwájú rẹ̀."'), 'Exod$18:23': ('lọ', None, 'Exod$18:23 "Bí ìwọ bá ṣe èyí, bí Ọlọ́run bá sì fi àṣẹ sí i fún ọ bẹ́ẹ̀, àárẹ̀ kò sì ní tètè mu ọ, àwọn ènìyàn wọ̀nyí yóò sì padà lọ ilé wọn ni ìtẹ́lọ́rùn.”"'), 'Exod$18:27': ('lọ', 'lọ', 'Exod$18:27 "Mósè sì jẹ́ kí àna rẹ̀ lọ ní ọ̀nà rẹ̀, Jẹ́tírò sì padà sí ilẹ̀ ẹ rẹ̀."'), 'Exod$19:8': ('dahùn', 'ṣe', 'Exod$19:8 "Gbogbo àwọn ènìyàn náà sì pa ohùn wọn pọ̀ wọ́n sì dáhùn wí pé, “Àwa yóò se ohun gbogbo ti OLÚWA wí.” Mósè sì mú ìdáhùn wọn padà tọ OLÚWA lọ."'), 'Exod$19:12': ('lọ', None, 'Exod$19:12 " Kí ìwọ kí ó se ààlà fún àwọn ènìyàn, ibi tí wọn lè dé dúró, kí ìwọ kí ó sì wí fún wọn pé, ‘Ẹ ṣọ́ra! Ẹ má ṣe gun orí òkè lọ, kí ẹ máa tilẹ̀ fi ọwọ́ kan etí ààlà rẹ̀; ẹnikẹ́ni tí ó bá fi ọwọ́ kan òkè náà yóò kú:"'), 'Exod$19:16': (None, None, 'Exod$19:16 " Ní òwúrọ̀ ọjọ́ kẹ́ta, àrá àti mọ̀nàmọ́ná sì wà pẹ̀lú ìkùùkuu tí ó sú dudu ní orí òkè, ìpè ńlá sì dún kíkan kíkan tó bẹ́ẹ̀ tí gbogbo àwọn tí ó wà ni ibùdó wárìrì."'), 'Exod$19:19': (None, 'ohun', 'Exod$19:19 "Ohùn ìpè sì ń rinlẹ̀ dòdò. Mósè sọ̀rọ̀, Ọlọ́run sì fi àrá dá a lóhùn."'), 'Exod$19:24': (None, None, 'Exod$19:24 "OLÚWA sì wí fún Mósè pé, “Sọ̀kalẹ̀ lọ, mú Árónì gòkè wá pẹ̀lú rẹ. Ṣùgbọ́n kí àwọn àlùfáà àti àwọn ènìyàn má ṣe fi tipátipá gòkè tọ OLÚWA. Bí bẹ́ẹ̀ kọ́ yóò kọlù wọ́n.”"'), 'Exod$20:1': (None, None, 'Exod$20:1 OLÚWA sì sọ gbogbo ọ̀rọ̀ yìí pé:'), 'Exod$20:7': (None, None, 'Exod$20:7 " “Ìwọ kò gbọdọ̀ pe orúkọ OLÚWA Ọlọ́run rẹ ní asán, nítorí OLÚWA kì yóò ka àwọn tí ó ń pe orúkọ rẹ̀ ní asán bí aláìlẹ́ṣẹ̀ ní ọ̀run."'), 'Exod$20:9': ('ṣe', 'iṣẹ', 'Exod$20:9 "Ọjọ́ mẹ́fà ni ìwọ yóò fi ṣiṣẹ́, kí ìwọ kí ó ṣe gbogbo iṣẹ́ rẹ,"'), 'Exod$20:24': (None, 'orukọ', 'Exod$20:24 " “ ‘Pẹpẹ erùpẹ̀ ni kí ìwọ mọ fún mi. Ní orí pẹpẹ yìí ni kí ìwọ ti máa rú ẹbọ sísun rẹ sí mi àti ẹbọ àlàáfíà rẹ, àgùntàn rẹ, ewúrẹ́ àti akọ màlúù rẹ. Ní ibi gbogbo tí mo gbé fi ìrántí orúkọ mi sí, èmi yóò sì máa tọ̀ ọ́ wá, èmi yóò sì máa bùkún ọ níbẹ̀."'), 'Exod$20:25': ('ṣe', 'kọ', 'Exod$20:25 " Bí ìwọ bá ṣe pẹpẹ òkúta fún mi, má ṣe fi òkúta tí a se lọ́sọ̀ọ́ kọ́ ọ, nítorí bí ìwọ bá lo ohun ọnà rẹ lorí rẹ̀, ìwọ sọ ọ di àìmọ́."'), 'Exod$21:4': (None, 'ni', 'Exod$21:4 "Bí olówó rẹ̀ bá fún un ní aya, tí aya náà sì bímọ ọkùnrin àti obìnrin, aya àti àwọn ọmọ rẹ̀ yóò jẹ́ ti olówó rẹ̀, yóò sì lọ ní òmìnira ní òun nìkan."'), 'Exod$21:8': ('jẹ ki', 'ni', 'Exod$21:8 "Bí òun kò bá sì tẹ́ olówó rẹ̀ lọ́rùn, ẹni tí ó fẹ́ ẹ fún ara rẹ̀, yóò jẹ́ kí wọn ó rà á padà, òun kò ní ẹ̀tọ́ láti tà á bí ẹrú fún àwọn èniyàn orílẹ̀ èdè mìíràn, nítorí pé òun ni kò se ojúse rẹ̀ pẹ̀lú ìwẹ̀fà náà."'), 'Exod$21:22': (None, None, 'Exod$21:22 "“Bí àwọn ènìyàn ti ń jà bá pa aboyún lára, tí aboyún náà bá bimọ láìpé ọjọ́, ṣùgbọ́n ti kò sí aburú mìíràn mọ́ lẹ́yìn rẹ̀, ẹni ti ó fa ìpalára yìí yóò san iyekíye ti ọkọ aboyún náà bá béèrè fún, bí ilé ẹjọ́ bá se gbà láàyè gẹ́gẹ́ bí owó ìtanràn."'), 'Exod$21:26': ('lọ', None, 'Exod$21:26 "“Bí ọkùnrin kan bá lu ẹrúkùnrin tàbí ẹrúbìnrin rẹ̀ ní ojú, ti ojú náà sì fọ́, ó ni láti jẹ́ kí ẹrú náà lọ ní òmìnira fún ìtanràn ojú rẹ̀ ti ó fọ́."'), 'Exod$21:33': (None, None, 'Exod$21:33 "“Bí ọkùnrin kan bá sí kòtò sílẹ̀ láìbò tàbí kí ó gbẹ́ kòtò sílẹ̀ láìbò, tí màlúù tàbi kẹ́tẹ́kẹ́tẹ́ bá se bẹ́ẹ̀ bọ́ sínú kòtò náà."'), 'Exod$22:2': (None, None, 'Exod$22:2 "“Bí a bá mú olè níbi ti ó ti ń fọ́lé, ti a sì lù ú pa, ẹni tí ó lù ú pa náà kò ní ẹ̀bi ìtàjẹ̀ sílẹ̀."'), 'Exod$22:3': ('ni', 'ṣe', 'Exod$22:3 "Ṣùgbọ́n ti ó bá ṣẹlẹ̀ ni ojú ọ̀sán, a ó kà á si ìpànìyàn. Ọkùnrin ti ó lù ú pa náà yóò ni ẹ̀bi ìtàjẹ̀ sílẹ̀. “Olè gbọdọ̀ san ohun tí ó jí padà. Ṣùgbọ́n tí kò bá ni ohun ti ó lè fi san án padà, a ó tà á, a ó sì fi sanwó ohun tí ó jí gbé padà."'), 'Exod$22:5': (None, None, 'Exod$22:5 "“Bí ọkùnrin kan bá ń bọ́ ẹran ọ̀sìn rẹ̀ lórí pápá tàbí nínú ọgbà àjàrà, tí ó sì jẹ́ kí ó lọ jẹ nínú oko ẹlòmíràn, a ó mú kí ó san ohun ti ẹran rẹ̀ jẹ padà pẹ̀lú èyí tí ó dára jù nínú oko tàbí nínú ọgbà rẹ̀ (ẹlòmíràn padà fún un)."'), 'Exod$22:6': (None, 'ina', 'Exod$22:6 "“Bí iná bá ṣẹ́ tí ó kán lu igbó tí ó sì jó àká ọkà tàbí gbogbo oko náà, ẹni tí iná ṣẹ́ lọ́wọ́ rẹ̀ yóò san ohun tí ìná ti ó ṣẹ́ jó padà."'), 'Exod$22:8': ('ri', None, 'Exod$22:8 "Ṣùgbọ́n ti a kò bá rí olè náà mú, ẹni tí ó ni ilé náà yóò fi ara hàn níwájú ìdájọ́ láti jẹ́ kí a mọ̀ bí oun fúnrarẹ̀ ni ó gbé ohun ti ó sọnù náà."'), 'Exod$22:17': ('fun', None, 'Exod$22:17 "Bí baba ọmọbìnrin náà bá kọ̀ jálẹ̀ láti fi fún un ní aya, ó ni láti san owó tó tó owó orí rẹ̀, fún fí fẹ́ ẹ ní wúndíá."'), 'Exod$23:2': ('ṣe', None, 'Exod$23:2 "“Ìwọ kò gbọdọ̀ tẹ̀lé ọ̀pọ̀ ènìyàn láti se aburú. Nígbà tí ìwọ bá jẹ́rìí sí ẹjọ́, ìwọ kò gbọdọ̀ yí ìdájọ́ po nípa gbígbé lẹ́yìn ọ̀pọ̀ ènìyàn."'), 'Exod$23:10': (None, None, 'Exod$23:10 " “Ní ọdún mẹ́fà ni ìwọ yóò gbin oko rẹ, ìwọ yóò sì kóre èso rẹ̀."'), 'Exod$23:13': (None, 'ni', 'Exod$23:13 "“Ẹ máa sọ́ra, kí ẹ sì ṣe ohun gbogbo tí mo wí fún un yín. Ẹ má se pe orúkọ òrìṣà, kí a má se gbọ́ orúkọ wọn ní ẹnu yín."'), 'Exod$23:19': ('mú', 'ilẹ', 'Exod$23:19 “Mú èso àkọ́so ilẹ̀ rẹ tí ó dára jùlọ wá sí ilé OLÚWA Ọlọ́run rẹ. “Ìwọ kò gbọdọ̀ fi omi ọmú ìyá ewúrẹ́ bọ ọmọ ewúrẹ́.'), 'Exod$23:20': (None, 'ibi', 'Exod$23:20 "“Kíyèsí èmi rán ańgẹ́lì kan lọ ní iwájú rẹ, láti ṣọ́ ọ ni ọ̀nà àti láti mú ọ dé ibi ti mo ti pèsè sílẹ̀ fún."'), 'Exod$23:24': ('ṣe', 'sin', 'Exod$23:24 "Ìwọ kò gbọdọ̀ foríbalẹ̀ fún òrìṣà wọn, bẹ́ẹ̀ ni, ìwọ kò gbọdọ̀ sìn wọ́n, tàbí kí ẹ tẹ̀lé ìṣe wọn. Kí ìwọ kí ó si wó wọn lulẹ̀, kí ìwọ kí ó sì fọ́ ère òkúta wọ́n túútúú."'), 'Exod$24:3': (None, 'ṣe', 'Exod$24:3 "Nígbà ti Mósè lọ láti sọ fún àwọn ènìyàn, gbogbo ọ̀rọ̀ àti òfin OLÚWA, wọ́n wí ní ohùn kan pé, “Gbogbo ohun ti OLÚWA wí ni àwa yóò ṣe.”"'), 'Exod$24:7': ('mu', 'ka', 'Exod$24:7 "Nígbà náà ni ó sì mú ìwé májẹ̀mú, ó sì kà á sí àwọn ènìyàn. Wọ́n dáhùn pé, “Àwa yóò ṣe gbogbo ohun tí OLÚWA wí: Àwa yóò sì gbọ́ràn.”"'), 'Exod$24:9': ('lọ', None, 'Exod$24:9 "Mósè àti Árónì, Nádábù àti Ábíhù àti àádọ́rin àwọn àgbààgbà Ísírẹ́lì gòkè lọ."'), 'Exod$24:12': ('fun', 'ni', 'Exod$24:12 " OLÚWA sì sọ fún Mósè pé, “Gòkè wá sí ọ̀dọ̀ mi, kí o sì dúró níhín-ín. Èmi yóò sì fún ọ ní òkúta ìkọ̀wé pẹ̀lú òfin àti ìlànà tí mo ti kọ sílẹ̀ fún ìtọ́sọ́nà wọn.”"'), 'Exod$24:13': (None, 'oke', 'Exod$24:13 Nígbà náà ni Mósè jáde lọ pẹ̀lú Jọsúà arákùnrin rẹ̀. Mósè lọ sí orí òkè Ọlọ́run.'), 'Exod$24:14': (None, 'ni', 'Exod$24:14 "Ó sì wí fún àwọn àgbààgbà pé, Ẹ dúró dè wá níhìn-ín yìí, títí àwa yóò fi tún padà tọ̀ yín wá, sì kíyèsìí, Árónì àti Húrì ń bẹ pẹ̀lú yín, bí ẹnikan bá ni ọ̀ràn kan, kí ó tọ̀ wọ́n lọ."'), 'Exod$25:1': (None, None, 'Exod$25:1 " OLÚWA sì wí fún Mósè pé,"'), 'Exod$25:3': (None, None, 'Exod$25:3 "Wọ̀nyí ni ọrẹ ti ìwọ yóò gbà ni ọwọ́ ọ wọn: wúrà, fàdákà, idẹ,"'), 'Exod$25:12': (None, None, 'Exod$25:12 "Ṣe òrùka wúrà mẹ́rin fún un. Kí o sì so wọ́n mọ́ ẹṣẹ̀ rẹ̀ mẹ́rẹ̀ẹ̀rin, òrùka wúrà méjì yóò wà ní ẹ̀gbẹ́ kìn-ín-ní, òrùka wúrà méjì yóò wà ní ẹgbẹ́ kejì."'), 'Exod$25:18': ('ṣe', None, 'Exod$25:18 Fi wúrà tí a lù se kérúbù méjì sí igun ìtẹ́-àánú.'), 'Exod$25:20': (None, 'wo', 'Exod$25:20 "Apá àwọn kérúbù náà yóò wà ní gbígbé sókè, tí wọn yóò sì fi apá wọn se ibòòji sí orí ìtẹ́ àánú. Àwọn kérúbù náà yóò kọ ojú sí ara wọn, wọn yóò máa wo ọ̀kánkán ìtẹ́."'), 'Exod$25:31': ('ṣe', None, 'Exod$25:31 " “Mú ojúlówó wúrà, lù ú dáradára, kí o sì fi ṣe ọ̀pá fìtílà, ìsàlẹ̀ àti apá rẹ̀, kọ́ọ̀bù rẹ̀ ti ó dàbí òdòdó, ìṣọ àti ìtànná rẹ̀ yóò jẹ́ ara kan náà pẹ̀lú rẹ̀."'), 'Exod$25:37': ('ṣe', None, 'Exod$25:37 "“Nígbà náà ni ìwọ yóò se fítílà méje. Ìwọ yóò sì gbe ka orí rẹ̀, kí wọn bá a lè tan iná sí iwájú rẹ̀."'), 'Exod$26:1': ('ṣe', 'iṣẹ', 'Exod$26:1 " “Ṣe àgọ́ náà pẹ̀lú aṣọ títa mẹ́wàá, aṣọ ọ̀gbọ̀ olókùn wíwẹ́ àti àsọ aláró, ti elésèé àlùkò, àti ti òdòdó, pẹ̀lú àwọn kérúbù ni kí ó ṣe iṣẹ́ ọlọ́nà sí wọn."'), 'Exod$26:5': ('ṣe', None, 'Exod$26:5 "Ṣe àádọ́ta (50) ọ̀jábó sí aṣọ títa kan, àádọ́ta ojábó (30) ni kí ó ṣe sí etí aṣọ títa ni ìbò mìíràn, pẹ̀lú ọ̀jábó ní ọ̀kánkán ara wọn."'), 'Exod$26:31': ('ṣe', None, 'Exod$26:31 " “Ìwọ yóò si ṣe aṣọ ìgélé aláró àti elése àlùkò, àti òdòdó, àti ògbọ̀ olókùn wẹ́wẹ́ tíí ṣe ọlọ́nà, pẹ̀lú ti àwọn kérúbu ni kí á ṣe é."'), 'Exod$26:34': ('ibi', None, 'Exod$26:34 Fi ìtẹ́ àánú bo àpótí ẹ̀rí náà ni ibi mímọ́ tí ó ga jùlọ.'), 'Exod$27:4': (None, 'àwọ̀n', 'Exod$27:4 "Ìwọ yóò sí ṣe ni wẹ́wẹ́, ìṣẹ́ àwọ̀n idẹ, kí o sì ṣe òrùka idẹ sí igun mẹ́rẹ̀ẹ̀rin ìṣẹ́ àwọ̀n náà."'), 'Exod$28:3': ('ni', None, 'Exod$28:3 "Ìwọ yóò sì sọ fún gbogbo àwọn ọlọ́gbọ́n ènìyàn, àwọn ẹni tí mo fún ní ọgbọ́n, kí wọn kí ó lè dá aṣọ fún Árónì, fún ìyà-sí-mímọ́ rẹ̀, kí ó lè ba à sìn mí gẹ́gẹ́ bí àlùfáà."'), 'Exod$28:6': ('ṣe', 'iṣẹ', 'Exod$28:6 " “Wọn yóò sì dá ẹ̀wù èfòdì ti wúrà, tí aṣọ aláró, elésèé àlùkò àti ti òdòdó àti ti ọ̀gbọ̀ olókún wíwẹ́ iṣẹ́ ọlọ́nà."'), 'Exod$28:11': ('ṣe', None, 'Exod$28:11 "Iṣẹ́ ọnà òkúta fínfín, bí ìfín èdìdì àmì, ni ìwọ yóò fún òkúta méjèèjì gẹ́gẹ́ bí orúkọ àwọn ọmọ Ísírẹ́lì: ìwọ yóò sì dè wọ́n sí ojú ìdè wúrà."'), 'Exod$28:13': ('ṣe', None, 'Exod$28:13 Ìwọ yóò sì ṣe ojú ìdè wúrà'), 'Exod$28:15': ('ṣe', 'iṣẹ', 'Exod$28:15 " “Ìwọ yóò fi iṣẹ́ ọgbọ́n ṣe ìgbàyà fún ìpinu iṣẹ́ ọnà gbẹ́nàgbẹ́nà. Ìwọ yóò ṣe é bí ẹ̀wù èfòdì ti wúrà, ti aṣọ aláró, ti elésèé àlùkò, ti òdòdó àti ti ọ̀gbọ̀ olókùn wíwẹ́ dáradára."'), 'Exod$28:22': ('ṣe', None, 'Exod$28:22 "“Fún ìgbàyà náà, ìwọ yóò se okùn ẹ̀wọ̀n ti kìkì wúrà, gẹ́gẹ́ bí ọ̀já àmùrè."'), 'Exod$28:38': ('gba', None, 'Exod$28:38 "Kí ó wà níwájú orí Árónì, kí ó sì lè máa ru ẹ̀bi tí ó jẹ mọ́ ẹ̀bùn mímọ́ ti àwọn ọmọ Ísírẹ́lì ti yà sí mímọ́, èyíkéyìí tí ẹ̀bùn wọn lè jẹ́. Yóò máa wà níwájú orí Árónì nígbà gbogbo, kí wọn lè jẹ́ ìtẹ́wọ́gbà fún OLÚWA."'), 'Exod$28:41': (None, None, 'Exod$28:41 "Lẹ́yìn tí ìwọ ti fi àwọn aṣọ wọ̀nyí wọ Árónì arákùnrin rẹ, ìwọ yóò sì fi wọ àwọn ọmọ rẹ̀ pẹlú, ìwọ óta òróró sí wọn ní orí, ìwọ yóò sì ya wọ́n sí mímọ́. Ìwọ yóò sì Sọ wọ́n di mímọ́, kí wọn kí o lè máa se iṣe àlùfáà."'), 'Exod$28:42': (None, None, 'Exod$28:42 "“Ìwọ yóò dá aṣọ àwòtẹ́lẹ̀ fún wọn, kí wọn lè máa fi bo ara wọn, kí ó dé ìbàdí dé itan."'), 'Exod$29:1': (None, None, 'Exod$29:1 " “Èyí ni ohun tí ìwọ yóò ṣe láti yà wọ́n sí mímọ́, nítorí kí wọn lè máa àti láti máa sìn se àlùfáà fún mi: Mú ọ̀dọ́ akọ màlúù kan àti àgbò méjì tí kò ní àbùkù."'), 'Exod$29:5': (None, None, 'Exod$29:5 "Ìwọ yóò sì fi ẹ̀wù àwọ̀tẹ́lẹ̀ wọ Árónì, àti aṣọ ìgúnwà éfódì, àti éfódì, àti ìgbàyà, kí ó sì fi onírúurú-ọnà ọ̀já ẹ̀wù èfòdì dìí."'), 'Exod$29:30': ('ibi', None, 'Exod$29:30 "Ọmọ rẹ̀ tí ó bá jẹ àlùfáà ní ipò rẹ̀, tí ó bá wọ̀ yóò máa sì wá sí àgọ́ àjọ láti ṣiṣẹ́ ní ibi mímọ́, yóò sì máa wọ̀ wọ́n ní ọjọ́ méje."'), 'Exod$29:33': (None, None, 'Exod$29:33 "Wọn yóò sì jẹ nǹkan wọ̀nyí èyí tí a fi se ètùtù náà fún ìyàsímímọ́ àti ìsọdimímọ́ wọn. Ṣùgbọ́n kò ní sí ẹlòmíràn ti yóò le è jẹ wọ́n, nítorí pé mímọ́ ni."'), 'Exod$29:46': ('mu', 'ilẹ', 'Exod$29:46 "Wọn yóò sì mọ̀ pé èmi ni OLÚWA Ọlọ́run wọn, ẹni tí ó mú wọn jáde láti ilẹ̀ Éjíbítì wá, nítorí kí èmi lè máa gbé àárin wọn. Èmi ni OLÚWA Ọlọ́run wọn."'), 'Exod$30:3': (None, 'oke', 'Exod$30:3 "Ìwọ yóò sì bo òkè rẹ̀, gbogbo ìhà rẹ̀ àti ìwo rẹ̀ pẹ̀lú u kìkì wúrà, ìwọ yóò sì se wúrà gbà á yíká."'), 'Exod$30:15': ('fun', 'ṣe', 'Exod$30:15 "Àwọn olówó kì yóò san ju ìdajì sékélì lọ, àwọn talákà kò sì gbọdọ̀ dín ní ìdajì sékélì nígbà tí ẹ̀yin bá mú ọrẹ wá fún OLÚWA láti se ètùtù fún ọkàn yín."'), 'Exod$30:16': ('gba', 'ṣe', 'Exod$30:16 "Ìwọ yóò sì gba owó ètùtù náà lọ́wọ́ àwọn ọmọ Ísírẹ́lì, ìwọ yóò sì fi lé lẹ̀ fún ìsìn àgọ́ àjọ. Yóò jẹ́ ìrántí fún àwọn ọmọ Ísírẹ́lì níwájú OLÚWA, láti ṣe ètùtù fún ọkàn yín.”"'), 'Exod$30:19': (None, None, 'Exod$30:19 Árónì àti àwọn ọmọ rẹ̀ yóò máa wẹ ọwọ́ àti ẹṣẹ̀ wọn pẹ̀lú omi tí ó wà nínú rẹ̀.'), 'Exod$31:1': (None, None, 'Exod$31:1 " OLÚWA wí fún Mósè pé,"'), 'Exod$31:6': ('ṣe', 'ni', 'Exod$31:6 "Ṣíwájú sí i, èmi ti yan Óhólíábù ọmọ Áhúsámákì, ti ẹ̀yà Dánì, láti ràn án lọ́wọ́. Bákan náà, èmi ti fi ọgbọ́n fún gbogbo àwọn oníṣẹ́ ọnà láti ṣe ohun gbogbo tí mo paṣẹ fún ọ:"'), 'Exod$31:16': (None, 'kiyesi', 'Exod$31:16 "Nítorí náà ni àwọn ọmọ Ísírẹ́lì yóò se máa pa ọjọ ìsinmi mọ́, ẹ máa ṣe àkíyèsí rẹ̀ dé ìrandíràn tó ń bọ̀ bí i májẹ̀mu títí láé."'), 'Exod$32:1': ('mu', None, 'Exod$32:1 " Nígbà tí àwọn ènìyàn rí i pé Mósè pẹ́ kí ó tó sọ̀kalẹ̀ láti orí òkè wá, wọn péjọ yí Árónì ká, wọ́n sì wí pé, “Wá, dá òrìṣà tí yóò máa ṣáájú wa fún wa. Bí ó ṣe ti Mósè tí ó mú wa jáde láti Éjíbítì wá, àwa kò mọ́ ohun tí ó ti sẹlẹ̀ sí i.”"'), 'Exod$32:4': ('mu', None, 'Exod$32:4 "Ó sì gbà wọ́n ní ọwọ́ wọn, ó sì fi ṣe ohun ọnà fínfin, ó sì dàá ní àwòrán ẹgbọrọ màlúù. Nígbà náà ni wọn wí pé, “Ísírẹ́lì, wọ̀nyí ni òrìṣà, ti ó mú un yín jáde wá láti Éjíbítì.”"'), 'Exod$32:6': ('mu', 'ṣeré', 'Exod$32:6 " Bẹ́ẹ̀ ni àwọn ènìyàn dìde ní kùtùkùtù ọjọ́ kejì, wọ́n sì rúbọ síṣun, wọ́n sì mú ẹbọ àlàáfíà wá. Lẹ́yìn náà wọ́n jókòó láti jẹ àti láti mu, wọ́n sì dìde láti ṣeré."'), 'Exod$32:7': ('ṣe', None, 'Exod$32:7 "OLÚWA sì wí fún Mósè pé, “Ṣọ̀kalẹ̀ lọ, nítorí àwọn ènìyàn rẹ, tí ìwọ mú gòkè wá láti Éjíbítì, wọ́n ti dẹ́ṣẹ̀."'), 'Exod$32:9': (None, 'ni', 'Exod$32:9 " OLÚWA wí fún Mósè pé, “Èmi ti rí àwọn ènìyàn wọ̀nyí, wọ́n sì jẹ́ ọlọ́rùn líle ènìyàn."'), 'Exod$32:11': ('mu', None, 'Exod$32:11 "Ṣùgbọ́n Mósè kígbe fún ojú rere OLÚWA Ọlọ́run rẹ̀, ó wí pé, “OLÚWA, è é ṣe tí ìbínú rẹ yóò ṣe gbóná sí àwọn ènìyàn rẹ, tí ìwọ mú jáde láti Éjíbítì wá pẹ̀lú agbára ńlá àti ọwọ́ agbára?"'), 'Exod$32:13': ('ni', None, 'Exod$32:13 "Rántí àwọn ọmọ ọ̀dọ̀ rẹ Ábúráhámù, Ísáákì àti Ísírẹ́lì, ẹni tí ìwọ búra fún fúnraàrẹ: ‘tí o wí fún wọn pé, èmi yóò mú irú ọmọ rẹ pọ̀ bí ìràwọ̀ ojú ọ̀run, èmi yóò sì fún irú ọmọ rẹ ní gbogbo ilẹ̀ tí mo ti pinnu fún wọn, yóò sì jẹ́ ogún ìní wọn láéláé.’ ”"'), 'Exod$32:19': (None, 'oke', 'Exod$32:19 "Nígbà tí Mósè dé àgọ́, ó sì rí ẹgbọrọ màlúù náà àti ijó, ìbínú rẹ gbóná, ó sì ju pálí ọwọ́ rẹ̀ sílẹ̀, ó sì fọ́ wọn sí wẹ́ẹ́wẹ́ ní ìṣàlẹ̀ òkè náà."'), 'Exod$32:23': ('mu', None, 'Exod$32:23 " Wọ́n sọ fún mi pé, ‘Ṣe òrìsà fún wa, tí yóò máa ṣáájú wa. Bí ó ṣe ti Mósè ẹni tí ó mú wa jáde láti Éjíbítì wá àwa kò mọ ohun tí ó sẹlẹ̀ sí i.’"'), 'Exod$32:30': ('ni', 'ṣe', 'Exod$32:30 "Ní ọjọ́ kejì Mósè sọ fún àwọn ènìyàn náà pé, “Ẹ̀yin ti dá ẹ̀ṣẹ̀ tí ó pọ̀. Ṣùgbọ́n nísinsin yìí èmi yóò gòkè lọ bá OLÚWA; bóyá èmi lè se ètùtù fún ẹ̀ṣẹ̀ yín.”"'), 'Exod$32:31': ('ṣe', 'ni', 'Exod$32:31 "Bẹ́ẹ̀ ni Mósè padà tọ OLÚWA lọ, ó sì wí pé, “Yé, kí ni ẹ̀ṣẹ̀ ńlá tí àwọn ènìyàn ti dá yìí! Wọ́n ti ṣe òrìsà wúrà fún ara wọn."'), 'Exod$32:34': ('lọ', None, 'Exod$32:34 "Ǹjẹ́ nísinsin yìí lọ, máa darí àwọn ènìyàn yìí lọ sí ibi ti mo sọ, angẹ́lì mi yóò sì máa lọ ṣáájú yín. Ìgbà ń bọ̀ fún mi tì èmi yóò ṣe ìbẹ̀wò, èmi yóò fi yà jẹ wọ́n fún ẹ̀ṣẹ̀ wọn.”"'), 'Exod$33:1': ('mu', 'ilẹ', 'Exod$33:1 "OLÚWA sọ fún Mósè pé, “Fi ìhín yìí sílẹ̀, ìwọ àti àwọn ènìyàn tí o mú gòkè láti Éjíbítì wá, kí ẹ sì gòkè lọ sí ilẹ̀ tí èmi ti pinnu ní ìbúra fún Ábúráhámù, Ísáákì àti Jákọ́bù wí pé, ‘Èmi yóò fi fún irú ọmọ rẹ.’"'), 'Exod$33:5': ('wá', 'ṣe', 'Exod$33:5 "Nítorí OLÚWA ti wí fún Mósè pé, “Sọ fún àwọn ọmọ Ísírẹ́lì pé, ‘Ènìyàn ọlọ́rùn líle ní ẹ̀yin, bí èmi bá lè wa sí àárin yín ni ìsẹ́jú kan, èmi lè pa yín run. Ní sinsin yìí, bọ́ ohun ọ̀sọ́ rẹ kúrò, èmi yóò sì gbèrò ohun tí èmi yóò se pẹ̀lú rẹ.’ ”"'), 'Exod$33:8': (None, None, 'Exod$33:8 "Nígbàkúgbà tí Mósè bá jáde lọ sí ibi àgọ́, gbogbo ènìyàn á dìde, wọ́n á sì dúró sí ẹnu ọ̀nà àgọ́ wọn, wọn yóò máa wo Mósè títí yóò fi wọ inú àgọ́ náà."'), 'Exod$33:13': ('ri', 'wa', 'Exod$33:13 "Bí ìwọ bá ní inú dídùn pẹ̀lú mi, kọ́ mi ní ọ̀nà rẹ, kí èmi kí ó lè mọ̀ ọ́, kí n sì le máa wá ojú rere rẹ pẹ̀lú. Rántí wí pé orílẹ̀ èdè yìí ènìyàn rẹ ni.”"'), 'Exod$33:19': (None, 'orukọ', 'Exod$33:19 " OLÚWA sì wí pé, “Èmi yóò sì mú gbogbo ire mí kọjá níwájú rẹ, Èmi yóò sì pòkìkí orúkọ OLÚWA níwájú rẹ. Èmi yóò sàánú fún ẹni tí èmi yóò sàánú fún, èmi yóò sì ṣoore-ọ̀fẹ́ fún ẹni tí èmi yóò ṣoore-ọ̀fẹ́ fún."'), 'Exod$34:4': (None, 'oke', 'Exod$34:4 "Bẹ́ẹ̀ Mósè sì gbẹ́ òkúta wàláà méjì bí ti àkọ́kọ́, ó sì gun orí òkè Sínáì lọ ní kùtùkùtù òwúrọ̀, gẹ́gẹ́ bí OLÚWA ti pa á láṣẹ fún-un; Ó sì gbé òkúta wàláà méjèèjì náà sí ọwọ́ rẹ̀."'), 'Exod$34:6': ('kọjá', None, 'Exod$34:6 " Ó sì kọjá níwájú Mósè, ó sì ń ké pé, “OLÚWA, OLÚWA, Ọlọ́run aláàánú àti olóore ọ̀fẹ́, ẹni tí ó lọ́ra láti bínú, tí ó pọ̀ ní ìfẹ́ àti olóòtítọ́,"'), 'Exod$34:9': ('jẹ ki', 'ni', 'Exod$34:9 "Ó wí pé, “OLÚWA, bí èmi bá rí ojú rere rẹ, nígbà náà jẹ́ kí OLÚWA lọ pẹ̀lú wa. Bí ó tilẹ̀ jẹ́ pé, ènìyàn ọlọ́rùn líle ni wọn, dárí búburú àti ẹ̀ṣẹ̀ wa jìn, kí o sì gbà wá gẹ́gẹ́ bí ìní rẹ.”"'), 'Exod$34:10': ('ṣe', 'iṣẹ', 'Exod$34:10 "Nígbà náà ni OLÚWA wí pé: “Èmi dá májẹ̀mú kan pẹ̀lú yín. Níwájú gbogbo ènìyàn rẹ èmi yóò se ìyanu, tí a kò tí ì ṣe ní orílẹ̀ èdè ní gbogbo ayé rí. Àwọn ènìyàn tí ìwọ ń gbé láàrin wọn yóò rí i bí iṣẹ́ tí èmi OLÚWA yóò ṣe fún ọ ti ní ẹ̀rù tó"'), 'Exod$34:12': (None, None, 'Exod$34:12 "Má a sọ́ra kí o má ba à bá àwọn ti ó n gbé ilẹ náà tí ìwọ ń lọ dá májẹ̀mú, nítorí wọn yóò jẹ́ ìdíwọ́ láàrin rẹ."'), 'Exod$34:26': (None, 'ilẹ', 'Exod$34:26 “Mú èyí tí ó dára nínú àkọ́so èso ilẹ̀ rẹ wá sí ilé OLÚWA Ọlọ́run rẹ. “Má ṣe ṣe ọmọ ewúrẹ́ nínú wàrà ìyá rẹ̀.”'), 'Exod$34:27': ('ṣe', 'ni', 'Exod$34:27 "OLÚWA sì wí fún Mósè pé, “Kọ àwọn ọ̀rọ̀ wọ̀nyí sílẹ̀, nítorí nípa àwọn ọ̀rọ̀ wọ̀nyí ni èmi bá ìwọ àti Ísírẹ́lì dá májẹ̀mú.”"'), 'Exod$35:3': (None, None, 'Exod$35:3 Ẹ má se dáná kankan ní ibùgbé yín ní ọjọ́ ìsinmi.”'), 'Exod$35:4': (None, None, 'Exod$35:4 " Mósè sọ fún gbogbo ìjọ àwọn ọmọ Ísírẹ́lì pé, “Èyí ni ohun tí OLÚWA pa láṣẹ:"'), 'Exod$35:10': (None, None, 'Exod$35:10 "“Gbogbo ẹni tí ó ní ọgbọ́n láàrin yín, kí ó wa, kí ó sì wá se gbogbo ohun tí OLÚWA ti pa láṣẹ:"'), 'Exod$35:21': ('mu', 'iṣẹ', 'Exod$35:21 "Olúkúlùkù ẹni tí ó fẹ́ àti ẹni tí ọkàn rẹ̀ gbé sókè wá, wọ́n sì mú ọrẹ fún OLÚWA, fún isẹ́ Àgọ́ àjọ, fún gbogbo ìsìn rẹ̀ àti fún asọ mímọ́ náà."'), 'Exod$35:22': (None, None, 'Exod$35:22 "Gbogbo àwọn tí ó fẹ́, ọkùnrin àti obìnrin, wọ́n wá wọ́n sì mú onírúurú ìlẹ̀kẹ wúrà: òrùka etí, òrùka àti ọ̀sọ́. Gbogbo wọn mú wúrà gẹ́gẹ́ bí ọrẹ wá fún OLÚWA."'), 'Exod$35:24': ('mu', 'iṣẹ', 'Exod$35:24 "Àwọn tí ó mú ọrẹ fàdákà tàbí idẹ wá, mú ọrẹ wá fún OLÚWA, àti olúkúlùkù ẹni tí ó ní igi kasíà fún ipa kankan nínú iṣẹ mú un wá."'), 'Exod$35:29': ('mu', 'iṣẹ', 'Exod$35:29 Gbogbo àwọn ènìyàn Isirẹli ọkùnrin àti obìnrin ẹni tí ó fẹ́ mú ọrẹ àtinúwá fún OLÚWA fún gbogbo iṣẹ́ tí OLÚWA ti pa láṣẹ fún wọn láti se nípaṣẹ̀ Mósè.'), 'Exod$35:30': (None, None, 'Exod$35:30 " Nígbà náà ni Mósè wí fún àwọn ọmọ Ísírẹ́lì pé, “Wò ó, OLÚWA ti yan Bésálélì ọmọ Úrì, ọmọ Húrì, ti ẹ̀yà Júdà,"'), 'Exod$36:2': ('wá', 'ṣe', 'Exod$36:2 Mósè sì pe Bésálélì àti Óhóábù àti gbogbo ọlọ́gbọ́n ènìyàn ẹni tí OLÚWA ti fún ni agbára àti gbogbo ẹni tí ó fẹ́ láti wá sé iṣẹ́ náà.'), 'Exod$36:4': (None, None, 'Exod$36:4 Bẹ́ẹ̀ ni gbogbo àwọn ọlọ́gbọ́n oníṣẹ́ ọnà tí wọn ń ṣe gbogbo iṣẹ́ ibi mímọ́ náà fi iṣẹ́ wọn sílẹ̀.'), 'Exod$36:6': ('ṣe', 'iṣẹ', 'Exod$36:6 "Mósè sì pàṣẹ, wọ́n sì rán iṣẹ́ yìí sí gbogbo ibùdó: “Kí ọkùnrin tàbí obìnrin má ṣe ṣe ohun kankan bí ọrẹ fún ibi mímọ́ náà mọ́.” Bẹ́ẹ̀ ni a dá àwọn ènìyàn lẹ́kun láti mú un wá sí i,"'), 'Exod$36:7': ('ṣe', 'iṣẹ', 'Exod$36:7 nítorí ohun tí wọ́n ti ni ti ju ohun tí wọn fẹ́ fi ṣe gbogbo iṣẹ́ náà lọ.'), 'Exod$36:8': ('ṣe', 'iṣẹ', 'Exod$36:8 " Gbogbo àwọn ọlọ́gbọ́n ọkùnrin láàrin àwọn òṣìṣẹ́ ni wọ́n ṣe iṣẹ́ náà pẹ̀lú aṣọ títa mẹ́wàá ti ọ̀gbọ̀ olókùn dáradára, tí aṣọ aláró, ti elésèé àlukò àti ti òdòdó, pẹ̀lú àwọn kérúbù ṣe iṣẹ́ sí wọn nípa ọgbọ́n iṣẹ́ ọnà."'), 'Exod$36:22': (None, 'ṣe', 'Exod$36:22 pẹ̀lú ìtẹ̀bọ̀ méjì tí ó kọjú sí ara wọn. Wọ́n ṣe gbogbo pákó Àgọ́ náà bí èyí.'), 'Exod$36:35': ('ṣe', 'iṣẹ', 'Exod$36:35 " Wọ́n ṣe aṣọ títa ti aṣọ aláró, ti elésèé àlùkò, ti òdòdó àti ti ọ̀gbọ̀ olókùn dáradára, wọ́n ṣe kérúbù si pẹ̀lú ọgbọ́n oníṣẹ́ ọnà."'), 'Exod$37:13': (None, None, 'Exod$37:13 "Ó sì dá òrùka wúrà fún tábìlì náà, ó sì so wọ́n mọ́ igun mẹ́rẹ̀ẹ̀rin, níbi tí ẹṣẹ̀ mẹ́rẹ̀ẹ̀rin wà."'), 'Exod$37:17': ('ṣe', None, 'Exod$37:17 " Ó sì se ọ̀pá fìtílà náà ní kìkì wúrà ó sì lù ú jáde, ọ̀pá rẹ̀, ìtànná ìfẹ́ rẹ̀, irudi rẹ àti ago rẹ̀, wọ́n jẹ́ òkan náà."'), 'Exod$37:26': (None, None, 'Exod$37:26 "Kìkì wúrà ni ó fi bo orí rẹ̀, gbogbo ìhà rẹ̀ àti ìwo rẹ̀, ó sì fi ìgbátí wúrà yìí ká."'), 'Exod$37:29': ('ṣe', 'iṣẹ', 'Exod$37:29 Ó sì tún ṣe òróró mímọ́ ìtasórí àti kìkì òórùn dídùn tùràrí-iṣẹ́ àwọn tí ń ṣe nǹkan olóòórùn.'), 'Exod$38:25': (None, None, 'Exod$38:25 " Sílífà tí a rí nínú ìjọ, ẹni tí a kà nínú ìkànìyàn jẹ́ ọgọ́rùn-ún (100) talẹ́ntì àti òjìlélẹ́gbẹ̀sán ó lé mẹ́ẹ̀dógún sékélì (1,775) gẹ́gẹ́ bí sékélì ibi mímọ́"'), 'Exod$39:3': ('ge', None, 'Exod$39:3 "Ó sì lu wúrà náà di ewé fẹ́lẹ́fẹ́lẹ́, ó sì tún gé e láti fi ṣe iṣẹ́ sí aṣọ aláró, ti elésèé àlùkò, ti òdòdó, àti sínú ọ̀gbọ̀ dáradára ni ti iṣẹ́ ọgbọ́n iṣẹ́ ọnà."'), 'Exod$39:8': ('ṣe', 'iṣẹ', 'Exod$39:8 " Ó ṣe iṣẹ́ ọnà sí igbáyà náà iṣẹ́ ọgbọ́n ọlọ́nà. Ó ṣe é bí, ẹ̀wù èfòdì: ti wúrà ti aṣọ aláró, ti elésèé àlùkò, ti òdòdó àti ti ọ̀gbọ̀ olókùn dáradára."'), 'Exod$39:15': ('ṣe', 'iṣẹ', 'Exod$39:15 "Fún igbáyà náà, wọ́n ṣe ẹ̀wọ̀n iṣẹ́ kìkì wúrà, gẹ́gẹ́ bi okùn."'), 'Exod$39:22': ('ṣe', 'iṣẹ', 'Exod$39:22 Ó sì ṣe ọ̀já àmùrè ẹ̀wù èfòdì gbogbo rẹ̀ jẹ́ aṣọ aláró iṣẹ́ alásọ híhun'), 'Exod$39:27': ('ṣe', 'iṣẹ', 'Exod$39:27 " Fún Árónì àti àwọn ọmọ rẹ, wọ́n ṣe àwọ̀tẹ́lẹ̀ ti ọ̀gbọ̀ dáradára tí iṣẹ́ alásọ híhun."'), 'Exod$39:33': (None, None, 'Exod$39:33 "Wọ́n sì mú tabánákù náà tọ Mósè wá: àgọ́ náà àti gbogbo ohun ọ̀sọ́ rẹ̀, ìkọ́ rẹ̀, pákó rẹ̀, ọ̀pá rẹ̀, òpó rẹ̀ àti àwọn ihò ìtẹ̀bọ̀ rẹ̀;"'), 'Exod$39:42': ('ṣe', 'iṣẹ', 'Exod$39:42 Àwọn ọmọ Ísírẹ́lì ti ṣe gbogbo iṣẹ́ náà gẹ́gẹ́ bí OLÚWA ti páṣẹ fún Mósè.'), 'Exod$40:9': (None, None, 'Exod$40:9 "“Mú òróró ìtasórí, kí ó sì ta á sára Àgọ́ náà àti ohun gbogbo tí ó wà nínú rẹ̀: yà pẹpẹ sí mímọ́ àti gbogbo ọ̀sọ́ rẹ̀, yóò sì jẹ́ mímọ́."'), 'Lev$1:7': (None, None, 'Lev$1:7 "Àwọn ọmọ Árónì tí í ṣe àlùfáà yóò fi iná sí orí pẹpẹ, wọn yóò sì to igi sórí pẹpẹ náà."'), 'Lev$1:12': ('jo', 'ge', 'Lev$1:12 "Kí ó gé e sí wẹ́wẹ́, kí àlùfáà sì tò ègé ẹran náà, orí àti ọ̀rá rẹ̀ sí orí igi tó ń jó lórí pẹpẹ."'), 'Lev$1:15': (None, None, 'Lev$1:15 "Kí àlùfáà mu wá sí ibi pẹpẹ, kí ó yín in lọ́rùn, kí ó sì sun ún lórí pẹpẹ, kí ó sì ro ẹ̀jẹ̀ rẹ̀ ní ẹ̀gbẹ́ pẹpẹ."'), 'Lev$2:1': (None, None, 'Lev$2:1 "“ ‘Bí ẹnìkan bá mú ọrẹ ẹbọ ohun jíjẹ wá fún OLÚWA ọrẹ rẹ̀ gbọdọ̀ jẹ́ ìyẹ̀fun darádara, kí ó da òróró sí i, kí o sì fi tùràrí sí i,"'), 'Lev$2:4': ('ororo', None, 'Lev$2:4 "“ ‘Bí ẹ bá mú ọrẹ ohun jíjẹ, èyí tí a yan lórí ààrò iná, kí ó jẹ́ ìyẹ̀fun kíkúná dáradára, àkàrà tí a se láì ní ìwúkàrà, tí a sì fi òróró pò àti àkàrà fẹ́lẹ́fẹ́lẹ́ tí a ṣe láì ní ìwúkàrà èyí tí a fi òróró pò."'), 'Lev$2:6': (None, None, 'Lev$2:6 "Rún un kí o sì da òróró síi lórí, ọrẹ ohun jíjẹ ni."'), 'Lev$2:9': (None, None, 'Lev$2:9 "Àlùfáà yóò sì mú ẹbọ ìrántí nínú ọrẹ ohun jíjẹ yìí, yóò sì sun ún lórí pẹpẹ gẹ́gẹ́ bí ọrẹ tí a fi iná sun, àní òórùn dídùn sí OLÚWA."'), 'Lev$2:13': (None, 'ọrẹ', 'Lev$2:13 "Ẹ fi iyọ̀ dun gbogbo ọrẹ ohun jíjẹ yín. Ẹ má se aláì fi iyọ̀ májẹ̀mu Ọlọ́run yín sínú àwọn ọrẹ ohun jíjẹ yín, ẹ fi iyọ̀ sí gbogbo ọrẹ yín."'), 'Lev$3:1': (None, 'ọrẹ', 'Lev$3:1 " “ ‘Bí ọrẹ ẹnìkan bá jẹ́ ọrẹ àlàáfíà, tí ó sì mú ẹran wá láti inú agbo màlúù yálà akọ tàbí abo, kí ó mú èyí tí kò ní àbùkù wá ṣíwájú OLÚWA."'), 'Lev$3:3': (None, None, 'Lev$3:3 "Láti ara ọrẹ àlàáfíà ni kí ẹni náà ti mú ẹbọ tí a fi iná sun sí OLÚWA, gbogbo ọ̀nà tí ó bo nǹkan inú ẹran náà àti gbogbo ọ̀rá tí ó so mọ́ wọn."'), 'Lev$3:9': ('ṣe', None, 'Lev$3:9 "Kí o sì mú wá nínú ẹbọ àlàáfíà náà, fún ẹbọ tí a fi iná se sí OLÚWA; ọ̀rá rẹ̀ àti gbogbo ìrù rẹ̀ tí ó ní ọ̀rá, òun ni kí o mú kúrò súnmọ́ egungun ẹ̀yìn àti ọ̀rá tí ó bo ìfun lórí; àti gbogbo ọ̀rá tí ń bẹ lára ìfun."'), 'Lev$3:14': (None, None, 'Lev$3:14 "Lára ọrẹ tó mú wá, kí ó mú ọrẹ sísun fún OLÚWA, gbogbo ọ̀rá tó bo nǹkan inú rẹ̀ àti ohun gbogbo tó so mọ́ ọn."'), 'Lev$4:5': (None, 'mú', 'Lev$4:5 "Kí àlùfáà tí a fi òróró yàn mú díẹ̀ nínú ẹ̀jẹ̀ akọ màlúù, kí ó sì gbé e lọ sínú àgọ́ ìpàdé."'), 'Lev$4:22': ('ṣe', None, 'Lev$4:22 "“ ‘Bí olórí kan bá ṣèèṣì ṣẹ̀ láìmọ̀ tí ó sì ṣe ohun tí kò yẹ kí ó ṣe sí ọ̀kan nínú àwọn òfin OLÚWA Ọlọ́run rẹ̀, ó jẹ̀bi."'), 'Lev$4:27': ('ṣe', None, 'Lev$4:27 " “ ‘Bí ènìyàn nínú àwọn ará ìlú bá ṣèèṣì ṣẹ̀ láìmọ̀ tí ó sì se ohun tí kò yẹ sí ọkan nínú àwọn òfin OLÚWA, ó jẹ̀bi."'), 'Lev$4:31': (None, 'ṣe', 'Lev$4:31 "Kí ó yọ gbogbo ọ̀rá ẹran náà gẹ́gẹ́ bó ṣe yọ ọ̀rá ẹran fún ọrẹ àlàáfíà. Kí àlùfáà sì sun ọ̀rá yìí lórí pẹpẹ bí òórùn dídùn sí OLÚWA. Báyìí ni àlùfáà yóò ṣe ètùtù fún ẹni náà, a ó sì dárí jìn-ín."'), 'Lev$4:34': ('ṣe', None, 'Lev$4:34 "Àlùfáà yóò sì ti ìka rẹ̀ bọ inú ẹ̀jẹ̀ ẹbọ ẹ̀ṣẹ̀ náà, yóò sì fi sí orí ìwo pẹpẹ ẹbọ sísun, yóò sì da ìyókù ẹ̀jẹ̀ náà sí ìṣàlẹ̀ pẹpẹ."'), 'Lev$5:1': ('ri', None, 'Lev$5:1 "“ ‘Bí ẹnìkan bá dẹ́ṣẹ̀ nítorí pé kò sọ̀rọ̀ nígbà tí a bi í ní gbangba pé kó jẹ̀ẹ́rí nípa nǹkan tó rí tàbí nǹkan tó mọ̀ nípa ìṣẹ̀lẹ̀ kan, a ó di ẹ̀bi rẹ̀ rù ú."'), 'Lev$5:12': (None, None, 'Lev$5:12 "Kí ó gbé e wá sí ọ̀dọ̀ àlùfáà, àlùfáà yóò sì bu ẹ̀kúnwọ́ ìyẹ̀fun gẹ́gẹ́ bí ẹbọ ìrántí, kí ó sì sun ún lórí pẹpẹ, gẹ́gẹ́ bí ẹbọ tí a fi iná sun sí OLÚWA. Ẹbọ ẹ̀ṣẹ̀ ni"'), 'Lev$5:16': ('fun', 'ṣe', 'Lev$5:16 "Kí ó ṣe àtúnṣe nítorí ohun tí ó ti kùnà láti ṣe ní ti àwọn ohun mímọ́ OLÚWA, kí ó sì fi ìdá márùn-ún kún iye ohun tí ó kùnà láti ṣe nípa ohun mímọ́ OLÚWA kí ó sì kó gbogbo rẹ̀ fún àlùfáà, ẹni tí yóò ṣe ètùtù fún un pẹ̀lú àgbò fún ẹbọ ẹ̀bi, a ó sì dárí jìn-ní."'), 'Lev$6:1': ('fun', None, 'Lev$6:1 OLÚWA sọ fún Mósè pé:'), 'Lev$6:10': (None, None, 'Lev$6:10 "kí àlùfáà sì wọ èwù funfun rẹ̀ pẹ̀lú àwọ̀tẹ́lẹ̀ lára rẹ̀, yóò sì kó eérú tó wà nibi ẹbọ sísun tí iná ti jó lórí pẹpẹ, sí ẹ̀gbẹ́ pẹpẹ."'), 'Lev$6:12': (None, None, 'Lev$6:12 "Iná tó wà lórí pẹpẹ gbọdọ̀ máa jó, kò gbọdọ̀ kú, ní àràárọ̀ ni kí àlùfáà máa to igi si, kí ó sì to ẹbọ sísun sórí iná, kí ó sì máa sun ọ̀rá ẹran ẹbọ àlàáfíà níbẹ̀."'), 'Lev$7:3': (None, None, 'Lev$7:3 "Gbogbo ọ̀rá rẹ̀ ni kí ẹ sun, ọ̀rá ìrù rẹ̀ àti ọ̀rá tó bo nǹkan inú rẹ̀."'), 'Lev$7:12': ('ororo', None, 'Lev$7:12 "“ ‘Bí ẹni náà bá gbé e wá gẹ́gẹ́ bí àfihàn ọkàn ọpẹ́, pẹ̀lú ẹbọ ọpẹ́ yìí, ó gbọdọ̀ mú àkàrà aláìwú tí a fi òróró pò wá àti àkàrà aláìwú fẹ́lẹ́fẹ́lẹ́ tí a dà òróró sí àti àkàrà tí a fi ìyẹ̀fun kíkúná dáradára tí a fi òróró pò ṣe."'), 'Lev$7:14': (None, 'ọrẹ', 'Lev$7:14 "kí ó mú ọ̀kọ̀ọ̀kan nínú oríṣìíríṣìí ọrẹ àlàáfíà rẹ wá gẹ́gẹ́ bí ẹ̀bùn fún OLÚWA, ó jẹ́ tí àlùfáà tí ó wọ́n ẹ̀jẹ̀ ọrẹ àlàáfíà."'), 'Lev$7:18': ('gba', None, 'Lev$7:18 "Bí ẹ bá jẹ ọ̀kankan nínú ẹran ọrẹ àlàáfíà ní ọjọ́ kẹta, kò ni jẹ́ ìtẹ́wọ́gbà. A kò ní í kà á sí fún ẹni tó rúbọ náà, nítorí pé ó jẹ́ àìmọ́, ẹnikẹ́ni tó bá sì jẹ ẹ́, ni a ó di ẹ̀bi rẹ̀ rù."'), 'Lev$7:21': (None, None, 'Lev$7:21 "Bí ẹnikẹ́ni bá fọwọ́ kan ohun aláìmọ́ yálà ohun àìmọ́ ti ènìyàn, ẹranko aláìmọ́, ohun àìmọ́ yówù kí ó jẹ́ tàbí ohunkóhun tí ó jẹ́ ìríra, tí ó sì tún jẹ́ ẹran ọrẹ àlàáfíà tó jẹ́ ti OLÚWA, a ó gé irú ẹni bẹ́ẹ̀ kúrò lára àwọn ènìyàn rẹ.’ ”"'), 'Lev$7:28': (None, None, 'Lev$7:28 OLÚWA sọ fún Mósè pé:'), 'Lev$7:34': (None, 'ni', 'Lev$7:34 "Nínú ọrẹ àlàáfíà àwọn ọmọ Ísírẹ́lì, mo ti ya igẹ̀ tí a fi àti itan tí ẹ mú wá sọ́tọ̀ fún Árónì àlùfáà àti àwọn ọmọ rẹ̀ gẹ́gẹ́ bí ìpín wọn ní gbogbo ìgbà láti ọ̀dọ̀ ara Ísírẹ́lì.’ ”"'), 'Lev$8:7': ('aṣọ', 'aṣọ', 'Lev$8:7 "Ó sì fi aṣọ àwọ̀tẹ́lẹ̀ wọ Árónì, ó fi àmùrè dì í, ó wọ̀ ọ́ ní aṣọ ìgúnwà, ó sì tún wọ̀ ọ́ ni aṣọ ìlékè oyè àlùfáà tí ó ní ìgbànú tí a ṣe ọnà dáradára sí."'), 'Lev$8:9': (None, 'iwaju', 'Lev$8:9 "Ó dé e ní fìlà, ó sì fi àwo wúrà tíí ṣe adé mímọ́ ṣíwájú rẹ̀ gẹ́gẹ́ bí OLÚWA ti pàṣẹ fún Mósè."'), 'Lev$8:10': ('ororo', None, 'Lev$8:10 Mósè sì fi òróró ìtasórí ya àgọ́ àti ohun gbogbo tó wà nínú rẹ̀ sí mímọ́.'), 'Lev$8:12': ('ororo', None, 'Lev$8:12 "ó da díẹ̀ lára òróró ìtasórí yìí sórí Árónì, ó sì yà á sí mímọ́."'), 'Lev$8:15': (None, 'ṣe', 'Lev$8:15 "Mósè pa akọ màlúù náà, ó sì ti ìka bọ inú ẹ̀jẹ̀ náà, ó fi sí orí gbogbo ìwo pẹpẹ láti wẹ pẹpẹ náà mọ́. Ó da ìyóòkù ẹ̀jẹ̀ náà sí ìṣàlẹ̀ pẹpẹ. Bẹ́ẹ̀ ní ó ṣe yà á sí mímọ́ láti ṣe ètùtù fún un."'), 'Lev$8:20': (None, 'ge', 'Lev$8:20 "Ó gé àgbò náà sí wẹ́wẹ́, ó sì sun orí àti àwọn ègé àti ọ̀rá rẹ̀."'), 'Lev$10:7': ('lọ', 'kú', 'Lev$10:7 "Ẹ má ṣe kúrò ní ẹnu ọ̀nà àgọ́ ìpàdé, bí ẹ bá kúrò níbẹ̀, ẹ ó kùú, nítorí pé òróró ìtasórí OLÚWA wà lórí yín” Wọ́n sì ṣe bí Mósè ti wí."'), 'Lev$10:8': (None, None, 'Lev$10:8 OLÚWA sì sọ fún Árónì pé.'), 'Lev$10:16': (None, 'ni', 'Lev$10:16 "Nígbà tí Mósè wádìí nípa ewúrẹ́ ẹbọ ẹ̀ṣẹ̀ tó sì rí i pé wọ́n ti sun ún, ó bínú sí Élíásárì àti Ítamárì, àwọn ọmọ Árónì yóòkù, ó sì bèèrè pé,"'), 'Lev$10:19': (None, 'ni', 'Lev$10:19 "Árónì sì dá Mósè lóhùn pé, “Lónìí tí wọ́n rú ẹbọ ẹ̀ṣẹ̀ àti ẹbọ sísun wọn níwájú OLÚWA ni irú èyí tún sẹlẹ̀ sí mi. Ǹjẹ́ inú OLÚWA yóò wá dùn bí mo bá jẹ ẹbọ ẹ̀ṣẹ̀ lónìí?”"'), 'Lev$11:4': (None, None, 'Lev$11:4 "“ ‘Àwọn mìíràn wà tó ń jẹ àpọ̀jẹ nìkan. Àwọn mìíràn wà tó jẹ́ pé pátakò ẹsẹ̀ wọn nìkan ni ó là, ìwọ̀nyí ni ẹ kò gbọdọ̀ jẹ fún àpẹrẹ bí ó tilẹ̀ jẹ́ pé ràkunmí ń jẹ àpọ̀jẹ kò ya pátakò ẹsẹ̀, àìmọ́ ni èyí jẹ́."'), 'Lev$11:29': (None, 'eku', 'Lev$11:29 "“ ‘Nínú gbogbo ẹranko tí ń rìn lórí ilẹ̀ ìwọ̀nyí ni ó jẹ́ àìmọ́ fún yín: Asé, eku àti orísirísi aláǹgbá,"'), 'Lev$11:35': ('fifọ', None, 'Lev$11:35 Gbogbo ohun tí èyíkéyìí nínú òkú wọn bá já lé lórí di àìmọ́. Yálà ààrò ni tàbí ìkòkò ìdáná wọn gbọdọ̀ di fífọ́. Wọ́n jẹ́ àìmọ́. Ẹ sì gbọdọ̀ kà wọ́n sí àìmọ́.'), 'Lev$12:1': (None, None, 'Lev$12:1 " OLÚWA sọ fún Mósè pé,"'), 'Lev$13:1': (None, None, 'Lev$13:1 "OLÚWA sọ fún Mósè àti Árónì pé,"'), 'Lev$13:4': ('funfun', None, 'Lev$13:4 "Bí àpá ara rẹ̀ bá funfun tí ó sì dàbí ẹni pé kò jinlẹ̀ jù awọ ara, tí irun rẹ̀ kò sì yípadà sí funfun kí àlùfáà fi ẹni náà pamọ́ fún ọjọ́ méje."'), 'Lev$13:12': (None, None, 'Lev$13:12 "“Bí àrùn náà bá ràn yíká gbogbo awọ ara rẹ̀ tí àlùfáà sì yẹ̀ ẹ́ wò, tí ó rí i pé àrùn náà ti gba gbogbo ara rẹ̀ láti orí títí dé ẹṣẹ̀,"'), 'Lev$13:16': (None, 'funfun', 'Lev$13:16 "Bí ẹran ara rẹ̀ bá yípadà sí funfun, kí o farahan àlùfáà."'), 'Lev$13:18': (None, None, 'Lev$13:18 “Bí oówo bá mú ẹnikẹ́ni nínú ara rẹ̀ tí ó sì san.'), 'Lev$13:38': ('funfun', None, 'Lev$13:38 “Bí ọkùnrin tàbí obìnrin kan bá ní ojú àpá funfun lára àwọ̀ ara rẹ̀.'), 'Lev$13:47': (None, None, 'Lev$13:47 “Bí àrùn ẹ̀tẹ̀ bá ba aṣọ kan jẹ́ yálà aṣọ onírun àgùtàn tàbí aṣọ funfun.'), 'Lev$14:1': (None, None, 'Lev$14:1 "OLÚWA sọ fún Mósè pé,"'), 'Lev$14:11': (None, None, 'Lev$14:11 Àlùfáà tí ó pè é ní mímọ́: yóò mú ẹni náà tí a ó sọ di mímọ́ àti ọrẹ rẹ̀ wá ṣíwájú OLÚWA ní ẹnu ọ̀nà àgọ́ ìpàdé.'), 'Lev$14:21': ('ṣe', None, 'Lev$14:21 "“Bí ẹni náà bá jẹ́ tálákà tí kò sì le è kó gbogbo nǹkan wọ̀nyí wá, kí ó mú ọ̀dọ́ àgbò kan bí ẹbọ ẹbí, tí yóò fì, láti ṣe ètùtù fún un, pẹ̀lú ìdámẹ́wá òṣùwọ̀n ìyẹ̀fun dídára, a pò pọ̀ mọ́ òróró fún ọrẹ ohun jíjẹ òṣùwọ̀n òróró"'), 'Lev$14:24': (None, None, 'Lev$14:24 Àlùfáà náà yóò sì mú ọ̀dọ́ àgùntàn ẹbọ ẹ̀bi pẹ̀lú òróró yóò sì fì wọ́n níwájú OLÚWA bí ẹbọ fífì.'), 'Lev$14:30': (None, 'gba', 'Lev$14:30 Lẹ́yìn náà kí ó fí àdàbà tàbí ọmọ ẹyẹlé rúbọ níwọn ti agbára rẹ̀ mọ.'), 'Lev$14:33': ('ṣe', None, 'Lev$14:33 OLÚWA sọ fún Mósè àti Árónì pé.'), 'Lev$14:49': (None, None, 'Lev$14:49 Láti sọ ilé yìí di mímọ́. Àlùfáà yóò mú ẹyẹ méjì àti igi sídà òdòdó àti hísópù.'), 'Lev$15:11': ('wẹ', 'wẹ', 'Lev$15:11 "“ ‘Ẹnikẹ́ni tí ọkùnrin tí ó ní ìṣunjáde ní ara rẹ̀ bá farakàn láì fi omi fọ ọwọ́ rẹ̀, gbọdọ̀ fọ aṣọ rẹ̀ kí ó sì wẹ̀. Yóò sì wà ní ipò àìmọ́ títí ìrọ̀lẹ́."'), 'Lev$15:13': (None, None, 'Lev$15:13 "“ ‘Ẹnikẹ́ni tí a bá wẹ̀ nù kúrò nínú ìṣunjáde rẹ̀ gbọdọ̀ ka ọjọ́ méje fún àwọn ọjọ́ ìwẹ̀nùmọ́: kí ó fọ aṣọ rẹ̀, kí ó sì fi omi wẹ ara rẹ̀: yóò sì di mímọ́."'), 'Lev$15:14': (None, None, 'Lev$15:14 "Ní ọjọ́ kẹjọ kí ó mú àdàbà méjì tàbí ọmọ ẹyẹlé méjì, kí ó sì wá sí ẹnu ọ̀nà àgọ́ ìpàdé níwájú OLÚWA, kí ó sì kó wọn fún àlùfáà."'), 'Lev$15:28': (None, None, 'Lev$15:28 "“ ‘Nígbà tí ó bá di mímọ́ kúrò nínú ìsun rẹ̀, kí ó ka ọjọ́ méje lẹ́yìn náà, a ó kà á sí mímọ́."'), 'Lev$15:29': (None, 'mú', 'Lev$15:29 Ní ọjọ́ kẹjọ kí ó mú àdàbà méjì àti ọmọ ẹyẹlé wá ṣíwájú àlùfáà ní ẹnu ọ̀nà àgọ́ ìpàdé.'), 'Lev$16:1': (None, None, 'Lev$16:1 OLÚWA sọ fún Mósè lẹ́yìn ikú àwọn ọmọ Árónì méjèèje tí wọ́n kú nígbà tí wọ́n súnmọ́ OLÚWA.'), 'Lev$16:4': (None, None, 'Lev$16:4 "Òun yóò sì wọ aṣọ funfun mímọ́ pẹ̀lú àwọ̀tẹ́lẹ̀ funfun, yóò sì fi àmùrè funfun mímọ́ dì í: yóò sì dé fìlà funfun: àwọn aṣọ mímọ́ nìwọ̀nyí: Òun yóò sì fi omi wẹ̀, kí ó tó wọ̀ wọ́n."'), 'Lev$16:11': ('ṣe', None, 'Lev$16:11 Árónì yóò sì mú akọ màlúù fún ẹbọ ẹ̀ṣẹ̀ wá láti ṣe ètùtù fún ara rẹ̀ àti ìdílé rẹ̀. Yóò sì pa akọ màlúù náà fún ẹbọ ẹ̀ṣẹ̀ tirẹ̀.'), 'Lev$16:15': (None, None, 'Lev$16:15 " Nígbà yìí ni yóò pa ewúrẹ́ ẹbọ ẹ̀ṣẹ̀ tí ó wà fún àwọn ènìyàn yóò sì mú ẹ̀jẹ̀ rẹ̀ wá sí ẹ̀yìn aṣọ títa, bí ó ti ṣe ẹ̀jẹ̀ akọ màlúù náà ni yóò ṣe ti ewúrẹ́ yìí, yóò sì wọ́n ọn sórí ìtẹ́ ètùtù àti ṣíwájú ìtẹ́ ètùtù."'), 'Lev$16:17': ('ni', 'ṣe', 'Lev$16:17 Kí ó má ṣe sí ẹyọ ènìyàn kan nínú àgọ́ ìpàdé nígbà tí òun bá wọ ibi mímọ́ lọ láti ṣe ètùtù: títí di ìgbà tí yóò fi jáde lẹ́yìn tí ó ba ṣe ètùtù fúnrarẹ̀ fún ìdílé rẹ̀ àti fún gbogbo àgbájọpọ̀ Ísírẹ́lì.'), 'Lev$16:18': (None, 'ṣe', 'Lev$16:18 "Lẹ́yìn náà òun yóò lọ sí ibi pẹpẹ tí ó wà níwájú OLÚWA: yóò sì ṣe ètùtù fún pẹpẹ náà. Yóò sì mú nínú ẹ̀jẹ̀ akọ màlúù àti nínú ẹ̀jẹ̀ ewúrẹ́ náà, yóò sì fi sí orí ọ̀kọ̀ọ̀kan ìwo pẹpẹ náà yíká."'), 'Lev$16:20': ('lọ', None, 'Lev$16:20 "Lẹ́yìn ti Árónì ti parí ṣíṣe ètùtù ti ibi mímọ́ jùlọ, ti àgọ́ ìpàdé àti ti pẹpẹ: òun yóò sì mú ààyè ewúrẹ́ wá."'), 'Lev$16:29': ('ṣe', 'iṣẹ', 'Lev$16:29 " Èyí ni yóò jẹ́ ìlànà títí láé fún yín: pé ní ọjọ́ kẹwàá oṣù keje ni ẹ gbọdọ̀ ṣẹ́ ara yín: kí ẹ má ṣe ṣe iṣẹ́ kankan, yálà onílé tàbí àlejò tí ó ń gbé pẹ̀lú yín."'), 'Lev$17:11': ('ni', 'ṣe', 'Lev$17:11 Nítorí pé nínú ẹ̀jẹ̀ ni ẹ̀mí ẹ̀dá wà èmi sì ti fi fún yín lórí pẹpẹ fún ètùtù ọkàn yín: nítorí pé ẹ̀jẹ̀ ni a fi ń ṣe ètùtù fún ẹ̀mí ènìyàn.'), 'Lev$18:3': ('mú', 'ilẹ', 'Lev$18:3 Ẹ kò gbọdọ̀ ṣe bí wọ́n ti ń ṣe ní Éjíbítì níbi tí ẹ ti gbé rí: bẹ́ẹ̀ ni ẹ kò sì gbọdọ̀ ṣe bí wọ́n ti ń ṣe ní ilẹ̀ Kénánì níbi tí èmi ń mú yín lọ. Ẹ kò gbọdọ̀ tẹ̀lé iṣe wọn.'), 'Lev$18:5': (None, 'gbe', 'Lev$18:5 " Ẹ pa àṣẹ àti òfin mi mọ́. Ẹni tí ó bá ṣe ìgbọ́ràn sí wọn yóò máa gbé nípa wọn, Èmi ni OLÚWA."'), 'Lev$18:26': ('ilẹ', None, 'Lev$18:26 Ṣùgbọ́n kí ẹ máa pa àṣẹ àti òfin mi mọ́. Onílé tàbí àlejò tó ń gbé láàrin yín kò gbọdọ̀ ṣe ọ̀kan nínú àwọn ohun ìríra wọ̀nyí.'), 'Lev$19:25': ('eso', None, 'Lev$19:25 "Ní ọdún karùn ún ni ẹ̀yin tó lè jẹ nínú èso igi náà, kí èso wọn ba à le máa pọ̀ sí i. Èmi ni OLÚWA Ọlọ́run yín."'), 'Lev$19:36': ('mu', 'ilẹ', 'Lev$19:36 "Ẹ jẹ́ olódodo pẹ̀lú àwọn òsùwọ̀n yín òsùwọ̀n ìtẹ̀wọ̀n, òsùwọ̀n wíwúwo, òsùwọ̀n ìyẹ̀fun àti òsùwọ̀n nǹkan olómi yín ní láti jẹ́ èyí tí kò ní èrú nínú. Èmi ni OLÚWA Ọlọ́run yín tí ó mú yín jáde láti ilẹ̀ Éjíbítì."'), 'Lev$20:4': (None, None, 'Lev$20:4 Bí àwọn ènìyàn ilẹ̀ náà bá mójú kúrò lára irú ẹni bẹ́ẹ̀ tí ó fi ọ̀kan nínú àwọn ọmọ rẹ̀ fún òrìṣà mólékì tí wọn kò sì pa irú ẹni bẹ́ẹ̀.'), 'Lev$20:13': ('ṣe', None, 'Lev$20:13 " “ ‘Bí ọkùnrin kan bá bá ọkùnrin mìíràn lòpọ̀ bí wọ́n í ti bá obìnrin lòpọ̀: àwọn méjèèjì ti ṣe ohun ìríra: pípa ni kí ẹ pa wọ́n, ẹ̀jẹ̀ wọn yóò wà lórí wọn."'), 'Lev$20:22': (None, 'ilẹ', 'Lev$20:22 "“ ‘Kí ẹ pa gbogbo àṣẹ àti òfin mi mọ́, kí ẹ sì máa ṣe wọ́n. Kí ilẹ̀ náà tí èmi ó fi fún yín láti máa gbé má baà pọ̀ yín jáde."'), 'Lev$20:24': ('ni', None, 'Lev$20:24 "Ṣùgbọ́n mo ti sọ fún yín pé, “Ẹ̀yin ni yóò jogún ilẹ̀ wọn; Èmi yóò sì fi fún yín láti jogún ilẹ̀ tí ń sàn fún wàrà àti fún oyin.” Èmi ni OLÚWA Ọlọ́run yín tí ó ti yà yín sọ́tọ̀ kúrò láàrin àwọn ènìyàn yòókù."'), 'Lev$20:25': ('ṣe', 'ni', 'Lev$20:25 “ ‘Ẹ gbọdọ̀ pààlà sáàrin ẹran tí ó mọ́ àti àwọn tí kò mọ́. Láàrin ẹyẹ tí ó mọ́ àti àwọn tí kò mọ́. Ẹ má ṣe sọ ara yín di àìmọ́ nípasẹ̀ àwọn ẹranko tàbí ẹyẹ tàbí ohunkohun tí ń rìn lórí ilẹ̀: èyí tí mo yà sọ́tọ̀ fún yín gẹ́gẹ́ bí ohun àìmọ́.'), 'Lev$21:6': (None, 'orukọ', 'Lev$21:6 "Wọ́n gbọdọ̀ jẹ́ mímọ́ fún Ọlọ́run, kí wọ́n má baà sọ orúkọ Ọlọ́run wọn di àìmọ́, nítorí pé àwọn ni wọ́n ń fí iná sun ẹbọ sí OLÚWA wọn, ti ó jẹ oúnjẹ Ọlọ́run wọn. Nítorí náà wọn gbọdọ̀ jẹ́ mímọ́."'), 'Lev$21:18': (None, None, 'Lev$21:18 "Ẹnikẹ́ni tí ó bá ní àbùkù kò gbọdọ̀ sún mọ́ ibi ẹbọ náà. Kò gbọdọ̀ sí afọ́jú tàbí arọ, alábùkù ara tàbí àwọn tí ara wọn kò pé."'), 'Lev$21:23': ('ṣe', None, 'Lev$21:23 "Nípa ṣíṣe bẹ́ẹ̀ yóò ba ibi mímọ́ mi jẹ́ torí pé, Èmi OLÚWA ló ti sọ wọ́n di mímọ́.’ ”"'), 'Lev$22:15': (None, None, 'Lev$22:15 Àwọn àlùfáà kò gbọdọ̀ sọ ọrẹ mímọ́ tí àwọn ọmọ Ísírẹ́lì mú wá ṣíwájú OLÚWA di àìmọ́.'), 'Lev$22:17': (None, None, 'Lev$22:17 "OLÚWA sọ fún Mósè pé,"'), 'Lev$22:22': (None, 'ṣe', 'Lev$22:22 "Ẹ má ṣe fi ẹranko tí ó fọ́jú, tí ó farapa, tí ó yarọ, tí ó ní koko, tí ó ní èkúkú, èépá àti egbò kíkẹ̀ rúbọ sí OLÚWA. Ẹ kò gbọdọ̀ gbé èyíkeyi nínú wọn sórí pẹpẹ gẹ́gẹ́ bi ọrẹ tí a fi iná ṣe sí OLÚWA."'), 'Lev$22:32': (None, None, 'Lev$22:32 Ẹ má ṣe ba orúkọ mímọ́ mi jẹ́. Àwọn ọmọ Ísírẹ́lì gbọdọ̀ mọ̀ mí ní mímọ́. Èmi ni OLÚWA tí ó sọ yín di mímọ́.'), 'Lev$23:8': ('ṣe', 'iṣẹ', 'Lev$23:8 "Ọjọ́ méje ni kí ẹ fi mú ọrẹ tí a fi iná ṣe wá fún OLÚWA. Ní ọjọ́ keje, ẹ pe ìpàdé àjọ mímọ́, ẹ kò gbọdọ̀ ṣe iṣẹ́ ojúmọ́.’ ”"'), 'Lev$23:9': (None, None, 'Lev$23:9 OLÚWA sọ fún Mósè pe'), 'Lev$23:25': ('ṣe', 'iṣẹ', 'Lev$23:25 "Ẹ má ṣe ṣe iṣẹ́ ojúmọ́, ṣùgbọ́n ẹ mú ọrẹ tí a fi iná sun wá ṣíwájú OLÚWA.’ ”"'), 'Lev$23:28': ('ṣe', 'iṣẹ', 'Lev$23:28 "Ẹ má ṣe ṣe iṣẹ́ kankan ní ọjọ́ náà, torí pé ọjọ́ ètùtù ni, nínú èyí tí a ń ṣe ètùtù fún yín níwájú OLÚWA Ọlọ́run yín."'), 'Lev$23:36': ('ṣe', 'iṣẹ', 'Lev$23:36 "Ọjọ́ méje ni kí ẹ fi mú ọrẹ tí a fi iná ṣe wá, ní ọjọ́ kẹjọ ẹ pe ìpàdé mímọ́ kí ẹ sì mú ọrẹ tí a fi iná ṣe wá fún OLÚWA, èyí jẹ́ ìpàdé tí ó gbẹ̀yìn; ẹ má ṣe ṣe iṣẹ́ ojojúmọ́."'), 'Lev$23:37': ('fun', None, 'Lev$23:37 "(“ ‘Ìwònyí ni àwon àjọ̀dún tí OLÚWA ti yàn tí ẹ gbọdọ̀ kéde gẹ́gẹ́ bí i ìpàdé mímọ́ fún mímu àwọn ọrẹ tí a fi iná ṣe sí OLÚWA wá—ọrẹ sísun àti ọrẹ ọkà, ọrẹ ẹbọ àti ọrẹ ohun mímu, fún ọjọ́ kọ̀ọ̀kan:"'), 'Lev$23:42': (None, None, 'Lev$23:42 Ẹ gbé inú àgọ́ fún ọjọ́ méje. Kí gbogbo ọmọbíbí Ísírẹ́lì gbé nínú àgọ́.'), 'Lev$24:19': ('ṣe', None, 'Lev$24:19 Bí ẹnìkan bá pa ẹnìkejì rẹ̀ lára: ohunkóhun tí ó ṣe ni kí ẹ ṣe sí i.'), 'Lev$24:22': ('ni', None, 'Lev$24:22 Òfin kan náà ló wà fún àlejò àti fún ọmọ Ísírẹ́lì. Èmi ni OLÚWA Ọlọ́run yín.’ ”'), 'Lev$25:9': (None, None, 'Lev$25:9 "Lẹ́yìn náà, kí ẹ fọn fèrè ní gbogbo ibikíbi ní ọjọ́ kẹwàá oṣù kéje ní ọjọ́ ìwẹ̀nùmọ́. Ní ọjọ́ ètùtù yìí, ẹ fọn fèrè yíká gbogbo ilẹ̀ yín."'), 'Lev$25:19': ('eso', None, 'Lev$25:19 "Nígbà yìí ni ilẹ̀ náà yóò so èso rẹ̀, ẹ ó sì jẹ àjẹyó, ẹ ó sì máa gbé láìléwu."'), 'Lev$25:26': (None, 'ni', 'Lev$25:26 "Ẹni tí kò ní ẹni tó lè ràápadà fún un, tí òun fúnrarẹ̀ sì ti lọ́rọ̀, tí ó sì ní ànító láti rà á."'), 'Lev$25:35': (None, 'gbe', 'Lev$25:35 “ ‘Bí arákùnrin yín kan bá talákà tí kò sì le è pèsè fún àìní ara rẹ̀: ẹ pèsè fún un bí ẹ ti ń ṣe fún àwọn àlejò tàbí àwọn tí ẹ gbà sílé: kí ó baà leè máa gbé láàrin yín.'), 'Lev$25:38': ('mu', 'ilẹ', 'Lev$25:38 Èmi ni OLÚWA Ọlọ́run yín tí ó mú yín jáde láti ilẹ̀ Éjíbítì láti fún yín ní ilẹ̀ Kénánì àti láti jẹ́ Ọlọ́run yín.'), 'Lev$25:42': ('mu', 'ilẹ', 'Lev$25:42 "Torí pé ìránṣẹ́ mi ni àwọn ará Ísírẹ́lì jẹ́. Ẹni tí mo mú jáde láti ilẹ̀ Éjíbítì wá, torí èyí ẹ kò gbọdọ̀ tà wọ́n lẹ́rú."'), 'Lev$25:55': ('mu', None, 'Lev$25:55 "Torí pé ìránṣẹ́ ni àwọn ọmọ Ísírẹ́lì jẹ́ fún mi. Ìránṣẹ́ mi ni wọ́n, tí mo mú jáde láti Éjíbítì wá. Èmi ni OLÚWA Ọlọ́run yín."'), 'Lev$26:3': (None, None, 'Lev$26:3 " “ ‘Bí ẹ̀yin bá tẹ̀lé àṣẹ mi, tí ẹ̀yin sì sọ́ra láti pa òfin mi mọ́."'), 'Lev$26:6': ('fun', None, 'Lev$26:6 "“ ‘Èmi yóò fún yín ní àlàáfíà ní ilẹ̀ yín, ẹ̀yin yóò sì sùn láìsí ìbẹ̀rù ẹnikẹ́ni. Èmi yóò mú kí gbogbo ẹranko búburú kúrò ní ilẹ̀ náà: bẹ́ẹ̀ ni kì yóò sì sí ogun mọ́."'), 'Lev$26:9': (None, None, 'Lev$26:9 "“ ‘Èmi yóò fi ojú rere wò yín: Èmi yóò mú kí ẹ bí sí i, èmi yóò jẹ́ kí ẹ pọ̀ sí i: Èmi yóò sì pa májẹ̀mú mi mọ́ pẹ̀lú yín."'), 'Lev$26:13': ('mu', 'ilẹ', 'Lev$26:13 Èmi ni OLÚWA Ọlọ́run yín tí ó mú yín jáde kúrò ní ilẹ̀ Éjíbítì: kí ẹ̀yin má baà jẹ́ ẹrú fún wọn mọ́. Mo sì já ìdè yín: mo sì mú kí ẹ̀yin máa rìn gẹ́gẹ́ bí ẹni tí a gbé lórí sókè.'), 'Lev$26:14': (None, None, 'Lev$26:14 “ ‘Bí ẹ̀yin kò bá fetí sí mi tí ẹ̀yin kò sì ṣe gbogbo òfin wọ̀nyí.'), 'Lev$26:19': (None, 'ilẹ', 'Lev$26:19 Èmi yóò fọ́ agbára ìgbéraga yín: ojú ọ̀run yóò le koko bí irin: ilẹ̀ yóò sì le bí idẹ (òjò kò ní rọ̀: ilẹ̀ yín yóò sì le).'), 'Lev$26:26': ('ni', None, 'Lev$26:26 "Èmi yóò dá ìpèsè oúnjẹ yín dúró, débi pé: inú àrò kan ni obìnrin mẹ́wàá yóò ti máa ṣe oúnjẹ yín. Òṣùwọ̀n ni wọn yóò fi máa yọ oúnjẹ yín: Ẹ ó jẹ ṣùgbọ́n, ẹ kò ní yó."'), 'Lev$26:32': (None, 'ilẹ', 'Lev$26:32 Èmi yóò pa ilẹ̀ yín run débi pé ẹnu yóò ya àwọn ọ̀ta yín tí ó bá gbé ilẹ̀ náà.'), 'Lev$26:40': (None, None, 'Lev$26:40 " “ ‘Bí wọ́n bá jẹ́wọ́ ẹ̀ṣẹ̀ wọn àti ti babańlá wọn, ìwà ìṣọ̀tẹ̀ wọn àti bí wọ́n ti ṣe lòdì sí mi."'), 'Lev$26:45': ('mu', 'ilẹ', 'Lev$26:45 "Nítorí wọn, èmi ó rántí májẹ̀mú mi tí mo ti ṣe pẹ̀lú babańlá wọn. Àwọn tí mo mú jáde kúrò ní ilẹ̀ Éjíbítì lójú gbogbo àwọn orílẹ̀ èdè láti jẹ́ Ọlọ́run wọn. Èmi ni OLÚWA.’ ”"'), 'Lev$27:9': (None, None, 'Lev$27:9 "“ ‘Bí ẹ̀jẹ́ tí ó jẹ́ bá jẹ́ ẹranko èyí tí ó jẹ́ ìtẹ́wọ̀gbà gẹ́gẹ́ bí ọrẹ fún OLÚWA, irú ẹran bẹ́ẹ̀ tí a bá fi fún OLÚWA di mímọ́."'), 'Lev$27:14': (None, None, 'Lev$27:14 “ ‘Bí ẹnikẹ́ni bá ya ilẹ̀ rẹ̀ sí mímọ́ fún OLÚWA: kí àlùfáà kó sọ iye tí yóò san gẹ́gẹ́ bí ilẹ̀ náà ti dára tàbí bàjẹ́ sí: iye owó náà ni kí ó jẹ́.'), 'Num$1:48': (None, None, 'Num$1:48 "Nítorí OLÚWA ti sọ fún Mósè pé,"'), 'Num$2:34': ('ṣe', None, 'Num$2:34 "Àwọn ọmọ Ísírẹ́lì se gbogbo ohun tí OLÚWA pa láṣẹ fún Mósè, báyìí ni wọ́n ṣe pa ibùdó lábẹ́ ọ̀págun wọn, bẹ́ẹ̀ náà sì ni wọ́n ṣe jáde, oníkálùkù pẹ̀lú ẹbí àti ìdílé rẹ̀."'), 'Num$3:11': (None, None, 'Num$3:11 " OLÚWA tún sọ fún Mósè pé,"'), 'Num$4:7': (None, None, 'Num$4:7 "Lórí tabilì ìfihàn ni kí wọn ó na aṣọ aláró kan sí, wọn ó sì kó àwọn àwo, páànù, àwokòtò àti ìgò fún ọrẹ ohun mímu; àkàrà tó sì máa ń wà níbẹ̀ ní gbogbo ìgbà náà gbọ́dọ̀ wà níbẹ̀."'), 'Num$4:15': (None, 'ni', 'Num$4:15 "“Lẹ́yìn tí Árónì àti àwọn ọmọ rẹ̀ ti parí bíbo ibi mímọ́ àti gbogbo ohun èèlò ibi mímọ́, nígbà tí àgọ́ bá sì ṣetán láti tẹ̀ṣíwájú, kí àwọn ọmọ Kóhátì bọ́ ṣíwájú láti gbé e, ṣùgbọ́n wọn kò gbọdọ̀ fọwọ́ kan ohun mímọ́ kankan, bí wọ́n bá ṣe bẹ́ẹ̀ wọn ó kú. Àwọn ọmọ Kóhátì ni yóò gbé gbogbo ohun tó wà nínú Àgọ́ Ìpàdé."'), 'Num$4:17': (None, None, 'Num$4:17 "OLÚWA sọ fún Mósè àti Árónì pé,"'), 'Num$4:21': ('ṣe', 'ṣe', 'Num$4:21 OLÚWA sọ fún Mósè pé:'), 'Num$4:24': ('sin', 'iṣẹ', 'Num$4:24 "“Èyí ni iṣẹ́ ìsìn ìdílé àwọn ọmọ Gáṣónì, bí wọ́n ti ń ṣiṣẹ́ àti ní ẹrù rírù:"'), 'Num$4:27': ('yan', None, 'Num$4:27 "Gbogbo iṣẹ́ ìsìn àwọn ọmọ Gáṣónì yálà ni iṣẹ́ ṣíṣe tàbí ní ẹrù rírù ni, Árónì àti àwọn ọmọ rẹ̀ ni yóò máa darí wọn; ìwọ ni kí o sì yàn ẹrù tí oníkálùkù yóò rù fún un."'), 'Num$4:46': (None, 'ṣe', 'Num$4:46 "Báyìí ni Mósè, Árónì àti olórí ìjọ ènìyàn ka gbogbo ọmọ Léfì nípa ìdílé àti ilé baba wọn."'), 'Num$5:5': ('fun', None, 'Num$5:5 OLÚWA sọ fún Mósè pé:'), 'Num$5:8': (None, 'ni', 'Num$5:8 "Ṣùgbọ́n bí irú ẹni bẹ́ẹ̀ kò bá ní ìbátan tí ó sún mọ́ ọn tí ó lè ṣe àtúnṣe àṣìṣe rẹ̀ náà fún, àtúnṣe náà jẹ́ ti OLÚWA, ẹ sì gbọdọ̀ ko fún àlùfáà pẹ̀lú àgbò tí a fi ṣe ètùtù fún ẹni náà"'), 'Num$5:11': (None, None, 'Num$5:11 "OLÚWA sọ fún Mósè wí pé,"'), 'Num$5:16': (None, None, 'Num$5:16 “ ‘Àlùfáà yóò sì mú-un wá ṣíwájú OLÚWA.'), 'Num$5:27': ('ṣe', 'wá', 'Num$5:27 "Bí ó bá ti mú obìnrin yìí mu omi náà, bí ó bá sì jẹ́ pé obìnrin náà ti ba ara rẹ̀ jẹ́, tí ó sì se àìsòótọ́ sí ọkọ rẹ̀, omi tí ń mú ègún wá, yóò wọ ara rẹ̀, yóò fa ìrora kíkorò fún un, ikùn rẹ̀ yóò wú, itan rẹ̀ yóò sì jẹrà dànù, yóò sì di ẹni ègún láàrin àwọn ènìyàn rẹ̀."'), 'Num$6:13': (None, None, 'Num$6:13 “ ‘Èyí ni òfin fún Násírì nígbà tí àkókò ìyàsọ́tọ̀ rẹ̀ bá pé. Wọn ó mu wá sí ẹnu ọ̀nà Àgọ́ Ìpàdé.'), 'Num$6:22': ('ṣe', 'fun', 'Num$6:22 "OLÚWA sọ fún Mósè pé,"'), 'Num$7:1': (None, None, 'Num$7:1 "Nígbà tí Móṣè ti pári gbígbé àgọ́ dúró, ó ta òróró sí i, ó sì yà á sí mímọ́ pẹ̀lú gbogbo àwọn ohun èlò rẹ̀, Ó tún ta òróró sí pẹpẹ, ó sì yà á sí mímọ́ pẹ̀lú gbogbo ohun èlò rẹ̀."'), 'Num$7:7': ('fun', None, 'Num$7:7 "Ó fún àwọn ọmọ Gáṣónì ní kẹ̀kẹ́ méjì àti akọ màlúù mẹ́rin, gẹ́gẹ́ bí iṣẹ́ wọn ṣe jẹ mọ́."'), 'Num$7:12': (None, None, 'Num$7:12 Ẹni tí ó mú ọrẹ tirẹ̀ wá ní ọjọ́ kìn-ín-ni Náṣónì ọmọ Ámínádábù láti inú ẹ̀yà Júdà.'), 'Num$7:18': (None, None, 'Num$7:18 Ní ọjọ́ kéjì ni Nétaníẹ́lì ọmọ Súárì olórí àwọn ọmọ Ísákárì mú ọrẹ tirẹ̀ wá.'), 'Num$7:24': (None, None, 'Num$7:24 "Élíábù ọmọ Hélónì, olórí àwọn ọmọ Sébúlónì ni ó mú ọrẹ rẹ̀ wá ní ọjọ́ kẹ́ta."'), 'Num$7:30': (None, None, 'Num$7:30 Élíṣúrì ọmọ Ṣédéúrì olórí àwọn ọmọ Rúbẹ́nì ni ó mú ọrẹ wá ní ọjọ́ kẹrin.'), 'Num$7:36': (None, None, 'Num$7:36 "Ṣélúmíélì ọmọ Suriṣádáì, olórí àwọn ọmọ Símónì ni ó mú ọrẹ wá ní ọjọ́ karùn-ún."'), 'Num$7:42': (None, None, 'Num$7:42 Eliásáfì ọmọ Déúélì olórí àwọn ọmọ Gáádì ní ó mú ọrẹ rẹ̀ wá ní ọjọ́ kẹfà.'), 'Num$7:48': (None, None, 'Num$7:48 "Élísámà ọmọ Ámíhúdì, olórí àwọn ọmọ Éfúráímù ni ó mú ọrẹ rẹ̀ wá ní ọjọ́ keje."'), 'Num$7:54': (None, None, 'Num$7:54 "Gàmálíèlì ọmọ Pédáṣúrì, olórí àwọn ọmọ Mánásè ni ó mú ọrẹ tirẹ̀ wá ní ọjọ́ kẹjọ."'), 'Num$7:60': (None, None, 'Num$7:60 "Ábídánì ọmọ Gídíónì, olórí àwọn ọmọ Bẹ́ńjámínì ni ó mú ọrẹ tirẹ̀ wá ní ọjọ́ kẹsan-an."'), 'Num$7:66': (None, None, 'Num$7:66 "Áhíésérì ọmọ Ámíṣádáyì, olórí àwọn ọmọ Dánì ni ó mú ọrẹ tirẹ̀ wá ní ọjọ́ kẹwàá."'), 'Num$7:72': (None, None, 'Num$7:72 "Págíélì ọmọ Ókíránì, olórí àwọn ọmọ Áṣérì ni ó mú ọrẹ rẹ̀ wá ní ọjọ́ kọkànlá."'), 'Num$7:78': (None, None, 'Num$7:78 "Áhírà ọmọ Énánì, olórí àwọn Náfítanì ni ó mú ọrẹ rẹ̀ wá ní ọjọ́ kejìlá."'), 'Num$8:4': ('ṣe', None, 'Num$8:4 Bí a ṣe ṣe ọ̀pá fìtílà náà nìyìí: A ṣe é láti ara wúrà lílù: láti ìṣàlẹ̀ títí dé ibi ìtànná rẹ̀. Wọ́n ṣe ọ̀pá fìtílà náà gẹ́gẹ́ bí bátànìi tí OLÚWA fi han Mósè.'), 'Num$8:7': (None, None, 'Num$8:7 "Báyìí ni kí o ṣe wẹ̀ wọ́n mọ́: Wọ́n omi ìwẹ̀nùmọ́ sí wọn lára, mú kí wọn ó fá irun ara wọn, kí wọn ó fọ aṣọ wọn, kí wọn ó ba à lè wẹ ara wọn mọ́ nípa ṣíṣe bẹ́ẹ̀."'), 'Num$8:16': (None, None, 'Num$8:16 Nítorí pé àwọn ni ó jẹ́ ti Èmi pátapáta nínú àwọn ọmọ Ísírẹ́lì. Mo ti gbà wọ́n fún ara mi dípò àwọn àkọ́bí àní àkọ́bí ọkunrin gbogbo Ísírẹ́lì.'), 'Num$8:18': (None, 'ni', 'Num$8:18 Mo sì ti gba àwọn ọmọ Léfì dípò gbogbo àkọ́bí ọmọ ọkùnrin nínú Isireli'), 'Num$8:19': ('ni', 'ṣe', 'Num$8:19 "Nínú Ísírẹ́lì, mo fi àwọn ọmọ Léfì fún Árónì àti àwọn ọmọ rẹ̀ gẹ́gẹ́ bí ẹ̀bùn láti máa ṣiṣẹ́ nínú àgọ́ ìpàdé fun àwọn ọmọ Ísírẹ́lì àti láti máa ṣe ètùtù fún wọn kí àjàkálẹ̀-àrùn má ba à kọlu àwọn ọmọ Ísírẹ́lì nígbà tí wọ́n bá súnmọ́ ibi mímọ́.”"'), 'Num$8:23': ('ṣe', None, 'Num$8:23 "OLÚWA sọ fún Mósè pé,"'), 'Num$9:6': (None, 'eniyan', 'Num$9:6 Àwọn díẹ̀ nínú wọn kò lè ṣe àjọ Ìrékọjá lọ́jọ́ náà nítorí pé wọ́n di aláìmọ́ nítorí òkú ènìyàn. Nítorí èyí wọ́n wá sọ́dọ̀ Mósè àti Árónì lọ́jọ́ náà.'), 'Num$9:14': ('bi', 'ni', 'Num$9:14 “ ‘Bí àlejò tí ń gbé láàrin yín bá fẹ́ ṣe Àjọ Ìrékọjá ó gbọdọ̀ pa á mọ́ ní ìbámu pẹ̀lú ìlànà àti òfin rẹ̀. Ìlànà kan náà ni kí ẹ ní fún àlejò àti àwọn ọmọ bíbí ilẹ̀ yín.’ ”'), 'Num$9:21': (None, None, 'Num$9:21 "Ìgbà mìíràn ìkúùkù lè dúró láti ìrọ̀lẹ́ di àárọ̀, nígbà tó bá sì kúrò ní àárọ̀, wọn ó gbéra. Ìbá à ṣe ní ọ̀sán tàbí òru, ìgbàkigbà tí ìkúùkù bá tó kúrò náà ni wọn ó tó gbéra."'), 'Num$10:21': (None, None, 'Num$10:21 Nígbà náà ni àwọn ọmọ Kóhátì tí ń ru ohun mímọ́ náà gbéra. Àwọn ti àkọ́kọ́ yóò sì ti gbé tabánákù dúró kí wọn tó dé.'), 'Num$10:29': ('wá', 'ṣe', 'Num$10:29 "Mósè sì sọ fún Hóbábì ọmọ Réúélì ará Mídíánì tí í se àna Mósè pé, “A ń gbéra láti lọ sí ibi tí OLÚWA sọ pé, ‘Èmi ó fi fún un yín.’ Bá wa lọ àwa ó se ọ́ dáradára nítorí pé OLÚWA ti ṣèlérí ohun rere fún Ísírẹ́lì.”"'), 'Num$10:30': ('lọ', 'lọ', 'Num$10:30 "Ó sì dáhùn pé, “Rárá, èmi kò ní bá yín lọ, mò ń padà lọ sí ilẹ̀ mi àti sí ọ̀dọ̀ àwọn ènìyàn mi.”"'), 'Num$11:1': (None, 'ina', 'Num$11:1 "Àwọn ènìyàn ń ṣe àròyé nípa wàhálà wọn sí etíìgbọ́ OLÚWA. Ìbúnú OLÚWA sì ru sókè nígbà tí ó gbọ́ àròyé yìí, Nígbà náà ni iná jáde láti ọ̀dọ̀ OLÚWA bọ́ sí àárin wọn, ó sì run àwọn tó wà ní òpin ibùdó."'), 'Num$11:13': ('ni', 'fun', 'Num$11:13 "Níbo ni n ó ti rí ẹran fún àwọn ènìyàn wọ̀nyí? Nítorí wọ́n ń sunkún sí mi pé, ‘Fún wa lẹ́ran jẹ́!’"'), 'Num$11:15': (None, 'ni', 'Num$11:15 "Bí ó bá sì ṣe pé báyìí ni ìwọ ó ṣe máa ṣe fún mi, kúkú pa mí báyìí, tí mo bá ti bá ojú rere rẹ pàdé—kí ojú mi má ba à rí ìparun mi.”"'), 'Num$11:18': ('ni', 'fun', 'Num$11:18 "“Sọ fún àwọn ènìyàn náà pé: ‘Ẹ ya ara yín sí mímọ́ ní ìmúrasílẹ̀ fún ọ̀la, ẹ ó sì jẹ ẹran. Nítorí pé OLÚWA ti gbọ́ igbe ẹkún yín, èyí tí ẹ sun pé, “Ìbá ṣe pé a lè rí ẹran jẹ ni! Ó sàn fún wa ní Éjíbítì jù báyìí lọ!” Nítorí náà ni OLÚWA yóò fi fún yín ní ẹran, ẹ ó sì jẹ ẹ́."'), 'Num$11:25': (None, None, 'Num$11:25 "Nígbà náà ni OLÚWA sọ̀kalẹ̀ nínú ìkúùkù ó sì bá wọn sọ̀rọ̀, ó sì mú lárá Ẹ̀mí tó wà lára Mósè sí ara àwọn àádọ́rin (70) àgbààgbà náà, Ó sì sẹlẹ̀ pé nígbà tí Ẹ̀mí náà bà lé wọn, wọ́n ṣọtẹ́lẹ̀, ṣùgbọ́n lẹ́yìn èyí wọn kò ṣọtẹ́lẹ̀ mọ́."'), 'Num$11:31': (None, None, 'Num$11:31 "Afẹ́fẹ́ sì jáde láti ọ̀dọ̀ OLÚWA ó sì kó àparò wá láti inú òkun. Ó sì dà wọ́n káàkiri gbogbo ibùdó ní ìwọ̀n gíga ẹsẹ̀ bàtà mẹ́ta sórí ilẹ̀, bí ìwọ̀n ìrìn ọjọ́ kan ní gbogbo àyíká."'), 'Num$12:3': (None, 'eniyan', 'Num$12:3 (Mósè sì jẹ́ ọlọ́kàn tútù ènìyàn ju gbogbo ènìyàn tó wà lórí ilẹ̀ ayé lọ).'), 'Num$13:1': (None, None, 'Num$13:1 "OLÚWA sọ fún Mósè pé,"'), 'Num$13:17': (None, 'lọ', 'Num$13:17 "Nígbà tí Mósè rán wọn lọ láti yẹ ilẹ̀ náà wò, ó sọ fún wọn pé, “Ẹ gba ọ̀nà Gúúsù lọ títi dé àwọn ìlú olókè"'), 'Num$13:26': ('mu', 'ilẹ', 'Num$13:26 "Wọ́n padà wá bá Mósè àti Árónì àti gbogbo àwọn ọmọ Ísiréli ní ijù Kádésí Páránì. Wọ́n mú ìròyìn wá fún wọn àti fún gbogbo ìjọ ènìyàn, wọ́n fi èso ilẹ̀ náà hàn wọ́n."'), 'Num$13:32': ('mu', 'ilẹ', 'Num$13:32 "Báyìí ni wọ́n ṣe mú ìròyìn búburú wá nípa ilẹ̀ tí wọ́n lọ yẹ̀ wò. Wọ́n wí pé, “Ilẹ̀ tí a lọ yẹ̀ wò jẹ́ ilẹ̀ tí ń run àwọn olùgbé ibẹ̀. Gbogbo àwọn ènìyàn tí a rí níbẹ̀ jẹ́ ènìyàn tó fìrìgbọ̀n tó sì sígbọnlẹ̀."'), 'Num$14:3': ('mu', 'ilẹ', 'Num$14:3 " Kí ló dé tí OLÚWA fi mú wa wá sí ilẹ̀ yìí láti fi idà pa wá?, Àwọn ìyàwó wa, àwọn ọmọ wa yóò sì di ìjẹ. Ǹjẹ́ kò wa, ní í dára fún wa bí a bá padà sí Éjíbítì?”"'), 'Num$14:8': ('mú', 'ilẹ', 'Num$14:8 "Bí inú OLÚWA bá dùn sí wa, yóò mú wa dé ilẹ̀ náà, ilẹ̀ tó ń ṣàn fún wàrà àti fún oyin, yóò fún wa ní ilẹ̀ náà."'), 'Num$14:13': ('ri', 'gbo', 'Num$14:13 "Ṣùgbọ́n Mósè sọ fún OLÚWA pé, “Nígbà náà ni àwọn ará Éjíbítì yóò gbọ́; Nítorí pé nípa agbára Írẹ ni ìwọ fi mú àwọn ènìyàn wọ̀nyí jáde kúrò láàrin wọn."'), 'Num$14:15': (None, 'ilẹ', 'Num$14:15 "Bí ìwọ bá pa àwọn ènìyàn wọ̀nyí lẹ́ẹ̀kan ṣoṣo, àwọn orílẹ̀ èdè tó bá gbọ́ ìròyìn yìí nípa rẹ yóò wí pé."'), 'Num$14:20': (None, None, 'Num$14:20 OLÚWA sì dáhùn pé: “Mo ti dárí jìn wọ́n gẹ́gẹ́ bí ọ̀rọ̀ rẹ;'), 'Num$14:22': ('ní', 'gbà', 'Num$14:22 " Kò sí ọ̀kan nínú àwọn ènìyàn tó rí òg mi àti wọn iṣẹ́ àmì ìyanu tí mo ṣe ní ilẹ̀ Éjíbítì àti nínú ihà ṣùgbọ́n tí wọ́n ṣe àìgbọ́ràn sí mi, tí wọn sì dán mi wò ní ìgbà mẹ́wàá yìí,"'), 'Num$14:28': ('wà', None, 'Num$14:28 "Sọ fún wọn, Bí Mo ti wà láàyè nítòótọ́ ni OLÚWA wí, gẹ́gẹ́ bí ohun tí ẹ wí létí mi ni èmi ó ṣe fún un yín."'), 'Num$14:31': ('mú', 'ilẹ', 'Num$14:31 "Ṣùgbọ́n ní ti àwọn ọmọ yín tí ẹ wí pé wọn ó di ìjẹ, àwọn ni n ó mú dé bẹ̀ láti gbádùn ilẹ̀ tí ẹ kọ̀ sílẹ̀."'), 'Num$14:35': (None, 'ni', 'Num$14:35 "Èmi, OLÚWA, lo sọ bẹ́ẹ̀; Èmi ó sì ṣe àwọn nǹkan wọ̀nyí sí ìjọ ènìyàn búburú yìí tí wọ́n kóra wọn jọ lòdì sí mi. Nínú ihà yìí ni òpin yóò dé bá wọn, ibẹ̀ ni wọn yóò kú sí.”"'), 'Num$14:36': ('mú', 'ilẹ', 'Num$14:36 "Àwọn ọkùnrin tí Mósè rán láti yẹ ilẹ̀ wò, tí wọ́n sì mú àwọn ènìyàn kùn sí i nípa ìròyìn búburú tí wọ́n mú wá nípa ilẹ̀ náà;"'), 'Num$15:1': ('ṣe', 'fun', 'Num$15:1 OLÚWA sọ fún Mósè pé;'), 'Num$15:17': (None, None, 'Num$15:17 " OLÚWA sọ fún Mósè pé,"'), 'Num$15:29': ('bi', 'ni', 'Num$15:29 "Òfin kan náà ló wà fún ẹnikẹ́ni tó bá ṣẹ̀ láìròtẹ́lẹ̀, yálà ọmọ bíbí ilẹ̀ yín tàbí àlejò tí ń gbé láàrin yín."'), 'Num$15:37': (None, None, 'Num$15:37 "OLÚWA sọ fún Mósè pé,"'), 'Num$15:41': ('mu', None, 'Num$15:41 Èmi ni OLÚWA Ọlọ́run yín tí ó mú yín jáde láti Éjíbítì láti jẹ́ Ọlọ́run yín. Èmi ni OLÚWA Ọlọ́run yín.’ ”'), 'Num$16:4': (None, None, 'Num$16:4 "Nígbà tí Mósè gbọ́ èyí, ó dojú bolẹ̀,"'), 'Num$16:6': (None, None, 'Num$16:6 "Kí Kórà àti àwọn ẹgbẹ́ rẹ̀ ṣe èyí, Ẹ mú àwo tùràrí."'), 'Num$16:12': ('mu', None, 'Num$16:12 Mósè sì ránṣẹ́ sí Dátanì àti Ábírámù àwọn ọmọ Élíábù. Ṣùgbọ́n wọ́n wí pé “Àwa kò ní í wá!'), 'Num$16:14': ('mu', 'ilẹ', 'Num$16:14 "Ṣíbẹ̀ṣíbẹ̀ ìwọ kò sì tí ì mú wa dé ilẹ̀ tó ń ṣàn fún wàrà àti oyin, bẹ́ẹ̀ ni o kò sì pín ilẹ̀ ìní wa fún wa. Ìwọ ha fẹ́ sọ wá di ẹrú bí? Rárá o, àwa kò ní wá!”"'), 'Num$16:15': (None, 'ni', 'Num$16:15 "Nígbà náà ni Mósè bínú gidigidi, ó sì sọ fún OLÚWA pé, “Má ṣe gba ọrẹ wọn, èmi kò gba kẹ́tẹ́kẹ́tẹ́ lọ́wọ́ wọn, bẹ́ẹ̀ ni n kò sì pa ẹnikẹ́ni nínú wọn lára!”"'), 'Num$16:16': (None, None, 'Num$16:16 "Mósè sọ fún Kórà pé, “Ìwọ àti ọmọ lẹ́yìn rẹ gbọdọ̀ fara hàn níwájú OLÚWA lọ́la—gbogbo yín, ìwọ, àwọn ọmọ lẹ́yìn rẹ àti Árónì."'), 'Num$16:28': ('ni', None, 'Num$16:28 "Nígbà náà ni Mósè wí pé, “Báyìí ni ẹ ó ṣe mọ̀ pé OLÚWA ló rán mi láti ṣe gbogbo iṣẹ́ wọ̀nyí àti pé kì í ṣe ìfẹ́ inú mi ni àwọn ohun tí mò ń ṣe."'), 'Num$16:30': ('ni', 'ṣe', 'Num$16:30 "Ṣùgbọ́n bí OLÚWA bá ṣe ohun túntún, tí ilẹ̀ sì la ẹnu, tó gbé wọn mì, àwọn pẹ̀lú gbogbo ohun tí wọ́n ní, tí wọ́n sì wọ inú ibojì wọn lọ láàyè, nígbà náà ni ẹ̀yin ó mọ̀ pé àwọn ènìyàn yìí ti kẹ́gàn OLÚWA.”"'), 'Num$16:31': (None, 'ilẹ', 'Num$16:31 "Bí ó sì ṣe parí ọ̀rọ̀ wọ̀nyí, ilẹ̀ pín sí méjì nísàlẹ̀ wọn,"'), 'Num$16:39': (None, 'wá', 'Num$16:39 "Élíásárì tí í ṣe àlùfáà sì kó gbogbo àwo tùràrí tí àwọn tí ó jóná mú wa, ó gún wọn pọ̀, ó fi ṣe ìbòrí fún pẹpẹ,"'), 'Num$16:46': ('Mu', 'ṣe', 'Num$16:46 "Mósè sì sọ fún Árónì pé, “Mú àwo tùràrí, kí o fi iná sí i lórí pẹpẹ, fi tùràrí sínú rẹ̀, kí o sì tètè mu lọ sí àárin ìjọ ènìyàn láti ṣètùtù fún wọn nítorí pé ìbínú OLÚWA ti jáde, àjàkálẹ̀-àrùn sì ti bẹ̀rẹ̀.”"'), 'Num$17:8': ('lọ', None, 'Num$17:8 " Ó sì se ní ọjọ́ kéjì Mósè wọ inú àgọ́ ẹ̀rí lọ, ó sì rí ọ̀pá Árónì, tí ó dúró fún ẹ̀yà Léfì, kì í ṣe wí pé ó hù nìkan Ṣùgbọ́n ó ruwé, ó yọ ìtànná, ó sì so èso jíjẹ."'), 'Num$18:6': (None, 'ebun', 'Num$18:6 "Èmi fúnra mi ti yan àwọn arákùnrin rẹ tí í ṣe ọmọ Léfì kún àwọn ọmọ Ísírẹ́lì yóòkù, wọn jẹ́ ẹ̀bùn fún ọ, èyí ni a fún OLÚWA láti ṣe iṣẹ́ tí ó wà ní àgọ́ ìpàdé."'), 'Num$18:7': (None, 'ebun', 'Num$18:7 "Ṣùgbọ́n ìwọ àti àwọn ọmọ rẹ ni yóò mójú tó iṣẹ́ àlùfáà yín fún gbogbo ohun tó bá jẹ mọ́ ibi pẹpẹ àti ti ẹ̀yin aṣọ títa, ẹ ó sì máa ṣiṣẹ́ ibi pẹpẹ àti nínú aṣọ tí a ta. Mo fún ọ ní iṣẹ́ àlùfáà gẹ́gẹ́ bí ẹ̀bùn. Ṣùgbọ́n àlejò tí ó bá sún mọ́ tòòsí ibi ìyà sí mímọ́ fún Ọlọ́run ni a ó pa.”"'), 'Num$18:8': (None, 'ni', 'Num$18:8 "Nígbà náà ni OLÚWA sọ fún Árónì pé, “Èmi fúnra mi ti fi ọ́ ṣọ́ ìdi gbogbo ẹbọ tí a bá mú wá fún gbogbo ọrẹ mímọ́ tí àwọn ọmọ Ísírẹ́lì bá fún mi, mo fún ìwọ àti àwọn ọmọ rẹ gẹ́gẹ́ bí ìpín rẹ tí yóò máa ṣe."'), 'Num$18:9': ('ẹbọ', 'ọrẹ', 'Num$18:9 "Ìwọ ni kí o ni ìpín ọrẹ mímọ́ jùlọ tí a mú kúrò ní ibi iná, nínú gbogbo ọrẹ tí wọ́n mú wá gẹ́gẹ́ bí ẹbọ mímọ́ jùlọ, yálà ẹbọ ohun jíjẹ, ẹbọ ẹ̀ṣẹ̀ tàbí ẹbọ ẹ̀bi, ìpin wọ̀nyí jẹ́ ti ìwọ àti àwọn ọmọ rẹ."'), 'Num$18:11': (None, 'ebun', 'Num$18:11 "“Èyí tún jẹ́ tìrẹ pẹ̀lú: ohunkóhun tí a bá yà sọ́tọ̀ lára ẹ̀bùn ọrẹ fífì àwọn ọmọ Ísírẹ́lì. Mó fún ìwọ àti àwọn ọmọ rẹ ọkùnrin àti ọmọ rẹ obìnrin gẹ́gẹ́ bí ipin tí yóò máa ṣe déédé nínú ìdílé rẹ, tí o bá wà ní mímọ́ ni o le jẹ ẹ́."'), 'Num$18:12': (None, 'ni', 'Num$18:12 “Mo fún ọ ní gbogbo òróró tí ó dára jùlọ àti gbogbo ọtí titun dáradára jùlọ àti ọkà tí wọ́n mú wá fún OLÚWA ní àkọ́so ohun ọ̀gbìn wọn tí wọ́n kórè.'), 'Num$18:13': ('mú', 'ilẹ', 'Num$18:13 "Gbogbo àkọ́so nǹkan ilẹ̀ wọ́n tí wọn mú wá fún OLÚWA yóò jẹ́ tìrẹ, Ẹnikẹ́ni nínú ìdílé rẹ, àwọn tí ó jẹ́ mímọ́ nínú ìdílé rẹ lè jẹ ẹ́."'), 'Num$18:15': (None, 'eniyan', 'Num$18:15 "Gbogbo àkọ́bí nínú gbogbo ohun alààyè tí wọ́n mú wá fún OLÚWA, ìbá à jẹ́ ti ènìyàn tàbí ti ẹranko jẹ́ tìrẹ ṣùgbọ́n o gbọdọ̀ ra gbogbo àkọ́bí ọmọkùnrin padà àti àkọ́bí àwọn ẹranko aláìmọ́."'), 'Num$18:19': (None, 'ni', 'Num$18:19 Ohunkóhun tí a bá ti yà sọ́tọ̀ nínú ọrẹ àwọn ọmọ Ísírẹ́lì tí wọ́n mú wá fún OLÚWA ni mo fún ìwọ àti àwọn ọmọ rẹ ọkùnrin àti ọmọ rẹ obìnrin gẹ́gẹ́ bí ìpín rẹ tí yóò máa ṣe déédé. Ó jẹ́ májẹ̀mú iyọ̀ láéláé níwájú OLÚWA fún ìwọ àti ọmọ rẹ.”'), 'Num$18:21': (None, 'iṣẹ', 'Num$18:21 "“Mó ti fún àwọn ọmọ Léfì ní gbogbo ìdámẹ́wàá ní Ísírẹ́lì gẹ́gẹ́ bí ogún fún iṣẹ́ tí wọ́n ṣe, nígbà tí wọ́n ń ṣiṣẹ́ níbi àgọ́ ìpàdé"'), 'Num$18:23': (None, 'ni', 'Num$18:23 "Àwọn ọmọ Léfì ní ó gbọdọ̀ ṣe iṣẹ́ tí ó wà nínú àgọ́ ìpàdé, wọn yóò sì jìyà ẹ̀ṣẹ̀ wọn bí wọ́n bá kúrò láti ṣe é. Èyí ni ìlànà láéláé fún àwọn ìran tí ó ń bọ̀. Wọn kò ní gba ogún kankan láàrin àwọn ọmọ Ísírẹ́lì."'), 'Num$18:24': (None, 'ni', 'Num$18:24 "Kàkà bẹ́ẹ̀, mo fún àwọn ọmọ Léfì gẹ́gẹ́ bí ogún wọn, ìdá kan nínú ìdá mẹ́wàá tí àwọn ọmọ Ísírẹ́lì pèsè gẹ́gẹ́ bí ọrẹ sí OLÚWA. Èyí ni mo wí nípa wọn: Wọn kò ní gba ogún kankan láàrin àwọn ọmọ Ísírẹ́lì.”"'), 'Num$18:25': (None, None, 'Num$18:25 "OLÚWA sọ fún Mósè pé,"'), 'Num$18:32': (None, 'ni', 'Num$18:32 "Nípa ṣíṣe ìgbékalẹ̀ ìpín tí ó dára jùlọ o kò ní jẹ̀bi lórí ọ̀rọ̀ yìí, ìwọ kò sì ní ba ọrẹ mímọ́ àwọn ọmọ Ísírẹ́lì jẹ́, ìwọ kì yóò sì kú.’ ”"'), 'Num$19:1': (None, None, 'Num$19:1 OLÚWA sọ fún Mósè àti Árónì pé:'), 'Num$19:22': (None, None, 'Num$19:22 "Gbogbo ohun tí ẹni tí ó jẹ́ aláìmọ́ bá fi ọwọ́ kàn yóò di àìmọ́, ẹnikẹ́ni tí ó bá sì fi ọwọ́ kàn án yóò jẹ́ aláìmọ́ títí di ìrọ̀lẹ́.”"'), 'Num$20:5': (None, 'ni', 'Num$20:5 "Kí ni ó dé tí o fi mú wa gòkè kúrò ní Éjíbítì wá sí ibi búburú yìí? Ibi tí kò ní oúnjẹ tàbí igi ọ̀pọ̀tọ́, èṣo àjàrà tàbí pamonganati. Bẹ́ẹ̀ ni kò sí omi tí a ó mu níhín-ín!”"'), 'Num$20:12': (None, 'ni', 'Num$20:12 "Ṣùgbọ́n OLÚWA sọ fún Mósè àti Árónì pé, “Nítorí pé ẹ̀yin kò gbà mí gbọ́ tó láti bu ọlá fún mi níwájú àwọn ọmọ Ísírẹ́lì, ìwọ kò ní kó àwọn ìjọ ènìyàn yìí dé ilẹ̀ tí mo fún wọn.”"'), 'Num$20:14': (None, None, 'Num$20:14 "Mósè sì ránṣẹ́ láti Kádésì sí ọba Édómù, wí pé: “Èyí ni nǹkan tí arákùnrin rẹ Ísírẹ́lì sọ: Ìwọ ti mọ̀ nípa gbogbo ìnira, tí ó wá sí orí wa."'), 'Num$20:23': (None, 'ni', 'Num$20:23 "Ní orí òkè Hórì, ní ẹ̀gbẹ́ ààlà Édómù OLÚWA sọ fún Mósè àti Árónì pé,"'), 'Num$21:7': ('ni', 'gba', 'Num$21:7 "Àwọn ènìyàn sì wá sí ọ̀dọ̀ Mósè wọn wí pé, “A ti dá ẹ̀ṣẹ̀ nígbà tí a sọ̀rọ̀ òdì sí OLÚWA àti sí ìwọ pẹ̀lú. Gba àdúrà pé kí OLÚWA mú ejò náà kúrò lọ́dọ̀ wa.” Nígbà náà ni Mósè gbàdúrà fún àwọn ènìyàn."'), 'Num$21:8': ('ni', 'gbe', 'Num$21:8 "OLÚWA sọ fún Mósè pé, “Rọ ejò, a ó sì gbé e kọ́ sókè lórí igi, ẹni tí ejò bá ti gé jẹ lè wò ó yóò sì yè.”"'), 'Num$21:21': (None, None, 'Num$21:21 Ísírẹ́lì rán oníṣẹ́ láti sọ fún Síhónì ọba àwọn ará Ámórì wí pé:'), 'Num$21:23': ('lọ', 'kọjá', 'Num$21:23 "Ṣùgbọ́n Síhónì kò ní jẹ́ kí àwọn Ísírẹ́lì kọjá ní ilẹ̀ wọn. Ó pe àwọn ọmọ ogun rẹ̀ jọ wọ́n sì wọ́de ogun lọ sí ihà nítorí àwọn ọmọ Ísírẹ́lì. Nígbà tí ó dé Jánásì, ó bá àwọn ọmọ Ísírẹ́lì jà."'), 'Num$21:24': ('ààlà', None, 'Num$21:24 "Àmọ́ Ísírẹ́lì ṣẹ́gun, wọn fi ojú idà pa wọ́n, wọ́n sì gba ilẹ̀ láti ọwọ́ Ánónì lọ dé Jábókù, títí tí ó fi dé ilẹ̀ àwọn ará Ámónì, nítorí pé ààlà wọn jẹ́ olódi."'), 'Num$21:27': (None, None, 'Num$21:27 Ìdí nì yí tí akọrin sọ wí pé: “Wá sí Hésíbónì kí ẹ jẹ́ kí a tún un kọ́; jẹ́ kí ìlú Ṣíhónì padà bọ̀ sípò.'), 'Num$21:34': (None, 'ni', 'Num$21:34 "OLÚWA sọ fún Mósè pé, “Má ṣe bẹ̀rù rẹ̀, nítorí tí mó tí fi í lé ọ lọ́wọ́, pẹ̀lú àwọn ọmọ ogun rẹ̀ pẹ̀lú ilẹ̀ rẹ̀. Ṣe fún un gẹ́gẹ́ bí o ti ṣe sí Ṣíhónì ọba Ámórì ẹni tí ó ń jọba ní Hésíbónì.”"'), 'Num$22:5': ('ilẹ', 'ayé', 'Num$22:5 "rán oníṣẹ́ pé Bálámù ọmọ Béórì, tí ó wà ní Pétórì, ní ẹ̀bá odò ti ilẹ̀ àwọn ènìyàn rẹ. Bálákì sọ pé: “Àwọn ènìyàn kan jáde wá láti Éjíbítì; wọ́n bo gbogbo ilẹ̀ ayé wọ́n sì pa ibùdó súnmọ́ ọ̀dọ̀ mi."'), 'Num$22:13': ('fun', None, 'Num$22:13 "Ní òwúrọ̀ ọjọ́ kejì Bálámù dìde ó sì sọ fún àwọn ìjòyè Bálákì pé, “Ẹ padà sí orílẹ̀ èdè yín, nítorí tí OLÚWA ti kọ̀ láti jẹ́ kí n tẹ̀lé yín.”"'), 'Num$22:16': (None, 'ṣe', 'Num$22:16 "Wọ́n lọ sí ọ̀dọ̀ Bálámù wọ́n sì sọ pé: “Èyí ni ohun tí Bálákì ọmọ Sípórì sọ: Má ṣe jẹ́ kí ohunkóhun kí ó dè ọ́ lọ́nà láti wá sí ọ̀dọ̀ mi,"'), 'Num$22:18': (None, 'ṣe', 'Num$22:18 "Ṣùgbọ́n Bálámù dá wọn lóhùn pé, “Kó dà tí Bálákì bá fún mi ní ààfin rẹ̀ tí ó kún fún fàdákà àti wúrà, n kò ṣe ohunkóhun tí ó tóbi tàbí kéré tí o kọjá òfin OLÚWA Ọlọ́run mi."'), 'Num$22:20': ('wá', 'ṣe', 'Num$22:20 "Ní alẹ́ ọjọ́ náà Ọlọ́run sì tọ Bálámù wá ó sì sọ wí pé, “Nígbà tí ọkùnrin yìí ti wá pè ọ́, lọ pẹ̀lú wọn, ṣùgbọ́n ẹnu nǹkan tí mo sọ fún ọ ni kí o ṣe.”"'), 'Num$22:34': ('ni', None, 'Num$22:34 "Bálámù sọ fún ángẹ́lì OLÚWA pé, “Mo ti dẹ́ṣẹ̀, èmi kò mọ̀ pé o dúró níwájú ọ̀nà láti dojú kọ mí, Nísinsin yìí tí inú rẹ kò bá dùn, èmi ó padà.”"'), 'Num$23:9': ('kà', 'wo', 'Num$23:9 "Láti ṣónṣó ibi gíga orí àpáta ni mo ti rí wọn, láti orí ibi gíga ni mo ti ń wò wọ́n. Mo rí àwọn ènìyàn tí ń gbé lọ́tọ̀ wọn kò sì ka ará wọn kún ara àwọn orílẹ̀ èdè."'), 'Num$23:24': (None, 'dide', 'Num$23:24 Àwọn ènìyàn dìde bí abo kìnnìún; wọ́n dìde bí i kìnnìún tí kì í sinmi títí yóò fi pa ìkógún jẹ tàbí mu ẹ̀jẹ̀ ẹni tí ó fi ara pa.”'), 'Num$23:26': ('dahùn', None, 'Num$23:26 "Bálámù dáhùn pé, “Ǹjẹ́ mi ò sọ fún ọ pé mo gbọdọ̀ Ṣe ohun tí OLÚWA bá sọ?”"'), 'Num$24:12': (None, None, 'Num$24:12 "Bálámù dá Bálákì lóhùn, “Ǹjẹ́ èmi kò sọ fún àwọn ìránṣẹ́ tí o rán sí mi wí pé,"'), 'Num$24:14': ('ṣe', 'wá', 'Num$24:14 "Nísinsin yìí mò ń padà lọ sí ọ̀dọ̀ àwọn ènìyàn mi, ṣùgbọ́n jẹ́ kí èmi kí ó kìlọ̀ fún ọ nítorí nǹkan tí àwọn ènìyàn wọ̀nyí yóò ṣe sí àwọn ènìyàn rẹ ní ọjọ́ iwájú.”"'), 'Num$24:15': (None, None, 'Num$24:15 "Nígbà náà ni ó bẹ̀rẹ̀ òwe: “Òwe Bálámù ọmọ Béórì, òwe ẹnì tí ojú rẹ̀ ríran kedere,"'), 'Num$25:12': ('fun', None, 'Num$25:12 Nítorí náà sọ fún un pé èmi ṣe májẹ̀mú àlàáfíà mi pẹ̀lú rẹ̀.'), 'Num$25:15': (None, None, 'Num$25:15 "Orúkọ ọmọbìnrin Mídíánì náà tí a pa ni Kósíbì ọmọbìnrin Súrù, tí ṣe olóyè àwọn ẹ̀yà kan nínú ìdílé kan ní Mídíanì."'), 'Num$26:1': (None, None, 'Num$26:1 " Lẹ́yìn àjàkálẹ̀-àrùn OLÚWA sọ fún Mósè àti Élíásárì ọmọ Árónì, àlùfáà pé"'), 'Num$27:2': ('ní', None, 'Num$27:2 "Wọ́n súnmọ́ ẹnu ọ̀nà Àgọ́ Ìpàdé wọ́n sì dúró níwájú Mósè, àti Élíásérì àlùfáà, àti níwájú àwọn olórí àti ìjọ, wọ́n sì wí pé,"'), 'Num$27:6': ('fun', None, 'Num$27:6 "OLÚWA sì wí fún un pé,"'), 'Num$27:8': ('ni', None, 'Num$27:8 "“Sọ fún àwọn ọmọ Ísírẹ́lì, ‘Tí ọkùnrin kan bá kú tí kò sì fi ọmọkùnrin sáyé, ẹ fún àwọn ọmọbìnrin rẹ̀ ní ogún ìní rẹ̀."'), 'Num$27:9': ('ni', 'fun', 'Num$27:9 "Tí kò bá ní ọmọbìnrin, fi ohun ìní rẹ́ fún àwọn arákùnrin rẹ̀."'), 'Num$27:10': ('ni', 'fun', 'Num$27:10 "Tí kò bá ní arakùnrin, fi ogún ìní rẹ̀ fún arákùnrin baba rẹ̀."'), 'Num$27:11': ('ni', None, 'Num$27:11 "Tí baba rẹ̀ kò bá ní arákùnrin, fún ará ilé rẹ̀ tí ó bá súnmọ́ jù ní ìdílé rẹ̀ ní ogún ìní rẹ̀, kí ó lè jogún rẹ̀. Èyí gbọdọ̀ jẹ́ ìlànà ìdájọ́ fún àwọn ọmọ Ísírẹ́lì gẹ́gẹ́ bí OLÚWA ti paláṣẹ fún Mósè.’ ”"'), 'Num$27:12': (None, None, 'Num$27:12 " Nígbá náà OLÚWA sọ fún Mósè pé, “Lọ sì orí òkè Ábárímù yìí, kí o sì lọ wo ilẹ̀ tí mo fún àwọn ọmọ Ísírẹ́lì."'), 'Num$27:15': (None, None, 'Num$27:15 "Mósè sọ fún OLÚWA wí pé,"'), 'Num$27:18': ('fun', 'ọwọ', 'Num$27:18 " Nígbà náà ní OLÚWA sọ fún Mósè pé, “Mú Jóṣúà ọmọ Núnì, ọkùnrin nínú ẹni tí èmi wà, kí o sì gbé ọwọ́ rẹ lé e."'), 'Num$27:22': ('fun', None, 'Num$27:22 Mósè sì se gẹ́gẹ́ bí OLÚWA ti sọ fún un. Ó mú Jóṣúà ó sì mú kí ó dúró níwájú Élíásárì àlùfáà àti níwájú gbogbo ìjọ.'), 'Num$28:11': (None, None, 'Num$28:11 "“ ‘Àti ní ọjọ́ tí o bẹ̀rẹ̀ oṣù kọ̀ọ̀kan, kí ẹ̀yin kí ó gbé ẹbọ sísun fún OLÚWA pẹ̀lú ọ̀dọ́ akọ màlúù méjì, àgbò kan àti akọ ọ̀dọ́ àgùntàn méje ọlọ́dún kọ̀ọ̀kan, kí gbogbo rẹ̀ sì jẹ́ aláìlábùkù."'), 'Num$28:18': ('ṣe', 'iṣẹ', 'Num$28:18 Ní ọjọ́ kìn-ní-ní ni kí ẹ ṣe ìpàdé àjọ mímọ́ kí ẹ wá kí ẹ sì má ṣe iṣẹ́ kankan.'), 'Num$28:26': ('mú', 'iṣẹ', 'Num$28:26 " “ ‘Ní ọjọ́ àkọ́so pẹ̀lú, nígbà tí ẹ̀yin bá mú ẹbọ ohun jíjẹ tuntun wá fún OLÚWA lẹ́yìn àsìkò Àjọ̀dún, kí ẹ̀yin kí ó ní àpéjọ mímọ́, ẹ̀yin kò gbọdọ̀ se iṣẹ́ kankan."'), 'Num$29:12': ('ṣe', 'iṣẹ', 'Num$29:12 " “ ‘Ní ijọ́ kẹẹdógún oṣù kéje, kí ẹ̀yin kí ó ní àpèjọ mímọ́, ẹ̀yin kò sì gbọdọ̀ ṣe iṣẹ́ kankan. Kí ẹ̀yin kí ó ṣi ṣe àjọyọ̀ fún OLÚWA fún ọjọ́ méje."'), 'Num$29:35': ('ṣe', 'iṣẹ', 'Num$29:35 " “ ‘Àti ní ọjọ́ kẹjọ kí ẹ̀yin kó ní àpèjọ, kí ẹ má sì ṣe iṣẹ́ kankan."'), 'Num$29:39': ('ṣe', None, 'Num$29:39 "“ ‘Pẹ̀lú ẹ̀jẹ́ yín, àti ẹbọ àtinúwá yín, kí ẹ̀yin kí o pèsè fún OLÚWA ní àjọ yín, ẹbọ sísun, ẹbọ ohun jíjẹ, ẹbọ ohun mímu àti ẹbọ ìkẹ́gbẹ́ yín.’ ”"'), 'Num$30:3': (None, None, 'Num$30:3 “Nígbà tí ọmọbìnrin kan bá sì wà ní ilé baba rẹ̀ tó bá jẹ́ ẹ̀jẹ́ fún OLÚWA tàbí búra láti fi de ara rẹ̀'), 'Num$30:10': ('dè', 'ìde', 'Num$30:10 "“Bí obìnrin tí ó ń gbé pẹ̀lú ọkọ rẹ̀ bá jẹ́ ẹ̀jẹ́ tabí de ara rẹ̀ ní ìdè lábẹ́ ìbúra,"'), 'Num$30:13': ('ṣe', None, 'Num$30:13 Ọkọ rẹ̀ lè ṣe ìwádìí tàbí sọ ọ́ dasán ẹ̀jẹ́ tàbí ìbúrà ìdè tí ó ti ṣe láti fi de ara rẹ̀.'), 'Num$31:20': ('ṣe', 'iṣẹ', 'Num$31:20 Kí ẹ̀yin kí ó ya aṣọ yín sí mímọ́ àti gbogbo ohun tí a fi awọ ṣe àti gbogbo iṣẹ́ irun ewúrẹ́ àti ohun tí a fi igi ṣe.”'), 'Num$31:21': ('lọ', None, 'Num$31:21 "Élíásárì àlùfáà sì wí fún àwọn olórí ogun náà pé, “Èyí ní ìlànà òfin tí OLÚWA fi lélẹ̀ ní àṣẹ fún Mósè."'), 'Num$31:25': (None, None, 'Num$31:25 "OLÚWA sọ fún Mósè pé,"'), 'Num$31:32': (None, None, 'Num$31:32 "Èrè tí ó kù lára ìkógun tí àwọn ọmọ ogun kó jẹ́, ọ̀kẹ́ mẹ́rìnlélọ́gbọ̀n (675,000) ó dín ẹgbẹ̀ẹ́dọ́gbọ̀n àgùntàn."'), 'Num$31:42': ('awọn ọkunrin', None, 'Num$31:42 "Ààbọ̀ tí ó jẹ́ ti àwọn ọmọ Ísírẹ́lì, tí Mósè yà sọ́tọ̀ kúrò lára àwọn ọkùnrin tí ó ja ogun."'), 'Num$31:48': (None, None, 'Num$31:48 "Pẹ̀lú àwọn olórí tí ó wà lórí ẹgbẹẹgbẹ̀rin ogun náà, àti àwọn balógun ọrọọrún wá sọ́dọ̀ Mósè."'), 'Num$31:50': ('ni', 'ṣe', 'Num$31:50 "Nítorí náà làwa ṣe mú ọrẹ ẹbọ wá fún OLÚWA, gbogbo ọrẹ wíwà tí a ní, wúrà, ẹ̀wọ̀n, àti júfù, àti òrùka: àmi, àti òrùka etí, àti ìlẹ̀kẹ̀ láti fi ṣe ètùtù fún ọkàn an wa níwájú OLÚWA.”"'), 'Num$31:53': (None, None, 'Num$31:53 Nítorí ológun kọ̀ọ̀kan ti kó ẹrù fún ara rẹ̀.'), 'Num$32:1': ('ní', None, 'Num$32:1 "Àwọn ọmọ Rúbẹ́nì àti àwọn ọmọ Gádì, tí wọ́n ní ẹran ọ̀sìn àti ohun ọ̀sìn rí wí pé ilé Jásérì àti Gádì dára fún ohun ọ̀sìn."'), 'Num$32:10': (None, None, 'Num$32:10 " Ìbínú OLÚWA sì dìde sí wọn ní ọjọ́ náà, ó sì búra, wí pé:"'), 'Num$32:16': (None, 'ilẹ', 'Num$32:16 "Nígbà náà wọ́n wá sí òkè ní ọ̀dọ̀ rẹ̀, “Àwa yóò fẹ́ láti kọ́ ilé ẹran níhínyìí fún ohun ọ̀sìn wa, àti ìlú fún àwọn obìnrin àti ọmọ wẹ́wẹ́ wa."'), 'Num$32:28': ('fun', None, 'Num$32:28 Nígbà náà ni Mósè pàṣẹ nípa wọn fún Élíásárì àlùfáà àti Jóṣúà ọmọ Núnì àti sí gbogbo olórí ìdílé ẹ̀yà Ísírẹ́lì.'), 'Num$32:31': ('dahùn', 'ṣe', 'Num$32:31 "Àwọn ọmọ Gádì àti ọmọ Rúbẹ́nì dáhùn pé, “Iránṣẹ́ rẹ yóò ṣe ohun tí OLÚWA ti sọ."'), 'Num$32:33': ('ilẹ', None, 'Num$32:33 Nígbà náà Mósè fún àwọn ọmọ Gádì àti àwọn ọmọ Rúbẹ́nì àti ààbọ̀ ẹ̀yà Mánásè ọmọ Jóṣẹ́fù ìjọba Ṣíhónì ọba àwọn ọmọ Ámórì àti ìjọba Ógù ọba Básánì ní gbogbo ilẹ̀ náà pẹ̀lú ìlú rẹ̀ àti agbégbé tí ó yí i ka.'), 'Num$33:50': (None, 'ni', 'Num$33:50 "Ní pẹ̀tẹ́lẹ̀ Móábù, lẹ́bá Jọ́dánì, létí Jẹ́ríkò, OLÚWA sọ fún Mósè pé,"'), 'Num$33:56': (None, 'ṣe', 'Num$33:56 "Nígbà náà, èmi yóò ṣe sí yín, ohun tí mo ti rò láti ṣe sí wọn.’ ”"'), 'Num$34:1': (None, None, 'Num$34:1 "OLÚWA sọ fún Mósè pé,"'), 'Num$34:13': ('fun', 'ni', 'Num$34:13 " Mósè pa á láṣẹ fún àwọn ọmọ Ísírẹ́lì pé: “Yan ilẹ̀ yìí pẹ̀lú kèké gẹ́gẹ́ bí ìní ogún: OLÚWA ti paláṣẹ láti fi fún ẹ̀yà mẹ́san, àti ààbọ̀."'), 'Num$35:4': (None, 'fun', 'Num$35:4 "“Ilẹ̀ pápá oko tútù káàkiri ìlú tí ẹ ó fi fún àwọn ọmọ Léfì, wíwọ̀n rẹ̀ yóò jẹ́ ẹgbàá ìgbọ̀nwọ́ (1500) ẹsẹ̀ bàtà láti ògiri ìlú náà."'), 'Num$35:8': ('fun', 'ni', 'Num$35:8 "Ìlú tí ẹ fún àwọn ọmọ Léfì láti ara ilẹ̀ tí àwọn ọmọ Ísírẹ́lì jogún, olúkúlùkù kí ó fi nínú ìlú rẹ̀ fún àwọn ọmọ Léfì gẹ́gẹ́ bí ilẹ̀ ìní rẹ̀ tí ó ní. Gba púpọ̀ lọ́wọ́ ẹ̀yà tí ó ní púpọ̀ àti díẹ̀ lọ́wọ́ ẹ̀yà tí ó ní díẹ̀.”"'), 'Num$35:13': ('ni', 'fun', 'Num$35:13 Mẹ́fà nínú ìlú tí ẹ ó fi fún wọn yóò jẹ́ ìlú ààbò fún yín.'), 'Num$35:20': (None, 'ju', 'Num$35:20 "Tí ẹnikẹ́ni tí ó bá ń yan odì pẹ̀lú ìríra tí ẹlòmíràn tàbí ju nǹkan sí i pẹ̀lú èrò, tí ó sì kú."'), 'Num$35:22': (None, None, 'Num$35:22 "“ ‘Ṣùgbọ́n bí ó bá fi nǹkan gún un lójijì láìṣọ̀ta, tàbí tí ó sọ ohunkóhun lù ú láìmọ̀-ọ́n-mọ̀ ṣe"'), 'Num$35:34': ('gbe', 'gbe', 'Num$35:34 "Má se sọ di àìmọ́, ilẹ̀ tí ẹ ń gbé, àti èyí tí mo ń gbé, nítorí Èmi JÈHÓFÀ, ń gbé láàrin àwọn ọmọ Ísírẹ́lì.’ ”"'), 'Num$36:3': (None, None, 'Num$36:3 "Wàyí, tí wọn bá fẹ́ ọkùnrin láti ẹ̀yà Ísírẹ́lì mìíràn; nígbà náà a ó gba ogún un wọn kúrò nínú ogún ìran wa, a ó sì fi kún ogún ẹ̀yà tí a fẹ́ wọn sí. Bẹ́ẹ̀ ni a ó sì gbàá kúrò nínú ìpín ilẹ̀ ogún ti wa."'), 'Num$36:4': (None, None, 'Num$36:4 "Nígbà tí ọdún Júbélì àwọn ọmọ Ísírẹ́lì bá dé, a ó pa ogún wọn pọ̀ mọ́ ẹ̀yà tí wọ́n fẹ́ wọn sí, a ó sì gba ogún ẹ̀yà wọn kúrò lọ́wọ́ babańlá wọn.”"'), 'Deut$1:8': (None, 'gbà', 'Deut$1:8 "Wò ó, èmi tí fi ilẹ̀ yí fún un yín: Ẹ wọ inú rẹ̀ lọ kí ẹ sì gba ilẹ̀ náà tí OLÚWA ti wí pé òun yóò fún àwọn baba yín: fún Ábúráhámù, Ísáákì, àti Jákọ́bù, àti fún àwọn àrọ́mọdọ́mọ wọn.”"'), 'Deut$1:14': (None, 'ṣe', 'Deut$1:14 "Ẹ̀yin sì dá mi lóhùn wí pé, “Ohun tí ìwọ ti gbérò láti ṣe n nì dára.”"'), 'Deut$1:15': (None, 'olori', 'Deut$1:15 "Bẹ́ẹ̀ ni mo yan àwọn aṣáájú ẹ̀yà yín, àwọn ọlọgbọ́n àti ẹni àpọ́nlé láti ṣe àkóso lórí yín: gẹ́gẹ́ bí olùdarí ẹgbẹgbẹ̀rún, ọgọgọ́rùn-ún, àràádọ́ta, ẹ̀wẹ̀ẹ̀wá àti gẹ́gẹ́ bí olórí àwọn ẹ̀yà."'), 'Deut$1:22': (None, 'ilẹ', 'Deut$1:22 " Nígbà náà ni gbogbo yín tọ̀ mí wá wí pé, “Jẹ́ kí a yan àwọn ayọ́lẹ̀wò láti yọ́ ilẹ̀ náà wò fún wa, kí wọn sì fún wa ní ìròyìn àwọn ọ̀nà tí a ó gbà àti àwọn ìlú tí a ó bá pàdé.”"'), 'Deut$1:25': ('mu', 'ilẹ', 'Deut$1:25 "Wọ́n sì mú lára èso ilẹ̀ náà wá fún wa pé, “Ilẹ̀ tí ó dára ni OLÚWA Ọlọ́run wa fún wa.”"'), 'Deut$1:26': (None, None, 'Deut$1:26 Ṣùgbọ́n ẹ̀yin kò fẹ́ gòkè lọ; ẹ ṣọ̀tẹ̀ sí àṣẹ OLÚWA Ọlọ́run yín.'), 'Deut$1:34': ('gbo', None, 'Deut$1:34 "Nígbà tí OLÚWA gbọ́ ohun tí ẹ sọ, inú bí i, ó sì búra wí pé:"'), 'Deut$1:39': ('ní', None, 'Deut$1:39 Àwọn èwe yín tí ẹ ṣọ pé wọn yóò kó lẹ́rú. Àwọn ọmọ yín tí ko tí ì mọ rere yàtọ̀ sí búburú ni yóò wọ ilẹ̀ náà.'), 'Deut$1:44': (None, None, 'Deut$1:44 "Àwọn ará Ámórì tí ń gbé ní àwọn òkè náà, dojú kọ yín bí àgbájọpọ̀ oyin. Wọ́n sì lù yín bolẹ̀ látí Séírì títí dé Hómà."'), 'Deut$1:45': ('pada', None, 'Deut$1:45 "Ẹ padà, ẹ sì sunkún níwájú OLÚWA, òun kò sì fetí sí igbe ẹkún yín, Ó sì kọ etí dídi sí i yín."'), 'Deut$2:4': (None, 'kọjá', 'Deut$2:4 " Fún àwọn ènìyàn náà ní àwọn òfin wọ̀nyí: ‘Ẹ ti fẹ́ la ilẹ̀ àwọn arákùnrin yín kọjá, àwọn ọmọ Ísọ̀; àwọn ará Édómù tí ń gbé ní Séírì. Ẹ̀rù yín yóò bà wọ́n, ṣùgbọ́n ẹ ṣọ́ra gidigidi."'), 'Deut$2:9': ('fun', 'ni', 'Deut$2:9 "OLÚWA sì sọ fún mi pé, “Ẹ má ṣe dẹ́rùba àwọn ará Móábù bẹ́ẹ̀ ni kí ẹ má ṣe bá wọn jagun torí pé n kò ní fi èyíkéyìí nínú ilẹ̀ wọn fún un yín. Mo ti fi Árì fún àwọn ọmọ Lọ́tì bí i ìní.”"'), 'Deut$2:16': (None, None, 'Deut$2:16 "Lẹ́yìn tí ẹni tí ó gbẹ̀yìn pátapáta nínú àwọn jagunjagun àwọn ènìyàn wọ̀nyí ti kú,"'), 'Deut$2:26': (None, None, 'Deut$2:26 "Mo rán àwọn ìránṣẹ́ sí Síhónì láti ihà Kédémótì pẹ̀lú ọ̀rọ̀ àlàáfíà wí pé,"'), 'Deut$2:28': ('fun', 'kọjá', 'Deut$2:28 Ẹ ta oúnjẹ tí a ó jẹ àti omi tí a ó mu fún wa ní iye owó wọn. Kìkì kí ẹ sáà jẹ́ kí a rìn kọjá:'), 'Deut$2:30': ('okan', None, 'Deut$2:30 "Ṣùgbọ́n Ṣíhónì ọba Héṣíbónì kò gbà fún wa láti kọjá. Torí pé OLÚWA Ọlọ́run yín ti mú ọkàn rẹ̀ yigbì, àyà rẹ̀ sì kún fún agídí kí ó ba à le fi lé e yín lọ́wọ́, bí ó ti ṣe báyìí."'), 'Deut$2:31': ('ni', 'gbà', 'Deut$2:31 "OLÚWA sì bá mi sọ̀rọ̀ pé, “Kíyèsí i, mo ti bẹ̀rẹ̀ sí ní fi Síhónì àti ilẹ̀ rẹ̀ lé e yín lọ́wọ́. Báyìí, ẹ máa ṣẹ́gun rẹ̀, kí ẹ sì gba ilẹ̀ rẹ̀.”"'), 'Deut$3:8': ('ilẹ', None, 'Deut$3:8 "Ní ìgbà náà ni a ti gba ilẹ̀ tí ó wà ní Jọ́dánì láti odò Ánónì, títí dé orí òkè Hámónì lọ́wọ́ àwọn ọba Ámórì méjèèjì wọ̀nyí."'), 'Deut$3:13': ('ilẹ', None, 'Deut$3:13 "Gbogbo ìyókù Gílíádì àti gbogbo Básánì, ní ilẹ̀ ọba Ógù ni mo fún ìdajì ẹ̀yà Mánásè. (Gbogbo agbégbé Ágóbù ni Básánì tí a mọ̀ sí ilẹ̀ àwọn ará Ráfátì."'), 'Deut$3:18': (None, 'kọjá', 'Deut$3:18 "Mo pàṣẹ fún un yín ní ìgbà náà pé, “OLÚWA Ọlọ́run yín ti fi ilẹ̀ yìí fún un yín láti ni ín. Ṣùgbọ́n, gbogbo àwọn ọkùnrin yín tí ó lera tí wọ́n sì ti dira ogun, gbọdọ̀ kọjá ṣíwájú àwọn arákùnrin yín: ará Ísírẹ́lì."'), 'Deut$3:19': ('ni', None, 'Deut$3:19 "Àwọn ẹ̀yà a yín, àwọn ọmọ yín àti àwọn ohun ọ̀sìn in yín (Mo mọ̀ pé ẹ ti ní ohun ọ̀sìn púpọ̀) lè dúró ní àwọn ìlú tí mo fi fún un yín,"'), 'Deut$4:1': ('lọ', None, 'Deut$4:1 "Gbọ́ Ísírẹ́lì, gbọ́ òfin àti ìlànà tí èmi yóò kọ́ ọ yín. Ẹ tẹ̀lé wọn kí ẹ ba à lè yè, kí ẹ ba à lè lọ láti gba ilẹ̀ tí OLÚWA Ọlọ́run àwọn baba yín yóò fi fún un yín."'), 'Deut$4:5': ('ni', None, 'Deut$4:5 "Ẹ kíyèsí i, mo ti kọ́ ọ yín ní àwọn òfin àti àṣẹ bí OLÚWA Ọlọ́run-ùn mi ti pàṣẹ fún mi, kí ẹ ba à le tẹ̀lé wọn ní ilẹ̀ náà tí ẹ ń lọ láti ní ní ìní."'), 'Deut$4:9': ('ṣe', 'gba', 'Deut$4:9 " Kìkì i kí ẹ kíyèsára, kí ẹ sì sọ́ra a yín gidigidi kí ẹ má ba à gbàgbé àwọn ohun tí ojú yín ti rí, kí wọn má sì ṣe sá kúrò lóókan àyà yín níwọ̀n ìgbà tí ẹ sì wà láàyè. Ẹ máa fi wọ́n kọ́ àwọn ọmọ yín àti àwọn ọmọ wọn tí ń bọ̀ lẹ́yìn wọn."'), 'Deut$4:15': (None, None, 'Deut$4:15 "Ẹ kíyèsára gidigidi, torí pé, ẹ kò rí ìrísí ohunkóhun ní ọjọ́ tí OLÚWA bá yín sọ̀rọ̀ ní Hórébù láti àárin iná wá. Torí náà ẹ sọ́ra yín gidigidi,"'), 'Deut$4:21': ('lọ', None, 'Deut$4:21 "Inú OLÚWA ru sí mi nítorí yín, ó sì ti búra pé èmi kì yóò la Jọ́dánì kọjá, èmi kì yóò sì wọ ilẹ̀ rere tí OLÚWA Ọlọ́run fi fún un yín, ní ìní yín."'), 'Deut$4:25': ('lọ', None, 'Deut$4:25 "Lẹ́yìn ìgbà tí ẹ ti ní àwọn ọmọ àti àwọn ọmọọmọ, tí ẹ sì ti gbé ní ilẹ̀ yìí pẹ́: Bí ẹ bá wá ba ara yín jẹ́ nípa ṣíṣe irú ère yòówù tó jẹ́, tí ẹ sì ṣe búburú lójú OLÚWA Ọlọ́run yín, tí ẹ sì mú un bínú."'), 'Deut$4:27': (None, None, 'Deut$4:27 "OLÚWA yóò fọ́n-ọn yín ká sí àárin àwọn ènìyàn náà, àwọn díẹ̀ nínú un yín ni yóò yè láàrin orílẹ̀ èdè tí OLÚWA yóò fọ́n ọn yín sí."'), 'Deut$4:37': ('mu', None, 'Deut$4:37 "torí pé ó fẹ́ràn àwọn baba ńlá yín, ó sì yan àwọn ọmọ wọn ní ipò lẹ́yìn wọn. Nípa ìwà láàyè àti nípa agbára ńlá rẹ̀ ni ó fi mú un yín kúrò ní Éjíbítì."'), 'Deut$5:4': (None, None, 'Deut$5:4 OLÚWA bá a yín sọ̀rọ̀ lójúkojú láàrin iná lórí òkè.'), 'Deut$5:11': (None, None, 'Deut$5:11 "“Ẹ má ṣe ṣi orúkọ OLÚWA Ọlọ́run yín lò, torí pé ẹnikẹ́ni tí o bá ṣi orúkọ rẹ̀ lò kì yóò lọ láìjẹ̀bi."'), 'Deut$5:15': (None, None, 'Deut$5:15 "Ẹ rántí pé ẹ ti jẹ́ ẹrú ní Éjíbítì rí, àti pé OLÚWA Ọlọ́run yín yọ yín kúrò níbẹ̀ pẹ̀lú agbára ńlá, àti nína ọwọ́ rẹ̀. Torí èyí ni OLÚWA Ọlọ́run yín ṣe pàṣẹ fún un yín láti ya ọjọ́ ìsinmi sí mímọ́."'), 'Deut$5:23': (None, None, 'Deut$5:23 "Nígbà tí ẹ gbóhùn láti inú òkùnkùn wá, nígbà tí iná náà ń yọná lala, àwọn aṣáájú ẹ̀yà a yín àti àwọn alàgbà a yín tọ̀ mí wá."'), 'Deut$5:29': (None, None, 'Deut$5:29 "ìbá ti dára tó, bí ọkàn wọn bá lè bẹ̀rù mi, tí ó sì ń pa gbogbo òfin mi mọ́ nígbà gbogbo, kí ó bá à lè dára fún wọn, àti àwọn ọmọ wọn láéláé."'), 'Deut$5:33': (None, None, 'Deut$5:33 "Ẹ rìn ní gbogbo ọ̀nà tí OLÚWA Ọlọ́run yín ti pàṣẹ fún un yín, kí ẹ bá à le yè, kí ó sì dára fún un yín, kí ọjọ́ ọ yín bá à lè pẹ́ ní ilẹ̀ tí ẹ̀yin yóò gbà."'), 'Deut$6:10': (None, 'fun', 'Deut$6:10 "Nígbà tí OLÚWA Ọlọ́run yín bá mú ọ dé ilẹ̀ náà tí ó ti búra fún àwọn baba yín: Fún Ábúráhámù, Ísáákì, àti Jákọ́bù láti fi fún un yín, Ilẹ̀ tí ó kún fún àwọn ìlú ńláńlá tí kì í ṣe ẹ̀yin ló kọ́ ọ,"'), 'Deut$6:18': (None, 'dara', 'Deut$6:18 "Ẹ ṣe èyí tí ó dára tí ó sì tọ́ lójú OLÚWA, kí ó bá à lè dára fún un yín, kí ẹ̀yin bá à lè lọ láti gba ilẹ̀ dáradára náà tí Ọlọ́run ti fi ìbúra ṣèlérí fún àwọn baba ńlá a yín."'), 'Deut$6:21': ('fun', 'ọwọ', 'Deut$6:21 "Sọ fún un pé: “Ẹrú Fáráò ní ilẹ̀ Éjíbítì ni wá tẹ́lẹ̀, ṣùgbọ́n OLÚWA fi ọwọ́ agbára ńlá yọ wá jáde lóko ẹrú Éjíbítì."'), 'Deut$7:1': (None, 'gba', 'Deut$7:1 " Nígbà tí OLÚWA Ọlọ́run yín bá mú u yín dé ilẹ̀ náà, tí ẹ ó wọ̀ lọ láti gbà, tí ó sì lé ọ̀pọ̀lọpọ̀ orílẹ̀ èdè kúrò níwájú u yín: Àwọn ará Hítì, Gígásì, Ámórì, Kénánì, Pérésì, Hífì àti àwọn ará Jébúsì: Àwọn orílẹ̀ èdè méje tí wọ́n lágbára tí wọ́n sì pọ̀ jù yín lọ,"'), 'Deut$7:4': (None, 'jẹ', 'Deut$7:4 "torí pé wọ́n á yí àwọn ọmọ yín padà kúrò lẹ́yìn mi, láti jẹ́ kí wọn máa sin òrìṣà, ìbínú OLÚWA yóò sì wá sórí yín, yóò sì run yín kíákíá."'), 'Deut$7:5': (None, 'ge', 'Deut$7:5 " Èyí ni kí ẹ ṣe sí wọn: Ẹ wó pẹpẹ wọn lulẹ̀, ẹ bi òpó òkúta ibi mímọ́ òrìṣà wọn lulẹ̀, òpó òrìṣà Áṣérà wọn ni kí ẹ gé lulẹ̀ kí ẹ sì sun ère òrìṣà wọn ní iná."'), 'Deut$7:7': (None, None, 'Deut$7:7 "OLÚWA kò torí pé ẹ pọ̀ lọ́pọ̀lọpọ̀ ju àwọn ènìyàn yóòkù lọ yàn yín, ẹ̀yin sáà lẹ kéré jù nínú gbogbo ènìyàn."'), 'Deut$7:12': ('fun', None, 'Deut$7:12 " Bí ẹ bá ń kíyèsí àwọn òfin wọ̀nyí tí ẹ sì ń ṣọ́ra láti ṣe wọ́n, nígbà náà ni OLÚWA Ọlọ́run yín yóò pa májẹ̀mú ìfẹ́ rẹ̀ mọ́ fún un yín, bí ó ti búra fún àwọn baba ńlá a yín."'), 'Deut$7:16': ('ni', 'jẹ', 'Deut$7:16 "Gbogbo àwọn ènìyàn tí OLÚWA Ọlọ́run yín fi lé yín lọ́wọ́ ni kí ẹ parun pátapáta. Ẹ má ṣe sàánú fún wọn, ẹ má ṣe sin olúwa ọlọ́run wọn torí pé ìdánwò ni èyí jẹ́ fún un yín."'), 'Deut$7:24': (None, 'ni', 'Deut$7:24 "Yóò fi àwọn ọba wọn lé e yín lọ́wọ́, ẹ̀yin ó sì pa orúkọ wọn rẹ́ lábẹ́ ọ̀run. Kò sí ẹni tí yóò lè dojú ìjà kọ yín títí tí ẹ ó fi pa wọ́n run."'), 'Deut$8:1': ('lọ', None, 'Deut$8:1 "Ẹ kíyèsí láti máa tẹ̀lé gbogbo àṣẹ tí mo fún un yín lónìí, kí ẹ báà le yè kí ẹ sì pọ̀ sí i wọ ilẹ̀ náà, kí ẹ sì le gba ilẹ̀ náà, tí OLÚWA fìbúra ṣèlérí fún àwọn baba ńlá a yín."'), 'Deut$8:3': ('mọ', None, 'Deut$8:3 " Ó rẹ̀ yín sílẹ̀ nípa fífi ebi pa yín, Ó sì fi mánà bọ́ ọ yín, èyí tí ẹ kò mọ̀, tí àwọn baba yín kò mọ̀, kí ó báà lè kọ́ ọ yín pé, ènìyàn kò wà láàyè nípa oúnjẹ nìkan, bí kò ṣe ohun gbogbo tí ó jáde láti ẹnu OLÚWA wá."'), 'Deut$8:7': (None, None, 'Deut$8:7 "Torí pé OLÚWA Ọlọ́run yín ń mú un yín bọ̀ wá sí ilẹ̀ rere ilẹ̀ tí ó kún fún odò àti ibú omi, pẹ̀lú àwọn ìsun tí ń ṣàn jáde láti orí òkè àti pẹ̀tẹ́lẹ̀."'), 'Deut$8:10': (None, None, 'Deut$8:10 Nígbà tí ẹ bá ti jẹ tí ẹ sì yó tán ẹ yin OLÚWA Ọlọ́run yín fún pípèṣè ilẹ̀ rere fún un yín.'), 'Deut$8:11': ('kun', None, 'Deut$8:11 " Ẹ ṣọ́ra kí ẹ má gbàgbé OLÚWA Ọlọ́run yín, nípa kíkùnà láti pa àṣẹ rẹ̀, àwọn òfin rẹ̀, àti àwọn ìlànà rẹ̀ tí mo ń fún un yín lónìí mọ́."'), 'Deut$8:19': ('sin', None, 'Deut$8:19 "Bí ẹ bá wá gbàgbé OLÚWA Ọlọ́run yín, tí ẹ sì tẹ̀lé àwọn òrìṣà mìíràn, tí ẹ sì sìn wọ́n, tí ẹ sì foríbalẹ̀ fún wọn, Mo kìlọ̀ fún un yín pé rírun ni ẹ ó run."'), 'Deut$9:1': ('lọ', 'kọjá', 'Deut$9:1 "Gbọ́, ìwọ Ísírẹ́lì. Báyìí, ẹ ti gbáradì látí la Jọ́dánì kọjá, láti lọ gba ilẹ̀ àwọn orílẹ̀ èdè tí wọ́n tóbi, tí wọ́n sì lágbára jù yín lọ, tí wọ́n ní àwọn ìlú ńláńlá, tí odi wọn kan ọ̀run."'), 'Deut$9:3': (None, None, 'Deut$9:3 " Ṣùgbọ́n kí ó dá a yín lójú lónìí pé OLÚWA Ọlọ́run yín ni ó ń ṣáájú u yín lọ gẹ́gẹ́ bí iná ajónirun. Yóò jó wọn run, yóò sì tẹ orí wọn ba níwájú yín. Ẹ ó sì lé wọ́n jáde, ẹ ó sì run wọ́n kíákíá, bí OLÚWA ti ṣèlérí fún un yín."'), 'Deut$9:4': ('mu', 'ilẹ', 'Deut$9:4 "Lẹ́yìn tí OLÚWA Ọlọ́run yín bá ti lé wọn jáde níwájú u yín, ẹ má ṣe wí fún ara yín pé, “Torí òdodo mi ní OLÚWA se mú mi wá síbí láti gba ilẹ̀ náà.” Rárá, ṣùgbọ́n nítorí ìwà búburú àwọn orílẹ̀ èdè wọ̀nyí ni Ọlọ́run yóò se lé wọn jáde níwájú u yín."'), 'Deut$9:11': ('fun', None, 'Deut$9:11 "Lópin ogójì ọ̀sán àti òru wọ̀nyí, OLÚWA fún mi ní sílétì òkúta méjì, sílétì òkúta májẹ̀mú náà."'), 'Deut$9:12': ('mu', None, 'Deut$9:12 "Bẹ́ẹ̀ ni OLÚWA sọ fún mi pé, “Sọ̀kalẹ̀ kúrò níhìnín kíákíá, torí pé àwọn ènìyàn rẹ tí o mú jáde láti Éjíbítì ti hùwà ìbàjẹ́. Wọ́n ti yípadà kíá, nínú àṣẹ mi, wọ́n sì ti ṣe ère dídá fún ara wọn.”"'), 'Deut$9:13': ('ni', None, 'Deut$9:13 "OLÚWA sì sọ fún mi pé, “Mo ti rí àwọn ènìyàn wọ̀nyí pé alágídí ènìyàn gbáà ni wọ́n."'), 'Deut$9:23': ('ni', 'gbà', 'Deut$9:23 "Nígbà tí OLÚWA ran an yín jáde ní Kadesi Báníyà, Ó wí pé, “Ẹ gòkè lọ, kí ẹ sì gba ilẹ̀ náà tí mo fi fún un yín.” Ṣùgbọ́n ẹ sọ̀tẹ̀ sí òfin OLÚWA Ọlọ́run yín. Ẹ kò gbẹ́kẹ̀lé e, bẹ́ẹ̀ ni ẹ kò gbọ́ tirẹ̀."'), 'Deut$9:27': ('mu', None, 'Deut$9:27 "Rántí àwọn ìránṣẹ́ rẹ Ábúráhámù, Ísáákì, àti Jákọ́bù. Fojú fo orí kunkun àwọn ènìyàn yìí, pẹ̀lú ìwà búburú àti ẹ̀ṣẹ̀ wọn."'), 'Deut$10:8': (None, 'duro', 'Deut$10:8 "Ní ìgbà yí ni OLÚWA yan àwọn ọmọ Léfì láti máa ru àpótí májẹ̀mú OLÚWA, láti máa dúró níwájú OLÚWA láti ṣe iṣẹ́ ìránṣẹ́, àti láti fi orúkọ OLÚWA súre bí wọ́n ti ń ṣe títí di òní yìí."'), 'Deut$10:12': (None, 'beere', 'Deut$10:12 "Nísinsin yìí ìwọ Ísírẹ́lì, kín ni ohun tí OLÚWA Ọlọ́run rẹ béèrè lọ́wọ́ rẹ? Bí kò ṣe kí ìwọ bẹ̀rù OLÚWA Ọlọ́run rẹ àti láti máa rìn ní gbogbo ọ̀nà rẹ̀, láti fẹ́ràn rẹ̀, láti fi gbogbo ọkàn rẹ sin OLÚWA Ọlọ́run rẹ ọkan àti àyà rẹ,"'), 'Deut$11:2': (None, None, 'Deut$11:2 "Ẹ rántí lónìí pé kì í ṣe àwọn ọmọ yín, ni ó rí ìbáwí OLÚWA Ọlọ́run yín; títóbi rẹ̀, ọwọ́ agbára rẹ̀, nína ọwọ́ rẹ̀;"'), 'Deut$11:13': ('fun', None, 'Deut$11:13 " Nítorí náà, bí ẹ bá gbọ́ràn sí àṣẹ tí mo fún un yín lónìí tọkàntọkàn: tí ẹ fẹ́ràn OLÚWA Ọlọ́run yín, tí ẹ sì sìn ín, pẹ̀lú gbogbo àyà yín àti gbogbo ọkàn yín:"'), 'Deut$11:16': (None, None, 'Deut$11:16 "Ẹ ṣọ́ra, kí a má bàá tàn yín jẹ láti yípadà kí ẹ sì sin Ọlọ́run mìíràn àti láti máa foríbalẹ̀ fún wọn."'), 'Deut$11:18': (None, 'okan', 'Deut$11:18 "Ẹ fi gbogbo ọ̀rọ̀ mi wọ̀nyí sí àyà a yín, àti ọkàn an yín, ẹ so wọ́n bí àmì sórí ọwọ́ ọ yín, kí ẹ sì ṣo wọ́n mọ́ iwájú orí i yín."'), 'Deut$11:20': (None, 'ilẹ', 'Deut$11:20 "Ẹ kọ wọ́n sí férémù ìlẹ̀kùn yín, àti ẹnu ọ̀nà àbáwọlé e yín."'), 'Deut$11:29': ('mu', 'ilẹ', 'Deut$11:29 "Nígbà tí OLÚWA Ọlọ́run yín ba ti mú un yín dé ilẹ̀ náà tí ẹ ń wọ̀ láti gbà, kí ẹ kéde àwọn ìbùkún náà ní orí òkè Gérísímù, kí ẹ sì kéde àwọn ègún ní orí òkè Ébálì."'), 'Deut$11:31': ('lọ', 'kọjá', 'Deut$11:31 "Ẹ ti ṣetan láti la Jọ́dánì kọjá láti lè gba ilẹ̀ náà tí OLÚWA Ọlọ́run yín ń fún un yín. Nígbà tí ẹ bá gbà á, tí ẹ bá sì ń gbé ibẹ̀,"'), 'Deut$11:32': (None, None, 'Deut$11:32 ẹ rí i dájú pé ẹ̀ ń gbọ́ran sí gbogbo ìlànà àti òfin tí mo ń gbé kalẹ̀ fún un yín lónìí.'), 'Deut$12:1': (None, 'ilẹ', 'Deut$12:1 " Wọ̀nyí ni àwọn ìlànà àti òfin tí ẹ gbọdọ̀ ṣọ́ra láti máa tẹ̀lé, ní ilẹ̀ tí OLÚWA Ọlọ́run àwọn baba yín, fi fún un yín láti gbà: ní gbogbo ìgbà tí ẹ bá fi wà lórí ilẹ̀ náà."'), 'Deut$12:5': (None, None, 'Deut$12:5 "Ṣùgbọ́n ẹ wá ibi tí OLÚWA Ọlọ́run yín yóò yàn láàrin àwọn ìran yín, láti fi orúkọ rẹ̀ ṣíbẹ̀ bí i ibùgbé rẹ̀. Ibẹ̀ ni kí ẹ lọ."'), 'Deut$12:10': (None, 'ilẹ', 'Deut$12:10 "Ṣùgbọ́n ẹ ó la Jọ́dánì kọjá, ẹ ó sì máa gbé ní ilẹ̀ tí OLÚWA Ọlọ́run yín fún un yín bí ìní, yóò sì fún un yín ní ìsinmi, kúrò láàrin àwọn ọ̀ta a yín, tí ó yí i yín ká, kí ẹ báà lè máa gbé láìléwu."'), 'Deut$12:21': (None, 'ni', 'Deut$12:21 "Bí ibi tí OLÚWA Ọlọ́run yín bá yàn fún orúkọ rẹ̀ bá jìnnà sí i yín, kí ẹ pa màlúù tàbí ewúrẹ́ tí OLÚWA Ọlọ́run yín tí fún un yín, bí mo ti pàṣẹ fún un yín ní ìlú u yín, ẹ lè jẹ ẹ́ bí ẹ ti fẹ́."'), 'Deut$12:26': (None, 'lọ', 'Deut$12:26 "Ẹ mú àwọn ohun tí ẹ yà sọ́tọ̀ àti àwọn ẹ̀jẹ́ yín, kí ẹ sì lọ síbi tí OLÚWA yóò yàn."'), 'Deut$12:28': (None, 'dara', 'Deut$12:28 "Ẹ kíyèsára láti máa gbọ́ràn sí gbogbo ìlànà tí mò ń fún un yín, kí o báà dára fún ẹ̀yin àti àwọn ọmọ yín nígbà gbogbo, nítorí pé nígbà náà ní ẹ tó ń ṣe ohun tí ó dára tí ó sì tọ̀nà níwájú OLÚWA Ọlọ́run yín."'), 'Deut$12:29': (None, None, 'Deut$12:29 OLÚWA Ọlọ́run yín yóò gé àwọn orílẹ̀ èdè tí ẹ fẹ́ lé kúrò tí ẹ sì fẹ́ gba ilẹ̀ wọn kúrò níwájú u yín. Ṣùgbọ́n nígbà tí ẹ bá tí lé wọn jáde tí ẹ sì ń gbé ilẹ̀ wọn.'), 'Deut$13:1': (None, None, 'Deut$13:1 "Bí wòlíì tàbí aláṣọtẹ́lẹ̀ nípa àlá lílá bá farahàn láàrin yín, tí ó sì ń kéde iṣẹ́ àmì tàbí iṣẹ́ ìyanu,"'), 'Deut$13:5': ('mu', None, 'Deut$13:5 "Wòlíì náà tàbí alálàá náà ni kí ẹ pa, nítorí pé wàásù ìṣọ̀tẹ̀ sí OLÚWA Ọlọ́run yín, tí ó mú yín jáde láti Éjíbítì wá, tí ó sì rà yín padà lóko ẹrú. Ó ti gbìyànjú láti fà yín kúrò lójú ọ̀nà tí OLÚWA Ọlọ́run yín ti pàṣẹ fún un yín láti máa rìn. Ẹ gbọdọ̀ yọ ibi náà kúrò láàrin yín."'), 'Deut$13:6': (None, None, 'Deut$13:6 "Bí arákùnrin tìrẹ gan-an ọmọ ìyá rẹ tàbí ọmọ rẹ ọkùnrin tàbí obìnrin, aya tí o fẹ́ràn jù, tàbí ọ̀rẹ́ tímọ́tímọ́ bá tàn ọ́ jẹ níkọ̀kọ̀ wí pé jẹ́ kí a lọ sin ọlọ́run mìíràn, (ọlọ́run tí ìwọ tàbí baba rẹ kò mọ̀"'), 'Deut$13:10': ('mu', None, 'Deut$13:10 "Sọ ọ́ ní òkúta pa nítorí pé ó gbìyànjú láti fà ọ́ kúrò lẹ́yìn OLÚWA Ọlọ́run rẹ, tí ó mú ọ jáde ti Éjíbítì wá kúrò ní oko ẹrú."'), 'Deut$13:12': (None, 'ni', 'Deut$13:12 "Bí ẹ bá gbọ́ tí a sọ nípa èyíkéyìí nínú àwọn ìlú u yín tí OLÚWA Ọlọ́run yín fún un yín láti máa gbé,"'), 'Deut$14:7': (None, None, 'Deut$14:7 "Ṣíbẹ̀ṣíbẹ̀ nínú gbogbo ẹran tí ń jẹ àpọ̀jẹ tí ó sì tún ya pátakò ẹṣẹ̀ tí ẹ kò gbọdọ̀ jẹ ni: ràkunmí, ehoro, àti ẹranko tí ó dàbí ehoro tí ń gbé inú àpáta. Bí ó tílẹ̀ jẹ́ pé wọ́n jẹ àpọ̀jẹ, ṣùgbọ́n wọn kò la pátakò, ẹṣẹ̀ a kà wọ́n sí àìmọ́ fún un yín."'), 'Deut$14:21': ('ajeji', None, 'Deut$14:21 Ẹ má ṣe jẹ ohun tí ó ti kú sílẹ̀. Ẹ lè fún àwọn àjèjì tí ń gbé ní èyíkéyí nínú ìlú yín. Òun lè jẹ ẹ́ tàbí kí ẹ tà á fún àwọn àjèjì. Ṣùgbọ́n ènìyàn mímọ́ fún OLÚWA Ọlọ́run yín ni ẹ̀yin jẹ́. Má ṣe ṣe ewúrẹ́ nínú wàrà ìyá rẹ̀.'), 'Deut$15:9': ('oju', None, 'Deut$15:9 "Ẹ sọ́ra kí èrò búburú kan má ṣe sí nínú àyà rẹ wí pé, “Ọdún kéje tí í ṣe ọdún ìyọ̀ǹda gbésè ti súnmọ́” nípa bẹ́ẹ̀, ojú rẹ a sì burú sí arákùnrin rẹ talákà, tí ìwọ kò sì fun ní nǹkan, o kò sì fun ní nǹkan kan. Òun a si kígbe pe olúwa nitori rẹ, a si di ẹ̀ṣẹ̀ fún ọ."'), 'Deut$15:10': ('fun', 'ọwọ', 'Deut$15:10 "Fún un tọkàntọkàn, láì sí ìkùnsínú, nítorí èyí, OLÚWA Ọlọ́run rẹ yóò bùkun fún ọ nínú iṣẹ́ ọwọ́ rẹ, àti gbogbo ìdáwọ́lé rẹ."'), 'Deut$15:19': ('ṣe', None, 'Deut$15:19 " Gbogbo àkọ́bí tí o jẹ́ akọ nínú agbo màlúù rẹ ni kí o yà sọ́tọ̀ fún OLÚWA, àti nínú agbo ewúrẹ́ bákan náà. Ẹ má ṣe fi àkọ́bí màlúù yín ṣiṣẹ́ rárá. Má ṣe rẹ́ irun àkọ́bí àgùntàn rẹ pẹ̀lú."'), 'Deut$16:7': (None, 'lọ', 'Deut$16:7 "Ẹ ṣun ún kí ẹ sì jẹ ẹ́ ní ibi tí OLÚWA Ọlọ́run yín yóò yàn. Ní àárọ̀, kí ẹ padà sí àgọ́ ọ yín."'), 'Deut$16:10': ('fun', None, 'Deut$16:10 "Nígbà náà ni kí ẹ se àjọ̀dún ọ̀ṣẹ̀ sí OLÚWA Ọlọ́run yín, nípa pípèṣè ọrẹ àtinúwá, gẹ́gẹ́ bí ìbùkún tí OLÚWA Ọlọ́run yín fún un yín."'), 'Deut$16:20': ('ni', 'tẹle', 'Deut$16:20 "Ẹ tẹ̀lé ìdájọ́ òtítọ́ àní ìdájọ́ òtítọ́ nìkan, kí ẹ báà lè gbé kí ẹ sì gba ilẹ̀ tí OLÚWA Ọlọ́run yín ń fún un yín."'), 'Deut$17:2': (None, 'kú', 'Deut$17:2 " Bí ọkùnrin tàbí obìnrin kan tó ń gbé láàrin yín ní èyíkéyìí ìlú wọ̀n-ọn-nì, tí OLÚWA fún un yín bá ń ṣe ibi ní ojú OLÚWA Ọlọ́run yín, ní ríré májẹ̀mú rẹ̀ kọjá,"'), 'Deut$17:8': (None, 'wá', 'Deut$17:8 "Bí ẹjọ́ bá wá sí ilé ẹjọ́ ọ yín tí ó nira jù láti dá: yálà ìtàjẹ̀sílẹ̀ ni, ọ̀ràn dídá tàbí ìkanni-lábùkù: Ẹ mú wọn lọ sí ibi tí OLÚWA Ọlọ́run yín yóò yàn."'), 'Deut$17:18': (None, None, 'Deut$17:18 "Nígbà tí ó bá dé orí oyè ilé ọba rẹ̀ kí ó ṣe àdàkọ àwọn òfin wọ̀nyí sí inú ìwé fún ara rẹ̀, láti inú un ti àlùfáà tí wọ́n jẹ́ ọmọ Léfì."'), 'Deut$18:1': ('ṣe', 'ni', 'Deut$18:1 " Àwọn àlùfáà tí wọ́n jẹ́ ọmọ Léfì, àní gbogbo ẹ̀yà Léfì, wọn kì yóò ní ìpín tàbí ogún láàrin Ísírẹ́lì, wọn yóò máa jẹ nínú ọrẹ ẹbọ tí a fi iná ṣe sí OLÚWA, nítorí òun ni ogún un tiwọn."'), 'Deut$18:3': ('fun', None, 'Deut$18:3 Gbogbo èyí ni yóò jẹ́ ìpín àwọn àlùfáà láti ọ̀dọ̀ àwọn ènìyàn tí ó fi akọ màlúù tàbí àgùntàn rúbọ; wọn yóò fi èjìká àti ẹ̀rẹ̀kẹ́ méjèèjì àti gbogbo inú fún àlùfáà.'), 'Deut$18:6': ('wá', 'ṣe', 'Deut$18:6 "Tí ọmọ Léfì kan bá sì wá láti ibikíbi nínú àwọn ìlú u yín ní Ísírẹ́lì, níbi tí ó ń gbé ṣe àtìpó, tí ó sì wá gẹ́gẹ́ bí ìfẹ́ rẹ̀ sí ibi tí Ọlọ́run yóò yàn."'), 'Deut$18:9': ('wá', None, 'Deut$18:9 "Nígbà tí o bá wọ ilẹ̀ tí OLÚWA Ọlọ́run rẹ yóò fi fún ọ, ìwọ kò gbọdọ̀ kọ́ láti tẹ̀lé àwọn ọ̀nà ìríra àwọn orílẹ̀ èdè náà tí ó wà níbẹ̀."'), 'Deut$18:11': ('oṣó', None, 'Deut$18:11 "tàbí afògèdè, tàbí jẹ́ a bá iwín gbìmọ̀ tàbí oṣó tàbí abókúsọ̀rọ̀."'), 'Deut$18:15': (None, None, 'Deut$18:15 OLÚWA Ọlọ́run rẹ yóò gbé wòlíì kan dìde gẹ́gẹ́ bí èmi láàrin àwọn arákùnrin rẹ. Tìrẹ ni kí o gbọ́.'), 'Deut$18:22': ('wá', None, 'Deut$18:22 "Nígbà tí ohun tí wòlíì bá sọ ní orúkọ OLÚWA kò bá wá sí ìmúṣẹ tàbí jẹ́ òtítọ́, ìyẹn ni ọ̀rọ̀ tí OLÚWA kò sọ, irú wòlíì bẹ́ẹ̀ tí sọ láìwádìí. Kí ẹ má ṣe bẹ̀rù rẹ̀."'), 'Deut$19:4': (None, None, 'Deut$19:4 Èyí ni òfin nípa ẹni tí ó pa ẹlòmíràn tí ó sì sá ṣíbẹ̀ láti pa ẹ̀mí rẹ̀ mọ́. Ẹni tí ó pa aládùúgbò láìmọ̀ọ́mọ̀ṣeé láì jẹ́ pé ó ti ní àrankàn pẹ̀lú u rẹ̀ láti ọjọ́ pípẹ́.'), 'Deut$19:8': (None, None, 'Deut$19:8 "Tí OLÚWA Ọlọ́run rẹ bá sì fẹ́ agbègbè rẹ, gẹ́gẹ́ bí ó ti ṣe ìbúra fún àwọn baba ńlá a yín, tí ó sì fún ọ ní gbogbo ilẹ̀ tí ó ti ṣe ìlérí fún wọn,"'), 'Deut$19:14': ('ni', 'gbà', 'Deut$19:14 Má ṣe pa òkúta ààlà aládúgbò rẹ dà tí aṣíwájú rẹ fi lélẹ̀ nínú ogún tí ó gbà nínú ilẹ̀ tí OLÚWA Ọlọ́run rẹ fi fún ọ láti ni ní ìní.'), 'Deut$19:16': ('ṣe', None, 'Deut$19:16 " Tí alárankàn ẹlẹ́rìí èké bá dúró láti fi ẹ̀ṣùn ẹ̀ṣẹ̀ kan ọkùnrin kan,"'), 'Deut$20:1': ('mu', 'ilẹ', 'Deut$20:1 "Nígbà tí o bá lọ sí ogun pẹ̀lú ọ̀ta rẹ, tí o sì rí ẹṣin àti àwọn kẹ̀kẹ́ ogun àti ológun tí ó jù ọ́ lọ, má ṣe bẹ̀rù u wọn, nítorí OLÚWA Ọlọ́run rẹ tí ń mú ọ jáde wá láti ilẹ̀ Éjíbítì, yóò wà pẹ̀lú rẹ."'), 'Deut$20:2': ('wá', None, 'Deut$20:2 "Nígbà tí o bá fẹ́ lọ jagun, àlùfáà yóò wá ṣíwájú, yóò sì bá ọmọ ogun sọ̀rọ̀,"'), 'Deut$20:9': ('ni', None, 'Deut$20:9 "Nígbà tí olórí ogun bá ti dákẹ́ ọ̀rọ̀ sísọ sí àwọn ènìyàn, wọn yóò yan olórí ogun lórí i rẹ̀."'), 'Deut$20:11': ('sin', None, 'Deut$20:11 "Tí wọ́n bá gbà tí wọ́n sì sí ìlẹ̀kùn, gbogbo àwọn tó wà níbẹ̀ yóò máa jẹ́ olùsìn fún ọ, wọn ó sì máa sìn ọ́."'), 'Deut$20:12': ('ṣe', 'gba', 'Deut$20:12 "Tí wọ́n bá kọ̀ láti bá ọ ṣe àlàáfíà ṣùgbọ́n tí wọ́n bá gbógun tì ọ́, nígbà náà ni kí ìwọ gbà á."'), 'Deut$20:16': ('fun', 'ni', 'Deut$20:16 "Ṣùgbọ́n, nínú àwọn ìlú ti àwọn orílẹ̀ èdè tí OLÚWA Ọlọ́run rẹ ń fún ọ gẹ́gẹ́ bí ogún, má ṣe dá ohun ẹlẹ́mìí kankan sí."'), 'Deut$20:19': (None, 'gba', 'Deut$20:19 "Nígbà tí o bá dóti ìlú kan láti ọjọ́ pípẹ́, ẹ bá wọn jà láti gbà á, má ṣe pa àwọn igi ibẹ̀ run nípa gbígbé àáké lé wọn, nítorí o lè jẹ èso wọn. Má se gé wọn lulẹ̀. Nítorí igi ìgbẹ́ ha á ṣe ènìyàn bí, tí ìwọ ó máa dọ̀tí rẹ̀."'), 'Deut$21:1': (None, None, 'Deut$21:1 "Tí a bá rí ọkùnrin tí a pa, ní ìdùbúlẹ̀ ní pápá nínú ilẹ̀ tí OLÚWA Ọlọ́run rẹ ń fún ọ láti jogún, tí a kò sì mọ ẹni tí ó pa á,"'), 'Deut$21:10': (None, None, 'Deut$21:10 "Nígbà tí o bá lọ sí ogun sí àwọn ọ̀ta rẹ tí OLÚWA Ọlọ́run rẹ sì fi wọ́n lé ọ lọ́wọ́ tí o sì mú àwọn ìgbékùn,"'), 'Deut$21:14': (None, None, 'Deut$21:14 "Bí inú rẹ̀ kò bá sì dùn si i, jẹ́ kí ó lọ sí ibikíbi tí ó bá fẹ́. O kò gbọdọ̀ tà á tàbí lò ó bí ẹrú, lẹ́yìn ìgbà tí o ti dójú tì í."'), 'Deut$21:15': (None, 'ni', 'Deut$21:15 "Bí ọkùnrin kan bá ní ìyàwó méjì, tí ó sì fẹ́ ọ̀kan ṣùgbọ́n tí kò fẹ́ èkejì, tí àwọn méjèèjì sì bí àwọn ọmọkùnrin fún un ṣùgbọ́n tí àkọ́bí jẹ́ ọmọ ìyàwó rẹ̀ tí kò fẹ́ràn."'), 'Deut$21:18': (None, 'ni', 'Deut$21:18 " Bí ọkùnrin kan bá ní aláìgbọ́ràn tàbí ọlọ̀tẹ̀ ọmọ tí kò gbọ́rọ̀ sí baba àti ìyá rẹ̀ tí kò sì ní í gbà tí wọ́n bá ń bá a wí,"'), 'Deut$21:22': (None, None, 'Deut$21:22 " Bí ọkùnrin kan tí ó jẹ̀bi ẹ̀sùn bá ní láti kú tí ó sì kú, tí a sì gbé òkú rẹ̀ kọ́ sára igi,"'), 'Deut$22:13': ('gba', 'mú', 'Deut$22:13 "Bí ọkùnrin kan bá mú ìyàwó àti, lẹ́yìn ìgbà tí ó ti dùbúlẹ̀ pẹ̀lú u rẹ̀, kórìíra rẹ̀,"'), 'Deut$22:20': (None, None, 'Deut$22:20 "Ṣùgbọ́n bí ẹ̀sùn náà bá jẹ́ òtítọ́ tí a kò sí rí ẹ̀rí ìbálé obìnrin náà,"'), 'Deut$22:22': (None, None, 'Deut$22:22 "Bí o bá rí ọkùnrin kan tí ó bá ìyàwó ọkùnrin mìíràn lòpọ̀, àwọn méjèèjì ní láti kú. Ẹ ní láti pọ ohun búburú kúrò láàrin Ísírẹ́lì."'), 'Deut$22:28': (None, None, 'Deut$22:28 Bí ó bá ṣẹlẹ̀ pé ọkùnrin pàde wúndíá tí kò ì tí ì ní àfẹ́sọ́nà tí ó sì fi tipátipá báa ṣe tí a gbá wọn mú.'), 'Deut$23:12': (None, 'ni', 'Deut$23:12 "Sàmì sí ibìkan lóde àgọ́, níbi tí o lè máa lọ láti dẹ ara rẹ lára."'), 'Deut$23:23': (None, 'ẹnu', 'Deut$23:23 "Rí i dájú pé o ṣe ohunkóhun tí o bá sọ jáde láti ẹnu rẹ, nítorí pé o jẹ́ ẹ̀jẹ́ fún OLÚWA Ọlọ́run rẹ pẹ̀lú ẹnu ara rẹ."'), 'Deut$24:1': ('ri', 'wa', 'Deut$24:1 " Bí ọkùnrin kan bá fẹ́ obìnrin kan ní ìyàwó tí ó wá padà bà á nínú jẹ́ nítorí ó rí ohun àìtọ́ kan nípa rẹ̀, tí ó sì kọ ìwé ìkọ̀sílẹ̀ si i, fi fún un kí ó sì lọ jáde kúrò ní ilé è rẹ,"'), 'Deut$24:9': ('ṣe', None, 'Deut$24:9 Rántí ohun tí OLÚWA Ọlọ́run rẹ ṣe sí Míríámù lójú ọ̀nà lẹ́yìn ìgbà tí o jáde kúrò ní Éjíbítì.'), 'Deut$24:11': ('mú', None, 'Deut$24:11 Dúró síta gbangba kí o sì jẹ́ kí ọkùnrin tí o wín mú ògò rẹ jáde wá fún ọ.'), 'Deut$24:19': ('lọ', 'iṣẹ', 'Deut$24:19 " Nígbà tí o bá ń kórè oko rẹ tí o sì fojú fo ìtí kan, má ṣe padà lọ mú u. Fi í kalẹ̀ fún àwọn àlejò, aláìní baba àti opó, kí OLÚWA Ọlọ́run rẹ lè bùkún fún gbogbo iṣẹ́ ọwọ́ ọ̀ rẹ."'), 'Deut$25:5': ('ni', None, 'Deut$25:5 " Bí àwọn arákùnrin rẹ bá jọ ń gbé pọ̀, tí ọ̀kan wọn sì kú láìní ọmọkùnrin, opó rẹ̀ kò gbọdọ̀ fẹ́ ọkọ mìíràn tayọ ẹbí ọkọ rẹ̀. Arákùnrin ọkọ rẹ̀ ni yóò mú u ṣe aya, òun ni yóò sì ṣe ojúṣe ọkọ fún un."'), 'Deut$25:8': (None, None, 'Deut$25:8 "Nígbà náà ni àwọn àgbààgbà ìlú rẹ̀ yóò pè é, wọn yóò sì bá sọ̀rọ̀. Bí ó bá sì kọ̀ jálẹ̀ tí ó bá sọ pé, “Èmi kò fẹ́ láti fẹ́ ẹ,”"'), 'Deut$25:17': (None, None, 'Deut$25:17 Rántí ohun tí àwọn ará Ámélékì ṣe sí i yín ní ọ̀nà nígbà tí ẹ̀ ń jáde láti ilẹ̀ Éjíbítì wá.'), 'Deut$26:1': ('ilẹ', None, 'Deut$26:1 " Nígbà tí o bá wọ ilẹ̀ tí OLÚWA Ọlọ́run rẹ ń fi fún ọ gẹ́gẹ́ bí ohun ìní tí ìwọ sì ti jogún, tí o sì ti ń gbé níbẹ̀,"'), 'Deut$26:5': ('ilẹ', None, 'Deut$26:5 "Nígbà náà ni ìwọ yóò sọ ní iwájú OLÚWA Ọlọ́run rẹ pé, “Baba mi jẹ́ alárìnkiri ará Árámíà, ó sì sọ̀kalẹ̀ wá sí Éjíbítì pẹ̀lú ènìyàn díẹ̀, ó sì ń gbé níbẹ̀, ó sì wá di orílẹ̀ èdè olókìkí, alágbára, tí ó kún fún ọ̀pọ̀ ènìyàn."'), 'Deut$26:10': (None, 'ni', 'Deut$26:10 "àti pé ní báyìí, mo mú àkọ́so ilẹ̀ tí ìwọ OLÚWA ti fún mi wá.” Gbé agbọ̀n náà ṣíwájú OLÚWA Ọlọ́run rẹ, kí o sì wólẹ̀ níwájú u rẹ̀."'), 'Deut$26:12': ('ṣe', 'ni', 'Deut$26:12 "Nígbà tí o bá ṣetán láti ya ìdámẹ́wàá gbogbo ohun tí o ti mú jáde ní ọdún kẹ́ta sọ́tọ̀ sí apákan. Ọdún ìdámẹ́wàá, ìwọ yóò fi fún ọmọ Léfì, àjòjì, aláìní baba àti opó, kí wọn kí ó lè jẹ ní àwọn ìlú rẹ, kí wọn sì yó."'), 'Deut$26:14': (None, 'ni', 'Deut$26:14 "Èmi kò jẹ lára ohun mímọ́ ní ìgbà tí mo ń ṣọ̀fọ̀, bẹ́ẹ̀ ni èmi kò mu nínú wọn ní ìgbà tí mo wà ní àìmọ́, bẹ́ẹ̀ ni èmi kò fi nínú wọn fún òkú. Èmi ti ṣe ìgbọ́ràn sí OLÚWA Ọlọ́run mi, èmi sì ti ṣe gbogbo ohun tí ó pa láṣẹ fún mi."'), 'Deut$26:17': ('ṣe', None, 'Deut$26:17 "Ìwọ jẹ́wọ́ OLÚWA ní òní pé OLÚWA ni Ọlọ́run rẹ, àti pé ìwọ yóò máa rìn ní ọ̀nà rẹ̀ gbogbo, àti pé ìwọ yóò máa pa ìlànà rẹ̀, àṣẹ rẹ̀ àti òfin rẹ̀ mọ́, àti pé ìwọ yóò máa ṣe ìgbọ́ràn sí i."'), 'Deut$27:2': ('lọ', 'kọjá', 'Deut$27:2 "Nígbà tí o bá ti kọjá a Jọ́dánì sí inú ilẹ tí OLÚWA Ọlọ́run rẹ ń fún ọ, gbékalẹ̀ lára àwọn òkúta ńlá àti aṣọ ìlekè rẹ pẹ̀lú ẹfun."'), 'Deut$27:14': (None, 'eniyan', 'Deut$27:14 Àwọn ẹ̀yà Léfì yóò ké sí gbogbo ènìyàn Ísírẹ́lì ní ohùn òkè:'), 'Deut$28:1': (None, None, 'Deut$28:1 " Bí o bá gbọ́ràn sí OLÚWA Ọlọ́run rẹ ní kíkún kí o sì kíyèsára láti tẹ̀lé gbogbo ohun tí ó pa láṣẹ tí mo fi fún ọ lónìí, OLÚWA Ọlọ́run rẹ yóò gbé ọ lékè ju gbogbo orílẹ̀ èdè ayé lọ."'), 'Deut$28:11': ('fun', None, 'Deut$28:11 "OLÚWA yóò fi àlàáfíà fún ọ ní ẹ̀kúnrẹ́rẹ́, fún ọmọ inú rẹ, irú ohun ọ̀sìn rẹ àti àwọn èso ilẹ̀ rẹ ní ilẹ̀ tí ó ti búra fún àwọn baba ńlá rẹ láti fún ọ."'), 'Deut$28:12': ('fun', None, 'Deut$28:12 "OLÚWA yóò ṣí ọ̀run sílẹ̀, ìṣúra rere rẹ̀ fún ọ, láti rọ òjò sórí ilẹ̀ rẹ ní àsìkò rẹ àti láti bùkún gbogbo iṣẹ́ ọwọ́ ọ̀ rẹ. Ìwọ yóò yá ọ̀pọ̀lọpọ̀ orílẹ̀ èdè ṣùgbọ́n ìwọ kò ní í yá lọ́wọ́ ẹnìkankan."'), 'Deut$28:13': ('ṣe', None, 'Deut$28:13 "OLÚWA yóò fi ọ́ ṣe orí, ìwọ kì yóò sì jẹ́ ìrù. Bí o bá fi etí sílẹ̀ sí àṣẹ OLÚWA Ọlọ́run rẹ tí mo fi fún ọ kí o sì máa rọra tẹ̀lé wọn, ìwọ yóò máa wà lókè, bẹ́ẹ̀ ni ìwọ kì yóò wà ní ìsàlẹ̀ láé."'), 'Deut$28:15': ('ṣe', 'wá', 'Deut$28:15 "Ṣùgbọ́n bí o kò bá gbọ́ ti OLÚWA Ọlọ́run rẹ kí o sì rọra máa tẹ̀lé gbogbo àṣẹ àti òfin rẹ̀ tí mò ń fún ọ lónìí, gbogbo ègún yìí yóò wá sóríì rẹ yóò sì máa bá ọ lọ:"'), 'Deut$28:21': ('ni', None, 'Deut$28:21 OLÚWA yóò fi àjàkálẹ̀-àrùn bá ọ jà títí tí yóò fi pa ọ́ run kúrò ní ilẹ̀ tí ò ń wọ̀ lọ láti ní.'), 'Deut$28:30': (None, 'gbe', 'Deut$28:30 "Ìwọ yóò gba ògo láti fẹ obìnrin kan ṣùgbọ́n ẹlòmíràn yóò gbà á yóò sì tẹ́ ẹ́ ní ògo. Ìwọ yóò kọ́ ilé, ṣùgbọ́n ìwọ kì yóò gbé ibẹ̀. Ìwọ yóò gbin ọgbà àjàrà, ṣùgbọ́n ìwọ kò tilẹ̀ ní gbádùn èṣo rẹ."'), 'Deut$28:31': (None, 'ni', 'Deut$28:31 "Màlúù rẹ yóò di pípa níwájú ù rẹ, ṣùgbọ́n ìwọ kì yóò jẹ ìkankan nínú rẹ̀. Kẹ́tẹ́kẹ́tẹ́ rẹ yóò sì di mímú pẹ̀lú agbára kúrò lọ́dọ̀ rẹ, a kì yóò sì da padà. Àgùntàn rẹ yóò di mímú fún àwọn ọ̀ta rẹ, ẹnìkankan kò sì ní gbà á."'), 'Deut$28:32': (None, 'ọwọ', 'Deut$28:32 "Àwọn ọmọkùnrin àti ọmọbìnrin rẹ yóò di mímú fún àwọn orílẹ̀ èdè mìíràn, ìwọ yóò sì fi ojú ù rẹ sọ́nà dúró dè wọ́n láti ọjọ́ dé ọjọ́, ìwọ yóò sì jẹ́ aláìlágbára láti gbé ọwọ́."'), 'Deut$28:33': ('ilẹ', 'orílẹ-èdè', 'Deut$28:33 "Èṣo ilẹ̀ rẹ àti gbogbo iṣẹ́ làálàá rẹ ni orílẹ̀-èdè mìíràn tí ìwọ kò mọ̀ yóò sì jẹ, ìwọ yóò jẹ́ kìkì ẹni ìnilára àti ẹni ìtẹ̀mọ́lẹ̀"'), 'Deut$28:40': (None, None, 'Deut$28:40 "Ìwọ yóò ní igi ólífì jákèjádò ilẹ̀ rẹ ṣùgbọ́n ìwọ kò ní lo òróró náà, nítorí ólífì náà yóò rẹ̀ dànù."'), 'Deut$28:43': (None, None, 'Deut$28:43 "Àlejò tí ń gbé láàrin rẹ yóò gbé sókè gíga jù ọ́ lọ, ṣùgbọ́n ìwọ yóò máa di ìrẹ̀sílẹ̀."'), 'Deut$28:45': (None, 'wá', 'Deut$28:45 "Gbogbo ègun yìí yóò wá sórí rẹ, wọn yóò lé ọ wọn yóò sì bá ọ títí tí ìwọ yóò fi parun, nítorí ìwọ kò gbọ́ràn sí OLÚWA Ọlọ́run rẹ, ìwọ kò sì kíyèsí àṣẹ àti òfin tí ó fi fún ọ."'), 'Deut$28:47': (None, None, 'Deut$28:47 Nítorí ìwọ kò sin OLÚWA Ọlọ́run rẹ ní ayọ̀ àti inú dídùn ní àkókò àlàáfíà.'), 'Deut$28:49': ('ilẹ', None, 'Deut$28:49 " OLÚWA yóò mú àwọn orílẹ̀ èdè wá sí ọ láti ọ̀nà jínjìn, bí òpin ayé, bí idì ti ń bẹ́ láti orí òkè sílẹ̀, orílẹ̀ èdè tí ìwọ kò gbọ́ èdè wọn."'), 'Deut$28:53': (None, 'fun', 'Deut$28:53 "Nítorí ìyà tí ọ̀ta à rẹ yóò fi jẹ ọ́ nígbà ìgbógun tì, ìwọ yóò jẹ, ẹran ara ọmọkùnrin àti ọmọbìnrin tí OLÚWA Ọlọ́run ti fi fún ọ."'), 'Deut$28:58': (None, 'orukọ', 'Deut$28:58 "Bí ìwọ kò bá rọra tẹ̀lé gbogbo àwọn ọ̀rọ̀ òfin yìí, tí a kọ sínú ìwé yìí, tí ìwọ kò sì bọlá fún ògo yìí àti orúkọ dáradára: orúkọ OLÚWA Ọlọ́run rẹ,"'), 'Deut$28:63': (None, 'ṣe', 'Deut$28:63 "Gẹ́gẹ́ bí ó ti dùn mọ́ OLÚWA nínú láti mú ọ ṣe rere àti láti pọ̀ si ní iye, bẹ́ẹ̀ ni yóò dùn mọ́ ọ nínú láti bì ọ́ ṣubú kí ó sì pa ọ́ run. Ìwọ yóò di fífà tu kúrò lórí ilẹ̀ tí ò ń wọ̀ lọ láti ní."'), 'Deut$28:65': ('oju', 'okan', 'Deut$28:65 "Ìwọ kì yóò sinmi láàrin àwọn orílẹ̀ èdè náà, kò sí ibi ìsinmi fún àtẹ́lẹ́ṣẹ̀ rẹ. Níbẹ̀ ni OLÚWA yóò ti fún ọ ní ọkàn ìnàngà, àárẹ̀ ojú, àti àìnírètí àyà."'), 'Deut$29:2': (None, None, 'Deut$29:2 "Móṣè pàṣẹ fún gbogbo àwọn ọmọ Ísírẹ́lì ó sì sọ fún wọn wí pé: Ojú rẹ ti rí gbogbo ohun tí OLÚWA ti ṣe ní Éjíbítì sí Fáráò àti, sí gbogbo àwọn ìjòyè e rẹ̀ àti sí ilẹ̀ ẹ rẹ̀."'), 'Deut$29:14': ('ṣe', None, 'Deut$29:14 "Èmi ń ṣe májẹ̀mú yìí, pẹ̀lú ìbúra rẹ̀, kì í ṣe fún ìwọ nìkan"'), 'Deut$29:21': (None, None, 'Deut$29:21 "OLÚWA yóò sì yọ òun nìkan kúrò nínú gbogbo ẹ̀yà Ísírẹ́lì fún àjálù, gẹ́gẹ́ bí gbogbo ègún májẹ̀mú tí a kọ sí inú ìwé òfin yìí."'), 'Deut$29:25': (None, None, 'Deut$29:25 "Ìdáhùn yóò sì jẹ́ báyìí: “Nítorí tí ènìyàn yìí kọ̀ OLÚWA Ọlọ́run baba wọn sílẹ̀, májẹ̀mú tí ó ti bá wọn dá nígbà tí ó mú wọn jáde wá láti Éjíbítì."'), 'Deut$30:1': (None, None, 'Deut$30:1 "Nígbà tí gbogbo ìbùkún àti ègún wọ̀nyí tí mo ti gbé kalẹ̀ ṣíwájú u yín bá wá sórí i yín àti tí o bá mú wọn sí àyà rẹ níbikíbi tí OLÚWA Ọlọ́run rẹ bá tú ọ ká sí láàrin àwọn orílẹ̀ èdè,"'), 'Deut$30:4': ('mú', 'mú', 'Deut$30:4 " Bí ó tilẹ̀ jẹ́ pé ó ti lé ẹni rẹ kan sí ilẹ̀ tí ó jìnnà jù lábẹ́ ọ̀run, láti ibẹ̀ OLÚWA Ọlọ́run rẹ yóò ṣà yín jọ yóò sì tún mú u yín padà."'), 'Deut$30:9': ('ṣe', 'iṣẹ', 'Deut$30:9 "Nígbà náà ni OLÚWA Ọlọ́run rẹ yóò mú ọ ṣe rere nínú gbogbo iṣẹ́ ọwọ́ ọ̀ rẹ, àti nínú gbogbo ọmọ inú ù rẹ, agbo ẹran ọ̀sìn rẹ àti èṣo ilẹ̀ rẹ. OLÚWA yóò tún mú inú dídùn sínú ù rẹ yóò sì mú ọ ṣe déédé, gẹ́gẹ́ bí ó ti fi inú dídùn sínú àwọn baba rẹ."'), 'Deut$30:15': ('ni', None, 'Deut$30:15 "Wòó mo gbé kalẹ̀ síwájú u yín lónìí, ìyè àti àlàáfíà, ikú àti ìparun."'), 'Deut$30:17': ('lọ', None, 'Deut$30:17 "Ṣùgbọ́n tí ọkàn an yín bá yí padà tí ìwọ kò sì ṣe ìgbọ́ràn, àti bí o bá fà, lọ láti foríbalẹ̀ fún ọlọ́run mìíràn àti sìn wọ́n,"'), 'Deut$30:19': (None, 'ayé', 'Deut$30:19 "Lónìí mo pe ọ̀run àti ayé bí ẹlẹ́rìí sí ọ pé mo ti gbékalẹ̀ síwájú rẹ ìyè àti ikú, ìbùkún àti ègún. Nísinsìnyìí yan ìyè, nítorí kí ìwọ àti àwọn ọmọ rẹ lè gbé"'), 'Deut$31:5': (None, 'fun', 'Deut$31:5 "OLÚWA yóò fi wọ́n fún ọ, kí o sì ṣe sí wọn gbogbo èyí tí mo ti pa láṣẹ fún ọ."'), 'Deut$31:6': ('lọ', None, 'Deut$31:6 " Jẹ́ alágbára kí o sì ní ìgboyà. Má se bẹ̀rù tàbí jáyà nítorí i wọn, nítorí OLÚWA Ọlọ́run rẹ ń lọ pẹ̀lú ù rẹ; Òun kò ní fi ọ́ sílẹ̀ tàbí kọ̀ ọ́ sílẹ̀.”"'), 'Deut$31:8': ('lọ', None, 'Deut$31:8 OLÚWA fúnra rẹ̀ ń lọ níwájú rẹ yóò sì wà pẹ̀lú ù rẹ; kò ní fi ọ́ sìlẹ̀ tàbí kọ̀ ọ́ sìlẹ̀. Má ṣe bẹ̀rù má sì ṣe fòyà.”'), 'Deut$31:10': (None, None, 'Deut$31:10 "Nígbà náà ni Móṣè pàṣẹ fún wọn, “Ní òpin ọdún méje méje, ní àkókò ọdún ìdásílẹ̀, nígbà àjọ àwọn àgọ́."'), 'Deut$31:12': ('lọ', 'gbe', 'Deut$31:12 "Pe àwọn ènìyàn jọ àwọn ọkùnrin, àwọn obìnrin àti àwọn àlejò tí ń gbé àwọn ìlú u yín kí wọn lè fetí sí i kí wọn sì lè kọ́ láti bẹ̀rù OLÚWA Ọlọ́run rẹ kí wọn sì rọra tẹ̀lé gbogbo ọ̀rọ̀ òfin yìí."'), 'Deut$31:14': ('fun', 'bayi', 'Deut$31:14 "OLÚWA sọ fún Móṣè pé, “Ọjọ́ ikú rẹ ti súnmọ́tílé báyìí. Pe Jóṣúà kí ẹ sì fi ara yín hàn nínú àgọ́ àjọ, níbi tí èmi yóò ti fi àṣẹ fún un.” Nígbà náà ni Móṣè àti Jóṣúà wá, wọ́n sì fara wọn hàn níbi àgọ́ àjọ."'), 'Deut$31:16': ('lọ', None, 'Deut$31:16 "OLÚWA sì sọ fún Móṣè pé: “Ìwọ ń lọ sinmi pẹ̀lú àwọn baba à rẹ, àwọn wọ̀nyí yóò sì ṣàgbèrè ara wọn sí ọlọ́run àjèjì ilẹ̀ tí wọn ń wọ̀ lọ láìpẹ́. Wọn yóò kọ̀ mí sílẹ̀ wọn yóò sì da májẹ̀mú tí mo bá wọn dá."'), 'Deut$31:20': ('mu', 'ilẹ', 'Deut$31:20 "Nígbà tí mo ti mú wọn wá sí ilẹ̀ tí ń ṣàn fún wàrà àti fún oyin, ilẹ̀ tí mo ti ṣe ìlérí ní ìbúra fún àwọn baba ńlá wọn, àti nígbà tí wọ́n bá jẹ, tí ó sì tẹ́ wọn lọ́rùn, tí wọ́n sì gbilẹ̀, wọn yóò yí padà sí ọlọ́run mìíràn wọn yóò sì sìn wọ́n, wọn yóò kọ̀ mí sílẹ̀, wọn yóò sì da májẹ̀mú mi."'), 'Deut$31:21': ('lọ', None, 'Deut$31:21 "Àti nígbà tí ọ̀pọ̀lọpọ̀ ìpọ́njú àti ìṣòro bá wá sórí i wọn, orin yìí yóò jẹ́ ẹ̀rí sí wọn, nítorí kò ní di ìgbàgbé fún àwọn ọmọ wọn. Mo mọ̀ ohun tí wọ́n ní inú dídùn sí láti ṣe, pàápàá kí èmi tó mú wọn wá sí ilẹ̀ tí mo ṣe ìlérí fún wọn lórí ìbúra.”"'), 'Deut$31:23': ('mú', 'ilẹ', 'Deut$31:23 "OLÚWA sì fún Jósúà ọmọ Núnì ní àṣẹ yìí: “Jẹ́ alágbára àti onígboyà, nítorí ìwọ yóò mú Ísírẹ́lì wá sí ilẹ̀ tí mo ṣèlérí fún wọn lórí ìbúra, èmi fúnra à mi yóò sì wà pẹ̀lú ù rẹ.”"'), 'Deut$31:24': ('ti pari', 'opin', 'Deut$31:24 "Lẹ́yìn ìgbà tí Móṣè ti parí i kíkọ ọ̀rọ̀ òfin yìí sínú ìwé láti ìbẹ̀rẹ̀ dé òpin,"'), 'Deut$31:29': ('yipada', None, 'Deut$31:29 "Nítorí tí mo mọ̀ pé lẹ́yìn ikú mi, ó dájú pé ìwọ yóò padà di ìbàjẹ́ pátápátá, ẹ ó sì yípadà kúrò ní ọ̀nà tí mo ti pa láṣẹ fún un yín. Ní ọjọ́ tó ń bọ̀, ìpọ́njú yóò sọ̀kalẹ̀ sórí i yín nítorí ẹ̀yin yóò ṣe búburú níwájú OLÚWA ẹ ó sì mú u bínú nípa ohun tí ọwọ́ ọ yín ti ṣe.”"'), 'Deut$32:9': (None, 'ipin', 'Deut$32:9 "Nítorí ìpín OLÚWA ni àwọn ènìyàn an rẹ̀, Jákọ́bù ni ìpín ìní i rẹ̀. "'), 'Deut$32:15': ('ọra', 'sanra', 'Deut$32:15 "Jéṣúrúnì sanra tán ó sì tàpá; ìwọ ṣanra tán, ìwọ ki tan, ọ̀rá sì bò ọ́ tán. O kọ Ọlọ́run tí ó dá ọ o sì kọ àpáta ìgbàlà rẹ."'), 'Deut$32:22': (None, 'ina', 'Deut$32:22 "Nítorí pé iná kan ràn nínú ìbínú un mi, yóò sì jó dé ipò ikú ní ìṣàlẹ̀. Yóò sì run ayé àti ìkórè e rẹ̀ yóò sì tiná bọ ìpìlẹ̀ àwọn òkè ńlá. "'), 'Deut$32:26': (None, None, 'Deut$32:26 "Mo ní èmi yóò tú wọn ká èmi yóò sì mú ìrántí i wọn dà kúró nínú àwọn ènìyàn,"'), 'Deut$32:41': ('gba', None, 'Deut$32:41 "nígbà tí mo bá pọ́n idà dídán mi àti tí mo bá sì fi ọwọ́ mi lé ìdájọ́, Èmi yóò san ẹ̀san fún àwọn ọ̀tá mi Èmi kò sì san fún àwọn tí ó kórìíra à mi."'), 'Deut$32:45': (None, 'ṣe', 'Deut$32:45 Nígbà tí Móṣè parí i kíka gbogbo ọ̀rọ̀ wọ̀nyí sí Ísírẹ́lì.'), 'Deut$32:48': (None, None, 'Deut$32:48 "Ní ọjọ́ kan náà, OLÚWA sọ fún Móṣè pé,"'), 'Deut$33:2': (None, 'oke', 'Deut$33:2 "Ó sì wí pé: “OLÚWA ti Sínáì wá, ó sì yọ sí wọn láti Ṣéírì wá ó sì tàn án jáde láti òkè Páránì wá. Ó ti ọ̀dọ̀ ẹgbẹgbàarùn ún àwọn mímọ́ wá láti ọwọ́ ọ̀tún rẹ̀ ni òfin kan amúbíiná ti jáde fún wọn wá."'), 'Deut$33:6': (None, None, 'Deut$33:6 "“Jẹ́ kí Rúbẹ́nì yè kí ó má ṣe kú, tàbí kí ènìyàn rẹ mọ níwọ̀n.”"'), 'Deut$33:8': ('mọ', None, 'Deut$33:8 "Ní ti Léfì ó wí pé: “Jẹ́ kí Túmímù àti Úrímù rẹ kí ó wà pẹ̀lú ẹni mímọ́ rẹ. Ẹni tí ó dánwò ní Másà, ìwọ bá jà ní omi Méríbà."'), 'Deut$33:13': ('ilẹ', None, 'Deut$33:13 "Ní ti Jóṣẹ́fù ó wí pé: “Kí OLÚWA bùkún ilẹ̀ rẹ, fún ohun iyebíye láti ọ̀run pẹ̀lú ìrì àti ibú tí ó ń bẹ níṣàlẹ̀;"'), 'Deut$34:4': ('ni', 'fun', 'Deut$34:4 "Nígbà náà ní OLÚWA sọ fún un pé, “Èyí ni ilẹ̀ tí mo ṣèlérí lórí ìbúra fún Ábúráhámù, Ísáákì, àti Jákọ́bù nígbà tí mo wí pé, ‘Èmi yóò fi fún irú ọmọ rẹ.’ Mo ti jẹ́ kí o rí i pẹ̀lú ojú ù rẹ, ṣùgbọ́n ìwọ kì yóò débẹ̀.”"'), 'Josh$1:3': (None, 'ni', 'Josh$1:3 "Èmi yóò fún un yín ní gbogbo ibi tí ẹ bá fi ẹṣẹ̀ ẹ yín tẹ̀, gẹ́gẹ́ bí mo ti ṣe ìlérí fún Móṣè."'), 'Josh$1:8': ('ṣe', 'ni', 'Josh$1:8 "Má ṣe jẹ́ kí Ìwé Ofin yìí kúrò ní ẹnu ù rẹ, máa ṣe àṣàrò nínú u rẹ̀ ní ọ̀ṣán àti ní òru, kí ìwọ kí ó lè ṣọ́ra láti ṣe gbogbo nǹkan tí a kọ sí inú u rẹ̀. Nígbà náà ni yóò dára fún ọ, ìwọ yóò sì ní àṣeyọrí."'), 'Josh$1:10': ('lọ', None, 'Josh$1:10 Báyìí ni Jóṣúà pàṣẹ fún olórí àwọn ènìyàn rẹ̀'), 'Josh$1:14': (None, 'kọjá', 'Josh$1:14 "Àwọn ìyàwó yín, àwọn ọmọ yín àti àwọn ẹran ọ̀sìin yín lè dúró ní ilẹ̀ tí Móṣè fún un yin ní ìlà-oòrùn Jọ́dánì; ṣùgbọ́n gbogbo àwọn jagunjagun un yín, pẹ̀lú ìhámọ́ra ogun gbọdọ̀ kọjá ṣíwájú àwọn arákùnrin yín. Ẹ̀yin yóò sì rán àwọn arákùnrin yín lọ́wọ́"'), 'Josh$1:16': (None, 'ṣe', 'Josh$1:16 "Nígbà náà ni wọ́n dá Jósúà lóhùn pé, “Ohunkóhun tí ìwọ pa láṣẹ fún wa ni àwa yóò ṣe; ibikíbi tí ìwọ bá rán wa ni àwa yóò lọ."'), 'Josh$2:4': (None, None, 'Josh$2:4 "Ṣùgbọ́n obìnrin náà ti mú àwọn ọkùnrin méjì náà, o sì fi wọ́n pamọ́. Ó sì wí pé, “lóótọ́ ní àwọn ọkùnrin náà wá sí ọ̀dọ̀ mi, ṣùgbọ́n èmi kò mọ ibi tí wọ́n ti wá."'), 'Josh$2:12': (None, None, 'Josh$2:12 "Ǹjẹ́ nísinsìnyìí, ẹ jọ̀wọ́, ẹ búra fún mi ní ti OLÚWA pé ẹ̀yin yóò ṣe àánú fún ìdílé mi, nítorí pé mo ti ṣe yín ní oore. Ẹ fún mi ní àmì tó dájú:"'), 'Josh$2:16': (None, 'lọ', 'Josh$2:16 "Ó sì ti ṣọ fún wọn pé, “Ẹ lọ sí orí òkè, kí ẹ sì fi ara pamọ́ níbẹ̀ fún ọjọ́ mẹ́ta kí àwọn tí ń lépa yín má baà rí i yín títí tí wọn yóò fi darí. Lẹ́yìn náà kí ẹ máa bá ọ̀nà yín lọ.”"'), 'Josh$2:18': ('mú', 'ilẹ', 'Josh$2:18 "Nígbà tí àwa bá wọ ilé è rẹ, ìwọ yóò ṣo okùn aláwọ̀ aró yìí sí ojú fèrèsé èyí tí ìwọ fi sọ̀ wá kalẹ̀, kí ìwọ kí ó mú baba rẹ, ìyá rẹ, àwọn arákùnrin in rẹ àti gbogbo ìdílé è rẹ kí wọ́n wá sí inú ilé è rẹ."'), 'Josh$2:23': (None, None, 'Josh$2:23 "Nígbà náà ni àwọn ọkùnrin méjì náà padà. Wọ́n sì ṣọ̀kalẹ̀ láti orí òkè, wọ odò, wọ́n sì wá sí ọ̀dọ̀ Jóṣúà ọmọ Núnì; wọ́n sì sọ fún un gbogbo ohun tí ó ti sẹlẹ̀ sí wọn."'), 'Josh$3:2': ('lọ', None, 'Josh$3:2 Lẹ́yìn ọjọ́ kẹ́ta àwọn olórí la àárin ibùdó já.'), 'Josh$3:4': (None, None, 'Josh$3:4 "Ẹyin yóò lè mọ ọ̀nà tí ẹ ó gbà, torí pé ẹ̀yin kò gba ọ̀nà yìí tẹ́lẹ̀ rí. Ṣùgbọ́n àlàfo gbọdọ̀ wà ní àárin yín àti àpótí náà, tó bí ìwọ̀n ẹgbẹ̀rún un mítà (1000).”"'), 'Josh$3:8': ('duro', None, 'Josh$3:8 "Sọ fún àwọn àlùfáà tí ó ru àpótí ẹ̀rí náà: ‘Nígbà tí ẹ bá dé etí omi Jọ́dánì, ẹ lọ kí ẹ sì dúró nínú odò náà.’ ”"'), 'Josh$3:12': (None, None, 'Josh$3:12 "Ǹjẹ́ nísinsìn yí, ẹ mú ọkùnrin méjìlá (12) nínú àwọn ẹ̀yà Ísírẹ́lì, ẹnìkan nínú ẹ̀yà kọ̀ọ̀kan."'), 'Josh$3:13': (None, None, 'Josh$3:13 "Bí àwọn àlùfáà tí ó ru àpótí OLÚWA, OLÚWA gbogbo ayé bá ti ẹṣẹ̀ bọ odò Jọ́dánì, omi tí ń ti òkè sàn wá yóò gé kúrò yóò sì gbájọ bí òkítì kan.”"'), 'Josh$4:1': (None, None, 'Josh$4:1 "Nígbà tí gbogbo orílẹ̀-èdè náà kọjá nínú odò Jọ́dánì tan, OLÚWA ṣọ fún Jóṣúà pé,"'), 'Josh$4:4': ('ní', None, 'Josh$4:4 "Bẹ́ẹ̀ ni Jóṣúà pe àwọn ọkùnrin méjìlá (12) tí ó ti yàn nínú àwọn ọmọ Ísírẹ́lì, ẹnìkan nínú ẹ̀yà kọ̀ọ̀kàn,"'), 'Josh$4:18': ('pada', None, 'Josh$4:18 "Àwọn àlùfáà náà jáde láti inú odò pẹ̀lú apòtí ẹ̀rí ni orí wọn. Bí wọ́n ti fi ẹṣẹ̀ ẹ wọn tẹ orí ilẹ̀ gbígbẹ ni omi Jọ́dánì náà padà sí àyè e rẹ̀, o sì kún wọ bèbè bí i ti àtẹ̀yìn wá."'), 'Josh$4:23': ('kọjá', None, 'Josh$4:23 "Nítorí OLÚWA Ọlọ́run yín mú Jọ́dánì gbẹ ní iwájú u yín títí ẹ̀yin fi kọjá. OLÚWA Ọlọ́run yín ti ṣe sí Jọ́dánì gẹ́gẹ́ bí ó ti se sí Òkun Pupa, nígbà tí ó mú un gbẹ ní iwájú wa títí àwa fi kọjá."'), 'Josh$5:1': ('okan', None, 'Josh$5:1 "Nígbà tí gbogbo àwọn ọba Ámórì ti ìlà-oòrùn Jọ́dánì àti gbogbo àwọn ọba Kénánì tí ń bẹ létí òkun gbọ́ bí OLÚWA ti mú Jọ́dánì gbẹ ní iwájú àwọn ọmọ Ísírẹ́lì títí ti a fi kọjá, ọkàn wọn pámi, bẹ́ẹ̀ ni wọn kò ní ìgboyà mọ́ láti dojú kọ àwọn ọmọ Ísírẹ́lì."'), 'Josh$5:4': (None, None, 'Josh$5:4 "Wàyí o, ìdí tí Jóṣúà fi kọ wọ́n nílà nìyìí: Gbogbo àwọn ọkùnrin tí ó ti Éjíbítì jáde wá, gbogbo àwọn ọkùnrin ogun kú ní asálẹ̀ ní ọ̀nà lẹ́yìn ìgbà tí wọ́n jáde kúrò ní ilẹ̀ Éjíbítì."'), 'Josh$5:13': ('lọ', None, 'Josh$5:13 "Nígbà tí Jóṣúà sún mọ́ Jẹ́ríkò, ó wo òkè ó sì rí ọkùnrin kan tí ó dúró ní iwájú rẹ̀ pẹ̀lú idà ní ọwọ́ rẹ̀. Jóṣúà sì tọ̀ ọ́ lọ, ó sì bi í pé, “Ǹjẹ́ ìwọ wà fún wa tàbí fún ọ̀ta a wa?”"'), 'Josh$6:2': (None, 'ọwọ', 'Josh$6:2 "Nígbà náà ni OLÚWA wí fún Jóṣúà pé, “Wò ó, mo ti fi Jẹ́ríkò lé ọ lọ́wọ́ pẹ̀lú ọba rẹ̀ àti àwọn jagunjagun rẹ̀."'), 'Josh$6:5': (None, None, 'Josh$6:5 "Nígbà tí ẹ bá gbọ́ ohùn un fèrè náà, kí àwọn ènìyàn hó-yèè ní igbe ńlá, nígbà náà ni odi ìlú náà yóò sì wó lulẹ̀, àwọn ènìyàn yóò sì lọ sí òkè, olúkúlùkù yóò sì wọ inú rẹ̀ lọ tààrà.”"'), 'Josh$6:10': ('ní', 'ṣe', 'Josh$6:10 "Ṣùgbọ́n Jóṣúà tí pàṣẹ fún àwọn ènìyàn pé, “Ẹ kò gbọdọ̀ kí-gbe ogun, ẹ kò gbọdọ̀ gbé ohùn yín sókè, ẹ má ṣe sọ ọ̀rọ̀ kan títí ọjọ́ tí èmi yóò sọ fún un yín pé kí ẹ hó. Nígbà náà ni ẹ̀yin yóò sì hó!”"'), 'Josh$6:16': (None, None, 'Josh$6:16 "Ní ìgbà kéje, nígbà tí àwọn àlùfáà fọn fèrè, Jóṣúà pàṣẹ fún àwọn ènìyàn pé, “Ẹ hó! Nítorí pé OLÚWA ti fún un yín ní ìlú náà."'), 'Josh$6:17': ('jẹ', None, 'Josh$6:17 Ìlú náà àti gbogbo ohun tí ó wà níbẹ̀ ni yóò jẹ́ ìyàṣọ́tọ̀ fún OLÚWA. Ráhábù tí ó jẹ́ panṣágà nìkan àti gbogbo àwọn tí ó wà pẹ̀lú u rẹ̀ ni a ó dásí; nítorí tí ó pa àwọn ayọ́lèẹ̀wò tí á rán mọ́.'), 'Josh$6:18': ('ṣe', None, 'Josh$6:18 "Ẹ pa ara yín mọ́ kúrò nínú ohun ìyàsọ́tọ̀ fún ìparun, kí ẹ̀yin kí ó má ba à ṣojú kòkòrò nípa mímú nínú àwọn ohun ìyàsọ́tọ̀. Bí kò ṣe bẹ́ẹ̀ ẹ ó sọ ibùdó Ísírẹ́lì di ìparun. Kí ẹ sì mú wàhálà wá sórí i rẹ̀."'), 'Josh$6:20': (None, None, 'Josh$6:20 "Nígbà tí àwọn àlùfáà fọn ìpè, àwọn ènìyàn náà sì hó Ó sì ṣe, bí àwọn ènìyàn ti gbọ́ ìró ìpè, tí àwọn ènìyàn sì hó ìhó-ńlá, odi ìlú náà wó lulẹ̀ bẹẹrẹ; bẹ́ẹ̀ ni olúkúlùkù ya wọ inú ìlú náà lọ tààrà, wọ́n sì kó ìlú náà."'), 'Josh$7:4': (None, None, 'Josh$7:4 "Bẹ́ẹ̀ ní àwọn bí ẹgbẹ̀rún mẹ́ta ọkùnrin lọ; ṣùgbọ́n àwọn ọkùnrin Áì lé wọn sá,"'), 'Josh$7:9': ('ayé', None, 'Josh$7:9 "Àwọn Kénánì àti àwọn ènìyàn ìlú tí ó kù náà yóò gbọ́ èyí, wọn yóò sì yí wa ká, wọn yóò sì ké orúkọ wa kúrò ní ayé. Kí ni ìwọ ó ha ṣe fún orúkọ ńlá à rẹ?”"'), 'Josh$7:11': ('ni', None, 'Josh$7:11 "Ísírẹ́lì ti ṣẹ̀; Wọ́n sì ti ba májẹ̀mú mi jẹ́, èyí tí mo pàṣẹ fún wọn pé kí wọn pamọ́. Wọ́n ti mú nínú ohun ìyàsọ́tọ̀, wọ́n ti jí, wọ́n pa irọ́, Wọ́n ti fi wọ́n sí ara ohun ìní wọn."'), 'Josh$7:19': ('fun', 'ṣe', 'Josh$7:19 "Nígbà náà ní Jóṣúà sọ fún Ákánì pé, “Ọmọ mi fi ògo fún OLÚWA, Ọlọ́run Ísírẹ́lì, kí o sì fi ìyin fún un. Sọ fún mi ohun tí ìwọ ti ṣe, má ṣe fi pamọ́ fún mi.”"'), 'Josh$7:20': (None, 'ni', 'Josh$7:20 "Ákánì sì dáhùn pé, “Òtítọ́ ni mo ti ṣẹ̀ sí OLÚWA, Ọlọ́run Ísírẹ́lì. Nǹkan tí mo ṣe nì yí:"'), 'Josh$8:1': (None, 'ọwọ', 'Josh$8:1 "Nígbà náà ni OLÚWA sọ fún Jóṣúà pé, “Má ṣe bẹ̀rù, kí àyà kí ó má ṣe fò ọ́. Kó gbogbo àwọn ọmọ ogun pẹ̀lúù rẹ, kí ẹ gòkè lọ gbógun ti Áì. Nítorí mo ti fi ọba Áì, àwọn ènìyàn an rẹ̀, ìlú u rẹ̀ àti ilẹ̀ ẹ rẹ̀ lé ọ lọ́wọ́."'), 'Josh$8:4': (None, None, 'Josh$8:4 Pẹ̀lú àwọn àṣẹ wọ̀nyí: “Ẹ fi etí sílẹ̀ dáadáa. Ẹ ba sí ẹ̀yìn ìlú náà. Ẹ má ṣe jìnnà sí i púpọ̀. Kí gbogbo yín wà ní ìmúra-sílẹ̀.'), 'Josh$8:8': ('ṣe', None, 'Josh$8:8 "Nígbà tí ẹ bá ti gba ìlú náà, kí ẹ sì ti iná bọ̀ ọ́. Kí ẹ ṣe ohun tí OLÚWA pàṣẹ. Mo ti fi àṣẹ fún un yín ná.”"'), 'Josh$8:14': (None, None, 'Josh$8:14 "Nígbà tí ọba Áì rí èyí, òun àti gbogbo ọkùnrin ìlú náà yára jáde ní òwúrọ̀ kùtùkùtù láti pàdé ogun Ísírẹ́lì ní pẹ̀tẹ́lẹ̀ aginjù. Ṣùgbọ́n kò mọ̀ pé àwọn kan wà ní ibùba ní ẹ̀yìn ìlú náà."'), 'Josh$8:18': ('fun', 'ọwọ', 'Josh$8:18 "Nígbà náà ni OLÚWA sọ fún Jóṣuà pé, “Na ọ̀kọ̀ tí ó wà lọ́wọ́ ọ rẹ sí Áì, nítorí tí èmi yóò fi ìlú náà lé ọ ní ọwọ́.” Bẹ́ẹ̀ ni Jóṣúà sì na ọ̀kọ̀ ọwọ́ ọ rẹ̀ sí Áì."'), 'Josh$8:24': (None, None, 'Josh$8:24 "Nígbà tí Ísírẹ́lì parí pípa àwọn ọkùnrin Áì ní pápá àti ní ihà ní ibi tí wọ́n ti lépa wọn lọ, nígbà tí wọ́n pa gbogbo wọn tan, gbogbo àwọn Ísírẹ̀lì sì padà sí Áì, wọ́n sì pa àwọn tí ó ṣẹ́kù síbẹ̀."'), 'Josh$9:3': (None, None, 'Josh$9:3 "Ṣùgbọ́n nígbà tí àwọn ènìyàn Gíbíónì gbọ́ ohun tí Jóṣúà ṣe sí Jẹ́ríkò àti Áì,"'), 'Josh$9:9': ('ṣe', 'wá', 'Josh$9:9 "Wọ́n sì dáhùn pé: “Ní ilẹ̀ òkèèrè ní àwọn ìránṣẹ́ rẹ tí wá, nítorí orúkọ OLÚWA Ọlọ́run rẹ. Nítorí tí àwa ti gbọ́ òkìkí rẹ àti ohun gbogbo tí ó ti ṣe ní Éjíbítì,"'), 'Josh$9:12': (None, None, 'Josh$9:12 Gbígbóná ní a mú oúnjẹ ní ojí nílé ní oji tí à ń bọ̀ wá sọ́dọ̀ yín. Ṣùgbọ́n ẹ wò ó bí ó ṣe gbẹ àti bí ó sì ṣe bu nísinsinyí.'), 'Josh$9:20': (None, None, 'Josh$9:20 "Èyí ní àwa yóò ṣe sí wọn, Àwa yóò dá wọn sí, kí ìbínú kí ó má baà wá sorí wa, nítorí ìbúra tí a búra fún wọn.”"'), 'Josh$9:21': ('jẹ', None, 'Josh$9:21 "Wọ́n tẹ̀ṣíwájú, “Ẹ dá wọn sí, ṣùgbọ́n ẹ jẹ́ kí wọn jẹ́ gégigégi àti apọnmi fún gbogbo ìlú.” Àwọn àgbààgbà náà sì mú ìlérí wọn sẹ fún wọn."'), 'Josh$9:24': ('fun', 'ni', 'Josh$9:24 "Wọ́n sì dá Jóṣúà lóhùn pé, “Nítorí tí a sọ fún àwọn ìránṣẹ́ rẹ dájú ṣáká bí OLÚWA Ọlọ́run rẹ ti pàṣẹ fún Mósè ìránṣẹ́ rẹ̀, láti fún yín ní gbogbo ilẹ̀ náà, kí ó sì pa gbogbo olùgbé ilẹ̀ náà run kúrò ní iwájú yín. Nítorí náà, àwa bẹ̀rù ẹ̀mí wa nítorí yín, èyí sì ni ìdí tí a fi ṣe bẹ́ẹ̀."'), 'Josh$9:25': (None, None, 'Josh$9:25 "Nísinsinyí, àwa wà ní ọwọ́ yín. Ohunkóhun tí ẹ bá rò pé ó yẹ ó si tọ́ lójú yín ní kí ẹ fi wá ṣe.”"'), 'Josh$10:1': (None, None, 'Josh$10:1 "Ní báyìí tí Adoni-Sédékì ọba Jérúsálẹ́mù gbọ́ pé Jóṣúà ti gba Áì, tí ó sì ti pa wọ́n pátapáta; tí ó sì ṣe sí Áì àti ọba rẹ̀ gẹ́gẹ́ bí ó ti ṣe sí Jẹ́ríkò àti ọba rẹ̀, àti bí àwọn ènìyàn Gíbíónì ti ṣe àdéhùn àlàáfíà pẹ̀lú Ísírẹ́lì, tí wọ́n sì ń gbé nítòsí wọn."'), 'Josh$10:8': (None, 'ni', 'Josh$10:8 "OLÚWA sì sọ fún Jóṣúà pé, “Má ṣe bẹ̀rù wọn; Mo ti fi wọ́n lé ọ lọ́wọ́. Kò sí ẹnikẹ́ni nínú wọn ti yóò lè dojú kọ ọ́.”"'), 'Josh$10:11': (None, None, 'Josh$10:11 "Bí wọ́n sì tí ń sá ní iwájú Ísírẹ́lì ní gẹ̀rẹ́gẹ̀rẹ́ ní ọ̀nà láti Bẹti-Hórónì títí dé Ásékà, OLÚWA rọ yìnyín ńlá sí wọn láti ọ̀run wá, àwọn tí ó ti ipa yìnyín kú sì pọju àwọn tí àwọn ará Ísírẹ́lì fi idà pa lọ."'), 'Josh$10:24': ('lọ', None, 'Josh$10:24 "Nígbà tí wọ́n mú àwọn ọba náà tọ Jóṣúà wá, ó pe gbogbo àwọn ọkùnrin Ísírẹ́lì, ó sì sọ fún àwọn olórí ọmọ ogun tí ó wà pẹ̀lú rẹ̀ pé, “Ẹ sún mọ́ bí, kí ẹ sì fi ẹsẹ̀ yín lé ọrùn àwọn ọba wọ̀nyí.” Bẹ́ẹ̀ ni wọ́n wá sí iwájú, wọ́n sì gbé ẹsẹ̀ lé ọrùn wọn."'), 'Josh$10:27': (None, None, 'Josh$10:27 "Nígbà tí oòrun wọ̀, Jóṣúà pàṣẹ, wọ́n sì ṣọ̀ wọ́n kalẹ̀ kúró ní orí igi, wọ́n sì gbé wọn jù sí inú ihò àpáta ní ibi tí wọ́n sápamọ̀ sí. Wọ́n sì fi òkúta ńlá dí ẹnu ihò náà, tí ó sì wà níbẹ̀ di òní yìí."'), 'Josh$10:29': ('kọjá', None, 'Josh$10:29 Nígbà náà ní Jóṣúà àti gbogbo Ísírẹ́lì tí ó wà pẹ̀lú rẹ̀ kọjá láti Mákédà lọ sí Líbínà wọ́n sì kọlù ú.'), 'Josh$10:31': ('kọjá', None, 'Josh$10:31 "Nígbà náà ni Jóṣúà àti gbogbo ará Ísírẹ́lì, tí ó wà pẹ̀lú rẹ̀ kọjá láti Líbínà lọ sí Lákíṣì; ó sì dótì í, ó sì kọlù ú."'), 'Josh$11:4': (None, 'ọpọlọpọ', 'Josh$11:4 "Wọ́n sì jáde pẹ̀lú gbogbo ogun wọn àti ọ̀pọ̀lọpọ̀ ẹsin àti kẹ̀kẹ́ ogun ńlá, wọ́n sì pọ̀, gẹ́gẹ́ bí iyanrìn etí òkun"'), 'Josh$11:16': ('òkè', None, 'Josh$11:16 "Bẹ́ẹ̀ ni Jóṣúà gba gbogbo ilẹ̀ náà: ilẹ̀ òkè, gbogbo Nẹ́gébù, gbogbo agbégbé Gósénì, ẹsẹ̀ òkè ti ìwọ̀-oòrùn, aginjù àti àwọn òkè àti pẹ̀tẹ́lẹ̀ Ísírẹ́lì,"'), 'Josh$13:2': (None, None, 'Josh$13:2 "“Èyí ni ilẹ̀ tí ó kù: gbogbo àwọn agbègbè àwọn Fílístínì, àti ti ara Gésúrì:"'), 'Josh$13:7': ('ilẹ', None, 'Josh$13:7 pín ilẹ̀ yìí ní ilẹ̀-ìní fún àwọn ẹ̀yà mẹ́sàn án àti ìdajì ẹ̀yà Mànásè.”'), 'Josh$13:25': (None, None, 'Josh$13:25 "Agbègbè ìlú Jásérì, gbogbo ìlú Gílíádì àti ìdajì orílẹ̀ èdè àwọn ọmọ ará Ámónì títí dé Áróérì ní ẹ̀bá Rábà;"'), 'Josh$14:7': ('mu', 'ilẹ', 'Josh$14:7 "Mo jẹ́ ọmọ ogójì ọdún nígbà tí Mósè ìránṣẹ́ OLÚWA rán mi láti Kádesi-Báníyà lọ rin ilẹ̀ náà wò. Mo sì mú ìròyìn wá fún un gẹ́gẹ́ bí o ti dá mi lójú,"'), 'Josh$14:9': (None, 'jẹ', 'Josh$14:9 "Ní ọjọ́ náà, Mósè búra fún mi pé, ‘Ilẹ̀ tí ẹsẹ̀ rẹ ti rìn ni yóò jẹ́ ìní rẹ àti ti àwọn ọmọ rẹ láéláé, nítorí pé ìwọ fi tọkàntọkàn wà Pẹ̀lú OLÚWA Ọlọ́run mi.’"'), 'Josh$14:11': (None, None, 'Josh$14:11 "Síbẹ̀ èmi ní agbára lónìí, bí ìgbà tí Mósè rán mi jáde. Èmi sì ní agbára láti jáde fún ogun nísinsìnyí gẹ́gẹ́ bí mo ti wà ní ígbà náà."'), 'Josh$14:12': (None, None, 'Josh$14:12 "Nísinsìnyí fi ìlú orí òkè fún mi èyí tí OLÚWA ṣe ìlérí fún mi ní ọjọ́ náà. Ìwọ gbọ́ ní ìgbà náà pé àwọn ọmọ Ánákì ti wà ní ibẹ̀, àti àwọn ìlú wọn tóbi, pé ó sì se olódi, ṣùgbọ́n bí OLÚWA ba fẹ́, èmi yóò lé wọn jáde, gẹ́gẹ́ bí ó ti sọ.”"'), 'Josh$15:19': (None, 'fun', 'Josh$15:19 "Ó sì dáhùn pé, “Ṣe ojúrere àtàtà fún mi. Níwọ̀n ìgbà tí o ti fún mi ní ilẹ̀ ní Negefi fun mi ní ìsun omi pẹ̀lú.” Báyìí ni Kélẹ́bù fún un ní ìsun omi ti òkè àti ti ìṣàlẹ̀."'), 'Josh$17:14': (None, 'ipin', 'Josh$17:14 "Àwọn ẹ̀yà Jósẹ́fù méjèèje sì sọ fún Jóṣúà pé, “Kí ló dé ìwọ fi fún wa ní ìpín ilẹ̀ kan àti ìdá kan ní ìní? Nítorí àwa jẹ́ ọ̀pọ̀ ènìyàn tí OLÚWA ti bùkún lọ́pọ̀lọpọ̀.”"'), 'Josh$17:15': ('ilẹ', None, 'Josh$17:15 "Jóṣúà dá wọn lóhùn pé, “Bí ẹ bá pọ̀ bẹ́ẹ̀, tí òkè ìlú Éfúráímù bá kéré fún yin, ẹ gòkè lọ sí igbó kí ẹ sì sán ilẹ̀ òkè fún ara yín ní ibẹ̀ ní ilẹ̀ àwọn ará Pérísì àti ará Réfì.”"'), 'Josh$17:17': (None, None, 'Josh$17:17 "Ṣùgbọ́n Jóṣúà sọ fún àwọn ilé Jósẹ́fù: fún Éfúráímù àti Mánásè pé, “Lóòtọ́ ni ẹ pọ̀ lọ́pọ̀lọpọ̀, ẹ sì jẹ́ alágbára. Ẹ̀yin kí yóò sì ní ìpín kan ṣoṣo."'), 'Josh$18:6': (None, None, 'Josh$18:6 "Lẹ́yìn ìgbà tí ẹ bá kọ àpèjúwe ọ̀nà méjèèje ilẹ̀ náà, ẹ mú wọn wá sí ọ̀dọ̀ mi ní ibí, èmi ó sì ṣẹ́ gègé fún yín ní iwájú OLÚWA Ọlọ́run wa."'), 'Josh$18:7': ('fun', 'ni', 'Josh$18:7 "Ṣùgbọ́n àwọn ọmọ Léfì kò ní ìpín ní àárin yín, nítorí iṣẹ́ àlùfáà. OLÚWA ni ìní wọ́n. Gádì, Rúbẹ́nì àti ìdajì ẹ̀yà Mánásè, ti gba ìní wọn ní ìhà ìlà-oòrùn Jọ́dánì. Mósè ìránṣẹ́ OLÚWA ti fi fún wọn.”"'), 'Josh$18:14': (None, None, 'Josh$18:14 "Láti òkè tí ó kọjú sí Bẹti-Hórónì ní gúsù ààlà náà yà sí gúsù ní ìhà ìwọ̀-oòrùn, ó sì jáde sí Kiriati-Báálì (tí í ṣe Kiriati-Jéárímù), ìlú àwọn ènìyàn Júdà. Èyí ni ìhà ìwọ̀-oòrùn."'), 'Josh$18:15': (None, None, 'Josh$18:15 "Ìhà gúsù bẹ̀rẹ̀ ní ìpẹ̀kun Kiriati-Jéárímù ní ìwọ̀-oòrùn, ààlà náà wà ní ibi ìsun omi Nẹ́fítóà."'), 'Josh$21:1': (None, None, 'Josh$21:1 " Báyìí ni olórí ìdílé àwọn ọmọ Léfì lọ bá Élíásárì àlùfáà, Jóṣúà ọmọ Núnì àti olórí ẹ̀yà àwọn ìdílé ẹ̀yà Ísírẹ́lì"'), 'Josh$21:8': ('fun', None, 'Josh$21:8 Báyìí ni àwọn ọmọ Ísírẹ́lì pín ìlú wọ̀nyí àti ilẹ̀ pápá oko tutù fún àwọn ọmọ Léfì gẹ́gẹ́ bí OLÚWA ti pàṣẹ láti ẹnu Móse.'), 'Josh$21:44': ('fun', 'ọwọ', 'Josh$21:44 "OLÚWA sì fún wọn ní ìsinmi ní gbogbo ọ̀nà, gẹ́gẹ́ bí ó ti sèlèrí fún baba ńlá wọn. Kò sì sí ọ̀kankan nínú àwọn ọ̀ta wọn tí ó lè dojú kọ wọ́n. OLÚWA sì fi gbogbo àwọn ọ̀ta wọn lé wọn ní ọwọ́."'), 'Josh$22:4': ('fun', 'pada', 'Josh$22:4 "Nísinsìn yìí tí OLÚWA Ọlọ́run yín ti fún àwọn arákùnrin yín ní ìsinmi gẹ́gẹ́ bí ó ti ṣèlérí, ẹ padà sí ilẹ̀ yín níbi tí Mósè ìránṣẹ́ OLÚWA fi fún yin ní òdì kéjì Jọ́dánì."'), 'Josh$22:5': ('sin', 'ṣe', 'Josh$22:5 "Ṣùgbọ́n ẹ sọ́ra gidigidi láti pa àṣẹ àti òfin tí Mósè ìránṣẹ́ OLÚWA fi fún yin mọ́. Láti fẹ́ràn OLÚWA Ọlọ́run yín, láti rìn nínú gbogbo ọ̀nà rẹ̀, láti gbọ́ràn sí àṣẹ rẹ̀, láti dì í mú ṣinṣin àti láti sìn ín pẹ̀lú gbogbo ọkàn yín àti gbogbo àyà yín.”"'), 'Josh$22:9': ('ilẹ', None, 'Josh$22:9 "Báyìí ni àwọn ẹ̀yà Rúbẹ́nì, ẹ̀yà Gádì àti ìdajì ẹ̀yà Mánásè fi àwọn ará Ísírẹ́lì sílẹ̀ ní Sílò ní Kénánì láti padà sí Gílíádì, ilẹ̀ wọn, èyí tí wọ́n ti gbà gẹ́gẹ́ bí àṣẹ OLÚWA láti ẹnu Mósè wá."'), 'Josh$22:13': (None, None, 'Josh$22:13 "Àwọn ọmọ Ísírẹ́lì rán Fínéhásì ọmọ Élíásárì àlùfáà, sí ilẹ̀ Gílíádì, sí Rúbẹ́nì, sí Gádì àti sí ìdajì ẹ̀yà Mánásè."'), 'Josh$22:21': ('ṣe', None, 'Josh$22:21 "Nígbà náà ni Rúbénì, Gádì àti ẹ̀yà Mánásè sọ nínú ìdáhùn wọn fún àwọn olórí Ísírẹ́lì pé."'), 'Josh$22:25': ('ṣe', 'ni', 'Josh$22:25 OLÚWA ti fi Jódánì ṣe ààlà láàárin àwa àti ẹ̀yin-àwọn ọmọ Réúbénì àti àwọn ọmọ Gádì! Ẹ kò ni ní ìpín nínú OLÚWA.’ Bẹ́ẹ̀ ni àwọn ọmọ yín lè mú kí àwọn ọmọ wa dẹ́kun láti máa bẹ̀rù OLÚWA.'), 'Josh$22:26': ('ṣe', 'wá', 'Josh$22:26 "“Nítorí èyí ni àwa ṣe wí pé, ‘Ẹ jẹ́ kí àwa múra láti mọ pẹpẹ kan, Ṣùgbọ́n kì í ṣe fún ọrẹ sísun, tàbí fún àwọn ìrúbọ.’"'), 'Josh$22:31': (None, 'ni', 'Josh$22:31 "Fínéhásì ọmọ Élieásárì, àlùfáà wí fún Réúbẹ́nì, Gádì àti Mánásè, “Ní òní ni àwa mọ̀ pé OLÚWA wà pẹ̀lú wa, nítorí tí ẹ̀yin kò hùwà àìṣòtítọ́ sí OLÚWA ní orí ọ̀rọ̀ yí nísinsin yìí, ẹ̀yin ti yọ àwọn ará Ísírẹ́ì kúrò ní ọwọ́ OLÚWA”."'), 'Josh$22:32': ('mu', None, 'Josh$22:32 "Nígbà náà ni Fínéhásì ọmọ Élíásárì àlùfáà àti àwọn olórí padà sí Kénánì láti ibi ìpàdé wọn pẹ̀lú àwọn ẹ̀yà Réúbẹ́nì àti ẹ̀yà Gádì ní Gílíádì, wọ́n sì mú ìròyìn tọ àwọn ọmọ Ísírẹ́lì lọ."'), 'Josh$23:1': (None, None, 'Josh$23:1 "Lẹ́yìn ọjọ́ pípẹ́, nígbà tí OLÚWA sì ti fún Ísírẹ́lì ní ìsinmi kúrò ní ọwọ́ àwọn ọ̀tá wọn tí ó yí wọn ká nígbà náà Jóṣúà sì ti di arúgbó."'), 'Josh$23:6': ('ṣe', None, 'Josh$23:6 "“Ẹ jẹ́ alágbára gidigidi, kí ẹ sì sọ́ra láti ṣe ìgbọ́ran sí ohun tí a kọ sí inú Ìwé Ofin Mósè, láìyí padà sí ọ̀tún tàbí sí òsì."'), 'Josh$23:12': (None, None, 'Josh$23:12 "“Ṣùgbọ́n bí ẹ bá yí padà, tí ẹ sì gba ìmọ̀ pọ̀ pẹ̀lú àwọn tó sẹ́kù lára àwọn orílẹ̀ èdè tí ó kù láàárin yín, tí ẹ sì ń fọmọ fún ara yín ní ìyàwó, tí ẹ sì bá wọn ní àjọṣepọ̀,"'), 'Josh$23:14': (None, None, 'Josh$23:14 "“Nísinsin yìí èmi ti fẹ́ máa lọ sí ibi tí àwọn àgbà ń rè. Ẹ mọ̀ pẹ̀lú gbogbo ọkàn yín àti àyà yín pé, kò sí ohun kan tí ó kùnà nínú àwọn ìlérí rere gbogbo tí OLÚWA Ọlọ́run yín fi fún un yín. Gbogbo ìlérí tó ti se kò sí ọ̀kan tí ó kùnà."'), 'Josh$23:15': ('fi fun', None, 'Josh$23:15 "Ṣùgbọ́n gẹ́gẹ́ bí ìlérí dáradára gbogbo ti OLÚWA Ọlọ́run yín ti wá sí ìmúsẹ, bẹ́ẹ̀ ni OLÚWA yóò mú ibi gbogbo tí ó ti kìlọ̀ wá sí orí yín, títí yóò fi pa yín run kúrò ní ilẹ̀ dáradára tí ó ti fi fún un yín."'), 'Josh$23:16': (None, 'ṣègbé', 'Josh$23:16 "Bí ẹ bá sẹ̀ sí májẹ̀mu OLÚWA Ọlọ́run yín, èyí tí ó pàṣẹ fún un yín, tí ẹ bá sì lọ tí ẹ sì sin àwọn òrìṣà tí ẹ sì tẹ orí ba fún wọn, ìbínú gbígbóná OLÚWA yóò wá sórí i yín, ẹ̀yin yóò sì ṣègbé kíákíá kúrò ní ilẹ̀ dáradára tí ó ti fi fún un yín”"'), 'Josh$24:8': ('mu', 'ilẹ', 'Josh$24:8 "“ ‘Èmi mú yín wá sí ilẹ̀ àwọn ará Ámórì tí ó ń gbé ìlà-oòrùn Jọ́dánì. Wọ́n bá yín jà, Ṣùgbọ́n mo fi wọ́n lé yín lọ́wọ́. Èmí pa wọ́n run kúrò ní wájú u yín, ẹ sì gba ilẹ̀ ẹ wọn."'), 'Josh$24:9': (None, None, 'Josh$24:9 "Nígbà tí Bálákì ọmọ Sípórì, ọba Móábù, múra láti bá Ísírẹ́lì jà, ó ránṣẹ́ sí Bálámù ọmọ Béóríù láti fi yín bú."'), 'Josh$24:13': (None, 'ni', 'Josh$24:13 "Bẹ́ẹ̀ ni mo fún un yín ní ilẹ̀ tí ẹ̀yin kò ṣiṣẹ́ fún, àti àwọn ìlú tí ẹ̀ yin kò kọ́; ẹ sì ń gbé inú wọn, ẹ sì ń jẹ nínú ọgbà àjàrà àti ọgbà ólífì tí ẹ kò gbìn.’"'), 'Josh$24:16': (None, 'ilẹ', 'Josh$24:16 "Àwọn ènìyàn sì dáhùn pé, “Kí a má ri tí àwa yóò fi kọ OLÚWA sílẹ̀ láti sin òrìṣà.!"'), 'Josh$24:20': ('ṣe', 'sin', 'Josh$24:20 "Bí ẹ bá kọ OLÚWA tí ẹ sì sin òrìṣà orílẹ̀ èdè mìíràn òun yóò padà yóò sì mú ibi bá a yín, yóò sì pa yín run, lẹ́yìn ìgbà tí ó ti ṣe rere fún un yín tan.”"'), 'Judg$1:2': (None, 'ni', 'Judg$1:2 "OLÚWA sì dáhùn pé, “Júdà ni yóò lọ; nítorí pé èmi ti fi ilẹ̀ náà lé e lọ́wọ́.”"'), 'Judg$1:15': (None, 'fun', 'Judg$1:15 "Ákíṣà sì dáhùn pé, “Mo ń fẹ́ kí o ṣe ojú rere kan fún mi, nígbà ti o ti fún mi ní ilẹ̀ ní Gúúsù (gúṣù) fún mi ní ìṣun omi náà pẹ̀lú.” Kélẹ́bù sì fún un ní ìṣun òkè àti ìṣun ìṣàlẹ̀."'), 'Judg$1:34': ('oke', 'òkè', 'Judg$1:34 "Àwọn ará Ámórì fi agbára dá àwọn ẹ̀yà Dánì dúró sí àwọn ìlú orí òkè, wọn kò sì jẹ́ kí wọn sọ̀kalẹ̀ wá sí pẹ̀tẹ́lẹ̀."'), 'Judg$2:1': ('lọ', None, 'Judg$2:1 "Ní ọjọ́ kan ańgẹ́lì OLÚWA dìde kúrò láti Gígálì lọ sí Bókímù ó sì wí pé, “Èmi mú un yín gòkè láti ilẹ̀ Éjíbítì wá, èmi sì ṣíwájú yín wá sí ilẹ̀ tí mo ti ṣèlérí láti fi fún àwọn baba ńlá yín. Èmi sì wí pé, ‘Èmi kì yóò yẹ májẹ̀mú mi pẹ̀lú yín."'), 'Judg$2:2': ('ni', None, 'Judg$2:2 "Ẹ̀yin kò gbọdọ̀ bá àwọn ènìyàn ilẹ̀ yí dá májẹ̀mú àlàáfíà, ṣùgbọ́n ẹ̀yin yóò wó pẹpẹ ibi ìsin òrìṣà wọn lulẹ̀.’ Ṣíbẹ̀ ẹ̀yin ṣe àìgbọ́ràn sími. Èéṣe tí ẹ̀yin fi ṣe èyí?"'), 'Judg$2:6': ('ní', 'gbà', 'Judg$2:6 " Lẹ́yìn tí Jóṣúà ti tú àwọn ènìyàn Ísírẹ́lì ká, àwọn ẹ̀yà Ísírẹ́lì kọ̀ọ̀kan lọ sí ilẹ̀ tí a fi fún wọn láti lọ gbà á ní ìní wọn."'), 'Judg$2:11': ('mu', None, 'Judg$2:11 "Àwọn ọmọ Ísírẹ́lì sì ṣe ohun tí ó burú níwájú OLÚWA, wọ́n sì ń sin òrìṣà Bálímù."'), 'Judg$2:19': ('pada', None, 'Judg$2:19 "Ṣùgbọ́n ní gbàrà tí onídàájọ́ bá ti kú àwọn ènìyàn náà a sì tún padà sí ọ̀nà ìbàjẹ́ àní ju ti àwọn baba wọn lọ, wọn a tẹ̀lé òrìṣà, wọ́n ń sìn wọ́n, wọn a sì forí balẹ̀ fún wọn, wọ́n kọ̀ láti yàgò kúrò ní ọ̀nà ibi wọn àti agídí ọkàn wọn."'), 'Judg$3:27': ('oke', 'òkè', 'Judg$3:27 "Nígbà tí ó dé ibẹ̀ ó fọn fèrè ní orí òkè Éfúráímù, fèrè ìpè ogun, ó sì kó ogun jọ lábẹ́ ara rẹ̀ bí olórí ogun."'), 'Judg$3:29': (None, 'eniyan', 'Judg$3:29 "Ní báyìí, wọ́n ti pa tó ẹgbẹ̀rún mẹ́wàá ọkùnrin ará Móábù tí wọ́n jẹ́ alágbára àti onígboyà ènìyàn, kò sí ènìyàn tí ó sálà."'), 'Judg$5:13': (None, 'ni', 'Judg$5:13 “Nígbà náà ni ó fi àwọn tókù jọba lórí àwọn ènìyàn; OLÚWA fún mi ìjọba lórí àwọn alágbára.'), 'Judg$6:5': ('wá', None, 'Judg$6:5 "Wọn a máa wá pẹ̀lú ohun ọ̀sìn wọn àti àwọn àgọ́ wọn, wọn a sì dàbí eṣú nítorí i púpọ̀ wọn. Ènìyàn kò sì lè ka iye àwọn ènìyàn náà bí ni àwọn ẹran ọ̀sìn, wọ́n pọ̀ débi pé wọn kò ṣe é kà ní iye, wọn a bo ilẹ̀ náà wọn a sì jẹ ẹ́ run."'), 'Judg$6:7': (None, 'ni', 'Judg$6:7 Nígbà tí àwọn ọmọ Ísírẹ́lì ké pe OLÚWA nítorí àwọn ará Mídíánì.'), 'Judg$6:19': ('ṣe', None, 'Judg$6:19 "Gídíónì sì yára wọ ilé lọ, ó sì pa ọ̀dọ́ ewúrẹ́ kan, wọ́n sì ṣè é, ó sì mú ìyẹ̀fun ìyẹ̀fun éfà kan (èyí tó lítà méjìlélógún) ó fi ṣe àkàrà (àkàrà) àìwú. Ó gbé ẹran náà sínú agbọ̀n ṣùgbọ́n ó fi ọbẹ̀ rẹ̀ sínú ìkòkò, ó gbé wọn jáde tọ ańgẹ́lì náà wá bí ọrẹ lábẹ́ igi óákù."'), 'Judg$6:35': (None, 'pade', 'Judg$6:35 "Ó rán àwọn oníṣẹ́ la ilẹ̀ Mánásè já pé kí wọ́n dira ogun, àti sí Ásérì, Ṣébúlúnì àti Náfítalì gbogbo pẹ̀lú sì lọ láti pàdé wọn."'), 'Judg$6:36': (None, None, 'Judg$6:36 "Gídíónì wí fún Ọlọ́run pé, “Bí ìwọ yóò bá gba Ísírẹ́lì là nípaṣẹ̀ mi bí ìwọ ti ṣe ìlérí—"'), 'Judg$7:2': ('fun', 'ọwọ', 'Judg$7:2 "OLÚWA wí fún Gídíónì pé, “Àwọn ọmọ ogun tí o kó jọ sọ́dọ̀ ti pọ̀ jù fún mi láti fi àwọn ogun Mídíánì lé wọn lọ́wọ́, kí Ísírẹ́lì má ba à gbé ara rẹ̀ ga sí mi wí pé agbára òun ni ó gbà á là,"'), 'Judg$7:3': ('lọ', 'lọ', 'Judg$7:3 "sì kéde sí àwọn ènìyàn nísinsìn yìí pé, ‘Ẹnikẹ́ni tí ó bá ń gbọ̀n, tí ó sì ń bẹ̀rù lè padà sẹ́yìn, kí ó sì kúrò lórí òkè Gílíádì.’ ” Báyìí ni Gídíónì ṣe ya àwọn ènìyàn náà. Ẹgbẹ̀rúnméjìlélógún ọkùnrin sì padà sẹ́yìn àwọn ẹgbẹ̀rún mẹ́wàá sì dúró."'), 'Judg$7:7': (None, None, 'Judg$7:7 "OLÚWA wí fún Gídíónì pé, “Àwọn ọ̀ọ́dúnrún (300) ọkùnrin tí ó lá omi ni èmi yóò lò láti gbà yín là àti láti fi ogun Mídíánì lé yín lọ́wọ́. Jẹ́ kí àwọn tókù padà sí ilé wọn.”"'), 'Judg$7:8': (None, None, 'Judg$7:8 "Báyìí ni Gídíónì ṣe dá àwọn Ísírẹ́lì tí ó kù padà sí àgọ́ wọn, ṣùgbọ́n ó dá àwọn ọ̀ọ́dúrún ọkùnrin náà dúró. Àwọn wọ̀nyí sì gba gbogbo ohun èlò àti fèrè àwọn tí ó ti padà. Ibùdó ogun àwọn Mídíánì wà ní àfonífojì ní ìṣàlẹ̀. Ibi tí ó wà."'), 'Judg$7:9': (None, 'ni', 'Judg$7:9 "Ní òru ọjọ́ náà OLÚWA sọ fún Gídíónì pé, “Dìde, dojú ogun kọ ibùdó ogun àwọn ará Mídíánì nítorí èmi yóò fi lé ọ lọ́wọ́."'), 'Judg$7:13': ('ṣubu', None, 'Judg$7:13 "Gídíónì dé sí àsìkò tí ọkùnrin kan bẹ̀rẹ̀ sí í rọ́ àlá tí ó lá sí ọ̀kan nínú àwọn ọ̀rẹ́ rẹ̀. Ó ní, “Mo lá àlá kan, nínú àlá náà mo rí àkàrà kan tó ṣe róbótó tí a fi Báálì ṣe ń yí wọ inú ibùdó àwọn ará Mídíánì, ó sì kọlu àgọ́ pẹ̀lú agbára ńlá débi wí pé àgọ́ náà dojú dé, ó sì ṣubú.”"'), 'Judg$7:14': (None, None, 'Judg$7:14 "Ọ̀rẹ́ rẹ̀ dá a lóhùn pé, “Èyí kò le túmọ̀ sí ohun mìíràn ju idà Gídíónì ọmọ Jóásì ará Ísírẹ́lì lọ: Ọlọ́run ti fi àwọn ará Mídíánì àti gbogbo ogun ibùdó lé e lọ́wọ́.”"'), 'Judg$7:16': (None, None, 'Judg$7:16 "Nígbà tí ó ti pín àwọn ọ̀ọ́dúnrún ọkùnrin wọ̀n-ọn-nì sí ọ̀nà mẹ́ta, ó fi fèrè, pẹ̀lú àwọn òfìfo ìkòkò lé ẹnìkọ̀ọ̀kan wọn lọ́wọ́, iná sì wà nínú àwọn ìkòkò náà."'), 'Judg$7:17': (None, 'ṣe', 'Judg$7:17 "Ó sọ fún wọn pé, “Ẹ máa wò mí, kí ẹ sì máa ṣe ohun tí mo bá ṣe. Nígbà tí mo bá dé igun ibùdó wọn ẹ ṣe ohun tí mo bá ṣe."'), 'Judg$8:5': (None, 'jẹ', 'Judg$8:5 "Ó wí fún àwọn ọkùnrin Ṣúkótì pé, “Ẹ fún àwọn ọmọ ogun mi ní oúnjẹ diẹ̀, nítorí ó ti rẹ̀ wọ́n, èmi sì ń lépa Ṣébà àti Ṣálímúnà àwọn ọba Mídíánì.”"'), 'Judg$8:6': ('fun', 'ọwọ', 'Judg$8:6 Ṣùgbọ́n àwọn ìjòyè Ṣúkótì fèsì pé ṣé ọwọ́ rẹ ti tẹ Ṣébà àti Ṣálímúnà náà ni? Èéṣe tí àwa yóò ṣe fún àwọn ológun rẹ ní oúnjẹ?'), 'Judg$8:13': (None, None, 'Judg$8:13 Gídíónì ọmọ Jóásì gba ọ̀nà ìgòkè Hérésì padà sẹ́yìn láti ojú ogun.'), 'Judg$8:15': ('fun', None, 'Judg$8:15 "Nígbà náà ni Gídíónì wá ó sọ fún àwọn ọkùnrin Ṣúkótì pé, “Ṣébà àti Sálímúnà nìwọ̀nyí nípa àwọn tí ẹ̀yin fi mí ṣẹ̀fẹ̀ nígbà tí ẹ wí pé, ‘Ṣé ó ti ṣẹ́gun Ṣébà àti Ṣálímúnà? Èéṣe tí àwa ó fi fún àwọn ọmọ ogun rẹ tí ó ti rẹ̀ ní oúnjẹ?’ ”"'), 'Judg$8:33': (None, None, 'Judg$8:33 "Láìpẹ́ jọjọ lẹ́yìn ikú Gídíónì ni àwọn ará Ísírẹ́lì ṣe àgbérè tọ Báálì lẹ́yìn, wọ́n fi Báál-Beriti ṣe òrìṣà wọn."'), 'Judg$9:1': (None, None, 'Judg$9:1 "Ní ọjọ́ kan Ábímélékì ọmọ Jérúbù-Báálì lọ sí ọ̀dọ̀ àwọn arákùnrin ìyá rẹ̀ ní Ṣékémù ó sì bá wọn sọ̀rọ̀ àti gbogbo àwọn ìbátan ìyá rẹ̀ wí pé,"'), 'Judg$9:3': ('ni', 'tẹle', 'Judg$9:3 "Nígbà tí àwọn arákùnrin rẹ̀ sọ èyí ní etí àwọn ará Ṣékémù, ọkàn wọn fà sí àti tẹ̀lé Ábímélékì torí, wọ́n sọ wí pé, “Arákùnrin wa ní í ṣe.”"'), 'Judg$9:5': (None, None, 'Judg$9:5 "Ó kó wọn lọ sí ilé baba rẹ̀ ní Ófírà, níbẹ̀ ní orí òkúta kan ṣoṣo ni ó ti pa àádọ́rin nínú àwọn arákùnrin rẹ̀, àwọn Jérú-Báálì, ṣùgbọ́n Jótamù, àbíkẹ́yìn nínú àwọn ọmọ Jérúb-Báálì, bọ́ yọ nítorí pé ó sá pamọ́."'), 'Judg$9:16': (None, 'ni', 'Judg$9:16 "“Báyìí tí ó bá jẹ́ pé ẹ̀yín ṣe ohun tí ó ní ọlá àti pẹ̀lú ẹ̀mí òtítọ́ ní fífi Ábímélékì jọba, tí ó bá ṣe pé ohun tí ó tọ́ ni ẹ ṣe sí Jérú-Báálì àti ìdílé rẹ̀, bí ẹ bá san ẹ̀san tó yẹ fún un."'), 'Judg$9:22': ('ṣe', None, 'Judg$9:22 "Lẹ́yìn tí Ábímélékì ti ṣe àkóso Ísírẹ́lì fún ọdún mẹ́ta,"'), 'Judg$9:32': (None, None, 'Judg$9:32 Wá ní òru kí ìwọ àti àwọn ogun rẹ sá pamọ́ dè wọ́n nínú igbó.'), 'Judg$9:42': ('lọ', None, 'Judg$9:42 "Ní ọjọ́ kejì àwọn ará Ṣékémù sì jà lọ́ sí oko, ẹnìkan ló ṣe òfófó rẹ̀ fún Ábímélékì."'), 'Judg$9:43': ('wò', None, 'Judg$9:43 "Ó kó àwọn ènìyàn rẹ̀, ó pín wọn sí ẹgbẹ́ mẹ́ta ó sì sá pamọ́ sí inú oko. Nígbà tí ó sì rí tí àwọn ènìyàn náà ń jáde kúrò nínú ìlú, ó dìde ó gbógun tì wọ́n."'), 'Judg$9:48': ('ni', 'ṣe', 'Judg$9:48 "Òun àti gbogbo àwọn ọmọ ogun rẹ̀ gun òkè Sálímónì lọ. Ó gé àwọn ẹ̀ka díẹ̀ pẹ̀lú àáké, ó gbé àwọn ẹ̀ka wọ̀nyí sí èjìká rẹ̀. Ó sọ fún àwọn ọmọ ogun rẹ̀ pé, “Ẹ ṣe ohun tí ẹ rí tí mo ń ṣe yìí ní kíákíá.”"'), 'Judg$9:49': ('awọn ọkunrin', 'eniyan', 'Judg$9:49 Báyìí gbogbo àwọn ọkùnrin tí ó wà lọ́dọ̀ rẹ̀ gé àwọn ẹ̀ka igi wọn tẹ̀lé Ábímélékì. Wọ́n kó wọn ti ilé ìṣọ́ agbára níbi tí àwọn ènìyàn sá pamọ́ sí wọ́n sì fi iná síi pẹ̀lú gbogbo àwọn ènìyàn tí ó wà níbẹ̀. Gbogbo àwọn ènìyàn náà tí ó tó ẹgbẹ̀rún ènìyàn lọ́kùnrin àti lóbìnrin sì kú.'), 'Judg$9:54': ('pa', None, 'Judg$9:54 "Ní ojú kan náà ni ó pe ẹni tí ó ru àpáta rẹ̀ pé, “Yára yọ idà rẹ kí o sì pa mí, kí wọn má ba à sọ pé, ‘Obìnrin ni ó pa á.’ ” Ọ̀dọ́mọkùnrin náà sì fi ọ̀kọ̀ gún-un ó sì kú."'), 'Judg$9:55': (None, None, 'Judg$9:55 "Nígbà tí àwọn ará Ísírẹ́lì rí i pé Ábímélékì kú, olúkúlùkù wọn padà sí ilé rẹ̀."'), 'Judg$10:13': (None, None, 'Judg$10:13 "Ṣíbẹ̀ṣíbẹ̀ ẹ̀yin kọ̀ mí sílẹ̀ láti sin àwọn ọlọ́run mìíràn, torí ìdí èyí, èmi kì yóò sì tún gbà yín mọ́."'), 'Judg$10:14': (None, 'ni', 'Judg$10:14 Ẹ lọ kí ẹ sì ké pe àwọn òrìṣà tí ẹ̀yin ti yàn fún ara yín. Jẹ́ kí wọn gbà yín sílẹ̀ ní àsìkò ìpọ́njú yín!”'), 'Judg$10:15': ('ni', None, 'Judg$10:15 "Àwọn ọmọ Ísírẹ́lì sì dá OLÚWA lóhùn pé, “Àwá ti ṣẹ̀, ṣe ohun tí ó bá fẹ́ pẹ̀lú wa, ṣùgbọ́n, jọ̀wọ́ gbà wá sílẹ̀ ná ní àsìkò yìí.”"'), 'Judg$11:12': ('ṣe', 'wá', 'Judg$11:12 "Jẹ́fítà sì rán àwọn oníṣẹ́ sí ọba àwọn ará Ámónì pé, “Kí ni ẹ̀ṣùn tí o ní sí wa láti fi kàn wá tí ìwọ fi dojú ìjà kọ ilẹ̀ wa?”"'), 'Judg$11:19': (None, None, 'Judg$11:19 "“Nígbà náà ni Ísírẹ́lì rán àwọn oníṣẹ́ sí Síhónì ọba àwọn ará Ámórì, ẹni tí ń ṣe àkóso ní Hésíbónì, wọ́n sì wí fún un pé, ‘Jẹ́ kí a la ilẹ̀ rẹ kọja lọ sí ibùgbé wa.’"'), 'Judg$11:21': ('ilẹ', None, 'Judg$11:21 "“OLÚWA Ọlọ́run Ísírẹ́lì sì fi Síónì àti àwọn ọmọ ogun rẹ̀ lé Ísírẹ́lì lọ́wọ́, wọ́n sì ṣẹ́gun wọn. Ísírẹ́lì sì gba gbogbo ilẹ̀ àwọn ará Ámórì tí wọ́n ń gbé ní agbégbé náà,"'), 'Judg$11:30': (None, None, 'Judg$11:30 "Jẹ́fítà sì jẹ́ ẹ̀jẹ́ fún OLÚWA pé, “Bí ìwọ bá fi àwọn ará Ámónì lé mi lọ́wọ́,"'), 'Judg$11:39': ('pada', None, 'Judg$11:39 "Lẹ́yìn oṣù méjì náà, ó padà tọ baba rẹ̀ wá òun sì ṣe sí i bí ẹ̀jẹ́ tí ó ti jẹ́. Ọmọ náà sì jẹ́ wúndíá tí kò mọ ọkùnrin rí. Èyí sì bẹ̀rẹ̀ àṣà kan ní Ísírẹ́lì"'), 'Judg$12:3': (None, 'ọwọ', 'Judg$12:3 "Nígbà tí mo ríi pé ẹ̀yin kò ṣetán láti ṣe ìrànlọ́wọ́, mo fi ẹ̀mi mi wéwu. Mó sì gòkè lọ láti bá àwọn ará Ámónì jà, OLÚWA sì fún mi ní ìṣẹ́gun lórí wọn, Èéṣe báyìí tí ẹ fi dìde wá lónìí láti bá mi jà?”"'), 'Judg$13:4': (None, None, 'Judg$13:4 " Báyìí rí i dájúdájú pé ìwọ kò mu wáìnì tàbí ọtí líle kankan àti pé ìwọ kò jẹ ohun aláìmọ́ kankan,"'), 'Judg$13:8': ('wá', 'ṣe', 'Judg$13:8 "Nígbà náà ni Mánóà gbàdúrà sí OLÚWA wí pé, “Háà OLÚWA, èmi bẹ̀ ọ́, jẹ́ kí ènìyàn Ọlọ́run tí ìwọ rán sí wa padà tọ̀ wá wá láti kọ́ wa bí àwa yóò ti ṣe tọ́ ọmọ tí àwa yóò bí náà.”"'), 'Judg$13:15': (None, None, 'Judg$13:15 "Mánóà sọ fún ańgẹ́lì OLÚWA náà pé, “Jọ̀wọ́ dára dúró títí àwa yóò fi pèṣè ọ̀dọ́ ewúrẹ́ kan fún ọ.”"'), 'Judg$13:17': ('wá', 'ṣe', 'Judg$13:17 "Mánóà sì béèrè lọ́wọ́ ańgẹ́lì OLÚWA náà pé, “Kí ni orúkọ rẹ, kí àwa bá à lè fi ọlá fún ọ nígbà tí ọ̀rọ̀ rẹ bá ṣẹ?”"'), 'Judg$13:20': (None, None, 'Judg$13:20 "Bí ẹ̀là ahọ́n iná ti là jáde láti ibi pẹpẹ ìrúbọ náà sí ọ̀run, ańgẹ́lì OLÚWA gòkè re ọ̀run láàárin ahọ́n iná náà. Nígbà tí wọ́n rí ìṣẹ̀lẹ̀ yìí, Mánóà àti aya rẹ̀ wólẹ̀ wọ́n sì dojú bolẹ̀."'), 'Judg$14:2': ('ni', 'gba', 'Judg$14:2 "Nígbà tí ó darí (padà sílé) dé, ó sọ fún àwọn òbí rẹ̀ (baba àti ìyá rẹ̀) pé, “Mo rí obìnrin Fílístínì kan ní Tímínà: báyìí ẹ fẹ́ ẹ fún mi bí aya mi”"'), 'Judg$14:15': (None, 'ni', 'Judg$14:15 "Ní ọjọ́ kẹrin, wọ́n wí fún ìyàwó Sámúsónì, pé, “Tan ọkọ rẹ kí ó lè ṣe àlàyé àlọ́ náà fún wa, bí bẹ́ẹ̀ kọ́ àwa yóò sun ìwọ àti ìdílé baba rẹ̀ ní iná. Tàbí ṣe o pè wá sí ibi àsè yìí láti sọ wá di òtòsì tàbí kó wa lẹ́rú ni?”"'), 'Judg$15:1': ('lọ', None, 'Judg$15:1 "Lẹ́yìn ìgbà díẹ̀, ní àkókò ìkórè jéró Sámúsónì mú ọ̀dọ́ ewúrẹ́ kan láti bẹ ìyàwó rẹ̀ wò. Ó ní, “Èmi yóò wọ yàrá ìyàwó mi lọ.” Ṣùgbọ́n baba rẹ̀ kò gbà á láàyè láti wọlé."'), 'Judg$15:5': ('ní', None, 'Judg$15:5 "Ó fi iná ran àwọn ètúfù tí ó so náà, ó sì jọ̀wọ́ wọn lọ́wọ́ lọ sínú àwọn oko ọkà àwọn Fílístínì. Ó jó àwọn pòpóòrò ọkà tí ó dúró àti àwọn tí a dì ní ìtí, ìtí, pẹ̀lú àwọn oko àjàrà àti ólífì."'), 'Judg$15:18': (None, 'ọwọ', 'Judg$15:18 "Nítorí tí òǹgbẹ gbẹ ẹ́ gidigidi, ó ké pe OLÚWA, wí pé, “Ìwọ ti fún ìránṣẹ́ ní ìṣẹ́gun tí ó tóbi yìí. Ṣé èmi yóò ha kú pẹ̀lú òǹgbẹ, kí èmi sì ṣubú sí ọwọ́ àwọn aláìkọlà ènìyàn?”"'), 'Judg$16:16': (None, None, 'Judg$16:16 Ó sì ṣe nígbà tí ó fi ọ̀rọ̀ rẹ̀ rọ̀ ọ́ ní ojoojúmọ́ pẹ̀lú ọ̀rọ̀ rẹ̀ títí dé bi pé ó ṣú u dé òpin ẹ̀mí rẹ̀.'), 'Judg$16:29': (None, None, 'Judg$16:29 "Sámúsónì sì na ọwọ́ mú àwọn òpó méjèèjì tí ó wà láàárin gbùngbùn, orí àwọn tí tẹ́ḿpìlì náà dúró lé, ó fi ọwọ́ ọ̀tún mú ọ̀kan àti ọwọ́ òsì mú èkejì, ó fi ara tì wọ́n,"'), 'Judg$17:3': (None, 'ṣe', 'Judg$17:3 "Nígbà tí ó da ẹ̀ẹ́dẹ́gbẹ̀fà ṣékélì fàdákà náà padà fún ìyá rẹ̀, ìyá rẹ̀ dáhùn pé, “Èmi ti fi òtítọ́ inú ya sílífà náà sọ́tọ̀ sí OLÚWA fún ọmọ mi láti fi dá ère dídá àti ère gbígbẹ́. Èmi yóò dá a padà fún ọ.”"'), 'Judg$17:4': ('fun', None, 'Judg$17:4 "Nítorí náà òun dá sílífà náà padà fún ìyá rẹ̀, ìyá rẹ̀ sì mú igba (200) ṣékélì fàdákà, ó sì fún alágbẹ̀dẹ fàdákà ẹni tí ó fi wọ́n rọ ère fínfín àti ère dídà. Wọ́n sì kó wọn sí ilé Míkà."'), 'Judg$18:2': ('ile', None, 'Judg$18:2 "Nítorí náà àwọn ẹ̀yà Dánì rán àwọn jagunjagun márùn ún lọ láti Ṣórà àti Ésítaólì láti yọ́ ilẹ̀ náà wò àti láti rìn ín wò. Àwọn ènìyàn wọ̀nyí ṣe ojú fún gbogbo àwọn ẹ̀yà wọn. Wọ́n wí fún wọn pé, “Ẹ lọ kí ẹ rin ilẹ̀ náà ká, kí ẹ sì wò ó fínnífínní.” Àwọn ọkùnrin náà wọ àwọn ìlú tí ó wà ní agbégbé òkè Éfúráímù, wọ́n sì dé ilé Míkà, níbi tí wọ́n sùn ní òru náà."'), 'Judg$18:7': (None, 'eniyan', 'Judg$18:7 "Àwọn ọkùnrin máràrùn náà kúrò, wọ́n sì wá sí Láìsí, níbi tí wọ́n ti rí i pé àwọn ènìyàn tí ń gbé ibẹ̀ ní ààbò, bí àwọn ará Ṣídónì, láì sí ìfòyà àti ní ìpamọ́. Ní ìgbà tí ilẹ̀ wọn kò ṣe aláìní ohunkóhun, wọ́n ní ọrọ̀ púpọ̀. Ibùgbé wọn tún jìnnà sí ti àwọn ará Ṣídónì, wọn kò fí ohunkóhun bá ẹnikẹ́ni dàpọ̀."'), 'Judg$18:10': (None, 'ilẹ', 'Judg$18:10 "Nígbà tí ẹ̀yin bá dé ibẹ̀, ẹ ó ò rí àwọn ènìyàn tí ọkàn wọn balẹ̀ àti ilẹ̀ tí ó tẹ́jú tí Ọlọ́run ti fi fún yín, ilẹ̀ tí kò ṣe aláìní nǹkan kan.”"'), 'Judg$18:19': ('ile', None, 'Judg$18:19 "Wọ́n dá a lóhùn pé, “Dákẹ́! Ma sọ nǹkan kan, tẹ̀lé wa kí o sì di baba àti àlùfáà wa. Kò ha sàn fún ọ láti máa ṣe ìránṣẹ́ ẹ̀yà àti ìdílé kan tí ó wá láti Ísírẹ́lì bí àlùfáà ju ilé ẹnìkan ṣoṣo lọ?”"'), 'Judg$18:24': ('ṣe', 'ni', 'Judg$18:24 "Ó dáhùn pé, “Ẹ̀yin kó àwọn ère tí mo ṣe, àti àlùfáà mi lọ. Kí ni ó kù tí mo ní? Báwo ni ẹ̀yin ò ṣe béèrè pé, ‘Kí ló ṣe ọ́?’ ”"'), 'Judg$19:3': ('ri', None, 'Judg$19:3 "ọkọ rẹ̀ lọ sí ibẹ̀ láti rọ̀ ọ́ pé kí ó padà sí ọ̀dọ̀ òun. Nígbà tí ó ń lọ ó mú ìránṣẹ́ àti kẹ́tẹ́kẹ́tẹ́ méjì lọ́wọ́, obìnrin náà mú un wọ inú ilé baba rẹ̀ lọ, nígbà tí baba obìnrin náà rí i ó fi tayọ̀ tayọ̀ gbà á."'), 'Judg$19:5': ('lọ', None, 'Judg$19:5 "Ní ọjọ́ kẹrin, wọ́n dìde ní òwúrọ̀ kùtùkùtù òun sì múra láti padà lọ, ṣùgbọ́n baba ọmọbìnrin náà wí fún àna rẹ̀ pé, “Fi ohun jíjẹ díẹ̀ gbé inú ró nígbà náà kí ìwọ máa lọ.”"'), 'Judg$19:6': ('ní', None, 'Judg$19:6 "Àwọn méjèèjì sì jùmọ̀ jókòó wọn láti jọ jẹun àti láti jọ mu. Lẹ́yìn èyí ni baba ọmọbìnrin wí pé, “Jọ̀wọ́ dúró ní alẹ́ yìí kí o sì gbádùn ara rẹ.”"'), 'Judg$19:9': ('lọ', 'gba', 'Judg$19:9 "Nígbà tí ọkùnrin náà, pẹ̀lú àlè àti ìránṣẹ́ rẹ̀, dìde láti máa lọ, àna rẹ̀, baba ọmọbìnrin náà ní, “Wò ó ilẹ̀ ti ń ṣú lọ, dúró níbí, ọjọ́ ti lọ. Dúró kí o sì gbádùn ara rẹ. Ìwọ lè jí ní àárọ̀ kùtùkùtù ọ̀la kí ìwọ sì máa lọlé.”"'), 'Judg$19:16': (None, 'eniyan', 'Judg$19:16 "Ní alẹ́ ọjọ́ náà ọkùnrin arúgbó kan láti àwọn òkè Éfúráímù, ṣùgbọ́n tí ń gbé ní Gíbíà (ibẹ̀ ni àwọn ènìyàn Bẹ́ńjámínì ń gbé) ń ti ibi iṣẹ́ rẹ̀ bọ̀ láti inú oko."'), 'Judg$19:19': (None, 'iranṣẹbinrin', 'Judg$19:19 "Àwa ní koríko àti oúnjẹ tó tó fún àwọn kẹ́tẹ́kẹ́tẹ́ wa àti oúnjẹ àti wáìnì fún àwa ìránṣẹ́ rẹ-èmi, ìránṣẹ́bìnrin rẹ àti ọ̀dọ́mọkùnrin tí ó wà pẹ̀lú wa. A ò ṣe aláìní ohun kankan.”"'), 'Judg$19:22': (None, 'lu', 'Judg$19:22 "Ǹjẹ́ bí wọ́n ti ń ṣe àríyá, kíyèsí i, àwọn ọkùnrin ìlú náà, àwọn ọmọ Bélíálì kan, yí ilé náà ká, wọ́n sì ń lu ìlẹ̀kún; wọ́n sì sọ fún baálé ilé náà ọkùnrin arúgbó náà pé, “Mú ọkùnrin tí ó wọ̀ sínú ilé rẹ wá, kí àwa lè mọ̀ ọ́n.”"'), 'Judg$19:23': ('ṣe', 'wá', 'Judg$19:23 "Ọkùnrin, baálé ilé náà sì jáde tọ̀ wọ́n lọ, ó sì wí fún un pé, “Bẹ́ẹ̀ kọ́ ẹyin arákùnrin mi, èmi bẹ̀ yín, ẹ má ṣe hùwà búburú; nítorí tí ọkùnrin yìí ti wọ ilé mi, ẹ má ṣe hùwà òmùgọ̀ yìí."'), 'Judg$19:25': ('awọn ọkunrin', None, 'Judg$19:25 "Ṣùgbọ́n àwọn ọkùnrin náà kò fetí sí tirẹ̀. Torí náà ọkùnrin náà mú àlè rẹ̀ ó sì tari rẹ̀ jáde sí wọn, wọ́n sì bá a fi ipá lòpọ̀, wọ́n sì fi gbogbo òru náà bá a lòpọ̀, nígbà tí ó di àfẹ̀mọ́júmọ́ wọ́n jọ̀wọ́ rẹ̀ lọ́."'), 'Judg$19:26': ('ṣubu', None, 'Judg$19:26 "Nígbà tí ojúmọ́ bẹ̀rẹ̀ sí là obìnrin náà padà lọ sí ilé tí ọ̀gá rẹ̀ wà, ó ṣubú lulẹ̀ lọ́nà, ó sì wà níbẹ̀ títí ó fi di òwúrọ̀."'), 'Judg$19:27': ('enu', None, 'Judg$19:27 "Nígbà tí ọ̀gá rẹ̀ jí tí ó sì dìde ní òwúrọ̀ tí ó sì sí ìlẹ̀kùn ilé náà, tí ó sì bọ́ sí òde láti tẹ̀ṣíwájú nínú ìrìnàjò rẹ̀, kíyèsí i àlè rẹ̀ wà ní síṣubú ní iwájú ilé, tí ọwọ́ rẹ̀ sì di òpó ẹnu ọ̀nà ibẹ̀ mú,"'), 'Judg$19:29': ('mu', None, 'Judg$19:29 "Nígbà tí ó dé ilé, ó mú ọ̀bẹ ó sì gé àlè rẹ̀ ní oríkeríke sí ọ̀nà méjìlá, ó sì fi wọ́n ránṣẹ́ sí gbogbo agbégbé Ísírẹ́lì."'), 'Judg$19:30': (None, 'ro', 'Judg$19:30 "Gbogbo ẹni tí ó rí i sì wí pé, “A kò ti rí i, bẹ́ẹ̀ a kò tí ì ṣe irú nǹkan yìí rí, kì í ṣe láti ọjọ́ tí Ísírẹ́lì ti jáde tí Éjíbítì wá títí di òní olónìí. Ẹrò ó wò, ẹ gbìmọ̀ràn kí ẹ sọ fún wa ohun tí a ó ò ṣe!”"'), 'Judg$20:6': ('mu', 'ni', 'Judg$20:6 "Mo mú àlè mi náà, mo sì gé e sí ekìrí-ekìrí, mo sì fi ekìrí kọ̀ọ̀kan ránṣẹ́ sí agbégbé ìní Ísírẹ́lì kọ̀ọ̀kan, nítorí pé wọ́n ti ṣe ohun tí ó jẹ́ èèwọ̀ àti ohun ìtìjú yìí ní ilẹ̀ Ísírẹ́lì."'), 'Judg$20:9': ('ṣe', 'wá', 'Judg$20:9 "Ṣùgbọ́n nísinsin yìí, ohun tí àwa yóò se sí Gíbíà ní yìí: Àwa yóò lọ kọlù ú bí ìbò bá ṣe darí wa."'), 'Judg$20:11': ('awọn ọkunrin', None, 'Judg$20:11 Báyìí ni gbogbo àwọn ọkùnrin Ísírẹ́lì para pọ̀ ṣọ̀kan bí ọkùnrin kan ṣoṣo wọ́n sì dìde sí ìlú náà.'), 'Judg$21:1': ('ní', 'fun', 'Judg$21:1 Àwọn ọkùnrin Ísírẹ́lì ti búra ní Mísípà pé: “Kò sí ẹnìkan nínú wa tí yóò fi ọmọ obìnrin rẹ̀ fún ẹ̀yà Bẹ́ńjámínì ní ìyàwó.”'), 'Judg$21:7': ('ni', 'fun', 'Judg$21:7 "Báwo ni a ó ò ṣe rí aya fún àwọn tí ó ṣẹ́kù, nítorí àwa ti fi OLÚWA búra láti má fi ọ̀kankan nínú àwọn ọmọbìnrin wa fún wọn ní aya."'), 'Judg$21:12': ('mu', None, 'Judg$21:12 "Wọ́n rí àwọn irinwó (400) ọ̀dọ́bìnrin tí kò mọ ọkùnrin rí nínú àwọn olùgbé Jabesi-Gílíádì, wọ́n sì mú wọn lọ sí ibùdó ní Ṣílò àwọn ará Kénánì."'), 'Judg$21:18': ('ni', 'fun', 'Judg$21:18 "Àwa kò le fi àwọn ọmọbìnrin wa fún wọn ní aya, nítorí tí àwa ọmọ Ísírẹ́lì ti ṣe ìbúra yìí pé: ‘Ègún ni fún ẹnikẹ́ni tí ó fi aya fún ẹnikẹ́ni nínú ẹ̀yà Bẹ́ńjámínì.’ ”"'), 'Judg$21:20': ('wo', None, 'Judg$21:20 "Wọ́n sì fi àṣẹ fún àwọn ará Bẹ́ńjámínì pé, “Ẹ lọ kí ẹ sì fara pamọ́ nínú àwọn ọgbà àjàrà"'), 'Judg$21:22': ('ṣe', 'wá', 'Judg$21:22 "Nígbà tí àwọn baba wọn tàbí àwọn arákùnrin wọn bá fi ẹjọ́ sùn wá, àwa yóò wí fún wọn pé, ‘Ẹ ṣe wá lóore nípa ríràn wọ́n lọ́wọ́, nítorí àwa kò rí aya fún wọn ní àkókò ogun, ẹ̀yin kò sì ní jẹ̀bi, nítorí pé ẹ̀yin kọ́ ni ó fi àwọn ọmọbìnrin yín fún wọn bí aya.’ ”"'), 'Ruth$1:6': (None, 'pada', 'Ruth$1:6 Nígbà tí Náómì gbọ́ ní Móábù tí ó wà wí pé OLÚWA ti bẹ àwọn ènìyàn rẹ̀ wò nípa fí fún wọn ní ọ̀pọ̀ oúnjẹ. Ó sì dìde pẹ̀lú àwọn ìyàwó ọmọ rẹ̀ méjèèjì láti padà sí ìlú rẹ̀.'), 'Ruth$1:16': ('lọ', None, 'Ruth$1:16 "Ṣùgbọ́n Rúùtù dáhùn wí pé, “Má ṣe rọ̀ mí láti fi ọ́ sílẹ̀ tàbí láti padà lẹ́yìn rẹ. Ibi tí o bá lọ ní èmi yóò lọ, ibi ti ó bá dúró ni èmi yóò dúró, àwọn ènìyàn rẹ ni yóò jẹ́ ènìyàn mi, Ọlọ́run rẹ ni yóò sì jẹ́ Ọlọ́run, mi,"'), 'Ruth$2:10': (None, None, 'Ruth$2:10 "Rúùtù wólẹ̀, ó sì wí fún Bóásì pé, “Èéṣe tí èmi fi bá ojúrere rẹ pàdé tó báyìí, tí o sì kíyèsí mi, èmi àjèjì àti àlejò?”"'), 'Ruth$2:11': ('ṣe', 'wá', 'Ruth$2:11 "Bóásì sì fèsì wí pé, “Èmi ti gbọ́ gbogbo bí o ti ń ṣe sí ìyá ọkọ ọ̀ rẹ láti ìgbà tí ọkọ rẹ ti kú àti bí o ti ṣe fi baba àti ìyá rẹ àti ilẹ̀ rẹ sílẹ̀, tí o sì wá láti gbé láàárin àwọn ènìyàn tí ìwọ kò mọ̀ rí tẹ́lẹ̀."'), 'Ruth$3:3': ('ṣe', None, 'Ruth$3:3 "Wẹ̀, kí o sì fi ìpara-olóòórùn dídùn pa ara rẹ, kí o sì wọ aṣọ rẹ tí ó dára jùlọ, kí o sì lọ sí ilẹ̀-ìpakà tí ó gbé ń pa ọkà, ṣùgbọ́n má ṣe jẹ́ kí ó mọ̀ pé o wà níbẹ̀ títí tí yóò fi jẹ tí yóò sì mu tán."'), 'Ruth$3:4': (None, None, 'Ruth$3:4 "Rí í dájú pé o mọ ibi tí ó sùn sí, lẹ́yìn ìgbà tí ó bá ti sùn, lọ kí o sí aṣọ ìbora rẹ̀ níbi ẹsẹ̀ rẹ̀ sókè kí o sì sùn síbi ẹsẹ̀ náà. Òun yóò sì sọ ohun tí ìwọ yóò ṣe fún ọ.”"'), 'Ruth$3:13': ('ṣe', 'ṣe', 'Ruth$3:13 "Dúró síbí títí ilẹ̀ yóò fi mọ́, bí ó bá sì di òwúrọ̀ tí ọkùnrin náà sì ṣe tan láti ṣe ìràpadà, ó dára kí ó ṣe bẹ́ẹ̀, bí bẹ́ẹ̀ kọ́, bí OLÚWA ti ń bẹ láàyè nígbà náà ni èmi yóò ṣe ìràpadà, sùn sí ìhín títí ilẹ̀ yóò fi mọ́.”"'), 'Ruth$3:18': (None, 'ọrọ', 'Ruth$3:18 "Náómì sì wí fún-un pé, “Dúró, ọmọbìnrin mi títí tí ìwọ yóò fi mọ bí ohun gbogbo yóò ti rí. Nítorí pé ọkùnrin náà kò ní sinmi títí tí ọ̀rọ̀ náà yóò fi yanjú lónìí.”"'), 'Ruth$4:3': ('ilẹ', None, 'Ruth$4:3 "ó sì sọ fún ìbátan rẹ̀ náà pé, “Arábìnrin Náómì tí ó dé láti ilẹ̀ Móábù fẹ́ ta ilẹ̀, èyí tí ó ṣe ti arákùnrin wa, Elimélékì."'), '1Sam$1:6': ('ní', 'ṣe', '1Sam$1:6 "Nítorí pé OLÚWA ti sé e nínú, orogún rẹ̀ bẹ̀rẹ̀ sí ní fín in níràn láti lè mú kí ó bínú."'), '1Sam$1:7': (None, None, '1Sam$1:7 "Eléyìí sì máa ń ṣẹlẹ̀ ní ọdọọdún. Nígbàkígbà tí Hánà bá gòkè lọ sí ilé OLÚWA, orogún rẹ̀ a máa fín-in níràn títí tí yóò fi máa sunkún tí kò sì ní lè jẹun."'), '1Sam$1:27': (None, 'ni', '1Sam$1:27 Ọmọ yìí ni mo ń tọrọ; OLÚWA sì fi ìdáhùn ìbéèrè tí mo béèrè lọ́dọ̀ rẹ̀ fún mi.'), '1Sam$2:8': (None, 'ayé', '1Sam$2:8 "Ó gbé tálákà sókè láti inú erùpẹ̀ wá, ó gbé alágbe sókè láti orí òkítì eérú wá, láti mú wọn jókòó pẹ̀lú àwọn ọmọ-aládé, láti mu wọn jogún ìtẹ́ ogo: “Nítorí pé ọ̀wọ̀n ayé ti OLÚWA ni, ó sì ti gbé ayé ka orí wọn"'), '1Sam$2:10': (None, 'fun', '1Sam$2:10 "A ó fọ àwọn ọ̀tá OLÚWA túútúú; láti ọrun wá ni yóò sánààrá sí wọn; OLÚWA yóò ṣe ìdájọ́ òpin ayé. “Yóò fi agbára fún ọba rẹ̀, yóò si gbé ìwo ẹni-àmì-òróró rẹ̀ sókè.”"'), '1Sam$2:15': (None, 'ni', '1Sam$2:15 "Pẹ̀lú, kí wọn tó sun ọ̀rá náà, ìráńṣẹ́ àlùfáà á dé, á sì wí fún ọkùnrin tí ó ń ṣe ìrúbọ pé, “Fi ẹran fún mi láti sun fún àlùfàá; nítorí tí kì yóò gba ẹran sísè lọ́wọ́ rẹ, bí kò se tútù.”"'), '1Sam$2:29': ('ni', 'ṣe', '1Sam$2:29 "Èé ha ṣe tí ẹ̀yin fi tàpá sí ẹbọ àti ọrẹ mi, tí mo páṣẹ ní ibùjókòó mi: ìwọ sì bu ọlá fún àwọn ọmọ rẹ jú mí lọ, tí ẹ sì fi gbogbo àṣàyàn ẹbọ Ísírẹ́lì àwọn ènìyàn mi mú ara yín sanra.’"'), '1Sam$2:33': ('oju', None, '1Sam$2:33 "Ọkùnrin tí ó jẹ́ tìrẹ, tí èmi kì yóò gé kúrò ni ibi pẹpẹ mi ni a ó dá sí láti sunkún yọ lójú àti láti banújẹ́: Ṣùgbọ́n gbogbo irú-ọmọ ilé rẹ̀ ni a ó fi idà pa ní ààbọ̀ ọjọ́ wọn."'), '1Sam$3:2': (None, None, '1Sam$3:2 "Ní alẹ́ ọjọ́ kan Élì ẹni tí ojú rẹ̀ ti bẹ̀rẹ̀ sí di aláìlágbára, tí kò sì ríran dáadáa, ó dùbúlẹ̀ ní ipò rẹ̀ bi ti àtẹ̀yìn wá."'), '1Sam$3:12': ('ni', 'ṣe', '1Sam$3:12 Ní ìgbà náà ni èmi yóò mú ohun gbogbo tí mo ti sọ sí ilé Élì ṣẹ láti ìbẹ̀rẹ̀ dé òpin.'), '1Sam$3:13': ('ṣe', 'ni', '1Sam$3:13 "Nítorí èmi sọ fún un pé, Èmi yóò ṣe ìdájọ́ fún ilé rẹ̀ títí láé nítorí ìwà ẹ̀ṣẹ̀ wọn tí òun mọ̀ nípa rẹ̀ nítorí àwọn ọmọ rẹ̀ sọ ọ̀rọ̀ òdì, òun kò sì dá wọn lẹ́kun."'), '1Sam$4:13': ('joko', None, '1Sam$4:13 "Nígbà tí ó sì dé, Élì sì jókòó sórí àga rẹ̀ ní ẹ̀gbẹ́ ọ̀nà, ó ń wò, ọkàn rẹ̀ kò balẹ̀ nítorí àpótí ẹ̀rí OLÚWA. Nígbà tí ọkùnrin náà wọ ìlú tí ó sì sọ ohun tí ó ṣẹlẹ̀, gbogbo ìlú bẹ̀rẹ̀ sí sunkún"'), '1Sam$4:18': ('ṣubu', None, '1Sam$4:18 "Nígbà tí ó dárúkọ àpótí ẹ̀rí OLÚWA, Élì sì ṣubú sẹ́yìn kúrò lórí àga ní ẹ̀gbẹ́ bodè, ọrùn rẹ̀ ṣẹ́, ó sì kú, nítorí tí ó jẹ́ arúgbó ọkùnrin, ó sì tóbi, ó ti darí àwọn Ísírẹ́lì fún ogójì ọdún."'), '1Sam$5:11': (None, None, '1Sam$5:11 "Nígbà náà ni wọ́n pe gbogbo àwọn olórí àwọn Fílístínì jọ wọ́n sì wí pé, “Ẹ gbé àpótí ẹ̀rí Ọlọ́run àwọn Ísírẹ́lì lọ: ẹ jẹ́ kí ó padà sí àyè rẹ̀, bí bẹ́ẹ̀ kọ́ yóò pa wá àti àwọn ènìyàn wa.” Ikú ti mú jìnnìjìnnì bá àwọn ará ìlú: ọwọ́ Ọlọ́run sì wúwo lára wọn."'), '1Sam$6:5': ('fun', 'ọwọ', '1Sam$6:5 "Mọ àwòrán àrùn oníkókó àti ti eku èlírí yin tí ó ń ba orílẹ̀ èdè náà jẹ́ kí o sì bu ọlá fún Ọlọ́run Ísírẹ́lì, Bóyá yóò gbé ọwọ́ rẹ̀ kúrò lára yín, lára ọlọ́run yín àti lára ilẹ̀ yín."'), '1Sam$6:7': ('ṣe', None, '1Sam$6:7 "“Nísinsìnyìí ẹ pèse kẹ̀kẹ́ ẹrù tuntun sílẹ̀, pẹ̀lú màlúù méjì tí ó ti fí ọmú fun ọmọ, èyí tí a kò tí ì fi ru ẹrù rí, kí ẹ sì so ó mọ́ kẹ̀kẹ́ náà, kí ẹ sì mú ọmọ wọn kúrò lọ́dọ̀ wọn wá sí ilé láti so."'), '1Sam$6:9': (None, None, '1Sam$6:9 "ṣùgbọ́n kí ẹ sì máa ṣọ́ ọ. Tí ó bá ń lọ sí ilẹ̀ rẹ̀, ìlú rẹ̀, níhà Bẹti-Sémésì. Nígbà náà ni OLÚWA mú àjálù ńlá yìí bá wa. Ṣùgbọ́n bí kò bá ṣe bẹ́ẹ̀ nígbà náà ni a mọ̀ pé kì í ṣe ọwọ́ rẹ̀ ni ó kọ lù wá, ṣùgbọ́n ó wá bẹ́ẹ̀ ni”"'), '1Sam$6:13': ('ri', 'wo', '1Sam$6:13 "Nísinsìn yìí, àwọn ará Bẹti-Sémésì ń kórè jéró wọn ní pẹ̀tẹ́lẹ̀, nígbà tí wọ́n wo òkè tí wọ́n sì rí àpótí ẹ̀rí OLÚWA, wọ́n yọ̀ níwájú rẹ̀."'), '1Sam$6:21': (None, None, '1Sam$6:21 "Nígbà náà, wọ́n rán àwọn ìránṣẹ́ sí àwọn ènìyàn tí Kiriati Jéárímì wí pé, “àwọn Fílístínì ti dá àpótí ẹ̀rí OLÚWA padà. Ẹ sọ̀kalẹ̀ wá, kí ẹ gbé e gòkè lọ sí ọ̀dọ̀ yín.”"'), '1Sam$7:3': (None, 'pada', '1Sam$7:3 "Sámúẹ́lì sọ fún gbogbo ilé Ísírẹ́lì pé, “Tí ẹ bá ń padà sí ọ̀dọ̀ OLÚWA pẹ̀lú gbogbo ọkàn yín, Ẹ yẹra kúrò lọ́dọ̀ ọlọ́run àjòjì àti Áṣitarótì kí ẹ sì farayín jìn fún OLÚWA kí ẹ sì sin òun nìkan ṣoṣo, òun yóò sì gbà yín kúrò lọ́wọ́ àwọn ará Fílístínì”"'), '1Sam$7:14': ('ní', None, '1Sam$7:14 "Àwọn ìlú láti Ékírónì dé Gátì tí àwọn Fílístínì ti gbà lọ́wọ́ Ísírẹ́lì ni ó ti gbà padà fún Ísírẹ́lì, ó sì gba gbogbo ilẹ̀ agbègbè rẹ̀ kúrò lọ́wọ́ ìṣàkóso àwọn Fílístínì. Ìrẹ́pọ̀ sì wà láàárin Ísírẹ́lì àti àwọn Ámórì."'), '1Sam$8:8': (None, 'ṣe', '1Sam$8:8 "Gẹ́gẹ́ bí wọ́n ti ṣe ní ọjọ́ tí mo mú wọn jáde wá láti Éjíbítì títí di ọjọ́ òní, wọ́n kọ̀ mí sílẹ̀, wọ́n sì ń sin Ọlọ́run mìíràn, bẹ́ẹ̀ ni wọ́n ń ṣe sí ọ."'), '1Sam$9:6': ('lọ', None, '1Sam$9:6 "Ṣùgbọ́n ìránṣẹ́ náà fún un ní èsì pé, “Wò ó, ní inú ìlú yìí ọkùnrin ènìyàn Ọlọ́run kan wà; ó jẹ́ ẹni tí wọ́n ń bu ọlá fún, àti gbogbo ohun tí ó bá sọ ní ó máa ń jẹ́ òtítọ́. Jẹ́ kí á lọ ṣíbẹ̀ nísinsin yìí, bóyá yóò sọ fún wá ọ̀nà tí a ó ò gbà.”"'), '1Sam$9:8': ('ni', 'fun', '1Sam$9:8 "Ìránṣẹ́ sì dá a lóhùn lẹ́ẹ̀kan sí i. “Wò ó,” ó wí pé, “Mo ní ìdámẹ́rin Ṣékélì fàdákà lọ́wọ́. Èmi yóò fún ènìyàn Ọlọ́run náà kí ó lè fi ọ̀nà hàn wá.”"'), '1Sam$9:27': (None, None, '1Sam$9:27 "Bí wọ́n ti ń sọ̀kalẹ̀ lọ sí ìpẹ̀kun ìlú náà, Sámúẹ́lì wí fún Ṣọ́ọ̀lù pé, “Sọ fún ìránṣẹ́ náà kí ó máa lọ ṣáájú u wa,” ìránṣẹ́ náà sì ṣe bẹ̀ẹ́, “ṣùgbọ́n kí ìwọ kí ó dúró díẹ̀, kí èmi kí ó lè sọ ọrọ̀ láti ọ̀dọ Ọlọ́run fún ọ.”"'), '1Sam$10:1': ('ororo', None, '1Sam$10:1 "Sámúẹ́lì sì mú ìgò kékeré tí òróró wà nínú rẹ̀, ó sì dà á sí orí Ṣọ́ọ̀lù. Ó sì fi ẹnu kò ó ní ẹnu, wí pé, “OLÚWA kò ha tí fi òróró yàn ọ ní olóri í lórí ohun ìní rẹ̀?"'), '1Sam$10:2': ('ri', 'wa', '1Sam$10:2 "Bí ìwọ bá kúrò lọ́dọ̀ mi lónìí, ìwọ yóò bá ọkùnrin méjì pàdé lẹ́bàá ibojì Rákélì ní Sélísà, ní agbégbé Bẹ́ńjámínì. Wọ́n yóò sọ fún ọ pé, ‘Kẹ́tẹ́kẹ́tẹ́ tí ìwọ jáde lọ láti wá ní wọn tí rí. Nísinsìn yìí, baba à rẹ tí dákẹ́ kò ronú nípa wọn mọ́, ó sì ń damú nípa à rẹ. Ó ń béèrè, “Kí ni èmi yóò ṣe nípà ọmọ mi?” ’"'), '1Sam$10:3': ('pade', 'gba', '1Sam$10:3 "“Nígbà náà, ìwọ yóò lọ láti ibẹ̀ títí yóò fi dé ibi igi Tábórì ńlá. Ọkùnrin mẹ́ta tí wọ́n ń gòkè lọ sọ́dọ̀ Ọlọ́run ní Bẹ́tẹ́lì yóò pàdé rẹ níbẹ̀. Ọ̀kan yóò mú ọmọ ewúrẹ́ mẹ́ta lọ́wọ́, èkejì, iṣù àkàrà mẹ́ta àti ẹ̀kẹtà yóò mú ìgò wáìnì."'), '1Sam$10:7': ('ṣe', None, '1Sam$10:7 "Bí ìwọ bá ti rí àmì wọ̀nyí, ṣe ohunkóhun tí ọwọ́ rẹ bá bà láti ṣe, nítorí Ọlọ́run wà pẹ̀lú rẹ."'), '1Sam$10:8': ('wá', 'ṣe', '1Sam$10:8 "“Lọ ṣáájú mi sí Gílgálì. Èmi yóò sọ̀kalẹ̀ tọ̀ ọ́ wá láti rú ẹbọ sísun àti láti rú ẹbọ ìrẹ́pọ̀, ṣùgbọ́n ìwọ gbọdọ̀ dúró fún ọjọ́ méje títí èmi yóò fi wá sí ọ̀dọ̀ rẹ láti sọ fún ọ ohun tí ó yẹ tí ìwọ yóò ṣe.”"'), '1Sam$10:9': ('lọ', None, '1Sam$10:9 "Bí Ṣọ́ọ̀lù ti yípadà láti fi Sámúẹ́lì sílẹ̀, Ọlọ́run yí ọkàn Ṣọ́ọ̀lù padà àti pé gbogbo àmì wọ̀nyí sì wá sí ìmúṣẹ ní ọjọ́ náà."'), '1Sam$10:17': (None, 'ni', '1Sam$10:17 Sámúẹ́lì pé àwọn ọmọ Ísírẹ́lì jọ sí iwájú OLÚWA ní Mísípà.'), '1Sam$11:10': ('wá', 'ṣe', '1Sam$11:10 "Wọ́n sọ fún àwọn ará Ámónì pé, “Àwa yóò fi ara wa fún un yín ní ọ̀la, kí ẹ̀yin kí ó ṣe gbogbo èyí tí ó tọ́ lójú yín sí wa.”"'), '1Sam$12:1': (None, None, '1Sam$12:1 "Sámúẹ́lì sì wí fún gbogbo Ísírẹ́lì pé, “Èmi tí gbọ́ gbogbo ohun tí ẹ sọ fún mi, èmi sì ti yan ọba fún un yín."'), '1Sam$12:3': (None, 'ni', '1Sam$12:3 "Èmi dúró níhìn yìí, ẹ jẹ́rìí sí mi níwájú OLÚWA àti níwájú ẹni àmì òróró rẹ̀. Màlúù ta ni mo gbà rí? Kẹ́tẹ́kẹ́tẹ́ ta ni mo gbà rí? Ta ni mo rẹ́ jẹ rí? Ọwọ́ ta ni mo ti gba owó àbẹ̀tẹ́lẹ̀ kan rí láti fi ti ara mi lójú? Bí mo bá ti ṣe ìkankan nínú ohun wọ̀nyí, èmi yóò sì san án padà fún un yín.”"'), '1Sam$12:5': ('ri', 'ẹlẹri', '1Sam$12:5 "Samúẹ́lì sì wí fún wọn pé, “OLÚWA ni ẹlẹ́rìí sí i yín, àti ẹni àmì òróró rẹ̀ ni ẹlẹ́rìí lónìí pé, ẹ̀yin kò rí ohunkóhun lọ́wọ́ mi.” Wọ́n sì sọ wí pé, “Òun ni ẹlẹ́rìí.”"'), '1Sam$12:6': ('mu', None, '1Sam$12:6 "Nígbà náà ni Sámúẹ́lì wí fún àwọn ènìyàn náà pé, “OLÚWA ni ó yan Mósè àti Árónì àti ti ó mú àwọn baba ńlá yín gòkè wá láti Éjíbítì."'), '1Sam$12:8': (None, 'ibi', '1Sam$12:8 "“Lẹ́yìn ìgbà tí Jákọ́bù wọ Éjíbítì, wọ́n ké pe OLÚWA fún ìrànlọ́wọ́, OLÚWA sì rán Mósè àti Árónì, tí wọ́n mú àwọn baba ńlá yín jáde láti Éjíbítì láti mú wọn jókòó níbí yìí."'), '1Sam$12:10': ('ni', None, '1Sam$12:10 "Wọ́n kégbe pe OLÚWA, wọ́n sì wí pé, ‘Àwa ti ṣẹ̀; a ti kọ OLÚWA sílẹ̀, a sì ti sin Báálì, àti Ásítarótì. Ṣùgbọ́n nísinsìn yìí gbà wá lọ́wọ́ àwọn ọ̀tá wa, àwa yóò sì sìn ọ́.’"'), '1Sam$12:14': (None, 'jẹ', '1Sam$12:14 "Bí ẹ̀yin bá bẹ̀rù OLÚWA àti bí ẹ̀yin bá ń sìn ín, tí ẹ sì gbọ́ tirẹ̀, tí ẹ̀yin kò sì ṣọ̀tẹ̀ sí àṣẹ rẹ̀, àti tí ẹ̀yin àti ọba tí ó jẹ lórí yín bá ń tẹ̀lé OLÚWA Ọlọ́run yín: ó dára"'), '1Sam$12:20': (None, None, '1Sam$12:20 "Sámúẹ́lì sì dá wọn lóhùn pé, “Ẹ má bẹ̀rù, ẹ ti ṣe gbogbo búburú yìí; ṣíbẹ̀ ẹ má ṣe yípadà kúrò lọ́dọ̀ OLÚWA, ṣùgbọ́n ẹ fi gbogbo àyà yín sin OLÚWA."'), '1Sam$12:22': ('ṣe', 'orukọ', '1Sam$12:22 "Nítorí orúkọ ńlá rẹ̀ OLÚWA kì yóò kọ àwọn ènìyàn rẹ̀ sílẹ̀, nítorí tí inú OLÚWA dùn láti fi yín ṣe ènìyàn rẹ̀."'), '1Sam$12:23': (None, None, '1Sam$12:23 "Bí ó ṣe ti èmi ni, kí á má rí i pé èmi dẹ̀ṣẹ̀ sí OLÚWA nípa dídẹ́kun àti gbàdúrà fún un yín. Èmi yóò sì kọ́ ọ yín ní ọ̀nà rere àti ọ̀nà òtítọ́."'), '1Sam$13:1': (None, None, '1Sam$13:1 "Ṣọ́ọ̀lù sì jẹ́ ọmọ ọgbọ̀n ọdún nígbà tí ó jọba, ó sì jọba lórí Ísírẹ́lì ní ọdún méjìlélógójì."'), '1Sam$13:5': (None, None, '1Sam$13:5 "Àwọn Fílístínì kó ara wọn jọ pọ̀ láti bá Ísírẹ́lì jà, pẹ̀lú ẹgbàá-mẹ́ẹ̀dógún kẹ̀kẹ́, (3000) ẹgbẹ̀ta (6000) ọkùnrin ẹlẹ́ṣin, àwọn ológun sì pọ̀ bí yanrìn etí òkun. Wọ́n sì gòkè lọ, wọ́n dó ní Míkímásì ní ìhà ilẹ̀ oòrùn Bẹti-Áfénì."'), '1Sam$13:10': ('lọ', None, '1Sam$13:10 "Bí ó sì ti ń parí rírú ẹbọ náà, Sámúẹ́lì sì dé, Ṣọ́ọ̀lù sì jáde láti lọ kí i."'), '1Sam$13:14': ('pa', None, '1Sam$13:14 "Ṣùgbọ́n nísinsìn yìí ìjọba rẹ kì yóò dúró pẹ́, OLÚWA ti wá ọkùnrin tí ó wù ú ní ọkàn rẹ̀ fún ara rẹ̀, ó sì ti yàn án láti ṣe olórí fún àwọn ènìyàn rẹ̀, nítorí pé ìwọ kò pa òfin OLÚWA mọ́.”"'), '1Sam$14:1': ('lọ', 'kọjá', '1Sam$14:1 "Ní ọjọ́ kan, Jónátanì ọmọ Ṣọ́ọ̀lù wí fún ọ̀dọ́mọkùnrin tí ó ń ru ìhámọ́ra rẹ̀ pé, “Wá, jẹ́ kí a rékọjá lọ sí ìlú olódi àwọn Fílístínì tí ó wà ní ìhà kejì.” Ṣùgbọ́n kò sọ fún baba rẹ̀."'), '1Sam$14:6': ('lọ', None, '1Sam$14:6 "Jónátanì sì wí fún ọ̀dọ́mọkùnrin tí ó ń ru ìhámọ́ra rẹ̀ pé, “Wá, jẹ́ kí a lọ sí ìlu olódi àwọn aláìkọlà yìí. Bóyá OLÚWA yóò jà fún wa, kò sí ohun tó lè di OLÚWA lọ́wọ́ láti gbàlà, yálà nípaṣẹ̀ púpọ̀ tàbí nípaṣẹ̀ díẹ̀.”"'), '1Sam$14:19': (None, None, '1Sam$14:19 "Nígbà tí Ṣọ́ọ̀lù sì ń bá àlùfáà sọ̀rọ̀, ariwo ní ibùdó àwọn Fílístínì sì ń pọ̀ ṣíwájú sí. Ṣọ́ọ̀lù sì wí fún àlùfáà pé, “Dá ọwọ́ rẹ dúró.”"'), '1Sam$14:24': (None, 'eniyan', '1Sam$14:24 "Gbogbo ọkùnrin Ísírẹ́lì sì wà ní ìpọ́njú ńlá ní ọjọ́ náà, nítorí pé Ṣọ́ọ̀lù ti fi àwọn ènìyàn gégùn ún wí pé, “Ègbé ni fún ẹni tí ó jẹ oúnjẹ títí di alẹ́, títí èmi yóò fi gbẹ̀san mi lára àwọn ọ̀ta mi!” Bẹ́ẹ̀ ni kò sí ẹnìkankan nínú ọ̀wọ́ ogun náà tí ó fi ẹnu kan oúnjẹ."'), '1Sam$14:25': ('ilẹ', 'ilẹ', '1Sam$14:25 "Gbogbo àwọn ènìyàn sì wọ inú igbó, oyin sì wà lórí ilẹ̀ náà."'), '1Sam$14:34': (None, 'nihin', '1Sam$14:34 "Nígbà náà ni ó wí pé, “Ẹ jáde lọ sáàrín àwọn ènìyàn náà kí ẹ sì wí fún wọn pé, ‘Kí olúkúlùkù wọn mú màlúù àti àgùntàn tirẹ̀ tọ̀ mí wá, kí wọ́n sì pa wọ́n níhìn, kí wọ́n sì jẹ́. Ẹ má ṣe ṣẹ̀ sí OLÚWA, kí ẹ má ṣe jẹ ẹran tòun tẹ̀jẹ̀.’ ” Bẹ́ẹ̀ ní olúkúlùkù mú màlúù tirẹ̀ wá ní alẹ́ ọjọ́ náà, wọ́n sì pa wọ́n níbẹ̀."'), '1Sam$14:44': (None, 'ṣe', '1Sam$14:44 "Ṣọ́ọ̀lù sì wí pé, “Kí OLÚWA kí ó ṣe bẹ́ẹ̀ àti jù bẹ́ẹ̀ lọ sí mi, bí ìwọ kò bá kú.”"'), '1Sam$15:6': ('wá', 'gba', '1Sam$15:6 "Nígbà náà ni ó wí fún àwọn Kénáítì pé, “Ẹ lọ, kúrò ní Ámálékì kí èmi má ba à run yín pẹ̀lú wọn; nítorí ẹ̀yin fi àánú hàn fún gbogbo àwọn ọmọ Ísírẹ́lì nígbà tí wọ́n gòkè ti Éjíbítì wá.” Bẹ́ẹ̀ ni àwọn Kénáítì lọ kúrò láàrin àwọn Ámálékì."'), '1Sam$15:9': ('ti o dara julọ', 'dara', '1Sam$15:9 Ṣùgbọ́n Ṣọ́ọ̀lù àti àwọn ọmọ ogun rẹ̀ dá Ágágì sí àti èyí tí ó dára jùlọ nínú àgùntàn àti màlúù àti ọ̀dọ́ àgùntàn àbọ́pa àti gbogbo nǹkan tó dára. Wọ́n kò sì fẹ́ pa àwọn wọ̀nyí run pátapáta ṣùgbọ́n gbogbo nǹkan tí kò dára tí kò sì ní láárí ni wọ́n parun pátapáta.'), '1Sam$15:10': (None, None, '1Sam$15:10 Nígbà náà ni ọ̀rọ̀ OLÚWA tọ Sámúẹ́lì wá pé:'), '1Sam$15:21': ('mu', 'ni', '1Sam$15:21 Àwọn ọmọ ogun ti mú àgùntàn àti màlúù lára ìkógùn èyí tí ó dára láti fi fún OLÚWA láti fi rúbọ sí OLÚWA Ọlọ́run rẹ ní Gílígálì.”'), '1Sam$16:7': ('iga', None, '1Sam$16:7 "Ṣùgbọ́n OLÚWA sọ fún Sámúẹ́lì pé, “Má ṣe wo ti ìrísí rẹ̀ tàbí ti gíga rẹ̀, nítorí èmi ti kọ̀ ọ́ sílẹ̀. OLÚWA kì í wo ohun tí ènìyàn máa ń wò. Ènìyàn máa ń wo òde ara ṣùgbọ́n OLÚWA máa ń wo ọkàn.”"'), '1Sam$16:13': ('ororo', None, '1Sam$16:13 "Nígbà náà ni Sámúẹ́lì mú ìwo òróró, ó sì ta á sí i ní orí ní iwájú àwọn ẹ̀gbọ́n rẹ̀, láti ọjọ́ náà lọ, Ẹ̀mi mímọ́ OLÚWA wá sí orí Dáfídì nínú agbára. Sámúẹ́lì sì lọ sí Rámà."'), '1Sam$16:17': (None, 'mú', '1Sam$16:17 "Nígbà náà ni Ṣọ́ọ̀lù wí fún àwọn ìránṣẹ́ rẹ̀ pé, “Ẹ wá ẹnìkan tí ó lè kọrin dáradára kí ẹ sì mú u wá fún mi.”"'), '1Sam$17:12': (None, None, '1Sam$17:12 "Nísinsìn yìí Dáfídì jẹ́ ọmọ ará Éfúrátà kan tí orúkọ rẹ̀ ń jẹ́ Jésè, tí ó wá látí Bétílẹ́hẹ́mù ní Júdà, Jésè ní ọmọ mẹ́jọ, ní àsìkò Sáulù ó ti darúgbó ẹni ọdún púpọ̀."'), '1Sam$17:17': (None, None, '1Sam$17:17 "Nísinsìn yìí Jésè wí fún ọmọ rẹ̀ Dáfídì pé, “Mú ìwọ̀n éfà àti àgbàdo yíyan àti ìṣù àkàrà mẹ́wàá lọ fún àwọn ẹ̀gbọ́n rẹ kí o sì ṣéré lọ sí ibùdó o wọn."'), '1Sam$17:24': (None, None, '1Sam$17:24 "Nígbà tí àwọn Ísírẹ́lì rí ọkùnrin náà, gbogbo wọn sì sá fún un ní ìbẹ̀rù-bojo."'), '1Sam$17:25': ('awọn ọkunrin', None, '1Sam$17:25 "Nígbà náà ni àwọn ọkùnrin Ísírẹ́lì wí pé, “Ǹjẹ́ ẹ̀yin kò ríi bí ọkùnrin yìí ṣe ń jáde wá? Ó jáde wá láti pe Ísírẹ́lì níjà. Ọba yóò fún ọkùnrin tí ó bá pa á ní ọrọ̀ púpọ̀. Yóò tún fi ọmọ rẹ̀ obìnrin fún un ní ìyàwó, yóò sì sọ ilé e baba rẹ̀ di òmìnira kúrò nínú sísan owó orí ní Ísírẹ́lì.”"'), '1Sam$17:26': (None, None, '1Sam$17:26 "Dáfídì béèrè lọ́wọ́ ọkùnrin tí ó dúró ní ẹ̀gbẹ́ ẹ rẹ̀ pé, “Kí ni a ó ò ṣe fún ọkùnrin náà tí ó bá pa Fílístínì yìí, tí ó sì mú ẹ̀gàn kúrò lára Ísírẹ́lì? Ta ni aláìkọlà Fílístínì tí ó jẹ́ wí pé yóò máa gan ogun Ọlọ́run alààyè?”"'), '1Sam$17:32': ('lọ', None, '1Sam$17:32 "Dáfídì sọ fún Ṣọ́ọ̀lù pé, “Kí ẹnikẹ́ni má ṣe ba ọkàn jẹ́ nítorí Fílístínì yìí, ìránṣẹ́ rẹ yóò lọ láti bá a jà.”"'), '1Sam$17:34': (None, None, '1Sam$17:34 "Ṣùgbọ́n Dáfídì sọ fún Ṣọ́ọ̀lù pé, “Ọmọ ọ̀dọ̀ rẹ ti ń tọ́jú agbo àgùntàn baba rẹ̀. Nígbà tí kìnìún tàbí àmọ̀tẹ́kùn bá wá láti wá gbé àgùntàn láti inú igbó."'), '1Sam$17:37': (None, None, '1Sam$17:37 "OLÚWA tí ó gbà mí kúrò lọ́wọ́ ìhàlẹ̀ kìnnìún àti ti ìhàlẹ̀ àmọ̀tẹ́kùn yóò gbà mí kúrò lọ́wọ́ Fílístínì yìí.” Ṣọ́ọ̀lù wí fún Dáfídì pé, “Lọ kí OLÚWA wà pẹ̀lú ù rẹ.”"'), '1Sam$17:46': ('fun', 'ọwọ', '1Sam$17:46 "Lónìí yìí ni OLÚWA yóò fi ọ́ lé mi lọ́wọ́, èmi yóò sì pa ọ́, èmi yóò sì gé orí ì rẹ. Lónìí èmi yóò fi òkú ogun Fílístínì fún ẹyẹ ojú ọ̀run àti fún ẹranko ìgbó, gbogbo ayé yóò sì mọ̀ pé Ọlọ́run wà ní Ísírẹ́lì."'), '1Sam$17:48': ('sáré', None, '1Sam$17:48 Bí Fílístínì ṣe súnmọ́ iwájú láti pàdé e rẹ̀. Dáfídì yára sáré sí oun náà láti pàdé e rẹ̀.'), '1Sam$17:51': ('ge', 'pa', '1Sam$17:51 "Dáfídì sì sáré ó sì dúró lórí rẹ̀. Ó sì mú idà Fílístínì, ó sì fà á yọ nínú àkọ̀ ọ rẹ̀. Lẹ́yìn ìgbà tí ó ti pa á tan, ó sì gé orí i rẹ̀ pẹ̀lú idà. Nígbà tí àwọn ará Fílístínì rí i wí pé akọni wọn ti kú, wọ́n yípadà wọ́n sì sálọ."'), '1Sam$18:1': ('ní', None, '1Sam$18:1 "Lẹ́yìn ìgbà tí Dáfídì ti parí ọ̀rọ̀ tí ó ń bá Ṣọ́ọ̀lù sọ, ọkàn Jónátanì di ọ̀kan pẹ̀lú ti Dáfídì, ó sì fẹ́ràn rẹ̀ gẹ́gẹ́ bí ara rẹ̀."'), '1Sam$18:2': ('ile', 'ile', '1Sam$18:2 Láti ọjọ́ náà Ṣọ́ọ̀lù pa Dáfídì mọ́ sí ọ̀dọ̀ rẹ̀ kò sì jẹ́ kí ó padà sí ilé baba rẹ̀ mọ́.'), '1Sam$18:6': ('pada', None, '1Sam$18:6 "Nígbà tí àwọn ènìyàn padà sí ilé lẹ́yìn ìgbà tí Dáfídì ti pa Fílístínì, gbogbo àwọn obìnrin tú jáde láti inú ìlú Ísírẹ́lì wá láti pàdé ọba Ṣọ́ọ̀lù pẹ̀lú orin àti ijó, pẹ̀lú orin ayọ̀ àti tamborínì àti ohun èlò orin."'), '1Sam$18:19': ('fi fun', None, '1Sam$18:19 "Nígbà tí àkókò tó fún Mérábù, ọmọbìnrin Ṣọ́ọ̀lù, láti fi fún Dáfídì, ni a sì fi fún Ádíríélì ara Méhólátì ní aya."'), '1Sam$18:21': ('fun', 'ọwọ', '1Sam$18:21 "Ó sọ nínú ara rẹ̀ pé, “Èmi yóò fi fún un kí òun ba à le jẹ́ tàkúté fún un, kí ọwọ́ àwọn ará Fílístínì lè wà lára rẹ̀.” Nígbà náà ni Ṣọ́ọ̀lù wí fún Dáfídì pé, “Nísinsìn yìí ìwọ ní àǹfààní eléyìí láti jẹ́ àna àn mi.”"'), '1Sam$18:30': ('lọ', None, '1Sam$18:30 "Àwọn olórí ogun Fílístínì tún tẹ̀ṣíwájú láti lọ sí ogun, ó sì ṣe lẹ́yìn ìgbà tí wọ́n lọ, Dáfídì ṣe àṣeyọrí ju gbogbo àwọn ìránṣẹ́ Ṣọ́ọ̀lù lọ, orúkọ rẹ̀ sì gbilẹ̀."'), '1Sam$19:2': (None, None, '1Sam$19:2 "Ó sì kìlọ̀ fún un pé, “Baba mi Ṣọ́ọ̀lù wá ọ̀nà láti pa ọ́, kíyèsí ara rẹ di òwúrọ̀ ọ̀la; lọ sí ibi ìkọ̀kọ̀, kí o sì sá pamọ́ sí ibẹ̀."'), '1Sam$19:4': ('pa', None, '1Sam$19:4 "Jónátanì sọ̀rọ̀ rere nípa Dáfídì fún Ṣọ́ọ̀lù baba rẹ̀, ó sì wí fún un pé, “Má ṣe jẹ́ kí ọba kí ó ṣe ohun tí kò dára fún Dáfídì ìránṣẹ́ rẹ̀; nítorí tí kò ṣẹ̀ ọ́, ohun tí ó sì ṣe pé ọ púpọ̀."'), '1Sam$20:5': ('lọ', None, '1Sam$20:5 "Dáfídì wí pé, “Wò ó, ọ̀la ni oṣù tuntun, mo sì gbọdọ́ bá ọba jẹun, ṣùgbọ́n jẹ́ kí èmi kí ó lọ láti fi ara pamọ́ lórí pápá títí di àṣálẹ́ ọjọ́ kẹta."'), '1Sam$20:12': ('dun', 'lọ', '1Sam$20:12 "Nígbà náà ni Jónátanì sọ fún Dáfídì: “Pẹ̀lú OLÚWA Ọlọ́run àwọn Ísírẹ́lì, èmi yóò rí i dájú pé mo gbọ́ ti ẹnu baba mi ní ìwòyí ọ̀la tàbí ní ọ̀túnla tí ó bá sì ní ojú rere ní inú dídùn sí ọ, tí èmi kò bá ránṣẹ́ sí ọ, kí èmi sì jẹ́ kí o mọ̀?"'), '1Sam$20:25': ('ibi', 'ijoko', '1Sam$20:25 "Ó sì jókòó gẹ́gẹ́ bí ipò o rẹ̀ ní ibi tí ó máa ń ijòkòó lórí ìjòkó tí ó wà lẹ́gbẹ̀ ẹ́ ògiri, ní òdìkejì Jónátanì, Ábínérì sì jókòó ti Ṣọ́ọ̀lù, ṣùgbọ́n àyè Dáfídì sì ṣófo."'), '1Sam$20:28': (None, 'wo', '1Sam$20:28 "Jónátanì sì dáhùn pé, “Dáfídì bẹ̀ mí tọ̀wọ̀tọ̀wọ̀ fún ààyè láti lọ sí Bẹ́tílẹ́hẹ́mù."'), '1Sam$20:31': ('mulẹ', 'ilẹ', '1Sam$20:31 "Níwọ̀n ìgbà tí ọmọ Jésè bá wà láàyè lórí ilẹ̀ ayé, a kì yóò fi ẹṣẹ̀ ìwọ tàbí ti ìjọba rẹ múlẹ̀. Nísinsìn yìí ránṣẹ́ kí o sì lọ mú un wá fún mi, nítorí ó gbọ́dọ̀ kú!”"'), '1Sam$20:35': ('lọ', None, '1Sam$20:35 "Ó sì ṣe, ní òwúrọ̀ ni Jónátanì jáde lọ sí oko ní àkókò tí òun àti Dáfídì ti fí àdéhùn sí, ọmọdekùnrin kan sì wá pẹ̀lú rẹ̀."'), '1Sam$20:39': ('nkan', None, '1Sam$20:39 (Ọmọdékùnrin náà kò sì mọ̀ nǹkan; ṣùgbọ́n Jónátanì àti Dáfídì ni ó mọ ọ̀ràn náà.)'), '1Sam$21:2': (None, 'ni', '1Sam$21:2 "Dáfídì sì wí fún Áhímélékì àlùfáà pé, “Ọbá pàṣẹ iṣẹ́ kan fún mi, ó sì wí fún mi pé, ‘Má ṣe jẹ́ kí ẹnìkan mọ ìdí iṣẹ́ náà ti mo rán ọ, àti èyí tí èmi ti pàṣẹ fún ọ.’ Èmi sì yan àwọn ìránṣẹ́ mi sí ibí báyìí."'), '1Sam$21:15': (None, None, '1Sam$21:15 Mo há ni un fi aṣiwèrè ṣe? Tí ẹ̀yin fi mú èyí tọ̀ mí wá lati hú ìwà aṣiwèrè níwájú mi? Eléyìí yóò há wọ inú ilé mi?”'), '1Sam$22:3': ('wá', 'ṣe', '1Sam$22:3 "Dáfídì sì ti ibẹ̀ náà lọ sí Mísípa tí Móábù: ó sì wí fún ọba Móábù pé, “Jẹ́ kí baba àti ìyá mi, èmi bẹ̀ ọ́ wá bá ọ gbé, títí èmi yóò fi mọ ohun ti Ọlọ́run yóò ṣe fún mi.”"'), '1Sam$22:6': ('ṣe', 'ni', '1Sam$22:6 "Ṣọ́ọ̀lù si gbọ́ pé a rí Dáfídì àti àwọn ọkùnrin tí ó wà lọ́dọ̀ rẹ̀; Ṣọ́ọ̀lù sì ń bẹ ní Gíbéà lábẹ́ igi kan ní Rámà; ọ̀kọ̀ rẹ̀ sì ń bẹ lọ́wọ́ rẹ̀, àti gbogbo àwọn ìránṣẹ́ rẹ̀ sì dúró tì í."'), '1Sam$22:13': ('fi fun', 'ni', '1Sam$22:13 "Ṣọ́ọ̀lù sì wí fún un pé, “Kí ni ó dé tí ẹ̀yin fi dìmọ̀lù sí mi, ìwọ àti ọmọ Jésè, tí ìwọ fi fún un ní àkàrà, àti idà, àti ti ìwọ fi béèré fún un lọ́dọ̀ Ọlọ́run kí òun lè dìde sí mi, láti ba dè mí, bí ó ti rí lónìí.”"'), '1Sam$23:23': ('lọ', None, '1Sam$23:23 "Ẹ sì wo, kí ẹ sì mọ ibi ìsápamọ́ tí ó máa ń sápamọ́ sí, kí ẹ sì tún padà tọ̀ mí wá, kí èmi lè mọ̀ dájú; èmi ó sì bá yín lọ: yóò sì ṣe bí ó bá wà ní ilẹ̀ Ísírẹ́lì, èmi ó sì wá a ní àwárí nínú gbogbo ẹgbẹ̀rún Júdà!”"'), '1Sam$23:26': ('gba', 'gba', '1Sam$23:26 "Ṣọ́ọ̀lù sì ń rin apákan òkè kan, Dáfídì àti àwọn ọmọkùnrin rẹ̀ ní apákejì òkè náà. Dáfídì sì yára láti sá kúrò níwájú Ṣọ́ọ̀lù; nítorí pé Ṣọ́ọ̀lù àti àwọn ọmọkùnrin rẹ̀ ti rọ̀gbà yí Dáfídì àti àwọn ọmọkùnrin rẹ̀ ká láti mú wọn."'), '1Sam$24:1': ('pada', None, '1Sam$24:1 "Nígbà tí Ṣọ́ọ̀lù padà kúrò lẹ́yìn àwọn Fílístínì a sì sọ fún un pé, “Wò ó, Dáfídì ń bẹ́ ni ihà Éńgédì.”"'), '1Sam$24:10': (None, 'ni', '1Sam$24:10 "Wò ó, ojú rẹ́ ríi lónìí, bí OLÚWA ti fi ìwọ lé mi lọ́wọ́ lónìí nínú ihò; àwọn kan ní kí èmi ó pa ọ; ṣùgbọ́n èmi dá ọ sí; ‘Èmi sì wí pé, èmi kì yóò nawọ́ mi sí olúwa mi, nítorí pé ẹni-àmì-òróró OLÚWA ni òun jẹ́.’"'), '1Sam$24:11': ('ni', 'gba', '1Sam$24:11 "Pẹ̀lúpẹ̀lú, baba mi, wò ó, àní wo etí aṣọ rẹ lọ́wọ́ mi; nítorí èmi gé etí aṣọ rẹ, èmi kò sì pa ọ́, sì wò ó, kí o sì mọ̀ pé kò sí ibi tàbí ẹ̀ṣẹ̀ lọ́wọ́ mi, èmi kò sì ṣẹ̀ ọ̀, ṣùgbọ́n ìwọ ń dọdẹ ẹ̀mi mi láti gbà á."'), '1Sam$25:11': (None, None, '1Sam$25:11 "Ǹjẹ́ ki èmi o mú òunjẹ́ mi, àti omi mi, àti ẹran mi ti mo pá fún àwọn olùrẹ́run mi, ki èmi si fi fún àwọn ọkùnrin ti èmi kò mọ̀ ibi wọ́n ti wá?”"'), '1Sam$25:13': (None, None, '1Sam$25:13 "Dáfídì sí wí fún àwọn ọmọkùnrin rẹ̀ pé, “Kí olúkúlùkù yín ó di idá rẹ́ mọ́ idi,” Olúkúlùkù ọkùnrin sì di idà tirẹ́ rẹ́ mọ̀ ìdí; àti Dáfídì pẹ̀lú sì di idà tirẹ̀; ìwọ̀n irinwó ọmọkùnrin si gòkè tọ Dáfídì lẹ́yin; igba si jókòó nibi ẹrù."'), '1Sam$25:18': ('ṣe', None, '1Sam$25:18 "Ábígáílì sì yára, ó sì mú igba ìṣù àkàrà àti ìgò-ọtí wáìnì méjì, àti àgùntàn márùn-ún, ti a ti ṣè, àti oṣuwọn àgbàdo yíyan márùn-ún, àti ọgọ́rùn-ún ìdì àjàrà, àti igba àkàrà èso ọ̀pọ̀tọ́, ó sì dì wọ́n ru kẹ́tẹ́kẹ́tẹ́."'), '1Sam$25:23': ('ri', None, '1Sam$25:23 "Ábígáílì sì rí Dáfídì, ó sì yára, ó sọ̀kalẹ̀ lórí kẹ́tẹ́kẹ́tẹ́, ó sì dojúbolẹ̀ níwájú Dáfídì, ó sì rẹ ara rẹ̀ sílẹ̀."'), '1Sam$25:25': (None, None, '1Sam$25:25 "OLÚWA mi, èmi bẹ̀ ọ́ má ka ọkùnrin Bélíálì yìí sí, àní Nábálì: nítorí pé bí orúkọ rẹ̀ ti jẹ́ bẹ́ẹ̀ ni òun náà rí: Nábálì ni orúkọ rẹ̀, àìmoye si wà pẹ̀lú rẹ̀; ṣùgbọ́n èmi ìránṣẹ́bìnrin rẹ kò ri àwọn ọmọkùnrin olúwa mi, ti ìwọ rán."'), '1Sam$25:27': (None, 'jẹ', '1Sam$25:27 "Ǹjẹ́ èyí ni ẹ̀bùn tí ìránṣẹ́bìnrin rẹ mú wá fún olúwa mi, jẹ́ kí a sì fi fún àwọn ọmọkùnrin ti ń tọ olúwa mi lẹ́yìn."'), '1Sam$25:30': ('ṣe', 'wá', '1Sam$25:30 "Yóò sì ṣe, OLÚWA yóò ṣe sí olúwa mi gẹ́gẹ́ bí gbogbo ìre tí ó ti wí nípa tirẹ̀, yóò sì yàn ọ́ ni aláṣẹ lórí Ísírẹ́lì."'), '1Sam$25:35': ('gba', 'ni', '1Sam$25:35 "Bẹ́ẹ̀ ni Dáfídì sì gba nǹkan tí ó mú wá fún un lọ́wọ́ rẹ̀, ó sì wí fún un pé, “Gòkè lọ ni àlàáfíà sí ilé rẹ, wò ó, èmi ti gbọ́ ohùn rẹ, inú mi sì dùn sí ọ.”"'), '1Sam$25:37': (None, None, '1Sam$25:37 "Ó sì ṣe; ni òwúrọ̀, nígbà ti ọtí náà si dá tán lójú Nábálì, obìnrin rẹ̀ si ro nǹkan wọ̀nyí fún un, ọkàn rẹ sì kú nínú rẹ̀, òun sì dàbí òkúta."'), '1Sam$25:41': ('awọn iranṣẹ', 'iranṣẹbinrin', '1Sam$25:41 "Ó sì dìde, ó sì dojúbolẹ̀, ó sì wí pé, “Wò ó, jẹ́ kí ìránṣẹ́bìnrin rẹ ó jẹ́ ìránṣẹ́ kan láti máa wẹ ẹsẹ̀ àwọn ìránṣẹ́ olúwa mi.”"'), '1Sam$26:10': (None, 'sokale', '1Sam$26:10 "Dáfídì sì wí pé, “Bí OLÚWA tí ń bẹ OLÚWA yóò pa á, tàbí ọjọ́ rẹ̀ yóò sì pé tí yóò kú, tàbí òun ó sọ̀kalẹ̀ lọ sí ibi ìjà yóò sì ṣègbé níbẹ̀."'), '1Sam$26:20': ('wá', None, '1Sam$26:20 Ǹjẹ́ má ṣe jẹ́ kí ẹ̀jẹ̀ mi ó ṣàn sílẹ̀ níwájú OLÚWA; nítorí ọba Ísírẹ́lì jáde láti wá ẹ̀mí mi bi ẹni ń dọdẹ àparò lórí òkè ńlá.”'), '1Sam$26:23': (None, None, '1Sam$26:23 "Kí OLÚWA o san án fún olúkúlùkù bí òdodo rẹ̀ àti bí òtítọ́ rẹ̀: nítorí pé OLÚWA tí fi ọ lé mi lọ́wọ́ lónìí, ṣùgbọ́n èmi kò fẹ́ nawọ́ mi sí ẹni àmì òróró OLÚWA."'), '1Sam$27:3': (None, None, '1Sam$27:3 "Dáfídì sì bá Ákíṣì jókòó ní Gátì, òun, àti àwọn ọmọkùnrin rẹ, olúkúlùkù wọn pẹ̀lú ará ilé rẹ̀; Dáfídì pẹ̀lú àwọn aya rẹ̀ méjèèjì, Áhínóámù ara Jésréélì, àti Ábígáílì ará Kámẹ́lì aya Nábálì."'), '1Sam$27:5': ('jẹ ki', 'ni', '1Sam$27:5 "Dáfídì sì wí fún Ákíṣì pé, “Bí ó bá jẹ́ pé èmi rí oore-ọ̀fẹ́ lójú rẹ̀, jẹ́ kí wọn ó fún mi ní ibìkan nínú àwọn ìletò wọ̀nyí; èmi yóò máa gbé ibẹ̀: èéṣé tí ìránṣẹ́ rẹ yóò sì máa bá ọ gbé ní ìlú ọba.”"'), '1Sam$27:10': (None, 'ni', '1Sam$27:10 "Ákíṣì sì bi í pé, “Níbo ni ẹ̀yin gbé rìn sí lónìí?” Dáfídì sì dáhùn pé, “Síhà gúsù ti Júdà ni, tàbí Síhà gúsù ti Jérámélì,” tàbí “Síhà gúsù ti àwọn ará Kénì.”"'), '1Sam$28:3': ('ní', None, '1Sam$28:3 "Sámúẹ́lì sì ti kú, gbogbo Ísírẹ́lì sì sọkún rẹ̀, wọ́n sì sin ín ní Rámà ní ìlú rẹ̀. Ṣọ́ọ̀lù sì ti mú àwọn abókúsọ̀rọ̀ ọkùnrin, àti àwọn abókúsọ̀rọ̀ obìnrin kúrò ní ilẹ̀ náà."'), '1Sam$28:9': ('ṣe', None, '1Sam$28:9 "Obìnrin náà sì dá a lóhùn pé “Wò ó, ìwọ sáà mọ ohun tí Ṣọ́ọ̀lù ṣe, bí òun ti gé àwọn abókúsọ̀rọ̀ obìnrin, àti àwọn abókúsọ̀rọ̀ ọkùnrin kúrò ní ilẹ̀ náà; ǹjẹ́ èé ha ṣe tí ìwọ dẹ́kùn fún ẹ̀mí mi, láti mú kí wọ́n pa mí.”"'), '1Sam$28:17': ('fi fun', 'ọwọ', '1Sam$28:17 "OLÚWA sì ṣe fún ara rẹ̀ gẹ́gẹ́ bí ó ti ti ipa ọwọ́ mi sọ: OLÚWA sì yá ìjọba náà kúrò ní ọwọ́ rẹ̀, ó sì fi fún aládùúgbò rẹ́, àní Dáfídì."'), '1Sam$28:22': (None, 'ni', '1Sam$28:22 "Ǹjẹ́, nísinsìnyìí èmi bẹ̀ ọ́, gbọ́ ohùn ìránṣẹ́bìnrin rẹ, èmi yóò sì fi oúnjẹ díẹ̀ ṣíwájú rẹ̀; sì jẹun, ìwọ yóò sì lágbára, nígbà tí ìwọ bá ń lọ lọ́nà.”"'), '1Sam$29:6': (None, None, '1Sam$29:6 "Ákíṣì sì pe Dáfídì, ó sì wí fún un pé, “Bí OLÚWA ti ń bẹ láàyè, ìwọ jẹ́ ólóòótọ́ àti ẹni ìwà rere lójú mi, ní àlọ rẹ àti ààbọ̀ rẹ pẹ̀lú mi ní ogun: nítorí pé èmi ko tìí ri búburú kan lọ́wọ́ rẹ́ láti ọjọ́ ti ìwọ ti tọ̀ mí wá, títí o fi dì òní yìí ṣùgbọ́n lójú àwọn ìjòyè, ìwọ kò ṣe ẹni tí ó tọ́."'), '1Sam$30:1': (None, None, '1Sam$30:1 "Ó sì ṣe nigbà ti Dáfídì àti àwọn ọmọkùnrin rẹ̀ sì bọ̀ sí Síkílágì ní ọjọ́ kẹta, àwọn ará Ámálékì sì ti kọlu Néséfà, àti Síkílágì, wọ́n sì ti kùn ún ní iná."'), '1Sam$30:16': (None, 'ilẹ', '1Sam$30:16 "Ó sì mú un sọ̀kalẹ̀, sì wò ó, wọ́n sì tàn ká ilẹ̀, wọ́n ń jẹ, wọ́n ń mu, wọ́n sì ń jó, nítorí ìkógun púpọ̀ tí wọ́n kó láti ilẹ̀ àwọn Fílístínì wá, àti láti ilẹ̀ Júdà."'), '1Sam$30:17': (None, None, '1Sam$30:17 "Dáfídì sì pa wọ́n láti àfẹ̀mọ́júmọ́ títí ó fi di àṣálẹ́ ijọ́ kejì: kò sí ẹnìkan tí ó là nínú wọn, bí kọ̀ ṣe irínwó ọmọkùnrin tí wọ́n gun ràkúnmí tí wọ́n sì sá."'), '1Sam$30:21': ('wá', None, '1Sam$30:21 "Dáfídì sì wá sọ́dọ̀ igba ọkùnrin tí àárẹ̀ ti mú jú, ti wọn kò lè tọ́ Dáfídì lẹ́yin mọ́, ti òun ti fi sílẹ̀, ni odò Bésórì: wọ́n sì lọ pàdé Dáfídì, àti láti pàdé àwọn ènìyàn tí ó lọ pẹ̀lú rẹ̀: Dáfídì sì pàdé àwọn ènìyàn náà, ó sì kí wọn."'), '1Sam$30:22': ('ni', 'fun', '1Sam$30:22 "Gbogbo àwọn ènìyàn búburú àti àwọn ọmọ Bélíálì nínú àwọn tí o bá Dáfídì lọ sì dáhùn, wọ́n sì wí pé, “Bí wọn kò ti bá wa lọ, àwa ki yóò fi nǹkan kan fún wọn nínú ìkógun ti àwa rí gbà bí kò ṣe obìnrin olúkúlùkù wọn, àti ọmọ wọn; ki wọn sì mú wọn, ki wọn sì máa lọ.”"'), '1Sam$30:23': (None, 'ọwọ', '1Sam$30:23 "Dáfídì sì wí pé, “Ẹ má ṣe bẹ́ẹ̀, ẹ̀yin ara mi: OLÚWA ni ó fi nǹkan yìí fún wa, òun ni ó sì pa wá mọ́, òun ni ó sì fi ẹgbẹ́-ogun ti ó dìde sí wa lé wa lọ́wọ́."'), '1Sam$31:8': ('ṣubu', None, '1Sam$31:8 "Ní ọjọ́ kejì, nígbà tí àwọn Fílístínì dé láti bọ́ nǹkán tí ń bẹ lára àwọn tí ó kù, wọ́n sì rí pé, Ṣọ́ọ̀lù àti àwọn ọmọ rẹ̀ mẹ́ta ṣubú ni òkè Gílíbóà."'), '2Sam$1:6': (None, None, '2Sam$1:6 " “Ọ̀dọ́mọkùnrin náà sọ pé, mo wà ní orí òkè Gílíbóà níbẹ̀ ni Ṣọ́ọ̀lù fi ara tí ọkọ̀ rẹ̀, àwọn kẹ̀kẹ́ àti ẹlẹ́ṣin sì ń lépa rẹ̀."'), '2Sam$1:10': ('mu', 'ni', '2Sam$1:10 "“Nígbà náà, mo dúró lórí i rẹ̀, mo sì pa á nítorí mo mọ̀ wí pé, lẹ́yìn ìgbà tí ó ti ṣubú, kò le è yè mọ́. Mo sì mú adé tí ó wà lórí rẹ̀, idẹ tí ó wà ní apá a rẹ̀, mo sì kó wọn wá síbí fún OLÚWA mi.”"'), '2Sam$1:11': ('mu', None, '2Sam$1:11 "Nígbà náà ni, Dáfídì àti gbogbo àwọn tí ó wà pẹ̀lú rẹ̀, di aṣọ wọn mú, wọ́n sì fà á ya."'), '2Sam$1:21': ('ororo', None, '2Sam$1:21 "“Ẹ̀yin òkè Gílíbóà, kí ẹ̀yin kí ó má ṣe rí ìrì tàbí òjò, tàbí oko tí ó mú èso ọrẹ wá. Nítorí ibẹ̀ ni aṣà alágbára ti ṣègbé, aṣà Ṣọ́ọ̀lù, bí ẹni pé a kò fi òróró yàn án."'), '2Sam$2:1': ('lọ', None, '2Sam$2:1 "Lẹ́yìn àkókò yìí, Dáfídì wádìí lọ́wọ́ OLÚWA. Ó sì béèrè pé, “Ṣé èmi lè gòkè lọ sí ọ̀kan lára àwọn ìlú Júdà?” OLÚWA sì wí pé, “Gòkè lọ.” Dáfídì sì béèrè pé, “Ní ibo ni kí èmi kí ó lọ?” OLÚWA sì dáa lóhùn pé, “Sí Hébúrónì.”"'), '2Sam$2:3': ('awọn ọkunrin', None, '2Sam$2:3 "Dáfídì sì mú àwọn ọkùnrin tí ó wà pẹ̀lú rẹ̀, olúkúlùkù pẹ̀lú ìdílé rẹ̀, wọ́n sì ń gbé ní Hébúrónì àti ìlú rẹ̀ mìíràn."'), '2Sam$2:7': (None, 'ni', '2Sam$2:7 "Ǹjẹ́ nísinsìn yìí, ẹ mú ara yín le, kí ẹ sì ní ìgboyà, nítorí Ṣọ́ọ̀lù ọba yín ti kú, ilé Júdà sì ti fi àmì òróró yàn mí ní ọba lórí wọn.”"'), '2Sam$2:21': ('gba', None, '2Sam$2:21 "Nígbà náà, Ábínérì sọ fún un pé, “Yípadà sí ọ̀tún tàbí òsì; mú ọ̀kan lára àwọn ọ̀dọ́mọkùnrin kí o sì bọ́ ìhámọ́ra rẹ.” Ṣùgbọ́n Ásáhélì kọ̀ láti dẹ́kun lílépa rẹ̀."'), '2Sam$2:23': ('ṣubu', None, '2Sam$2:23 "Ṣùgbọ́n ó sí kọ̀ láti padà, Ábínérì sì fi òdì ọ̀kọ̀ gún un lábẹ́ inú, ọ̀kọ̀ náà sì jáde ní ẹ̀yìn rẹ̀: òun sì ṣubú lulẹ̀ níbẹ́, ó sì kú ní ibi kan náà; ó sí ṣe gbogbo ènìyàn tí ó dé ibi tí Ásáhélì gbé ṣúbú si, tí ó sì kú, sì dúró jẹ́."'), '2Sam$3:8': (None, 'ni', '2Sam$3:8 "Ábínérì sì bínú gidigidi nítorí ọ̀rọ̀ wọ̀nyí ti Iṣibósẹ́tì sọ fún un, ó sì wí pé, “Èmi jẹ́ bí orí ajá ti Júdà bí? Di òní yìí ni mọ ṣàáánú fún ìdílé Ṣọ́ọ̀lù bàbá rẹ, àti fún àwọn arákùnrin rẹ̀, àti fún àwọn ọ̀rẹ́ rẹ̀, tí èmi kò sì fi ọ́ lé Dáfídì lọ́wọ́, ìwọ sì kà ẹ̀ṣẹ̀ sí mí lọ́rùn nítorí obìnrin yìí lóní?"'), '2Sam$3:9': ('ṣe', None, '2Sam$3:9 "Bẹ́ẹ̀ ni kí Ọlọ́run ó ṣe Ábínérì, àti jù bẹ́ẹ̀ lọ pẹ̀lú, bí OLÚWA ti búra fún Dáfídì, bí èmi kò ní ṣe bẹ́ẹ̀ fún un."'), '2Sam$3:34': (None, 'eniyan', '2Sam$3:34 "A kò sáà dè ọ́ lọ́wọ́, bẹ́ẹ̀ ni a kò kan ẹsẹ̀ rẹ ní àbà. Gẹ́gẹ́ bí ènìyàn tí ń ṣubú níwájú àwọn ìkà ènìyàn, bẹ́ẹ̀ ni ìwọ ṣubú.” Gbogbo àwọn ènìyàn náà sì tún sọkún lórí rẹ̀."'), '2Sam$3:35': ('wá', 'ṣe', '2Sam$3:35 "Nígbà tí gbogbo ènìyàn sì wá láti gba Dáfídì ní ìyànjú kí ó jẹun, nígbà tí ọjọ́ sì ń bẹ, Dáfídì sì búra wí pé, “Bẹ́ẹ̀ ni kí Ọlọ́run ó ṣe sí mi àti jù bẹ́ẹ̀ lọ, bí èmi yóò bá tọ́ ounjẹ wò, tàbí nǹkan mìíràn títí òòrùn yóò fi wọ̀!”"'), '2Sam$4:9': (None, None, '2Sam$4:9 "Dáfídì sì dá Rákábù àti Báánà arakùnrin rẹ̀, àwọn ọmọ Rímímónì ará Béérótì lóhùn, ó sì wí fún wọn pé, “Bí OLÚWA ti ń bẹ, ẹni tí ó gba ẹ̀mí mi lọ́wọ́ gbogbo ìpọ́njú."'), '2Sam$4:12': ('ge', 'pa', '2Sam$4:12 "Dáfídì sì fi àṣẹ fún àwọn ọdọ́mọkùnrin rẹ̀, wọ́n sì pa wọ́n, wọ́n sì gé ọwọ́ àti ẹṣẹ̀ wọn, a sì fi wọ́n há lórí igi ní Hébírónì. Ṣùgbọ́n wọ́n mú orí Íṣíbóṣétì, wọ́n sì sin ín ní ibojì Ábínérì ní Hébírónì."'), '2Sam$5:7': (None, None, '2Sam$5:7 Ṣùgbọ́n Dáfídì fi agbára gba ìlú odì Síónì: èyí náà ni í ṣe ìlú Dáfídì.'), '2Sam$5:24': ('lọ', None, '2Sam$5:24 "Yóò sì ṣe, nígbà tí ìwọ bá gbọ́ ìró ẹṣẹ̀ lórí àwọn igi Bákà náà, nígbà náà ni ìwọ yóò sì yára, nítorí pé nígbà náà ni OLÚWA yóò jáde lọ níwájú rẹ láti kọlù ogun àwọn Fílístínì.”"'), '2Sam$5:25': ('ṣe', None, '2Sam$5:25 "Dáfídì sì ṣe bẹ́ẹ̀, gẹ́gẹ́ bí OLÚWA ti pàṣẹ fún un; ó sì kọlu àwọn Fílístínì láti Gébà títí dé Gásérì."'), '2Sam$6:6': ('mu', None, '2Sam$6:6 "Nígbà tí wọ́n sì dé ibi ìpakà Nákónì, Úsà sì na ọwọ́ rẹ̀ sí àpótí-ẹ̀rí Ọlọ́run, ó sì dì í mú, nítorí tí màlúù kọsẹ̀."'), '2Sam$6:8': (None, 'orukọ', '2Sam$6:8 Inú Dáfídì sì bàjẹ́ nítorí tí OLÚWA gé Úsà kúrò: ó sì pe orúkọ ibẹ̀ ní Peresí-Úsà títí ó fi di òní yìí.'), '2Sam$6:11': ('ile', None, '2Sam$6:11 "Àpótí-ẹ̀rí OLÚWA sì gbé ní ilé Obedì-Édómù ará Gátì ní oṣù mẹ́ta; OLÚWA sì bùkún fún Obedì-Édómù, àti gbogbo ilé rẹ̀."'), '2Sam$6:18': ('ṣe', 'orukọ', '2Sam$6:18 "Dáfídì sì parí ìṣe ẹbọ sísun àti ẹbọ ìrẹ́pọ̀ náà, ó sì súre fún àwọn ènìyàn náà ní orúkọ OLÚWA àwọn ọmọ-ogun."'), '2Sam$6:19': (None, None, '2Sam$6:19 "Ó sì pín fún gbogbo àwọn ènìyàn náà, àní fún gbogbo ọ̀pọ̀ ènìyàn Ísírẹ́lì, àti ọkùnrin àti obìnrin; fún olúkúlùkù ìṣù àkàrà kan àti èkìrí ẹran kan, àti àkàrà díndín kan. Gbogbo àwọn ènìyàn náà sì túká lọ, olúkúlùkù sí ilé rẹ̀."'), '2Sam$6:22': ('ní', None, '2Sam$6:22 "Èmi ó sì tún rẹ ara mi sílẹ̀ jú bẹ́ẹ̀ lọ, èmi ó sì ṣe aláìnìyín lójú ara mi, àti lójú àwọn ìránṣẹ́bìnrin náà ti ìwọ wí, lọ́dọ̀ wọn náà ni èmi ó sì ní ògo.”"'), '2Sam$7:1': (None, None, '2Sam$7:1 " Ó sì ṣe, nígbà tí ọba ń gbé ní ilé rẹ̀, tí OLÚWA sì fún un ní ìsinmi yíká kiri kúrò lọ́wọ́ gbogbo àwọn ọ̀tá rẹ̀."'), '2Sam$7:8': ('ṣe', 'ni', '2Sam$7:8 "“Ǹjẹ́, nítorí náà, báyìí ni ìwọ yóò sì wí fún ìránṣẹ́ mi àní Dáfídì pé, ‘Báyìí ni OLÚWA àwọn ọmọ-ogun wí: Èmi ti mú ìwọ kúrò láti inú agbo àgùntàn wá láti má tẹ̀lé àwọn àgùntàn, mo sì fi ọ́ jẹ olórí àwọn ènìyàn mi, àní Ísírẹ́lì."'), '2Sam$7:10': (None, 'ni', '2Sam$7:10 "Èmi ó sì yan ibìkan fún àwọn ènìyàn mi, àní Ísírẹ́lì, èmi ó sì gbìn wọ́n, wọn ó sì má gbé bí ibùjókòó ti wọn, wọn kì yóò sì ṣípò padà mọ́; àwọn ọmọ ènìyàn búburú kì yóò sì pọ́n wọn lójú mọ́, bí ìgbà àtijọ́."'), '2Sam$7:23': ('ayé', 'ilẹ', '2Sam$7:23 "Orílẹ̀-èdè kan wo ni ó sì ń bẹ ní ayé tí ó dà bí àwọn ènìyàn rẹ, àní Ísírẹ́lì, àwọn tí Ọlọ́run lọ ràpadà láti sọ wọ́n di ènìyàn rẹ̀, àti láti sọ wọ́n ní orúkọ, àti láti ṣe nǹkan ńlá fún un yín, àti nǹkan ìyanu fún ilé rẹ̀, níwájú àwọn ènìyàn rẹ, tí ìwọ ti rà padà fún ara rẹ láti Éjíbítì wá, àní àwọn orílẹ̀-èdè àti àwọn òrìṣà wọn."'), '2Sam$8:2': (None, None, '2Sam$8:2 "Ó sì kọlu Móábù, ó sì fi okùn títa kan dìwọ́n, ó sì dá wọn dùbúlẹ̀; ó sì ṣe òṣùwọ̀n okùn méjì ni iye àwọn tí yóò dá sí. Àwọn ará Móábù sì ń sin Dáfídì, wọn a sì máa mú ẹ̀bùn wá."'), '2Sam$9:6': (None, 'wá', '2Sam$9:6 "Méfibóṣétì ọmọ Jónátaanì ọmọ Ṣọ́ọ̀lù sì tọ Dáfídì wá, ó sì wólẹ̀ níwájú rẹ̀, ó sì bú ọlá fún un. Dáfídì sì wí pé, “Méfibóṣétì!” Òun sì dáhún wí pé, “Wò ó, ìránṣẹ́ rẹ!”"'), '2Sam$9:9': ('fi fun', 'ni', '2Sam$9:9 "Ọba sì pe Síbà ìránṣẹ́ Ṣọ́ọ̀lù, ó sì wí fún un pé, “Gbogbo nǹkan tí í ṣe ti Ṣọ́ọ̀lù, àti gbogbo èyí tí í ṣe ti ìdílé rẹ̀ ni èmi fi fún ọmọ olúwa rẹ."'), '2Sam$11:1': (None, None, '2Sam$11:1 " Lẹ́yìn ìgbà tí ọdún yípo, ní àkókò ìgbà tí àwọn ọba náà ń jáde ogun, Dáfídì sì rán Jóábù, àti àwọn ìránṣẹ́ rẹ̀ pẹ̀lú rẹ̀, àti gbogbo Ísírẹ́lì; wọ́n sì pa àwọn ọmọ Ámónì, wọ́n sì dó ti Rábà. Dáfídì sì jókòó ní Jérúsálẹ́mù."'), '2Sam$11:2': ('ri', 'wo', '2Sam$11:2 "Ó sì ṣe, ní ìgbà àṣálẹ́ kan, Dáfídì sì díde ní ibùsùn rẹ̀, ó sì ń rìn lórí òrùlé ilé ọba, láti orí òrùlé náà ni ó sì rí obìnrin kan tí ó ń wẹ̀ ara rẹ̀; obìnrin náà sì ṣe arẹwà lójú láti wò."'), '2Sam$11:7': ('ṣe', None, '2Sam$11:7 "Nígbà tí Ùráyà sì dé ọ̀dọ̀ rẹ̀, Dáfídì sì bi í léèrè báwo ni Jóábù ti ṣe àti àlàáfíà àwọn ènìyàn náà, àti bí ogun náà ti ń ṣe."'), '2Sam$11:24': (None, None, '2Sam$11:24 "Àwọn tafàtafà sì ta sí ìránṣẹ́ rẹ láti orí odi wá, díẹ̀ nínú àwọn ìránṣẹ́ ọba sì kú, ìránṣẹ́ rẹ̀ Ùráyà ará Hítì sì kú pẹ̀lú.”"'), '2Sam$12:2': ('ní', None, '2Sam$12:2 Ọkùnrin ọlọ́rọ̀ náà sì ní àgùntàn àti màlúù lọ́pọ̀lọpọ̀.'), '2Sam$12:20': (None, None, '2Sam$12:20 "Dáfídì sì dìde ní ilẹ̀, ó sì wẹ̀, ó fi òróró pa ara, ó sì pààrọ̀ aṣọ rẹ̀, ó sì wọ inú ilé OLÚWA lọ, ó sì wólẹ̀ sin: ó sì wá sí ilé rẹ̀ ó sì bèèrè, wọ́n sì gbé òunjẹ kalẹ̀ níwájú rẹ̀, ó sì jẹun."'), '2Sam$12:22': (None, None, '2Sam$12:22 "Ó sì wí pé, “Nígbà tí ọmọ náà ń bẹ láàyè, èmi gbààwẹ̀, èmi sì sunkún: nítorí tí Èmi wí pé, ‘Ta ni ó mọ̀ bí Ọlọ́run ó ṣàánú mi, kí ọmọ náà le yè.’"'), '2Sam$12:27': (None, None, '2Sam$12:27 "Jóábù sì rán àwọn ìránṣẹ́ sí Dáfídì, ó sì wí pé, “Èmi ti bá Rábà jà, èmi sì ti gba àwọn ìlú olomi."'), '2Sam$13:5': ('ṣe', 'fun', '2Sam$13:5 "Jónádábù sì wí fún un pé, “Dùbúlẹ̀ ní ibùsùn rẹ kí ìwọ sì díbọ́n pé, ìwọ kò sàn: baba rẹ yóò sì wá wò ó, ìwọ ó sì wá fún un pé, ‘Jọ̀wọ́ jẹ́ kí Támárì àbúrò mi wá kí ó sì fún mi ní oúnjẹ́ kí ó sì ṣe oúnjẹ náà níwájú mi kí èmi ó rí i, èmi ó sì jẹ ẹ́ ní ọwọ́ rẹ̀.’ ”"'), '2Sam$13:7': ('ile', 'ile', '2Sam$13:7 "Dáfídì sì ránṣẹ́ sí Támárì ní ilé pé, “Lọ sí ilé Ámúnónì ẹ̀gbọ́n rẹ, kí ó sì ṣe òunjẹ́ fún un.”"'), '2Sam$13:11': ('mu', None, '2Sam$13:11 "Nígbà tí ó sì súnmọ́ ọn láti fi oúnjẹ fún un, òun sì dì í mú, ó sì wí fún un pé, “Wá dùbúlẹ̀ tì mí, àbúrò mi.”"'), '2Sam$13:12': (None, 'ṣe', '2Sam$13:12 "Òun sì dá a lóhùn wí pé, “Bẹ́ẹ̀ kọ́ ẹ̀gbọ́n mi, má ṣe tẹ́ mi; nítorí pé kò tọ kí a ṣe irú nǹkan bẹ́ẹ̀ ní Ísírẹ́lì, ìwọ má ṣe hùwà òmùgọ̀ yìí."'), '2Sam$13:32': ('sọ', 'ro pe', '2Sam$13:32 "Jónádábù ọmọ Ṣíméà arakùnrin Dáfídì sì dáhùn ó sì wí pé, “Kí OLÚWA mi ọba má ṣe rò pé wọ́n ti pa gbogbo àwọn ọ̀dọ́mọdé-kùnrin àwọn ọmọ ọba; nítorí pé Ámúnónì nìkan ṣoṣo ni ó kú: nítorí láti ẹnu Ábúsálómù wá ni a ti pinnu rẹ̀ láti ọjọ́ tí ó ti fi agbára mú Támárì àbúrò rẹ̀."'), '2Sam$14:15': ('ṣe', 'ni', '2Sam$14:15 "“Ǹjẹ́ nítorí náà ni èmi sì ṣe wá sọ nǹkan yìí fún OLÚWA mi ọba, bí ó tilẹ̀ jẹ́ pé àwọn ènìyàn ti dẹ́rùbà mí; ìránṣẹ́bìnrin rẹ sì wí pé, ‘Ǹjẹ́ èmi ó sọ fún ọba; ó lè rí bẹ́ẹ̀ pé ọba yóò ṣe ìfẹ́ ìránṣẹ́bìnrin rẹ̀ fún un."'), '2Sam$14:22': ('ri', None, '2Sam$14:22 "Jóábù sì wólẹ̀ ó dojú rẹ̀ bolẹ̀, ó sì tẹríba fún un, ó sì súre fún ọba. Jóábù sì wí pé, “Lónìí ni ìránṣẹ́ rẹ mọ̀ pé, èmi rí oore-ọ̀fẹ́ gbà lójú rẹ, OLÚWA mi, ọba, nítorí pé ọba ṣe ìfẹ́ ìránṣẹ́ rẹ̀.”"'), '2Sam$15:1': (None, None, '2Sam$15:1 "Lẹ́yin èyí náà, Ábúsálómù sì pèṣè kẹ̀kẹ́ àti ẹsin fún ara rẹ̀, àti àádọ́ta ọmọkùnrin tí yóò máa sáré níwájú rẹ̀."'), '2Sam$15:2': (None, None, '2Sam$15:2 "Ábúsálómù sì dìde ní kùtùkùtù, ó sì dúró ní apákan ọ̀nà ẹnu ibodé: bí ẹnìkan bá ní ẹjọ́ tí ó ń fẹ́ mú tọ ọba wá fún ìdájọ́, a sì pè é sọ́dọ̀ rẹ̀, a sì bi í pé, “Ará ìlú wo ni ìwọ?” Òun a sì dáhùn pé, “Ìránṣẹ́ rẹ ti inú ọ̀kan nínú ẹ̀yà Ísírẹ́lì wá.”"'), '2Sam$15:3': (None, 'dara', '2Sam$15:3 "Ábúsálómù yóò sì wí fún un pé, “Wò ó, ọ̀ràn rẹ sá dára, ó sì tọ́: ṣùgbọ́n kò sí ẹnìkan tí ọba fi àṣẹ fún láti gbọ́ ọ̀ràn rẹ.”"'), '2Sam$15:4': (None, None, '2Sam$15:4 "Ábúsálómù a sì wí pé, “À bá jẹ́ fi mi ṣe onídájọ́ ní ilẹ̀ yìí! Kí olúkúlùkù ẹni tí ó ní ẹjọ́ tàbí ọ̀ràn kan báà lè máa tọ̀ mí wá, èmi ìbá sì ṣe ìdájọ́ òtítọ́ fún un.”"'), '2Sam$15:5': (None, None, '2Sam$15:5 "Bẹ́ẹ̀ ni bí ẹnìkan bá sì súnmọ́ ọn láti tẹ́ribá fún un, òun a sì nawọ́ rẹ̀, a sì dì í mú, a sì fí ẹnu kò ó lẹ́nú."'), '2Sam$15:7': ('jẹ ki', 'ni', '2Sam$15:7 "Ó sì ṣe lẹ́yìn ogójì ọdún, Ábúsálómù sì wí fún ọba pé, “Èmi bẹ́ ọ́, jẹ́ kí èmi ó lọ, kí èmi sì san ìlérí mi tí èmi ti ṣe fún OLÚWA, ní Hébírónì."'), '2Sam$15:24': (None, None, '2Sam$15:24 "Sì wò ó, Sádókù pẹ̀lú àti gbogbo àwọn ọmọ Léfì tí ó wà lọ́dọ̀ rẹ̀ sí ń ru àpótí-ẹ̀rí Ọlọ́run: wọ́n sì gbé àpótí-ẹ̀rí Ọlọ́run náà sọ̀kalẹ̀; Ábíátarì sí gòkè, títí gbogbo àwọn ènìyàn sì fi dẹ́kun àti máa kọjá láti ìlú wá."'), '2Sam$15:25': (None, None, '2Sam$15:25 "Ọba sì wí fún Sádókù pé, “Sì tún gbé àpótí-ẹ̀rí Ọlọ́run náà padà sí ìlú: bí èmi bá rí ojú rere gbà lọ́dọ̀ OLÚWA, yóò sì tún mú mi padà wá, yóò sì fi àpótí-ẹ̀rí náà hàn mí àti ibùgbé rẹ̀."'), '2Sam$15:32': (None, None, '2Sam$15:32 "Ó sì ṣe, Dáfídì dé orí òkè, níbi tí ó gbé wólẹ̀ sin Ọlọ́run, sì wò ó, Húsáì ará Áríkà sì wá láti pàdé rẹ̀ pẹ̀lú aṣọ rẹ̀ yíya, àti erùpẹ̀, lórí rẹ̀."'), '2Sam$16:5': ('ile', None, '2Sam$16:5 "Dáfídì ọba sì dé Bahúrímù, sì wò ó, Ọkùnrin kan ti ibẹ̀ jáde wá, láti ìdílé Ṣọ́ọ̀lù wá, orúkọ rẹ̀ sì ń jẹ́ Ṣíméhì, ọmọ Gérà: ó sì ń bú èébú bí o tí ń bọ̀."'), '2Sam$16:11': ('wá', 'ṣe', '2Sam$16:11 "Dáfídì sì wí fún Ábíṣáì, àti fún gbogbo àwọn ìránṣẹ́ rẹ̀ pé, “Wò ó, ọmọ mi tí ó ti inú mi wá, ń wá mi kiri: ǹjẹ́ mélòó mélòó ni ará Bẹ́ńjámínì yìí yóò sì ṣe? Fi í sílẹ̀, sì jẹ́ kí o máa yan èébú; nítorí pé OLÚWA ni ó sọ fún un."'), '2Sam$16:13': (None, None, '2Sam$16:13 "Dáfídì àti àwọn ọmọkùnrin rẹ̀ sì ń lọ ní ọ̀nà, Ṣíméhì sì ń rìn lẹ́bá òkè tí ó wà lẹ́gbẹ̀ẹ́ rẹ̀, ó sì ń yan èébú bí ó ti ń lọ, ó sì ń sọ ọ́ lókúta, ó sì ń fọ́n erùpẹ̀."'), '2Sam$17:1': (None, None, '2Sam$17:1 "Áhítófélì sì wí fún Ábúsálómù pé, “Èmi ó yan ẹgbàáfà ọkùnrin èmi ó sì dìde, èmi ó sì lépa Dáfídì lóru yìí."'), '2Sam$17:5': (None, None, '2Sam$17:5 "Ábúsálómù sì wí pé, “Ǹjẹ́ pe Húṣáì ará Áríkì, àwa ó sì gbọ́ èyí tí ó wà lẹ́nu rẹ̀ pẹ̀lú.”"'), '2Sam$17:6': ('wá', 'ṣe', '2Sam$17:6 "Húṣáì sì dé ọ̀dọ̀ Ábúsálómù, Ábúsálómù sì wí fún un pé, “Báyìí ni Áhítófélì wí, kí àwa ṣe bí ọ̀rọ̀ rẹ bí? Bí ko bá sì tọ́ bẹ́ẹ̀, ìwọ wí.”"'), '2Sam$17:8': (None, 'eniyan', '2Sam$17:8 "Húṣáì sì wí pé, “Ìwọ mọ baba rẹ àti àwọn ọmọkùnrin rẹ̀ pé alágbára ni wọ́n, wọ́n sì wà ní Kíkorò ọkàn bí àmọ̀tẹ́kùn tí a gbà ní ọmọ ni pápá: baba rẹ sì jẹ́ jagunjagun ọkùnrin kì yóò bá àwọn ènìyàn náà gbé pọ̀ lóru."'), '2Sam$17:9': (None, None, '2Sam$17:9 "Kíyèsí i, ó ti fi ara rẹ̀ pamọ́ nísinsin yìí ni ihò kan, tàbí ní ibòmíràn: yóò sì ṣe, nígbà tí díẹ̀ nínú wọn bá kọ́ ṣubú, ẹnikẹ́ni tí ó bá gbọ́ yóò sì wí pé, ‘Ìparun sì ń bẹ́ nínú àwọn ènìyàn tí ń tọ Ábúsálómù lẹ́yìn.’"'), '2Sam$17:10': (None, None, '2Sam$17:10 "Ẹni tí ó sì ṣe alágbára, tí ọkàn rẹ̀ sì dàbí ọkàn kìnnìun, yóò sì rẹ̀ ẹ́: nítorí gbogbo Ísírẹ́lì ti mọ̀ pé alágbára ni baba rẹ, àti pé, àwọn tí ń bẹ lọ́dọ̀ rẹ jẹ́ alágbára."'), '2Sam$17:12': ('ri', 'ilẹ', '2Sam$17:12 "Àwá ó sì yọ sí i níbikíbi tí àwa o gbé rí i, àwa ó sì yí i ká bí ìrì ti ń ṣẹ̀ sí ilẹ̀ àní, ọkàn kan kì yóò kù pẹ̀lú rẹ̀ nínú gbogbo àwọn ọmọkùnrin tí ó wà lọ́dọ̀ rẹ̀."'), '2Sam$17:13': (None, 'mú', '2Sam$17:13 "Bí o bá sì bọ́ sí ìlú kan, gbogbo Ísírẹ́lì yóò mú okùn wá sí ìlú náà, àwa ó sì fà á lọ sí odo, títí a kì yóò fi rí òkúta kékeké kan níbẹ̀.”"'), '2Sam$17:21': ('lọ', None, '2Sam$17:21 "Ó sì ṣe lẹ́yìn ìgbà tí wọ́n ti lọ tán, àwọn ọkùnrin náà sì jáde kúrò nínú kànga, wọ́n sì lọ wọ́n sì rò fún Dáfídì ọba. Wọ́n sọ fún Dáfídì pé, “Dìde kí o sì gòkè odò kánkán: nítorí pé bayìí ni Áhítófélì gbìmọ̀ sí ọ.”"'), '2Sam$17:23': ('ile', 'ile', '2Sam$17:23 "Nígbà tí Áhítófélì sì ríi pé wọn kò fi ìmọ̀ tirẹ̀ ṣe, ó sì di kẹ́tẹ́kẹ́tẹ́ rẹ̀ ni gàárí, ó sì dìde, ó lọ ilé rẹ̀, ó sì palẹ̀ ilé rẹ̀ mọ̀, ó sì so, ó sì kú, a sì sin ín sí ibojì baba rẹ̀. Ábúsálómù gbógun ti Dáfídì."'), '2Sam$17:25': ('ṣe', 'orukọ', '2Sam$17:25 "Ábúsálómù sì fi Ámásà ṣe olórí ogun ní ipò Jóábù: Ámásà ẹni tí í ṣe ọmọ ẹnìkan, orúkọ ẹni tí a ń pè ní Itírà, ará Ísírẹ́lì, tí ó wọlé tọ Ábígáílì ọmọbìnrin Náhásì, arabìnrin Sérúíà, ìyá Jóábù."'), '2Sam$17:27': (None, None, '2Sam$17:27 "Nígbà tí Dáfídì sì wá sí Mahánáímù, Ṣóbì ọmọ Nahásì ti Rábà tí àwọn ọmọ Ámónì, àti Mákírì ọmọ Ámíélì ti Lodebárì, àti Básíláì ará Gílíádì ti Rógélímù."'), '2Sam$18:13': ('ni', None, '2Sam$18:13 "Bí ó bá ṣe bẹ́ẹ̀ èmi ìbá ṣe sí ara mi: nítorí pé kò sí ọ̀ràn kan tí ó pamọ́ fún ọba, ìwọ tìkárarẹ̀ ìbá sì kọjú ìjà sí mi pẹ̀lú.”"'), '2Sam$18:17': ('òkúta', None, '2Sam$18:17 "Wọ́n sì gbé Ábúsálómù, wọ́n sì sọ ọ́ sínú ihò ńlá kan ní igbó náà, wọ́n sì kó òkúta púpọ̀ jọ sí i lórí, gbogbo Ísírẹ́lì sì sá, olúkúlùkù sí inú àgọ́ rẹ̀."'), '2Sam$18:18': ('ní', None, '2Sam$18:18 "Ábúsálómù ní ìgbà ayé rẹ̀, sì mọ ọ̀wọ̀n kan fún ara rẹ̀, tí ń bẹ ní àfonífojì ọba: nítorí tí ó wí pé, Èmi kò ní ọmọkùnrin tí yóò pa orúkọ mi mọ́ ní ìrántí: òun sì pe ọ̀wọ̀n náà nípa orúkọ rẹ̀: a sì ń pè é títí di òní, ní ọ̀wọ́n Ábúsálómù."'), '2Sam$19:14': ('awọn ọkunrin', 'eniyan', '2Sam$19:14 "Òun sì yí gbogbo àwọn ọkùnrin Júdà lọ́kàn padà àní bí ọkàn ènìyàn kan; wọ́n sì ránṣẹ́ sí ọba, pé, “Ìwọ padà àti gbogbo àwọn ìránṣẹ́ rẹ.”"'), '2Sam$19:28': ('ṣe', None, '2Sam$19:28 Nítorí pé gbogbo ilé baba mi bí òkú ènìyàn ni wọ́n sáà rí níwájú OLÚWA mi ọba: ìwọ sì fi ipò fún ìránṣẹ́ rẹ láàrin àwọn tí ó ń jẹ́un ní ibi oúnjẹ́. Nítorí náà àre kín ni èmi ní tí èmi yóò fi máa ké pe ọba síbẹ̀.”'), '2Sam$19:34': ('lọ', None, '2Sam$19:34 "Básíláì sì wí fún ọba pé, “Ọjọ́ mélòó ni ọdún ẹ̀mí mi kù, tí èmi ó fi bá ọba gòkè lọ sí Jérúsálẹ́mù"'), '2Sam$19:38': ('dahùn', 'ṣe', '2Sam$19:38 "Ọba sì dáhùn wí pé, “Kímhámù yóò bá mi gòkè, èmi ó sì ṣe èyí tí ó tọ́ lójú rẹ̀ fún un; ohunkóhun tí ìwọ bá sì béèrè lọ́wọ́ mi, èmi ó ṣeé fún ọ.”"'), '2Sam$20:2': ('awọn ọkunrin', None, '2Sam$20:2 "Gbogbo àwọn ọkùnrin Ísírẹ́lì sì lọ kúrò lẹ́yìn Dáfídì, wọ́n sì ń tọ́ Ṣébà ọmọ Bíkírì lẹ́yìn: ṣùgbọ́n àwọn ọkùnrin Júdà sì fi ara mọ́ ọba wọn láti odò Jódánì wá títí ó fi dé Jérúsálẹ́mù."'), '2Sam$20:6': (None, None, '2Sam$20:6 "Dáfídì sì wí fún Ábíṣáì pé, “Nísinsinyìí Ṣébè ọmọ Bíkírì yóò ṣe wá ní ibi ju ti Ábúsálómù lọ; ìwọ mú àwọn ìránṣẹ́ olúwa rẹ, kí o sì lépa rẹ̀, kí ó má baà rí ìlú olódì wọ̀, kí ó sì bọ́ lọ́wọ́ wa.”"'), '2Sam$20:15': (None, None, '2Sam$20:15 "Wọ́n wá, wọ́n sì dó tì í ní Ábélì tí Bẹti Máákà, wọ́n sì mọ odí ti ìlú náà, odi náà sì dúró ti odi ìlú náà, gbogbo ènìyàn tí ń bẹ lọ́dọ̀ Jóábù sì ń gbìyànjú láti wó ògiri náà lulẹ̀."'), '2Sam$21:5': (None, None, '2Sam$21:5 "Wọ́n sì wí fún ọba pé, “Ọkùnrin tí ó run wá, tí ó sì rò láti pa wá rẹ́ ki a má kù níbikíbi nínú gbogbo agbégbé Ísírẹ́lì."'), '2Sam$21:8': (None, None, '2Sam$21:8 "Ọba sì mú àwọn ọmọkùnrin méjèèjì tí Rísípà ọmọbìnrin Áíyà bí fún Ṣọ́ọ̀lù, àní Ámónì àti Méfíbóṣétì àwọn ọmọkùnrin máràrùn ti Mérábù, ọmọbìnrin Ṣọ́ọ̀lù, àwọn tí ó bí fún Ádíríélì ọmọ Básílíà ará Méhólátì."'), '2Sam$22:1': (None, None, '2Sam$22:1 "Dáfídì sì kọ ọ̀rọ̀ orin yìí sí OLÚWA ní ọjọ́ tí OLÚWA gbà á kúrò lọ́wọ́ gbogbo àwọn ọ̀ta rẹ̀, àti kúrò lọ́wọ́ Sọ́ọ̀lù."'), '2Sam$22:5': (None, None, '2Sam$22:5 “Nígbà tí ìbìlù ìrora ikú yí mi káàkiri; tí àwọn ìṣàn ènìyàn búburú dẹ́rùbà mí.'), '2Sam$22:9': (None, 'ina', '2Sam$22:9 "Èéfín sì jáde láti ihò-imú rẹ̀ wá; iná láti ẹnu rẹ̀ wá, ó sì ń jónirun, ẹ̀yín sì ń ràn nípasẹ rẹ̀."'), '2Sam$22:13': (None, 'ina', '2Sam$22:13 Nípaṣẹ̀ ìmọ́lẹ̀ iwájú rẹ̀ ẹ̀yín-iná ràn.'), '2Sam$22:38': ('run', None, '2Sam$22:38 "“Èmi ti lépa àwọn ọ̀tá mi, èmi sì ti run wọ́n, èmi kò pẹ̀yìndà títí èmi fi run wọ́n."'), '2Sam$22:39': ('run', None, '2Sam$22:39 "Èmi ti pa wọ́n run, èmi sì ti fọ́ wọn, wọn kò sì le dìde mọ́: wọ́n ṣubú lábẹ́ mi."'), '2Sam$23:4': (None, 'imole', '2Sam$23:4 "Yóò sì dà bí ìmọ́lẹ̀ òwúrọ̀ nígbà tí oòrùn bá là, òwúrọ̀ tí kò ní ìkúukùu, nígbà tí koríko tútù bá hù wá láti ilẹ̀ lẹ́yìn òjò.’ "'), '2Sam$23:8': (None, None, '2Sam$23:8 " Wọ̀nyí sì ni orúkọ àwọn ọkùnrin alágbára tí Dáfídì ní: Jósébù-básébè ti ará Takímónì ni olorí àwọn Balógun, òun sì ni akọni rẹ̀ tí ó pa ọ̀ọ́dúnrún ènìyàn lẹ́ẹ̀kan náà."'), '2Sam$23:20': ('ní', None, '2Sam$23:20 "Bénáyà, ọmọ Jéhóíádà, ọmọ akọni ọkùnrin kan tí Kabiseélì, ẹni tí ó pọ̀ ní iṣẹ́ agbára, òun pa àwọn ọmọ Áríélì méjì ti Móábù; ó sọ̀kalẹ̀ pẹ̀lú ó sì pa kìnnìún kan nínú ihò lákoko òjòdídì."'), '2Sam$24:11': ('wá', None, '2Sam$24:11 "Dáfídì sì dìde ní òwúrọ̀, ọ̀rọ̀ OLÚWA sì tọ Gádì wòlíì wá, aríran Dáfídì wí pé:"'), '2Sam$24:20': ('wò', 'oju', '2Sam$24:20 "Áráúnà sì wò, ó sì rí ọba àti àwọn ìránṣẹ́ rẹ̀ ń bọ̀ wá lọ́dọ̀ rẹ̀: Áráúnà sì jáde, ó sì wólẹ̀ níwájú ọba ó sì dojú rẹ̀ bolẹ̀."'), '2Sam$24:24': (None, 'owo', '2Sam$24:24 "Ọba sì wí fún Áráúnà pé, “Bẹ́ẹ̀ kọ́; ṣùgbọ́n èmi ó rà á ní iye kan lọ́wọ́ rẹ, bí ó ti wù kí ó ṣe; bẹ́ẹ̀ ni èmi kì yóò fi èyí tí èmi kò náwó fún, rú ẹbọ sísun sí OLÚWA Ọlọ́run mi.” Dáfídì sì ra ibi ìpakà náà, àti àwọn màlúù náà ní àádọ́ta sékélì fàdákà."'), '1Kgs$1:2': ('jẹ ki', 'gba', '1Kgs$1:2 "Nígbà náà ni àwọn ìránṣẹ́ rẹ̀ wí fún un pé, “Ẹ jẹ́ kí a wá ọ̀dọ́mọbìnrin wúndíá kan kí ó dúró ti ọba, kì ó sì máa tọ́jú rẹ̀. Kí ó dùbúlẹ̀ ní àyà rẹ̀ kí ara ọba OLÚWA wa lè móoru.”"'), '1Kgs$1:33': (None, 'mú', '1Kgs$1:33 "Ó sì wí fún wọn pé: “Ẹ mú àwọn ìránṣẹ́ OLÚWA yín pẹ̀lú yín kí ẹ sì mú kí Sólómónì ọmọ mi kí ó gun ìbaka mi, kí ẹ sì mú-un sọ̀kalẹ̀ wá sí Gíhónì."'), '1Kgs$1:39': ('ororo', None, '1Kgs$1:39 "Sádókù àlùfáà sì mú ìwo òróró láti inú àgọ́, ó sì dà á sí Sólómónì lórí. Nígbà náà ni wọ́n sì fọn fèrè, gbogbo àwọn ènìyàn sì ké pé, “Kí Sólómónì ọba kí ó pẹ́!”"'), '1Kgs$1:44': (None, 'ni', '1Kgs$1:44 "Ọba sì ti rán Sádókù àlùfáà, Nátanì wòlíì, Bénáyà ọmọ Jéhóíádà àti àwọn ará Kérétì àti Pélétì pẹ̀lú rẹ̀, wọ́n sì ti gbé e gun ìbaka ọba,"'), '1Kgs$1:47': ('ṣe', 'orukọ', '1Kgs$1:47 "Àwọn ìránṣẹ́ ọba sì ti tún wá láti bá OLÚWA wa Dáfídì Ọba yọ̀, wí pé, ‘Kí Ọlọ́run rẹ̀ mú orúkọ Sólómónì lókìkí ju tirẹ̀ lọ àti kí ìtẹ́ rẹ̀ kí ó pọ̀ ju tirẹ̀ lọ!’ Ọba sì tẹ ara rẹ̀ ba lórí ibùsùn rẹ̀,"'), '1Kgs$2:1': ('lọ', None, '1Kgs$2:1 "Nígbà tí ọjọ́ ikú Dáfídì súnmọ́ etílé, ó pàṣẹ fún Sólómónì ọmọ rẹ̀."'), '1Kgs$2:15': ('yipada', None, '1Kgs$2:15 "Ó wí pé, “Gẹ́gẹ́ bí o ti mọ̀, ti tèmi ni ìjọba náà. Gbogbo Ísírẹ́lì ti wò mí bí ọba wọn. Ṣùgbọ́n nǹkan yípadà, ìjọba náà sì ti lọ sí ọ̀dọ̀ arákùnrin mi, nítorí ó ti wá sọ́dọ̀ rẹ̀ láti ọwọ́ OLÚWA wá."'), '1Kgs$2:23': ('ni', None, '1Kgs$2:23 "Nígbà náà ni Sólómónì ọba fi OLÚWA búra pé: “Kí Ọlọ́run ki ó jẹ mí níyà, àti jù bẹ́ẹ̀ pẹ̀lú, bí Àdóníjà kò bá ní fi ẹ̀mí rẹ̀ san ìbéèrè yìí!"'), '1Kgs$2:32': (None, 'pada', '1Kgs$2:32 "OLÚWA yóò sì san ẹ̀jẹ̀ tí ó ti ta sílẹ̀ padà fún un, nítorí tí ó kọlu ọkùnrin méjì, ó sì fi idà rẹ̀ pa wọ́n, Dáfídì bàbá mi kò sì mọ̀ àwọn méjèèjì ni. Ábínérì ọmọ Nérì olórí ogun Ísírẹ́lì, àti Ámásà ọmọ Jétérì olórí ogun Júdà, wọ́n jẹ́ olódodo, wọ́n sì sàn ju òun fúnrarẹ̀ lọ."'), '1Kgs$2:39': (None, None, '1Kgs$2:39 "Ṣùgbọ́n lẹ́yìn ọdún mẹ́ta, àwọn ọmọ ọ̀dọ̀ Síméì méjì sì sá lọ sọ́dọ̀ Ákíṣì ọmọ Máákà, ọba Gátì, a sì sọ fún Síméì pé, “Àwọn ọmọ ọ̀dọ̀ rẹ méjì wà ní Gátì.”"'), '1Kgs$3:1': ('ní', None, '1Kgs$3:1 "Sólómónì sì bá Fáráò ọba Éjíbítì dá àna, ó sì fẹ́ ọmọbìnrin rẹ̀ ní ìyàwó. Ó sì mú-un wá sí ìlú Dáfídì títí tí ó fi parí kíkọ́ ààfin rẹ̀ àti tẹ́ḿpìlì OLÚWA, àti odi tí ó yí Jérúsálẹ́mù ká."'), '1Kgs$3:8': ('eniyan', None, '1Kgs$3:8 "Ìránṣẹ́ rẹ nìyí láàrin àwọn ènìyàn tí o ti yàn, àwọn ènìyàn ńlá, wọ́n pọ̀ tó bẹ́ẹ̀ tí a kò sì lè kà wọ́n tàbí mòye wọn."'), '1Kgs$3:9': (None, None, '1Kgs$3:9 Nítorí náà fi ọkàn ìmòye fún ìránṣẹ́ rẹ láti le ṣàkóso àwọn ènìyàn rẹ àti láti mọ ìyàtọ̀ láàrin rere àti búburú. Nítorí ta ni ó lè ṣe àkóso àwọn ènìyàn ńlá rẹ yìí?”'), '1Kgs$3:11': (None, 'ni', '1Kgs$3:11 "Nígbà náà ni Ọlọ́run wí fún-un pé, “Nítorí tí ìwọ ti béèrè fún èyí, tí kì í ṣe ẹ̀mí gígùn tàbí ọrọ̀ fún ara rẹ, tàbí béèrè fún ikú àwọn ọ̀ta rẹ, ṣùgbọ́n fún òye láti mọ ẹjọ́ dá,"'), '1Kgs$3:13': (None, 'ni', '1Kgs$3:13 "Ṣíwájú síi, èmi yóò fi ohun tí ìwọ kò bèrè fún ọ: ọrọ̀ àti ọlá ní gbogbo ayé rẹ tí kì yóò sí ẹnìkan nínú àwọn ọba tí yóò dà bí rẹ."'), '1Kgs$3:18': (None, None, '1Kgs$3:18 "Ní ọjọ́ kẹta lẹ́yìn ìgbà tí mo bímọ tan, obìnrin yìí sì bímọ pẹ̀lú. A sì nìkan dá wà; kò sí ẹlòmíràn ní ilé bí kò ṣe àwa méjèèjì nìkan."'), '1Kgs$3:21': ('ní', None, '1Kgs$3:21 "Ní òwúrọ̀ ọjọ́ kejì mo sì dìde láti fi ọmú fún ọmọ mi: ó sì ti kú! Ṣùgbọ́n nígbà tí mo sì wò ó fín ní òwúrọ̀, mo sì ríi pé kì í ṣe ọmọ mi tí mo bí.”"'), '1Kgs$4:8': (None, None, '1Kgs$4:8 Orúkọ wọn ni wọ̀nyí: Bénhúrì ní ìlú olókè Éfúráímù.'), '1Kgs$4:21': ('mu', 'ilẹ', '1Kgs$4:21 "Sólómónì sì ń ṣàkóso lórí gbogbo àwọn ijọba láti odò Éjíbítì títí dé ilẹ̀ àwọn ará Fílístínì, àti títí dé etí ilẹ̀ Éjíbítì. Àwọn orílẹ̀ èdè yìí ń mú owó òde wá, wọ́n sì ń sin Sólómónì ní gbogbo ọjọ́ ayé rẹ̀."'), '1Kgs$5:7': (None, None, '1Kgs$5:7 "Nígbà tí Hárámù sì gbọ́ iṣẹ́ Sólómónì, inú rẹ̀ sì dùn gidigidi, ó sì wí pé, “Ògo ni fún OLÚWA lónìí, nítorí tí ó ti fún Dáfídì ní ọlọgbọ́n ọmọ láti sàkóso àwọn ìlú ńlá yìí.”"'), '1Kgs$5:9': ('sọ', 'mú', '1Kgs$5:9 "Àwọn ènìyàn mi yóò mú igi náà sọ̀kalẹ̀ láti Lébánónì wá sí òkun, èmi ó sì fi wọ́n ránṣẹ́ sí ọ ní fífò lójú omi òkun títí dé ibi tí ìwọ ó na ìka sí fún mi. Níbẹ̀ ni èmi yóò ti yà wọ́n sọ́tọ̀, ìwọ yóò sì kó wọn lọ. Ìwọ yóò sì gba ìfẹ́ mi nípa pípèsè oúnjẹ fún ilé mi.”"'), '1Kgs$5:13': (None, None, '1Kgs$5:13 "Sólómónì ọba sì sa asìnrú ènìyàn jọ ní gbogbo Ísírẹ́lì; àwọn tí ń sìnrú náà jẹ́ ẹgbàá mẹ́ẹ̀dógún ènìyàn (30,000)."'), '1Kgs$6:7': (None, None, '1Kgs$6:7 "Ní kíkọ́ ilé náà, òkúta tí a ti gbẹ́ sílẹ̀ kí a tó mú-un wá ibẹ̀ nìkan ni a lò, a kò sì gbúròó òòlù tàbí àáké tàbí ohun èlò irin kan nígbà tí a ń kọ́ ọ lọ́wọ́."'), '1Kgs$6:11': (None, None, '1Kgs$6:11 Ọ̀rọ̀ OLÚWA sì tọ Sólómónì wá wí pé:'), '1Kgs$6:32': (None, None, '1Kgs$6:32 "Àti lára ilẹ̀kùn igi ólífì náà ni ó ya àwòrán àwọn kérúbù, igi ọ̀pẹ àti ti ìtànná ewéko sí, ó sì fi wúrà bò wọ́n, ó sì tan wúrà sí ara àwọn kérúbù àti sí ara igi ọ̀pẹ."'), '1Kgs$7:16': ('iga', 'oke', '1Kgs$7:16 "Ó sì tún ṣe ìparí méjì ti idẹ dídá láti fi sókè àwọn ọ̀wọ̀n náà, ìparí kan sì jẹ́ ìgbọ̀nwọ́ márùn ún ní gíga."'), '1Kgs$7:28': ('ṣe', None, '1Kgs$7:28 Báyìí ni a sì ṣe ẹsẹ̀ náà: Wọ́n ní àlàfo ọnà àárin tí a so mọ́ agbede-méjì ìpàdé etí.'), '1Kgs$7:38': (None, None, '1Kgs$7:38 " Nígbà náà ni ó ṣe agbádá idẹ mẹ́wàá, ọ̀kọ̀ọ̀kan gbà tó ogójì ìwọ̀n Bátì, ó sì wọn ìgbọ̀nwọ́ mẹ́rin, lórí ọ̀kọ̀ọ̀kan ẹsẹ̀ mẹ́wẹ̀ẹ̀wá náà ni agbádá kọ̀ọ̀kan wà."'), '1Kgs$7:51': ('ṣe', 'iṣẹ', '1Kgs$7:51 "Nígbà tí gbogbo iṣẹ́ tí Sólómónì ọba ṣe fún ilé OLÚWA parí, ó mú gbogbo nǹkan tí Dáfídì baba rẹ̀ ti yà sí mímọ́ wá; fàdákà, wúrà àti ohun èlò, ó sì fi wọ́n sínú ìṣúra ilé OLÚWA."'), '1Kgs$8:1': ('awọn olori', 'olori', '1Kgs$8:1 " Nígbà náà ni Sólómónì pe àwọn àgbà Ísírẹ́lì àti gbogbo àwọn olórí àwọn ẹ̀yà àti àwọn olórí ìdílé àwọn ọmọ Ísírẹ́lì jọ papọ̀ níwájú rẹ̀ ní Jérúsálẹ́mù, láti gbé àpótí-ẹ̀rí májẹ̀mú OLÚWA wá láti ìlú Dáfídì, tí ń ṣe Ṣíónì."'), '1Kgs$8:21': ('mu', 'ilẹ', '1Kgs$8:21 "Èmi sì ti pèsè ibìkan níbẹ̀ fún àpótí-ẹ̀rí, èyí tí í ṣe májẹ̀mú OLÚWA tí ó ti bá àwọn baba wa dá nígbà tí ó mú wọn jáde láti ilẹ̀ Éjíbítì wá.”"'), '1Kgs$8:28': ('ṣe', 'ni', '1Kgs$8:28 "Síbẹ̀ ṣe ìfetísílẹ̀ àdúrà ìránṣẹ́ rẹ àti ẹ̀bẹ̀ rẹ fún àánú, OLÚWA Ọlọ́run mi. Gbọ́ ẹkún àti àdúrà tí ìránṣẹ́ rẹ̀ ń gbà níwájú rẹ lónìí."'), '1Kgs$8:31': ('ṣe', 'wá', '1Kgs$8:31 "“Nígbà tí ẹnìkan bá ṣẹ̀ sí ẹnìkejì rẹ̀, tí a sì fi wá lée láti mú-un búra, bí ìbúra náà bá sì dé iwájú pẹpẹ rẹ ní ilé yìí,"'), '1Kgs$8:33': ('ṣe', 'ni', '1Kgs$8:33 "“Nígbà tí àwọn ọ̀tá bá ṣẹ́gun Ísírẹ́lì, ènìyàn rẹ, nítorí tí wọ́n ti ṣẹ̀ sí ọ, tí wọ́n bá yípadà sí ọ, tí wọ́n sì jẹ́wọ́ orúkọ rẹ, tí wọ́n sì gbàdúrà, tí wọ́n sì bẹ̀bẹ̀ lọ́dọ̀ rẹ nínú ilé yìí"'), '1Kgs$8:35': (None, 'ni', '1Kgs$8:35 "“Nígbà tí ọ̀run bá ṣé mọ́, tí kò sí òjò nítorí tí àwọn ènìyàn rẹ ti ṣẹ̀ sí ọ, bí wọ́n bá gbàdúrà sí ìhà ibí yìí, tí wọ́n sì jẹ́wọ́ orúkọ rẹ, tí wọ́n sì yípadà kúrò nínú ẹ̀ṣẹ̀ wọn nítorí tí ìwọ ti pọ́n wọn lójú,"'), '1Kgs$8:37': (None, None, '1Kgs$8:37 "“Nígbà tí ìyàn tàbí àjàkálẹ̀-àrùn bá wá sí ilẹ̀ náà, tàbí ìrẹ̀dànù tàbí ìmúwòdú, eṣú tàbí kòkòrò tí ń jẹni run, tàbí nígbà tí àwọn ọ̀tá bá dó tì wọ́n nínú àwọn ìlú wọn, irú ìpọ́njú tàbí àrùnkárùn tó lè wá,"'), '1Kgs$8:41': ('ṣe', 'wá', '1Kgs$8:41 "“Níti àwọn àlejò tí kì í ṣe Ísírẹ́lì ènìyàn rẹ, ṣùgbọ́n tí ó ti ilẹ̀ òkèèrè jáde wá nítorí orúkọ rẹ,"'), '1Kgs$8:44': (None, 'adura', '1Kgs$8:44 "“Nígbà tí àwọn ènìyàn rẹ bá jáde lọ sí ogun sí àwọn ọ̀ta wọn, níbikíbi tí ìwọ bá rán wọn, nígbà tí wọ́n bá gbàdúrà sí OLÚWA sí ìhà ìlú tí ìwọ ti yàn àti síhà ilé tí mo ti kọ́ fún orúkọ rẹ,"'), '1Kgs$8:46': ('ṣe', None, '1Kgs$8:46 "“Nígbà tí wọ́n bá ṣẹ̀ sí ọ, nítorí kò sí ẹnìkan tí kì í ṣẹ̀, tí ìwọ sì bínú sí wọn, tí ìwọ sì fi wọ́n lé ọ̀tá lọ́wọ́, tí ó kó wọn ní ìgbékùn lọ sí ilẹ̀ wọn, jínjìnnà tàbí nítòòsí;"'), '1Kgs$8:54': ('ebe', 'adura', '1Kgs$8:54 "Nígbà tí Sólómónì ti parí gbogbo àdúrà àti ẹ̀bẹ̀ yìí sí OLÚWA tán, ó dìde kúrò lórí eékún rẹ̀ níwájú pẹpẹ OLÚWA níbi tí ó ti kúnlẹ̀ tí ó sì na ọwọ́ rẹ̀ sí òkè ọ̀run"'), '1Kgs$8:55': (None, None, '1Kgs$8:55 Ó sì dìde dúró ó sì fi ohùn rara súre fún gbogbo ìjọ ènìyàn Ísírẹ́lì wí pé:'), '1Kgs$8:59': ('jẹ ki', 'ni', '1Kgs$8:59 "Àti kí ó sì jẹ́ kí àwọn ọ̀rọ̀ mi wọ̀nyí, tí mo ti gbà ní àdúrà níwájú OLÚWA, kí ó wà nítòsí OLÚWA Ọlọ́run wa ní ọ̀sán àti ní òru, kí ó lè mú ọ̀rọ̀ ìránṣẹ́ rẹ̀ dúró àti ọ̀rọ̀ Ísírẹ́lì ènìyàn rẹ̀, gẹ́gẹ́ bí àìní wa ojoojúmọ́,"'), '1Kgs$9:1': (None, 'ṣe', '1Kgs$9:1 " Nígbà tí Solómónì sì parí kíkọ́ ilé OLÚWA àti ààfin ọba, tí ó sì ti ṣe gbogbo nǹkan tí ó ń fẹ́ láti ṣe,"'), '1Kgs$9:3': (None, 'okan', '1Kgs$9:3 "OLÚWA sì wí fún un pé: “Èmi ti gbọ́ àdúrà rẹ àti ẹ̀bẹ̀ rẹ tí ìwọ ti bẹ̀ níwájú mi, mo ti ya ilé yìí sí mímọ́, tí ìwọ ti kọ́, nípa fífi orúkọ mi síbẹ̀ títí láé. Ojú mi àti ọkàn mi yóò wà níbẹ̀ nígbà gbogbo."'), '1Kgs$9:6': ('ṣe', None, '1Kgs$9:6 "“Ṣùgbọ́n tí ìwọ tàbí àwọn ọmọ rẹ bá yípadà kúrò lọ́dọ̀ mi, tí ẹ kò sì pa òfin mi mọ́ àti àṣẹ mi tí mo ti fi fún ọ, tí ẹ sì lọ láti sin Ọlọ́run mìíràn, tí ẹ sì ń sìn wọ́n,"'), '1Kgs$9:9': (None, 'ni', '1Kgs$9:9 "Àwọn ènìyàn yóò sì dáhùn wí pé, ‘Nítorí tí wọ́n ti kọ̀ OLÚWA Ọlọ́run wọn sílẹ̀, tí ó mú àwọn bàbá wọn jáde láti Éjíbítì wá, wọ́n sì ti gbá Ọlọ́run mìíràn mú, wọ́n ń bọ wọ́n, wọ́n sì ń sìn wọ́n, ìdí nìyìí tí OLÚWA ṣe mú gbogbo ìjàǹbá yìí wá sórí wọn.’ ”"'), '1Kgs$9:10': (None, None, '1Kgs$9:10 "Lẹ́yìn ogún ọdún, nígbà tí Sólómónì kọ́ ilé méjèèjì yìí tan: ilé OLÚWA àti ààfin ọba."'), '1Kgs$9:15': (None, None, '1Kgs$9:15 "Ìdí àwọn asìnrú ti Sólómónì ọba kójọ ni èyí; láti kọ́ ilé OLÚWA àti ààfin òun tìkárarẹ̀; Mílò, odi Jérúsálẹ́mù, Hásórì, Mégídò àti Gésérì."'), '1Kgs$9:16': (None, None, '1Kgs$9:16 "Fáráò ọba Éjíbítì sì ti kọlu Gésérì, ó sì ti fi iná sun ún, ó sì pa àwọn ará Kénánì tí ń gbé ìlú náà, ó sì fi ta ọmọbìnrin rẹ̀, aya Sólómónì lọ́rẹ."'), '1Kgs$10:9': (None, 'ṣe', '1Kgs$10:9 "Ìbùkún ni fún OLÚWA Ọlọ́run rẹ, tí ó ní inú dídùn sí ọ, tí ó sì gbé ọ ka orí ìtẹ́ Ísírẹ́lì. Nítorí tí OLÚWA fẹ́ràn Ísírẹ́lì títí láé, ni ó ṣe fi ọ́ jọba, láti ṣe ìdájọ́ àti òdodo.”"'), '1Kgs$10:10': ('ayaba', 'ọba', '1Kgs$10:10 "Ó sì fún ọba ní ọgọ́fà (120) talẹ́ńtì wúrà, tùràrí olóòórún dídùn lọ́pọ̀lọpọ̀, àti òkúta iyebíye. Kò sí irú ọ̀pọ̀lọpọ̀ tùràrí tí a mú wá tí ó dà bí irú èyí tí ayaba Ṣébà fi fún Sólómónì ọba."'), '1Kgs$10:13': ('ayaba', 'ọba', '1Kgs$10:13 "Sólómónì ọba sì fún ayaba Ṣébà ní gbogbo ìfẹ́ rẹ̀ àti ohun tí ó béèrè, yàtọ̀ sí èyí tí a fi fún un láti ọwọ́ Sólómónì ọba wa. Nígbà náà ni ó yípadà, ó sì lọ sí ìlú rẹ̀, pẹ̀lú àwọn ìránṣẹ́ rẹ̀."'), '1Kgs$10:19': ('ibi', 'ijoko', '1Kgs$10:19 "Ìtẹ́ náà sì ní àtẹ̀gùn mẹ́fà, èyín rẹ̀ sì ṣe róbótó lókè. Ní ibi ìjókòó méjèèjì náà ni irọpá wà, pẹ̀lú kìnnìún tí ó dúró lẹ́gbẹ̀ẹ́ ìkọ̀ọ̀kan wọn."'), '1Kgs$11:11': ('ni', 'fun', '1Kgs$11:11 "Nítorí náà OLÚWA wí fún Sólómónì pé, “Nítorí bí ìwọ ti ṣe nǹkan yìí, tí ìwọ kò sì pa májẹ̀mú mi àti àṣẹ mi mọ́, tí mo ti pa láṣẹ fún ọ, dájúdájú èmi yóò fa ìjọba ya kúrò lọ́wọ́ rẹ, èmi yóò sì fi fún ẹlòmìíràn."'), '1Kgs$11:13': ('fun', 'ni', '1Kgs$11:13 "Síbẹ̀ èmi kì yóò fa gbogbo ìjọba náà ya, ṣùgbọ́n èmi yóò fi ẹ̀yà kan fún ọmọ rẹ nítorí Dáfídì ìránṣẹ́ mi, àti nítorí Jérúsálẹ́mù tí èmi ti yàn.”"'), '1Kgs$11:15': (None, None, '1Kgs$11:15 "Ó sì ṣe, nígbà tí Dáfídì wà ní Édómù, Jóábù olórí ogun sì gòkè lọ láti sìn àwọn ọmọ ogun Ísírẹ́lì ti a pa lójú ogun, ó sì pa gbogbo ọkùnrin Édómù."'), '1Kgs$11:20': (None, None, '1Kgs$11:20 "Arábìnrin Tápénésì bí ọmọkùnrin kan fún un tí à ń pe orúkọ rẹ̀ ní Génúbátì, ẹni tí Tápénésì tọ́ dàgbà ní ààfin ọba. Níbẹ̀ ni Génúbátì ń gbé pẹ̀lú àwọn ọmọ Fáráò fún ra rẹ̀."'), '1Kgs$11:21': ('lọ', 'lọ', '1Kgs$11:21 "Nígbà tí ó sì wà ní Éjíbítì, Hádádì sì gbọ́ pé Dáfídì ti sùn pẹ̀lú àwọn baba rẹ̀, àti pé Jóábù olórí ogun sì ti kú pẹ̀lú. Nígbà náà ni Hádádì wí fún Fáráò pé, “Jẹ́ kí n lọ, kí èmi kí ó le padà sí ìlú mi.”"'), '1Kgs$11:29': ('ní', None, '1Kgs$11:29 "Ó sì ṣe, ní àkókò náà Jéróbóámù ń jáde kúrò ní Jérúsálẹ́mù. Wòlíì Áhíjà ti Ṣílò sì pàdé rẹ̀ lójú ọ̀nà, ó sì wọ agbádá túntún. Àwọn méjèèjì nìkan ni ó sì ń bẹ ní oko,"'), '1Kgs$11:34': ('ṣe', 'gba', '1Kgs$11:34 "“ ‘Ṣùgbọ́n èmi kì yóò gba gbogbo ìjọba náà lọ́wọ́ Sólómónì; èmi ti mú un jẹ́ olórí ní gbogbo ọjọ́ ayé rẹ̀ nítorí ti Dáfídì ìránṣẹ́ mi, ẹni tí mo yàn, tí ó sì ti pa òfin mi àti àṣẹ mi mọ́."'), '1Kgs$11:36': ('ni', 'fun', '1Kgs$11:36 "Èmi yóò fi ẹ̀yà kan fún ọmọ rẹ kí Dáfídì ìránṣẹ́ mi lè máa ní ìmọ́lẹ̀ níwájú mi nígbà gbogbo ní Jérúsálẹ́mù, ìlú tí mo ti yàn láti fi orúkọ mi síbẹ̀."'), '1Kgs$12:6': (None, None, '1Kgs$12:6 "Nígbà náà ni Réhóbóámù ọba fi ọ̀rọ̀ lọ àwọn àgbààgbà tí ń dúró níwájú Sólómónì baba rẹ̀ nígbà tí ó wà láàyè. Ó sì béèrè pé, “Ìmọ̀ràn wo ni ẹ̀yin yóò gbà mí láti dá àwọn ènìyàn wọ̀nyí lóhùn?”"'), '1Kgs$12:7': (None, 'sin', '1Kgs$12:7 "Wọ́n sì dá a lóhùn pé, “Bí ìwọ yóò bá jẹ́ ìránṣẹ́ fún àwọn ènìyàn wọ̀nyí lónìí, kí o sì sìn wọ́n, àti kí o sì sọ ọ̀rọ̀ rere sí wọn nígbà tí ìwọ bá ń dá wọn lóhùn, wọn yóò má a ṣe ìránṣẹ́ rẹ títí láé.”"'), '1Kgs$12:8': (None, 'fun', '1Kgs$12:8 "Ṣùgbọ́n Réhóbóámù kọ ìmọ̀ràn tí àwọn àgbààgbà fún un, ó sì fi ọ̀rọ̀ náà lọ àwọn ọmọdé tí wọ́n dàgbà pẹ̀lú rẹ̀, tí wọ́n sì ń sìn ín."'), '1Kgs$12:20': (None, 'wá', '1Kgs$12:20 "Nígbà tí gbogbo Ísírẹ́lì sì gbọ́ pé Jéróbóámù ti padà dé, wọ́n ránṣẹ́, wọ́n sì pè é wá sí àjọ, wọ́n sì fi jẹ ọba lórí gbogbo Ísírẹ́lì. Kò sí ẹnìkan tí ó tọ ilé Dáfídì lẹ́yìn bí kò ṣe kìkì ẹ̀yà Júdà nìkan."'), '1Kgs$12:28': ('mu', 'ilẹ', '1Kgs$12:28 "Lẹ́yìn tí ó ti gba ìmọ̀ràn, ọba sì yá ẹgbọ̀rọ̀ màlúù wúrà méjì. Ó sì wí fún àwọn ènìyàn pé, “Ó ti pọ̀jù fún yín láti máa gòkè lọ sí Jérúsálẹ́mù. Àwọn Ọlọ́run yín nìyìí, Ísírẹ́lì, tí ó mú yín láti ilẹ̀ Éjíbítì wá.”"'), '1Kgs$12:33': ('ní', None, '1Kgs$12:33 "Ní ọjọ́ kẹẹ̀dógún oṣù kẹjọ, oṣù tí ó rò ní ọkàn ara rẹ̀, ó sì rú ẹbọ lórí pẹpẹ tí ó kọ́ ní Bétélì. Bẹ́ẹ̀ ni ó sì dá àsè sílẹ̀ fún àwọn ọmọ Ísírẹ́lì, ó sì gun orí pẹpẹ náà lọ láti rú ẹbọ."'), '1Kgs$13:8': (None, 'lọ', '1Kgs$13:8 "Ṣùgbọ́n ènìyàn Ọlọ́run náà dá ọba lóhùn pé, “Bí ìwọ yóò bá tilẹ̀ fún mi ní ìdajì ìní rẹ, Èmi kì yóò lọ pẹ̀lú rẹ tàbí kí èmi jẹ oúnjẹ tàbí mu omi ní ibí yìí."'), '1Kgs$13:20': (None, 'wá', '1Kgs$13:20 "Bí wọ́n sì ti jókòó ti tábìlì, ọ̀rọ̀ OLÚWA sì tọ wòlíì tí ó mú un padà wá pé;"'), '1Kgs$14:3': ('lọ', None, '1Kgs$14:3 "Mú ìṣù àkàrà mẹ́wàá pẹ̀lú rẹ̀, àti àkàrà wẹ́wẹ́ àti ìgò oyin, kí o sì lọ sí ọ̀dọ̀ rẹ̀. Òun yóò sì ṣọ fún ọ ohun tí yóò ṣẹlẹ̀ sí ọmọkùnrin náà.”"'), '1Kgs$14:7': (None, None, '1Kgs$14:7 "Lọ, sọ fún Jéróbóámù pé báyìí ni OLÚWA Ọlọ́run Ísírẹ́lì wí; Èmi sì gbé ọ ga láti inú àwọn ènìyàn, mo sì fi ọ́ jẹ olórì lórí Ísírẹ́lì ènìyàn mi."'), '1Kgs$14:15': ('fun', 'ni', '1Kgs$14:15 "OLÚWA yóò kọlu Ísírẹ́lì, yóò sì dà bí a ti ń mi ìyẹ́ nínú omi. Yóò sì fa Ísírẹ́lì tu kúrò ní ilẹ̀ rere yìí, tí ó ti fi fún àwọn baba wọn, yóò sì fọ́n wọn ká kọjá odò, nítorí wọ́n ti rú ìbínú OLÚWA nípa ṣíṣe ère òrìṣà."'), '1Kgs$14:16': ('ṣe', None, '1Kgs$14:16 Yóò sì kọ Ísírẹ́lì sílẹ̀ nítorí ẹ̀ṣẹ̀ tí Jéróbóámù ti ṣẹ̀ àti tí ó mú Ísírẹ́lì ṣẹ̀.”'), '1Kgs$14:17': (None, 'iloro', '1Kgs$14:17 "Nígbà náà ni aya Jéróbóámù sì dìde, ó sì lọ, ó sì lọ sí Tírà. Bí ó sì ti fi ẹsẹ̀ tẹ ìloro ilé, ọmọdé náà sì kú."'), '1Kgs$15:4': ('fun', None, '1Kgs$15:4 "Ṣùgbọ́n, nítorí i Dáfídì OLÚWA Ọlọ́run rẹ̀ fún un ní ìmọ́lẹ̀ kan ní Jérúsálẹ́mù nípa gbígbé ọmọ rẹ̀ ró láti jọba ní ipò rẹ̀ àti láti fi ìdí Jérúsálẹ́mù múlẹ̀."'), '1Kgs$15:18': (None, 'lọ', '1Kgs$15:18 "Nígbà náà ni Áṣà mú gbogbo fàdákà àti wúrà tí ó kù nínú ìṣúra ilé OLÚWA àti ìṣúra ilé ọba. Ó sì fi lé àwọn ìránṣẹ́ rẹ̀ lọ́wọ́, ó sì rán wọn lọ sí ọ̀dọ̀ Bẹni-Hádádì ọmọ Tábírímónì, ọmọ Hésíónì ọba Síríà tí ó ń gbé ní Dámásíkù."'), '1Kgs$16:1': ('ṣe', None, '1Kgs$16:1 "Ó sì ṣe, ọ̀rọ̀ OLÚWA sì tọ Jéhù ọmọ Hánánì wá sí Bááṣà pé:"'), '1Kgs$16:18': ('lọ', None, '1Kgs$16:18 "Nígbà tí Ṣímírì sì ri pé a ti gba ìlú, ó sì wọ inú ààfin ilé ọba lọ, ó sì tẹ iná bọ ilé ọba lórí ara rẹ̀, ó sì kú,"'), '1Kgs$16:31': (None, None, '1Kgs$16:31 "Ó sì ṣe bí ẹni pé ó ṣe ohun kékeré fún un láti máa rìn nínú ẹ̀ṣẹ̀ Jéróbóámù ọmọ Nébátì, ó sì mú Jésébélì, ọmọbìnrin Étíbáálì, ọba àwọn ará Sídónì ní aya, ó sì bẹ̀rẹ̀ sí ń sin Báálì, ó sì bọ ọ́."'), '1Kgs$16:34': (None, None, '1Kgs$16:34 " Ní ìgbà ayé Áhábù, Híélì ará Bétélì kọ́ Jẹ́ríkò. Ó fi ìpìlẹ̀ rẹ̀ lé ilẹ̀ ní Ábírámù, àkọ́bí rẹ, ó sì gbé àwọn ilẹ̀kùn ibodè rẹ̀ kọ́ ní Ṣégúdù àbíkẹ́yìn rẹ̀, gẹ́gẹ́ bí ọ̀rọ̀ OLÚWA tí ó ti ipa Jóṣúà ọmọ Núnì sọ."'), '1Kgs$17:8': (None, None, '1Kgs$17:8 Ọ̀rọ̀ OLÚWA sì tọ̀ ọ́ wá wí pé:'), '1Kgs$17:12': ('lọ', 'kú', '1Kgs$17:12 "Obìnrin opó náà sì dá a lóhùn wí pé, “Bí OLÚWA Ọlọ́run rẹ ti wà, èmi kò ní àkàrà: bí kò ṣe ìkúnwọ́ ìyẹ̀fun nínú ìkòkò àti òróró díẹ̀ nínú kólòbó. Èmi ń ṣa igi díẹ̀ jọ láti kó lọ ilé, kí èmi kí ó sì pèsè rẹ̀ fún mi àti fún ọmọ mi, kí àwa lè jẹ ẹ́: kí a sì kú.”"'), '1Kgs$17:17': (None, None, '1Kgs$17:17 "Ó sì ṣe lẹ́yìn nǹkan wọ̀nyí, ọmọ obìnrin tí ó ni ilé náà ṣe àìsàn, àìsàn náà sì le tó bẹ́ẹ̀, tí ó fi kú."'), '1Kgs$18:6': ('lọ', None, '1Kgs$18:6 "Bẹ́ẹ̀ ni wọ́n sì pín ilẹ̀ tí wọ́n fẹ́ dé láàrin ara wọn, Áhábù gba ọ̀nà kan lọ, Ọbadíà sì gba ọ̀nà mìíràn lọ."'), '1Kgs$18:9': ('ni', None, '1Kgs$18:9 "Ọbadíà sì béèrè pé, “Ẹ̀ṣẹ̀ kí ni mo ha dá tí ìwọ fi ń fi ìránṣẹ́ rẹ lé Áhábù lọ́wọ́ láti pa?"'), '1Kgs$18:12': (None, None, '1Kgs$18:12 "Èmi kò sì mọ ibi tí ẹ̀mí OLÚWA yóò gbé ọ lọ nígbà tí mo bá fi ọ́ sílẹ̀. Bí mo bá lọ, tí mo sì sọ fún Áhábù, tí kò sì rí ọ, òun a sì pa mí. Ṣíbẹ̀ èmi ìránṣẹ́ rẹ bẹ̀rù OLÚWA láti ìgbà èwe mi wá."'), '1Kgs$18:31': (None, 'orukọ', '1Kgs$18:31 "Èlíjà sì mú òkúta méjìlá, ọ̀kọ̀ọ̀kan fún ẹ̀yà ọmọ Jákọ́bù kan, ẹni tí ọ̀rọ̀ OLÚWA tọ̀ wá wí pé, “Ísírẹ́lì ni orúkọ rẹ̀ yóò máa jẹ́.”"'), '1Kgs$18:33': (None, None, '1Kgs$18:33 "Ó sì to igi náà dáradára, ó sì ké ẹgbọ̀rọ̀ akọ màlúù náà wẹ́wẹ́, ó sì tò ó sórí igi. Nígbà náà ni ó sì wí fún wọn wí pé, “Ẹ fi omi kún ìkòkò mẹ́rin, kí ẹ sì tun sórí ẹbọ sísun àti sórí igi náà.”"'), '1Kgs$18:36': ('jẹ ki', 'ni', '1Kgs$18:36 "Ó sì ṣe, ní ìrúbọ àṣáálẹ́, wòlíì Èlíjà sì súnmọ́ tòsí, ó sì gbàdúrà wí pé, “OLÚWA, Ọlọ́run Ábúráhámù, Ísákì àti Ísírẹ́lì, jẹ́ kí ó di mímọ̀ lónìí pé ìwọ ni Ọlọ́run ní Ísírẹ́lì àti pé èmi ni ìránṣẹ́ rẹ, àti pé mo ṣe gbogbo nǹkan wọ̀nyí nípa àṣẹ rẹ."'), '1Kgs$19:4': ('lọ', 'kú', '1Kgs$19:4 "nígbà tí òun tìkárarẹ̀ sì lọ ní ìrìn ojọ́ kan sí ihà, ó sì wá sí ibi igi ọwọ̀ kan, ó sì jókòó lábẹ́ rẹ̀, ó sì gbàdúrà kí òun bá le kú, wí pé, “Mo ti ní tó, OLÚWA, gba ẹ̀mí mi kúrò; nítorí èmi kò sàn ju àwọn baba mi lọ”"'), '1Kgs$19:9': (None, 'nibẹ', '1Kgs$19:9 "Níbẹ̀, ó lọ sí ibi ihò òkúta, ó sì wọ̀ níbẹ̀. Ọ̀rọ̀ OLÚWA sì tọ Èlíjà wá wí pé, “Kí ni ìwọ ń ṣe níhìn-ín yìí, Èlíjà?”"'), '1Kgs$19:10': ('da', 'ni', '1Kgs$19:10 " Ó sì dáhùn wí pé, “Èmi ti ń jowú fún OLÚWA Ọlọ́run àwọn ọmọ ogun. Àwọn ọmọ Ísírẹ́lì ti kọ májẹ̀mu rẹ sílẹ̀, wọ́n sì ti wó àwọn pẹpẹ rẹ lulẹ̀, wọ́n sì ti fi idà pa àwọn wòlíì rẹ. Èmi nìkan ṣoṣo ni ó kù, wọ́n sì ń wá ẹ̀mí mi láti gbà á kúrò báyìí.”"'), '1Kgs$19:14': ('da', 'ni', '1Kgs$19:14 "Ó sì dáhùn pé, “Èmi ti ń jowú fún OLÚWA Ọlọ́run àwọn ọmọ ogun. Àwọn ọmọ Ísírẹ́lì ti kọ májẹ̀mu rẹ sílẹ̀, wọ́n sì ti wó pẹpẹ rẹ lulẹ̀, wọ́n sì ti fi idà pa àwọn wòlíì rẹ. Èmi nìkan ṣoṣo ni ó kù, wọ́n sì ń gbìyànjú láti pa èmi náà báyìí.”"'), '1Kgs$19:15': (None, 'ọna', '1Kgs$19:15 "OLÚWA sì wí fún un pé, “Padà lọ sí ọ̀nà tí ìwọ ti wá, kí o sì lọ sí ihà Dámásíkù. Nígbà tí ìwọ bá dé ibẹ̀, fi òróró yan Hásáélì ní ọba lórí Árámù."'), '1Kgs$20:5': (None, 'ni', '1Kgs$20:5 "Àwọn ońṣẹ́ náà sì tún padà wá, wọ́n sì wí pé, “Báyìí ni Bẹni-Hádádì sọ wí pé: ‘Mo ránṣẹ́ láti béèrè fún fàdákà rẹ àti wúrà rẹ, àwọn aya rẹ àti àwọn ọmọ rẹ."'), '1Kgs$20:10': ('ṣe', 'to', '1Kgs$20:10 "Bẹni-Hádádì sì tún rán oníṣẹ́ mìíràn sí Áhábù wí pé, “kí àwọn òrìṣà kí ó ṣe bẹ́ẹ̀ sí mi àti jù bẹ́ẹ̀ lọ pẹ̀lú bí eekuru Samáríà yóò tó fún ìkúnwọ́ fún gbogbo ènìyàn tí ń tẹ̀lé mi.”"'), '1Kgs$20:26': ('lọ', None, '1Kgs$20:26 "Ó sì ṣe ní àmọ́dún, Bẹni-Hádádì ka iye àwọn ará Árámù, ó sì gòkè lọ sí Áfékì, láti bá Ísírẹ́lì jagun."'), '1Kgs$20:28': (None, 'ni', '1Kgs$20:28 "Ènìyàn Ọlọ́run kan sì gòkè wá, ó sì sọ fún ọba Ísírẹ́lì pé, “Báyìí ni OLÚWA wí: ‘Nítorí tí àwọn ará Árámù rò pé OLÚWA, Ọlọ́run òkè ni, ṣùgbọ́n òun kì í ṣe Ọlọ́run àfonífojì, nítorí náà èmi ó fi gbogbo ọ̀pọ̀lọpọ̀ ènìyàn yìí lé ọ lọ́wọ́, ẹ̀yin yóò sì mọ̀ pé, Èmi ni OLÚWA.’ ”"'), '1Kgs$20:31': ('jẹ ki', 'ni', '1Kgs$20:31 "Àwọn ìránṣẹ́ rẹ̀ sì wí fún un pé, “Wò ó, a ti gbọ́ pé àwọn ọba ilẹ̀ Ísírẹ́lì jẹ́ ọba aláàánú, mo bẹ̀ ọ́ jẹ́ kí àwa kí ó tọ̀ ọ́ lọ pẹ̀lú aṣọ ọ̀fọ̀ ní ẹ̀gbẹ́ wa, àti okùn yí orí wa ká. Bóyá òun yóò gba ẹ̀mí rẹ là.”"'), '1Kgs$20:33': ('ṣe', 'wá', '1Kgs$20:33 "Àwọn ọkùnrin náà sì ṣe àkíyèsí gidigidi, wọ́n sì yára gbá ọ̀rọ̀ rẹ̀ mú, wọ́n sì wí pé, “Bẹ́ẹ̀ ni, Bẹni-Hádádì arákùnrin rẹ.” Ọba sì wí pé, “Ẹ lọ mú u wá.” Nígbà tí Bẹni-Hádádì jáde tọ̀ ọ́ wá, Áhábù sì mú u gòkè wá sínú kẹ̀kẹ́."'), '1Kgs$20:34': ('mu', 'ṣe', '1Kgs$20:34 "Bẹni-Hádádì sì wí pé, “Èmi yóò dá àwọn ìlú tí baba mi ti gbà lọ́wọ́ baba rẹ padà, ìwọ sì le la ọ̀nà fún ara rẹ ní Dámáskù, bí baba mi ti ṣe ní Samáríà.” Áhábù sì wí pé, “Èmi yóò rán ọ lọ pẹ̀lú májẹ̀mú yìí.” Bẹ́ẹ̀ ni ó ba dá májẹ̀mú, ó sì rán an lọ."'), '1Kgs$21:2': (None, 'dara', '1Kgs$21:2 "Áhábù sì wí fún Nábótì pé, “Fún mi ní ọgbà àjàrà rẹ, kí èmi kí ó fi ṣe ọgbà-ewébẹ̀, nítorí tí ó súnmọ́ tòsí ilé mi; Èmi yóò sì fún ọ ní ọgbà àjàrà tí ó sàn jù ú lọ dípò rẹ̀; tàbí bí ó bá sì dára ní ojú rẹ, èmi yóò fi iye owó rẹ̀ fún ọ.”"'), '1Kgs$21:4': ('ní', 'fun', '1Kgs$21:4 "Áhábù sì wá sí ilé rẹ̀, ó wugbọ́, inú rẹ̀ sì bàjẹ́ nítorí ọ̀rọ̀ tí Nábótì ará Jésérẹ́lì sọ fún un; nítorí tí ó wí pé, “Èmi kì yóò fún ọ ní ogún àwọn bàbá mi.” Ó sì dùbúlẹ̀ lórí ibùsùn rẹ̀, ó sì yí ojú rẹ̀ padà, kò sì fẹ́ jẹun."'), '1Kgs$21:15': ('fun', None, '1Kgs$21:15 "Bí Jésébélì sì ti gbọ́ pé, a ti sọ Nábótì ní Òkúta pa, ó sì wí fún Áhábù pé, “Dìde! Kí o sì jogún ọgbà àjàrà Nábótì, ará Jésérẹ́lì, tí ó kọ̀ láti tà fún ọ kò sí láàyè mọ́, ó ti kú.”"'), '1Kgs$21:16': ('lọ', None, '1Kgs$21:16 "Nígbà tí Áhábù sì gbọ́ pé Nábótì ti kú, ó sì dìde, ó sì sọ̀kalẹ̀ lọ láti jogún ọgbà àjàrà Nábótì."'), '1Kgs$21:21': (None, None, '1Kgs$21:21 "‘Èmi yóò mú ibi wá sórí rẹ. Èmi yóò sì mú ìran rẹ kúrò, èmi yóò sì ké kúrò lọ́dọ̀ Áhábù gbogbo ọmọdé ọkùnrin, àti ọmọ ọ̀dọ̀, àti òmìnira ní Ísírẹ́lì."'), '1Kgs$21:27': ('lọ', None, '1Kgs$21:27 "Nígbà tí Áhábù sì gbọ́ àwọn ọ̀rọ̀ wọ̀nyí, ó sì fa aṣọ rẹ̀ ya, ó sì fi aṣọ ọ̀fọ̀ sí ara rẹ̀, ó sì gba ààwẹ̀. Ó sì dùbúlẹ̀ nínú aṣọ ọ̀fọ̀, ó sì ń lọ jẹ́ ẹ́."'), '1Kgs$22:11': ('ṣe', 'ni', '1Kgs$22:11 "Ṣedekíà ọmọ Kénáánà sì ṣe ìwo irin fún ara rẹ̀, ó sì wí pé, “Báyìí ni OLÚWA wí: ‘Wọ̀nyí ni ìwọ yóò fi kan àwọn ará Árámù títí ìwọ yóò fi run wọ́n.’ ”"'), '1Kgs$22:17': (None, 'ni', '1Kgs$22:17 " Míkáyà sì dáhùn pé, “Mo rí gbogbo Ísírẹ́lì túkáàkiri lórí àwọn òkè bí àgùntàn tí kò ní olùṣọ́, OLÚWA sì wí pé, ‘Àwọn wọ̀nyí kò ní olúwa. Jẹ́ kí olúkúlùkù padà sí ilé rẹ̀ ní àlàáfíà.’ ”"'), '1Kgs$22:43': (None, 'kuro', '1Kgs$22:43 "Ó sì rìn nínú gbogbo ọ̀nà Áṣà baba rẹ̀, kò sì yípadà kúrò nínú rẹ̀; ó sì ṣe èyí tí ó tọ́ ní ojú OLÚWA. Kìkì àwọn ibi gíga ni a kò mú kúrò, àwọn ènìyàn sì ń rú ẹbọ, wọ́n sì ń sun tùràrí níbẹ̀."'), '1Kgs$22:48': (None, 'lọ', '1Kgs$22:48 " Jèhósáfátì kan ọkọ̀ Táríṣíṣì láti lọ sí Ófírì fún wúrà, ṣùgbọ́n wọn kò lọ: nítorí àwọn ọkọ̀ náà fọ́ ní Ésíónì-Gébérì."'), '2Kgs$1:4': (None, 'kú', '2Kgs$1:4 Nítorí náà ohun tí OLÚWA sọ ni èyí: ‘Ìwọ kò ní kúrò lórí ìbùsùn tí o dúbúlẹ̀ lé. Dájúdájú ìwọ yóò kú!’ ” Bẹ́ẹ̀ ni Èlíjà lọ.'), '2Kgs$2:1': ('lọ', None, '2Kgs$2:1 "Nígbà tí Ọlọ́run ń fẹ́ gbé Èlíjà lọ sí òkè ọ̀run nínú àjà, Èlíjà àti Èlíṣà wà ní ọ̀nà láti Gílgálì."'), '2Kgs$2:9': (None, 'kọjá', '2Kgs$2:9 "Nígbà tí wọ́n rékọjá, Èlíjà sì wí fún Èlíṣà pé, “Sọ fún mi, kí ni èmi lè ṣe fún ọ kí ó tó di wí pé wọ́n gbà mí kúrò lọ́dọ̀ rẹ?” “Jẹ́ kí èmi kí ó jogún ìlọ́po méjì ẹ̀mí rẹ.” Ó dá a lóhùn."'), '2Kgs$2:11': ('lọ', None, '2Kgs$2:11 " Gẹ́gẹ́ bí wọ́n ti ń rìn lọ tí wọ́n sì ń sọ̀rọ̀ pọ̀, lọ́gán kẹ̀kẹ́ iná àti ẹsin iná yọ sí wọn ó sì ya àwọn méjèèjì nípa, Èlíjà sì gòkè lọ sí ọ̀run pẹ̀lú àjà."'), '2Kgs$2:15': ('ri', None, '2Kgs$2:15 "Àwọn ọmọ wòlíì láti Jẹ́ríkò, tí wọ́n ń wò, wí pé, “Ẹ̀mí Èlíjà simi lé Èlíṣà.” Wọ́n sì lọ láti lọ bá a, wọ́n sì dojú bolẹ̀ níwájú rẹ̀."'), '2Kgs$3:14': ('wo', 'wo', '2Kgs$3:14 "Èlíṣà wí pé, “Gẹ́gẹ́ bí ó ti wà pé OLÚWA àwọn ọmọ ogun wà láyé, ẹni tí mo ń sìn tí èmi kò bá ní ọ̀wọ̀ fún ojú Jèhósáfátì ọba Júdà, Èmi kò ní wò ó tàbí èmi kì bá ti rí ọ."'), '2Kgs$3:22': (None, None, '2Kgs$3:22 "Nígbà tí wọ́n dìde ní òwúrọ̀ ọjọ́ kejì, òòrùn ti tàn sí orí omi náà sí àwọn ará Móábù ní ìkọjá ọ̀nà, omi náà sì pupa bí ẹ̀jẹ̀."'), '2Kgs$3:25': (None, 'eniyan', '2Kgs$3:25 "Wọ́n sì wọ gbogbo ìlú náà olúkúlùkù ènìyàn, wọ́n ju òkúta sí gbogbo ohun dáradára orí pápá títí tí ó fi run. Wọ́n sì dá gbogbo orísun omi dúró wọ́n sì gé gbogbo orísun dáradára. Kíríháráṣétì nìkan ni wọ́n fi òkúta rẹ̀ sílẹ̀ ní àyè rẹ̀, ṣùgbọ́n àwọn ènìyàn ológun pẹ̀lú kanakáná yíká, wọ́n sì kọlù ìlú náà."'), '2Kgs$3:27': ('mu', 'ni', '2Kgs$3:27 "Nígbà náà ó mú àkọ́bí ọmọkùnrin rẹ̀, tí kò bá jẹ́ gẹ́gẹ́ bí ọba, ó sì fi rú ẹbọ sísun ní orí ògiri ìlú. Wọ́n sì bínú lórí Ísírẹ́lì púpọ̀púpọ̀; wọ́n yọ́ kúrò wọ́n sì padà sí ìlú wọn."'), '2Kgs$4:1': ('ṣe', 'wá', '2Kgs$4:1 "Ìyàwó ọkùnrin kan láti ara ẹgbẹ́ wòlíì sunkún tọ Èlíṣà wá, “Ìránṣẹ́ rẹ ọkọ mi ti kú, ó sì mọ̀ wí pé ó bu ọlá fún OLÚWA. Ṣùgbọ́n Nísinsìn yìí onígbèsè rẹ̀ ti ń bọ̀ láti wá kó ọmọ ọkùnrin mi gẹ́gẹ́ bí ẹrú rẹ̀.”"'), '2Kgs$4:10': ('ṣe', None, '2Kgs$4:10 "Jẹ́ kí a ṣe yàrá kékeré kan sórí ilé kí a sì gbé ibùṣùn àti tábìlì oúnjẹ, àga kan àti àtùpà fún un. Nígbà náà ó lè dúró níbẹ̀ ní ìgbàkúgbà tí ó bá wá sọ́dọ̀ wa.”"'), '2Kgs$4:11': (None, None, '2Kgs$4:11 "Ní ọjọ́ kan nígbà tí Èlíṣà wá, ó lọ sí orí òkè ní yàrá rẹ̀ ó sì dùbúlẹ̀ síbẹ̀."'), '2Kgs$4:22': (None, 'eniyan', '2Kgs$4:22 "Ó pe ọkọ rẹ̀ ó sì wí pé, “Jọ̀wọ́ rán ọ̀kan lára àwọn ìránṣẹ́ rẹ àti kẹ́tẹ́kẹ́tẹ́ kan kí èmi kí ó lè lọ sí ọ̀dọ̀ ènìyàn Ọlọ́run kíákíá kí n sì padà.”"'), '2Kgs$4:44': ('ṣe', None, '2Kgs$4:44 "Nígbà náà ó gbé e ka iwájú wọn, wọ́n sì jẹ ẹ́, wọ́n sì ní èyí tó ṣẹ́kù, gẹ́gẹ́ bí ọ̀rọ̀ OLÚWA."'), '2Kgs$5:1': (None, None, '2Kgs$5:1 "Námánì jẹ́ olórí ogun ọba Árámù. Ó jẹ́ ènìyàn ńlá níwájú ọ̀gá rẹ̀, wọ́n sì bu ọlá fún un, nítorí nípasẹ̀ rẹ̀ OLÚWA fún wa ní ìṣẹ́gun fún Árámù. Ó jẹ́ alágbára, akọni ọkùnrin ṣùgbọ́n, ó dẹ́tẹ̀."'), '2Kgs$5:2': ('mu', None, '2Kgs$5:2 "Nísinsìnyìí ẹgbẹgbẹ́ láti Árámù ti jáde lọ láti mú ọmọ obìnrin kékeré kan ní ìgbèkùn láti Ísírẹ́lì, ó sì sin ìyàwó Námánì."'), '2Kgs$5:7': ('ní', None, '2Kgs$5:7 "Bí ọba Ísírẹ́lì ti ka ìwé náà ó fa aṣọ rẹ̀ ya, ó sì wí pé, “Èmi ha jẹ́ Ọlọ́run? Ǹjẹ́ èmi le pa kí n sì mú wá sí àyè padà? Kí ni ó dé tí eléyìí rán ènìyàn sí mi láti wo àrùn ẹ̀tẹ̀ rẹ sàn, kí ẹ wo bí ó ti ń wá ọ̀nà láti wá ìjà pẹ̀lú mi!”"'), '2Kgs$5:13': (None, 'ṣe', '2Kgs$5:13 "Ìránṣẹ́ Námánì lọ sọ́dọ̀ rẹ̀ ó sì wí pé, “Baba mi, tí wòlíì bá ti sọ fún ọ láti ṣe ohun ńlá kan, ṣé ìwọ kì bá ti ṣe, mélòómélòó nígbà náà, nígbà tí ó sọ fún ọ pé, ‘Wẹ̀ kí o sì mọ́’!”"'), '2Kgs$6:2': (None, None, '2Kgs$6:2 "Jẹ́ kí àwa kí ó lọ sí Jọ́dánì, ní ibi tí ẹnìkọ̀ọ̀kan ti le rí ọ̀pá kan, jẹ́ kí àwa kí ó kọ́lé síbẹ̀ fún wa láti gbé.” Ó sì wí pé, “lọ.”"'), '2Kgs$6:9': (None, None, '2Kgs$6:9 "Ènìyàn Ọlọ́run rán iṣẹ́ sí ọba Ísírẹ́lì, “kíyèsí ara láti kọjá ní ibẹ̀ yẹn, nítorí pé ará Árámù wọ́n sọ̀kalẹ̀ lọ síbẹ̀.”"'), '2Kgs$6:10': (None, 'ibi', '2Kgs$6:10 "Bẹ́ẹ̀ ni ọba Ísírẹ́lì wo ibi tí ènìyàn Ọlọ́run náà fi hàn, ní ẹ̀ẹ̀kan sí i Èlíṣà kìlọ̀ fún ọba, bẹ́ẹ̀ ni ó wà lórí sísọ ní ibẹ̀."'), '2Kgs$6:19': ('ni', None, '2Kgs$6:19 "Èlíṣà sọ fún wọn pé “Èyí kì í ṣe ọ̀nà bẹ́ẹ̀ ni èyí kì í ṣe ìlú náà. Tẹ̀lé mi, èmi yóò mú u yín lọ sí ọ̀dọ̀ ọkùnrin tí ẹ̀yin ń wá.” Ó sì sìn wọ́n lọ sí Samáríà."'), '2Kgs$6:24': (None, None, '2Kgs$6:24 "Lẹ́yìn ìgbà díẹ̀, ni Bẹni-Hádádì ọba Árámù kó gbogbo àwọn ogun rẹ̀ jọ wọ́n sì yan sókè wọ́n sì dúró ti Samáríà."'), '2Kgs$6:25': (None, None, '2Kgs$6:25 "Ìyàn ńlá sì mú ní ìlú náà; wọ́n dúró tìí tó bẹ́ẹ̀ tí a fi ń ta orí kẹ́tẹ́kẹ́tẹ́ kan ní ọgọ́ọ̀rùnún ìwọ̀n fàdákà àti ìdámẹ́rin òṣùwọ̀n kábù imí ẹyẹlé, fún ìwọ̀n fàdákà márùn ún."'), '2Kgs$7:9': ('wá', 'ṣe', '2Kgs$7:9 "Nígbà náà wọ́n wí fún ara wọn pé, “Àwa kò ṣe ohun rere. Oni yìí jẹ́ ọjọ́ ìròyìn rere àwa si paamọ́ ara wa. Tí àwa bá dúró títí di àfẹ̀mọ́júmọ́, ìjayà yóò jẹ́ ti wa. Ẹ jẹ́ kí a lọ ní ẹ̀ẹ̀kan kí a lọ ròyìn èyí fún àwọn ilé ọba.”"'), '2Kgs$7:13': ('run', None, '2Kgs$7:13 "Ọ̀kan lára àwọn ìránṣẹ́ rẹ̀ dáhùn pé, “Mú ọkùnrin díẹ̀ mú márùnún lára àwọn ẹsin tí wọ́n fi sílẹ̀ nínú ìlú. Ìwà wọn yóò dà bí gbogbo àwọn ará Ísírẹ́lì tí ó kù níbẹ̀ bẹ́ẹ̀ ni, wọn yóò dà bí gbogbo àwọn Ísírẹ́lì, yìí nìkan tí a run. Bẹ́ẹ̀ ni ẹ jẹ́ kí a rán wọn lọ láti lọ wo ohun tí ó ṣẹlẹ̀.”"'), '2Kgs$7:17': ('yan', None, '2Kgs$7:17 "Nísìn yìí ọba sì mú ìjòyè náà lórí ẹni tí ó fi ọwọ́ ara rẹ̀ tì ní ìkáwọ́ ẹnu ibodè, àwọn ènìyàn sì tẹ̀ẹ́ mọ́lẹ̀ ní ẹnu ibodè. Ó sì kú, gẹ́gẹ́ bí ènìyàn Ọlọ́run ti ṣọ tẹ́lẹ̀ nígbà tí ọba sọ̀kalẹ̀ lọ sí ilé rẹ̀."'), '2Kgs$7:18': ('ní', 'ṣe', '2Kgs$7:18 "Ó sì ti sẹlẹ̀ gẹ́gẹ́ bí ènìyan Ọlọ́run ti ṣọ fún ọba: “Ní àsìkò yìí ní ọ̀la, òsùnwọ̀n ìyẹ̀fun ni a ó ta nì Ṣékélì kan àti òsùnwọ̀n méjì báálì ní Ṣékélì kan ní ẹnu ọ̀nà ibodè Ṣamáríà.”"'), '2Kgs$8:3': ('lọ', None, '2Kgs$8:3 Ní ẹ̀yìn ọdún méje ó sì padà wá láti ilẹ̀ àwọn ará Fílístínì ó sì lọ sí ọ̀dọ̀ ọba láti lọ bẹ̀ẹ́ fún ilé àti ilẹ̀ rẹ̀.'), '2Kgs$9:1': ('ororo', None, '2Kgs$9:1 " Wòlíì Èlíṣà fi àṣẹ pe ọkùnrin kan láti ẹgbẹ́ àwọn wòlíì, ó sì wí fún un pé, “Ki agbádá rẹ sínú ọ̀já àmùrè rẹ, gba ṣágo kékeré òróró yìí pẹ̀lú rẹ, kí o sì lọ sí Ramoti-Gílíádì."'), '2Kgs$9:6': ('ororo', None, '2Kgs$9:6 "Jéhù dìde sókè, ó sì wọ inú ilé lọ. Nígbà náà, wòlíì náà da òróró náà sórí Jéhù; ó sì wí pé, “Èyí ni ohun tí OLÚWA Ọlọ́run Ísírẹ́lì wí: ‘Èmi fi àmì òróró yàn ọ́ ní ọba lórí àwọn ènìyàn OLÚWA Ísírẹ́lì."'), '2Kgs$9:30': ('wò', 'oju', '2Kgs$9:30 "Nígbà náà Jéhù lọ sí Jésérẹ́lì. Nígbà tí Jésébélì gbọ́ nípa rẹ̀, ó kun ojú u rẹ̀, ó to irun rẹ̀, ó sì wò jáde láti ojú fèrèsé."'), '2Kgs$9:32': ('ẹgbẹ', 'oju', '2Kgs$9:32 "Ó gbójú sókè láti wo fèrèsé, ó sì pè jáde, “Ta ni ó wà ní ẹ̀gbẹ́ mi? Ta ni?” Ìwẹ̀fà méjì tàbí mẹ́ta bojú wò ó nílẹ̀."'), '2Kgs$9:34': (None, None, '2Kgs$9:34 "Jéhù wọ inú ilé lọ, ó jẹ ó sì mu. “Tọ́jú obìnrin yẹn tí a fi bú,” Ó wí, “Kí o sì sin-ín, nítorí ọmọbìnrin ọba ni ó jẹ́.”"'), '2Kgs$10:7': (None, None, '2Kgs$10:7 "Nígbà tí ìwé náà dé àwọn ọkùnrin wọ̀nyí mú gbogbo àwọn ọmọ aládé bìnrin, wọ́n sì pa gbogbo àádọ́rin wọn. Wọ́n gbé orí i wọn sí inú apẹ̀rẹ̀, wọ́n fi ránṣẹ́ sí Jéhù ní Jésérẹ́lì."'), '2Kgs$10:9': ('lọ', None, '2Kgs$10:9 "Ní òwúrọ̀ ọjọ́ kejì, Jéhù jáde lọ. Ó dúró níwájú gbogbo àwọn ènìyàn, ó sì wí pé, “Ẹ̀yin jẹ́ aláìjẹ̀bi. Èmi ni mo dìtẹ̀ sí ọ̀gá mi, tí mo sì pa á, ṣùgbọ́n ta ni ó pa gbogbo àwọn wọ̀nyí!?"'), '2Kgs$10:19': (None, 'ni', '2Kgs$10:19 "Nísinsìn yìí, ẹ pe gbogbo àwọn wòlíì Báálì jọ, gbogbo àwọn òjíṣẹ́ rẹ̀ àti gbogbo àlùfáà rẹ̀. Rí i wí pé, kò sí nǹkankan tí ó ń sọnù nítorí ẹ̀mi yóò rú ẹbọ ńlá fún Báálì. Ẹnìkíní tí ó bá kọ̀ láti wá, kò ní wà láàyè mọ́.” Ṣùgbọ́n Jéhù fi ẹ̀tàn ṣe é, kí ó lè pa àwọn òjíṣẹ́ Báálì run."'), '2Kgs$10:25': ('ní', 'jẹ ki', '2Kgs$10:25 "Ní kété tí Jéhù ti parí ṣíṣe ọrẹ sísun, ó pàṣẹ fún àwọn olùṣọ́ àti àwọn ìjòyè: “Wọlé lọ, kí o sì pa wọ́n; Má ṣe jẹ́ kí ẹnikẹ́ni kí ó sálọ.” Bẹ́ẹ̀ ni wọ́n gé wọn lulẹ̀ pẹ̀lú idà. Àwọn olùṣọ́ àti ìjòyè ju ara wọn síta, wọ́n sì wọ inú ojúbọ ti ilé tí a kọ́ fún òrìṣà Báálì."'), '2Kgs$10:30': ('oju', 'okan', '2Kgs$10:30 "OLÚWA sì sọ fún Jéhù pé, “Nítorí ìwọ ti ṣe dáradára ní ṣíṣe èyí tí ó tọ́ ní ojú mi, tí o sì ti ṣe sí ilé Áhábù gẹ́gẹ́ bí gbogbo èyí tí ó wà ní ọkàn mi, àwọn ọmọ ọ̀ rẹ yóò jókòó lórí ìtẹ́ Ísírẹ́lì títí dé ìran kẹrin.”"'), '2Kgs$11:5': (None, None, '2Kgs$11:5 "Ó pàṣẹ fún wọn, wí pé, “Ìwọ tí ó wà nínú àwọn ẹgbẹ́ mẹ́tẹ̀ta tí ó ń lọ fún iṣẹ́ ìsìn ní ọjọ́ ìsinmi—ìdámẹ́ta yín fún síṣọ́ ààfin ọba."'), '2Kgs$11:9': ('awọn ọkunrin', None, '2Kgs$11:9 Olórí àwọn ọrọọrún ṣe gẹ́gẹ́ bí Jéhóíádà àlùfáà ti paà láṣẹ́. Olúkúlùkù mú àwọn ọkùnrin rẹ̀ àwọn tí ó ń lọ sí iṣẹ́ ìsìn ní ọjọ́ ìsinmi àti àwọn tí wọ́n wá sí ọ̀dọ̀ Jéhóíádà àlùfáà.'), '2Kgs$11:19': ('mu', 'ilẹ', '2Kgs$11:19 "Ó mú pẹ̀lú u rẹ̀ àwọn olórí ọ̀rọọrún àti gbogbo balógun àti àwọn olùṣọ́ àti gbogbo àwọn ènìyàn ilẹ̀ náà àti lápapọ̀, wọ́n mú ọba sọ̀kalẹ̀ wá láti ilé tí a kọ́ fún OLÚWA. Wọ́n sì wọ inú ààfin lọ, wọ́n sì wọlé nípa ìlẹ̀kùn ti àwọn olùṣọ́. Ọba sì mú àyè rẹ̀ ní orí ìtẹ́ ọba."'), '2Kgs$12:4': ('gba', 'mú', '2Kgs$12:4 "Jóásì sọ fún àwọn àlùfáà pé, “Gba gbogbo owó tí wọ́n mú wá gẹ́gẹ́ bí ọrẹ mímọ́ sí ilé tí a kọ́ fún OLÚWA owó tí a gbà ní ìgbà kíka àwọn ènìyàn ìlú, owó tí a gbà láti ọwọ́ olúkúlùkù bí wọ́n ti ṣe jẹ́ ẹ̀yà àti owó tí ó ti ọkàn olúkúlùkù wá tí a múwá sí ilé tí a kọ́ fún OLÚWA."'), '2Kgs$12:18': ('ní', None, '2Kgs$12:18 "Ṣùgbọ́n Jóásì ọba Júdà mú gbogbo ohun mímọ́ tí a gbé ka iwájú tí a yà sọ́tọ̀ nípasẹ̀ baba rẹ̀—Jéhósáfátì, Jéhórámù àti Áhásáyà, àwọn ọba Júdà, àti àwọn ẹ̀bùn tí òun tìkálára rẹ̀ ti yà sọ́tọ̀ àti gbogbo wúrà, tí ó rí nínú ibi ìfowópamọ́ sí ilé tí a kọ́ fún OLÚWA àti níti ààfin ọba, ó sì fi wọ́n ránṣẹ́ sí Hásáélì; ọba Ṣíríà, tí ó sì fa padà kúrò ní Jérúsálẹ́mù."'), '2Kgs$13:5': ('fun', 'ọwọ', '2Kgs$13:5 "OLÚWA pèsè Olùgbàlà fún Ísírẹ́lì, wọ́n sì sá kúrò lọ́wọ́ agbára Ṣíríà. Bẹ́ẹ̀ ni àwọn ọmọ Ísírẹ́lì gbé nínú ilé ara wọn gẹ́gẹ́ bí wọ́n ti wà tẹ́lẹ̀."'), '2Kgs$13:19': ('run', 'ni', '2Kgs$13:19 "Ènìyàn Ọlọ́run sì bínú sí i, ó sì wí pé, “Ìwọ kò bá ti lu ilẹ̀ ní ẹẹ̀marùn-ún tàbí ní ẹ̀ẹ̀mẹfà; Nígbà náà, ìwọ kò bá ti ṣẹ́gun Ṣíríà àti pa á run pátapáta ṣùgbọ́n Nísinsìn yìí ìwọ yóò ṣẹ́gun rẹ̀ ní ẹ̀mẹ́ta péré.”"'), '2Kgs$13:21': (None, None, '2Kgs$13:21 "Bí àwọn ọmọ Ísírẹ́lì kan ti ń sin òkú ọkùnrin kan, lójijì wọ́n rí ẹgbẹ́ àwọn oníjadì: Bẹ́ẹ̀ ni wọ́n ju òkú ọkùnrin náà sínú ibojì Èlíṣà. Nígbà tí ara ọkùnrin náà kan egungun Èlíṣà, ó padà wá sí ayé, ó sì dúró ní ẹsẹ̀ rẹ̀."'), '2Kgs$14:8': ('wo', 'oju', '2Kgs$14:8 " Nígbà náà, Ámásáyà rán àwọn ìránṣẹ́ sí Jóásì ọmọ Jéhóáhásì ọmọ Jéhù ọba Ísírẹ́lì pẹ̀lú ìpèlẹ́jọ́ “Wá, í bá mi lójúkorojú.”"'), '2Kgs$15:19': ('fun', None, '2Kgs$15:19 "Nígbà náà, Púlù ọba Ásíríà, gbógun ti ilẹ̀ náà, Ménáhémù sì fún un ní ẹgbẹ̀rin talẹ́ńtì fàdákà láti fi gba àtìlẹyìn rẹ̀ àti láti fún ìdúró tirẹ̀ lágbára lórí ìjọba."'), '2Kgs$15:20': (None, None, '2Kgs$15:20 "Ménáhémù fi agbára gba owó náà lọ́wọ́ Ísírẹ́lì. Gbogbo ọkùnrin ọlọ́lá ní láti dá àádọ́ta ṣékélì fàdákà láti fún ọba Áṣíríà. Bẹ́ẹ̀ ni ọba Áṣíríà padà sẹ́yìn, kò sì dúró ní ilẹ̀ náà mọ́."'), '2Kgs$16:10': ('ri', 'pade', '2Kgs$16:10 "Ọba sì lọ sí Dámásíkù láti pàdé Tigilati-Pílésérì, ọba Ásíríà, ó sì rí pẹpẹ kan tí ó wà ní Dámásíkù: Áhásì ọba sì rán àwòrán pẹpẹ náà, àti àpẹẹrẹ rẹ̀ sí Úráyà àlùfáà, gẹ́gẹ́ bí gbogbo iṣẹ́ ọ̀nà rẹ̀."'), '2Kgs$17:7': ('mu', None, '2Kgs$17:7 "Gbogbo eléyìí ṣẹlẹ̀ nítorí àwọn ọmọ Ísírẹ́lì ti dẹ́sẹ̀ sí OLÚWA Ọlọ́run wọn, ẹni tí ó mú wọn jáde ní Éjíbítì lábẹ́ agbára Fáráò ọba Éjíbítì. Wọ́n sin ọlọ́run mìíràn"'), '2Kgs$17:10': (None, None, '2Kgs$17:10 Wọ́n sì gbé àwọn òkúta tí a yà sọ́tọ sókè àti ère òrìṣà lórí gbogbo igi túútúú'), '2Kgs$17:25': (None, None, '2Kgs$17:25 "Nígbà tí wọ́n gbé bẹ́ ẹ ní àkọ́kọ́, wọn kò sì bẹ̀rù OLÚWA, Bẹ́ẹ̀ ni ó rán kìnnìún sí àárin wọn. Wọ́n sì pa nínú wọn."'), '2Kgs$17:26': (None, None, '2Kgs$17:26 "Wọ́n sì sọ fún ọba Ásíríà pé: “Àwọn ènìyàn tí ìwọ lé kúrò tí o sì fi sínú ìlú Ṣamáríà kò mọ ohun tí OLÚWA ìlú náà béèrè. Ó sì ti rán kìnnìún sí àárin wọn, tí ó sì ń pa wọ́n run, nítorí ènìyàn wọn kò mọ ohun tí ó béèrè.”"'), '2Kgs$17:27': ('mu', 'ilẹ', '2Kgs$17:27 "Nígbà náà ọba Ásíríà pàṣẹ yìí wí pé, “Mú ọ̀kan lára àwọn àlùfáà tí ó mú láti Ṣamáríà lọ padà gbé níbẹ̀ kí ó sì kọ́ àwọn ènìyàn ní, ohun tí OLÚWA ilẹ̀ náà béèrè.”"'), '2Kgs$17:29': ('ní', 'tirẹ', '2Kgs$17:29 "Bí ó tilẹ̀ rí bẹ́ẹ̀, olúkúlùkù orílẹ̀ èdè ṣe òrìṣà tirẹ̀ ní gbogbo ìlú níbi tí wọ́n gbé wà, wọ́n sì gbé wọn nínú ilé òrìṣà àti àwọn ènìyàn Ṣamáríà ó sì ṣe wọ́n sí ibi gíga wọ̀n-ọn-nì."'), '2Kgs$17:34': (None, None, '2Kgs$17:34 "Láti ìgbà náà wá àwọn àlùfáà wọn ṣe bí ti àtẹ̀yìnwá. Wọn kò sin OLÚWA tàbí kí wọ́n fi ara mọ́ ìlànà àti àṣẹ àti òfin tí OLÚWA fi fún ìránṣẹ́ Jákọ́bù, tí wọ́n pe orúkọ rẹ̀ ní Ísírẹ́lì."'), '2Kgs$17:38': ('ṣe', None, '2Kgs$17:38 Ẹ má ṣe gbàgbé májẹ̀mú tí mo ti ṣe pẹ̀lú yín mọ́ àti kí ẹ má sin ọlọ́run mìíràn.'), '2Kgs$17:41': (None, 'ṣe', '2Kgs$17:41 "Bẹ́ẹ̀ ni àwọn ènìyàn wọn sin OLÚWA, wọ́n sì ń sin òrìṣà wọn. Títí di ọjọ́ òní ọmọ wọn àti àwọn ọmọ-ọmọ wọn sì ń ṣe bí àwọn baba wọn ti ń ṣe."'), '2Kgs$18:4': ('jo', 'ge', '2Kgs$18:4 "Ó mú ibi gíga náà kúrò, ó sì fọ́ òkúta àwọn ère ó sì gé àwọn ère lulẹ̀ ó sì fọ́ ejò idẹ náà túútúú tí Móṣè ti ṣe, títí di ọjọ́ tí àwọn ọmọ Ísírẹ́lì ń sun tùràrí sí. (Wọ́n sì pè é ní Néhúṣítanì.)"'), '2Kgs$18:13': ('ṣe', None, '2Kgs$18:13 "Ní ọdún kẹrìnlá tí Heṣekáyà jọba, Ṣenakérúbù ọba Ásíríà kọlu gbogbo ìlú olódi ti Júdà ó sì pa wọ́n run."'), '2Kgs$18:14': (None, 'ni', '2Kgs$18:14 "Bẹ́ẹ̀ ni Heṣekáyà ọba Júdà rán oníṣẹ́ yìí sí ọba Áṣíríà ní Lákísì, “Mo ti mú ìṣe ohun tí kò dára kúrò lọ́dọ̀ mi, èmi yóò sì san ohunkóhun tí ìwọ bá béèrè lọ́wọ́ mi.” Ọba bu ọ̀ọ́dúnrún talẹ́ntì fàdákà àti ọgbọ̀n talẹ́ntì wúrà."'), '2Kgs$18:24': (None, None, '2Kgs$18:24 "Báwo ni ìwọ yóò ṣe le padà sẹ́yìn ọ̀kan lára àwọn oníṣẹ́, tí ó gbẹ̀yìn lára àwọn oníṣẹ́ ọ̀gá mi, tí ó bá tilẹ̀ jẹ́ pé; ẹ̀yin gbẹ́kẹ̀ yín lé Éjíbítì fún àwọn kẹ̀kẹ́ àti ẹlẹ́ṣin?"'), '2Kgs$18:31': ('jade', None, '2Kgs$18:31 "“Má ṣe tẹ́tí sí Héṣékíáyà. Èyí ni ohun tí ọba Ásíríà sọ: ‘Ṣe àlàáfíà pẹ̀lú mi kí o sì jáde wá sí ọ̀dọ̀ sí mi.’ Nígbà náà olúkúlùkù yín yóò jẹun láti inú àjàrà rẹ̀ àti igi ọ̀pọ̀tọ́, yóò sì mumi láti inú àmù rẹ̀,"'), '2Kgs$18:35': (None, 'ni', '2Kgs$18:35 Ta ni nínú gbogbo àwọn òrìṣà ilẹ̀ yìí tí ó ti gbìyànjú láti gba ilẹ̀ rẹ̀ kúrò lọ́wọ́ mi? Báwo ni OLÚWA yóò ṣe gba Jérúsálẹ́mù kúrò lọ́wọ́ mi?”'), '2Kgs$19:1': ('lọ', None, '2Kgs$19:1 "Nígbà tí ọba Ẹṣkáyà gbọ́ èyí, ó fa aṣọ rẹ̀ ya ó sì wọ aṣọ ọ̀fọ̀ ó sì lọ sí àgọ́ OLÚWA"'), '2Kgs$19:7': (None, 'pada', '2Kgs$19:7 "Gbọ́! Èmi yóò rán ẹ̀mí kan sínú rẹ̀ nígbà tí ó bá sì gbọ́ ariwo, yóò sì padà sí ìlú rẹ̀, níbẹ̀ ni èmi yóò sì gbé gée lulẹ̀ pẹ̀lú idà.’ ”"'), '2Kgs$19:11': (None, None, '2Kgs$19:11 "Lọ́ọ̀tọ́ ìwọ ti gbọ́ gbogbo ohun tí ọba Ásíríà tí ó ṣe sí gbogbo àwọn ìlú, ó pa wọ́n run pátapáta. Ìwọ yóò sì gbàlà?"'), '2Kgs$19:12': (None, 'ni', '2Kgs$19:12 "Ṣé àwọn òrìṣà orílẹ̀ èdè tí a ti parun láti ọ̀dọ̀ àwọn babańlá mi gbà wọ́n là: òrìṣà Gósánì, Háránì Réṣéfù àti gbogbo ènìyàn Édẹ́nì tí wọ́n wà ní Télásárì?"'), '2Kgs$19:16': ('gbo', None, '2Kgs$19:16 "Dẹtí sílẹ̀, OLÚWA kí o sì gbọ́; la ojú ù rẹ, OLÚWA, kí o sì rí i, gbọ́ ọ̀rọ̀ Ṣenakérúbù tí ó rán láti fi bú Ọlọ́run alààyè."'), '2Kgs$19:35': ('lọ', None, '2Kgs$19:35 "Ní alẹ́ ọjọ́ náà, ańgẹ́lì OLÚWA jáde lọ ó sì pa ọ̀kẹ́ mẹ́sàn-án lé ẹgbẹ̀ẹ́dọ́gbọ̀n ènìyàn ní ibùdó àwọn ará Aṣíríà. Nígbà tí wọ́n sì dìde dúró ní òwúrọ̀ ọjọ́ kejì níbẹ̀ ni gbogbo òkú wà!"'), '2Kgs$20:4': (None, None, '2Kgs$20:4 "Kí ó tó di wí pé Àìṣáyà jáde kúrò ní àárin àgbàlá ààfin, ọ̀rọ̀ OLÚWA wá sí ọ̀dọ̀ rẹ̀ pé."'), '2Kgs$20:8': ('ile', None, '2Kgs$20:8 "Heṣekáyà sì béèrè lọ́wọ́ Àìṣáyà pé, “Kí ni yóò jẹ́ àmìn pé OLÚWA yóò wò mí sàn àti wí pé èmi yóò lọ sókè sí ilé tí a kọ́ fún OLÚWA láti ọjọ́ kẹta títí di òní?”"'), '2Kgs$21:7': ('ṣe', 'ni', '2Kgs$21:7 "Ó sì gbé fínfín ère òrìṣà tí ó ti ṣe ó sì gbé e sínú ilé OLÚWA, èyí tí OLÚWA ti sọ fún Dáfídì àti sí ọmọ rẹ̀ Ṣólómónì, “Nínú ilé OLÚWA yìí àti ní Jérúsálẹ́mù, tí èmi ti yàn jáde lára gbogbo ẹ̀yà Ísírẹ́lì, èmi yóò kọ orúkọ mi títí láéláé."'), '2Kgs$21:14': (None, 'ni', '2Kgs$21:14 "Èmi yóò sì kọ ìyókù àwọn ìní mi sílẹ̀ èmi yóò sì kó wọn lé àwọn ọ̀ta wọn lọ́wọ́. Wọn yóò sì di ìkógun àti ìjẹ fún gbogbo àwọn ọ̀ta wọn,"'), '2Kgs$22:3': (None, 'ni', '2Kgs$22:3 " Ní ọdún kejìdínlógún tí ó fi jọba. Ọba Jòsáyà rán akọ̀wé, Ṣáfánì ọmọ Ásálíà, ọmọ Mésúlámù, sí ilé OLÚWA. Ó wí pé;"'), '2Kgs$22:9': (None, 'ni', '2Kgs$22:9 "Nígbà náà, Ṣáfánì akọ̀wé lọ sí ọ̀dọ̀ ọba. Ó sì sọ fún un pé: “Àwọn ìjòyè rẹ ti san owó náà tí ó wà nínú ilé OLÚWA. Èmi sì ti fi lé ọwọ́ àwọn tí ó ń ṣe iṣẹ́ náà àti àwọn alábojútó nílé OLÚWA.”"'), '2Kgs$22:15': (None, None, '2Kgs$22:15 "Ó wí fún wọn pé, “Èyí ni ohun tí OLÚWA Ọlọ́run Ísírẹ́lì wí: Sọ fún ọkùnrin tí ó rán an yín sími,"'), '2Kgs$22:18': (None, None, '2Kgs$22:18 "Sọ fún ọba Júdà tí ó rán yín láti bèrè lọ́wọ́ OLÚWA, ‘Èyí ni ohun tí OLÚWA Ọlọ́run Ísírẹ́lì wí nípa ọ̀rọ̀ wọ̀n-ọn-nì tí ìwọ ti gbọ́."'), '2Kgs$23:24': ('ṣe', 'ṣe', '2Kgs$23:24 "Síwájú sí, Jòsíáyà sì lé àwọn oṣó àti àwọn ẹ̀mí ní àwọn ìdílé, àti àwọn òrìṣà àti gbogbo àwọn nǹkan ìríra tí a rí ní Júdà àti ní Jérúsálẹ́mù. Èyí ni ó ṣe kí ó le è mú ọ̀rọ̀ òfin náà ṣe ní ti òfin tí a kọ sínú ìwé tí Hílíkíyà àlùfáà ti rí nínú ilé OLÚWA."'), '2Kgs$23:26': ('ibinu', 'ibinu', '2Kgs$23:26 "Bí ó ti wù kí ó rí OLÚWA kò yípadà kúrò nínú ìmúná ìbínú ńlá rẹ̀ tí ó jó sí Júdà, nítorí gbogbo èyí tí Mánásè ti ṣe láti mú un bínú."'), '2Kgs$23:34': ('ṣe', 'orukọ', '2Kgs$23:34 "Fáráò Nékò ṣe Élíákímù ọmọ Jòṣíàh ní ọba ní ipò baba rẹ̀ Jòsáyà. Ó sì yí orúkọ Élíákímù padà sí Jéhóíákímù. Ṣùgbọ́n ó mú Jéhóáhásì, ó sì gbéé lọ sí Éjíbítì, níbẹ̀ ni ó sì kú."'), '2Kgs$24:17': (None, 'orukọ', '2Kgs$24:17 "Ó sì mú Mátanáyà arákùnrin baba Jéhóíákínì, ọba ní ìlú rẹ̀ ó sì yí orúkọ rẹ̀ padà sí Ṣedekáyà."'), '2Kgs$25:11': ('eniyan', None, '2Kgs$25:11 "Nebukadinésárí olórí ẹ̀ṣọ́ Kó lọ sí ìgbékùn gbogbo ènìyàn tí ó kù ní ìlú, àti àwọn Ísánsà àti àwọn tí ó ti lo sí ọ̀dọ̀ ọba Bábílón."'), '2Kgs$25:18': ('mu', None, '2Kgs$25:18 "Olórí àwọn ọ̀sọ́ sì mú gẹ́gẹ́ bí olórí àwọn ẹlẹ́wọ̀n Ṣéráíáyà olórí àwọn àlùfáà, Ṣéfáníà àlùfáà ẹni tí ó kù nínú oyè gíga àti àwọn olùṣọ́nà mẹ́ta."'), '2Kgs$25:27': (None, None, '2Kgs$25:27 " Ní ọdún kẹtàdínlógójì tí a lé Jéhóíákínì ọba Júdà kúrò nílú, ní ọdún tí Efili-Méródákì di ọba Bábílónì, ó tú Jéhóíákínì kúrò nínú túbú ní ọjọ́ kẹtàdínlógbọ̀n oṣú kéjìlá."'), '1Chr$6:32': (None, None, '1Chr$6:32 Wọ́n jíṣẹ́ pẹ̀lú orin ńiwájú Àgọ́ ìpàdé títí tí Ṣólómónì fi kọ́ ilé OLÚWA ní Jérúsálẹ́mù. Wọ́n se iṣẹ́ ìsìn wọn ní ìbámu pẹ̀lú ìlànà tí a fi lélẹ̀ fún wọn.'), '1Chr$6:49': ('ṣe', 'iṣẹ', '1Chr$6:49 "Ṣùgbọ́n Árónì àti àwọn ìran ọmọ Rẹ̀ jẹ́ àwọn tí ó gbé ọrẹ kalẹ̀ lórí pẹpẹ ọrẹ sísun àti lórí pẹpẹ tùràrí ní ìbátan pẹ̀lú gbogbo ohun tí a se ní ibi mímọ́ jùlọ. Ṣíṣe ètùtù fún Ísírẹ́lì, ní ìbámu pẹ̀lú gbogbo ohun tí Mósè ìránṣẹ́ Ọlọ́run ti paláṣẹ."'), '1Chr$7:11': ('lọ', None, '1Chr$7:11 Gbogbo àwọn ọmọ Jédíádì jẹ́ olórí ìdátan Àwọn ẹgbẹ̀rúnmẹ́tadínlógún-ó-lé-nígba akọni ọkùnrin ni ó ti ṣetan láti jáde lọ sí ogun.'), '1Chr$7:40': (None, 'olori', '1Chr$7:40 "Gbogbo wọ̀nyí jẹ́ ìran ọmọ Áṣérì olórí ìdílé, àwọn okùnrin tí a yàn àwọn ògbóyà jagunjagun àti àwọn adarí tí ó dúró sinsin. Iye àwọn ọkùnrin tí ó setan fún ogun, gẹ́gẹ́ bí à ti se kọ ọ́ lẹ́sẹsẹ nínú ìtàn ìdílé wọn láti ọ̀dọ̀ baba ńlá wọn jẹ́ ẹgbàá mẹ́rìndínlọ́gbọ̀n."'), '1Chr$8:28': (None, 'olori', '1Chr$8:28 "Gbogbo àwọn wọ̀nyí jẹ́ olórí àwọn ìdílé, olóyè gẹ́gẹ́ bí a ti kọ ó lẹ́sẹsẹ nínú ìtàn ìdílé láti ọ̀dọ̀ baba ńlá wọn, wọ́n sì ń gbé ní Jérúsálẹ́mù. "'), '1Chr$9:27': (None, None, '1Chr$9:27 "Wọ́n á lo gbogbo òru ní dídúró yí ilé OLÚWA ká, nítorí wọ́n ní láti ṣọ́ ọ; Wọ́n sì ní àṣẹ sí kọ́kọ́rọ́ fún ṣíṣí i ní àràárọ̀."'), '1Chr$10:8': ('ṣubu', None, '1Chr$10:8 "Ní ọjọ́ kejì, Nígbà tí àwọn ará Fílístínì wá láti kó okú, wọ́n rí Ṣọ́ọ̀lù àti àwọn ọmọ Rẹ̀ tí wọ́n ṣubú sórí òkè Gílíbóà."'), '1Chr$10:9': (None, None, '1Chr$10:9 "Wọ́n bọ́ ọ láọ (Stripped), wọ́n sì gbé orí Rẹ̀ àti ìhámọ́ra Rẹ̀. Wọ́n sì rán ìránṣẹ́ lọ kákiri ilẹ̀ Àwọn ará Fílístínì láti kéde ìròyìn náà láàrin àwọn òrìṣà wọn àti àwọn ènìyàn wọn."'), '1Chr$11:10': ('ní', 'ṣe', '1Chr$11:10 " Àwọn wọ̀nyí sì ni ìjòyè alágbára ọkùnrin Dáfídì, àwọn pẹ̀lú gbogbo àwọn ọmọ Ísírẹ́lì, wọ́n sì fún ìjọba Rẹ̀ ní àtìlẹ́yìn tó lágbára láti mú un tàn káàkiri gbogbo ilẹ̀, gẹ́gẹ́ bí Olúwa ti sèlérí:"'), '1Chr$11:22': ('awọn ọkunrin', None, '1Chr$11:22 "Bẹ́náyà ọmọ Jéhóíádà ó sì jẹ́ akọni oníjà láti Kábísélì, ẹni tí ó se iṣẹ́ agbára ńlá. Ó sì pa méjì nínú àwọn ọkùnrin tí ó dára jù, ó sì tún sọ̀kalẹ̀ lọ sí inú ihò lákókò sno ó sì pa kìnnìún kan"'), '1Chr$12:8': ('asà', None, '1Chr$12:8 "Díẹ̀ lára àwọn ará Gádì yà sọ́dọ̀ Dáfídì ní ibi gíga ní ihà. Wọ́n sì jẹ́ onígboyà ológun tí ó múra fún ogun tí ó sì lágbára láti di àṣà àti ẹsin mú. Ojú wọn sì dàbí ojú kìnìún, wọ́n sì yára bí àgbọ̀nrín lórí àwọn òkè."'), '1Chr$12:17': (None, 'pade', '1Chr$12:17 "Dáfídì sì lọ láti lọ pàdé wọn ó sì wí fún wọn pé, “Tí ẹ bá wá sọ́dọ̀ mi lálàáfíà, láti ràn mí lọ́wọ́ mo sẹtan láti mú un yín wà ní ìṣọ̀kan pẹ̀lú mi ṣùgbọ́n tí ẹ bá wá láti dá mi sí fún àwọn ọ̀ta mi nígbà tí ọwọ́ mi bá mọ́ kúrò nínú ìwà agbára, kí Ọlọ́run àwọn baba wa kí ó sì ri kí ó sì ṣe ìdájọ́.”"'), '1Chr$12:19': (None, None, '1Chr$12:19 "Díẹ̀ nínú àwọn ọkùnrin Mánásè sì yà sí ọ̀dọ̀ Dáfídì nígbà tí ó lọ pẹ̀lú àwọn ará Fílístínì láti bá Ṣọ́ọ̀lù jà. (Òun àti àwọn ọkùnrin Rẹ̀ wọn kò sì ran ará Fílístínì lọ́wọ́ nítorí, lẹ́yìn àjùmọ̀sọ̀rọ̀, àwọn olórí wọn rán wọn jáde, wọ́n sì wí pé Yóò ná wa ní orí wa tí ó bá sì fi sílẹ̀ fún ọ̀gá Rẹ̀ Ṣọ́ọ̀lù)."'), '1Chr$12:23': (None, None, '1Chr$12:23 "Àwọn wọ̀nyí sì ni iye ọkùnrin tí ó ti di ìhámọ́ra fún ogun àwọn ẹni tí ó wá sọ́dọ̀ Dáfídì ní Hébúrónì láti yí ìjọba Dáfídì padà sí ọ̀dọ̀ Rẹ̀, gẹ́gẹ́ bí OLÚWA ti sọ:"'), '1Chr$12:31': ('ṣe', 'orukọ', '1Chr$12:31 "Àwọn ọkùnrin nínú ààbọ̀ ẹ̀yà Mánásè, tí a yàn nípa orúkọ láti wá àti láti yan Dáfídì gẹ́gẹ́ bí ọba-ẹgbàásàn (18,000). "'), '1Chr$13:2': (None, None, '1Chr$13:2 "Ó sì wí fún gbogbo àwọn ìjọ Ísírẹ́lì pé Tí ó bá dára lójú yín àti tí ó bá ṣe jẹ́ àṣẹ OLÚWA Ọlọ́run wa, jẹ́ kí a ránṣẹ́ sí ọ̀nà jínjìn àti gbígbòòrò sí àwọn arákùnrin wa tó kù ní gbogbo àwọn agbègbè ìlú Ísírẹ́lì àti pẹ̀lú àwọn àlùfáà àti àwọn ará Léfì àwọn tí ó wà pẹ̀lú wọn nínú ìlú wọn àti pápá oko tútù, láti wá kó ara wọn jọpọ̀ sọ́dọ̀ wa."'), '1Chr$13:13': ('ile', 'ile', '1Chr$13:13 "Kò gbé àpótí ẹ̀rí náà wá sí ọ̀dọ̀ ará Rẹ̀ ní ìlú ti Dáfídì dípò èyí, ó sì gbé e sí ẹ̀gbé, sí ilé Obedi-Édómù ará Gítì."'), '1Chr$13:14': ('ile', None, '1Chr$13:14 "Àpótí ẹ̀rí OLÚWA sì wà lọ́dọ̀ àwon ará ilé Obedi-Édómù ní ilé Rẹ̀ fún oṣù mẹ́ta, OLÚWA sì bùkún agbo ilé àti gbogbo ohun tí ó ní."'), '1Chr$14:15': ('lọ', None, '1Chr$14:15 "Tí ó bá sì ṣe, tí ẹ̀yin bá ti gbọ́ ìró yíyan ẹsẹ̀ ní orí òkè igi Bálísámù, ẹ jáde fún ogun, nítorí èyí yóò fi hàn pé Ọlọ́run ti jáde níwájú rẹ láti kọlu àwọn ọmọ ogun Fílístínì."'), '1Chr$15:16': ('ohun', 'ohun', '1Chr$15:16 "Dáfídì sọ fún àwọn olórí àwọn Léfì láti yan àwọn arákùnrin wọn gẹ́gẹ́ bí akọrin láti kọ orin ayọ̀, pẹ̀lú àwọn ohun-èlò orin pisalitérì, dùùrù, àti Ṣíḿbálì."'), '1Chr$15:27': ('aṣọ', 'aṣọ', '1Chr$15:27 "Nísinsìn yìí Dáfídì sì wọ aṣọ ìgúnwà ọ̀gbọ̀ dáradára, gẹ́gẹ́ bí akọrin, àti Kenaníyà, ẹnití ó wà ní ìkáwó orin kíkọ àwọn akọrin. Dáfídì sì wọ aṣọ ìgúnwà fun fun."'), '1Chr$16:2': (None, 'orukọ', '1Chr$16:2 "Lẹ́yìn ọrẹ sísun àti ọrẹ ìdàpọ̀, ó bùkún àwọn ènìyàn ní orúkọ OLÚWA."'), '1Chr$16:3': (None, None, '1Chr$16:3 "Nígbà náà ó fún olúkúlùkù ọkùnrin àti obìnrin ọmọ Ísírẹ́lí ní ìṣù àkàrà kan, àkàrà dídùn ti àkókò kan àti àkàrà dídùn ti èṣo àjàrà kan."'), '1Chr$16:8': ('ṣe', 'orukọ', '1Chr$16:8 " Ẹ fi ọpẹ́ fún OLÚWA, ẹ pe orúkọ Rẹ̀, ẹ fi iṣẹ́ Rẹ̀ hàn nínú àwọn ènìyàn fún ohun tí ó ṣe"'), '1Chr$16:9': ('kọrin', None, '1Chr$16:9 "Ẹ kọrin síi, ẹ kọrin ìyìn, síi, Ẹ sọ ti gbogbo iṣẹ́ ìyanu Rẹ̀"'), '1Chr$16:15': ('ṣe', 'fun', '1Chr$16:15 "Ó ránti májẹ̀mú rẹ́ títí láé, ọ̀rọ̀ tí Ó pa láṣẹ fún ẹgbẹ̀rún ìran,"'), '1Chr$16:30': (None, 'ayé', '1Chr$16:30 "Wárìrì níwájú Rẹ̀, gbogbo ayé! Ayé sì fi ìdímúlẹ̀; a kò sì le è síi."'), '1Chr$16:37': (None, None, '1Chr$16:37 "Dáfídì fi Áṣáfù àti àwọn ẹlẹgbẹ́ Rẹ̀ sílẹ̀ níwájú àpótí ẹ̀rí májẹ̀mú OLÚWA láti jísẹ́ níbẹ̀ déédé, gẹ́gẹ́ bí ọjọ́ kọ̀ọ̀kan ṣe gbà."'), '1Chr$17:3': (None, None, '1Chr$17:3 "Ní àsálẹ́ ọjọ́ náà, ọ̀rọ̀ Ọlọ́run tọ Nátanì wá, wí pé:"'), '1Chr$17:7': ('ṣe', 'ni', '1Chr$17:7 "“Nígbà náà, sọ fún ìránṣẹ́ mi Dáfídì pé, ‘Èyí ni ohun tí OLÚWA alágbára wí: Èmi mú un yín láti pápá oko tútù wá, àní kúrò lẹ́yìn agbo àgùntàn, kí ìwọ lè máa ṣe olórí àwọn ènìyàn mi Ísírẹ́lì."'), '1Chr$17:11': (None, None, '1Chr$17:11 "Nígbà ti ọjọ́ rẹ bá kọjá, tí ìwọ bá lọ láti lọ bá àwọn baba à rẹ, Èmi yóò gbé àwọn ọmọ rẹ sókè láti rọ́pò rẹ, ọ̀kan lára àwọn ọmọ tìrẹ, Èmi yóò sì fìdí ìjọba Rẹ̀ kalẹ̀."'), '1Chr$17:21': ('ṣe', 'orukọ', '1Chr$17:21 "Pẹ̀lú ta ni ó dà bí àwọn ènìyàn rẹ Ísírẹ́lì-orílẹ̀ èdè kan ní ayé, tí Ọlọ́run jáde lọ láti ra àwọn ènìyàn kan padà fún ara Rẹ̀, àti láti lè ṣe orúkọ fún ara à rẹ, àti lati ṣe ohun ńlá àti ọwọ́ ìyanu nípa lílé àwọn orílẹ̀ èdè kúrò níwáju àwọn ènìyàn Rẹ̀, ẹni tí ó gbà là láti Éjíbítì?"'), '1Chr$19:13': ('ṣe', None, '1Chr$19:13 Jẹ́ alágbára kí ẹ sì jẹ́ kí a jà pẹ̀lú ìgboyà fún àwọn ènìyàn wa àti àwọn ìlú ńlá ti Ọlọ́run wa. OLÚWA yóò ṣe ohun tí ó dára ní ojú Rẹ̀.'), '1Chr$20:1': ('mu', None, '1Chr$20:1 " Ní àkókò àkọ́rọ̀ òjò, ni ìgbà tí àwọn ọba lọ sí ogun, Jóábù ṣamọ̀nà àwọn ọ̀wọ́ ọmọ ogun. Ó fi ilẹ̀ àwọn ará Ámónì ṣòfò. Ó lọ sí ọ̀dọ̀ Rábà. Ó sì fi ogun dúró tì í. Ṣùgbọ́n Dáfídì dúró sí Jérúsálẹ́mù Jóábù kọlu Rábà, ó sì fi sílẹ̀ ní ìparun."'), '1Chr$21:21': ('wò', 'oju', '1Chr$21:21 "Nígbà náà Dáfídì sì súnmọ́, Nígbà tí Órínánì sì wò tí ó sì rí, ó sì kúrò ní ilẹ̀ ìpakà ó sì doju bolẹ̀ níwájú Dáfídì pẹ̀lú ojú Rẹ̀ ní ilẹ̀."'), '1Chr$21:24': ('ni', 'iye owo', '1Chr$21:24 "Ṣùgbọ́n ọba Dáfídì dá Órínánì lóhùn pé, “Rárá, mo wà lórí àti san iye owó rẹ ní pípé, èmi kò sì ní mú fún OLÚWA èyí tí ó jẹ́ tìrẹ, tàbí láti rú ẹbọ ọrẹ sísun tí kò ná mi ní ohun kan.”"'), '1Chr$22:7': (None, 'kọ', '1Chr$22:7 " Dáfídì sì wí fún Sólómónì pé, “Ọmọ mi, mo sì ní-in ní ọkàn mi láti kọ́ ilé fún orúkọ OLÚWA Ọlọ́run mi."'), '1Chr$22:10': (None, 'kọ', '1Chr$22:10 "Òun ni ẹni náà tí yóò kọ́ ilé fún orúkọ mi. Yóò sì jẹ́ ọmọ mi, èmi yóò sì jẹ́ baba Rẹ̀ èmi yóò sì fi ìdi ìtẹ́ ìjọba Rẹ̀ múlẹ̀ lórí Ísírẹ́lì láéláé.’"'), '1Chr$23:3': (None, 'ọdun', '1Chr$23:3 "Àwọn ọmọ Lefì lati ọgbọ̀n ọdún tàbí jù bẹ́ẹ̀ lọ ni wọ́n kà àpapọ̀ iye àwọn ọkùnrin wọn sì jẹ́ ẹgbàá mọ́kàndínlógún (38,000)"'), '1Chr$23:4': ('ṣe', 'iṣẹ', '1Chr$23:4 "Dáfídì sì wí pe, Níti èyí, ẹgbàáméjìlá (24,000) ni kí wọn jẹ́ alábojútó iṣẹ́ ilé fún OLÚWA àti ẹgbàáta (6,000) ni kí ó ṣe olórí àti onídàájọ́."'), '1Chr$23:13': ('bukun', None, '1Chr$23:13 " Àwọn ọmọ Ámírámù. Árónì àti Móṣè. A sì ya Árónì sọ́tọ̀ òun àti àwọn ọmọ Rẹ̀ títí láéláé, láti kọjú sí ohun mímọ́ jùlọ, láti fi rúbọ sísun níwájú OLÚWA, láti máa ṣe òjísẹ́ níwájú Rẹ̀ àti láti kéde ìbùkún ní orúkọ Rẹ̀ títí láéláé."'), '1Chr$23:24': (None, 'ọdun', '1Chr$23:24 "Àwọn wọ̀nyí sì ni ọmọ Léfì bí ìdílé wọn. Olórí ìdílé gẹ́gẹ́ bí wọ́n ti fi orúkọ sílẹ̀ lábẹ́ orúkọ wọn, ó sì kà wọn ní ẹyọ kọ̀ọ̀kan, wí pé, àwọn òṣìṣẹ́ tí ó jẹ́ ogún ọdún tàbí jù bẹ́ẹ̀ lọ tí ń ṣiṣẹ́ ìsìn nínú ilé OLÚWA"'), '1Chr$23:27': (None, None, '1Chr$23:27 "Gẹ́gẹ́ bí ọ̀rọ̀ ìkẹyìn Dáfídì sí, àwọn ọmọ Léfì, wọ́n sì kà wọ́n, bẹ̀rẹ̀ láti orí àwọn ọmọ ogún ọdún tàbí jù bẹ́ẹ̀ lọ."'), '1Chr$27:1': ('wá', None, '1Chr$27:1 "Wọ̀nyí ni ìwé àkójọ orúkọ àwọn ọmọ Ísírẹ́lì, àwọn olórí àwọn ìdílé, àwọn balógun ẹgbẹgbẹ̀rún àti àwọn balógun ọrọrún àti àwọn ìjòyè wọn. Tí ó n sin ọba nínú gbogbo ohun tí ó kan ìpín àwọn ọmọ ogun tí ó wà lẹ́nu iṣẹ́ ìsìn ní oṣoṣù ní gbogbo ọdún. Ìpín kọ̀ọ̀kan jẹ́ ẹgbẹ̀rún mẹ́rin ólé ẹgbàá mẹ́rin (24,000) ọkùnrin."'), '1Chr$28:2': ('ṣe', None, '1Chr$28:2 "Ọba Dáfídì dìde dúró ní ẹṣẹ̀ Rẹ̀, o sì wí pé Fetísílẹ̀ sí mi, ẹyin ará mi àti ẹ̀yin ènìyàn mi. Èmi ní o ni ọkàn mi láti kọ́ ilé gẹ́gẹ́ bí ibi ìsinmi fún àpótí ẹ̀rí májẹ̀mú tí OLÚWA fún àpótí ìtìsẹ̀ Ọlọ́run wa, èmi sì gbèrò láti kọ́ ọ."'), '1Chr$28:4': (None, 'ṣe', '1Chr$28:4 "“Síbẹ̀ OLÚWA, Ọlọ́run Ísírẹ́lì yàn mí láti gbogbo ìdílé láti jẹ́ ọba lórí Ísírẹ́lì, títí láé. Ó yan Júdà gẹ́gẹ́ bí olórí, àti láti ilé Júdà, ó yan ìdílé mi, àti láti ọ̀dọ̀ àwọn ọmọ baba à mí, ó tẹ́ Ẹ lọ́rùn láti fi mí ṣe ọba lórí gbogbo Ísírẹ́lì."'), '1Chr$28:11': ('ibi', None, '1Chr$28:11 "Nígbà náà, Dáfídì fún Sólómónì ọmọ Rẹ̀ ní ètò fún èbúté ilé OLÚWA náà, kíkọ́ ọ Rẹ̀, àwọn yàrá ìṣúra Rẹ̀, ibi òkè Rẹ̀, àwọn ìyẹ̀wù Rẹ̀ àti ibi ìpètù sí Rẹ̀."'), '1Chr$29:2': ('ṣe', 'ni', '1Chr$29:2 "Pẹ̀lu gbogbo ìrànlọ́wọ́ mi èmi ti pèsè fún ilé Ọlọ́run mi wúrà fún iṣẹ́ wúrà, fàdákà fún ti fàdákà, òjíá fún ti òjíá, irin fún ti irin àti igi fún ti igi àti òkúta oníyebíye fún títọ́ Rẹ̀, (túríkúóṣè) òkúta lóríṣíríṣí, àmọ̀ àti oríṣìíríṣìí ẹ̀yà òkúta àti òkuta dáradára kan gbogbo wọ̀nyí ní iye púpọ̀."'), '1Chr$29:3': ('ni', None, '1Chr$29:3 "Yàtọ̀ fún èyí, nínú ìfọkànsìn mi sí ilé Ọlọ́run mi, èmí fi ìṣúra mi tìkalára mí ti wúrà àti fàdákà fún ilé Ọlọ́run mi, jù gbogbo Rẹ̀ lọ, èmi ti pèṣè fún ilé mímọ́ ti OLÚWA yìí:"'), '1Chr$29:4': (None, None, '1Chr$29:4 "Ẹgbẹ̀rún mẹ́ta (3,000) talẹ́ntì wúrà (wúrà ti ofírì) àti ẹgbẹ̀rin méje tálẹ́ntì fàdákà tí a yọ̀rọ̀ kúrò, fún bíbo àwọn ògiri ilé náà."'), '1Chr$29:8': (None, None, '1Chr$29:8 Ẹnikẹ́ni tí ó ní òkúta iyebíye fi wọn sí ilé ìṣúra ilé Ọlọ́run ní ìhámọ́ Jéhíélì ará Géríṣónì.'), '1Chr$29:12': ('fun', 'ọwọ', '1Chr$29:12 Ọlá àti ọ̀wọ̀ wá láti ọ̀dọ̀ Rẹ; ìwọ ni alákòóso gbogbo nǹkan. Ní ọwọ́ rẹ ni ipá àti agbára wà láti gbéga àti láti fi agbára fún ohun gbogbo.'), '1Chr$29:16': (None, 'tirẹ', '1Chr$29:16 "OLÚWA Ọlọ́run wa tí fi gbogbo pípọ̀ yìí tí àwa ti pèṣè fún kíkọ́ ilé OLÚWA fun orúkọ, mímọ́ Rẹ̀, ó wá láti ọwọ́ ọ̀ rẹ, gbogbo Rẹ̀ sì jẹ́ tirẹ̀."'), '1Chr$29:18': (None, 'ni', '1Chr$29:18 "OLÚWA Ọlọ́run àwọn baba a wa Ábúráhámù, Ísákì àti Ísírẹ́lì pa ìfẹ́ yìí mọ́ ninú ọkàn àwọn ènìyàn rẹ títí láé pẹ̀lú pípa ọkàn mọ́ lóòtọ́ sí ọ."'), '1Chr$29:28': (None, None, '1Chr$29:28 "Òun sì darúgbó, ó sì kú ikú rere, ó gbádùn ẹ̀mí gígùn, ọlá àti ọrọ̀. Sólómónì ọmọ Rẹ̀ sì jẹ ọba ní ipò Rẹ̀"'), '1Chr$29:29': (None, None, '1Chr$29:29 "Ní ti ohun tí ó ṣẹlẹ̀ nígbà ìjọba ọba Dáfídì, láti ìbẹ̀rẹ̀ dé òpin, a kọ wọ́n sínú ìwe ìrántí ti Sámúẹ́lì aríran, ìwé ìrántí ti Nátanì wòlíì àti ìwé ìrántí ti Gádì aríran,"'), '2Chr$1:9': ('eniyan', None, '2Chr$1:9 "Nísinsinyìí OLÚWA Ọlọ́run, jẹ́ kí ìlérí rẹ sí baba à mi Dáfídì di mímúsẹ, nítorí ìwọ ti fi mí jẹ ọba lóri àwọn ènìyàn tí ó pọ̀ bí erùpẹ̀ ilẹ̀."'), '2Chr$1:11': ('ọrọ', 'ọrọ', '2Chr$1:11 "Ọlọ́run wí fún Solómónì pé, “Níwọ̀n ìgbà tí ó jẹ́ pé ìfẹ́ ọkàn rẹ nìyí, tí ìwọ kò sì béèrè, fún ọrọ̀, ọlá tàbí ọ̀wọ̀, tàbí fún ikú àwọn ọ̀ta rẹ, àti nígbà tí ìwọ kò ti béèrè fún ẹ̀mí gígùn, ṣùgbọ́n tí ìwọ béèrè fún ọgbọ́n àti ìmọ̀ láti ṣàkóso àwọn ènìyàn mi tí èmi fi ọ́ jẹ ọba lé lórí,"'), '2Chr$2:7': (None, None, '2Chr$2:7 "“Nítorí náà, rán ọkùnrin kan sí mi, tí a kọ́ láti ṣiṣẹ́ pẹ̀lú wúrà àti fàdákà àti idẹ àti irin, àti ní àwọ̀ àlùkò àti àwọ̀ pupa fòò àti ní awọ̀ oju ọ̀run, tí ó jẹ́ onímọ̀ nínú isẹ́ igi gbígbẹ́ láti ṣiṣẹ́ ní Júdà àti Jérúsálẹ́mù pẹ̀lú àwọn onímọ̀ oníṣọ̀nà tí Baba à mi Dáfídì pèsè."'), '2Chr$2:12': (None, 'kọ', '2Chr$2:12 "Hírámì fi kún un pe: “Ìyìn ni fún OLÚWA, Ọlọ́run Ísírẹ́lì, tí ó dá ọ̀run òun ayé! O ti fún ọba Dáfídì ní ọmọ tí ó gbọ́n, tí ó kún fún ìmọ̀ àti àkíyèsí, tí yóò kọ́ ilé fún OLÚWA àti ààfin fún ara rẹ̀."'), '2Chr$2:13': (None, None, '2Chr$2:13 "“Èmi ń rán Húrámì-Abi, sí ọ ọkùnrin tí ó kún fún ìmọ̀ ńlá,"'), '2Chr$3:10': (None, None, '2Chr$3:10 "Ní ibi mímọ́ jùlọ, ógbẹ́ àwòrán igi kérúbù kan, ó sì tẹ́ wọn pẹ̀lú wúrà."'), '2Chr$4:7': ('ṣe', None, '2Chr$4:7 Ó ṣe ìgbe fìtílà dúró mẹ́wàá ti wúrà gẹ́gẹ́ bí èyí tí a yàn fún wọn. A sì gbé wọn kalẹ̀ sínú ilé OLÚWA márùn un ní ìhà gúsù àti márùn ún ní ìhà àríwá.'), '2Chr$4:19': (None, None, '2Chr$4:19 Sólómónì pẹ̀lú ṣe gbogbo ohun èlò tí ó wà ní ilé Ọlọ́run: pẹpẹ wúrà tábìlì èyí tí àkàrà ìfihàn wà lórí rẹ̀;'), '2Chr$5:1': (None, 'iṣẹ', '2Chr$5:1 "Nígbà tí gbogbo iṣẹ́ tí Sólómónì ti ṣe fún tẹ́ḿpìlì ti parí, ó kó gbogbo àwọn nǹkan tí Dáfìdì baba rẹ̀ ti yà sọ́tọ̀: wúrà àti fàdákà àti gbogbo ohun èlò ọ̀sọ́, ó sì fi wọ́n sínú ìsúra ilé Ọlọ́run."'), '2Chr$5:2': ('awọn olori', 'olori', '2Chr$5:2 "Nígbà náà ni Sólómónì pe àwọn àgbààgbà Ísírẹ́lì jọ sí Jérúsálẹ́mù, gbogbo àwọn olórí ẹ̀yà kọ̀ọ̀kan àti àwọn olóyè àwọn ìdílé Ísírẹ́lì, kí wọn kí ó gbé àpótí ẹ̀rí Májẹ̀mu OLÚWA gòkè wá láti síónì ìlú Dáfídì."'), '2Chr$5:11': ('ní', None, '2Chr$5:11 Lẹ́yìn náà ni àwọn àlùfáà jáde wá láti ibi mímọ́. Gbogbo àwọn àlùfáà tí ó wà níbẹ̀ ni ó ya ara wọn sí mímọ́ láìbìkítà fún ìpín wọn.'), '2Chr$6:2': (None, None, '2Chr$6:2 ṣùgbọ́n èmi ti kọ́ tẹ́ḿpìlì dáradára fún ọ ibi tí ìwọ yóò máa gbé títí láé.”'), '2Chr$6:4': ('mu', None, '2Chr$6:4 "Nígbà náà ni ó sì wí pé: “Ògo ni fún OLÚWA, Ọlọ́run Ísírẹ́lì, tí ó fi ọwọ́ rẹ̀ mú ohun tí ó fi ẹnu rẹ̀ sọ fún Dáfídì baba mi ṣẹ. Nitorí tí ó wí pé,"'), '2Chr$6:11': (None, 'ni', '2Chr$6:11 "Níbẹ̀ ni èmi sì gbé àpótí ẹ̀rí OLÚWA sí, nínú èyí ti májẹ̀mú ti OLÚWA bá àwọn Ísirẹ́lì ènìyàn mi dá wà.”"'), '2Chr$6:19': ('ebe', 'adura', '2Chr$6:19 "Síbẹ̀ ṣe àfiyèsí àdúrà ìránṣẹ́ rẹ àti ẹ̀bẹ̀ rẹ̀ fún àánú, OLÚWA Ọlọ́run mi, gbọ́ ẹkún àti àdúrà tí ìránṣẹ́ rẹ̀ ń gbà níwájú rẹ."'), '2Chr$6:22': ('ṣe', 'wá', '2Chr$6:22 "“Nígbà tí ọkùnrin kan bá sì ṣe aburú sí aládùúgbò rẹ̀ tí ó sì gbà kí ó ṣe ìbúra tí ó sì wá tí o sì búra níwájú pẹpẹ rẹ nínú ilé OLÚWA yìí,"'), '2Chr$6:24': ('ṣe', 'ni', '2Chr$6:24 "“Nígbà tí àwọn ènìyàn rẹ Ísírẹ́lì bá ní ìjákulẹ̀ níwájú àwọn ọ̀tá nítorí wọ́n ti dẹ́sẹ̀ sí ọ àti nígbà tí wọ́n bá sì yípadà, tí wọ́n sì jẹ́wọ́ orúkọ rẹ, tí wọ́n gbàdúrà, tí wọ́n sì bẹ̀bẹ̀ níwájú rẹ nínú ilé OLÚWA yìí,"'), '2Chr$6:26': (None, 'ni', '2Chr$6:26 "“Nígbà tí a bá ti ọ̀run tí kò sì sí òjò nítorí àwọn ènìyàn rẹ ti dẹ́sẹ̀ sí ọ, Nígbà tí wọ́n bá sì gbàdúrà sí ibí yìí tí wọ́n sì jẹ́wọ́ orúkọ rẹ tí wọ́n sì yípadà kúrò nínú ẹ̀ṣẹ̀ wọn nítorí ìwọ ti pọ́n wọn lójú,"'), '2Chr$6:28': (None, None, '2Chr$6:28 "“Nígbà tí ìyàn tàbí àjàkálẹ̀-àrùn bá wá sí ilẹ̀ náà, tàbí ìrẹ̀dànù tàbí ìmúwòdù, irúgbà tàbí ẹlẹ́ǹgà, tàbí nígbà tí àwọn ọ̀tá wọn bá yọ wọ́n lẹ́nu nínú ọ̀kan lára àwọn ìlú wọn, ohunkóhun, ìpọnjú tàbí àrùn lè wá,"'), '2Chr$6:32': ('wá', None, '2Chr$6:32 "“Ní ti àwọn àlejò tí wọn kò sí lára àwọn ènìyàn rẹ Ísírẹ́lì ṣùgbọ́n tí wọ́n wá láti ọ̀nà jínjìn nítorí orúkọ ńlá rẹ àti ọwọ́ agbára rẹ àti nínà apá rẹ, nígbà tí ó bá wá tí ó sì gbàdúrà lórí ilé OLÚWA yìí."'), '2Chr$6:34': (None, 'adura', '2Chr$6:34 "“Nígbà tí àwọn ènìyàn rẹ bá lọ sójú ogun lórí àwọn ọ̀ta wọn, nígbàkígbà tí o bá rán wọn, tí wọ́n sì gbàdúrà sí ọ lórí ìlú yìí tí ìwọ ti yàn àti ilé OLÚWA tí èmi ti kọ́ fún orúkọ rẹ."'), '2Chr$6:36': (None, None, '2Chr$6:36 "“Nígbà tí wọ́n bá sì dẹ́sẹ̀ sí ọ-nítorí kò sí ènìyàn kan tí kò dẹ́sẹ̀-bí o bá sì bínú sí wọn tí o bá sì fi wọ́n fún àwọn ọ̀tá, tí wọ́n kó wọn ní ìgbèkùn sí ilẹ̀ tí ó jìnnà réré tàbí nítòsí,"'), '2Chr$7:7': ('ní', 'gba', '2Chr$7:7 "Sólómónì sì yà sọ́tọ̀ láàrin àgbàlá níwájú ilé OLÚWA, níbẹ̀ sì ni ó ti se ìrúbọ ọrẹ sísun àti ọ̀rá ẹbọ àlàáfíà, nítorí pẹpẹ idẹ tí ó ti ṣe kò lè gba ọrẹ sísun, àti ọrẹ oúnjẹ, àti ọ̀rá náà."'), '2Chr$7:16': (None, None, '2Chr$7:16 "Èmi sì ti yàn, èmi sì ti ya ilé yìí sí mímọ́ bẹ́ẹ̀ ni kí orúkọ mi kí ó le wà níbẹ̀ títí láéláé."'), '2Chr$7:19': ('ṣe', None, '2Chr$7:19 "“Ṣùgbọ́n tí ìwọ bá yípadà tí o sì kọ òfin mi sílẹ̀ àti àṣẹ tí mo ti fi fún yín tí ẹ sì lọ sókè láti lọ sin Ọlọ́run mìíràn tí ẹ sì bọ wọ́n,"'), '2Chr$7:21': (None, None, '2Chr$7:21 "Àti ní gbogbo àyíká ilé yìí nísinsinyí, bẹ́ẹ̀ ni yóò di ohun ìtànjẹ; gbogbo àwọn tí ó bá sì kọjá níbẹ̀ ni yóò jáláyà, wọn yóò sì wí pé, ‘Kí ni ó dé tí OLÚWA fì ṣe irú èyí sí ilẹ̀ yí àti sí ilé yìí?’"'), '2Chr$7:22': ('mu', 'ilẹ', '2Chr$7:22 "Àwọn ènìyàn yóò sì dáhùn wí pé: ‘Nítorí tí wọ́n ti kọ̀ OLÚWA sílẹ̀, Ọlọ́run baba wọn ẹni tí ó mú wọn jáde láti Éjíbítì wá, wọ́n sì ti fi ọwọ́ gba Ọlọ́run mìíràn mọ́ra, wọ́n bọ wọ́n, bẹ́ẹ̀ ni wọ́n sì ń sìn wọ́n, ìdí nìyí tí ó fi mú gbogbo ìjàǹbá náà wá sórí wọn.’ ”"'), '2Chr$8:1': ('ní', None, '2Chr$8:1 Lẹ́yìn ogún ọdún lásìkò ìgbà tí Sólómónì kọ́ ilé OLÚWA àti ilé òun fúnrarẹ̀.'), '2Chr$8:9': (None, None, '2Chr$8:9 "Ṣùgbọ́n Solómónì kò mú ọmọ ọ̀dọ̀ lára àwọn ọmọ Ísírẹ́lì fún Ísírẹ́lì, wọn jẹ́ ọ̀gágun rẹ̀, olùdarí àwọn oníkẹ̀kẹ́ àti olùdarí kẹ̀kẹ́."'), '2Chr$9:5': ('ní', None, '2Chr$9:5 "Ó sì wí fún ọba pé, “Ìròyìn tí mo gbọ́ ní ìlú mi nípa iṣẹ́ rẹ àti ọgbọ́n rẹ, òtítọ́ ni."'), '2Chr$9:8': ('ṣe', None, '2Chr$9:8 "Ìyìn lóyẹ fún OLÚWA Ọlọ́run rẹ, ẹni tí ó ní inú dídùn nínú rẹ tí ó sì gbé ọ ka orí ìtẹ́ ìjọba rẹ̀ láti jẹ ọba fún OLÚWA Ọlọ́run rẹ. Nítorí ìfẹ́ Ọlọ́run rẹ fún Ísírẹ́lì láti fi ìdí wọn kalẹ̀ láéláé, ó sì ti fi ọ́ ṣe ọba lórí wọn, láti ṣe ìdájọ́ àti òtítọ́.”"'), '2Chr$9:9': ('ayaba', 'ọba', '2Chr$9:9 "Nígbà náà ni ó sì fún ọba ní ọgọ́fà talẹ́ntì wúrà (120). Ọ̀pọ̀lọpọ̀ iye tùràrí, àti òkúta iyebíye. Kò sì tíì sí irú tùràrí yí gẹ́gẹ́ bí èyí tí ayaba Ṣébà fifún ọba Sólómónì."'), '2Chr$9:12': ('ayaba', None, '2Chr$9:12 Ọba Sólómónì fún ayaba Ṣébà ní gbogbo ohun tí ó bèèrè fún àti ohun tí ó wù ú; ó sì fi fún un ju èyí tí ó mú wá fún un lọ. Nígbà náà ni ó lọ ó sì padà pẹ̀lú àwọn ọmọ-ẹ̀yìn rẹ̀ sí ìlú rẹ̀.'), '2Chr$9:29': (None, 'woli', '2Chr$9:29 "Àti gẹ́gẹ́ bí ìyókù iṣẹ́ ìjọba Sólómónì, láti ìbẹ̀rẹ̀ títí dé òpin, ṣé a kò kọ wọ́n sínú ìwé ìtàn Nátanì wòlíì, àti nínú ìsọtẹ́lẹ̀ Áhíjà ará Sílónì àti nínú ìran Ídò, wòlíì tí o kan Jéróbámù ọmọ Nébátì?"'), '2Chr$10:2': ('pada', None, '2Chr$10:2 "Nígbà tí Jéróbóámù ọmọ Nébátì gbọ́ èyí ó wà ní Éjíbítì, níbi tí ó ti sá kúrò lọ́dọ̀ ọba Sólómónì ó sì padà láti Éjíbítì."'), '2Chr$10:6': (None, None, '2Chr$10:6 "Nígbà náà ni ọba Réhóbóámù fi ọ̀ràn lọ̀ àwọn àgbààgbà tí ó ti ń sin Baba rẹ̀ Sólómónì nígbà ayé rẹ̀, ó sì bi wọ́n léèrè pe “Báwo ni ẹ̀yin yóò ṣe gbà mí ní ìmọ̀ràn láti dá àwọn ènìyàn wọ̀nyí lóhùn?”."'), '2Chr$10:9': ('fun', None, '2Chr$10:9 "Ó sì bèrè lọ́wọ́ wọn pé, “Kí ni ìmọ̀ràn yín? Báwo ni kí a ṣe dá àwọn ènìyàn wọ̀nyí tí wọ́n wí fún mi pé, ‘Mú kí àjàgà tí baba rẹ gbé lé wa kí ó fúyẹ́ díẹ̀.’ ”"'), '2Chr$11:14': (None, None, '2Chr$11:14 "Àwọn ará Léfì fi ìgbéríko sílẹ̀, wọ́n sì wá sí Júdà àti Jérúsálẹ́mù nítorí Jéróbóamù àti àwọn ọmọ rẹ̀, ti kọ̀ wọ́n sílẹ̀ gẹ́gẹ́ bí àwọn àlùfáà OLÚWA."'), '2Chr$12:2': (None, None, '2Chr$12:2 Nítorí tí wọn kò ṣọ̀ọ́tọ́ sí OLÚWA. Ṣíṣákì ọba Ị́jíbítì dojúkọ Jérúsálẹ́mù ní ọdún karùnún ti ọba Réhóbóamù'), '2Chr$12:15': ('aríran', 'woli', '2Chr$12:15 "Fún tí iṣẹ́ ìjọba Réhóbóámù láti ìbẹ̀rẹ̀ dé ìparí, ṣé a kò kọ wọ́n sínú ìwé ìrantí ti Ṣémáíà wòlíì àti ti Idò, aríran tí ó ní ṣe pẹ̀lú ìtàn ìdílé láti ọ̀dọ̀ àwọn baba ńlá? Ìtẹ̀ṣíwájú ogun jíjà sì wà láàárin Réhóbóámù àti Jéróbóámù."'), '2Chr$13:9': ('ṣe', 'ni', '2Chr$13:9 "Ṣùgbọ́n ṣé ìwọ kò lé àwọn àlùfáà Ọlọ́run jáde, àwọn ọmọ Árónì àti àwọn ará Léfì. Tí ó sì se àwọn àlùfáà ti ara rẹ̀ gẹ́gẹ́ bí àwọn ènìyàn ilẹ̀ mìíràn ti ṣe? Ẹnikẹ́ni tí ó bá wá láti ya ara rẹ̀ sọ́tọ̀ pẹ̀lú ọ̀dọ́ akọ màlúù àti àgbò méjì lè jẹ́ àlùfáà èyí tí kì í ṣe àwọn Ọlọ́run."'), '2Chr$13:15': (None, None, '2Chr$13:15 "Olúkúlùkù, ọkùnrin Júdà sì hó: ó sì ṣe, bí àwọn ọkùnrin Júdà sì ti hó, ni Ọlọ́run kọlu Jéróbóámù àti gbogbo Ísírẹ́lì níwájú Ábíjà àti Júdà."'), '2Chr$14:2': ('dara', None, '2Chr$14:2 "Ásà ṣe ohun tí ó dára, tí ó sì tọ́ ní ojú OLÚWA Ọlọ́run rẹ̀."'), '2Chr$14:7': ('ni', 'ṣe', '2Chr$14:7 "“Ẹ jẹ́ kí a kọ́ àwọn ìlú wọ̀nyí” Ó wí fún Júdà, “Kí ẹ sì mọ odi yí wọn ká pẹ̀lú àwọn ile ìṣọ́ gíga, àwọn ẹnu ọ̀nà òde, àti àwọn ìpẹ́ẹ̀rẹ̀. Ilé náà ti wà, nítorí a ti bèrè lọ́wọ́ OLÚWA Ọlọ́run wa; a béèrè lọ́wọ́ rẹ̀, ó sì ti fún wa ní ìsinmi ní gbogbo ọ̀nà.” Bẹ́ẹ̀ ni wọ́n kọ́ ọ, wọn sì ṣe rere."'), '2Chr$15:16': ('jó', 'ge', '2Chr$15:16 " Ọba Ásà rọ ìyá ńlá rẹ̀ Mákà lóyè láti ipò rẹ̀ gẹ́gẹ́ bí ìyá ọba nítorí tí ó ti ṣe òpó Áṣérà tí ń lé ni sá. Ásà gé òpó náà lulẹ̀, wòó, ó sì jóo ní pẹ̀tẹ́lẹ̀ Kídírónì."'), '2Chr$16:2': (None, None, '2Chr$16:2 "Nígbà náà ni Ásà mú wúrà àti fàdákà jáde ninú ilé ìsúra ilé OLÚWA àti ààfin ọba ó sì ránsẹ́ sí Bẹni-Hádádì ọba Árámù, ẹni tí ń gbé ní Dámásíkù, ó wí pé,"'), '2Chr$16:9': ('oju', 'okan', '2Chr$16:9 "Nítorí ojú OLÚWA yí gbogbo ayé ká láti fi agbára fún àwọn ẹni tí ó ní ọkàn pípé sí i. Ìwọ ti ṣe ohun aṣiwèrè, láti ìsinsìnyí lọ, ìwọ yóò wà lójú ogun.”"'), '2Chr$16:14': ('ní', None, '2Chr$16:14 "Wọ́n sì sin ín sínú ìsà òkú tí ó ti gbé jáde fún ara rẹ̀ ní ìlú Dáfídì. Wọ́n sì tẹ sí orí àkéte tí ó fi òórùn dìdùn kún, àti onírúurú tùràrí tí a fi ọgbọ́n àwọn alápòlù pèsè, wọ́n sì ṣe ìjóná ńlá nínú ọlá ńlá rẹ̀."'), '2Chr$17:17': (None, None, '2Chr$17:17 "Láti ọ̀dọ̀ Bẹ́ńjámínì: Élíádà, alágbára akọni ọkùnrin pẹ̀lú ọ̀kẹ́ mẹ́wàá (200,000) àwọn jagunjagun ọkùnrin pẹ̀lú ọrun àti àpáta ìhámọ́ra; "'), '2Chr$18:16': (None, 'ni', '2Chr$18:16 " Nígbà náà Míkáyà dáhùn, “Mo rí gbogbo Ísírẹ́lì fọ́n káàkiri lórí àwọn òkè, bí àgùntàn tí kò ní olùsọ́. OLÚWA sì wí pé, ‘Àwọn ènìyàn wọ̀nyí kò ní ọ̀gá. Jẹ́ kí olúkúlùkù lọ sí ilé ní àlàáfíà.’ ”"'), '2Chr$18:18': ('ri', 'gbo', '2Chr$18:18 "Mikáyà tẹ̀ṣíwájú pé, “Nítorí náà, gbọ́ ọ̀rọ̀ OLÚWA: Mo rí OLÚWA jòkó lórí ìtẹ́ rẹ̀ gbogbo ogun ọ̀run sì dúró lápá ọ̀tún àti lápá òsì."'), '2Chr$19:5': (None, None, '2Chr$19:5 "Ó sì yan àwọn onídàájọ́ sí ilẹ̀ náà, ní olúkúlùkù ìlú olódi, Júdà."'), '2Chr$19:8': ('ṣe', None, '2Chr$19:8 "Ní Jérúsálẹ́mù pẹ̀lú, Jéhóṣáfatì yan díẹ̀ lára àwọn Léfì, àwọn àlùfáà àti àwọn olórí ìdílé Ísírẹ́lì si pípa òfin OLÚWA mọ́ àti láti ṣe ìdájọ́. Wọn sì ń gbé ní Jérúsálẹ́mù."'), '2Chr$19:10': ('wá', 'ṣe', '2Chr$19:10 "Ní gbogbo ẹjọ́ tí ó bá wà níwájú rẹ láti ọ̀dọ̀ àwọn arákùnrin yín tí ń gbé ìlú wọn bóyá ẹ̀jẹ̀ tí ń ṣàn tàbí ìyókù òfin pẹ̀lú, paálásẹ ìlànà àti ẹ̀tọ́, ìwọ gbọdọ̀ kìlọ̀ fún wọn láti má ṣe dẹ́sẹ̀ sí OLÚWA bí bẹ́ẹ̀ kọ́, ìbínú yóò sì wá sórí yín, àti sórí àwọn arákùnrin. Ṣe èyi, ìwọ kò sì ní dẹ́ṣẹ̀."'), '2Chr$20:25': ('gba', None, '2Chr$20:25 "Bẹ́ẹ̀ ni Jéhóṣáfatì ati àwọn èniyàn rẹ̀ lọ láti kó ìkógun wọn, wọ́n sì rí lára wọn ọ̀pọ̀ iyebíye ọrọ̀ púpọ̀, ó sì ju èyí tí wọ́n lè kó lọ. Ọ̀pọ̀ ìkógun sì wà níbẹ̀, èyí tí ó gbà wọ́n ní ọjọ́ mẹ́ta láti gbà pọ̀."'), '2Chr$21:7': ('ṣe', 'fun', '2Chr$21:7 "Bí ó tilẹ̀ rí bẹ́ẹ̀, nítorí tí májẹ̀mú tí OLÚWA ti dá fún Dáfídì kì í ṣe ífẹ́ OLÚWA láti pa ìdílé Dáfídì run ó ti ṣe ìlérí láti tọ́jú fìtílà kan fún un àti àwọn àtẹ̀lé rẹ̀ títí láé."'), '2Chr$21:12': (None, 'ni', '2Chr$21:12 "Jóhórámì gba ìwé láti ọwọ́ Èlíjà wòlíì, tí ó wí pé: “Èyí ní ohun tí OLÚWA, Ọlọ́run baba à rẹ Dáfídì wí: ‘Ìwọ kò tí ì rìn ní ọ̀nà àwọn baba à rẹ Jèhóṣáfátì tàbí Ásà ọba Júdà."'), '2Chr$21:19': (None, None, '2Chr$21:19 "Ní àkókò kan ní ìparí ọdún kejì, àwọn ìfun rẹ̀ jáde nítorí àrùn náà ó sì kú nínu ìrora ńlá. Àwọn Ènìyàn rẹ̀ kò dá iná láti fi bọlá fún un, gẹ́gẹ́ bí wọ́n ti ṣe fún àwọn baba a rẹ̀."'), '2Chr$22:4': (None, 'iku', '2Chr$22:4 "Ó ṣe búburú ní ojú OLÚWA, gẹ́gẹ́ bí ilé Áhábù ti ṣe, Nítorí lẹ́yìn ikú bàbá a rẹ̀, wọ́n di olùgbani lámọ̀ràn rẹ̀ sí ṣíṣe rẹ̀."'), '2Chr$23:6': (None, None, '2Chr$23:6 "Kò sí ẹnìkan tí ó gbọdọ̀ wọ inú ilé OLÚWA yàtọ̀ sí àwọn àlùfáà àti àwọn ará Léfì tí a rán ní iṣẹ́ ìsìn. Wọ́n lè wọlé nítori tí a ti yà wọ́n sọ́tọ̀, ṣùgbọ́n gbogbo ọkùnrin mìíràn ni kí wọn ó ṣọ ohun tí OLÚWA ti yàn fún wọn."'), '2Chr$23:8': ('awọn ọkunrin', None, '2Chr$23:8 "Àwọn ará Léfì àti gbogbo ọkùnrin Júdà ṣe gẹ́gẹ́ bí Jehóádà Àlùfáà ti palásẹ. Olukúlùkù mú àwọn ọkùnrin rẹ̀, àwọn tí wọ́n lọ fún iṣẹ́ ìsìn ní ọjọ́ ìsinmi àti àwọn tí wọ́n ń kúrò ní ibi iṣẹ́. Nítorí Jéhóiádà àlùfáà kò ì tí tú ìpín kankan sílẹ̀."'), '2Chr$23:14': ('Ni', None, '2Chr$23:14 "Jéhóiádà àlùfáà mú àwọn alákóso ọrọrún, tí ó wà ní àbojútó àwọn ọ̀wọ́ ogun, ó sì wá wí fún wọn pé: “Mú un jáde wá láàrin àwọn ọgbà, kí ẹ sì pa ẹnikẹ́ni tí ó bá tẹ̀le.” Nítorí tí àlùfáà ti wí pé, “Má se paá nínú ilé OLÚWA.”"'), '2Chr$23:20': ('mu', 'ilẹ', '2Chr$23:20 "Ó mú pẹ̀lú rẹ̀ àwọn alákóso ọrọrún, Àwọn eni-ọlá, àwọn olórí àwọn ènìyàn àti gbogbo àwọn Ènìyàn ilẹ̀ náà. Ó sì mú ọba sọ̀kalẹ̀ wá láti ilé OLÚWA. Wọ́n lọ sínú Ààfin láti ẹnu òde tòkè. Wọ́n sì fi ọba jókoó lórí ìtẹ́."'), '2Chr$24:11': (None, None, '2Chr$24:11 "Nígbà kígbà tí a bá gbé àpótí wọlé láti ọwọ́ àwọn ará Léfì sí ọwọ́ àwọn ìjòyè ọba, tí wọ́n bá sì rí wí pe owó ńlá wà níbẹ̀ àwọn akọ̀wé ọna àti ìjòyè olórí àlùfáà yóò wá láti kó owó rẹ̀ kúrò, wọn yóò sì dá a padà sí àyè rẹ̀. Wọ́n ṣe èyí déédéé, wọ́n sì kó iye owó ńlá."'), '2Chr$24:15': (None, 'kun', '2Chr$24:15 "Ṣùgbọ́n Jéhóiádà di arúgbó, ó sì kún fún ọjọ́, ó sì kú, ẹni àádóje ọdún ni nígbà tí ó kú."'), '2Chr$25:9': (None, 'ni', '2Chr$25:9 "Ámásíà sì bi ènìyàn Ọlọ́run pé, “Ọgọ́rùnún tálẹ́ntì tí mo ti san fún àwọn ọ̀wọ́-ogun àwọn ọmọ Ísírẹ́lì ń kọ́?” Ènìyàn Ọlọ́run dáhùn pé “OLÚWA lè fún ọ ní èyí tí ó ju ìyẹn lọ.”"'), '2Chr$26:13': ('agbara', 'ọba', '2Chr$26:13 "Lábẹ́ olórí àti olùdarí wọn wọ́n sì jẹ́ alágbára akọni ogun ọ̀kẹ́ mẹ́ẹ̀ẹ́dogún ó lé ẹ̀ẹ́dẹ́gbàárún (307,500), tí ó ti múra fún ogun ńlá náà, àti alágbára ńlá jagunjagun kan láti ran ọba lọ́wọ́ sí ọ̀tá rẹ̀."'), '2Chr$28:9': (None, 'ni', '2Chr$28:9 "Ṣùgbọ́n wòlíì OLÚWA tí orúkọ rẹ̀ ń jẹ́ Ódédì wà níbẹ̀, ó sì jáde lọ láti lọ pàdé ogun nígbà tí ó padà sí Saáríà. Ó sì wí fún wọn pé, “Nítorí OLÚWA, Ọlọ́run Baba yín bínú sí Júdà ó sì fi wọ́n lé yín lọ́wọ́. Ṣùgbọ́n ẹ̀yin pa wọ́n ní ìpa oró tí ó de òkè ọ̀run."'), '2Chr$28:11': ('ni', None, '2Chr$28:11 Nísinsinyìí ẹ gbọ́ tèmi! Ẹ rán àwọn ìgbékùn tí ẹ̀yin ti mú gẹ́gẹ́ bí àwọn ẹlẹwọ̀n padà nítorí ìbínú kíkan OLÚWA ńbẹ lórí yín.”'), '2Chr$28:12': (None, 'mú', '2Chr$28:12 "Lẹ́yìn náà, díẹ̀ nínú àwọn olórí ní Éfùráímù Ásáríyà ọmọ Jehóhánánì, Béríkià ọmọ Méṣílemóti, Jehísikíà ọmọ Ṣaílúmù, àti Ámásà ọmọ Hádíà, dìde sí àwọn tí o ti ogun náà bọ̀."'), '2Chr$29:25': ('aríran', 'woli', '2Chr$29:25 Ó sì mú àwọn Léfì dúró nínú ilé OLÚWA pẹ̀lú Kíḿbálì ohun èlò orin àti dùùrù ní ọ̀nà tí a ti palásẹ fún wọn láti ọ̀dọ̀ Dáfídì àti Gádì aríran ọba àti Nátanì wòlíì: Èyí ni a pa lásẹ láti ọ̀dọ̀ OLÚWA láti ọwọ́ àwọn wòlíì rẹ̀.'), '2Chr$30:5': (None, 'ṣe', '2Chr$30:5 "Bẹ́ẹ̀ ni wọ́n fi àṣẹ kan lélẹ̀, láti kéde ká gbogbo Ísírẹ́lì láti Beerí-sébà àní títí dé Dánì, láti wá pa àjọ ìrékọjá mọ́ sí OLÚWA Ọlọ́run Ísírẹ́lì ní Jérúsálẹ́mù: nítorí wọn kò pa á mọ́ ní ọjọ́ púpọ̀ gẹ́gẹ́ bí a ti kọ ọ́."'), '2Chr$30:12': ('fun', 'ọwọ', '2Chr$30:12 "Ní Júdà pẹ̀lú, ọwọ́ Ọlọ́run wá láti fún wọn ní ọkàn kan láti pa òfin ọba mọ́ àti tí àwọn ìjòyè, nípa ọ̀rọ̀ OLÚWA."'), '2Chr$30:18': ('eniyan', 'ọpọlọpọ', '2Chr$30:18 "Ọ̀pọ̀lọpọ̀ ènìyàn, àni ọ̀pọ̀lọpọ̀ nínú Éfíráimù àti Mánásè, Ísákárì, àti Sébúlúnì kò sá wẹ̀ ara wọn mọ́ síbẹ̀ wọ́n jẹ ìrékọjá naà, kì íṣe gẹ́gẹ́ bí a ti kọ ọ́. Ṣùgbọ́n Heṣekáyà bẹ̀bẹ̀ fún wọn, wí pé, OLÚWA, ẹni rere, dáríjin olúkúlùkù,"'), '2Chr$31:20': (None, 'dara', '2Chr$31:20 "Èyí ni Heṣekáyà ṣe jákèjadò Júdà, ó sì ṣe ohun tí ó dára àti tí ó tọ́ àti ohun òtítọ́ níwájú OLÚWA Ọlọ́run rẹ̀,"'), '2Chr$32:2': ('ṣe', 'wá', '2Chr$32:2 "Nígbà tí Heṣekáyà rí i pé Senakérébù ti wá, àti pé ó fẹ́ láti dá ogun sílẹ̀ lóri Jérúsálẹ́mù,"'), '2Chr$32:6': (None, 'eniyan', '2Chr$32:6 "Ó yan àwọn ìjòyè ológun sórí àwọn ènìyàn, ó sì pèwọ́n jọ, níwájú rẹ̀ ní ìbámu ní ìlú ńlá ti Dáfídì. Ó sì ki wọ́n láyà pẹ̀lú àwọn ọ̀rọ̀ wọ̀nyí:"'), '2Chr$32:9': (None, 'ọba', '2Chr$32:9 Lẹ́yìn ìgbà tí Senakéríbù ọba Ásíríà àti gbogbo àwọn ogun rẹ̀ ń gbógun sí Lákíṣì. Ó rán àwọn ìjòyè rẹ̀ sí Jérúsálẹ́mù pẹ̀lú iṣẹ́ yí fún Heṣekáyà ọba Júdà àti fún gbogbo àwọn ènìyàn Júdà tí ó wà níbẹ̀:'), '2Chr$32:11': ('fun', 'ọwọ', '2Chr$32:11 "Nígbà tí Heṣekáyà wí pé ‘OLÚWA Ọlọ́run wa yóò gbà wá kúrò lọ́wọ́ ọba Ásíríà, ó ń sì yín tọ́ sọ́nà, kí ẹ bá lè kú fún ebi àti òǹgbẹ.’"'), '2Chr$32:17': (None, 'ni', '2Chr$32:17 "Ọba pẹ̀lú sì kọ àwọn ìwé ní bíbú OLÚWA Ọlọ́run Ísírẹ́lì àti ní sísọ èyí ní ìlòdì sí: “Ní gẹ́gẹ́ bí àwọn ọlọ́run àwọn ènìyàn ilẹ̀ mìíràn kò ṣe gba àwọn ènìyàn wọn kúrò lọ́wọ́ mi, Bẹ́ẹ̀ ni Ọlọ́run Heṣekáyà kì yóò gba àwọn ènìyàn rẹ̀ kúrò lọ́wọ́ mi.”"'), '2Chr$32:31': (None, None, '2Chr$32:31 "Ṣùgbọ́n nígbà tí ara àwọn oníṣẹ́ ọba wá láti Bábílónì láti bi í lérè nípa àmìn ìyanu tí ó sẹlẹ̀ nì, Ọlọ́run fi sílẹ̀ láti dán an wò àti láti mọ gbogbo nǹkan tí ó wà lọ́kàn rẹ̀."'), '2Chr$33:7': ('ní', None, '2Chr$33:7 "Ó mú ère gbígbẹ́ tí ó ti gbẹ́ ó sì gbé e sínú ilé OLÚWA, ní èyí tí Ọlọ́run ti sọ fún Dáfídì àti sí ọmọ rẹ̀ Solómónì, “Nínú ilé OLÚWA yìí àti ní Jérúsálẹ́mù, tí mo ti yàn kúrò nínú gbogbo àwọn ẹ̀yà Ísírẹ́lì, èmi yóò fi orúkọ mi síbẹ̀ títí láé."'), '2Chr$33:15': (None, None, '2Chr$33:15 "Ó pa gbogbo àwọn Ọlọ́run àjòjì run. Ó sì hú gbogbo àwọn ère kúrò nínú ilé OLÚWA, pẹ̀lú gbogbo àwọn pẹpẹ tí ó ti kọ́ ní òkè ilé OLÚWA àti ní Jérúsálẹ́mù; ó sì kó wọn jáde kúrò nínú ìlú."'), '2Chr$33:17': ('ṣi', None, '2Chr$33:17 "Àwọn ènìyàn, bí ó ti wù kí ó rí, tẹ̀ṣíwájú láti máa rú ẹbọ ní àwọn ibi gíga. Ṣùgbọ́n sí OLÚWA Ọlọ́run wọn nìkan."'), '2Chr$34:10': (None, None, '2Chr$34:10 "Nígbà náà wọn si fi le àwọn tí ń ṣiṣẹ́ ilé lọ́wọ́, àwọn tí ń ṣe alábojútó iṣẹ́ ilé OLÚWA. Àwọn ọkùnrin yìí sì san owó fún àwọn òṣìṣẹ́ tí ó ń tún ṣe, tí wọ́n sì ń ṣìṣẹ́ ní ilé OLÚWA."'), '2Chr$34:12': ('ṣe', None, '2Chr$34:12 "Àwọn ọkùnrin náà ṣe iṣẹ́ náà pẹ̀lú òtítọ́ lórí wọn, láti darí wọn ní Jáhátì àti Obadíà, àwọn ọmọ Léfì láti Mérárì, àti Sekaríà àti Mèsúlámù, sọ̀kalẹ̀ láti Kónátì àwọn ọmọ Léfì gbogbo tí ó ní ọgbọ̀n ohun èlò orin."'), '2Chr$34:17': (None, 'ni', '2Chr$34:17 Wọ́n ti san owó náà tí ó wà nínú ilé OLÚWA wọ́n sì ti fi lé àwọn alábojútó lọ́wọ́ àti àwọn òṣìsẹ́.”'), '2Chr$34:23': (None, None, '2Chr$34:23 "Ó sì wí fún wọn wí pé, “Èyí ni ohun tí OLÚWA, Ọlọ́run Ísírẹ́lì ti sọ, sọ fún àwọn ọkùnrin tí ó rán ọ sí mi pé,"'), '2Chr$34:26': (None, None, '2Chr$34:26 "Sọ fún ọba Júdà, ẹni tí ó rán yín láti bèèrè lọ́wọ́ OLÚWA, ‘Èyí ni ohun tí OLÚWA ti sọ, Ọlọ́run Ísírẹ́lì, sọ nípa ọ̀rọ̀ wọ̀n nì tí íwọ gbọ́:"'), '2Chr$35:2': ('ṣe', None, '2Chr$35:2 "Ó sì yàn àwọn àlùfáà sí iṣẹ́ wọn, ó sì gbà wọ́n ní ìyànjú ní ìsìn ilé OLÚWA."'), '2Chr$35:7': ('fun', None, '2Chr$35:7 "Jósíà sì pàṣẹ fún gbogbo àwọn ènìyàn tí ó dùbúlẹ̀ tí ó wà níbẹ̀ iye rẹ̀ ẹgbàámẹ́ẹ̀dógún ọ̀dọ́ àgùntàn àti ọmọ ewurẹ́ fún ẹbọ ìrékọjá, àti pẹ̀lú ẹgbẹ̀ẹ́dọ́gbọ̀n akọ màlúù (3,000) gbogbo wọ̀nyí jẹ́ ohun ìní láti ọ̀dọ̀ ọba."'), '2Chr$35:14': (None, None, '2Chr$35:14 "Lẹ́yìn èyí, wọ́n sì múra sílẹ̀ fún ara wọn àti fún àwọn àlùfáà nítorí àwọn àlùfáà àti àwọn ọmọ Árónì, ni wọ́n rú ẹbọ sísun àti ọ̀rá títí di àsálẹ́. Bẹ́ẹ̀ ni àwọn ọmọ Léfì sì múra sílẹ̀ fún ara wọn àti fún àwọn ọmọ Árónì àlùfáà."'), '2Chr$36:4': (None, 'orukọ', '2Chr$36:4 "Ọba Éjíbítì sì mú Élíákímù, arákùnrin Jehóáhásì, jẹ ọba lórí Júdà àti Jérúsálẹ́mù, ó sì yí orúkọ Eliákímù padà sí Jéhóíákíámù, ṣùgbọ́n Nékò mú arákùnrin Eliákímù, Jehóáhásì ó sì gbé e lórí Éjíbítì."'), '2Chr$36:15': ('ranṣẹ', None, '2Chr$36:15 "OLÚWA, Ọlọ́run àwọn baba wọn ránsẹ́ sí wọn láti ọwọ́ àwọn ìránṣẹ́ rẹ̀ ṣíwájú àti ṣíwájú síi, nítorí tí ó ní ìyọ́nú sí àwọn ènìyàn rẹ̀ àti sí ibùgbé rẹ̀."'), '2Chr$36:17': (None, None, '2Chr$36:17 "Ó sì mú wá sórí wọn ọba àwọn ará Bábílónì tí wọ́n bá àwọn ọ̀dọ́mọkùnrin wọn jà pẹ̀lú idà ní ilẹ̀ ibi mímọ́, kò sì ní ìyọ́nú sí àgbà ọkùnrin tàbí ọ̀dọ́mọdé bìnrin, wúndíá, tàbí arúgbó. Ọlọ́run sì fi gbogbo wọn lé Nebukadinésárì lọ́wọ́."'), '2Chr$36:20': (None, 'ijọba', '2Chr$36:20 Ó sì kó èyí tí ó kù lọ sí Bábílónì àwọn tí ó rí ibi sá láti ẹnu idà wọ́n sì di ìránṣẹ́ fún un àti àwọn ọmọ rẹ̀ títí tí ìjọba Pásíà fi gba agbára.'), '2Chr$36:22': ('ṣe', 'kọ', '2Chr$36:22 " Ní ọdún kín-ín-ní Kírúsì ọba Pásíà, kí ọ̀rọ̀ OLÚWA tí a tẹnu Jeremáyà sọ báà le ṣẹ pé, OLÚWA run ẹ̀mí Kírúsì ọba Pásíà láti ṣe ìkéde ní gbogbo ìjọba rẹ̀, ó sì kọọ́ sínú ìwé pẹ̀lú."'), 'Ezra$3:7': ('fun', None, 'Ezra$3:7 "Nígbà náà ni wọ́n fún àwọn ọ̀mọ̀lé àti àwọn gbẹ́nàgbẹ́nà ní owó, wọ́n sì tún fi oúnjẹ, ohun mímu àti òróró fún àwọn ará Ṣídónì àti Tírè, kí wọ́n ba à le è kó igi Sídà gba ti orí omi òkun láti Lébánónì wá sí Jópà, gẹ́gẹ́ bí Sáírúsì ọba Páṣíà ti pàṣẹ."'), 'Ezra$4:1': (None, None, 'Ezra$4:1 "Nígbà tí àwọn ọ̀ta Júdà àti Bẹ́ńjámínì gbọ́ wí pé àwọn ìgbèkùn tí ó padà dé ń kọ́ tẹ́ḿpìlì fún OLÚWA, Ọlọ́run Ísírẹ́lì,"'), 'Ezra$4:13': (None, None, 'Ezra$4:13 "Ṣíwájú síi, ọba gbọdọ̀ mọ̀ pé tí a bá kọ́ ìlú yìí àti tí a sì tún àwọn ògiri rẹ̀ mọ, kò sí owó orí, owó òde tàbí owó ibodè tí a ó san, owó tí ó sì ń wọlé fún ọba yóò sì dínkù."'), 'Ezra$4:14': (None, None, 'Ezra$4:14 "Nísinsìn yìí níwọ̀n ìgbà tí a ní ojúṣe sí ààfin ọba, kò sì bójúmu fún wa láti rí ìtàbùkù ọba, àwa ń rán iṣẹ́ yìí láti sọ fún ọba,"'), 'Ezra$4:19': ('iṣọtẹ', 'iṣọtẹ', 'Ezra$4:19 "Mo pa àṣẹ, a sì ṣe ìwádìí, nínú ìtàn ọjọ́ pípẹ́ àti ti ìsinsinyìí ti ìṣọ̀tẹ̀ sí àwọn ọba pẹ̀lú, ó sì jẹ́ ibi ìṣọ̀tẹ̀ àti ìrúkèrúdò."'), 'Ezra$4:22': (None, 'ibajẹ', 'Ezra$4:22 "Ẹ ṣọ́ra kí ẹ má ṣe àìkọbi-ara sí ọ̀rọ̀ yìí. Èéṣe tí ìbàjẹ́ yóò fi pọ̀ sí i, sí ìpalára àwọn ọba?"'), 'Ezra$4:23': ('agbara', 'ọba', 'Ezra$4:23 "Ní kété tí a ka ẹ̀dà ìwé ọba Aritaṣéṣéṣì sí Réhúmì àti Ṣímíṣáì akọ̀wé àti àwọn ẹlẹgbẹ́ ẹ wọn, wọ́n yára lọ sí ọ̀dọ̀ àwọn Júù ní Jérúsálẹ́mù pẹ̀lú, wọ́n fi agbára mú wọn láti dáwọ́ dúró."'), 'Ezra$5:3': ('ṣe', 'kọ', 'Ezra$5:3 "Ní àkókò náà Táténáì, Baálẹ̀ ti agbègbè Yúfúrátè àti Ṣétarì-Bóṣénáì àti àwọn ẹgbẹ́gbẹ́ wọn lọ sí ọ̀dọ̀ wọn Wọ́n sì béèrè pé, “Ta ni ó fún un yín ní àṣẹ láti tún tẹ́ḿpìlì yìí kọ́ àti láti tún odi yìí mọ?”"'), 'Ezra$5:8': ('lọ', 'iṣẹ', 'Ezra$5:8 "Kí ọba kí ó mọ̀ pé a lọ sí ẹ̀kún Júdà, sí tẹ́ḿpìlì Ọlọ́run alágbára. Àwọn ènìyàn náà ń kọ́ ọ pẹ̀lú àwọn òkúta ńlá ńlá àti pẹ̀lú fífi àwọn igi sí ara ògiri. Wọ́n ṣe iṣẹ́ náà pẹ̀lú àìsinmi, ó sì ń ní ìtẹ̀ṣíwájú kíákíá lábẹ́ ìṣàkóso wọn."'), 'Ezra$5:9': ('ṣe', 'kọ', 'Ezra$5:9 "A bi àwọn àgbààgbà, a sì fi ọ̀rọ̀ wá wọn lẹ́nu wò pé, “Ta ni ó fún un yín ní àṣẹ láti tún ilé OLÚWA yìí kọ́ àti láti tún odi rẹ̀ mọ?”"'), 'Ezra$5:12': (None, 'ọwọ', 'Ezra$5:12 "Ṣùgbọ́n nítorí tí àwọn baba wa mú Ọlọ́run ọ̀run bínú, ó fi wọ́n lé Nebukadinésárì ti Kálídéà, ọba Bábílónì lọ́wọ́, ẹni tí ó run tẹ́ḿpìlì OLÚWA yìí tí ó sì kó àwọn ènìyàn náà padà sí Bábílónì."'), 'Ezra$5:13': ('ṣe', 'kọ', 'Ezra$5:13 " Ṣùgbọ́n ní ọdún kìn-ín-ní ìjọba Ṣáírúsì ọba Bábílónì, ọba Ṣáírúsì pàṣẹ pé kí a tún ilé Ọlọ́run yìí kọ́."'), 'Ezra$5:14': ('ní', 'gba', 'Ezra$5:14 "Kí àwọn ènìyàn ní ibikíbi tí ẹni tí ó lọ bá ń gbé kí ó fi fàdákà àti wúrà pẹ̀lú ohun tí ó dára àti ẹran-ọ̀sìn pẹ̀lú ọrẹ àtinúwá ràn-án lọ́wọ́ fún tẹ́ḿpìlì Ọlọ́run ní Jérúsálẹ́mù. Kí wọn sì mú wá sí tẹ́ḿpílì ní Bábílónì Nígbà náà ọba Sáírúsì kó wọn fún ọkùnrin kan tí orúkọ rẹ̀ a máa jẹ́ Ṣesibásárì, ẹni tí ó ti yàn gẹ́gẹ́ bí i Baálẹ̀,"'), 'Ezra$5:16': (None, 'ani', 'Ezra$5:16 "Nígbà náà ni Ṣesibásásárì náà wá, ó sí fi ìpìlẹ̀ ilé Ọlọ́run tí ó wá ní Jérúsálẹ́mù lẹ́lẹ̀, láti ìgbà náà àní títí di ìsínsìn yìí ni o ti n bẹ ní kíkọ́ ṣùgbọ́n, kò sí tí ì parí tán."'), 'Ezra$5:17': ('ṣe', 'kọ', 'Ezra$5:17 "Nísinsìn yìí tí ó bá tẹ́ ọba lọ́rùn, ẹ jẹ́ kí a ṣe ìwádìí nínú ìwé ìrántí ní ilé ìṣúra ọba ní Bábílónì láti rí bí ọba Ṣáírúsì fi àṣẹ lélẹ̀ lóòótọ́ láti tún ilé Ọlọ́run yìí kọ́ ní Jérúsálẹ́mù. Nígbà náà jẹ́ kí ọba kó fi ìpinnu rẹ̀ lórí ọ̀rọ̀ yìí ránṣẹ́ sí wa."'), 'Ezra$6:8': (None, 'ṣe', 'Ezra$6:8 "Síwájú sí i, mo pàṣẹ ohun tí ẹ gbọdọ̀ ṣe fún àwọn àgbààgbà Júù wọ̀nyí lórí kíkọ́ ilé Ọlọ́run yìí: Gbogbo ìnáwó àwọn ọkùnrin yìí ni kí ẹ san lẹ́kùn-ún rẹ́rẹ́ lára ìṣúra ti ọba, láti ibi àkójọpọ̀ owó ìlú ti agbègbè Yúfúrátè kí iṣẹ́ náà má bà dúró."'), 'Ezra$6:9': (None, 'ni', 'Ezra$6:9 "Ohunkóhun tí wọ́n bá fẹ́—àwọn akọ ọ̀dọ́ màlúù, àwọn àgbò, ọ̀dọ́ àgùntàn fún ọrẹ sísun sí Ọlọ́run ọ̀run, àti jéró, iyọ̀, wáìnì àti òróró, ìfiyàn bí àwọn àlùfáà ní Jérúsálẹ́mù ti béèrè ni ẹ gbọdọ̀ fún wọn lójoojúmọ́ láì yẹ̀."'), 'Ezra$6:11': (None, 'ni', 'Ezra$6:11 "Síwájú sí i, mo pàṣẹ pé tí ẹnikẹ́ni bá yí àṣẹ yìí padà, kí fa igi àjà ilé rẹ̀ yọ jáde, kí a sì gbe dúró, kí a si fi òun náà kọ́ sí orí rẹ̀ kí ó wo ilé rẹ̀ palẹ̀ a ó sì sọ ọ́ di ààtàn."'), 'Ezra$6:21': (None, 'iṣẹ', 'Ezra$6:21 Bẹ́ẹ̀ ni àwọn ọmọ Ísírẹ́lì tí ó ti dé láti ìgbèkùn jẹ ẹ́ lápapọ̀ pẹ̀lú gbogbo àwọn tí ó ti ya ara wọn kúrò nínú gbogbo iṣẹ́ àìmọ́ ti àwọn kèfèrí aládùúgbò wọn láti wá OLÚWA Ọlọ́run Ísírẹ́lì.'), 'Ezra$7:1': (None, None, 'Ezra$7:1 "Lẹ́yìn nǹkan wọ̀nyí, ní àkókò ìjọba ọba Aritaṣéṣéṣì ní Páṣíà, Ẹ́sírà ọmọ Ṣéráíyà, ọmọ Ásáríyà, ọmọ Hílíkíyà,"'), 'Ezra$7:26': (None, 'ṣe', 'Ezra$7:26 Ẹnikẹ́ni tí kò bá ṣe ìgbọ́ràn sí òfin Ọlọ́run rẹ àti sí òfin ọba ní ó gbọdọ̀ kú tàbí kí a lé e jáde tàbí kí a gbẹ́sẹ̀ lé ẹrù rẹ̀ tàbí kí a sọ ọ́ sínú ẹ̀wọ̀n.'), 'Ezra$8:17': ('ranṣẹ', 'ibi', 'Ezra$8:17 "mo rán wọn sí Ídò, tí ó wà ní ibi ti a ń pè ni Kásífíà, mo sì sọ ohun ti wọn yóò sọ fun Ídò àti àwọn arakunrin rẹ̀ ti wọn jẹ́ òṣìṣẹ́ tẹ́ḿpìlì ní Kásífíà fún wọn, pé, kí wọn mú àwọn ìránṣẹ́ wá fún wa fún ilé Ọlọ́run wa."'), 'Ezra$8:22': (None, 'ọba', 'Ezra$8:22 "Mo tijú láti béèrè lọ́wọ́ ọba fún àwọn jagunjagun orí ilẹ̀, àti ti orí ẹsin láti dáàbò bò wá kúrò lọ́wọ́ àwọn ọ̀tá ní ọ̀nà wa, nítorí àti sọ fún ọba pé, “Ọwọ́ àánú Ọlọ́run wà ní ara gbogbo ẹni tí ó gbé ojú sókè sí i, ṣùgbọ́n ìbìnú ńlá rẹ wà lórí ẹni tó kọ̀ sílẹ̀.”"'), 'Ezra$8:30': ('mu', 'mú', 'Ezra$8:30 Nígbà náà ni àwọn àlùfáà àti àwọn Léfì gba fàdákà àti wúrà àti àwọn ohun èlò mímọ́ ti a ti wọ̀n jáde fún kíkó lọ sí ilé Ọlọ́run wa ní Jérúsálẹ́mù.'), 'Ezra$9:2': ('ya', 'ni', 'Ezra$9:2 "Wọ́n ti fẹ́ lára àwọn ọmọbìnrin wọn fún ara wọn àti fún àwọn ọmọkùnrin wọn gẹ́gẹ́ bí aya, wọ́n ti da ìran mímọ́ pọ̀ mọ́ àwọn ènìyàn ti ó wà ní àyíká wọn. Àwọn olórí àti àwọn ìjòyè ta àwọn ènìyàn tó kù yọ nínú hihu ìwà “Àìsòótọ́.”"'), 'Ezra$9:5': ('dide', None, 'Ezra$9:5 "Ní ìgbà tí o di àkókò ìrúbọ àṣálẹ̀, mo dìde kúrò nínú ìrẹ̀wẹ̀sì ọkàn mi, pẹ̀lú àwọ̀tẹ́lẹ̀ àti agbádá mi yíya ní ọrùn mi, mo kúnlẹ̀ lórí eékún mi méjèèjì, mo sì tẹ́ ọwọ́ mi méjèèjì sí OLÚWA Ọlọ́run mi."'), 'Ezra$9:7': (None, 'ni', 'Ezra$9:7 "Láti ìgbà àwọn baba wá, títí di ìsinsin yìí, àìṣedéédéé wa ti pọ̀ jọjọ. Nítorí ẹ̀ṣẹ̀ wa, àwa àti àwọn ọba wa àti àwọn àlùfáà wa ni a ti sọ di ẹni idà àti ìgbèkùn, ìkógun àti ẹni ẹ̀ṣín lọ́wọ́ àwọn àjèjì ọba, bí ó ti rí lónìí."'), 'Ezra$9:8': ('ibi', None, 'Ezra$9:8 "Ṣùgbọ́n ni ìsinsin yìí, fún ìgbà díẹ̀, OLÚWA Ọlọ́run ti fi àánú rẹ̀ dá àwa tí ó sẹ́kù sí tí ó sì fún wa ni ibi pàtàkì nínú ibi mímọ́ rẹ̀, nítorí náà Ọlọ́run wa ti fi ìmọ́lẹ̀ fún ojú wa àti ìgbé ayé túntún kúrò nínú ìgbékùn wa."'), 'Ezra$9:12': ('fi silẹ', 'fun', 'Ezra$9:12 "Nítorí náà, ẹ má ṣe fi àwọn ọmọbìnrin yín fún àwọn ọmọkùnrin wọn ní ìyàwó tàbí kí ẹ fẹ́ àwọn ọmọbìnrin wọn fún àwọn ọmọkùnrin yín ní ìyàwó. Ẹ má ṣe dá májẹ̀mú àlàáfíà pẹ̀lú wọn nígbà-kí-gbà kí ẹ̀yin kí ó sì le lágbára, kí ẹ sì jẹ ohun dáradára ilẹ̀ náà, kí ẹ sì fi sílẹ̀ fún àwọn ọmọ yín gẹ́gẹ́ bí ogún ayé rayé;"'), 'Ezra$9:13': (None, None, 'Ezra$9:13 "Ohun tí ó ti sẹlẹ̀ sí wa jẹ́ ayọrísí iṣẹ́ búburú wa àti ẹ̀bi-ẹ̀ṣẹ̀ ńlá wa, ṣíbẹ̀, Ọlọ́run wa, ìjìyà ti ìwọ fún wa kéré si ìjìyà tí ó yẹ fún ẹ̀ṣẹ̀ ti a dá, ìwọ sì fún wa ní àwọn ènìyàn tó sẹ́kù bí èyí."'), 'Ezra$10:2': ('ya', 'ni', 'Ezra$10:2 "Nígbà náà ni Ṣékáníáyà ọmọ Jéhíélì, ọ̀kan lára ìran Élámù, sọ fún Ẹ́sírà pé, Àwa ti jẹ́ aláìsọ̀ọ́tọ́ sí Ọlọ́run wa nípa fífẹ́ àwọn obìnrin àjèjì láàrin àwọn ènìyàn tí ó wà yí wa ká. Ṣùgbọ́n ṣíbẹ̀ náà, ìrètí sì wà fún Ísírẹ́lì"'), 'Ezra$10:10': (None, None, 'Ezra$10:10 "Nígbà náà ni àlùfáà Ẹ́sírà dìde, ó sì wí fún wọn pé, “Ẹ̀yin ti ṣe àìsòótọ́, ẹ ti fẹ́ obìnrin àjèjì, ẹ ti dá kún ẹ̀bi Ísírẹ́lì."'), 'Ezra$10:12': ('dahùn', 'ṣe', 'Ezra$10:12 Àpéjọpọ̀ ènìyàn náà sì dáhùn lóhùn rara pé: ohun tí ó sọ dára! A gbọdọ̀ ṣe bí o ti wí.'), 'Ezra$10:14': ('jẹ ki', 'ni', 'Ezra$10:14 "Jẹ́ kí àwọn ìjòyè wa ṣojú fún gbogbo ìjọ ènìyàn, lẹ́yìn náà, jẹ́ kí ẹnìkọ̀ọ̀kan nínú àwọn ìlú wa tí ó ti fẹ obìnrin àjèjì wá ní àsìkò tí a yàn, pẹ̀lú àwọn àgbààgbà àti àwọn onídàájọ́ ìlú kọ̀ọ̀kan, títí ìbínú gíga Ọlọ́run wa lórí ọ̀rọ̀ yìí yóò fi kúrò ní orí wa."'), 'Neh$1:8': ('ṣe', None, 'Neh$1:8 "“Rántí ìlànà tí o fún Móṣè ìránṣẹ́ rẹ, wí pé, ‘Bí ìwọ bá jẹ́ aláìṣòótọ́, èmi yóò fọ́n-ọn yín ká sí àárin àwọn orílẹ̀ èdè."'), 'Neh$2:1': ('fun', None, 'Neh$2:1 "Ní oṣù Níṣánì (oṣù kẹrin) ní ogún ọdún ìjọba ọba Aritaṣéṣéṣì, nígbà tí a gbé wáìnì wá fún-un, mo gbé wáìnì náà mo fi fún ọba, Ìbànújẹ́ kò hàn ní ojú mi rí ní iwájúu rẹ̀."'), 'Neh$2:6': (None, 'ọba', 'Neh$2:6 "Nígbà náà ni ọba, pẹ̀lú ìyàwó rẹ̀ tí ó jókòó lẹ́gbẹ̀ẹ́ rẹ̀, bi mí pé, “Báwo ni ìrìnàjò náà yóò ṣe pẹ́ ọ tó, nígbà wo sì ni ìwọ yóò padà?” Ó dùn mọ́ ọba láti rán mi lọ, bẹ́ẹ̀ ni mo dá àkókò kan."'), 'Neh$2:7': (None, 'fun', 'Neh$2:7 "Mo sì tún wí fún-un pé, “Tí ó bá wu ọba, kí ó fún mi ní lẹ́tà sí àwọn baálé agbégbé Éúfúrétè nítorí kí wọ́n báà lè jẹ́ kí n la ọ̀dọ̀ wọn kọjá lọ sí Júdà láìléwu"'), 'Neh$2:12': ('awọn ọkunrin', None, 'Neh$2:12 "Mo jáde ní òru pẹ̀lú àwọn ọkùnrin díẹ̀. Èmi kò sì sọ fún ẹnikankan ohun tí Ọlọ́run mi ti fi sí ọkàn mi láti ṣe fún Jérúsálẹ́mù. Kò sí ẹranko kankan pẹ̀lúu mi, bí kò ṣe ọ̀kan ṣoṣo tí mo gùn."'), 'Neh$2:14': ('lọ', 'kọjá', 'Neh$2:14 "Nígbà náà ni mo lọ sí ẹnu ibodè oríṣun àti sí adágún omi ọba, ṣùgbọ́n kò sí ààyè tó fún ẹranko mi láti kọjá;"'), 'Neh$2:16': ('lọ', 'iṣẹ', 'Neh$2:16 "Àwọn olórí kò mọ ibi tí mo lọ tàbí mọ ohun tí mo ń ṣe, nítorí èmi kò tí ì sọ fún àwọn ará Júù tàbí àwọn àlùfáà tàbí àwọn ọlọ́lá tàbí àwọn ìjòyè tàbí ẹnikẹ́ni mìíràn tí yóò máa ṣe iṣẹ́ náà."'), 'Neh$3:3': ('ṣe', None, 'Neh$3:3 "Àwọn ọmọ Háṣénáyà ni wọ́n mọ Ìbodè Ẹja. Wọ́n kún bíìmù rẹ̀, wọ́n sì gbé ìlẹ̀kùn rẹ̀, ìdábùú ìlẹ̀kùn àti àwọn ìdè e rẹ̀ sí ààyè e wọn."'), 'Neh$4:3': ('lọ', 'fọ', 'Neh$4:3 "Tóbíyà ará Ámónì, ẹni tí ó wà lẹ́gbẹ̀ẹ́ rẹ̀, wí pé, “Ohun tí wọ́n ń mọ—bí kọ̀lọ̀kọ̀lọ̀ lásán bá gùn-ún sókè, yóò fọ́ odi òkúta wọn lulẹ̀!”"'), 'Neh$4:4': (None, 'ni', 'Neh$4:4 "Gbọ́ ti wa, Ọlọ́run wa, nítorí àwa di ẹni ẹ̀gàn. Dá ẹ̀gàn wọn padà sórí ara wọn. Kí o sì fi wọ́n fún ìkógun ní ilẹ̀ ìgbékùn."'), 'Neh$4:12': ('pada', None, 'Neh$4:12 "Nígbà náà ni àwọn Júù tí ó ń gbé ní ẹ̀gbẹ́ẹ wọn wá sọ fún wa ní ìgbà mẹ́wàá pé, “Ibikíbi tí ẹ̀yin bá padà sí, wọn yóò kọ lù wá.”"'), 'Neh$4:15': (None, 'iṣẹ', 'Neh$4:15 "Nígbà tí àwọn ọ̀ta wa gbọ́ pé àwa ti mọ ọ̀tá wọn àti wí pé Ọlọ́run ti bàájẹ́, gbogbo wa padà sí ibi odi, ẹnìkọ̀ọ̀kan sí ibi iṣẹ́ tirẹ̀."'), 'Neh$4:19': (None, None, 'Neh$4:19 "Nígbà náà ni mo sọ fún àwọn ọlọ́lá, àwọn ìjòyè àti àwọn ènìyàn tó kù pé, “Iṣẹ́ náà fẹ̀ ó sì pọ̀, a sì ti jìnnà sí ara wa púpọ̀ lórí odi."'), 'Neh$5:2': (None, None, 'Neh$5:2 "Àwọn kan ń wí pé, “Àwa àti àwọn ọmọkùnrin àti àwọn ọmọbìnrin wa pọ̀; kí àwa kí ó le è jẹ, kí a sì wà láàyè, a gbọdọ̀ rí oúnjẹ.”"'), 'Neh$5:15': (None, 'ṣekeli', 'Neh$5:15 Ṣùgbọ́n àwọn baálẹ̀ ìṣáájú—tí ó ti wà ṣáájú mi—gbe àjàgà wúwo lé àwọn ènìyàn lórí yàtọ̀ fún oúnjẹ àti wáìnì wọ́n sì tún gba ogójì ṣékélì fàdákà lọ́wọ́ wọn. Kódà àwọn ìránṣẹ́ wọn tún jẹ gàba lóríi wọn. Ṣùgbọ́n èmi kò ṣe bẹ́ẹ̀ nítorí ìbẹ̀rù Ọlọ́run.'), 'Neh$6:1': ('ní', None, 'Neh$6:1 "Nígbà tí Sáńbálátì, Tòbáyà Géṣémù ará Árábíà àti àwọn ọ̀taa wa tó kù gbọ́ pé, mo ti tún odi náà mọ, kò sì sí àlàfo kankan tí ó ṣẹ́ kù nínú un rẹ̀—bí ó tilẹ̀ jẹ́ pé èmi kò ì tí ì rì àwọn ìlẹ̀kùn ibodè ní àkókò náà."'), 'Neh$6:3': ('n ṣe', 'wá', 'Neh$6:3 "Bẹ́ẹ̀ ni mo rán oníṣẹ́ padà pẹ̀lú èsì yìí pé; “Èmi ń ṣe iṣẹ́ ńlá kan, èmi kò le è sọ̀kalẹ̀ wá. Èéṣe tí iṣẹ́ náà yóò fi dúró, nígbà tí mo bá fi í sílẹ̀ tí mo sì sọ̀kalẹ̀ tọ̀ yín wá?”"'), 'Neh$6:9': ('ṣe', 'iṣẹ', 'Neh$6:9 "Gbogbo wọn múra láti dẹ́rù bà wá, wọ́n ń rò ó wí pé, “Ọwọ́ wọn kò ní ran iṣẹ́ náà, àti wí pé wọn kò ní paríi rẹ̀.” Ṣùgbọ́n mo gbàdúrà pé, “Nísinsìn yìí Ọlọ́run fi agbára fún ọwọ́ mi.”"'), 'Neh$7:1': ('yan', None, 'Neh$7:1 "Lẹ́yìn ìgbà tí a tún odi mọ tán tí mo sì rì àwọn ìlẹ̀kùn sí ààyè wọn, a sì yan àwọn aṣọ́bodè, àwọn akọrin àti àwọn Léfì."'), 'Neh$7:4': (None, 'tobi', 'Neh$7:4 "Ìlú náà tóbi ó sì ní ààyè, ṣùgbọ́n ènìyàn inú rẹ̀ kéré, a kò sì tí ì tún àwọn ilé inú rẹ̀ kọ́."'), 'Neh$8:3': ('ka', None, 'Neh$8:3 "Ó kà á sókè láti òwúrọ̀ títí di ọ̀sán (bí agogo méjìlá) bí ó ti kọjú sí ìta ní iwájú Ibodè-Omi ní ojú u gbogbo ọkùnrin, obìnrin àti àwọn ènìyàn tó kù tí òye le è yé tí wọ́n wà níbẹ̀. Gbogbo ènìyàn sì fetísílẹ̀ sí ìwé òfin náà pẹ̀lú ìfarabalẹ̀."'), 'Neh$8:8': (None, 'ka', 'Neh$8:8 "Wọ́n kà láti inú ìwé òfin Ọlọ́run, wọ́n túmọ̀ rẹ̀, wọ́n ṣe àlàyé kí ohun tí wọ́n kà bá à le yé àwọn ènìyàn yékéyéké."'), 'Neh$8:12': ('ní', 'ṣe', 'Neh$8:12 "Nígbà náà ni gbogbo ènìyàn lọ láti jẹ àti láti mu, wọ́n sì fi ìpín oúnjẹ ránṣẹ́, wọ́n sì ṣe àjọyọ̀ pẹ̀lú ayọ̀ ńlá, nítorí báyìí àwọn òfin tí a sọ di mímọ́ fun wọn ti yé wọn"'), 'Neh$8:14': ('ní', 'ṣe', 'Neh$8:14 "Wọ́n ri ní àkọsílẹ̀ nínú ìwé òfin, èyí tí OLÚWA ti pa ní àṣẹ nípaṣẹ̀ Mósè, kí àwọn ọmọ Ísírẹ́lì gbé inú àgọ́ ní àkókò àjọ àgọ́ oṣù keje"'), 'Neh$8:17': ('ṣe', None, 'Neh$8:17 "Gbogbo ìjọ ènìyàn tí wọ́n padà láti ìgbèkùn kọ́ àgọ́, wọ́n sì ń gbé inú un wọn. Láti ọjọ́ Jóṣúà ọmọ Núnì títí di ọjọ́ náà, àwọn ọmọ Ísírẹ́lì kò tí ì ṣe àjọyọ̀ ọ rẹ̀ bí irú èyí. Ayọ̀ ọ wọn sì pọ̀."'), 'Neh$9:13': ('fun', None, 'Neh$9:13 “Ìwọ sọ̀kalẹ̀ wá sí orí òkè Ṣínáì; ìwọ bá wọn sọ̀rọ̀ láti ọ̀run. Ìwọ fún wọn ní ìlànà àti àwọn òfin tí ó jẹ́ òdodo tí ó sì tọ́ àti ìlànà tí ó dára.'), 'Neh$9:18': ('lọ', None, 'Neh$9:18 "Kódà nígbà tí wọ́n yá ère dídá (ère ọmọ màlúù) fún ara wọn, tí wọ́n sì wí pé, Èyí ni Ọlọ́run rẹ tí ó mú ọ gòkè láti Éjíbítì wá; tàbí nígbà tí wọ́n sọ ọ̀rọ̀-òdì tí ó burú jàì."'), 'Neh$9:25': (None, None, 'Neh$9:25 "Wọ́n gba àwọn ìlú olódì àti ilẹ̀ ọlọ́ràá; wọ́n gba àwọn ilé tí ó kún fún onírúurú gbogbo nǹkan rere, àwọn kàǹga tí a ti gbẹ́, awọn ọgbà àjàrà, awọn ọgbà ólífì àti àwọn igi eléso ní ọ̀pọ̀lọpọ̀. Wọ́n jẹ, wọ́n yó, wọ́n sì sanra dáadáa; wọ́n sì yọ̀ nínú oore ńlá rẹ"'), 'Neh$9:26': (None, None, 'Neh$9:26 "“Ṣùgbọ́n wọ́n ṣe àìgbọ́ràn, wọ́n sì ṣọ̀tẹ̀ sí ọ; wọ́n gbàgbé òfin rẹ. Wọ́n pa àwọn wòlíì rẹ, tí o fi gbà wọn ni ìyànjú pé kí wọn yí padà sí ọ; wọ́n sì ṣe ọ̀rọ̀-òdì tí ó burú jàì."'), 'Neh$9:28': (None, 'ilẹ', 'Neh$9:28 "“Ṣùgbọ́n bí wọ́n bá ti wà nínú ìsinmi, wọn a sì tún ṣe búburú lójùu rẹ. Nígbà náà ni ìwọ kọ̀ wọ́n sílẹ̀ sọ́wọ́ àwọn ọ̀ta kí wọ́n lè jọba lóríi wọn. Nígbà tí wọ́n bá sì tún kígbe sí ọ, ìwọ a gbọ́ láti ọ̀run wá, àti nínú àánú rẹ ni ìwọ gbà láti ìgbà dé ìgbà."'), 'Neh$9:30': ('fun', 'ọwọ', 'Neh$9:30 "Fún ọ̀pọ̀lọ̀pọ̀ ọdún ni ìwọ fi ní ṣùúrù pẹ̀lùu wọn. Nìpa ẹ̀míì rẹ ni ìwọ kìlọ̀ fún wọn nípaṣẹ̀ àwọn wòlíì. Síbẹ̀ wọn kò fi etí sílẹ̀, nìtorì náà ni ìwọ ṣe fi wọ́n lé àwọn aládùúgbò wọn lọ́wọ́."'), 'Neh$9:35': ('nla', 'tobi', 'Neh$9:35 "Àní nígbà tí wọ́n wà nínú ìjọba wọn, tí wọ́n ń gbádùn oore ńlá tí ìwọ fi fún wọn, ní ilẹ̀ tí ó tóbi tí ó sì lọ́ràá, wọn kò sìn ọ́ tàbí padà kúrò nínú àwọn ọ̀nà búburúu wọn."'), 'Neh$10:28': ('ní', None, 'Neh$10:28 "“Àwọn ènìyàn tó kù—àwọn àlùfáà, àwọn ọmọ Léfì, àwọn aṣọ́nà, àwọn ìránṣẹ́ tẹ́ḿpìlì àti gbogbo àwọn tí wọ́n ya ara wọn sọ́tọ̀ kúrò lára àwọn ènìyàn àjèjì nítorí òfin Ọlọ́run, papọ̀ pẹ̀lú ìyàwóo wọn, gbogbo ọmọkùnrin àti àwọn ọmọbìnrin wọn, tí òye yé"'), 'Neh$10:32': ('ṣe', 'iṣẹ', 'Neh$10:32 “Àwa gbà ojúṣe láti máa pa àṣẹ mọ́ pé a ó máa san ìdámẹ́ta ṣékélì ní ọdọọdún fún iṣẹ́ ilé Ọlọ́run wa:'), 'Neh$10:38': ('gba', 'mú', 'Neh$10:38 "Àlùfáà tí o ti ìdílé Árónì wá ni yóò wá pẹ̀lú àwọn ọmọ Léfì nígbà tí wọ́n bá ń gba ìdámẹ́wàá, àwọn ọmọ Léfì yóò sì mú ìdámẹ́wàá ti ìdámẹ́wàá náà wá sí ilé Ọlọ́run, sí yàrá ìpamọ́ inú ilé ìṣúra."'), 'Neh$12:28': (None, None, 'Neh$12:28 "A kó àwọn akọrin náà jọ papọ̀ láti àwọn ìletò tí ó yí Jérúsálẹ́mù náà ká—láti àwọn abúlé Nétófátítì,"'), 'Neh$12:31': (None, None, 'Neh$12:31 Mo sì tún yan àwọn olórí Júdà láti gun orí odi náà. Mo sì tún yan àwọn ẹgbẹ́ akọrin ńlá méjì láti dúpẹ́. Àwọn kan yóò gòkè odi lọ sí apá ọ̀tún sí ọ̀nà ibodè Ààtàn.'), 'Neh$12:38': ('ṣi', None, 'Neh$12:38 "Àwọn ẹgbẹ́ akọrin kejì gba ọ̀nà òdì kejì lọ. Mo tẹ̀lé wọn ní orí odi, pẹ̀lú ìdajì àwọn ènìyàn, kọjá ilé ìṣọ́ ìléru lọ sí odi fífẹ̀,"'), 'Neh$13:19': (None, 'ẹrù', 'Neh$13:19 "Nígbà tí ojú ọjọ́ ń bora ní ẹnu ibodè Jérúsálẹ́mù, ṣáájú ọjọ́ ìsinmi, mo pàṣẹ pé kí a ti àwọn ìlẹ̀kùn, kí wọn má sì sí i títí tí ọjọ́ ìsinmi yóò fi kọjá. Mo yan àwọn ìránṣẹ́ẹ̀ mi láti ṣọ́ ẹnu ibodè, kí a má ba à lè gbé ẹrù kankan wọlé ní ọjọ́ ìsinmi."'), 'Neh$13:23': (None, None, 'Neh$13:23 "Ní ọjọ́ wọ̀n ọn nì, mo rí àwọn ọkùnrin Júdà tí wọ́n fẹ́ àwọn obìnrin láti Áṣídódù, Ámónì àti Móábù."'), 'Neh$13:25': (None, None, 'Neh$13:25 "Mo bá wọn wí mo sì gégùn-ún lé wọn lórí. Mo lu àwọn ènìyàn díẹ̀ nínú un wọn mo sì fa irun oríi wọn tu. Mo mú kí wọn búra ní orúkọ Ọlọ́run, kí wọn wí pé, “Ẹ̀yin kì yóò fi àwọn ọmọbìnrin wọn fún àwọn ọmọkùnrin yín ní ìyàwó tàbí fún ẹ̀yin tìkáara yín."'), 'Esth$1:9': (None, 'ọba', 'Esth$1:9 "Ayaba Fásítì náà ṣe àṣè fún àwọn obìnrin ní ààfin ọba Ṣérísésì,"'), 'Esth$1:10': (None, 'ọba', 'Esth$1:10 "Ní ọjọ́ keje, nígbà tí wáìnì mú inú ọba dùn, ó pàṣẹ fún Méhúmínì, Bísítà, Hábónà, Bígítà àti Ábágítà, Ṣétarì àti Kákásì, àwọn ìwẹ̀fà méje tí ń jíṣẹ́ fún un."'), 'Esth$1:12': ('ọba', 'ayaba', 'Esth$1:12 "Ṣùgbọ́n nígbà tí àwọn ìránṣẹ́ jẹ́ iṣẹ́ ọba, ayaba Fásítì kọ̀ láti wá. Nígbà náà ni ọba bínú gidgidi, ìbínú náà sì pọ̀ jọjọ."'), 'Esth$1:13': (None, 'ọba', 'Esth$1:13 "Gẹ́gẹ́ bí ìṣe rẹ̀ ní ìgbà gbogbo, ọba máa ń béèrè ọ̀rọ̀ lọ́wọ́ àwọn tí ó ní ìmọ̀ òfin àti ìdájọ́, ó sọ ọ́ fún àwọn amòye tí wọ́n mòye àkókò,"'), 'Esth$1:16': ('ọba', None, 'Esth$1:16 "Mémúkánì sì dáhùn níwájú ọba àti àwọn ọlọ́lá pé, “Ayaba Fásítì ti ṣe búburú, kì í ṣe sí ọba nìkan Ṣùgbọ́n sí gbogbo àwọn ọlọ́lá àti àwọn ènìyàn tí ó wà ní gbogbo agbégbé ilẹ̀ ọba Ṣérísésì."'), 'Esth$1:17': ('ayaba', 'ọba', 'Esth$1:17 "Nítorí ìwà ayaba yìí yóò tàn dé ọ̀dọ̀ gbogbo àwọn obínrin, tó bẹ́ẹ̀ tí ọkọ wọn yóò di gígàn lójúu wọn, wọn yóò sì sọ pé, ọba Ṣérísésì pàṣẹ̀ pé kí á mú ayaba Fásítì wá ṣíwájú òun, ṣùgbọ́n ó kọ̀ láti wá."'), 'Esth$1:18': ('ayaba', 'ọba', 'Esth$1:18 Ní ọjọ́ yìí gan-an ni àwọn ọlọ́lá obinrin Páṣíà àti ti Médíánì tí wọ́n ti gbọ́ nípa ìwà ayaba wọn yóò ṣe bẹ́ẹ̀ sí gbogbo àwọn ìjòyè ọba bákan náà. Àfojúdi àti àìnírẹ́pọ̀ tí kò lópin yóò wà.'), 'Esth$1:20': ('fun', 'ṣe', 'Esth$1:20 "Nígbà náà tí a bá kéde òfin tí ọba ṣe ká gbogbo ilẹ̀ ìjọba rẹ̀, gbogbo àwọn obìnrin ni yóò bọ̀wọ̀ fún ọkọ wọn, láti ọ̀dọ̀ ẹni tí ó kéré títí dé ọ̀dọ̀ ẹni ńlá.”"'), 'Esth$2:2': ('ọba', None, 'Esth$2:2 "Nígbà naà ni ìránṣẹ́ ọba tí ó wà ní ọ̀dọ̀ ọ rẹ̀ wí pé, “jẹ́ kí a wá ọmọbìnrin arẹwà tí kòì mọ ọkùnrin rí fún ọba."'), 'Esth$2:7': ('ní', None, 'Esth$2:7 "Módékáì ní arákùnrin kan ẹni tí orúkọ rẹ̀ ń jẹ́ Hádásà, ẹni tí ó tọ́ dàgbà nítorí tí kò ní bàbá bẹ́ẹ̀ sì ni kò ní ìyá. Obìnrin yìí, ẹni tí a tún mọ̀ sí Ẹ́sítà, ó dára ó sì lẹ́wà, Módékáì mú u gẹ́gẹ́ bí ọmọ rẹ̀ nígbà tí bàbá àti ìyá rẹ̀ ti kú."'), 'Esth$2:9': ('awọn iranṣẹbinrin', None, 'Esth$2:9 "Ọmọbìnrin náà sì wù ú, ó sì rí ojú rere rẹ̀, lẹ́ṣẹ̀kẹ́ṣẹ̀ ó pèṣè àwọn ohun tí ó dára àti oúnjẹ pàtàkì fún-un. Ó sì yan àwọn ìránṣẹ́bìnrin wúndíá méje láti ààfin ọba òun àti àwọn ìránṣẹ́bìnrin náà lọ sí ibi tí ó dára jù nínú ilé àwọn obìnrin."'), 'Esth$2:12': (None, None, 'Esth$2:12 "Kí àkókò tó tó fún obìnrin kọ̀ọ̀kan láti lọ sí ọ̀dọ̀ ọba Ṣérísésì, ó ní láti lo ohun èlò tí ń mú ara lẹ́wà tí a yàn fún àwọn obìnrin fún oṣù méjìlá, yóò lo òróró òjía fún oṣù mẹ́fà, yóò sì lo ohun èlò olóòrùn dídùn tùràrí àti ìpara fún oṣù mẹ́fà pẹ̀lú."'), 'Esth$2:15': (None, 'lọ', 'Esth$2:15 "Nígbà tí ó kan Ẹ́sítà (ọmọbìnrin tí Módékáì gbà ṣe ọmọ, ọmọbìnrin arákùnrin rẹ̀ tí ó ń jẹ́ Ábíháílì) láti lọ sí ọ̀dọ̀ ọba, wọn kò bèrè fún ohunkóhun ju èyí tí Hégáì, ìwẹ̀fà ọba ẹni tí ó jẹ́ olùtọ́jú ilé àwọn obìnrin sọ pé kí ó ṣe lọ. Ẹ́sítà sì rí ojú rere lọ́dọ̀ gbogbo àwọn tí ó rí i."'), 'Esth$2:17': ('ọba', 'ayaba', 'Esth$2:17 "Ésítà sì wu ọba ju àwọn obìnrin tó kù lọ, Ó sì rí ojú rere àti oore ọ̀fẹ́ gbà ju ti àwọn wúndíá tó kù lọ. Nítorí náà ó fi adé ọba dé e ní orí ó sì fi ṣe ayaba dípò Fásítì."'), 'Esth$2:22': ('ayaba', 'ọba', 'Esth$2:22 "Ṣùgbọ́n Módékáì sì mọ̀ nípa ọ̀tẹ̀ náà, ó sọ̀ fún ayaba Ẹ́sítà, Ẹ́sítà sì sọ fún ọba, wọ́n sì fi ọlá fún Módékáì."'), 'Esth$3:1': (None, None, 'Esth$3:1 "Lẹ́yìn àwọn ìṣẹ̀lẹ̀ wọ̀nyí, ọba Ṣérísésì dá Hámánì ọmọ Hámádátà, ará a Ágágì lọ́lá, ọba gbé e ga, ó si fún un ní àga ọlá tí ó ju ti gbogbo àwọn ọlọ́lá tó kù lọ."'), 'Esth$3:9': (None, None, 'Esth$3:9 "Tí ó bá tẹ́ ọba lọ́rùn, kí a gbé òfin kan jáde tí yóò pa wọ́n run, èmi yóò sì fi ẹgbàarùn-ún talẹ́ńtì fàdákà sínú ìṣúra ọba fún àwọn ọkùnrin tí wọn ṣe iṣẹ́ náà.”"'), 'Esth$3:10': ('fun', None, 'Esth$3:10 "Nítorí náà ọba sì bọ́ òrùka dídán (èdìdì) tí ó wà ní ìka rẹ̀ ó sì fi fún Hámánì ọmọ Hámédátà, ará Ágágì, ọ̀ta àwọn Júù."'), 'Esth$3:12': (None, 'oruka', 'Esth$3:12 "Nígbà tí ó di ọjọ́ kẹtàlá oṣù kìn-ín-ní àkọ́kọ́, wọ́n pe àwọn akọ̀wé ọba jọ. Wọ́n kọ ọ́ ní ìlànà bí ìgbèríko kọ̀ọ̀kan ṣe ń kọ̀wé, ó kọ̀ wé sí àwọn ènìyàn kọ̀ọ̀kan ní èdè wọn gbogbo èyí tí Hámánì ti pa láṣẹ sí àwọn akọ̀wé ọba, sí baálẹ̀ ìgbèríko kọ̀ọ̀kan àti àwọn ọlọ́lá àwọn onírúurú ènìyàn. A kọ èyí ní orúkọ ọba Ṣérísésì fúnra rẹ̀ ó sì fi òrùka ọba ṣe èdìdì i rẹ̀."'), 'Esth$4:8': ('fun', 'ṣe', 'Esth$4:8 "Ó sì tún fún un ní ọ̀kan lára ìwé tí ó gbé jáde fún ìparun àwọn, èyí tí a tẹ̀ jáde ní Ṣúṣà, láti fi han Ésítà kí ó sì ṣe àlàyé e rẹ̀ fún-un, ó sì sọ fún-un pé kí ó bẹ̀ ẹ́ kí ó lọ ṣíwájú ọba láti bẹ̀bẹ̀ fún àánú, kí ó bẹ̀bẹ̀ nítorí àwọn ènìyàn an rẹ̀."'), 'Esth$4:10': (None, None, 'Esth$4:10 "Nígbà náà ni Ésítà pàṣẹ fún un pé kí ó sọ fún Módékáì,"'), 'Esth$4:15': (None, None, 'Esth$4:15 Nígbà náà ni Ẹ́sítà rán iṣẹ́ yìí sí Módékáì:'), 'Esth$5:2': (None, 'ayaba', 'Esth$5:2 "Nígbà tí ó rí ayaba Ẹ́sítà tí ó dúró nínú àgbàlá, inú un rẹ̀ yọ́ síi, ó sì na ọ̀pá aládé wúrà ọwọ́ọ rẹ̀ sí i. Bẹ́ẹ̀ ni Ẹ́sítà ṣe ṣún mọ́ ọn ó sì fi ọwọ́ kan orí ọ̀pá náà."'), 'Esth$5:3': ('ọba', 'ayaba', 'Esth$5:3 " Nígbà náà ni ọba béèrè pé, “kí ni ó dé, ayaba Ẹ́sítà? Kí ni o fẹ́? Bí ó tilẹ̀ ṣe títí dé ìdajìn mi ìjọba àní, a ó fi fún ọ.”"'), 'Esth$5:4': ('jẹ ki', 'ni', 'Esth$5:4 "Ẹ́sítà sì dáhùn pé, “Bí ó bá tẹ́ ọba lọ́rùn, jẹ́ kí ọba, pẹ̀lúu Hámánì, wá lónìí sí ibi àṣè tí èmi ti pèṣè fún-un.”"'), 'Esth$5:7': (None, 'ni', 'Esth$5:7 "Ẹ́sítà sì dáhùn, “Ẹ̀bẹ̀ mi àti ìbéèrè mi ni èyí:"'), 'Esth$5:12': ('ayaba', 'ọba', 'Esth$5:12 "Hámánì tún fi kún-un pé, “Kìí ṣe èyí nìkan. Èmi nìkan ni ayaba Ẹ́sítà pè láti sin ọba wá sí ibi àsè tí ó sè. Bákan náà, ó sì tún ti pè mí pẹ̀lú ọba ní ọ̀la."'), 'Esth$6:1': (None, 'iwe', 'Esth$6:1 "Ní òru ọjọ́ náà ọba kò le è ṣùn; nítorí náà, ó pàṣẹ kí wọn mú ìwé ìrántí wá, àkọsílẹ̀ ìjọba rẹ̀, wọ́n mú un wá wọ́n sì kàá síi létí."'), 'Esth$6:7': (None, None, 'Esth$6:7 "Nítorí náà ó dá ọba lóhùn pé, “Fún ọkùnrin tí inú ọba dùn sí láti dá lọ́lá,"'), 'Esth$7:1': (None, 'ayaba', 'Esth$7:1 "Ọba àti Hámánì sì lọ sí àpèjẹ pẹ̀lú Ẹ́sítà ayaba,"'), 'Esth$7:2': ('ọba', 'ayaba', 'Esth$7:2 " Bí wọ́n sì ṣe ń mu wáìnì ní ọjọ́ kejì yìí, ọba sì tún béèrè pé, “Ẹ́sítà ayaba, kí ni ẹ̀bẹ̀ rẹ? A ó sì fi fún ọ. Kí ni ìbéèrè rẹ? Bí ó ti lẹ̀ jẹ́ ìdajì ìjọbaà mi, n ó fi fún ọ.”"'), 'Esth$7:3': (None, 'ni', 'Esth$7:3 "Nígbà náà ni ayaba Ẹ́sítà dáhùn, “Bí èmi bá rí ojú reree rẹ, ìwọ ọba, bí ó bá sì tẹ́ ọláà ńlá à rẹ lọ́rùn, fún mi ní ẹ̀míì mi-èyí ni ẹ̀bẹ̀ mi. Kí o sì pa àwọn ènìyàn mi mọ́-èyí ni ìbéèrè mi."'), 'Esth$7:5': ('ayaba', None, 'Esth$7:5 "Ọba Ṣéríṣésì sì bi Ésítà ayaba léèrè pé, “ta a ni ẹni náà? Níbo ni ẹni náà wà tí kò bẹ̀rù láti ṣe irú nǹkan bẹ́ẹ̀?”"'), 'Esth$7:7': ('ayaba', 'ọba', 'Esth$7:7 "Ọba sì dìde pẹ̀lú ìbínú, ó fi wáìnì sílẹ̀ ó sì jáde lọ sí àgbàlá ààfin. Ṣùgbọ́n nígbà tí Hámánì, ti ríi dájú pé ọba ti pinnu láti pa òun, ó dúró lẹ́yìn láti bẹ Ẹ́sítà ayaba nítorí ẹ̀míi rẹ̀."'), 'Esth$8:1': ('ayaba', 'ọba', 'Esth$8:1 "Ní ọjọ́ kan náà ni ọba Ṣérísésì fún Ésítà ayaba ní ilée Hámánì, ọ̀ta àwọn Júù. Módékáì sì wá síwájú ọba, nítorí Ẹ́sítà ti sọ bí ó ṣe jẹ́ sí ọba."'), 'Esth$8:7': ('ayaba', None, 'Esth$8:7 "Ọba Ṣéríṣésì dá Ẹ́sítà ayaba àti Módékáì aráa Júù náà lóhùn pé, “Nítorí Hámánì kọ lu àwọn aráa Júù, èmi ti fi ilée rẹ̀ fún Ẹ́sítà, wọ́n sì ti ṣo ó kọ́ sórí igi."'), 'Esth$8:10': (None, 'ọba', 'Esth$8:10 "Modékáì sì fi àṣẹ ọba Ṣéríṣésì kọ̀wé, ó sì fi òrùka ọba ṣe èdìdì i rẹ̀, ó rán-an lọ ní kíákíá, ó sì rán àwọn ìránṣẹ́ ayaba, bí àṣà àwọn tí wọ́n ń gun ẹṣin tí ó yára, ní pàtàkì èyí tí a ń bọ́ fún ọba."'), 'Esth$8:15': ('ọba', None, 'Esth$8:15 "Módékáì sì kúrò níwájú ọba, ó wọ aṣọ aláró àti funfun, ó dé adé e wúrà ńlá pẹ̀lú ìgbànú eléṣèé àlùkò dáradára, ìlú Ṣúṣà sì ṣe àjọyọ̀ ńlá."'), 'Esth$9:1': ('agbara', 'ọba', 'Esth$9:1 "Ní ọjọ́ kẹtàlá oṣù kejìlá, oṣù Ádárì, tí ó yẹ kí a mú àṣẹ tí ọba pa wá sí ìmúṣẹ. Ní ọjọ́ yìí ni ọ̀tá àwọn Júù rò pé àwọn yóò borí i wọn, ṣùgbọ́n nísinsin yìí a ti yìí padà, àwọn Júù sì na ọwọ́ agbára tó ga lórí àwọn tí ó kóòríra wọn."'), 'Esth$9:4': (None, None, 'Esth$9:4 "Módékáì sì jẹ́ ẹni pàtàkì ní ààfin, òkìkíi rẹ̀ sì tàn jákè jádò àwọn ìgbéríko, ó sì ní agbára kún agbára."'), 'Esth$9:12': ('ayaba', 'ọba', 'Esth$9:12 "Ọba sì sọ fún Ẹ́sítà ayaba pé, “Àwọn Júù ti pa ẹ̀ẹ́dẹ̀gbẹ̀ta (500) ọkùnrin àti àwọn ọmọkùnrin mẹ́wẹ̀ẹ̀wá tí i ṣe ọmọ Hámánì ní ilé ìṣọ́ Ṣúṣà run. Kí ni wọ́n ṣe ní gbogbo ìgbèríko ọba tó kù? Báyìí kí ni ẹ̀bẹ̀ rẹ? A ó fi fún ọ. Kí ni ìbéèrè rẹ? A ó sì tún fi fún ọ.”"'), 'Esth$9:23': ('ní', None, 'Esth$9:23 "Bẹ́ẹ̀ ni àwọn Júù gbà láti máa ṣe àjọyọ̀ tí wọ́n ti bẹ̀rẹ̀, wọ́n ń ṣe bí Módékáì ti kọ̀wé sí wọn."'), 'Esth$10:2': ('agbara', 'ọba', 'Esth$10:2 "Àti gbogbo ìṣe agbára àti títóbi rẹ̀, papọ̀ pẹ̀lú ìròyìn títóbi Módékáì ní èyí tí ọba ti gbé e ga, kò ha wà nínú àkọsílẹ̀ ìwé ọdọọdún ọba ti Médíà àti ti Páṣíà?"'), 'Esth$10:3': ('eniyan', None, 'Esth$10:3 "Módékáì ará Júù ni ó jẹ́ igbá kejì ọba Ṣéríṣésì, ó tóbi láàrin àwọn Júù, ó sì jẹ́ ẹni iyì lọ́dọ̀ àwọn Júù ẹlẹgbẹ́ ẹ rẹ̀, nítorí tí ó ń ṣiṣẹ́ fún ire àwọn ènìyàn an rẹ̀, ó sì ń sọ̀rọ̀ fún àlàáfíà gbogbo àwọn Júù."'), 'Job$1:3': (None, None, 'Job$1:3 "Ohun ọ̀sìn rẹ̀ sì jẹ́ ẹ̀ẹ́dẹ́gbàarin (7000) àgùntàn, àti ẹgbẹ̀ẹ́dógún (3000) ìbákasíẹ, àti ẹ̀ẹ́dẹ́gbẹ̀ta (500) àjàgà ọdámàlúù, àti ẹ̀ẹ́dẹ́gbẹ̀ta (500) abo kẹ́tẹ́kẹ́tẹ́, ó sì pọ̀; bẹ́ẹ̀ ni ọkùnrin yìí sì pọ̀ jù gbogbo àwọn ará ìlà òòrun lọ."'), 'Job$1:13': (None, None, 'Job$1:13 "Ó sì di ọjọ́ kan nígbà tí àwọn ọmọ rẹ̀ ọkùnrin àti obìnrin, tí wọ́n mu ọtí wáìnì nínú ilé ẹ̀gbọ́n wọn ọkùnrin:"'), 'Job$1:16': ('nikan', 'nikan', 'Job$1:16 "Bí ó ti ń sọ ní ẹnu; ẹnìkan dé pẹ̀lu tí ó sì wí pé, “Iná ńlá Ọlọ́run ti ọ̀run bọ́ sí ilẹ̀, ó sì jó àwọn àgùntàn àti àwọn ìránṣẹ́; èmi níkàn ṣoṣo ní ó sálà láti ròyìn fún ọ.”"'), 'Job$1:17': ('ṣe', 'ni', 'Job$1:17 "Bí ó sì ti ń sọ ní ẹnu, ẹnìkan sì dé pẹ̀lú tí ó wí pé, “Àwọn ará Kádéà píngun sí ọ̀nà mẹ́ta, wọ́n sì kọ lù àwọn ìbákasíẹ, wọ́n sì kó wọn lọ, pẹ̀lupẹ̀lu wọ́n sì fi idà ṣá àwọn ìránṣẹ́ pa; èmi nìkan ṣoṣo ni ó sá àsálà láti ròyìn fún ọ!”"'), 'Job$1:18': ('nikan', 'nikan', 'Job$1:18 "Bí ó ti ń sọ ní ẹnu, ẹnikàn dé pẹ̀lú tí ó sì wí pé àwọn ọmọ rẹ ọkùnrin àti obìnrin ń jẹ, wọ́n ń mu ọtí wáìnì nínú ilé ẹ̀gbọ́n."'), 'Job$1:20': (None, None, 'Job$1:20 "Nígbà náà ni Jóòbù dìde, ó sì fa aṣọ ìgunwà rẹ̀ ya, ó sì fá orí rẹ̀ ó wólẹ̀ ó sì gbàdúrà"'), 'Job$2:11': ('ní', None, 'Job$2:11 "Nígbà tí àwọn ọ̀rẹ́ Jóòbù mẹ́ta gbúròó gbogbo ibi tí ó bá a, wọ́n wá, olúkúlùkù láti ibùjókòó rẹ̀; Élífásì, ara Témà àti Bílídádì, ara Ṣúà, àti Sófárì, ará Náámù: nítorí pé wọ́n ti dájọ́ ìpàdé pọ̀ láti bá a sọ̀fọ̀ àti láti sìpẹ̀ fún un."'), 'Job$3:5': ('okunkun', 'ojiji', 'Job$3:5 Kí òkùnkùn àti òjìjì ikú fi ṣe ti ara wọn; kí àwọ-sánmọ̀ kí ó bà lé e; kí ìṣúdúdú ọjọ́ kí ó pa láyà.'), 'Job$3:6': (None, None, 'Job$3:6 "Kí òkùnkùn kí ó ṣú bo òru náà biribiri, kí ó má ṣe yọ pẹ̀lú ọjọ́ ọdún náà: kí ó má ṣe kà a mọ́ iye ọjọ́ oṣù."'), 'Job$3:9': (None, 'ni', 'Job$3:9 "Kí ìràwọ̀ òféfé ọjọ́ rẹ̀ kí ó ṣókùnkùn; kí ó má wá ìmọ́lẹ̀, ṣùgbọ́n kí ó máa mọ́ síi, bẹ́ẹ̀ ni kí ó má ṣe rí àfẹ̀rẹ̀mọ́jú mọ́"'), 'Job$3:13': (None, None, 'Job$3:13 "Ǹjẹ́ nísinsìn yìí èmi ìbá ti dúbúlẹ̀ jẹ́ẹ́; èmi a sì dákẹ́, èmi ìbá ti sùn ǹjẹ́ èmi ìbá ti sinmi"'), 'Job$4:15': (None, None, 'Job$4:15 "Nígbà náà ni iwin kan kọjá lọ ní iwájú mi, irun ara mi dìde ró ṣánṣán."'), 'Job$5:3': (None, None, 'Job$5:3 "Èmi ti rí aláìmòye ti ó ta gbọ̀n-ọ́n-gbọ̀n-ọ́n lulẹ̀, ṣùgbọ́n lójú kan náà mo fi ibùjókòó rẹ̀ bú."'), 'Job$6:4': (None, None, 'Job$6:4 "Nítorí pé ọfà Olódùmárè wọ̀ mi nínú, oró èyí tí ọkàn mi mú; ìpayà-ẹ̀rù Ọlọ́run dúró tì mí."'), 'Job$6:10': (None, 'ni', 'Job$6:10 "Nígbà náà ní èmi ì bá ní ìtùnú síbẹ̀, àní èmi ì bá mú ọkàn mi le nínú ìbànújẹ́ mi ti kò dá ni sí: nítorí èmi kò fi ọ̀rọ̀ ẹni mímọ́ ni sin rí. "'), 'Job$6:15': (None, None, 'Job$6:15 "Àwọn ará mi ṣọ̀tẹ̀ bí odò sólo bí ìṣàn gúru omi odò sólo, wọ́n ṣàn kọjá lọ."'), 'Job$6:18': (None, None, 'Job$6:18 "Ìyà ọ̀nà wọn a sì yípadà sí apá kan, wọ́n gòkè sí ibi asán, wọ́n sì run."'), 'Job$6:24': ('ni', None, 'Job$6:24 "“Ẹ kọ́ mi, èmi ó sì pa ẹnu mi mọ́ kí ẹ sì mú mi wòye níbi tí mo gbé ti sìnà."'), 'Job$6:26': (None, None, 'Job$6:26 Ẹ̀yin ṣè bí ẹ ó bá ọ̀rọ̀ àti ohùn ẹnu tí ó takú wí tí ó dà bí afẹ́fẹ́.'), 'Job$7:2': ('ṣe', 'iṣẹ', 'Job$7:2 Bí ọmọ-ọ̀dọ̀ tí máa ń kanjú bojúwo òjíjì àti bí alágbàṣe ti í kánjú wo ọ̀nà owó iṣẹ́ rẹ̀.'), 'Job$7:13': (None, None, 'Job$7:13 "Nígbà tí mo wí pé, ibùsùn mi yóò tù mí lára, ìtẹ́ mi yóò gbé ẹrù ìráhùn mi pẹ̀lú."'), 'Job$7:16': ('ni', None, 'Job$7:16 "O ṣú mi, èmi kò le wà títí: jọ̀wọ́ mi jẹ́, nítorí pé asán ni ọjọ́ mi. "'), 'Job$8:4': ('ṣe', 'ni', 'Job$8:4 "Nígbà tí àwọn ọmọ rẹ̀ sẹ̀ sí i, ó sì gbá wọn kúrò nítorí àṣẹ wọn."'), 'Job$8:15': ('duro', None, 'Job$8:15 "Yóò fi ara ti ilé rẹ̀, ṣùgbọ́n kì yóò lè dúró, yóò fi di ara rẹ̀ mú ṣinṣin ṣùgbọ́n kì yóò lè dúró pẹ́"'), 'Job$8:20': (None, None, 'Job$8:20 "“Kíyèsí i, Ọlọ́run kì yóò ta ẹni òtítọ́ nù, bẹ́ẹ̀ ni kì yóò ran oníwà-búburú lọ́wọ́"'), 'Job$9:24': (None, 'ọwọ', 'Job$9:24 Nígbà tí a bá fi ayé lé ọwọ́ ènìyàn búburú; ó sì bo àwọn onídàájọ́ rẹ̀ lójú; bí kò bá rí bẹ́ẹ̀ ǹjẹ́ ta ni?'), 'Job$9:27': ('gbagbe', None, 'Job$9:27 "Bí èmi bá wí pé, ‘Èmi ó gbàgbé arò ìbìnújẹ́ mi, èmi ó fi ọkàn lélẹ̀, èmi ó sì rẹ ara mi lẹ́kún.’"'), 'Job$10:8': (None, 'ni', 'Job$10:8 "“Ọwọ́ rẹ ni ó dámi, tí ó sì mọ mí pọ̀ yíkákiri; síbẹ̀ ìwọ sì ńbà mí jẹ́."'), 'Job$10:12': (None, 'ẹmi', 'Job$10:12 "Ìwọ ti fún mi ní ẹ̀mí àti ojú rere, ìbẹ̀wò rẹ sì pa ọkàn mi mọ́. "'), 'Job$11:3': (None, None, 'Job$11:3 Ṣé àmọ̀tàn rẹ le mú ènìyàn pa ẹnu wọn mọ́ bí? Ṣé ẹnikẹ́ni kò ní bá ọ wí bí ìwọ bá yọ ṣùtì sí ni?'), 'Job$11:12': (None, 'eniyan', 'Job$11:12 "Ṣùgbọ́n òmùgọ̀ ènìyàn yóò di ọlọgbọ́n, nígbà tí ọmọ kẹ́tẹ́kẹ́tẹ́ bá bí ènìyàn. "'), 'Job$12:4': (None, None, 'Job$12:4 "“Èmi dàbí ẹni tí a ń fi ṣe ẹlẹ́yà lọ́dọ̀ aládùúgbò rẹ̀, tí ó ké pe Ọlọ́run, tí ó sì dá a lóhùn: à ń fi olóòótọ́ ẹni-ìdúró-ṣinṣin rẹ́rìn-ín ẹlẹ́yà."'), 'Job$12:22': ('okunkun', 'ojiji', 'Job$12:22 "Ó hú ìdí ohun ìjìnlẹ̀ jáde láti inú òkùnkùn wá, Ó sì mú òjìji ikú wá sínú ìmọ́lẹ̀."'), 'Job$13:18': ('ni', None, 'Job$13:18 "Wò ó nísinsin yìí, èmi ti làdí ọ̀ran mi sílẹ̀; èmi mọ̀ pé a ó dá mi láre."'), 'Job$14:8': (None, 'ilẹ', 'Job$14:8 "Bí gbòǹgbò rẹ̀ tilẹ̀ di ogbó nínú ilẹ̀, tí kùkùté rẹ̀ si kú ni ilẹ̀;"'), 'Job$15:6': ('ète', None, 'Job$15:6 "Ẹnu ara rẹ̀ ni ó dá lẹ́bi, kì í ṣe èmi; àní ètè ara rẹ̀ ni ó jẹ́rìí tì ọ́. "'), 'Job$15:17': (None, 'ni', 'Job$15:17 "“Èmi ó fihàn ọ́: gbọ́ ti èmi; Èyí tí èmi sì rí, òun ni èmi ó sì sọ,"'), 'Job$15:24': ('ṣe', None, 'Job$15:24 "Ìpọ́njú pẹ̀lú ìrora ọkàn yóò mú un bẹ̀rù, wọ́n ó sì sẹ́gun rẹ̀ bi ọba ti ímúra ogun."'), 'Job$15:26': (None, None, 'Job$15:26 "Ó súre, ó sì fi ẹ̀yìn giga, àní fi ìké kòóko àpáta rẹ̀ tí ó nipọn kọlù ú. "'), 'Job$15:30': ('lọ', 'lọ', 'Job$15:30 "Òun kì yóò jáde kúrò nínú òkùnkùn; ọ̀wọ́ iná ni yóò jó ẹ̀ka rẹ̀, àti nípaṣẹ̀ ẹ̀mí ẹnu rẹ̀ ní yóò máa kọjá lọ kúrò."'), 'Job$16:5': ('ète', 'ẹnu', 'Job$16:5 "Èmi ìbá fi ọ̀rọ̀ ẹnu mi gbà yín ni ìyànjú, àti ṣíṣí ètè mi ìbá sì tu ìbìnújẹ́ yín. "'), 'Job$16:12': (None, None, 'Job$16:12 "Mo ti jókòó jẹ́ẹ́, ṣùgbọ́n ó fà mí já; ó sì dì mí ọrùn mú, ó sì gbọ̀n mí túútúú, ó sì gbé mi kalẹ̀ ṣe àmì—ìtàfàsí rẹ̀."'), 'Job$16:18': (None, 'ni', 'Job$16:18 "“Áà! Ilẹ̀ ayé, ìwọ má ṣe bò ẹ̀jẹ̀ mi, kí ẹkún mi má ṣe wà ní ipò kan."'), 'Job$17:10': ('ṣe', None, 'Job$17:10 "“Ṣùgbọ́n bí ó ṣe ti gbogbo yín, ẹ yí padà, kí ẹ sì tún bọ̀ nísinsin yìí; èmi kò le rí ọlọgbọ́n kan nínú yín."'), 'Job$17:12': (None, 'imole', 'Job$17:12 "Àwọn ènìyàn wọn yí ìsọ́ òru di ọ̀sán; wọ́n ní, ìmọ́lẹ̀ súnmọ́ ibi tí òkùnkùn dé."'), 'Job$17:13': (None, 'ni', 'Job$17:13 "Bí mo tilẹ̀ ní ìrètí, ipò òku ní ilé mi; mo ti tẹ́ ìbùsùn mi sínú òkùnkùn."'), 'Job$18:5': ('imole', None, 'Job$18:5 "“Nítòótọ́ ìmọ́lẹ̀ ènìyàn búburú ni a ó pa kúrò, Ọ̀wọ́ iná rẹ̀ kì yóò sì tan ìmọ́lẹ̀."'), 'Job$18:10': (None, None, 'Job$18:10 "A dẹkùn sílẹ̀ fún un lórí ilẹ̀, a sì wà ọ̀fìn fún un lojú ọ̀nà."'), 'Job$19:3': (None, 'ni', 'Job$19:3 Ìgbà mẹ́wàá ní ẹ̀yin ti ń gàn mí; ojú kò tìyín tí ẹ fi jẹ mí níyà.'), 'Job$19:15': ('ajeji', None, 'Job$19:15 Àwọn ará inú ilé mi àti àwọn ìránṣẹ́bìnrin mi kà mí sí àjèjì; èmi jásí àjèjì ènìyàn ní ojú wọn.'), 'Job$20:1': (None, None, 'Job$20:1 "Ígbà náà ní Sófárì, ara Náámà dáhùn, ó sì wí pé:"'), 'Job$20:6': (None, None, 'Job$20:6 "Bí ọláńlá rẹ̀ tilẹ̀ gòkè dé ọ̀run, ti ori rẹ̀ sì kan àwọsánmà;"'), 'Job$20:9': ('ri', None, 'Job$20:9 "Ojú tí ó ti rí i rí kì yóò sì rí i mọ́, bẹ́ẹ̀ ni ibùjókòó rẹ̀ kì yóò sì ri i mọ́."'), 'Job$20:15': (None, None, 'Job$20:15 "Ó ti gbé ọrọ̀ mì, yóò sì tún bí i jáde; Ọlọ́run yóò pọ̀ ọ́ yọ jáde láti inú rẹ̀ wá."'), 'Job$21:7': ('ni', None, 'Job$21:7 "Nítorí kí ní ènìyàn búburú fi wà ní ayé, tí wọ́n gbó, àní tí wọ́n di alágbára ní ipa?"'), 'Job$21:32': ('ibojì', 'ibojì', 'Job$21:32 "Síbẹ̀ a ó sì sin ín ní ọ̀nà ipò òkú, a ó sì máa ṣọ́ ibojì òkú."'), 'Job$23:8': ('lọ', None, 'Job$23:8 "“Sì wòó, bí èmi bá lọ sí iwájú, òun kò sí níbẹ̀, àti sí ẹ̀yìn, èmi kò sì rí òye rẹ̀:"'), 'Job$23:11': (None, 'ni', 'Job$23:11 "Ẹṣẹ̀ mí ti tẹ̀lé ipaṣẹ̀ ìrìn rẹ̀; ọ̀nà rẹ̀ ni mo ti kíyèsí, tí ń kò sì yà kúrò."'), 'Job$23:12': (None, 'ẹnu', 'Job$23:12 "Bẹ́ẹ̀ ni èmi kò padà sẹ́yìn kúrò nínú òfin ẹ̀nu rẹ̀, èmi sì pa ọ̀rọ̀ ẹnu rẹ̀ mọ́ ju oúnjẹ òòjọ́ lọ "'), 'Job$24:2': ('gba', None, 'Job$24:2 "Díẹ̀ nínú wọn a ṣún àmì ààlà ilẹ̀, wọ́n á fi agbára kó agbo ẹran lọ, wọ́n sì ji wọn."'), 'Job$24:3': ('gba', None, 'Job$24:3 "Wọ́n á sì dà kẹ́tẹ́kẹ́tẹ́ aláìní baba lọ, wọ́n a sì gba ọ̀dá-màlúù opó ní ohun ògo."'), 'Job$24:5': (None, None, 'Job$24:5 "Kíyèsí i, bí i kẹ́tẹ́kẹ́tẹ́ ìgbẹ́ nínú ijù ni àwọn talákà ijáde lọ sí iṣẹ́ wọn; Wọ́n a tètè dìde láti wá ohun ọdẹ; ijù pèsè oúnjẹ fún wọn àti fún àwọn ọmọ wọn"'), 'Job$24:7': ('ni', None, 'Job$24:7 "Ní hòòhò ni wọn má a sùn láìní aṣọ, tí wọn kò ní ìbora nínú òtútù."'), 'Job$24:10': (None, None, 'Job$24:10 "Wọ́n rìn kiri níhòòhò láìní aṣọ; àwọn tí ebi ń pa rẹrù ìdì ọkà,"'), 'Job$24:19': ('ni', None, 'Job$24:19 "Ọ̀dá àti òru ní ímú omi ojọ-didi gbẹ, bẹ́ẹ̀ ní isà òkú írun àwọn ẹlẹ́ṣẹ̀."'), 'Job$27:6': ('lọ', None, 'Job$27:6 "Òdodo mi ni èmi dìmú ṣinṣin, èmi kì yóò sì jọ̀wọ́ rẹ̀ lọ́wọ́; àyà mi kì yóò sì gan ọjọ́ kan nínú ọjọ́ ayé mi. "'), 'Job$27:20': (None, None, 'Job$27:20 Ẹ̀rù ńlá bàá bí omi ṣíṣàn; ẹ̀fúùfù ńlá jí i gbé lọ ní òru.'), 'Job$28:3': (None, 'iku', 'Job$28:3 "Ènìyàn ni ó parí òkùnkùn, ó sì ṣe àwárí òkúta òkùnkùn àti ti inú òjìji ikú sí ìhà gbogbo."'), 'Job$28:16': ('iyebiye', None, 'Job$28:16 "A kò le è fi wúrà Ófírì, tàbí òkútà Óníkísì iyebíye, tàbí òkúta Sáfírì díye lé e."'), 'Job$28:17': (None, None, 'Job$28:17 Góòlù àti òkúta Kírísítalì kò tó ẹgbẹ́rẹ̀; bẹ́ẹ̀ ni a kò le è fi ohun èló wúrà ṣe pàsípààrọ̀ rẹ̀.'), 'Job$28:19': (None, None, 'Job$28:19 Òkùta tópásì ti Kúsì kò tó ẹgbẹ́ rẹ̀; bẹ́ẹ̀ ni a kò le fi wúrà dáradára díwọ̀n iye rẹ̀.'), 'Job$29:9': (None, 'ẹnu', 'Job$29:9 "Àwọn ọmọ aládé dákẹ́ ọ̀rọ̀ sí sọ, wọn a sì fí ọwọ́ wọn lé ẹnu;"'), 'Job$29:11': ('ri', None, 'Job$29:11 "Nígbà tí etí gbọ́, ó sì súre fún mi, àti nígbà tí ojú sì rí mi, ó jẹ́rìí mi;"'), 'Job$29:14': ('aṣọ', 'aṣọ', 'Job$29:14 "Èmi sì mú òdodo wọ̀ bí aṣọ, ẹ̀tọ́ mi dà bí aṣọ ìgúnwà àti adé ọba."'), 'Job$29:22': ('ọrọ', None, 'Job$29:22 "Lẹ́yìn ọ̀rọ̀ mi, wọn kò tún sọ̀rọ̀ mọ́; ọ̀rọ̀ mi wọ̀ wọ́n ní etí ṣinṣin."'), 'Job$30:11': ('ni', None, 'Job$30:11 "Nítorí Ọlọ́run ti tú okùn ìyè mi, ó sì pọ́n mi lójú; àwọn pẹ̀lú sì dẹ ìjánu níwájú mi."'), 'Job$30:16': (None, None, 'Job$30:16 “Àti nísinsin yìí ọkàn mí sì dà jáde síi; ọjọ́ ìpọ́njú mi dì mí mú.'), 'Job$31:7': ('oju', None, 'Job$31:7 "Bí ẹṣẹ̀ mí bá yà kúrò lójú ọ̀nà, tí àyà mí sì tẹ̀lé ipa ojú mi, tàbí bí àbàwọ́n kan bá sì lẹ̀mọ́ mi ní ọwọ́,"'), 'Job$31:9': (None, 'ni', 'Job$31:9 "“Bí àyà mi bá di fífà sípasẹ̀ obinrin kan, tàbí bí mo bá lọ í ba de ènìyàn ní ẹnu ọ̀nà ilé aládùúgbò mi,"'), 'Job$31:13': ('ṣe', None, 'Job$31:13 "“Tí mo bá sì se àìka ọ̀ràn ìránṣẹ́kùnrin mi tàbí ìránṣẹ́bìnrin mi sí, nígbà tí wọ́n bá ń bá mi jà;"'), 'Job$31:16': (None, None, 'Job$31:16 "“Bí mo bá fa ọwọ́ sẹ́yìn fún ìfẹ́ inú talákà, tàbí bí mo bá sì mú kí ojú opó di aláìríran,"'), 'Job$31:24': ('ṣe', 'ni', 'Job$31:24 "“Bí ó bá ṣe pé mo fi wúrà ṣe ìgbẹ́kẹ̀lé mi, tàbí tí mo bá wí fún fàdákà dídára pé, ‘Ìwọ ni ààbò mi;’"'), 'Job$31:29': (None, 'ni', 'Job$31:29 "“Bí ó bá ṣepé mo yọ̀ sì ìparun ẹni tí ó korìíra mi. Tàbí bí mo bá sì gbéra sókè, nígbà tí ibi bá a."'), 'Job$31:38': (None, None, 'Job$31:38 “Bí ilẹ̀ mi bá sì ké rara lòdì sí mi tí a sì fi omijé kún gbogbo poro rẹ̀.'), 'Job$32:14': (None, None, 'Job$32:14 "Bí òun kò ti sọ̀rọ̀ sí mi, bẹ́ẹ̀ ni èmí kì yóò fi ọ̀rọ̀ yín dá a lóhùn. "'), 'Job$33:1': (None, None, 'Job$33:1 "“Njẹ́ nítorí náà, Jóòbù, èmí bẹ̀ ọ, gbọ́ ọ̀rọ̀ mi kí o sì fetísí ọ̀rọ̀ mi!"'), 'Job$33:4': (None, None, 'Job$33:4 "Ẹ̀mí Ọlọ́run Ní ó tí dá mi, Àti ìmísí Olódùmáre ni ó ti fún mi ní ìyè."'), 'Job$33:15': (None, 'eniyan', 'Job$33:15 "Nínú àlá, ní ojúran òru, nígbà tó orun ìjìká bá kùn ènìyàn lọ, ní sísùn lórí ìbùṣùn,"'), 'Job$33:25': (None, 'pada', 'Job$33:25 "Ara rẹ̀ yóò sì di ọ̀tun bí i ti ọmọ kékeré, yóò sì tún padà sí ọjọ́ ìgbà èwe rẹ̀;"'), 'Job$33:27': ('ni', None, 'Job$33:27 "Ó wá sọ́dọ̀ ènìyàn ó sì wí pé èmi ṣẹ̀, kò sì sí èyí tí o tọ́, mo sì ti yí èyí tí ó tọ́ po, a kò sì san ẹ̀san rẹ̀ fún mi;"'), 'Job$34:1': ('ni', None, 'Job$34:1 "Nígbà náà ni Élíhù dáhùn, ó sì wí pé:"'), 'Job$34:14': ('okan', 'ẹmi', 'Job$34:14 "Bí ó bá gbé ayé rẹ̀ lé kìkì ara rẹ̀ tí ó sì gba ọkàn rẹ̀ àti ẹ̀mí rẹ̀ sọ́dọ̀ ara rẹ̀,"'), 'Job$34:22': ('okunkun', 'ojiji', 'Job$34:22 "Kò sí ibi òkùnkùn, tàbí ojìjì ikú, níbi tí àwọn oníṣẹ́ ẹ̀ṣẹ̀ yóò gbé sá pamọ́ sí."'), 'Job$34:31': (None, None, 'Job$34:31 "“Nítorí pé ẹnìkan ha lè wí fún Ọlọ́run pé, èmi jẹ̀bí; èmi kò sì ní ṣẹ̀ mọ́?"'), 'Job$34:34': (None, 'eniyan', 'Job$34:34 "“Àwọn ènìyàn amòye yóò wí fún mi, àti pẹ̀lúpẹ̀lú ẹnikẹ́ni tí í ṣe ọlọ́gbọ́n tí ó sì gbọ́ mi pé,"'), 'Job$36:16': ('gbooro', None, 'Job$36:16 "“Bẹ́ẹ̀ ni pẹ̀lúpẹ̀lú ó sì dè ọ lọ láti inú ìhágágá síbi gbòòrò, sí ibi tí ó ní ààyè tí kò ní wàhálà nínú rẹ̀ ohun tí a sì gbé kalẹ̀ ní tábìlì rẹ̀ a jẹ́ kìkì ọ̀rá oúnjẹ tí ó fẹ́."'), 'Job$36:17': (None, None, 'Job$36:17 Ṣùgbọ́n ìwọ kún fún ìdájọ́ àwọn búburú; ìdájọ́ àti òtítọ́ dì ọ́ mú.'), 'Job$36:21': (None, None, 'Job$36:21 Máa sọra kí ìwọ ki ó má yí ara rẹ̀ padà sí búrubú; Nítori èyí tí ìwọ rò pé ó dára jù ìpọ́njú lọ.'), 'Job$37:7': (None, 'eniyan', 'Job$37:7 Ó fi èdìdì di ọwọ́ gbogbo ènìyàn kí gbogbo wọn kí ó lè mọ iṣẹ́ rẹ̀ ó sì tún dá olúkúlùkù ẹ̀nìyàn dúró lẹ́nu iṣẹ́ rẹ̀'), 'Job$37:11': (None, None, 'Job$37:11 "Pẹ̀lúpẹ̀lú ó fi omi púpọ̀ mú àwọ̀sánmọ̀ wúwo, a sì tú àwọ̀sánmọ̀ ìmọ́lẹ̀ rẹ̀ ká ara wọn."'), 'Job$37:19': (None, None, 'Job$37:19 “Kọ́ wa ní èyí tí a lè wí fún un; nítorí pé àwa kò le ílàdí ọ̀rọ̀ nítorí òkùnkùn wa.'), 'Job$38:12': ('owurọ', 'ọjọ', 'Job$38:12 "“Ìwọ pàṣẹ fún òwúrọ̀ láti ìgbà ọjọ́ rẹ̀ wá ìwọ sì mú ìlà oòrùn mọ ipò rẹ̀,"'), 'Job$38:25': (None, None, 'Job$38:25 "Ta ni ó la ipadò fún ẹkun omi, àti ọ̀nà fún mọ̀nàmọ́ná àrá,"'), 'Job$39:6': ('ṣe', 'ni', 'Job$39:6 "Èyí tí mo fi ihà ṣe ilé fún, àti ilẹ̀ iyọ̀ ní ibùgbé rẹ̀"'), 'Job$40:16': ('agbara', 'ipa', 'Job$40:16 "Wò o nísinsìnyí, agbára rẹ wà ní ẹ̀gbẹ́ rẹ, àti ipa rẹ nínú ìṣàn ìkún rẹ."'), 'Job$40:20': ('mú', None, 'Job$40:20 "Nítòótọ́ òkè ńláńlá ní imu ohun jíjẹ fún un wá, níbi tí gbogbo ẹranko ìgbẹ́ máa siré ní ẹ̀gbẹ́ ibẹ̀"'), 'Job$41:1': (None, 'fa', 'Job$41:1 "“Ǹjẹ́ ìwọ lè ífi ìwọ fa Lefíátanì, ọ̀nì ńlá jáde? Tàbí ìwọ lè fi okùn so ahọ́n rẹ̀ mọ́lẹ̀?"'), 'Job$41:10': (None, None, 'Job$41:10 Kò sí ẹni aláyà lílé tí ó lè ru sókè; Ǹjẹ́ ta ni ó lè dúró níwájú mi.'), 'Job$41:20': ('ikoko', 'ẹfin', 'Job$41:20 "Láti imu rẹ ni èéfín ti jáde wá, bí ẹni pé láti inú ìkòkò tí a fẹ́ iná ìféfé lábẹ́ rẹ̀."'), 'Job$42:1': (None, None, 'Job$42:1 "Ígbà náà ní Jóòbù dá OLÚWA lóhùn, ó sì wí pé:"'), 'Job$42:8': ('gba', 'gba', 'Job$42:8 "Nítorí náà, ẹ mú akọ ẹgbọ̀rọ̀ màlúù méje, àti àgbò méje, kí ẹ sì tọ̀ Jóòbù ìránṣẹ́ mi lọ, kí ẹ sì fi rú ẹbọ sísun fún ara yín; Jóòbù ìránṣẹ́ mi yóò sì gbàdúrà fún yin; nítorí pé àdúrà rẹ̀ ní èmi yóò gbà; kí èmi kí ó má ba ṣe si yín bí ìsìnà yín, níti pé ẹ̀yin kò sọ̀rọ̀ ohun tí ó tọ́ sí mi bi Jóòbù ìránṣẹ́ mi ti ṣe."'), 'Job$42:11': (None, 'eniyan', 'Job$42:11 "Nigbà náà ní gbogbo àwọn arákùnrin rẹ̀, àti gbogbo àwọn arábìnrin rẹ̀, àti gbogbo àwọn tí ó ti ṣe ojúlùmọ̀ rẹ̀ rí; wọ́n báa jẹun nínú ilé rẹ̀. Wọ́n sì ṣe ìdárò rẹ̀, wọ́n sì sìpẹ̀ fún un nítorí ibi gbogbo tí OLÚWA ti mú bá a: Olúkúlùkù ènìyàn pẹ̀lú sì fún un ní ẹyọ owo kọ̀ọ̀kan àti olúkúlùkù ni òrùka wúrà etí kọ̀ọ̀kan."'), 'Ps$4:8': ('dubulẹ', 'gbe', 'Ps$4:8 "Èmi yóò dùbúlẹ̀, èmi ó sì sùn ní àlàáfíà, nítorí ìwọ nìkan, OLÚWA, ni o mú mi gbé láìléwu."'), 'Ps$5:10': (None, 'ni', 'Ps$5:10 "Dá wọn lẹ́bi Ọlọ́run! Jẹ́ kí rìkísí wọn jẹ́ ìṣubú wọn. Lé wọn jáde nítorí ọ̀pọ̀lọpọ̀ ẹ̀ṣẹ̀ wọn, nítorí wọ́n ti ṣọ̀tẹ̀ sí ọ."'), 'Ps$6:4': (None, None, 'Ps$6:4 "Yípadà, OLÚWA, kí ó sì gbà mí; gbà mí là nípa ìfẹ́ Rẹ tí kì í ṣákìí. "'), 'Ps$6:9': (None, 'adura', 'Ps$6:9 OLÚWA ti gbọ́ ẹkún mi fún àánú; OLÚWA ti gba àdúrà mi.'), 'Ps$7:1': (None, None, 'Ps$7:1 "OLÚWA Ọlọ́run mi, ààbò mi wà nínú Rẹ; gba mí là kí o sì tú mí sílẹ̀ lọ́wọ́ àwọn tó ń lé mi,"'), 'Ps$7:3': (None, None, 'Ps$7:3 "OLÚWA Ọlọ́run mi, tí èmi bá ṣe èyí tí ẹ̀bi sì wà ní ọwọ́ mi"'), 'Ps$7:12': (None, None, 'Ps$7:12 "Bí kò bá yípadà, òun yóò pọ́n idà Rẹ̀ mú ó ti fa ọrun Rẹ̀ le ná ó ti múra Rẹ̀ sílẹ̀"'), 'Ps$9:6': (None, 'opin', 'Ps$9:6 "Ìparun àìlópin ti borí àwọn ọ̀ta, Ìwọ ti fa àwọn ìlú wọn tu; Àní ìrántí wọn sì ti sègbé. "'), 'Ps$9:18': (None, None, 'Ps$9:18 "Ṣùgbọ́n àwọn aláìní ni a ò ní gbàgbé láéláé, ìrètí àwọn ti ó talakà lójú kí yóò ṣègbé láéláé. "'), 'Ps$10:2': (None, 'ni', 'Ps$10:2 "Nínú àrékérekè ènìyàn búburú ti ó gbérò ni kí a ti mú wọn, lí a mú-un nínú ìlànà tí o gbérò."'), 'Ps$11:2': (None, None, 'Ps$11:2 "Wò ó nítorí náà, ènìyàn búburú fa ọfà Rẹ̀; wọn ti ọfà wọn sí ojú okùn láti tafà níbi òjìjì sí àyà ẹni ìdúró ṣinṣin."'), 'Ps$12:1': (None, 'eniyan', 'Ps$12:1 "Ràn wá lọ́wọ́, OLÚWA nítorí ẹni ìwà bí Ọlọ́run kò sí mọ́; Olótìítọ́ tí pòórá kúrò láàrin àwọn ènìyàn."'), 'Ps$12:3': (None, None, 'Ps$12:3 Kí OLÚWA kí ó gé ètè èké wọn àti gbogbo ahọ́n ìfọ́nnu'), 'Ps$12:7': (None, None, 'Ps$12:7 "OLÚWA, ìwọ yóò pa wá mọ́ kí o sì gbà wá lọ́wọ́ àwọn ènìyàn wọ̀nyí títí láé."'), 'Ps$13:2': (None, None, 'Ps$13:2 "Yóò ti pẹ́ tó tí èmi ó máa bá èrò mi jà, àti ní ojojúmọ́ ni èmi ń ní ìbànújẹ́ ní ọkàn mi? Yóò ti pẹ́tó tí àwọn ọ̀ta mi yóò máa borí mi? "'), 'Ps$14:2': ('wo', None, 'Ps$14:2 "OLÚWA sì bojú wolẹ̀ láti ọ̀run wá lórí àwọn ọmọ ènìyàn bóyá ó le rí ẹni tí òye yé, ẹnikẹ́ni tó ń wá Ọlọ́run."'), 'Ps$14:3': (None, 'di', 'Ps$14:3 "Gbogbo wọn sì ti yípadà, gbogbo wọn sì ti díbàjẹ́; kò sì sí ẹni tí ó ń ṣe rere, kò sí ẹnìkan. "'), 'Ps$16:5': (None, None, 'Ps$16:5 "OLÚWA, ni ìní mi tí mo yàn àti ago mi, ó ti pa ohun tí íṣe tèmi mọ́."'), 'Ps$16:11': (None, 'ayo', 'Ps$16:11 "Ìwọ ti fi ipa ọ̀nà ìyè hàn mí; Ìwọ yóò kún mi pẹ̀lú ayọ̀ ní ìwájú Rẹ, pẹ̀lú ìdùnnú ayérayé ní ọwọ́ ọ̀tún Rẹ."'), 'Ps$17:2': (None, None, 'Ps$17:2 Kí ìdáláre mi wá láti ọ̀dọ̀ Rẹ; kí ojú Rẹ kí ó rí ohun tí ó tọ́.'), 'Ps$17:13': (None, 'ni', 'Ps$17:13 "Dìde, OLÚWA, dojúkọ wọ́n, Rẹ̀ wọ́n sílẹ̀; gbà mí sílẹ̀ lọ́wọ́ ẹni búburú nípa idà Rẹ."'), 'Ps$18:6': (None, None, 'Ps$18:6 "Nínú ìpọ́njú mo ké pe OLÚWA; Mo sunkún sí OLÚWA mi fún ìrànlọ́wọ́. Láti inú tẹ́ḿpìlì Rẹ̀, ó gbọ́ igbe mi; ẹkún mi wá sí iwájú Rẹ̀, sí inú etí Rẹ̀. "'), 'Ps$18:8': (None, 'ina', 'Ps$18:8 "Èéfín ti ihò imú Rẹ̀ jáde wá; Iná ajónirun ti ẹnu Rẹ̀ jáde wá, ẹ̀yin iná bú jáde láti inú Rẹ̀."'), 'Ps$18:35': (None, 'ọwọ', 'Ps$18:35 "Ìwọ fi àṣà ìṣẹ́gun Rẹ̀ fún mi, ọwọ́ ọ̀tún Rẹ̀ gbé mí sókè; àti ìwà ìpẹ̀lẹ́ Rẹ̀ sọ mi di alágbára àti ẹni ńlá."'), 'Ps$18:37': ('run', None, 'Ps$18:37 Èmi lépa àwọn ọ̀tá mi èmi sì bá wọn èmi kò sì padà lẹ́yìn wọn títí a fi run wọ́n.'), 'Ps$18:43': (None, 'ni', 'Ps$18:43 "Ìwọ ti gbà mí lọ́wọ́ ìkọlù àwọn ènìyàn; Ìwọ ti fi mí ṣe olórí àwọn orílẹ́ èdè; àwọn ènìyàn ti èmi kò mọ, yóò sì máa sìn mí."'), 'Ps$19:3': (None, 'ede', 'Ps$19:3 Kò sí ohùn tàbí èdè níbi tí a kò gbé gbọ́ ìró wọn'), 'Ps$19:4': (None, 'ayé', 'Ps$19:4 " Ohùn wọn jáde lọ sí gbogbo orílẹ̀ ayé ọ̀rọ̀ wọn títí dé òpin ilẹ̀ ayé, ó ti kọ́ àgọ́ fún oòrùn nínú àwọn ọ̀run."'), 'Ps$19:8': ('oju', 'okan', 'Ps$19:8 "Ìlànà OLÚWA tọ̀nà, ó ń fi ayọ̀ fún ọkàn. àṣẹ OLÚWA ní mímọ́, ó ń fi ìmọ́lẹ̀ fún ojú."'), 'Ps$19:13': ('jẹ ki', 'ni', 'Ps$19:13 "Wẹ ìrànsẹ́ Rẹ mọ́ kúrò nínú ẹ̀ṣẹ̀ àmọ̀ọ́n-mọ̀-dá; Má ṣe jẹ kí wọn kí ó jọba lórí mi. Nígbà náà ní èmí yóò dúró ṣinṣin, èmi yóò sì ṣe aláìlẹ́bi kúrò nínú ẹ̀ṣẹ̀ ńlá. "'), 'Ps$21:9': ('ibinu', 'ibinu', 'Ps$21:9 "Nígbà tí ìwọ bá yọ ìwọ yóò mú wọn dàbí ìnà ìléru. OLÚWA yóò gbé wọn mì nínú ìbínú Rẹ̀, àti pé iná Rẹ̀ yóò jó wọn run."'), 'Ps$21:12': (None, None, 'Ps$21:12 Nítorí pé ìwọ yóò mú wọn yí ẹ̀yìn wọn padà nígbà tí o bá pinnu láti ta wọ́n ní ọfà.'), 'Ps$22:6': (None, 'eniyan', 'Ps$22:6 Ṣùgbọ́n kòkòrò ní mo jẹ́ kì í sì í ṣe ènìyàn; mo di ẹlẹ́gàn àwọn ènìyàn ẹlẹ́yà àwọ́n ènìyàn'), 'Ps$22:9': ('mu', None, 'Ps$22:9 Ṣíbẹ̀ ìwọ ni ó ti mú mi wá láti inú; ìwọ ni ó mú mi wà láìléwu nígbà tí mo rọ̀ mọ́ ọmú ìyá mi.'), 'Ps$22:26': ('jẹ', None, 'Ps$22:26 talákà yóò jẹ yóò sì yó; àwọn tí n wá OLÚWA yóò yin jẹ́ kí ẹ̀mí Rẹ̀ wà láàyè títí ayárayé!'), 'Ps$22:31': ('ṣe', None, 'Ps$22:31 "Wọn yóò polongo òdodo Rẹ̀ sí àwọn ọmọ tí a kò tí ì bí, wí pé òun ni ó ṣe èyí."'), 'Ps$24:1': (None, 'ayé', 'Ps$24:1 " Ti OLÚWA ni ilẹ̀, àti ẹ̀kún Rẹ̀, ayé àti àwọn tí ó tẹ̀dó sínú Rẹ̀;"'), 'Ps$26:2': (None, None, 'Ps$26:2 "Wádìí mi wò, Ìwọ OLÚWA, kí o sì dán mi wò, dán àyà àti ọkàn mi wò;"'), 'Ps$27:11': ('ọna', 'ona', 'Ps$27:11 "Kọ́ mi ní ọ̀nà Rẹ, OLÚWA, kí o sì sìn mi lọ sí ọ̀nà tí ó tẹ́jú nítorí àwọn ọ̀tá mi."'), 'Ps$28:1': ('lọ', 'di', 'Ps$28:1 "Ìwọ OLÚWA, mo képe àpáta mi; Má ṣe kọ eti dídi sí mi. Tí ìwọ bá dákẹ́ sí mi, èmí yóò dàbí àwọn tí ó lọ sí ọ̀gbun."'), 'Ps$28:4': ('fun', 'ṣe', 'Ps$28:4 Ṣan ẹ̀ṣan wọn gẹ́gẹ́ bí isẹ́ wọn àti fún iṣẹ́ ibi wọn; gba ẹ̀san wọn gẹ́gẹ́ bí iṣẹ́ ọwọ́ wọn; kí o sì san ẹ̀ṣan wọn bí ó ti tọ́.'), 'Ps$31:2': (None, None, 'Ps$31:2 "Tẹ́ etí Rẹ sí mi, gbà mí kíákíá; jẹ́ àpáta ààbò mi, jẹ́ odi alágbára láti gbà mí."'), 'Ps$31:3': (None, None, 'Ps$31:3 "Ìwọ pàápàá ni àpáta àti ààbò mi, nítorì orúkọ Rẹ máa ṣe ìtọ́ mi tọ́ mi kí o sì ṣe amọ̀nà mi."'), 'Ps$31:13': (None, 'gba', 'Ps$31:13 " Nítorí mo gbọ́ ọ̀rọ̀ ẹ̀gàn ọ̀pọ̀lọpọ̀ ìpayà tí ó yí mi ká; tí wọn gbìmọ̀ pọ̀ sí mi, wọ́n sì ṣọ̀tẹ̀ sí mi láti gba ẹ̀mi mi. "'), 'Ps$31:17': ('jẹ ki', 'ni', 'Ps$31:17 "Má ṣe jẹ́ kí á fi mí sínú ìtijú, OLÚWA; nítorí pé mo ké pè ọ́; jẹ́ kí ojú kí ó ti ènìyàn búburú; jẹ́ kí wọ́n lọ pẹ̀lú ìdààmú sí isà òkú."'), 'Ps$32:9': (None, 'ni', 'Ps$32:9 "Má ṣe dàbí ẹsin tàbí ìbaka, tí kò ní òye ẹnu ẹni tí a gbọdọ̀ fi ìjánu bọ̀, kí wọn má ba à sún mọ́ ọ."'), 'Ps$33:8': (None, 'ayé', 'Ps$33:8 Jẹ́ kí gbogbo ayé kí ó bẹ̀rù OLÚWA: jẹ́ kí gbogbo àwọn olùgbé ayé kí ó wà nínú ìbẹ̀rù Rẹ̀.'), 'Ps$34:18': ('okan', None, 'Ps$34:18 OLÚWA sún mọ́ etí ọ̀dọ̀ àwọn oníròbìnújẹ́ ọkàn; ó sì gba irú àwọn tí i ṣe oníròra ọkàn là.'), 'Ps$35:2': ('asà', None, 'Ps$35:2 "Di aṣà àti àpáta mú, kí o sì dìde fún ìrànlọ́wọ́ mi!"'), 'Ps$35:7': ('ni', None, 'Ps$35:7 "Nítorí pé, ní àìnídìí ní wọ́n dẹ àwọ̀n wọn sílẹ̀ fún mi, ní àìnídìí ni wọ́n wa kòtò fún ọkàn mi."'), 'Ps$35:8': ('wá', 'ṣubu', 'Ps$35:8 "Jẹ́ kí ìparun kí ó wá sí orí wọn lójijì. Àwọ̀n Rẹ̀ tí ó dẹ pamọ́, kí ó mú àwọn tìkálára wọn; kí wọn ṣubú sínú kòtò sí ìparun ara Rẹ̀."'), 'Ps$35:25': ('jẹ ki', 'ni', 'Ps$35:25 "Má ṣe jẹ́ kí wọn wí nínú ara wọn pé, “Áà! Àti rí ohun tí ọkàn wa ń fẹ́: Má ṣe jẹ kí wọn kí ó wí pé, a ti gbé e mì.” "'), 'Ps$35:27': ('ayo', None, 'Ps$35:27 "Jẹ́ kí àwọn tí ó fẹ́ ìdáláre mi fó fún ayọ̀ àti ìdùnnú, kí wọn máa sọ ọ́ titi lọ, pé gbígbéga ni “OLÚWA sí àlàáfíà ìránṣẹ Rẹ̀”."'), 'Ps$36:1': ('oju', 'okan', 'Ps$36:1 Ìrékọjá sọ̀rọ̀ sí ènìyàn búburú jinlẹ̀ nínú ọkàn wọn; Ìbẹ̀rù Ọlọ́run kò sí níwájú ojú wọn'), 'Ps$37:8': (None, None, 'Ps$37:8 "Fàsẹ́yìn nínú inúbíbí, kí o sì kọ ìkannú sílẹ̀. Má ṣe ṣe ìkanra, nítorí pé ó gbéni sí búburú pẹ̀lú."'), 'Ps$37:14': ('tẹ', None, 'Ps$37:14 "Ènìyàn búburú fa idà yọ, wọ́n sì tẹ àwọn ọrun wọn, láti sọ talákà àti aláìní kalẹ̀, láti pa àwọn tí ó dúró ṣinṣin."'), 'Ps$37:40': (None, None, 'Ps$37:40 "OLÚWA yóò ràn wọ́n lọ́wọ́ yóò sì gbà wọ́n; yóò gbà wọ́n lọ́wọ́ ènìyàn búburú, yóò sì gbà wọ́n là, nítorí tí wọ́n gbẹ́kẹ̀ lé e."'), 'Ps$38:10': ('oju', None, 'Ps$38:10 "Àyà mi ń mí hẹlẹ, agbára mi yẹ̀ mí sílẹ̀ bí ó ṣe ti ìmọ́lẹ̀ ojú mi ni, ó ti lọ kúrò lára mi."'), 'Ps$38:20': ('ni', None, 'Ps$38:20 Àwọn tí wọn ń fi ibi san rere fún mi àwọn ni ọ̀ta mi nítorí pé mò ń tọ ire lẹ́yìn.'), 'Ps$39:12': (None, None, 'Ps$39:12 "“Gbọ́ àdúrà mi, OLÚWA, kí o sì fetí sí igbe mi; kí o má ṣe di etí Rẹ sí ẹkún mi nítorí àlejò ni èmi lọ́dọ̀ Rẹ àti àtìpó, bí gbogbo àwọn baba mi ti rí."'), 'Ps$40:7': (None, None, 'Ps$40:7 "Nígbà náà ni mo wí pé, “Èmi nìyí; nínú ìwé kíkà nì a kọ ọ nípa temí wí pé."'), 'Ps$40:12': ('ya', None, 'Ps$40:12 "Nítorí pé àìníye ibi ni ó yí mi káàkiri, ẹ̀ṣẹ̀ mi sì dì mọ́ mi, títí tí èmi kò fi ríran mọ́; wọ́n pọ̀ ju irun orí mi lọ, àti wí pé àyà mí ti kùnà. "'), 'Ps$41:2': (None, None, 'Ps$41:2 OLÚWA yóò dààbò bòó yóò sí pa ọkàn Rẹ̀ mọ́: yóò bùkún fún-un ni orí ilẹ̀ kò sì ní fi sílẹ̀ fún ìfẹ́ àwọn ọ̀tá Rẹ̀.'), 'Ps$44:9': (None, None, 'Ps$44:9 "Ṣùgbọ́n nísinsìnyí ìwọ ti kọ̀ wá, ìwọ sì ti dójútì wá; Ìwọ kò sì bá àwọn ọmọ ogun wa jáde mọ́."'), 'Ps$45:1': ('ṣe', None, 'Ps$45:1 Ọkàn mi mọ ọ̀rọ̀ rere gẹ́gẹ́ bí mo ṣe ń sọ ohun tí mo ti ṣe fún ọba ahọ́n mi ni kálámù ayára kọ̀wé.'), 'Ps$45:7': ('ororo', None, 'Ps$45:7 "Ìwọ fẹ́ olódodo, ìwọ sì kórìíra ìwà búburú nígbà náà Ọlọ́run, Ọlọ́run Rẹ̀ ti yàn ọ́ ṣe olórí àwọn ẹlẹgbẹ́ Rẹ̀, nípa fífí àmì òróró ayọ̀ kùn ọ́"'), 'Ps$45:8': ('ṣe', 'ni', 'Ps$45:8 Gbogbo aṣọ Rẹ̀ ni ó ń rùn pẹ̀lú òjíá àti alóe àti kaṣíà; láti inú ààfin tí a fi eyín erin ṣe orin olókùn tẹ́ẹ́rẹ́ mú inú Rẹ̀ dùn.'), 'Ps$45:14': (None, 'jẹ', 'Ps$45:14 "Nínú aṣọ ìbànújẹ́ iyebíye ni a mú-un wá sí ọ̀dọ̀ ọba àwọn wúndíá ẹgbẹ́ Rẹ̀ tẹ̀ lé e wọ́n, sí mú-un tọ̀ ọ́ wá"'), 'Ps$45:17': (None, 'orukọ', 'Ps$45:17 Èmí yóò máa rántí orúkọ Rẹ̀ ní ìran gbogbo nígbà náà ni orílẹ̀ èdè yóò yìn ọ́ láé àti láéláé.'), 'Ps$46:9': (None, 'opin', 'Ps$46:9 "O mú ọ̀tẹ̀ tán de òpin ilẹ̀ ayé ó ṣẹ́ ọrun, ó sì gé ọ̀kọ̀ sí méjì ó fi kẹ̀kẹ́ ogun jóná"'), 'Ps$47:5': (None, None, 'Ps$47:5 Ọlọ́run tó gòkè lọ tí òun tayọ̀ OLÚWA ti òun ti àriwo ipè'), 'Ps$49:5': (None, 'ibi', 'Ps$49:5 "Èéṣé ti èmi yóò bẹ̀rù nígbà tí ọjọ́ ibi dé Nígbà tí àwọn ènìyàn búburú àti ayan nijẹ yí mi ká,"'), 'Ps$49:6': ('ọrọ', 'ọrọ', 'Ps$49:6 Àwọn tí o gbẹ́kẹ̀lé ọrọ̀ wọn tí wọn sì ń ṣe ìlérí ni ọpọlọpọ ọrọ̀ wọn'), 'Ps$49:14': ('ni', 'jẹ', 'Ps$49:14 Gẹ́gẹ́ bí àgùntàn ni a tẹ́ wọn sínú isà òkú ikú yóò jẹun lórí wọn; ẹni tí ó dúró ṣinṣin ní yóò jọba lórí wọn ní òwúrọ̀; Ẹwà wọn yóò díbàjẹ́ isà òkú ni ibùgbé ẹwà wọn.'), 'Ps$50:5': (None, 'ni', 'Ps$50:5 “Kó àwọn ènìyàn mímọ́ jọ fún mi àwọn tí wọn fi ẹbọ dá májẹ̀mú fún mi”.'), 'Ps$50:21': ('ṣe', None, 'Ps$50:21 "Àwọn nǹkan wọ̀nyí ni ìwọ ti ṣe, mo sì dákẹ́; ìwọ rò pé mo jẹ́ irú kan náà bí ìrẹ, ṣùgbọ́n èmi yóò bá ọ wí èmi yóò sì fi ẹ̀sùn kàn ọ́ ní ojú ara Rẹ. "'), 'Ps$51:7': ('funfun', None, 'Ps$51:7 "Wẹ̀ mí pẹ̀lú ewé ísópù, èmi yóò sì mọ́; fọ̀ mí, èmi yóò sì funfun ju sínóò lọ."'), 'Ps$51:10': ('okan', 'ẹmi', 'Ps$51:10 "Dá ọkàn mímọ́ sí inú mi, Ọlọ́run, kí o sì tún ẹ̀mí dídúró ṣinṣin ṣe sínú mi."'), 'Ps$51:15': ('ète', 'ẹnu', 'Ps$51:15 "OLÚWA, sí mi ní ètè mi gbogbo, àti ẹnu mi yóò sì máa kéde ìyìn Rẹ̀."'), 'Ps$51:16': (None, None, 'Ps$51:16 "Nítorí ìwọ kò ní inú dídùn sí ẹbọ, tí èmi kò bá mú wá; Ìwọ kò ní inú dídùn sí ọọrẹ̀-ẹbọ sísun"'), 'Ps$51:17': ('okan', None, 'Ps$51:17 Ẹbọ Ọlọ́run ni ìròbínújẹ́ ọkan ìròbìnújẹ́ àti ìrora àyà.'), 'Ps$53:2': ('wo', None, 'Ps$53:2 "Ọlọ́run bojú wo ilẹ̀ láti ọ̀run sórí àwọn ọmọ ènìyàn láti wò bóyá ẹnìkan wà tí ó ní òye, tí ó sì ń wá Ọlọ́run."'), 'Ps$53:3': (None, 'di', 'Ps$53:3 "Gbogbo ènìyàn tí ó ti yí padà, wọ́n ti parapọ̀ di ìbàjẹ́; kò sí ẹnìkan tí ó ń ṣe rere, kò sí ẹnìkan. "'), 'Ps$55:1': ('ebe', 'adura', 'Ps$55:1 "Fi etí sí àdúrà mi, Ọlọ́run, Má ṣe fi ojú pa ẹ̀bẹ̀ mi rẹ́:"'), 'Ps$55:8': ('iji', 'iji', 'Ps$55:8 "Èmi ìbá yára lọ́ si ibùgbé mi ní ibi ààbò, jìnnà kúrò nínú ìjì àti èfúùfù líle.” "'), 'Ps$55:12': (None, None, 'Ps$55:12 "Tí ọ̀tá bá tàbùkù mi, èmi yóò fara mọ́ ọn; tí ọ̀ta bá gbé ara Rẹ̀ ga sími, èmi ibá sá pamọ́ fún un."'), 'Ps$55:22': (None, None, 'Ps$55:22 Gbé ẹrù Rẹ lọ sí ọ̀dọ̀ OLÚWA yóò sì mú ọ dúró; òun kò ní jẹ́ kí olódodo ṣubú.'), 'Ps$56:13': (None, 'imole', 'Ps$56:13 "Nítorí ìwọ tí gbà mí lọ́wọ́ ikú àti ẹsẹ̀ mi lọ́wọ́ ìṣubú, kí èmí lè máa rìn níwájú Ọlọ́run ní ìmọ́lẹ̀ àwọn alààyè."'), 'Ps$58:7': (None, None, 'Ps$58:7 "Jẹ́ kí wọn parẹ́ bí omi tó ń ṣàn lọ; nígbà tí ó bá fa ọfà Rẹ̀, kí ọ̀kọ̀ wọn kí ó ṣẹ́."'), 'Ps$59:7': ('ète', 'ẹnu', 'Ps$59:7 "Kíyèsí ohun tí wọ́n tú jáde ní ẹnu: wọn ń tú idà jáde láti ètè wọn, wọn sì wí pé, “Ta ní ó le gbọ́ ọ̀rọ̀ wa?”"'), 'Ps$59:12': ('ète', 'ẹnu', 'Ps$59:12 "Nítorí ẹ̀ṣẹ̀ ẹnu wọn, ni ọ̀rọ̀ ètè wọn, kí a mú wọn nínú ìgbéraga wọn. Nítorí ẹ̀gàn àti èké tí wọn ń sọ,"'), 'Ps$60:5': (None, None, 'Ps$60:5 " Fi ọwọ́ ọ̀tún Rẹ̀ gbà wá kí ó sì ràn wá lọ́wọ́, kí a lè gba àwọn tí o fẹ́ là."'), 'Ps$63:5': ('ète', 'ẹnu', 'Ps$63:5 "A ó tẹ́ ọkàn mi lọ́rùn bí ọlọ́ràá oúnjẹ; pẹ̀lú ètè ìyìn, ẹnu mi yóò yìn ọ. "'), 'Ps$66:1': (None, None, 'Ps$66:1 "Ẹ hó ìhó ayọ̀ sí Ọlọ́run, gbogbo ayé!"'), 'Ps$66:13': (None, None, 'Ps$66:13 Èmi ó wá sí tẹ́ḿpìlì Rẹ pẹ̀lú ọrẹ ẹbọ sísun kí n sì mú ẹ̀jẹ́ mi ṣẹ sí ọ'), 'Ps$68:30': (None, 'ọpọlọpọ', 'Ps$68:30 "Bá àwọn ẹranko búburú wí, tí ń gbé láàrin ìko ọ̀pọ̀lọpọ̀ àwọn akọ màlúù pẹ̀lú àwọn ọmọ màlúù títí olúkúlùkù yóò fi forí balẹ̀ pẹ̀lú ìwọn fàdákà: tú àwọn orílẹ̀-èdè ti ń ṣe inú dídùn si ogun ká"'), 'Ps$69:8': ('ajeji', 'alejò', 'Ps$69:8 Mo jẹ́ àjèjì si àwọn arákùnrin mi; àlejò sí àwọn arákùnrin ìyá mi;'), 'Ps$69:18': (None, None, 'Ps$69:18 Súnmọ́ tòsí kí ó sì gbà mí là; rà mí padà nítorí àwọn ọ̀ta mi.'), 'Ps$69:22': (None, 'ni', 'Ps$69:22 "Jẹ́ kí tábìlì wọn kí ó dí ìkẹ́kùn ni iwájú wọn, kí ó sì di okùn dídẹ fún àwọn tó wà ní àlàáfíà."'), 'Ps$69:24': (None, None, 'Ps$69:24 Tú ìbínú Rẹ jáde sí wọn; kí ìbínú gbígbóná Rẹ bò wọ́n mọ́lẹ̀.'), 'Ps$69:32': (None, None, 'Ps$69:32 "Àwọn òtòsì yóò rí wọn yóò sì yọ̀: Ẹ̀yin yóò wá Ọlọ́run, ọkàn yin yóò sì wà láàyè!"'), 'Ps$71:6': ('mu', None, 'Ps$71:6 Mo gbẹ́kẹ̀lé ọ láti ìgbà ìbí mi; Ìwọ mu mi jáde láti inú ìyá mi wá èmi ó máa yìn ọ títí láé'), 'Ps$72:13': (None, None, 'Ps$72:13 Yóò káànú àwọn aláìlera àti aláìní yóò pa aláìní mọ́ kúrò nínú ikú.'), 'Ps$72:15': (None, None, 'Ps$72:15 Yóò sì yè pẹ́! A ó sì fún un ní wúrà Ṣébà. Àwọn ènìyàn yóò sì máa gbàdúrà fún-un nígbà gbogbo kí a sì bùkún fún-un lójojúmọ́.'), 'Ps$73:2': (None, 'fere', 'Ps$73:2 "Ṣùgbọ́n bí ó ṣe ti èmi, ẹsẹ̀ mi ti yẹ̀ tán; ìrìn mi fẹ́rẹ̀ yọ̀ tán."'), 'Ps$73:7': (None, 'okan', 'Ps$73:7 Láti inú ọkàn àrékérekè ni àìsòdodo ti wá; ẹ̀rí ọkàn búburú wọn kò ní òdiwọ̀n'), 'Ps$73:27': ('lọ', 'ṣègbé', 'Ps$73:27 Àwọn tí ó jìnnà sí ọ yóò ṣègbé ìwọ ti pa gbogbo wọn run; tí ń ṣe àgbèrè kiri kúrò lọ́dọ̀ Rẹ'), 'Ps$74:8': (None, 'ni', 'Ps$74:8 Wọ́n wí ní ọkàn wọn. “Àwa ó run wọ́n pátapáta!” Wọ́n sun gbogbo ibi ìjọ́sìn Ọlọ́run ní ilẹ̀ náà.'), 'Ps$77:6': ('okan', None, 'Ps$77:6 "Mo rántí orin mi ní òru. Èmi ń bá àyà mí sọ̀rọ̀, ọkàn mí sì ń ṣe àwárí jọjọ. "'), 'Ps$77:17': (None, None, 'Ps$77:17 "Àwọn ojú ọ̀run tú omi sílẹ̀, àwọ̀sánmọ̀ fí àrá dáhùn; ọfà Rẹ̀ ń tàn sẹ́yìn àti síwájú"'), 'Ps$77:18': (None, 'ayé', 'Ps$77:18 "Àrá Rẹ̀ ni a gbọ ní ibi ìjì, ìmọ́lẹ̀ Rẹ̀ mọ́lẹ̀ sí gbogbo ayé; ayé bẹ̀rù, wọ́n sì wárìrì."'), 'Ps$77:19': ('ọna', 'ona', 'Ps$77:19 "Ìpa Rẹ̀ gba òkun, ọ̀nà Rẹ ń bẹ nínú òkun, Ọ̀nà la omi alágbára kọ́já ipa Rẹ ń bẹ nínú líla omi alágbára kọjá, nítòótọ́ a kò rí ojú ẹṣẹ̀ Rẹ̀."'), 'Ps$78:2': (None, 'ẹnu', 'Ps$78:2 " Èmi ó la ẹnu mi ní òwe èmi o sọ ohun ìkọ̀kọ̀, ohun ti ọjọ́ pípẹ́;"'), 'Ps$78:4': ('ṣe', 'wá', 'Ps$78:4 "Àwa kí yóò pa wọ́n mọ́ kúrò lọ́dọ̀ àwọn ọmọ, ní fífí ìyìn OLÚWA, àti ipa Rẹ̀ àti iṣẹ́ ìyanu tó ti ṣe hàn fún ìran tí ń bọ̀."'), 'Ps$78:5': (None, None, 'Ps$78:5 "Ó gbé ẹ̀rí kalẹ̀ ní Jákọ́bù o sì fìdí àṣẹ múlẹ̀ ní Ísírẹ́lì, èyí tí ó páláṣẹ fún àwọn baba ńlá wa láti kọ́ àwọn ọmọ wọn,"'), 'Ps$78:18': (None, None, 'Ps$78:18 Wọ́n mọ̀ ọ́n mọ̀ dán Ọlọ́run wò nípa bíbéèrè fún oúnjẹ tí wọn bẹ̀bẹ̀ fún'), 'Ps$78:21': (None, 'ina', 'Ps$78:21 "Nígbà tí OLÚWA gbọ́ wọn, ó bínú gidigidi; iná Rẹ̀ sọ̀kalẹ̀ sí Jákọ́bù, ìbínú Rẹ̀ sì rú sí Ísírẹ́lì,"'), 'Ps$78:38': ('ibinu', 'ibinu', 'Ps$78:38 Ṣíbẹ̀ ó ṣàánú; ó dárí àìṣedédé wọn jìn òun kò sì pa wọn run nígbà kí ì gbà ló ń dá ìbínú Rẹ̀ dúró kò sì rú ìbínú Rẹ̀ sókè'), 'Ps$78:49': ('ibinu', 'ibinu', 'Ps$78:49 "Ó mú kíkorò ìbínú Rẹ̀ wá sí wọn lára, ìrunú àti ikáánú, àti ìpọ́njú, nípa rírán ańgẹ́lì apanirun sí wọn."'), 'Ps$79:2': (None, 'ni', 'Ps$79:2 "Wọn ti fi ara òkú àwọn ìránṣẹ́ Rẹ̀ fún àwọn ẹyẹ ojú ọ̀run ní oúnjẹ, ẹran ara àwọn ènìyàn mímọ́ Rẹ̀ fún ẹranko ilẹ̀."'), 'Ps$80:9': ('kun', None, 'Ps$80:9 "Ìwọ sí àyè sílẹ̀ fún un, ìwọ sì mu tọ gbòǹgbò jìnlẹ̀ ó sì kún ilẹ̀ náà."'), 'Ps$80:13': (None, None, 'Ps$80:13 Ìmọ̀do láti inú igbó ń bàá jẹ́ àti ẹranko igbó ń jẹ ẹ́ run.'), 'Ps$80:16': (None, 'ge', 'Ps$80:16 "A gé àjàrà Rẹ lulẹ̀, a ti fi iná sun ún; ní ìfibú, àwọn ènìyàn Rẹ̀ ń ṣègbé."'), 'Ps$81:10': ('mu', None, 'Ps$81:10 "Èmi ni OLÚWA Ọlọ́run Rẹ, ẹni tí ó mú un yín jáde láti Éjíbítì. Ẹ la ẹnu yín gbòòrò, èmi yóò sì kún un. "'), 'Ps$83:2': (None, None, 'Ps$83:2 "Wo bí àwọn ọ̀tá Rẹ ti ń rọ́kẹ̀kẹ̀ lọ, bi àwọn ọ̀tá Rẹ ti ń gbé ohùn wọn sókè."'), 'Ps$83:3': ('ya', 'ni', 'Ps$83:3 Pẹ̀lú àrékérekè ni wọn dìtẹ̀ sí àwọn ènìyàn Rẹ; wọn gbìmọ̀ lòdì sí àwọn tí ó fẹ.'), 'Ps$83:4': ('jẹ ki', 'ni', 'Ps$83:4 "Wọn wí pé, “wá,” ẹ jẹ́ kí a pa wọn run bí orílẹ̀ èdè, kí orúkọ Ísírẹ́lì ma bá a sí ní ìrántí mọ́. "'), 'Ps$83:14': (None, None, 'Ps$83:14 "Bí ìna ti i jó ìgbẹ́ ìgbó àti bí ọ̀wọ́ iná ti ń mú òkè-ńlá gbiná,"'), 'Ps$84:9': ('wo', 'oju', 'Ps$84:9 "Wo àsà wa, Ọlọ́run; fi ojú àánú wò àwọn ẹni àmì òroro Rẹ. "'), 'Ps$85:1': ('mu', 'ilẹ', 'Ps$85:1 "Ìwọ fi ojú rere han fún ilé Rẹ, OLÚWA; ìwọ mú ohun ìní Jákọ́bù bọ̀ sí pò."'), 'Ps$85:3': ('ibinu', 'ibinu', 'Ps$85:3 Ìwọ fi àwọn ìbínú Rẹ sápá kan ìwọ sì yípadà kúrò nínú ìbínú gbígbóná Rẹ.'), 'Ps$85:12': ('fun', None, 'Ps$85:12 "OLÚWA yóò fi ohun dídára fún ni nítòótọ́, ilẹ̀ wa yóò sì mú ìkórè Rẹ̀ jáde."'), 'Ps$86:6': (None, 'adura', 'Ps$86:6 "Gbọ́ àdúrà mi, OLÚWA; tẹ́tí sí ẹkún fún àánú."'), 'Ps$86:9': (None, 'orukọ', 'Ps$86:9 "Gbogbo àwọn orílẹ̀ èdè tí ìwọ dá yóò wá, láti wá jọ́sìn níwájú Rẹ, OLÚWA; wọn o mú ògo wà fún orúkọ Rẹ̀."'), 'Ps$86:16': ('ni', 'fun', 'Ps$86:16 Yípadà sí mi kí ó sì ṣàánú fún mi; fún àwọn ènìyàn Rẹ ní agbára kí o sì gba ọ̀dọ́mọ̀kunrin ìránṣẹ́-bìnrin Rẹ là.'), 'Ps$88:18': (None, 'ọrẹ', 'Ps$88:18 Ìwọ ti mú ọ̀rẹ́ àti olùfẹ́ mi kúrò lọ́dọ̀ mi; òkùnkùn sì jẹ́ ọ̀rẹ́ tímọ́tímọ́ mi.'), 'Ps$89:2': (None, None, 'Ps$89:2 "Èmi ó wí pé, ìfẹ́ Rẹ dúró títí láé, pe ìwọ gbe òtítọ́ Rẹ kalẹ̀ ní ọ̀run fúnrarẹ. "'), 'Ps$89:3': (None, None, 'Ps$89:3 " Ìwọ wí pé, “Èmi ti dá májẹ̀mu pẹ̀lú àwọn àyànfẹ́ mi mo ti búra fún Dáfídì ìránṣẹ́ mi."'), 'Ps$89:7': ('bẹru', None, 'Ps$89:7 "Nínú àjọ ìgbìmọ̀ ẹni mímọ́, wọn bẹ̀rù Ọlọ́run gidigidi; ó sì ní ibùyìn fún jú gbogbo àwọn tí ó yí i ká."'), 'Ps$89:11': (None, 'ayé', 'Ps$89:11 "Tìrẹ ní àwọn ọ̀run, ayé pẹ̀lú jẹ tìrẹ: ayé àti ohun gbogbo tí ó wà nínú Rẹ̀: ìwọ ti gbé wọn kalẹ̀."'), 'Ps$89:20': ('ororo', None, 'Ps$89:20 Èmi tí rí Dáfídì ìránṣẹ́ mi; pẹ̀lú òróró mímọ́ mi ní mo fi yàn án;'), 'Ps$89:34': (None, None, 'Ps$89:34 "Èmi kì yóò sẹ́ májẹ̀mú mí, tàbí kí èmi yí ọ̀rọ̀ tí o ti ẹnu mi jáde padà."'), 'Ps$89:40': (None, None, 'Ps$89:40 Ìwọ tí wo gbogbo àwọn odi Rẹ̀ ìwọ sọ ibi gíga Rẹ̀ di ahoro.'), 'Ps$89:50': ('ni', None, 'Ps$89:50 "Rántí, OLÚWA, bí àti ń gan àwọn ìránṣẹ́ Rẹ; bí èmi ti gbà ní àyà mi láti ọ̀dọ̀ àwọn ènìyàn,"'), 'Ps$90:2': (None, 'ayé', 'Ps$90:2 "Kí a to bí àwọn òkè nla àti kí ìwọ tó dá ilẹ̀ àti ayé, láti ayérayé dé ayérayé ìwọ ni Ọlọ́run. "'), 'Ps$90:3': (None, 'eniyan', 'Ps$90:3 "Ìwọ yí ènìyàn padà sí erùpẹ̀, wí pé “padà sí erùpẹ̀, ìwọ ọmọ ènìyàn”."'), 'Ps$90:7': ('ibinu', 'ibinu', 'Ps$90:7 A pa wá run nípa ìbínú Rẹ nípa ìbínú Rẹ ara kò rọ̀ wá.'), 'Ps$90:9': ('kọjá', None, 'Ps$90:9 Gbogbo ọjọ́ wa ń kọjá lọ lábẹ́ ìbínú Rẹ; àwa n lo ọjọ wá lọ bí àlá ti a ń rọ́.'), 'Ps$91:4': (None, None, 'Ps$91:4 "Òun yóò fi ìyẹ́ Rẹ̀ bò mí, àti ni abẹ́ ìyẹ́ Rẹ̀ ni èmi yóò ti rí ààbò; òtítọ́ Rẹ̀ ni yóò ṣe ààbò àti odi mi."'), 'Ps$91:7': (None, 'ṣubu', 'Ps$91:7 "Ẹgbẹ̀rún yóò ṣubú ní ẹ̀gbẹ́ Rẹ, ẹgbàarùn ún ní ọwọ́ ọ̀tún Rẹ ṣùgbọ́n kì yóò sún mọ́ ọ̀dọ̀ Rẹ"'), 'Ps$91:9': (None, None, 'Ps$91:9 "Nítorí ìwọ fi OLÚWA ṣe ààbò Rẹ, ìwọ fi Ọ̀ga Ògo ṣe ibùgbé Rẹ."'), 'Ps$91:14': ('mọ', 'ife', 'Ps$91:14 "“Nítorí tí ìfẹ́ Rẹ sì mi,” “èmi yóò gbà ọ́; èmi yóò pa ọ́ mọ́, nítorí ìwọ jẹ́wọ́ orúkọ mi."'), 'Ps$92:1': (None, None, 'Ps$92:1 "Ohun rere ni láti máa fi ọpẹ́ fún OLÚWA àti láti máa kọrin sí orúkọ Rẹ̀, Ọ̀gá ògo,"'), 'Ps$92:4': ('ṣe', 'iṣẹ', 'Ps$92:4 Nítorí ìwọ ni ó mú inú mi dùn nípa iṣẹ́ Rẹ OLÚWA; èmi kọrin ayọ̀ sí iṣẹ́ ọwọ́ Rẹ.'), 'Ps$92:10': ('ororo', None, 'Ps$92:10 Ìwọ tí gbé ìwo mi ga bí i ti màlúù igbó; òróró dídára ni a dà sími ní orí.'), 'Ps$92:11': ('wo', None, 'Ps$92:11 Ojú mi ti rí ìṣubú àwọn ọ̀ta mi; ìparun sí àwọn ènìyàn búburú tí ó dìde sí mi.'), 'Ps$94:7': (None, None, 'Ps$94:7 "Wọ́n sọ pé, “OLÚWA kò rí i; Ọlọ́run Jákọ́bù kò sì kíyèsí i.” "'), 'Ps$96:12': (None, None, 'Ps$96:12 Jẹ́ kí oko kún fún ayọ̀ àti ohun gbogbo ti ń bẹ nínú Rẹ̀: nígbà náà ni gbogbo igi igbó yóò máa yọ̀'), 'Ps$97:4': (None, 'ayé', 'Ps$97:4 Ìmọ́lẹ̀ Rẹ̀ tàn ó sí kárí ayé ayé rí i ó sì wárìrì'), 'Ps$98:8': (None, None, 'Ps$98:8 "Ẹ jẹ́ kí odo kí ó sápẹ́, ẹ jẹ́ kí òkè kí o kọrin pẹ̀lú ayọ̀;"'), 'Prov$1:11': (None, 'lọ', 'Prov$1:11 "Bí wọn bá wí pé, “Tẹ̀lé wa kálọ; jẹ́ ká ba ní ibùba fún ẹ̀jẹ̀ ẹnìkan, jẹ́ ká dá àwọn aláìlera kan lọ́nà;"'), 'Prov$1:28': (None, None, 'Prov$1:28 "“Nígbà náà ni wọn yóò ké pè mí ṣùgbọ́n, èmi kò ní dáhùn; wọn yóò farabalẹ̀ wá mi ṣùgbọ́n wọn kì yóò rí mi."'), 'Prov$2:1': (None, 'gba', 'Prov$2:1 "Ọmọ mi, bí ìwọ bá gba ọ̀rọ̀ mi, tí ìwọ sì pa òfin mi mọ́ sí inú rẹ,"'), 'Prov$2:10': (None, None, 'Prov$2:10 Nítorí ọgbọ́n yóò wọ inú ọkàn rẹ ìmọ̀ yóò sì jẹ́ ìtura fún ọkàn rẹ'), 'Prov$2:19': (None, None, 'Prov$2:19 Kò sẹ́ni tó lọ sọ́dọ̀ rẹ̀ tí ó padà tàbí tí ó rí ipa ọ̀nà ìyè.'), 'Prov$3:11': (None, 'ibawi', 'Prov$3:11 " Ọmọ mi, má ṣe kẹ́gàn ìbáwí OLÚWA má si ṣe bínú nígbà tí ó bá ń bá ọ wí,"'), 'Prov$3:30': ('ni', None, 'Prov$3:30 Má ṣe fẹ̀ṣùn kan ènìyàn láì-ní-ìdí nígbà tí kò ṣe ọ́ ní ibi kankan rárá.'), 'Prov$4:4': (None, None, 'Prov$4:4 "Ó kọ́ mi ó sì wí pé “Jẹ́ kí àyà rẹ kí ó gba ọ̀rọ̀ mi dúró, pa òfin mí mọ́, kí ìwọ kí ó sì yè."'), 'Prov$4:6': (None, None, 'Prov$4:6 "Má ṣe kọ ọgbọ́n sílẹ̀, yóò sì dáàbò bò ọ́, fẹ́ràn rẹ̀, yóò sì bojú tó ọ."'), 'Prov$4:13': (None, 'tọju', 'Prov$4:13 "Di ẹ̀kọ́ mú, má ṣe jẹ́ kí ó lọ; tọ́jú u rẹ̀ dáradára Nítorí òun ni ìyè rẹ."'), 'Prov$4:14': ('ọna', 'ona', 'Prov$4:14 Má ṣe gbé ẹṣẹ̀ rẹ sí ojú ọnà àwọn ènìyàn búburú tàbí kí o rìn ní ọ̀nà àwọn ẹni ibi.'), 'Prov$4:16': ('ni', None, 'Prov$4:16 "Nítorí wọn kò le sùn àyàfi tí wọ́n bá ṣe ibi, wọn kò ní tòògbé àyàfi tí wọ́n bá gbé ẹlòmíràn ṣubú"'), 'Prov$4:21': ('oju', 'okan', 'Prov$4:21 Má ṣe jẹ́ kí wọ́n rá mọ́ ọ lójú pa wọ́n mọ́ sínú ọkàn rẹ;'), 'Prov$4:24': (None, 'ẹnu', 'Prov$4:24 mú àrékérekè kúrò ní ẹnu rẹ; sì jẹ́ kí ọ̀rọ̀ ìṣọkúṣọ jìnnà réré sí ẹnu rẹ.'), 'Prov$4:26': ('awọn ọna', 'ona', 'Prov$4:26 Kíyèsí ìrìn ẹṣẹ̀ rẹ sì rìn ní àwọn ọ̀nà tí ó dára nìkan'), 'Prov$5:3': ('ète', None, 'Prov$5:3 "Nítorí ètè alágbèrè aṣẹ́wó obìnrin a máa ṣun oyin, ọ̀rọ̀ rẹ̀ sì kúnná ju òróró lọ"'), 'Prov$5:5': (None, None, 'Prov$5:5 Ẹsẹ̀ rẹ̀ lọ sí ọ̀nà ikú ìgbéṣẹ̀ rẹ̀ lọ tààrà sí ibojì òkú.'), 'Prov$5:6': (None, 'ona', 'Prov$5:6 "Kò tilẹ̀ ronú nípa ọ̀nà ìyè ọ̀nà rẹ̀ rí pálapàla, ṣùgbọ́n kò tilẹ̀ mọ́. "'), 'Prov$5:8': (None, 'ni', 'Prov$5:8 Rìn ní ọ̀nà tí ó jìnnà síi dáadáa má ṣe súnmọ́ ẹnu ọ̀nà ilé rẹ̀'), 'Prov$5:20': (None, None, 'Prov$5:20 "Ọmọ mi, èéṣe tí ìfẹ́ aṣẹ́wó fi ń fà ọ́ tí ìwọ sì ń dì mọ́ ìyàwó ẹlòmíràn? "'), 'Prov$5:23': ('lọ', 'kú', 'Prov$5:23 Yóò kú nítorí àìgba ẹ̀kọ́ ìwà òmùgọ̀ ara rẹ̀ ló mú kó ṣáko lọ.'), 'Prov$6:13': (None, None, 'Prov$6:13 "tí ó ń sẹ́jú pàkòpàkò, ó ń fi ẹsẹ̀ ṣe àmì ó sì ń fi ìka ọwọ́ rẹ̀ júwe,"'), 'Prov$6:26': (None, None, 'Prov$6:26 nítorí Aṣẹ́wó yóò sọ ọ́ di àkàrà lásánlàsàn ẹ̀mí rẹ gan an sì ni alágbérè ń dọdẹ.'), 'Prov$7:6': (None, None, 'Prov$7:6 Ní ojú fèrèsé ilé è mi mo wo ìta láti ojú ù fèrèsé.'), 'Prov$7:18': ('gba', 'kun', 'Prov$7:18 "Wá, jẹ́ kí a lo ìfẹ́ papọ̀ ní kíkún títí di àárọ̀; jẹ́ kí a gbádùn ara wa pẹ̀lú ìfẹ́!"'), 'Prov$8:4': (None, 'eniyan', 'Prov$8:4 "Sí i yín ẹ̀yin ènìyàn, ní mo ń kígbe pè; Mo gbé ohun mi sókè sí gbogbo ènìyàn,"'), 'Prov$8:7': ('ète', None, 'Prov$8:7 "Ẹnu mi ń sọ ohun tí í ṣe òtítọ́, nítorí ètè mi kórìíra ibi."'), 'Prov$8:25': (None, None, 'Prov$8:25 "kí a tó fi àwọn òkè sí ipò wọn, ṣáájú àwọn òkè ni a ti bí mi,"'), 'Prov$8:27': (None, None, 'Prov$8:27 "Mo wà níbẹ̀ nígbà tí ó fi àwọn ọ̀run sí ipò wọn, nígbà tí ó fi òṣùwọ̀n àyíká sórí ibú omi,"'), 'Prov$9:3': (None, None, 'Prov$9:3 "ó ti rán àwọn ìránṣẹ́bìnrin rẹ̀ jáde, ó sì ń pè, láti ibi tí ó ga jù láàrin ìlú."'), 'Prov$9:6': (None, None, 'Prov$9:6 Fi ìwà àìmọ̀kan rẹ sílẹ̀ ìwọ yóò sì yè; rìn ní ọ̀nà òye.'), 'Prov$9:9': ('eko', 'kọ', 'Prov$9:9 "kọ́ Ọlọgbọ́n ènìyàn lẹ́kọ̀ọ́, yóò sì gbọ́n sí i kọ́ Olódodo ènìyàn lẹ́kọ̀ọ́, yóò sì fi kún ìmọ̀ rẹ̀. "'), 'Prov$9:14': (None, 'ijoko', 'Prov$9:14 "Ó jókòó lẹ́nu ọ̀nà ilé rẹ̀ lórí ìjòkó níbi tí ó ga jù láàrin ìlú,"'), 'Prov$10:32': ('ète', None, 'Prov$10:32 "Ètè Olódodo mọ ohun tí ó tọ́, ṣùgbọ́n ètè ènìyàn búburú kò mọ̀ ju èké lọ."'), 'Prov$11:7': (None, 'eniyan', 'Prov$11:7 "Nígbà tí ènìyàn búburú bá kú, ìrètí rẹ̀ a parun gbogbo ohun tó ń fojú sọ́nà fún nípa agbára rẹ̀ já sófo. "'), 'Prov$11:14': (None, 'ọpọlọpọ', 'Prov$11:14 Nítorí àìní ìtọ́sọ́nà orílẹ̀ èdè ṣubú ṣùgbọ́n nípaṣẹ̀ ọ̀pọ̀lọpọ̀ olùbádámọ̀ràn ìṣẹ́gun dájú.'), 'Prov$13:3': (None, 'ẹnu', 'Prov$13:3 "Ẹnikẹ́ni tí ó ṣọ́ ẹnu rẹ̀ pa ẹnu ara rẹ̀ mọ́, ṣùgbọ́n ẹni tí ó ń sọ̀rọ̀ gbàù-gbàù yóò parun. "'), 'Prov$13:5': ('eniyan buburu', 'irira', 'Prov$13:5 Olódodo kórìíra ohun tí í ṣe irọ́ Ṣùgbọ́n ènìyàn búburú hu ìwà ìríra àti ìtìjú.'), 'Prov$13:22': (None, None, 'Prov$13:22 "Ènìyàn rere a máa fi ogún sílẹ̀ fún àwọn ọmọ ọmọ rẹ̀, ṣùgbọ́n, a kó ọrọ̀ àwọn ẹlẹ́ṣẹ̀ pamọ́ fún àwọn olódodo. "'), 'Prov$14:3': ('ète', None, 'Prov$14:3 "Ọ̀rọ̀ aṣiwèrè a máa ṣokùnfà pàsán fún ẹ̀yìn rẹ̀, ṣùgbọ́n ọ̀rọ̀ ètè ọlọ́gbọ́n a máa dáàbò bò ó. "'), 'Prov$14:12': (None, 'iku', 'Prov$14:12 "Ọ̀nà kan wà tí ó dàbí i pé ó dára lójú ènìyàn, ṣùgbọ́n ní ìgbẹ̀yìn gbẹ́yín, a máa já sí ikú. "'), 'Prov$14:28': (None, None, 'Prov$14:28 "Ènìyàn púpọ̀ ní ìlú jẹ́ ògo Ọba, ṣùgbọ́n láì sí ìjòyè, Ọba á parun. "'), 'Prov$15:1': ('ibinu', 'ibinu', 'Prov$15:1 Ìdáhùn pẹ̀lẹ́ yí ìbínú padà ṣùgbọ́n ọ̀rọ̀ líle máa ń ru ìbínú sókè.'), 'Prov$15:4': (None, 'ẹmi', 'Prov$15:4 Ahọ́n tí ń mú ìtura wá jẹ́ igi ìyè ṣùgbọ́n ahọ́n ẹ̀tàn ń pa ẹ̀mí run.'), 'Prov$15:13': ('okan', 'ẹmi', 'Prov$15:13 Inú dídùn máa ń mú kí ojú túká ṣùgbọ́n ìbànújẹ́ ọkàn máa ń pa ẹ̀mí run.'), 'Prov$15:22': (None, 'imoran', 'Prov$15:22 Ìgbèrò a máa dasán níbi tí kò sí ìmọ̀ràn; ṣùgbọ́n a máa yege níbi tí ọ̀pọ̀lọpọ̀ olùbádámọ̀ràwà.'), 'Prov$15:27': ('ni', None, 'Prov$15:27 Ọ̀kánjúwà ènìyàn mú ìyọnu bá ilé rẹ̀ ṣùgbọ́n ẹni tí ó kórìíra àbẹ̀tẹ́lẹ̀ yóò yè.'), 'Prov$15:30': (None, 'okan', 'Prov$15:30 "Ojú tó túká máa ń mú ayọ̀ wá fún ọkàn, ìròyìn ayọ̀ sì ń mú ìlera wá sínú egungun. "'), 'Prov$16:6': (None, 'ibi', 'Prov$16:6 Nípaṣẹ̀ ìfẹ́ àti òtítọ́ a ṣètùtù ẹ̀ṣẹ̀ nípaṣẹ̀ ìbẹ̀rù OLÚWA ènìyàn sá fún ibi.'), 'Prov$16:10': (None, 'ẹnu', 'Prov$16:10 Ètè ọba a máa sọ̀rọ̀ nípa inú síi ẹnu rẹ̀ kò gbọdọ̀ gbé ẹ̀bi fún ara rẹ̀.'), 'Prov$16:23': ('ète', 'ẹnu', 'Prov$16:23 Ọkàn ọlọgbọ́n ènìyàn a máa ṣọ́ ẹnu rẹ̀ ètè rẹ̀ sì ń mú kí ẹ̀kọ́ gbèrú.'), 'Prov$16:25': (None, 'iku', 'Prov$16:25 Ọ̀nà kan tí ó dàbí i pé ó dára lójú ènìyàn ṣùgbọ́n ní ìgbẹ̀yìn a ṣokùnfà ikú.'), 'Prov$17:22': ('okan', None, 'Prov$17:22 Ọkàn tí ó túká jẹ́ ogún gidi ṣùgbọ́n ọkàn tí ó bàjẹ́ a máa mú kí egungun gbẹ.'), 'Prov$17:26': ('dara', None, 'Prov$17:26 Kò dára láti fìyà jẹ ènìyàn tí kò ṣẹ̀ tàbí láti na ìjòyè lórí òtítọ́ inú wọn.'), 'Prov$18:6': (None, 'ẹnu', 'Prov$18:6 Ètè aláìgbọ́n dá ìjà sílẹ̀ ẹnu rẹ̀ sì ń ṣokùnfà ẹgba.'), 'Prov$18:7': ('ète', None, 'Prov$18:7 Ẹnu aláìgbọ́n ni ó ba tirẹ̀ jẹ́ ètè rẹ̀ sì jẹ́ ìdẹkùn fún ọkàn rẹ̀.'), 'Prov$18:16': (None, None, 'Prov$18:16 Ẹ̀bùn máa ń sí ọ̀nà fún ẹni tí ń fún ni lẹ́bùn a sì mú un wọlé sí ọ̀dọ̀ àwọn olókìkí.'), 'Prov$18:19': (None, None, 'Prov$18:19 "Arákùnrin tí a ṣẹ̀ ṣòroó yípadà ju ìlú olódi lọ, ìjà sì dàbí ibodè ìlú olódi ńlá tí a ṣe. "'), 'Prov$18:20': (None, 'ẹnu', 'Prov$18:20 Láti inú èso ẹnu rẹ̀ ikùn ènìyàn a yó; láti inú ìkórè ẹnu rẹ̀ ni ó ti jẹ yó.'), 'Prov$19:17': (None, None, 'Prov$19:17 "Ẹni tí ó ṣáànú talákà, OLÚWA ní ó yá yóò sì pín in lérè ohun tí ó ti ṣe. "'), 'Prov$20:6': (None, 'eniyan', 'Prov$20:6 "Ọ̀pọ̀lọpọ̀ ènìyàn ni wọ́n máa ń wí oore wọn jáde, ṣùgbọ́nn kò rọrùn láti rí ènìyàn olóòótọ́. "'), 'Prov$20:9': ('ṣe', 'ni', 'Prov$20:9 "Táni ó le è wí pé, “Mo ti ṣe ọkàn mi ní mímọ́, mo mọ́, n kò sì lẹ́ṣẹ̀”? "'), 'Prov$20:18': (None, None, 'Prov$20:18 "Pa ètè rẹ mọ́ nípa wíwá ìmọ̀ràn bí o bá ń jagun, gba ìtọ́ṣọ́nà. "'), 'Prov$21:14': ('ibinu', 'ibinu', 'Prov$21:14 "Ọ̀rẹ́ ìkọ̀kọ̀, mú ìbínú kúrò: àti owó-àbẹ̀tẹ́lẹ̀ láti ibi ìkọ̀kọ̀ wá, dẹ́kun ìbínú líle. "'), 'Prov$22:6': (None, None, 'Prov$22:6 "Tọ́ ọmọdé ní ọ̀nà tí yóò tọ̀: nígbà tí ó bá dàgbà, kì yóò kúrò nínú rẹ̀. "'), 'Prov$22:19': (None, 'ni', 'Prov$22:19 "Kí ìgbẹ́kẹ̀lé rẹ lè wà níti OLÚWA, èmi fi hàn ọ́ lónìí, àní fún ọ."'), 'Prov$22:24': (None, None, 'Prov$22:24 Má ṣe bá oníbìínu ènìyàn ṣe ọ̀rẹ́; má sì ṣe bá ọkùnrin onínú-fùfù rìn.'), 'Prov$23:6': ('oju', None, 'Prov$23:6 "Má ṣe jẹ oúnjẹ olójú búburú, bẹ́ẹ̀ ni kí o má sì ṣe fẹ́ oúnjẹ-dídùn rẹ̀."'), 'Prov$23:26': ('oju', None, 'Prov$23:26 "Ọmọ mi, fi àyà rẹ fún mi, kí o sì jẹ́ kí ojú rẹ ní inú-dídùn sí ọ̀nà mi."'), 'Prov$23:33': (None, None, 'Prov$23:33 "Ojú rẹ yóò wò àwọn àjèjì obìnrin, àyà rẹ yóò sì sọ̀rọ àyídáyidà."'), 'Prov$24:6': (None, None, 'Prov$24:6 "Láti jagun, ó nílò ìtọ́sọ́nà: nínú ìsẹ́gún ni ọ̀pọ̀ onígbìmọ̀. "'), 'Prov$24:19': (None, 'eniyan', 'Prov$24:19 "Má ṣe fòyà nítorí àwọn ènìyàn ibi tàbí jowú àwọn ènìyàn búburú,"'), 'Prov$24:27': ('ṣe', 'kọ', 'Prov$24:27 Parí gbogbo iṣẹ́ ajé rẹ sì rí i pé oko rẹ ti ṣe dáradára; lẹ́yìn náà kọ́ ilé rẹ.'), 'Prov$25:2': (None, 'ọrọ', 'Prov$25:2 Ògo OLÚWA ní láti fi ọ̀rọ̀ kan pamọ́; láti rí ìdí ọ̀rọ̀ kan ni ògo àwọn ọba.'), 'Prov$25:21': (None, None, 'Prov$25:21 " Bí ebi bá ń pa ọ̀tá rẹ, fún un ní oúnjẹ jẹ; bí òrùngbẹ bá ń gbẹ ẹ́, fún un ní omi mú."'), 'Prov$25:26': (None, None, 'Prov$25:26 Bí ìsun tí ó di àbàtà tàbí kanga tí omi rẹ̀ bàjẹ́ ni olódodo tí ó fi àyè gba ènìyàn búburú.'), 'Prov$26:7': ('ni', None, 'Prov$26:7 Bí ẹsẹ̀ arọ tí ó ń mi dirodiro ni òwe lẹ́nu àṣiwèrè.'), 'Prov$26:21': ('ina', 'ina', 'Prov$26:21 "Bí èédú ti rí sí ẹyin iná, igi fún iná, bẹ́ẹ̀ ni ènìyàn oníjà fún ìjà dídá sílẹ̀. "'), 'Prov$26:27': (None, 'okuta', 'Prov$26:27 "Bí ènìyàn kan bá gbẹ́ kòtò, yóò ṣubú sínú rẹ̀. Bí ẹnikan bá ju òkúta, yóò padà sọ́dọ̀ òun tìkára rẹ̀. "'), 'Prov$27:2': ('ète', 'ẹnu', 'Prov$27:2 "Jẹ́ kí ẹlòmíràn yìn ọ́ dípò ẹnu ara rẹ àní ẹlòmíràn, kì í ṣe ètè ìwọ fúnra rẹ. "'), 'Prov$27:3': ('wuwo', None, 'Prov$27:3 "Òkúta wúwo, erùpẹ̀ sì wúwo ṣùgbọ́n ìmúbínú un aṣiwèrè wúwo ju méjèèjì lọ. "'), 'Prov$28:2': (None, 'ilẹ', 'Prov$28:2 "Nígbà tí orílẹ̀ èdè bá ní orí kunkun, àwọn ọba rẹ̀ a máa pọ̀ ṣùgbọ́n olóye àti onímọ̀ a máa pa òfin mọ́ "'), 'Prov$28:8': (None, 'ere', 'Prov$28:8 "Ẹni tí ó mú ọrọ̀ rẹ̀ di púpọ̀ nípa èrè àjẹjù ń kó o jọ fún ẹlòmìíràn, tí yóò ní àánú àwọn talákà. "'), 'Prov$28:10': ('ni', None, 'Prov$28:10 Ẹni tí ó mú olódodo rìn ọ̀nà búburú yóò bọ́ sínú pàkúté ara rẹ̀ ṣùgbọ́n aláìlẹ́gàn yóò gba ogún rere.'), 'Prov$28:12': (None, 'eniyan', 'Prov$28:12 "Nígbà tí olódodo ń lékè ariwo ayọ̀ ta; ṣùgbọ́n nígbà tí ènìyàn búburú gorí òye, àwọn ènìyàn a na pápá bora. "'), 'Prov$28:21': ('eniyan', None, 'Prov$28:21 Ojúṣááj ú ṣíṣe kò dára ṣíbẹ̀ ènìyàn kan ń ṣẹ̀ nítorí òkèlè oúnjẹ kan.'), 'Prov$29:15': (None, 'fun', 'Prov$29:15 Ọ̀pá ìbániwí ń fún ni ní ọgbọ́n ṣùgbọ́n ọmọ tí a fi sílẹ̀ fúnra rẹ̀ a dójú ti ìyá rẹ̀.'), 'Prov$29:22': (None, None, 'Prov$29:22 "Oníbìínú ènìyàn a ru ìjà sókè, Onínú-fùfù ènìyàn a sì máa dá ọ̀pọ̀lọpọ̀ ẹ̀ṣẹ̀. "'), 'Prov$29:23': (None, 'onirẹlẹ', 'Prov$29:23 Ìgbéraga ènìyàn a máa sọ ọ́ di ẹni ilẹ̀ ṣùgbọ́n onírẹ̀lẹ̀ ènìyàn a máa gba iyì kún iyì.'), 'Prov$29:27': (None, None, 'Prov$29:27 Olódodo kórìíra àwọn aláìsòótọ́: ènìyàn búburú kórìíra olódodo.'), 'Prov$30:7': ('ni', None, 'Prov$30:7 "“Ohun méjì ni mo ń bèèrè lọ́wọ́ rẹ, OLÚWA; má ṣe fi wọ́n dù mí kí ń tó kú:"'), 'Prov$30:21': (None, None, 'Prov$30:21 “Lábẹ́ nǹkan mẹ́ta ni ile ayé ti ń wárìrì lábẹ́ ohun mẹ́rin ni kò ti lè mí fín ín.'), 'Prov$30:24': (None, None, 'Prov$30:24 “Àwọn ohun mẹ́rin ló kéré láyé síbẹ̀ wọ́n gbọ́n gidigidi;'), 'Eccl$1:13': ('wá', 'wa', 'Eccl$1:13 "Mo fi àsìkò mi sílẹ̀ láti kọ́ àti láti ṣe àwárí pẹ̀lú ọgbọ́n, gbogbo ohun tí ó ń ṣẹlẹ̀ lábẹ́ ọ̀run. Háà ẹrù wúwo tí Ọlọ́run ti gbé lé àwọn ènìyàn:"'), 'Eccl$1:16': ('ni', 'iriri', 'Eccl$1:16 "Mo rò nínú ara mi, “Wòó, mo ti dàgbà, ọgbọ́n mi sì ti pọ̀ ju ti ẹnikẹ́ni tí ó ti ṣe alákòóṣo Jérúsálẹ́mù síwájú mi lọ, mo ti ní ìrírí púpọ̀ nípa ọgbọ́n àti ìmọ̀.”"'), 'Eccl$1:17': (None, None, 'Eccl$1:17 "Nígbà náà ni mo fi ara jìn láti ní ìmọ̀ nípa ọgbọ́n, àti pàápàá àìgbọ́n àti àgọ̀, ṣùgbọ́n mo rí, wí pé èyí pẹ̀lú bí ẹni ń gbìyànjú àti mú afẹ́fẹ́ ni."'), 'Eccl$1:18': (None, 'ibanujẹ', 'Eccl$1:18 "Nítorí pé ọgbọ́n púpọ̀ ìbànújẹ́ púpọ̀ ní ń mú wá, bí ìmọ̀ bá sì ṣe pọ̀ tó náà ni ìbànújẹ́ ń pọ̀ tó."'), 'Eccl$2:3': ('fun', None, 'Eccl$2:3 "Mo tiraka láti dun ara mi nínú pẹ̀lú ọtí wáìnì, àti láti fi ọwọ́ lé òmùgọ̀,—ọkàn mi sì ń tọ́ mi pẹ̀lú ọgbọ́n. Mo fẹ́ wo ohun tí ó yẹ ní ṣíṣe fún ènìyàn ní abẹ́ ọ̀run ní ìwọ̀nba ọjọ́ ayé rẹ̀."'), 'Eccl$2:10': ('oju', 'okan', 'Eccl$2:10 "Èmi kò jẹ́ kí ojú mi ṣe aláìrí ohun tí ó bá ń fẹ́. N kò sì jẹ́ kí ọkàn mi ó ṣe aláìní ìgbádùn. Ọkàn mi yọ̀ nínú gbogbo iṣẹ́ mi, èyí sì ni èrè fún gbogbo wàhálà mi."'), 'Eccl$2:12': ('isinwin', None, 'Eccl$2:12 "Nígbà náà ni mo tún bẹ̀rẹ̀ sí ọgbọ́n, àti ìsínwín àti àìgbọ́n kí ni ọba tí ó jẹ lẹ́yìn tí ọba kan kú le è ṣe ju èyí tí ọba ìṣáájú ti ṣe lọ."'), 'Eccl$2:17': (None, None, 'Eccl$2:17 "Nítorí náà, mo kórìíra ìwà-láàyè, nítorí pé iṣẹ́ tí wọn ń ṣe ní abẹ́ oòrùn ti mú ìdààmú bá mi. Gbogbo rẹ̀ aṣán ni, ó dàbí ẹni ń gbìyànjú àti mú afẹ́fẹ́ ni."'), 'Eccl$2:24': (None, 'dara', 'Eccl$2:24 " Ènìyàn kò le è ṣe ohun kóhun tí ó dára jù pé kí ó jẹ kí ó sì mu, kí ó sì rí ìtẹ́lọ́rùn nínú iṣẹ́ rẹ̀. Mo rí wí pé eléyìí pẹ̀lú wá láti ọwọ́ Ọlọ́run."'), 'Eccl$2:26': (None, None, 'Eccl$2:26 "Fún ẹni tí ó bá tẹ́ Ọlọ́run lọ́rùn ni Ọlọ́run yóò fún ni ọgbọ́n, ìmọ̀ àti ìdùnnú, ṣùgbọ́n fún ẹlẹ́ṣẹ̀, o fún-un ní iṣẹ́ láti ṣà àti láti kó ohun-ìní pamọ́ kí ó sì fi fún ẹni tí o tẹ Ọlọ́run lọ́rùn. Eléyìí pẹ̀lú, aṣán ni, ó dàbí ẹni a gbìyànjú àti mú."'), 'Eccl$3:1': (None, None, 'Eccl$3:1 Àsìkò wà fún ohun gbogbo àti ìgbà fún gbogbo nǹkan ní abẹ́ ọ̀run.'), 'Eccl$3:10': (None, 'ni', 'Eccl$3:10 Mo ti rí àjàgà tí Ọlọ́run gbé lé ọmọ ènìyàn.'), 'Eccl$3:11': (None, 'eniyan', 'Eccl$3:11 "Ó ti ṣe ohun gbogbo dáradára ní àsìkò tirẹ̀, ó sì ti fi èrò nípa ayérayé sí ọkàn ọmọ ènìyàn, ṣíbẹ̀, wọn kò le ṣe àwárí ìdí ohun tí Ọlọ́run ti ṣe láti ìbẹ̀rẹ̀ títí dé òpin."'), 'Eccl$3:19': (None, 'eniyan', 'Eccl$3:19 "Ó ní láti jẹ́ ohun ìrántí pé ìpín ènìyàn rí bí i tẹranko ìpín ń dúró dè wọ́n. Bí ọ̀kan ti ń kú náà ni èkejì yóò kú. Àwọn méjèèjì ni irú èémí kan náà, ènìyàn kò ṣàn ju ẹranko lọ, nítorí pé aṣán ni yíyè jẹ́ fún wọn."'), 'Eccl$4:9': (None, None, 'Eccl$4:9 "Ẹni méjì ṣàn ju ẹnìkan, nítorí wọ́n ní ààbò rere fún iṣẹ́ wọn:"'), 'Eccl$5:1': (None, 'irubọ', 'Eccl$5:1 "Ṣọ́ ìrìn rẹ nígbà tí o bá lọ sí ilé Ọlọ́run. Kí ìwọ kí ó sì múra láti gbọ́ ju àti se ìrúbọ aṣiwèrè, tí kò mọ̀ wí pé òun ń ṣe búburú."'), 'Eccl$5:2': (None, 'ẹnu', 'Eccl$5:2 "Má ṣe yára pẹ̀lú ẹnu un rẹ má sọ ohunkóhun níwájú Ọlọ́run Ọlọ́run ń bẹ ní ọ̀run Ìwọ sì wà ní ayé, nítorí náà jẹ́ kí ọ̀rọ̀ rẹ mọ ní ìwọ̀n"'), 'Eccl$5:15': (None, None, 'Eccl$5:15 "Ìhòòhò ni ènìyàn wá láti inú ìyá rẹ̀, bí ó sì ṣe wá, bẹ́ẹ̀ ni yóò kúrò kò sí ohunkóhun nínú iṣẹ́ rẹ̀ tí ó le mú ní ọwọ́ rẹ̀."'), 'Eccl$5:19': (None, 'ebun', 'Eccl$5:19 "Síwájú sí, nígbà tí Ọlọ́run fún ẹnikẹ́ni ní ọrọ̀ àti ohun ìní, tí ó sì fún-un lágbára láti gbádùn wọn, láti gba ìpín rẹ̀ kí inú rẹ̀ sì dùn sí iṣẹ́ rẹ̀—ẹ̀bùn Ọlọ́run ni èyí."'), 'Eccl$6:1': (None, None, 'Eccl$6:1 Mo ti rí ibi mìíràn lábẹ́ oòrùn.'), 'Eccl$6:3': (None, None, 'Eccl$6:3 "Ọkùnrin kan le è ní ọgọ́rún ọmọ kí ó sì wà láàyè ní ọ̀pọ̀lọpọ̀ ọdún, ṣíbẹ̀ kò sí bí ó ti le wà láàyè pẹ́ tó, bí kò bá le è gbádùn ohun-ìní rẹ̀ kí ó sì gba ìsìnkú tí ó tọ́, mo ṣọ wí pé àbíkú ọmọ ṣàn jù ú lọ."'), 'Eccl$6:6': ('lọ', 'gbe', 'Eccl$6:6 "Kó dà, bí ó wà láàyè fún ẹgbẹ̀rún ọdún méjì yípo ṣùgbọ́n tí ó kùnà láti gbádùn ohun-ìní rẹ̀. Kìí ṣe ibìkan ni gbogbo wọn ń lọ?"'), 'Eccl$7:1': (None, 'dara', 'Eccl$7:1 Orúkọ rere ṣàn ju ìpara olóòórùn dídùn lọ ọjọ́ ikú sì dára ju ọjọ́ tí a bí ènìyàn lọ'), 'Eccl$7:13': ('ṣe', None, 'Eccl$7:13 Wo ohun tí Ọlọ́run ti ṣe: “Ta ni ó le è to ohun tí ó ti ṣe ní wíwọ́?”'), 'Eccl$7:18': (None, None, 'Eccl$7:18 Ó dára láti mú ọ̀kan kí o má sì ṣe fi èkejì sílẹ̀ Ọkùnrin tí ó bẹ̀rù Ọlọ́run yóò bọ́ lọ́wọ́ gbogbo àrékérekè.'), 'Eccl$7:20': (None, None, 'Eccl$7:20 Kò sí olódodo ènìyàn kan láyé tí ó ṣe ohun tí ó tọ́ tí kò dẹ́ṣẹ̀ rárá.'), 'Eccl$7:25': ('isinwin', None, 'Eccl$7:25 "Mó wá ròó nínú ọkàn mi láti mọ̀, láti wá àti láti ṣàwárí ọgbọ́n àti ìdí ohun gbogbo, àti láti mọ àgọ́ ìwà búburú àti ti ìsínwín tàbí òmùgọ̀. "'), 'Eccl$7:27': ('ri', 'wa', 'Eccl$7:27 "Oníwàásù wí pé, “Wò ó” eléyìí ni ohun tí mo ti ṣàwárí: “Mímú ohun kan pọ̀ mọ́ òmíràn láti ṣàwárí ìdí ohun gbogbo."'), 'Eccl$7:29': (None, 'ni', 'Eccl$7:29 "Eléyìí nìkan ni mo tíì rí: Ọlọ́run dá ìran ènìyàn dáradára, ṣùgbọ́n ènìyàn ti lọ láti ṣàwárí ohun púpọ.”"'), 'Eccl$8:4': (None, 'ọba', 'Eccl$8:4 "Níwọ̀n ìgbà tí ọ̀rọ̀ ọba ni àṣẹ, ta ni ó le è ṣọ fún-un wí pé, “Kí ni ìwọ ń ṣe?”"'), 'Eccl$8:9': ('ni', None, 'Eccl$8:9 "Gbogbo ǹnkan wọ̀nyí ni mo ti rí, tí mo sì ń mú lò ní ọkàn mi sí gbogbo iṣẹ́ tí a ti ṣe lábẹ́ oòrùn. Ìgbà kán wà tí ẹnìkan ń ṣe olórí àwọn tó kù fún ìpalára rẹ̀."'), 'Eccl$8:10': ('ṣe', 'wá', 'Eccl$8:10 Nígbà náà ni mo tún rí ìsìnkú òsìkà—àwọn tí wọ́n máa ń wá tí wọ́n sì ń lọ láti ibi mímọ́ kí wọn sì gba ìyìn ní ìlú tàbí tí wọ́n ti ṣe èyí. Eléyìí pẹ̀lú kò ní ìtumọ̀.'), 'Eccl$8:11': (None, 'ṣe', 'Eccl$8:11 "Nígbà tí a kò bá tètè ṣe ìdájọ́ fún ẹlẹ́ṣẹ̀ kíákíá, ọkàn àwọn ènìyàn a máa kún fún ọ̀pọ̀lọpọ̀ ìmọ̀ láti ṣe ibi."'), 'Eccl$8:16': ('wa', 'wo', 'Eccl$8:16 Nígbà tí mo lo ọkàn mi láti mọ ọgbọ́n àti láti wo wàhálà ènìyàn ní ayé.'), 'Eccl$9:3': (None, None, 'Eccl$9:3 "Ohun búburú ni èyí jẹ́ nínú gbogbo ohun tí ó ń ṣẹlẹ̀ lábẹ́ oòrùn. Ìpín kan ṣoṣo ni ó ń dúró de gbogbo wọn, ọkàn ènìyàn pẹ̀lú kún fún ibi, ìṣínwín sì wà ní ọkàn wọn nígbà tí wọ́n wà láàyè àti nígbà tí wọ́n bá darapọ̀ mọ́ òkú."'), 'Eccl$9:12': (None, 'eniyan', 'Eccl$9:12 "Síwájú sí i, kò sí ẹni tí ó mọ ìgbà tí àkókò rẹ̀ yóò dé: Gẹ́gẹ́ bí a ti ń mú ẹja nínú àwọ̀n búburú tàbí tí a ń mú ẹyẹ nínú okùn gẹ́gẹ́ bẹ́ẹ̀ ni a ń mú ènìyàn ní àkókò ibi tí ó ṣubú lù wọ́n láì rò tẹ́lẹ̀."'), 'Eccl$9:13': (None, None, 'Eccl$9:13 Mo sì tún rí àpẹẹrẹ ọgbọ́n tí ó dùn mọ́ mi lábẹ́ oòrùn:'), 'Eccl$9:18': (None, 'dara', 'Eccl$9:18 "Ọgbọ́n dára ju ohun—èlò ogun lọ, ṣùgbọ́n ẹlẹ́ṣẹ̀ kan a máa ba ohun dídára púpọ̀ jẹ́."'), 'Eccl$10:12': ('ète', 'ẹnu', 'Eccl$10:12 Ọ̀rọ̀ tí ó wá láti ẹnu ọlọ́gbọ́n a máa ní oore-ọ̀fẹ́ ṣùgbọ́n ètè òmùgọ̀ fúnrarẹ̀ ni yóò parun.'), 'Eccl$10:13': ('isinwin', None, 'Eccl$10:13 Ìbẹ̀rẹ̀ ọ̀rọ̀ rẹ̀ jẹ́ òmùgọ̀; ìparí rẹ̀ sì jẹ́ ìṣínwín búburú.'), 'Eccl$11:7': (None, None, 'Eccl$11:7 Ìmọ́lẹ̀ dùn Ó sì dára fún ojú láti rí oòrùn.'), 'Eccl$11:9': ('oju', 'okan', 'Eccl$11:9 "Jẹ́ kí inú rẹ dùn, ìwọ ọ̀dọ́mọdé ní ìgbà tí o wà ní èwe kí o sì jẹ́ kí ọkàn rẹ fún ọ ní ayọ̀ ní ìgbà èwe rẹ. Tẹ̀lé ọ̀nà ọkàn rẹ àti ohunkóhun tí ojú rẹ rí ṣùgbọ́n mọ̀ dájú pé nípa gbogbo nǹkan wọ̀nyí ni Ọlọ́run yóò mú ọ wá sí ìdájọ́."'), 'Eccl$12:1': (None, None, 'Eccl$12:1 "Rántí Ẹlẹ́dàá rẹ ní ọjọ́ èwe rẹ, nígbà tí ọjọ́ ibi kò tíì dé àti tí ọdún kò tíì ní ṣun mọ́ etílé, nígbà tí ìwọ yóò wí pé, “Èmi kò ní ìdùnnú nínú wọn”"'), 'Eccl$12:7': ('fun', 'pada', 'Eccl$12:7 "Tí erùpẹ̀ yóò sì padà sí ilẹ̀ ibi tí ó ti wà rí, tí ẹ̀mí yóò sì padà ṣọ́dọ̀ Ọlọ́run tí ó fi í fún ni. "'), 'Eccl$12:14': (None, 'iṣẹ', 'Eccl$12:14 "Nítorí Ọlọ́run yóò ṣe ìdájọ́ olúkúlùkù iṣẹ́ àti ohun ìkọ̀kọ̀, kì bá à ṣe rere kì bá à ṣe búburú."'), 'Song$1:6': ('ni', None, 'Song$1:6 Má ṣe wò mí nítorí wí pé mo dúdú Nítorí òòrùn mú mi dúdú Ọmọkùnrin ìyá mi bínú sími Ó sì fi mí ṣe olùtọ́jú ọgbà àjàrà. Ọgbà àjàrà tèmi ni èmi kò tọ́jú'), 'Song$2:10': (None, None, 'Song$2:10 "Olùfẹ́ mi fọhùn ó sì sọ fún mi pé, “Dìde, Olùfẹ́ mi, Arẹwà mi, kí o sì wà pẹ̀lú mi."'), 'Song$2:11': (None, None, 'Song$2:11 "Wò ó! Ìgbà otútù ti kọjá; Òjò ti rọ̀ dawọ́, ó sì ti lọ."'), 'Song$2:14': ('jẹ ki', 'oju', 'Song$2:14 "Àdàbà mi wà nínú pàlàpálá òkúta, ní ibi ìkọ̀kọ̀ ní orí òkè gíga, fi ojú rẹ hàn mí, jẹ́ kí èmi gbọ́ ohùn rẹ; Nítorí tí ohùn rẹ dùn, tí ojú rẹ sì ní ẹwà."'), 'Song$3:4': ('ní', 'jẹ ki', 'Song$3:4 "Gẹ́rẹ́ tí mo fi wọ́n sílẹ̀ Ni mo rí ẹni tí ọkàn mi fẹ́. Mo dì í mú, èmi kò sì jẹ́ kí ó lọ títí tí mo fi mú u wá sílé ìyá mi, sínú yàrá ẹni tí ó lóyún mi"'), 'Song$4:9': ('oju', 'okan', 'Song$4:9 "Ìwọ ti gba ọkàn mi, arábìnrin mi, ìyàwó mi; Ìwọ ti gba ọkàn mi pẹ̀lú ìwò ẹ̀ẹ̀kan ojú rẹ, pẹ̀lú ọ̀kan nínú ìlẹ̀kẹ̀ ọrùn rẹ,"'), 'Song$4:12': (None, 'orisun', 'Song$4:12 "Arábìnrin mi ni ọgbà tí a sọ, arábinrin mi, ìyàwó mi ìṣun tí a sé mọ́, oríṣun tí a fi èdìdì dì."'), 'Song$5:1': ('oyin', 'olufẹ', 'Song$5:1 "Mo ti dé inú ọgbà mi, arábìnrin mi, ìyàwó mi; mo ti kó òjíá pẹ̀lú òórùn dídùn mi jọ. Mo ti jẹ afárá mi pẹ̀lú oyin mi; mo ti mu ọtí wáìnì mi pẹ̀lú wàrà mi. Ẹ jẹ, ẹ̀yin ọ̀rẹ́, ẹ mu, àní ẹ mu àmuyó, ẹ̀yin olùfẹ́"'), 'Song$5:2': (None, 'olufẹ', 'Song$5:2 "Mo ti sùn ṣùgbọ́n ọkàn mi jí. Gbọ́! Olùfẹ́ mi ń kan ilẹ̀kùn. “Ṣí i fún mi, arábìnrin mi, olùfẹ́ mi, àdàbà mi, aláìlábàwọ́n mi Orí mi kún fún omi ìrì, irun mi kún fún òtútù òru.”"'), 'Song$5:8': ('ife', 'olufẹ', 'Song$5:8 "Ẹ̀yin ọmọbìnrin Jérúsálẹ́mù, mo bẹ̀ yín bí ẹ̀yin bá rí olùfẹ́ mi, kí ni ẹ̀yin yóò wí fún un? Ẹ wí fún un pé àìṣàn ìfẹ́ ń ṣe mi."'), 'Song$7:8': (None, None, 'Song$7:8 "Mo ní, “Èmi yóò gun igi ọ̀pẹ lọ; Èmi yóò di ẹ̀ka rẹ̀ mú” Kí ọmú rẹ rí bí ìdì èso àjàrà, Àti èémi imú rẹ bí i ápù."'), 'Song$7:12': (None, 'gba', 'Song$7:12 "Jẹ́ kí a lọ sínú ọgbà àjàrà ní kùtùkùtù láti wo bí àjàrà rúwé bí ìtànná àjàrà bá là. Àti bí póméegíránéètì bá ti rúdí, níbẹ̀ ni èmi yóò ti fi ìfẹ́ mi fún ọ."'), 'Song$7:13': ('fun', 'ni', 'Song$7:13 "Àwọn èṣo mánídárákì mú òórùn wọn jáde ní ẹnu ọ̀nà wa ni onírúurú àṣàyàn èso, èso ọ̀tún àti àkúgbó tí mo ti kó pamọ́ fún ọ, olùfẹ́ mi."'), 'Song$8:6': ('ina', 'ina', 'Song$8:6 "Gbé mi sí àyà rẹ bí èdìdì bí èdìdì lé apá rẹ; nítorí ìfẹ́ lágbára bí ikú, ìjowú sì le bí isà òkú jíjò rẹ̀ rí bí ìjò iná, gẹ́gẹ́ bí ọ̀wọ́ iná OLÚWA."'), 'Song$8:11': ('ní', None, 'Song$8:11 Sólómónì ní ọgbà àjàrà kan ní Báálí-Hómónì ó gbé ọgbà àjàrà rẹ̀ fún àwọn alágbàtọ́jú olúkúlùkù ni ó ń mú èso rẹ̀ wá ẹgbẹ̀rún fàdákà.'), 'Isa$1:2': (None, None, 'Isa$1:2 "Gbọ́ ẹ̀yin ọ̀run! Fi etí sílẹ̀, ìwọ ayé! Nítorí OLÚWA ti sọrọ̀: “Mo tọ́ àwọn ọmọ dàgbà, Ṣùgbọ́n wọ́n ti ṣọ̀tẹ̀ sí mi."'), 'Isa$1:7': ('ilẹ', None, 'Isa$1:7 "Orílẹ̀-èdè yín dahoro, a dáná sun àwọn ìlú yín, oko yín ni àwọn àjèjì ti jẹ run lójú ara yín náà, ni gbogbo rẹ̀ ṣòfò bí èyí tí àwọn àjèjì borí rẹ̀."'), 'Isa$1:24': (None, None, 'Isa$1:24 "Nítorí náà ni OLÚWA, OLÚWA àwọn ọmọ-ogun Alágbára kanṣoṣo tí Ísírẹ́lì sọ wí pé: “Á à! Èmi yóò ní ìfọ̀kànbalẹ̀ lórí àwọn ọ̀tá mi n ó sì gbẹ̀ṣan lára àwọn ọ̀tá mi."'), 'Isa$2:7': (None, None, 'Isa$2:7 "Ilẹ̀ wọ́n kún fún fàdákà àti wúrà ìṣúra wọn kò sì ní òpin. Ilẹ̀ ẹ wọn kún fún ẹṣin, kẹ̀kẹ́ ogun wọn kò sì lópin."'), 'Isa$2:11': (None, 'eniyan', 'Isa$2:11 "Ojú agbéraga ènìyàn ni a ó rẹ̀ sílẹ̀ a ó sì tẹrí ìgbéraga ènìyàn ba, OLÚWA nìkan ṣoṣo ni a ó gbéga ní ọjọ́ náà. "'), 'Isa$2:12': ('giga', None, 'Isa$2:12 "OLÚWA àwọn ọmọ-ogun ní ọjọ́ kan nípamọ́ fún gbogbo agbéraga àti ọlọ́kàn gíga nítorí gbogbo àwọn tí a gbéga (ni a ó rẹ̀sílẹ̀),"'), 'Isa$2:17': (None, None, 'Isa$2:17 "Ìgbéraga ènìyàn ni a ó tẹ̀ lórí ba a ó sì rẹ ìgbéraga ènìyàn sílẹ̀, OLÚWA nìkan ṣoṣo ni a ó gbéga ní ọjọ́ náà,"'), 'Isa$3:6': ('ṣe', None, 'Isa$3:6 "Ọkùnrin kan yóò di ọ̀kan nínu àwọn arákùnrin rẹ̀ mú nínú ilé baba rẹ̀, yóò sì wí pé, “Ìwọ́ ní aṣọ, ìwọ máa ṣe olórí wa, sì mójútó àwọn ahoro wọ̀nyí!”"'), 'Isa$4:1': (None, None, 'Isa$4:1 "Ní ọjọ́ náà, obìnrin méje yóò dì mọ́ ọkunrin kan yóò sì wí pé, “Àwa ó má a jẹ oúnjẹ ara wa a ó sì pèsè aṣọ ara wa; sáà jẹ́ kí a má a fi orúkọ rẹ̀ pè wá. Mú ẹgan wá kúrò!”"'), 'Isa$4:5': ('ina', None, 'Isa$4:5 "Lẹ́yìn náà, OLÚWA yóò dá sorí òkè Síhónì àti sóríi gbogbo àwọn tí ó péjọ pọ̀ ṣíbẹ̀, kúrúkúrú èéfín ní ọ̀ṣán àti ìtànsán ọ̀wọ́ iná ní òru, lórí gbogbo ògo yìí ni ààbò yóò wà."'), 'Isa$5:5': (None, None, 'Isa$5:5 "Ní ìsinsìn yìí, èmi yóò sọ fún ọ ohun tí n ó ṣe sí ọgbà-àjàrà mi: Èmi yóò gé igi inú un rẹ̀ kúrò, a ó sì pa á run, Èmi yóò wó ògiri rẹ̀ lulẹ̀ yóò sì di ìtẹ̀mọ́lẹ̀."'), 'Isa$5:13': ('eniyan', None, 'Isa$5:13 "Nítorí náà àwọn ènìyàn mi yóò lọ sí ìgbèkùn nítorí òye kò sí fún wọn, ebi ni yóò pa àwọn bọ̀rọ̀kìnní wọn kú; ẹgbàágbèje wọn ni òrùgbẹ yóò sì gbẹ."'), 'Isa$5:17': ('jẹ', None, 'Isa$5:17 "Nígbà náà ni àwọn àgùntàn yóò máa jẹ́ ko ní ibùgbé e wọn, àwọn ọ̀dọ́ àgùntàn yóò máa jẹ nínú ahoro àwọn ọlọ́rọ̀. "'), 'Isa$5:18': (None, None, 'Isa$5:18 "Ègbé ni fún àwọn tí ń fi okùn, àti àwọn tí o dàbí ẹni pé wọ́n ń fí okùn kẹ́kẹ́ ẹ̀sìn fa ìkà,"'), 'Isa$5:24': ('ina', 'ina', 'Isa$5:24 "Nítorí náà, gẹ́gẹ́ bí ahọ́n iná ṣe ń jó àkékù koríko run àti bí koríko ṣe relẹ̀ wẹ̀sì nínú iná, bẹ́ẹ̀ ni egbò wọn yóò jẹrà tí òdodo wọn yóò sì fẹ́ lọ bí eruku: nítorí pé wọ́n ti kọ òfin OLÚWA àwọn ọmọ-ogun sílẹ̀ wọ́n sì gan ọ̀rọ̀ Ẹni mímọ́ Ísírẹ́lì."'), 'Isa$5:26': (None, None, 'Isa$5:26 "Ó gbé ọ̀págun ṣókè sí àwọn orílẹ̀-èdè tí ó jìnnà-réré, Ó súfèé sí àwọn tí ó wà ní ìpẹ̀kun ilẹ̀. Àwọn rè é, wọ́n ti sáré"'), 'Isa$6:6': ('ni', None, 'Isa$6:6 "Lẹ́yìn náà, ọ̀kan nínú àwọn Ṣéráfù wọ̀nyí fò wá sí ọ̀dọ̀ mi pẹ̀lú ẹ̀ṣẹ́-iná ní ọwọ́ rẹ̀, èyí tí ó ti fi ẹ̀mú mú ní oríi pẹpẹ."'), 'Isa$6:9': ('wo', None, 'Isa$6:9 " Òun sì wí pé, “Tọ àwọn ènìyàn mi lọ kí o sì wí pé; “ ‘Ní gbígbọ́ ẹ̀yin yóò gbọ́, kì yóò sì yé e yín; ní rírí ẹ̀yin yóò ri, ẹ̀yin kì yóò mọ òye.’"'), 'Isa$6:10': ('wo', 'gbo', 'Isa$6:10 "Mú kí àyà àwọn ènìyàn wọ̀nyí yigbì, mú kí wọn kútí kí o sì pa ojú wọn dé. Bí kò ṣe bẹ́ẹ̀, wọn yóò fi ojú u wọn ríran, ki wọn má ba à fi etíi wọn gbọ́ràn, kí òye máa ba à yé wọn ní ọkàn an wọn kí wọn má ba à yípadà kí a má ba à sì wò wọ́n sàn.”"'), 'Isa$7:1': (None, None, 'Isa$7:1 "Nígbà tí Áhásì ọmọ Jótamù ọmọ Hùṣáyà jẹ́ ọba Júdà, ọba Résínì ti Árámù àti Pẹ́kà ọmọ Rẹ̀málíà ọba Ísírẹ́lì gòkè wá láti bá Jérúsálẹ́mù jà, ṣùgbọ́n wọn kò sì le è borí i rẹ̀."'), 'Isa$7:5': (None, None, 'Isa$7:5 "Árámù, Éfáímù àti Rèmálíà ti dìtẹ̀ ìparun rẹ, wọ́n wí pé,"'), 'Isa$7:21': (None, None, 'Isa$7:21 "Ní ọjọ́ náà, ọkùnrin kan yóò máa sin ọ̀dọ́ màlúù kan àti ewúrẹ́ méjì."'), 'Isa$8:4': ('ya', 'ni', 'Isa$8:4 Kí ọmọkùnrin náà tó mọ bí a ti ń sọ pé ‘Baba mi’ tàbí ‘Ìyá mi’ gbogbo ọrọ̀ Dámásíkù àti ìkógun ti Ṣamáríà ni ọba àwọn Áṣíríà yóò ti kó lọ.”'), 'Isa$8:5': (None, None, 'Isa$8:5 OLÚWA sì tún sọ fún mi pé:'), 'Isa$8:9': (None, 'fun', 'Isa$8:9 "Ké ariwo ogun, ẹ̀yin orílẹ̀-èdè, kí a sì fọ́ọ yín túútúúú, fetísílẹ̀, ẹ̀yin ilẹ̀ jínjìn réré. Ẹ palẹ̀mọ́ fún ogun, kí a sì fọ́ ọ túútúúú!"'), 'Isa$8:14': (None, None, 'Isa$8:14 " Òun yóò sì jẹ́ ibi mímọ́ ṣùgbọ́n fún ilé Ísírẹ́lì méjèèjì ni yóò jẹ́, òkúta tí í mú ni kọṣẹ̀ àti àpáta tí ó mú wọn ṣubú àti fún àwọn ènìyàn Jérúsálẹ́mù ni yóò jẹ́ tàkúté àti ẹ̀bìtì."'), 'Isa$8:17': ('duro', None, 'Isa$8:17 " Èmi yóò dúró de OLÚWA, ẹni tí ó ń fi ojú rẹ̀ pamọ́ fún ilé Jákọ́bù. Mo fi ìgbẹ́kẹ̀lé mi sínú rẹ."'), 'Isa$9:2': ('okunkun', 'ojiji', 'Isa$9:2 Àwọn ènìyàn tí ń rìn nínú òkùnkùn ti rí ìmọ́lẹ̀ ńlá; Lórí àwọn tí ń gbé ní ilẹ̀ òjìji ikú ni ìmọ́lẹ̀ ti ràn.'), 'Isa$9:18': ('ina', 'ina', 'Isa$9:18 "Nítòótọ́ ìkà ń jóni bí iná, ó ń jó àtẹ̀wọ̀n, àtẹ̀gùn run, ó sì ń dáná sun ìgbẹ́ tó bẹ́ẹ̀ tí ó fi ń yí lọ sókè ní ọ̀wọ̀n èéfín."'), 'Isa$10:1': ('ni', None, 'Isa$10:1 Ègbé ni fún àwọn ti ń ṣe òfin àìṣòdodo'), 'Isa$10:3': ('wá', 'ṣe', 'Isa$10:3 Kí ni ìwọ yóò ṣe ní ọjọ́ ìṣirò nígbà tí ìparun bá ti ọ̀nà jínjìn wá? Ta ni ìwọ yóò sá tọ̀ fún ìrànlọ́wọ́? Níbo ni ìwọ yóò fi ọrọ̀ rẹ sí?'), 'Isa$10:6': (None, None, 'Isa$10:6 "Mo rán an sí orílẹ̀ èdè aláìní Ọlọ́run Mo dojúu rẹ̀ kọ àwọn ènìyàn tí ó múmi bínú láti já ẹrù gbà, àti láti kó ìkógun láti tẹ̀mọ́lẹ̀ bí amọ̀ ní ojú òpópó."'), 'Isa$10:16': ('ina', 'ina', 'Isa$10:16 "Nítorí náà, ni OLÚWA, OLÚWA àwọn ọmọ-ogun, yóò rán àrùn ìrẹ̀dànù sórí àwọn akíkanjú jagunjagun, lábẹ́ ògo rẹ̀ ni iná kan yóò ti sọ gẹ́gẹ́ bí iná ajónirun."'), 'Isa$10:17': (None, None, 'Isa$10:17 "Ìmọ́lẹ̀ Ísírẹ́lì yóò di iná, ẹni Mímọ́ an wọn ahọ́n iná, ní ijọ́ kan ṣoṣo yóò jó yóò sì run àti ẹ̀gún àti ẹ̀wọ̀n."'), 'Isa$10:28': ('ya', 'ni', 'Isa$10:28 "Wọ́n wọ Áíyátì, Wọ́n gba Mígírónì kọjá Wọ́n kó nǹkan pamọ́ sí Mísímásì."'), 'Isa$11:1': (None, None, 'Isa$11:1 " Èhíhù kan yóò sọ láti ibi kùkùté Jéésè, láti ara gbòǹgbò rẹ̀ ni ẹ̀ka kan yóò ti so èso."'), 'Isa$11:6': (None, 'gbe', 'Isa$11:6 " Ìkookò yóò sì máa bá ọmọ àgùntàn gbé, àmọ̀tẹ́kùn yóò sì sùn ti ewúrẹ́ ọmọ màlúù òun ọmọ kìnnìún àti ọmọ ẹran ó wà papọ̀ ọ̀dọ́mọdé yóò sì máa dà wọ́n."'), 'Isa$11:7': ('jẹ', None, 'Isa$11:7 "Màlúù àti béárì yóò máa jẹun pọ̀, àwọn ọmọ wọn yóò dùbúlẹ̀ pọ̀, kìnnìún yóò sì máa jẹ koríko gẹ́gẹ́ bí akọ màlúù."'), 'Isa$11:12': (None, None, 'Isa$11:12 "Òun yóò gbé àṣíá sókè fún àwọn orílẹ̀ èdè, yóò sì kó àwọn ìgbèkùn Ísírẹ́lì jọ, yóò kó gbogbo àwọn ènìyàn Júdà tí a ti fọ́n káàkiri jọ, láti igun mẹ́rẹ̀ẹ̀rin ilẹ̀ ayé."'), 'Isa$12:4': ('ṣe', 'orukọ', 'Isa$12:4 "Ní ọjọ́ náà, ìwọ yóò wí pé: “Fi ọpẹ́ fún OLÚWA, ké pe orúkọ rẹ̀, Jẹ́ kí ó di mímọ̀ láàrin àwọn orílẹ̀ èdè, ohun tí ó ti ṣe kí o sì kéde pé a ti gbé orúkọ rẹ̀ ga."'), 'Isa$13:4': ('eniyan', None, 'Isa$13:4 "Gbọ́ ohùn kan ní àwọn orí òkè, gẹ́gẹ́ bí i ti ogunlọ́gọ̀ ènìyàn Gbọ́, ìdàrúdàpọ̀ láàrin àwọn ìjọba, gẹ́gẹ́ bí i ti ìkórajọ àwọn orílẹ̀ èdè! OLÚWA àwọn ọmọ-ogun ti kó ogun rẹ̀ jọ àwọn jagunjagun fún ogun."'), 'Isa$13:5': ('ilẹ', None, 'Isa$13:5 "Wọ́n wá láti ọ̀nà jínjìn réré, láti ìpẹ̀kun ọ̀run wá OLÚWA pẹ̀lú àwọn ohun ìjà ìbínú un rẹ̀— láti pa gbogbo orílẹ̀ èdè náà run. "'), 'Isa$13:7': (None, None, 'Isa$13:7 "Nítorí èyí, gbogbo ọwọ́ ni yóò rọ, ọkàn ẹnìkọ̀ọ̀kan yóò rẹ̀wẹ̀sì."'), 'Isa$13:9': ('ibinu', 'ibinu', 'Isa$13:9 "Kíyèsí i, ọjọ́ OLÚWA ń bọ̀ ọjọ́ búburú, pẹ̀lú ìbínú àti ìrunú gbígbóná— láti sọ ilẹ̀ náà dahoro àti láti pa àwọn ẹlẹ́ṣẹ̀ inú un rẹ̀ run."'), 'Isa$13:11': (None, 'ibi', 'Isa$13:11 "Èmi yóò jẹ ayé ní ìyà nítorí ibi rẹ̀, àwọn ìkà nítorí ẹ̀ṣẹ̀ẹ wọn. Èmi yóò fi òpin sí gààrù àwọn agbéraga èmi ó sì rẹ ìgbéraga àwọn aláìláàánú sílẹ̀."'), 'Isa$13:13': ('ibinu', 'ibinu', 'Isa$13:13 "Nítorí náà èmi yóò jẹ́ kí àwọn ọ̀run kí ó wárìrì; ayé yóò sì mì tìtì ní ibùjókòó rẹ̀ láti ọwọ́ ìbínú OLÚWA àwọn ọmọ-ogun, ní ọjọ́ ìbínú gbígbóná an rẹ. "'), 'Isa$13:21': (None, 'gbe', 'Isa$13:21 " Ṣùgbọ́n àwọn ohun abẹ̀mí aṣálẹ̀ ni yóò gbé bẹ̀, àwọn ajáko yóò kún inú ilé wọn, níbẹ̀ ni àwọn òwììwí yóò máa gbé níbẹ̀ ni àwọn ewúrẹ́-igbó yóò ti máa bẹ́ kiri."'), 'Isa$14:1': ('ni', None, 'Isa$14:1 "OLÚWA yóò fi àánú hàn fún Jákọ́bù, yóò tún Ísírẹ́lì yàn lẹ́ẹ̀kan síi yóò sì fi ìdíi wọn kalẹ̀ ní ilẹ̀ àwọn tìkálára wọn. Àwọn àjèjì yóò darapọ̀ mọ́ wọn, wọn yóò sì fọwọ́ṣowọ́pọ̀ pẹ̀lú ilée Jákọ́bù."'), 'Isa$14:2': ('gba', 'mú', 'Isa$14:2 Àwọn orílẹ̀ èdè yóò gbà wọ́n wọn yóò sì mú wọn wá sí àyèe wọn. Ilé Ísírẹ́lì yóò gba àwọn orílẹ̀ èdè gẹ́gẹ́ bí àwọn ẹrúkùnrin àti ẹrúbìnrin ní ilẹ̀ OLÚWA. Wọn yóò kó àwọn akónilẹ́rú wọn ní ìgbèkùn wọn yóò sì jọba lórí àwọn amúnisìn wọn.'), 'Isa$14:3': ('fun', None, 'Isa$14:3 "Ní ọjọ́ tí OLÚWA yóò fi ìtùra fún un yín kúrò nínú ìpọ́njú àti ìyà àti ìdè ìkà,"'), 'Isa$14:6': (None, None, 'Isa$14:6 "èyí tí ó ti lu àwọn ènìyàn bolẹ̀ pẹ̀lú ti kò dáwọ́ dúró, nínú ìrunú ni ó ṣẹ́gun àwọn orílẹ̀ èdè pẹ̀lú ìgbónára tí kò lópin."'), 'Isa$14:16': (None, None, 'Isa$14:16 "Àwọn tí ó rí ọ yóò tẹjúmọ́ ọ, wọ́n ronú nípa àtubọ̀tán rẹ: “Ǹjẹ́ èyí ni ẹni tí ó mi ayé tìtì tí ó sì jẹ́ kí àwọn ilẹ̀ ọba wárìrì."'), 'Isa$14:19': ('òkúta', None, 'Isa$14:19 "Ṣùgbọ́n a jù ọ́ síta kúrò nínú ibojì gẹ́gẹ́ bí ẹ̀ka igi tí a kọ̀ sílẹ̀, àwọn tí a pa ni ó bò ọ́ mọ́lẹ̀, àwọn tí idà ti gún, àwọn tí ó ṣọ̀kalẹ̀ lọ sínú òkúta inú ọ̀gbun. Gẹ́gẹ́ bí òkú ó di ìtẹ̀mọ́lẹ̀ ní abẹ́ àtẹ́lẹṣẹ̀,"'), 'Isa$15:7': (None, 'ni', 'Isa$15:7 Báyìí gbogbo ọrọ̀ tí wọ́n ti ní tí wọ́n sì tòjọ wọ́n ti kó wọn kọjá lọ lóríi gẹ̀rẹ́gẹ̀rẹ́ odò pópúlárì.'), 'Isa$15:9': ('mú', 'ilẹ', 'Isa$15:9 "Omi Dímónì kún fún ẹ̀jẹ̀, ṣíbẹ̀ èmi ó tún mu ohun tí ó jù báyìí lọ wá sórí Dímónì— kìnnìún kan wá ṣórí àwọn ìṣáǹṣá Móábù àti lórí àwọn tí ó tún ṣẹ́kù sórí ilẹ̀ náà."'), 'Isa$16:5': ('idajọ', 'idajo', 'Isa$16:5 "Nínú ìfẹ́ a ó fi ìdí ìjọba kan múlẹ̀, ní òdodo ọkùnrin kan yóò jókòó lóríi rẹ̀— ọ̀kan láti ilé Dáfídì wá. Ẹni nní ti ìdájọ́ ń wá ẹ̀tọ́ tí ó sì fi ìyára wá ohun tí í ṣe òdodo. "'), 'Isa$16:10': (None, 'ni', 'Isa$16:10 "Ayọ̀ àti ìdùnnú ni a ti mú kúrò nínú ọgbà-igi eléso rẹ; kò sí ẹnìkan tí ó kọrin tàbí kígbe nínu ọgbà-àjàrà: ẹnikẹ́ni kò fún ọtí níbi ìfúntí, nítorí mo ti fi òpin sí gbogbo igbe."'), 'Isa$17:4': (None, None, 'Isa$17:4 “Ní ọjọ́ náà ni ògo Jákọ́bù yóò ṣá; ọ̀rá ara rẹ̀ yóò ṣòfò dànù.'), 'Isa$17:8': ('ṣe', 'iṣẹ', 'Isa$17:8 "Wọn kò ní wo àwọn pẹpẹ mọ́, èyí tí í ṣe iṣẹ́ ọwọ́ọ wọn, wọn kò sì ní kọbi ara sí òpó Áṣérà mọ́ tàbí pẹpẹ tùràrí tí ìka ọwọ́ wọn ti ṣe."'), 'Isa$17:10': (None, None, 'Isa$17:10 "Ẹ ti gbàgbé Ọlọ́run Olùgbàlà yín; ẹ kò sì rántí àpáta náà, àní odi agbára yín."'), 'Isa$17:12': ('eniyan', None, 'Isa$17:12 "Kíyèsii, ìrunú àwọn orílẹ̀ èdè— wọ́n ń runú bí ìgbì òkun! Kíyèsii, rògbòdìyàn tí ogunlọ́gọ̀ ènìyàn wọ́n bú ramúramù gẹ́gẹ́ bí ariwo odò ńlá!"'), 'Isa$18:1': ('ilẹ', None, 'Isa$18:1 " Ègbé ni fún ìwọ ilẹ̀ tí ó kún fún ariwo ìyẹ́, ní àwọn ipadò Kúṣì,"'), 'Isa$18:3': (None, 'ayé', 'Isa$18:3 "Gbogbo ẹ̀yin ènìyàn ayé, tí ó ń gbé orílẹ̀ ayé, nígbà tí a bá gbé àṣíá kan ṣókè lórí òkè, ẹ ó rí i, nígbà tí a bá fun fèrè kan ẹ ó gbọ́ ọ."'), 'Isa$18:4': (None, None, 'Isa$18:4 "Èyí ni ohun tí OLÚWA sọ fún mi: “Èmi yóò dákẹ́ jẹ́ ẹ́, n ó sì máa wo òréré láti ibùgbé e mi wá, gẹ́gẹ́ bí ooru gbígbóná nínú ìtànsán òòrùn, gẹ́gẹ́ bí òjò-dídì ní àárin gbùngbùn ìkóórè.”"'), 'Isa$18:5': (None, 'ge', 'Isa$18:5 "Nítorí, kí ìkóórè tó bẹ̀rẹ̀, nígbà tí ìtànná kò sí mọ́, nígbà tí ìtànná di èso tí ó ń pọ́n bọ̀. Òun yóò sì fi ọ̀bẹ ìwẹgi gé àwọ ìsó tuntun kúrò, yóò sì gé àwọn ẹ̀ka tí ó ń gbilẹ̀ lulẹ̀ a sì kó wọn dànù."'), 'Isa$18:7': ('ilẹ', None, 'Isa$18:7 "Ní àkókò náà ni a ó mú ẹ̀bùn wá fún OLÚWA àwọn ọmọ-ogun láti ọ̀dọ̀ àwọn ènìyàn gíga tí ẹran ara wọn jọ̀lọ̀, láti ọ̀dọ̀ àwọn ènìyàn tí a bẹ̀rù níbi gbogbo, orílẹ̀ èdè aláfojúdi àti alájèjì èdè, ilẹ̀ ẹni tí omi pín sí yẹ́lẹyẹ̀lẹ— a ó mú àwọn ẹ̀bùn náà wá sí òkè Síónì, ibi tí orúkọ OLÚWA àwọn ọmọ-ogun gbé wà."'), 'Isa$19:4': (None, 'ọwọ', 'Isa$19:4 "Èmi yóò fi Éjíbítì lé agbára àwọn ìkà amúnisìn lọ́wọ́, ọba aláìláàánú ni yóò jọba lé wọn lórí,” ni OLÚWA, OLÚWA Alágbára wí. "'), 'Isa$19:11': (None, 'imoran', 'Isa$19:11 "Àwọn oníṣẹ́ Ṣóánì ti di aláìgbọ́n, àwọn ọlọgbọ́n olùdámọ̀ràn Fáráò ń mú ìmọ̀ràn àìgbọ́n wá. Báwo ló ṣe lè sọ fún Fáráò pé, “Mo jẹ́ ọ̀kan nínú àwọn ọlọ́gbọ́n ènìyàn, ọ̀kan nínú àwọn ọmọ-ẹ̀yìn àwọn ọba ijọ́un.” "'), 'Isa$19:14': (None, 'ni', 'Isa$19:14 "OLÚWA ti da ẹ̀mí òòyì sínú un wọn; wọ́n mú Éjíbítì ta gbọ̀n-ọ́n gbọ̀n-ọ́n nínú ohun gbogbo tí ó ń ṣe, gẹ́gẹ́ bí ọ̀mùtí ti í ta gbọ̀n-ọ́ngbọ̀n-ọ́n nínú èébì i rẹ̀."'), 'Isa$19:21': ('ṣe', 'ṣe', 'Isa$19:21 "Báyìí ni OLÚWA yóò sọ ara rẹ̀ di mímọ̀ fún àwọn ará Éjíbítì àti ní ọjọ́ náà ni wọn yóò gba OLÚWA gbọ́. Wọn yóò sìn ín pẹ̀lú ẹbọ ọrẹ oníhóró, wọn yóò jẹ́jẹ̀ẹ́ fún OLÚWA wọn yóò sì mú un ṣẹ."'), 'Isa$21:3': (None, None, 'Isa$21:3 "Pẹ̀lú èyí, ìrora kómi lára gírígírí, ìrora gbámimú, gẹ́gẹ́ bí i ti obìnrin tí ń rọbí, Mo ta gbọ̀n-ọ́n gbọ̀n-ọ́n nítorí ohun tí mo gbọ́, ọkàn mi pòrúúrù nípa ohun tí mo rí."'), 'Isa$21:14': (None, None, 'Isa$21:14 "gbé omi wá fún àwọn tí òùngbẹ ń gbẹ; ẹ̀yin tí ó ń gbé Tẹ́mà, gbé oúnjẹ wá fún àwọn ìsáǹsá."'), 'Isa$22:11': ('ṣe', 'ni', 'Isa$22:11 "Ìwọ mọ agbemi sí àárin ògiri méjì fún omi inú adágún àtijọ́, ṣùgbọ́n ìwọ kò wo ẹni tí ó ṣe é tẹ́lẹ̀ tàbí kí o kọbi ara sí ẹni tí ó gbérò rẹ̀ ní ìgbà pípẹ́ ṣẹ́yìn. "'), 'Isa$22:20': (None, None, 'Isa$22:20 "“Ní ọjọ́ náà. Èmi yóò ké sí ọmọ ọ̀dọ̀ mi, Eliákímù ọmọ Hílíkíyà."'), 'Isa$22:24': (None, 'ọmọ', 'Isa$22:24 Gbogbo ògo ìdílée rẹ̀ ni yóò rọ̀ mọ́ ọn ní ọrùn: ìran rẹ̀ àti àrọ́mọdọ́mọ rẹ̀ gbogbo ohun èlò aláìlágbára láti orí abọ́ rẹ̀ dé orí ìdẹ̀ rẹ̀.'), 'Isa$23:11': (None, 'ọwọ', 'Isa$23:11 OLÚWA ti na ọwọ́ọ rẹ̀ jáde sí orí òkun ó sì mú kí àwọn ilẹ̀ ọba wárìrì. Ó ti pa àṣẹ kan tí ó kan Fonísíà pé kí a pa ilé ààbò rẹ̀ run.'), 'Isa$23:13': (None, 'ilẹ', 'Isa$23:13 "Bojú wo ilẹ̀ àwọn ará Bábílónì, àwọn ènìyàn tíkò jámọ́ nǹkankan bàyìí Àwọn Ásíríà ti sọ ọ́ di ibùgbé àwọn ohun abẹ̀mí ihà; wọn ti gbé ilé ìṣọ́ ìtẹ̀gun wọn ṣókè, wọ́n ti tú odi rẹ̀ sí ìhòòhò wọ́n sì ti sọ ọ́ di ààtàn. "'), 'Isa$23:17': (None, 'ayé', 'Isa$23:17 " Ní òpin àádọ́rin ọdún náà, OLÚWA yóò bá Tírè jà. Òun yóò sì padà sí àyálò rẹ̀ gẹ́gẹ́ bí aṣẹ́wó, yóò sì máa ṣe òwò rẹ̀ pẹ̀lú àwọn ilẹ̀ ọba tí ó wà ní ilẹ̀ ayé."'), 'Isa$24:4': (None, 'ayé', 'Isa$24:4 "Ilẹ̀ ayé ti gbẹ ó sì ṣá, ayé ń ṣòjòjò, àárẹ̀ mú un, àwọn ẹni gíga ilẹ̀ ayé wà nínú ìpọ́njú"'), 'Isa$24:18': ('wá', None, 'Isa$24:18 "Ẹnikẹ́ni tí ó bá sá nítorí ariwo ìpayà yóò ṣubú sínú ihò, ẹnikẹ́ni tí ó bá sì yọ́ jáde nínú ihò ni ìdẹkùn yóò gbámú. Ibodè ọ̀run ti wà ní sísí sílẹ̀ Ìpìlẹ̀ ayé mì tìtì."'), 'Isa$25:12': (None, None, 'Isa$25:12 "Òun yóò sì bi gbogbo ògiri gíga alágbára yín lulẹ̀ wọn yóò sì wà nílẹ̀ Òun yóò sì mú wọn wá si ilẹ̀, àní sí erùpẹ̀ lásán."'), 'Isa$26:7': (None, None, 'Isa$26:7 "Ipa ọ̀nà àwọn olódodo tẹ́jú Ìwọ adúró ṣinṣin, ìwọ mú ọ̀nà àwọn olódodo kúnná."'), 'Isa$26:9': (None, 'ayé', 'Isa$26:9 Ọkàn mi ń pòùngbẹẹ̀ rẹ ní ọ̀gànjọ́ òru; ní òwúrọ̀ ni ẹ̀mí mi ń ṣàfẹ́rí rẹ. Nígbà tí ìdájọ́ọ̀ rẹ bá sọ̀kalẹ̀ sórí ayé àwọn ènìyàn yóò kọ́ ìṣòdodo.'), 'Isa$26:13': ('ni', None, 'Isa$26:13 "OLÚWA Ọlọ́run wa, àwọn olúwa mìíràn lẹ́yìn rẹ ti jọba lé wa lórí, ṣùgbọ́n orúkọọ̀ rẹ nìkan ni àwa fi ọ̀wọ̀ fún."'), 'Isa$26:14': ('ni', None, 'Isa$26:14 "Wọ́n ti kú báyìí, wọn ò sí láàyè mọ́; gbogbo ẹ̀mí tí ó ti kọjá lọ wọ̀nyí kò le dìde mọ́. Ìwọ jẹ́ wọ́n ní ìyà o sì sọ wọ́n di asán, Ìwọ pa gbogbo ìrántíi wọn rẹ́ pátapáta."'), 'Isa$26:18': (None, 'ayé', 'Isa$26:18 "Àwa wà nínú oyún, a wà nínú ìrora ṣùgbọ́n, afẹ́fẹ́ ni a bí lọ́mọ. Àwa kò mú ìgbàlà wá sí ilẹ̀ ayé; àwa kọ́ ni a bí àwọn ènìyàn tí ó wà láyé. "'), 'Isa$27:5': (None, None, 'Isa$27:5 "Bí bẹ́ẹ̀ kọ́, jẹ́ kí wọn wá sọ́dọ̀ mi fún ààbò; jẹ́ kí wọ́n wá àlàáfíà pẹ̀lúu mi, bẹ́ẹ̀ ni, jẹ́ kí wọn wá àlàáfíà pẹ̀lúu mi.” "'), 'Isa$27:6': (None, 'kun', 'Isa$27:6 "Ní ọjọ́ iwájú Jákọ́bù yóò ta gbòǹgbò, Ísírẹ́lì yóò tanná yóò sì rudi èṣo rẹ̀ yóò sì kún gbogbo ayé. "'), 'Isa$27:11': ('ṣe', 'ni', 'Isa$27:11 "Nígbà tí àwọn ẹ̀ka rẹ̀ gbẹ, a kán wọn dànù àwọn obìnrin wá wọ́n, sì fi wọ́n dáná nítorí àwọn ènìyàn tí òye kò yé ni wọ́n jẹ́; Nítorí náà ni Ẹlẹ́dàá wọn kò ṣe yọ́nú sí wọn. Ẹlẹ́dàá wọn kò sì síjú àánú wò wọ́n."'), 'Isa$28:2': (None, None, 'Isa$28:2 "Kíyèsíì, OLÚWA ní ẹnìkan tí ó le tí ó sì lágbára, gẹ́gẹ́ bí ẹ̀wọ́ yìnyín àti bí àtẹ̀gùn apanirun, gẹ́gẹ́ bí àrọ̀ọ̀dá òjò àti òjò tí ó mú ẹ̀kún omi wá, òun yóò fi tipátipá sọ ọ́ sílẹ̀."'), 'Isa$28:5': ('adé', 'adé', 'Isa$28:5 "Ní ọjọ́ náà OLÚWA àwọn ọmọ-ogun yóò jẹ́ adé tí ó lógo, Òdòdó tí ó lẹ́wà fún àwọn ènìyàn an rẹ̀ tí ó ṣẹ́kù."'), 'Isa$28:13': (None, 'lọ', 'Isa$28:13 "Fún ìdí èyí, ọ̀rọ̀ OLÚWA sí wọn yóò di pé ṣe kí o ṣe, ṣe kí o ṣe, àṣẹ lé àṣẹ, àṣẹ lé àṣẹ; díẹ̀ níhìnín, díẹ̀ ní ọ̀hún bẹ́ẹ̀ ni wọn yóò lọ tí wọn yóò tún ṣubu ṣẹ́yìn, wọn yóò farapa, wọn yóò bọ́ sínú okùn a ó sì gbá wọn mú. "'), 'Isa$28:15': ('ṣe', 'ni', 'Isa$28:15 "Ẹ fọ́n pé, “Àwa ti bá ikú mulẹ̀, pẹ̀lú ibojì ni àwa ti jọ ṣe àdéhùn. Nígbà tí ìbáwí gbígbóná fẹ́ kọjá, kò le kàn wá lára, nítorí a ti fi irọ́ ṣe ààbò o wa àti àìṣòtítọ́ ibi ipamọ́ wa.”"'), 'Isa$28:21': ('dide', 'oke', 'Isa$28:21 "OLÚWA yóò dìde sókè gẹ́gẹ́ bí ó ti ṣe ní òkè Péráṣímù yóò ru ara rẹ̀ ṣókè gẹ́gẹ́ bí ó ti ṣe ní àfonífojì Gíbíónì— láti ṣe iṣẹ́ rẹ̀, abàmì iṣẹ́ ẹ rẹ̀, yóò ṣe iṣẹ́ rẹ̀, àní àjèjì iṣẹ́ rẹ̀."'), 'Isa$28:22': ('ṣe', 'ni', 'Isa$28:22 "Ní ìsinsìn yìí ẹ dákẹ́ ẹlẹ́yà ṣíṣe, bí bẹ́ẹ̀ kọ́ ìdè e yín yóò le sí i; OLÚWA, àní OLÚWA àwọn-ogun ti sọ fún mi nípa àṣẹ ìparun ti ó ti pa lórí gbogbo ilẹ̀ náà. "'), 'Isa$29:6': (None, None, 'Isa$29:6 "OLÚWA àwọn ọmọ-ogun yóò wá pẹ̀lú àrá, ilẹ̀ ríri àti ariwo ńlá àti ẹ̀fúúfù líle àti iná ajónirun orílẹ̀ èdè tí ó bá Áríẹ́lì jà, tí ó dojú ìjà kọ òun àti ilé olódì rẹ̀ tí ó sì dó tì í, yóò dàbí ẹni pé nínú àlá, bí ìran ní òru"'), 'Isa$29:8': (None, None, 'Isa$29:8 "àti bí ìgbà tí ẹni tí ebi ń pa ń lá àlá pé òun ń jẹun, ṣùgbọ́n ó jí, ebi rẹ̀ sì wà pẹ̀lúu rẹ̀; àti bí ẹni tí òùngbẹ ń gbẹ lá àlá pé òun ń mumi, ṣùgbọ́n nígbà tí ó yajú pẹ́ ẹ́, pẹ̀lú òùngbẹ tí kò dáwọ́ dúró ni. Bẹ́ẹ̀ ni yóò sì rí fún ọ̀pọ̀lọpọ̀ orílẹ̀ èdè tí ń bá òkè Ṣíhónì jà. "'), 'Isa$29:13': ('ète', 'ẹnu', 'Isa$29:13 " OLÚWA wí pé: “Àwọn ènìyàn yìí súnmọ́ ọ̀dọ̀ mi pẹ̀lú ẹnu wọn wọ́n sì bọ̀wọ̀ fún mi pẹ̀lú ètè wọn, ṣùgbọ́n ọkàn wọn jìnnà sí mi. Ìsìn wọn fún mi ni a gbé ka orí òfin tí àwọn ọkùnrin kọ́ ni."'), 'Isa$29:16': ('ṣe', None, 'Isa$29:16 " Ẹ̀yin dojú nǹkan délẹ̀, bí ẹni pé wọ́n rò pé amọ̀kòkò dàbí amọ̀! Ǹjẹ́ ohun tí a ṣe le sọ fún olùṣe pé “Òun kọ́ ló ṣe mí”? Ǹjẹ́ ìkòkò lè sọ nípa amọ̀kòkò pé, “kò mọ nǹkan”? "'), 'Isa$29:18': ('wo', 'gbo', 'Isa$29:18 " Ní ọjọ́ náà adití yóò gbọ́ ọ̀rọ̀ ìwé kíká náà, láti inú fìrífìrí àti òkùnkùn ni àwọn ojú afọ́jú yóò ríran."'), 'Isa$30:1': (None, None, 'Isa$30:1 "“Ègbé ni fún orílẹ̀-èdè alágídí náà,” ni OLÚWA wí, “Fún àwọn tí ó gbé ètò jáde tí kì í ṣe tèmi, tí wọ́n ń gbìmọ̀ pọ̀, ṣùgbọ́n kì í ṣe nípa Ẹ̀mí mi, tí wọ́n ń dá ẹ̀ṣẹ̀ lórí ẹ̀ṣẹ̀;"'), 'Isa$30:8': (None, None, 'Isa$30:8 "Lọ nísinsìn yìí, kí o sì kọ ọ́ sí ara pátakó fún wọn, tẹ̀ ẹ́ sí ara ìwé kíká, pé fún àwọn ọjọ́ tí ó ń bọ̀ kí ó sì lè jẹ́ ẹ̀rí ayérayé."'), 'Isa$30:12': (None, None, 'Isa$30:12 "Nítorí náà, èyí ni ohun tí Ẹni-Mímọ́ Ísírẹ́lì wí: “Nítorí pé ẹ ti kọ ọ̀rọ̀ yìí sílẹ̀, ẹ gbáralé ìnilára kí ẹ sì gbọ́kànlé ẹ̀tàn,"'), 'Isa$30:20': (None, 'wo', 'Isa$30:20 "Bí ó tilẹ̀ jẹ́ pé OLÚWA fún ọ ní àkàrà ìyà àti omi ìpọ́njú, àwọn olùkọ́ rẹ kì yóò farasin mọ́; pẹ̀lú ojú rẹ ni ìwọ ó rí wọn."'), 'Isa$30:23': ('fun', None, 'Isa$30:23 "Òun yóò sì rọ òjò fún un yín sí àwọn irúgbìn tí ẹ gbìn sórí ilẹ̀, oúnjẹ tí yóò ti ilẹ̀ náà wá yóò ní ọ̀rá yóò sì pọ̀. Ní ọjọ́ náà ni àwọn ẹran ọ̀sìn yín yóò máa jẹ koríko ní pápá oko tútù tí ó tẹ́jú."'), 'Isa$30:27': (None, None, 'Isa$30:27 "Kíyèsí i, orúkọ OLÚWA ti òkèèrè wá pẹ̀lú ìbínú gbígbóná àti kùrukùru èéfín tí ó nípọn; ètè rẹ̀ kún fún ìbínú ahọ́n rẹ̀ sì jẹ́ iná ajónirun."'), 'Isa$30:30': ('ina', 'ina', 'Isa$30:30 "OLÚWA yóò jẹ́ kí wọn ó gbọ́ ohùn ògo rẹ̀ yóò sì jẹ́ kí wọ́n rí apá rẹ̀ tí ó ń bọ̀ wálẹ̀ pẹ̀lú ìbínú gbígbóná àti iná ajónirun, pẹ̀lúu mọ̀nàmọ́ná, àrá àti yìnyín."'), 'Isa$30:33': ('ina', 'ina', 'Isa$30:33 "A ti tọ́jú Tófẹ́tì sílẹ̀ tipẹ́tipẹ́, a ti tọ́jú rẹ̀ sílẹ̀ fún ọba. Ojú ààrò rẹ̀ ni a ti gbẹ́ jìn tí ó sì fẹ̀, pẹ̀lú ọ̀pọ̀lọpọ̀ iná àti igi ìdáná; èémí OLÚWA, gẹ́gẹ́ bí ìsàn ṣúfúrù tí ń jó ṣe mú un gbiná."'), 'Isa$31:2': (None, 'mú', 'Isa$31:2 "Ṣíbẹ̀ṣíbẹ̀ òun pẹ̀lú jẹ́ ọlọgbọ́n ó sì lè mú ìparun wá; òun kì í kó ọ̀rọ̀ rẹ̀ jẹ. Òun yóò dìde sí ilé àwọn ìkà, àti sí àwọn tí ń ṣe ìrànlọ́wọ́ fún aṣebi."'), 'Isa$31:4': (None, None, 'Isa$31:4 "Èyí ni ohun tí OLÚWA sọ fún mi: “Gẹ́gẹ́ bí i kìnnìún ti í ké àní kìnnìún ńlá lórí ẹranànjẹ rẹ̀ bí a tilẹ̀ rí ẹgbẹ́ àwọn olùṣọ́-àgùntàn tí a sì pè wọ́n papọ̀ láti kojúu rẹ̀, ẹ̀rù kò lè bà á pẹ̀lú igbe wọn akitiyan wọn kò sì lè dí i lọ́wọ́ bẹ́ẹ̀ gẹ́gẹ́ ni OLÚWA àwọn ọmọ-ogun yóò sọ̀kalẹ̀ wá láti jagun lórí òkè Ṣíhónì àti lórí ibi gíga rẹ̀."'), 'Isa$31:7': ('ni', None, 'Isa$31:7 "Nítorí ní ọjọ́ náà, ẹnì kọ̀ọ̀kan yín yóò kọ àwọn ère fàdákà àti wúrà tí ọwọ́ ẹ̀ṣẹ̀ẹ yín ti ṣe."'), 'Isa$31:8': (None, 'eniyan', 'Isa$31:8 “Ásíríà yóò ṣubú lọ́wọ́ idà kan tí kò ti ọwọ́ ènìyàn wá; idà tí kì í ṣe ti ẹ̀dá alààyè ni yóò pa wọ́n. Wọn yóò sì sá níwájú idà náà àti àwọn ọmọkùnrin wọn ni a ó fún ní iṣẹ́ ipá ṣe.'), 'Isa$32:1': (None, None, 'Isa$32:1 "Wòó, ọba kan yóò jẹ nínú òdodo àwọn olórí yóò máa ṣàkóso ní ìdájọ́."'), 'Isa$32:3': ('wo', 'gbo', 'Isa$32:3 "Nígbà náà ni ojú àwọn tí ó rí kò ní pàdé mọ́, àti etí àwọn tí ó gbọ́ yóò tẹ́tí sílẹ̀."'), 'Isa$32:6': ('ọkàn', None, 'Isa$32:6 "Nítorí òmùgọ̀ ṣọ̀rọ̀ òmùgọ̀, ọkàn rẹ̀ kún fún ìwà ibi: òun hùwà àìwà-bí-Ọlọ́run ó sì ń tan àṣìṣe tí ó kan OLÚWA kalẹ̀; ẹni ebi ń pa ló fi sílẹ̀ lófo àti fún àwọn tí òrùngbẹ ń gbẹ ni ó mú omi kúrò."'), 'Isa$32:11': (None, None, 'Isa$32:11 "Wárìrì, ẹ̀yin obìnrin onítẹ̀lọ́rùn bẹ̀rù, ẹ̀yin ọ̀dọ́mọbìnrin tí ẹ rò pé ọkàn yín balẹ̀! Ẹ bọ́ aṣọ yín kúrò, ẹ ró aṣọ ọ̀fọ̀ mọ́ ẹ̀gbẹ́ẹ yín."'), 'Isa$32:13': (None, 'eniyan', 'Isa$32:13 "àti fún ilẹ̀ àwọn ènìyàn mi ilẹ̀ tí ó ti kún fún ẹ̀gún àti ẹ̀wọ̀n— bẹ́ẹ̀ ni, kẹ́dùn fún gbogbo ilé ìtura àti fún ìlú àríyá yìí."'), 'Isa$32:18': ('bọ', None, 'Isa$32:18 "Àwọn ènìyàn mi yóò máa gbé ní àlàáfíà ní ibùgbé àlàáfíà, ní àwọn ilé ààbò, ní àwọn ibi ìsinmi tí kò ti sí ìdíwọ́."'), 'Isa$33:7': (None, None, 'Isa$33:7 "Wò ó, àwọn onígboyà ọkùnrin wọn pohùnréré ẹkún ní òpópónà; àwọn ikọ̀ àlàáfíà ṣunkún kíkorò."'), 'Isa$33:10': (None, 'dide', 'Isa$33:10 "“Ní ìsinsìn yìí ni èmi yóò dìde,” ni OLÚWA wí. “Ní ìsinsìn yìí ni a ó gbé mi ga, ní ìsinsìn yìí ni a ó gbé mi sókè."'), 'Isa$33:12': ('jó', 'ge', 'Isa$33:12 A ó jó àwọn ènìyàn run bí eérú; bí igbó ẹ̀gún tí a gé la ó dáná sí wọn.”'), 'Isa$33:19': ('wo', None, 'Isa$33:19 "Ìwọ kì yóò rí àwọn agbéraga ènìyàn wọ̀nyẹn mọ́, àwọn ènìyàn tí èdè wọn farasin, pẹ̀lú ahọ́n tí ó ṣàjèjì tí kò sì yéni. "'), 'Isa$33:21': ('lọ', 'kọjá', 'Isa$33:21 "Níbẹ̀ ni OLÚWA yóò ti jẹ́ Alágbára kan fún wa. Yóò sì dàbí ibi àwọn odò ńlá ńlá àti odò kéékèèkéé. Kì yóò sí ọkọ̀ ojú omi pẹ̀lú tí yóò kọjá lóríi wọn, ọkọ̀ ojú omi ńlá ni a kì yóò tù lórí wọn."'), 'Isa$34:1': (None, 'ayé', 'Isa$34:1 " Súnmọ́ tòsí, ẹ̀yin orílẹ̀-èdè láti gbọ́, tẹ́tísílẹ̀ ẹyín ènìyàn jẹ́ kí ayé gbọ́, àti ẹ̀kún rẹ̀, ayé àti ohun gbogbo tí ó ti inú un rẹ̀ jáde."'), 'Isa$34:6': ('ọra', 'sanra', 'Isa$34:6 "Idà OLÚWA kún fún ẹ̀jẹ̀ a mú un ṣanra fún ọ̀rá, àti fún ẹ̀jẹ̀ ọ̀dọ́-agùtàn àti ewurẹ, fún ọ̀rá iwe àgbò— nítorí OLÚWA ni ìrúbọ kan ní Bósírà, àti ìpakúpa ńlá kan ní ilẹ̀ Édómù."'), 'Isa$34:7': ('ọra', None, 'Isa$34:7 "Àti àwọn àgbáǹréré yóò ba wọn ṣọ̀kalẹ̀ wá, àti àwọn ẹgbọ̀rọ̀ màlúù pẹ̀lú àwọn akọ màlúù, ilé wọn ni a ó fi ẹ̀jẹ̀ rin, a ó si fi ọ̀rá sọ ekuru wọn di Ọlọ́ràá. "'), 'Isa$34:9': (None, 'di', 'Isa$34:9 " Odò rẹ̀ ni a ó sì sọ di ọ̀dà, àti eruku rẹ̀ di imí-ọjọ́, ilẹ̀ rẹ̀ yóò sì di ọ̀dà tí ń jóná."'), 'Isa$34:10': (None, 'kọjá', 'Isa$34:10 "A ki yóò paá ní òru tàbí ní ọ̀sán, èéfín rẹ̀ yóò gòkè láéláé: yóò dahoro láti ìran dé ìran, kò sí ẹnìkan tí yóò là á kọjá láé àti láéláé."'), 'Isa$35:3': (None, None, 'Isa$35:3 " Fún ọwọ́ àìlera lókun, mú orúnkún tí ń yẹ̀ lókun:"'), 'Isa$35:7': ('ilẹ', 'ilẹ', 'Isa$35:7 Yanrìn tí ń jóná yóò di adágún ilẹ̀ tí ń pòǹgbẹ yóò di orísun omi. Òpópó ibi tí àwọn ajáko sùn tẹ́lẹ̀rí koríko àti koríko odò àti ewéko mìíràn yóò hù níbẹ̀.'), 'Isa$35:9': ('ri', None, 'Isa$35:9 "Kì yóò sí kìnnìún níbẹ̀, tàbí kí ẹranko búburú kí ó dìde lóríi rẹ̀; a kì yóò rí wọn níbẹ̀. Ṣùgbọ́n àwọn ẹni ìràpadà nìkan ni yóò rìn níbẹ̀,"'), 'Isa$36:9': (None, None, 'Isa$36:9 "Báwo ni ẹ ṣe lè lé ẹyọ kanṣoṣo padà nínú èyí tí ó kéré jù nínú àwọn ìjòyè ọ̀gá mi, bí ó tilẹ̀ jẹ́ pé ẹ gbẹ́kẹ̀lé Éjíbítì fún kẹ̀kẹ́ ẹṣin àti àwọn ẹlẹ́ṣin?"'), 'Isa$36:13': ('ede', 'awọn ọrọ', 'Isa$36:13 "Lẹ́yìn náà ni ọ̀gágun náà dìde tí ó sì ké sítà ní èdè Hébérù pé, “Ẹ gbọ́ àwọn ọ̀rọ̀ ọba ńlá, ọba Ásíríà!"'), 'Isa$36:16': ('jade', None, 'Isa$36:16 "“Ẹ má ṣe tẹ́tí sí Heṣekáyà. Ohun tí ọba Áṣíríà wí nìyìí: Ẹ ṣètò àlàáfíà pẹ̀lúù mi kí ẹ sì jáde tọ̀ mí wá. Lẹ́yìn náà ẹnìkọ̀ọ̀kan yín yóò sì jẹ nínú àjàrà àti igi ọ̀pọ̀tọ́ rẹ̀ bẹ́ẹ̀ ni yóò sì mumi nínú kàǹga rẹ̀,"'), 'Isa$36:20': (None, 'ni', 'Isa$36:20 Èwo nínú àwọn òrìṣà orílẹ̀ èdè wọ̀nyí ló ha ti dáàbò bo ilẹ̀ẹ rẹ̀ kúrò lọ́wọ́ mi? Báwo ni OLÚWA ṣe wá le gba Jérúsálẹ́mù kúrò lọ́wọ́ mi?”'), 'Isa$37:1': ('lọ', None, 'Isa$37:1 "Nígbà tí ọba Heṣekáyà gbọ́ èyí, ó fa aṣọ rẹ̀ ya, ó sì fi aṣọ ọ̀fọ̀ bo ara rẹ̀, ó sì wọ inú tẹ́ḿpìlì OLÚWA lọ."'), 'Isa$37:7': (None, 'pada', 'Isa$37:7 "Tẹ́tísílẹ̀! Èmi yóò fi ẹ̀mí kan sínú rẹ̀ tó bẹ́ẹ̀ tí ó fi jẹ́ pé bí o bá ti gbọ́ ìròyìn kan, òun yóò padà sí orílẹ̀ èdè rẹ̀, níbẹ̀ ni n ó sì ti jẹ́ kí wọn ké e lulẹ̀ pẹ̀lú idà.’ ”"'), 'Isa$37:11': (None, 'ni', 'Isa$37:11 "Dájúdájú, ìwọ ti gbọ́ ohun tí ọba Ásíríà ti ṣe sí àwọn orílẹ̀ èdè, tí ó pa wọ́n run pátapáta. Ǹjẹ́ a ó ha dá ọ nídè bí?"'), 'Isa$37:12': (None, 'ni', 'Isa$37:12 "Ǹjẹ́ àwọn òrìṣà àwọn orílẹ̀ èdè tí àwọn baba ńlá mi parun ha gbà wọ́n sílẹ̀ bí àwọn òrìṣà Gósénì, Háránì, Résípì àti àwọn ènìyàn Ẹ́dẹ́nì tí wọ́n wà ní ìlú Áṣárì?"'), 'Isa$37:17': ('gbo', 'wo', 'Isa$37:17 "Tẹ́tí sílẹ̀, Ìwọ OLÚWA, kí o gbọ́, ya ojú rẹ, Ìwọ OLÚWA, kí o rí i; tẹ́tí sí gbogbo ọ̀rọ̀ tí Ṣenakérúbù rán láti fi àbùkù kan Ọlọ́run alààyè."'), 'Isa$38:11': (None, None, 'Isa$38:11 "Èmi wí pé, “Èmi kì yóò lè tún rí OLÚWA mọ́, àni OLÚWA, ní ilẹ̀ àwọn alààyè; èmi kì yóò lè síjú wo ọmọnìyàn mọ́, tàbí kí n wà pẹ̀lú àwọn tí ó sì ń gbe orílẹ̀ ayé báyìí."'), 'Isa$38:12': ('ni', 'ṣe', 'Isa$38:12 "Gẹ́gẹ́ bí àgọ́ olùṣọ́-àgùntàn, ilé mi ni a ti wó lulẹ̀ tí a sì gbà kúrò lọ́wọ́ọ̀ mi. Gẹ́gẹ́ bí ahunsọ mo ti ká ayé mi nílẹ̀, bẹ́ẹ̀ ni òun sì ti ké mi kúrò lára àṣà; ọ̀ṣán àti òru ni ìwọ ṣe òpin mi."'), 'Isa$38:16': ('ni', 'gbe', 'Isa$38:16 "OLÚWA, nípa nǹkan báwọ̀nyí ni àwọn ènìyàn ń gbé; àti pé ẹ̀mí mi rí iyè nínú un wọn pẹ̀lú. Ìwọ dá ìlera mi padà kí o sì jẹ́ kí n wà láàyè"'), 'Isa$38:21': (None, 'gba', 'Isa$38:21 "Àìṣáyà ti sọ pé, “Pèṣè ìsù (ohun gbígbóná tí a dì mọ́ ojú egbò) kí o sì fi sí ojú oówo náà, òun yóò sì gbádùn.”"'), 'Isa$38:22': (None, None, 'Isa$38:22 "Heṣekáyà ti sọ pé, “Kí ni yóò jẹ́ àmì pé èmi yóò gòkè lọ sí tẹ́ḿpìlì OLÚWA?”"'), 'Isa$40:14': ('ọna', 'ona', 'Isa$40:14 Ta ni OLÚWA ké sí kí ó là á lọ́yẹ àti ta ni ó kọ́ òun ní ọ̀nà tí ó tọ́? Ta ni ẹni náà tí ó kọ́ ọ ní ọgbọ́n tàbí tí ó fi ipa ọ̀nà òye hàn án?'), 'Isa$40:25': (None, 'dogba', 'Isa$40:25 “Ta ni ẹ ó fi mi wé? Tàbí ta ni ó bámi dọ́gba?” ni Ẹni-Mímọ́ wí.'), 'Isa$40:30': ('èwe', 'odo', 'Isa$40:30 "Àní àwọn ọ̀dọ́ ń rẹ̀wẹ̀sì, ó ń rẹ̀ wọ́n, àwọn ọ̀dọ́mọkùnrin sì ń kọsẹ̀ wọ́n ṣubú;"'), 'Isa$41:9': ('ya', 'ni', 'Isa$41:9 "mo mú ọ láti ìpẹ̀kun ilẹ̀ ayé, láti kọ̀rọ̀ rẹ̀ tí ó jìnnà jùlọ ni mo ti pè ọ́. Èmi wí pé, ‘Ìwọ ni ìránṣẹ́ mi;’ Èmi ti yàn ọ́ bẹ́ẹ̀ ni èmi kò sì tí ì kọ̀ ọ́ sílẹ̀."'), 'Isa$41:15': (None, None, 'Isa$41:15 "“Kíyèsi, Èmi yóò sọ ọ́ di òòlù ìpakà, tuntun tí ó mú ti eyín rẹ̀ mu, ìwọ yóò lu àwọn òkè ńlá, ìwọ yóò fọ́ wọn túútúú, a ó sì sọ òkè kékeré di ìyàngbò."'), 'Isa$41:19': (None, None, 'Isa$41:19 "Èmi yóò fi sínú aṣálẹ̀ igi kédárì àti akaṣíà, mítílì àti ólífì. Èmi yóò da páínì sí inú ilẹ̀ síṣá, igi fíri àti ṣípírẹ́ṣì papọ̀"'), 'Isa$41:21': (None, None, 'Isa$41:21 "“Mú ẹjọ́ wá,” ni OLÚWA wí. “Tẹ́ àwọn àwíjàre rẹ sílẹ̀,” ni ọba Jákọ́bù wí"'), 'Isa$41:23': ('wá', 'ṣe', 'Isa$41:23 "ẹ sọ fún wa ohun ti ọjọ́ iwájú mú dání kí àwa kí ó lè mọ̀ pé Ọlọ́run niyín. Ẹ ṣe nǹkankan, ìbáà ṣe rere tàbí búburú, tó bẹ́ẹ̀ tí àyà yóò fi fò wá tí ẹ̀rù yóò sì fi kún inú wa."'), 'Isa$42:5': ('fun', None, 'Isa$42:5 " Èyí ni ohun tí OLÚWA Ọlọ́run wí Ẹni tí ó dá àwọn ọ̀run tí ó sì tẹ́ wọ́n ṣóde, tí ó tẹ́ ilẹ̀ ayé àti ohun gbogbo tí ó jáde nínú un wọn, Ẹni tí ó fún àwọn ènìyàn rẹ̀ ní èémí àti ẹ̀mí fún gbogbo àwọn tí ń rìn nínú un rẹ̀:"'), 'Isa$42:9': (None, 'wá', 'Isa$42:9 "Kíyèsí i, àwọn nǹkan àtijọ́ ti wáyé, àti àwọn nǹkan tuntun ni mo ti wí pé; kí wọn tó hù jáde mo ti kéde rẹ̀ fún ọ.”"'), 'Isa$42:16': ('ni', 'ṣe', 'Isa$42:16 "Èmi yóò tọ àwọn afọ́jú ní ọ̀nà tí wọn kò tí ì mọ̀, ní ipa-ọ̀nà tí ó ṣàjèjì sí wọn ni èmi yóò tọ́ wọn lọ; Èmi yóò sọ òkùnkùn di ìmọ́lẹ̀ níwájúu wọn àti ibi pálapàla ni èmi ó sọ di kíkúnná. Àwọn nǹkan tí máa ṣe nìyìí; Èmi kì yóò kọ̀ wọ́n sílẹ̀."'), 'Isa$42:18': ('wo', 'wo', 'Isa$42:18 "“Gbọ́, ìwọ adití, wòó, ìwọ afọ́jú, o sì rí!"'), 'Isa$43:2': ('ina', 'ina', 'Isa$43:2 "Nígbà tí ìwọ bá ń la omi kọjá, Èmi yóò wà pẹ̀lúu rẹ; àti nígbà tí ìwọ bá ń la odò kọjá wọn kì yóò bò ọ́ mọ́lẹ̀. Nígbà tí ìwọ bá la iná kọjá, kò ní jó ọ; ahọ́n iná kò ní jó ọ lára."'), 'Isa$43:4': ('ni', 'fun', 'Isa$43:4 "Nítorí pé o ṣe iyebíye àti ọ̀wọ́n níwájú mi, àti nítorí pé mo fẹ́ràn rẹ, Èmi yóò fi ènìyàn rọ́pò fún ọ, àti ènìyàn dípò ẹ̀míì rẹ."'), 'Isa$43:5': (None, 'ni', 'Isa$43:5 Má bẹ̀rù nítorí èmi wà pẹ̀lúù rẹ; Èmi yóò mú àwọn ọmọ rẹ láti ìlà oòrùn wá èmi ó sì kó ọ jọ láti ìwọ̀ oòrùn.'), 'Isa$43:16': ('ọna', 'ona', 'Isa$43:16 "Èyí ni ohun tí OLÚWA wí Ẹni náà tí ó la ọ̀nà nínú òkun, ipa-ọ̀nà láàrin alagbalúgbú omi,"'), 'Isa$43:18': ('atijọ', None, 'Isa$43:18 “Gbàgbé àwọn ohun àtẹ̀yìnwá; má ṣe gbé nínú ohun àtijọ́.'), 'Isa$43:28': (None, 'ni', 'Isa$43:28 "Nítorí náà, èmi yóò dójú ti àwọn ẹni àyẹ́sí inú tẹ́ḿpìlìi yín, bẹ́ẹ̀ ni èmi yóò mú ìparun bá Jákọ́bù àti ìtìjú bá Ísírẹ́lì."'), 'Isa$44:1': ('ṣe', 'ni', 'Isa$44:1 "“Ṣùgbọ́n tẹ́tísílẹ̀ nísinsìn yìí, Ìwọ Jákọ́bù, Ìránṣẹ́ mi Ísírẹ́lì, ẹni tí mo ti yàn."'), 'Isa$44:9': ('wo', None, 'Isa$44:9 "Gbogbo àwọn tí ń gbẹ́ ère jásí asán, àti àwọn ohun tí wọn ń kó pamọ́ kò jámọ́ nǹkankan. Àwọn tí yóò sọ̀rọ̀ fún wọn fọ́ lójú; wọ́n jẹ́ aláìmọ́kan sí ìtìjú ara wọn."'), 'Isa$44:16': ('ri', None, 'Isa$44:16 "Ìlàjì igi náà ni ó jó nínú iná; lóríi rẹ̀ ni ó ti ń tọ́jú oúnjẹ rẹ̀, ó dín ẹran rẹ̀ ó sì jẹ àjẹyó. Ó tún mú ara rẹ̀ gbóná ó sì sọ pé, “Á à! Ara mi gbóná, mo rí iná.”"'), 'Isa$44:18': ('wo', None, 'Isa$44:18 "Wọn kò mọ nǹkankan, nǹkankan kò yé wọn; a fi ìbòjú bo ojú wọn, wọn kò lè rí nǹkankan; bẹ́ẹ̀ ni àyà wọn sébọ́, wọn kò lè mọ nǹkankan."'), 'Isa$44:19': ('ṣe', 'ni', 'Isa$44:19 "Kò sí ẹni tí ó dúró láti ronú, kò sí ẹni tí ó ní ìmọ̀ tàbí òye láti sọ wí pé, “Ìlàjì rẹ̀ ni mo fi dáná; Mo tilẹ̀ ṣe àkàrà lórí èédú rẹ̀, Mo dín ẹran, mo sì jẹ ẹ́. Ǹjẹ́ ó wá yẹ kí n ṣe ohun ìríra kan nínú èyí tí ó ṣẹ́kù bí? Ǹjẹ́ èmi yóò ha foríbalẹ̀ fún ìtì igi?”"'), 'Isa$45:1': ('yo', 'fun', 'Isa$45:1 "Èyí ni ohun tí OLÚWA sọ fún ẹni òróró rẹ̀, sí Kírúsì, ọwọ́ ọ̀tún ẹni tí mo dì mú láti dojú àwọn orílẹ̀ èdè bolẹ̀ níwájú rẹ̀ àti láti gba ohun ìjà àwọn ọba lọ́wọ́ wọn, láti sí àwọn ìlẹ̀kùn níwájú rẹ̀ tí ó fi jẹ́ pé a kì yóò ti àwọn ẹnu-ọ̀nà."'), 'Isa$45:7': ('ṣe', None, 'Isa$45:7 "Mo dá ìmọ́lẹ̀, mo sì dá òkùnkùn mo mú àlàáfíà wá, mo sì dá àjálù; Èmi OLÚWA ni ó ṣe nǹkan wọ̀nyí. "'), 'Isa$45:8': ('jẹ ki', 'ni', 'Isa$45:8 "“Ìwọ ọ̀run lókè rọ òjò òdodo sílẹ̀; jẹ́ kí àwọ̀sánmọ̀ kí ó rọ̀ ọ́ sílẹ̀. Jẹ́ kí ilẹ̀ kí ó yanu gbàgàdà, jẹ́ kí ìgbàlà kí ó dìde sókè, jẹ́ kí òdodo kí ó dàgbà pẹ̀lú rẹ̀; Èmi OLÚWA ni ó ti dá a. "'), 'Isa$45:12': (None, 'ni', 'Isa$45:12 Èmi ni ẹni tí ó dá ilẹ̀ ayé tí ó sì da ọmọnìyàn sóríi rẹ̀. Ọwọ́ mi ni ó ti ta àwọn ọ̀run mo sì kó àwọn agbájọ ìràwọ̀ rẹ̀ síta'), 'Isa$45:13': (None, 'ni', 'Isa$45:13 "Èmi yóò gbé Kírúsì ṣókè nínú òdodo mi: Èmi yóò mú kí gbogbo ọ̀nà rẹ̀ kí ó tọ́. Òun yóò tún ìlú mi kọ́ yóò sì tú àwọn àtìpó mi sílẹ̀, ṣùgbọ́n kì í ṣe fún owó tàbí ẹ̀bùn kan, ni OLÚWA àwọn ọmọ-ogun wí.”"'), 'Isa$45:14': ('wá', None, 'Isa$45:14 " Ohun tí OLÚWA wí nìyìí: “Àwọn èròjà ilẹ̀ Éjíbítì àti àwọn ọjà ilẹ̀ Kúṣì, àti àwọn Ṣábíáṣì— wọn yóò wá sọ́dọ̀ rẹ wọn yóò sì jẹ́ tìrẹ; wọn yóò máa wọ́ tẹ̀lé ọ lẹ́yìn, wọn yóò máa wá lọ́wọ̀ọ̀wọ́. Wọn yóò máa foríbalẹ̀ níwájú rẹ̀, wọn yóò sì máa bẹ̀bẹ̀ níwájú rẹ pé, ‘Nítòótọ́ Ọlọ́run wà pẹ̀lú rẹ kò sì sí ẹlòmìíràn; kò sí ọlọ́run mìíràn.’ ” "'), 'Isa$45:21': ('gba', None, 'Isa$45:21 "J ⓓ ẹ́ kí a mọ ohun tí yóò ṣẹlẹ̀, fihàn wá jẹ́ kí wọn jọ dámọ̀ràn papọ̀. Ta ló ti sọ èyí lọ́jọ́ tí o ti pẹ́, ta ló ti wí èyí láti àtètèkọ́ṣe? Kì í ha á ṣe Èmi, OLÚWA? Àti pé kò sí Ọlọ́run mìíràn lẹ́yìn mi, Ọlọ́run olódodo àti olùgbàlà; kò sí ẹlòmìíràn àfi èmi. "'), 'Isa$46:2': (None, None, 'Isa$46:2 "Wọ́n bẹ̀rẹ̀ wọ́n sì foríbalẹ̀ papọ̀; wọn kò lè gba ẹrù náà, àwọn pẹ̀lú ni a kó lọ ní ìgbèkùn. "'), 'Isa$46:3': (None, None, 'Isa$46:3 "“Tẹ́tí sími ìwọ ilé Jákọ́bù, Gbogbo ẹ̀yin tí ó ṣẹ́kù nínú ilé Ísírẹ́lì, Ìwọ tí mo ti gbérò láti ìgbà tí o ti wà nínú oyún, tí mo sì ti ń pọ̀n láti ìgbà tí a ti bí ọ."'), 'Isa$46:5': (None, None, 'Isa$46:5 “Ta ni ìwọ yóò fi mí wé tàbí ta ni èmi yóò bá dọ́gba? Ta ni ìwọ yóò fi ṣe àkàwé mi tí àwa yóò jọ farawéra?'), 'Isa$46:7': (None, None, 'Isa$46:7 "Wọ́n gbé e lé èjìká wọn, wọ́n rù wọ́n, wọ́n sì gbé e sí àye rẹ̀ níbẹ̀ ni ó sì dúró sí. Láti ibẹ̀ náà kò le è paradà Bí ènìyàn tilẹ̀ pariwo lé e lórí, òun kò le è dáhùn; òun kò lè gbà á nínú ìyọnu rẹ̀. "'), 'Isa$46:9': ('atijọ', None, 'Isa$46:9 "Rántí àwọn nǹkan àtẹ̀yìnwá, àwọn ti àtijọ́tijọ́; Èmi ni Ọlọ́run, kò sì sí ẹlọ̀mìíràn; Èmi ni Ọlọ́run, kò sí ẹlòmìíràn bí ì mi."'), 'Isa$47:3': (None, None, 'Isa$47:3 Ìhòòhò rẹ ni a ó fihàn síta àti ìtìjú rẹ ni a ó sí sílẹ̀. Èmi yóò sì gba ẹ̀ṣan; Èmi kì yóò sì dá ẹnìkan sí.”'), 'Isa$47:6': (None, 'ọwọ', 'Isa$47:6 "Inú bí mi sí àwọn ènìyàn mi tí mo sì ba ogún mi jẹ́; Mo fi wọ́n lé ọ lọ́wọ́, Ìwọ kò sì síjú àánú wò wọ́n. Lórí àwọn arúgbó pẹ̀lú ní o gbé àjàgà tí ó wúwo lé."'), 'Isa$47:11': ('wá', 'ṣubu', 'Isa$47:11 Ìparun yóò dé bá ọ bẹ́ẹ̀ ni ìwọ kì yóò mọ ọ̀nà láti ré e kúrò. Àjálù kan yóò ṣubú lù ọ́ tí o kì yóò le è fi ètùtù ré kúrò; òfò kan tí o kò le ròtì ni yóò wá lójijì sí oríì rẹ.'), 'Isa$47:14': ('ina', 'ina', 'Isa$47:14 Lóòótọ́ wọ́n dàbí pòpó; iná ni yóò jó wọn dànù. Wọn kò kúkú lè gba ara wọn là lọ́wọ́ agbára iná náà. Kò sí èédú láti mú ara ẹnikẹ́ni gbóná níhìnín kò sí iná tí ènìyàn le jókòó tì.'), 'Isa$48:1': ('ṣe', 'orukọ', 'Isa$48:1 "“Tẹ́tí sí èyí, Ìwọ ilée Jákọ́bù, ìwọ tí a ń pè pẹ̀lú orúkọ Ísírẹ́lì tí o sì wá láti ẹ̀ka Júdà, ìwọ tí ò ń búra ní orúkọ OLÚWA tí o sì ń pe Ọlọ́run Ísírẹ́lì ṣùgbọ́n kì í ṣe ní òtítọ́ àti òdodo"'), 'Isa$48:3': ('lọ', None, 'Isa$48:3 "Èmi sọ àṣọtẹ́lẹ̀ àwọn nǹkan ti tẹ́lẹ̀ lọ́jọ́ tó ti pẹ́, ẹnu mi ló ti kéde wọn, mo sì sọ wọ́n di mímọ̀; Lẹ́yìn náà lójijì mo gbé ìgbéṣẹ̀, wọ́n sì wá sí ìmúṣẹ."'), 'Isa$48:15': ('ni', 'ṣe', 'Isa$48:15 "Èmi, àní Èmi ló ti sọ̀rọ̀; bẹ́ẹ̀ ni, mo ti pè é. Èmi yóò mú un wá, òun yóò sì ṣe àṣeyọrí nínú ìrìnàjò rẹ̀."'), 'Isa$49:1': (None, 'orukọ', 'Isa$49:1 " Tẹ́tí sí mi, ẹ̀yin erékùṣù: gbọ́ èyí, ẹ̀yin orílẹ̀ èdè jínjìn-nà réré: kí a tó bí mi OLÚWA ti pè mí; láti ìgbà bíbí mi ni ó ti dá orúkọ mi."'), 'Isa$49:8': ('fun', None, 'Isa$49:8 " Ohun tí OLÚWA wí nìyìí: “Ní àkókò ojú rere mi, èmi yóò dá ọ lóhùn, àti ní ọjọ́ ìgbàlà, èmi yóò ràn ọ́ lọ́wọ́; Èmi yóò pa ọ́ mọ́, n ó sì ṣe ọ́ láti jẹ́ májẹ̀mú fún àwọn ènìyàn, láti mú ilẹ̀ padà bọ̀ sípò àti láti ṣe àtúnpín ogún rẹ̀ tí ó ti dahoro,"'), 'Isa$49:10': (None, None, 'Isa$49:10 " Ebi kì yóò pa wọ́n bẹ́ẹ̀ ni òrùngbẹ kì yóò gbẹ wọ́n, tàbí kí ooru inú aṣálẹ̀ tàbí òòrùn kí ó pa wọ́n. Ẹni tí ó ṣàánú fún wọn ni yóò máa tọ́ wọn, tí yóò sì mú wọn lọ sí ibi oríṣun omi."'), 'Isa$49:20': ('ni', 'fun', 'Isa$49:20 "Àwọn ọmọ tí a bí ní àkókò ọ̀fọ̀ rẹ yóò sọ ní etígbọ̀ọ́ rẹ, ‘Ibi yìí ti kéré jù fún wa; ẹ fún wa láyè sí i tí a ó máa gbé.’"'), 'Isa$49:25': (None, None, 'Isa$49:25 "Ṣùgbọ́n ohun tí OLÚWA wí níyìí: “Bẹ́ẹ̀ ni, a ó gba àwọn ìgbékùn lọ́wọ́ àwọn jagunjagun, àti ìkógun lọ́wọ́ àwọn akíkanjú; Ẹni tí ó bá ọ jà ni èmi ó bá jà, àti àwọn ọmọ rẹ ni èmi ó gbàlà."'), 'Isa$50:4': (None, 'gbo', 'Isa$50:4 "OLÚWA àwọn ọmọ-ogun ti fún mi ni ahọ́n tí a fi iṣẹ́ rán, láti mọ àwọn ọ̀rọ̀ tí ó lè gbé àwọn aláàárẹ̀ ró. O jí mi láràárọ̀, o jí etí mi láti gbọ́ gẹ́gẹ́ bí ẹni tí à ń kọ́."'), 'Isa$50:6': ('ẹrẹkẹ', 'oju', 'Isa$50:6 "Mo sí ẹ̀yìn mi sílẹ̀ fún àwọn tí ó ń nà mí, àti ẹ̀rẹ̀kẹ́ mi fún àwọn tí ń fa irúngbọ̀n mi; Èmi kò fi ojú mi pamọ́ kúrò lọ́wọ́ ẹlẹ́yà àti ìyọsùtì sí."'), 'Isa$50:11': ('ina', 'ina', 'Isa$50:11 "Ṣùgbọ́n ní àkókò yìí, gbogbo ẹ̀yin ti ń tanná tí ẹ sì ń pèṣè iná ìléwọ́ fún ara yín, ẹ lọ, kí ẹ sì máa rìn nínú ìmọ́lẹ̀ iná yín àti ti ìtùfú tí ẹ ti kánná lù. Ohun tí ẹ ó gbà lọ́wọ́ mi nìyìí: Ẹ̀yin ó dùbúlẹ̀ nínú ìpọ́njú."'), 'Isa$51:1': ('ni', None, 'Isa$51:1 "“Tẹ́tí sí mi, ẹ̀yin tí ó ń lépa òdodo àti ẹ̀yin tí ń wá OLÚWA: Wo àpáta, nínú èyí tí a ti gé yín jáde àti ihò òkúta níbi tí a ti gbẹ́ yín jáde;"'), 'Isa$51:4': (None, None, 'Isa$51:4 "“Tẹ́tí sími, ẹ̀yin ènìyàn mi; gbọ́ tèmi, ẹ̀yin orílẹ̀ èdè mi: Òfin yóò ti ọ̀dọ̀ mi jáde wá; ìdájọ́ mi yóò di ìmọ́lẹ̀ sí àwọn orílẹ̀ èdè."'), 'Isa$51:12': (None, None, 'Isa$51:12 "“Èmi, àní Èmi, èmi ni ẹni tí ó tù ọ́ nínú. Ta ni ọ́ tí o fi ń bẹ̀rù ènìyàn ẹlẹ́ran-ara, àti ọmọ ènìyàn, tí ó jẹ́ koríko lásán,"'), 'Isa$51:14': (None, 'kú', 'Isa$51:14 "Àwọn ẹlẹ́wọ̀n tí ń ṣojo la ó dá sílẹ̀ ní àìpẹ́ jọjọ wọn kò ní kú sínú túbú wọn, bẹ́ẹ̀ ni wọn kò ní ṣe aláìní àkàrà."'), 'Isa$51:21': (None, 'ni', 'Isa$51:21 "Nítorí náà ẹ gbọ́ èyí, ẹ̀yin tí a ti ṣẹ́ níṣẹ̀ẹ́, tí ọtí ń pa, ṣùgbọ́n kì í ṣe fún wáìnì"'), 'Isa$52:5': ('ya', 'ni', 'Isa$52:5 " “Àti ní àkókò yìí, kí ni mo ní níbí?” ni OLÚWA wí. “Nítorí a ti kó àwọn ènìyàn mi lọ lọ́fẹ̀ẹ́, àwọn tí ó sì ń jọba lé wọn fi wọ́n ṣẹlẹ́yà,” ni OLÚWA wí. “Àti ní ọjọọjọ́ orúkọ mi ni a ṣọ̀rọ̀ òdì sí nígbà gbogbo."'), 'Isa$52:11': ('lọ', 'lọ', 'Isa$52:11 " Ẹ túká, ẹ túká, ẹ jáde kúrò níhìnínyìí! Ẹ má fọwọ́ kan ohun àìmọ́ kan! Ẹ jáde kúrò nínú rẹ̀ kí ẹ sì di mímọ́, ẹ̀yin tí ẹ̀ ń gbé ohun èlò OLÚWA."'), 'Isa$52:14': (None, 'wo', 'Isa$52:14 Gẹ́gẹ́ bí a ti rí ọ̀pọ̀lọpọ̀ tí wọ́n ń bọlá fún un— ìwò ojú rẹ ni a ti bà jẹ́ kọjá ti ẹnìkẹ́ni àti ìrísí rẹ̀ ní a ti bàjẹ́ kọjá ohun tí ènìyàn ń fẹ́'), 'Isa$53:3': (None, 'eniyan', 'Isa$53:3 "A kẹ́gàn rẹ̀ àwọn ènìyàn sì kọ̀ ọ́ sílẹ̀, ẹni ìbànújẹ́, tí ó sì mọ bí ìpọ́njú ti rí. Gẹ́gẹ́ bí ẹnìkan tí àwọn ènìyàn ń fojú pamọ́ fún a kẹ́gàn rẹ, a kò sì bọlá fún un rárá. "'), 'Isa$53:6': ('ni', None, 'Isa$53:6 "Gbogbo wa bí àgùntàn, ti sìnà lọ, ẹnìkọ̀ọ̀kan wa ti yà sí ọ̀nà ara rẹ̀; OLÚWA sì ti gbé e ka orí ara rẹ̀ gbogbo àìṣedédé wa. "'), 'Isa$55:4': (None, 'ni', 'Isa$55:4 "Kíyèsí i, mo ti fi òun ṣe ẹlẹ́rìí fún àwọn ènìyàn, olórí àti apàṣẹ fún àwọn ènìyàn."'), 'Isa$55:6': (None, 'ni', 'Isa$55:6 Ẹ wá OLÚWA nígbà tí ẹ lè rí i; ẹ pè é nígbà tí ó wà nítòsí.'), 'Isa$55:10': ('fun', 'pada', 'Isa$55:10 " Gẹ́gẹ́ bí òjò àti sílóò ti wálẹ̀ láti ọ̀run tí kì í sì padà sí ibẹ̀ láì bomirin ilẹ̀ kí ó sì mú kí ó tanná kí ó sì rudi, tó bẹ́ẹ̀ tí yóò fi mú irúgbìn fún afúnrúgbìn àti àkàrà fún ọ̀jẹun,"'), 'Isa$55:12': ('lọ', None, 'Isa$55:12 Ẹ̀yin yóò jáde lọ ní ayọ̀ a ó sì daríi yín lọ ní àlàáfíà; òkè ńlá ńlá àti kéékèèkéé yóò bú sí orin níwájúu yín àti gbogbo igi inú un pápá yóò máa pàtẹ́wọ́.'), 'Isa$56:1': (None, 'ṣe', 'Isa$56:1 "Ohun tí OLÚWA wí nìyìí: “Ẹ pa ìdájọ́ mọ́ ẹ sì ṣe ohun tí ó tọ̀nà, nítorí ìgbàlà mi súnmọ́ tòsí àti òdodo mi ni a ó fi hàn láìpẹ́ jọjọ."'), 'Isa$56:4': (None, None, 'Isa$56:4 "Nítorí pé ohun tí OLÚWA wí nìyìí: “Sí àwọn ìwẹ̀fà yìí tí ó pa ọjọ́ ìsinmi mi mọ́, tí wọ́n yan ohun tí ó dùn mọ́ mi tí wọ́n sì di májẹ̀mú mi mú ṣinṣin"'), 'Isa$56:6': (None, None, 'Isa$56:6 "Àti àwọn àjèjì tí ó ṣo ara wọn mọ́ OLÚWA láti sìn ín, láti fẹ́ orúkọ OLÚWA àti láti foríbalẹ̀ fún un gbogbo àwọn tí ń pa ọjọ́ ìsinmi mọ́ láì bà á jẹ́ àti tí wọ́n sì di májẹ̀mú mi mú ṣinṣin—"'), 'Isa$56:11': ('ni', 'tirẹ', 'Isa$56:11 "Wọ́n jẹ́ àwọn ajá tí ó kúndùn púpọ̀; wọn kì í ní ànító. Wọ́n jẹ́ olùṣọ́ àgùntàn tí kò ní òye; olúkúlùkù ń yà sọ́nà ara rẹ̀, ẹnìkọ̀ọ̀kan wọn sì wá ère tirẹ̀."'), 'Isa$57:1': (None, None, 'Isa$57:1 "Olódodo ṣègbé kò sí ẹnìkan tí ó rò ó lọ́kàn ara rẹ̀; a mú àwọn ẹni mímọ́ lọ, kò sì sí ẹni tó yé pé a ti mú àwọn olódodo lọ láti yọ wọ́n kúrò nínú ibi."'), 'Isa$57:6': ('dà', None, 'Isa$57:6 "Àwọn ère tí ó wà ní àárin òkúta dídán wọ̀n n nì, nínú kòtò jínjìn ni ìpín in yín; àwọn, àwọ̀n ni ìpín in yín. Bẹ́ẹ̀ ni, sí wọn ni ẹ ti ta ọrẹ ohun mímu yín sílẹ̀ àti láti ta ọrẹ oníhóró. Nítorí àwọn nǹkan wọ̀nyí, ǹjẹ́ ó yẹ kí n dáwọ́ dúró?"'), 'Isa$57:13': ('gba', None, 'Isa$57:13 "Nígbà tí ẹ bá kígbe fún ìrànlọ́wọ́ ẹ jẹ́ kí àkójọ àwọn ère yín gbà yín! Atẹ́gùn yóò gbá gbogbo wọn lọ, èémí lásán làsàn ni yóò gbá wọn lọ. Ṣùgbọ́n ẹni tí ó bá fi mí ṣe ààbò rẹ̀ ni yóò jogún ilẹ̀ náà yóò sì jogún òkè mímọ́ mi.”"'), 'Isa$57:15': (None, 'ẹmi', 'Isa$57:15 " Nítorí èyí ni ohun tí Ẹni gíga àti ọlọ́lá jùlọ wí ẹni tí ó wà títí láé, tí orúkọ rẹ̀ jẹ́ mímọ́: “Mo ń gbé ní ibi gíga àti ibi mímọ́, ṣùgbọ́n pẹ̀lú ẹni n nì tí ó ní ìròbìnújẹ́ àti ẹ̀mí ìrẹ̀lẹ̀, láti sọ ẹ̀mí onírẹ̀lẹ̀ náà jí àti láti sọ ẹ̀mí oníròbìnújẹ́ n nì jí."'), 'Isa$57:16': (None, 'ni', 'Isa$57:16 "Èmi kì yóò fẹ̀ṣùn kan ni títí láé, tàbí kí n máa bínú ṣá á, nítorí nígbà náà ni ọkàn ọmọnìyàn yóò rẹ̀wẹ̀sì níwájú mi èémí ọmọnìyàn tí mo ti dá."'), 'Isa$58:2': (None, 'beere', 'Isa$58:2 "Nítorí ọjọ́ dé ọjọ́ ni wọ́n ń wá mi kiri; wọ́n ṣe bí ẹni ní ìtara láti mọ ọ̀nà mi, àfi bí ẹni pé wọ́n jẹ́ orílẹ̀ èdè kan tí ń ṣe ohun tí ó tọ̀nà tí òun kò sì tí ì kọ àṣẹ Ọlọ́run rẹ̀. Wọ́n ń béèrè lọ́wọ́ mi fún ìpinnu nìkan wọ́n sì ṣe bí ẹni ń tara fún Ọlọ́run láti súnmọ́ ọ̀dọ̀ wọn."'), 'Isa$58:8': ('lọ', None, 'Isa$58:8 "Nígbà náà ni ìmọ́lẹ̀ rẹ yóò tàn jáde bí òwúrọ̀ àti ìmúláradá rẹ yóò farahàn kíákíá; nígbà náà ni òdodo rẹ yóò sì lọ níwájúù rẹ, ògo OLÚWA yóò sì jẹ́ ààbò lẹ́yìn rẹ."'), 'Isa$58:9': ('pe', 'kigbe', 'Isa$58:9 "Nígbà yìí ni ẹ̀yin yóò pè, tí OLÚWA yóò sì dáhùn; ẹ̀yin yóò kígbe fún ìrànlọ́wọ́, òun yóò sì wí pé: Èmi nìyí. “Bí ẹ̀yin bá mú àjàgà aninilára kúrò, pẹ̀lú ìka àléébù nínà àti ọ̀rọ̀ ìṣáátá,"'), 'Isa$58:13': (None, None, 'Isa$58:13 "“Bí ìwọ bá pa ẹṣẹ̀ rẹ mọ́ kúrò nínú bíba ọjọ́-ìsinmi jẹ́, àti síse bí ó ti wù ọ́ ni ọjọ́ mímọ́ mi, bí ìwọ bá pe ọjọ-ìsinmi ní ohun dídùn àti ọjọ́ mímọ́ OLÚWA ní ohun ọ̀wọ̀ àti bí ìwọ bá bu ọlá fún un láti máa bá ọ̀nà tìrẹ lọ àti láti má ṣe bí ó ti wù ọ́ tàbí kí o máa ṣọ̀rọ̀ òòrayè,"'), 'Isa$59:7': (None, 'ibi', 'Isa$59:7 Ẹsẹ̀ wọn yára bọ́ sínú ẹ̀ṣẹ̀; wọ́n yára láti ta ẹ̀jẹ̀ aláìṣẹ̀ sílẹ̀. Èrò wọn sì jẹ́ èrò ibi; ìparun àti ìdahoro ni ó wà ní ṣíṣe àmì ọ̀nà wọn.'), 'Isa$59:8': (None, 'ni', 'Isa$59:8 "Ọ̀nà àlàáfíà èyí ni wọn kò mọ̀; kò sí òdodo ní ojú ọ̀nà wọn wọ́n ti sọ wọ́n dì ọ̀nà kọ́rọkọ́rọ, kò sí ẹni tí ó tọ ọ̀nà yìí tí yóò rí àlàáfíà. "'), 'Isa$59:9': ('imole', 'imọlẹ', 'Isa$59:9 "Nítorí èyí ni ẹ̀tọ́ fi jìnnà sí wa, àti tí òdodo kò fi tẹ̀wá lọ́wọ́. A ń wá ìmọ́lẹ̀, ṣùgbọ́n gbogbo rẹ jẹ́ òkùnkùn; fún ìmọ́lẹ̀, ṣùgbọ́n à ń rìn nínú òjìji."'), 'Isa$59:17': (None, None, 'Isa$59:17 " Ó gbé òdodo wọ̀ gẹ́gẹ́ bí ìgbàya rẹ̀, àti àsíborí ìgbàlà ní oríi rẹ̀; ó gbé ẹ̀wù ẹ̀san wọ̀ ó sì yí ara rẹ̀ ní ìtara bí ẹ̀wù."'), 'Isa$60:3': ('imole', 'imọlẹ', 'Isa$60:3 "Àwọn orílẹ̀ èdè yóò wá sí ìmọ́lẹ̀ rẹ, àti àwọn ọba sí ìmọ́lẹ̀ òwúrọ̀ rẹ. "'), 'Isa$60:14': (None, 'tẹriba', 'Isa$60:14 " Àwọn ọ̀dọ́mọkùnrin àwọn apọ́nilójú rẹ yóò wá foríbalẹ̀ fún ọ; gbogbo àwọn tí ó ti kẹ́gàn rẹ ni wọn yóò tẹríba níwájú rẹ wọn yóò sì pè ọ́ ní Ìlú OLÚWA, Ṣíhónì ti Ẹni Mímọ́ Ísírẹ́lì. "'), 'Isa$60:19': ('imole', 'imọlẹ', 'Isa$60:19 "Òòrùn kì yóò sì jẹ́ ìmọ́lẹ̀ rẹ ní ọ̀sán mọ́, tàbí kí ìtànsán òṣùpá tún ràn sí ọ mọ́, nítorí OLÚWA ni yóò jẹ́ ìmọ́lẹ̀ rẹ ayérayé, àti Ọlọ́run rẹ yóò jẹ́ ògo rẹ."'), 'Isa$61:1': ('ororo', None, 'Isa$61:1 " Ẹ̀mí OLÚWA àwọn ọmọ-ogun wà lára mi nítorí OLÚWA ti fi àmì-òróró yàn mí láti wàásù ìhìnrere fún àwọn talákà. Ó ti rán mi láti ṣe àwòtan oníròbìnújẹ́ láti kéde ìdásílẹ̀ fún àwọn ìgbèkùn àti ìtúsílẹ̀ kúrò nínú òkùnkùn fún àwọn ẹlẹ́wọ̀n,"'), 'Isa$61:4': ('atijọ', None, 'Isa$61:4 Wọn yóò tún àwọn ahoro ìṣẹ̀ǹbáyé kọ́ wọn yóò sì mú àwọn ibi ìparun àtijọ́tijọ́ náà bọ̀ sípò; wọn yóò jí àwọn ahoro ìlú náà padà tí a ti parun láti ìrandíran sẹ́yìn.'), 'Isa$61:7': ('ni', 'gbà', 'Isa$61:7 "Dípò àbùkù wọn àwọn ènìyàn mi yóò gba ìlọ́po-méjì, àti dípò àbùkù wọn wọn yóò yọ̀ nínú ìníi wọn; bẹ́ẹ̀ ni wọn yóò sì jogún ìlọ́po méjì ní ilẹ̀ wọn, ayọ̀ ayérayé yóò sì jẹ́ ti wọn. "'), 'Isa$61:8': ('ṣe', None, 'Isa$61:8 "“Nítorí Èmi, OLÚWA fẹ́ràn ìdájọ́ òdodo; mo kórìíra olè jíjà àti ẹ̀ṣẹ̀ Ní òtítọ́ mi èmi yóò ṣẹ̀san fún wọn èmi yóò sì dá májẹ̀mú ayérayé pẹ̀lúu wọn."'), 'Isa$61:9': ('mọ', None, 'Isa$61:9 A ó mọ ìrandíran wọn láàrin àwọn orílẹ̀ èdè àti àwọn ìran wọn láàrin àwọn ènìyàn Gbogbo àwọn tí ó bá rí wọn yóò mọ̀ pé wọ́n jẹ́ àwọn ènìyàn tí OLÚWA ti bùkún.”'), 'Isa$61:10': ('aṣọ', 'aṣọ', 'Isa$61:10 "Èmi yọ̀ gidigidi nínú OLÚWA; ọkàn mi yọ̀ nínú Ọlọ́run mi. Nítorí ó ti wọ̀ mí ní aṣọ ìgbàlà ó sì ṣe mí lọ́ṣọ̀ọ́ nínú aṣọ òdodo; gẹ́gẹ́ bí ọkọ ìyàwó ti ṣe oríi rẹ̀ lọ́ṣọ̀ọ́ bí àlùfáà, àti bí ìyàwó ṣe ń ṣe ara rẹ̀ lọ́ṣọ̀ọ́ pẹ̀lú ohun ọ̀ṣọ́."'), 'Isa$62:3': ('adé', 'adé', 'Isa$62:3 "Ìwọ yóò sì jẹ́ adé dídán ní ọwọ́ OLÚWA, adé ọba ní ọwọ́ Ọlọ́run rẹ."'), 'Isa$62:6': ('ṣe', None, 'Isa$62:6 "Èmi ti fi olùṣọ́ ránṣẹ́ sórí odi rẹ ìwọ Jérúsálẹ́mù; wọn kì yóò lè dákẹ́ tọ̀sán tòru. Ẹ̀yin tí ń ké pe OLÚWA, ẹ má ṣe fún ra yín ní ìsinmi,"'), 'Isa$62:8': ('ni', 'fun', 'Isa$62:8 OLÚWA ti búra pẹ̀lú ọ̀tún rẹ̀ àti nípa agbára apá rẹ: “Èmi kì yóò jẹ́ kí hóró rẹ di oúnjẹ fún ọ̀tá rẹ bẹ́ẹ̀ ni àwọn àjèjì kì yóò mu wáìnì tuntun rẹ mọ́ èyí tí ìwọ ti ṣe làálàá fún;'), 'Isa$63:3': ('tẹ', None, 'Isa$63:3 " “Èmi nìkan ti dá tẹ ìfúntí wáìnì; láti àwọn orílẹ̀ èdè tí ẹnikẹ́ni kò sì wà pẹ̀lú mi. Mo tẹ̀wọ́n mọ́lẹ̀ ní ìbínú mi mo sì tẹ̀ wọ́n rẹ́ ní ìrunú mi ẹ̀jẹ̀ wọn sì fọ́n sí aṣọ mi, mo sì da àbàwọ́n sí gbogbo aṣọ mi."'), 'Isa$63:12': ('ṣe', None, 'Isa$63:12 "ta ni ó rán ògo apá ti agbára rẹ̀ láti wà ní apá ọ̀tún Mósè, ta ni ó pín omi níyà níwájú wọn, láti gba òkìkí ayérayé fún ara rẹ̀,"'), 'Isa$63:14': ('ṣe', 'orukọ', 'Isa$63:14 "gẹ́gẹ́ bí ọ̀wọ́ ẹran tí ó lọ sí pápá oko, a fún wọn ní ìsinmi láti ọwọ́ Ẹ̀mí OLÚWA. Báyìí ni ẹ ṣe tọ́ àwọn ènìyàn yín láti fún ara yín ní orúkọ kan tí ó lógo. "'), 'Isa$63:18': (None, 'ni', 'Isa$63:18 "Fún ìgbà díẹ̀ ni àwọn ènìyàn rẹ fi gba ibi mímọ́ rẹ, ṣùgbọ́n nísinsìn yìí àwọn ọ̀tá wa ti tẹ ibi mímọ́ rẹ mọ́lẹ̀."'), 'Isa$64:1': (None, None, 'Isa$64:1 "Ìwọ ìbá fa ọ̀run ya kí o sì ṣọ̀kalẹ̀ wá, tí àwọn òkè ńlá yóò fi wárìrì níwájú rẹ!"'), 'Isa$64:4': (None, None, 'Isa$64:4 " Láti ìgbà ìwásẹ̀ kò sí ẹni tí ó gbọ́ rí kò sí etí kan tí ó gbọ́ ọ, kò sí ojú tí ó tí ì rí Ọlọ́run mìíràn lẹ́yìn rẹ, tí ó máa ń ṣe nǹkan lórúkọ àwọn tí ó dúró dè é."'), 'Isa$64:6': (None, 'ni', 'Isa$64:6 "Gbogbo wa ti dàbí ẹnìkan tí ó jẹ́ aláìmọ́, gbogbo òdodo wa sì dàbí èkíṣà ẹlẹ́gbin; gbogbo wa kákò bí ewé, àti bí afẹ́fẹ́, ẹ̀ṣẹ̀ wa ti gbá wa lọ kúrò."'), 'Isa$64:7': (None, None, 'Isa$64:7 Ẹnikẹ́ni kò pe orúkọ rẹ tàbí kí ó gbìyànjú láti dì ẹ́ mú; nítorí ìwọ ti fi ojú rẹ pamọ́ fún wa ó sì jẹ́ kí àwa ṣòfò dànù nítorí àwọn ẹ̀ṣẹ̀ wa.'), 'Isa$65:2': ('ni', None, 'Isa$65:2 "Gbogbo ọjọ́ ni mo ti nawọ́ mi síta sí àwọn ènìyàn ọlọ́rùn líle, tí wọn ń rìn lọ́nà tí kò dára, tí wọ́n sì gbáralé èrò ara wọn"'), 'Isa$65:10': ('agbo', 'agbo', 'Isa$65:10 "Ṣárónì yóò di pápá oko fún ọ̀wọ́ ẹran, àti àfonífojì Ákò yóò di ibi ìsinmi fún agbo ẹran, fún àwọn ènìyàn mi tí ó wá mi. "'), 'Isa$65:12': ('ṣe', None, 'Isa$65:12 "Èmi yóò yà ọ́ ṣọ́tọ̀ fún idà, àti pé ẹ̀yin yóò bẹ̀rẹ̀ mọ́lẹ̀ fún àwọn tí a pa; nítorí mo pè, ṣùgbọ́n ẹ̀yin kò dáhùn. Mo ṣọ̀rọ̀ ṣùgbọ́n ẹ̀yin kò tẹ́tísílẹ̀ Ẹ̀yin ṣe búrurú ní ojú mi ẹ sì yan ohun tí ó bàmí lọ́kàn jẹ́.”"'), 'Isa$65:13': (None, None, 'Isa$65:13 "Nítorí náà ohun tí OLÚWA àwọn ọmọ-ogun wí nìyìí: “Àwọn ìránṣẹ́ mi yóò jẹun; ṣùgbọ́n ebi yóò máa pa ẹ̀yin, àwọn ìránṣẹ́ mi yóò mu, ṣùgbọ́n òrùngbẹ yóò máa gbẹ ẹ̀yin; àwọn ìránṣẹ́ mi yóò ṣe àjọyọ̀, ṣùgbọ́n a ó dójú ti ẹ̀yin."'), 'Isa$65:15': (None, None, 'Isa$65:15 "Ẹ̀yin yóò fi orúkọ yín sílẹ̀ fún àwọn àyànfẹ́ mi gẹ́gẹ́ bí ègún; OLÚWA àwọn ọmọ-ogun yóò sì pa yín, ṣùgbọ́n fún àwọn ìránṣẹ́ rẹ̀ ni òun yóò fún ní orúkọ mìíràn"'), 'Isa$65:25': ('jẹ', None, 'Isa$65:25 " Ìkookò àti ọ̀dọ́ àgùntàn yóò jẹun pọ̀, kìnìún yóò sì jẹ koríko gẹ́gẹ́ bí akọ màlúù, ṣùgbọ́n erùpẹ̀ ni yóò jẹ́ oúnjẹ ejò. Wọn kì yóò panilára tàbí panirun ní gbogbo òkè mímọ́ mi,” ni OLÚWA wí."'), 'Isa$66:2': ('ṣe', 'ni', 'Isa$66:2 "Kì í ha á ṣe ọwọ́ mi ló tí ṣe nǹkan wọ̀nyí, bẹ́ẹ̀ ni wọ́n sì wà?” ni OLÚWA wí. “Eléyìí ni ẹni tí mo kà sí: ẹni náà tí ó rẹrarẹ̀ sílẹ̀ tí ó sì kẹ́dùn ní ọkàn rẹ̀, tí ó sì wárìrì nípasẹ̀ ọ̀rọ̀ mi."'), 'Isa$66:3': (None, 'pa', 'Isa$66:3 "Ṣùgbọ́n ẹni tí ó fi akọ màlúù rúbọ ó dàbí ẹni tí ó pa ènìyàn kan àti ẹni tí ó bá fi ọ̀dọ́ àgùntàn kan tọrẹ, dàbí ẹni tí ó kán ajá kan lọ́rùn; ẹnikẹ́ni tí ó bá fi irúgbin oníhóró tọrẹ dàbí ẹni tí ó mú ẹ̀jẹ̀ ẹlẹ́dẹ̀ wá, ẹni tí ó bá sì ṣun tùràrí ìrántí, dàbí ẹni tí ó sin ère òrìṣà. Wọ́n ti yan ipa ọ̀nà tiwọn, ọkàn wọn pẹ̀lú sì láyọ̀ nínú ohun ìríra wọn;"'), 'Isa$66:4': ('ṣe', None, 'Isa$66:4 "Fún ìdí èyí ni èmi pẹ̀lú yóò ṣe fi ọwọ́ líle mú wọn n ó sì mú ohun tí wọ́n bẹ̀rù rẹ̀ wá sórí wọn. Nítorí nígbà tí mo pè, kò sí ẹni tí ó dáhùn, nígbà tí mo sọ̀rọ̀, ẹnikẹ́ni kò tẹ́tísílẹ̀. Wọ́n ṣe ohun búburú ní ojú mi wọ́n sì yan ohun tí mo kóríìra rẹ̀.” "'), 'Isa$66:17': (None, None, 'Isa$66:17 "“Gbogbo àwọn tí wọ́n ya ara wọn sí mímọ́ tí wọ́n sì sọ ara wọn di mímọ́ láti lọ sínú ọgbà, tí wọ́n tẹ̀lé ẹni tí ó wà láàrin àwọn tí ó jẹ ẹran ẹlẹ́dẹ̀ àti èkúté àti àwọn ohun ìríra mìíràn ni wọn yóò bá òpin wọn pàdé papọ̀,” ni OLÚWA wí."'), 'Isa$66:22': ('ṣe', 'orukọ', 'Isa$66:22 " “Gẹ́gẹ́ bí àwọn ọ̀run tuntun àti ayé tuntun tí mo dá yóò ṣe faradà á níwájú mi,” ni OLÚWA wí, “Bẹ́ẹ̀ ni orúkọ yín àti ìrandíran yín yóò pegedé."'), 'Isa$66:24': ('lọ', 'kú', 'Isa$66:24 " “Wọn yóò sì jáde lọ wọn yóò sì lọ wo òkú àwọn tí wọ́n dìtẹ̀ sí mi; kòkòrò wọn kì yóò kú, bẹ́ẹ̀ ni iná wọn ni a kì yóò pa, bẹ́ẹ̀ ni wọn yóò sì jẹ́ ohun ìríra fún gbogbo ọmọ ènìyàn.”"'), 'Jer$1:10': (None, None, 'Jer$1:10 " Wòó, mo yàn ọ́ lórí àwọn orílẹ̀ èdè àti ìjọba gbogbo láti fàtu, wó lulẹ̀, láti bàjẹ́, láti jágbà, láti má a kọ́, àti láti máa gbìn."'), 'Jer$1:16': ('ni', None, 'Jer$1:16 "Èmi yóò sì kéde ìdájọ́ mi lórí àwọn ènìyàn mi nítorí ìwà búburú wọn nípa kíkọ̀ mí sílẹ̀, nípa rírúbọ sí Ọlọ́run mìíràn àti sínsin àwọn ohun tí wọ́n fi ọwọ́ wọn ṣe."'), 'Jer$1:18': (None, 'ni', 'Jer$1:18 "Ní òní èmi ti sọ ọ́ di orílẹ̀ èdè alágbára, òpó ìrin àti odi idẹ sí àwọn Ọba Júdà, àwọn ìjòyè rẹ̀, sí àwọn àlùfáà àti àwọn ènìyàn ilẹ̀ náà."'), 'Jer$2:4': (None, 'di', 'Jer$2:4 "Gbọ́ ọ̀rọ̀ OLÚWA, ẹ̀yin ìdílé Jákọ́bù àti gbogbo ẹ̀yin ará ilé Ísírẹ́lì."'), 'Jer$2:6': ('mu', 'ilẹ', 'Jer$2:6 "Wọn kò béèrè, ‘Níbo ni OLÚWA wà tí ó mú wọn, jáde láti ilẹ̀ Éjíbítì, tí ó mú wa la ihà já, tí ó mu wọn la àárin àwọn aṣálẹ̀ àti òkè láàrin ìyàngbẹ ilẹ̀ àti òkùnkùn wá ilẹ̀ ibi tí ẹnikẹ́ni kò ti rìnrìn-àjò, tí ẹníkẹ́ni kò sì gbé?’"'), 'Jer$2:7': ('mu', 'ilẹ', 'Jer$2:7 "Mo mú yín wá sí ilẹ̀ ọlọ́ràá láti máa jẹ èso ibẹ̀ àti ọrọ̀ rẹ̀, ẹ̀yin sì wá, ẹ sì ba ilẹ̀ náà jẹ́, ẹ sì mú àwọn ohun ogún mi di ohun ìríra."'), 'Jer$2:10': ('wo', None, 'Jer$2:10 "Rékọjá sí ẹkùn kan kítímì, kí o sì wò ó, ránṣẹ́ sí kéda, kí o sì wò ó dáradára, wò ó kí ẹ sì wò ó bí irú nǹkan báyìí bá wà níbẹ̀ rí?"'), 'Jer$2:13': (None, 'ni', 'Jer$2:13 "“Àwọn ènìyàn mi ti dẹ́sẹ̀, méjì Wọ́n ti kọ̀ mí sílẹ̀, Èmi orísun omi ìyè, wọ́n sì ti wọ àmù, àmù fífọ́ tí kò lè gba omi dúró."'), 'Jer$2:20': (None, None, 'Jer$2:20 "“Nígbà àtijọ́, o ti fọ́ àjàgà rẹ sọnù, ìwọ sì já ìdè rẹ ìwọ wí pé, ‘Èmi kì yóò sìn ọ́!’ Lóòtọ́, lórí gbogbo òkè gíga ni àti lábẹ́ igi tí ó tàn kálẹ̀ ni ìwọ dùbúlẹ̀ sí gẹ́gẹ́ bí panṣágà."'), 'Jer$2:30': ('ni', None, 'Jer$2:30 "“Nínú aṣán mo fìyà jẹ àwọn ènìyàn yín, wọn kò sì gba ìbáwí, idà yín ti pa àwọn wòlíì yín run, gẹ́gẹ́ bí kìnnìún tí ń bú ramúramù."'), 'Jer$3:1': ('lọ', 'di', 'Jer$3:1 "“Bí ọkùnrin kan bá sì kọ ìyàwó rẹ̀ sílẹ̀ tí obìnrin náà sì lọ fẹ́ ọkọ mìíràn, ǹjẹ́ ọkùnrin náà tún lè tọ̀ ọ́ wá? Ǹjẹ́ ilẹ̀ náà kò ní di aláìmọ́ bí? Ṣùgbọ́n ìwọ ti gbé pẹ̀lú onírúurú panṣágà gẹ́gẹ́ bí olùfẹ́, ṣé ìwọ yóò tún padà sọ́dọ̀ mi bí?” ni OLÚWA wí."'), 'Jer$3:8': ('ṣe', None, 'Jer$3:8 "Mo fún Ísírẹ́lì aláìnígbàgbọ́ ní ìwé ìkọ̀sílẹ̀, mo sì ké wọn kúrò nítorí gbogbo àgbèrè wọn. Ṣíbẹ̀ mo rí pé Júdà tí ó jẹ́ arábìnrin rẹ̀ kò bẹ̀rù, òun náà sì jáde lọ láti lọ ṣe àgbèrè."'), 'Jer$3:14': ('gba', 'mú', 'Jer$3:14 "“Padà, ẹ̀yin ènìyàn aláìnígbàgbọ́,” ni OLÚWA wí, “nítorí èmi ni ọkọ rẹ. Èmi ó yàn ọ́, ọ̀kan láti ìlú àti méjì láti ẹ̀yà. Èmi ó sì mú ọ wá sí Síónì."'), 'Jer$3:16': (None, 'wá', 'Jer$3:16 "Ní ọjọ́ wọ̀n-ọn-nì, tí ẹ̀yin bá pọ̀ sí i lórí ilẹ̀ náà,” ni OLÚWA wí, “àwọn ènìyàn kò tún lè sọ pé, ‘Àpótí ẹ̀rí OLÚWA’ kò ní wá sí ìrántí wọn mọ́, a kì yóò pàdánù rẹ̀, bẹ́ẹ̀ ni a kì yóò sì tún kan òmíràn mọ́."'), 'Jer$3:18': ('fi fun', 'ni', 'Jer$3:18 Ní ọjọ́ wọ̀n-ọn-nì ni ilé Júdà yóò darapọ̀ mọ́ ilé Ísírẹ́lì. Wọn yóò sì wá láti ilẹ̀ àríwá wá sí ilẹ̀ tí mo fi fún àwọn baba ńlá yín gẹ́gẹ́ bí ilẹ̀ ìní.'), 'Jer$4:11': (None, None, 'Jer$4:11 "Nígbà náà ni a ó sọ fún Jérúsálẹ́mù àti àwọn ènìyàn pé, “Ẹ̀fúùfù líle láti aṣálẹ̀ fẹ́ lu àwọn ènìyàn mi, kì í ṣe láti sọ di mímọ́."'), 'Jer$4:21': (None, 'gbo', 'Jer$4:21 "Yóò ti pẹ́ tó, tí èmi yóò rí ogun tí èmi yóò sì gbọ́ ìró fèrè? "'), 'Jer$5:1': ('wa', 'wo', 'Jer$5:1 "“Lọ sókè àti sódò àwọn òpó Jérúsálẹ́mù Wò yíká, kí o sì mọ̀, kí o sì wá kiri Bí o bá le è rí ẹnìkan, tí ó jẹ́ olóòótọ́ àti olódodo, N ó dárí jìn ìlú yìí."'), 'Jer$5:5': (None, None, 'Jer$5:5 "Nítorí náà, èmi yóò tọ àwọn adarí lọ, n ó sì bá wọn sọ̀rọ̀; ó dájú wí pé wọ́n mọ ọ̀nà OLÚWA àti ohun tí Ọlọ́run wọn ń fẹ́.” Ṣùgbọ́n pẹ̀lú ìfìmọ̀ṣọ̀kan, wọ́n ti ba àjàgà jẹ́, wọ́n sì ti já ìdè."'), 'Jer$5:10': ('gba', 'ṣe', 'Jer$5:10 "“Lọ sí ọgbà àjàrà rẹ̀, kí ẹ sì pa wọ́n run, ẹ má ṣe pa wọ́n run pátapáta. Ya ẹ̀ka rẹ̀ kúrò, nítorí àwọn ènìyàn wọ̀nyí kì í ṣe ti OLÚWA."'), 'Jer$5:12': ('ṣe', 'wá', 'Jer$5:12 "Wọ́n ti pa irọ́ mọ́ OLÚWA; wọ́n wí pé, “Òun kì yóò ṣe ohun kan! Ibi kan kì yóò sì tọ̀ wá wá; àwa kì yóò sì rí idà tàbí ìyàn."'), 'Jer$5:20': (None, None, 'Jer$5:20 "“Kéde èyí fún ilé Jákọ́bù, kí ẹ sì polongo rẹ̀ ní Júdà."'), 'Jer$5:27': (None, None, 'Jer$5:27 "Bí àgò tí ó kún fún ẹyẹ, ilé wọn sì kún fún ẹ̀tàn. Wọ́n ti di ọlọ́lá àti alágbára,"'), 'Jer$5:28': (None, None, 'Jer$5:28 Wọ́n sanra wọ́n sì dán. Ìwà búburú wọn kò sì lópin; wọn kò bẹ̀bẹ̀ fún ẹjọ́ àwọn aláìní baba láti borí rẹ̀. Wọn kò jà fún ẹ̀tọ́ àwọn tálákà.'), 'Jer$5:30': ('ni', None, 'Jer$5:30 “Nǹkan ìbànújẹ́ àti ohun ìtara ti ṣẹlẹ̀ ní ilẹ̀ náà.'), 'Jer$6:16': ('wa', 'wo', 'Jer$6:16 "Èyí ni ohun tí OLÚWA wí: “Ẹ dúró sí ìkòríta, kí ẹ sì wò, ẹ bere fún ipa ìgbàanì, ẹ bèèrè ọ̀nà dáradára nì, kí ẹ sì wọ inú rẹ, ẹ̀yin yóò sì rí ìsinmi fún ọkàn yín. Ṣùgbọ́n ẹ̀yin wí pé, ‘Àwa kì yóò rin nínú rẹ.’"'), 'Jer$6:23': (None, 'ni', 'Jer$6:23 "Wọ́n sì dìmọ́ra pẹ̀lú ọrun àti ọ̀kọ̀, Wọ́n jẹ́ ẹni ibi, wọn kò sì ní àánú wọ́n ń hó bí omi òkun, bí wọ́n ti ṣe ń gun àwọn ẹsin wọn lọ; wọ́n sì wá bí ọkùnrin tí yóò jà ọ́ lógun, ìwọ ọmọbìnrin Síónì.” "'), 'Jer$6:24': (None, None, 'Jer$6:24 "Àwa ti gbọ́ ìròyìn nípa wọn, ọwọ́ wa sì di rírọ, ìrora sì mú wa bí ìrora bí obìnrin tí í rọbí."'), 'Jer$6:26': ('ṣọfọ', 'ẹkún', 'Jer$6:26 "Ẹ̀yin ènìyàn mi, ẹ gbé asọ ọ̀fọ̀ wọ̀, kí ẹ sì sùn nínú eérú, ẹ sọ̀fọ̀ pẹ̀lú ìpohùnréré ẹkún gẹ́gẹ́ bí i lórí ọmọkùnrin yín, kanṣoṣo nítorí pé lójijì ni àwọn apanirun yóò kọlù wá. "'), 'Jer$7:9': ('ṣe', None, 'Jer$7:9 "“ ‘Ẹ̀yin yóò ha jalè, kí ẹ pànìyàn, kí ẹ se pánṣágà, kí ẹ búra èké, kí ẹ sun tùràrí sí Báálì, kí ẹ sì tọ Ọlọ́run mìíràn tí ẹ̀yin kò mọ̀ lọ."'), 'Jer$7:12': ('ṣe', None, 'Jer$7:12 "“ ‘Ẹ lọ nísinsìn yìí sí ṣílò níbi ti mo kọ́ fi ṣe ibùgbé fún orúkọ mi, kí ẹ sì rí ohun tí mo ṣe sí i nítorí ìwà búburú Ísírẹ́lì tí í ṣe ènìyàn mi."'), 'Jer$7:13': ('dahùn', 'ṣe', 'Jer$7:13 "Nígbà tí ẹ̀yin ń ṣe àwọn nǹkan wọ̀nyí ni èmi bá a yín sọ̀rọ̀ léraléra ni OLÚWA wí ẹ̀yin kò gbọ́, èmi pè yín, ẹ̀yin kò dáhùn"'), 'Jer$7:18': ('ina', 'binu', 'Jer$7:18 "Àwọn ọmọ ṣa igi jọ, àwọn baba fi iná síi, àwọn ìyá sì po ìyẹ̀fun láti ṣe àkàrà fún ayaba ọ̀run, wọ́n tú ẹbọ ọrẹ mímu sí àwọn Ọlọ́run àjòjì láti mú mi bínú sókè."'), 'Jer$7:22': ('mu', 'ilẹ', 'Jer$7:22 Ní tòsí nígbà tí mo mú àwọn baba-ńlá yín jáde kúrò ní ilẹ̀ Éjíbítì tí mo sì bá wọn sọ̀rọ̀. N kò pàṣẹ fún wọn lórí ẹbọ sísun lásán.'), 'Jer$7:30': ('ṣe', None, 'Jer$7:30 "“ ‘Àwọn ènìyàn Júdà ti ṣe búburú lójú mi, ni OLÚWA wí. Wọ́n ti to àwọn ère ìríra wọn jọ sí ilé tí a fi orúkọ mi pè wọ́n sì ti sọ ọ́ di àìmọ́."'), 'Jer$8:9': (None, 'ni', 'Jer$8:9 "Ojú yóò ti àwọn ọlọ́gbọ́n, a ó dà wọ́n láàmú, wọn yóò sì kó sí ìgbèkùn. Nítorí wọ́n ti kọ ọ̀rọ̀ OLÚWA. Irú ọgbọ́n wo ló kù tí wọ́n ní?"'), 'Jer$8:13': ('ni', None, 'Jer$8:13 "“ ‘Èmi yóò mú ìkórè wọn kúrò ni OLÚWA wí. Kì yóò sí èṣo lórí igi àjàrà, kì yóò sí ọ̀pọ̀tọ́ lórí igi, ewé wọn yóò sì rẹ̀ sílẹ̀. Ohun tí mo ti fi fún wọn ni à ó gbà kúrò.’ ” "'), 'Jer$8:21': ('ya', None, 'Jer$8:21 "Níwọ̀n ìgbà tí a pa àwọn ènìyàn mi run, èmi náà run pẹ̀lú, mo sọ̀fọ, ìrora sì mú mi káká."'), 'Jer$9:3': (None, None, 'Jer$9:3 "Wọ́n ti pèsè ahọ́n wọn sílẹ̀ bí ọfà láti fi pa irọ́; kì í ṣe nípa òótọ́ ni wọ́n fi borí ní ilẹ̀ náà. Wọ́n ń lọ láti inú ẹ̀ṣẹ̀ kan sí òmíràn, wọn kò sì náání mi, ní OLÚWA wí."'), 'Jer$9:10': ('ti lọ', 'kọjá', 'Jer$9:10 "Èmi yóò sì sunkún, pohùnréré ẹkún fún àwọn òkè; àti ẹkún ìrora lórí pápá oko ihà wọ̀n-ọn-nì. Nítorí wọ́n di ahoro, wọn kò sì kọjá ní ibẹ̀. A kò sì gbọ́ igbe ẹran ọ̀sìn, àwọn ẹyẹ ojú ọ̀run sì ti sá lọ, bẹ́ẹ̀ náà ni àwọn ẹranko sì ti lọ. "'), 'Jer$9:13': (None, None, 'Jer$9:13 "OLÚWA sì wí pé, nítorí pé wọ́n ti kọ òfin mi sílẹ̀, èyí tí mo gbé kalẹ̀ níwájú wọn, wọn ṣe àìgbọ́ràn sí wọn, wọn kò sì rìn nínú òfin mi."'), 'Jer$9:16': ('run', 'ni', 'Jer$9:16 "Èmi yóò sì tú wọn ká láàrin àwọn orílẹ̀ èdè, nínú èyí tí àwọn tàbí àwọn baba wọn kò mọ̀. Èmi yóò sì lépa wọn pẹ̀lú idà títí èmi yóò fi pa wọ́n run.”"'), 'Jer$9:17': (None, 'ṣe', 'Jer$9:17 Èyí sì ni ohun tí OLÚWA Ọlọ́run Àwọn ọmọ ogun wí: “Ṣáà wò ó nísisìnyìí! Ké sí obìnrin ọlọ́fà nì kí ó wá; sì ránsẹ́ pe àwọn tí ó mòye nínú wọn.'), 'Jer$10:1': (None, 'ile', 'Jer$10:1 Gbọ́ ọ̀rọ̀ tí OLÚWA sọ fún yín ẹ̀yin ilé Ísírẹ́lì.'), 'Jer$10:9': ('mu', 'iṣẹ', 'Jer$10:9 "Sílífà tí a ti kàn ni a mú wá láti Tásísì, àti wúrà láti Lépásì; èyí tí àwọn onísọ́nà àti alágbẹ̀dẹ ṣe tí wọ́n kùn ní àwọ̀ aró àti eléṣé àlùkò, èyí jẹ́ iṣẹ́ ọwọ́ àwọn ọlọ́gbọ́n oníṣẹ́."'), 'Jer$10:11': ('ṣe', 'ni', 'Jer$10:11 “Sọ èyí fún wọn: ‘Àwọn ọlọ́run kékèèké tí kò dá ọ̀run àti ayé ni yóò ṣègbé láti ayé àti ní abẹ́ ọ̀run.’ ”'), 'Jer$10:12': ('agbaye', 'ayé', 'Jer$10:12 "Ọlọ́run dá ayé pẹ̀lú agbára rẹ̀, ó dá àgbáyé pẹ̀lú ọgbọ́n rẹ̀, ó mú kí ọ̀run kí ó fẹ̀ tóbi síta nípa òye rẹ̀."'), 'Jer$10:18': (None, None, 'Jer$10:18 "Nítorí èyí ni OLÚWA wí: “Ní àkókò yìí, èmi yóò gbọn àwọn tí ó ń gbé ilẹ̀ náà jáde. Èmi yóò mú ìpọ́njú bá wọn, kí wọn kí ó lè rí wọn mú.” "'), 'Jer$10:25': (None, 'orukọ', 'Jer$10:25 " Tú ìbínú rẹ síta sórí àwọn orílẹ̀ èdè tí kò mọ̀ ọ́, sórí àwọn ènìyàn tí wọn kò pe orúkọ rẹ. Nítorí pé wọ́n ti jẹ Jákọ́bù run, wọ́n ti jẹ ẹ́ run pátapáta, wọ́n sì ti ba ilẹ̀ ibùgbé rẹ̀ jẹ́."'), 'Jer$11:1': (None, 'ni', 'Jer$11:1 Èyí ni ọ̀rọ̀ OLÚWA tí ó tọ Jeremáyà wá: láti ọ̀dọ̀ OLÚWA:'), 'Jer$11:7': ('mu', 'ilẹ', 'Jer$11:7 "Láti ìgbà tí mo ti mú àwọn baba yín jáde láti ilẹ̀ Éjíbítì wá títí di òní, mo kìlọ̀ fún wọn lọ́pọ̀ ìgbà wí pé, “Ẹ gbọ́ tèmi.”"'), 'Jer$11:10': ('ṣe', None, 'Jer$11:10 "Wọ́n ti padà sí ìdí ẹ̀ṣẹ̀ àwọn baba ńlá wọn tí ó kọ̀ láti tẹ́tí sí ọ̀rọ̀ mi, wọ́n ti tẹ̀lé àwọn Ọlọ́run mìíràn láti sìn wọ́n. Ilé Ísírẹ́lì àti ilé Júdà ti fọ́ májẹ̀mú tí mo ṣe pẹ̀lú àwọn baba ńlá wọn."'), 'Jer$11:16': (None, 'ina', 'Jer$11:16 OLÚWA pè ọ́ ní igi ólífì pẹ̀lú èṣo rẹ tí ó dára ní ojú. Ṣùgbọ́n pẹ̀lú ìrọ́kẹ̀kẹ̀ ìjì líle ọ̀wọ́ iná ni yóò sun ún tí àwọn ẹ̀ka rẹ̀ yóò sì dá.'), 'Jer$11:19': ('ní', 'jẹ ki', 'Jer$11:19 "Mo ti dàbí ọ̀dọ́ àgùntàn jẹ́ẹ́jẹ́ tí a mú lọ fún pípa; N kò mọ̀ pé wọ́n ti gbìmọ̀ búburú sí mi wí pé: “Jẹ́ kí a run igi àti èso rẹ̀; jẹ́ kí a gé e kúrò ní orí ilẹ̀ alààyè, kí a mọ́ lè rántí orúkọ rẹ̀ mọ́.”"'), 'Jer$11:20': ('jẹ ki', 'ni', 'Jer$11:20 "Ṣùgbọ́n ìwọ OLÚWA àwọn ọmọ ogun, tí ó ń ṣe ìdájọ́ òdodo, tí ó ń ṣe àyẹ̀wò ẹ̀mí àti ọkàn, jẹ́ kí n rí ẹ̀san rẹ lórí wọn; nítorí ìwọ ni mo fi ọ̀rọ̀ mi lé lọ́wọ́."'), 'Jer$12:2': (None, 'mú', 'Jer$12:2 "Ó ti gbìn wọ́n, wọ́n sì ti fi egbò múlẹ̀, wọ́n dàgbà wọ́n sì so èso. Gbogbo ìgbà ni ó wà ní ètè wọn, o jìnnà sí ọkàn wọn."'), 'Jer$12:7': (None, 'ọwọ', 'Jer$12:7 "Èmi yóò kọ ilé mi sílẹ̀, màá fi ìní mi sílẹ̀. Èmi yóò fi ẹni tí mo fẹ́ lé àwọn ọ̀tá rẹ̀ lọ́wọ́."'), 'Jer$12:10': (None, 'ni', 'Jer$12:10 "Ọ̀pọ̀lọpọ̀ olùṣọ́ àgùntàn ni yóò sì ba oko àjàrà mi jẹ́, tí wọn yóò sì tẹ oko mi mọ́lẹ̀; Wọ́n ó sọ oko dídára mi di ibi tí a ń da ìdọ̀tí sí."'), 'Jer$12:11': (None, 'ni', 'Jer$12:11 "A ó sọ ọ́ di aṣálẹ̀ ilẹ̀ tí kò wúlò níwájú mi, gbogbo ilẹ̀ ni yóò di ahoro nítorí kò sí ẹnìkankan tí yóò náání."'), 'Jer$12:14': (None, 'ni', 'Jer$12:14 "Èyí ni ohun tí OLÚWA wí: “Ní ti gbogbo àwọn aládùúgbò mi búburú tí wọ́n gbẹ́sẹ̀ lé ogún tí mo fún àwọn ènìyàn Ísírẹ́lì mi, Èmi yóò fà wọ́n tu kúrò lórí ilẹ̀ wọn, èmi yóò fa ìdílé Júdà tu kúrò ní àárin wọn."'), 'Jer$12:15': ('pada', None, 'Jer$12:15 "Ṣùgbọ́n tí mo bá fà wọ́n tu tán, Èmi yóò padà yọ́nú sí wọn. Èmi yóò sì padà fún oníkálùkù ní ogún ìní rẹ̀ pẹ̀lú ilẹ̀ ìní oníkálùkù."'), 'Jer$13:3': ('ni', None, 'Jer$13:3 Nígbà náà ni ọ̀rọ̀ OLÚWA tọ̀ mí wá nígbà kejì:'), 'Jer$13:6': ('lọ', None, 'Jer$13:6 "Lẹ́yìn ọjọ́ díẹ̀, OLÚWA sọ fún mi: Lọ sí Pérátì kí o lọ mú àmùrè tí mo ní kí o pamọ́ síbẹ̀."'), 'Jer$13:11': (None, 'ni', 'Jer$13:11 "Nítorí bí a ti lẹ àmùrè mọ́ ẹ̀gbẹ́ ènìyàn, bẹ́ẹ̀ ni a lẹ agbo ilé Ísírẹ́lì àti gbogbo ilé Júdà mọ́ mi,’ ni OLÚWA wí, ‘kí wọn kí ó lè jẹ́ ènìyàn ògo àti ìyìn fún mi, ṣùgbọ́n wọn kò fẹ́ gbọ́.’"'), 'Jer$13:16': ('okunkun', 'ojiji', 'Jer$13:16 "Ẹ fi ògo fún OLÚWA Ọlọ́run yín, kí ó tó mú òkùnkùn wá, àti kí ó tó mú ẹsẹ̀ yín tàsé lórí òkè tí ó ṣókùnkùn, Nígbà tí ẹ̀yin sì ń retí ìmọ́lẹ̀, òun yóò sọ ọ́ di òjìji yóò si ṣe bi òkùnkùn biribiri."'), 'Jer$13:17': ('gbe', None, 'Jer$13:17 "Ṣùgbọ́n bí ẹ̀yin kò bá fetísílẹ̀, Èmi yóò sunkún ní ìkọ̀kọ̀ nítorí ìgbéraga yín; Ojú mi yóò sun ẹkún kíkorò, tí omi ẹkún, yóò sì máa ṣàn jáde, nítorí a kó agbo OLÚWA lọ ìgbèkùn. "'), 'Jer$13:18': ('ayaba', None, 'Jer$13:18 "Sọ fún Ọba àti ayaba pé, “Ẹ rẹ ara yín sílẹ̀, ẹ sọ̀kalẹ̀ láti orí ìtẹ́ yín, adé ògo yín bọ́ sí ilẹ̀ láti orí yín.”"'), 'Jer$13:25': (None, 'ipin', 'Jer$13:25 "Èyí ni ìpín tìrẹ; tí mo ti fi sílẹ̀ fún ọ,” ni OLÚWA wí, “nítorí ìwọ ti gbàgbé mi tí o sì gbẹ́kẹ̀lé àwọn ọlọ́run àjèjì."'), 'Jer$14:4': (None, 'ilẹ', 'Jer$14:4 "Ilẹ̀ náà sán nítorí pé kò sí òjò ní ilẹ̀ náà; ìrètí àwọn àgbẹ̀ di òfo, wọ́n sì bo orí wọn."'), 'Jer$14:10': ('ni', 'gba', 'Jer$14:10 "Báyìí ni OLÚWA sọ nípa àwọn ènìyàn wọ̀nyí: “Wọ́n fẹ́ràn láti máa rìn kiri; wọn kò kó ọkàn wọn ní ìjánu. Nítorí náà OLÚWA kò gbà wọ́n; yóò wá rántí ìwà búburú wọn báyìí, yóò sì fi ìyà ẹ̀ṣẹ̀ wọn jẹ wọ́n.”"'), 'Jer$14:12': ('ẹbọ', 'ọrẹ', 'Jer$14:12 " Bí ó tilẹ̀ jẹ́ pé wọ́n gbààwẹ̀, èmi kò ní tẹ́tí sí igbe wọn. Bí ó tilẹ̀ jẹ́ pé wọ́n rúbọ ọrẹ sísun àti ẹbọ ọkà, èmi ò ní gbà wọ́n. Dípò bẹ́ẹ̀, èmi ó fi idà, ìyànu àti àjàkálẹ̀-àrùn pa wọ́n run.”"'), 'Jer$14:17': (None, None, 'Jer$14:17 “Sọ ọ̀rọ̀ yìí fún wọn báyìí pé: “ ‘Jẹ́ kí omijé dà lójú mi lọ́sàn-án àti lóru láìdúró. Fún wúndíá mi-àwọn ènìyàn mi tí a dá lọ́gbẹ́ àti lílù bolẹ̀.'), 'Jer$15:2': ('lọ', None, 'Jer$15:2 " Tí wọ́n bá sì bi ọ́ pé, ‘Níbo ni kí a lọ?’ Sọ fún wọn pé, ‘Báyìí ni OLÚWA wí: “ ‘Àwọn tí a kọ ikú mọ́, sí ikú; àwọn tí a kọ idà mọ́, sí idà; àwọn tí a kọ ìyàn mọ́, sí ìyàn; àwọn tí a kọ ìgbékùn mọ́ sí ìgbékùn.’"'), 'Jer$15:4': ('ṣe', None, 'Jer$15:4 N ó sọ wọ́n di ẹni ìkórìíra níwájú gbogbo ìjọba ayé torí ohun tí Mánásè ọmọ Heṣekáyà Ọba àwọn Júdà ṣe ní Jérúsálẹ́mù.'), 'Jer$15:8': (None, 'ni', 'Jer$15:8 Èmi yóò jẹ́ kí àwọn opó wọn pọ̀ ju yanrìn òkun lọ. Ní ọjọ́kanrí ni èmi ó mú apanirun kọlu àwọn ìyá ọmọkùnrin wọn. Lójìjì ni èmi yóò mú ìfòyà àti ìbẹ̀rù bá wọn.'), 'Jer$15:14': (None, 'ina', 'Jer$15:14 Èmi yóò fi ọ́ ṣẹrú fún àwọn ọ̀tá rẹ ní ìlú tí ìwọ kò mọ̀ nítorí ìbínú mi yóò rán iná tí yóò jó ọ.”'), 'Jer$15:15': ('jiya', 'ni', 'Jer$15:15 "Ó yé ọ, ìwọ OLÚWA rántí mi kí o sì ṣe ìtọ́jú mi; gbẹ̀san mi lára àwọn tó dìtẹ̀ mi. Ìwọ ti jìyà fún ìgbà pípẹ́, má ṣe mú mi lọ, nínú bí mo ṣe jìyà nítorí tìrẹ."'), 'Jer$15:19': ('pada', None, 'Jer$15:19 "Nítorí náà báyìí ni OLÚWA wí: “Tí o bá ronúpìwàdà, Èmi ó dá ọ padà wá kí o lè máa sìn mí. Tí ó bá sọ ọ̀rọ̀ tó dára ìwọ yóò di ọ̀gbẹnusọ mi. Jẹ́ kí àwọn ènìyàn yìí kọjú sí ọ; ṣùgbọ́n ìwọ kò gbọdọ̀ kọjú sí wọn"'), 'Jer$15:20': (None, None, 'Jer$15:20 "Èmi fi ọ́ ṣe odi idẹ tí ó lágbára sí àwọn ènìyàn wọ̀nyí. Wọn ó bá ọ jà ṣùgbọ́n, wọn kò ní lè borí rẹ nítorí pé, mo wà pẹ̀lú rẹ láti gbà ọ́ là, kí n sì dáàbò bò ọ́,” ni OLÚWA wí."'), 'Jer$15:21': (None, None, 'Jer$15:21 "“Èmi yóò gbà ọ́ sílẹ̀ lọ́wọ́ àwọn ìkà ènìyàn, Èmi yóò sì rà ọ́ padà kúrò lọ́wọ́ àwọn ẹni búburú.”"'), 'Jer$16:1': (None, 'ni', 'Jer$16:1 Bákan náà ni ọ̀rọ̀ OLÚWA tọ̀ mí wá wí pé:'), 'Jer$16:3': ('ilẹ', None, 'Jer$16:3 "Nítorí èyí ni ohun tí OLÚWA wí nípa ọmọkùnrin tàbí ọmọbìnrin tí wọ́n bá bí nílẹ̀ yìí, àti ìyá tí ó bí wọn àti bàbá wọn."'), 'Jer$16:5': ('ṣọfọ', None, 'Jer$16:5 "Nítorí báyìí ni OLÚWA wí: “Má ṣe wọ ilé tí oúnjẹ ìsìnkú wà, má se lọ ibẹ̀ láti káànú tàbí sọ̀fọ̀, nítorí mo ti mú ìbùkún, ìfẹ́ àti àánú mi kúrò lórí àwọn ènìyàn wọ̀nyí,” ni OLÚWA wí."'), 'Jer$16:6': ('ṣe', None, 'Jer$16:6 "“Àti ẹni ńlá àti kékeré ni yóò ṣègbé ní ilẹ̀ yìí, wọn kò ní sin wọ́n tàbí sọ̀fọ̀ wọn, bẹ́ẹ̀ ni kò sí ẹni tí yóò fá irun orí wọn nítorí wọn."'), 'Jer$16:11': (None, 'sin', 'Jer$16:11 "Nígbà náà ni ìwọ yóò sọ fún wọn wí pé, ‘Nítorí tí àwọn baba yín ti kọ̀ mí sílẹ̀,’ ni OLÚWA wí, ‘tí wọ́n sì tẹ̀lé àwọn òrìṣà mìíràn tí wọ́n ń bọ, tí wọ́n ń sìn, wọ́n kọ̀ mí sílẹ̀ wọn kò sì tẹ̀lé òfin mi mọ́."'), 'Jer$16:14': ('mu', None, 'Jer$16:14 "“Nítorí náà, ọjọ́ náà ń bọ̀ wá,” ni OLÚWA wí, “tí àwọn ènìyàn kò ní sọ pé, ‘Dájúdájú OLÚWA wà láàyè OLÚWA ń bẹ Ẹni tí ó mú Ísírẹ́lì jáde kúrò ní Éjíbítì.’"'), 'Jer$16:18': (None, None, 'Jer$16:18 "Èmi yóò san ẹ̀san ìwà búburú àti ẹ̀ṣẹ̀ wọn fún wọn ní ìlọ́poméjì, nítorí wọ́n ti ba ilẹ̀ mi jẹ́ pẹ̀lú àwọn ère wọn aláìlẹ́mìí, wọ́n sì ti kún ohun ìní mi pẹ̀lú ẹ̀gbin òrìṣà wọn.”"'), 'Jer$17:4': (None, None, 'Jer$17:4 "Láti ipaṣẹ̀ àìṣedéédé yín ni ẹ̀yin yóò ti sọ ogún tí mo fún un yín nù. Èmi yóò fi yín fún ọ̀tá yín bí ẹrú ní ilẹ̀ tí ẹ̀yin kò mọ̀; nítorí ẹ̀yin ti mú inú bí mi, èyí tí yóò sì wà títí ayé.”"'), 'Jer$17:16': (None, None, 'Jer$17:16 "Èmi kò sá kúrò láti máa jẹ́ olùsọ àgùntàn rẹ, ìwọ mọ̀ wí pé èmi kò kẹ̀rẹ̀ ní ọjọ́ ìpọ́njú. Ohun tí ó jáde ní ètè mi jẹ́ èyí tí ó hàn sí ọ."'), 'Jer$17:19': (None, None, 'Jer$17:19 Èyí ni ohun tí OLÚWA wí fún mi: “Lọ dúró ní ẹnu ọ̀nà àwọn ènìyàn níbi tí àwọn Ọba Júdà ń gbà wọlé tí wọ́n ń gbà jáde àti ní gbogbo ẹnu bodè Jérúsálẹ́mù.'), 'Jer$17:24': ('ṣe', None, 'Jer$17:24 "Ṣùgbọ́n tí ẹ̀yin bá kíyèsí láti gbọ́ tèmi ní OLÚWA wí, tí ẹ kò sì gbe ẹrù gba ti ẹnu bodè ìlú ní ọjọ́ ìsinmi, ṣùgbọ́n tí ẹ ya ọjọ́ ìsinmi, sí mímọ́, nípa pé ẹ kò ṣe iṣẹ́kísẹ́ ní ọjọ́ náà."'), 'Jer$17:26': ('mú', 'ilẹ', 'Jer$17:26 "Àwọn ènìyàn yóò wá láti ìlú Júdà àti ní agbègbè Jérúsálẹ́mù, láti ilẹ̀ Bẹ́ńjámínì àti níbi òkè gúṣù láti orílẹ̀ èdè gíga. Wọn yóò wá pẹ̀lú ọrẹ ẹbọ sísun àti orẹ ọkà ẹran, ọrẹ ọpẹ́ tùràrí àti ìyìn wá sí ilé OLÚWA."'), 'Jer$17:27': ('ina', 'ina', 'Jer$17:27 "Ṣùgbọ́n bí ẹ̀yin kò bá pa òfin mi mọ́ láti ya ọjọ́ ìsinmi sí mímọ́, kí ẹ má sì ṣe ru ẹrùkẹ́rù bí ẹ̀yin yóò ṣe máa gba ti ẹnu-bodè Jérúsálẹ́mù wọlé ní ọjọ́ ìsinmi, nígbà náà ni èmi yóò da iná tí kò ní ṣe é parun ní ẹnu bodè Jérúsálẹ́mù tí yóò sì jó odi agbára rẹ̀.’ ”"'), 'Jer$18:3': ('lọ', 'iṣẹ', 'Jer$18:3 Nígbà náà ni mo lọ sí ilé amọ̀kòkò mo sì rí i tí ó ń ṣiṣẹ́ kan lórí kẹ̀kẹ́.'), 'Jer$18:5': ('wá', None, 'Jer$18:5 Nígbà náà ni ọ̀rọ̀ OLÚWA tọ̀ mí wá pé:'), 'Jer$18:7': (None, 'fa', 'Jer$18:7 "Bí ó bá jẹ́ ìgbà kan, tí èmi kéde kí orílẹ̀ èdè tàbí ìjọba kan di fífà tu láti dojúdé àti láti parun."'), 'Jer$18:15': (None, 'ni', 'Jer$18:15 "Síbẹ̀ ni àwọn ènìyàn mi ti gbàgbé mi, wọ́n sun tùràrí fún òrìṣà aṣán tí ó mú wọn kọsẹ̀ ní ọ̀nà wọn àti ọ̀nà wọn àtijọ́. Wọ́n mú wọn rìn ní ọ̀nà àtijọ́ àti ní ojú tí a kò kọ́"'), 'Jer$18:22': ('ni', 'gba', 'Jer$18:22 Jẹ́ kí a gbọ́ ohùn ẹkún láti ilé wọn nígbà tí ó bá mu àwọn jagunjagun kọ lù wọ́n lójijì nítorí wọ́n ti gbẹ́ kòtò láti mú mi. Wọ́n ti dẹ ìkẹkùn fún ẹṣẹ̀ mi'), 'Jer$18:23': ('oju', None, 'Jer$18:23 "Ṣùgbọ́n ìwọ OLÚWA mọ gbogbo ète wọn láti pa mí, má ṣe fojú fò ibi wọn tàbí pa ẹ̀ṣẹ̀ wọn rẹ́ kúrò lójú rẹ. Jẹ́ kí a mú wọn kúrò níwájú rẹ bá wọn jà nígbà ìbínú rẹ."'), 'Jer$19:1': ('gba', 'mú', 'Jer$19:1 "Èyí ní ohun tí OLÚWA wí: “Lọ ra ìkòkò lọ́dọ̀ alámọ̀, mú dání lára àwọn àgbààgbà ọkùnrin àti wòlíì."'), 'Jer$19:7': ('fun', 'ṣe', 'Jer$19:7 "“ ‘Ní ibí yìí ni èmi yóò ti pa èrò Júdà àti Jérúsálẹ́mù run. Èmí yóò mú wọn ṣubú nípa idà níwájú àwọn ọ̀tá wọn, lọ́wọ́ àwọn tí ó ń wá ẹ̀mi wọn. Èmi yóò sì fi òkú wọn ṣe oúnjẹ fún àwọn ẹyẹ ojú ọ̀run àti ẹranko ilẹ̀."'), 'Jer$19:10': ('lọ', 'fọ', 'Jer$19:10 “Nígbà náà ni ìwọ yóò fọ́ ìkòkò náà ní oju àwọn tí ó bá ọ lọ.'), 'Jer$19:12': ('ṣe', 'ni', 'Jer$19:12 Èyí ni ohun tí èmi yóò ṣe sí ibí yìí àti àwọn tí ń gbé ní ibí yìí ni OLÚWA wí. Èmi yóò mú ìlú yìí dàbí Tófẹ́tì.'), 'Jer$20:4': (None, 'ọwọ', 'Jer$20:4 "Nítorí èyí ni ohun tí OLÚWA sọ. Èmi yóò mú ọ di ẹ̀rùjẹ̀jẹ̀ sí ara rẹ àti sí gbogbo àwọn ọ̀rẹ́ rẹ. Ní ojú ara rẹ, ìwọ yóò sì rí tí wọ́n ṣubú nípa idà ọ̀tá wọn. Èmi yóò fi gbogbo àwọn ọ̀tá Júdà lé Ọba Bábílónì lọ́wọ́, òun yóò sì kó wọn lọ sí Bábílónì tàbí kí ó pa wọ́n."'), 'Jer$20:5': (None, None, 'Jer$20:5 Èmi yóò jọ̀wọ́ gbogbo ọrọ̀ inú ìlú yìí fún ọ̀ta wọn. Gbogbo ìní wọ́n gbogbo ọlá Ọba Júdà. Wọn yóò sì ru ìkógun lọ sí Bábílónì.'), 'Jer$20:6': ('lọ', 'kú', 'Jer$20:6 "Àti ìwọ Páṣúrì, gbogbo ènìyàn inú ilé rẹ yóò sì lọ ṣe àtìpó ní Bábílónì. Níbẹ̀ ni ìwọ yóò sì kú sí, tí wọn yóò sì sin ọ́ síbẹ̀, ìwọ àti gbogbo àwọn ọ̀rẹ́ rẹ tí ìwọ ti sọ àṣọtẹ́lẹ̀ irọ́ fún.”"'), 'Jer$20:9': ('ṣe', 'orukọ', 'Jer$20:9 "Ṣùgbọ́n bí mo bá sọ pé, “Èmi kì yóò dárúkọ rẹ tàbí sọ nípa orúkọ rẹ̀ mọ́, ọ̀rọ̀ rẹ̀ ń bẹ bi iná tí ń jó nínú mi tí a sé nínú egungun mi Agara dá mi ní inú mi nítòótọ́ èmi kò lè ṣe é."'), 'Jer$20:12': ('jẹ ki', 'ni', 'Jer$20:12 "OLÚWA alágbára, ìwọ tí ó ń dán olódodo pípé wò tí o sì ń ṣe àyẹ̀wò ọkàn àti ẹ̀yà fínní-fínní, jẹ́ kí èmi kí ó rí ìgbẹ̀san rẹ lórí wọn, nítorí ìwọ ni mo gbé ara mi lé. "'), 'Jer$20:16': (None, 'ni', 'Jer$20:16 "Kí ọkùnrin náà dàbí ìlú tí OLÚWA gbàkóso lọ́wọ́ rẹ̀ láìkáànú Kí o sì gbọ́ ariwo ọ̀fọ̀ ní àárọ̀, ariwo ogun ní ọ̀sán."'), 'Jer$21:5': ('ibinu', 'ibinu', 'Jer$21:5 Èmi gan an yóò sì bá yín jà pẹ̀lú ohun ìjà olóró nínú ìbínú àti ìrunú líle.'), 'Jer$21:7': (None, 'ni', 'Jer$21:7 "OLÚWA sọ pé lẹ́yìn ìyẹn, èmi yóò fi Sedekáyà Ọba Júdà, àwọn ìjòyè àti gbogbo àwọn tí ó yọ nínú ìṣẹ̀lẹ̀ ogun àti ìyàn lé Ọba Nebukadinésárì àti Bábílónì àti gbogbo ọ̀ta wọn tí ń lépa ẹ̀mí wọn lọ́wọ́. Òun yóò sì fi idà pa wọ́n, kì yóò sì ṣàánú wọn.’"'), 'Jer$21:9': (None, None, 'Jer$21:9 "Ẹnikẹ́ni tí ó bá dúró sí ìlú yìí yóò ti ipa idà, ìyàn tàbí àjàkálẹ̀-àrùn kú. Ṣùgbọ́n ẹni tí ó bá jáde tí ó sì jọ̀wọ́ ara rẹ̀ fún àwọn Bábílónì tí ó ń lépa yín yóò sì yè. Òun yóò sá àsálà fún ẹ̀mí rẹ̀."'), 'Jer$21:10': (None, None, 'Jer$21:10 "Mo ti pinnu láti jẹ ìlú yìí níyà, ni OLÚWA wí. A ó sì gbé e fún Ọba Bábílónì, yóò sì run ún pẹ̀lú iná.’"'), 'Jer$21:14': ('ina', 'ina', 'Jer$21:14 Èmi yóò jẹ ẹ́ níyà gẹ́gẹ́ bí iṣẹ́ ọwọ́ rẹ̀ ni OLÚWA wí. Èmi yóò mú kí iná jó ilé rẹ̀; ìyẹn yóò jó gbogbo ohun tí ó wà ní àyíká rẹ.’ ”'), 'Jer$22:9': (None, None, 'Jer$22:9 "Ìdáhùn wọn yóò sì jẹ́: ‘Nítorí tí wọ́n ti kọ májẹ̀mú OLÚWA Ọlọ́run wọn sílẹ̀, wọn ti ń fi oríbalẹ̀ fún Ọlọ́run mìíràn, wọ́n sì sìn wọ́n.’ ”"'), 'Jer$22:11': (None, 'kú', 'Jer$22:11 "Nítorí báyìí ni OLÚWA wí, nípa Ṣálúmù ọmọ Jòsáyà Ọba Júdà tí ó jọba lẹ́yìn baba rẹ̀, ṣùgbọ́n tí ó jáde kúrò níhìn ín: “Òun kì yóò padà wá mọ́."'), 'Jer$22:17': ('oju', 'okan', 'Jer$22:17 “Ṣùgbọ́n ojú àti ọkàn rẹ wà lára rẹ̀ ní èrè àìsòtítọ́ láti ta ẹ̀jẹ̀ aláìṣẹ̀ sílẹ̀ ìnilára àti ìlọ́nilọ́wọ́gbà.”'), 'Jer$22:24': (None, None, 'Jer$22:24 "“Dájúdájú bí èmi ti wà láàyè,” ni OLÚWA wí, “Bí Jéhóíákínì ọmọ Jéhóíákímù Ọba Júdà tilẹ̀ jẹ́ òrùka èdìdì lọ́wọ́ ọ̀tún mi, síbẹ̀ èmi ó fà ọ́ tu kúrò níbẹ̀."'), 'Jer$23:3': ('agbo', 'agbo', 'Jer$23:3 "“Èmi OLÚWA tìkálárami yóò kó ìyókù agbo ẹran mi jọ láti inú gbogbo orílẹ̀ èdè tí mo ti lé wọn, Èmi yóò mú wọn padà sínú pápá oko wọn, níbẹ̀ ni wọn ó ti bí síi, tí wọn ó sì pọ̀ síi."'), 'Jer$23:7': ('ní', None, 'Jer$23:7 "“Nítorí náà, ọjọ́ ń bọ̀,” ni OLÚWA wí, “tí ènìyàn kì yóò tún wí pé, ‘Dájúdájú OLÚWA wà láàyè tí ó mú àwọn ọmọ Ísírẹ́lì jáde kúrò ní ilẹ̀ Éjíbítì.’"'), 'Jer$23:13': (None, 'ni', 'Jer$23:13 "“Láàrin àwọn wòlíì Saáríà, Èmi rí ohun tí ń lé ni sá: Wọ́n sọ àsọtẹ́lẹ̀ lórúkọ Báálì wọ́n sì mú Ísírẹ́lì ènìyàn mi sìnà."'), 'Jer$23:17': ('ni', None, 'Jer$23:17 "Wọ́n ń sọ fún àwọn tí ó ń gàn mí pé, ‘OLÚWA ti wí pé: Ẹ ó ní àlàáfíà,’ Wọ́n sì wí fún gbogbo àwọn tí ó rìn nípa agídí ọkàn rẹ̀ pé, ‘Kò sí ìpalára kan tí yóò ṣẹlẹ̀ sí yín.’"'), 'Jer$23:22': (None, 'ni', 'Jer$23:22 "Ṣùgbọ́n ìbáṣepọ̀ wọn dúró nínú ìgbìmọ̀ mi, wọn ì bá ti kéde ọ̀rọ̀ mi fún àwọn ènìyàn mi. Wọn ì bá ti wàásù ọ̀rọ̀ mi sí àwọn ènìyàn wọn ìbá ti yípadà kúrò nínú ọ̀nà àti ìṣe búburú wọn. "'), 'Jer$23:32': (None, None, 'Jer$23:32 "Nítòótọ́, mo lòdì sí àwọn tí ń sọ àsọtẹ́lẹ̀ nípa àlá èké,” ni OLÚWA wí. “Wọ́n ń sọ bẹ́ẹ̀ ni wọ́n sì ń mú àwọn ènìyàn mi sìnà nípa onírúurú èké wọn, síbẹ̀ èmi kò rán wọn tàbí yàn wọ́n. Wọn kò sì ran àwọn ènìyàn wọ̀nyí lọ́wọ́ bí ó ti wù kí ó kéré mọ,” ni OLÚWA wí."'), 'Jer$23:38': ('fun', 'ni', 'Jer$23:38 "Bí ó tilẹ̀ jẹ́ pé ẹ̀yin ń sọ wí pé, ‘Èyí ni ọ̀rọ̀ ìjìnlẹ̀ OLÚWA,’ èyí ni ohun tí OLÚWA sọ: Ó ń lo ọ̀rọ̀ yìí, ‘Èyí ni ọ̀rọ̀ ìjìnlẹ̀ OLÚWA,’ bí ó tilẹ̀ jẹ́ pé mo sọ fún un yín láti má ṣe gbà á, ‘Èyí ni ọ̀rọ̀ ìjìnlẹ̀ OLÚWA.’"'), 'Jer$24:2': ('dara', None, 'Jer$24:2 Agbọ̀n kan ni èṣo ọ̀pọ̀tọ́ tí ó dára bí èyí tí ó tètè pọ́n; èkejì sì ní èṣo ọ̀pọ̀tọ́ tí ó burú rékọjá tí kò sì le è ṣe é jẹ'), 'Jer$24:4': (None, None, 'Jer$24:4 Nígbà náà ni ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Jer$24:6': (None, 'fa', 'Jer$24:6 "Ojú mi yóò máa ṣọ́ wọn lọ fún rere; Èmi yóò gbé wọn ró, n kò ní já wọn lulẹ̀. Èmi yóò gbìn wọ́n, n kò sì ní fà wọ́n tu."'), 'Jer$24:7': ('fun', 'pada', 'Jer$24:7 "Èmi yóò fún wọn ní ọkàn láti mọ̀ mí pé, Èmi ni OLÚWA. Wọn yóò jẹ́ ènìyàn mi; Èmi ni yóò sì jẹ́ Ọlọ́run wọn; nítorí pé wọn yóò padà sọ́dọ̀ mi pẹ̀lú gbogbo ọkàn wọn."'), 'Jer$24:8': ('ilẹ', None, 'Jer$24:8 "“ ‘Ṣùgbọ́n gẹ́gẹ́ bí èṣo ọ̀pọ̀tọ́ tí kò dára, tí ó burú tí kò ṣe é jẹ,’ ni OLÚWA wí, ‘bẹ́ẹ̀ ni Èmi yóò ṣe pẹ̀lú Ṣedekáyà Ọba Júdà, àwọn ìjòyè rẹ̀ àti àwọn tí ó yẹ ní Jérúsálẹ́mù, yálà wọ́n wà lórí ilẹ̀ yìí tàbí wọ́n ń gbé Éjíbítì."'), 'Jer$25:5': (None, None, 'Jer$25:5 "Wọ́n sì wí pé, “Ẹ yípadà olúkúlùkù yín kúrò ní inú ibi rẹ̀ àti ní ọ̀nà ibi rẹ̀, ẹ̀yin yóò sì lè dúró ní ilẹ̀ tí Ọlọ́run fún un yín àti àwọn baba yín títí láéláé."'), 'Jer$25:7': ('ni', None, 'Jer$25:7 "“Ṣùgbọ́n ẹ̀yin kò fetí sí mi,” ni OLÚWA wí; “bẹ́ẹ̀ ni ẹ sì ti mú inú bí mi pẹ̀lú ohun tí ẹ fi ọwọ́ yín ṣe, ẹ sì ti mú ibi wá sórí ara yín.”"'), 'Jer$25:8': (None, 'gba', 'Jer$25:8 "Nítorí náà, Ọlọ́run alágbára sọ èyí: “Nítorí wí pé ẹ̀yin kò fetí sí ọ̀rọ̀ mi,"'), 'Jer$25:12': ('ilẹ', None, 'Jer$25:12 "“Ṣùgbọ́n, nígbà tí àádọ́rin ọdún náà bá pé; Èmi yóò fi ìyà jẹ Ọba Bábílónì àti orílẹ̀ èdè rẹ, ilẹ̀ àwọn ará Bábílónì nítorí ẹ̀ṣẹ̀ wọn,” ni OLÚWA wí; “bẹ́ẹ̀ ni Èmi yóò sọ ọ́ di ahoro títí láé."'), 'Jer$25:13': ('mú', 'ilẹ', 'Jer$25:13 "Èmi yóò sì mú gbogbo ohun tí mo ti sọ wọ̀nyí wá sórí ilẹ̀ náà, gbogbo ohun tí a ti sọ ani gbogbo èyí tí a ti kọ sínú ìwé yìí, èyí tí Jeremáyà ti sọ tẹ́lẹ̀ sí gbogbo orílẹ̀-èdè."'), 'Jer$25:17': ('ní', None, 'Jer$25:17 "Mo sì gba aago náà lọ́wọ́ OLÚWA, bẹ́ẹ̀ ni mo sì mú gbogbo orílẹ̀ èdè tí ó rán mi sí mu ún."'), 'Jer$25:33': (None, 'ilẹ', 'Jer$25:33 "Nígbà náà, àwọn tí OLÚWA ti pa yóò wà ní ibi gbogbo, láti ìpẹ̀kun kan sí òmíràn. A kì yóò sọ̀fọ̀ wọn, a kì yóò kó wọn jọ tàbí sin wọ́n; ṣùgbọ́n wọn yóò dàbí ààtàn lórí ilẹ̀."'), 'Jer$26:1': ('wá', 'ṣe', 'Jer$26:1 "Ní ìbẹ̀rẹ̀ pẹ̀pẹ̀ ìṣèjọba Ọba Jehóáíkímù ọmọ Jòsáyà tí ń ṣe Ọba Júdà, ọ̀rọ̀ yìí wá láti ọ̀dọ̀ OLÚWA:"'), 'Jer$26:4': (None, 'ni', 'Jer$26:4 "Sọ fún wọn wí pé, ‘Èyí ni ohun tí OLÚWA wí: Tí ẹ kò bá fi etí sí ọ̀rọ̀ mi, tí ẹ kò sì gbọ́ òfin mi, ti mo gbé síwájú yín."'), 'Jer$26:17': (None, None, 'Jer$26:17 "Lára àwọn àgbààgbà ilẹ̀ náà sì sún ṣíwájú, wọ́n sì sọ fún gbogbo àwọn ènìyàn náà wí pé,"'), 'Jer$26:24': (None, 'ọwọ', 'Jer$26:24 "Áhíkámù ọmọ Sáfánì ń bẹ pẹ̀lú Jeremáyà, wọn kò sì fi í lé àwọn ènìyàn lọ́wọ́ láti pa á."'), 'Jer$27:1': (None, 'ọba', 'Jer$27:1 "Ní ìbẹ̀rẹ̀ pẹ̀pẹ̀ ìṣèjọba Sedekáyà ọmọ Jòsáyà Ọba Júdà, ọ̀rọ̀ yìí tọ Jeremáyà wá láti ọ̀dọ̀ OLÚWA:"'), 'Jer$27:6': (None, None, 'Jer$27:6 "Nísinsìn yìí, Èmi yóò fa gbogbo orílẹ̀ èdè rẹ fún Nebukadinésárì Ọba Bábílónì ìránṣẹ́ mi; Èmi yóò sì mú àwọn ẹranko búburú wọ̀n-ọn-nì jẹ́ tirẹ̀."'), 'Jer$27:8': ('run', 'ni', 'Jer$27:8 "“ ‘ “Àmọ́ tí orílẹ̀ èdè tàbí ìjọba kan kò bá ní sin Nebukadinésárì Ọba Bábílónì, tàbí kí ó tẹ orí rẹ̀ ba ní abẹ́ àjàgà rẹ̀; Èmi yóò fi ìyà jẹ orílẹ̀ èdè náà nípa idà, ìyàn àti àjàkálẹ̀-àrùn, ni OLÚWA wí títí Èmi yóò fi run wọ́n nípa ọwọ́ rẹ̀."'), 'Jer$27:11': (None, 'ilẹ', 'Jer$27:11 "Ṣùgbọ́n bí orílẹ̀ èdè kankan bá tẹ orí rẹ̀ ba lábẹ́ àjàgà Ọba Bábílónì, tí ó sì sìn ín, Èmi yóò jẹ́ kí orílẹ̀ èdè náà wà lórí ilẹ̀ rẹ̀ láti máa ro ó, àti láti máa gbé ibẹ̀ ni OLÚWA wí.” ’ ”"'), 'Jer$27:17': ('jẹ', None, 'Jer$27:17 "Má ṣe tẹ́tí sí wọn, ẹ máa sin Ọba Bábílónì, ẹ̀yin yóò sì yè. Èéṣe tí ẹ̀yin yóò fi di ìdíbàjẹ́?"'), 'Jer$27:20': ('mu', 'mú', 'Jer$27:20 "Èyí tí Nebukadinésárì Ọba Bábílónì kò kó lọ nígbà tí ó mú Jéhóíákímù Ọba Júdà lọ sí ìgbèkùn láti Jérúsálẹ̀mù lọ sí Bábílónì, pẹ̀lú àwọn ọlọ́lá Júdà àti Jérúsálẹ́mù."'), 'Jer$28:3': ('lọ', None, 'Jer$28:3 "Láàrin ọdún méjì, mà á mú gbogbo ohun èlò tí Ọba Nebukadinésárì; Ọba Bábílónì kó kúrò ní ilé OLÚWA tí ó sì kó lọ sí Bábílónì padà wá."'), 'Jer$28:5': (None, None, 'Jer$28:5 Wòlíì Jeremáyà fún wòlíì Hananáyà lésì ní iwájú ọmọ àlùfáà àti àwọn ènìyàn tí wọ́n dúró ní ilé OLÚWA.'), 'Jer$28:12': ('ní', None, 'Jer$28:12 Láìpẹ́ ni ọ̀rọ̀ OLÚWA tọ Jeremáyà wòlíì wá lẹ́yìn ìgbà tí wòlíì Hananáyà ti gbé àjàgà kúrò ní ọrùn wòlíì Jeremáyà wí pé:'), 'Jer$28:14': (None, 'ni', 'Jer$28:14 "Èyí ni ohun tí OLÚWA àwọn ọmọ-ogun Ọlọ́run Ísírẹ́lì wí: mà á fi àjàgà onírin sí ọrùn gbogbo orílẹ̀ èdè láti lè máa sin Nebukadinésárì ti Bábílónì, àti pé gbogbo yín ni ẹ̀ ó máa sìn ín. Mà á tún fún un ní àṣẹ lórí àwọn ẹranko búburú.’ ”"'), 'Jer$29:1': (None, 'ni', 'Jer$29:1 "Èyí ni ọ̀rọ̀ inú lẹ́tà tí wòlíì Jeremáyà fi ránṣẹ́ láti Jérúsálẹ́mù sí àwọn tí ó yè nínú àwọn aṣàtìpó àti àwọn àlùfáà, àwọn wòlíì àti gbogbo àwọn ènìyàn Nebukadinésárì tí wọ́n ṣe àtìpó láti Jérúsálẹ́mù ní Bábílónì."'), 'Jer$29:7': (None, 'ni', 'Jer$29:7 "Bákan náà, ẹ máa wá àlàáfíà àti ire ilẹ̀, èyí tí mo kó yín lọ láti ṣe àtìpó. Ẹ gbàdúrà sí OLÚWA fún ire ilẹ̀ náà; nítorí pé bí ó bá dára fún ilẹ̀ náà, yóò dára fún ẹ̀yin pẹ̀lú.”"'), 'Jer$29:13': ('wá', 'wa', 'Jer$29:13 "Ẹ̀yin yóò wá mi, bí ẹ̀yin bá sì fi gbogbo ọkàn yín wá mi: ẹ̀yin yóò rí mi ni OLÚWA Ọlọ́run wí."'), 'Jer$29:14': (None, 'ni', 'Jer$29:14 Èmi yóò sì mú yín kúrò ní ìgbékùn. Èmi yóò ṣà yín jọ láti gbogbo orílẹ̀ èdè àti ibi gbogbo tí mo ti lé yín jáde. Èmi yóò sì kó yín padà sí Jérúsálẹ́mù ibi tí mo ti kó jáde lọ sí ilé àtìpó.”'), 'Jer$29:15': (None, 'ni', 'Jer$29:15 "Ẹ lè sọ pé, “OLÚWA ti gbé àwọn wòlíì dìde fún wa ní Bábílónì.”"'), 'Jer$29:18': (None, 'ni', 'Jer$29:18 "Pẹ̀lú idà, ìyàn àti àjàkálẹ̀-àrùn ni Èmi yóò fi lé wọn; Èmi yóò sì sọ wọ́n di ìríra lójú gbogbo ìjọba ayé, àti ohun ẹ̀gún àti ẹ̀rù; ohun ẹ̀gbin àti ohun ẹ̀dùn láàrin gbogbo orílẹ̀ èdè ayé."'), 'Jer$29:20': (None, 'ni', 'Jer$29:20 "Nítorí náà, gbogbo ẹ̀yin àtìpó, ẹ gbọ́ ọ̀rọ̀ OLÚWA, ẹ̀yin tí mo rán lọ kúrò ní Jérúsálẹ́mù lọ sí Bábílónì."'), 'Jer$29:24': ('ṣe', None, 'Jer$29:24 "Wí fún Ṣemáyà tí í ṣe Neelamíyà pé,"'), 'Jer$29:30': ('wá', None, 'Jer$29:30 "Nígbà náà ni ọ̀rọ̀ OLÚWA tọ Jeremáyà wá wí pé,"'), 'Jer$30:3': ('fun', 'pada', 'Jer$30:3 "Ọjọ́ ń bọ̀ nígbà tí ń ó mú àwọn ènìyàn mi, àwọn ọmọ Ísírẹ́lì àti Júdà kúrò nínú ìgbèkùn, tí n ó sì dá wọn padà sí orí ilẹ̀ tí mo fi fún àwọn baba ńlá wọn láti ní,’ ni OLÚWA Ọlọ́run wí.”"'), 'Jer$30:8': (None, None, 'Jer$30:8 "“ ‘Ní ọjọ́ náà,’ OLÚWA wí pé; ‘Èmi yóò gbé àjàgà kúrò lọ́rùn wọn, Èmi yóò sì tú ìdè wọn sọnù. Àwọn alejo kì yóò sì mú ọ sìn wọ́n mọ́"'), 'Jer$30:11': (None, 'ni', 'Jer$30:11 "Èmi wà pẹ̀lú rẹ, n ó sì gbà ọ́,’ ni OLÚWA wí. ‘Bí mo tilẹ̀ pa gbogbo orílẹ̀ èdè run, nínú èyí tí mo ti fọ́n ọn yín ká, síbẹ̀ èmi kì yóò pa yín run pátapáta. Èmi yóò bá yín wí pẹ̀lú ìdájọ́ òdodo nìkan; Èmi kò ní fi yín sílẹ̀ láìjìyà.’"'), 'Jer$30:18': ('ni', None, 'Jer$30:18 "“Èyí ni ohun tí OLÚWA wí: “ ‘Èmi yóò dá gbogbo ire àgọ́ Jákọ́bù padà, èmi yóò sì ṣe àánú fún olùgbé àgọ́ rẹ̀; ìlú náà yóò sì di títúnṣe tí ààfin ìlú náà yóò sì wà ní ipò rẹ̀."'), 'Jer$31:4': (None, None, 'Jer$31:4 "Èmi yóò tún gbé e yín sókè, àní a ó tún gbé e yín ró iwọ wúndíá ilẹ̀ Ísírẹ́lì. Àní ẹ ó tún tún ohun èlò orin yín gbé, ẹ ó sì jáde síta pẹ̀lú ijó àti ayọ̀."'), 'Jer$31:23': (None, 'ilẹ', 'Jer$31:23 "Báyìí ni ohun tí OLÚWA àwọn ọmọ ogun Ísírẹ́lì wí: “Nígbà tí èmi bá kó wọn dé láti ìgbèkùn; àwọn ènìyàn tí ó wà ní ilẹ̀ Júdà àti ní àwọn ìlú rẹ̀ yóò tún lo àwọn ọ̀rọ̀ yìí lẹ́ẹ̀kan síi; wí pé, ‘OLÚWA bùkún fún ọ, ìwọ tí ń gbé nínú òdodo, ìwọ òkè ìwà mímọ́.’"'), 'Jer$31:27': ('irugbin', None, 'Jer$31:27 "“Wò ó, ọjọ́ ń bọ̀,” ni OLÚWA wí, “nígbà tí èmi yóò gbin ilé Ísírẹ́lì àti ilé Júdà pẹ̀lú irúgbìn ọmọ ènìyàn àti ẹranko."'), 'Jer$31:28': ('ni', None, 'Jer$31:28 "Gẹ́gẹ́ bí mo ṣe ń ṣọ́ wọn, láti fàtu, àti láti wó lulẹ̀, àti láti gba ìṣàkóso, láti bàjẹ́ àti láti mú ibi wá, bẹ́ẹ̀ ni èmi yóò ṣọ́ wọn láti kọ́ àti láti gbìn,” ni OLÚWA wí."'), 'Jer$31:31': ('mu', 'mú', 'Jer$31:31 " “Ìgbà kan ń bọ̀,” ni OLÚWA wí, “tí Èmi yóò bá ilé Ísírẹ́lì àti ilé Júdà dá májẹ̀mú tuntun."'), 'Jer$32:3': ('ní', 'gba', 'Jer$32:3 "Nítorí Sedekáyà Ọba Júdà ti há a mọ́lé síbẹ̀; pé, “Kí ló dé tí ìwọ fi ń sọ àsọtẹ́lẹ̀ báyẹn? Tí o sì wí pé, ‘Báyìí ni OLÚWA wí: Èmi ń bọ̀ wá fi ìlú yìí fún Ọba Bábílónì, tí yóò sì gbà á."'), 'Jer$32:11': (None, None, 'Jer$32:11 "Mo mú ìwé tí mo fi rà á, èyí tí a di pa nípa àṣẹ àti òfin wa, àti èyí tí a kò lẹ̀."'), 'Jer$32:25': (None, 'ọwọ', 'Jer$32:25 "Ṣíbẹ̀ à ò fi ìlú náà fún àwọn ará Kálídéà. Ìwọ OLÚWA Ọba sọ fún mi pé, ‘Ra pápá náà pẹ̀lú owó fàdákà, kí o sì pe ẹlẹ́rìí.’ ”"'), 'Jer$32:28': ('fun', None, 'Jer$32:28 "Nítorí náà, báyìí ni OLÚWA wí: Èmi ṣetan láti fi ìlú yìí fún àwọn ará Kálídéà àti fún Nebukadinésárì Ọba Bábílónì ẹni tí yóò kó o."'), 'Jer$32:33': (None, None, 'Jer$32:33 "Wọ́n kọ ẹ̀yìn sími, wọ́n sì yí ojú wọn padà. Èmi kọ̀ wọ́n, síbẹ̀ wọn kò fetísílẹ̀ láti gbọ́ ẹ̀kọ́ tàbí kọbiara sí ìwà ìbàjẹ́."'), 'Jer$32:35': ('wá', 'ṣe', 'Jer$32:35 "Wọ́n kọ́ ibi gíga fún Báálì ní àfonífojì Hínómù láti fi ọmọ wọn ọkùnrin àti obìnrin rúbọ sí Mólékì. Èmi kò páláṣẹ, fún wọn, bẹ́ẹ̀ ni kò wá sí ọkàn mi pé kí wọ́n ṣe ohun ìríra wọ̀nyí tí ó sì mú Júdà ṣe."'), 'Jer$32:36': (None, None, 'Jer$32:36 "“Ìwọ ń sọ̀rọ̀ nípa ti ìlú yìí pé, ‘Pẹ̀lú idà, ìyàn àti àjàkálẹ̀-àrùn ni à ó fi wọ́n fún Ọba Bábílónì,’ ṣùgbọ́n ohun tí OLÚWA Ọlọ́run Ísírẹ́lì sọ nì yìí:"'), 'Jer$32:42': (None, 'bayi', 'Jer$32:42 "“Nítorí bayìí ni OLÚWA wí: Gẹ́gẹ́ bí èmi ti mú gbogbo ibi ńlá yìí wá sórí àwọn ènìyàn yìí, bẹ́ẹ̀ ni èmi ó mú gbogbo rere tí èmi ti sọ nípa ti wọn wá sórí wọn."'), 'Jer$32:43': ('fi fun', None, 'Jer$32:43 "Lẹ́ẹ̀kan síi, pápá yóò di rírà ní ilẹ̀ yìí tí ìwọ ti sọ pé, ‘Ohun òfò ni tí kò bá sí ọkùnrin tàbí àwọn ẹran, nítorí tí a ti fi fún àwọn ará Bábílónì.’"'), 'Jer$32:44': (None, None, 'Jer$32:44 "Wọn ó fi owó fàdákà ra oko, wọn yóò fi ọwọ́ sí ìwé, wọn ó dí i pa pẹ̀lú ẹlẹ́rìí láti ilẹ̀ Bẹ́ńjámínì àti ní ìlú kékèké tí ó yí Jérúsálẹ́mù ká àti ní ìlú Júdà àti ní ìlú ọwọ́ òkè orílẹ̀ èdè ní ìhà gúṣù olókè ilẹ̀ àti ní Gúsù, nítorí èmi ó mú ìgbékùn wọn padà wá, ni OLÚWA wí.”"'), 'Jer$33:1': (None, None, 'Jer$33:1 "Nígbà tí Jeremáyà wà nínú àgbàlá túbú, ọ̀rọ̀ Ọlọ́run tọ̀ ọ́ wá lẹ́ẹ̀kejì:"'), 'Jer$33:4': ('da', 'ni', 'Jer$33:4 Nítorí èyí ni ohun tí OLÚWA Ọlọ́run àwọn Ísírẹ́lì sọ nípa àwọn ilẹ̀ ìlú yìí àti ààfin àwọn Ọba Júdà tí ó ti wó lulẹ̀ nítorí àwọn ìdọ̀tí àti idà'), 'Jer$33:25': (None, 'ni', 'Jer$33:25 Báyìí ni OLÚWA wí: ‘Bí èmi kò bá ti da májẹ̀mú mi pẹ̀lú ọ̀sán àti òru àti pẹ̀lú òfin ọ̀run àti ayé tí kò lè yẹ̀.'), 'Jer$34:1': (None, None, 'Jer$34:1 " Nígbà tí Nebukadinésárì Ọba Bábílónì àti àwọn ọmọ ogun rẹ̀, pẹ̀lú gbogbo ìjọba àti àwọn ènìyàn ní ilẹ̀ Ọba tí ó jọba lé lórí ń bá Jérúsálẹ́mù jà, àti gbogbo àwọn ìlú tí ó yí ká, ọ̀rọ̀ yìí tọ Jeremáyà wá láti ọ̀dọ̀ OLÚWA pé:"'), 'Jer$34:4': (None, None, 'Jer$34:4 "“ ‘Síbẹ̀, gbọ́ ìlérí OLÚWA, ìwọ Sedekáyà Ọba Júdà. Èyí ni ohun tí Ọlọ́run wí nípa rẹ; ìwọ kì yóò ti ipa idà kú;"'), 'Jer$34:8': ('ní', None, 'Jer$34:8 Ọ̀rọ̀ OLÚWA sì tọ Jeremáyà wá lẹ́yìn ìgbà tí Ọba Sedekáyà ti dá májẹ̀mú pẹ̀lú gbogbo àwọn ènìyàn ní Jérúsálẹ́mù láti polongo ìtúsílẹ̀ fún àwọn ẹrú.'), 'Jer$34:10': ('ní', 'jẹ ki', 'Jer$34:10 "Nítorí náà, gbogbo àwọn aláṣẹ àti àwọn ènìyàn tí wọ́n fi ọwọ́ sí májẹ̀mú náà láti dá ẹrúkùnrin àti ẹrúbìnrin sílẹ̀ kí wọ́n sì má ṣe fi wọ́n sínú ìgbékùn mọ́. Wọ́n sì gbà, wọ́n sì jẹ́ kí wọn lọ lọ́fẹ̀ẹ́"'), 'Jer$34:11': (None, None, 'Jer$34:11 "Ṣùgbọ́n lẹ́yìn ìgbà díẹ̀, wọ́n yí ọkàn wọn padà; wọ́n sì mú àwọn ẹrú tí wọ́n ti dá sílẹ̀ padà láti tún máa sìn wọ́n."'), 'Jer$34:12': (None, None, 'Jer$34:12 Nígbà náà ni ọ̀rọ̀ OLÚWA tọ Jeremáyà wá wí pé:'), 'Jer$34:15': ('ṣe', None, 'Jer$34:15 "Láìpẹ́ yìí ẹ ti ronúpìwàdà, ẹ sì ṣe ohun tí ó tọ́ lójú mi, ẹnìkọ̀ọ̀kan yín sì ṣe ìtúsílẹ̀ fún àwọn ènìyàn. Ẹ sì tún bá mi dá májẹ̀mú ní àwọn ilé tí a ti ń pe orúkọ mi."'), 'Jer$34:17': (None, 'ni', 'Jer$34:17 "“Nítorí náà báyìí ni OLÚWA wí: Ẹ̀yin kò fetí sími nípa ọ̀rọ̀ òmìnira láàrin ẹ̀gbọ́n sí àbúrò, láàrin ẹnìkan sí èkejì rẹ̀. Wò ó, èmi yóò kéde òmìnira fún un yín ni OLÚWA wí. Sí idà, sí àjàkálẹ̀-àrùn, àti sí ìyàn, èmi ó sì fi yín fún ìwọ̀sí ní gbogbo ìjọba ilẹ̀ ayé."'), 'Jer$34:18': ('ní', None, 'Jer$34:18 "Èmi ó ṣe àwọn ọkùnrin náà tí ó da májẹ̀mú mi, tí kò tẹ̀lé májẹ̀mú tí wọ́n ti dá níwájú mi bí ẹgbọ̀rọ màlúù tí wọ́n gé sí méjì, tí ó sì kọjá láàrin ìpín méjì náà."'), 'Jer$34:21': (None, 'ọwọ', 'Jer$34:21 "“Sédékáyà Ọba àti àwọn ìjòyè rẹ̀ ní Júdà ni èmí yóò fi lé àwọn ọ̀ta wọn lọ́wọ́, àti àwọn tí ń wá ẹ̀mí wọn àti lé ọwọ́ ogun Bábílónì tí ó ti lọ kúrò lọ́dọ̀ yín."'), 'Jer$34:22': (None, 'ṣe', 'Jer$34:22 "Wò ó èmi ó pàṣẹ ni OLÚWA wí, èmi ó sì mú wọn padà sí ìlú yìí, wọn ó sì baà jà, wọn ó sì kóo, wọn ó fi iná jóo. Èmi ó sì sọ ìlú Júdà dí ahoro.”"'), 'Jer$35:6': (None, None, 'Jer$35:6 "Ṣùgbọ́n wọ́n wí pé, “Àwa kì yóò mu ọtí wáìnì nítorí Jónádábù ọmọ Rékábù, baba wa pàṣẹ fún wa pé: ‘Ẹ̀yin kò gbọdọ̀ mu ọtí wáìnì ẹ̀yin àti àwọn ọmọ yín láéláé"'), 'Jer$35:11': ('lọ', None, 'Jer$35:11 "Ó sì se, nígbà tí Nebukadinésárì Ọba Bábílónì gòkè wá sí ilẹ̀ náà; àwa wí pé, ‘Ẹ wá, ẹ jẹ́ kí a lọ sí Jérúsálẹ́mù, nítorí ìbẹ̀rù ogun àwọn ara Síríà.’ Bẹ́ẹ̀ ni àwa sì ń gbé Jérúsálẹ́mù.”"'), 'Jer$35:15': ('fi fun', 'ni', 'Jer$35:15 "Èmi rán gbogbo àwọn ìránṣẹ́ àti àwọn wòlíì mí sí yín pẹ̀lú wí pé, “Ẹ yípadà nísisiyìí kúrò ní ọ̀nà búburú yín, kí ẹ sì tún ìṣe yín ṣe sí rere. Kí ẹ ma sì ṣe bọ òrìṣà tàbí sìn wọ́n; ẹ̀yin ó sì máa gbé ní ilẹ̀ náà tí mo fi fún un yín àti fún àwọn baba ńlá yín.” Ṣùgbọ́n ẹ̀yin kò fetí sími."'), 'Jer$35:18': (None, 'ofin', 'Jer$35:18 "Nígbà náà ni Jeremáyà wí fún ìdílé Rékábù pé, “Báyìí ni OLÚWA Ọlọ́run àwọn ọmọ ogun Ọlọ́run Ísírẹ́lì wí pé: ‘Nítorí tí ẹ̀yin gba òfin Jónádábù baba yín, tí ẹ sì pa gbogbo rẹ̀ mọ́, tí ẹ sì ṣe gẹ́gẹ́ bí èyí tí ó palásẹ fún un yín.’"'), 'Jer$36:3': ('aiṣedede', 'ibi', 'Jer$36:3 "Ó lè jẹ́ wí pé ilé Júdà yóò gbọ́ gbogbo ibi tí mo ti pinnu láti ṣe fún wọn; kí wọn kí ó sì yípadà, olúkúlùkù wọn kúrò ní ọ̀nà búburú rẹ̀. Kí èmi kí ó lè dárí àìṣedédé àti ẹ̀ṣẹ̀ wọn jìn wọ́n.”"'), 'Jer$36:5': (None, None, 'Jer$36:5 "Nígbà náà ni Jeremáyà wí fún Bárúkì pé, “A ṣé mi mọ́! Èmi kò lè lọ sí ilé OLÚWA."'), 'Jer$36:14': (None, 'ka', 'Jer$36:14 "Gbogbo àwọn ìjòyè sì rán Jéhúdù ọmọ Métamáyà ọmọ Sélémáyà ọmọ Kúsì sí Bárúkì wí pé, mú ìwé kíká náà ní ọwọ́ rẹ láti inú èyí tí ìwọ kà ní etí àwọn ènìyàn; kí o si wá. Nígbà náà ni Bárúkì ọmọ Nétayà wá sí ọ̀dọ̀ wọn pẹ̀lú ìwé kíká ní ọwọ́ rẹ̀."'), 'Jer$36:15': (None, 'ka', 'Jer$36:15 "Wọ́n sì wí fún pé, “Jòkòó, jọ̀wọ́ kà á sí etí wa!” Nígbà náà ni Bárúkì sì kàá ní etí wọn."'), 'Jer$36:23': (None, None, 'Jer$36:23 "Nígbà tí Jéhúdù ka ojú ìwé mẹ́ta sí mẹ́rin nínú ìwé náà, Ọba fi ọ̀bẹ gé ìwé, ó sì sọ sínú iná tí ó wà nínú ìdáná, títí tí gbogbo ìwé náà fi jóná tán."'), 'Jer$36:31': ('aiṣedede', 'ibi', 'Jer$36:31 "Èmi ó sì jẹ òun àti irú ọmọ rẹ̀, ìránṣẹ́ rẹ̀ níyà nítorí àìṣedédé wọn; èmi ó sì mú gbogbo ibi tí èmi ti sọ sí wọn wá sórí wọn, àti sórí àwọn olùgbé Jérúsálẹ́mù àti àwọn ènìyàn Júdà, nítorí tí wọn kò fetísí mi.’ ”"'), 'Jer$37:10': (None, None, 'Jer$37:10 "Kódà tó bá ṣe pé wọn yóò ṣẹ́gun àwọn ọmọ ogun Bábílónì tí ń gbógun tì yín àti àwọn tí ìjàǹbá ṣe, tí wọ́n ti fi sílẹ̀ ní ibùdó wọn; wọn yóò jáde láti jó ìlú náà kanlẹ̀.”"'), 'Jer$37:11': (None, None, 'Jer$37:11 Lẹ́yìn tí àwọn ọmọ ogun Bábílónì ti kúrò ní Jérúsálẹ́mù nítorí àwọn ọmọ ogun Fáráò.'), 'Jer$37:21': ('fun', None, 'Jer$37:21 "Ọba Sedekáyà wá pàṣẹ pé kí wọ́n fi Jeremáyà sínú àgbàlá àwọn ẹ̀ṣọ́, àti kí wọn sì fún ní àkàrà ní ọjọ́ kọ̀ọ̀kan títí tí àkàrà yóò fi tán; bẹ́ẹ̀ ni Jeremáyà wà nínú àgbàlá náà."'), 'Jer$38:3': (None, 'ọwọ', 'Jer$38:3 Àti pé èyí ni ohun tí OLÚWA wí: ‘Ìdánilójú wà wí pé a ó fi ìlú náà lé ọwọ́ àwọn ọmọ ogun Bábílónì; tí yóò sì kó wa nígbékùn.’ ”'), 'Jer$38:4': (None, 'eniyan', 'Jer$38:4 "Nígbà náà ni àwọn ìjòyè wí fún Ọba pé, “Ó yẹ kí a pa ọkùnrin yìí; ó ń kó ìrẹ̀wẹ̀sì bá àwọn ọmọ ogun tó kù nínú ìlú pẹ̀lú gbogbo àwọn ènìyàn nípa ohun tí ó ń sọ fún wọn. Ọkùnrin yìí kò fẹ́ ire fún àwọn ènìyàn bí kò ṣe ìparun.”"'), 'Jer$38:7': (None, None, 'Jer$38:7 "Ṣùgbọ́n, Ebedimélékì, ará Kúṣì ìjòyè nínú ààfin Ọba gbọ́ pé wọ́n ti ju Jeremáyà sínú àmù. Nígbà tí Ọba jókòó lẹ́nubodè Bẹ́ńjámínì."'), 'Jer$38:14': ('mu', None, 'Jer$38:14 "Nígbà náà ni Ọba Sedekáyà ránṣẹ́ pe, Jeremáyà òjíṣẹ́ Ọlọ́run àti láti mú un wá sí ẹnubodè kẹta nílé Ọlọ́run. Ọba sì sọ fún Jeremáyà pé, “Èmi yóò bi ọ́ ní ohun kan; má sì ṣe fi ohun kan pamọ́ fún mi.”"'), 'Jer$38:16': ('fun', None, 'Jer$38:16 "Ṣùgbọ́n Ọba Sedekáyà búra ní ìkọ̀kọ̀ fún Jeremáyà wí pé, “Dájúdájú bí OLÚWA ti ń bẹ, ẹni tí ó fún wa ní ẹ̀mí, èmi kò ní pa ọ́ tàbí fà ọ́ fún àwọn tó ń lépa ẹ̀mí rẹ.”"'), 'Jer$38:17': ('lọ', None, 'Jer$38:17 "Nígbà náà ni Jeremáyà sọ fún Sedekáyà pé, “Èyí ni ohun tí OLÚWA Ọlọ́run alágbára, Ọlọ́run Ísírẹ́lì wí: ‘Àyàfi bí o bá jọ̀wọ́ ara rẹ fún àwọn olóyè Ọba Bábílónì, a ó dá ẹ̀mí rẹ sí àti pé ìlú yìí kò ní di jíjó níná; ìwọ àti ilé rẹ yóò sì wà láàyè."'), 'Jer$38:23': ('ya', 'mú', 'Jer$38:23 "“Wọn yóò kó àwọn ìyàwó àti ọmọ rẹ wá sí Bábílónì. Ìwọ gan-an kò ní bọ́ níbẹ̀, Ọba Bábílónì yóò mú ọ, wọn yóò sì jó ìlú yìí kanlẹ̀.”"'), 'Jer$38:25': (None, None, 'Jer$38:25 "Tí àwọn ìjòyè bá mọ̀ pé mo bá ọ sọ̀rọ̀, tí wọ́n bá wá bá ọ wí pé, ‘Sọ fún wa ohun tí o bá Ọba sọ tàbí ohun tí Ọba sọ fún ọ; má ṣe fi pamọ́ fún wa tàbí kí a pa ọ́,’"'), 'Jer$39:4': ('lọ', None, 'Jer$39:4 "Nígbà tí Sedekáyà Ọba Júdà àti àwọn ọmọ ogun rí wọn, wọ́n sá, wọ́n kúrò ní ìlú ní alẹ́, wọ́n gba ọ̀nà ọgbà Ọba lọ láàrin ẹnubodè pẹ̀lú odi méjì, wọ́n dojúkọ aginjù."'), 'Jer$39:5': ('mu', 'ilẹ', 'Jer$39:5 "Ṣùgbọ́n nígbà tí àwọn ọmọ ogun Bábílónì lé wọn, wọ́n bá Sedekáyà láàrin ihà Jẹ́ríkò. Wọ́n mú un ní ìgbékùn, wọ́n sì mú u tọ Nebukadinésárì Ọba Bábílónì àti Rábílà ní ilẹ̀ Hámátì, níbi tí wọ́n ti ṣe ìdájọ́ rẹ̀."'), 'Jer$39:13': (None, None, 'Jer$39:13 "Bẹ́ẹ̀ gẹ́gẹ́ pẹ̀lú, Nebusárádánì olùdarí ọmọ ogun, Nebusasibánì, olóyè àgbà, Nágálì Sárésà, olóyè àgbà àti gbogbo àwọn olóyè ọba,"'), 'Jer$39:17': (None, 'ọwọ', 'Jer$39:17 Ṣùgbọ́n Èmi yóò gbà ọ́ lọ́jọ́ náà ni OLÚWA wí. A kò ní fi ọ́ lé ọwọ́ àwọn tí o bẹ̀rù.'), 'Jer$40:1': ('ní', None, 'Jer$40:1 Ọ̀rọ̀ náà sì tọ Jeremáyà wá láti ọ̀dọ̀ OLÚWA lẹ́yìn tí Nebukadinésárì balógun àwọn ẹ̀ṣọ́ ti tú u sílẹ̀ ní Rámà. Ó rí Jeremáyà tí a fi ẹ̀wọ̀n dè láàrin gbogbo àwọn tí wọ́n mú ní Jérúsálẹ́mù àti Júdà. Wọ́n kó lọ sí ilẹ̀ àjèjì Bábílónì.'), 'Jer$40:3': ('ṣe', None, 'Jer$40:3 "Nísinsìn yìí, OLÚWA ti mú un jáde; ó ti ṣe gẹ́gẹ́ bí ó ti sọ pé òun yóò ṣe. Gbogbo èyí ṣẹlẹ̀ nítorí wí pé ẹ̀yin ènìyàn ṣẹ̀ sí OLÚWA, àti pé ẹ kò gbọ́rọ̀ sí àṣẹ rẹ̀."'), 'Jer$40:10': (None, 'ni', 'Jer$40:10 "Èmi fúnra mi yóò dúró ní Mísípà láti ṣojú yín níwájú Bábílónì tí wọ́n tọ̀ wá wá. Ṣùgbọ́n ẹ̀yin ni yóò máa kórè ọtí wáìnì, èso igi àti òróró; kí ẹ sì kó wọn sí inú àpò àpamọ́ yín; kí ẹ̀yin sì máa gbé ní ilẹ̀ tí ẹ ti gbà.”"'), 'Jer$40:15': ('lọ', None, 'Jer$40:15 "Nígbà náà ni Jóhánánì ọmọkùnrin Káréà sọ ní ìkọ̀kọ̀ fún Jedáláyà ní Mísípà pé, “Jẹ́ kí èmi lọ pa Íṣímáẹ́lì ọmọkùnrin Netanáyà, ẹnikẹ́ni kò sì ní mọ èyí. Kí ni ìdí rẹ̀ tí yóò ṣe mú ẹ̀mí rẹ, tí o sì ṣe fẹ́ mú àwọn Júù tí ó yí ọ ká túká, kí ìyókù Júdà sì parun?”"'), 'Jer$41:4': (None, None, 'Jer$41:4 Ní ọjọ́ kejì tí wọ́n pa Jedaláyà kí ó tó di wí pé ẹnikẹ́ni mọ̀'), 'Jer$41:6': ('lọ', None, 'Jer$41:6 "Ísímáẹ́lì ọmọkùnrin Nétanáyà jáde kúrò láti Mísípà láti lọ pàdé wọn. Ó sì ń sunkún bí ó ti ṣe ń lọ, nígbà tí ó pàdé wọn, ó wí pé, “Ẹwá sọ́dọ̀ Jedaláyà ọmọkùnrin Álíkámù.”"'), 'Jer$41:16': ('mu', 'ilẹ', 'Jer$41:16 "Lẹ́yìn náà Jóhánánì ọmọkùnrin Káréà àti gbogbo àwọn ọmọ ogun tí wọn wà pẹ̀lú rẹ̀ sì kó gbogbo àwọn tí ó kù ní Mísípà; àwọn tí ó ti rí gbà lọ́wọ́ Ísímáẹ́lì ọmọkùnrin Nétanáyà; lẹ́yìn ìgbà tí ó ti pa Gédáláyà ọmọ Álíkámù. Àwọn ọmọ ogun, àwọn obìnrin, àwọn ọmọdé àti àwọn olórí ilé ẹjọ́ tí ó ti kó wa láti Gíbíónì."'), 'Jer$42:1': ('wá', None, 'Jer$42:1 Nígbà náà ní gbogbo àwọn ọmọ ogun àti Jóhánánì ọmọkùnrin Káréà àti Jésánáyà omọkùnrin Hòsáyà àti kékeré títí dé orí ẹni ńlá wá.'), 'Jer$42:6': ('dara', None, 'Jer$42:6 "Ìbá à ṣe rere, ìbá à ṣe búburú, àwa yóò gbọ́ràn sí OLÚWA Ọlọ́run wa; èyí tí àwa ń rán ọ sí; Kí ó ba à lè dára fún wa. Nítorí pé àwa yóò gbọ́ràn sí OLÚWA Ọlọ́run wa.”"'), 'Jer$42:8': ('ani', None, 'Jer$42:8 Nígbà náà ni a ó pa Jóhánánì ọmọkùnrin Káréà àti gbogbo àwọn ọmọ-ogun tí wọ́n wà pẹ̀lú rẹ̀; àti gbogbo àwọn ènìyàn láti orí ẹní tí ó kéré dé orí ẹni ńlá.'), 'Jer$42:11': (None, None, 'Jer$42:11 "Ẹ má ṣe bẹ̀rù Ọba Bábílónì tí ẹ̀yin ń bẹ̀rù, báyìí ẹ má ṣe bẹ̀rù rẹ̀ ni OLÚWA wí; nítorí tí èmi wà pẹ̀lú yín láti pa yín mọ́ àti láti gbàyín ní ọwọ́ rẹ̀."'), 'Jer$42:12': (None, None, 'Jer$42:12 "Èmi yóò fi àánú hàn sí i yín, kí ó lè ṣàánú fún un yín, kí ó sì mú un yín padà sí ilẹ̀ yín.’"'), 'Jer$42:13': (None, None, 'Jer$42:13 "“Àmọ́ ṣá, bí ẹ̀yin bá wí pé, ‘Àwa kò ní gbé ilẹ̀ yìí,’ ẹ̀yin ti ṣe àìgbọ́ràn sí OLÚWA Ọlọ́run yín."'), 'Jer$42:17': (None, 'kú', 'Jer$42:17 "Nítorí náà, gbogbo àwọn tí ó ti pinnu láti ṣe àtìpó ní Éjíbítì ni yóò ti ipa idà kú, ìyàn àti àjàkálẹ̀-àrùn kò sì ní jẹ́ kí ẹníkẹ́ni nínú wọn yè tàbí sá àsálà nínú ibi tí èmi yóò mú wá sórí wọn.’"'), 'Jer$42:18': (None, 'egún', 'Jer$42:18 "Báyìí ni ohun tí OLÚWA àwọn ọmọ ogun Ọlọ́run Ísírẹ́lì wí: ‘Gẹ́gẹ́ bí èmi ti da ìbínú àti ìrunú síta sórí àwọn tí ó ń gbé ní Jérúsálẹ́mù, bẹ́ẹ̀ ni ìrunú mi yóò dà sita sórí yín, nígbà tí ẹ̀yin bá lọ sí Éjíbítì. Ẹ̀yin ó sì di ẹni ègún àti ẹní ẹ̀gàn àti ẹ̀sín, ẹ̀yin kì yóò sì rí ibí yìí mọ́.’"'), 'Jer$42:22': ('lọ', 'kú', 'Jer$42:22 "Ǹjẹ́ nísinsìnyìí ẹ mọ èyí dájú pé: Ẹ̀yin yóò kú nípa idà, ìyàn àti àjàkálẹ̀-àrùn níbikíbi tí ẹ̀yin bá fẹ́ lọ láti ṣe àtìpó.”"'), 'Jer$43:1': (None, None, 'Jer$43:1 Nígbà tí Jeremáyà párí sísọ ọ̀rọ̀ tí OLÚWA Ọlọ́run rán an sí wọn tan.'), 'Jer$43:8': (None, None, 'Jer$43:8 Ní Táfánésì ọ̀rọ̀ OLÚWA tọ Jeremáyà wá:'), 'Jer$43:12': ('ina', 'ina', 'Jer$43:12 "Yóò dá, iná sun Tẹ́ḿpìlì àwọn òrìṣà Éjíbítì, yóò sun Tẹ́ḿpìlì àwọn òrìṣà Éjíbítì, yóò sì mú wọn lọ ìgbèkùn. Gẹ́gẹ́ bí olùṣọ́ àgùntàn, yóò ró aṣọ rẹ̀ mọ́ra, bẹ́ẹ̀ gẹ́gẹ́ ni yóò ró Éjíbítì òun yóò sì lọ kúrò níbẹ̀ ní àlàáfíà"'), 'Jer$44:1': (None, None, 'Jer$44:1 "Ọ̀rọ̀ OLÚWA tọ Jeremáyà wá nípa àwọn Júù tí ń gbé ní ìsàlẹ̀ Éjíbítì ní Mígídò, Táfánásì àti Mémífísì àti ní apá òkè Íjíbìtì:"'), 'Jer$44:6': (None, None, 'Jer$44:6 "Fún ìdí èyí, ìbínú gbígbóná mi ni èmi yóò yọ sí àwọn ìlú Júdà àti òpópó Jérúsálẹ́mù àti sísọ wọ́n di ìparun bí ó ṣe wà lónìí yìí."'), 'Jer$44:7': (None, None, 'Jer$44:7 "“Báyìí tún ni OLÚWA Ọlọ́run àwọn ọmọ ogun Ísírẹ́lì wí: Kí ló dé tí ẹ fi ń mu ibi ńlá yìí wá sí orí ara yín nípa yíyapa kúrò lára Júdà ọkùnrin àti obìnrin, ọmọdé àti èwe, tí ẹ kò sì ku ọ̀kankan?"'), 'Jer$44:9': ('ni', None, 'Jer$44:9 "Ṣé ẹ̀yin ti gbàgbé ibi tí àwọn bàbá ńlá yín àti àwọn Ọba; àwọn ayaba Júdà, àti àwọn ibi tí ẹ ti ṣe àti àwọn ìyàwó yín ní ilẹ̀ Júdà àti ní òpópó Jérúsálẹ́mù?"'), 'Jer$44:12': (None, None, 'Jer$44:12 "Èmi yóò sì mú àwọn èérún tí ó kù ní Júdà, tí wọ́n ṣetán láti lọ Éjíbítì. Wọn yóò ṣubú pẹ̀lú idà tàbí kí wọn kú pẹ̀lú ìyàn láti orí ọmọdé títí dé àgbà ni wọn yóò kú láti ọwọ́ ìyàn tàbí idà. Wọn yóò di ẹni ìfiré àti ìparun, ẹni ẹ̀kọ̀ àti ẹni ẹ̀gàn."'), 'Jer$44:15': (None, 'eniyan', 'Jer$44:15 "Lẹ́yìn èyí, gbogbo àwọn ọkùnrin tí ẹ bá mọ̀ pé, ìyàwó wọn sun tùràrí sí àwọn òrìṣà pẹ̀lú àwọn obìnrin tí ó bá wá àwọn ènìyàn púpọ̀, pàápàá àwọn ènìyàn tí wọ́n ń gbé ní òkè àti ìsàlẹ̀ Éjíbítì, bẹ́ẹ̀ ni a wí fún Jeremáyà."'), 'Jer$44:17': ('ní', None, 'Jer$44:17 "Dájúdájú; à ó ṣe gbogbo nǹkan tí a sọ pé à ò ṣe: A ó sun tùràrí sí ayaba ọ̀run; à ó sì da ohun mímu sílẹ̀ gẹ́gẹ́ bí ẹbọ sí àwa àti àwọn baba wa, àwọn Ọba àti àwọn aláṣẹ ti ṣe ní àwọn ìlú Júdà àti ni àwọn ìgboro Jérúsálẹ́mù. Nígbà naà àwa ní ounjẹ púpọ̀ a sì ṣe rere a kò sì rí ibi"'), 'Jer$44:18': ('run', 'ni', 'Jer$44:18 "Ṣùgbọ́n láti ìgbà tí a ti dáwọ́ tùràrí sísun sí ayaba ọrun sílẹ̀ àti láti da ẹbọ ohun mímu fún un, àwa ti ṣaláìní ohun gbogbo, a sì run nípa idà àti nípa ìyàn.”"'), 'Jer$44:20': (None, None, 'Jer$44:20 "Wàyí o, Jeremáyà sọ fún àwọn ọ̀pọ̀ ènìyàn náà, ọkùnrin àti obìnrin, tí wọn sì ń dáhùn pé,"'), 'Jer$44:22': ('ni', None, 'Jer$44:22 "Nígbà tí Ọlọ́run kò lè fi ara da ìwà búburú yín àti àwọn nǹkan ìbínú gbogbo tí ẹ ṣe, ilẹ̀ yín sì di ohun ìfiré àti ìkọ̀sílẹ̀, láìsí olùgbé gẹ́gẹ́ bí ó ti ṣe wà lónìí."'), 'Jer$44:29': ('fun', None, 'Jer$44:29 “ ‘Èyí ni yóò jẹ́ àmì fún un yín pé èmi yóò fi ìyà jẹ yín níbi tí OLÚWA ti sọ láti lè jẹ́ kí ẹ mọ̀ pé ìjìyà mi tí mo sọ pé ẹ̀ ó jẹ yóò ṣẹ.’'), 'Jer$46:3': (None, None, 'Jer$46:3 "“Pèsè ọ̀kọ̀ rẹ sílẹ̀, èyí tí ó tóbi, àti èyí tí ó kéré, kí o sì yan lọ síta fún ogun!"'), 'Jer$46:9': ('tẹ', None, 'Jer$46:9 "Ẹ gòkè wá ẹ̀yin ẹṣin, ẹ sì sáré kíkankíkan ẹ̀yin kẹ̀kẹ́. Kí àwọn alágbára túbọ̀ tẹ̀síwájú, ẹ̀yin ọkùnrin Kúṣì àti Pútì tí ń gbé ọ̀kọ̀; àti ẹ̀yin ọkùnrin Lìdíà tí ń fa ọrun."'), 'Jer$46:16': ('ṣubu', 'ṣubu', 'Jer$46:16 "Wọn yóò máa ṣubú léraléra wọn yóò máa ṣubú lu ara wọn. Wọn yóò sọ wí pé, ‘Ẹ dìde, ẹ jẹ́ kí a padà sí ọ̀dọ̀ àwọn ènìyàn wa àti ilẹ̀ ìbí wa, kúrò níbi idà àwọn aninilára.’"'), 'Jer$46:18': ('ni', None, 'Jer$46:18 "“Bí èmi ti wà láàyè,” ni Ọba, ẹni tí orúkọ rẹ̀ ń jẹ́ OLÚWA àwọn ọmọ ogun wí pé, “nítòótọ́ gẹ́gẹ́ bí Tábórì láàrin àwọn òkè àti gẹ́gẹ́ bi Kámẹ́lì lẹ́bàá òkun bẹ́ẹ̀ ni òun yóò dé."'), 'Jer$46:21': (None, None, 'Jer$46:21 "Àwọn jagunjagun rẹ̀ dàbí àbọ́pa màlúù. Àwọn pẹ̀lú yóò yípadà, wọn ó sì jùmọ̀ sá, wọn kò ní le dúró, Nítorí tí ọjọ́ ibi ńbọ̀ lórí wọn àsìkò láti jẹ wọ́n níyà."'), 'Jer$46:27': (None, None, 'Jer$46:27 "“Má bẹ̀rù, ìwọ Jákọ́bù ìránṣẹ́ mi, má fòyà, ìwọ Ísírẹ́lì. Dájúdájú èmi ó gbà ọ́ láti ọ̀nà jínjìn, àwọn ọmọ ọmọ rẹ ní ilẹ̀ ìgbèkùn. Jákọ́bù yóò tún ní àlàáfíà àti ààbò, kò sì sí ẹni tí yóò dẹ́rù bà á."'), 'Jer$46:28': ('ni', 'ṣe', 'Jer$46:28 "Má bẹ̀rù, Jákọ́bù ìránṣẹ́ mi, nítorí pé mo wà pẹ̀lú rẹ,” bẹ́ẹ̀ ni OLÚWA wí. “Bí mo tilẹ̀ pa gbogbo orílẹ̀ èdè run, láàrin àwọn tí mo fọ́n yín ká sí. Èmi kò ní run yín tán. Èmi yóò jẹ ọ́ níyà lórí òdodo, èmi kò ní fi ọ́ sílẹ̀ láìjẹ ọ́ níyà.”"'), 'Jer$47:2': (None, None, 'Jer$47:2 "Báyìí ni OLÚWA wí: “Wo bí omi ti ń ru sókè ní àríwá, wọn ó di odò tí ń bo bèbè mọ́lẹ̀. Wọn ò ní borí ilẹ̀ àti ohun gbogbo tó wà lórí rẹ̀, ìlú àti àwọn tó ń gbé nínú wọn. Àwọn ènìyàn yóò kígbe; gbogbo ẹni tó ń gbé lórí ilẹ̀ yóò pohùnréré ẹkún"'), 'Jer$48:2': (None, 'ni', 'Jer$48:2 "Móábù kò ní ní ìyìn mọ́, ní Héṣíbónì ni wọn ó pète ìparun rẹ̀, ‘Wá, kí a pa orílẹ̀ èdè náà run.’ Àti ẹ̀yin òmùgọ̀ ọkùnrin pàápàá ni a ó pa lẹ́nu mọ́, a ó fi idà lé e yín."'), 'Jer$48:4': (None, None, 'Jer$48:4 Móábù yóò di wíwó palẹ̀; àwọn ọmọdé rẹ̀ yóò kígbe síta.'), 'Jer$48:20': (None, None, 'Jer$48:20 "Ojú ti Móábù nítorí tí a wó o lulẹ̀, ẹ hu, sì kígbe síta! Ámónì kéde pé a pa Móábù run."'), 'Jer$48:33': (None, 'ni', 'Jer$48:33 "Ayọ̀ àti ìdùnnú ti kúrò nínú ọgbà àjàrà àti oko Móábù, mo dá ọwọ́ ṣíṣàn ọtí wáìnì dúró lọ́dọ̀ olùfúnni; kò sí ẹni tí ó ń tẹ̀ wọ́n pẹ̀lú igbe ayọ̀ Bí ó tilẹ̀ jẹ́ pé wọn kì í ṣe ti ayọ̀. "'), 'Jer$48:44': ('ya', 'mú', 'Jer$48:44 "“Ẹnikẹ́ni tí ó bá sá fún ẹ̀rù yóò ṣubú sínú ọ̀fìn ẹnikẹ́ni tí o bá jáde síta nínú ọ̀fìn ní à ó mú nínú okùn dídè nítorí tí èmi yóò mú wá sórí Móábù àní ọdún Ìjìyà rẹ,” ní OLÚWA wí. "'), 'Jer$48:45': ('ina', 'ina', 'Jer$48:45 “Ní abẹ́ òjìji Hésíbónì àwọn tí ó sá dúró láìní olùrànlọ́wọ́ nítorí iná ti jáde wá láti Hésíbónì àti ọwọ́ iná láti àárin Síónì sì jó iwájú orí Móábù run àti agbárí àwọn ọmọ àlùfáà.'), 'Jer$49:3': (None, 'lọ', 'Jer$49:3 "“Pohùnréré ẹkún ìwọ Hésíbónì nítorí Áì tí rún, kígbe jáde ẹ̀yin ènìyàn Rábà, ẹ wọ aṣọ ọ̀fọ̀ kí ẹ sì ṣọ̀fọ̀. Ẹ sáré sókè sódò nínú ọgbà nítorí Mákómù yóò lọ sí ìgbèkùn pẹ̀lú àwọn àlùfáà àti ìjòyè rẹ̀."'), 'Jer$49:10': (None, 'ni', 'Jer$49:10 "Ṣùgbọ́n èmi yóò tu Ísọ̀ sí ìhòòhò èmi kò ní bo ibi ìkọ̀kọ rẹ nítorí kí o máa baà fi ara rẹ pamọ́. Àwọn ọmọ rẹ, ẹbí rẹ àti àwọn ará ilé rẹ yóò parun. Wọn kò sì ní sí mọ́."'), 'Jer$49:20': (None, None, 'Jer$49:20 "Nítorí náà, ẹ gbọ́ ohun tí OLÚWA ní fún Édómù, ohun tí ó ní ní pàtàkì fún àwọn tí ń gbé ní Témánì. Àwọn ọ̀dọ́ àgbò ni à ó lé jáde. Pápá oko wọn ni yóò run nítorí wọn."'), 'Jer$49:24': (None, 'ni', 'Jer$49:24 "Dámásíkù di aláìlera, ó pẹ̀yindà láti sálọ, ìwárìrì sì dé bá a, ìbẹ̀rù àti ìrora dì í mú, ìrora bí ti obìnrin tí ó wà ní ipò ìrọbí."'), 'Jer$49:27': ('ina', 'ina', 'Jer$49:27 "“Èmi yóò fi iná sí odi Dámásíkù, yóò sì jó gbọ̀ngàn Bẹni-hádádì run.”"'), 'Jer$49:30': (None, None, 'Jer$49:30 "“Sálọ kíákíá, dúró nínú ihò ìwọ tí ò ń gbé Ásórì,” báyìí ni OLÚWA wí. “Nebukadinésárì Ọba Bábílónì ti dojú ìjà kọ ọ́. "'), 'Jer$49:31': ('ni', 'gba', 'Jer$49:31 "“Dìde kí o sì dojú ìjà kọ orílẹ̀ èdè kan pẹ̀lú ìrọ̀rùn, èyí tí ó dúró pẹ̀lú ìgboyà;” báyìí ní OLÚWA wí. “Orílẹ̀ èdè tí kò ní yálà gbàgede tàbí irin, àwọn ènìyàn re ń dágbé."'), 'Jer$49:37': (None, 'ni', 'Jer$49:37 "Èmi yóò kẹ́gàn Élámù lójú àwọn ọ̀tá rẹ̀ àti lọ́dọ̀ àwọn tí wọ́n jọ ń gbé. Èmi yóò mú ibi wá sí orí wọn, pàápàá ìbínú gbígbóná mi;” bẹ́ẹ̀ ni OLÚWA wí. “Èmi yóò lé wọn pẹ̀lú idà, di ìgbà tí èmi yóò rẹ́yìn wọn."'), 'Jer$50:3': (None, None, 'Jer$50:3 Àwọn ìlú ní apá àríwá yóò sì máa gbógun tì wọ́n. Gbogbo àwọn ènìyàn àti ẹranko ni yóò sá kúrò ní ìlú yìí.'), 'Jer$50:6': (None, 'ni', 'Jer$50:6 "“Àwọn ènìyàn mi ti jẹ́ àgùntàn tí ó sọnù, àwọn olùṣọ́ àgùntàn wọn ti jẹ́ kí wọn sìnà, wọ́n ti jẹ́ kí wọn rìn lórí òkè wọ́n ti lọ láti orí òkè ńlá dé òkè kékeré, wọn ti gbàgbé ibùsùn wọn."'), 'Jer$50:14': (None, None, 'Jer$50:14 "“Dúró sí àyè rẹ ìwọ Bábílónì àti gbogbo ẹ̀yin tí ẹ ta ọfà náà. Ẹ ta ọfà náà síi, nítorí ó ti sẹ̀ sí OLÚWA."'), 'Jer$50:15': (None, None, 'Jer$50:15 "Kígbe mọ-ọn ní gbogbo ọ̀nà! Ó tẹríba, òpó rẹ̀ sì yẹ̀, níwọ̀n ìgbà tí ó ti jẹ́ wí pé èyí ni ìgbẹ̀san OLÚWA, gbẹ̀san lára rẹ̀. Ṣe síi gẹ́gẹ́ bí ó ti ṣe sí àwọn ẹlòmìíràn."'), 'Jer$50:24': (None, 'ni', 'Jer$50:24 "Mo dẹ pàkúté sílẹ̀ fún ọ ìwọ Bábílónì, kí o sì tó mọ ohun tí ó ń ṣẹlẹ̀, o ti kó sínú rẹ̀. A mú ọ nítorí pé o tako OLÚWA."'), 'Jer$50:25': (None, 'iṣẹ', 'Jer$50:25 "OLÚWA ti kó àwọn ohun èlò ìjà rẹ̀ jáde, nítorí pé OLÚWA Ọlọ́run ọmọ ogun ti ṣiṣẹ́ ní ilẹ̀ Bábílónì."'), 'Jer$50:29': (None, None, 'Jer$50:29 "“Pe ọ̀pọ̀lopọ̀ ènìyàn, àní gbogbo tafàtafà, sórí Bábílónì, ẹ doti iyikakiri, ma jẹ́ ki ẹnikẹ́ni sálà. Sán fún un gẹ́gẹ́ bí iṣẹ́ rẹ̀ gẹ́gẹ́ bi gbógbó èyí ti o ti ṣé, ẹ ṣe bẹ́ẹ̀ síi, nítorí tí ó ti gberaga sí OLÚWA, sí Ẹni-MÍMỌ̀ Ísirẹ́lì."'), 'Jer$50:32': (None, 'ina', 'Jer$50:32 "Onígbéraga yóò kọsẹ̀, yóò sì ṣubú, kì yóò sì sí ẹni tí yóò gbé dìde. Èmi yóò tan iná ní ìlú náà, èyí tí yóò sì jo run.”"'), 'Jer$50:43': ('mu', None, 'Jer$50:43 "Ọba Bábílónì ti gbọ́ ìròyìn nípa wọn, ọwọ́ wọn sì rọ, ìrora mú wọn bí obìnrin tí ó ń rọbí."'), 'Jer$50:45': (None, None, 'Jer$50:45 "Nítorí náà, gbọ́ ohun tí OLÚWA sọ nípa Bábílónì ẹni tí ó gbìmọ̀ lòdì sí Bábílónì; n ó sì pa agbo ẹran wọn run."'), 'Jer$51:3': (None, None, 'Jer$51:3 "Má ṣe jẹ́ kí tafàtafà yọ ọfà rẹ̀ jáde tàbí kí o di ìhámọ́ra rẹ̀; má ṣe dá àwọn ọdọ́mọkùnrin sí, pátapáta ni kí o pa àwọn ọmọ ogun rẹ̀."'), 'Jer$51:6': (None, 'ṣe', 'Jer$51:6 " “Sá kúrò ní Bábílónì! Sá àsálà fún ẹ̀mí rẹ! Má ṣe ṣègbé torí ẹ̀ṣẹ̀ rẹ. Àsìkò àti gbẹ̀san Ọlọ́run ni èyí, yóò sán fún òun gẹ́gẹ́ bí ó ti tọ́."'), 'Jer$51:7': (None, 'ni', 'Jer$51:7 " Ifẹ́ wúrà ni Bábílónì ní ọwọ́ OLÚWA; ó sọ gbogbo ayé di ọ̀mùtí. Gbogbo orílẹ̀ èdè mu ọtí rẹ̀, wọ́n sì ti ya òmùgọ̀ kalẹ̀."'), 'Jer$51:9': ('jẹ ki', 'ni', 'Jer$51:9 "“ ‘À bá ti wo Bábílónì sàn, ṣùgbọ́n kò lè sàn, ẹ jẹ́ kí a fi sílẹ̀, kí oníkálùkù lọ sí ilẹ̀ rẹ̀ torí ìdájọ́ rẹ̀ tó gòkè, ó ga àní títí dé òfurufù.’ "'), 'Jer$51:10': (None, None, 'Jer$51:10 "“ ‘OLÚWA ti dá wa láre, wá jẹ́ kí a sọ ọ́ ní Síónì ohun tí OLÚWA Ọlọ́run wa ti ṣe.’ "'), 'Jer$51:12': ('ṣe', None, 'Jer$51:12 "Gbé àsìá sókè sí odi Bábílónì! Ẹ ṣe àwọn ọmọ ogun gírí, ẹ pín àwọn olùsọ́ káàkiri, ẹ ṣètò àwọn tí yóò sápamọ́ OLÚWA! Yóò gbé ète rẹ̀ jáde, òfin rẹ̀ sí àwọn ará Bábílónì."'), 'Jer$51:15': (None, 'ayé', 'Jer$51:15 "“Ó dá ilẹ̀ nípa agbára rẹ̀, o dá ilẹ̀ ayé pẹ̀lú ọgbọ́n rẹ̀, o sì tẹ́ ọ̀run pẹ̀lú ìmọ̀ rẹ̀."'), 'Jer$51:20': (None, None, 'Jer$51:20 "“Ìwọ ni kùmọ̀ ohun èlò ogun mi, ohun èlò ìjà mi, pẹ̀lú rẹ èmi ó fọ́ orílẹ̀ èdè túútúú, èmi ó bà àwọn ilé Ọba jẹ́."'), 'Jer$51:30': (None, 'ni', 'Jer$51:30 "Gbogbo àwọn jagunjagun Bábílónì tó dáwọ́ ìjà dúró la sí àgọ́ wọn. Agbára wọn ti tán, wọ́n ti dàbí obìnrin. Ibùgbé rẹ̀ ni a ti dáná sun, gbogbo irin ẹnu ọ̀nà wọn ti di fífọ́."'), 'Jer$51:31': (None, None, 'Jer$51:31 Iránsẹ́ kan ń tẹ̀lé òmíràn láti sọ fún Ọba Bábílónì pé gbogbo ìlú rẹ̀ ni a ti kó ní ìgbèkùn.'), 'Jer$51:36': (None, 'ṣe', 'Jer$51:36 "Nítorí náà, báyìí ni OLÚWA wí: “Wò ó, èmi yóò tì ọ́ lẹ́yìn lórí ohun tí o fẹ́ ṣe. Èmi ó sì gbẹ̀san, èmi yóò mú kí omi òkun rẹ̀ àti orísun omi rẹ̀ gbẹ."'), 'Jer$51:39': ('ji', None, 'Jer$51:39 "Ṣùgbọ́n nígbà tí ọkàn wọn bá ru sókè, èmi yóò se àsè fún wọn, èmi yóò jẹ́ kí wọn mutí yó débi tí wọn yóò máa kọ ẹ̀rín lẹ́yìn náà, wọn yóò sun oorun àsùnjí,” ni OLÚWA wí."'), 'Jer$51:44': (None, 'ṣàn', 'Jer$51:44 Èmi yóò fi ìyà jẹ Bélì ti Bábílónì àti pé èmi yóò jẹ́ kí ó pọ gbogbo àwọn ohun tí ó gbé mì. Orílẹ̀ èdè kò ní i jẹ́ ìṣàn fún-un mọ́. Odi Bábílónì yóò sì wó.'), 'Jer$51:47': ('ṣubu', 'wá', 'Jer$51:47 "Nítorí ìgbà náà yóò wá dandan nígbà tí èmi yóò fi ìyà jẹ àwọn òrìṣà Bábílónì, gbogbo ilẹ rẹ̀ ni a ó dójú tì gbogbo àwọn tí a pa yóò sì ṣubú ní àárin rẹ̀."'), 'Jer$51:50': ('jẹ ki', 'ni', 'Jer$51:50 "Ẹ̀yin tí ó ti bọ́ lọ́wọ́ idà, ẹ lọ, ẹ má dúró: Ẹ rántí OLÚWA ní òkèrè, ẹ sì jẹ́ kí Jérúsálẹ́mù wá sí ọkàn yín.” "'), 'Jer$51:52': (None, 'ṣe', 'Jer$51:52 "“Nítorí náà, wò ó, ọjọ́ ń bọ̀,” ni OLÚWA wí, “tí èmi yóò se ìbẹ̀wò lórí àwọn ère fífin rẹ̀: àti àwọn tí ó gbọgbẹ́ yóò sì máa gbin já gbogbo ilẹ̀ rẹ̀"'), 'Jer$51:54': (None, None, 'Jer$51:54 "“Ìró igbe láti Bábílónì, àti ìparun láti ilẹ̀ àwọn ará Kálídéà!"'), 'Jer$51:57': (None, 'orukọ', 'Jer$51:57 "Èmi ó sì mú kí àwọn ìjòyè rẹ̀ yó bí ọ̀mùtí, àti àwọn ọlọ́gbọ́n rẹ̀ àti àwọn alákòóṣo rẹ̀, àti àwọn akọni rẹ̀, wọn ó sì sun oorun láéláé, wọn kì ó sì jí mọ́,” ní Ọba wí, ẹni tí orúkọ rẹ̀ ń jẹ́ OLÚWA ọmọ ogun."'), 'Jer$51:61': ('sọ', 'ka', 'Jer$51:61 "Jeremáyà sì sọ fún Séráíà pé, “nígbà tí ìwọ bá dé Bábílónì, kí ìwọ kí ó sì wò, kí ìwọ kí ó sì ka gbogbo ọ̀rọ̀ wọ̀nyí."'), 'Jer$51:63': ('kika', 'sọ', 'Jer$51:63 " Yóò sì ṣe, nígbà tí ìwọ bá parí kíka ìwé yìí tán kí ìwọ kí ó di òkúta mọ́ ọn, kí ó sì sọ ọ́ sí àárin odò Éfúrétè:"'), 'Jer$52:15': (None, None, 'Jer$52:15 Nebusarádánì balógun ìsọ kó gbogbo àwọn tálákà àti gbogbo àwọn tí ó kù ní ìlú náà lọ sí ilẹ̀ àjèjì pẹ̀lú àwọn oníṣọ̀nà tí ó kù àti gbogbo àwọn tí ó ti lọ sọ́dọ̀ Ọba Bábílónì.'), 'Jer$52:31': (None, None, 'Jer$52:31 Ní ọdún kẹtàdín lógójì ti Jéhóáíkímù Ọba Júdà ni Efili-merodaki di Ọba Bábílónì. Ó tú Jéhóáíkímù Ọba Júdà sílẹ̀ nínú túbú ní ọjọ́ kẹ́ẹ̀dọ́gbọ̀n oṣù kéjìlá.'), 'Jer$52:32': (None, None, 'Jer$52:32 "Ó ń sọ̀rọ̀ rere sí i, ó sì fún un ní ìjókòó ìgbéga, èyí tí ó ju ti àwọn Ọba yóòkù lọ tí ó wà pẹ̀lú rẹ̀ ní Bábílónì."'), 'Lam$1:4': ('ṣe', 'wá', 'Lam$1:4 "Gbogbo ọ̀nà tí ó wọ Ṣíónì ń sọ̀fọ̀, nítorí kò sẹ́ni tí ó wá láti jọ́sìn. Ẹnu bodè pátapáta ni ó dahoro, àwọn Olórí àlùfáà ibẹ̀ kérora, àwọn ọ̀dọ́bìnrin rẹ̀ sì ń kẹ́dùn, òun gan-an wà pẹ̀lú ìbànújẹ́ ọkàn. "'), 'Lam$1:6': (None, 'di', 'Lam$1:6 Gbogbo ẹwà ti di ohun ìgbàgbé. Àwọn ọmọ ọba kùnrin dàbí i ìgalà tí kò rí ewé tútù jẹ; nínú àárẹ̀ wọ́n sáré níwájú ẹni tí ó ń lé wọn.'), 'Lam$1:9': (None, None, 'Lam$1:9 "Ìdọ̀tí wà ní etí aṣọ rẹ̀, bẹ́ẹ̀ ni kò wo ọjọ́ iwájú rẹ̀, Ìṣubú rẹ̀ ya ni lẹ́nu; olùtùnú kò sì sí fún un. “Wo ìpọ́njú mi, OLÚWA, nítorí àwọn ọ̀ta ti borí.” "'), 'Lam$1:11': (None, 'ni', 'Lam$1:11 "Àwọn ènìyàn rẹ̀ ń kérora bí wọ́n ti ń wá oúnjẹ; wọ́n fi ohun ìní wọn se pàsípààrọ̀ oúńjẹ láti mú wọn wà láàyè. “Wò ó, OLÚWA, kí o sì rò ó, nítorí a kọ̀ mí sílẹ̀.” "'), 'Lam$1:14': (None, None, 'Lam$1:14 “Ẹ̀ṣẹ̀ mi ti di síṣopọ̀ sí àjàgà; ọwọ́ ọ rẹ̀ ni a fi hun ún papọ̀. Wọ́n ti yí ọrùn mi ká OLÚWA sì ti dín agbára mi kù. Ó sì ti fi mí lé àwọn tí n kò le dojúkọ lọ́wọ́.'), 'Lam$1:22': ('ṣe', 'wá', 'Lam$1:22 “Jẹ́ kí ìwà ìkà wọn wá síwájú rẹ; jẹ wọ́n níyà bí o se jẹ mí níyà nítorí gbogbo ẹ̀ṣẹ̀ mi. Ìrora mi pọ̀ ọkàn mi sì káàárẹ̀.”'), 'Lam$2:3': (None, None, 'Lam$2:3 Ní ìbínú gbígbóná rẹ̀ ni ó ké gbogbo ìwo Ísírẹ́lì. Ó ti mú ọwọ́ ọ̀tún rẹ̀ kúrò nígbà tí àwọn ọ̀tá dé. Ó run ní Jákọ́bù bí ọ̀wọ́ iná ní àgọ́ àwọn ọmọbìnrin Síónì.'), 'Lam$2:4': (None, None, 'Lam$2:4 Ó na ọfà rẹ̀ bí ọtá; ọwọ́ ọ̀tún rẹ̀ sì múra Bí ti ọ̀ta tí ó ti parun ó tú ìbínú rẹ̀ jáde bí iná sórí àgọ́ ọmọbìnrin Síónì.'), 'Lam$2:5': (None, None, 'Lam$2:5 OLÚWA dà bí ọ̀tá; ó gbé Ísírẹ́lì mì. Ó ti gbé gbogbo ààfin rẹ̀ mì ó pa ibi gíga rẹ̀ run. Ó sọ ìmí ẹ̀dùn àti ìbànújẹ́ di púpọ̀ fún àwọn ọmọbìnrin Júdà.'), 'Lam$2:7': (None, 'ọwọ', 'Lam$2:7 OLÚWA ti kọ̀ pẹpẹ rẹ̀ sílẹ̀ ó sì ti kọ̀ ibi mímọ́ rẹ̀. Ó sì fi lé ọ̀ta lọ́wọ́ àwọn odi ààfin rẹ̀; wọ́n sì kígbe ní ilé OLÚWA gẹ́gẹ́ bí ọjọ́ ìpèjẹ tí a yàn.'), 'Lam$2:8': ('odi', None, 'Lam$2:8 "OLÚWA pinnu láti fa ògiri tí ó yí ọmọbìnrin Síónì ya. Ó gbé wọn sórí òṣùnwọ̀n, kò sì fa ọwọ́ rẹ̀ padà kúrò nínú ìparun wọn. Ó mú kí ilé-ìsọ́ àti odi rẹ̀ sọ̀fọ̀ wọ́n ṣòfò papọ̀. "'), 'Lam$2:15': ('ori', None, 'Lam$2:15 "Àwọn tí ó gba ọ̀nà ọ̀dọ̀ rẹ pàtẹ́wọ́ lé ọ lórí; wọ́n kẹ́gàn wọ́n ju orí wọn sí ọmọbìnrin Jérúsálẹ́mù: “Èyí ha ni ìlú tí à ń pè ní àṣepé ẹwà, ìdùnnú gbogbo ayé?” "'), 'Lam$2:17': ('ṣe', None, 'Lam$2:17 "OLÚWA ti ṣe ohun tí ó pinnu; ó ti mú ọ̀rọ̀ rẹ̀ ṣẹ, tí ó sì pàṣẹ ní ọjọ́ pípẹ́. Ó ti sí ọ nípò láì láàánú, ó fún ọ̀tá ní ìsẹ́gun lórí rẹ, ó ti gbé ìwo àwọn ọ̀tá rẹ̀ ga. "'), 'Lam$2:18': ('oju', None, 'Lam$2:18 "Ọkàn àwọn ènìyàn kígbe jáde sí OLÚWA. Odi ọmọbìnrin Síónì, jẹ́ kí ẹkún rẹ̀ ṣàn bí odò ní ọ̀sán àti òru; má ṣe fi ara rẹ fún ìtura, ojú rẹ fún ìsinmi. "'), 'Lam$2:22': ('run', 'ni', 'Lam$2:22 "“Bí ó ti ṣe ní ọjọ́ àṣè, bẹ́ẹ̀ ni ó ṣe fi ẹ̀rù sí ẹ̀gbẹ́ mi. Ní ọjọ́ ìbínú OLÚWA kòsí ẹni tí ó sálà tí ó sì yè; àwọn tí mo ti tọ́jú tí mo sì fẹ́ràn, ni ọ̀ta mi parun.”"'), 'Lam$3:7': (None, None, 'Lam$3:7 Ó ti tì mí mọ́lé nítorí náà n kò le è sálọ; ó ti fi ẹ̀wọ̀n mú mi mọ́lẹ̀'), 'Lam$3:12': (None, None, 'Lam$3:12 Ó fa ọfà rẹ̀ yọ ó sì fi mí sohun ìtafàsí.'), 'Lam$3:31': ('ni', None, 'Lam$3:31 Ènìyàn kò di ìtanù lọ́dọ̀ OLÚWA títí láé.'), 'Lam$3:51': (None, 'okan', 'Lam$3:51 Ohun tí mo rí mú ìbẹ̀rù wá ọkàn mi nítorí gbogbo àwọn obìnrin ìlú mi.'), 'Lam$4:3': (None, None, 'Lam$4:3 "Àwọn ọ̀wàwà pèsè ọmú wọn fún ìtọ́jú àwọn ọmọ wọn, ṣùgbọ́n àwọn ènìyàn mi wá láì lọ́kàn bí ògòǹgò ní aṣálẹ̀. "'), 'Lam$4:10': ('ni', None, 'Lam$4:10 Pẹ̀lú ọwọ́ àwọn obìnrin aláàánú ni wọ́n ṣe ọmọ wọn tí ó di oúnjẹ fún wọn nígbà tí a pa àwọn ènìyàn mi run.'), 'Lam$4:11': (None, 'ina', 'Lam$4:11 OLÚWA ti fi ihò kíkún fún ìbínú rẹ̀; ó sì tú ìbínú gbígbóná rẹ̀ jáde. Ó da iná ní Síónì tí ó jó ìpìlẹ̀ rẹ̀ run.'), 'Lam$4:12': (None, 'ayé', 'Lam$4:12 "Àwọn ọba ayé kò gbàgbọ́, tàbí àwọn ènìyàn ayé, wí pé àwọn ọ̀tá àti aninilára le wọ odi ìlú Jérúsálẹ́mù. "'), 'Lam$5:6': (None, 'ọwọ', 'Lam$5:6 Àwa jọ̀wọ́ ara wa fún Éjíbítì àti Ásíríà láti rí oúnjẹ tótó jẹ.'), 'Lam$5:7': (None, None, 'Lam$5:7 "Àwọn baba wa ti ṣẹ̀, wọn kò sì sí mọ́, àwa sì ń ru ìjìyà ẹ̀ṣẹ̀ wọn."'), 'Lam$5:8': ('ni', None, 'Lam$5:8 "Àwọn ẹrú ń jọba lórí wa, kò sì sí ẹni tí yóò gbà wá lọ́wọ́ wọn."'), 'Lam$5:17': (None, None, 'Lam$5:17 "Nítorí èyí, àárẹ̀ mú ọkàn wa, nítorí èyí, ojú wa sì ṣú."'), 'Ezek$1:10': (None, 'oju', 'Ezek$1:10 " Báyìí ni ìrísí ojú àwa ẹ̀dá alààyè yìí: ọ̀kọ̀ọ̀kan wọn ní ojú ènìyàn, ní apa ọ̀tún wọn, wọ́n ní ojú kìnnìún ní ìhà ọ̀tún, wọ́n ní ojú màlúù ní ìhà òsì, ọ̀kọ̀ọ̀kan wọ́n sì tún ní ojú ẹyẹ idì."'), 'Ezek$1:25': (None, None, 'Ezek$1:25 "Bí wọ́n ṣe dúró tí wọ́n sì rẹ ìyẹ́ wọn sílẹ̀, ohùn kan jáde láti inú òfuurufú tó rán bò wọ́n."'), 'Ezek$2:8': ('fun', None, 'Ezek$2:8 " Ṣùgbọ́n ìwọ, ọmọ ènìyàn, gbọ́ ohun tí mo ń sọ fún ọ. Má se ṣọ̀tẹ̀ bí i ti ìdílé ọlọ́tẹ̀; la ẹnu rẹ kí o sì jẹ ohun tí mo fún ọ.”"'), 'Ezek$2:9': (None, None, 'Ezek$2:9 "Mo sì wò, mo sì rí ọwọ́ kan tí a nà sí mi. Ìwé tí a ká sì wà níbẹ̀,"'), 'Ezek$3:4': ('lọ', None, 'Ezek$3:4 "Ó sì tún sọ fún mi pé: “Ọmọ ènìyàn, lọ ni ìsinsinyìí bámi sọ ọ̀rọ̀ mi fún ìle Ísírẹ́lì."'), 'Ezek$3:5': ('ede', 'ọrọ', 'Ezek$3:5 "A kò rán ọ sí àwọn tó ń sọ èdè àjèjì tàbí tí èdè wọn ṣòro láti gbọ́, ṣùgbọ́n orílẹ̀ èdè Ísírẹ́lì ni mo rán ọ sí"'), 'Ezek$3:8': ('ṣe', None, 'Ezek$3:8 Ṣùgbọ́n bí i ti wọn lèmi náà yóò ṣe jẹ́ kí ojú àti ọkàn rẹ le.'), 'Ezek$3:9': ('ṣe', 'ni', 'Ezek$3:9 Èmi yóò ṣe iwájú orí rẹ bí òkúta tó le jùlọ àni tó le ju òkúta ìbọn lọ. Má ṣe bẹ̀rù wọn tàbí jẹ́ kí wọn ó dáyà já ọ. Bí ó tilẹ̀ jẹ́ pé ìdílé ọlọ̀tẹ̀ ni wọ́n.”'), 'Ezek$3:11': ('lọ', None, 'Ezek$3:11 Lọ nísinsìnyìí sí ọ̀dọ̀ àwọn ènìyàn rẹ tó wà ní ìgbèkùn. Sọ fún wọn pé báyìí ni OLÚWA Ọlọ́run wí yálà wọ́n gbọ́ tàbí wọn kò gbọ́.”'), 'Ezek$3:16': (None, None, 'Ezek$3:16 " Lẹ́yìn ọjọ́ méje, ọ̀rọ̀ OLÚWA tọ̀ mí wá wí pé:"'), 'Ezek$3:20': (None, 'ọwọ', 'Ezek$3:20 "“Bákan náà, bí olódodo kan bá yípadà, tó sì bẹ̀rẹ̀ sí í ṣe àìsòdodo, tí mo sì fi ohun ìkọ̀sẹ̀ sí ọ̀nà rẹ̀, yóò kú sínú ẹ̀ṣẹ̀ rẹ̀. Nítorí pé ìwọ kọ̀ láti kìlọ̀ fún un, yóò kú sínú ẹ̀ṣẹ̀ rẹ̀, a ó gbàgbé gbogbo ìwà rere tó ti hù tẹ́lẹ̀, ṣùgbọ́n ní ọwọ́ rẹ̀ ni èmi yóò ti béèrè ẹ̀jẹ̀ rẹ̀."'), 'Ezek$3:21': ('ni', None, 'Ezek$3:21 "Ṣùgbọ́n bí o bá kìlọ̀ fún olódodo tó sì dáwọ́ ìwà ẹ̀ṣẹ̀ rẹ̀ dúró, dájúdájú òun yóò yè nítorí pé ó ti gba ìkìlọ̀, ọrùn tìrẹ sì mọ́.”"'), 'Ezek$4:15': (None, 'ni', 'Ezek$4:15 "Nígbà náà ló wí fún mi pé, “Wò ó, Èmi ó fún ọ ní ìgbẹ́ màlúù dípò ìgbẹ́ ènìyàn. Èmi yóò mú ki ó dín àkàrà rẹ lórí ìgbẹ́ màlúù dípò igbẹ ènìyàn.”"'), 'Ezek$4:16': ('jẹ', 'jẹ', 'Ezek$4:16 "Ó sì tún sọ fún mi pé: “Ọmọ ènìyàn, èmi yóò dáwọ́ ìpèsè oúnjẹ dúró ní Jérúsálẹ́mù. Ní pípín ni àwọn ènìyàn yóò máa pín oúnjẹ́ jẹ pẹ̀lú ìfọ̀kansọ́nà bẹ́ẹ̀ gẹ́gẹ́ ni wọn yóò máa mu omi pẹ̀lú ìdààmú,"'), 'Ezek$5:1': ('ori', None, 'Ezek$5:1 "“Báyìí, ọmọ ènìyàn, mú ìdá tí ó mú kí o sì fi se abẹ ìfárí onígbàjámọ̀, kí o fi fá irun orí àti irùngbọ̀n rẹ. Fi irun yìí sórí ìwọ̀n kí o sì pín in."'), 'Ezek$5:7': ('ṣe', 'ṣe', 'Ezek$5:7 "“Nítorí náà, báyìí ni OLÚWA Ọlọ́run wí: Nítorí pé o ti ṣe àìgbọ́ràn ju àwọn orílẹ̀ èdè tó yí ọ ká lọ, tí o kò sì tẹ̀lé ìlànà mi tàbí kí o pa òfin mi mọ́. O kò tilẹ̀ tún ṣe dáadáa tó àwọn orílẹ̀ èdè tó yí ọ ká."'), 'Ezek$5:13': ('ni', None, 'Ezek$5:13 "“Ìbínú mi yóò sì dúró, bẹ́ẹ̀ ni ìbínú gbígbónná mi yóò sì rolẹ̀, n ó sì ti gba ẹ̀san mi. Nígbà tí mo bá sì parí ìbínú mi lórí wọn, wọn ó mọ̀ pé Èmi OLÚWA ti sọ̀rọ̀ nínú ìtara mi."'), 'Ezek$6:9': (None, 'lọ', 'Ezek$6:9 "Àwọn ti ó bọ́ nínú yin yóò sì ránti mi láàrin àwọn orílẹ́-èdè níbi tí wọn o dì wọ́n ní ìgbèkùn lọ, nítorí ti mo ti tọ́ ọkàn àgbèrè wọn ti o ti lọ kúrò, lọ́dọ̀ mi àti pẹ̀lú ojú wọn, ti n sàgbérè lọ sọ́dọ̀ òrìsà wọn, wọn o si sú ara wọn nítorí ìwà ibi ti wọn ti hù nínú gbogbo ìríra wọn."'), 'Ezek$7:14': ('ni', 'ṣe', 'Ezek$7:14 "Bí wọn tilẹ̀ fọn fèrè ogun tí wọ́n sì pèsè ohun gbogbo sílẹ̀, kò sẹ́ni tí yóò lọ ojú ogun, torí ìbínú gbígbóná mi ti wà lórí gbogbo ènìyàn"'), 'Ezek$7:17': (None, None, 'Ezek$7:17 "Gbogbo ọwọ́ yóò rọ, gbogbo orúnkún yóò di aláìlágbára bí omi."'), 'Ezek$7:19': (None, 'kun', 'Ezek$7:19 "Wọn ó dà fàdákà wọn sí ojú pópó, wúrà wọn ó sì dàbí èérí ìdọ̀tí fàdákà àti wúrà wọn kò ní le gbà wọ́n ní ọjọ́ ìbínú gbígbóná OLÚWA. Wọn kò ní le jẹun tẹ́ ra wọn lọ́rùn tàbí kí wọn kún ikùn wọn pẹ̀lú oúnjẹ nítorí pé ó ti di ohun ìkọ̀sẹ̀ fún wọn."'), 'Ezek$7:20': ('ṣe', 'ni', 'Ezek$7:20 "Wọn n ṣe ìgbéraga pẹ̀lú ohun ọ̀sọ́ wọn, tí ó lẹ́wà, wọn sì ti fi ṣe òrìṣà, wọn sì tún ya àwòrán ìríra wọn níbẹ̀. Nítorí náà, ní o sọ àwọn ǹnkan wọ̀nyí di ohun èérí tabi ìdọ̀tí fún wọn."'), 'Ezek$8:1': (None, None, 'Ezek$8:1 "Ní ọjọ́ karùn-ún (5), oṣù kẹfà (6) ọdún kẹfà (6) bí mo ṣe jókòó nílé mi pẹ̀lú àwọn àgbààgbà Júdà níwájú mi, ọwọ́ OLÚWA Ọlọ́run bà lé mi níbẹ̀."'), 'Ezek$8:11': (None, None, 'Ezek$8:11 "Níwájú wọn ni àádọ́rin (70) ọkùnrin tó jẹ́ àgbà ilé Ísírẹ́lì dúró sí, Jáásáníà ọmọ Sáfánì sì dúró sáàrin wọn. Ọ̀kọ̀ọ̀kan wọn sì mú àwo tùràrí lọ́wọ́, òórùn sì ń tú jáde."'), 'Ezek$9:2': (None, None, 'Ezek$9:2 "Lójú ẹsẹ̀ ni ọkùnrin mẹ́fà (6) jáde láti ẹnu ọ̀nà òkè tó kọjú sí ìhà àríwá, olúkúlùkù pẹ̀lú ohun ìjà olóró lọ́wọ́ wọn. Ọkùnrin kan tó wọ aṣọ funfun lẹlẹ wa láàrin wọn, pẹ̀lú ohun ìkọ̀we lẹ́gbẹ̀ẹ́ rẹ̀. Gbogbo wọn sì dúró lẹ́gbẹ̀ẹ́ pẹpẹ idẹ."'), 'Ezek$9:5': (None, None, 'Ezek$9:5 "Bí mo ṣe ń fetí sí èyí, O tún sọ fún àwọn yòókù pé, “Tẹ̀lé ọkùnrin náà lọ sáàrin ìlú láti pa láì dásí àti láì ṣàánú rárá."'), 'Ezek$9:8': (None, None, 'Ezek$9:8 "Èmi nìkan ló ṣẹ́kù nígbà tí wọ́n lọ pa àwọn ènìyàn mo dójú bolẹ̀, mo kígbe pé, “Áà! OLÚWA Ọlọ́run! Ìwọ yóò ha pa ìyókù Ísírẹ́lì pẹ̀lú dída ìbínú gbígbónà rẹ sórí Jérúsálẹ́mù?”"'), 'Ezek$9:9': (None, 'ilẹ', 'Ezek$9:9 "Ó dá mi lóhùn pé, “Ẹ̀ṣẹ̀ ilé Ísírẹ́lì àti Júdà pọ gan an ni; ilé wọn kún fún ìtàjẹ̀sílẹ̀ àti àìsòótọ́. Wọ́n ní, ‘OLÚWA tí kọ ilẹ̀ náà sílẹ̀; OLÚWA kò sì rí wa.’"'), 'Ezek$10:6': ('lọ', None, 'Ezek$10:6 "Nígbà tí OLÚWA pàṣẹ fún ọkùnrin aláṣọ funfun yìí pé, “Mú iná láàrin àwọn kẹ̀kẹ́, láàrin àwọn kérúbù,” ó sì lọ dúró sẹ́gbẹ̀ẹ́ àwọn kẹ̀kẹ́ náà."'), 'Ezek$11:2': (None, None, 'Ezek$11:2 "OLÚWA sì sọ fún mi pé, “Ọmọ ènìyàn, àwọn ọkùnrin yìí ló ń pète ibi, wọ́n sì ń gba ìmọ̀ràn búburú kalẹ̀ nínú ìlú."'), 'Ezek$11:5': (None, 'wá', 'Ezek$11:5 "Ẹ̀mí OLÚWA sì bà lé mi, ó sì wí pé, “Sọ̀rọ̀!” Báyìí ni OLÚWA wí, Ohun tí ẹ-ń sọ níyìí, ilé Ísírẹ́lì, mo mọ ohun tó wà lọ́kàn yín."'), 'Ezek$11:14': (None, None, 'Ezek$11:14 Ọ̀rọ̀ OLÚWA tún tọ̀ mí wá:'), 'Ezek$11:17': (None, None, 'Ezek$11:17 "“Nítorí náà, sọ pé: ‘Báyìí ni OLÚWA Ọlọ́run wí: Èmi yóò kó wọn jọ kúrò láàrin àwọn orílẹ̀ èdè tí mo fọ́n wọn ká sí, Èmi yóò sì fún wọn ní ilẹ̀ Ísírẹ́lì padà.’"'), 'Ezek$11:19': ('okan', 'ẹmi', 'Ezek$11:19 " N ó fún wọn ní ọ̀kan kan; èmi yóò sì fi ẹ̀mí tuntun sínú wọn; N ó mú ọkàn òkúta kúrò lára wọn, n ó sì fún wọn ní ọkàn tó rọ̀ bí ara ẹran."'), 'Ezek$12:1': (None, None, 'Ezek$12:1 Ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$12:6': ('ile', None, 'Ezek$12:6 "Gbé ẹrù rẹ lé èjìká lójú wọn, bo ojú rẹ, kí o má ba à rí ilẹ̀, sì ru ẹrù rẹ lọ lálẹ́ nítorí pé mo fi ọ́ ṣe àmì fún ilé Ísírẹ́lì.”"'), 'Ezek$12:12': (None, None, 'Ezek$12:12 "“Ọmọ aládé tó wà láàrin wọn yóò di ẹrù rẹ lé èjìká lálẹ́ yóò sì jáde lọ, òun náà yóò da ògiri lu kí ó le gba ibẹ̀ jáde. Yóò sì bo ojú rẹ̀ kí ó má ba à rí ilẹ̀."'), 'Ezek$12:13': ('mú', 'ilẹ', 'Ezek$12:13 "N ó ta àwọ̀n mi lé e lórí, yóò sì kó sínú okùn, N ó sì mú lọ sí Bábílónì, ní ilẹ̀ Kádíyà, ṣùgbọ́n kò ní fojú rí ilẹ̀ náà ibẹ̀ ni yóò kú sí."'), 'Ezek$13:1': (None, None, 'Ezek$13:1 Ọ̀rọ̀ OLÚWA tọ̀ mí wá pé:'), 'Ezek$13:5': (None, 'ni', 'Ezek$13:5 Ẹ kò gòkè láti mọ odi tí ó ya ní ilẹ̀ Ísírẹ́lì kí wọ́n ba lè dúró gbọin lójú ogun lọ́jọ́ OLÚWA.'), 'Ezek$13:6': ('ṣe', 'ni', 'Ezek$13:6 "Ìran àti àfọ̀ṣẹ wọn jẹ́ èké. Wọ́n wí pé, “OLÚWA wí” nígbà tí OLÚWA kò rán wọn, síbẹ̀ wọ́n fẹ́ kí OLÚWA mú ọ̀rọ̀ wọn ṣẹ."'), 'Ezek$13:14': (None, None, 'Ezek$13:14 "Èmi yóò wó ògiri tí ẹ fi amọ̀ àìpò rẹ́, èyí tí ẹ fi ẹfun kùn lulẹ̀ débi pé ìpìlẹ̀ rẹ yóò hàn jáde. Nígbà ti odi náà wó palẹ̀, a ó sì run yín sínú rẹ̀, ẹ ó sì mọ̀ pé Èmi ni OLÚWA."'), 'Ezek$13:22': (None, None, 'Ezek$13:22 "Nítorí pé ẹ ti ba ọkàn àwọn olódodo jẹ́ pẹ̀lú irọ́ yín, nígbà tí Èmi kò mú ìbànújẹ́ bá wọn, àti nítorí ẹ̀yin ti mú ọkàn àwọn ènìyàn búburú le, kí wọ́n má baà kúrò nínú ọ̀nà ibi wọn débi tí wọn yóò fi gba ẹ̀mí wọn là,"'), 'Ezek$14:2': (None, None, 'Ezek$14:2 Nígbà náà ni ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$14:7': ('ile', None, 'Ezek$14:7 "“ ‘Bí ẹnikẹ́ni ní ilé Ísírẹ́lì tàbí àlejò tó ń gbé ní ilẹ̀ Ísírẹ́lì, bá ya ara rẹ̀ kúrò lọ́dọ̀ mi, tó gbé òrìṣà rẹ̀ sọ́kàn rẹ̀, tó tún gbé ohun tó ń mú ni ṣubú sínú ẹ̀ṣẹ̀ ṣíwájú rẹ, lẹ́yìn èyí tó tún lọ sọ́dọ̀ wòlíì láti béèrè nǹkan lọ́wọ́ mi! Èmi OLÚWA fúnra ara mi ní ń o dá a lóhùn."'), 'Ezek$14:12': (None, None, 'Ezek$14:12 Ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$14:15': (None, None, 'Ezek$14:15 "“Tàbí bí mo bá jẹ́ kí ẹranko búburú gba orílẹ̀ èdè náà kọjá tí wọn fi sílẹ̀ láìní ọmọ tí wọ́n sì sọ di ahoro, tó bẹ́ẹ̀ tí kò sẹ́ni tó le gba ibẹ̀ kọjá torí ẹranko búburú yìí,"'), 'Ezek$14:17': ('lọ', None, 'Ezek$14:17 "“Tàbí tí mo bá mú idà kọjá láàrin orílẹ̀ èdè náà tí mo wí pé, ‘Ẹ jẹ́ kí idà kọjá láàrin ilẹ̀ náà,’ tí mo sì tú ìbínú gbígbóná lé wọn lórí nípa ìtàjẹ́sílẹ̀, tí mo pa ènìyàn àti ẹranko inú rẹ."'), 'Ezek$14:19': (None, None, 'Ezek$14:19 "“Tàbí bí mo rán àjàkálẹ̀-àrùn si ilẹ̀ náà, ti mo si da ìrunu mi lé e lórí pẹ̀lú ẹ̀jẹ̀, láti gé ènìyàn àti ẹranko kúrò nínú rẹ̀,"'), 'Ezek$14:23': ('ni', None, 'Ezek$14:23 "Nígbà tí ẹ bá rí ìwà àti ìṣe wọn, a ó tù yín nínú, nítorí pé ẹ ó mọ̀ pé n kò ṣe nǹkan kan láì nídìí, ni OLÚWA Ọlọ́run wí.”"'), 'Ezek$15:6': ('ni', None, 'Ezek$15:6 "“Nítorí náà, báyìí ni OLÚWA Ọlọ́run wí: Bí mo ṣe sọ igi àjàrà tó wà láàrin àwọn igi inú igbó yóòkù di igi ìdáná, bẹ́ẹ̀ ni Èmi yóò ṣe ṣe gbogbo ènìyàn tó ń gbé Jérúsálẹ́mù."'), 'Ezek$15:8': ('ni', None, 'Ezek$15:8 "Èmi yóò sọ ilé náà di ahoro nítorí ìwà àìsòótọ́ wọn, ni OLÚWA Ọlọ́run wí.”"'), 'Ezek$16:1': (None, None, 'Ezek$16:1 Ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$16:7': (None, 'ni', 'Ezek$16:7 "Mo mú ọ dàgbà bí ohun ọ̀gbìn inú oko. O ga sókè, o sì dàgbà, o wá di arẹwà jùlọ, ọmú rẹ yọ, irun rẹ gùn, ìwọ tí o wà ní ìhòòhò láì fi nǹkan kan bora."'), 'Ezek$16:9': (None, None, 'Ezek$16:9 "“ ‘Nígbà náà ni mo fi omi wẹ ẹ̀jẹ̀ kúrò lára rẹ, mo fi ìpara pa ọ́ lára."'), 'Ezek$16:17': (None, 'dá', 'Ezek$16:17 O tún mú àwọn ohun ọ̀ṣọ́ dáradára tí mo fi wúrà àti fàdákà ṣe fún ọ láti fi yá ère ọkùnrin tí ó ń bá ọ ṣe àgbèrè papọ̀.'), 'Ezek$16:27': (None, 'ni', 'Ezek$16:27 "Nítorí náà ni mo fi na ọwọ mi lòdì sí ọ, mo ge ilé rẹ kúrú; èmi yóò sì mú ìfẹ́ àwọn to korìíra rẹ ṣẹ́ lé ọ lórí, àwọn ọmọbìnrin Fílístínì ti ìwàkiwà rẹ̀ jẹ ìyàlẹ́nu fún,"'), 'Ezek$16:30': (None, None, 'Ezek$16:30 "“OLÚWA Ọlọ́run wí pé, ‘Báwo ni ọkàn rẹ ṣe jẹ aláìlera tó tí o n ṣe gbogbo nǹkan wọ̀nyí, gẹ́gẹ́ bí iṣẹ́ àwọn agídí alágbérè!"'), 'Ezek$16:34': (None, 'dá', 'Ezek$16:34 "Nínú àgbèrè rẹ ìwọ yàtọ̀ sí àwọn alágbèrè obìnrin yòókù; kò sí ẹni tó ń wá ọ fún àgbèrè. Ìwọ ló ń sánwó: nígbà tí wọn yóò sanwó fún ọ, ìdákejì ni ọ́; nítorí pé ìwọ ló ń sanwo bẹ́ẹ̀ ni ìwọ kò rí gbà."'), 'Ezek$16:35': ('gbo', 'wo', 'Ezek$16:35 "“ ‘Nítorí náà, ìwọ alágbèrè, gbọ́ ọ̀rọ̀ OLÚWA!"'), 'Ezek$16:38': (None, None, 'Ezek$16:38 "Èmi yóò dá ọ lẹ́jọ́, gẹ́gẹ́ bí a ti dá obìnrin tó ba ìgbeyàwó jẹ́, tí wọn sì ta ẹ̀jẹ̀ sílẹ̀; Èmi yóò sì mú ẹ̀jẹ̀ ìbínú àti owú mi wá sórí rẹ."'), 'Ezek$16:39': (None, None, 'Ezek$16:39 "Nígbà náà ni èmi yóò fà ọ lé àwọn olólùfẹ́ rẹ lọ́wọ́, wọn yóò sì wó gbogbo òkìtì rẹ pẹ̀lú àwọn ojúbọ rẹ palẹ̀. Wọn yóò tú aṣọ kúrò lára rẹ̀, gbogbo ọ̀ṣọ́ rẹ ni wọn yóò gbà, wọn yóò sì fi ọ sílẹ̀ ní ìhòòhò àti ààbò."'), 'Ezek$17:17': ('ní', 'ṣe', 'Ezek$17:17 "Nígbà tí wọ́n bá pa bùdó ogun tì í, tí wọ́n sì mọ odi láti pa ọ̀pọ̀ ènìyàn, Fáráò pẹ̀lú àwọn ọmọ ogun àti àjọ ńlá rẹ̀ kò ní lè ṣe ìrànwọ́ fún-un lójú ogun."'), 'Ezek$17:20': (None, 'mú', 'Ezek$17:20 "Èmi yóò ta àwọ̀n mi sórí rẹ̀, yóò sì bọ sínú okùn mi, N ó mú ọ lọ Bábílónì láti ṣe ìdájọ́ ìwà ọ̀dàlẹ̀ tó hù sí mi."'), 'Ezek$17:22': ('ga julọ', None, 'Ezek$17:22 "“ ‘Báyìí ní OLÚWA Ọlọ́run wí: Èmi fúnra mi yóò mú ọ̀kan lára ẹ̀ka tí ó ga jùlọ lori igi Kédàrí gíga, tí èmi yóò sì gbìn ín, èmi yóò sì gé ọ̀mùnú tí ó sẹ̀sẹ̀ yọ láti òkè, èmi yóò sì gbìn ín sórí òkè tó ga tó sì lókìkí."'), 'Ezek$17:24': ('ṣe', 'ni', 'Ezek$17:24 "Gbogbo igi inú oko yóò mọ̀ pé, Èmi OLÚWA ni ó mú igi gíga walẹ̀, tí mo sì mú kúkurú ga sókè, tí mo mu igi tútù gbẹ, tí mo sì mú igi gbígbẹ rúwé. “ ‘Èmi OLÚWA ni ó sọ bẹ́ẹ̀, Èmi yóò sì se e.’ ”"'), 'Ezek$18:5': (None, None, 'Ezek$18:5 "“Bí ọkùnrin Olódodo kan bá wà, tó ń ṣe ohun tó tọ́, tó sì yẹ"'), 'Ezek$18:10': ('ni', None, 'Ezek$18:10 "“Bí ó bá bi ọmọkùnrin, oniwà ipá, tó ń jalè, tó tún ń pànìyàn tó sì ń ṣe gbogbo àwọn nǹkan wọ̀nyí sí arákùnrin rẹ̀"'), 'Ezek$18:14': (None, None, 'Ezek$18:14 "“Bí ọkùnrin yìí bá bímọ, tó sì rí gbogbo ẹ̀ṣẹ̀ ti baba rẹ ń ṣẹ̀ yìí, tó sì bẹ̀rù, ti kò ṣe bẹ́ẹ̀:"'), 'Ezek$18:21': ('ni', None, 'Ezek$18:21 "“Ṣùgbọ́n bí ènìyàn búburú bá yípadà kúrò nínú gbogbo ẹ̀ṣẹ̀ rẹ̀ tó ti dá, tó sì bẹ̀rẹ̀ sí í pa àsẹ mi mọ́, tó sì ń ṣe ohun tó tọ́ àti ohun tó yẹ, nítòótọ́ ni yóò yè, kò sì ní kú."'), 'Ezek$18:22': (None, None, 'Ezek$18:22 "A kò sì ní rántí gbogbo ẹ̀ṣẹ̀ rẹ tó ti dá tẹ́lẹ̀ láti kàá sí lọ́rùn nítorí tí ìwà òdodo rẹ tó fihàn, yóò yè"'), 'Ezek$18:25': ('ni', None, 'Ezek$18:25 "“Bẹ́ẹ̀ ni, ẹ tún sọ pe, ‘OLÚWA kò ṣe é da kò tọ́.’ Gbọ́, ilé Ísírẹ́lì: se ọ̀nà mi ni kò tọ́? Kì í wa ṣé pé ọ̀nà ti yín gan-an ni kò tọ́?"'), 'Ezek$18:29': ('ni', None, 'Ezek$18:29 "Síbẹ̀, ilé Ísírẹ́lì wí pé, ‘Ọ̀nà OLÚWA kò tọ́.’ Ọ̀nà mi kò ha tọ́ bí Ilé Ísírẹ́lì? Kì í wa se pè ọ̀nà tiyín gan an ni ko tọ?"'), 'Ezek$18:31': (None, 'ṣe', 'Ezek$18:31 " Ẹ kọ̀ gbogbo ẹ̀ṣẹ̀ ti ẹ ti dá sílẹ̀, kí ẹ sì gba ọkàn àti ẹ̀mi tuntun. Nitori kí ló fi máa kú, ilé Ísírẹ́lì?"'), 'Ezek$19:4': ('mu', 'ilẹ', 'Ezek$19:4 "Àwọn orílẹ̀ èdè gbọ́ nípa rẹ̀, wọ́n sì fi ẹ̀wọ̀n mú un nínú ààfin wọn. Wọn fi ẹ̀wọ̀n mu nu lọ sí ilẹ̀ Éjíbítì. "'), 'Ezek$19:6': (None, None, 'Ezek$19:6 "Ó sì ń rìn káàkiri láàrin àwọn kìnnìún nítorí pé ó ti lágbára, o kọ ọdẹ ṣíṣe, ó sì pa àwọn ènìyàn."'), 'Ezek$19:7': (None, 'di ahoro', 'Ezek$19:7 Ó sì wó odi wọn palẹ̀ ó sì sọ àwọ̀n ìlú wọn di ahoro. Ilẹ̀ náà àti àwọn olùgbé ibẹ̀ sì wà ní ìpayà nítorí bíbú ramúramù rẹ̀.'), 'Ezek$19:11': ('iga', None, 'Ezek$19:11 "Àwọn ẹ̀ka rẹ̀ lágbára tó láti fi ṣe ọ̀pá àṣẹ ìjòyè, ó ga sókè láàrin ewé rẹ̀, gíga rẹ̀ hàn jáde láàrin ọ̀pọ̀lọpọ̀ ẹ̀ka rẹ̀."'), 'Ezek$20:9': ('ṣe', 'orukọ', 'Ezek$20:9 "Ṣùgbọ́n nítorí orúkọ mi, mo ṣe ohun tí kò ní jẹ́ kí orúkọ mi bàjẹ́ lójú àwọn orílẹ̀ èdè tí wọn ń gbé láàrin wọn, lójú àwọn ẹni tí mo ti fi ara hàn fún ara Ísírẹ́lì nípa mímú wọn jáde ní ilẹ̀ Éjíbítì."'), 'Ezek$20:10': ('mu', 'ilẹ', 'Ezek$20:10 "Nítorí náà, mo mú wọn jáde ní ilẹ̀ Éjíbítì mo sì mú wọn wá sínú ihà."'), 'Ezek$20:15': ('fi fun', 'ọwọ', 'Ezek$20:15 "Nítorí náà, mo tún gbọ́wọ́ mi sókè jẹ́jẹ̀ẹ́ fún wọn nínú ihà pé ń kò ní i mú wọn dé ilẹ̀ tí mo fi fún wọn—ilẹ̀ tí ń ṣàn fún wàrà àti oyin, ilẹ̀ tí ó lẹ́wà jùlọ láàrin àwọn ilẹ̀ yòókù."'), 'Ezek$20:18': (None, None, 'Ezek$20:18 "Ṣùgbọ́n mo sọ fún àwọn ọmọ wọn nínú ihà pé, “Ẹ má ṣe rìn ní ìlànà àwọn baba yín tàbí kí ẹ pa òfin wọn mọ́ tàbí kí ẹ bara yín jẹ́ pẹ̀lú òrìṣà wọn."'), 'Ezek$20:25': (None, None, 'Ezek$20:25 Èmi náà sì fi wọn fún ìlànà tí kò dára àti òfin tí wọn kò le e yè nípa rẹ̀;'), 'Ezek$20:28': ('ní', 'fun', 'Ezek$20:28 "Nígbà tí mo mú wọn dé ilẹ̀ tí mo ṣe ìlérí láti fún wọn, gbogbo igi ilẹ̀ gíga àti gbogbo igi to rúwé ni wọn tí ń rúbọ wọn ṣe irubọ to ń mú mi bínú, níbẹ̀ sì ni wọn ń ṣe òórùn dídùn wọn, ti wọn sì ń ta ọrẹ ohun mímu sílẹ̀."'), 'Ezek$20:33': ('ni', None, 'Ezek$20:33 "Bí mo ti wà láàyè ní OLÚWA Ọlọ́run wí, ń ó jọba lórí yín pẹ̀lú ọwọ́ agbára tí ń ó nà jáde pẹ̀lú ìtújáde ìbínú gbígbóná mi."'), 'Ezek$20:37': (None, None, 'Ezek$20:37 "Èmi yóò kíyèsí i yín bí mo ti mú un yín kọjá lábẹ́ ọ̀pá, èmi yóò sì mú yín wá sí abẹ́ ìdè májẹ̀mú"'), 'Ezek$20:41': ('gba', 'ni', 'Ezek$20:41 "N o tẹ́wọ́ gbà yín gẹ́gẹ́ bí tùràrí olóórùn dídùn nígbà tí mo ba mú yín jáde láàrin àwọn orílẹ̀ èdè tí a fọ́n yín ká sí, ń ó sì fi ìwà mímọ́ mi hàn láàrin yín lójú àwọn orilẹ̀ èdè."'), 'Ezek$20:42': ('fun', 'ọwọ', 'Ezek$20:42 "Nígbà náà ni ẹ o mọ̀ pé èmi ní OLÚWA, nígbà tí mo bá mú yín wa sí ilẹ̀ Ísírẹ́lì; sí ilẹ̀ tí mo gbọ́wọ́ mí sókè nínú ẹ̀jẹ́ láti fún àwọn baba yín."'), 'Ezek$20:43': ('ni', None, 'Ezek$20:43 " Níbẹ̀ ni ẹ ó wa rántí ìwà àti gbogbo ìsesí yín, èyí tí ẹ̀yin fi sọ ara yín di aláìmọ́, ẹ̀yin yóò sì kóríra ara yín fún gbogbo ibi tí ẹ̀yin ti se."'), 'Ezek$20:45': (None, None, 'Ezek$20:45 Ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$21:11': ('fun', 'ọwọ', 'Ezek$21:11 "“ ‘Idà ní a yàn láti pọ́n, kí ó lè ṣe é gbá mú; a pọ́n ọn a sì dan án, ó ṣetán fún ọwọ́ àwọn apani."'), 'Ezek$21:17': (None, None, 'Ezek$21:17 Èmi gan an yóò pàtẹ́wọ́ ìbínú mi yóò sì rẹlẹ̀ Èmi OLÚWA ti sọ̀rọ̀.”'), 'Ezek$21:23': (None, None, 'Ezek$21:23 "Yóò dàbí àmì èké sí àwọn tí ó ti búra ìtẹríba fún un, ṣùgbọ́n òun yóò ran wọn létí ẹbí wọn yóò sì mú wọn lọ sí ìgbèkùn."'), 'Ezek$21:24': ('ṣe', 'ni', 'Ezek$21:24 "“Nítorí náà èyíí ní ohun tí OLÚWA àwọn ọmọ-ogun wí: ‘Nítorí ti ẹ̀yin ti mú ẹ̀bi yín wá sí ìrántí nípa ìṣọ̀tẹ̀ ní gbangba, ní ṣíṣe àfihàn àwọn ẹ̀sẹ̀ rẹ nínú gbogbo ohun tí ó ṣe, nítorí tí ẹ̀yin ti ṣe èyí, a yóò mú yín ní ìgbékùn."'), 'Ezek$21:25': ('ni', None, 'Ezek$21:25 "“ ‘Ìwọ aláìmọ́ àti ẹni búburú ọmọ aládé Ísírẹ́lì, ẹni ti ọjọ́ rẹ̀ ti dé, ẹni tí àsìkò ìjìyà rẹ̀ ti dé góngó,"'), 'Ezek$21:28': ('ni', None, 'Ezek$21:28 " “Àti ìwọ, ọmọ ènìyàn sọ̀tẹ̀lẹ kí ó sì wí pé, ‘Èyí yìí ní ohun tí OLÚWA àwọn ọmọ-ogun wí nípa àwọn ará Ámónì àti àbùkù wọn: “ ‘Idà kan idà kan tí á fa yọ fún ìpànìyàn tí a dán láti fi ènìyàn ṣòfò àti láti kọ bí ìmọ̀nàmọ́ná!"'), 'Ezek$22:4': (None, 'ni', 'Ezek$22:4 "Ìwọ ti jẹ̀bi nítorí ẹ̀jẹ̀ tí ìwọ ti ta sílẹ̀, àti pé àwọn ère tí ó ṣe tí bà ọ́ jẹ́. Ìwọ tí mú ọjọ́ rẹ súnmọ́ tòsí, iparí àwọn ọdún rẹ sì ti dé. Nítorí náà èmi yóò fi ọ ṣe ohun ẹ̀gàn lójú àwọn orílẹ̀ èdè àti ohun ẹ̀sín lójú gbogbo ìlú."'), 'Ezek$22:12': ('ya', 'ni', 'Ezek$22:12 Nínú rẹ àwọn ènìyàn gba àbẹ̀tẹ́lẹ̀ láti tàjẹ̀ sílẹ̀; Ìwọ gba èlé lọ́pọ̀pọpọ̀ láti mú aláìsòótọ́ jèrè láti ara aládùúgbò rẹ nípa ìrẹ́jẹ. Ìwọ sì ti gbàgbé ẹ̀ mi; ni OLÚWA àwọn ọmọ-ogun wí.'), 'Ezek$22:13': (None, 'ere', 'Ezek$22:13 "“ ‘Èmi yóò kúkú pàtẹ́wọ́ lórí èrè àìmọ̀ tí ìwọ ti jẹ, àti lórí ẹ̀jẹ̀ tí ìwọ ti ta sílẹ̀ ní àárin yín."'), 'Ezek$22:14': ('ọwọ', None, 'Ezek$22:14 "Ǹjẹ́ ìwọ yóò lè ní ìgboyà tó, tàbí ọwọ́ rẹ yóò ni agbára ní ọjọ́ tí èmi yóò ni ṣíṣe pẹ̀lú rẹ? Èmi OLÚWA ti sọ̀rọ̀, Èmi yóò sì ṣe bẹ́ẹ̀."'), 'Ezek$22:20': (None, None, 'Ezek$22:20 "Gẹ́gẹ́ bí ènìyàn ṣe ń kó fàdákà, bàbà, irin, òjé àti tánúnganran jọ sínú iná ìléru láti fi amúbí ina yọ́ ọ, bẹ́ẹ̀ ni èmi yóò kó ọ jọ ní ìbínú àti ìrunnú mi, èmi yóò sì fi ọ sì àárin ìlú, èmi yóò sì yọ́ ọ. Níbẹ̀ ní ìwọ yóò sì ti yọ́."'), 'Ezek$22:25': (None, 'ni', 'Ezek$22:25 "Ìdìtẹ̀ sì wà láàrin àwọn ọmọ aládé inú rẹ̀, tó dàbí bíbú kìnnìún tó ń fà ẹ̀ran ya, wọ́n ń ba àwọn ènìyàn jẹ́, wọ́n ń kó ìsúra àti àwọn ohun iyebíye wọ́n sì ń sọ púpọ̀ di opó nínú rẹ̀."'), 'Ezek$22:26': (None, None, 'Ezek$22:26 "Àwọn àlùfáà rẹ̀ ti rú ofin mi, wọn si ti sọ ohun mímọ́ mi di àìlọ́wọ̀: wọn kò fi ìyàtọ̀ sáàrin ohun mímọ́ àti àìlọ́wọ̀, bẹ́ẹ̀ ni wọn kò fi ìyàtọ̀ hàn láàrin ohun àìmọ́, àti mímọ́, wọn sì ti fi ojú wọn pamọ́ kúrò ní ọjọ ìsinmi mi, mó sì dí ẹni àìlọ́wọ̀ láàrin wọn."'), 'Ezek$22:27': (None, None, 'Ezek$22:27 "Àwọn ọmọ aládé àárin rẹ̀ dàbí ikokò ti ń ṣọ̀tẹ̀, láti tàjẹ̀ sílẹ̀, láti pa ọkàn run, láti jèrè àìsòótọ́."'), 'Ezek$22:31': ('ni', None, 'Ezek$22:31 "Nítorí náà ni mo ṣe ma da ìbínú mi sí wọn lórí; máa fí iná ìbínú mi run wọn: mo si ma fi ọ̀nà wọn gbẹ̀san lórí ara wọn, ní OLÚWA Ọlọ́run wí.”"'), 'Ezek$23:9': (None, 'ni', 'Ezek$23:9 "“Nítorí náà mo fi i sílẹ̀ fún àwọn olólùfẹ́ rẹ̀, ará Ásíríà, tí ó ní ìfẹ́kúfẹ̀ẹ́ sí i."'), 'Ezek$23:13': ('ri', 'wo', 'Ezek$23:13 Mo rí i pé òun náà ba ara rẹ̀ jẹ́; àwọn méjèèjì rìn ojú ọ̀nà kan náà.'), 'Ezek$23:24': ('asà', None, 'Ezek$23:24 "Wọn yóò wa dojú kọ ọ pẹ̀lú ohun ìjà, kẹ̀kẹ́ ogun pẹ̀lú kẹ̀kẹ́ ẹrù àti pẹ̀lú ìwọ́jọpọ̀ ènìyàn; wọn yóò mú ìdúró wọn lòdì sí ọ ní gbogbo ọ̀nà pẹ̀lú àṣà ńlá àti kékeré pẹ̀lú àsíborí. Èmi yóò yí ọ padà sí wọn fun ìjìyà, wọn yóò sì fi ìyà jẹ ọ gẹ́gẹ́ bí wọn tí tó."'), 'Ezek$23:25': (None, None, 'Ezek$23:25 "Èmi yóò sì dojú ìbínú owú mi kọ ọ́, wọn yóò sì fìyà jẹ ọ́ ní ìrunnú. Wọ́n yóò gé àwọn imú àti àwọn ètí yín kúrò, àwọn tí ó kù nínú yín yóò ti ipá idà ṣubú. Wọn yóò mú àwọn ọmọkùnrin àti àwọn ọmọkùnrin yín lọ, àwọn tí o kù nínú yín ni iná yóò jó run."'), 'Ezek$23:27': (None, None, 'Ezek$23:27 "Èmi yóò sì fi òpin sí ìwà ìfẹ́kúfẹ̀ẹ́ àti iṣẹ́ aṣẹ́wó tí ẹ bẹ̀rẹ̀ ni Éjíbítì. Ẹ̀yin kò ní wo àwọn nǹkan wọ̀nyí pẹ̀lú aáyun, bẹ́ẹ̀ ni ẹ kò ní rántí Éjíbítì mọ."'), 'Ezek$23:28': (None, None, 'Ezek$23:28 "“Nítorí báyìí ni OLÚWA Olódùmarè wí: Èmi yóò fi ọ lé ọwọ́ àwọn tí ó korìíra, lọ́wọ́ àwọn ẹni tí ọkàn rẹ ti sí kúrò."'), 'Ezek$23:31': (None, 'ọwọ', 'Ezek$23:31 Ìwọ ti rin ọ̀nà ti ẹ̀gbọ́n rẹ rìn; Èmi yóò sì fi aago rẹ̀ lé ọ lọ́wọ́.'), 'Ezek$23:34': ('ni', None, 'Ezek$23:34 "Ìwọ yóò mú un, ni àmugbẹ; ìwọ yóò sì fọ sí wẹ́wẹ́ ìwọ yóò sì fa ọmú rẹ̀ ya. Èmi ti sọ̀rọ̀ ni OLÚWA Olódùmarè wí."'), 'Ezek$23:47': (None, None, 'Ezek$23:47 "Àwọn ènìyànkénìyàn náà yóò sọ wọ́n ni òkúta, yóò sì gé wọn lulẹ̀ pẹ̀lú idà wọn; wọn ó sì pa àwọn ọmọkùnrin àti àwọn ọmọbìnrin wọn, wọ́n ó sì jó àwọn ilé wọn kanlẹ̀."'), 'Ezek$23:48': ('ṣe', 'fa', 'Ezek$23:48 "“Èmi yóò sì fi òpin sí ìwà ìfẹ́kúfẹ̀ẹ́ ni ilẹ̀ náà, kí gbogbo àwọn obìnrin le gba ìkìlọ̀ kí wọn kí ó ma sì ṣe fara wé ọ."'), 'Ezek$24:5': (None, None, 'Ezek$24:5 Mú àwọn tí ó jọjú nínú agbo ẹran. Kó àwọn egungun sí abẹ́ ẹ rẹ̀; sì jẹ́ kí ó hó dára dára sì jẹ́ kí àwọn egungun náà bọ̀ nínú rẹ̀.'), 'Ezek$24:7': (None, 'ni', 'Ezek$24:7 "“ ‘Nítorí ẹ̀jẹ̀ tí ó ta sílẹ̀ wà ní àárin rẹ̀; o dà á sí orí àpáta kan lásán kò dà á sí orí ilẹ̀, níbi ti erùpẹ̀ yóò ti bò ó"'), 'Ezek$24:10': ('ina', 'ina', 'Ezek$24:10 "Nítorí náà kó igi náà jọ sí i, kí o sì fi iná sí i. Ṣe ẹran náà dáadáa, fi tùràrí dùn ún; ki o sì jẹ́ kí egungun náà jóná"'), 'Ezek$24:13': (None, 'ni', 'Ezek$24:13 "“ ‘Nísìn yìí èérí rẹ̀ ni ìwà ìfẹ́kúfẹ̀ẹ́ wà. Nítorí mo gbìyànjú láti wẹ̀ ọ́ mọ́, ṣùgbọ́n ìwọ kò sì mọ́ kúrò nínú ìwa ìfẹ́kúfẹ̀ẹ́ rẹ, a kì yóò sì tún wẹ̀ ọ mọ́ kúrò nínú èèrí rẹ, títí èmi yóò fi jẹ́ kí ìbínú mi balẹ̀ sórí rẹ̀."'), 'Ezek$24:14': (None, 'ṣe', 'Ezek$24:14 "“ ‘Èmi OLÚWA ni ó sọ ọ́, yóò sì ṣe, èmi yóò sì ṣe é. Èmi kì yóò pada sẹ́yìn; bẹ́ẹ̀ ni èmi kì yóò dá sí i, bẹ́ẹ̀ ni èmi kì yóò yí ọkàn mi padà; gẹ́gẹ́ bí ọ̀nà rẹ, àti gẹ́gẹ́ bí iṣẹ́ rẹ, ni wọn yóò dá ọ lẹ́jọ́, ni OLÚWA Ọlọ́run wí.’ ”"'), 'Ezek$24:20': ('wá', None, 'Ezek$24:20 "Mo sọ báyìí fún wọn pé, “Ọ̀rọ̀ OLÚWA tọ̀ mí wá wí pé:"'), 'Ezek$25:1': (None, None, 'Ezek$25:1 Ọ̀rọ̀ OLÚWA tọ̀ mí wá wí pé:'), 'Ezek$25:8': (None, None, 'Ezek$25:8 " “Báyìí ni OLÚWA Ọlọ́run wí: ‘Nítorí pé Móábù àti Séírì sọ wí pé, “Wò ó, ilé Júdà ti dàbí gbogbo àwọn aláìkọlà,”"'), 'Ezek$25:12': ('mu', 'ṣe', 'Ezek$25:12 " “Báyìí ni OLÚWA Ọlọ́run wí pé: ‘Nítorí pé Édómù gbẹ̀san lára ilé Júdà, ó sì jẹ̀bi gidigidi nípa síse bẹ́ ẹ̀,"'), 'Ezek$25:15': (None, 'ni', 'Ezek$25:15 " “Báyìí ni OLÚWA Ọlọ́run wí pé: ‘Nítorí tí àwọn ará Fílísítíà hùwà ẹ̀san tí wọ́n sì gbẹ̀san pẹ̀lú odì ní ọkàn wọn, àti pẹ̀lú ìjà gbangba àtijọ́ ní wíwá ọ̀nà láti pa Júdà run,"'), 'Ezek$26:14': ('ni', None, 'Ezek$26:14 "Èmi yóò sọ ọ di àpáta lásán, ìwọ yóò sì di ibi tí a ń sá àwọ̀n ẹja sí. A kì yóò sì tún ọ mọ nítorí èmi OLÚWA ti sọ ọ̀rọ̀ ní OLÚWA Ọlọ́run wí."'), 'Ezek$26:19': (None, None, 'Ezek$26:19 "“Èyí yìí ní OLÚWA Ọlọ́run wí: Nígbà tí mo bá sọ ọ́ di ìlú ahoro, gẹ́gẹ́ bi àwọn ìlú tí a kò gbé inú wọn mọ́, àti nígbà tí èmi yóò mú ibú agbami òkun wá sí orí rẹ, omi ńlá yóò sì bò ọ́,"'), 'Ezek$27:5': ('ni', 'ṣe', 'Ezek$27:5 "Wọn ti fi pákó firi ti Sénárì kan gbogbo ọkọ̀ rẹ, wọ́n ti mú kédárì ti Lébánónì wá láti fi ṣe òpó ọkọ̀ fún ọ."'), 'Ezek$27:6': ('ṣe', 'ni', 'Ezek$27:6 Nínú igi Oákù ti Báṣánì ní wọn ti fi gbẹ́ ìtukọ̀ òbèlè rẹ̀; ìjókòó rẹ ni wọn fi eyín erin ṣe pẹ̀lú igi bókísì láti erékùsù Kítímù wá'), 'Ezek$27:11': (None, 'ni', 'Ezek$27:11 "Àwọn ènìyàn Árífádì àti Hélékì wà lórí odi rẹ yíká; àti àwọn akọni Gámádì, wà nínú ilé ìṣọ́ rẹ. Wọ́n fi àwọn asà wọn kọ ara odi rẹ; wọn ti mú ẹwà rẹ pé."'), 'Ezek$27:24': ('ṣe', 'iṣẹ', 'Ezek$27:24 "Wọ̀nyí ní onírúurú nǹkan: aṣọ aláró, àti oniṣẹ́-ọnà àti àpótí aṣọ oníyebíye, tí a fi okùn dì, tí a sì fi igi kédárì ṣe, nínú àwọn ilé-ìtajà rẹ."'), 'Ezek$27:29': (None, None, 'Ezek$27:29 "Gbogbo àwọn alájẹ̀ àwọn atukọ̀ àti àwọn atọ́kọ̀ ojú òkun; yóò sọ̀ kálẹ̀ kúrò nínú ọkọ̀ wọn, wọn yóò dúró lórí ilẹ̀."'), 'Ezek$27:33': (None, 'ọpọlọpọ', 'Ezek$27:33 Nígbà tí ọjà títà rẹ ti òkun jáde wá ìwọ tẹ́ ọ̀pọ̀lọpọ̀ orílẹ̀ èdè lọ́rùn ìwọ fi ọrọ̀ tí ó pọ̀ àti àwọn ọjà títà rẹ sọ àwọn ọba ayé di ọlọ́rọ̀.'), 'Ezek$28:1': (None, None, 'Ezek$28:1 Ọ̀rọ̀ OLÚWA tún tọ̀ mí wá wí pé:'), 'Ezek$28:17': (None, None, 'Ezek$28:17 Ọkàn rẹ gbéraga nítorí ẹwà rẹ. Ìwọ sì ba ọgbọ́n rẹ jẹ́ nítorí dídára rẹ. Nítorí náà mo le ọ sórí ayé; mo sọ ọ di awò ojú níwájú àwọn ọba.'), 'Ezek$28:25': (None, 'ni', 'Ezek$28:25 "“ ‘Èyí yìí ní OLÚWA Ọlọ́run wí: Nígbà tí èmi yóò bá ṣa àwọn ènìyàn Ísírẹ́lì jọ kúrò ní gbogbo orílẹ̀ èdè tí wọ́n ti fọ́nká sí, tí a ó sì yà mí sí mímọ́ láàárin wọn lójú àwọn aláìkọlà; Nígbà náà ni wọn yóò gbé ní ilẹ̀ àwọn tìkalára wọn, èyí tí mo fún ìránṣẹ́ mi Jákọ́bù."'), 'Ezek$29:1': (None, None, 'Ezek$29:1 " Ní ọjọ́ kejìlá, oṣù kẹ́wàá, ọdún kẹ́wàá, ọ̀rọ̀ OLÚWA tọ̀ mí wá wí pé:"'), 'Ezek$29:5': (None, None, 'Ezek$29:5 Èmi yóò sọ ọ́ nù sí ihà ìwọ àti gbogbo ẹja inú odò rẹ: ìwọ yóò ṣubú sí gbangba oko a kì yóò sì ṣà ọ́ jọ tàbí gbé ọ sókè. Èmi ti fi ọ ṣe oùnjẹ fún àwọn ẹranko igbó àti fún àwọn ẹyẹ ojú ọ̀run láti jẹ.'), 'Ezek$29:7': ('mu', None, 'Ezek$29:7 "Nígbà tí wọn fi ọwọ́ agbára wọn dì ọ́ mú. Ìwọ fọ́, ìwọ sì ya gbogbo èjìká wọn; nígbà tí wọn fi ara tì ọ́, ìwọ sẹ́, ìwọ sì mú gbogbo ẹ̀gbẹ́ wọn gbọ̀n."'), 'Ezek$29:9': ('ṣe', 'ni', 'Ezek$29:9 "Ilẹ̀ Éjíbítì yóò di ihà àti ahoro, nígbà náà ní wọn yóò sì mọ̀ wí pé èmi ní OLÚWA. “ ‘Nítorí tí ìwọ wí pé, “Tèmi ni odò Náílì; Èmi ni mo ṣe é,”"'), 'Ezek$29:13': (None, None, 'Ezek$29:13 "“ ‘Ṣùgbọ́n báyìí ní OLÚWA Ọlọ́run wí: Ní òpin ogójì ọdún, èmi yóò ṣa àwọn ará Éjíbítì jọ láti ọ̀dọ̀ àwọn ènìyàn níbi tí a fọ́n wọn ká sí."'), 'Ezek$29:17': ('ní', None, 'Ezek$29:17 "Ó sì ṣe ní ọjọ́ kìn-ín-ní, oṣù kìn-ín-ní, ọdún kẹtàdínlọ́gbọ̀n, ọ̀rọ̀ OLÚWA tọ̀ mí wá wí pé:"'), 'Ezek$29:20': (None, 'ni', 'Ezek$29:20 "Èmi ti fi Éjíbítì fún un gẹ́gẹ́ bí èrè wàhálà rẹ̀ nítorí òun àti àwọn ológun rẹ ṣe é fún mi, ni OLÚWA Ọlọ́run wí."'), 'Ezek$30:4': ('wá', 'ṣubu', 'Ezek$30:4 Idà yóò wá sórí Éjíbítì ìrora ńlá yóò sì wá sórí Kúṣì Nígbà tí àwọn tí a pa yóò subú ní Éjíbítì wọn yóò kò ọrọ̀ rẹ̀ lọ ìpìlẹ̀ rẹ̀ yóò sì wó lulẹ̀.'), 'Ezek$30:6': ('wá', 'ṣubu', 'Ezek$30:6 “ ‘Èyí yìí ní OLÚWA wí: “ ‘Àwọn alejò Éjíbítì yóò ṣubú agbára ìgbéraga rẹ yóò kùnà láti Mígídólì títí dé Ásúwánì wọn yóò ti ipa idà ṣubú láàárin rẹ; ní OLÚWA Ọlọ́run wí:'), 'Ezek$30:7': (None, 'di ahoro', 'Ezek$30:7 "Wọn yóò sì wà lára àwọn ilẹ̀ tí ó di ahoro, ìlú rẹ yóò sì wà ní ara àwọn ìlú tí ó di ahoro."'), 'Ezek$30:11': ('mu', 'ilẹ', 'Ezek$30:11 Òun àti àwọn ológun rẹ̀ ẹ́rù àwọn orílẹ̀-èdè ní a o mú wá láti pa ilẹ náà run. Wọn yóò fa idà wọn yọ sí Éjíbítì ilẹ̀ náà yóò sì kún fún àwọn tí a pa.'), 'Ezek$30:12': ('ni', None, 'Ezek$30:12 Èmi yóò mú kí àwọn odò Náílì gbẹ Èmi yóò sì ta ilẹ náà fún àwọn ènìyàn búburú: láti ọwọ́ àwọn àjòjì ènìyàn Èmi yóò jẹ́ kí ilẹ̀ náà àti gbogbo ohun tí ó wá nínú rẹ ṣòfò. Èmi OLÚWA ni ó ti sọ ọ́.'), 'Ezek$30:18': ('lọ', None, 'Ezek$30:18 Ojú ọjọ́ yóò ṣókùnkùn ní Tápárebì nígbà tí mo bá já àjàgà Éjíbítì kúrò; níbẹ̀ agbára iyì rẹ̀ yóò dópin wọn yóò fi ìkùukùu bò ó àwọn abúlé rẹ ní wọn yóò lọ sí ìgbèkùn.'), 'Ezek$30:20': (None, None, 'Ezek$30:20 Ní ọjọ́ keje osù kìn-ín-ní ọdún kọkànlá ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$31:9': ('ṣe', 'ni', 'Ezek$31:9 Mo mú kí ó ní ẹwà pẹ̀lú ẹ̀ka lọ́pọ̀lọpọ̀ tó fi jẹ́ ìlara àwọn igi gbogbo ní Édẹ́nì tí í ṣe ọgbà Ọlọ́run.'), 'Ezek$31:10': ('iga', 'oke', 'Ezek$31:10 "“ ‘Nítorí náà, èyí yìí ńi OLÚWA àwọn ọmọ-ogun wí; Nítorí pé ó ga lọ sókè fíofío, tí ó sì gbé òkè rẹ̀ ga ju ewé tí ó nípọn kọ́, àti nítorí pé gíga rẹ mú kí ó gbéraga,"'), 'Ezek$31:12': ('ilẹ', 'ayé', 'Ezek$31:12 "àwọn orílẹ̀ èdè àjòjì aláìláàánú jùlọ ké e lulẹ̀, wọn sì fi kalẹ̀. Àwọn ẹ̀ka rẹ̀ ṣubú sórí òkè àti sí gbogbo àárin àwọn òkè; àwọn ẹ̀ka rẹ̀ tí ó ṣẹ nà sílẹ̀ ní gbogbo àlàfo jíjìn ilẹ̀. Gbogbo àwọn orílẹ̀ èdè ilẹ̀ ayé jáde kúrò ní abẹ́ ìjì rẹ̀ wọn sì fi sílẹ̀."'), 'Ezek$31:13': (None, None, 'Ezek$31:13 "Gbogbo àwọn ẹyẹ ojú ọ̀run ṣe àtìpó ni orí igi tí ó subú lulẹ̀ náà, gbogbo àwọn ẹranko ìgbẹ́ wà ní àárin ẹ̀ka rẹ̀."'), 'Ezek$31:16': ('ti o dara julọ', None, 'Ezek$31:16 "Mo mú kí orílẹ̀ èdè wárìrì sì ìró ìṣubú rẹ̀ nígbà tí mo mú un wá sí ìsàlẹ̀ isà òkú pẹ̀lú àwọn tí ó lọ sí ọ̀gbun ìṣàlẹ̀. Nígbà náà gbogbo igi Édẹ́nì, àṣàyàn àti èyí tí ó dára jùlọ nínú Lẹ́bánónì, gbogbo igi tí ó ní omi dáadáa ni a tù nínú ni ayé ìsàlẹ̀."'), 'Ezek$32:4': ('ayé', 'ilẹ', 'Ezek$32:4 Èmi yóò jù ọ́ sí orí ilẹ̀ èmi yóò sì fà ọ́ sókè sí orí pápá gbangba. Èmi yóò jẹ́ kí gbogbo ẹyẹ ojú ọ̀run ṣe àtìpó ní orí rẹ. Gbogbo àwọn ẹranko ayé yóò fi ìwọra gbé ara wọn mù lórí rẹ.'), 'Ezek$32:7': (None, None, 'Ezek$32:7 "Nígbà tí mo bá fọ́n ọ jáde, èmi yóò pa ọ̀run dé àwọn ìràwọ̀ wọn yóò sì ṣókùnkùn; èmi yóò sì fi ìkùukùu bo oòrùn òṣùpá kì yóò sì tan ìmọ́lẹ̀ rẹ̀"'), 'Ezek$32:14': (None, None, 'Ezek$32:14 "Lẹ́yìn náà èmi yóò mú kí omi rẹ̀ tòrò kí àwọn odò rẹ̀ kí o ṣàn bí epo, ni OLÚWA àwọn ọmọ-ogun wí."'), 'Ezek$32:15': ('ilẹ', None, 'Ezek$32:15 "Nígbà tí mo bá sọ Éjíbítì di ahoro, tí mo sì kó gbogbo ohun tí ó wà ní orí ilẹ̀ náà kúrò. Nígbà tí mo bá gé àwọn olùgbé ibẹ̀ lulẹ̀, nígbà náà wọn yóò mọ̀ pé èmi ni OLÚWA.’"'), 'Ezek$32:17': (None, None, 'Ezek$32:17 "Ní ọjọ́ kẹẹdógún oṣù, ọdún kejìlá, ọ̀rọ̀ OLÚWA tọ mí wá:"'), 'Ezek$32:24': ('ilẹ', 'ayé', 'Ezek$32:24 "“Élámù wà níbẹ̀, o yí isà òkú rẹ̀ ká pẹ̀lú gbogbo ijọ rẹ̀. Gbogbo wọn ni a pa, àwọn tí ó ti ipa idà ṣubú. Gbogbo àwọn tí ó ti tan ẹ̀rù ká ilẹ̀ alààyè lọ sí ìsàlẹ̀ láìkọlà sí abẹ́ ilẹ̀. Wọ́n gba ìtìjú wọn pẹ̀lú àwọn tí ó lọ sí ìsàlẹ̀ kòtò."'), 'Ezek$32:25': (None, None, 'Ezek$32:25 "A ṣe ibùsùn fún un láàárin àwọn tí a pa, pẹ̀lú gbogbo ìjọ rẹ̀ tí ó yí isà òkú ká. Gbogbo wọn jẹ́ aláìkọlà, tí a fi idà pa. Nítorí pé a tan ẹ̀rù wọn ká ilẹ̀ alààyè, wọn ru ìtìjú wọn pẹ̀lú àwọn tí ó lọ sí ìsàlẹ̀ kòtò; a tẹ́ wọn pẹ̀lú àwọn tí a pa."'), 'Ezek$32:32': (None, 'ni', 'Ezek$32:32 "Bí ó tilẹ̀ jẹ́ pé mo ti tan ẹ̀rù ká ilẹ̀ alààyè, Fáráò àti gbogbo ìjọ rẹ̀ ni a óò tẹ́ sí àárin aláìkọlà, pẹ̀lú àwọn tí a fi idà pa ní OLÚWA àwọn ọmọ-ogun wí.”"'), 'Ezek$33:1': (None, None, 'Ezek$33:1 Ọ̀rọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$33:6': ('gba', 'beere', 'Ezek$33:6 "Ṣùgbọ́n bí alóre bá rí idà ti o ń bọ̀, tí kò sì fọn ìpè láti ki àwọn ènìyàn nílọ̀, tí idà náà wá, tí ó sì gba ẹ̀mí ọ̀kan nínú wọn, a óò mú ọkùnrin náà lọ nítorí ẹ̀ṣẹ̀ rẹ̀, ṣùgbọ́n èmi yóò bèèrè ẹ̀jẹ̀ rẹ̀ lọ́wọ́ rẹ̀.’"'), 'Ezek$33:10': (None, None, 'Ezek$33:10 "“Ọmọ ènìyàn; sọ fún ilé Ísírẹ́lì, ‘Èyí yìí ní ìwọ sọ: “Àwọn ìrékọjá àti ẹ̀ṣẹ̀ wa tẹ orí wa ba, àwa sì ń sòfò dànù nítorí wọn. Bá wó wa ni a ṣe lè yè?” ’"'), 'Ezek$33:12': ('jẹ', None, 'Ezek$33:12 "“Nítorí náà, ọmọ ènìyàn, sọ fún àwọn ènìyàn ilẹ̀ rẹ, ‘Ìsòdodo ti olódodo ènìyàn kì yóò gbà á nígbà tí òun bá ṣe àìgbọ́ràn, ìwà búburú ènìyàn búburú kì yóò mú kí o ṣubú nígbà tí ó bá yí padà kúrò nínú rẹ̀. Bí olódodo ènìyàn bá ṣẹ̀, a kò ni jẹ́ kí ó yè nítorí òdodo rẹ̀ ti tẹ́lẹ̀.’"'), 'Ezek$33:13': (None, 'gbe', 'Ezek$33:13 "Bí mo bá sọ fún olódodo ènìyàn pé òun yóò yè, ṣùgbọ́n nígbà náà tí ó gbẹ́kẹ̀lé òdodo rẹ̀ tí ó sì ṣe búburú, a kò ní rántí ìkankan nínú iṣẹ́ òdodo tí o ti ṣe sẹ́yìn; òun yóò kú nítorí búburú tí ó ṣe."'), 'Ezek$33:14': ('ní', 'fun', 'Ezek$33:14 "Bí mo bá sọ fún ènìyàn búburú pé, ‘Ìwọ yóò kú dandan,’ ṣùgbọ́n tí ó bá yípadà kúrò nínú ẹsẹ̀ rẹ̀, ti o sì ṣe èyí tí ó tọ́ àti èyí tí o yẹ."'), 'Ezek$33:16': ('ni', None, 'Ezek$33:16 A kò ní rántí ìkan nínú ẹ̀ṣẹ̀ tí ó ti dá. Ó ti ṣe èyí tí ó tọ àti èyí tí o yẹ; òun yóò sì yè dájúdájú.'), 'Ezek$33:17': ('ni', None, 'Ezek$33:17 "“Síbẹ̀ àwọn ènìyàn ilẹ̀ rẹ sọ pé, ‘ọ̀nà OLÚWA kò tọ́.’ Ṣùgbọ́n ọ̀nà tí àwọn ní ó tọ́."'), 'Ezek$33:19': ('ni', None, 'Ezek$33:19 "Bí ènìyàn búburú bá yípadà kúrò nínú búburú rẹ̀, tí ó sì ṣe èyí tí ó tọ́ àti èyí tí ó yẹ, òun yóò yè nípa ṣíṣe bẹ́ẹ̀."'), 'Ezek$33:20': ('ni', None, 'Ezek$33:20 "Ṣíbẹ̀, ilé Ísírẹ́lì ìwọ wí pé, ‘ọ̀nà OLÚWA ki i ṣe òtítọ́.’ Ṣùgbọ́n èmi yóò ṣe ìdájọ́ ẹnìkọ̀ọ̀kan gẹ́gẹ́ bí ọ̀nà rẹ̀ ṣe rí.”"'), 'Ezek$33:27': ('jẹ', None, 'Ezek$33:27 "“Sọ èyí fún wọn: ‘Èyí yìí ni OLÚWA àwọn ọmọ-ogun wí: Bí mo ti wà láààyè, àwọn tí ó kúrò nínú ìparun yóò ti ipa idà ṣubú, àwọn tí ẹranko búburú yóò pajẹ, àwọn tí ó sì wà ní ilé ìsọ àti inú ihò òkúta ni àjàkálẹ̀-àrùn yóò pa."'), 'Ezek$33:31': ('wá', 'ṣe', 'Ezek$33:31 "Àwọn ènìyàn mi wá sọ́dọ̀ rẹ, gẹ́gẹ́ bí wọn ṣe ń ṣe, wọn sì jòkòó níwájú rẹ láti fetísí ọ̀rọ̀ rẹ, ṣùgbọ́n wọn kò mú wá sí ìṣe. Ẹnu wọn ní wọ́n fí n sọ̀rọ̀ ìfọkànsìn, ṣùgbọ́n ọkàn wọn ní ìwọra èrè tí kò tọ́."'), 'Ezek$34:1': ('wá', None, 'Ezek$34:1 Ọ̀rọ̀ OLÚWA tọ́ mi wá:'), 'Ezek$34:3': (None, 'jẹ', 'Ezek$34:3 "Ìwọ jẹ wàrà. O sì wọ aṣọ olówùú sí ara rẹ, o sì ń pa ẹran tí ó wù ọ́, ṣùgbọ́n ìwọ kò tọ́jú agbo ẹran."'), 'Ezek$34:4': (None, None, 'Ezek$34:4 Ìwọ kò ì tíì mú aláìlágbára lára le tàbí wo aláìsàn sàn tàbí di ọgbẹ́ fún ẹni tí o fi ara pa. Ìwọ kò í tíì mú aṣàko padà tàbí wá ẹni tí ó nù. Ìwọ ṣe àkóso wọn ní ọ̀nà lílé pẹ̀lú ìwà òǹrorò.'), 'Ezek$34:6': (None, None, 'Ezek$34:6 Àgùntàn mi n rin ìrìn àrè kiri ní gbogbo àwọn òkè gíga àti òkè kékeré. A fọ́n wọn ká gbogbo orí ilẹ̀ ẹni kankan kò sì wá wọn.'), 'Ezek$34:7': (None, None, 'Ezek$34:7 "“ ‘Nítorí náà, ìwọ olùṣọ́ àgùntàn, gbọ́ ọ̀rọ̀ OLÚWA:"'), 'Ezek$34:11': ('wá', 'wa', 'Ezek$34:11 "“ ‘Nítorí èyí ni OLÚWA àwọn ọmọ-ogun wí: Èmi fúnra mi yóò wá àgùntàn mi kiri, èmi yóò sì ṣe àwárí wọn."'), 'Ezek$34:12': ('ni', None, 'Ezek$34:12 "Gẹ́gẹ́ bí olùṣọ́ àgùntàn ṣe fojú tó agbo ẹran rẹ̀ tí ó fọ́n ká nígbà tí ó wà pẹ̀lú wọn, bẹ́ẹ̀ ni èmi yóò se fojú tó àgùntàn mi. Èmi yóò gbá wọn kúrò ni gbogbo ibi tí wọ́n fọ́n ká sí ni ọjọ ìkúukùu àti òkùnkùn."'), 'Ezek$34:13': ('ilẹ', None, 'Ezek$34:13 "Èmi yóò mú wọn jáde kúrò ní àwọn orílẹ̀ èdè, èmi yóò sì kó wọn jọ láti inú àwọn ìlú, èmi yóò sì mú wọn wá sí ilẹ ara wọn. Èmi yóò mú wọn jẹ ni orí àwọn òkè gíga Ísírẹ́lì, ni àárin àwọn òkè àti ní gbogbo ibùdó ilẹ̀ náà."'), 'Ezek$34:25': (None, None, 'Ezek$34:25 "“ ‘Èmi yóò dá májẹ̀mú àlàáfíà pẹ̀lú wọn, èmi yóò sì gba ilẹ̀ náà kúrò lọ́wọ́ ẹranko búburú, kí wọn kí o lè gbé ní aṣálẹ̀, kí àgùntàn sì gbé ní inú igbó ní àìléwu."'), 'Ezek$34:26': (None, None, 'Ezek$34:26 Èmi yóò bùkún wọn àti ibi agbègbè ti ó yí òkè mi ká. Èmi yóò rán ọ̀wààrà òjò ni àkókò; òjò ìbùkún yóò sì wà.'), 'Ezek$34:27': (None, 'ilẹ', 'Ezek$34:27 "Àwọn igi ìgbẹ́ yóò mú èso wọn jáde, ilẹ̀ yóò sì mu irúgbìn jáde; A óò sì dá ààbò bo àwọn ènìyàn ní ilẹ̀ náà. Wọn yóò mọ̀ pé èmi ni OLÚWA, nígbà tí mo bá já àwọn ohun tí ó ṣe okùnfà àjàgà, tí mo sì gbà wọ́n kúrò lọ́wọ́ àwọn tí ó fi wọ́n ṣe ẹrú."'), 'Ezek$35:10': (None, 'ni', 'Ezek$35:10 "“ ‘Nítorí tí ìwọ sọ pé, “Àwọn orílẹ̀ èdè àti ilẹ̀ méjì wọ̀nyí yóò jẹ́ tiwa, àwa yóò sì gbà wọ́n ní ìní,” bí ó tilẹ̀ jẹ́ pé èmi OLÚWA wà níbẹ̀,"'), 'Ezek$35:12': ('ni', 'jẹ', 'Ezek$35:12 "Nígbà náà ni ìwọ yóò mọ̀ pé èmi OLÚWA ti gbọ gbogbo ohun ẹ̀gàn tí ìwọ ti sọ lòdì sí àwọn òkè Ísírẹ́lì náà. Ìwọ wí pé, “A ti fi wọ́n sílẹ̀ ní ṣíṣọ̀fọ̀, a sì fi fún wa láti pajẹ.”"'), 'Ezek$36:1': (None, 'ni', 'Ezek$36:1 "“Ọmọ ènìyàn, sọ tẹ́lẹ̀ sí àwọn òkè gíga Ísírẹ́lì kí o sì wí pé, ‘Ẹ̀yin òkè gíga Ísírẹ́lì gbọ́ ọ̀rọ̀ OLÚWA."'), 'Ezek$36:6': (None, 'ni', 'Ezek$36:6 "Nítorí náà, sọtẹ́lẹ̀ nípa ilẹ̀ Ísírẹ́lì náà kí o sì sọ sí àwọn òkè gíga náà àti àwọn òkè kékèké, sí àwọn àfonífojì ńlá àti sí àwọn àfonífojì kéékèèkéé: ‘Èyí yìí ni OLÚWA àwọn ọmọ-ogun wí: Èmi sọ̀rọ̀ nínú ìrunú owú mi nítorí pé ìwọ ti jìyà ìfiṣẹ̀sín àwọn orílẹ̀ èdè."'), 'Ezek$36:8': ('eso', None, 'Ezek$36:8 "“ ‘Ṣùgbọ́n ìwọ, òkè gíga Ísírẹ́lì, yóò mú ẹ̀ka àti èso fún àwọn ènìyàn mi Ísírẹ́lì, nítorí wọn yóò wá sílé ní àìpẹ́ yìí."'), 'Ezek$36:9': (None, None, 'Ezek$36:9 "Èmi ń ṣe àníyàn fún ọ, èmi yóò sì fi rere wọ̀ ọ́; àwa yóò tu ọ́, àwa yóò sì gbìn ọ́,"'), 'Ezek$36:24': (None, 'mú', 'Ezek$36:24 “ ‘Nítorí èmi yóò mú yín jáde kúrò ni àwọn orílẹ̀ èdè náà; Èmi yóò ṣà yín jọ kúrò ní gbogbo àwọn ìlú náà; Èmi yóò sì mú yín padà sí ilẹ̀ ẹ̀yin tìkarayín.'), 'Ezek$36:26': ('okan', 'ẹmi', 'Ezek$36:26 "Èmi yóò fún yín ni ọkàn tuntun, èmi yóò sì fi ẹmi tuntun sínú yín; Èmi yóò sì mú ọkàn òkúta kúrò nínú yín, èmi yóò sì fi ọkàn ẹran fún yín."'), 'Ezek$36:27': ('ṣe', None, 'Ezek$36:27 "Èmi yóò fi ẹ̀mí mi sínú yín, èmi yóò sì mú ki ẹ tẹ̀lé àṣẹ mi, kí ẹ sì fiyèsí àti pa òfin mi mọ́."'), 'Ezek$36:31': (None, None, 'Ezek$36:31 " Nígbà náà ni ẹ̀yin yóò rántí àwọn ọ̀nà búburú àti àwọn ìwà ìkà yín, ẹ̀yin yóò sì korìíra ara yín fún ẹ̀ṣẹ̀ yín, ìwà tí kò bójúmu."'), 'Ezek$36:33': ('ni', None, 'Ezek$36:33 "“ ‘Èyí yìí ni ohun tí OLÚWA àwọn ọmọ-ogun wí: Ní ọjọ́ tí mo wẹ̀ yín kúrò nínú ẹ̀ṣẹ̀ yín gbogbo, èmi yóò ṣe àtúnṣe àwọn ìlú yín, wọn yóò sì ṣe àtúnkọ́ iwólulẹ̀"'), 'Ezek$37:5': ('mọ', None, 'Ezek$37:5 Èyí ni ohun tí OLÚWA àwọn ọmọ-ogun wí fún àwọn egungun wọ̀nyí: Èmi yóò mú kí èémí wọ inú yín ẹ̀yin yóò sì wá di ààyè.'), 'Ezek$37:12': ('mú', 'ilẹ', 'Ezek$37:12 "Nítorí náà ṣọtẹ́lẹ̀, kí o sì ṣọ fun wọn pé, ‘Èyí yìí ni ohun tí OLÚWA àwọn ọmọ-ogun sọ: Ẹ̀yin ènìyàn mi, Èmi yóò sí àwọn ibojì yín, èmi yóò sì mú yín wá si ilẹ̀ Isírẹ́lì."'), 'Ezek$37:13': ('mu', None, 'Ezek$37:13 "Ẹ̀yin o sì mọ̀ èmi ni OLÚWA, nígbà tí èmi bá ti sí bojì yín, ẹ̀yin ènìyàn mi, ti èmi bá si mú un yín dìde kúrò nińú bojì yín"'), 'Ezek$37:19': ('gba', 'ṣe', 'Ezek$37:19 "Ṣọ fún wọn pé, ‘Èyí yìí ni ohun tí OLÚWA àwọn ọmọ-ogun wí: Èmi yóò gba igi Jósẹ́fù—èyí tí ó wà ní ọwọ́ Éfúráímù—àti ti ẹ̀yà Ísírẹ́lì tí ó parapọ̀ mọ́ ọn, kí ó sì só papọ̀ mọ́ igi Júdà, láti sọ wọ́n di igi kan ṣoṣo, wọn yóò sì di ọ̀kan ní ọwọ́ mi.’"'), 'Ezek$37:21': (None, None, 'Ezek$37:21 "kí ó sì sọ fún wọn pé, ‘Èyí yìí ni ohun tí OLÚWA àwọn ọmọ-ogun wí: Èmi yóò mú Ísírẹ́lì jáde kúrò ní àwọn orílẹ̀ èdè tí wọ́n ti lọ. Èmi yóò ṣà wọ́n jọ pọ̀ láti gbogbo agbègbè, èmi yóò sì mú wọn padà sí ilẹ̀ ara wọn."'), 'Ezek$37:25': (None, 'ni', 'Ezek$37:25 "Wọn yóò gbé ní ilẹ̀ tí mo fún Jákọ́bù ìránṣẹ́ mi, ilẹ̀ tí àwọn baba yín ń gbé. Àwọn àti àwọn ọmọ wọn àti àwọn ọmọ ọmọ wọn ni yóò máa gbé ibẹ̀ láéláé, Ìránṣẹ́ mi Dáfídì ni yóò jẹ́ ọmọ Aládé wọn láéláé."'), 'Ezek$38:1': (None, None, 'Ezek$38:1 Ọrọ̀ OLÚWA tọ̀ mí wá:'), 'Ezek$38:8': ('mu', 'ilẹ', 'Ezek$38:8 "Lẹ́yìn ọ̀pọ̀lọpọ̀ ọjọ́, àwa yóò pè ọ́ fún àwọn ohun èlò ogun. Ní àwọn ọdún ọjọ́ iwájú ìwọ yóò dóti ilẹ̀ tí a ti gbà nígbà ogun, tí àwọn ènìyàn wọn kórajọ pọ̀ láti ọ̀pọ̀ orílẹ̀ èdè sí àwọn òkè gíga ti Ísírẹ́lì, tí ó ti di ahoro fún ìgbà pípẹ́. A ti mú wọn jáde láti ọ̀dọ̀ àwọn orílẹ̀ èdè, nísínsínyìí gbogbo wọn ń gbé ní àìléwu."'), 'Ezek$38:10': (None, None, 'Ezek$38:10 "“ ‘Èyí ni OLÚWA àwọn ọmọ-ogun wí: Ní ọjọ́ náà èrò kan yóò wá ṣọ́kan rẹ, ìwọ yóò sì pète ìlànà búbúru."'), 'Ezek$38:15': (None, 'ilẹ', 'Ezek$38:15 "Ìwọ yóò wá láti ààyè rẹ ní jìnnàjìnnà àríwá, ìwọ àti ọ̀pọ̀lọpọ̀ orílẹ̀ èdè pẹ̀lú rẹ, gbogbo wọn yóò sì gun ẹsìn ìjọ ńlá, jagunjagun alágbára."'), 'Ezek$38:19': ('ilẹ', 'ilẹ', 'Ezek$38:19 "Ní ìtara mi àti ní gbígbóná ìbínú mi, Mo tẹnumọ́-ọn pé; ní àsìkò náà ilẹ̀ mímì tíi ó lágbára ní ilẹ̀ Ísírẹ́lì yóò ṣẹlẹ̀."'), 'Ezek$39:1': (None, None, 'Ezek$39:1 "“Ọmọ ènìyàn, ṣọtẹ́lẹ̀ sí Gógù, kí ó sì wí pé: ‘Èyí yìí ni ohun tí OLÚWA àwọn ọmọ-ogun wí: Èmi lòdì sí ọ, Ìwọ Gógì, olórí ọmọ-aládé ti Mésékì àti Túbálì."'), 'Ezek$39:7': (None, 'orukọ', 'Ezek$39:7 "“ ‘Èmi yóò sọ orúkọ mímọ́ mí di mí mọ̀ láàrin àwọn èniyàn mì Isìrẹli. Èmi ki yóò jẹ́ kí orúkọ mímọ́ mi di aláìmọ́ àwọn orílẹ èdè yóò mọ̀ pé èmi OLÚWA, èmi ni ẹni mímọ́ ní Ísírẹ́lì."'), 'Ezek$39:8': ('ṣe', None, 'Ezek$39:8 "Ó ń bọ̀! Ó dájú pé yóò ṣẹlẹ̀, ni OLÚWA Ọba tẹnumọ́. Èyi yìí ni ọjọ́ tí mo sọ̀rọ̀ nípa rẹ̀."'), 'Ezek$39:9': ('jo', None, 'Ezek$39:9 "“ ‘Nígbà náà, àwọn ti ó ń gbé nì àárin àwọn ìlú tí ó wà ni Ísírẹ́lì yóò jáde, wọn yóò sì lo àwọn ohun ìjà bi epo ìdáná yóò sì jó wọ́n kanlẹ—àpáta kékèké àti àpáta ńlá, àwọn ọfà, àwọn kùmọ̀ ogun àti ọ̀kọ̀ fún ọdún méje wọn yóò lò wọ́n bi epo ìdáná."'), 'Ezek$39:11': ('fun', None, 'Ezek$39:11 "“ ‘Ní ọjọ́ náà èmi yóò sì fi ibi ìsìnkú fún Gógì ní Ísírẹ́lì, ni àfonífojì àwọn ti ó rìn ìrìnàjò lọ sí agbègbè ilà òòrùn òkun. Yóò di ojú ọ̀nà àwọn arìnrìn àjò, nítorí Gógì àti gbogbo ìjọ rẹ̀ ni àwa yóò sin síbẹ̀. Nítorí náà, a óò pè é ní àfonífojì tí Ámónì Gógì."'), 'Ezek$39:14': (None, 'ilẹ', 'Ezek$39:14 "“ ‘Wọn yóò gbà àwọn ènìyàn ṣíṣẹ́ lóòrèkóórè láti ṣe àfọ̀mọ́ náà. Ọ̀pọ̀ yóò lọ káàkiri ilẹ̀ náà, ní àfikún pẹ̀lú wọn, àwọn tó kù yóò sìn àwọn tí ó kú lórí ilẹ̀: Ní ìparí oṣù kéje wọn yóò bẹ̀rẹ̀ wíwá kiri wọn."'), 'Ezek$39:17': (None, None, 'Ezek$39:17 "“Ọmọ ènìyàn èyí yìí ni ohun tí OLÚWA Ọba wí pé: pe gbogbo oríṣìíríṣìí ẹyẹ àti gbogbo àwọn ẹranko ìgbẹ́ jáde:” kí wọn pé jọ pọ̀ láti gbogbo agbègbè sí ìrúbọ tí mó ń múra rẹ̀ fún ọ, ìrúbọ ńlá náà ní orí òkè gíga tí Ísírẹ́lì. Níbẹ̀ ẹ̀yin yóò jẹ ẹran, ẹ̀yin yóò sì mú ẹ̀jẹ̀."'), 'Ezek$39:23': (None, 'ọwọ', 'Ezek$39:23 "Àwọn orílẹ̀ èdè yóò sì mọ̀ pé àwọn ènìyàn Ísírẹ́lì lọ sí ìgbèkùn nítorí ẹ̀ṣẹ̀ wọn, nítorí pé wọ́n jẹ́ aláìsòótọ́ sí mi. Nítorí náà mo mú ojú mi pamọ́ kúrò lọ́dọ̀ wọn, mo sì fi wọn lé àwọn ọ̀tá wọn lọ́wọ́, gbogbo wọn sì subú nípasẹ̀ idà."'), 'Ezek$39:25': (None, 'ni', 'Ezek$39:25 "“Nítorí náà èyí ni ohun tí OLÚWA Ọba wí: Èmi yóò mú Jákọ́bù padà kúrò ní oko ẹrú, èmi yóò ní iyọ́nú si gbogbo ènìyàn Ísírẹ́lì, èmi yóò sì ní ìtara fún orúkọ mímọ́ mi."'), 'Ezek$39:27': (None, 'ni', 'Ezek$39:27 "Nígbà tí mo ti mú wọn padà kúrò ní àwọn orílẹ̀ èdè, tí mo sì kó wọn jọ pọ̀ kúrò ni ìlú àwọn ọ̀tá wọn, èmi yóò fi ara mi hàn ni mímọ́ nípasẹ̀ wọn ní ojú àwọn orílẹ̀ èdè púpọ̀."'), 'Ezek$40:2': ('mu', 'ilẹ', 'Ezek$40:2 " Nínú ìran Ọlọ́run, ó mú mí lọ sí ilẹ̀ Ísírẹ́lì, ó sì gbé mi lọ sí orí òkè gíga fíofío. Ní ẹ̀gbẹ́ gúsù ọ̀pọ̀ ilé tó wà níbẹ̀ dàbí ìlú ńlá."'), 'Ezek$40:4': ('oju', None, 'Ezek$40:4 "Ọkùnrin náà sọ fún mi pé, “Ọmọ ènìyàn, wò pẹ̀lú ojú rẹ kí o sì gbọ́ pẹ̀lú etí rẹ, kí ó sì farabalẹ̀ sì gbogbo ohun tí mo máa fi hàn ọ, nítorí ìdí nìyí tí a fi mú ọ wá síhìn-ín. Sọ gbogbo ohun tí ó bá rí fún ilé Ísírẹ́lì.”"'), 'Ezek$41:1': (None, None, 'Ezek$41:1 "Lẹ́yìn náà ọkùnrin náà mú mi lọ sí ìta ibi mímọ́, ó sì wọn àwọn àtẹ́rígbà náà; ìbú atẹrígbà náà sì jẹ́ ìgbọ̀nwọ́ mẹ́fà ní ègbẹ́ kọ̀ọ̀kan."'), 'Ezek$41:6': (None, 'ni', 'Ezek$41:6 "Àwọn yàrá ẹ̀gbẹ́ wà ni ìpele mẹ́ta, lórí ara wọn, nígbà ọgbọ̀n lórí ìkọ̀ọ̀kan. Àwọn arópòdógìri wà yípo ògiri ilé Ọlọ́run náà mú kí ògiri àwọn yàrá ẹ̀gbẹ́gbẹ́ náà ní agbára, ni ọ̀nà tí àwọn ìfaratì náà kò fi wọ inú ògiri ilé Ọlọ́run náà lọ."'), 'Ezek$41:15': (None, 'ibi', 'Ezek$41:15 "Ó wá wọn gígùn ilé tí ó dojúkọ gbangba ìta ní agbègbè ilé Ọlọ́run papọ̀ mọ́ àwọn ọ̀dẹ̀dẹ̀ lókè ní ẹ̀gbẹ́ kọ̀ọ̀kan; ó jẹ́ ọgọ́rùn-ún ìgbọ̀nwọ́. Ìta ibi mímọ́, inú ibi mímọ́ náà àti àtẹ̀wọ̀ ẹnu ọ̀nà tí ó dojúkọ ìta gbangba,"'), 'Ezek$41:18': (None, 'oju', 'Ezek$41:18 ní wọn fín àwọn kérúbù àti àwọn igi ọ̀pẹ. Wọn fi àwọn igi ọ̀pẹ bo àárin àwọn kérúbù. Kérúbù kọ̀ọ̀kan ní ojú méjìméjì:'), 'Ezek$42:20': ('ní', None, 'Ezek$42:20 "Báyìí ní ó wọn agbégbé ẹ̀gbẹ́ mẹ́rẹ̀ẹ̀rin. Ó ní ògiri kan yípo rẹ̀, ọgọ́rùn-ún márùn-ún ìgbọ̀nwọ́ ní fífẹ̀, àti ọgọ́rùn-ún ìgbọ̀nwọ́ ní gígùn, láti ya ibi mímọ́ sọ́tọ̀ kúrò ní ibi tí gbogbo ènìyàn ń lọ sọ́tọ̀."'), 'Ezek$43:8': ('run', 'ni', 'Ezek$43:8 "Nígbà tí wọ́n ba gbé ìlóro ilé wọn kangun sí ìlóro ilé mi àti ìlẹ̀kùn wọn sì ẹ̀gbẹ́ ilẹ̀kùn mi, pẹ̀lú ògiri nìkan ní àárin èmi pẹ̀lú wọn, wọ́n ba orúkọ mímọ́ mi jẹ́ pẹ̀lú ìwà ìríra wọn. Nígbà náà ni mo pa wọ́n run ní ìbínú mi."'), 'Ezek$43:13': (None, 'eti', 'Ezek$43:13 "“Ìwọ̀nyí ni wíwọ̀n pẹpẹ ìrúbọ ní gígùn ìgbọ̀nwọ́, ó jẹ́ ìgbọ̀nwọ́ kan ni jíjìn, ó sì jẹ́ ìgbọ̀nwọ́ kan ni fífẹ̀, igun rẹ̀ yípo àti etí rẹ̀ jẹ́ ibú àtẹ́lẹwọ́ kan: Èyí yìí sì ni gíga pẹpẹ ìrúbọ náà:"'), 'Ezek$43:26': (None, None, 'Ezek$43:26 "Fún ọjọ́ méje wọn yóò jẹ́ ètùtù fún pẹpẹ, wọn yóò sì sọ ọ́ di mímọ́; nípa bẹ́ẹ̀ wọn yóò yà á sọ́tọ̀."'), 'Ezek$44:4': ('wò', 'oju', 'Ezek$44:4 " Lẹ́yìn náà, ọkùnrin náà mú mi gba ẹnu ọ̀nà àríwá lọ sí iwájú ilé Ọlọ́run. Mo wò ó mo sì rí ògo OLÚWA tí ó kún inú ilé OLÚWA, mo sì dojúkọ ilẹ."'), 'Ezek$44:6': (None, 'ni', 'Ezek$44:6 "Sọ fún ọlọ̀tẹ̀ ilé Ísírẹ́lì pé, ‘Èyí yìí ni ohun tí OLÚWA Ọba wí: Ìwà ìríra rẹ tí tó gẹ́ẹ́, ìwọ ilé Ísírẹ́lì!"'), 'Ezek$44:12': (None, 'ni', 'Ezek$44:12 "Ṣùgbọ́n nítorí pé wọn ṣe ìránṣẹ́ fún wọn níwájú àwọn ère wọn, tí wọn sì mu ilé Ísírẹ́lì subú sínú ẹ̀ṣẹ̀, nítorí náà, Èmi tí búra nípa nína ọwọ́ sókè pé, wọn gbọdọ̀ jèrè ẹ̀ṣẹ̀ wọn, ní OLÚWA Ọba sọ."'), 'Ezek$44:13': (None, 'ni', 'Ezek$44:13 "Wọn kò gbọdọ̀ súnmọ́ ọ̀dọ̀ mi láti se ìránṣẹ́ fún mi, gẹ́gẹ́ bí àlùfáà tàbí súnmọ́ ìkankan nínú àwọn ohun mímọ́ mi tàbí ẹbọ mi mímọ́ jùlọ; wọn gbọdọ̀ gba ìtìjú ìwà ìríra wọn."'), 'Ezek$44:22': ('ní', None, 'Ezek$44:22 "Wọn ko gbọdọ̀ fẹ́ opó ni ìyàwó, wọn kò sì gbọdọ̀ kọ ìyàwó wọn sílẹ̀; Wọ́n lè fẹ́ àwọn ọmọbìnrin ti kò tí i mọ ọkùnrin rí, tí ó sì jẹ́ ìran àwọn ọmọ Ísírẹ́lì, tàbí kí wọn fẹ́ àwọn opó Àlùfáà."'), 'Ezek$45:1': (None, 'ipin', 'Ezek$45:1 "“ ‘Nígbà ti ẹ̀yin bá pín ilẹ̀ gẹ́gẹ́ bí ogún, ẹ gbọdọ̀ gbé ìpín ilẹ̀ kan kalẹ̀ fún OLÚWA gẹ́gẹ́ bí agbègbè mímọ́, ẹgbẹ̀rún mẹ́ẹ̀dọ́gbọ̀n ìgbọ̀nwọ́ ni gígùn àti ogún ẹgbẹ̀rún ìgbọ̀nwọ́ ni fífẹ̀; gbogbo agbègbè náà ni yóò jẹ́ mímọ́."'), 'Ezek$45:7': (None, None, 'Ezek$45:7 "“ ‘Àwọn ọmọ aládé ni yóò bá ibi mímọ́ pààlà ní ẹ̀gbẹ́ kọ̀ọ̀kan agbégbé tí ó jẹ ibi mímọ́ àti ohun tí ó jẹ́ ti ìlú. Yóò fẹ̀ sẹ́yìn sí ìhà ìwọ̀ oòrùn láti apá ìwọ̀ oòrùn àti si ìlà oòrùn, láti apá ìlà oòrùn, gígùn rẹ̀ yóò jẹ́ láti ìwọ̀ oòrùn sí ààlà ìlà oòrùn, ti ìṣe déédéé rẹ̀ yóò jẹ́ ti ọ̀kan lára àwọn ìpín ẹ̀yà."'), 'Ezek$45:9': ('gba', None, 'Ezek$45:9 "“ ‘Èyí yìí ni OLÚWA Ọba wí: Ìwọ ti rìn jìnnà tó, Ẹ̀yin ọmọ aládé tí Ísírẹ́lì! Fi ìwà ipá àti ìrẹ́jẹ rẹ sílẹ̀ kí ó sì se èyí tí ó tọ́ àti èyí tí ó yẹ. Dáwọ́ gbígbà lọ́wọ́ àwọn ènìyàn mi dúró, ni OLÚWA Ọba sọ."'), 'Ezek$45:13': ('ṣe', None, 'Ezek$45:13 “ ‘Èyí yìí ni ẹ̀bùn pàtàkì tí o gbọdọ̀ ṣe: éfà kẹ́fà láti inú hómérì ọkà kọ̀ọ̀kan àti éfà kẹ́fà láti inú hómérì báálì kọ̀ọ̀kan.'), 'Ezek$45:17': ('fun', 'ṣe', 'Ezek$45:17 "Yóò jẹ́ ojúṣe ọmọ aládé láti pèsè ọrẹ ẹbọ sísun, ọrẹ ẹbọ jíjẹ àti ọrẹ ẹbọ mímu níbi àṣè gbogbo, ti oṣù tuntun àti ní àwọn ọjọ ìsìnmi ni gbogbo àjọ ilé Ísírẹ́lì. Oun yóò pèsè ọrẹ ẹbọ ẹ̀ṣẹ̀, ọrẹ ẹbọ jíjẹ, ọrẹ ẹbọ sísun àti ọrẹ ẹbọ ìdàpọ̀, láti ṣe ètùtù fún ilé Ísírẹ́lì."'), 'Ezek$46:6': ('tuntun', 'odo', 'Ezek$46:6 "Àti ni ọjọ́ oṣù tuntun, ẹgbọrọ màlúù kan àìlábàwọ́n, àti ọ̀dọ́-àgùntàn mẹ́fà, àti àgbò kan: wọn o wà láìlábàwọ́n."'), 'Ezek$46:16': ('fun', None, 'Ezek$46:16 "“ ‘Èyí yìí ni ohun tí OLÚWA Ọba sọ: Tí ọmọ aládé bá mú ọrẹ láti inú ogún ìní rẹ̀ fún ọ̀kan lára àwọn ọmọkùnrin rẹ̀, yóò jẹ́ ìní tíwọn nípa ogún jíjẹ."'), 'Ezek$46:17': ('fun', 'pada', 'Ezek$46:17 "Tí ó bá mú ọrẹ láti inú ogún ìní rẹ̀ fún ọkan lára àwọn ọmọ ọ̀dọ̀, ọmọ le pa mọ́ títí di ọdún ìdásílẹ̀; Lẹ́yìn náà yóò padà sí ọ̀dọ̀ ọmọ aládé. Ogún ìní rẹ̀ jẹ́ ti àwọn ọmọ rẹ̀ nìkan; Ó jẹ́ tiwọn."'), 'Ezek$47:1': (None, 'iloro', 'Ezek$47:1 " Ọkùnrin náà mú mi padà lọ sí àbáwọlé tẹ́ḿpìlì náà, mo sì rí omi tí ó ń tú jáde láti abẹ́ ìloro tẹ́ḿpìlì náà sí apá ìhà ìlà oòrùn (nítorí tẹ́ḿpìlì náà dojúkọ ìhà ìlà oòrùn) Omi náà ń tú jáde sí ìsàlẹ̀ láti abẹ́, ní ìhà gúsù tẹ́ḿpìlì náà, ní ìhà gúsù pẹpẹ."'), 'Ezek$47:9': (None, 'gbe', 'Ezek$47:9 "Àwọn ohun alààyè tí o ń rákò yóò máa gbé ní íbikíbi tí odò ti ń ṣàn. Ẹja yóò pọ̀ ni ọ̀pọ̀lọpọ̀, nítorí pé odò ń ṣàn síbẹ̀ ó sì mú kí omi iyọ̀ tutù nini; nítorí náà níbi tí omi ti ń ṣàn gbogbo nǹkan ni yóò wà ni ààyè."'), 'Ezek$47:10': (None, None, 'Ezek$47:10 Àwọn apẹja yóò dúró ní etí bèbè odò; láti Éńgédì títí dé Énégíláémù àyè yóò wa láti tẹ́ àwọ̀n wọn sílẹ̀. Orísìírísìí ẹja ni yóò wà gẹ́gẹ́ bí ẹja omi òkun ńlá.'), 'Ezek$47:14': ('fun', 'ọwọ', 'Ezek$47:14 "Ìwọ yóò pín ilẹ̀ náà déédéé ní àárin wọn. Nítorí pé mo búra nípa nína ọwọ́ sókè láti fi fún àwọn baba ńlá yin, ilẹ̀ yìí yóò di ogún ìní yín."'), 'Ezek$47:22': ('bi', 'ni', 'Ezek$47:22 Ìwọ yóò pín gẹ́gẹ́ bí ìní fún ara yín àti fún àwọn àjòjì tí ó ń gbé ní àárin yín tí ó sì ní àwọn ọmọ. Ìwọ yóò sì ṣe sí wọn gẹ́gẹ́ bí ọmọ ìbílẹ̀ àwọn ará Ísírẹ́lì; papọ̀ mọ́ yin ni kí a pín ìní fún wọn ní àárin àwọn ẹ̀yà Ísírẹ́lì.'), 'Ezek$47:23': ('fun', None, 'Ezek$47:23 "Ní àárin ẹ̀yàkẹyà tí àwọn àlejò ń gbé; wọn gbọdọ̀ fi ìní tirẹ̀ fún-ún,” ní OLÚWA Ọba pa lásẹ."'), 'Ezek$48:21': (None, None, 'Ezek$48:21 "“Èyí tí ó sẹ́kù ni ẹ̀gbẹ́ méjèèjì agbégbé tí ó jẹ́ ìpín ibi mímọ́ àti ohun ìní ìlú ńlá náà yóò jẹ́ ti àwọn ọmọ aládé. Yóò lọ títí dé ìhà ìlà oòrùn láti ẹgbẹ̀rún mẹ́ẹ̀dọ́gbọ̀n (25,000) ìgbọ̀nwọ́ ti ìpín ibi mímọ́ títí dé ààlà ìlà oòrùn. Agbégbé méjèèjì wọ̀nyí ni gígùn àwọn ìpín ẹlẹ́yàmẹ́yà yóò jẹ́ ti àwọn ọmọ aládé, àti ìpín ibi mímọ́ pẹ̀lú tẹ́ḿpìlì yóò wa ní àárin wọn."'), 'Ezek$48:29': (None, None, 'Ezek$48:29 "“Èyí ni ilẹ̀ tí ẹ̀yin o fi ìbò pín ní ogún fún àwọn ẹ̀yà Ísírẹ́lì, wọ̀nyí sì ni ìpín wọn,” ní OLÚWA Ọlọ́run wí."'), 'Dan$1:2': ('mu', 'ilẹ', 'Dan$1:2 "OLÚWA sì fa Jéhóíákímù ọba Júdà lé e lọ́wọ́, pẹ̀lú àwọn ohun èlò ilé Ọlọ́run. Àwọn nǹkan wọ̀nyí ni ó kó lọ sí ilé òrìṣà ní Bábílónì, sí inú ilé ìṣúra òrìṣà a rẹ̀."'), 'Dan$1:8': (None, 'okan', 'Dan$1:8 "Ṣùgbọ́n Dáníẹ́lì pinnu ní ọkàn ara rẹ̀ pé òun kò ní ba ara òun jẹ́ pẹ̀lú oúnjẹ àti wáìnì ọba, nígbà náà ni ó gba ààyè lọ́wọ́ olórí àwọn ìwẹ̀fà wí pé òun kò fẹ́ ba ara òun jẹ́ ní ọ̀nà yìí."'), 'Dan$1:11': ('ní', None, 'Dan$1:11 "Nígbà náà ni Dáníẹ́lì sọ fún olùṣọ́ tí olórí àwọn ìwẹ̀fà yàn lórí i Dáníẹ́lì, Hananiáyà, Mísáẹ́lì àti Ásáríyà pé,"'), 'Dan$1:13': (None, None, 'Dan$1:13 "Nígbà náà ni kí o fi ìrísí i wa wé ti àwọn ọ̀dọ́mọkùnrin tí wọ́n ń jẹ oúnjẹ ọba, kí o sì ṣe àwa ìránṣẹ́ rẹ ní ìbámu pẹ̀lú u bí o bá ṣe rí i sí."'), 'Dan$2:9': (None, 'ni', 'Dan$2:9 "Tí ẹ̀yin kò bá lè sọ àlá mi, ìjìyà kan ṣoṣo ló wà fún un yín. Ẹ̀yin ti gbèrò láti pa irọ́ àti láti sọ àwọn ọ̀rọ̀ ti ó lè ṣi ni lọ́nà fún mi, títí tí nǹkan yóò fi yí wọ́. Nítorí náà ẹ rọ́ àlá náà fún mi, èmi yóò sì mọ̀ pé ẹ lè túmọ̀ rẹ̀ fún mi.”"'), 'Dan$2:10': ('ohun', None, 'Dan$2:10 "Àwọn awòràwọ̀ sì dá ọba lóhùn pé, “Kò sí ènìyàn kan ní ayé tí ó lè sọ nǹkan tí ọba béèrè! Kò sí ọba náà bí ó ti wù kí ó tóbi àti kí ó lágbára tó, tí í béèrè irú nǹkan bẹ́ẹ̀ lọ́wọ́ àwọn onídán tàbí aláfọ̀ṣẹ tàbí awòràwọ̀."'), 'Dan$2:12': (None, 'binu', 'Dan$2:12 "Èyí mú kí ọba bínú, ó sì kanra, nítorí náà ó pàṣẹ kí a pa gbogbo àwọn amòye Bábílónì run."'), 'Dan$2:17': ('lọ', None, 'Dan$2:17 "Nígbà náà ni Dáníẹ́lì padà lọ sí ilé e rẹ̀, ó sì sọ fún àwọn ẹlẹgbẹ́ ẹ rẹ̀ Hananíáyà, Míṣáẹ́lì àti Ásáríyà."'), 'Dan$2:25': ('ni', 'ṣe', 'Dan$2:25 "Lẹ́ṣẹ̀kan náà, Áríókù yára mú Dáníẹ́lì lọ sí iwájú ọba, ó sọ fún ọba pé, “Èmi ti rí ọkùnrin kan lára àwọn àjèjì tí ó wá láti Júdà, ẹni tí ó lè sọ ìtumọ̀ àlá náà fún ọba.”"'), 'Dan$2:26': ('ni', None, 'Dan$2:26 "Ọba béèrè lọ́wọ́ ọ Dáníẹ́lì ẹni tí a tún ń pè ní Beliteṣáṣárì pé, “Ṣé ìwọ lè sọ ohun tí mo rí nínú àlá mi àti ìtumọ̀ ọ rẹ̀ fún mi?”"'), 'Dan$2:30': ('ni', 'ṣe', 'Dan$2:30 "Ṣùgbn fún èmi, a fi àsírí yìí hàn mí, kì í ṣe pé mo ní ọgbọ́n tí ó pọ̀ ju ti alààyè kankan lọ, ṣùgbọ́n nítorí kí ọba lè mọ ìtumọ̀ àlá àti kí ó lè mọ ohun tí ó wà ní ọkàn rẹ̀."'), 'Dan$2:37': ('agbara', 'ọba', 'Dan$2:37 "Ìwọ ọba jẹ́ ọba àwọn ọba. Ọlọ́run ọ̀run ti fi ìjọba agbára, títóbi àti ògo fún ọ;"'), 'Dan$2:38': (None, 'ọwọ', 'Dan$2:38 "ní ọwọ́ rẹ ló fi gbogbo ènìyàn, àwọn ẹranko ìgbó àti ẹyẹ ojú ọ̀run sí. Ní ibi gbogbo tí wọ́n ń gbé, ó ti fi ọ́ ṣe olórí i wọn. Ìwọ ni orí i wúrà náà."'), 'Dan$2:41': ('pin', 'apakan', 'Dan$2:41 "Bí o ṣe rí i tí àtẹ́lẹsẹ̀ àti ọmọ ìka ẹṣẹ̀ jẹ́ apákan amọ̀ àti apákan irin, bẹ́ẹ̀ ni ìjọba yóò ṣe pín; ṣùgbọ́n yóò sì ní agbára irin díẹ̀ nínú rẹ̀, bí ó ṣe rí i tí irin dàpọ̀ mọ́ amọ̀."'), 'Dan$2:42': (None, 'apakan', 'Dan$2:42 "Bí ọmọ ìka ẹsẹ̀ ṣe jẹ́ apákan irin àti apákan amọ̀, bẹ́ẹ̀ ni ìjọba yìí yóò lágbára lápákan tí kò sì ní lágbára lápákan."'), 'Dan$2:45': (None, None, 'Dan$2:45 "Gẹ́gẹ́ bí o ṣe rí i pé a gé òkúta láti ara òkè, láì ti ọwọ́ ẹnikẹ́ni wá, òkúta èyí tí ó fọ́ irin, idẹ, amọ̀, fàdákà àti wúrà sí wẹ́wẹ́. “Ọlọ́run tí ó tóbi ti fi han ọba, ohun tí yóò ṣẹlẹ̀ ní ọjọ́ iwájú. Òtítọ́ ni àlá náà, bẹ́ẹ̀ ni ìtúmọ̀ rẹ̀ ṣe é gbẹ́kẹ̀lé.”"'), 'Dan$2:48': (None, None, 'Dan$2:48 "Nígbà náà ni ọba gbé Dáníẹ́lì ga, ó sì fún un ní ẹ̀bùn ńlá tí ó pọ̀. Ó sì fi ṣe olórí i gbogbo agbègbè ìjọba Bábílónì àti olórí gbogbo àwọn amòye Bábílónì."'), 'Dan$3:14': ('sin', None, 'Dan$3:14 "Nebukadinéṣárì wí fún wọn wí pé, “Ṣé òtítọ́ ni, Sádírákì, Méṣákì àti Àbẹ́dinígò wí pé ẹ̀yin kò sin òrìṣà mi àti pé ẹ̀yin kò fi orí balẹ̀ fún ère wúrà tí èmi gbé kalẹ̀."'), 'Dan$3:15': (None, 'ni', 'Dan$3:15 "Ní ìsinsìnyìí, tí ẹ̀yin bá ti gbọ́ ohùn ìwo, fèrè, dùùrù, ohun èlò orin olókun, ìpè àti onírúurú orin, bí ẹ̀yin bá ṣetan láti wólẹ̀ kí ẹ̀yin fi orí balẹ̀ fún ère tí mo gbé kalẹ̀ ó dára. Ṣùgbọ́n tí ẹ̀yìn bá kọ̀ láti sìn ín, lójúkan náà ni a ó gbé e yín jù sínú iná ìléru. Ǹjẹ́, ta ni Ọlọ́run náà tí yóò gbà yín kúrò lọ́wọ́ mi?”"'), 'Dan$3:27': ('agbara', 'ọba', 'Dan$3:27 "Àwọn ọmọ aládé, ìjòyè, baálẹ̀, àwọn ìgbìmọ̀ ọba pé jọ sí ọ̀dọ̀ ọ wọn. Wọ́n rí i wí pé iná kò ní agbára lára wọn, bẹ́ẹ̀ ni kò jó wọn lára, bẹ́ẹ̀ ni irun orí i wọn kò jóná, àwọ̀tẹ́lẹ̀ wọn kò jóná, òórùn iná kò rùn ní ara wọn rárá."'), 'Dan$3:28': (None, 'ni', 'Dan$3:28 "Nígbà náà, ni Nebukadinésárì sọ wí pé, “Ìbùkún ni fún Ọlọ́run Ṣádírákì, Méṣákì àti Àbẹ́dinígò, ẹni tí ó rán ańgẹ́lì rẹ̀ láti gba àwọn ìránṣẹ́ rẹ̀ tí ó gbẹ́kẹ̀lé e, wọn kọ àsẹ ọba, dípò èyí, wọ́n fi ara wọn lélẹ̀ ju kí wọn sìn tàbí forí balẹ̀ fún Ọlọ́run mìíràn yàtọ̀ sí Ọlọ́run wọn."'), 'Dan$4:13': (None, None, 'Dan$4:13 "“Lórí ibùsùn mi, mo rí ìran náà, olùṣọ́ kan dúró ṣíwájú u mi, àní ẹni mímọ́ kan, ó ń bọ̀ wá láti ọ̀run"'), 'Dan$4:23': (None, None, 'Dan$4:23 "“Ìwọ ọba, rí ìránṣẹ́ ẹni mímọ́ kan, tí ó ń bọ̀ láti ọ̀run ó sì sọ pé, ‘Gé igi náà kí o sì run ún, ṣùgbọ́n fi kùkùté rẹ tí a dè pẹ̀lú irin àti idẹ sílẹ̀ nínú koríko igbó, nígbà tí gbòǹgbò rẹ̀ sì wà nínú ilẹ̀ kí o sì jẹ́ kí ìrì ọ̀run ṣẹ̀ sórí i rẹ̀, jẹ́ kí ìpín in rẹ̀ wà láàrin ẹranko búburú títí ìgbà méje yóò fi kọjá lórí i rẹ̀.’"'), 'Dan$4:30': ('agbara', 'ọba', 'Dan$4:30 "Ó sọ pé, “Èyí ha kọ́ ni Bábílónì ńlá tí mo kọ́ gẹ́gẹ́ bí ilé ọba, nípa agbára à mi àti fún ògo ọláńlá à mi?”"'), 'Dan$5:2': ('ya', 'mú', 'Dan$5:2 "Bí Beliṣáṣárì ṣe ń mu wáìnì, ó pàṣẹ pé kí wọn kó kọ́ọ̀bù wúrà àti ti fàdákà wá, èyí tí Nebukadinéṣárì bàbá rẹ̀ kó wá láti inú tẹ́ḿpìlì ní Jérúsálẹ́mù, kí ọba àti àwọn ọlọ́lá a rẹ̀, àwọn ìyàwó àti àwọn àlè rẹ̀ kí ó ba à le fi mu wáìnì."'), 'Dan$5:10': (None, None, 'Dan$5:10 "Nígbà tí ayaba gbọ́ ohùn ọba àti àwọn ọlọ́lá rẹ̀, ó wá ilé àsè wá. Ó wí pé, “Kí ọba kí ó pẹ́! Má ṣe jẹ́ kí inú un rẹ bàjẹ́, má sì ṣe jẹ́ kí ojú u rẹ fàro."'), 'Dan$5:15': (None, None, 'Dan$5:15 "A ti mú àwọn amòye àti àwọn awòràwọ̀ wá sí iwájú mí kí wọn ba à le è wá ka àkọsílẹ̀ yìí kí wọn sì sọ ìtumọ̀ rẹ̀ fún mi, ṣùgbọ́n wọn kò le è sọ ìtumọ̀ ohun tí ó jẹ́."'), 'Dan$5:17': ('fun', None, 'Dan$5:17 "Nígbà náà ni Dáníẹ́lì dá ọba lóhùn wí pé, “Fi ẹ̀bùn rẹ pamọ́ fún ara rẹ, tàbí kí o fún ẹlòmíràn. Síbẹ̀ èmi yóò ka àkọsílẹ̀ náà fún ọba, èmi yóò sì sọ ìtumọ̀ rẹ̀."'), 'Dan$5:22': ('wo', 'gbo', 'Dan$5:22 "“Ṣùgbọ́n ìwọ ọmọ rẹ̀, Beliṣáṣárì, ìwọ kò rẹ ara à rẹ sílẹ̀, bí ó tilẹ̀ jẹ́ pé ìwọ mọ nǹkan wọ̀nyí."'), 'Dan$6:1': (None, None, 'Dan$6:1 "Ó dára lójú Dáríúsì láti yan ọgọ́fà àwọn baálẹ̀ sórí ìjọba,"'), 'Dan$6:4': ('ri', 'wa', 'Dan$6:4 "Nítorí èyí, gbogbo àwọn alákòóso àti àwọn baálẹ̀ ń gbérò láti wá ẹ̀ṣẹ̀ kà sí Dáníẹ́lì lọ́rùn nínú ètò ìṣèjọba rẹ̀, ṣùgbọ́n wọn kò rí ẹ̀ṣẹ̀ kankan kà sí i lọ́rùn, wọn kò rí ìwà ìbàjẹ́ kankan tí ó ṣe, nítorí ó jẹ́ olóòtítọ́ kò sì ní ìwà ìjáfara."'), 'Dan$6:6': ('ọba', None, 'Dan$6:6 "Nígbà náà ni àwọn alákòóso àti àwọn baálẹ̀ lọ gẹ́gẹ́ bí ikọ̀ sí ọ̀dọ̀ ọba, wọ́n wí pé: “Ìwọ Dáríúsì ọba, kí o pẹ́!"'), 'Dan$6:7': (None, None, 'Dan$6:7 "Àwọn alákòóso ọba, ìjòyè, baálẹ̀, olùdámọ̀ràn, àti àwọn olórí gbìmọ̀ pọ̀ wí pé kí ọba kéde òfin kan pé ẹnikẹ́ni tí ó bá gba àdúrà sí Ọlọ́run tàbí ènìyàn kankan fún ọgbọ̀n ọjọ́, yàtọ̀ fún ìwọ ọba, a ó ju ẹni náà sí inú ihò kìnnìún."'), 'Dan$6:10': ('fun', None, 'Dan$6:10 "Lóòótọ́ Dáníẹ́lì mọ̀ pé a ti fi ọwọ́ sí ìwé òfin náà, síbẹ̀ ó wọ ilé e rẹ̀ lọ, nínú yàrá òkè, ó sí fèrèsé èyí tí ó kọjú sí Jérúsálẹ́mù sílẹ̀. Ó kúnlẹ̀ lórí orúnkún un rẹ̀ ní ẹ̀mẹ́ta lójoojúmọ́, ó gbàdúrà, ó fi ọpẹ́ fún Ọlọ́run gẹ́gẹ́ bí ìṣe rẹ̀ tẹ́lẹ̀."'), 'Dan$7:5': ('ní', None, 'Dan$7:5 "“Mo sì tún rí ẹranko kejì, ó rí bí irú ẹranko ńlá kan tí ó ń gbé ilẹ̀ òtútù; bíárì. Ó gbé ara sókè ní apá kan, ó sì ní egungun ìhà mẹ́ta láàrin ẹ̀yìn in rẹ̀, wọ́n sì sọ fún un pé, ‘Dìde kí o sì jẹ ẹran tó pọ̀!’"'), 'Dan$7:9': ('ina', 'ina', 'Dan$7:9 " “Bí mo ṣe ń wò, “a gbé ìtẹ́ ọba sí ìkàlẹ̀ ẹni ìgbàanì orí ìtẹ́ rẹ̀ Aṣọ rẹ̀ funfun bí ẹ̀gbọ̀n òwú Irun orí i rẹ̀ funfun bí òwú Ìtẹ́ ọba rẹ̀ rí bí ọwọ́ iná. Àwọn kẹ̀kẹ́ rẹ ń jó bí i iná."'), 'Dan$7:14': (None, 'kọjá', 'Dan$7:14 " A sì fi ìjọba, ògo àti agbára ilẹ̀ ọba fún un; gbogbo ènìyàn, orílẹ̀ èdè àti èdè gbogbo wọn wólẹ̀ fún un. Ìjọba rẹ̀, ìjọba ayérayé ni, èyí tí kò le è kọjá, ìjọba rẹ̀ kò sì le è díbàjẹ́ láéláé."'), 'Dan$7:25': (None, 'ọwọ', 'Dan$7:25 " Yóò sọ̀rọ̀ odi sí ọ̀gá ògo, yóò sì pọ́n ẹni mímọ́ lójú, yóò sì gbèrò láti yí ìgbà àti òfin padà. A ó fi àwọn ẹni mímọ́ lé e lọ́wọ́ fún ìgbà díẹ̀, ní ọdún méjì àti ààbọ̀."'), 'Dan$7:26': ('gba', None, 'Dan$7:26 "“ ‘Ṣùgbọ́n àwọn onídàájọ́ yóò jókòó, nígbà náà ni a ó gba agbára rẹ̀, a ó sì pa á rùn pátapáta títí ayé."'), 'Dan$8:3': (None, None, 'Dan$8:3 "Mo wo òkè mo sì rí àgbò kan tí ó ní ìwo méjì níwájú ù mi, ó dúró sí ẹ̀gbẹ́ odò Úláì, àwọn ìwo méjèèje sì gùn. Ṣùgbọ́n ọ̀kan gùn ju èkejì lọ, èyí tí ó gùn jù ni ó yọ jáde kẹ́yìn."'), 'Dan$8:5': ('ayé', 'ilẹ', 'Dan$8:5 "Bí mo ṣe ń ronú nípa èyí, lójijì ni òbúkọ kan tí ó ní ìwo láàrin ojú u rẹ̀ méjèèjì jáde láti ìhà ìwọ̀ oòrùn, ó la gbogbo ayé kọjá láì fi ara kan ilẹ̀."'), 'Dan$8:12': (None, None, 'Dan$8:12 A fún-un ní ẹgbẹ́ ogun ọ̀run àti ẹbọ ojojúmọ́ nítorí ìwà ọlọ̀tẹ̀ ẹ rẹ̀: ó sọ òtítọ́ nù nínú gbogbo ohun tó ṣe.'), 'Dan$8:13': ('fun', None, 'Dan$8:13 " Nígbà náà, ni mo gbọ́ tí ẹni mímọ́ ń sọ̀rọ̀, àti ẹni mímọ́ mìíràn sọ̀rọ̀ fún-un pé, “Yóò ti pẹ́ tó tí ìran yìí yóò fi wá sí ìmúsẹ-ìran nípa ẹbọ ojojúmọ́, ìṣọ̀tẹ̀ tí ó ń sọ dahoro, àti mímú kí ibi mímọ́ àti ẹgbẹ́ ogun ọ̀run di ìtẹ̀mọ́lẹ̀?”"'), 'Dan$8:19': (None, None, 'Dan$8:19 "Ó sọ wí pé: “Èmi yóò sọ ohun tí yóò sẹlẹ̀ ní ìkẹyìn ní ìgbà ìbínú, nítorí ìran náà jẹ mọ́ àkókò ohun tí a yàn nígbà ìkẹyìn."'), 'Dan$8:23': (None, None, 'Dan$8:23 "“Ní ìgbà ìkẹyìn ìjọba wọn, nígbà tí ìwà búburú àwọn ọlọ̀tẹ̀ bá burú gidigidi ọba tí ojú u rẹ̀ le koko, balógun rìkísí yóò dìde."'), 'Dan$9:1': (None, None, 'Dan$9:1 "Ní ọdun kìn-ín-ní Dáríúsì ọmọ Áhásúérésì, ẹni tí a bí ní Médíà, òun ló jẹ ọba lórí ìjọba Bábílónì."'), 'Dan$9:3': (None, None, 'Dan$9:3 "Nígbà náà, ni mo yípadà sí OLÚWA Ọlọ́run, mo bẹ̀ẹ́ pẹ̀lú àdúrà àti ẹ̀bẹ̀, pẹ̀lú ààwẹ̀, aṣọ ọ̀fọ̀ àti eérú."'), 'Dan$9:13': (None, 'ibi', 'Dan$9:13 "Bí a ti kọ ọ́ sínú òfin Mósè bẹ́ẹ̀ ni gbogbo ibi yìí ti dé bá wa, ṣíbẹ̀ a kò bẹ̀bẹ̀ fún ojú rere OLÚWA Ọlọ́run wa, nípa yíyí padà kí a kúrò nínú ẹ̀ṣẹ̀ wa kí a sì mọ òtítọ́ ọ rẹ."'), 'Dan$9:15': ('mu', 'ilẹ', 'Dan$9:15 "“Ní ìsinsin yìí, OLÚWA Ọlọ́run wa, ẹni tí ó mú àwọn ènìyàn rẹ̀ jáde láti ilẹ̀ Éjíbítì wá, pẹ̀lú ọwọ́ agbára, tí ó fún ara rẹ̀ ní orúkọ tí ó wà títí di òní, a ti ṣẹ̀, a sì ti ṣe búrurú."'), 'Dan$9:16': (None, 'di', 'Dan$9:16 "OLÚWA, ní ìbámu pẹ̀lú ìwà òdodo rẹ, yí ìbínú àti ìrunú rẹ padà kúrò ní Jérúsálẹ́mù ìlú u rẹ, òkè mímọ́ rẹ. Ẹ̀ṣẹ̀ wa àti àìṣedéédé àwọn baba wa ti mú Jérúsálẹ́mù àti ènìyàn rẹ di ẹ̀gàn fún gbogbo àwọn tí ó yí wọn ká."'), 'Dan$9:17': ('ebe', 'adura', 'Dan$9:17 "“Nísinsinyìí, Ọlọ́run wa, gbọ́ àdúrà àti ẹ̀bẹ̀ ìránṣẹ́ rẹ, nítorí i tìrẹ OLÚWA, fi ojú àánú wo ibi mímọ́ rẹ tí ó ti dahoro."'), 'Dan$9:20': ('ebe', 'adura', 'Dan$9:20 "Bí mo ṣe ń sọ̀rọ̀, tí mo sì ń gba àdúrà, tí mo ń jẹ́wọ́ ẹ̀ṣẹ̀ mi àti ẹ̀ṣẹ̀ àwọn Ísírẹ́lì ènìyàn mi, tí mo sì ń mú ẹ̀bẹ̀ mi tọ OLÚWA Ọlọ́run mi nítorí òkè mímọ́ rẹ."'), 'Dan$9:24': ('pari', 'opin', 'Dan$9:24 "“Àádọ́rin ọ̀sẹ̀ ni a pàṣẹ fún àwọn ènìyàn rẹ àti fún àwọn ìlú mímọ́ ọ rẹ láti parí ìrékọjá, láti fi òpin sí ẹ̀ṣẹ̀, láti ṣe ètùtù sí ìwà búburú, láti mú òdodo títí ayé wá, láti ṣe èdìdì ìran àti wòlíì àti láti fi òróró yan ẹni mímọ́ jùlọ."'), 'Dan$9:27': ('ṣe', None, 'Dan$9:27 " Yóò sì fi ìdí i májẹ̀mu kan múlẹ̀ pẹ̀lú ọ̀pọ̀lọpọ̀ fún ọ̀sẹ̀ kan. Ní àárin ọ̀sẹ̀ ni yóò mú òpin bá ẹbọ àti ọrẹ ẹbọ. Àti lórí ìyẹ́ tẹ́ḿpìlì ni yóò dà ìríra sórí àwọn tí ó mú ìdahoro wá, títí tí òpin tí a ti pàṣẹ lórí asọnidahoro yóò fi padà fi dé bá a.”"'), 'Dan$10:4': (None, None, 'Dan$10:4 "Ní ọjọ́ kẹrìnlélógún oṣù àkọ́kọ́, bí mo ti dúró sí etí i bèbè odò ńlá Tígírísì,"'), 'Dan$10:16': ('ète', 'ẹnu', 'Dan$10:16 "Nígbà náà, ni ẹnìkan tí ó rí bí ènìyàn fi ọwọ́ kan ètè mi, mo la ẹnu mi, mo sì bẹ̀rẹ̀ sí ní sọ ọ̀rọ̀, mo sọ fún ẹni tí ó dúró níwájú mi, “Ìrònú sì mú mi, nítorí ìran náà, OLÚWA mi, n kò sì ní okun."'), 'Dan$11:6': ('agbara', 'ọba', 'Dan$11:6 "Lẹ́yìn ọdún púpọ̀ wọn yóò dá májẹ̀mú àlàáfíà, ọmọbìnrin ọba gúṣù yóò lọ sí ọ̀dọ̀ ọba àríwá láti bá a dá májẹ̀mú àlàáfíà, ṣùgbọ́n òun kì yóò lè di agbára apá a rẹ̀ mú, bẹ́ẹ̀ ni òun kì yóò lè dá dúró, ṣùgbọ́n a ó tẹrí i rẹ̀ ba àti àwọn tí ó mú un wá, àti ọmọ tí ó bí, àti ẹni tí ó ń mu lọ́kàn le ní gbogbo àkókò wọ̀nyí."'), 'Dan$11:9': ('ilẹ', None, 'Dan$11:9 "Nigbà náà, ni ọba àríwá yóò gbógun ti ilẹ̀ ọba, Gúṣù, ṣùgbọ́n yóò padà sí orílẹ̀ èdè Òun fúnra rẹ̀"'), 'Dan$11:10': (None, None, 'Dan$11:10 "Àwọn ọmọ rẹ̀ yóò múra ogun wọn yóò kó ogun ńlá jọ, èyí tí a ó gbá lọ bí àgbàrá omi ńlá, yóò sì gbá a lọ títí dé ìlú olódi rẹ̀."'), 'Dan$11:11': (None, None, 'Dan$11:11 "“Nígbà náà ni ọba Gúṣù yóò jáde pẹ̀lú ìbínú, yóò sì bá ọba àríwá jà, ẹni tí yóò kó ọmọ ogun púpọ̀ jọ, ṣùgbọ́n ọba Gúṣù yóò borí i wọn."'), 'Dan$11:13': (None, None, 'Dan$11:13 "Nítorí ọba àríwá yóò lọ kó ogun mìíràn jọ, tí ó pọ̀ ju ti àkọ́kọ́ lẹ́yìn ọ̀pọ̀lọpọ̀ ọdún, yóò jáde pẹ̀lú ọ̀pọ̀lọpọ̀ ogun tí ó ti dira ogun fún dáradára."'), 'Dan$11:17': (None, None, 'Dan$11:17 "Yóò pinnu láti wá pẹ̀lú agbára ìjọba rẹ̀, yóò sì ní májẹ̀mú àlàáfíà pẹ̀lú ọba Gúṣù yóò sì fi ọmọbìnrin rẹ̀ fún-un láti fẹ́ ẹ ní ìyàwó nítorí kí ó lè gba ìjọba, ṣùgbọ́n ète rẹ̀ yìí kì yóò ṣiṣẹ tàbí se ìrànlọ́wọ́ fún-un."'), 'Dan$11:23': ('ṣe', None, 'Dan$11:23 "Lẹ́yìn tí ó wá ṣe ìpinnu pẹ̀lú u rẹ̀, yóò hùwà ẹ̀tàn, pẹ̀lú iwọ̀nba ènìyàn kékeré ni yóò gba ìjọba."'), 'Dan$11:24': (None, 'ni', 'Dan$11:24 "Nígbà tí àwọn agbègbè tí ó lọ́rọ̀ gidigidi wà láìbẹ̀rù ni yóò gbógun tì wọ́n, yóò ṣe ohun tí baba rẹ̀ tàbí baba ńlá rẹ̀ kò ṣe rí, yóò pín ìkógún ẹrù àti ọrọ̀ fún àwọn ọmọ lẹ́yìn in rẹ̀. Yóò pète àti bi àwọn ìlú olódi ṣubú, ṣùgbọ́n fún ìgbà díẹ̀ ni."'), 'Dan$11:25': ('agbara', 'ọba', 'Dan$11:25 "“Pẹ̀lú ogun púpọ̀, yóò sì ru agbára rẹ̀ àti ìgboyà rẹ̀ ṣókè sí ọba Gúṣù. Ọba Gúṣù yóò dìde ogun pẹ̀lú ọ̀pọ̀lọpọ̀ ọmọ ogun tí ó lágbára gidigidi, ṣùgbọ́n kò ní leè dúró, nítorí ọ̀tẹ̀ tí ó gbérò sí i."'), 'Dan$11:30': (None, None, 'Dan$11:30 "Nítorí pé, ọkọ̀ ojú omi àwọn ilẹ̀ etídò ìwọ̀-oòrùn yóò ta kòó, ọkàn rẹ̀ yóò sì pámi. Nígbà náà, ni yóò padà, yóò sì bínú sí májẹ̀mu mímọ́, yóò sì padà, yóò sì fi ojú rere hàn sí àwọn tí ó kọ májẹ̀mu mímọ́ náà."'), 'Dan$11:35': (None, None, 'Dan$11:35 "Lára àwọn tí ó mòye yóò kọsẹ̀, nítorí kí a baà tún wọn ṣe, wọ́n di mímọ́ àti aláìlábàwọ́n títí di ìgbà ìkẹyìn nítorí yóò sì wá ní àkókò tí a yàn."'), 'Dan$11:36': (None, None, 'Dan$11:36 "“Ọba yóò ṣe bí ó ti wù ú yóò sì gbé ara rẹ̀ ga, ju gbogbo òrìṣà lọ, yóò máa sọ àwọn ọ̀rọ̀ tí kò-ṣe-é-gbọ́-létí sí Ọlọ́run àwọn Ọlọ́run, a yóò sì máa yege títí àkókò ìbínú yóò fi parí, nítorí ohun tí a ti pinnu yóò ṣẹlẹ̀."'), 'Dan$11:39': ('ṣe', None, 'Dan$11:39 "Yóò kọ lu àwọn ìlú olódi tí ó lágbára pẹ̀lú ìrànlọ́wọ́ òrìṣà àjèjì, yóò sì bu ọlá ńlá fún ẹni tí ó jẹ́wọ́ rẹ̀. Yóò mú wọn ṣe alákòóso lórí ọ̀pọ̀lọpọ̀ ènìyàn, yóò sì pín ilẹ̀ náà fún wọn gẹ́gẹ́ bí èrè."'), 'Dan$12:7': (None, 'ni', 'Dan$12:7 " Ọkùnrin tí ó wọ aṣọ àlà, ẹni tí ó wà lórí omi odò, gbé ọwọ́ ọ̀tún rẹ̀ àti ọwọ́ òsì rẹ̀, mo gbọ́ tí ó fi ẹni tí ó wà títí láé búra, ó sọ wí pé, “Yóò ṣe ní àkókò kan, àkókò méjì àti ààbọ̀. Nígbà tí agbára àwọn ẹni mímọ́ yóò ti fọ́ tán pátapáta, gbogbo nǹkan wọ̀nyí yóò sì parí.”"'), 'Hos$1:10': (None, None, 'Hos$1:10 "“Ṣùgbọ́n àwọn ọmọ Ísírẹ́lì yóò pọ̀ bíi iyanrìn etí òkun tí a kò le wọ̀n, tí a kò sì le è kà, yóò síṣe. Ní ibi tí wọ́n ti sọ fún wọn pé. ‘Ẹ̀yin kì í ṣe ènìyàn mi,’ níbẹ̀ ni a ó ti máa pè wọ́n ní ‘ọmọ Ọlọ́run alààyè.’"'), 'Hos$2:9': (None, 'pada', 'Hos$2:9 "“Nítorí náà, èmi yóò mú ọkà mi kúrò nígbà tó bá pọ́n, èmi yóò sì mú wáìnì mi kúrò ní àsìkò rẹ̀. Èmi yóò sì gba ìrun àgùntàn irun àgùtàn àti ọ̀gbọ̀ mi padà mo ti fifun un láti bo ìhòòho rẹ̀."'), 'Hos$2:12': ('ni', None, 'Hos$2:12 "Èmi yóò pa àjàrà àti igi ọ̀pọ̀tọ́ rẹ̀ run, Èyí tí ó pè ní èrèe rẹ̀ láti ọ̀dọ̀ àwọn Olólùfẹ́ rẹ̀, Èmi yóò sọ wọ́n di igbó, àwọn ẹranko búburú yóò sì jẹ́ wọn run."'), 'Hos$2:18': (None, 'ilẹ', 'Hos$2:18 "Ní ọjọ́ náà, èmi yóò ṣe májẹ̀mú fún wọn àti àwọn ẹranko ìgbẹ́ àti ẹyẹ ojú ọ̀run àti àwọn ẹ̀dá tí ń rìn lórí ilẹ̀. Kí wọn má bà á bẹ̀rù ara wọn mọ́ Ọrun, idà àti ogun jíjà ni èmi ó parun ní ilẹ̀ náà kí gbogbo ènìyàn bá a lè wà ní àìléwu."'), 'Hos$3:1': ('ife', None, 'Hos$3:1 "OLÚWA sì wí fún mi pé, “Lọ, fi ìfẹ́ hàn sí ìyàwó rẹ̀ padà bí ó tilẹ̀ jẹ́ pé ẹlòmíràn ti fẹ́ ẹ, tí òun sì tún jẹ́ alágbèrè. Fẹ́ ẹ gẹ́gẹ́ bí OLÚWA ṣe fẹ́ àwọn ọmọ Ísírẹ́lì bí ó tilẹ̀ jẹ́ pé ó ti tẹ́lé àwọn òrìṣà tí ó sì fẹ́ràn oúnjẹ tí àwọn aláìkọlà yà sọ́tọ̀.”"'), 'Hos$3:2': (None, None, 'Hos$3:2 "Nítorí náà, mo sì rà á padà ní ṣékélì fàdákà mẹ́ẹ̀dógún (15) àti Ómírì kan àti lẹ́tíkì báálì kan."'), 'Hos$4:10': ('ni', None, 'Hos$4:10 "“Wọn ó máa jẹun ṣùgbọ́n wọn kò ní yó; Wọn ó ṣe àgbèrè ṣùgbọ́n, wọn kò ni pọ̀ sí i, nítorí pé wọ́n ti kọ OLÚWA sílẹ̀"'), 'Hos$4:12': (None, None, 'Hos$4:12 Wọ́n ń gba ìmọ̀ràn lọ́wọ́ ère igi Ọ̀pá igi sì ń dá wọn lóhùn. Ẹ̀mí àgbèrè ti mú wọn sìnà wọ́n sì jẹ́ aláìṣòótọ́ sí Ọlọ́run wọn.'), 'Hos$4:18': (None, 'ni', 'Hos$4:18 Bí ohun mímu wọn bá tilẹ̀ tán wọ́n tún tẹ̀ṣíwájú nínú àgbèrè àwọn olórí wọn fẹ́ràn ohun ìtìjú ju ohun ìyìn lọ.'), 'Hos$5:1': (None, 'ni', 'Hos$5:1 "“Ẹ gbọ́ èyí, ẹ̀yìn àlùfáà! Ẹ fetí sílẹ̀ ẹ̀yin ọmọ Ísírẹ́lì! Ẹ gbọ́, ẹ̀yìn ilé ọba! Ìdájọ́ yìí kàn yín: Ẹ ti jẹ́ ẹ̀bìtì ní Mísípà Àwọ̀n ti a nà sìlẹ̀ lórí Tábórì"'), 'Hos$5:2': ('ni', None, 'Hos$5:2 "Àwọn ọlọ̀tẹ̀ ti gbilẹ̀ nínú ìpànìyàn Gbogbo wọn ni èmi ó bá wí,"'), 'Hos$5:9': (None, 'ni', 'Hos$5:9 Éfúráímù yóò di ahoro ní ọjọ́ ìbáwí láàrin àwọn ẹ̀yà Ísírẹ́lì Mo sọ ohun tí ó dájú.'), 'Hos$5:13': (None, None, 'Hos$5:13 "“Nígbà ti Éfúráímù ri àìsàn rẹ̀, tí Júdà sì rí ojú egbò rẹ̀ ni Éfúráímù bá tọ ará Síríà lọ, ó sì ránṣẹ́ sí ọba ńlá náà fún ìrànlọ́wọ́ ṣùgbọ́n kò le è wò ó sàn bẹ́ẹ̀ ni kò le wo ojú egbò rẹ̀ jinná"'), 'Hos$6:3': ('wá', None, 'Hos$6:3 "Ẹ jẹ́ kí a mọ OLÚWA Ẹ jẹ́ kí a tẹ̀ṣíwájú láti mọ̀ ọ́. Gẹ́gẹ́ bí oòrùn ṣe ń yọ, Yóò jáde; Yóò tọ̀ wá wá bí òjò bí òjò àkọ́rọ̀ ti ń bomirin ilẹ̀.”"'), 'Hos$7:2': ('ni', None, 'Hos$7:2 Ṣùgbọ́n wọn kò mọ̀ pé mo rántí gbogbo ìwà búburú wọn: Ẹ̀ṣẹ̀ wọn gbé wọn mì pátapáta wọ́n wà níwájú mi nígbà gbogbo.'), 'Hos$7:5': (None, 'ni', 'Hos$7:5 Ní ọjọ́ àjọ̀dún ọba wa wáìnì mú ara àwọn ọmọ aládé gbóná ó sì darapọ̀ mọ́ àwọn oníyẹ̀yẹ́.'), 'Hos$7:6': (None, None, 'Hos$7:6 "Ọkàn wọn ń gbóná bí i ààrò wọ́n sì tọ̀ ọ́ lọ pẹ̀lú rìkísí, ìbínú wọn pa lọ́lọ́ ní gbogbo òru ó sì bú jáde bí ọ̀wọ́ iná ní òwúrọ̀."'), 'Hos$7:16': ('ṣubu', 'pada', 'Hos$7:16 "Wọn kò padà sí ọ̀dọ̀ Ọ̀gá ògo; Wọ́n dàbí ọrun tí ó wà fún ìtànjẹ́ Àwọn aṣíwájú wọn yóò ti ipa idà ṣubú nítorí irúnú ahọ́n wọn. Torí èyí, wọn ó fi wọ́n ṣe ẹlẹ́yà ní ilẹ̀ Éjíbítì."'), 'Hos$8:4': ('ṣe', 'ni', 'Hos$8:4 "Wọ́n fi àwọn ọba jẹ ṣùgbọ́n, kì í ṣe nípasẹ̀ mi wọ́n yan ọmọ aládé láì si ìmọ̀ mi nibẹ̀. Wọ́n fi fàdákà àti wúrà ṣe ère fún ara wọn, kì a ba le ké wọn kúrò."'), 'Hos$9:12': (None, 'lọ', 'Hos$9:12 "Bí wọ́n tilẹ̀ tọ́ ọmọ dàgbà. Èmi yóò mú wọn ṣọ̀fọ̀ lórí gbogbo wọn Ègbé ni fún wọn, nígbà tí mo yípadà kúrò lọ́dọ̀ wọn!"'), 'Hos$9:16': (None, None, 'Hos$9:16 "Éfúráímù ti rẹ̀ dànù Gbogbo rẹ̀ sì ti rọ, kò sì so èso, Bí wọ́n tilẹ̀ bímọ. Èmi ó pa àwọn ọmọ wọn tí wọ́n fẹ́ràn jùlọ.” "'), 'Hos$10:1': ('ṣe', 'ni', 'Hos$10:1 Ísírẹ́lì jẹ́ igi àjàrà tó gbalé ó ń sọ èṣo fún ara rẹ̀ Bí èso rẹ̀ ṣe ń pọ̀ bẹ́ẹ̀ ni ó ń kọ́ pẹpẹ sí i bí ilẹ̀ rẹ̀ ṣe ń ṣe rere Ó bu ọlá fún òkúta ìyàsọ́tọ̀ ère rẹ̀.'), 'Hos$10:4': (None, 'ni', 'Hos$10:4 "Wọ́n ṣe ìlérí púpọ̀, wọ́n ṣe ìbúra èké, wọ́n da májẹ̀mú: báyìí ni ìdájọ́ hù sókè bí igi ìwọ̀ ni aporo oko, bi i koríko májèlé láàrin oko tí a ro."'), 'Hos$11:6': (None, 'jẹ', 'Hos$11:6 Idà yóò kọ mọ̀nà ní gbogbo ìlú wọn yóò si bá gbogbo irin ẹnú odi ìlú wọn jẹ́ yóò sì fòpin sí gbogbo èrò wọn.'), 'Hos$12:8': (None, None, 'Hos$12:8 " Éfúráímù gbéraga, “Èmi ní ìní fún ara mi, mo sì ti di ọlọ́rọ̀, pẹ̀lú gbogbo ọrọ̀ mi yìí, wọn kò le ká àìṣedéédéé tàbí ẹ̀ṣẹ̀ kankan mọ́ mi lọ́wọ́” "'), 'Hos$13:2': ('ni', None, 'Hos$13:2 "Báyìí, wọ́n ń dá ẹ̀ṣẹ̀ kún ẹ̀ṣẹ̀ wọn fi fàdákà ṣe ère òrìṣà fúnra wọn ère tí a fi ọgbọ́n dá àrà sí gbogbo wọn jẹ́ iṣẹ́ ọwọ́ oníṣọ̀nà Wọn ń sọ nípa àwọn ènìyàn wọ̀nyí. Pé, Jẹ́ kí àwọn “Ènìyàn tí ń rúbọ fi ẹnu ko àwọn màluu ni ẹnu.”"'), 'Hos$13:6': (None, None, 'Hos$13:6 Wọn ni ìtẹ́lọ́rùn nígbà tí mo fún wọn ní oúnjẹ Nígbà tí wọ́n ní ìtẹ́lọ́rùn tan wọ́n di agbéraga Nígbà náà ni wọ́n sì gbàgbé mi.'), 'Hos$13:11': ('ibinu', 'ibinu', 'Hos$13:11 "Nítorí èyí nínú ìbínú mi ni mo fún un yín lọ́ba Ninú ìbínú gbígbóná mi, Mo sì mú un kúrò"'), 'Hos$13:14': ('iparun', 'iku', 'Hos$13:14 "“Èmi yóò rà wọ́n padà kúrò lọ́wọ́ agbára isà òkú. Èmi yóò rà wọ́n padà lọ́wọ́ ikú Ikú, àjàkálẹ̀-àrùn rẹ dà? Isà okú, ìparun rẹ dà? “Èmi kò ní ṣàánú mọ́."'), 'Hos$13:15': ('orisun omi', 'orisun', 'Hos$13:15 "Bí ó tilẹ̀ gbilẹ̀ láàrin àwọn arákùnrin rẹ̀ afẹ́fẹ́ ìlà oòrùn láti ọ̀dọ̀ OLÚWA yóò wá, Yóò fẹ́ wá láti inú asálẹ̀ orísun omi rẹ̀ yóò gbẹ kànga rẹ̀ yóò gbẹ pẹ̀lú olè yóò fọ́ ilé ẹrù àti gbogbo ilé ìṣúra rẹ̀"'), 'Hos$14:1': (None, 'pada', 'Hos$14:1 Yípadà ìwọ Ísírẹ́lì sí OLÚWA Ọlọ́run rẹ. Ẹ̀ṣẹ̀ rẹ ló fa ìparun rẹ!'), 'Joel$1:6': ('ilẹ', None, 'Joel$1:6 " Nítorí orílẹ̀ èdè kan ti sígun sí ilẹ̀ mìíràn ó ní agbára púpọ̀, kò sì ní òǹkà; ó ní eyín kìnnìún ó sì ní èrìgì abo kìnnìún."'), 'Joel$1:7': (None, None, 'Joel$1:7 "Ó ti pa àjàrà mi run, ó sì ti ya ẹ̀ka igi ọ̀pọ̀tọ́ mi kúrò, ó ti bò èèpo rẹ̀ jálẹ̀, ó sì sọ ọ́ nù; àwọn ẹ̀ka rẹ̀ ni a sì sọ di fun-fun. "'), 'Joel$1:14': ('pe', None, 'Joel$1:14 "Ẹ yà ààwẹ̀ kan sí mímọ́, ẹ pe àjọ kan tí o ní ìrònú, ẹ pe àwọn àgbà, àti gbogbo àwọn ará ilẹ̀ náà jọ sí ilé OLÚWA Ọlọ́run yín, kí ẹ sí képe OLÚWA "'), 'Joel$1:19': ('ina', 'ina', 'Joel$1:19 "OLÚWA, sí ọ ni èmi o ké pè, nítorí iná tí run pápa oko tútú ihà, ọwọ́ iná sí ti jó gbogbo igi igbó."'), 'Joel$2:3': ('ina', 'ina', 'Joel$2:3 "Iná ń jó níwájú wọ́n; ọwọ́ iná sì ń jó lẹ́yìn wọn: ilẹ̀ náà dàbí ọgbà Édẹ́nì níwájú wọn, àti lẹ́yìn wọn bí ahoro ijù; nítòótọ́, kò sì sí ohun tí yóò bọ́ lọ́wọ́ wọn."'), 'Joel$2:5': ('ina', 'ina', 'Joel$2:5 "Bí ariwo kẹ̀kẹ́ ogun ni wọn ń fo ní orí òkè bí ariwo ọ̀wọ́ iná tí ń jó koríko gbígbẹ, bí akọni ènìyàn tí a kó jọ fún ogun. "'), 'Joel$2:7': (None, 'ona', 'Joel$2:7 "Wọn yóò sáré bi àwọn alágbára; wọn yóò gùn odi bí ọkùnrin ológun; olúkúlúkù wọn yóò sì rìn lọ ní ọ̀nà rẹ̀, wọn kì yóò sì yà kúrò ni ọ̀nà wọn."'), 'Joel$2:15': (None, None, 'Joel$2:15 "Ẹ fún ìpè ní Ṣíónì, ẹ ya ààwẹ̀ kan sí mímọ́, ẹ pe àjọ tí ó ni ìrònú."'), 'Joel$2:19': ('ṣe', None, 'Joel$2:19 "Nítòótọ́, OLÚWA yóò dá wọn lóhùn: “Yóò sì wí fun àwọn ènìyàn rẹ̀ pé, Wò ó èmi yóò rán ọkà, àti ọtí wáìnì tuntun, àti òróró síi yín, a ó sì fi wọn tẹ́ yín lọ́rùn: èmi kì yóò si fí yín ṣe ẹ̀gan mọ́ láàrin àwọn aláìkọlà. "'), 'Joel$2:22': ('eso', None, 'Joel$2:22 "Ẹ má bẹ̀rù, ẹranko igbó, nítorí pápá-oko ihà ń rú, nítorí igi ń so èso rẹ̀, igi ọ̀pọ̀tọ́ àti àjàrà ń so èso ipá wọn."'), 'Joel$3:1': ('mú', None, 'Joel$3:1 "“Ní ọjọ́ wọ̀n ọn nì, àti ní àkókò náà, nígbà tí èmi tún mú ìgbékùn Júdà àti Jérúsálẹ́mù padà bọ̀."'), 'Joel$3:3': (None, 'ni', 'Joel$3:3 "Wọ́n si ti di ibò fún àwọn ènìyàn mi; wọ́n sì ti fi ọmọdé-kùnrin kan fún panṣágà obìnrin kan, wọ́n sì ta ọmọdé bìnrin kan fún ọtí wáìnì, kí wọ́n kí ó lè mu."'), 'Joel$3:7': ('ni', None, 'Joel$3:7 "“Kíyèsì í, èmi yóò gbé wọ́n dìde kúrò níbi tì ẹ̀yin ti tà wọ́n sí, èmi yóò sì san ẹ̀san ohun ti ẹ ṣe padà sórí ara yín."'), 'Joel$3:13': (None, 'wá', 'Joel$3:13 "Ẹ tẹ̀ dòjé bọ̀ ọ́, nítorí ìkórè pọ́n: ẹ wá, ẹ ṣọ̀kalẹ̀; nítorí ìfúntí kún, nítorí àwọn ọpọ́n kún-à-kún-wọ́sílẹ̀ nítorí ìwà búburú wọn pọ̀!” "'), 'Amos$1:5': ('lọ', None, 'Amos$1:5 "Èmi yóò ṣẹ́ ọ̀pá ìdáàbú Dámásíkù; Èmi yóò sì pa ọba tí ó wà ní pẹ̀tẹ́lẹ̀ Áfénì run Àti ẹni tí ó di ọ̀pá aládé mú ní Bẹti-Édénì. Àwọn ará a Árámù yóò lọ sí ìgbèkùn sí Kírì,” ni OLÚWA wí."'), 'Amos$1:11': ('ibinu', 'ibinu', 'Amos$1:11 " Èyí ni ohun tí OLÚWA wí: “Nítorí ẹ̀ṣẹ̀ mẹ́ta tí Édómù, àní nítorí mẹ́rin, Èmi kò ní yí ìpinnu ìjìyà mi padà Nítorí òhun fi idà lépa arákùnrin rẹ̀, Ó sì gbé gbogbo àánú sọnù ìbínú rẹ̀ sì ń faniya títí ó sì pa ìbínú rẹ̀ bí èéfín mọ́"'), 'Amos$1:13': (None, None, 'Amos$1:13 " Èyí ni ohun tí OLÚWA wí: “Nítorí ẹ̀ṣẹ̀ mẹ́ta ti Ámónì àní mẹ́rin, Èmi kò ní yí ìpinnu ìjìyà mi padà, Nítorí wọn ti la inú àwọn aboyún Gílíádì kí wọ́n ba à lè fẹ ilẹ̀ wọn sẹ́yìn."'), 'Amos$2:1': (None, None, 'Amos$2:1 " Èyí ni ohun tí OLÚWA wí; “Nítorí ẹ̀ṣẹ̀ mẹ́ta ti Móábù, àní fún mẹ́rin, Èmi kò ní yí ìpinnu ìjìyà mi padà, Nítorí ó ti sun-ún, di eérú, egungun ọba Édómù"'), 'Amos$2:4': (None, 'ni', 'Amos$2:4 "Èyí ni ohun tí OLÚWA wí: “Nítorí ẹ̀ṣẹ̀ mẹ́ta ti Júdà, àní fún mẹ́rin, Èmi kò ní yí ìpinnu ìjìyà mi padà Nítorí wọn ti kọ òfin OLÚWA sílẹ̀ wọn kò sì pa àwọn ìlànà rẹ̀ mọ́ Nítorí àwọn òrìṣà ti sì wọ́n lọ́nà Òrìṣà tí àwọn Baba ńlá wọn ń tẹ̀lé"'), 'Amos$2:6': (None, None, 'Amos$2:6 "Èyí ni ohun tí OLÚWA wí: “Nítorí ẹ̀ṣẹ̀ mẹ́ta Ísírẹ́lì àní fún mẹ́rin, Èmi kò ní yí ìpinnu ìjìyà mi padà wọ́n ta olódodo fún fàdákà àti aláìní fún bàtà ẹsẹ̀ méjèèjì."'), 'Amos$2:10': ('mu', 'ilẹ', 'Amos$2:10 "“Èmi mú un yín gòkè láti ilẹ̀ Éjíbítì wá, mo sì sìn yín la ihà já ní ogójì ọdún láti fi ilẹ̀ àwọn ará Ámórì fún un yín."'), 'Amos$3:1': ('mu', 'ilẹ', 'Amos$3:1 "Ẹ gbọ́ ọ̀rọ̀ tí Ọlọ́run ti sọ nípa rẹ̀, ẹ̀yin ènìyàn Ísírẹ́lì nípa àwọn ìdílé tí mo mú jáde láti Éjíbítì:"'), 'Amos$3:5': (None, None, 'Amos$3:5 Ǹjẹ́ ẹyẹ ṣubú sínú okùn ọdẹ lórí ilẹ̀ nígbà tí a kò dẹ okùn ọdẹ fún un? Okùn ọdẹ ha lè hù jáde lórí ilẹ̀ nígbà tí kò sí ohun tí yóò mú?'), 'Amos$3:11': (None, 'ilẹ', 'Amos$3:11 "Nítorí náà, báyìí ni OLÚWA Ọlọ́run wí: “Àwọn ọ̀tá yóò pa ilẹ̀ náà run; yóò wó ibi gíga yín palẹ̀ a ó sì ba ààfin rẹ̀ jẹ́.”"'), 'Amos$4:6': (None, 'ni', 'Amos$4:6 "“Èmi fún un yín ní inú òfìfo ní gbogbo ìlú àti láìní àkàrà ní gbogbo ibùgbé yín, ṣíbẹ̀, ẹ̀yin kò tí ì yípadà sími,” ni OLÚWA wí. "'), 'Amos$4:7': (None, 'ni', 'Amos$4:7 “Àti pẹ̀lú mo mú òjò dúró nígbà tí ìkórè ku oṣù mẹ́ta. Mo rán òjò sí ibùgbé kan ṣùgbọ́n kò rọ̀ sí ìlú mìíràn. Oko kan ní òjò; àwọn mìíràn kò ní ó sì gbẹ.'), 'Amos$4:10': ('ṣe', 'ni', 'Amos$4:10 "“Mo rán àjàkálẹ̀-àrùn sí i yín bí mo ti ṣe sí Éjíbítì. Mo fi idà pa àwọn ọ̀dọ́mọkùnrin yín. Pẹ̀lú àwọn ẹṣin tí a kó ní ìgbékùn. Mo kún imú yín fún òórùn ibùdó yín, síbẹ̀ ẹ̀yin kò yípadà sí mi,” ni OLÚWA wí. "'), 'Amos$5:2': ('dide', None, 'Amos$5:2 “Wúndíá Ísírẹ́lì ṣubú láì kò sì le padà dìde ó di ẹni ìkọ̀tì ní ilẹ̀ rẹ̀ kò sí ẹni tí yóò gbé e nàró.”'), 'Amos$5:4': (None, None, 'Amos$5:4 Èyí ni ohun tí OLÚWA sọ fún ilé Ísírẹ́lì: “Wá mi kí o sì yè;'), 'Amos$5:6': (None, None, 'Amos$5:6 "Ẹ wá OLÚWA, ẹ̀yin yóò sì yè, Kí ó má ba à gbilẹ̀ bí iná ní Jósẹ́fù A sì jó o run Bẹ́tẹ́lì kò sì ní rí ẹni tí yóò bomi pa á. "'), 'Amos$5:7': (None, None, 'Amos$5:7 Ẹ̀yin tí ẹ̀ sọ òdodo di ìkorò Tí ẹ sì gbé olódodo ṣánlẹ̀'), 'Amos$5:11': ('gba', 'ni', 'Amos$5:11 "Ìwọ ń tẹ talákà mọ́lẹ̀ o ń fi ipá gba ọkà lọ́wọ́ wọn Nítorí náà, ìwọ ti fi òkúta tí a gbẹ́ kọ́lé ṣùgbọ́n ẹ kò sì ní gbé inú wọn Nítòótọ́ ìwọ ti gbin ọgbà àjàrà tí ó lẹ́wà. Ìwọ kò ní mu wáìnì inú wọn"'), 'Amos$5:14': (None, None, 'Amos$5:14 "Wá rere, má ṣe wá búburú kí ìwọ ba à le yè Nígbà náà ni OLÚWA alágbára yóò wà pẹ̀lú ù rẹ. Òun yóò sì wà pẹ̀lú ù rẹ bí ìwọ ṣe wí"'), 'Amos$5:26': (None, 'ni', 'Amos$5:26 "Ẹ̀yin ń gbé ibi ìrúbọ àwọn ọba yín ibùgbé àwọn òrìṣà yín àní, ti àwọn òrìṣà yín tí ó níyì jùlọ, èyí tí ẹ̀yin fi ọwọ́ ara yín ṣe."'), 'Amos$6:10': ('gba', None, 'Amos$6:10 "Bí ẹbí tí ó yẹ kí ó gbé òkú wọn jáde fún sínsin bá wọlé, bí o ba sì béérè pé ǹjẹ́ ẹnìkan wa tí ó farapamọ́ níbẹ̀, “Njẹ ẹnìkankan wà lọ́dọ̀ yín?” tí ó bá sì dáhùn wí pé, “Rárá,” nígbà náà ni yóò wí pé, “Pa ẹnu rẹ mọ́ àwa kò gbọdọ̀ dárúkọ OLÚWA.”"'), 'Amos$7:17': ('lọ', 'kú', 'Amos$7:17 "“Nítorí náà, èyí ni ohun ti OLÚWA wí: “ ‘Ìyàwó rẹ yóò di paṣángà ni ìlú, àwọn ọmọ ọkùnrin àti obìnrin rẹ yóò ti ipa idà ṣubú. A ó wọn ilẹ̀ rẹ, a ó sì pín in àti ìwọ pẹ̀lú yóò kú ni ilẹ̀ àìmọ́. Ísírẹ́lì yóò sì lọ sí ìgbèkùn, kúrò ní ilẹ̀ ìní wọn.’ ”"'), 'Amos$8:4': (None, None, 'Amos$8:4 "Ẹ gbọ́ èyí, ẹ̀yin tí ń tẹ aláìní lórí ba, tí ẹ sì ń sọ talákà di ilẹ"'), 'Amos$8:9': ('ṣe', None, 'Amos$8:9 "“Yóò sì ṣe ní ọjọ́ náà,” ni OLÚWA Ọlọ́run wí, “Èmi yóò mú òòrùn wọ̀ ní ọ̀sán, Èmi yóò mú kí ayé ṣókùnkùn ní ọ̀ṣán gangan."'), 'Amos$9:2': (None, 'mú', 'Amos$9:2 "Bí wọ́n tilẹ wa ilẹ lọ sí ìpò òkú Láti ibẹ̀ ni ọwọ́ mi yóò ti tẹ̀ wọ́n Bí wọ́n tilẹ̀ gun òkè ọ̀run lọ, láti ibẹ̀ ni èmi yóò ti mú wọn sọ̀kalẹ̀."'), 'Amos$9:9': (None, None, 'Amos$9:9 "“Nítorí Èmi yóò pàṣẹ, Èmi yóò sì mi ilé Ísírẹ́lì ní àárin àwọn orílẹ̀ èdè bí a ti ń jọ ọkàn nínú ajọ̀ tí hóró kín ń kín kì yóò bọ́ sórí ilẹ̀."'), 'Amos$9:13': (None, 'wá', 'Amos$9:13 " “Ọjọ́ náà ń bọ,” ni OLÚWA wí, “Tí ẹni tí ń tulẹ̀ yóò lé ẹni tí ń kórè bá Tí ẹni tí ń fún èso àjàrà yóò lé ẹni tí ń gbìn bá Àwọn òkè ńlá yóò sì kán ọtí wáìnì sílẹ̀ Tí yóò sì ṣàn láti ara àwọn òkè kékèké."'), 'Amos$9:14': ('ṣe', 'kọ', 'Amos$9:14 "Èmi yóò si tún mú ìgbèkùn Ísírẹ́lì ènìyàn mi padà bọ̀ Wọn yóò sì kọ́ ahoro ìlú, wọn yóò sì máa gbé inú wọn Wọn yóò sì gbin àjàrà, wọn yóò sì mu ọtí wáìnì wọn wọn yóò sì se ọgbà pẹ̀lú, wọn yóò sì jẹ èso inú wọn"'), 'Amos$9:15': ('fi fun', 'ni', 'Amos$9:15 "Èmi yóò gbin Ísírẹ́lì sí orí ilẹ̀ rẹ̀. A kì yóò sì fà wọn tu mọ́ láéláé kúrò ni orí ilẹ̀ ti mo fi fún wọn,” ni OLÚWA Ọlọ́run rẹ wí."'), 'Obad$1:2': (None, None, 'Obad$1:2 "“Kíyèsí i, Èmi yóò sọ ọ́ di kékeré láàrin àwọn aláìkọlà; ìwọ yóò sì di gígàn lọ́pọ̀lọpọ̀,"'), 'Obad$1:13': (None, 'ni', 'Obad$1:13 "Ìwọ kì bá tí wọ inú ibodè àwọn ènìyàn mi lọ, ní ọjọ́ àjálù wọn. Ìwọ kì bá tí fojú kó wọn mọ́lẹ̀ nínu ìdàmú wọn, ní ọjọ́ àjálù wọn. Ìwọ kì bá tí jí ẹrù wọn kó, ní ọjọ́ ìpọ́njú wọn."'), 'Obad$1:18': ('ina', 'ina', 'Obad$1:18 "Ilé Jákọ́bù yóò sì jẹ́ iná àti ilé Jósẹ́fù ọwọ́ iná ilé Ísọ̀ yóò jẹ àkékù koríko wọn yóò fi iná sí i, wọn yóò jo run. Kì yóò sí ẹni tí yóò kù ní ilé Ísọ̀.” Nítorí OLÚWA ti wí i."'), 'Obad$1:19': (None, None, 'Obad$1:19 "Àwọn ará Gúsù yóò ni òkè Ísọ̀, àwọn ará ẹsẹ̀ òkè yóò ni ilẹ̀ àwọn ará Fílísítíánì ní ìní. Wọn yóò sì ni oko Éfúráímù àti Samáríà; Bẹ́ńjámínì yóò ní Gílíádì ní ìní."'), 'Jonah$1:4': (None, None, 'Jonah$1:4 "Nígbà náà ni OLÚWA rán ìjì ńlá jáde sí ojú òkun, ìjì líle sì wà nínú òkun tóbẹ́ẹ̀ tí ọkọ̀ náà dàbí ẹni pé yóò fọ́."'), 'Jonah$1:13': ('mú', 'ilẹ', 'Jonah$1:13 "Ṣùgbọ́n àwọn ọkùnrin náà wà á kíkan láti mú ọkọ̀ wà sí ilẹ̀: ṣùgbọ́n wọn kò lè ṣe é: nítorí tí òkun túbọ̀ ru síi, ó sì ja ẹ̀fúùfù líle sí wọn."'), 'Jonah$4:3': ('ni', None, 'Jonah$4:3 "Ǹjẹ́ báyìí, OLÚWA, èmi bẹ̀ ọ, gba ẹ̀mí mi kúrò lọ́wọ́ mi nítorí ó sàn fún mi láti kú ju àti wà láàyè lọ.”"'), 'Jonah$4:5': (None, None, 'Jonah$4:5 "Jónà sì jáde kúrò ní ìlú náà, ó sì jókòó níhà ìlà-oòrùn ìlú náà. Ó sì pa àgọ́ kan níbẹ̀ fún ara rẹ̀, ó sì jókòó ni ibòòji ní abẹ́ rẹ̀ títí yóò fi rí ohun tí yóò ṣẹlẹ̀ sí ìlú náà."'), 'Jonah$4:8': ('ni', None, 'Jonah$4:8 "Ó sì ṣe, nígbà tí òòrùn yọ, Ọlọ́run pèsè ẹ̀fúùfù gbígbóná tí ìlà-oòrùn; oòrùn sì pa Jónà lórí tóbẹ́ẹ̀ tí ó fi rẹ̀ ẹ́. Ó sì fẹ́ nínú ara rẹ̀ láti kú, ó sì wí pé, “Ó sàn fún mi láti kú ju àti wà láàyè lọ.”"'), 'Mic$1:11': (None, 'gba', 'Mic$1:11 "Ẹ kọjá lọ ni ìhòòhò àti ni ìtìjú, ìwọ tí ó ń gbé ni Sáfírì. Àwọn tí ó ń gbé ni Sáánánì kì yóò sì jáde wá. Bétésélì wà nínú ọ̀fọ̀; A ó sì gba ààbò rẹ̀ kúrò lọ́wọ́ yin."'), 'Mic$2:1': (None, 'ibi', 'Mic$2:1 "Ègbé ni fún àwọn tí ń gbérò àìṣedéédéé tí wọ́n sì ń ṣiṣẹ́ ibi lórí ibùsùn wọn! Nígbà tí ojúmọ́ bá mọ́, wọn yóò gbé e jáde nítorí ó wà ní agbára wọn láti se é."'), 'Mic$2:9': ('ya', 'ni', 'Mic$2:9 "Ẹ̀yin sì lé obìnrin àwọn ènìyàn mi kúrò nínú ilé ayọ̀ wọn, ẹ̀yin sì ti gba ògo mi, kúrò lọ́wọ́ àwọn ọmọ wọn láéláé."'), 'Mic$2:12': ('agbo', 'agbo', 'Mic$2:12 "“Èmi yóò kó gbogbo yín jọ, ìwọ Jákọ́bù; Èmi yóò gbá ìyókù Ísírẹ́lì jọ. Èmi yóò kó wọn jọ pọ̀ gẹ́gẹ́ bí àgùntàn nínú agbo ẹran, gẹ́gẹ́ bí ọ̀wọ́ ẹran nínú agbo wọn, ibẹ̀ yóò sì pariwo nítorí ọ̀pọ̀ ènìyàn."'), 'Mic$2:13': (None, 'kọjá', 'Mic$2:13 "Ẹni tí ó ń fọ ọ̀nà yóò lọ sókè níwájú wọn; Wọn yóò fọ láti ẹnu-bodè, wọn yóò sì jáde lọ. Àwọn ọba wọn yóò sì kọjá lọ níwájú wọn, OLÚWA ni yóò sì ṣe olórí wọn.”"'), 'Mic$3:5': (None, 'mura', 'Mic$3:5 "Báyìí ni OLÚWA wí: “Ní ti àwọn Wòlíì tí ń sí àwọn ènìyàn mi lọ́nà, tí ẹnìkan bá fún wọn ní oúnjẹ, wọn yóò kéde àlàáfíà; Ṣùgbọ́n tí kò bá fi nǹkan sí wọn ní ẹnu, wọn yóò múra ogun sí i."'), 'Mic$3:12': (None, 'òkè', 'Mic$3:12 "Nítorí náà, nitorí tiyín, ni a ó ṣe ro Síónì bí oko, Jérúsálẹ́mù yóò sì di ebè àti òkè tẹ́ḿpìlì bí i ibi gíga igbo."'), 'Mic$4:6': (None, None, 'Mic$4:6 "“Ní ọjọ́ náà,” ni OLÚWA wí, “Èmi yóò kó àwọn arọ jọ; èmi yóò sì ṣà àwọn tí a lé kúrò ní ìlú jọ, àti àwọn ẹni tí èmi ti pọ́n lójú."'), 'Mic$4:10': (None, 'ìrora', 'Mic$4:10 "Má a yí síhìn-ín sọ́hùn-ún nínú ìrora, ìwọ obìnrin Síónì, bí ẹni tí ń rọbí, nítorí nísinsìn yìí ìwọ yóò jáde lọ kúrò ní ìlú, ìwọ yóò sì máa gbé inú igbó. Ìwọ yóò lọ sí Bábílónì; níbẹ̀ ni ìwọ yóò sì ti rí ìgbàlà. Níbẹ̀ ni OLÚWA yóò ti rà ọ́ padà kúrò lọ́wọ́ àwọn ọ̀tá rẹ. "'), 'Mic$4:13': (None, 'ere', 'Mic$4:13 "“Dìde, kí ó sì máa pa ọkà ìwọ ọmọbìnrin Síónì, nítorí èmi yóò sọ ìwo rẹ̀ di irin, èmi yóò sì sọ pátakó rẹ̀ di idẹ ìwọ yóò run ọ̀pọ̀lọpọ̀ orilẹ̀-èdè pátapáta.” Èmi yóò sì ya èrè wọn sọ́tọ̀ fún OLÚWA àti ọrọ̀ wọn sí OLÚWA gbogbo aye."'), 'Mic$5:3': (None, 'pada', 'Mic$5:3 "Nítorí náà Ísírẹ́lì yóò di kíkọ̀sílẹ̀ pátápátá títí di àkókò tí ẹni tí ń rọbí yóò fi bí, àti àwọn ìyókù arákùnrin rẹ̀ yóò fi padà láti dara pọ̀ mọ́ àwọn Ísírẹ́lì. "'), 'Mic$5:4': ('duro', 'duro', 'Mic$5:4 "Òun yóò sì dúró, yóò sì máa ṣe Olùsọ́-àgùntàn àwọn agbo-ẹran rẹ̀ ní agbára OLÚWA, ní ọláńlá orúkọ OLÚWA Ọlọ́run rẹ̀. Wọn yóò sì wà láìléwu, nítorí nísinsin yìí ni títóbi rẹ yóò sì dé òpin ayé."'), 'Mic$5:5': (None, 'eniyan', 'Mic$5:5 "Òun yóò sì jẹ́ àlàáfíà wọn. Nígbà tí àwọn Ásíríà bá gbógun sí ilẹ̀ wa tí wọ́n sì ń yan lórí odi alágbára wa, nígbà náà ni a ó gbé olúsọ́-àgùntàn méje dìde sí i, àti olórí ènìyàn mẹ́jọ."'), 'Mic$5:7': (None, 'eniyan', 'Mic$5:7 "Ìyòókù Jákọ́bù yóò sì wà láàrin ọ̀pọ̀lọpọ̀ ènìyàn bí ìrì látí ọ̀dọ̀ OLÚWA wa, bí ọ̀wàrà òjò lórí koríko, tí kò gbẹ́kẹ̀lé ènìyàn tàbí kò dúró de àwọn ọmọ ènìyàn."'), 'Mic$5:10': (None, None, 'Mic$5:10 "“Ní ọjọ́ náà,” ni OLÚWA wí, “Èmi yóò pa àwọn esin rẹ̀ run kúrò láàrin rẹ èmi yóò sì pa àwọn kẹ̀kẹ́ ogun rẹ̀ run."'), 'Mic$6:4': ('mu', 'ilẹ', 'Mic$6:4 "Nítorí èmi mú un yín gòkè láti ilẹ̀ Éjíbítì wá, mo sì rà yín padà láti ilẹ̀ ẹrú wá. Mo rán Mósè láti darí yín bákan náà mo rán Árónì àti Míríámù ṣíwájú rẹ̀."'), 'Mic$6:9': (None, 'gbo', 'Mic$6:9 "Gbọ́! Ohùn OLÚWA kígbe sí ìlú ńlá náà, ọlọgbọ́n yóò sì rí orúkọ rẹ̀! “Ẹ gbọ́ ọ̀pá àti Ẹni náà tí ó yàn án"'), 'Mic$6:14': (None, None, 'Mic$6:14 "Ìwọ yóò jẹun ṣùgbọ́n kì yóò yó; ìyàn yóò wà láàrin rẹ; Ìwọ yóò kó pamọ́, ṣùgbọ́n ìwọ kì yóò lò làìléwu, nítorí ohun tí ìwọ kò pamọ́ ni èmi yóò fi fún idà."'), 'Mic$7:2': (None, None, 'Mic$7:2 "Àwọn olódodo sì ti kúrò ní ilẹ̀ náà, kò sì sí ẹnìkan tí ó ṣẹ́kù tí ó jọ olóòótọ́; gbogbo wọn sì ń purọ́ ní dídúró nítorí ìtàjẹ̀sílẹ̀, olúkúlùkù wọ́n sì ń fi àwọ̀n de arákùnrin rẹ̀."'), 'Mic$7:4': (None, None, 'Mic$7:4 "Ẹni tí ó sàn jùlọ nínú wọn sì dàbí ẹ̀gún, ẹni tí ó jẹ́ olódodo jùlọ burú ju ẹ̀gún ọgbà lọ. Ọjọ́ àwọn olùsọ́ rẹ̀ ti dé, àti ọjọ́ tí Ọlọ́run bẹ̀ ọ́ wò. Nísinsin yìí ní àkókò ìdààmú wọn."'), 'Mic$7:6': (None, None, 'Mic$7:6 " Nítorí tí ọmọkùnrin kò bọ̀wọ̀ fún baba rẹ̀ ọmọbìnrin sì dìde sí ìyá rẹ̀, aya ọmọ sí ìyá ọkọ rẹ̀, ọ̀tá olúkúlùkù ni àwọn ará ilé rẹ̀."'), 'Mic$7:7': ('duro', 'wo', 'Mic$7:7 "Ṣùgbọ́n ní tèmi, èmi ní wòye ní ìrètí sí OLÚWA, Èmi dúró de Ọlọ́run olùgbàlà mi; Ọlọ́run mi yóò sì tẹ́tí sí mi."'), 'Mic$7:8': ('ṣubu', 'imole', 'Mic$7:8 "Má ṣe yọ̀ mí, ìwọ ọ̀tá mi; Bí mó bá ṣubú, èmi yóò dìde. Nígbà tí mo bá jókòó sínú òkùnkùn OLÚWA yóò jẹ́ ìmọ́lẹ̀ fún mi."'), 'Mic$7:9': (None, None, 'Mic$7:9 "Nítorí èmi ti dẹ́sẹ̀ sí i, Èmi yóò faradà ìbínú OLÚWA, títí òun yóò ṣe gba ẹjọ́ mi rò, tí yóò sì ṣe ìdájọ́ mi. Òun yóò sì mú mi wá sínú ìmọ́lẹ̀; èmi yóò sì rí òdodo rẹ̀."'), 'Nah$1:5': (None, 'ayé', 'Nah$1:5 "Àwọn òkè ńlá wárìrì níwájú rẹ̀, àwọn òkè kéékèèkéé sì di yíyọ́, ilẹ̀ ayé sì jóná níwájú rẹ̀, àní ayé àti gbogbo àwọn tí ń gbé inú rẹ̀."'), 'Nah$1:13': (None, None, 'Nah$1:13 Nísinsin yìí ni èmi yóò já àjàgà wọn kúrò ní ọrùn rẹ èmi yóò já sẹkẹ́sẹkẹ̀ rẹ dànù.”'), 'Nah$1:14': ('ṣe', 'orukọ', 'Nah$1:14 "OLÚWA ti fi àṣẹ kan lélẹ̀ nítorí tìrẹ Nínéfè: “Ìwọ kì yóò ni irú ọmọ láti máa jẹ́ orúkọ rẹ mọ́, Èmi yóò pa ère fínfín àti ère dídà run tí ó wà nínú tẹ́ḿpílì àwọn ọlọ́run rẹ. Èmi yóò wa ibojì rẹ, nítorí ẹ̀gbin ni ìwọ.” "'), 'Nah$2:5': ('pese', 'ṣe', 'Nah$2:5 "Òun yóò ṣe àṣàrò àwọn ọlọ́lá rẹ̀; ṣíbẹ̀ wọ́n ń kọsẹ̀ ní ojú ọ̀nà wọn; wọn sá lọ sí ibi odi rẹ̀, a ó sì pèsè ààbò rẹ̀."'), 'Nah$2:13': ('jo', None, 'Nah$2:13 "“Kíyèsíi èmi dojú kọ ọ́,” ni OLÚWA awọn ọmọ ogun wí. “Èmi yóò sì fi kẹ̀kẹ́ rẹ̀ wọ̀n-ọn-nì jóná nínú èéfín, idà yóò sì jẹ ọmọ kìnnìún rẹ̀ wọ̀n-ọn-nì run. Èmi yóò sì ké ohun ọdẹ rẹ kúrò lórí ilẹ̀ ayé Ohùn awọn òjíṣẹ́ rẹ ni a kì yóò sì tán gbọ́ mọ́.”"'), 'Nah$3:8': ('odi', None, 'Nah$3:8 "Ǹjẹ́ ìwọ ha sàn ju Tébésì lọ, èyí tí ó wà ní ibi odò, Náílì tí omi sì yí káàkiri? Odò náà sì jẹ́ ààbò rẹ̀, omi si jẹ́ odi rẹ̀."'), 'Nah$3:14': (None, 'ṣe', 'Nah$3:14 "Ìwọ pọn omi de ìhámọ́, mú ile ìsọ́ rẹ lágbára sí i wọ inú amọ̀ kí o sì tẹ erùpẹ̀, kí ó sì tún ibi tí a ti ń sun bíríkì-amọ̀ ṣe kí ó le."'), 'Nah$3:18': (None, None, 'Nah$3:18 "Ìwọ ọba Ásíríà, àwọn olùṣọ àgùntàn rẹ̀ ń tòògbé; awọn ọlọ́lá rẹ yóò máa gbé inú ekuru. Rẹ àwọn ènìyàn rẹ fọ́nká lórí àwọn òkè ńlá, tí ẹnikẹni kò sì kó wọn jọ."'), 'Hab$1:6': ('ilẹ', None, 'Hab$1:6 " Nítorí pé, èmi yóò gbé àwọn ara Bábílónì dìde, àwọn aláìláàánú àti onínú fùfù ènìyàn tí ó rin gbogbo ilẹ̀ ayé já láti fi agbára gbà àwọn ibùgbé tí kì í ṣe ti wọn."'), 'Hab$1:10': ('gba', None, 'Hab$1:10 "Wọn ó si máa fí àwọn ọba ṣẹ̀ṣín wọn ó sì tún kẹ́gàn àwọn aládé. Wọn yóò sì fi gbogbo ìlú odi alágbára nì rẹ́rìnín; Nítorí pé, wọn yóò ko erùpẹ̀ jọ, wọn yóò sì gbà á"'), 'Hab$1:13': (None, 'ibi', 'Hab$1:13 Ojú rẹ mọ́ tónítóní láti wo ibi; ìwọ kò le gbà ìwà ìkà nítorí kí ni ìwọ ṣe gba àwọn alárékérekè láàyè? Nítorí kí ni ìwọ ṣe dákẹ́ nígbà tí ẹni búburú ń pa ẹni tí i ṣe olododo ju wọn lọ run?'), 'Hab$2:1': ('wo', None, 'Hab$2:1 Èmi yóò dúró lórí ibùṣọ́ mi láti máa wòye Èmi yóò sì gbé ara mi ka orí alóre Èmi yóò si ṣọ́ láti gbọ́ ohun tí yóò sọ fún nu àti èsì ti èmi yóò fún ẹ̀sùn yìí nígbà tó bá ń bá mi wí;'), 'Hab$2:4': ('ni', 'gbe', 'Hab$2:4 " “Kíyèsí, ọkàn rẹ tí ó gbéga; Ìfẹ́ rẹ̀ kò dúró ṣinṣin nínú rẹ̀, ṣùgbọ́n olododo yóò wa nípa ìgbàgbọ́ rẹ̀."'), 'Hab$2:18': (None, None, 'Hab$2:18 "“Èrè kí ni òrìṣà ni, tí oníṣọ̀nà rẹ̀ fi gbẹ́ ẹ, ère dídá àti olùkọ́ èké? Nítorí ti ẹni ti ó dá a gbẹ́kẹ̀ rẹ̀ lé ohun tí ó fúnrarẹ̀ dá; ó sì mọ ère tí kò le fọhùn."'), 'Hab$3:2': ('ṣe', 'iṣẹ', 'Hab$3:2 "OLÚWA mo tí gbọ́ ohùn rẹ; ẹrú sì ba mi fún iṣẹ́ rẹ OLÚWA ṣọ wọn di ọ̀tún ní ọjọ́ ti wa, ní àkókò tiwa, jẹ́ kó di mímọ̀; ni ìbínú, rántí àánú. "'), 'Hab$3:4': ('imole', 'imọlẹ', 'Hab$3:4 "Dídán rẹ ṣí dàbí ìmọ́lẹ̀; Ìmọ́lẹ̀ kọ-ṣàn-án láti ọwọ́ rẹ wá, níbi tí wọn fi agbára rẹ̀ pamọ́ sí."'), 'Hab$3:17': ('agbo', 'agbo', 'Hab$3:17 "Bí igi ọ̀pọ̀tọ́ ko tilẹ̀ tanná, tí èso kò sí nínú àjàrà; tí iṣẹ igi olifi yóò jẹ́ àṣedànù, àwọn oko ki yóò sì mú oúnjẹ wá; tí a ṣi ke agbo ẹran kúrò nínú agbo, tí ki yóò sì sí ọwọ́ ẹran ni ibùsọ̀ mọ́,"'), 'Zeph$1:12': ('wá', 'ṣe', 'Zeph$1:12 "Ní àkókò wọ̀n-ọn-nì, èmi yóò wá Jérúsálẹ́mù kiri pẹ̀lú fìtílà, èmi ó sì fi ìyà jẹ àwọn tí kò ní ìtẹ́lọ́rùn, tí wọn sì dàbí àwọn ènìyàn tí ó sinmi sínú gẹ̀dẹ̀gẹ́dẹ̀ wọn, àwọn tí wọn sì ń wí ní ọkàn wọn pé, ‘OLÚWA kì yóò ṣe nǹkan kan tí ó jẹ́ rere tàbí tí ó jẹ́ búburú.’"'), 'Zeph$2:9': (None, None, 'Zeph$2:9 "Nítorí náà, bí Èmi tí wà,” ni OLÚWA Sódómù wí, Ọlọ́run àwọn Ísírẹ́lì, “Ní tòótọ́ Móábù yóò dàbí Sódómù àti Ámónì yóò sì dàbí Gòmórà, ibi tí ó kún fún yèrèpè àti ihó iyọ̀ àti ìdahoro títí láéláé. Ìyòókù àwọn ènìyàn mi yóò kó wọn; àwọn tí ó sì yọ nínú ewu ní orílẹ̀-èdè mi ni yóò jogún ilẹ̀ wọn.” "'), 'Zeph$2:15': ('gbé', None, 'Zeph$2:15 "Èyí ni ìlú aláyọ̀ tí ó ń gbé láìléwu. Ó sì sọ sí ara rẹ̀ pé, “Èmi ni, kò sì sí ẹnìkan tí ó ń bẹ lẹ́yìn mi.” Irú ahoro wo ni òun ha ti jẹ́, ibùgbé fún àwọn ẹranko igbó! Gbogbo ẹni tí ó bá kọjá ọ̀dọ̀ rẹ̀ yóò fi rẹ́rìnín ẹlẹ́yà, wọ́n yóò sì gbọn ẹsẹ̀ wọn."'), 'Zeph$3:6': ('ṣe', 'ni', 'Zeph$3:6 "“Èmi ti ké àwọn orílẹ̀-èdè kúrò, ilé gíga wọn sì ti bàjẹ́. Mo ti fi ìgboro wọn sílẹ̀ ní ihà tóbẹ́ẹ̀ tí ẹnìkan kan kò kọjá níbẹ̀. Ìlú wọn parun tóbẹ́ẹ̀ tí kò sí ẹnikan tí yóò sẹ́kù, kò sì ní sí ẹnìkan rárá."'), 'Zeph$3:8': (None, None, 'Zeph$3:8 "Nítorí náà ẹ dúró dèmi,” ni OLÚWA wí, “títí di ọjọ́ náà tí èmi yóò dìde si ohun ọdẹ; nítorí ìpinnu mi ni láti kó orílẹ̀-èdè jọ kí èmi kí ó lè kó ilẹ̀ ọba jọ àti láti rú ìbínú jáde sórí wọn, àní gbogbo ibínú gbígbóná mi. Nítorí, a ó fi iná owú ibínú jẹ gbogbo ayé run. "'), 'Zeph$3:19': ('ni', 'gba', 'Zeph$3:19 "Ní àkókò náà ni èmi yóò dojúkọ àwọn tí ń ni yín lára, èmi yóò gba atiro là, èmi yóò sì ṣa àwọn tí ó ti fọ́nká jọ, èmi yóò fi ìyìn àti ọlá fún wọn ní gbogbo ilẹ̀ tí a bá ti dójútì wọ́n."'), 'Zeph$3:20': (None, None, 'Zeph$3:20 "Ní àkókò náà ni èmi yóò ṣà yín jọ; Nígbà náà ni èmi yóò mú un yín padà wá sílé. Èmi yóò fi ọlá àti ìyìn fún un yín láàárin gbogbo ènìyàn àgbáyé, nígbà tí èmi yóò yí ìgbèkùn yín padà bọ sípò ní ojú ara yín,” ni OLÚWA wí."'), 'Hag$1:3': (None, None, 'Hag$1:3 Nígbà náà ni ọ̀rọ̀ OLÚWA wá láti ọ̀dọ̀ wòlíì Hágáì wí pé:'), 'Hag$1:8': (None, 'mú', 'Hag$1:8 "Ẹ gun orí àwọn òkè ńlá lọ, kí ẹ sì mú igi wá pẹ̀lú yín. Kí ẹ sì kọ́ ilé náà, kí inú mi bà leè dùn sí i, kí a sì yín mí lógo,” ni OLÚWA wí."'), 'Hag$1:11': ('ilẹ', 'ilẹ', 'Hag$1:11 "Mo sì ti pe ọ̀dá sórí ilẹ̀ àti sórí awọn òkè-ńlá, sórí ọkà àti sórí wáìnì tuntun, sórí òróró àti sórí ohun ti ilẹ̀ ń mú jáde, sórí ènìyàn, sórí ẹran àti sórí gbogbo iṣẹ́ ọwọ́ yín.”"'), 'Hag$2:6': ('ayé', 'ilẹ', 'Hag$2:6 "“Báyìí ni OLÚWA àwọn ọmọ-ogun wí: ‘Láìpẹ́ jọjọ, Èmi yóò mi àwọn ọrun àti ayé, òkun àti ìyàngbẹ ilẹ̀ lẹ́ẹ̀kan sí i."'), 'Hag$2:10': ('wá', None, 'Hag$2:10 "Ní ọjọ́ kẹrìnlélógún oṣù kẹsàn-án ní ọdún kejì Dáríúsì, ni ọ̀rọ̀ OLÚWA tọ wòlíì Hágáì wá pé:"'), 'Hag$2:23': (None, 'ṣe', 'Hag$2:23 "OLÚWA àwọn wí pé, “Ní ọjọ́ náà, àwọn ọmọ-ogun iwọ ìránṣẹ́ mi Sérúbábélì ọmọ Séítíélì, Èmi yóò mú ọ, Èmi yóò sì sọ ọ di bí òrùka èdìdì mi, nítorí mo ti yan ọ, ni OLÚWA àwọn ọmọ-ogun wí.”"'), 'Zech$1:6': (None, None, 'Zech$1:6 "Ṣùgbọ́n ọ̀rọ̀ mi àti ìlànà mi, ti mo pà ni àṣẹ fún àwọn ìrànsẹ́ mi wòlíì, kò ha tún bá àwọn baba yín? “Wọ́n sì padà wọ́n wí pé, ‘Gẹ́gẹ́ bí OLÚWA àwọn ọmọ ogun ti rò láti ṣe sí wa, gẹ́gẹ́ bi ọ̀nà wa, àti gẹ́gẹ́ bí ìṣe wa, bẹ́ẹ̀ ní o ti ṣe sí wa.’ ”"'), 'Zech$1:21': ('wá', 'ṣe', 'Zech$1:21 "Nígbà náà ni mo wí pé, “Kí ni àwọn wọ̀nyí wá ṣe?” O sì sọ wí pé, “Àwọn wọ̀nyí ni ìwo tí ó ti tú Júdà ká, tóbẹ́ẹ̀ tí ẹnikẹ́ni kò fi gbé orí rẹ̀ sòkè? Ṣùgbọ́n àwọn wọ̀nyí wá láti dẹ́rùbà wọ́n, láti lé ìwo àwọn orilẹ̀-èdè jáde, ti wọ́n gbé ìwo wọn sorí ilẹ̀ Júdà láti tú enìyàn rẹ̀ ká.”"'), 'Zech$2:3': (None, None, 'Zech$2:3 "Sì kiyesí i, ańgẹ́li tí ó ń bá mi sọ̀rọ̀ jáde lọ, ańgẹ́lì mìíràn si jáde lọ pade rẹ̀."'), 'Zech$5:3': (None, None, 'Zech$5:3 "Ó sì wí fún mi pé, “Èyí ni ègún tí ó jáde lọ sí gbogbo ilẹ̀ ayé: nítorí gbogbo àwọn tí ó bá jalè ni a ó gèé kúrò láti ìhín lọ nípa rẹ̀; gbogbo àwọn tí ó bá sì búra èké ni a ó gèé kúrò láti ìhín lọ nípa rẹ̀."'), 'Zech$5:9': (None, None, 'Zech$5:9 "Mo sì gbé ojú mi sókè, mo sì wò, sì kíyèsí i, obìnrin méjì jáde wá, èfúùfù sì wá nínú ìyẹ́ wọn; nítorí wọ́n ní ìyẹ́ bí ìyẹ́ àkọ̀: Wọ́n sì gbé òṣùwọ̀n éfà náà dé àárin méjì ayé àti ọ̀run."'), 'Zech$5:11': ('mulẹ', 'kọ', 'Zech$5:11 "Ó si wí fún mi pé, “Sí orílẹ̀ èdè Bábílónì láti kọ ilé fún un. Tí ó bá ṣe tán, a ó sì fi ìdí rẹ̀ mulẹ̀, a o sì fi ka orí ìpìlẹ̀ rẹ̀ níbẹ̀.”"'), 'Zech$6:9': (None, None, 'Zech$6:9 "Ọ̀rọ̀ OLÚWA sì tọ̀ mí wá, wí pé:"'), 'Zech$7:12': (None, 'ẹmi', 'Zech$7:12 "Wọ́n sé àyà wọn bí òkúta ádámáńtì, kí wọn má ba à gbọ́ òfin, àti ọ̀rọ̀ tí OLÚWA àwọn ọmọ-ogun ti fi ẹ̀mí rẹ̀ rán nípa ọwọ́ àwọn wòlíì ìṣáájú wá: ìbínú ńlá sì dé láti ọ̀dọ̀ OLÚWA àwọn ọmọ ogun wá."'), 'Zech$8:4': (None, None, 'Zech$8:4 "Báyìí ni OLÚWA àwọn ọmọ-ogun wí: “Arúgbó ọkùnrin, àti arúgbó obìnrin, yóò gbé ìgboro Jérúsálẹ́mù, àti olúkúlùkù pẹ̀lú ọ̀pá ni ọwọ́ rẹ̀ fún ogbó."'), 'Zech$8:10': (None, 'eniyan', 'Zech$8:10 "Nítorí pé, ṣáájú ọjọ́ wọ̀nyí, ọ̀yà ènìyàn kò tó nǹkan, bẹ́ẹ̀ ni ọ̀yà ẹran pẹ̀lú, bẹ́ẹ̀ ni kò sí àlàáfíà fún ẹni ń jáde lọ, tàbí ẹni ti ń wọlé bọ, nítorí ìpọ́njú náà: nítorí mo dojú gbogbo ènìyàn, olukuluku kọ aládùúgbò rẹ̀."'), 'Zech$8:20': (None, None, 'Zech$8:20 "Báyìí ni OLÚWA àwọn ọmọ-ogun wí pé: “Àwọn ènìyàn yóò ṣa tún wa, àti ẹni tí yóò gbe ìlú-ńlá púpọ̀."'), 'Zech$8:23': (None, None, 'Zech$8:23 "Báyìí ni OLÚWA àwọn ọmọ-ogun wí pé: “Ní àwọn ọjọ́ wọ̀nyí ni ọkùnrin mẹ́wàá láti inú gbogbo èdè àti orílẹ̀-èdè yóò dì í mú, àní yóò di etí aṣọ ẹni tí i ṣe Júù mú, wí pé, ‘Àwa yóò ba ọ lọ, nítorí àwa tí gbọ́ pé, Ọlọ́run wà pẹ̀lú rẹ.’ ”"'), 'Zech$9:12': (None, None, 'Zech$9:12 "Ẹ padà sínú odi agbára yín, ẹ̀yin òǹdè ìrètí: àní lónìí yìí èmi sọ pé, èmi o san án fún ọ ni ìlọ́poméjì."'), 'Zech$10:1': ('fun', None, 'Zech$10:1 "Ẹ bèèrè òjò nígbà àrọ̀kúrò ni ọwọ́ OLÚWA; OLÚWA tí o dá mọ̀nàmọ́ná, tí ó sì fi ọ̀pọ̀ òjò fún ènìyàn, fún olúkúlúkù koríko ní pápá."'), 'Zech$10:10': ('mú', 'ilẹ', 'Zech$10:10 "Èmi ó sì tún mú wọn padà kúrò ni ilẹ̀ Éjíbítì pẹ̀lú, èmi ó sì ṣà wọn jọ kúrò ni ilẹ̀ Áṣíríà: èmi ó sì mú wọn wá sí ilẹ̀ Gílíádì àti Lébánónì; a a kì yóò sì rí àyè fún wọn bí ó ti yẹ."'), 'Zech$11:2': ('ṣubu', None, 'Zech$11:2 "Pohùnréré-ẹkún, igi fírì; nítorí igi Kédárì ṣubú, nítorí tí a ba àwọn igi tí o lógo jẹ́: ṣunkún kíkorò ẹ̀yin igi óákù tí Básánì, nítorí gé igbó àjàrà lulẹ̀."'), 'Zech$11:9': (None, 'jẹ', 'Zech$11:9 "Mo sì wí pé, “Èmi kì yóò bọ́ yin: èyí ti ń ku lọ, jẹ́ kí òkú o kú; èyí tí a o ba sì gé kúrò, jẹ́ kí a gé e kúrò; ki olúkúlùkù nínú àwọn ìyókù jẹ́ ẹran-ara ẹnìkejì rẹ̀.”"'), 'Zech$11:16': ('jẹ', None, 'Zech$11:16 "Nítorí Èmi o gbé olùṣọ́-àgùntàn kan dídé ni ilẹ̀ náà, tí kí yóò bẹ àwọn tí ó ṣègbé wò, ti kì yóò sì wá èyí tí ó yapa; tí kì yóò ṣe ìtọ́jú èyí tí a pa lára tàbí kí ó bọ́ àwọn tí ara wọn dá pépé: Ṣùgbọ́n òun yóò jẹ ẹran èyi tí ó ni ọ̀rá, yóò sì fa èékánna wọn ya pẹ́rẹpẹ̀rẹ."'), 'Zech$12:10': ('ẹmi', 'wo', 'Zech$12:10 " “Èmi ó sì tu ẹ̀mí oore-ọ̀fẹ́ àti ẹ̀bẹ̀ ṣórí ilé Dáfídì àti sórí Jérúsálẹ́mù: wọn ó sì máa wo ẹni tí wọn tí gún ni ọ̀kọ̀, wọn ó sì máa sọ̀fọ̀ rẹ̀, bí ẹnìkan ti ń sọ̀fọ̀ fún ọmọ rẹ̀ kan ṣoṣo àti gẹ́gẹ́ bí ènìyàn tí wọn yóò sì wà ni ìbànújẹ́, bí ẹni tí ń banújẹ́ fún àkọ́bí rẹ̀."'), 'Zech$12:12': ('ile', None, 'Zech$12:12 "Ilẹ̀ náà yóò ṣọ̀fọ̀, ìdílé, ìdílé, lọ́tọ̀ọ̀tọ̀; ìdílé Dáfídì lọ́tọ̀; àti àwọn aya wọn lọ́tọ̀; ìdílé Nátanì lọ́tọ̀, àti àwọn aya wọn lọ́tọ́."'), 'Zech$13:9': (None, None, 'Zech$13:9 "Èmi ó sì mú apá kẹ́ta náà la àárin iná, èmi yóò sì yọ́ wọn bí a ti yọ́ fàdákà, èmi yóò sì dán wọn wò, bi a tí ń dán góòlu wò: wọn yóò sì pé orúkọ mi, èmi yóò sì dá wọn lóhùn: èmi yóò wí pé, ‘Àwọn ènìyàn mi ni,’ àwọn yóò sì wí pé, ‘OLÚWA ni Ọlọ́run wa.’ ”"'), 'Zech$14:2': (None, 'lọ', 'Zech$14:2 "Nítorí èmi ó kó gbogbo orílẹ̀-èdè jọ sí Jérúsálẹ́mù fún ogun; a ó sì ko ìlú naà, a ó sì kó àwọn ilé, a ó sì ba àwọn obìnrin jẹ́, ààbọ̀ ìlú náà yóò lọ sí ìgbékùn, a kì yóò sì gé ìyókù àwọn ènìyàn náà kúrò ni ìlú náà."'), 'Zech$14:4': ('oke', 'òkè', 'Zech$14:4 "Ẹṣẹ̀ rẹ̀ yóò sì dúró ni ọjọ́ náà lórí òkè Ólífì, tí ó wà níwájú Jérúsálẹ́mù ni ila-oòrùn, òkè Ólífì yóò sì làá sí méjì, sí ìhà ilà-oòrùn àti ìhà ìwọ̀-oòrun, àfonífojì ńláńlá yóò wà: ìdajì òké náà yóò sì sí síhà àríwá, àti ìdajì rẹ̀ síhà gúṣù."'), 'Zech$14:6': (None, 'imole', 'Zech$14:6 "Yóò sì ṣe ni ọjọ́ náà, ìmọ́lẹ̀ kì yóò mọ́, bẹ́ẹ̀ ni kì yóò ṣókùnkùn."'), 'Zech$14:8': (None, None, 'Zech$14:8 "Yóò sì ṣe ní ọjọ́ náà, omi ìyè yóò tí Jérúsálẹ́mù ṣàn lọ; ìdájì wọn sìhà òkun ilà-oòrùn, àti ìdájì wọn síhà okùn ẹ̀yìn: nígbà ẹ̀rùn àti nígbà òtútù ni yóò rí bẹ́ẹ̀."'), 'Zech$14:12': ('ni', 'jẹ', 'Zech$14:12 "Èyí ni yóò sì jẹ́ àrùn tí OLÚWA yóò fi kọlu gbogbo àwọn ènìyàn ti ó tí ba Jérúsálẹ́mù jà; ẹran-ara wọn yóò rù nígbà tí wọn dúró ni ẹṣẹ̀ wọn, ojú wọn yóò sì rà ni ihò wọn, ahọ́n wọn yóò sì bàjẹ́ ni ẹnu wọn."'), 'Zech$14:16': ('lọ', None, 'Zech$14:16 "Yóò sì ṣe, olúkúlùkù ẹni tí o kù nínú gbogbo àwọn orílẹ̀-èdè tí ó dìde sí Jérúsálẹ́mù yóò máa gòkè lọ lọ́dọọdún láti sìn Ọba, OLÚWA àwọn ọmọ-ogun, àti láti ṣe àjọyọ̀ àṣè àgọ́ náà."'), 'Mal$2:2': ('fun', 'ni', 'Mal$2:2 "Bí ẹ̀yin kò bá ni gbọ́, bí ẹ̀yin kò bá ní fi í sí àyà láti fi ọ̀wọ̀ fún orúkọ mi,” ni OLÚWA àwọn ọmọ ogun wí; èmi yóò sì ran ègún sí orí yín, èmi yóò sì fi ìbùkún yín ré. Nítòótọ́ mó ti fi ré ná, nítorí pé, ẹ̀yin kò fi sí ọkàn yín láti bu ọlá fún mi."'), 'Mal$2:3': (None, None, 'Mal$2:3 "“Nítorí ti yín èmí yóò ba àwọn ọmọ yín wí èmi ó sì fi ìgbẹ́ rẹ́ yín lójú, àní àwọn ìgbẹ́ ọrẹ ọwọ́ yín wọ̀nyí, a ó sì kó yín lọ pẹ̀lú rẹ̀."'), 'Mal$2:6': ('ète', 'ẹnu', 'Mal$2:6 "Òfin òtítọ́ wà ni ẹnu rẹ̀, a kò sì rí irọ́ ni ètè rẹ̀: ó ba mi rìn ní àlàáfíà àti ni ídúró ṣinṣin, ó sì yí ọ̀pọ̀lọpọ̀ kúrò nínú ẹ̀ṣẹ̀."'), 'Mal$2:7': ('ète', 'ẹnu', 'Mal$2:7 "“Nítorí ètè àlùfáà ní a ti máa pa ìmọ̀ mọ́, kí àwọn ènìyàn lè máa wá ìtọ́ni ni ẹnu rẹ̀: nítorí òun ni iransẹ OLÚWA àwọn ọmọ ogun."'), 'Mal$2:8': (None, 'ni', 'Mal$2:8 "Ṣùgbọ́n ẹ̀yin ti yapa kúrò ní ọ̀nà náà; ẹ̀yin sì ti fi ìkọ́ni yín mú ọ̀pọ̀lọpọ̀ kọsẹ̀; ẹ̀yin ti ba májẹ̀mú tí mo da pẹ̀lú Léfì jẹ́,” ni OLÚWA àwọn ọmọ ogun wí."'), 'Mal$2:9': ('ṣe', 'ni', 'Mal$2:9 "“Nítorí náà ni èmi pẹ̀lú ṣe sọ yín di ẹ̀gàn, àti ẹni àìkàsí níwájú gbogbo ènìyàn, nítorí ẹ̀yin kò tẹ̀lé ọ̀nà mi, ṣùgbọ́n ẹ̀yin ń ṣe ojúṣàájú nínú òfin.”"'), 'Mal$2:16': (None, None, 'Mal$2:16 "“Mo kórìíra ìkọ̀sílẹ̀,” ni OLÚWA Ọlọ́run Ísírẹ́lì wí, ni OLÚWA Ọlọ́run Ísírẹ́lì wí, “bẹ́ẹ̀ ni mo kóríra kí èníyàn máa fi ipá bó ara rẹ̀ àti pẹ̀lú asọ rẹ̀,” ni OLÚWA àwọn ọmọ ogun wí. Nítorí náà ẹ sọ́ ẹ̀mí yín, kí ẹ má se hùwà ẹ̀tàn."'), 'Mal$3:4': ('atijọ', None, 'Mal$3:4 "nígbà náà ni ọrẹ Júdà àti ti Jérúsálẹ́mù yóò jẹ́ ìtẹ́wọ́gbà fún OLÚWA, gẹ́gẹ́ bí ti ọjọ́ àtijọ́, àti gẹ́gẹ́ bí ọdún ìgbàanì."'), 'Mal$4:2': (None, None, 'Mal$4:2 "Ṣùgbọ́n fún ẹ̀yin tí ó bẹ̀rù orúkọ mi, òòrùn ododo yóò yọ, pẹ̀lú ìmúlárada ni ìyẹ́ apá rẹ̀. Ẹ̀yin yóò sì jáde lọ, ẹ̀yin yóò sì máa fò fún ayọ̀ bi àwọn ẹgbọ̀rọ̀ màlúù tí a tú sílẹ̀ lórí ìso."'), 'Matt$2:11': (None, None, 'Matt$2:11 " Bí wọ́n tí wọ inú ilé náà, wọn rí ọmọ ọwọ́ náà pẹ̀lú Màríà ìyá rẹ̀, wọ́n wólẹ̀, wọ́n forí balẹ̀ fún un. Nígbà náà ni wọ́n tú ẹrù wọn, wọ́n sì ta Jésù lọ́rẹ: wúrà, tùràrí àti òjíà."'), 'Matt$2:13': ('gba', None, 'Matt$2:13 "Nígbà tí wọn ti lọ, ańgẹ́lì Olúwa fara hàn Jósẹ́fù ní ojú àlá pé, “Dìde, gbé ọmọ-ọwọ́ náà àti ìyá rẹ̀, kí ó sì sá lọ sí Éjíbítì. Dúró níbẹ̀ títítí èmi yóò fi sọ fún ọ, nítorí Hẹ́rọ̀dù yóò wá ọ̀nà láti pa ọmọ-ọwọ́ náà.”"'), 'Matt$2:14': ('mu', 'ni', 'Matt$2:14 "Nígbà náà ni ó sì dìde, ó mú ọmọ-ọwọ́ náà àti ìyá rẹ̀ ní òru, ó sì lọ sí Éjíbítì,"'), 'Matt$2:17': (None, None, 'Matt$2:17 Nígbà náà ni èyí tí a ti sọ tẹ́lẹ̀ láti ẹnu wòlíì Jeriemáyà wá sẹ pé:'), 'Matt$3:15': (None, None, 'Matt$3:15 "Jésù sì dáhùn pé, “Jọ̀wọ́ rẹ̀ bẹ́ẹ̀ náà, ní torí bẹ́ẹ̀ ni ó yẹ fún wa láti mú gbogbo òdodo ṣẹ.” Bẹ́ẹ̀ ni Jòhánù gbà, ó sì ṣe ìbamitíìsì fún un."'), 'Matt$4:4': ('ni', None, 'Matt$4:4 " Ṣùgbọ́n Jésù dáhùn pé, “A ti kọ ìwé rẹ̀ pé: ‘Ènìyàn kì yóò wà láàyè nípa àkàrà nìkan, bí kò ṣe nípa gbogbo ọ̀rọ̀ ti ó ti ẹnu Ọlọ́run jáde wá.’ ”"'), 'Matt$4:12': (None, None, 'Matt$4:12 Nígbà tí Jésù gbọ wí pé a ti fi Jòhánù sínu túbú ó padà sí Gálílì.'), 'Matt$4:25': (None, 'eniyan', 'Matt$4:25 "Ọ̀pọ̀lọpọ̀ ènìyàn láti Gálílì, Dékápólì, Jerúsálémù, Jùdíà, àti láti òkè odò Jọ́dánì sì tọ̀ ọ́ lẹ́yìn."'), 'Matt$5:19': (None, None, 'Matt$5:19 " Ẹnikẹ́ni ti ó bá rú òfin tí ó tilẹ̀ kéré jù lọ, tí ó sì kọ́ ẹlòmíràn láti ṣe bẹ́ẹ̀, òun ni yóò kéré jù lọ ní ìjọba ọ̀run, ṣùgbọ́n ẹni tí ó bá ń sewọn, tí ó sì ń kọ́ wọn, ni yóò jẹ́ ẹni ńlá ní ìjọba ọ̀run."'), 'Matt$5:21': ('ni', None, 'Matt$5:21 " “Ẹ̀yin ti gbọ́ bí a ti wí fún àwọn ará ìgbàanì pé, ‘Ìwọ kò gbọdọ̀ pànìyàn, ẹnikẹni tí ó bá pànìyàn yóò wà nínú ewu ìdájọ́.’"'), 'Matt$5:31': (None, None, 'Matt$5:31 " “A ti wí pẹ̀lú pé, ‘Ẹnikẹ́ni tí ó bá kọ aya rẹ̀ sílẹ̀ gbọdọ̀ fún un ní ìwé-ẹ̀rí ìkọ̀sílẹ̀.’"'), 'Matt$5:40': (None, 'ni', 'Matt$5:40 "Bí ẹnì kan bá fẹ́ gbé ọ lọ sílé ẹjọ́, tí ó sì fẹ́ gba ẹ̀wù àwọ̀tẹ́lẹ̀, jọ̀wọ́ agbádá rẹ fún un pẹ̀lú."'), 'Matt$5:44': (None, None, 'Matt$5:44 "Ṣùgbọ́n èmi wí fún yín pé, ẹ fẹ́ràn àwọn ọ̀ta yín kí ẹ sì gbàdúrà fún àwọn tí ń ṣe inúnibíni sí yín,"'), 'Matt$6:10': ('ṣe', None, 'Matt$6:10 "Kí ìjọba yín dé, Ìfẹ́ tiyín ni kí a ṣe ní ayé bí ti ọ̀run."'), 'Matt$7:26': (None, None, 'Matt$7:26 "Ẹnikẹ́ni tí ó bá sì gbọ́ ọ̀rọ̀ tèmi wọ̀nyí, tí kò bá sì ṣe wọ́n, òun ni èmi yóò fi wé aṣiwèrè ènìyàn kan tí ó kọ́ ilé rẹ̀ sí orí iyanrìn."'), 'Matt$8:1': (None, None, 'Matt$8:1 "Nígbà tí ó ti orí òkè sọ̀ kalẹ̀, ọ̀pọ̀ ènìyàn ń tọ̀ ọ́ lẹ́yìn."'), 'Matt$8:16': (None, None, 'Matt$8:16 "Nígbà tí ó di àṣálẹ́, ọ̀pọ̀ ènìyàn tí ó ní ẹ̀mí èṣù ni a mú wá sọ́dọ̀ rẹ̀, ó sì fi ọ̀rọ̀ rẹ̀ lé àwọn ẹ̀mí èṣù náà jáde. A sì mú gbogbo àwọn olókùnrùn láradá."'), 'Matt$8:19': ('wá', None, 'Matt$8:19 "Olùkọ́ òfin kan sì tọ̀ ọ́ wá, ó wí fún un pé, “Olùkọ́, èmi ó má tọ̀ ọ́ lẹ́yìn níbikíbi tí ìwọ bá ń lọ.”"'), 'Matt$8:27': ('awọn ọkunrin', 'eniyan', 'Matt$8:27 "Ṣùgbọ́n ẹnu yà àwọn ọkùnrin náà, wọ́n sì béèrè pé, “Irú ènìyàn wo ni èyí? kódà ìji-líle àti rírú omi òkun gbọ́ tirẹ̀?”"'), 'Matt$8:28': ('gidigidi', 'kọjá', 'Matt$8:28 " Nígbà ti ó sì dé àpa kejì ní ilẹ̀ àwọn ara Gádárénésì, àwọn ọkùnrin méjì ẹlẹ́mìí-èṣù ti inú ibojì wá pàdé rẹ̀. Wọn rorò gidigidi tó bẹ́ẹ̀ tí ẹnikẹ́ni kò le kọjá ní ọ̀nà ibẹ̀."'), 'Matt$8:34': ('ri', 'pade', 'Matt$8:34 "Nígbà náà ni gbogbo ará ìlú náà sì jáde wá í pàdé Jésù. Nígbà tí wọ́n sì rí i, wọ́n bẹ̀ ẹ́, kí ó lọ kúrò ní agbégbé wọn."'), 'Matt$9:6': ('mọ', None, 'Matt$9:6 "Ṣùgbọ́n kí ẹ̀yin kí ó lè mọ̀ pé Ọmọ Ènìyàn ní agbára ní ayé láti dárí ẹ̀ṣẹ̀ jì ni.” Ó sì wí fún arọ náà pé, “Dìde, sì gbé ẹní rẹ, kí o sì máa lọ ilé rẹ.”"'), 'Matt$9:14': ('wá', 'ṣe', 'Matt$9:14 " Nígbà náà ni àwọn ọmọ-ẹ̀yìn Jòhánù tọ̀ ọ́ wá láti béèrè wí pé, “Èé ṣe tí àwa àti àwọn Farisí fi ń gbààwẹ̀, ṣùgbọ́n tí àwọn ọmọ-ẹ̀yìn rẹ kò gbààwẹ̀?”"'), 'Matt$9:16': (None, None, 'Matt$9:16 "“Kò sí ẹni tí í fi ìrépé aṣọ tutun lẹ ògbólògbó ẹ̀wù; nítorí èyí tí a fi lẹ̀ ẹ́ yóò ya ní ojú lílẹ̀, aṣọ náà yóò sì ya púpọ̀ sì i ju ti ìṣáájú lọ."'), 'Matt$9:18': ('ni', None, 'Matt$9:18 "Bí ó ti ń sọ nǹkan wọ̀nyí fún wọn, kíyèsí i, ìjòyè kan tọ̀ ọ́ wá, ó sì wólẹ̀ níwájú rẹ̀ wí pé, “Ọmọbìnrin mi kú nísinsìn yìí, ṣùgbọ́n wá fi ọwọ́ rẹ̀ lé e, òun yóò sì yè.”"'), 'Matt$9:28': ('wá', 'ṣe', 'Matt$9:28 "Nígbà tí ó sì wọ̀ ilé, àwọn afọ́jú náà tọ̀ ọ́ wá, Jésù bi wọ́n pé, “Ẹ̀yin gbàgbọ́ pé mo le ṣe èyí?” Wọn sì wí fún un pé, “Bẹ́ẹ̀ ni Olúwa, ìwọ lè ṣe é.”"'), 'Matt$10:8': ('ni', 'fun', 'Matt$10:8 "Ẹ máa ṣe ìwòṣan fún àwọn aláìsàn, ẹ si jí àwọn okú dìde, ẹ sọ àwọn adẹ́tẹ̀ di mímọ́, kí ẹ sì máa lé àwọn ẹ̀mí-èṣù jáde. Ọ̀fẹ́ ni ẹ̀yin gbàá, ọ̀fẹ́ ni kú ẹ fi fún ni."'), 'Matt$10:24': (None, None, 'Matt$10:24 " “Akẹ́kọ̀ọ́ kì í ju olùkọ́ rẹ̀ lọ, bẹ́ẹ̀ ni ọmọ-ọ̀dọ̀ kì í ju ọ̀gá rẹ̀ lọ."'), 'Matt$10:25': (None, None, 'Matt$10:25 " Ó tọ́ fún akẹ́kọ̀ọ́ láti dà bí olùkọ́ rẹ̀ àti fún ọmọ-ọ̀dọ̀ láti rí bí ọ̀gá rẹ̀. Nígbà tí wọ́n bá pe baálé ilé ní Béélísébúbù, mélòó mélòó ni ti àwọn ènìyàn ilé rẹ̀!"'), 'Matt$11:2': (None, 'ṣe', 'Matt$11:2 " Nígbà tí Jòhánù gbọ́ ohun tí Kírísítì ṣe nínú ẹ̀wọ̀n, ó rán àwọn ọmọ-ẹ̀yìn rẹ̀"'), 'Matt$11:4': ('dahùn', None, 'Matt$11:4 "Jésù dáhùn ó wí pé, “Ẹ padà lọ, ẹ sì sọ fún Jòhánù ohun tí ẹ̀yin gbọ́, àti èyí tí ẹ̀yin rí."'), 'Matt$11:12': ('ipa', None, 'Matt$11:12 "Láti ìgbà ọjọ́ Jòhánù onítẹ̀bọmi títí di àkókò yìí ni ìjọba ọ̀run ti di àfagbárawọ̀, àwọn alágbára ló ń fi ipá gbà á."'), 'Matt$11:27': (None, None, 'Matt$11:27 " “Ohun gbogbo ni Baba mi ti fi sí ìkáwọ́ mi. Kò sí ẹni tí ó mọ ọmọ bí kò se Baba, bẹ́ẹ̀ ni kò sí ẹnikẹ́ni tí ó mọ Baba, bí kò ṣe ọmọ, àti àwọn tí ọmọ yan láti fi ara hàn fún."'), 'Matt$11:30': (None, 'rọrun', 'Matt$11:30 Nítorí àjàgà mi rọrùn ẹrù mi sì fúyẹ́.”'), 'Matt$12:3': (None, 'ka', 'Matt$12:3 " Jésù dáhùn pé, “Ẹ̀yin kò ha ka ohun tí Dáfídì se nígbà tí ebi ń pa á àti àwọn tí ó wà lọ́dọ̀ rẹ̀."'), 'Matt$12:11': (None, 'ni', 'Matt$12:11 " Ó dá wọn lóhùn pé, “Bí ó bá jẹ́ wí pé bí ẹnikẹ́ni nínú yín bá ní àgùntàn kan ṣoṣo, tí ó sì bọ́ sínú kòtò ní ọjọ́ ìsinmi, tí kì yóò dì í mú, kí ó sì fà á jáde."'), 'Matt$12:15': (None, 'ni', 'Matt$12:15 " Ṣùgbọ́n Jésù mọ, ó yẹra kúrò níbẹ̀. Ọ̀pọ̀ ènìyàn sì ń tọ̀ ọ́ lẹ́yìn, ó sì mú gbogbo àwọn aláìsàn láradá."'), 'Matt$12:32': (None, 'eniyan', 'Matt$12:32 "Ẹnikẹ́ni tí ó bá sọ̀rọ̀ òdì sí Ọmọ ènìyàn, a ó dárí jì í, ṣùgbọ́n ẹni tí ó bá sọ ọ̀rọ̀ òdì sí ẹ̀mí mímọ́, a kì yóò dárí jì í, ìbá à ṣe ní ayé yìí tàbí ní ayé tí ń bọ̀."'), 'Matt$13:12': (None, 'ni', 'Matt$13:12 " Ẹnikẹ́ni tí ó ní, òun ni a ó fún sí i, yóò sì ní lọ́pọ̀lọpọ̀. Ṣùgbọ́n lọ́wọ́ ẹni tí kò ní, ni a ó ti gbà èyí kékeré tí ó ní náà."'), 'Matt$13:13': (None, 'gbo', 'Matt$13:13 "Ìdí nì yìí tí mo fi ń fi òwe bá wọn sọ̀rọ̀: “Ní ti rírí, wọn kò rí; ní ti gbígbọ́ wọn kò gbọ́, bẹ́ẹ̀ ni kò sì yé wọn."'), 'Matt$13:14': (None, None, 'Matt$13:14 " Sí ara wọn ni a ti mú àṣọtẹ́lẹ̀ wòlíì Àìsáyà ṣẹ: “ ‘Ní gbígbọ́ ẹ̀yin yóò gbọ́ ṣùgbọn kì yóò sì yé yín; ní rírí ẹ̀yin yóò rí, ṣùgbọ́n ẹ̀yin kì yóò sì mòye."'), 'Matt$13:16': ('wo', 'gbo', 'Matt$13:16 " Ṣùgbọ́n ìbùkún ni fún ojú yín, nítorí wọ́n ríran, àti fún etí yín, nítorí ti wọ́n gbọ́."'), 'Matt$13:17': ('wo', 'gbo', 'Matt$13:17 "Lóòótọ́ ni mo wí fún yín, ọ̀pọ̀ àwọn wòlíì àti ènìyàn Ọlọ́run ti fẹ́ rí ohun tí ẹ̀yin ti rí, ki wọ́n sì gbọ́ ohun tí ẹ ti gbọ́, ṣùgbọ́n kò ṣe é ṣe fún wọn."'), 'Matt$13:24': (None, None, 'Matt$13:24 Jésù tún pa òwe mìíràn fún wọn: “Ìjọba ọ̀run dàbí àgbẹ̀ kan tí ó gbin irúgbìn rere sí oko rẹ̀;'), 'Matt$13:31': (None, None, 'Matt$13:31 " Jésù tún pa òwe mìíràn fún wọn: “Ìjọba ọ̀run dàbí èso hóró músítádì, èyí tí ọkùnrin kan mú tí ó gbìn sínú oko rẹ̀."'), 'Matt$13:34': (None, None, 'Matt$13:34 " Òwe ni Jésù fi sọ nǹkan wọ̀nyí fún àwọn ènìyàn, òwe ni ó fi bá wọn sọ gbogbo ọ̀rọ̀ tó sọ."'), 'Matt$13:37': ('eniyan', None, 'Matt$13:37 "Ó sì dá wọn lóhùn pé, “Ọmọ ènìyàn ni ẹni tí ó ń fúnrúgbìn rere."'), 'Matt$13:40': (None, None, 'Matt$13:40 "“Gẹ́gẹ́ bí a ti kó èpò jọ, tí a sì sun ún nínú iná, bẹ́ẹ̀ gẹ́gẹ́ ni yóò rí ní ìgbẹ̀yìn ayé."'), 'Matt$13:47': (None, None, 'Matt$13:47 " “Bákan náà, a sì tún lè fi ìjọba ọ̀run wé àwọ̀n kan tí a jù sínú odò, ó sì kó onírúurú ẹja."'), 'Matt$14:3': ('dè', None, 'Matt$14:3 " Nísìnsin yìí Hẹ́rọ́dù ti mú Jòhánù, ó fi ẹ̀wọ̀n dè é, ó sì fi sínú túbú, nítorí Hẹ́rọ́díà aya Fílípì arákùnrin rẹ̀,"'), 'Matt$14:15': (None, None, 'Matt$14:15 "Nígbà ti ilẹ̀ ń sú lọ, àwọn ọmọ-ẹ̀yìn rẹ̀ tọ̀ ọ́ wá, wọ́n sì wí pé, “Ibi yìí jìnnà sí ìlú ilẹ̀ sì ń ṣú lọ. Rán àwọn ènìyàn lọ sí àwọn ìletò kí wọ́n lè ra oúnjẹ jẹ.”"'), 'Matt$14:22': ('lọ', None, 'Matt$14:22 " Lẹ́sẹ̀kẹ́sẹ̀ lẹ́yìn èyí, Jésù wí fún àwọn ọmọ-ẹ̀yìn rẹ̀ kí wọ́n bọ sínú ọkọ̀ wọn, àti kí wọ́n máa kọjá lọ ṣáájú rẹ̀ sí òdì kejì. Òun náà dúró lẹ́yìn láti tú àwọn ènìyàn ká lọ sí ilé wọn"'), 'Matt$14:35': ('ranṣẹ', None, 'Matt$14:35 "Nígbà ti wọn mọ̀ pé Jésù ni, wọn sì ránṣẹ́ sí gbogbo ìlú náà yíká. Wọ́n sì gbé gbogbo àwọn aláìsàn tọ̀ ọ́ wá."'), 'Matt$15:1': ('wá', None, 'Matt$15:1 " Nígbà náà ní àwọn Farisí àti àwọn olùkọ́ òfin tọ Jésù wá láti Jerúsálémù,"'), 'Matt$15:3': (None, 'ṣe', 'Matt$15:3 "Jésù sì dá wọn lóhùn pé, “È é ha ṣe tí ẹ̀yin fi rú òfin Ọlọ́run, nítorí àṣà yín?"'), 'Matt$15:7': (None, None, 'Matt$15:7 "Ẹ̀yin àgàbàgebè, ní òtítọ́ ni Wòlíì Àìsáyà sọ tẹ́lẹ̀ nípa yín wí pé:"'), 'Matt$15:9': ('ofin', 'ẹkọ', 'Matt$15:9 Lásán ni ìsìn wọn; nítorí pé wọ́n ń fi òfin ènìyan kọ́ ni ní ẹ̀kọ́.’ ”'), 'Matt$15:30': (None, 'wo', 'Matt$15:30 "Ọ̀pọ̀ àwọn ènìyàn sì tọ̀ ọ́ wá, àti àwọn arọ, afọ́jú, amúkùn-ún, odi àti ọ̀pọ̀ àwọn aláìsàn mìíràn. Wọ́n gbé wọn kalẹ̀ lẹ́sẹ̀ Jésù. Òun sì mú gbogbo wọn lárada."'), 'Matt$15:39': (None, None, 'Matt$15:39 "Lẹ́yìn náà, Jésù rán àwọn ènìyàn náà lọ sí ilé wọn, ó sì bọ́ sínú ọkọ̀, ó ré kọjá sí Mágádánì."'), 'Matt$16:5': (None, 'gba', 'Matt$16:5 " Nígbà tí wọ́n dé apá kejì adágún, àwọn ọmọ-ẹ̀yìn rẹ̀ sàkíyèsí pé wọ́n ti gbàgbé láti mú àkàrà kankan lọ́wọ́."'), 'Matt$16:7': (None, 'ni', 'Matt$16:7 Wọ́n ń sọ eléyìí ni àárin ara wọn nítorí pé wọ́n ti gbàgbé láti mú àkàrà lọ́wọ́.'), 'Matt$16:13': (None, 'eniyan', 'Matt$16:13 " Nígbà tí Jésù sì dé Kesaríà-Fílípì, ó bi àwọn ọmọ ẹ̀yin rẹ̀ pé, “Ta ni àwọn ènìyàn ń fi Ọmọ ènìyàn pè?”"'), 'Matt$16:19': ('dè', None, 'Matt$16:19 " Èmi yóò fún ní àwọn kọ́kọ́rọ́ ìjọba Ọ̀run; Ohun tí ìwọ bá dè ní ayé, òun ni a ó dè ní ọ̀run. Ohunkóhùn tí ìwọ bá sì tú ní ayé yìí, a ó sì tú ní ọ̀run.”"'), 'Matt$16:24': (None, None, 'Matt$16:24 " Nígbà náà ni Jésù wí fún àwọn ọmọ-ẹ̀yìn rẹ̀ pé, “Bí ẹnikẹ́ni bá fẹ́ tọ̀ mí lẹ́yìn, kí ó sẹ́ ara rẹ̀, kí ó sì gbé àgbélébùú rẹ̀, kí ó sì máa tọ̀ mí lẹ́yìn."'), 'Matt$16:26': (None, 'eniyan', 'Matt$16:26 "Èrè kí ni ó jẹ́ fún ènìyàn bí ó bá jèrè gbogbo ayé yìí, tí ó sì sọ ẹ̀mí rẹ̀ nù? Tàbí kí ni ènìyàn yóò fi dípò ẹ̀mí rẹ̀?"'), 'Matt$17:12': (None, 'wá', 'Matt$17:12 " Ṣùgbọ́n èmi wí fún yín lóòótọ́; Èlíjà ti dé. Ṣùgbọ́n wọn kò mọ̀ ọ́n, ọ̀pọ̀ tilẹ̀ hu ìwà búburú sí i. Bákan náà, Ọmọ ènìyàn náà yóò jìyà lọ́wọ́ wọn pẹ̀lú.”"'), 'Matt$17:22': (None, 'eniyan', 'Matt$17:22 " Nígbà tí wọ́n sì ti wà ní Gálílì, Jésù sọ fún wọn pé, “Láìpẹ́ yìí a ó fi Ọmọ ènìyàn lé àwọn ènìyàn lọ́wọ́."'), 'Matt$17:27': ('ṣí', 'fun', 'Matt$17:27 " ‘Ṣíbẹ̀ṣíbẹ̀, àwa kò fẹ́ mú wọn bínú’ Nítorí náà, ẹ lọ sí etí òkun, kí ẹ sì sọ ìwọ̀ sí omi. Ẹ mú ẹja àkọ́kọ́ tí ẹ kọ́ fà sókè, ẹ ya ẹnu rẹ̀, ẹ̀yin yóò sì rí owó idẹ kan níbẹ̀, ki ẹ fi fún wọn fún owó-orí tèmi àti tirẹ̀.”"'), 'Matt$18:18': ('dè', None, 'Matt$18:18 " “Àti pé mo tún sọ èyí fún yín, Ohunkóhun tí ẹ̀yin bá dè ní ayé, ni a dè ní ọ̀run. Ohunkóhùn ti ẹ̀yin bá sì ti tú ni ayé, á ò tú u ní ọ̀run."'), 'Matt$18:32': (None, None, 'Matt$18:32 "“Nígbà tí olúwa rẹ̀ pè Ọmọ-ọ̀dọ̀ náà wọlé, ó wí fún un pé, ‘Ìwọ Ọmọ-ọ̀dọ̀ búburú, níhìn-ín ni mo ti dárí gbogbo gbésè rẹ jì ọ́ nítorí tí ìwọ bẹ̀ mi;."'), 'Matt$18:35': (None, None, 'Matt$18:35 " “Bẹ́ẹ̀ gẹ́gẹ́ sì ni Baba mi tí ń bẹ ní ọ̀run yóò sí ṣe sì ẹnì kọ̀ọ̀kan, bí ẹ̀yin kò bá fi tọkàntọkàn dárí ji àwọn arákùnrin yín.”"'), 'Matt$19:4': (None, None, 'Matt$19:4 "Ó dáhùn pé, “A ti rí i kà pé ‘ní ìpilẹ̀sẹ, Ọlọ́run dá wọn ni ọkùnrin àti obìnrin.’"'), 'Matt$19:9': ('agbere', 'agbere', 'Matt$19:9 " Mo sọ èyí fún yín pé, ẹni tí ó bá kọ aya rẹ̀ sílẹ̀, láìṣe pé nítorí àgbèrè, tí ó sì fẹ́ òmíràn, ó ṣe panṣágà.”"'), 'Matt$19:12': (None, 'ni', 'Matt$19:12 "Àwọn mìíràn jẹ́ akúra nítorí bẹ́ẹ̀ nía bí wọn, àwọn mìíràn ń bẹ tí ènìyàn sọ wọn di bẹ́ẹ̀; àwọn mìíràn kọ̀ láti gbé ìyàwó nítorí ìjọba ọ̀run. Ẹni tí ó bá lè gbà á kí ó gbà á.”"'), 'Matt$19:16': ('wá', 'ṣe', 'Matt$19:16 " Ẹnì kan sì wá ó bí Jésù pé, “Olùkọ́, ohun rere wo ni èmi yóò ṣe kí n tó lè ní ìyè àìnípẹ̀kun?”"'), 'Matt$19:20': (None, None, 'Matt$19:20 "Ọmọdékùnrin náà tún wí pé, “Gbogbo òfin wọ̀nyí ni èmi ti ń pamọ́, kí ni nǹkan mìíràn tí èmi mo ní láti ṣe?”"'), 'Matt$19:21': ('fun', 'ni', 'Matt$19:21 " Jésù wí fún un pé, “Bí ìwọ bá fẹ́ di ẹni pípé, lọ ta ohun gbogbo tí ìwọ ní, kí o sì fi owó rẹ̀ tọrẹ fún àwọn aláìní. Ìwọ yóò ní ọrọ̀ ńlá ní ọ̀run. Lẹ́yìn náà, wá láti máa tọ̀ mi lẹ́yìn.”"'), 'Matt$20:8': (None, None, 'Matt$20:8 " “Nígbà tí ó di àṣálẹ́, ẹni tó ni ọgbà àjàrà sọ fún aṣojú rẹ̀ pé, ‘Pe àwọn òsiṣẹ́ náà, kí ó san owó iṣẹ́ wọn fún wọn, bẹ̀rẹ̀ láti ẹni ìkẹyìn lọ sí ti ìṣáájú.’"'), 'Matt$20:11': (None, 'ni', 'Matt$20:11 "Bí wọ́n ti ń gbà á, wọ́n ń kùn sí onílẹ̀ náà,"'), 'Matt$20:13': (None, 'ṣe', 'Matt$20:13 " “Ṣùgbọ́n ó dá ọ̀kan nínú wọn lóhùn pé, ‘Ọ̀rẹ́, kò sí aburú nínú nǹkan tí èmi ṣe sí yín. Kì í ha ṣe pé ẹ̀yin gbà láti ṣiṣẹ́ fún owó dínárì kan."'), 'Matt$20:14': ('lọ', None, 'Matt$20:14 "Ó ní, Gba èyí tíí ṣé tìrẹ, ki ó sì máa lọ. Èmi fẹ́ láti fún ẹni ìkẹ̀yìn gẹ́gẹ́ bí mo ti fi fún ọ."'), 'Matt$20:23': ('fun', None, 'Matt$20:23 " Jésù sì wí fún wọn pé, “Dájúdájú, ẹ̀yin yóò mu nínú aago mi, ṣùgbọ́n èmi kò ní àṣẹ láti sọ irú ènìyàn tí yóò jòkòó ní apá ọ̀tún tàbí apá òsì. Baba mi ti pèsè àyè wọ́n-ọn-nì sílẹ̀ fún kìkì àwọn tí ó yàn.”"'), 'Matt$21:6': ('ṣe', None, 'Matt$21:6 "Àwọn ọmọ-ẹ̀yìn náà sí lọ, wọ́n ṣe bí Jésù ti sọ fún wọn"'), 'Matt$21:12': (None, 'ni', 'Matt$21:12 "Jésù sì wọ inú tẹ́ḿpì Ọlọ́run. Ó sì lé àwọn ti ń tà àti àwọn ń rà níbẹ̀ jáde. Ó yí tábìlì àwọn onípàsípààrọ̀ owó dànù, àti tábìlì àwọn tí ó ń ta ẹyẹlẹ́."'), 'Matt$21:21': (None, 'ṣe', 'Matt$21:21 " Jésù wí fún wọn pé, “Lóòótọ́ ni mo wí fún yín, bí ẹ̀yin bá lè ní ìgbàgbọ́ láì síyè méjì, nígbà náà ẹ̀yin yóò lè ṣe irú tí a se sí igi ọ̀pọ̀tọ́, Ẹ̀yin yóò lè sọ fún òkè yìí pé, ‘Yí ipò padà sínú òkun,’ yóò sì ríbẹ́ẹ̀."'), 'Matt$21:24': ('dahùn', 'ṣe', 'Matt$21:24 "Jésù sì dáhùn ó sì wí fún wọn pé, “Èmi yóò si bí yin léère ohun kan pàtọ́, bí ẹ̀yin bá lé sọ fún mi, nígbà náà èmi yóò sọ fún yín, àṣẹ tí Èmi fi ń ṣe àwọn nǹkan wọ̀nyí."'), 'Matt$22:1': (None, None, 'Matt$22:1 Jésù tún fi òwe sọ̀rọ̀ fún wọn wí pé:'), 'Matt$22:11': ('ri', 'wo', 'Matt$22:11 "“Ṣùgbọ́n nígbà tí ọba sì wọlé wá láti wo àwọn àlejò tí a pè, ó sì rí ọkùnrin kan nínú wọn tí kò wọ àṣọ ìgbéyàwó."'), 'Matt$22:16': (None, 'eniyan', 'Matt$22:16 "Wọ́n sì rán àwọn ọmọ-ẹ̀yìn wọn pẹ̀lú àwọn ọmọ-ẹ̀yìn Hérọ́dù lọ sọ́dọ̀ rẹ̀ pé, “Olùkọ́, àwa mọ̀ pé olótìítọ́ ni ìwọ, ìwọ sì ń kọ́ni ní ọ̀nà Ọlọ́run ní òtítọ́. Bẹ́ẹ̀ ni ìwọ kì í wo ojú ẹnikẹ́ni; nítorí tí ìwọ kì í ṣe ojúsàájú ènìyàn."'), 'Matt$22:29': (None, None, 'Matt$22:29 "Ṣùgbọ́n Jésù dá wọn lóhùn pé, “Àìmọ̀kan yín ni ó fa irú ìbéèrè báyìí. Nítorí ẹ̀yin kò mọ ìwé Mímọ́ àti agbára Ọlọ́run."'), 'Matt$22:31': (None, None, 'Matt$22:31 "Ṣùgbọ́n nísinsìn yìí, nípa ti àjíǹde òkú, tàbí ẹ̀yin kò mọ̀ pé Ọlọ́run ń bá yín sọ̀rọ̀ nígbà ti ó wí pé:"'), 'Matt$23:4': ('wuwo', None, 'Matt$23:4 " Wọ́n á di ẹrù wúwo tí ó sì ṣòro láti rù, wọ́n a sì gbé e le àwọn ènìyàn léjìká, ṣùgbọ́n àwọn tìkára wọn kò jẹ́ fi ìka wọn kan an."'), 'Matt$24:6': ('wo', 'gbo', 'Matt$24:6 " Ẹ ó máa gbọ́ ìró ogun àti ìdágìrì ogun, kí ẹ̀yin má se jáyà nítorí nǹkan wọ̀nyí kò lè ṣe kí ó ma ṣẹlẹ̀, ṣùgbọ́n òpin kì í ṣe àkókò náà."'), 'Matt$24:15': ('wo', None, 'Matt$24:15 " “Nítorí náà, nígbà tí ẹ̀yin bá rí ìríra ìṣọdahoro, tí a ti ẹnu wòlíì Dáníẹ́lì sọ, tí ó bá dúró ní ibi mímọ́, (ẹni tí ó bá kà á, kí òye kí ó yé e)."'), 'Matt$24:29': (None, None, 'Matt$24:29 " “Lójú kan náà lẹ́yìn ìpọ́njú ọjọ́ wọ̀n-ọn-nì, “ ‘ni oòrùn yóò ṣókùnkùn, òṣùpá kì yóò sì fi ìmọ́lẹ̀ rẹ hàn; àwọn ìràwọ̀ ojú ọ̀run yóò já sílẹ̀, agbára ojú ọ̀run ni a ó mì tìtì.’"'), 'Matt$24:32': (None, None, 'Matt$24:32 "“Nísinsìn yìí, ẹ kọ́ ẹ̀kọ́ lára igi ọ̀pọ̀tọ́: Nígbà tí ẹ̀ka rẹ̀ bá ti ń yọ titun tí ó bá sì ń ru ewé, ẹ̀yin mọ̀ pé ìgbà ẹ̀ẹ̀rùn sún mọ́ tòòsí,"'), 'Matt$24:43': (None, None, 'Matt$24:43 " Ṣùgbọ́n, kí ẹ mọ èyí pé, baálé ilé ìbá mọ wákàtí náà tí olè yóò wá, ìbá máa ṣọ́nà, òun kì bá ti jẹ́ kí a já wọ ilé rẹ̀."'), 'Matt$24:45': ('ṣe', None, 'Matt$24:45 “Ǹjẹ́ olóòótọ́ àti ọlọ́gbọ́n ọmọ-ọ̀dọ̀ wo ni olúwa rẹ̀ lè fi ṣe olùbojútó ilé rẹ̀ láti bọ́ àwọn ọmọ rẹ̀ lójoojúmọ́?'), 'Matt$25:26': ('ni', None, 'Matt$25:26 "“Ṣùgbọ́n olúwa rẹ̀ dáhùn pé, ‘Ìwọ ọmọ ọ̀dọ̀ búburú, ìwọ mọ̀ pé èmi ń kórè níbi tí èmi kò fúnrúgbìn sì, èmi sì ń kó jọ níbi tí èmi kò ó ká sí."'), 'Matt$25:29': (None, 'ni', 'Matt$25:29 " Nítorí ẹnikẹ́ni tí ó bá ní a ó fún sí i, yóò sí tún ní sí lọ́pọ̀lọpọ̀. Ṣùgbọ́n láti ọwọ́ ẹni tí kò ní ni a ó ti gbà èyí tí ó ní."'), 'Matt$25:34': ('fun', 'ọwọ', 'Matt$25:34 " “Nígbà náà ni Ọba yóò wí fún àwọn tí ó wà lọ́wọ́ ọ̀tún pé, ‘Ẹ wá, ẹ̀yin tí Baba mi ti bùkún fún, ẹ jogún ìjọba tí a ti pèsè fún yín láti ìpìlẹ̀ṣẹ̀ ayé."'), 'Matt$25:40': ('ṣe', None, 'Matt$25:40 " “Ọba náà yóò sì dáhùn yóò sì wí fún wọn pé, ‘lóòótọ́ ni mo wí fún yín pé níwọ̀n ìgbà tí ẹ̀yin ṣe nǹkan wọ̀nyí fún àwọn arákùnrin mi yìí tí o kéré jú lọ, ẹ̀ ń ṣe wọn fún mi ni.’"'), 'Matt$25:41': ('fun', 'ọwọ', 'Matt$25:41 " “Nígbà náà ni yóò sọ fún àwọn tí ọwọ́ òsì pé, ‘Ẹ kúrò lọ́dọ̀ mi, ẹ̀yin ẹni ègún, sínú iná àìnípẹ̀kun tí a ti tọ́jú fún èṣù àti àwọn ańgẹ́lì rẹ̀."'), 'Matt$25:44': (None, None, 'Matt$25:44 "“Nígbà náà wọn yóò dáhùn pé, ‘Olúwa, nígbà wo ni àwa rí ọ, tí ebi ń pa ọ́ tàbí tí òrùngbẹ ń gbẹ ọ́, tàbí tí o ṣàìsàn, tàbí tí o wà lọ́gbà ẹ̀wọ̀n, tí a kò sí ràn ọ́ lọ́wọ́?’"'), 'Matt$25:45': ('ṣe', None, 'Matt$25:45 "“Yóò sì dáhùn pé, ‘Nígbà tí ẹ̀yin ti kọ̀ láti ran ọ̀kan nínú àwọn tí ó kéré jù lọ́wọ́ nínú arákùnrin mi ẹ̀yin tí kọ̀ láti ṣe ìrànlọ́wọ́ fún mi ni.’"'), 'Matt$26:9': (None, 'ni', 'Matt$26:9 "È é ha ti ṣe, obìnrin yìí ìbá tà á ní owó púpọ̀, kí a sì fi owó náà fún àwọn aláìní.”"'), 'Matt$26:19': ('ṣe', None, 'Matt$26:19 " Nítorí náà, àwọn ọmọ-ẹ̀yìn náà ṣe gẹ́gẹ́ bí Jésù ti sọ fún wọn. Wọ́n sì tọ́jú oúnjẹ àsè ti ìrékọjá níbẹ̀."'), 'Matt$26:39': (None, 'kọjá', 'Matt$26:39 " Òun lọ sí iwájú díẹ̀ sí i, ó sì dojú bolẹ̀, ó sì gbàdúrà pé, “Baba mi, bí ó bá ṣeé se, jẹ́ kí a mú aago yìí ré mi lórí kọjá, ṣùgbọ́n ìfẹ́ tìrẹ ni mo fẹ́ kí ó ṣẹ, kì í ṣe ìfẹ́ tèmi.”"'), 'Matt$26:42': ('lọ', 'kọjá', 'Matt$26:42 " Ó tún fi wọ́n sílẹ̀ nígbà kejì, ó sí gbàdúrà pé, “Baba mi, bí aago yìí kò bá lè ré mi lórí kọjá àfi tí mo bá mu ún, ìfẹ́ tìrẹ ni kí a ṣe.”"'), 'Matt$26:47': ('eniyan', None, 'Matt$26:47 " Bí ó ti ń sọ̀rọ̀ lọ́wọ́, Júdásì, ọ̀kan nínú àwọn méjìlá dé pẹ̀lú ọ̀pọ̀ ènìyàn pẹ̀lú idà àti kùmọ̀ lọ́wọ́ wọn, wọ́n ti ọ̀dọ̀ àwọn olórí àlùfàá àti àgbààgbà Júù wá."'), 'Matt$26:48': ('fun', None, 'Matt$26:48 "Ẹni tí ó sì fi í hàn ti fi àmì fún wọn, pé, “Ẹnikẹ́ni tí mo bá fi ẹnu kò ní ẹnu, òun náà ni; ẹ mú un.”"'), 'Matt$26:59': ('ri', 'ẹlẹri', 'Matt$26:59 "Nígbà náà ni àwọn olórí àlùfáà àti gbogbo ìgbìmọ̀ Júù ní ilé-ẹjọ́ tí ó ga jùlọ ti Júù pé jọ síbẹ̀, wọ́n wá àwọn ẹlẹ́rìí tí yóò purọ́ mọ́ Jésù, kí a ba à lè rí ẹjọ́ rò mọ́ ọn lẹ́sẹ̀, eléyìí tí yóò yọrí si ìdájọ́ ikú fún un."'), 'Matt$27:19': ('jiya', 'ni', 'Matt$27:19 " Bí Pílátù sì ti ṣe jókòó lórí ìtẹ́ ìdájọ́ rẹ̀, ìyàwó rẹ̀ ránṣẹ́ sí i pé, “Má ṣe ní ohun kankan ṣe pẹ̀lú ọkùnrin aláìṣẹ̀ náà. Nítorí mo jìyà ohun púpọ̀ lójú àlá mi lónìí nítorí rẹ̀.”"'), 'Matt$27:24': ('ri', 'wo', 'Matt$27:24 " Nígbà tí Pílátù sì rí i pé òun kò tún rí nǹkan kan ṣe mọ́, àti wí pé rògbòdìyàn ti ń bẹ̀rẹ̀, ó béèrè omi, ó sì wẹ ọwọ́ rẹ̀ níwájú ọ̀pọ̀ ènìyàn, ó wí pé, “Ọrùn mí mọ́ nípa ẹ̀jẹ̀ ọkùnrin yìí. Ẹ̀yin fúnraa yín, ẹ bojú tó o!”"'), 'Matt$27:31': (None, 'tirẹ', 'Matt$27:31 "Nígbà tí wọ́n fi ṣẹ̀sín tán, wọ́n bọ́ aṣọ ara rẹ̀. Wọ́n tún fi aṣọ tirẹ̀ wọ̀ ọ́. Wọ́n sì mú un jáde láti kàn án mọ́ àgbélébùú."'), 'Matt$27:43': ('ni', 'jẹ ki', 'Matt$27:43 "Ó gba Ọlọ́run gbọ́, jẹ́ kí Ọlọ́run gbà á là ní ìsinsin yìí tí òun bá fẹ́ ẹ. Ǹjẹ́ òun kò wí pé, èmi ni Ọmọ Ọlọ́run?”"'), 'Matt$27:59': (None, None, 'Matt$27:59 Jósẹ́fù sì gbé òkú náà. Ó fi aṣọ funfun mímọ́ dì í.'), 'Matt$27:64': (None, None, 'Matt$27:64 "Nítorí náà, pàṣẹ kí a ti ibojì rẹ̀ gbọn-ingbọn-in títí ọjọ́ kẹta, kí àwọn ọmọ-ẹ̀yìn Rẹ̀ má ṣe wá jí i gbé lọ, wọn a sì bẹ̀rẹ̀ sí í sọ fún gbogbo ènìyàn pé, ‘Òun ti jíǹde,’ Bí èyí bá ní láti ṣẹlẹ̀, yóò burú fún wa púpọ̀ ju ti àkọ́kọ́ lọ.”"'), 'Matt$27:65': ('ṣe', None, 'Matt$27:65 "Pílátù sì pàṣẹ pé, “Ẹ lo àwọn olùṣọ́ yín kí wọn dáàbò bo ibojì náà bí ẹ bá ti fẹ́.”"'), 'Mark$1:13': (None, None, 'Mark$1:13 Ó sì wà níbẹ̀ fún ogójì ọjọ́. A sì fi Í lé Èṣù lọ́wọ́ láti dán an wò. Àwọn ańgẹ́lì sì wá ṣe ìtọ́jú Rẹ̀.'), 'Mark$1:23': (None, 'ni', 'Mark$1:23 " Ní àsìkò náà gan-an ni ọkùnrin kan tí ó wà nínú sínágọ́gù wọn, tí ó ní ẹ̀mí àìmọ́, ó sì bẹ̀rẹ̀ sí í kígbe wí pé,"'), 'Mark$1:32': (None, None, 'Mark$1:32 " Nígbà tí ó di àṣálẹ́, tí òòrùn wọ̀, wọ́n gbé gbogbo àwọn aláìsàn àti àwọn tó ni ẹ̀mí àìmọ́ tọ̀ ọ́ wá."'), 'Mark$1:34': (None, 'aisan', 'Mark$1:34 "Jésù sì wo ọ̀pọ̀ tí wọ́n ní onírúurú aláìsàn sàn. Bákan náà ni ó lé ọ̀pọ̀ ẹ̀mí àìmọ́ jáde, Ṣùgbọ́n kò sì jẹ́ kí àwọn ẹ̀mí àìmọ́ náà kí ó sọ̀rọ̀, nítorí tí wọ́n mọ ẹni tí òun í se."'), 'Mark$1:43': (None, 'lọ', 'Mark$1:43 Jésù sì kìlọ̀ fún un gidigidi'), 'Mark$2:4': ('ní', None, 'Mark$2:4 "Nígbà tí wọn kò sì le dé ọ̀dọ̀ Jésù, nítorí ọ̀pọ̀ ènìyàn, wọ́n dá òrùlé ilé lu ní ọ̀gangan ibi tí Jésù wà. Wọ́n sì sọ ọkùnrin arọ náà kalẹ̀ ti òun ti ẹní rẹ̀ níwájú Jésù."'), 'Mark$2:8': (None, None, 'Mark$2:8 "Lojú kan náà bí Jésù tí wòye nínú ọkàn rẹ̀ pé wọn ń gbèrò bẹ́ẹ̀ ní àárín ara wọn, ó wí fún wọn pé, “È é ṣe tí ẹ̀yìn fí ń ro nǹkan wọ̀nyí nínú ọkàn yín?"'), 'Mark$2:10': ('mọ', None, 'Mark$2:10 "Ṣùgbọ́n ẹ lè mọ̀ pé Ọmọ-Ènìyàn ní agbára ní ayé làti dárí ẹ̀ṣẹ̀ rẹ̀ ji ní.” Ó wí fún ọkùnrin arọ náà pé,"'), 'Mark$2:18': ('wá', 'ṣe', 'Mark$2:18 " Àwọn ọmọ-ẹ̀yìn Jòhánù àti àwọn Farisí a máa gbààwẹ̀: Àwọn ènìyàn kan sì wá, wọ́n sì bi í pé, “È é ṣe tí àwọn ọmọ-ẹ̀yìn Jòhánù àti àwọn ọmọ-ẹ̀yìn Farisí fi ń gbààwẹ̀, ṣùgbọ́n àwọn ọmọ ẹ̀yin rẹ kò gbààwẹ̀?”"'), 'Mark$2:21': (None, None, 'Mark$2:21 "“Kò si ẹni tí ń fi ìrépé aṣọ túntún lẹ ògbólógbo ẹ̀wù, bí ó ba se bẹ́ẹ̀, èyí túntún ti a fi lẹ̀ ẹ́ yóò fà ya kúrò lára ògbólógbòó, yíyọ́ rẹ̀ yóò sí burú púpọ̀ jù."'), 'Mark$2:23': ('lọ', 'kọjá', 'Mark$2:23 " Ó sì ṣe ni ọjọ ìsinmi, bí Jésù àti àwọn ọmọ-ẹ̀yìn rẹ̀ ti ń kọjá lọ láàrin oko ọkà, àwọn ọmọ-ẹ̀yìn rẹ̀ bẹ̀rẹ̀ sí í ya ìpẹ́ ọká."'), 'Mark$2:25': (None, 'ka', 'Mark$2:25 "Ó dá wọn lóhùn pé, “Ẹ̀yín kò tí kà ohun tí Dáfídì ṣe, nígbà tí ó ṣe aláìní, tí ebi sì ń pa á, òun àti àwọn tí ó wà lọ́dọ̀ rẹ̀?"'), 'Mark$3:9': (None, None, 'Mark$3:9 "Nítorí ẹ̀rọ̀, Jésù sọ fún àwọn ọmọ-ẹ̀yìn Rẹ̀ lati sètò ọkọ̀ ojú omi kekere kan sílẹ̀ fún un láti lọ, láti lé àwọn ẹ̀rọ̀ sẹ́yìn."'), 'Mark$4:2': (None, None, 'Mark$4:2 Ó bẹ̀rẹ̀ sí í fi òwe bá wọn sọ̀rọ̀ nínú ẹ̀kọ́ rẹ̀ pé:'), 'Mark$4:5': (None, 'ilẹ', 'Mark$4:5 "Díẹ̀ bọ́ sórí ilẹ̀ àpátá, níbi tí erùpẹ̀ ko sí púpọ̀. Láìpẹ́ ọjọ́, ó hú jáde."'), 'Mark$4:8': ('eso', None, 'Mark$4:8 "Ṣùgbọ́n òmíràn bọ́ sí ilẹ̀ ọlọ́ràá, Ó sì so èso lọ́pọ̀lọpọ̀-òmíràn ọgbọọgbọ̀n, òmíràn ọgọọgọ́ta, àti òmíràn ọgọọgọ́rùn-ún.”"'), 'Mark$4:11': ('gbo', 'wo', 'Mark$4:11 " Ó sì dá wọn lóhùn pé, “Ẹ̀yin ni a gbà láàyè láti mọ ohun ìjìnlẹ̀ nípa ìjọba Ọlọ́run. Èyí tí ó pamọ́ sí àwọn tí ó wà lẹ́yìn agbo ìjọba náà."'), 'Mark$4:16': ('ni', None, 'Mark$4:16 "Bákan náà, àwọn tí ó bọ́ sórí àpata, ni àwọn ènìyàn tí wọ́n fi ayọ̀ gbọ́ ọ̀rọ̀ Ọlọ́run."'), 'Mark$4:23': (None, 'ni', 'Mark$4:23 " Bí ẹnikẹ́ni bá ní etí láti fi gbọ́, kí ó gbọ́.”"'), 'Mark$4:26': (None, None, 'Mark$4:26 " Ó sì tún ọ èyí pé, “Èyí ni a lè fí ìjọba Ọlọ́run wé. Ọkùnrin kan ń fúnrúgbìn sí ilẹ̀."'), 'Mark$4:31': (None, None, 'Mark$4:31 "Ó dàbí èso hóró Músítádì kan, lóòótọ́, ó jọ ọ̀kan níńu àwọn èso tí ó kéré jùlọ ti a gbin sínú ilẹ̀."'), 'Mark$4:36': (None, 'ọkọ oju omi', 'Mark$4:36 "Nígbà tí wọn ti tú ìjọ ká, wọ́n si gbà á sínú ọkọ̀ ojú omi gẹ́gẹ́ bí ó ti wà. Àwọn ọkọ̀ ojú-omi kékeré mìíràn sì wà lọ́dọ̀ rẹ̀."'), 'Mark$5:2': ('dè', None, 'Mark$5:2 Bí Jésù sì ti ń ti inú ọkọ̀ ojú omi jáde. Ọkùnrin kan tí ó ní ẹ̀mí àìmọ́ jáde ti ibojì wá pàdé rẹ̀.'), 'Mark$5:13': ('fun', None, 'Mark$5:13 "Jésù fún wọn láàyè, àwọn ẹ̀mí àìmọ́ náà sì jáde kúrò lára ọkùnrin náà, wọ́n sì wọ inú àwọn ẹlẹ́dẹ̀ náà lọ. Agbo ẹlẹ́dẹ̀ náà tí ó tó ìwọ̀n ẹgbàá sì túká lọ́gán, wọ́n sì sáré lọ ní gẹ̀rẹ́gẹ̀rẹ́ òkè rọ́ sínú òkun, wọ́n sì ṣègbé."'), 'Mark$5:22': (None, 'wá', 'Mark$5:22 " Ọ̀kan nínú àwọn olórí sínágọ́gù tí à ń pè ni Jáírù wá sọ́dọ̀ Jésù, nígbà tí ó sì rí i, ó wólẹ̀ níwájú rẹ̀."'), 'Mark$5:32': ('wò', 'wo', 'Mark$5:32 "Ṣíbẹ̀, Jésù bẹ̀rẹ̀ sí wò yíká láti rí ẹni náà, tí ó fi ọwọ́ kan òun."'), 'Mark$5:42': (None, None, 'Mark$5:42 "Lẹ́sẹ̀kan-náà, ọmọbìnrin náà sì dìde. Ó sì ń rìn, ẹ̀rù sì ba wọn, ẹnú sì ya àwọn òbí rẹ̀ gidigidi."'), 'Mark$6:1': (None, None, 'Mark$6:1 Jésù fi ibẹ̀ sílẹ̀ lọ sí ìlú rẹ̀ pẹ̀lú àwọn ọmọ-ẹ̀yìn rẹ̀'), 'Mark$6:13': ('ororo', None, 'Mark$6:13 " Wọ́n lé ọ̀pọ̀lọpọ̀ àwọn ẹ̀mí àìmọ́ jáde. Wọ́n sì ń fi òróró kun orí àwọn tí ara wọn kò dá, wọ́n sì mú wọn láradá."'), 'Mark$6:17': (None, None, 'Mark$6:17 " Hẹ́rọ́dù tikararẹ́ sá ti ránṣẹ mú Johanu, tìkaararẹ̀ sínu túbú nítorí Hẹrodíà aya Fílípì arákùnrin rẹ̀ nítorí tí ó fi ṣe aya."'), 'Mark$6:21': ('ṣe', 'fun', 'Mark$6:21 "Níkẹ̀yìn Hẹ́rọ́díà rí ààyè. Àkókò yìí ni ọjọ́ ìbí Hẹ́rọ́dù, òun sì pèṣè àsè ní ààfin ọba fún àwọn olùrànlọ́wọ́ rẹ̀: àwọn balógun àti àwọn jàǹkànjànkàn ní Gálílì."'), 'Mark$6:35': (None, 'lọ', 'Mark$6:35 "Nígbà tí ọjọ́ sì ti bu lọ tán, àwọn ọmọ-ẹ̀yìn rẹ̀ tọ ọ́ wa, wọ́n wí fún un pé, ibi aṣálẹ̀ ni ìbí yìí, ọjọ́ sì bù lọ tán."'), 'Mark$6:45': ('lọ', None, 'Mark$6:45 " Lẹ́sẹ̀kẹsẹ̀ lẹ́yìn èyí, Jésù pàṣẹ fún àwọn ọmọ-ẹ̀yìn rẹ̀ láti padà sínú ọkọ̀ kí wọn sì ṣáájú rékọjá sí Bẹtisáídà. Níbẹ̀ ni òun yóò ti wà pẹ̀lú wọn láìpẹ́. Nítorí òun fúnra a rẹ̀ yóò dúró sẹ́yìn láti rí i pé àwọn ènìyàn túká lọ ilé wọn."'), 'Mark$6:48': ('ri', None, 'Mark$6:48 " Ó rí i wí pé àwọ́n ọmọ-ẹyin wà nínú wàhálà púpọ̀ ní wíwa ọkọ̀ náà nítorí ti ìjì líle ṣe ọwọ́ òdì sí wọn, nígbà tí ó sì dì ìwọ̀n ìṣọ́ kẹrin òru, ó tọ̀ wọ́n wá, ó ń rìn lórí omi òkun, òun sì fẹ́ ré wọn kọjá,"'), 'Mark$6:54': (None, None, 'Mark$6:54 "Wọ́n jáde kúrò nínú ọkọ̀. Àwọn ènìyàn tó wà níbẹ̀ rí Jésù, wọ́n sì dá a mọ̀ ọ́n."'), 'Mark$7:7': ('ofin', None, 'Mark$7:7 "Ìsìn wọn jẹ́ lásán, ìkọ́ni wọ́n jẹ́ kìkì dá òfin tí àwọn ènìyàn fi ń kọ́ni.’"'), 'Mark$7:12': (None, 'ni', 'Mark$7:12 Bẹ́ẹ̀ ni ẹ̀yin kò si jẹ́ kí ó ṣe ohunkóhun fún baba tàbí iyá rẹ̀ mọ́.'), 'Mark$7:16': (None, 'ni', 'Mark$7:16 Ẹni tí ó bá ní etí láti fi gbọ́ kí ó gbọ́.”'), 'Mark$7:21': (None, 'okan', 'Mark$7:21 "Nítorí pé láti inú ọkàn ènìyàn ni àwọn èrò búburú wọ̀nyí ti ń jáde wá: àgbèrè, olè, ìpànìyàn, panṣágà,"'), 'Mark$7:27': (None, None, 'Mark$7:27 "Jésù sọ fún obìnrin yìí pé, “Ní àkọ́kọ́, ó yẹ kí a fi oúnjẹ tẹ́ àwọn ọmọ lọ́rùn ná. Nítorí kò tọ́ kí a mú oúnjẹ àwọn ọmọ fún àwọn ajá.”"'), 'Mark$7:33': ('ahọn', None, 'Mark$7:33 " Jésù sì mú ọkùnrin náà kúrò láàrin ọ̀pọ̀ ènìyàn. Ó sì fi àwọn ìka rẹ̀ sí etí ọkùnrin náà, ó tu itọ sọ́wọ́. Ó sì fi kan ahọ́n rẹ̀."'), 'Mark$8:1': (None, None, 'Mark$8:1 " Ní ọjọ́ kan, bí ọ̀pọ̀ ènìyàn ti péjọ, kò sí oúnjẹ fún wọn mọ́ láti jẹ. Jésù pe àwọn ọmọ-ẹ̀yìn rẹ̀."'), 'Mark$8:4': (None, None, 'Mark$8:4 "Àwọn ọmọ-ẹ̀yìn rẹ̀ béèrè pé, “Níbo ni a ó ti rí búrẹ́dì tí ó tó láti fibọ wọn nínú aṣálẹ̀ yìí?”"'), 'Mark$8:6': ('ṣe', None, 'Mark$8:6 "Nítori náà, ó pàsẹ fún àwọn ènìyàn náà láti jókòó lórí ilẹ̀. Ó sì mú odindi búrédì méje náà, ó dúpẹ́ lọ́wọ́ Ọlọ́run, ó sì bù wọ́n sí wẹ́wẹ́, ó pín wọ́n fún àwọn ọmọ-ẹ̀yìn rẹ̀ láti gbé ka iwájú àwọn ènìyàn, wọ́n sì ṣe bẹ́ẹ̀."'), 'Mark$8:14': ('ní', 'gba', 'Mark$8:14 Sùgbọ́n àwọn ọmọ-ẹ̀yìn ti gbàgbé láti mú búrédì tí yóò tó wọn ọ́n jẹ lọ́wọ́. Ẹyọ ìṣù búrédì kan ṣoṣo ni ó wà nínú ọkọ̀ wọn.'), 'Mark$8:24': ('wò', 'wo', 'Mark$8:24 "Ọkùnrin náà wò àyíká rẹ̀, ó sì dáhùn pé, “Bẹ́ẹ̀ ni Olúwa, mo rí àwọn ènìyàn, ṣùgbọ́n n kò rí wọn kedere, wọ́n n rìn kiri bí àgékù igi.”"'), 'Mark$8:25': ('ri', 'wo', 'Mark$8:25 "Nígbà náà, Jésù tún gbé ọwọ́ rẹ̀ méjèèjì lé àwọn ojú ọkùnrin náà, bí ọkùnrin náà sì ti ranjú mọ́ ọn, a dá ìran rẹ̀ padà, ó sì rí gbogbo nǹkan kedere."'), 'Mark$8:34': (None, None, 'Mark$8:34 " Nígbà náà ni Jésù pe àwọn ọmọ-ẹ̀yìn rẹ̀ àti ọ̀pọ̀ ènìyàn láti bá wọn sọ̀rọ̀, ó wí fún wọn pé, “Bí ẹnikẹ́ni bá fẹ́ tọ̀ mí lẹ́yìn, kí ó ṣe ara rẹ̀, kí ó si gbé àgbélébùú rẹ̀, kí ó si máa tọ̀ mi lẹ́yìn."'), 'Mark$8:36': (None, None, 'Mark$8:36 "Nítorí èrè kí ni ó jẹ fún ẹni tí ó jèrè gbogbo ayé, tí ó sọ ẹ̀mí rẹ̀ nù.?"'), 'Mark$9:13': ('ṣe', 'wá', 'Mark$9:13 "Ṣùgbọ́n mo wí fún yín pé, Èlíjà ti wa ná, wọ́n sì ti ṣe ohunkóhun tí ó wù wọ́n sí i, gẹ́gẹ́ bí a ti kọ̀wé nípa rẹ̀.”"'), 'Mark$9:31': (None, 'eniyan', 'Mark$9:31 " Nítorí o kọ̀ awọn Ọmọ-ẹ̀yìn rẹ̀, o si wí fun wọn pe, “A o fi Ọmọ ènìyàn lé àwọn ènìyàn lọ́wọ́, wọn ó sì pa á, lẹ́yìn ìgbà tí a bá sì pa á tan yóò jíǹde ní ijọ́ kẹta.”"'), 'Mark$9:36': ('ní', 'gba', 'Mark$9:36 " Ó sì mú ọmọ kékeré kan, ó fi sáàárín wọn, nígbà tí ó sì gbé e sí apá rẹ̀, ó wí fún wọn pé,"'), 'Mark$10:7': (None, None, 'Mark$10:7 " Nítorí ìdí èyí, ọkùnrin yóò fi baba àti ìyá rẹ̀ sílẹ̀ yóò sì faramọ́ aya rẹ̀."'), 'Mark$10:17': ('wá', 'ṣe', 'Mark$10:17 " Bí Jésù ti bẹ̀rẹ̀ ìrìnàjò kan, ọkùnrin kan sáré wá sọ́dọ̀ rẹ̀. Ó sì kúnlẹ̀, ó béèrè pé, “Olùkọ́ rere, kí ni èmi yóò ṣe láti jogún ìyè àìnipẹ̀kun?”"'), 'Mark$10:21': ('fun', 'ni', 'Mark$10:21 " Jésù wò ó tìfẹ́tìfẹ́. Ó wí fún un pé, “Bí ó bá rí bẹ́ẹ̀, ohun kan ló kù fún ọ láti ṣe, lọ nísinsìn yìí, ta gbogbo nǹkan tí o ní, kí o sì pín owó náà fún àwọn aláìníìwọ yóò sì ni ìsura ní ọ̀run, sì wá, gbe àgbélébú, kí ó sì máa tọ̀ mí lẹ́yìn.”"'), 'Mark$10:29': (None, None, 'Mark$10:29 "Jésù dáhùn pé, “Lóòótọ́ ni mo wí fún un yín pé, kò sí ẹnikẹ́ni tí ó fi ohúnkóhun sílẹ̀ bí: ilé, tàbí àwọn arákùnrin, tàbí àwọn arábìnrin, tàbí ìyá, tàbí baba, tàbí àwọn ọmọ tàbí ohun ìní,"'), 'Mark$10:35': ('wá', 'ṣe', 'Mark$10:35 " Lẹ́yìn èyí, Jákọ́bù àti Jòhánù, àwọn ọmọ Sébédè wá sọ́dọ̀ Jésù. Wọ́n sì bá a sọ̀rọ̀ kẹ́lẹ́kẹ́lẹ́, wọ́n wí pé, “Olùkọ́, inú wa yóò dùn bí ìwọ bá lè ṣe ojú rere ohunkóhun tí a bá béèrè fún wa.”"'), 'Mark$10:46': (None, None, 'Mark$10:46 " Wọ́n dé Jẹ́ríkò, bí Jésù àti àwọn ọmọ-ẹ̀yìn rẹ̀ àti ọ̀pọ̀ ènìyàn ti fẹ́ kúrò ní ìlú Jẹ́ríkò, ọkùnrin afọ́jú kan, Bátíméù, ọmọ Tíméù jókòó lẹ́gbẹ̀ẹ́ ọ̀nà ó ń sagbe."'), 'Mark$10:48': (None, 'ni', 'Mark$10:48 "Àwọn tó wà níbẹ̀ kígbe mọ́ ọn pé, “Pa ẹnu rẹ mọ́.” Ṣùgbọ́n dípò kí ó pa ẹnu mọ́, ṣe ló ń kígbe lóhùn rara pé, “Jésù ọmọ Dáfídì ṣàánú fún mi.”"'), 'Mark$10:51': ('dahùn', 'ṣe', 'Mark$10:51 "Jésù béèrè lọ́wọ́ rẹ̀ pé, “Kí ni ìwọ fẹ́ kí èmi kí ó ṣe fún ọ?” Afọ́jú náà dáhùn pé, “Rábì, jẹ́ kí èmi ki o ríran.”"'), 'Mark$11:6': ('ní', None, 'Mark$11:6 Àwọn ọmọ-ẹ̀yìn méjì wọ̀nyí sọ ohun tí Jésù ní kí wọ́n sọ. Nítorí náà àwọn ènìyàn náà yọ̀ǹda fún wọn láti mú ọmọ kẹ́tẹ́kẹ́tẹ́ náà lọ.'), 'Mark$11:9': (None, None, 'Mark$11:9 " Jésù wà láàrin ọ̀pọ̀lọpọ̀ ènìyàn níwájú, lẹ́yìn, gbogbo wọn sì ń kígbe wí pé, “Hòsánà!” “Olùbùkún ni ẹni náà tí ó ń bọ̀ wà ní orúkọ Olúwa!” "'), 'Mark$11:12': (None, None, 'Mark$11:12 " Ní òwúrọ̀ ọjọ́ kejì, bí wọ́n ti kúrò ní Bẹ́tanì, ebi ń pa Jésù."'), 'Mark$11:24': ('ni', 'gba', 'Mark$11:24 " Torí náà, mo wí fún yín ohunkóhun tí ẹ bá béèrè fún nínú àdúrà, ẹ ní ìgbàgbọ́ pé, ó tí tẹ̀ yín lọ́wọ́, yóò sì jẹ́ ti yín."'), 'Mark$11:29': (None, None, 'Mark$11:29 "Jésù dá wọn lóhùn pé, “Èmi yóò sọ fún un yín bí ẹ bá lè dáhùn ìbéèrè mi yìí.”"'), 'Mark$12:14': (None, None, 'Mark$12:14 "Bí wọn ti dé ọ̀dọ̀ rẹ̀, wọ́n wí pé: “Olùkọ́, àwa mọ̀ pé ìwọ máa ń sọ òtítọ́ tìrẹ láìsí ìbẹ̀rù ẹnikẹ́ni. Ṣùgbọ́n òtítọ́ ọ̀rọ̀ Ọlọ́run ni ìwọ máa ń kọ́ni. Nísinsin yìí, sọ fún wa, ó tọ́ tàbí kò tọ́ láti máa san owó orí fún Késárì?"'), 'Mark$12:18': ('beere', 'gba', 'Mark$12:18 " Àwọn Ṣadusí tún wá sọ́dọ̀ rẹ̀, àwọn wọ̀nyí kò gbàgbọ́ pé àjíǹde ń bẹ. Ìbéèrè wọn ni pé,"'), 'Mark$12:26': (None, 'ka', 'Mark$12:26 " Ṣùgbọ́n nísinsin yìí, nípa bóyá àjíǹde yóò wà. Àbí ẹ̀yin kò ì tí ka ìwé Ẹ́kísódù, nípa Mósè àti pápá tí ń jó? Ọlọ́run sọ fún Mósè pé, ‘Èmi ni Ọlọ́run Ábúráhámù, Ọlọ́run Ísáákì àti Ọlọ́run Jákọ́bù.’"'), 'Mark$12:42': ('da', 'ṣe', 'Mark$12:42 "Ṣùgbọn obìnrin opó kan wà, ó sì fi ààbọ̀ kọ́bọ̀ méjì síbẹ̀, tí ì ṣe ìdáméjì owó-bàbà kan sínú rẹ̀."'), 'Mark$13:11': ('gba', None, 'Mark$13:11 " Ṣùgbọ́n nígbà tí a bá mú yín, tí ẹ sì dúró níwájú adájọ́, ẹ má ṣe dààmú nípa ohun tí ẹ ó wí fún ààbò. Ẹ ṣáà sọ ohun tí Ọlọ́run bá fi sí yín lọ́kàn. Ẹ̀yin kọ́ ní yóò sọ̀rọ̀, bí kò ṣe Ẹ̀mí Mímọ́."'), 'Mark$13:14': ('wo', None, 'Mark$13:14 " “Ṣùgbọ́n nígba tí ẹ̀yin bá rí ìríra ìsọdahoro, tí ó dúró ní bí tí kò tọ́, tí a tí ẹnu wòlíì Dáníẹ́lì sọ, (ẹnikẹ́ni tí ó bá kà á ki í ó yé e) nígbà náà ni kí àwọn tí ń bẹ ni Jùdíà sá lọ sí orí òkè."'), 'Mark$13:20': ('yàn', 'yan', 'Mark$13:20 "À fi tí Olúwa bá gé àkókò ìjìyà náà kúrú, ẹyọ ẹ̀mí kan ní ayé kì yóò là. Ṣùgbọ́n nítorí àwọn tí Ọlọ́run yàn, ni yóò ṣe dín àwọn ọjọ́ náà kù."'), 'Mark$13:23': (None, None, 'Mark$13:23 "Nítorí náà, ẹ sọ́ra. Mo ti kìlọ̀ fún un yín ṣáájú tó!"'), 'Mark$13:24': (None, 'imole', 'Mark$13:24 "“Ṣùgbọ́n ni ọjọ́ wọ̀nyí lẹ́yìn ìpọ́njú tí mo sọ yìí, “ ‘òòrùn yóò sókùnkùn, òṣùpá kò ní tan ìmọ́lẹ̀ rẹ̀."'), 'Mark$13:28': (None, None, 'Mark$13:28 "“Nísinsin yìí, ẹ kọ́ ẹ̀kọ́ kan lára igi ọ̀pọ̀tọ́, nígbà tí ẹ̀ka rẹ bá ń yọ titun, tí ó bá sì ń rú ewé, èyí fi hàn pé àkókò ẹ̀rùn ti dé."'), 'Mark$14:5': (None, 'ni', 'Mark$14:5 "Òun ìbá tà á ju owó iṣẹ́ ọdún kan lọ, kí ó sì fi owó rẹ̀ ta àwọn talákà lọ́rẹ.” Báyìí ni wọ́n ń fi ọ̀rọ̀ gún obìnrin náà lára."'), 'Mark$14:8': ('ṣe', None, 'Mark$14:8 " Ó ti ṣe èyí tí ó lè ṣe, Ó ti fi òróró kùn mí ni ara ní ìmúra sílẹ̀ de ìgbà tí wọn yóò sin òkú mi."'), 'Mark$14:15': ('ṣe', None, 'Mark$14:15 "Òun yóò sì mú un yín lọ sí gbàngàn ńlá kan lókè ilé náà, tí a ti ṣe lọ́sọ́. Níbẹ̀ ni kí ẹ pèṣè sílẹ̀ dè wá.”"'), 'Mark$14:16': ('ṣe', None, 'Mark$14:16 Àwọn ọmọ-ẹ̀yìn méjì náà sì jáde lọ àárin ìlú. Wọ́n bá gbogbo nǹkan gẹ́gẹ́ bí Jésù tí sọ fún wọn. Wọ́n sì pèsè àṣè ìrékọjá.'), 'Mark$14:35': (None, 'kọjá', 'Mark$14:35 "Ó sì lọ ṣíwájú díẹ̀, ó dojú bolẹ̀, ó sì bẹ̀rẹ̀ sí i gbàdúrà pé, bí ó bá ṣe e ṣe kí wákàtí tí ó bọ̀ náà ré òun kọjá."'), 'Mark$14:44': (None, None, 'Mark$14:44 "Júdásì tí fí àmì fún wọn wí pe, “Ẹni tí mo bá fi ẹnu kò lẹ́nu nínú wọn, òun ní Jésù, Ẹ mú un.”"'), 'Mark$14:51': (None, None, 'Mark$14:51 Ọ̀dọ́mọkùnrin kan sì ń tọ̀ ọ́ lẹ́yìn tí ó fi aṣọ ọ̀gbọ̀ bo ìhòòhò rẹ̀ àwọn ọmọ-ogun gbìyànjú láti mú òun náà.'), 'Mark$14:55': ('ri', None, 'Mark$14:55 "Bí ó ti ń ṣe èyí lọ́wọ́, àwọn olórí àlùfáà àti gbogbo ìgbìmọ̀ ilé ẹjọ́ tí ó ga jùlọ ń wá ẹni tí yóò jẹ́rí èkè sí Jésù, èyí tí ó jọjú dáadáa láti lè dájọ́ ikú fún un. Ṣùgbọ́n wọn kò rí."'), 'Mark$14:57': (None, None, 'Mark$14:57 "Níkẹyìn àwọn kan dìde, wọ́n bẹ̀rẹ̀ sí í jẹ́rìí èké sí i, wọ́n ní"'), 'Mark$15:12': (None, 'ṣe', 'Mark$15:12 "Pílátù sì tún béèrè lọ́wọ́ wọn pé, “Kí ni ẹ̀yin ń fẹ́ kí èmi kí ó ṣe pẹ̀lú ẹni ti ẹ̀yin n pè ní ọba àwọn Júù?”"'), 'Mark$15:20': (None, None, 'Mark$15:20 "Nígbà tí wọ́n sì fi ṣẹ̀sín tan, wọ́n sì bọ aṣọ elésè àlùkò náà kúrò lára rẹ̀, wọ́n sì fi aṣọ rẹ̀ wọ̀ ọ́, wọ́n sì mú un jáde lọ láti kàn án mọ́ àgbélébùú."'), 'Mark$15:24': ('ní', None, 'Mark$15:24 " Wọ́n sì kàn án mọ́ àgbélébùú. Wọ́n sì pín aṣọ rẹ̀ láàrin ara wọn, wọ́n dìbò lórí àwọn aṣọ náà ni kí wọn báà lè mọ èyí tí yóò jẹ́ ti olúkúlùkù."'), 'Mark$15:36': (None, 'gba', 'Mark$15:36 " Nígbà náà ni ẹnìkan wá sáré lọ ki kàn-ìn-kàn-ìn bọ inú ọtí kíkan, ó fi lé orí ọ̀pá, ó sì nà án sí Jésù kí ó lè mu ún. Ó wí pé, “Ẹ jẹ́ kí a máa wò ó, bóyá Èlíjà yóò wá sọ̀ ọ́ kalẹ̀.”"'), 'Mark$16:14': (None, 'okan', 'Mark$16:14 "Lẹ́yìn náà, Jésù fi ara rẹ̀ hàn fún àwọn mọ́kànlá níbi tí wọ́n ti jìjọ ń jẹun; Ó sì bá wọn wí fún àìgbàgbọ́ àti ọkàn líle wọn, nítorí wọn kò gba ẹ̀rí àwọn tí ó ti rí i lẹ́yìn àjíǹde rẹ̀ gbọ́."'), 'Mark$16:17': (None, None, 'Mark$16:17 Àmì wọ̀nyí yóò sì máa bá àwọn tí ó gbàgbọ́ lọ. Ní orúkọ mi ni wọ́n yóò máa lé ẹ̀mí èsù jáde. Wọn yóò máa fi èdè titun sọ̀rọ̀.'), 'Luke$1:8': ('lọ', None, 'Luke$1:8 "Ó sì ṣe, nígbà tí ó ń ṣe iṣẹ́ àlùfáà níwájú Ọlọ́run níipaṣẹ̀ tirẹ̀"'), 'Luke$1:10': ('eniyan', None, 'Luke$1:10 Gbogbo Ìjọ àwọn ènìyàn sì ń gbàdúrà lóde ní àkókò sísun tùràrí.'), 'Luke$1:14': ('ni', None, 'Luke$1:14 Òun yóò sì jẹ́ ayọ̀ àti ìdùnnú fún ọ: ènìyàn púpọ̀ yóò sì yọ̀ sí ìbí rẹ.'), 'Luke$1:17': ('ṣe', None, 'Luke$1:17 " Ẹ̀mí àti agbára Èlíjàh ni Olúwa yóò sì fi ṣáájú rẹ̀ lọ, láti pa ọkàn àwọn baba dà sí ti àwọn ọmọ, àti ti àwọn aláìgbọ́ràn sí ọgbọ́n àwọn olóòótọ́; kí ó le pèṣè àwọn ènìyàn tí a múra sílẹ̀ de Olúwa.”"'), 'Luke$1:44': (None, 'ohun', 'Luke$1:44 "Sá wò ó, bí ohùn kíkí rẹ ti bọ́ sí mi ní etí, ọlẹ̀ sọ nínú mi fún ayọ̀."'), 'Luke$1:62': ('ṣe', None, 'Luke$1:62 "Wọ́n sì ṣe àpẹẹrẹ sí bàbá rẹ̀, bí ó ti ń fẹ́ kí a pè é."'), 'Luke$1:67': (None, 'ni', 'Luke$1:67 "Sakaráyà baba rẹ̀ sì kún fún Ẹ̀mí Mímọ́, ó sì ṣọtẹ́lẹ̀, ó ní:"'), 'Luke$1:76': (None, None, 'Luke$1:76 " “Àti ìwọ, ọmọ mi, wòlíì Ọ̀gá-ògo jùlọ ni a ó máa pè ọ́: Nítorí ìwọ ni yóò ṣáájú Olúwa láti tún ọ̀nà rẹ̀ ṣe;"'), 'Luke$2:1': (None, None, 'Luke$2:1 " Ó sì ṣe ní ọjọ́ wọ̀n-ọn-nì, àṣẹ ti ọ̀dọ̀ Késárì Ògọ́sítù jáde wá pé, kí a kọ orúkọ gbogbo ìjọba Rosínú ìwé."'), 'Luke$2:15': ('lọ', None, 'Luke$2:15 "Ó sì ṣe, nígbà tí àwọn ańgẹ́lì náà padà kúrò lọ́dọ̀ wọn lọ sí ọ̀run, àwọn Olùṣọ́-àgùntàn náà bá ara wọn sọ pé, “Ẹ jẹ́ kí á lọ tàrà sí Bẹ́tílẹ́hẹ́mù, kí á lè rí ohun tí ó ṣẹ̀ jẹ, tí Olúwa ti sọ nípa rẹ̀ fún wa.”"'), 'Luke$2:27': ('wá', 'ṣe', 'Luke$2:27 "Ó sì ti ipa Ẹ̀mí wá sínú tẹ́ḿpìlì: nígbà tí àwọn òbí rẹ̀ sì gbé ọmọ náà Jésù wá, láti ṣe fún un bí ìṣe òfin,"'), 'Luke$2:34': (None, None, 'Luke$2:34 "Síméónì sì súre fún wọn, ó sì wí fún Màríà ìyá rẹ̀ pé: “Kíyèsí i, a gbé ọmọ yìí kalẹ̀ fún ìṣubú àti ìdìde ọ̀pọ̀ ènìyàn ní Ísírẹ́lì; àti fún àmì tí a ń sọ̀rọ̀ òdì sí;"'), 'Luke$2:36': (None, 'ọdun', 'Luke$2:36 " Ẹnìkan sì ń bẹ, Ánà wòlíì, ọmọbìnrin Fánúénì, ní ẹ̀yà Ásérì: ọjọ́ ogbó rẹ̀ pọ̀, ó ti bá ọkọ gbé ní ọdún méje láti ìgbà wúndíá rẹ̀ wá;"'), 'Luke$2:39': (None, None, 'Luke$2:39 "Nígbà tí wọ́n sì ti ṣe nǹkan gbogbo tán gẹ́gẹ́ bí òfin Olúwa, wọ́n padà lọ sí Gálílì, sí Násárẹ́tì ìlú wọn."'), 'Luke$3:12': ('wá', 'ṣe', 'Luke$3:12 "Àwọn agbowó òde sì tọ̀ ọ́ wá láti ìtẹ̀bọmi lọ́dọ̀ rẹ̀, wọ́n sì bi í pé, “Olùkọ́, kí ni àwa ó ha ṣe?”"'), 'Luke$3:23': (None, 'ọdun', 'Luke$3:23 " Jésù tìkara rẹ̀ ń tó bí ẹni ọgbọ̀n ọdún, nígbà tí ó bẹ̀rẹ̀ iṣẹ́ ìránṣẹ́ rẹ̀ ó jẹ́ (bí a ti fi pè) ọmọ Jósẹ́fù, tí í ṣe ọmọ Élì,"'), 'Luke$4:4': ('ni', None, 'Luke$4:4 " Jésù sì dáhùn, ó wí fún un pé, “A ti kọ̀wé rẹ̀ pé: ‘Ènìyàn kì yóò wà láàyè nípa àkàrà nìkan.’ ”"'), 'Luke$4:8': ('dahùn', 'sin', 'Luke$4:8 " Jésù sì dáhùn ó sì wí fún un pé, “Kúrò lẹ́yìn mi, Sátánì, nítorí tí a kọ̀wé rẹ̀ pé, ‘Ìwọ foríbalẹ̀ fún Olúwa Ọlọ́run rẹ, òun nìkan ṣoṣo ni kí ìwọ kí ó sì máa sìn.’ ”"'), 'Luke$4:33': (None, 'ni', 'Luke$4:33 "Ọkùnrin kan sì wà nínú Sínágọ́gù, ẹni tí ó ní ẹ̀mí àìmọ́, ó kígbe ní ohùn rara,"'), 'Luke$4:42': ('lọ', 'lọ', 'Luke$4:42 " Nígbà tí ilẹ̀ sì mọ́, Jésù sì jáde lọ ó sì ya ara rẹ̀ sọ́tọ̀. Ìjọ ènìyàn sì ń wá a kiri, wọ́n sì tọ̀ ọ́ wá, wọ́n sì dá a dúró, nítorí kí ó má baà lọ kúrò lọ́dọ̀ wọn."'), 'Luke$5:1': (None, None, 'Luke$5:1 " Ó sì ṣe, nígbà tí ìjọ ènìyàn ṣún mọ́ ọn láti gbọ́ ọ̀rọ̀ Ọlọ́run, tí ó sì dúró létí adágún Gẹ́nẹ́sárétì,"'), 'Luke$5:4': ('ní', None, 'Luke$5:4 "Bí ó sì ti dákẹ́ ọ̀rọ̀ í sọ, ó wí fún Símónì pé, “Tì í sí ibú, kí o sì ju àwọ̀n yín sí ìsàlẹ̀ fún àkópọ̀.”"'), 'Luke$5:5': (None, 'ni', 'Luke$5:5 " Símónì sì dáhùn ó sì wí fún un pé, “Olùkọ́ni, gbogbo òru ni àwa fi ṣiṣẹ́, àwa kò sì mú nǹkan: ṣùgbọ́n nítorí ọ̀rọ̀ rẹ èmi ó ju àwọ̀n náà sí ìsàlẹ̀.”"'), 'Luke$5:11': ('mu', 'ilẹ', 'Luke$5:11 "Nígbà tí wọ́n sì ti mú ọkọ̀ wọn dé ilẹ̀, wọ́n fi gbogbo rẹ̀ sílẹ̀, wọ́n sì tọ̀ ọ́ lẹ́yìn lọ."'), 'Luke$5:12': (None, None, 'Luke$5:12 " Ó sì ṣe, nígbà tí Jésù wọ ọ̀kan nínú àwọn ìlú náà, kíyèsí i, ọkùnrin kan tí ẹ̀tẹ̀ bò: nígbà tí ó rí Jésù, ó wólẹ̀, ó sì ń bẹ̀ ẹ́, wí pé, “Olúwa, bí ìwọ bá fẹ́, ìwọ lè sọ mí di mímọ́.”"'), 'Luke$5:14': (None, 'lọ', 'Luke$5:14 " Ó sì kìlọ̀ fún u pé, “Kí ó má ṣe sọ fún ẹnìkan: ṣùgbọ́n kí ó lọ, kí ó sì fi ara rẹ̀ hàn fún àlùfáà, kí ó sì ta ọrẹ fún ìwẹ̀nùmọ́ rẹ̀, gẹ́gẹ́ bí Mósè ti pàṣẹ fún ẹ̀rí sí wọn.”"'), 'Luke$5:18': (None, None, 'Luke$5:18 "Sáà sì kíyèsí i, àwọn Ọkùnrin kan gbé ẹnikantí ó ní àrùn egba wà lórí àketè: wọ́n ń wá ọ̀nà àti gbé e wọlé, àti láti tẹ́ ẹ ṣíwájú Jésù."'), 'Luke$5:36': (None, None, 'Luke$5:36 "Ó sì pa òwe yìí fún wọn wí pé: “Kò sí ẹni tó lè ya asọ tuntun kí ó sì rán mọ́, èyí tó ti gbó. Tí ó ba ṣe èyí, yóò ba asọ tuntun jẹ́, èyí tí ó tuntun náà kì yóò ṣe dógba pẹ̀lú èyí tí ó ti gbó."'), 'Luke$6:1': (None, 'kọjá', 'Luke$6:1 " Ní ọjọ́ ìsinmi kan, Jésù ń kọjá láàrin oko ọkà; àwọn ọmọ-ẹ̀yìn rẹ̀ sì ń ya ìpẹ́ ọkà, wọ́n sì ń jẹ ẹ́."'), 'Luke$6:3': ('gba', 'ka', 'Luke$6:3 " Jésù sì dá wọn lóhùn pé, “Ẹ̀yin kò kà nípa ohun tí Dáfídì ṣe, nígbà tí ebi ń pa òun tìkárarẹ̀ àti àwọn tí ó wà lọ́dọ̀ rẹ̀;"'), 'Luke$6:12': ('lọ', None, 'Luke$6:12 " Ọ̀kan nínú àwọn ọjọ́ wọnnì, Jésù lọ sí orí òkè lọ gbàdúrà, ó sì fi gbogbo òru náà gbàdúrà sí Ọlọ́run."'), 'Luke$6:17': ('eniyan', None, 'Luke$6:17 " Ó sì bá wọn sọ̀kalẹ̀, ó sì dúró ní pẹ̀tẹ́lẹ̀, pẹ̀lú ọpọ àwọn ọmọ-ẹ̀yìn rẹ̀, àti ọ̀pọ̀ ìjọ ènìyàn, láti gbogbo Jùdéà, àti Jerúsálémù, àti agbègbè Tírè àti Ṣídónì, tí wọ́n wá láti gbọ́ ọ̀rọ̀ rẹ̀, àti láti gba ìmúláradá kúrò nínú àrùn wọn;"'), 'Luke$6:22': (None, 'eniyan', 'Luke$6:22 " Alábùkún fún ni ẹ̀yin, nígbà tí àwọn ènìyàn bá kóríra yín, tí wọ́n bá yà yín kúrò nínú ẹgbẹ́ wọn, tí wọ́n bá gàn yín, tí wọ́n bá ta orúkọ yín nù bí ohun búburú, nítorí ọmọ ènìyàn."'), 'Luke$6:31': (None, None, 'Luke$6:31 " Gẹ́gẹ́ bí ẹ̀yin sì ti fẹ́ kí ènìyàn ṣe sí yín, kí ẹ̀yin sì ṣe bẹ́ẹ̀ sí wọn pẹ̀lú."'), 'Luke$6:34': ('ni', 'gba', 'Luke$6:34 "Bí ẹ̀yin bá wín fún ẹni tí ẹ̀yin ń reti láti rí gbà padà, ọpẹ́ kínni ẹ̀yin ní? Àwọn ‘ẹlẹ́sẹ̀’ pẹ̀lú ń yá ‘ẹlẹ́sẹ̀,’ kí wọn lè gba ìwọ̀n bẹ́ẹ̀ padà."'), 'Luke$6:47': (None, None, 'Luke$6:47 " Ẹnikẹ́ni tí ó tọ̀ mí wá, tí ó sì ń gbọ́ mi, tí ó sì ń ṣe é, èmi yóò fi ẹni tí ó jọ hàn yín;"'), 'Luke$7:4': (None, 'ṣe', 'Luke$7:4 "Nígbà tí wọ́n sì dé ọ̀dọ̀ Jésù, wọ́n fi ìtara bẹ̀ ẹ́, pé, “Ó yẹ ní ẹni tí òun ìbá ṣe èyí fún:"'), 'Luke$7:8': ('Ṣe', None, 'Luke$7:8 "Nítorí èmi náà pẹ̀lú jẹ́ ẹni tí a fi sí abẹ́ àṣẹ, tí ó ní ọmọ-ogun lẹ́yìn mi, mo sì wí fún ọ̀kan pé, ‘Lọ,’ a sì lọ; àti fún òmíràn pé, ‘Wá,’ a sì wá; àti fún ọmọ ọ̀dọ̀ mi pé, ‘Ṣe èyí,’ a sì ṣe é.”"'), 'Luke$7:11': ('lọ', None, 'Luke$7:11 " Ní ijọ́ kejì, ó lọ sí ìlú kan tí a ń pè ní Náínì: àwọn púpọ̀ nínú àwọn ọmọ-ẹ̀yìn rẹ̀ sì ń bá a lọ àti ọ̀pọ̀ ìjọ ènìyàn."'), 'Luke$7:22': ('gbo', 'wo', 'Luke$7:22 " Jésù sì dáhùn ó wí fún wọn pé, “Ẹ padà lọ, ẹ ròyìn nǹkan tí ẹ̀yín rí, tí ẹ̀yín sì gbọ́ fún Jòhánù: àwọn afọ́jú ń ríran, àwọn amúkún-ún rìn, a sọ àwọn adẹ́tẹ̀ di mímọ́, àwọn adití ń gbọ́ràn, a ń jí àwọn òkú dìde, àti fún àwọn òtòsì ni à ń wàásù ìyìn rere."'), 'Luke$7:43': (None, None, 'Luke$7:43 " Símónì dáhùn wí pé, “Mo ṣebí ẹni tí ó dáríjìn jù ni.” Ó wí fún un pé, “O wí i re.”"'), 'Luke$7:46': ('ororo', None, 'Luke$7:46 "Ìwọ kò fi òróró pa mí lórí, ṣùgbọ́n òun ti fi òróró pa mí lẹ́ṣẹ̀."'), 'Luke$8:1': ('lọ', 'kọjá', 'Luke$8:1 " Ó sì ṣe lẹ́yìn náà, tí ó ń lá gbogbo ìlú àti ìletò kọjá lọ, ó ń wàásù, ó ń ròyìn ayọ̀ ìjọba Ọlọ́run: àwọn méjìlá sì ń bẹ lọ́dọ̀ rẹ̀,"'), 'Luke$8:4': (None, 'wá', 'Luke$8:4 " Nígbà tí ọ̀pọ̀ ìjọ ènìyàn pé jọ pọ̀, àwọn ènìyàn láti ìlú gbogbo sì tọ̀ ọ́ wá, ó fi òwe bá wọn sọ̀rọ̀ pé:"'), 'Luke$8:10': (None, None, 'Luke$8:10 "Ó sì wí fún wọn pé, “Ẹ̀yin ni a fi fún láti mọ ohun ìjìnlẹ̀ ìjọba Ọlọ́run; ṣùgbọ́n fún àwọn ẹlòmíràn wọn yóò jẹ́ òwe, pé ní rírí, “ ‘kí wọn má baà rí, àti ní gbígbọ́ kí ó má lè yé wọn.’"'), 'Luke$8:13': ('ni', 'gba', 'Luke$8:13 "Àwọn ti orí àpáta ni àwọn, tí wọ́n gbọ́, wọ́n fi ayọ̀ gba ọ̀rọ̀ náà, àwọn wọ̀nyí kò sì ní gbòǹgbò, wọ́n á gbàgbọ́ fún sáà díẹ̀; nígbà ìdánwò, wọn á padà sẹ́yìn."'), 'Luke$8:14': (None, 'lọ', 'Luke$8:14 "Àwọn tí ó bọ́ sínú ẹ̀gún ni àwọn, tí wọ́n gbọ́ tán, wọ́n lọ, wọn a sì fi ìtọ́jú àti ọrọ̀ àti ìrora ayé fún un pa, wọn kò sì lè so èso àsogbó."'), 'Luke$8:15': (None, None, 'Luke$8:15 "Ṣùgbọ́n ti ilẹ̀ rere ni àwọn tí wọ́n fi ọkàn òtítọ́ àti rere gbọ́ ọ̀rọ̀ náà, wọ́n dì í mú ṣinṣin, wọ́n sì fi sùúrù so èso."'), 'Luke$8:18': (None, 'ni', 'Luke$8:18 " Ǹjẹ́ kí ẹ̀yin má a kíyèsára bí ẹ̀yin ti ń gbọ́: nítorí ẹnikẹ́ni tí ó bá ní, lọ́wọ́ rẹ̀ ni a ó sì gba èyí tí ó ṣebí òun ní.”"'), 'Luke$8:21': ('dahùn', 'ṣe', 'Luke$8:21 " Ó sì dáhùn wí fún wọn pé, “Ìyá mi àti àwọn arákùnrin mi ni àwọn wọ̀nyí tí wọ́n ń gbọ́ ọ̀rọ̀ Ọlọ́run, tí wọ́n sì ń ṣe é.”"'), 'Luke$8:22': ('lọ', 'kọjá', 'Luke$8:22 " Ní ijọ́ kan, ó sì wọ ọkọ̀ ojú-omi kan lọ òun pẹ̀lú àwọn ọmọ-ẹ̀yìn rẹ̀: ó sì wí fún wọn pé, “Ẹ jẹ́ kí a rékọjá lọ sí ìhà kejì adágún.” Wọ́n sì ṣíkọ̀ lọ."'), 'Luke$8:35': ('ri', 'wo', 'Luke$8:35 "Nígbà náà ni wọ́n jáde lọ wo ohun náà tí ó ṣẹlẹ̀, wọ́n sì tọ Jésù wá, wọ́n sì rí ọkùnrin náà, lára ẹni tí àwọn ẹ̀mí èṣù ti jáde lọ, ó jókòó lẹ́bá ẹsẹ̀ Jésù, ó wọ aṣọ, iyè rẹ̀ sì bọ́ sí ipò: ẹ̀rù sì bà wọ́n."'), 'Luke$8:37': ('lọ', 'lọ', 'Luke$8:37 "Nígbà náà ni gbogbo ènìyàn láti ilẹ̀ Gádárà yíká bẹ̀ ẹ́ pé, kí ó lọ kúrò lọ́dọ̀ wọn; nítorí ẹ̀rù bà wọ́n gidigidi. Ó sì bọ́ sínú ọkọ̀ ojúomi, ó padà sẹ́yìn."'), 'Luke$8:40': ('pada', None, 'Luke$8:40 " Ó sì ṣe, nígbà tí Jésù padà lọ, àwọn ènìyàn tẹ́wọ́gbà á; nítorí tí gbogbo wọn ti ń retí rẹ̀."'), 'Luke$8:41': (None, 'wá', 'Luke$8:41 "Sì kíyèsí i, ọkùnrin kan tí a ń pè ní Jáírù, ọ̀kan nínú àwọn olórí sínágọ́gù wá; ó sì wólẹ̀ lẹ́bá ẹṣẹ̀ Jésù, ó bẹ̀ ẹ́ pé, kí ó wá sí ilé òun:"'), 'Luke$8:47': ('ní', 'fa', 'Luke$8:47 "Nígbà tí Obìnrin náà sì mọ̀ pé òun kò farasin, ó wárìrì, ó wá, ó sì wólẹ̀ níwájú rẹ̀, ó sì sọ fún un lójú àwọn ènìyàn gbogbo nítorí ohun tí ó ṣe, tí òun fi fi ọwọ́ kàn án, àti bí a ti mú òun láradá lójúkan náà."'), 'Luke$9:3': (None, 'ni', 'Luke$9:3 "Ó sì wí fún wọn pé, “Ẹ má ṣe mú nǹkan lọ fún àjò yín, ọ̀pá, tàbí àpò tàbí àkàrà, tàbí owó: bẹ́ẹ̀ ni kí ẹ̀yin má sì ṣe ní ẹ̀wù méjì."'), 'Luke$9:12': ('wá', 'gba', 'Luke$9:12 "Nígbà tí ọjọ́ bẹ̀rẹ̀ sí rẹlẹ̀, àwọn méjìlá wá, wọ́n sì wí fún un pé, “Tú ìjọ ènìyàn ká, kí wọn lè lọ sí ìletò àti sí ìlú yíká, kí wọn lè wò, àti kí wọn lè wá oúnjẹ: níbi ijù ni àwa sá wà níhín-ín.”"'), 'Luke$9:23': (None, None, 'Luke$9:23 " Ó sì wí fún gbogbo wọn pé, “Bí ẹnìkan bá ń fẹ́ láti má a tọ̀ mí lẹ́yìn, kí ó sẹ́ ara rẹ̀, kí ó sì gbé àgbélébùú rẹ̀ ní ọjọ́ gbogbo, kí ó sì máa tọ̀ mí lẹ́yìn."'), 'Luke$9:25': (None, 'eniyan', 'Luke$9:25 "Nítorí pé èrè kínni fún ènìyàn, bí ó jèrè gbogbo ayé, tí ó sì sọ ara rẹ̀ nù tàbí kí ó ṣòfò."'), 'Luke$9:28': ('lọ', None, 'Luke$9:28 " Ó sì ṣe bí ìwọ̀n ijọ́ mẹ́jọ lẹ́yìn ọ̀rọ̀ wọ̀nyí, ó mú Pétérù àti Jòhánù àti Jákọ́bù, ó gun orí òkè lọ láti lọ gbàdúrà."'), 'Luke$9:51': ('lọ', None, 'Luke$9:51 " Nígbà tí ọjọ́ àti gbà á sókè ń súnmọ́, ó fi ojú lé ọ̀nà láti lọ sí Jerúsálémù."'), 'Luke$9:54': ('ṣe', 'wá', 'Luke$9:54 " Nígbà tí àwọn ọmọ-ẹ̀yìn rẹ̀ Jákọ́bù àti Jòhánù sì rí i, wọ́n ní, “Olúwa wa, jẹ́ kí a pe iná láti ọ̀run wá, kí a sì pa wọ́n run, (bí Èlíjà ti ṣe?)”"'), 'Luke$9:56': (None, 'eniyan', 'Luke$9:56 "Nítorí ọmọ ènìyàn kò wá láti pa ẹ̀mí ènìyàn run, bí kò ṣe láti gbà á là. Wọ́n sì lọ sí ìletò mìíràn."'), 'Luke$9:57': ('wá', None, 'Luke$9:57 " Bí wọ́n ti ń lọ lọ́nà, ọkùnrin kan wí fún un pé, “Olúwa, Èmi ń fẹ́ láti máa tọ̀ ọ́ lẹ́yìn ní ibikíbi tí ìwọ bá ń lọ.”"'), 'Luke$9:61': ('ile', 'ile', 'Luke$9:61 " Ẹlòmíràn sì wí fún un pé, “Olúwa, èmi yóò tọ̀ ọ́ lẹ́yìn ṣùgbọ́n jẹ́ kí n kọ́kọ́ lọ dágbéré fún àwọn ará ilé mi.”"'), 'Luke$10:1': (None, 'ibi', 'Luke$10:1 " Lẹ́yìn nǹkan wọ̀nyí, Olúwa sì yan àádọ́rin ọmọ-ẹ̀yìn míràn pẹ̀lú, ó sì rán wọn ní méjìméjì lọ ṣáájú rẹ̀, sí gbogbo ìlú àti ibi tí òun tìkárarẹ̀ yóò sì dé."'), 'Luke$10:10': (None, None, 'Luke$10:10 "Ṣùgbọ́n ní ìlúkílùú tí ẹ̀yin bá sì wọ̀, tí wọn kò bá sì gbà yín, nígbà tí ẹ̀yin bá sì jáde sí ìgboro ìlú náà, kí ẹ̀yin sì wí pé,"'), 'Luke$10:23': (None, 'wo', 'Luke$10:23 " Ó sì yípadà sí àwọn ọmọ-ẹ̀yìn rẹ̀ ní apákan. Ó ní, “Ìbùkún ni fún ojú tí ń rí ohun tí ẹ̀yin ń rí."'), 'Luke$10:28': ('dahùn', 'ṣe', 'Luke$10:28 " Ó sì wí fún un pé, “Ìwọ́ dáhùn rere: ṣe èyí, ìwọ ó sì yè.”"'), 'Luke$10:33': ('ní', None, 'Luke$10:33 " Ṣùgbọ́n ará Samáríà kan, bí ó ti ń re àjò, ó dé ibi tí ó gbé wà; nígbà tí ó sì rí i, àánú ṣe é."'), 'Luke$10:38': ('lọ', None, 'Luke$10:38 " Ó sì se, bí wọ́n ti ń lọ, o wọ ìletò kan, obìnrin kan tí a ń pè ní Mátà sì gbà á sí ilé rẹ̀."'), 'Luke$11:36': (None, None, 'Luke$11:36 "Ǹjẹ́ bí gbogbo ara rẹ bá kún fún ìmọ́lẹ̀, tí kò ní apákan tí ó ṣókùnkùn, gbogbo rẹ̀ ni yóò ní ìmọ́lẹ̀, gẹ́gẹ́ bí ìgbà tí fìtílà bá fi ìtànsán rẹ̀ fún ọ ní ìmọ́lẹ̀.”"'), 'Luke$11:41': ('ni', 'fun', 'Luke$11:41 " Kí ẹ̀yin kúkú má a ṣe ìtọrẹ àánú nínú ohun tí ẹ̀yin ní, sì kíyèsii, ohun gbogbo ni ó di mímọ́ fún yín."'), 'Luke$12:1': ('eniyan', None, 'Luke$12:1 " Ó sì ṣe, nígbà tí àìníye ìjọ ènìyàn péjọ pọ̀, tó bẹ́ẹ̀ tí wọn ń tẹ ara wọn mọ́lẹ̀, ó bẹ̀rẹ̀ sí sọ fún àwọn ọmọ-ẹ̀yìn rẹ̀ pé, “Ẹ máa sọ́ra yín nítorí ìwúkàrà àwọn Farisí tí í ṣe àgàbàgebè"'), 'Luke$12:8': (None, 'eniyan', 'Luke$12:8 "“Mo sì wí fún yín pẹ̀lú, Ẹnikẹ́ni tí ó bá jẹ́wọ́ mi níwájú àwọn ènìyàn. Ọmọ ènìyàn yóò sì jẹ́wọ́ rẹ̀ níwájú àwọn áńgẹ́lì Ọlọ́run:"'), 'Luke$12:11': ('gba', 'mú', 'Luke$12:11 " “Nígbà tí wọ́n bá sì mú yín wá sí sínágọ́gù, àti síwájú àwọn olórí, àti àwọn alásẹ, ẹ má ṣe ṣàníyàn pé, báwo tàbí ohun kínni ẹ̀yin yóò fèsì rẹ̀ tàbí kínni ẹ̀yin ó wí:"'), 'Luke$12:16': (None, 'fun', 'Luke$12:16 "Ó sì pa òwe kan fún wọn, pé, ilẹ̀ ọkùnrin ọlọ́rọ̀ so ọ̀pọ̀lọpọ̀ èso:"'), 'Luke$12:33': ('fun', 'ni', 'Luke$12:33 " Ẹ ta ohun tí ẹ̀yin ní, kí ẹ sì tọrẹ àánú kí ẹ sì pèsè àpò fún ara yín, tí kì í gbó, ìṣúra ní ọ̀run tí kì í tán, ní ibi tí olè kò lè súnmọ́, àti ibi tí kòkòrò kì í bà á jẹ́."'), 'Luke$12:39': (None, None, 'Luke$12:39 " Ṣùgbọ́n ẹ mọ èyí pé, baálé ilé ìbá mọ wákàtí tí olè yóò wá, (òun ìbá máa ṣọ́nà) kì bá tí jẹ́ kí a já wọ inú ilé òun."'), 'Luke$12:42': ('fun', None, 'Luke$12:42 " Olúwa sì dáhùn wí pé, “Ta ni olóòótọ́ àti ọlọ́gbọ́n ìríjú náà, tí olúwa rẹ̀ fi jẹ olórí agbo ilé rẹ̀, láti máa fi ìwọ̀n oúnjẹ wọ̀n fún wọn ní àkókò?"'), 'Luke$12:49': (None, None, 'Luke$12:49 “Iná ni èmi wá láti sọ sí ayé; kínni èmi sì ń fẹ́ bí kò ṣe kí a ti dá a ná!'), 'Luke$13:6': ('gbin', 'ri', 'Luke$13:6 " Ó sì pa òwe yìí fún wọn pé: “Ọkùnrin kan ní igi ọ̀pọ̀tọ́ kan tí a gbìn sí ọgbà àjàrà rẹ̀; ó sì dé, ó ń wá èso lórí rẹ̀, kò sì rí nǹkan."'), 'Luke$13:33': (None, 'jẹ', 'Luke$13:33 "Ṣùgbọ́n èmi kò jẹ́ má rìn lóní, àti lọ́la, àti ní ọ̀túnla: kò lè jẹ́ bẹ́ẹ̀ pé wòlíì yóò ṣègbé lẹ́yìn odi Jerúsálémù."'), 'Luke$14:1': ('lọ', None, 'Luke$14:1 " Nígbà tí ó wọ ilé ọ̀kan nínú àwọn olórí Farisí lọ ní ọjọ́ ìsinmi láti jẹun, wọ́n sì ń ṣọ́ ọ."'), 'Luke$14:8': (None, None, 'Luke$14:8 " “Nígbà tí ẹnìkan bá pè ọ́ wá sí ibi ìyàwó, má ṣe jókòó ní ipò ọlá; kí ó má ba à jẹ́ pé, a ó pe ẹni tí ó lọ́lá jù ọ́ lọ."'), 'Luke$14:12': ('pe', None, 'Luke$14:12 " Nígbà náà ni ó sì wí fún alásè tí ó pè é pé, “Nígbà tí ìwọ bá se àṣè ọ̀sán má ṣe pe àwọn arákùnrin rẹ tàbí àwọn ìbátan rẹ, tàbí àwọn ọlọ́rọ̀ aládúgbò rẹ̀; nítorí kí wọn má ṣe pè ọ́ padà láti san ẹ̀san padà."'), 'Luke$14:16': ('ṣe', None, 'Luke$14:16 " Jésù dá a lóhùn pé, “Ọkùnrin kan se àsè alẹ́ ńlá, ó sì pe ènìyàn púpọ̀."'), 'Luke$15:11': ('ní', None, 'Luke$15:11 Ó sì wí pé ọkùnrin kan ní ọmọ méjì:'), 'Luke$15:16': ('fun', 'ni', 'Luke$15:16 Ayọ̀ ni ìbá fi jẹ oúnjẹ tí àwọn ẹlẹ́dẹ̀ ń jẹ ní àjẹyó: ẹnikẹ́ni kò sì fifún un.'), 'Luke$15:18': ('ni', 'ṣe', 'Luke$15:18 "Èmi ó dìde, èmi ó sì tọ bàbá mi lọ, èmi ó sì wí fún un pé: Bàbá, èmí ti dẹ́sẹ̀ sí ọ̀run, àti níwájú rẹ;"'), 'Luke$15:29': (None, 'ṣe', 'Luke$15:29 "Ó sì dáhùn ó wí fún bàbá rẹ̀ pé, ‘Wòó, láti ọdún mélòó wọ̀nyí ni èmi ti ń sìn ọ́, èmi kò sì rú òfin rẹ rí: ìwọ kò sì tí ì fi ọmọ ewúrẹ́ kan fún mi, láti fi bá àwọn ọ̀rẹ́ mi ṣe àríyá:"'), 'Luke$16:11': (None, None, 'Luke$16:11 "Ǹjẹ́ bí ẹ̀yin kò bá ti jẹ́ olóòótọ́ ní mámónì àìṣòótọ́, tani yóò fi ọ̀rọ̀ tòótọ́ ṣú yín?"'), 'Luke$16:12': ('ni', None, 'Luke$16:12 "Bí ẹ̀yin kò bá sì ti jẹ́ olóòtítọ́ ní ohun tí í se ti ẹlòmíràn, tani yóò fún yín ní ohun tí í se ti ẹ̀yin tìkara yín?"'), 'Luke$16:26': (None, None, 'Luke$16:26 "Àti pẹ̀lú gbogbo èyí, a gbé ọ̀gbun ńlá kan sí agbede-méjì àwa àti ẹ̀yin, kí àwọn tí ń fẹ́ má baa le rékọjá láti ìhín lọ sọ́dọ̀ yín, kí ẹnikẹ́ni má sì le ti ọ̀hún rékọjá tọ̀ wá wá.’"'), 'Luke$16:29': (None, 'ni', 'Luke$16:29 " “Ábúráhámù sì wí fún un pé, ‘Wọ́n ní Mósè àti àwọn wòlíì; kí wọn kí ó gbọ́ ti wọn.’"'), 'Luke$17:11': ('lọ', 'kọjá', 'Luke$17:11 " Ó sì ṣe, bí ó ti ń lọ sí Jerúsálémù, ó kọjá láàrin Samaríà òun Gálílì."'), 'Luke$17:18': ('pada', 'fun', 'Luke$17:18 "A kò rí ẹnìkan tí ó padà wá fi ògo fún Ọlọ́run, bí kò ṣe àlejò yìí?”"'), 'Luke$17:33': (None, None, 'Luke$17:33 Ẹnikẹ́ni tí ó bá ń wá àti gba ẹ̀mí rẹ̀ là yóò sọ ọ́ nù; ẹnikẹ́ni tí ó bá sì sọ ọ́ nù yóò gbà á là.'), 'Luke$18:1': (None, None, 'Luke$18:1 " Ó sì pa òwe kan fún wọn nítorí èyí yìí pé, ó yẹ kí a má a gbàdúrà nígbà gbogbo, kí a má sì ṣàárẹ̀;"'), 'Luke$18:22': ('wá', None, 'Luke$18:22 " Nígbà tí Jésù sì gbọ́ èyí, ó wí fún un pé, “Ohun kan ni ó kù fún ọ síbẹ̀: lọ ta ohun gbogbo tí o ní, kí o sì pín in fún àwọn talákà, ìwọ ó sì ní ìṣúra lọ́run: sì wá, kí o máa tọ̀ mí lẹ́yìn.”"'), 'Luke$18:29': (None, None, 'Luke$18:29 "Ó sì wí fún wọn pé, “Lóòótọ́ ni mo wí fún yín, kò sí ẹni tí ó fi ilé, tàbí aya, tàbí ará, tàbí òbí, tàbí ọmọ sílẹ̀, nítorí ìjọba Ọlọ́run,"'), 'Luke$18:39': (None, 'ni', 'Luke$18:39 "Àwọn tí ń lọ níwájú bá a wí, pé, kí ó pa ẹnu rẹ̀ mọ́: ṣùgbọ́n òun sì kígbe sókè sí í pé, “Ìwọ Ọmọ Dáfídì, ṣàánú fún mi!”"'), 'Luke$18:40': (None, 'wá', 'Luke$18:40 "Jésù sì dúró, ó ní, kí á mú un tọ òun wá nígbà tí ó sì súnmọ́ ọn, ó bi í,"'), 'Luke$19:4': (None, 'kọjá', 'Luke$19:4 "Ó sì súré ṣíwájú, ó gun orí igi síkámórè kan, kí ó bà lè rí i: nítorí tí yóò kọjá lọ níhà ibẹ̀."'), 'Luke$19:8': ('fun', 'ni', 'Luke$19:8 " Sákéù sì dìde, ó sì wí fún Olúwa pé, “Wòó, Olúwa, ààbọ̀ ohun ìní mi ni mo fifún talákà; bí mo bá sì fi èké gba ohun kan lọ́wọ́ ẹnikẹ́ni, èmi san án padà ní ìlọ́po mẹ́rin!”"'), 'Luke$19:15': (None, None, 'Luke$19:15 "“Nígbà tí ó gba ìjọba tan, tí ó padà dé, ó pàṣẹ pé, kí a pe àwọn ọmọ-ọ̀dọ̀ wọ̀nyí tí òun ti fi owó fún wá sọ́dọ̀ rẹ̀, nítorí kí ó le mọ iye èrè tí olúkúlùkù ti jẹ nínú iṣẹ́-òwò wọn."'), 'Luke$19:19': (None, None, 'Luke$19:19 "“Olúwa rẹ sì wí fún un pẹ̀lú pé, ‘Ìwọ joyè ìlú márùnún!’"'), 'Luke$19:45': (None, 'ni', 'Luke$19:45 " Ó sì wọ inú tẹ́ḿpìlì lọ, ó sì bẹ̀rẹ̀ sí lé àwọn tí ń tà nínú rẹ̀ sóde;"'), 'Luke$20:20': ('gba', None, 'Luke$20:20 " Wọ́n sì ń sọ́ ọ, wọ́n sì rán àwọn amí tí wọ́n ṣe ara wọn bí ẹni pé olóòtọ́ ènìyàn, kí wọn baà lè gbá ọ̀rọ̀ rẹ̀ mú, kí wọn baà lè fi í lé agbára àti àṣẹ Baálẹ̀ lọ́wọ́."'), 'Luke$20:26': ('gba', None, 'Luke$20:26 "Wọn kò sì lè gbá ọ̀rọ̀ rẹ̀ mú níwájú àwọn ènìyàn: ẹnu sì yà wọ́n sí ìdáhùn rẹ̀, wọ́n sì pa ẹnu wọn mọ́."'), 'Luke$20:27': (None, None, 'Luke$20:27 " Àwọn Ṣadusí kan sì tọ̀ ọ́ wá, àwọn tí wọ́n ń wí pé àjíǹde òkú kò sí: wọ́n sì bi í,"'), 'Luke$20:34': (None, None, 'Luke$20:34 "Jésù sì dáhùn ó wí fún wọn pé, “Àwọn ọmọ ayé yìí a máa gbéyàwó, wọn a sì máa fa ìyàwó fún-ni."'), 'Luke$20:38': ('ni', None, 'Luke$20:38 "Bẹ́ẹ̀ ni òun kì í ṣe Ọlọ́run àwọn òkú, bí kò ṣe ti àwọn alààyè: nítorí gbogbo wọn wà láàyè fún un.”"'), 'Luke$21:28': (None, None, 'Luke$21:28 " Ṣùgbọ́n nígbà tí nǹkan wọ̀nyí bá bẹ̀rẹ̀ sí í ṣẹ, ǹjẹ́ kí ẹ wo òkè, kí ẹ sì gbé orí yín sókè; nítorí ìdáǹdè yín kù sí dẹ̀dẹ̀.”"'), 'Luke$22:10': ('ni', None, 'Luke$22:10 "Ó sì wí fún wọn pé, “Kíyèsí i, nígbà tí ẹ̀yin bá ń wọ ìlú lọ, ọkùnrin kan tí ó ru iṣà omi yóò pàdé yín, ẹ bá a lọ sí ilé tí ó bá wọ̀,"'), 'Luke$22:12': ('ṣe', None, 'Luke$22:12 "Òun ó sì fi gbọ̀ngàn ńlá kan lókè hàn yín, tí a ṣe lọ́ṣọ̀ọ́: níbẹ̀ ni kí ẹ̀yin pèsè sílẹ̀.”"'), 'Luke$22:13': ('ṣe', None, 'Luke$22:13 Wọ́n sì lọ wọ́n sì bá a gẹ́gẹ́ bí ó tí sọ fún wọn: wọ́n sì pèsè ìrékọjá sílẹ̀.'), 'Luke$22:15': ('ni', 'jiya', 'Luke$22:15 " Ó sì wí fún wọn pé, “Tinú-tinú ni èmi fẹ́ fi bá yín jẹ ìrékọjá yìí, kí èmi tó jìyà:"'), 'Luke$22:41': (None, None, 'Luke$22:41 "Ó sì fi wọ́n sílẹ̀ níwọ̀n ìsọ-òkò kan, ó sì kúnlẹ̀ ó sì ń gbàdúrà."'), 'Luke$22:49': ('wà', None, 'Luke$22:49 " Nígbà tí àwọn tí ó wà lọ́dọ̀ rẹ̀ ń wo bí nǹkan yóò ti jásí, wọ́n bi í pé, “Olúwa kí àwá fi idà ṣá wọn?”"'), 'Luke$22:55': (None, None, 'Luke$22:55 "Nígbà tí wọ́n sì ti dáná láàrin gbọ̀ngàn, tí wọ́n sì jókòó pọ̀, Pétérù jokòó láàrin wọn."'), 'Luke$23:8': ('gbo', 'wo', 'Luke$23:8 " Nígbà tí Hẹ́rọ́dù, sì rí Jésù, ó yọ̀ gidigidi; nítorí tí ó ti ń fẹ́ rí i pẹ ó sáà ti ń gbọ́ ìhìn púpọ̀ nítorí rẹ̀; ó sì ń retí láti rí i kí iṣẹ́ àmì díẹ̀ ti ọwọ́ rẹ̀ ṣe."'), 'Luke$23:26': (None, None, 'Luke$23:26 " Bí wọ́n sì ti ń fà á lọ, wọ́n mú ọkùnrin kan, Símónì ara Kírénè, tí ó ń ti ìgbéríko bọ̀, òun ni wọ́n sì gbé àgbélèbú náà lé, kí ó máa rù ú lọ tẹ̀lé Jésù."'), 'Luke$23:50': (None, None, 'Luke$23:50 " Sì kíyèsí i, ọkùnrin kan tí a ń pè ní Jóṣẹ́fù, láti ìlú àwọn Júù kan tí ń jẹ́ Arimatíyà. Ó jẹ́ ọ̀kan nínú àwọn ọmọ ìgbìmọ̀, ènìyàn rere, àti olóòótọ́."'), 'Luke$24:6': (None, None, 'Luke$24:6 " Kò sí níhínyìí, ṣùgbọ́n ó jíǹde; ẹ rántí bí ó ti wí fún yín nígbà tí ó wà ní Gálílì."'), 'Luke$24:12': (None, None, 'Luke$24:12 "Nígbà náà ni Pétérù dìde, ó súré lọ sí ibojì; nígbà tí ó sì bẹ̀rẹ̀, ó rí aṣọ àlà lọ́tọ̀ fúnrarawọn, ó sì padà lọ sí ilé rẹ̀, ẹnu yà á sí ohun tí ó ṣe."'), 'Luke$24:15': ('lọ', None, 'Luke$24:15 "Ó sì ṣe, bí wọ́n ti ń bá ara wọn sọ̀rọ̀, tí wọ́n sì ń bá ara wọn jíròrò, Jésù tìkara rẹ̀ súnmọ́ wọn, ó sì ń bá wọn rìn lọ."'), 'Luke$24:25': (None, 'ni', 'Luke$24:25 "Ó sì wí fún wọn pé, “Ẹ̀yin tí òye kò yé, tí ẹ sì yigbì ní àyà láti gba gbogbo èyí tí àwọn wòlíì ti wí gbọ́:"'), 'Luke$24:28': ('ṣe', 'ni', 'Luke$24:28 Wọ́n sì súnmọ́ ìletò tí wọ́n ń lọ: ó sì ṣe bí ẹni pé yóò lọ sí iwájú.'), 'Luke$24:30': ('fun', None, 'Luke$24:30 " Ó sì ṣe, bí ó ti bá wọn jókòó ti oúnjẹ, ó mú àkàrà, ó súre sí i, ó sì bù ú, ó sì fifún wọn."'), 'John$1:9': (None, 'eniyan', 'John$1:9 Ìmọ́lẹ̀ òtítọ́ ń bẹ tí ń tan ìmọ́lẹ̀ fún olúkúlùkù ènìyàn tí ó wá sí ayé.'), 'John$1:41': ('ni', None, 'John$1:41 " Ohun àkọ́kọ́ tí Ańdérù ṣe ni láti wá Símónì arákùnrin rẹ̀, ó sì wí fún un pé, “Àwa ti rí Mèṣáyà” (ẹni tí ṣe Kírísítì)."'), 'John$2:9': (None, None, 'John$2:9 "Olórí àsè náà tọ́ omi tí a sọ di wáìnì wò. Òun kò sì mọ ibi tí ó ti wá, ṣùgbọ́n àwọn ìránṣẹ́ tí ó bu omi náà wá mọ̀. Olórí àsè sì pe ọkọ ìyàwó sí apákan,"'), 'John$2:24': (None, 'eniyan', 'John$2:24 "Ṣùgbọ́n Jésù kò gbé ara lé wọn, nítorí tí ó mọ gbogbo ènìyàn."'), 'John$3:1': ('wá', None, 'John$3:1 " Ọkùnrin kan sì wà nínú àwọn Farisí, tí a ń pè ní Níkódémù, ìjòyè kan láàrin àwọn Júù:"'), 'John$3:11': ('ni', 'gba', 'John$3:11 " Lóòótọ́ lóòótọ́ ni mo wí fún ọ, Àwa ń sọ èyí tí àwa mọ̀, a sì ń jẹ́rìí èyí tí àwa ti rí; ẹ̀yin kò sì gba ẹ̀rí wa."'), 'John$3:16': ('fun', 'ni', 'John$3:16 " “Nítorí Ọlọ́run fẹ́ aráyé tó bẹ́ẹ̀ gẹ́gẹ́, tí ó fi ọmọ bíbí rẹ̀ kan ṣoṣo fúnni, kí ẹnikẹ́ni tí ó bá gbà á gbọ́, má bà á ṣègbé, ṣùgbọ́n kí ó lè ní iyè àìnípẹ̀kun."'), 'John$3:19': (None, None, 'John$3:19 " Èyí ni ìdájọ́ náà pé, ìmọ́lẹ̀ wá sí ayé, àwọn ènìyàn sì fẹ́ òkùnkùn ju ìmọ́lẹ̀ lọ, nítorí tí iṣẹ́ wọn burú."'), 'John$3:35': (None, 'ọwọ', 'John$3:35 "Baba fẹ́ Ọmọ, ó sì ti fi ohun gbogbo lé e lọ́wọ́"'), 'John$4:10': (None, 'ebun', 'John$4:10 " Jésù dáhùn, ó sì wí fún un pé, “Ìbáṣepé ìwọ mọ ẹ̀bùn Ọlọ́run, ati ẹni tí ó wí fún ọ pé, fún mi mu, ìwọ ìbá sì ti bèèrè lọ́wọ́ rẹ̀, Òun ìbá ti fi omi ìyè fún ọ.”"'), 'John$4:28': (None, 'eniyan', 'John$4:28 "Nígbà náà ni obìnrin náà fi ládugbó rẹ̀ sílẹ̀, ó sì mú ọ̀nà rẹ̀ pọ̀n lọ sí ìlú, ó sì wí fún àwọn ènìyàn pé,"'), 'John$4:41': ('ni', None, 'John$4:41 Àwọn ọ̀pọ̀lọpọ̀ sì gbàgbọ́ sí i nítorí ọ̀rọ̀ rẹ̀.'), 'John$4:45': ('ṣe', None, 'John$4:45 "Nítorí náà nígbà tí ó dé Gálílì, àwọn ará Gálílì gbà á, nítorí ti wọ́n ti rí ohun gbogbo tí ó ṣe ní Jérúsálẹ́mù nígbà àjọ; nítorí àwọn tìkára wọn lọ sí àjọ pẹ̀lú."'), 'John$4:54': ('ṣe', 'wá', 'John$4:54 Èyí ni iṣẹ́ àmì kéjì tí Jésù ṣe nígbà tí ó ti Jùdéà jáde wá sí Gálílì.'), 'John$5:19': ('dahùn', 'ṣe', 'John$5:19 " Nígbà náà ni Jésù dáhùn, ó sì wí fún wọn pé, “Lóòótọ́, lóòótọ́ ni mo wí fún yín, ọmọ kò lè ṣe ohunkóhun fún ara rẹ̀, bí kò se ohun tí ó bá rí pé Baba ń ṣe: nítorí ohunkóhun tí baba bá ń ṣe, ìwọ̀nyí ni ọmọ náà sì ń ṣe pẹ̀lú."'), 'John$5:22': (None, None, 'John$5:22 "Nítorí pé Baba kì í ṣe ìdájọ́ ẹnikẹ́ni, ṣùgbọ́n ó ti fi gbogbo ìdájọ́ lé ọmọ lọ́wọ́:"'), 'John$5:25': ('ni', None, 'John$5:25 " Lóòótọ́, lóòótọ́ ni mo wí fún yín, wákàtí náà ń bọ̀, ó sì dé tán nísinsìn yìí, nígbà tí àwọn òkú yóò gbọ́ ohùn ọmọ Ọlọ́run: àwọn tí ó bá gbọ́ yóò sì yè."'), 'John$5:26': ('fi fun', 'ni', 'John$5:26 Nítorí pé gẹ́gẹ́ bí Baba ti ní ìyè nínú ara rẹ̀; gẹ́gẹ́ bẹ́ẹ̀ ni ó sì fi fún ọmọ láti ní ìyè nínú ara rẹ̀;'), 'John$5:28': ('ṣe', 'wá', 'John$5:28 "“Kí èyí má ṣe yà yín lẹ́nu; nítorí pé wákàtí ń bọ̀, nínú èyí tí gbogbo àwọn tí ó wà ní ibojì yóò gbọ́ ohun rẹ̀."'), 'John$5:36': (None, 'ni', 'John$5:36 " “Ṣùgbọ́n èmi ní ẹ̀rí tí ó pọ̀ju ti Jòhánù lọ: nítorí iṣẹ́ tí Baba ti fi fún mi láti ṣe parí, iṣẹ́ náà pàápàá tí èmi ń ṣe náà ń jẹ́rí mi pé, Baba ni ó rán mi."'), 'John$5:42': ('mọ', 'ife', 'John$5:42 "Ṣùgbọ́n èmi mọ̀ pé, ẹ̀yin fúnra yín kò ní ìfẹ́ Ọlọ́run nínú yín."'), 'John$6:14': ('awọn ọkunrin', None, 'John$6:14 " Nítorí náà nígbà tí àwọn ọkùnrin náà rí iṣẹ́-àmì tí Jésù ṣe, wọ́n wí pé, “Lóòótọ́ èyí ni wòlíì náà tí ń bọ̀ wá sí ayé.”"'), 'John$6:15': ('gba', None, 'John$6:15 " Nígbà tí Jésù sì wòye pé wọ́n ń fẹ́ wá fi agbára mú òun láti lọ fi jọba, ó tún padà lọ sórí òkè, òun nìkan."'), 'John$6:35': ('ebi', None, 'John$6:35 " Jésù wí fún wọn pé, “Èmi ni oúnjẹ ìyè: ẹnikẹ́ni tí ó bá tọ̀ mí wá, ebi kì yóò pa á; ẹni tí ó bá sì gbà mí gbọ́, òrùngbẹ kì yóò gbẹ ẹ́ mọ́ láé."'), 'John$6:38': ('wá', 'ṣe', 'John$6:38 " Nítorí èmi sọ̀kalẹ̀ láti ọ̀run wá, kì í ṣe láti má a ṣe ìfẹ́ ti èmi fúnra mi, bí kò ṣe ìfẹ́ ti ẹni tí ó rán mi."'), 'John$6:51': (None, None, 'John$6:51 "Èmi ni oúnjẹ ìyè náà tí ó ti ọ̀run sọ̀kalẹ̀ wá: bí ẹnikẹ́ni bá jẹ nínú oúnjẹ yìí, yóò yè títí láéláé: oúnjẹ náà tí èmi ó sì fifúnni fún ìyè aráyé ni ara mi.”"'), 'John$6:58': ('ni', None, 'John$6:58 " Èyí sì ni oúnjẹ náà tí ó sọ̀kalẹ̀ láti ọ̀run wá: kì í ṣe bí àwọn baba yín ti jẹ mánà, tí wọ́n sì kú: ẹni tí ó bá jẹ́ oúnjẹ yìí yóò yè láéláé.”"'), 'John$6:63': (None, 'ẹmi', 'John$6:63 " Ẹ̀mi ní ń sọni di ààyè; ara kò ní èrè kan; ọ̀rọ̀ wọ̀nyí tí mo sọ fún yín, ẹ̀mí ni, ìyè sì ni."'), 'John$7:23': ('ni', 'gba', 'John$7:23 " Bí ènìyàn bá ń gba ìkọlà ní ọjọ́ ìsinmi, kí a má ba à rú òfin Mósè, ẹ ha ti ṣe ń bínú sími, nítorí mo mú ènìyàn kan láradá ṣáṣá ní ọjọ́ ìsinmi?"'), 'John$7:44': (None, 'ni', 'John$7:44 Àwọn mìíràn nínú wọn sì fẹ́ láti mú un; ṣùgbọ́n kò sí ẹnìkan tí ó gbé ọwọ́ lé e.'), 'John$8:9': ('lọ', None, 'John$8:9 "Nígbà tí wọ́n gbọ́ èyí, (tí ẹ̀rí-ọkan wọn sì dá wọn lẹ́bi) wọ́n sì jáde lọ lọ́kọ̀ọ̀kan, bẹ̀rẹ̀ láti ọ̀dọ̀ àwọn àgbà títí dé àwọn tí ó kẹ́yìn; a sì fi Jésù nìkan sílẹ̀, àti obìnrin náà láàárin, níbi tí ó wà."'), 'John$8:21': ('lọ', 'kú', 'John$8:21 " Nítorí náà ó tún wí fún wọn pé, “Èmi ń lọ, ẹ̀yin yóò sì wá mi, ẹ ó sì kú nínú ẹ̀ṣẹ̀ yín: ibi tí èmi gbé ńlọ, ẹ̀yin kì yóò lè wá.”"'), 'John$8:38': ('baba', 'Baba', 'John$8:38 Ohun tí èmi ti rí lọ́dọ̀ Baba ni mo sọ: ẹ̀yin pẹ̀lú sì ń ṣe èyí tí ẹ̀yin ti gbọ́ láti ọ̀dọ̀ baba yín.”'), 'John$8:49': ('dahùn', None, 'John$8:49 "Jésù sì dáhùn pé, “Èmi kò ní ẹ̀mí èṣù, ṣùgbọ́n èmi ń bu ọlá fún Baba mi, ẹ̀yin kò sì bu ọlá fún mi."'), 'John$8:56': ('ri', None, 'John$8:56 " Ábúráhámù baba yín yọ̀ láti rí ọjọ́ mi: ó sì rí i, ó sì yọ̀.”"'), 'John$9:32': (None, None, 'John$9:32 "Láti ìgbà tí ayé ti ṣẹ̀, a kò ì tí ì gbọ́ pé ẹnìkan la ojú ẹni tí a bí ní afọ́jú rí."'), 'John$10:25': (None, 'ṣe', 'John$10:25 " Jésù dá wọn lóhùn pé, “Èmi ti wí fún yín, ẹ̀yin kò sì gbàgbọ́; iṣẹ́ tí èmi ń ṣe lórúkọ Baba mi, àwọn ni ó ń jẹ́rìí mi."'), 'John$10:27': (None, None, 'John$10:27 "Àwọn àgùntàn mi ń gbọ́ ohùn mi, èmi sì mọ̀ wọ́n, wọn a sì máa tọ̀ mí lẹ́yìn:"'), 'John$10:29': ('fun', 'ọwọ', 'John$10:29 "Baba mi, ẹni tí ó fi wọ́n fún mi pọ̀ ju gbogbo wọn lọ; kò sì sí ẹni tí ó lè já wọn gbà kúrò lọ́wọ́ Baba mi."'), 'John$10:32': (None, 'ṣe', 'John$10:32 "Jésù dá wọn lóhùn pé, “Ọ̀pọ̀lọpọ̀ iṣẹ́ rere ni mo fi hàn yín láti ọ̀dọ̀ Baba mi wá; nítorí èwo nínú iṣẹ́ wọ̀nyí ni ẹ̀yin ṣe sọ mí ní òkúta?”"'), 'John$11:13': ('sun', None, 'John$11:13 "Ṣùgbọ́n Jésù ń sọ ti ikú rẹ̀: ṣùgbọ́n wọ́n rò pé, ó ń sọ ti orun sísùn."'), 'John$11:16': ('lọ', 'kú', 'John$11:16 " “Nítorí náà Tómásì, ẹni tí à ń pè ní Dídímù, wí fún àwọn ọmọ-ẹ̀yìn ẹgbẹ́ rẹ̀ pé, Ẹ jẹ́ kí àwa náà lọ, kí a lè bá a kú pẹ̀lú.”"'), 'John$11:18': (None, None, 'John$11:18 Ǹjẹ́ Bétanì sún mọ́ Jérúsálẹ́mù tó ibùsọ Mẹ́ẹ̀dógún:'), 'John$11:25': (None, None, 'John$11:25 " Jésù wí fún un pé, “Èmi ni àjíǹde àti ìyè: ẹni tí ó bá gbà mí gbọ́, bí ó tilẹ̀ kú, yóò yè:"'), 'John$11:32': (None, 'wá', 'John$11:32 " Nígbà tí Màríà sì dé ibi tí Jésù wà, tí ó sì rí i, ó wólẹ̀ lẹ́bàá ẹsẹ̀ rẹ̀, ó wí fún un pé, “Olúwa, ìbáṣe pé ìwọ ti wà níhín-ín, arákùnrin mi kì bá kú.”"'), 'John$11:37': (None, 'ni', 'John$11:37 " Àwọn kan nínú wọn sì wí pé, “Ọkùnrin yìí, ẹni tí ó la ojú afọ́jú, kò lè ṣeé kí ọkùnrin yìí má kú bí?”"'), 'John$11:57': ('ní', None, 'John$11:57 "Ǹjẹ́ àwọn olórí àlùfáà àti àwọn Farisí ti pàṣẹ pé bí ẹnìkan bá mọ ibi tí ó gbé wà, kí ó fi í hàn, kí wọn baà lè mú un."'), 'John$12:4': (None, 'da', 'John$12:4 " Nígbà náà ni ọ̀kan nínú àwọn ọmọ-ẹ̀yìn rẹ̀, Júdásì Isíkáríótù, ọmọ Símónì ẹni tí yóò fi í hàn, wí pé,"'), 'John$12:18': ('ní', None, 'John$12:18 "Nítorí èyí ni ìjọ ènìyàn sì ṣe lọ pàdé rẹ̀, nítorí tí wọ́n gbọ́ pé ó ti ṣe iṣẹ́ àmì yìí."'), 'John$12:26': (None, None, 'John$12:26 "Bí ẹnikẹ́ni bá ń sìn mí, kí ó máa tọ̀ mí lẹ́yìn: àti níbi tí èmi bá wà, níbẹ̀ ni ìránṣẹ́ mi yóò wà pẹ̀lú: bí ẹnikẹ́ni bá ń sìn mí, òun ni Baba yóò bu ọlá fún."'), 'John$12:39': ('wo', None, 'John$12:39 "Nítorí èyí ni wọn kò fi lè gbàgbọ́, nítorí Ìsáyà sì tún sọ pé:"'), 'John$12:47': (None, None, 'John$12:47 " “Bí ẹnikẹ́ni bá sì gbọ́ ọ̀rọ̀ mi, tí kò sì pa wọ́n mọ́, èmi kì yóò ṣe ìdájọ́ rẹ̀ nítorí tí èmi kò wá láti ṣe ìdájọ́ ayé, bí kò ṣe láti gba ayé là."'), 'John$12:49': ('fun', 'ni', 'John$12:49 "Nítorí èmi kò dá ọ̀rọ̀ sọ fún ara mi, ṣùgbọ́n Baba tí ó rán mi, òun ni ó ti fún mi ní àṣẹ, ohun tí èmi ó sọ, àti èyí tí èmi ó wí."'), 'John$12:50': (None, None, 'John$12:50 "Èmi sì mọ̀ pé ìyè àìnípẹ̀kun ni òfin rẹ̀: nítorí náà, àwọn ohun tí mo bá wí, gẹ́gẹ́ bí Baba ti sọ fún mi, bẹ́ẹ̀ ni mo wí!”"'), 'John$13:1': (None, 'tirẹ', 'John$13:1 " Ǹjẹ́ kí àjọ ìrékọjá tó dé, nígbà tí Jésù mọ̀ pé, wákàtí rẹ̀ dé tan, tí òun ó ti ayé yìí kúrò lọ sọ́dọ̀ Baba, fífẹ́ tí ó fẹ́ àwọn tirẹ̀ tí ó wà ní ayé, ó fẹ́ wọn títí dé òpin."'), 'John$13:7': (None, 'ṣe', 'John$13:7 "Jésù dá a lóhùn, ó sì wí fún un pé, “Ohun tí èmi ń ṣe ni ìwọ kò mọ̀ nísinsin yìí; ṣùgbọ́n yóò yé ọ ní ìkẹyìn.”"'), 'John$13:12': ('ṣe', None, 'John$13:12 "Nítorí náà lẹ́yìn tí ó wẹ ẹṣẹ̀ wọn tán, tí ó sì ti mú agbádá rẹ̀, tí ó tún jókòó, ó wí fún wọn pé, “Ẹ̀yin mọ ohun tí mo ṣe sí yín bí?"'), 'John$13:15': (None, None, 'John$13:15 " Nítorí mo fi àpẹẹrẹ fún yín, kí ẹ̀yin lè máa ṣe gẹ́gẹ́ bí mo ti ṣe sí yín."'), 'John$13:26': ('ni', 'fun', 'John$13:26 " Nítorí náà Jésù dáhùn pé, “Òun náà ni, ẹni tí mo bá fi àkàrà fún nígbà tí mo bá fi run àwo.” Nígbà tí ó sì fi run ún tan, ó fi fún Júdásì Ísíkárótù ọmọ Símónì."'), 'John$13:29': ('ni', 'fun', 'John$13:29 " Nítorí àwọn mìíràn nínú wọn rò pé, nitorí Júdásì ni ó ni àpò, ni Jésù fi wí fún un pé, Ra nǹkan wọ̀n-ọn-nì tí a kò le ṣe aláìní fún àjọ náà; tàbí kí ó lè fi nǹkan fún àwọn talákà."'), 'John$13:34': ('fun', 'ni', 'John$13:34 " “Òfin titun kan ni mo fi fún yín, kí ẹ̀yin kí ó fẹ́ ọmọnìkejì yín; gẹ́gẹ́ bí èmi ti fẹ́ràn yín, kí ẹ̀yin kí ó sì lè fẹ́ràn ọmọnìkejì yín."'), 'John$13:35': ('mọ', 'ife', 'John$13:35 "Nípa èyí ni gbogbo ènìyàn yóò fi mọ̀ pé, ọmọ-ẹ̀yìn mi ni ẹ̀yin ń se, nígbà tí ẹ̀yin bá ní ìfẹ́ sí ọmọ ẹnìkejì yín.”"'), 'John$14:27': ('fun', 'fi silẹ', 'John$14:27 " Àlàáfíà ni mo fi sílẹ̀ fún yín pé, Èmi ń lọ, èmi fi fún yín. Ẹ má ṣe jẹ́ kí ọkàn yín dàrú, ẹ má sì jẹ́ kí ó wárìrì."'), 'John$14:31': ('mọ', None, 'John$14:31 " Ṣùgbọ́n nítorí kí ayé lè mọ̀ pé èmi fẹ́ràn Baba; gẹ́gẹ́ bí Baba sì ti fi àṣẹ fún mi, bẹ́ẹ̀ ni èmi ń ṣe. “Ẹ dìde, ẹ jẹ́ kí a lọ kúrò níhínyìí."'), 'John$15:6': (None, None, 'John$15:6 "Bí ẹnìkan kò bá gbé inú mi, a gbé e sọnù gẹ́gẹ́ bí ẹ̀ka, a sì gbẹ; wọn a sì kó wọn jọ, wọn a sì sọ wọ́n sínú iná, wọn a sì jóná."'), 'John$15:15': ('ṣe', 'ni', 'John$15:15 "Èmi kò pè yín ní ọmọ ọ̀dọ̀ mọ́; nítorí ọmọ ọ̀dọ̀ kò mọ ohun tí olúwa rẹ̀ ń ṣe: ṣùgbọ́n èmi pè yín ní ọ̀rẹ́ nítorí ohun gbogbo tí mo ti gbọ́ láti ọ̀dọ̀ Baba mi wá, mo ti fi hàn fún yín."'), 'John$15:16': ('ni', 'fun', 'John$15:16 " Kì í ṣe ẹ̀yin ni ó yàn mí, ṣùgbọ́n èmi ni ó yàn yín, mo sì fi yín sípò, kí ẹ̀yin kí ó lè lọ, kí ẹ sì so èso, àti kí èṣo yín lè dúró; kí ohunkóhun tí ẹ bá bèrè lọ́wọ́ Baba ní orúkọ mi, kí ó lè fi í fún yín."'), 'John$15:19': ('ni', 'tirẹ', 'John$15:19 "Ìbáṣepé ẹ̀yin ń ṣe ti ayé, ayé ìbá fẹ́ àwọn tirẹ̀; ṣùgbọ́n nítorí tí ẹ̀yin kì ń ṣe ti ayé, ṣùgbọ́n èmi ti yàn yín kúrò nínú ayé, nítorí èyí ni ayé ṣe kórìíra yín."'), 'John$15:26': (None, 'ni', 'John$15:26 " “Ṣùgbọ́n nígbà tí Olùtùnú náà bá dé, ẹni tí èmi ó rán sí yín láti ọ̀dọ̀ Baba wá, àní Ẹ̀mí òtítọ́ nì, tí ń ti ọ̀dọ̀ Baba wá, òun náà ni yóò jẹ́rìí mi:"'), 'John$16:7': ('lọ', 'lọ', 'John$16:7 " Ṣùgbọ́n òtítọ́ ni èmi ńsọ fún yín; àǹfàní ni yóò jẹ́ fún yín bí èmi bá lọ: nítorí bí èmi kò bá lọ, Olùtùnú kì yóò tọ̀ yín wá; ṣùgbọ́n bí mo bá lọ, èmi ó rán an sí yín."'), 'John$16:12': (None, 'ni', 'John$16:12 "“Mo ní ohun pípọ̀ láti sọ fún yín pẹ̀lú, ṣùgbọ́n ẹ kò lè gbà wọ́n nísinsìn yìí."'), 'John$16:21': ('eniyan', None, 'John$16:21 " Nígbà tí obìnrin bá ń rọbí, a ní ìbìnújẹ́, nítorí tí wákàtí rẹ̀ dé: ṣùgbọ́n nígbà tí ó bá ti rí ọmọ náà tán, òun kì í sì í rántí ìrora náà mọ́, fún ayọ̀ nítorí a bí ènìyàn sí ayé."'), 'John$16:24': ('ni', 'gba', 'John$16:24 " Títí di ìsinyìí ẹ kò tíì bèèrè ohunkóhun ní orúkọ mi: ẹ bèèrè, ẹ ó sì rí gbà, kí ayọ̀ yín kí ó lè kún."'), 'John$17:5': ('ní', None, 'John$17:5 " Ǹjẹ́ nísinsìn yìí, Baba, ṣe mí lógo pẹ̀lú ara rẹ, ògo tí mo ti ní pẹ̀lú rẹ kí ayé kí ó tó wà."'), 'John$17:6': (None, None, 'John$17:6 "“Èmi ti fi orúkọ rẹ hàn fún àwọn ènìyàn tí ìwọ ti fún mi láti inú ayé wá: tìrẹ ni wọ́n ti jẹ́ rí, ìwọ sì ti fi wọ́n fún mi; wọ́n sì ti pa ọ̀rọ̀ rẹ mọ́."'), 'John$17:7': ('fi fun', 'ni', 'John$17:7 "Nísinsìn yìí, wọ́n mọ̀ pé ohunkóhun gbogbo tí ìwọ ti fi fún mi, láti ọ̀dọ̀ rẹ wá ni."'), 'John$17:8': ('fi fun', 'ni', 'John$17:8 "Nítorí ọ̀rọ̀ tí ìwọ fi fún mi, èmi ti fi fún wọn, wọ́n sì ti gbà á, wọ́n sì ti mọ̀ nítòótọ́ pé, lọ́dọ̀ rẹ ni mo ti jáde wá, wọ́n sì gbàgbọ́ pé ìwọ ni ó rán mi."'), 'John$17:14': (None, 'ni', 'John$17:14 " Èmi ti fi ọ̀rọ̀ rẹ fún wọn; ayé sì ti kórìíra wọn, nítorí tí wọn kì í ṣe ti ayé, gẹ́gẹ́ bí èmi kì í ti í ṣe ti ayé."'), 'John$17:22': ('fi fun', 'ni', 'John$17:22 "Ògo tí ìwọ ti fi fún mi ni èmi sì ti fi fún wọn; kí wọn kí ó lè jẹ́ ọ̀kan, gẹ́gẹ́ bí àwa ti jẹ́ ọ̀kan;"'), 'John$18:8': ('jẹ ki', 'ni', 'John$18:8 "Jésù dáhùn pé, “Mo ti wí fún yín pé, èmi nìyí: ǹjẹ́ bí èmi ni ẹ bá ń wá, ẹ jẹ́ kí àwọn wọ̀nyí máa lọ:"'), 'John$18:20': (None, None, 'John$18:20 "Jésù dá a lóhùn pé, “Èmi ti sọ̀rọ̀ ní gbangba fún aráyé; nígbà gbogbo ni èmi ń kọ́ni nínú Sínágọ́gù, àti ní Tẹ́ḿpìlì níbi tí gbogbo àwọn Júù ń pé jọ sí: èmi kò sì sọ ohun kan ní ìkọ̀kọ̀."'), 'John$18:22': (None, None, 'John$18:22 "Bí ó sì ti wí èyí tan, ọ̀kan nínú àwọn alasẹ́ tí ó dúró tì í fi ọwọ́ rẹ̀ lu Jésù, pé, “Olórí àlùfáà ni ìwọ ń dá lóhùn bẹ́ẹ̀?”"'), 'John$18:23': ('ni', None, 'John$18:23 " Jésù dá a lóhùn pé, “Bí mo bá sọ̀rọ̀ búburú, jẹ́rìí sí búburú náà: ṣùgbọ́n bí rere bá ni, èé ṣe tí ìwọ fi ń lù mí?”"'), 'John$18:30': (None, None, 'John$18:30 "Wọ́n sì dáhùn wí fún un pé, “Ìbá má ṣe pé ọkùnrin yìí ń hùwà ibi, a kì bá tí fà á lé ọ lọ́wọ́.”"'), 'John$19:7': ('ṣe', 'ni', 'John$19:7 " Àwọn Júù dá a lóhùn wí pé, “Àwa ní òfin kan, àti gẹ́gẹ́ bí òfin, wa ó yẹ fún un láti kú, nítorí ó gbà pé ọmọ Ọlọ́run ni òun ń ṣe.”"'), 'John$19:11': (None, 'ni', 'John$19:11 " Jésù dá a lóhùn pé, “Ìwọ kì bá tí ní agbára kan lórí mi, bí kò se pé a fi í fún ọ láti òkè wá: nítorí náà ẹni tí ó fi mí lé ọ lọ́wọ́ ni ó ní ẹ̀ṣẹ̀ pọ̀ jù.”"'), 'John$19:13': ('ibi', None, 'John$19:13 "Nítorí náà nígbà tí Pílátù gbọ́ ọ̀rọ̀ wọ̀nyí, ó mú Jésù jáde wá, ó sì jókòó lórí ìtẹ́ ìdájọ́ ní ibi tí a ń pè ní Òkúta-títẹ́, ṣùgbọ́n ní èdè Hébérù, Gábátà."'), 'John$19:38': ('fun', None, 'John$19:38 " Lẹ́yìn nǹkan wọ̀nyí ní Jóṣéfù ará Arimatíyà, ẹni tí ó jẹ́ ọmọ-ẹ̀yìn Jésù, ṣùgbọ́n ní ìkọ̀kọ̀, nítorí ìbẹ̀rù àwọn Júù, o bẹ Pílátù kí òun lè gbé òkú Jésù kúrò: Pílátù sì fún un ní àṣẹ. Nígbà náà ni ó wá, ó sì gbé òkú Jésù lọ."'), 'John$20:2': ('feran', 'mọ', 'John$20:2 "Nítorí náà, ó sáré, ó sì tọ Símónì Pétérù wá, àti ọmọ-ẹ̀yìn mìíràn náà ẹni tí Jésù fẹ́ràn, ó sì wí fún wọn pé, “Wọ́n ti gbé Olúwa kúrò nínú ibojì, àwa kò sì mọ ibi tí wọ́n gbé tẹ́ ẹ sí.”"'), 'John$20:27': (None, 'ọwọ', 'John$20:27 " Nígbà náà ni ó wí fún Tọ́másì pé, “Mú ìka rẹ wá níhín yìí, kí o sì wo ọwọ́ mi; sì mú ọwọ́ rẹ wá níhín, kí o sì fi sí ìhà mi: kí ìwọ má ṣe jẹ́ aláìgbàgbọ́ mọ́, ṣùgbọ́n jẹ́ onígbàgbọ́.”"'), 'John$21:23': (None, 'kú', 'John$21:23 "Ọ̀rọ̀ yìí sì tàn ká láàrin àwọn arákùnrin pé, ọmọ-ẹ̀yìn náà kì yóò kú: ṣùgbọ́n Jésù kò wí fún un pé, Òun kì yóò kú; ṣùgbọ́n, “Bí èmi bá fẹ́ kí ó dúró títí èmi ó fi dé, kínni èyí jẹ́ sí ọ?”"'), 'Acts$1:14': (None, 'adura', 'Acts$1:14 Gbogbo àwọn wọ̀nyí pẹ̀lú àwọn obìnrin àti Màríà ìyá Jésù àti àwọn arákùnrin rẹ̀ fi ọkàn kan dúró láti máa gbàdúrà.'), 'Acts$1:15': (None, None, 'Acts$1:15 Ní ọjọ́ wọ̀nyí ni Pétérù sí díde dúró láàrin àwọn ọmọ-ẹ̀yìn (iye àwọn ènìyàn gbogbo nínú ìjọ jẹ́ ọgọ́fà)'), 'Acts$1:21': ('ya', 'ni', 'Acts$1:21 "Nítorí náà, ó di dandan láti yan ọ̀kan nínú àwọn ọkùnrin wọn tí wọn ti ń bá wa rìn ni gbogbo àkókò tí Jésù Olúwa ń wọlé, tí ó sì jáde láàrin wa."'), 'Acts$2:9': (None, None, 'Acts$2:9 "Àwọn ará Pátíríà, àti Médísì, àti Élámù; àti àwọn tí ń gbé Mesopotamíà, Júdíà, àti Kapadókíà, Pọ́ńtù, àti Ásíà."'), 'Acts$2:14': ('sọ', 'ro pe', 'Acts$2:14 "Nígbà náà ni Pétérù díde dúró pẹ̀lú àwọn mọ́kànlá yòókù, ó gbé ohùn rẹ̀ sókè, ó sì wí fún wọn pé, “Ẹ̀yin Júù ènìyàn mi àti gbogbo ẹ̀yin tí ń gbé Jerúsálémù, ẹ jẹ́ kí èyí kí ó yé yin; kí ẹ sì fétísí ọ̀rọ̀ mi."'), 'Acts$2:22': (None, 'eniyan', 'Acts$2:22 "“Ẹ̀yin ènìyàn Ísírẹ́lì, ẹ gbọ́ ọ̀rọ̀ wọ̀nyí; Jésù tí Násárẹ́tì, ọkùnrin tí a mọ̀ láti ọ̀dọ̀ Ọlọ́run wá nípa iṣẹ́ agbára àti tí ìyanu, àti àmì ti Ọlọ́run ti ọwọ́ rẹ̀ ṣe láàrin yín, bí ẹ̀yin tíkárayín ti mọ̀ pẹ̀lú."'), 'Acts$2:33': ('wo', 'gbo', 'Acts$2:33 "A ti gbé ga sí ọwọ́ ọ̀tún Ọlọ́run, ó ti gba ìlérí Ẹ̀mí Mímọ́ láti ọ̀dọ̀ Baba, ó tu èyí tí ẹ̀yin rí àti gbọ́ nísinsìyìí sítá."'), 'Acts$2:36': (None, 'jẹ ki', 'Acts$2:36 "“Ǹjẹ́ kí gbogbo ilé Ísírẹ́lì mọ̀ dájúdájú pé: Ọlọ́run ti fi Jésù náà, ti ẹ̀yin kàn mọ́ àgbélébùú, se Olúwa àti Kírísítì.”"'), 'Acts$2:39': ('pe', 'ileri', 'Acts$2:39 " Nítorí fún yín ni ìlérí náà, àti fún àwọn ọmọ yín, àti fún gbogbo àwọn tí ó jìnnà réré, àní gbogbo àwọn tí Olúwa Ọlọ́run wa yóò pè.”"'), 'Acts$2:44': (None, None, 'Acts$2:44 " Gbogbo àwọn tí ó sì gbàgbọ́ wà ni ibìkan, wọn ní ohun gbogbo sọ́kàn;"'), 'Acts$2:46': (None, 'okan', 'Acts$2:46 "Wọ́n fi ọkàn kan dúró lójoojúmọ́ nínú tẹ́mpílì. Wọ́n ń bu àkàrà ní ilé wọn, wọn ń fi inú dídùn àti ọkàn òtítọ́ jẹ oúnjẹ wọn."'), 'Acts$3:6': ('ni', 'fun', 'Acts$3:6 "Pétérù wí pé, “Góòlù àti fàdákà èmi kò ní, ṣùgbọ́n ohun tí mo ní èyí náà ni mo fifún ọ: Ní orúkọ Jésù Kírísítì ti Násárẹ́tì, dìde kí o sì máa rìn.”"'), 'Acts$3:16': (None, 'orukọ', 'Acts$3:16 "Nípa ìgbàgbọ́ nínú orúkọ Jésù, òun ni ó mú ọkùnrin yìí láradá, ẹni tí ẹ̀yin rí, tí ẹ sì mọ̀. Orúkọ Jésù àti ìgbàgbọ́ tí ó wá nípa rẹ̀ ni ó fún un ní ìlera pípé ṣáṣá yìí ni ojú gbogbo yín."'), 'Acts$3:18': ('ní', 'jiya', 'Acts$3:18 "Ṣùgbọ́n báyìí ni Ọlọ́run ti se ìmúṣẹ àwọn ohun tí ó ti sọ tẹ́lẹ̀ láti ẹnu gbogbo àwọn wòlíì pé, Kírísítì rẹ̀ yóò jìyà."'), 'Acts$4:4': (None, None, 'Acts$4:4 Ṣùgbọ́n ọ̀pọ̀ àwọn tí ó gbọ́ ọ̀rọ̀ náà gbàgbọ́; iye àwọn ọkùnrin náà sì tó ẹgbẹ̀ẹ́dọ́gbọ̀n (5000).'), 'Acts$4:7': ('ṣe', None, 'Acts$4:7 "Wọ́n mú Pétérù àti Jòhánù dúró níwájú wọn, wọ́n sì bẹ̀rẹ̀ sí bèèrè ìbéèrè lọ́wọ́ wọn pé, “Nípa agbára tàbí orúkọ wo ni ẹ̀yin fi ṣe èyí?”"'), 'Acts$4:8': (None, None, 'Acts$4:8 "Nígbà náà ni Pétérù kún fún Ẹ̀mí Mímọ́, ó sì wí fún wọn pé, “Ẹ̀yin olórí àti ẹ̀yin alàgbà àwọn ènìyàn!"'), 'Acts$4:15': (None, 'ṣe', 'Acts$4:15 "Ṣùgbọ́n nígbà tí wọ́n sì ti pàṣẹ pé kí wọn jáde kúrò ní ìgbìmọ̀, wọ́n bá ara wọn gbérò."'), 'Acts$4:21': ('ní', None, 'Acts$4:21 "Nígbà tí wọ́n sì kìlọ̀ fún wọn sí i, wọn fi wọ́n sílẹ̀ lọ, nígbà tí wọn kò ì tí ì rí nǹkan tí wọn ì bá fi jẹ wọ́n ní ìyà, nítorí àwọn ènìyàn; nítorí gbogbo wọn ni wọ́n yin Ọlọ́run lógo fún ohun tí ó ṣe."'), 'Acts$4:23': (None, None, 'Acts$4:23 "Nígbà tí wọ́n sì ti fi wọ́n sílẹ̀ wọ́n lọ sọ́dọ̀ àwọn ẹlẹgbẹ́ wọn, wọ́n sì ròyìn ohun gbogbo tí àwọn olórí àlùfáà àti àwọn alàgbà sọ fún wọn."'), 'Acts$4:27': (None, 'ṣe', 'Acts$4:27 " Àní nítòótọ́ ní Hẹ́rọ́dù, àti Pọ́ńtíù Pílátù, pẹ̀lú àwọn aláìkọlà àti àwọn ènìyàn Ísírẹ́lì kó ara wọn jọ ní ìlú yìí láti dìtẹ̀ sí Jésù Ìrànṣẹ́ mímọ́ rẹ, ẹni tí ìwọ ti fi òróró yàn,"'), 'Acts$4:32': (None, None, 'Acts$4:32 Ìjọ àwọn tí ó gbàgbọ́ sì wà ní ọkàn kan àti inú kan; kò sì sí ẹnìkan tí ó wí pé ohun kan nínú ohun ìní rẹ̀ jẹ́ ti ara rẹ̀; ṣùgbọ́n gbogbo wọn ní ohun gbogbo ní ìsọ̀kan.'), 'Acts$4:36': (None, None, 'Acts$4:36 "Àti Jóṣéfù, tí a ti ọwọ́ àwọn àpósítélì sọ àpèlé rẹ̀ ní Bánábà (ìtúmọ̀ èyí tí ń jẹ Ọmọ-Ìtùnú), ẹ̀yà Léfì, àti ará Sáípúrọ́sì."'), 'Acts$5:31': ('fun', 'ọwọ', 'Acts$5:31 Òun ni Ọlọ́run fi ọwọ́ ọ̀tún rẹ̀ gbé gẹ́gẹ́ bí Ọmọ-aladé àti Olùgbàlà láti fi ìrònúpìwàdà àti ìdàríjì ẹ̀ṣẹ̀ fún Ísírẹ́lì.'), 'Acts$5:34': ('ní', None, 'Acts$5:34 "Ṣùgbọ́n ọ̀kan nínú àjọ ìgbìmọ̀, tí a ń pè ni Gàmálíẹ́lì, Farisí àti àmofìn, tí ó ní ìyìn gidigidi lọ́dọ̀ gbogbo ènìyàn, ó dìde dúró, ó ni kí a mú àwọn àpósítélì bì sẹ́yìn díẹ̀."'), 'Acts$5:36': (None, None, 'Acts$5:36 "Nítorí ṣáájú ọjọ́ wọ̀nyí ni Téúdà dìde, ó ń wí pé, ẹni ńlá kan ní òun; ẹni tí ìwọ̀n irínwó ọkùnrin dara wọn pọ̀ mọ́; ṣùgbọ́n a pá a; àti gbogbo iye àwọn tí ó gba tirẹ̀ sì túká, tí wọn sí di asán."'), 'Acts$5:38': (None, 'iṣẹ', 'Acts$5:38 "Ǹjẹ́ èmi wí fún un yín nísinsin yìí, Ẹ gáfárà fún àwọn ọkùnrin wọ̀nyí, kí ẹ sí fi wọn sílẹ̀, nítorí bí ìmọ̀ tàbí iṣẹ́ yìí bá jẹ́ ti ènìyàn, a ó bì í ṣúbu."'), 'Acts$5:40': ('ní', None, 'Acts$5:40 "Wọ́n sì gbà ìmọ̀ràn rẹ̀. Wọn pe àwọn àpósítélì wọlé, wọ́n lù wọ́n. Wọn sí kìlọ̀ fún wọn pé kí wọn má ṣe sọ̀rọ̀ ni orúkọ Jésù mọ́, wọ́n sì jọ̀wọ́ wọn sílẹ̀ lọ."'), 'Acts$6:1': (None, None, 'Acts$6:1 "Ǹjẹ́ ní ọjọ́ wọ̀nyí, nígbà tí iye àwọn ọmọ-ẹ̀yìn ń pọ̀ sí i, ìkùn-sínú wà ní àárin àwọn Gíríkì tí se Júù àti àwọn Hébérù tí se Júù, nítorí tí a gbàgbé nípa ti àwọn opó wọn nínú ìpín-fúnni ojoojúmọ́."'), 'Acts$6:7': (None, None, 'Acts$6:7 "Ọ̀rọ̀ Ọlọ́run sì gbilẹ̀, iye àwọn ọmọ-ẹ̀yìn sì pọ̀ sí i gidigidi ni Jerúsálémù, ọ̀pọ̀ nínú ẹgbẹ́ àwọn àlùfáà sí fetí sí tí ìgbàgbọ́ náà."'), 'Acts$6:12': (None, 'ni', 'Acts$6:12 "Wọ́n sí ru àwọn ènìyàn sókè, àti àwọn alàgbà, àti àwọn olùkọ́ni ní òfin. Wọ́n dìde sí i, wọ́n gbá a mú, wọ́n sì mú un wá sí iwájú àjọ ìgbìmọ̀."'), 'Acts$6:15': (None, 'oju', 'Acts$6:15 "Gbogbo àwọn tí ó sì jókò ni àjọ ìgbìmọ̀ tẹjúmọ́ Sítéfánù, wọ́n sì rí ojú rẹ̀ dàbí ojú ańgẹ́lì."'), 'Acts$7:2': ('ilẹ', None, 'Acts$7:2 " Ní ìdáhùn sí èyí ó wí pé, “Ẹ̀yin arakùnrin àti ẹ̀yin baba, ẹ fetísílẹ̀ sí mi! Ọlọ́run ògo fi ara hàn fún Ábúráhámù baba wa, nígbà tí ó wà ni Mesopotamíà, kí ó to ṣe àtìpó ni Háránì."'), 'Acts$7:5': ('ní', 'fun', 'Acts$7:5 " Kò sí fún un ni ìní kan, àní tó bi ìwọ̀n ààyè ẹṣẹ̀ kan. Ṣùgbọ́n Ọlọ́run se ìlérí fún pé, òun yóò fi ilẹ̀-ìní náà fún un àti fún àwọn irú ọmọ rẹ̀ lẹ́yìn rẹ̀, nígbà tí kò tí ì ní ọmọ."'), 'Acts$7:6': (None, 'ilẹ', 'Acts$7:6 " Ọlọ́run sì sọ báyìí pé: ‘Irú-ọmọ rẹ̀ yóò ṣe àtìpó ni ilẹ̀ àjèjì; wọn ó sì sọ wọn di ẹrú, wọn ó sì pọ́n wọn lójú ni irínwó ọdún.’"'), 'Acts$7:24': ('ni', None, 'Acts$7:24 "Nígbà tí ó sì rí ọ̀kan nínú wọ́n tí ara Éjíbítì kan ń jẹ́ ní ìyà, ó gbéjà rẹ̀, ó gbẹ̀sàn ẹni tí wọ́n jẹ ní ìyà nípa lílu ara Íjíbítì náà pa:"'), 'Acts$7:26': (None, 'ṣe', 'Acts$7:26 "Ní ọjọ́ kejì Mósè yọ sí àwọn ọmọ Ísírẹ́lì méjì bí wọ́n ti ń jà. Òun ì bá sí parí rẹ̀ fún wọn. Ó wí pé, ‘Ènìyàn mi, ará ni ẹ̀yin; è é ṣe tí ẹ̀yin fi ń ṣe ohun tí kò tọ́ sí ara yín?’"'), 'Acts$7:30': ('ina', 'ina', 'Acts$7:30 " “Nígbà tí ogójì ọdún sì pé, ańgẹ́lì Olúwa fara hàn Mósè ní ijù, ní òkè Sínáì, nínú ọ̀wọ́ iná ìgbẹ́."'), 'Acts$7:33': ('ibi', 'bata', 'Acts$7:33 "“Olúwa sì wí fún un pé, ‘Tú bátà rẹ kúrò ni ẹsẹ̀ rẹ, nitorí ibi tí ìwọ gbé dúró sí yìí ilẹ̀ mímọ́ ni."'), 'Acts$7:34': ('ni', None, 'Acts$7:34 "Ní rírí mo ti rí ìpọ́njú àwọn ènìyàn mi tí ń bẹ ni Íjíbítì, mo sì ti gbọ́ ìkérora wọn, mo sì sọ̀kalẹ̀ wá láti gbà wọ́n. Wá nísinsìn yìí, èmi ó sì rán ọ lọ sí ilẹ̀ Íjíbítì.’"'), 'Acts$7:36': ('mu', 'ilẹ', 'Acts$7:36 " Òun ni ó mú wọn jáde, lẹ́yìn ìgbà tí ó ṣe iṣẹ́ ìyanu àti iṣẹ́ àmì ní ilẹ̀ Íjíbítì, àti ni òkun pupa, àti ni ihà ní ogójì ọdún."'), 'Acts$7:38': (None, None, 'Acts$7:38 " Èyí náà ni ẹni tí ó wà nínú ìjọ ní ijù pẹ̀lú ańgẹ́lì náà tí ó bá a sọ̀rọ̀ ní orí òkè Sínáì, àti pẹ̀lú àwọn baba wa; ẹni ti ó gba ọ̀rọ̀ ìyè láti fi fún wa."'), 'Acts$7:42': ('fun', 'ni', 'Acts$7:42 "Ọlọ́run sì padà, ó fi wọn sílẹ̀ láti máa sin ogun ọ̀run. Èyí wà ní ìbámu pẹ̀lú bí a ti kọ ọ́ nínú ìwé àwọn wòlíì pé: “ ‘Ẹ̀yin hà mú ẹran tí a pa àti ẹbọ wa fún mi ní ogójì ọdún ní ijù bí, Ìwọ ilé Ísírẹ́lì?"'), 'Acts$7:43': (None, None, 'Acts$7:43 "Ẹ̀yin sì tẹ́wọ́gba ojúbọ Mólókù, àti ìràwọ̀ Ráfánì òrìṣà yín, àwọn òrìṣà tí ẹ̀yin ṣe láti máa bọ. Nítorí náà èmi yóò kó yín lọ sí ìgbèkùn rékọjá Bábílónì.’"'), 'Acts$7:44': ('ri', None, 'Acts$7:44 " “Àwọn baba wa ní àgọ́ ẹ̀rí pẹ̀lú wọn ní ijú. Èyí tí a se gẹ́gẹ́ bí Ọlọ́run tí ó ba Mósè sọ̀rọ̀ pé kí ó ṣe é, gẹ́gẹ́ bí àpẹrẹ tí ó ti rí."'), 'Acts$7:45': (None, 'wa', 'Acts$7:45 " Ti àwọn baba wa tí ó tẹ̀lé wọn sí mú bá Jóṣúà wá sí ilẹ̀-ìní àwọn aláìkọlà, tí Ọlọ́run lè jáde kúrò níwájú àwọn baba wa, títí dí àkókó Dáfídì."'), 'Acts$7:48': (None, 'kọ', 'Acts$7:48 “Ṣùgbọ́n Ọ̀gá-ògo kì í gbé ilé ti a fi ọwọ́ kọ́: gẹ́gẹ́ bí wòlíì ti wí pé:'), 'Acts$7:55': ('wò', 'wo', 'Acts$7:55 "Ṣùgbọ́n Sítéfánù, ẹni tí ó kún fún Ẹ̀mí Mímọ́, ó tẹjúmọ́ ọ̀run, ó sì rí ògo Ọlọ́run, àti Jésù dúró ní ọwọ́ ọ̀tún Ọlọ́run."'), 'Acts$8:9': (None, 'nla', 'Acts$8:9 "Ṣùgbọ́n ọkùnrin kan wà, tí a ń pè ní Símónì, tí ó ti máa ń pa idán ní ìlú náà, ó sì mú kí ẹnu ya àwọn ará Samaríà. Ó sì máa ń fọ́nnu pé ènìyàn ńlá kan ni òun."'), 'Acts$8:14': ('ní', 'gba', 'Acts$8:14 "Nígbà tí àwọn àpósítélì tí ó wà ní Jerúsálémù sí gbọ́ pé àwọn ara Samaríà ti gba ọ̀rọ̀ Ọlọ́run, wọ́n rán Pétérù Àti Jòhánù sí wọn."'), 'Acts$8:21': (None, None, 'Acts$8:21 "Ìwọ kò ni ipa tàbí ìpín nínú ọ̀ràn yìí, nítorí ọkàn rẹ kò ṣe déedé níwájú Ọlọ́run."'), 'Acts$8:23': (None, None, 'Acts$8:23 " Nítorí tí mo wòye pé, ìwọ wa nínú òróǹrò ìkorò, àti ní ìdè ẹ̀ṣẹ̀.”"'), 'Acts$8:30': (None, 'ka', 'Acts$8:30 "Fílípì si súré lọ, ó gbọ́ ti ó ń ka ìwé wòlíì Àìṣáyà, Fílípì sì bí i pé, “Ohun tí ìwọ ń kà yìí ha yé ọ bí?”"'), 'Acts$8:32': (None, 'ka', 'Acts$8:32 " Ibi-ìwé-mímọ́ tí Ìwẹ̀fà náà ń kà náà ni èyí: “A fà á bí àgùntàn lọ fún pípa; àti bí ọ̀dọ́-àgùntàn tí ń dákẹ́ níwájú olúrẹ́run rẹ̀, bẹ́ẹ̀ gẹ́gẹ́ ni kò wí ohun kan."'), 'Acts$9:3': (None, 'imole', 'Acts$9:3 "Ó sì ṣe, bí ó ti ń lọ, ó sì súmọ́ Dámásíkù; lójijì, ìmọ́lẹ̀ láti ọ̀run wá mọlẹ̀ yí i ká."'), 'Acts$9:7': (None, None, 'Acts$9:7 "Àwọn ọkùnrin tí wọ́n sì ń bá Sọ́ọ̀lù lọ àjò dúró, wọn kò sì le sọ ohun kan, wọn gbọ́ ohùn náà ṣùgbọ́n wọn kò rí ẹnìkan."'), 'Acts$9:11': (None, None, 'Acts$9:11 "Olúwa sì wí fún un pé, “Dìde kí ó sì lọ sí ilé Júdà ní Òpópónà Tààrà, kí ó sì bèèrè ẹni tí a ń pè ni Ṣọ́ọ̀lù, ara Tásọ́sì, sá wò ó, ó ń gbàdúrà."'), 'Acts$9:15': (None, None, 'Acts$9:15 "Ṣùgbọ́n Olúwa wí fún un pé, “Má a lọ; nítorí ohun-èlò ààyò ni òun jẹ́ fún mi, láti gbé orúkọ mi lọ sí iwájú àwọn aláìkọlà, àti àwọn ọba, àti àwọn ọmọ Ísírẹ́lì."'), 'Acts$9:36': (None, None, 'Acts$9:36 "Ọmọ-ẹyìn kan sí wà ní Jópà ti a ń pè ni Tàbítà, èyí tí ó túmọ̀ sí Dọ́kásì; obìnrin yìí pọ̀ ni iṣẹ́ oore, àti ìtọrẹ-àánú ṣíṣe."'), 'Acts$9:41': (None, 'ọwọ', 'Acts$9:41 "Ó sì na ọwọ́ rẹ̀ sí i, ó fà á dìde; nígbà tí ó sì pe àwọn ènìyàn mímọ́ àti àwọn opó, ó fi i lé wọn lọ́wọ́ láàyè."'), 'Acts$10:5': (None, None, 'Acts$10:5 "Sì rán ènìyàn nísinsìnyìí lọ sí Jópà, kí wọn sì pe Símónì wá, ẹni ti àpèlé rẹ̀ ń jẹ́ Pétérù."'), 'Acts$10:9': ('ní', None, 'Acts$10:9 "Ni ijọ́ kéjì bí wọ́n ti ń lọ lọ́nà àjò wọn, tí wọ́n sì súnmọ́ ilé náà, Pétérù gun òkè ilé lọ gbàdúrà ni ìwọ̀n wákàtí kẹfà ọjọ́:"'), 'Acts$10:20': ('lọ', None, 'Acts$10:20 "Njẹ́ dìde, sọ̀kalẹ̀ kí ó sì bá wọn lọ, má ṣe kọminú ohunkohun: nítorí èmi ni ó rán wọn.”"'), 'Acts$10:22': (None, None, 'Acts$10:22 "Wọ́n sì wí pé, “Kọ̀nẹ́líù balógun ọ̀rún, ọkùnrin olóòtọ́, àti ẹni ti ó bẹ̀rù Ọlọ́run, tí ó sì ní orúkọ́ rere lọ́dọ̀ gbogbo orílẹ̀-èdè àwọn Júù, òun ni a ti ọ̀dọ̀ Ọlọ́run kọ́ nípasẹ̀ ańgẹ́lì mímọ́, láti ránṣẹ́ pè ọ́ wá sí ilé rẹ̀ àti láti gbọ́ ọ̀rọ̀ lẹ́nu rẹ.”"'), 'Acts$10:33': ('ṣe', 'wá', 'Acts$10:33 "Nítorí náà ni mo sì ṣe ránṣẹ́ sì ọ lójúkan náà, ìwọ sì ṣeun tí ó fi wá. Gbogbo wa pé níwájú Ọlọ́run nísinsìnyìí, láti gbọ́ ohun gbogbo, ti a pàṣẹ fún ọ láti ọ̀dọ̀ Ọlọ́run wá.”"'), 'Acts$11:4': (None, None, 'Acts$11:4 " Ṣùgbọ́n Pétérù bẹ̀rẹ̀ sí i là á yé wọn lẹ́sẹẹsẹ, wí pé,"'), 'Acts$11:17': ('fun', 'ebun', 'Acts$11:17 "Ǹjẹ́ bí Ọlọ́run sì ti fi irú ẹ̀bùn kan náà fún wọn bí ó ti fi fún àwa pẹ̀lú nígbà tí a gba Jésù Kírísítì Olúwa gbọ́, ta ni èmi tí n ó fi rò pé mo le è de Ọlọ́run ní ọ̀nà?”"'), 'Acts$11:21': (None, None, 'Acts$11:21 "Ọwọ́ Olúwa sì wà pẹ̀lú wọn: ọ̀pọ̀lọpọ̀ ènìyàn sì gbàgbọ́, wọ́n sì yípadà sí Olúwa."'), 'Acts$11:24': ('kun', None, 'Acts$11:24 "Nítorí òun jẹ́ ènìyàn rere, ó sì kún fún Ẹ̀mí Mímọ́ àti fún ìgbàgbọ́; ènìyàn púpọ̀ ni a sì kà kún Olúwa."'), 'Acts$12:17': (None, None, 'Acts$12:17 "Ṣùgbọ́n ó juwọ́ sí wọn pé kí wọn dákẹ́, ó sì ròyìn fún wọn bí Olúwa ti mú òun jáde kúrò nínú túbú. Ó sì wí pé “Ẹ ro èyí fún Jákọ́bù àti àwọn arákùnrin yòókù!” Ó sì jáde, ó lọ sí ibòmíràn."'), 'Acts$13:8': (None, None, 'Acts$13:8 "Ṣùgbọ́n Élímù oṣó (nítorí bẹ́ẹ̀ ni ìtúmọ̀ orúkọ rẹ̀) takò wọ́n, ó ń fẹ́ pa báalẹ̀ ni ọkàn dà kúrò ni ìgbàgbọ́."'), 'Acts$13:17': ('mu', 'ilẹ', 'Acts$13:17 " Ọlọ́run àwọn ènìyàn Ísírẹ́lì yìí yan àwọn baba wa, ó sì gbé àwọn ènìyàn náà lékè, nígbà tí wọ́n ṣe àtìpó ní ilẹ̀ Íjíbítì, apá gíga ni ó sì fi mú wọn jáde kúrò nínú rẹ̀,"'), 'Acts$13:22': ('ni', None, 'Acts$13:22 " Nígbà ti ó sì mú Ṣọ́ọ̀lù kúrò, ó gbé Dáfídì dìde ní ọba fún wọn, ẹni tí ó sì jẹ́rìí rẹ̀ pé, ‘Mo rí Dáfídì ọmọ Jésè ẹni bí ọkàn mi, ti yóò ṣe gbogbo ìfẹ́ mi.’"'), 'Acts$13:32': ('ṣe', None, 'Acts$13:32 "“Àwa sì mú ìyìn rere wá fún yín pé: Ìlérí èyí tí Ọlọ́run ti ṣe fún àwọn baba wa,"'), 'Acts$13:34': ('fun', 'pada', 'Acts$13:34 " Àti ni tí pé Ọlọ́run jí i dìde kúrò nínú òkú, ẹni tí kì yóò tún padà sí ìbàjẹ́ mọ́, ó wí báyí pé: “ ‘Èmi ó fún yín ní ọ̀rẹ́ mímọ́ Dáfídì, tí ó dájú.’"'), 'Acts$13:36': ('ní', None, 'Acts$13:36 "“Nítorí lẹ́yìn ìgbà ti Dáfídì ti sin ìran rẹ tan nípa Ìfẹ́ Ọlọ́run, ó sùn, a sì tẹ́ ẹ ti àwọn baba rẹ̀, ara rẹ̀ sì rí ìdíbàjẹ́."'), 'Acts$13:38': (None, None, 'Acts$13:38 "“Ǹjẹ́ kí ó yé yín, ará pé nípasẹ̀ Jésù yìí ni a ń wàásù ìdáríjì ẹ̀ṣẹ̀ fún yín."'), 'Acts$13:46': (None, 'yipada', 'Acts$13:46 "Pọ́ọ̀lù àti Básébà sì dá wọn lóhùn láìbẹ̀rù pé, “Ẹ̀yin ni ó tọ́ sí pé ki a kọ́kọ́ sọ ọ̀rọ̀ Ọlọ́run fún yín, ṣùgbọ́n bí ẹ̀yin ti ta á nù, tí ẹ sì ka ara yín sí aláìyẹ fún ìyè àìnípẹ̀kun, wò ó, àwa yípadà sọ́dọ̀ àwọn aláìkọlà."'), 'Acts$14:1': ('lọ', None, 'Acts$14:1 "Ó sí ṣe, ni Ìkóníónì, Pọ́ọ̀lù àti Bánábà júmọ́ wọ inú sínágọ́gù àwọn Júù lọ, wọ́n sì sọ̀rọ̀ tó bẹ́ẹ̀ tí ọ̀pọ̀lọpọ̀ àwọn Júù àti àwọn aláìkọlà gbàgbọ́,"'), 'Acts$14:4': ('pin', 'apakan', 'Acts$14:4 "Ṣùgbọ́n àwọn ènìyàn ìlú náà pín sí méjì: apákan dàpọ̀ mọ́ àwọn Júù, apákan pẹ̀lú àwọn àpósítélì."'), 'Acts$14:11': ('ṣe', 'wá', 'Acts$14:11 "Nígbà tí àwọn ènìyàn sì rí ohun tí Pọ́ọ̀lù ṣe, wọ́n gbé ohùn wọn sókè ni èdè Líkáóníà, wí pé, “Àwọn ọlọ́run sọ̀kalẹ̀ tọ̀ wá wá ni àwọ̀ ènìyàn!”"'), 'Acts$14:14': (None, None, 'Acts$14:14 "Ṣùgbọ́n nígbà tí àwọn àpósítélì Bánábà àti Pọ́ọ̀lù gbọ́, wọ́n fa aṣọ wọn ya, wọn sí súré wọ inú àwùjọ, wọn ń ké rara pé:"'), 'Acts$14:25': (None, None, 'Acts$14:25 "Nígbà tí wọn sì ti sọ ọ̀rọ̀ náà ni Págà, wọ́n sọ̀kalẹ̀ lọ sí Ataláyà:"'), 'Acts$14:27': ('ṣe', None, 'Acts$14:27 "Nígbà tí wọ́n sì dé, tí wọ́n sì pé ìjọ jọ, wọ́n ròhìn gbogbo ohun tí Ọlọ́run fi wọ́n ṣe, àti bí ó ti ṣí ilẹ̀kùn ìgbàgbọ́ fún àwọn aláìkọlà."'), 'Acts$15:4': ('ṣe', None, 'Acts$15:4 "Nígbà tí wọn sì dé Jerúsálémù, àti àwọn àpósítélì àti àwọn alàgbà tẹ́wọ́gbà wọ́n, wọ́n sì ròyìn ohun gbogbo tí Ọlọ́run ti ṣe nípasẹ̀ wọn."'), 'Acts$15:13': (None, None, 'Acts$15:13 "Lẹ̀yìn tí wọn sì dákẹ́, Jákọ́bù dáhùn, wí pé, “Ará, ẹ gbọ́ tèmi:"'), 'Acts$15:15': (None, None, 'Acts$15:15 Báyìí ni ọ̀rọ̀ àwọn wòlíì bá ṣe dédé; bí a ti kọ̀wé rẹ̀ pé:'), 'Acts$15:24': ('fun', 'ni', 'Acts$15:24 "Níwọ̀n bí àwa ti gbọ́ pé, àwọn kan ti ó ti ọ̀dọ̀ wa jáde wá ń fi ọ̀rọ̀ yọ yín lẹ́nu, tí wọ́n ń yí yín lọ́kàn po, (wí pé, Ẹ̀yin kò gbọdọ̀ ṣàìmá kọ ilà, àti ṣaima pa òfin Mósè mọ́:) ẹni tí àwa kò fún ní àṣẹ:"'), 'Acts$15:28': ('dara', None, 'Acts$15:28 "Nítorí ó dára lójú Ẹ̀mí Mímọ́, àti lójú wa, kí a má ṣe di ẹrù kà yín, bí kò ṣe àwọn nǹkan wọ̀nyí tí ó ṣe pàtàkì;"'), 'Acts$15:33': ('ní', None, 'Acts$15:33 "Nígbà tí wọn sì pẹ́, díẹ̀, àwọn arákùnrin rán wọn padà lọ ni àlàáfíà sí ọ̀dọ̀ àwọn tí ó rán wọn wá."'), 'Acts$15:36': ('wo', None, 'Acts$15:36 "Lẹ́yìn ọjọ́ mélóòkán. Pọ́ọ̀lù sì sọ fún Bánábà pé, “Jẹ́ kí a tún padà lọ bẹ àwọn arákùnrin wa wò, bí wọn ti ń ṣe sí ni ìlú gbogbo tí àwa ti wàásù ọ̀rọ̀ Olúwa.”"'), 'Acts$15:38': ('lọ', 'iṣẹ', 'Acts$15:38 "Ṣùgbọ́n Pọ́ọ̀lù rò pé, kò yẹ láti mú un lọ pẹ̀lú wọn, ẹni tí ó fi wọn sílẹ̀ ni Páḿfílíà, tí kò sì bá wọn lọ sí iṣẹ́ náà."'), 'Acts$16:3': ('mu', 'ni', 'Acts$16:3 "Òun ni Pọ́ọ̀lù fẹ́ kí ó bá òun lọ; ó sì mú un, ó sì kọ ọ́ ní ilà, nítorí àwọn Júù tí ó wà ní agbégbé wọ̀nyí: nítorí gbogbo wọn mọ̀ pé, Gíríkì ni baba rẹ̀."'), 'Acts$16:5': (None, None, 'Acts$16:5 "Bẹ́ẹ̀ ni àwọn ìjọ sì fẹsẹmúlẹ̀ ní ìgbágbọ́, wọn ṣí ń pọ̀ sí i ní iye lójoojumọ́."'), 'Acts$16:15': ('ile', None, 'Acts$16:15 "Nígbà tí a sí bamitíìsì rẹ̀, àti àwọn ara ilé rẹ̀, ó bẹ̀ wá, wí pé, “Bí ẹ̀yin bá kà mí ni olóòtọ́ sí Olúwa, ẹ wá si ilé mi, kí ẹ sí wọ̀ níbẹ̀!” O sí rọ̀ wá."'), 'Acts$16:16': ('lọ', None, 'Acts$16:16 "Bí àwa tí nlọ ṣí ibi àdúrà náà, ọmọbìnrin kan tí o ní ẹ̀mí ìwosẹ́, pàdé wa, ẹni tí ó fi àfọ̀ṣẹ mú èrè púpọ̀ wá fún àwọn ọ̀gá rẹ̀:"'), 'Acts$16:19': (None, 'ṣe', 'Acts$16:19 "Nígbà tí àwọn ọ̀gá rẹ̀ sì ríì pé, ìgbẹ́kẹ̀lé èrè wọn pin, wọ́n mú Pọ́ọ̀lù àti Sílà, wọn sì wọ́ wọn lọ sí ọjà tọ àwọn aláṣẹ lọ;"'), 'Acts$16:29': (None, None, 'Acts$16:29 "Nígbà tí ó sì beere iná, ó bẹ́ wọ inú ilé, ó ń wárìrì, ó wólẹ̀ níwájú Pọ́ọ̀lù àti Sílà."'), 'Acts$16:36': (None, 'ni', 'Acts$16:36 "Onítúbú sì sọ ọ̀rọ̀ náà fún Pọ́ọ̀lù, wí pé, “Àwọn onídájọ́ ránṣẹ́ pé kí á da yín ṣílẹ̀: ǹjẹ́ nisinsìnyìí ẹ jáde kí ẹ sì máa lọ ní àlàáfíà.”"'), 'Acts$17:1': (None, 'ni', 'Acts$17:1 "Nígbà tí wọn sì ti kọjá Áḿfípólì àti Apolóníà, wọ́n wá sí Tẹsalóníkà, níbi tí sínágọ́gù àwọn Júù wà:"'), 'Acts$17:5': ('mu', 'mú', 'Acts$17:5 "Ṣùgbọ́n àwọn Júù jowú, wọn si fa àwọn jàgídíjàgan nínú àwọn ọmọ ìta ènìyàn mọ́ra, wọ́n gbá ẹgbẹ́ jọ, wọ́n sì ń dá ìlú rú; wọ́n sì kọlu ilé Jásónì, wọ́n ń fẹ́ láti mú wọn jáde tọ àwọn ènìyàn lọ."'), 'Acts$17:6': (None, None, 'Acts$17:6 "Nìgbà tí wọn kò sì rí wọn, wọ́n wọ́ Jásónì, àti àwọn arákùnrin kan tọ àwọn olórí ìlú lọ, wọ́n ń kígbe pé, “Àwọn wọ̀nyí tí o tí dorí ayé kodò wá sí ìhínyìí pẹ̀lú."'), 'Acts$17:9': ('ní', None, 'Acts$17:9 "Nígbà tí wọ́n sì gbà ògo lọ́wọ́ Jásónì àti àwọn ìyókù, wọ́n fi wọ́n ṣílẹ̀ lọ."'), 'Acts$17:24': (None, 'gbe', 'Acts$17:24 " “Ọlọ́run náà tí ó dá ayé àti ohun gbogbo tí ń bẹ nínú rẹ̀, òun náà tí í ṣe Olúwa ọ̀run àti ayé, kì í gbé ilé tí a fi ọwọ́ kọ́;"'), 'Acts$17:30': ('eniyan', None, 'Acts$17:30 Pẹ̀lúpẹ̀lù ìgbà àìmọ̀ yìí ni Ọlọ́run tí gbójú fò dá; ṣùgbọ́n nísìnyìí ó pàṣẹ fún gbogbo ènìyàn níbi gbogbo láti ronúpìwàdà;'), 'Acts$18:9': (None, 'ni', 'Acts$18:9 " Olúwa sì sọ fún Pọ́ọ̀lù lóru ni ojúran pé, “Má bẹ̀rù, sá máa sọ, má sì se pá ẹnu rẹ̀ mọ́:"'), 'Acts$18:22': (None, None, 'Acts$18:22 "Nígbà tí ó sì tí gúnlẹ̀ ni Keṣaríà; tí ó gòkè, tí ó sì kí ijọ, ó sọ̀kalẹ̀ lọ sì Áńtíókù."'), 'Acts$18:27': ('ní', 'gba', 'Acts$18:27 "Nígbà tí ó sì ń fẹ́ kọjá lọ sì Ákáyà, àwọn arakùnrin gbà á ní ìyànjú, wọ́n sì kọ̀wé sí àwọn ọmọ-ẹ̀hìn kí wọ́n gbà á: nígbà tí ó sì dé, ó ràn àwọn tí ó gbàgbọ́ nípasẹ̀ oore-ọfẹ lọ́wọ́ púpọ̀,"'), 'Acts$19:21': ('kọjá', 'lọ', 'Acts$19:21 "Ǹjẹ́ bí nǹkan wọ̀nyí tí parí tan, Pọ́ọ̀lù pínnu nínú ẹ̀mí rẹ̀ pé, nígbà tí òun bá kọjá ní Makedóníà àti Ákáyà, òun ó lọ sí Jerúsálémù, ó wí pé, “Lẹ́yìn ìgbà tí mo bá dé ibẹ̀, èmi kò lè ṣàì má dé Róòmù pẹ̀lú.”"'), 'Acts$19:24': ('ṣe', 'ere', 'Acts$19:24 "Nítorí ọkùnrin kan tí a ń pè ní Démétíríù, alágbẹ̀dẹ fàdákà, tí o máa ń fi fàdákà ṣe ilé-òrìṣà fún Dáyánà, ó mú ère tí kò mọ níwọ̀n wá fún àwọn oníṣọ̀nà;"'), 'Acts$19:26': ('gbo', 'wo', 'Acts$19:26 "Ẹ̀yin sì rí i, ẹ sì gbọ́ pé, kì í ṣe ni Éfésù nìkanṣoṣo ni, ṣùgbọ́n ó fẹrẹ jẹ́ gbogbo Éṣíà, ni Pọ́ọ̀lù yìí ń yí ọ̀pọ̀ ènìyàn lọ́kàn padà, tí ó sì ń dárí wọn wí pé, ohun tí a fi ọwọ́ ṣe, kì í ṣe Ọlọ́run."'), 'Acts$19:30': (None, 'ni', 'Acts$19:30 "Nígbà ti Pọ́ọ̀lù sì ń fẹ́ wọ àárin àwọn ènìyàn lọ, àwọn ọmọ-ẹ̀yìn kọ̀ fún un."'), 'Acts$19:35': (None, 'eniyan', 'Acts$19:35 "Nígbà tí akọ̀wé ìlú sì mú kí ìjọ ènìyàn dákẹ́, ó ní, “Ẹ̀yin ará Éfésù, ta ni ẹni tí ó wà tí kò mọ̀ pé, ìlú ará Éfésù ní í ṣe olùsìn Dáyánà òrìṣà ńlá, àti tí ère tí ó ti ọ̀dọ̀ Júpítérì bọ́ sílẹ̀?"'), 'Acts$19:38': ('jẹ ki', 'ni', 'Acts$19:38 "Ǹjẹ́ nítorí náà tí Démétíríù, àti àwọn oníṣẹ́-ọnà tí o wà pẹ̀lú rẹ̀ bá ní gbólóhùn-asọ̀ kan sí ẹnìkẹ́ni, ilé-ẹjọ́ ṣí sílẹ̀, àwọn onídájọ́ sì ń bẹ: jẹ́ kí wọn lọ fi ara wọn sùn."'), 'Acts$20:13': ('ní', 'gba', 'Acts$20:13 "Nígbà ti àwa sì ṣáájú, àwa sì ṣíkọ̀ lọ sì Ásósì, níbẹ̀ ni a ti lérò láti gba Pọ́ọ̀lù sínú ọkọ̀: nítorí bẹ́ẹ̀ ni ó tí pinnu rẹ̀, òun tìkararẹ̀ ń fẹ́ bá ti ọ̀nà-ẹṣẹ̀ lọ."'), 'Acts$20:35': ('ni', 'gba', 'Acts$20:35 "Nínú ohun gbogbo mo fi àpẹẹrẹ fún un yín pé, nípa ṣíṣe iṣẹ́ bẹ́ẹ̀, ó yẹ kí ẹ máa ran àwọn aláìlera lọ́wọ́, ki ẹ sì máa rántí ọ̀rọ̀ Jésù Olúwa, bí òun tìkararẹ̀ tí wí pé, ‘láti fún ni ní ìbùkún ju láti gbà lọ.’ ”"'), 'Acts$21:1': ('lọ', None, 'Acts$21:1 "Lẹ́yìn tí àwa ti kúrò lọ́dọ̀ wọn, tí a sì ṣíkọ̀, àwa bá ọ̀nà wa lọ tàrà sí Kúúsì. Ní ijọ́ kéjì a sì lọ sí Ródésì, àti gba ibẹ̀ lọ sí Pátarà:"'), 'Acts$21:23': ('ni', None, 'Acts$21:23 Ǹjẹ́ èyí tí àwa ó wí fún ọ yìí ni kí o ṣe: Àwa ni ọkùnrin mẹ́rin tí wọ́n wà lábẹ́ ẹ̀jẹ́;'), 'Acts$21:25': (None, None, 'Acts$21:25 "Ṣùgbọ́n ní ti àwọn aláìkọlà tí ó gbàgbọ́, àwa tí kọ̀wé, a sì ti pinnu rẹ̀ pé, kí wọn pa ara wọn mọ́ kúrò nínú ohun tí a fi rúbọ sí òrìṣà, àti ẹ̀jẹ̀ àti ohun ìlọ́lọ́rùn pa, àti àgbérè.”"'), 'Acts$21:27': (None, 'eniyan', 'Acts$21:27 "Nígbà tí ọjọ́ méje sì fẹ́rẹ̀ pé, tí àwọn Júù tí ó ti Éṣíà wá rí i ni tẹ́ḿpílì, wọ́n rú gbogbo àwọn ènìyàn sókè, wọ́n nawọ́ mú un."'), 'Acts$21:36': ('eniyan', None, 'Acts$21:36 "Nítorí ọ̀pọ̀ ènìyàn rọ́ tẹ̀lé e, wọ́n ń kígbe pé, “Mú un kúrò!”"'), 'Acts$21:40': (None, None, 'Acts$21:40 "Nígbà tí ó sì tí fún un ní ààyè, Pọ́ọ̀lù dúró lórí àtẹ̀gùn, ó sì juwọ́ sí àwọn ènìyàn. Nígbà tí wọ́n sì dákẹ́rọ́rọ́, ó bá wọn sọ̀rọ̀ ní èdè Hébérù, wí pé:"'), 'Acts$22:12': (None, None, 'Acts$22:12 "“Ọkùnrin kan tí a ń pè ní Ananáyà tọ̀ mí wá, ẹni tó jẹ́ olùfọkànṣìn ti òfin, tí ó sì lórúkọ rere lọ́dọ̀ gbogbo àwọn Júù tí ó ń gbé ibẹ̀."'), 'Acts$22:14': ('wo', 'gbo', 'Acts$22:14 "“Nígbà náà ni ó wí pé: ‘Ọlọ́run àwọn baba wa ti yàn ọ́ láti mọ ìfẹ́ rẹ̀, àti láti ri Ẹni Òdodo náà, àti láti gbọ́ ọ̀rọ̀ láti ẹnu rẹ̀."'), 'Acts$22:15': ('ri', 'ẹlẹri', 'Acts$22:15 "Ìwọ yóò jẹ́ ẹlẹ́rìí rẹ̀ fún gbogbo ènìyàn, nínú ohun tí ìwọ tí rí tí ìwọ sì ti gbọ́."'), 'Acts$22:17': ('wá', 'gba', 'Acts$22:17 "“Nígbà tí mo padà wá sí Jerúsálémù tí mo ǹ gbàdúrà ní tẹ́ḿpílì, mo bọ́ sí ojúran,"'), 'Acts$22:22': ('ni', 'gbe', 'Acts$22:22 "Wọ́n sì fi etí sí ọ̀rọ̀ Pọ́ọ̀lù títí tí ó fi sọ ọ̀rọ̀ yìí. Nígbà yìí ni wọ́n gbé ohùn wọn sókè, wọ́n sì kígbe wí pé, “Ẹ mu ẹni yìí kúrò láyé! Kò yẹ kí ó wà láàyè!”"'), 'Acts$22:23': (None, None, 'Acts$22:23 "Bí wọ́n sì ti ń kígbe, tí wọ́n sì ń jú aṣọ wọn káàkiri, tí wọ́n ń ku eruku sí ojú ọ̀run,"'), 'Acts$23:10': ('gba', 'mú', 'Acts$23:10 "Nígbà tí ìyapa sì di ńlá, tí olórí ogun bẹ̀rù kí Pọ́ọ̀lù má baà di fífàya lọ́wọ́ wọn, ó pàṣẹ pé ki àwọn ọmọ-ogun sọ̀kalẹ̀ lọ láti fi ipá mú un kúrò láàrin wọn, kí wọn sì mú un wá sínú àgọ́ àwọn ológun."'), 'Acts$23:18': ('mu', 'mú', 'Acts$23:18 "Ó mú un, ó sì sìn ín lọ sọ́dọ̀ olórí-ogun. Balógun ọ̀rún náà wí pé, “Pọ́ọ̀lù òǹdè pè mi sọ́dọ̀ rẹ̀, ó sí bẹ̀ mí pé ki èmi mú ọmọkùnrin yìí tọ̀ ọ́ wá, ẹni tí ó ní nǹkan láti sọ fún ọ.”"'), 'Acts$23:21': (None, None, 'Acts$23:21 "Nítorí náà má ṣe gbọ́ ti wọn: nítorí àwọn tí ó dènà dè é nínú wọn ju ogójì ọkùnrin lọ, tí wọ́n fi ara wọn bú pé, àwọn kì yóò jẹ, bẹ́ẹ̀ ni àwọn kì yóò mu títí àwọn o fi pa á. Wọ́n sì ti múra tan nísinsìn yìí, wọ́n ń retí ìdáhùn lọ́dọ̀ rẹ.”"'), 'Acts$23:23': (None, None, 'Acts$23:23 "Ó sì pe méjì nínú àwọn balógun ọ̀run sọ́dọ̀ rẹ̀, ó sì wí pé, “Ẹ múra igba ọmọ-ogun sílẹ̀, láti lọ ṣí Kesaríà, àti àádọ́rin ẹlẹ́ṣin, àti igba ọlọ́kọ̀, ní wákàtí kẹ́ta òru."'), 'Acts$23:27': (None, 'ni', 'Acts$23:27 "Àwọn Júù mú ọkùnrin yìí, wọ́n sì ń pète láti pa á: nígbà náà ni mo dé pẹ̀lú ogun, mo sì gbà á lọ́wọ́ wọn nígbà tí mo gbọ́ pé ará Róòmù ni í ṣe."'), 'Acts$23:28': (None, None, 'Acts$23:28 "Nígbà tí mo sì ń fẹ́ mọ ìdí ọ̀ràn tí wọn fi ẹ̀sùn kàn án sí, mo mú un sọ̀kalẹ̀ lọ sí àjọ ìgbìmọ̀ wọn."'), 'Acts$24:5': ('eniyan', None, 'Acts$24:5 "“Nítorí àwa rí ọkùnrin yìí, ó jẹ́ oníjàngbàn ènìyàn, ẹni tí ó ń dá rúkèrúdò ṣílẹ̀ láàrin gbogbo àwọn Júù tí ó wà ní gbogbo ayé. Òun ni aṣáájú búburú kan nínú ẹ̀yà àwọn Násárénè:"'), 'Acts$24:10': (None, 'ṣe', 'Acts$24:10 "Nígbà tí baálẹ̀ ṣẹ́wọ́ sì i pé kí ó sọ̀rọ̀, Pọ́ọ̀lù sì dáhùn wí pe: “Bí mo tí mọ̀ pé láti ọdún mélòó yìí wá, ní ìwọ tí ṣe onídájọ́ orílẹ̀-èdè yìí, nítorí náà mo fi tayọ̀ tayọ̀ wí tí ẹnu mi."'), 'Acts$24:12': (None, None, 'Acts$24:12 "Bẹ́ẹ̀ ni àwọn olùfisùn mi kò rí mi kí ń máa bá ẹnìkẹ́ni jiyàn nínú téḿpílì, bẹ́ẹ̀ ni èmi kò ru àwọn ènìyàn sókè nínú ṣínágọ́gù tàbí ní ibikíbi nínú ìlú:"'), 'Acts$24:16': (None, 'ṣe', 'Acts$24:16 "Nínú èyí ni èmi sì ti ń gbìyànjú láti ní ẹ̀rí-ọkàn tí kò lẹ́ṣẹ̀ sí Ọlọ́run, àti sí ènìyàn nígbà gbogbo."'), 'Acts$24:20': ('jẹ ki', 'ni', 'Acts$24:20 "Bí kò ṣe bẹ́ẹ̀, jẹ́ kí àwọn ènìyàn wọ̀nyí tíkarawọn sọ iṣẹ́ búburú tí wọ́n rí lọ́wọ́ mi, nígbà tí mo dúró níwájú àjọ ìgbìmọ̀."'), 'Acts$24:22': (None, None, 'Acts$24:22 "Nígbà tí Fẹ́líkísì gbọ́ nǹkan wọ̀nyí òye sà yè e ní àyétan nípa ọ̀nà náà; ó tú wọn ká ná, ó ní, “Nígbà tí Lísíà olórí ogun bá ṣọ̀kalẹ̀ wá, èmi ó wádìí ọ̀ràn yín dájú.”"'), 'Acts$24:23': (None, 'ni', 'Acts$24:23 "Ó sì pàṣẹ fún balógun ọ̀run kan kí ó fi Pọ́ọ̀lù sí a bẹ́ ìsọ́, ṣùgbọ́n kí ó fún un ní ààyè, àti pé kí ó má ṣe dá àwọn ọ̀rẹ́ rẹ̀ lẹ́kùn láti máa ṣe ìránṣẹ́ fún un."'), 'Acts$24:26': (None, 'ni', 'Acts$24:26 "Ní àkókó yìí kan náà, ó ń retí pẹ̀lú pé Pọ́ọ̀lù yóò mú owó-ẹ̀yìn wá fún òun, kí òun baà lè dá a sílẹ̀: nítorí náà, a sì máa ránṣẹ́ sì í nígbàkúgbà, a máa bá a sọ̀rọ̀."'), 'Acts$25:6': ('joko', None, 'Acts$25:6 "Lẹ́yìn tí ó sì ti gbé níwọ̀n ọjọ́ mẹ́jọ tàbí mẹ́wàá pẹ̀lú wọn, ó ṣọ̀kalẹ̀ lọ sì Kesaríà, ni ọjọ́ kéjì ó jókòó lórí ìtẹ́ ìdájọ́, ó sì pàṣẹ pé ki a mú Pọ́ọ̀lù wá síwájú òun."'), 'Acts$25:9': (None, 'ṣe', 'Acts$25:9 "Ṣùgbọ́n Fẹ́sítúsì ń fẹ́ láti ní ojúrere lọ́dọ̀ àwọn Júù, ó sì dá Pọ́ọ̀lù lóhùn, wí pe, “Ìwọ ń fẹ́ gòkè lọ sì Jerúsálémù, kí a sì ṣe ẹjọ́ nǹkan wọ̀nyí níbẹ̀ níwájú mi bí?”"'), 'Acts$25:11': ('ni', None, 'Acts$25:11 "Ǹjẹ́ bí mo bá ṣẹ̀, tí mo sí ṣe ohun kan tí ó yẹ fún ikú, èmi kò kọ̀ láti kú: ṣùgbọn bí kò bá sí otítọ́ kan nínú ẹ̀sùn tí àwọn Júù yìí fi mi sùn sí, ẹnìkan kò lẹ́tọ̀ọ́ láti fà mí lé wọn lọ́wọ́. Mo fí ọ̀ràn mi lọ Késárì.”"'), 'Acts$25:14': ('ní', None, 'Acts$25:14 "Bí wọ́n sì tí wà níbẹ̀ lọ́jọ́ púpọ̀, Fẹ́sítúsì mú ọ̀ràn Pọ́ọ̀lù wá síwájú ọba, wí pé, “Fẹ́líkísì fi ọkùnrin kan ṣílẹ̀ nínú túbú."'), 'Acts$25:16': ('ni', None, 'Acts$25:16 "“Àwọn ẹni tí mo sì dá lóhùn pé, kì í ṣe àṣà àwọn ará Róòmù láti dá ẹnìkẹ́ni lẹ́bí, kí ẹni tí a fiṣùn náà tó ko àwọn olùfisùn rẹ̀ lójú, láti lè ri àyè wí tí ẹnu rẹ̀, nítorí ọ̀ràn tí a kà sí i lọ́rùn,"'), 'Acts$25:17': ('joko', None, 'Acts$25:17 "Nítorí náà nígbà tí wọ́n jùmọ̀ wá sí ìhín yìí, èmi kò fi ọ̀rọ̀ náà falẹ̀ rara, níjọ́ kéjì mo jókòó lórí ìtẹ́ ìdájọ́, mo sì pàṣẹ pé kí a mú ọkùnrin náà wá."'), 'Acts$25:18': (None, None, 'Acts$25:18 "Nígbà tí àwọn olùfiṣùn náa dìde, wọn kò ka ọ̀ràn búburú irú èyí tí mo rò sí i lọ́rùn."'), 'Acts$25:24': (None, 'eniyan', 'Acts$25:24 "Fẹ́sítúsì sì wí pé, “Àgírípà Ọba, àti gbogbo ẹ̀yin ènìyàn tí ó wà níhìn pẹ̀lú wa, ẹ̀yin rí ọkùnrin yìí, nítorí ẹni tí gbogbo ìjọ àwọn Júù tí fi ẹ̀bẹ̀ bèèrè lọ́wọ́ mi ni Jerúsálémù àti Kesaríà níhìn yìí, tí wọ́n ń kígbe pé, kò yẹ fún un láti wà láàyè mọ́."'), 'Acts$25:25': ('ṣe', None, 'Acts$25:25 "Ṣùgbọ́n èmi rí i pé, kò se ohun kan tí ó yẹ sí ikú, bí òun tìkárarẹ̀ sí tí fi ọ̀ràn rẹ̀ lọ Aúgọ́sítù, mo tí pinnu láti rán an lọ."'), 'Acts$26:4': ('ibere', None, 'Acts$26:4 "“Àwọn Júù mọ irú ìgbé ayé tí mo ń gbé láti ìgbà èwe mi, láti ìbẹ̀rẹ̀ ayé mi ní orílẹ̀-èdè mi àti ní Jerúsálémù."'), 'Acts$26:6': ('ireti', 'ileri', 'Acts$26:6 Nísinsìn yìí nítorí ìrètí ìlérí tí Ọlọ́run tí ṣe fún àwọn baba wa ni mo ṣe dúró níhìn-ín fún ìdájọ́.'), 'Acts$26:12': (None, None, 'Acts$26:12 “Nínú irú ìrìnàjò yìí ní mo ń bá lọ sí Dámásíkù pẹ̀lú ọlá àti àṣẹ ikọ̀ láti ọ̀dọ̀ àwọn olórí àlùfáà.'), 'Acts$26:16': ('ṣe', 'ni', 'Acts$26:16 " Ṣùgbọ́n dìde, kí o sí fi ẹṣẹ̀ rẹ tẹlẹ̀: nítorí èyí ni mo ṣe farahàn ọ́ láti yàn ọ́ ní ìránṣẹ́ àti ẹlẹ́rìí, fún ohun wọ̀nyí tí ìwọ tí rí nípa mi, àti àwọn ohun tí èmi yóò fí ara hàn fún ọ:"'), 'Acts$26:19': (None, 'ṣe', 'Acts$26:19 "“Nítorí náà, Àgírípà ọba, èmi kò ṣe àìgbọ́ràn sì ìran ọ̀run náà."'), 'Acts$26:22': ('ṣe', None, 'Acts$26:22 "Ṣùgbọ́n bí mo sì tí ri ìrànlọ́wọ́ gbà láti lọ́dọ̀ Ọlọ́run, mo dúró títí ó fí di òní, mo ń jẹ́rìí fún àti èwe àti àgbà, èmi kò sọ ohun mìíràn bí kò ṣe ohun tí àwọn wòlíì àti Móṣè tí wí pé, yóò ṣẹ:"'), 'Acts$27:9': (None, None, 'Acts$27:9 "Nígbà ti a sì ti sọ ọjọ́ púpọ̀ nù, àti pé ìrìn-àjò wa sì ti léwu gan-an nítorí nisinsinyìí ààwẹ̀ ti kọjá lọ, Pọ́ọ̀lù dá ìmọ̀ràn."'), 'Acts$27:12': ('lọ', None, 'Acts$27:12 "Àti nítorí pé èbúté náà kò rọrùn láti lo àkókò òtútù níbẹ̀, àwọn púpọ̀ sí dámọ̀ràn pé, kí a lọ kúrò níbẹ̀, bóyá wọn ó lè làkàkà dé Fóníkè, tí i ṣe èbúté Kírétè ti ó kọjú sí òsì ìwọ̀-oorùn, àti ọ̀tún ìwọ̀-oorùn, láti lo àkókò òtútù níbẹ̀."'), 'Acts$27:16': (None, None, 'Acts$27:16 "Nígbà tí ó sì gbá a lọ lábẹ́ erékùsù kan tí a ń pè Kíláúdà, ó di iṣẹ́ púpọ̀ kí a tó lè súnmọ́ ìgbàjà."'), 'Acts$27:30': ('ní', None, 'Acts$27:30 "Ṣùgbọ́n nígbà tí àwọn atukọ̀ ń wá ọ̀nà láti sá kúrò nínú ọkọ̀, tí wọ́n sì ti sọ ìgbàjá kalẹ̀ sí ojú òkun, bí ẹni pé wọn ń fẹ́ sọ ìdákọ̀ró sílẹ̀ níwájú ọkọ̀."'), 'Acts$27:33': ('gba', 'ni', 'Acts$27:33 "Nígbà tí ilẹ̀ ń mọ́ bọ̀, Pọ́ọ̀lù bẹ gbogbo wọn kí wọn jẹun díẹ̀, ó wí pé, “Òní ni ó di ìjẹrìnlá tí ẹ̀yin ti ń rétí, ti ẹ kò dẹkun gbígbaàwẹ̀, tí ẹ kò sì jẹun."'), 'Acts$27:41': (None, None, 'Acts$27:41 "Nígbà tí wọ́n sì dé ibi tí òkun méjì pàde, wọn fi orí-ọkọ̀ sọlẹ̀: iwájú rẹ̀ sì kàn mọ́lẹ̀ ṣinṣin, ó dúró, kò lè yí, ṣùgbọ́n agbára rírú omi bẹ̀rẹ̀ sí fọ́ ìdí ọkọ̀ náà."'), 'Acts$27:43': ('pa', None, 'Acts$27:43 "Ṣùgbọ́n balógun ọ̀rún ń fẹ́ gba Pọ́ọ̀lù là, ó kọ èrò wọn, ó sì pàṣẹ fún àwọn tí ó lè wẹ̀ kí wọn bọ́ sí òkun lọ sì ilẹ̀."'), 'Acts$28:2': (None, None, 'Acts$28:2 "Kì í ṣe oore díẹ̀ ni àwọn alàìgbédè náà ṣe fún wa: nítorí ti wọ́n dáná, wọ́n sì gbà gbogbo wa sí ọ̀dọ̀ nítorí òjò ń rọ nígbà náà, àti nítorí òtútù."'), 'Acts$28:4': ('ni', 'gbe', 'Acts$28:4 "Bí àwọn aláìgbédè náà sì ti rí ẹranko olóró náà tí ó dì mọ́ ọn lọ́wọ́, wọ́n bá ara wọn sọ pé, “Dájúdájú àpànìyàn ni ọkùnrin yìí, ẹni ti ó yọ nínú òkun tan, ṣùgbọ́n tí ẹ̀san kò sì jẹ́ kí ó wà láàyè.”"'), 'Acts$28:6': ('ṣubu', None, 'Acts$28:6 "Ṣùgbọ́n wọn ń wòye ìgbà tí yóò wù, tàbí tí yóò sì ṣúbu lulẹ̀ kú lójijì: nígbà tí wọ́n wò títí, tí wọn kò sì rí nǹkan kan kí ó ṣe é, wọ́n pa èrò wọn dà pé, òrìṣà kan ni ọkùnrin yìí."'), 'Acts$28:15': ('ri', 'pade', 'Acts$28:15 "Àwọn arákùnrin ibẹ̀ gbúró pé a ń bọ̀, wọ́n sì rìnrìn àjọ títí wọ́n fi dé Api-fórù àti sí ilé-èrò mẹ́ta láti pàdé wa: nígba tí Pọ́ọ̀lù sì rí wọn, ó dúpẹ́ lọ́wọ́ Ọlọ́run, ó sì mú ọkàn le."'), 'Acts$28:17': (None, 'ni', 'Acts$28:17 "Lẹ́yìn ọjọ́ mẹ́ta, Pọ́ọ̀lù pe àwọn olórí Júù jọ: nígbà tí wọ́n sì péjọ, ó wí fún wọn pé, “Ẹ̀yin ara, bí ó ti se pé èmi kò se ohun kan lòdì sí àwọn ènìyàn, tàbí sí àṣà àwọn baba wa, ṣíbẹ̀ wọ́n fi mí lé àwọn ara Róòmù lọ́wọ́ ní òǹdè láti Jerúsálémù wá."'), 'Acts$28:18': (None, 'ni', 'Acts$28:18 "Nígbà tí wọ́n sì wádìí ọ̀ràn mi, wọ́n fẹ́ jọ̀wọ́ mi lọ́wọ́ lọ, nítorí tí wọn kò rí ẹ̀sùn kan tí ó tọ́ sí ikú pẹ̀lú mi."'), 'Acts$28:20': ('ni', None, 'Acts$28:20 "Ǹjẹ́ nítorí ọ̀ràn yìí ni mo ṣe ránṣẹ́ pè yín, láti rí yín àti láti bá yín sọ̀rọ̀ nítorí pé, nítorí ìrètí Ísírẹ́lì ni a ṣe fi ẹ̀wọ̀n yìí dè mí.”"'), 'Acts$28:25': (None, 'okan', 'Acts$28:25 "Nígbà tí ohùn wọn kò ṣọ̀kan láàrin ara wọn, wọ́n túká, lẹ́yìn ìgbà tí Pọ́ọ̀lù sọ̀rọ̀ kan pé, “Ẹ̀mí Mímọ́ sọ òtítọ́ fún àwọn baba yín nígbà tí ó sọ láti ẹnu wòlíì Àìṣáyà wí pé:"'), 'Rom$1:9': ('ṣe', 'ni', 'Rom$1:9 "Ọlọ́run, ẹni tí èmí ń sìn tí mo sì fi gbogbo ọkàn mi jìn fún un láti máa wàásù ìyìnrere ọmọ rẹ̀, bí ó ti ṣe pé ní àìsinmi ni èmí ń rántí yín nígbà gbogbo nínú àdúrà mi"'), 'Rom$1:13': ('ni', None, 'Rom$1:13 " Mo fẹ́ kí ẹ mọ èyí, ẹ̀yin ará mi, pé mo ti gbìyànjú lọ́pọ̀ ìgbà láti tọ̀ yín wá, (ṣùgbọ́n ìdíwọ́ wà fún mi), kí èmi ki ó lè jèrè ọkàn díẹ̀ láàrin yín, gẹ́gẹ́ bí mo ti ní in láàrin àwọn aláìkọlà yóòkù."'), 'Rom$1:17': ('ni', None, 'Rom$1:17 " Nítorí nínú ìyìnrere ni òdodo Ọlọ́rùn ti farahàn, òdodo Ọlọ́run láti ìbẹ̀rẹ̀ dé òpin gẹ́gẹ́ bí a ti kọ ọ́ nínú ìwé Mímọ́ pé, “Olódodo yóò yè nípa ìgbàgbọ́ rẹ̀.”"'), 'Rom$1:20': (None, None, 'Rom$1:20 " Nítorí pé láti ìgbà dídá ayé, gbogbo ohun àìlèfojúrí rẹ̀: bí agbára ayérayé àti ìwà-bí-Ọlọ́run rẹ̀ ni a rí gbangba tí a sì ń fi òye ohun tí a dá mọ̀ ọ́n kí ènìyàn má ba à wá àwáwí."'), 'Rom$1:26': (None, None, 'Rom$1:26 Nítorí èyí yìí ni Ọlọ́run ṣe fi wọ́n fún ìfẹ́ ìwàkíwà: nítorí àwọn obìnrin wọn tilẹ̀ yí ìlò àdánidá padà sí èyí tí ó lòdì sí ti àdánidá:'), 'Rom$1:28': ('fun', 'ni', 'Rom$1:28 "Àti gẹ́gẹ́ bí wọ́n ti kọ̀ láti gba Ọlọ́run nínú ìmọ̀ tí ó tọ́, Ọlọ́run fi wọ́n fún iyè ríra láti ṣe ohun tí kò tọ́ fún wọn láti ṣe:"'), 'Rom$2:5': (None, None, 'Rom$2:5 "Ṣùgbọ́n gẹ́gẹ́ bí líle àti àìronúpìwàdà ọkàn rẹ̀, ìwọ ń to ìbínú jọ fún ara rẹ de ọjọ́ ìbínú àti ìfihàn ìdájọ́ òdodo Ọlọ́run."'), 'Rom$2:14': (None, 'ni', 'Rom$2:14 "Nítorí nígbà tí àwọn aláìkọlà, tí kò ní òfin, bá ṣe ohun tí ó wà nínú òfin nípa ìwà àdánidá, àwọn wọ̀nyí, jẹ́ òfin fún ara wọn bí wọn kò tilẹ̀ ní òfin."'), 'Rom$2:28': (None, None, 'Rom$2:28 " Kì í ṣe èyí tí ó farahàn ní òde ni Júù, bẹ́ẹ̀ ni kì í ṣe èyí tí ó farahàn ní ara ni ìkọlà:"'), 'Rom$3:8': ('wá', 'ṣe', 'Rom$3:8 Èéṣe tí a kò fi ṣe búburú kí rere lè jáde wá? Bí àwọn kan tí ń fi ẹnu àtẹ́ sọ wí pé a ń sọ bẹ́ẹ̀; ti àwọn kan sì ń tẹnumọ́ ọn pé a sọ; àwọn ẹni tí ìdálẹ́bí wọn tọ́.'), 'Rom$3:12': (None, 'di', 'Rom$3:12 "Gbogbo wọn ni ó ti yapa, wọ́n jùmọ̀ di aláìlérè; kò sí ẹni tí ń ṣe rere, kò tilẹ̀ sí ẹnìkan.”"'), 'Rom$3:13': ('ète', None, 'Rom$3:13 “Ibojì tí ó ṣí sílẹ̀ ní Ọ̀fun wọn: ahọ́n wọn di aláìlérè; kò sí ẹni ìtànjẹ.” “Oró paramọ́lẹ̀ ń bẹ lábẹ́ ètè wọn.”'), 'Rom$4:16': ('ṣe', 'ni', 'Rom$4:16 "Nítorí náà ni ó ṣe gbé e karí ìgbàgbọ́, kí ìlérí náà bá a lè sinmi lé oore-òfẹ́, kí a sì lè mú un dá gbogbo irú ọmọ lójú, kì í ṣe fún àwọn tí ń pa òfin mọ́ nìkan, ṣùgbọ́n bí kò ṣe pẹ̀lú fún àwọn ti ó pín nínú ìgbàgbọ́ Ábúráhámù, ẹni tí í se baba gbogbo wa pátapáta,"'), 'Rom$4:19': ('ní', None, 'Rom$4:19 " Ẹni tí kò rẹ̀wẹ̀sì nínú ìgbàgbọ́, nígbà tí ó ro ti ara òun tìkarárẹ̀ tí ó ti kú tan, nítorí tí ó tó bí ẹni ìwọ̀n ọgọ́rún-ún ọdún, àti nígbà tí ó ro ti yíyàgàn inú Sárà:"'), 'Rom$5:7': (None, None, 'Rom$5:7 Nítorí ó ṣọ̀wọ́n kí ẹnìkan tó kú fún olódodo: ṣùgbọ́n fún ènìyàn rere bóyá ẹlòmíràn tilẹ̀ lè dábàá láti kú.'), 'Rom$5:12': (None, 'eniyan', 'Rom$5:12 " Nítorí gẹ́gẹ́ bí ẹ̀ṣẹ̀ ti tipa ọ̀dọ̀ ènìyàn kan wọ ayé, àti ikú nípa ẹ̀ṣẹ̀ bẹ́ẹ̀ ni ikú sì kọjá sórí ènìyàn gbogbo, láti ọ̀dọ̀ ẹni tí gbogbo ènìyàn ti dẹ́sẹ̀:"'), 'Rom$6:8': (None, None, 'Rom$6:8 " Níwọ̀n ìgbà tí ògbólógbòó ara yín tó ń fẹ́ máa dẹ́sẹ̀ ti kú pẹ̀lú Kírísítì, àwa mọ̀ pé, ẹ̀yin yóò pín nínú ìyè titun rẹ̀."'), 'Rom$6:17': (None, 'ni', 'Rom$6:17 "Ọpẹ́ ni fún Ọlọ́run pé, bí ó tilẹ̀ jẹ́ pé ẹ̀yin ti se ìgbọ́ràn, pẹ̀lú ọkàn yín, sí ẹ̀kọ́ èyí tí Ọlọ́run fi lé yín lọ́wọ́."'), 'Rom$6:22': ('ṣe', 'ni', 'Rom$6:22 "Ṣùgbọ́n báyìí, ẹ ti bọ́ kúrò lọ́wọ́ agbára ẹ̀ṣẹ̀, ẹ sì ti di ìránṣẹ́ Ọlọ́run àwọn ìbùkún rẹ̀ sí yin ni ìwà mímọ́ àti ìyè tí kò nípẹ̀kun."'), 'Rom$7:5': (None, 'iṣẹ', 'Rom$7:5 " Nítorí ìgbà tí ìfẹ́kúfẹ́ ara ń darí wa, ar ẹ̀ṣẹ̀ tí ó wà nípa òfin, ó ń ṣisẹ́ nínú àwọn ara wa láti so èso fún ikú."'), 'Rom$7:12': ('dara', None, 'Rom$7:12 " Bẹ́ẹ̀ ni mímọ́ ni òfin, mímọ́ sì ni àṣẹ, àti òdodo, àti dídára."'), 'Rom$8:12': (None, None, 'Rom$8:12 "Nítorí náà ará, kò jẹ́ ọ̀rọ̀ iyàn fún un yín láti se nǹkan tí ara ẹ̀ṣẹ̀ àtijọ́ ń rọ̀ yín láti se."'), 'Rom$8:16': (None, None, 'Rom$8:16 " Nítorí ẹ̀mí mímọ́ ń sọ̀rọ̀ ìjìnlẹ̀ nínú ọkàn wa, ó sì ń sọ fún wa pé, ní tòótọ́, àwa jẹ́ ọmọ Ọlọ́run."'), 'Rom$8:28': ('mọ', None, 'Rom$8:28 "Àwa sì mọ̀ pé ohun gbogbo ni ó ń siṣẹ́ pọ̀ sí rere fún àwọn tí ó fẹ́ Ọlọ́run, àní fún àwọn ẹni tí a pè gẹ́gẹ́ bí ìpinnu rẹ̀."'), 'Rom$9:1': (None, 'ni', 'Rom$9:1 "Ẹ̀yin ọmọ Ísírẹ́lì, ìbátan mi nípa ti ara, ó wù mí púpọ̀ láti rí i pé ẹ gba Kírísítì gbọ́."'), 'Rom$9:10': ('ṣe', None, 'Rom$9:10 " Kì sì íṣe kìkì èyí; Ṣùgbọ́n nígbà tí Rèbékà pẹ̀lú lóyún fún ẹnìkan, fún Ísákì baba wa;"'), 'Rom$9:22': (None, 'ṣe', 'Rom$9:22 " Ǹjẹ́ bí Ọlọ́run bá fẹ́ fi ìbínú rẹ̀ hàn ń kọ́? Tí ó sì fẹ́ sọ agbára rẹ̀ di mímọ̀, tí ó sì mú sùúrù púpọ̀ fún àwọn ohun èlò ìbínú tí a ṣe fún ìparun;"'), 'Rom$9:27': (None, None, 'Rom$9:27 " Ìsáíà sì kí gbe nnítorí Ísírẹ́lì pé: “Bí iye àwọn ọmọ Ísírẹ́lì bá rí bí iyanrìn etí òkun, apákan ni ó gbàlà."'), 'Rom$10:2': ('ni', None, 'Rom$10:2 " Mo mọ irú ìfẹ́ àti ìtara tí wọ́n ní sí ọlá àti ògo Ọlọ́run, ṣùgbọ́n ọ̀nà ìsìnà ni wọn ń gbà wá Ọlọ́run;"'), 'Rom$10:3': ('ni', None, 'Rom$10:3 " Ìdí ni pé, wọ́n ń gbìyànjú láti hu ìwà rere nípa pípa òfin àti àṣà ìbílẹ̀ àwọn Júù mọ́, kí wọn báà lè rí ojú rere Ọlọ́run. Kò yé wọn pé, Kírísítì ti kú láti mú wọn dọ́gba pẹ̀lú Ọlọ́run. Òfin àwọn Júù àti àṣà ìbílẹ̀ wọn kì í ṣe ọ̀nà tí Ọlọ́run lè fi gba ènìyàn là."'), 'Rom$10:5': ('ni', None, 'Rom$10:5 " Nítorí pé Mósè kọ ọ́ pé, “Kí ènìyàn tó lè rí ìdáríjì ẹ̀ṣẹ̀ àti ìgbàlà (nípa òfin) gbà, ó ní láti ja àjàṣẹ́gun nínú gbogbo ìdánwò, kí ó sì wà láì dá ẹ̀ṣẹ̀ kan soso nínú gbogbo ọjọ́ ayé rẹ̀.”"'), 'Rom$11:5': (None, None, 'Rom$11:5 " ẹ́gẹ́ bẹ́ẹ̀ sì ni li àkókò ìsinsin yìí pẹ̀lú, apákan wà nípa ìyànfẹ́ ti ore-ọ̀fẹ́."'), 'Rom$11:8': ('wo', None, 'Rom$11:8 " Èyí ni ìwé Mímọ́ ń wí nígbà tí ó sọ pé: “Ọlọ́run ti fi oorun kùn wọ́n, ó sì dí ojú àti etí wọn, wọn kò sì le ní ìmọ̀ Kírísítì nígbà tí a bá ń sọ̀rọ̀ rẹ̀ síwọn. Bẹ́ẹ̀ gẹ́gẹ́ ni ó sì rí títí di ọjọ́ òní.”"'), 'Rom$11:9': (None, None, 'Rom$11:9 " Ohun kan náà yìí ni Dáfídì ọba sọ̀rọ̀ rẹ̀ wí pé: “Kí oúnjẹ àti àwọn nǹkan mèremère wọn tàn wọ́n, láti rò pé wọ́n rí ojú rere Ọlọ́run. Jẹ́ kí àwọn nǹkan wọ̀nyí padà dojú ìjà kọ wọ́n, kí a bá à lè fi òtítọ́ wó wọn túútúú."'), 'Rom$11:30': (None, None, 'Rom$11:30 "Nítorí gẹ̀gẹ̀ bí ẹyin kò ti gba Ọlọ́run gbọ́ rí, ṣùgbọ́n nísinsin yìí tí ẹ̀yin rí ànú gbà nípa àìgbàgbọ́ wọn."'), 'Rom$12:18': (None, None, 'Rom$12:18 " Bí ó le ṣe, bí ó ti wà ní ipa ti yín, ẹ má wà ní àlàáfíà pẹ̀lú gbogbo ènìyàn."'), 'Rom$12:20': ('ebi', 'oungbe', 'Rom$12:20 " Ṣùgbọ́n bí ebi bá ń pa ọ̀ta rẹ: “Fún un ní oúnjẹ; bí oùngbẹ bá ń gbẹ ẹ́, fún un ní omi mu; ní ṣíṣe bẹ́ẹ̀ ìwọ ó kó ẹ̀yín iná lé e ní orí.”"'), 'Rom$13:12': (None, None, 'Rom$13:12 " Òru bù kọjá tan, ilẹ̀ sì fẹ́rẹ mọ́: nítorí náà ẹ jẹ́ kí a bọ́ ara iṣẹ́ òkùnkùn sílẹ̀, kí a sì gbé ìhámọ́ra ìmọ́lẹ̀ wọ̀."'), 'Rom$14:8': ('ni', None, 'Rom$14:8 " Bí a bá wà láààyè, a wà láààyè fún Olúwa; bí a bá sì kú, a kú fún Olúwa. Nítorí náà, yálà ní kíkú tàbí ní yíyè, ti Olúwa ni àwá jẹ́."'), 'Rom$14:11': ('ni', None, 'Rom$14:11 " A ti kọ ìwé rẹ̀ pé: “ ‘Níwọ̀n ìgbà tí mo wà láàyè,’ ni Olúwa wí ‘gbogbo eékún ni yóò wólẹ̀ fún mi; gbogbo ahọ́n ni yóò jẹ́wọ́ fún Ọlọ́run.’ ”"'), 'Rom$15:5': ('gẹgẹ bi', None, 'Rom$15:5 " Kí Ọlọ́run, ẹni tí ń fún ni ní sùúrù àti ìtùnú fún yin ní ẹ̀mí ìrẹ́pọ̀ láàrin ara yín gẹ́gẹ́ bí i Jésù Kírísítì,"'), 'Rom$15:8': ('ṣe', None, 'Rom$15:8 "Mo sì wí pé, a rán Kírísítì láti ṣe ìránṣẹ́ àwọn tí se Júù nítorí òtítọ́ Ọlọ́run, láti fi ìdí àwọn ìlérí tí a ti ṣe fún àwọn baba múlẹ̀,"'), 'Rom$15:15': (None, 'ni', 'Rom$15:15 "Mo ti fi ìgboyà kọ̀wé sí yín lórí àwọn kókó ọ̀rọ̀ kan, bí ẹni ti ń rán yín létí àwọn kókó ọ̀rọ̀ náà, nítorí oore-ọ̀fẹ́ tí a ti fifún mi láti ọ̀dọ̀ Ọlọ́run"'), 'Rom$15:18': ('ni', 'ṣe', 'Rom$15:18 " Èmi kò sa à gbọdọ̀ sọ ohun kan bí kò se èyí tí Kírisítì ti ọwọ́ mi se, ní títọ́ àwọn aláìkọlà sọ́nà láti ṣe ìgbọ́ran sí ọ̀rọ̀ Ọlọ́run nípa ọ̀rọ̀ àti ìṣe mi:"'), 'Rom$15:20': ('wo', None, 'Rom$15:20 " Ó jẹ́ èrò mi ní gbogbo ìgbà láti wàásù ìyìn rere Kírísítì ní ibi gbogbo tí wọn kò tí i gbọ́ nípa rẹ̀, kí èmi kí ó má se máa mọ àmọlé lórí ìpìlẹ̀ ẹlòmíràn."'), 'Rom$15:22': ('ni', None, 'Rom$15:22 Ìdí nì yìí tí ààyè fi há pẹ́ tó bẹ́ẹ̀ fún mi kí n tó wa bẹ̀ yín wò.'), 'Rom$15:23': (None, None, 'Rom$15:23 " Ṣùgbọ́n báyìí tí kò tún sí ibòmíràn fún mi mọ́ ní agbègbè yìí, tí èmi sì ti ń pòùngbẹ láti ọdún púpọ̀ sẹ́yìn láti tọ̀ yín wá,"'), 'Rom$15:30': (None, 'ni', 'Rom$15:30 "Èmí rọ̀ yín, ẹ̀yin ará, nítorí Olúwa wa Jésù Kírísítì, àti nítorí ìfẹ́ Ẹ̀mí, kí ẹ̀yin kí ó kún mi láti bá mi làkàkà nínú àdúrà yín sí Ọlọ́run fùn mi."'), 'Rom$16:3': ('ni', None, 'Rom$16:3 " Ẹ kí Pìrìsílà àti Àkúílà, àwọn tí ó ti jẹ́ alábásiṣẹ́pọ̀ mi nínú Kírísítì Jésù."'), 'Rom$16:17': ('ni', 'fa', 'Rom$16:17 " Èmí rọ̀ yín, ara, kí ẹ máa sọ àwọn tí ń fa ìyapa, àti àwọn tí ń mú ohun ìkọ̀sẹ̀ wá sí ọ̀nà yín, èyí tí ó lòdì sí ẹ̀kọ́ tí ẹ̀yin kọ́. Ẹ yà kúrò ní ọ̀dọ̀ wọn."'), 'Rom$16:18': (None, None, 'Rom$16:18 "Nítorí irú àwọn ẹni bẹ́ẹ̀ kò sin Kírísítì Olúwa wa, bí kò se ikùn ara wọn. Nípa ọ̀rọ̀ geere àti ọ̀rọ̀ dídùndídùn ni wọ́n fi ń yí àwọn aláìmọ̀kan ní ọkàn padà."'), '1Cor$1:4': ('fi fun', None, '1Cor$1:4 Nígbà gbogbo ni mo ń dúpẹ́ lọ́wọ́ Ọlọ́run fún oore-ọ̀fẹ́ rẹ̀ tó fi fún un yín nínú Kírísítì Jésù.'), '1Cor$1:10': (None, None, '1Cor$1:10 "Mo bẹ̀ yín ẹ̀yin ara, ní orúkọ Olúwa wa Jésù Kírísítì, pé kí gbogbo yín fohùnsọ̀kan kí ó máa ṣe sí ìyàpá láàrin yín, àtipé kí a lè ṣe yín pé ní inú àti ìmọ̀ kan náà."'), '1Cor$1:14': ('ní', None, '1Cor$1:14 Inú mi dún púpọ̀ pé èmi kò tẹ ẹnikẹ́ni nínú yín bọmi yàtọ̀ sí Kírísípù àti Gáíù.'), '1Cor$1:26': (None, None, '1Cor$1:26 "Ará, ẹ kíyèsí ohun tí nígbà tí a pè yín. Bí ó ti ṣe pé, kì í ṣe ọ̀pọ̀ àwọn ọlọ́gbọ́n ènìyàn nípa ti ara, kí í ṣe ọ̀pọ̀ àwọn alàgbà, kì ì ṣe ọ̀pọ̀ àwọn ọlọ́lá ni a pè."'), '1Cor$2:4': (None, 'eniyan', '1Cor$2:4 " Ìwáàsu mi àti ẹ̀kọ́ mi, kì í ṣe nípa ọgbọ́n ènìyàn àti ọ̀rọ̀ tí a fi ń yí ènìyàn lọ́kàn padà, bí kò ṣe nípa ìfihàn agbára Ẹ̀mí."'), '1Cor$2:6': (None, None, '1Cor$2:6 " Ṣùgbọ́n àwa ń sọ̀rọ̀ ọgbọ́n láàrin àwọn tí a pè; ṣùgbọ́n kì í ṣe ọgbọ́n ti ayé yìí tàbí ti àwọn olórí ayé yìí, èyí tí yóò di asán"'), '1Cor$2:12': ('fi fun', 'ni', '1Cor$2:12 " Àwa kò gbà ẹ̀mí ti ayé yìí, bí kò ṣe Ẹ̀mí ẹni tí ó ti ọ̀dọ̀ Ọlọ́run wa, kí a lè ní òye ohun tí Ọlọ́run fi fún wa lọ́fẹ̀ẹ́."'), '1Cor$3:2': (None, 'ni', '1Cor$3:2 " Wàrà ni mo ti fi bọ́ yín, kì í ṣe oúnjẹ; nítorí ẹ kò í tí le gbà á nísinsin yìí náà, ẹ kò í tí le gbà a."'), '1Cor$3:6': (None, 'ni', '1Cor$3:6 " Èmi gbìn, Àpólò ń bomirin; ṣùgbọ́n Ọlọ́run ni ń mú ìbísí wá."'), '1Cor$3:10': ('fi fun', None, '1Cor$3:10 " Nípa oore-ọ̀fẹ́ tí Ọlọ́run tí fi fún mi, gẹ́gẹ́ bí ọlọ́gbọ́n ọ̀mọ̀lé, mo ti fi ìpìlẹ̀ ilé lélẹ̀, ẹlòmíràn sì ń mọ lé e, ṣùgbọ́n kí olúkúlùkù kíyèsára bí yóò ṣe mọ ọ́n lé e."'), '1Cor$3:12': (None, 'kọ', '1Cor$3:12 "Ǹjẹ́ bí ẹnikẹ́ni bá fí wúrà, fàdákà, òkúta olówó-iyebiye, igi, koríko, àgékù koríko mọ lé orí ìpìlẹ̀ yìí."'), '1Cor$3:21': (None, 'eniyan', '1Cor$3:21 Nítorí kí ẹnikẹ́ni má ṣe ṣògo nínú ènìyàn. Nítorí tí yín ni ohun gbogbo.'), '1Cor$4:5': ('ni', 'ṣe', '1Cor$4:5 " Nítorí náà, kí ẹ má ṣe se ìdájọ́ ohukóhun, kí Olúwa tó padà dé, ẹni tí yóò mú ohunkóhun tí ó farasin wá sí ìmọ́lẹ̀, tí yóò sì fi ìmọ̀ ọkàn ènìyàn hàn, nígbà náà ni olukúlukù yóò sì ní ìyìn tirẹ̀ lọ̀dọ́ Ọlọ́run."'), '1Cor$4:9': (None, None, '1Cor$4:9 " Nítorí mo rò pé Ọlọ́run ń fi àwa àpósítélì hàn ní ìkẹyin bí ẹni tí a dá lẹ́bi ikú bí àwọn, nítori tí a fi wá ṣe ìran wò fún àwọn ènìyàn àti àwọn ańgẹ́lì ati gbogbo ayé."'), '1Cor$4:11': (None, 'ni', '1Cor$4:11 " Títí di wákàtí yìí ni a ń rìn kiri nínú ebi àti òùngbẹ, a wọ aṣọ àkísà, tí a sì ń lù wa, tí a kò sì ní ibùgbé kan."'), '1Cor$4:17': ('ni', None, '1Cor$4:17 " Nítorí náà ni mo ṣe rán Tìmótíù sí i yín, ẹni tí í ṣe ọmọ mi olùfẹ́ àti olódodo nínú Olúwa, ẹni tí yóò máa mú yín rántí ọ̀nà mi tí ó wà nínú Kírísítì, gẹ́gẹ́ bí mo ti ń kọ́ni nínú gbogbo ìjọ níbi gbogbo."'), '1Cor$5:2': (None, 'ni', '1Cor$5:2 "Ẹ̀yin ń se ìgberaga, kí ni ṣe tí ojú kò tì yín, kí ẹ sì kún fún ìbànújẹ́, kí ẹ sì rí i pé ẹ yọ ọkùnrin náà tí ó hu ìwà yìí kúrò láàrin àwọn ọmọ ìjọ yín?"'), '1Cor$5:3': (None, 'ni', '1Cor$5:3 " Lóòtọ́ èmi kò sí láàrin yín, ṣùgbọ́n mo wà lọ́dọ̀ yín nínú ẹ̀mí àti pé, ní orúkọ Olúwa Jésù Kírísítì, mo tí ṣe ìdájọ̀ lórí irú ẹni bẹ́ẹ̀, bí ẹni pé mo wá láàrin yín."'), '1Cor$7:2': (None, 'ni', '1Cor$7:2 Ṣùgbọ́n nítorí àgbérè pọ̀ tó bẹ́ẹ̀ gẹ́ẹ̀ kí ọkùnrin kọ̀ọ̀kan gbéyàwó tirẹ̀. Bẹ́ẹ̀ ni kí obìnrin kọ̀ọ̀kan ní ọkọ tirẹ̀.'), '1Cor$7:25': ('ni', 'fun', '1Cor$7:25 "Ṣùgbọ́n nípa ti àwọn wúndíá: èmi kò ní aṣẹ kankan láti ọ̀dọ̀ Olúwa, ṣùgbọ́n mo wí fún yín ní ìdájọ́ bí ẹni tí ó rí àánú Olúwa gbà láti jẹ́ olódodo."'), '1Cor$7:26': (None, None, '1Cor$7:26 "Nítorí náà mo rò pé èyí dára nítorí wàhálà ìsinsinyìí, èyi nì ni pé o dára fún ènìyàn kí ó wà bí ẹ ṣe wà."'), '1Cor$7:29': (None, 'ni', '1Cor$7:29 " Òun ti mo ń wí ará, ni pé kúkúrú ni àkókò, láti ìsinsinyìí lọ, ẹni tí ó ni aya kí ó dàbí ẹni tí kò ní rí;"'), '1Cor$7:37': (None, None, '1Cor$7:37 "Ṣùgbọ́n ẹni tí ó dúró sinsin ni ọkàn rẹ̀, tí kò ní àìgbọ́dọ̀ má ṣe, ṣùgbọ́n tí ó ní agbára lóri ìfẹ́ ara rẹ̀, tí ó sì ti pinnu ní ọkàn rẹ̀ pé, òun ó pa wúndíá ọmọbìnrin òun mọ́, yóò ṣe rere."'), '1Cor$7:38': (None, None, '1Cor$7:38 "Bẹ́ẹ̀ sì ní ẹni tí ó fi wúndíá ọmọbìnrin fún ni ní ìgbéyàwó, ó ṣe rere; ṣùgbọ́n ẹni tí kò fi fún ni ní ìgbéyàwó ṣe rere jù lọ."'), '1Cor$8:3': ('mọ', None, '1Cor$8:3 " Ṣùgbọ́n ẹni tí ó fẹ́ràn Ọlọ́run nítòótọ́, òun ni ẹni tí a ṣí lójú sí ìmọ̀ àti òye Ọlọ́run."'), '1Cor$9:3': (None, 'idahun', '1Cor$9:3 Èyí ni ìdáhùn mi sí àwọn tí ń béèrè ẹ̀tọ́ Àpósítélì mi.'), '1Cor$9:13': (None, None, '1Cor$9:13 " Ǹjẹ́ ó yé e yín pé Ọlọ́run sọ fún àwọn tí ó ń siṣẹ́ nínú tẹ́ḿpìlì pé kí wọ́n mú oúnjẹ tàbí àwọn ẹ̀bùn tí wọ́n mú wá fún òun, làti fi se ìtọ́ju ara wọn? Àti àwọn tí ń dúró tí pẹpẹ wọn a máa se àjọpín pẹ̀lú pẹpẹ."'), '1Cor$9:15': ('ni', None, '1Cor$9:15 " Ṣíbẹ̀síbẹ̀ n kò ì tí ì lo iru àwọn ẹ̀tọ́ wọ̀nyí rí. Bẹ́ẹ̀ ni èmi kò sì kọ lẹ́tà yìí láti fi sọ fún un yín pé, mo fẹ́ bẹ̀rẹ̀ sí í gbà irú nǹkan bẹ̀ẹ́ lọ́wọ́ yín. Kí a sọ òtítọ́, ó sàn fún mi láti kú sínú ebi ju pé kí n sọ ayọ̀ tí mo ní láti wàásù nù."'), '1Cor$9:19': (None, 'ere', '1Cor$9:19 "Bí mo ti jẹ́ òmìnira tí ń kò sì dara pọ̀ mọ́ ẹnikẹ́ni, mo sọ ara mi di ẹrú lọ́dọ̀ gbogbo ènìyàn, láti lè jèrè ọ̀pọ̀lọ́pọ̀ sí i."'), '1Cor$9:22': (None, 'ere', '1Cor$9:22 " Nígbà tí mo bá wà láàrin àwọn aláìlera, èmi náà yóò di aláìlera, kí èmi lé jèrè àwọn aláìlera. Mo di ohun gbogbo fún gbogbo ènìyàn, kí èmi baà lè gba díẹ̀ là lábẹ́ bí ó ti wù kí ó rí."'), '1Cor$9:24': (None, 'ije', '1Cor$9:24 " Ẹ̀yin kò mọ̀ pé, olúkúlùkù ẹni tí ó ní ipa nínú rẹ̀ ni ó máa ń sáré, ṣùgbọ́n ẹnìkan ṣoṣo ni ń gba ipò kìn-ín-ní. Nítorí náà, ẹ sá eré ìje yín kí ẹ baà le borí."'), '1Cor$9:26': (None, None, '1Cor$9:26 "Nítorí náà, mo ń sá eré ìje lọ sójú àmì, kì í ṣe bí ẹni ti kò dá lóju. Mò ń jà kí n lè borí, kì í ṣe bí ẹni tí ń bá afẹ́fẹ́ jà."'), '1Cor$10:1': (None, 'jẹ', '1Cor$10:1 " Nítorí èmi kò fẹ́ kí ẹ̀yin jẹ́ òpè si òtítọ́, ẹ̀yin arakùnrin ọ̀wọ́n, a kó gbọdọ̀ gbàgbé ohun tí ó ṣẹlẹ̀ sí àwọn ènìyàn wa nínú ihà. Ọlọ́run samọ̀nà wọn nípa rírán ìkùúkù ṣíwájú wọn, ó sì sìn wọ́n la omi òkun pupa já."'), '1Cor$10:13': (None, None, '1Cor$10:13 " Kò sí ìdànwò kan tí ó ti bá yín, bí kò ṣe irú èyí tí ó ti bá ènìyàn rí, ṣùgbọ́n olódodo ni Ọlórun, ẹni ti kì yóò jẹ́ kí á dán an yín wò ju bí ẹ̀yin ti le gbà, ṣùgbọ̀n tí yóò sì ṣe ọ̀nà àbáyọ pẹ̀lú nínú ìdánwò náà, kí ẹ́yin baà lè faradà á."'), '1Cor$11:10': ('ni', None, '1Cor$11:10 "Nítorí ìdí èyí àti nítorí àwọn ańgẹ́lì, ni obìnrin ṣe gbọdọ̀ ní àmì àṣẹ rẹ̀ ní orí rẹ̀."'), '1Cor$11:15': ('fi fun', 'ni', '1Cor$11:15 "Ṣùgbọ́n bí obìnrin bá ní irun gígún, ògo ni ó jẹ́ fùn un nítorí irun gígùn tí a fi fún un jẹ́ ìbòrí fún-ún."'), '1Cor$11:23': (None, 'ni', '1Cor$11:23 "Nítorí èyí tí èmi gbà lọ́wọ́ Olúwa ni mo ti fi fún un yin. Ní alẹ́ ọjọ́ tí Júdásì fi hàn, Olúwa Jésù Kírísítì mú búrẹ́dí."'), '1Cor$11:26': ('wá', 'ṣe', '1Cor$11:26 " Nítorí nígbákùúgbà tí ẹ bá ń jẹ lára àkàrà yìí, tí ẹ sì ǹ mu nínú kọ́ọ̀bù yìí, ni ẹ tún sọ nipa ikú Olúwa. Ẹ máa ṣe eléyìí títí yóò fi padà dé."'), '1Cor$12:13': ('ṣe', 'ni', '1Cor$12:13 " Nítorí pé nínú Ẹ̀mí kan ní a ti bamitísì gbogbo wa sínú ara kan, ìbáà ṣe Júù, ìbaà ṣe Gíríkì, ìbáà ṣe ẹrú, ìbaà ṣe òmìnira, gbogbo wa ni a mu nínú Ẹ̀mí Mímọ́ kan náà."'), '1Cor$12:24': (None, 'ni', '1Cor$12:24 "Nítorí pé àwọn ibi tí ó ní ẹ̀yẹ ní ara wa kò nílò ìtọ́jú ọ̀tọ̀. Ṣùgbọ́n Ọlọ́run ti pa gbogbo ẹ̀yà ara pọ̀ sọ̀kan lọ́nà kan, ó sì ti fi ẹ̀yẹ tó ga jùlọ fún ibi tí ó ṣe aláìní."'), '1Cor$13:3': ('fun', None, '1Cor$13:3 "Bí mo bá fi gbogbo nǹkan tí mo ní tọrẹ fún àwọn aláìní, tí mo sì fi ara mi fún ni láti sùn ṣùgbọ́n tí n kò ní ìfẹ̀, kò ní èrè kan fún mi."'), '1Cor$13:4': (None, None, '1Cor$13:4 "Ìfẹ́ a máa mú sùúrù, ìfẹ́ a máa ṣeun, ìfẹ̀ kì í ṣe ìlara, ìfẹ́ kì í fẹ̀, ìfẹ́ kì í sọ̀rọ̀ ìgbéraga."'), '1Cor$13:10': ('ṣe', None, '1Cor$13:10 "Ṣùgbọ́n nígbà tí èyí ti o pé bá dé, èyí tí í ṣe tí apákan yóò dópin."'), '1Cor$14:2': (None, 'eniyan', '1Cor$14:2 "Nítorí ẹni tí ń sọ̀rọ̀ ní èdè àìmọ̀, kò bá ènìyàn sọ̀rọ̀ bì ko ṣe Ọlọ́run: nítorí kó sí ẹni tí ó gbọ́; ṣùgbọ́n nípá ti Ẹ̀mí ó ń sọ ohun ìjìnlẹ̀;"'), '1Cor$14:24': (None, 'wá', '1Cor$14:24 "Ṣùgbọ́n bí gbogbo yín bá ń sọtẹ́lẹ̀, ti ẹnìkan tí kò gbàgbọ́ tàbi tí ko ni ẹ̀kọ́ bá wọlé wá, gbogbo yin ní yóò fi òye ẹ̀ṣẹ̀ yé e, gbogbo yin ní yóò wádìí rẹ̀."'), '1Cor$15:1': (None, None, '1Cor$15:1 "Ǹjẹ́ ará, èmi ń sọ ìyinrere náà dí mímọ̀ fún un yín, tí mo ti wàásù fún un yín, èyí tí ẹ̀yin pẹ̀lú ti gbá, nínú èyí tí ẹ̀yin sì dúró."'), '1Cor$15:24': (None, 'ni', '1Cor$15:24 "Nígbá náà ni òpín yóò dé, nígbà tí ó bá ti fi ìjọba fún Ọlọ́run Baba, nígbà tí ó bá pá gbogbo àṣẹ àti gbogbo ọlá àti agbára run."'), '1Cor$15:26': (None, None, '1Cor$15:26 Ikú ní ọ̀ta ìkẹ́yìn tí a ó párun'), '1Cor$15:49': (None, 'ni', '1Cor$15:49 "Bí àwa ó sì rú àwòrán ẹni erùpẹ̀, bẹ́ẹ̀ ni àwa ó sì rú àworán ẹni ti ọ̀run."'), '1Cor$15:51': (None, None, '1Cor$15:51 "Kíyesí í, ohun ìjìnlẹ̀ ni mo sọ fún un yín: gbogbo wá kì yóò sùn, ṣùgbọ́n gbogbo wá ní a ó paláradà,"'), '1Cor$15:54': (None, None, '1Cor$15:54 " Ṣùgbọ́n nígbá tí ará ìdíbàjẹ́ yìí bá ti gbé àìdíbàjẹ́ wọ̀, ti ara kíkú yìí bá sí ti gbé àìkú wọ̀ bẹ́ẹ̀ tan, nígbá náà ni ọ̀rọ̀ ti a kọ yóò ṣẹ pé, “A gbé ikú mí ní iṣẹ́gun.”"'), '1Cor$16:1': ('fi fun', 'ni', '1Cor$16:1 " Ǹjẹ́ ní ti ìdáwó fún àwọn ènìyàn mímọ́, bí mo tí fi fún àwọn ìjọ Gálátíà, bẹ́ẹ̀ gẹ́gẹ́ ní kí ẹ ṣe."'), '1Cor$16:5': ('wá', None, '1Cor$16:5 " Ṣùgbọ́n èmi yóò tọ̀ yin wá, nígbà tí èmí bá ti kọjá láàrin Makedóníà: nítorí èmi yóò kọjá láàrin Makedóníà."'), '1Cor$16:10': ('wá', 'ṣe', '1Cor$16:10 " Ǹjẹ́ bí Tímótíù bá dé, ẹ jẹ́ kí ó wà lọ́dọ̀ yín láìbẹ̀rù: nítorí òun ń ṣé iṣẹ́ Olúwa, bí èmi pẹ̀lú ti ń ṣe."'), '2Cor$1:8': (None, 'ni', '2Cor$1:8 "Ará, àwa kò fẹ́ kí ẹ̀yin jẹ́ òpè nípa wàhálà tí ó dé bá wa ní agbègbè Éṣíà, ní ti pé a pọ́n wa lójú gidigidi rékọjá agbára ìfaradà wa, tó bẹ́ẹ̀ tí ìrètí kò tilẹ̀ sí fún ẹ̀mí wa mọ́."'), '2Cor$2:4': ('mọ', 'ife', '2Cor$2:4 "Nítorí pé nínú ọ̀pọ̀lọpọ̀ wàhálà àti ìrora ọkàn mí ni mo ti fi ọ̀pọ̀lọpọ̀ omijé kọ̀wé sí yín; kì í ṣe nítorí kí a lè bà yín nínú jẹ́, ṣùgbọ́n kí ẹ̀yin bá a lè mọ bí ìfẹ́ tí mo ní sí yín ṣe jinlẹ̀ tó."'), '2Cor$2:5': (None, 'ni', '2Cor$2:5 "Bí ẹnikẹ́ni bá fa ohun ìbànújẹ́ kì í ṣe èmi ni ó bà nínú jẹ́, bí kò ṣe ẹyin fúnra yín, níwọ̀n ìyówù kí ó jẹ́; n kò fẹ́ sọ ọ́ lọ́nà líle jù."'), '2Cor$3:4': ('ṣe', 'ni', '2Cor$3:4 Irú ìgbẹ́kẹ̀lẹ́ yìí ni àwa ní nípaṣẹ̀ Kírísítì sọ́dọ̀ Ọlọ́run:'), '2Cor$3:12': (None, None, '2Cor$3:12 "Ǹjẹ́ nítorí náà bí a tí ní irú ìrètí bí èyí, àwa ń fí ìgbóyà púpọ̀ sọ̀rọ̀."'), '2Cor$4:11': (None, None, '2Cor$4:11 "Nítorí pé nígbà gbogbo ní a ń fí àwa tí ó wà láàyè fún ikú nítorí Jésù, kí a lè fi ìyè hàn nínú ara kíkú wa pẹ̀lú."'), '2Cor$4:15': ('ọpẹ', 'oore-ọfẹ', '2Cor$4:15 "Nítorí tiyín ní gbogbo rẹ̀, ki ọpẹ lè dí púpọ̀ fún ògo Ọlọ́run gẹ́gẹ́ bí oore-ọ̀fẹ́ ti ń gbòòrò sí i fún ọ̀pọ̀lọpọ̀ àwọn ènìyàn."'), '2Cor$4:17': ('ri', 'wo', '2Cor$4:17 Nítorí ìpọ́njú díẹ̀ kíún yìí ń pèsè ògo tí ó ní ìwọ̀n ayérayè tí ó pọ̀ rékọjá sílẹ̀ fún wa.'), '2Cor$5:1': (None, 'ni', '2Cor$5:1 "Nítorí àwa mọ̀ pé, bi ilé àgọ́ wa ti ayé ba wó, àwa ní ilé kan láti ọ̀dọ̀ Ọlọ́run, ilé tí a kò fi ọwọ́ kọ́, ti ayérayé nínú àwọn ọ̀run."'), '2Cor$5:12': ('fun', 'ni', '2Cor$5:12 " Nítorí àwa kò sì ní máa tún yin ara wá sí i yín mọ́, ṣùgbọ́n àwa fi ààyè fún yín láti máa ṣògo nítorí wa, kí ẹ lè ní ohun tí ẹ̀yin yóò fi dá wọn lóhùn, àwọn ti ń ṣògo lodé ara kì í ṣe ní ọkàn."'), '2Cor$5:14': ('wà', None, '2Cor$5:14 " Nítorí ifẹ́ Kírísítì ń rọ̀ wá, nítorí àwa mọ̀ báyìí pé, bí ẹnìkan bá kú fún gbogbo ènìyàn, ǹjẹ́ nígbà náà, gbogbo wọ́n ni ó ti kú."'), '2Cor$6:2': ('gba', 'ni', '2Cor$6:2 " Nítorí o wí pé, “Ní àkókò ìtẹ́wọ́gbà mi, èmi tí gbọ́ ohùn rẹ, àti ọjọ́ ìgbàlà, èmi sì ti ràn ọ́ lọ́wọ́.” Èmi wí fún ọ, nísínsín yìí ní àkókò ìtẹ́wọ́gbà, nísínsín yìí ní ni ọjọ́ ìgbàlà."'), '2Cor$7:3': ('ni', None, '2Cor$7:3 " Èmi kò sọ èyí làti dá a yín lẹ́bí; nítorí mo tí wí ṣáájú pé, ẹ̀yin wà nínú ọkàn wa kí a lè jùmọ̀ kú, àti kí a lè jùmọ̀ wà láàyè."'), '2Cor$7:12': ('ní', 'fa', '2Cor$7:12 " Nítorí náà, bí mo tilẹ̀ tí kọ̀wé sí yín, èmi kò kọ ọ́ nítorí ẹni tí ó ṣe ohun búburú náà tàbí nítorí ẹni ti a fi ohun búburú náà ṣe, ṣùgbọ́n kí àníyàn yín nítorí wá lè farahàn níwájú Ọlọ́run."'), '2Cor$7:14': (None, 'ni', '2Cor$7:14 "Bí mo tilẹ̀ ti lérí ohunkóhun fún ún nítorí yín, a kò dójú tì mí; ṣùgbọ́n gẹ́gẹ́ bí àwa ti sọ ohun gbogbo fún yín ní òtítọ́, gẹ́gẹ́ bẹ́ẹ̀ ni ìlérí wá níwájú Títù sì já sí òtítọ́."'), '2Cor$8:10': ('fun', 'ni', '2Cor$8:10 " Àti nínú èyí ni mo fí ìmọ̀ràn mi fún yín: nítorí èyí ṣe àǹfààní fún yín, ẹ̀yin tí ó kọ́kọ́ bẹ̀rẹ̀ níwọ̀n ọdún tí ó kọjá, kì í ṣe láti ṣe nǹkan, ṣùgbọ́n láti fẹ́ pẹ̀lú."'), '2Cor$8:11': (None, None, '2Cor$8:11 "Ǹjẹ́ nísinsin yìí, ẹ parí ṣíṣe iṣẹ́ náà pẹ̀lú; kí ó baà lè ṣe pé, bí ìmúra tẹ́lẹ̀ fún ṣiṣe ti wà, bẹ́ẹ̀ ni kí ìparí sì wà láti inú agbára yín."'), '2Cor$8:13': ('fẹ', None, '2Cor$8:13 "Nítorí èmi kò fẹ kí àwọn ẹlòmíràn wà ní ìrọ̀rùn, kí ó sì jẹ́ ìpọ́njú fún yín. Ṣùgbọ́n pé nípa mímú dọ́gba,"'), '2Cor$8:18': (None, None, '2Cor$8:18 " Àwa ti rán arákùnrin náà pẹ̀lú rẹ̀, ìyìn ẹni tí ó wà nínú ìyìn rere yíká gbogbo ìjọ."'), '2Cor$9:1': (None, None, '2Cor$9:1 " Nísinsinyìí, nípa ti ìpínfúnni fún àwọn ènìyàn mímọ́, kò tún yẹ mọ́ fún mi láti kọ̀wé sí yin ju bẹ́ẹ̀ lọ."'), '2Cor$9:5': ('ṣe', None, '2Cor$9:5 "Nítorí náà ni mo ṣe rò pé ó yẹ láti gba àwọn arákùnrin níyànjú, kí wọn kọ́kọ́ tọ̀ yín wá, kí ẹ sì múra ẹ̀bùn yín, tí ẹ ti ṣe ìlérí tẹ́lẹ̀ sílẹ̀, kí ó lè ti wà ní lẹ̀ bí ẹ̀bùn gidi, kí ó má sì ṣe dàbí ohun ti ìfipágbà."'), '2Cor$9:8': ('ṣe', 'iṣẹ', '2Cor$9:8 " Ọlọ́run sì lè mú kí gbogbo oore ọ̀fẹ́ máa bí sí i fún yín; kí ẹ̀yin tí ó ní ànító ohun gbogbo, nígbà gbogbo, lè máa pọ̀ sí i ní iṣẹ́ rere gbogbo."'), '2Cor$9:12': (None, None, '2Cor$9:12 "Nítorí iṣẹ́-ìránṣẹ́ ìsìn yìí kò kún ìwọ̀n àìní àwọn ènìyàn-mímọ́ nìkan, ṣùgbọ́n ó túbọ̀ pọ̀ sí i nípa ọ̀pọ̀lọpọ̀ ọpẹ́ sí Ọlọ́run."'), '2Cor$10:14': (None, None, '2Cor$10:14 "Nítorí àwa kò nawọ́ wa rékọjá rárá, bí ẹni pé àwa kò dé ọ̀dọ̀ yín: nítorí àwa tilẹ̀ dé ọ̀dọ̀ yín pẹ̀lú nínú ìyìn rere Kírísítì."'), '2Cor$11:4': (None, 'ni', '2Cor$11:4 " Nítorí bí ẹnìkan bá wá tí ó sì wàásù Jésù mìíràn, yàtọ̀ sí èyí tí a ti wàásù rí tàbí bí ẹ̀yin bá gba ẹ̀mí mìíràn, yàtọ̀ sí èyí tí ẹ ti gbà tàbí ìyìn rere mìíràn yàtọ̀ sí èyí tí ẹ ti gbà, tí ẹ sì ti yára tẹ́wọ́ gbà á."'), '2Cor$11:6': ('ṣe', 'ni', '2Cor$11:6 " Bí mo tilẹ̀ jẹ́ òpè nínú ọ̀rọ̀ sísọ, kì í ṣe nínú ìmọ̀; Ní ọ̀nàkọnà ni àwa ti fi èyí hàn dájúdájú fún yín nínú ohun gbogbo."'), '2Cor$11:25': (None, 'ni', '2Cor$11:25 " Nígbà mẹ́ta ni a fi ọ̀gọ̀ lù mí, ẹ̀ẹ̀kanṣoṣo ni a sọ mí ní òkúta, ẹ̀ẹ̀mẹ́ta ni ọkọ̀ ojú omi mi rì, ọ̀sán kan àti òru kan ni mo wà nínú ibú."'), '2Cor$12:17': (None, None, '2Cor$12:17 Èmi há rẹ́ yín jẹ nípa ẹnikẹ́ni nínú àwọn tí mo rán sí yín bi?'), '2Cor$12:20': ('ri', 'wa', '2Cor$12:20 " Nítorí ẹ̀rù ń bà mí pé, nígbà tí mo bá dé, èmi kì yóò bá yín gẹ́gẹ́ bí irú èyí tí mo fẹ́, àti pé ẹ̀yin yóò sì rí mi gẹ́gẹ́ bí irú èyí tí ẹ̀yin kò fẹ́: Kí ìjà, owú-jíjẹ, ìbínú, ìpínyà, ìsọ̀rọ̀-ẹni-lẹ́yìn, ìjírọ̀sọ̀, ìfẹẹ́gẹ̀, ìrúkèrúdò, má bà à wà."'), '2Cor$13:10': (None, None, '2Cor$13:10 "Ìdí nìyí mo ṣe kọ̀wé àwọn nǹkan wọ̀nyí nígbà tí èmi kò sí lọ́dọ̀ yín, pé nígbà tí mo bá dé kí èmi má ba à lo ìkanra ní lílo àṣẹ, gẹ́gẹ́ bí àṣẹ tí Olúwa ti fifún mí, láti mú dàgbàsókè, kì í ṣe láti fà yín subú."'), 'Gal$1:8': ('jẹ ki', 'ni', 'Gal$1:8 " Ṣùgbọ́n bí ó ṣe àwa ni tàbí ańgẹ́lì kan láti ọ̀run wá, ni ó bá wàásù ìyìn rere mìíràn fún yín ju èyí tí a tí wàásù rẹ̀ fún yín lọ, jẹ́ kí ó di ẹni ìfibú títí láéláé!"'), 'Gal$1:9': ('jẹ ki', 'ni', 'Gal$1:9 "Bí àwa ti wí ṣáájú, bẹ́ẹ̀ ni mo sì tún wí nísinsinyìí pé: Bí ẹnìkan bá wàásù ìyìn rere mìíràn fún yín ju èyí tí ẹ̀yin tí gbà lọ, jẹ́ kí ó di ẹni ìfibú títí láéláé!"'), 'Gal$1:13': ('ni', None, 'Gal$1:13 " Nítorí ẹ̀yin ti gbúrò ìwà-ayé mi nígbà àtijọ́ nínú ìsìn àwọn Júù, bí mo tí ṣe inúnibíni sí ìjọ ènìyàn Ọlọ́run rékọjá ààlà, tí mo sì lépa láti bà á jẹ́:"'), 'Gal$2:2': ('lọ', None, 'Gal$2:2 "Mo gòkè lọ ní ìbámu ìfihàn, mo gbé ìyìn rere náà tí mo ń wàásù láàrin àwọn aláìkọlà kalẹ̀ níwájú wọn. Ṣùgbọ́n mo se èyí ní ìkọ̀kọ̀ fún àwọn tí ó jẹ́ olùdarí, ni ẹ̀rù pé mo ń sáré tàbí mo tí sáré ìje mi lásán."'), 'Gal$2:3': (None, 'ni', 'Gal$2:3 "Ṣùgbọ́n a kò fi agbára mú Títù tí ó wà pẹ̀lú mi, ẹni tí í ṣe ara Gíríkì láti kọlà."'), 'Gal$2:6': (None, 'lọ', 'Gal$2:6 " Ṣùgbọ́n ní ti àwọn tí ó dàbí ẹni pàtàkì—ohunkóhun tí ó wù kì wọn já sí, kò jẹ́ nǹkan kan fún mi; Ọlọ́run kò fi bí ẹnìkẹ́ni se rí se ìdájọ́ rẹ̀—àwọn eniyan yìí kò fi ohunkóhun kún ọ̀rọ̀ mi."'), 'Gal$2:12': ('ṣe', 'wá', 'Gal$2:12 "Nítorí pé kí àwọn kan tí ó ti ọ̀dọ̀ Jákọ́bù wá tó dé, ó ti ń ba àwọn aláìkọlà jẹun; Ṣùgbọ́n nígbà tí wọ́n dé, ó fà sẹ́yìn, ó sì ya ara rẹ̀ sọ́tọ̀ kúrò lọ́dọ̀ àwọn aláìkọlà nítorí ó bẹ̀rù àwọn ti ó kọlà."'), 'Gal$2:13': (None, None, 'Gal$2:13 "Àwọn Júù tí ó kù pawọ́pọ̀ pẹ̀lú rẹ̀ lati jùmọ̀ ṣe àgàbàgebè, tó bẹ́ẹ̀ tí wọn sì fi àgàbàgebè wọn si Bánábà lọnà."'), 'Gal$2:14': (None, None, 'Gal$2:14 "Nígbà tí mo rí i pé wọn kò rìn déédéé gẹ́gẹ́ bí òtítọ́ ìyìn rere, mo wí fún Pétérù níwájú gbogbo wọn pé, “Bí ìwọ, tí ì ṣe Júù ba ń rìn gẹ́gẹ́ bí ìwà àwọn aláìkọlà, è é ṣe tí ìwọ fi ń fi agbára mu àwọn aláìkọlà láti máa rìn bí àwọn Júù?"'), 'Gal$2:18': (None, None, 'Gal$2:18 "Nítorí pé bí mo bá sì tún gbé àwọn ohun tí mo tí wó palẹ̀ ró, mo fí ara mi hàn bí arúfin."'), 'Gal$2:19': ('emi', None, 'Gal$2:19 "Nítorí pé nípa òfin, mo tí di òkú sí òfin, kí èmi lè wà láàyè sí Ọlọ́run."'), 'Gal$2:20': (None, None, 'Gal$2:20 " A ti kàn mí mọ́ àgbélébùú pẹ̀lú Kírísítì, èmí kò sì wà láàyè mó, ṣùgbọ́n Kírísítì ń gbé inú mi wíwà tí mo sì wà láàyè nínú ara, mo wà láàyè nínú ìgbàgbọ́ ọmọ Ọlọ́run, ẹni tí o fẹ́ mi, tí ó sì fi òun tìkararẹ̀ fún mi."'), 'Gal$2:21': ('ṣe', None, 'Gal$2:21 "Èmi kò ya oore-ọ̀fẹ́ Ọlọ́run sí apákan: nítorí pé bí a bá le ti pasẹ̀ òfin jèrè òdodo, a jẹ́ pé Kírísítì kú lásán.”"'), 'Gal$3:11': ('ni', None, 'Gal$3:11 " Nítorí ó dánilójú pé, a kò dá ẹnìkẹ́ni láre níwájú Ọlọ́run nípa iṣẹ́ òfin: nítorí pé, olódodo yóò yè nípa ìgbàgbọ́.”"'), 'Gal$3:12': ('ni', None, 'Gal$3:12 Òfin kì í sì í ṣe ti ìgbàgbọ́: ṣùgbọ́n “Ẹnikẹ́ni tí ń se wọn yóò yè nípaṣẹ̀ wọn.”'), 'Gal$3:15': (None, 'eniyan', 'Gal$3:15 "Ará, èmi ń ṣọ̀rọ̀ bí ènìyàn: bí ó tilẹ̀ jẹ́ pé májẹ̀mú ènìyàn ni, ṣùgbọ́n bí a ti fi ìdí rẹ̀ múlẹ̀, kò sí ẹni tí ó lè sọ ọ́ di asán, tàbí tí ó lè fi kún un mọ́."'), 'Gal$4:3': (None, None, 'Gal$4:3 " Gẹ́gẹ́ bẹ́ẹ̀ sì ni àwa, nígbà tí àwa wà ní èwe, àwa wà nínú ìdè lábẹ́ ìpilẹ̀ṣẹ̀ ẹ̀dá."'), 'Gal$5:10': (None, 'ni', 'Gal$5:10 "Mo ní ìgbẹ́kẹ̀lé sí yín nínú Olúwa pé, ẹ̀yin kì yóò ní èrò ohun mìíràn; ṣùgbọ́n ẹni tí ń yọ yín lẹ́nu yóò ru ìdájọ́ tirẹ̀, ẹnikẹ́ni tí ó wù kí ó jẹ́."'), 'Gal$5:15': ('run', None, 'Gal$5:15 "Ṣùgbọ́n bí ẹ̀yin bá ń bu ara yín ṣán, tí ẹ sì ń jẹ ara yín run, ẹ kíyèsára kí ẹ má ṣe pa ara yín run."'), 'Gal$6:4': (None, 'ni', 'Gal$6:4 "Ṣùgbọ́n kí olúkúlùkù yẹ iṣẹ́ ara rẹ̀ wò, nígbà náà, ohun ìmú-ṣogo rẹ̀ yóò jẹ́ nínú ti ara rẹ̀ nìkan, kì yóò sì jẹ nínú ti ọmọnìkejì rẹ̀."'), 'Gal$6:10': ('jẹ ki', 'ni', 'Gal$6:10 "Ǹjẹ́ bí a ti ń rí àkókò, ẹ jẹ́ kí a máa ṣoore fún gbogbo ènìyàn, àti pàápàá fún àwọn tí í ṣe ará ilé ìgbàgbọ́."'), 'Gal$6:11': (None, None, 'Gal$6:11 Ẹ wo bí mo ti fi ọwọ́ ara mi kọ̀wé gàdàgbà-gàdàgbà sí yín.'), 'Eph$1:3': ('ibukun', None, 'Eph$1:3 " Ògo ni fún Ọlọ́run àti Baba Jésù Kírísítì Olúwa wa, ẹni tí ó ti bùkún wa láti inú ọ̀run wá pẹ̀lú àwọn ìbùkún ẹ̀mí gbogbo nínú Kírísítì."'), 'Eph$1:7': ('ṣe', 'ni', 'Eph$1:7 " Nínú rẹ̀ ni àwa ni ìràpadà wa nípa ẹ̀jẹ̀ rẹ̀, ìdáríjì àwọn ẹ̀ṣẹ̀ wa, ní ìbámu pẹ̀lú ọrọ̀ oore-ọ̀fẹ́ Ọlọ́run"'), 'Eph$1:15': ('mọ', 'ife', 'Eph$1:15 " Nítorí ìdí èyí, nígbà tí mo ti gbúro ìgbàgbọ́ ti ń bẹ láàárin yín nínú Jésù Olúwa, àti ìfẹ́ yín sí gbogbo àwọn ènìyàn mímọ́."'), 'Eph$2:11': (None, None, 'Eph$2:11 "Nítorí náà ẹ rántí pé, nígbà àtijọ́ rí, ẹ̀yin tí ẹ ti jẹ́ aláìkọlà nípa ti ìbí, tí àwọn tí a ń pè ní Akọlà ti a fi ọwọ́ ṣe ni ara ń pè ní Aláìkọlà."'), 'Eph$2:14': (None, 'ṣe', 'Eph$2:14 "Nítorí oun ni àlàáfíà wa, ẹni tí o ti ṣe méjèèjì ni ọ̀kan, tí ó sì ti wó ògiri ìkélé ti ìkórira èyí tí ń bẹ láàrin yín."'), 'Eph$3:1': ('ṣe', None, 'Eph$3:1 "Nítorí èyí náà ni èmi Pọ́ọ̀lù ṣe di òǹdè Jésù Kírísítì nítorí ẹ̀yin aláìkọlà,"'), 'Eph$3:8': (None, 'ni', 'Eph$3:8 "Fún èmi tí o kéré jùlọ nínú gbogbo àwọn ènìyàn mímọ́, ní a fi oore ọ̀fẹ́ yìí fún, láti wàásù àwámárídi ọ̀rọ̀ Kírísítì fún àwọn aláìkọlà;"'), 'Eph$3:14': ('mọ', None, 'Eph$3:14 Nítorí ìdí èyí ni mo ṣe ń fi eekún mi kúnlẹ̀ fún Baba Olúwa wa Jésù Kírísítì.'), 'Eph$4:7': (None, 'ebun', 'Eph$4:7 Ṣùgbọ́n olúkúlùkù wa ni a fi oore ọ̀fẹ́ fun gẹ́gẹ́ bi òṣùwọ̀n ẹ̀bùn Kírísítì.'), 'Eph$4:17': (None, 'ni', 'Eph$4:17 "Ǹjẹ́ èyí ni mo ń wí, tí mo sì ń jẹ́rìí nínú Olúwa pé, láti ìsinsin yìí lọ, kí ẹ̀yin má ṣe rìn mọ́, àní gẹ́gẹ́ bí àwọn aláìkọlà ti ń rìn nínú ìrònú asán wọn."'), 'Eph$4:20': (None, None, 'Eph$4:20 Ṣùgbọ́n a kò fi Kírísítì kọ́ yín bẹ́ẹ̀.'), 'Eph$4:28': ('ni', None, 'Eph$4:28 "Kí ẹni tí ń jalè má ṣe jalè mọ́: ṣùgbọ́n kí ó kúkú máa ṣe làálàá, kí ó máa fi ọwọ́ rẹ̀ ṣiṣẹ́ ohun tí ó dara, kí òun lè ní láti pín fún ẹni tí ó ṣe aláìní."'), 'Eph$4:31': ('ibinu', 'ibinu', 'Eph$4:31 "Gbogbo ìwà kíkorò àti ìbínú, àti ìrunú, àti ariwo, àti ọ̀rọ̀ búburú ni kí a mú kúrò lọ́dọ̀ yín, pẹ̀lú gbogbo àránkan:"'), 'Eph$5:1': (None, None, 'Eph$5:1 "Nítorí náà, ẹ máa ṣe àfarawé Ọlọ́run bí àwọn olùfẹ́ ọmọ,"'), 'Eph$5:18': (None, None, 'Eph$5:18 "Ẹ má sí ṣe mú wáìnì ní àmupara, nínú èyí tí rúdurúdu wà; ṣùgbọ́n ẹ kún fún Ẹ̀mí Mímọ́."'), 'Eph$5:25': ('fun', None, 'Eph$5:25 "Ẹ̀yín ọkọ, ẹ fẹ́ràn àwọn aya yín, gẹ́gẹ́ bí Kírísítì tí fẹ́ràn ìjọ, tí ó sì fí ara rẹ̀ fún-un."'), 'Eph$5:29': (None, None, 'Eph$5:29 Nítorí kò sì ẹnìkan tí ó ti kóríra ara rẹ̀; bí kò ṣe pé kí ó máa bọ́ ọ kí ó sì máa sìkẹ́ rẹ̀ gẹ́gẹ́ bí Kírísítì sì ti ń ṣe sí ìjọ.'), 'Eph$6:2': ('ni', None, 'Eph$6:2 " “Bọ̀wọ̀ fún bábá àtí ìyá rẹ”: èyí tí í ṣe ofín kìn-ín-ní pẹ̀lú ilérí,"'), 'Eph$6:5': (None, None, 'Eph$6:5 "Ẹ̀yín ọmọ-ọ̀dọ̀, ẹ máa gbọ́ ti àwọn olúwa yín nípá ti ara, pẹ̀lú ìbẹ̀rù àtí ìwárìrì, ní òtítọ́ ọkàn yín, bí ẹni pé sí Kírísítì."'), 'Eph$6:12': ('okunkun', 'ibi', 'Eph$6:12 "Nítorí pé kì í ṣe ẹ̀jẹ̀ àti ẹran ara ní awá ń bá jìjàkadì, ṣùgbọ́n àwọn ìjòyé, àwọn ọlọ́lá, àwọn aláṣẹ ìbí òkùnkùn ayé yìí, àtí àwọn ẹmí búburú ní ojú ọrun."'), 'Eph$6:13': (None, 'gba', 'Eph$6:13 "Nítorí náà, ẹ gbé gbogbo ìhámọ́ra Ọlọ́run wọ̀, kí ẹ̀yín lè dúró tiiri sí ọjọ́ ibi, nígbà tí ẹ̀yin bá sì ti ṣe ohun gbogbo tan kí ẹ sì dúró."'), 'Eph$6:17': ('ṣe', 'gba', 'Eph$6:17 "Kí ẹ sì mú àṣíborí ìgbàlà, àti idà ẹ̀mí, tí í ṣe ọ̀rọ̀ Ọlọ́run."'), 'Eph$6:21': ('ni', 'ṣe', 'Eph$6:21 " Ṣùgbọ́n kí ẹ̀yin pẹ̀lú lè mọ̀ bí nǹkán ti rí fún mí, bí mo tí ń ṣe sí; Tíkíkù, arákùnrin olùfẹ́ àti ìránṣẹ́ olóòótọ́ nínú Olúwa yóò sọ ohun gbogbo dí mímọ̀ fún yín."'), 'Phil$1:3': (None, None, 'Phil$1:3 Mo ń dúpẹ́ lọ́wọ́ Ọlọ́run mi ní gbogbo ìgbà tí mo bá rántí yín:'), 'Phil$1:15': (None, None, 'Phil$1:15 "Òtítọ́ ni pé àwọn ẹlòmíràn tilẹ̀ ń fi ìjà àti ìlara wàásù Kírísítì, ṣùgbọ́n àwọn ẹlòmíràn sì ń fi inú rere ṣe é."'), 'Phil$1:21': ('ni', None, 'Phil$1:21 " Nítorí, níti èmi, láti wa láàyè jẹ́ Kírísítì, láti kú jẹ́ èrè."'), 'Phil$1:22': ('ni', None, 'Phil$1:22 "Ṣùgbọ́n bí èmi bá sì wà láàyè nínú ara, èyí yóò jásí èrè fún iṣẹ́ mi. Síbẹ̀, kíni èmí yóò yàn? Èmi kò mọ̀?"'), 'Phil$1:27': ('gbo', 'wo', 'Phil$1:27 "Ohun tó wù kí ó ṣẹlẹ̀, ẹ jẹ́ kí iṣẹ́ yín wà ní ìbámu pẹ̀lú ìyìnrere Kírísítì. pé yálà bí mo tilẹ̀ wá wò yín, tàbí bí èmi kò wá, kí èmi lè máa gbúròó bí ẹ ti ń ṣe, pé ẹ̀yin dúró ṣinṣin nínú Ẹ̀mí kan, ẹ̀yin sì jùmọ̀ n jìjàkadì nítorí ìgbàgbọ́ ìyìn rere, pẹ̀lú ọkàn kan;"'), 'Phil$1:29': (None, 'jiya', 'Phil$1:29 "Nítorí a ti fi fún yín nítorí Kírísítì, kì í ṣe láti gbàgbọ́ nínú rẹ̀ nìkan, ṣùgbọ́n láti jìyà nítorí rẹ̀ pẹ̀lú."'), 'Phil$2:1': ('itunu', 'itunu', 'Phil$2:1 " Bí ìwọ bá ní ìmúlọ́kànlé nítorí pé ìwọ wà ní ìsọ̀kan pẹ̀lú Kírísítì, bi ìtùnú kan bá wà nínú ìfẹ́ rẹ̀, bí ìdàpọ̀ Ẹ̀mí kan bá wà, bí ìyọ́nú àti ìṣeun bá wà,"'), 'Phil$2:5': (None, None, 'Phil$2:5 Ìṣe yín gbọdọ̀ dàbí ti Kírísítì Jésù:'), 'Phil$2:12': ('ni', None, 'Phil$2:12 "Nítorí náà ẹ̀yin ará mi, gẹ́gẹ́ bí ẹ̀yin ti ń gbọ́ràn nígbà gbogbo, kì í ṣe nígbà ti mo wà lọ́dọ̀ yín nìkan, ṣùgbọ́n pàápàá nísinsin yìí tí èmi kò sí ni àárin yín, ẹ máa ṣiṣẹ́ ìgbàlà yín yọrí pẹ̀lú ìbẹ̀rù àti ìwárìrì,"'), 'Phil$2:18': (None, None, 'Phil$2:18 "Bákan náà ni kí ẹ̀yin máa yọ̀, kí ẹ sì máa bá mi yọ̀ pẹ̀lú."'), 'Phil$2:23': (None, None, 'Phil$2:23 "Nítorí náà, mo ní ìrètí láti rán sí yín ní àìpẹ́ tí mo bá wòye bí yóò ti rí fún mi."'), 'Phil$3:8': (None, None, 'Phil$3:8 "Nítòótọ́ láìṣe àní-àní mo sì ka ohun gbogbo sí òfo nítorí ìtayọ ìmọ̀ Kírísítì Jésù Olúwa mi; nítorí ẹni ti mo ti ṣòfò ohun gbogbo, mo si kà wọn sí ohun tí kò ní láárí, kí èmi lè jèrè Kírísítì,"'), 'Phil$3:12': ('emi', None, 'Phil$3:12 "Kì í ṣe pé ọwọ́ mi ti tẹ̀ ẹ́ ná, tàbí mo ti di pípé: ṣùgbọ́n èmi ń lépa ǹṣó, bí ọwọ́ mi yóò lè tẹ èrè náà nítorí èyí tí a ti dì mí mú pẹ̀lú, láti ọ̀dọ̀ Kírísítì Jésù wá."'), 'Phil$3:16': ('jẹ ki', 'ni', 'Phil$3:16 "Kìkì pé, ibi tí a ti dé ná, ẹ jẹ́ kí a máa rìn ní ojú-ọ̀nà kan náà, kí a ni èrò kan náà."'), 'Phil$3:18': (None, None, 'Phil$3:18 "Nítorí ọ̀pọ̀lọpọ̀ ní ń rìn, nípasẹ̀ àwọn ẹni tí mo ti ń wí fún yín nígbàkúùgbà, àní, tí mo sì ń sọkún bí mo ti ń wí fún yín nísinsinyìí, pé, ọ̀ta àgbélébùú Kírísítì ni wọ́n:"'), 'Phil$4:6': ('ebe', 'adura', 'Phil$4:6 " Ẹ má ṣe ṣe àníyàn ohunkóhun; ṣùgbọ́n nínú ohun gbogbo, nípa àdúrà àti ẹ̀bẹ̀ pẹ̀lú ìdúpẹ́, ẹ máa fi ìbéèrè yín hàn fún Ọlọ́run."'), 'Phil$4:8': (None, 'ooto', 'Phil$4:8 "Ní àkótán, ará, ohunkóhun tí í ṣe òótọ́, ohunkóhun tí í ṣe ọ̀wọ̀, ohunkóhun tí í ṣe títọ́ ohunkóhun tí í ṣe mímọ́, ohunkóhun tí í ṣe fífẹ́, ohunkóhun tí ó ni ìròyìn rere, bí ìwà títọ́ kan bá wà, bí ìyìn kan bá sì wà, ẹ máa gba nǹkan wọ̀nyí rò."'), 'Col$1:3': (None, None, 'Col$1:3 Nígbàkúùgbà tí a bá ń gbàdúrà fún un yín ni a máa ń dúpẹ́ lọ́wọ́ Ọlọ́run Baba Olúwa wa Jésù Kírísítì.'), 'Col$1:9': (None, 'ni', 'Col$1:9 " Nítorí ìdí èyí, láti ọjọ́ tí a ti gbọ́ nípa yín, a kò yéé gbàdúrà fún yin, tí a sì ń gbàdúrà pé kí Ọlọ́run fi ìmọ̀ ìfẹ́ rẹ̀ kún yín nípaṣẹ̀ ọgbọ́n àti òye gbogbo tí ń ṣe ti Ẹ̀mí"'), 'Col$1:21': (None, 'ni', 'Col$1:21 "Ẹ̀yin ya ara yín nípá kúrò lọ́dọ̀ Ọlọ́run nígbà kan rí, ẹ jẹ́ ọ̀tá nínú ọkàn yín nítorí ìwà búburú yín."'), 'Col$2:1': ('mọ', None, 'Col$2:1 Mo fẹ́ kí ẹ mọ bí mo ti ń jìjàkadì fún un yín àti fún àwọn ará Laodékíà àti fún ọ̀pọ̀ ènìyàn mìíràn tí wọn kò ì tí ì rí mi sójú rí.'), 'Col$2:6': (None, None, 'Col$2:6 "Nítorí náà, bí ẹ̀yin ti gba Jésù Kírísítì gẹ́gẹ́ bí Olúwa, bẹ́ẹ̀ ni kí ẹ̀yin máa gbé nínú rẹ̀."'), 'Col$2:8': (None, 'eniyan', 'Col$2:8 "Ẹ máa kiyesii pé ẹnikẹ́ni kò fi ìmọ̀ àti ẹtan asan ko yín ní ìgbèkùn, èyí tí ó jẹmọ́ ìtàn ènìyàn bi ìpilẹ̀sẹ ẹ̀kọ́ ayé láìse ohun tí Kírísítì ti wí."'), 'Col$2:13': (None, None, 'Col$2:13 "Àti ẹ̀yin, ẹni tí o ti kú nítorí ẹ̀ṣẹ̀ yín àti àìkọlà ará yín, mo ni, ẹ̀yin ni ó sì ti sọ di ààyè pọ̀ pẹ̀lú rẹ̀, ó sì ti dárí gbogbo ẹ̀ṣẹ̀ yín jì yín;"'), 'Col$2:20': (None, None, 'Col$2:20 " Bí ẹ̀yin bá ti kú pẹ̀lú Kírísítì kúrò nínú ìpilẹ̀ṣẹ̀ ayé, èé ha ti ṣe tí ẹ̀yin ń tẹ́ríba fún òfin bí ẹni pé ẹ̀yin wà nínú ayé,"'), 'Col$3:8': ('ibinu', 'ibinu', 'Col$3:8 "Ṣùgbọ́n báyìí, ẹ gbọdọ̀ fi àwọn wọ̀nyìí sílẹ̀: ìbínú, ìrúnú, àránkan, fífi ọ̀rọ̀-kẹ́lẹ́-ba-tẹni-jẹ́ àti ọ̀rọ̀ èérí kúrò ní ètè yín."'), 'Col$3:9': (None, None, 'Col$3:9 "Ẹ má ṣe purọ́ fún ara yín, níwọ̀n ìgbà tí ẹ ti bọ́ ògbólógbòó ayé yín pẹ̀lú ìṣe rẹ̀ sílẹ̀,"'), 'Col$3:12': (None, None, 'Col$3:12 " Nítorí náà, bí àyànfẹ́ Ọlọ́run, ẹni mímọ́ àti olùfẹ́, ẹ gbé ọkàn ìyọ́nú wọ̀, ìsoore, ìrẹ̀lẹ̀, inú tútù àti sùúrù."'), 'Col$3:22': ('ṣe', None, 'Col$3:22 "Ẹ̀yin ẹrú, ẹ gbọ́ ti àwọn olówó yín nípa ti ara ní ohun gbogbo; kì í ṣe ní àrojú ṣe, bí àwọn aláṣehàn ènìyàn; ṣugbọ́n ní òtítọ́ inú, ní ìbẹ̀rù Ọlọ́run:"'), 'Col$4:1': ('fun', 'ni', 'Col$4:1 " Ẹyin olúwa, ẹ má a fi èyí tí ó tọ́ tí ó sì dọ́gba fún àwọn ọmọ-ọ̀dọ̀ yín; kí ẹ sì mọ̀ pé ẹ̀yin pẹ̀lú ni owa kan ní ọ̀run."'), 'Col$4:12': ('pipe', None, 'Col$4:12 " Épáfúrà, ẹni tí ń ṣe ọ̀kan nínú yín àtiìránṣẹ́ Kírísítì kí í yín. Òun fí ìwàyájà gbàdúrà nígbà gbogbo fún-ún yín, kí ẹ̀yin kí ó lè dúró ní pípé nínú ohun gbogbo nínú ìfẹ́ Ọlọ́run."'), '1Thess$1:2': (None, None, '1Thess$1:2 " Gbogbo ìgbà ni a máa ń fi ọpẹ́ fún Ọlọ́run nítorí yín, a sì ń gbàdúrà fún un yín nígbà gbogbo pẹ̀lú."'), '1Thess$2:7': (None, None, '1Thess$2:7 Ṣùgbọ́n àwa se pẹ̀lẹ́pẹ̀lẹ́ láàrin yín tí a sì se ìtọ́jú yín gẹ́gẹ́ bí ìyá ti ń tọ́jú àwọn ọmọ rẹ̀.'), '1Thess$2:9': ('laala', None, '1Thess$2:9 "Nítòótọ́ ẹ rántí, ará, iṣẹ́ àti làálàá wa; lọ́sàn-án àti lóru ni àwa ń ṣiṣẹ́ kí ìnáwó wa má bà á di ìṣòro fún ẹnìkẹ́ni bí a ti ń wàásù ìyìnrere Ọlọ́run fún un yín."'), '1Thess$2:14': (None, 'ni', '1Thess$2:14 "Nítorí, ẹ̀yin ara, ẹ jẹ́ àwòkọ́se àwọn ìjọ tí o wà ní Jùdíà jẹ. Bí a ti gbógun tì wọ́n, láti ọwọ́ àwọn ará ìlú wọn (àwọn Júù), bẹ́ẹ̀ ni a gbógun ti ẹ̀yin pàápàá láti ọ̀dọ̀ àwọn Júù,"'), '1Thess$3:5': ('ni', None, '1Thess$3:5 " Bí mo wí tẹ́lẹ̀, nígbà tí ara mi kò gba ìdákẹ́jẹ́ẹ́ náà, mo rán Tímótíù láti bẹ̀ yín wò, kí ó lè mọ̀ bóyá ìgbàgbọ́ yín sì dúró gbọn-ingbọn-in. Mo funra sí i wí pé, sàtánì ti ṣẹ́gun yín. Bí ó bá rí bẹ́ẹ̀, iṣẹ́ wa (láàrin yín) jásí aṣán."'), '1Thess$3:6': ('wà', 'gbe', '1Thess$3:6 " Nísìnsinyìí, Tímótíù ti dé pẹ̀lú ìròyìn ayọ̀ wí pé, ìgbàgbọ́ àti ìfẹ́ yín sì lágbára bí i ti àtẹ̀yìnwá. Inú wa dùn láti gbọ́ wí pé ẹ sì ń rántí ìbágbé wa láàrin yín pẹ̀lú ayọ̀. Tímótíù tún fi yé wa pé bí ó ti mú wa lọ́kàn tó láti rí i yín, ni ó ṣe ẹ̀yin pàápàá."'), '1Thess$3:12': ('bọ', 'ṣe', '1Thess$3:12 "A sì béérè pé, kí Ọlọ́run ràn yín lọ́wọ́ tó bẹ́ẹ̀ tí ìfẹ́ yín yóò fi gbòòrò tí yóò sì ṣàn kan olúkúlùkù yín. Èyí yóò jẹ́ àpẹrẹ gẹ́gẹ́ bí ìfẹ́ àwa náà se sàn kan ẹ̀yin náà."'), '1Thess$4:3': ('ni', None, '1Thess$4:3 " Nítorí ìfẹ́ Ọlọ́run ni pé kí ẹ jẹ́ mímọ, kí ẹ sì yàgò kúrò nínú ẹ̀ṣẹ̀ àgbérè,"'), '1Thess$4:10': (None, None, '1Thess$4:10 "Nitòótọ́, ẹ ní ìfẹ́ fún àwọn arákùnrin tí ó wà káàkiri Makidóníà. Ṣíbẹ̀ṣíbẹ̀ àwa ń bẹ̀ yín, ará, pé kí ẹ̀yin kí ó máa tẹ̀síwájú nínú rẹ̀."'), '1Thess$5:12': (None, None, '1Thess$5:12 " Ẹ̀yin ará, ẹ fi ọlá fún àwọn olórí tí ń ṣe iṣẹ́ àṣekára láàrin yín tí wọn ń kìlọ̀ fún jẹ́ olórí fún yín nínú Olúwa."'), '1Thess$5:15': (None, 'eniyan', '1Thess$5:15 " Ẹ rí i pé kò sí ẹni tí ó fi búburú san búburú, ṣùgbọ́n ẹ maa lépa èyí tíi ṣe rere láàrin ara yín àti sí ènìyàn gbogbo."'), '2Thess$1:3': (None, None, '2Thess$1:3 " Ó yẹ kí a máa dúpẹ́ nígbà gbogbo nítorí yín, ará, àní gẹ́gẹ́ bí ó ti yẹ, nítorí pé ìgbàgbọ́ yín ń dàgbà gidigidi, àti ìfẹ́ olúkúlùkù yín gbogbo sí ara yín ń di púpọ̀."'), '2Thess$2:3': (None, None, '2Thess$2:3 " Ẹ má ṣe jẹ́ kí ẹnikẹ́ni kí ó tàn yín jẹ ní ọ̀nàkọnà, nítorí pé ọjọ́ náà kì yóò dé, bí kò ṣe pé ìṣọ̀tẹ̀ bá kọ́ ṣẹlẹ̀, tí a sì fi ẹni ẹ̀ṣẹ̀ hàn, tí í ṣe ọmọ ègbé."'), '2Thess$2:11': (None, None, '2Thess$2:11 " Nítorí ìdí èyí, Ọlọ́run rán ohun tó ń ṣiṣẹ́ ìsìnà sí wọn kí wọn lè gba èké gbọ́,"'), '2Thess$2:15': (None, 'ni', '2Thess$2:15 " Nítorí náà ará, ẹ dúró sinsin, kí ẹ sì di àwọn ẹ̀kọ́ wọ̀n-ọn-nì tí a kọ́ yín mú, yálà nípa ọ̀rọ̀ ẹnu tàbí lẹ́tà."'), '2Thess$3:1': (None, 'ni', '2Thess$3:1 " Ní àkótan, ará, ẹ máa gbàdúrà fún wa, kí ọ̀rọ̀ Olúwa lè máa tàn káàkiri, kí ó sì jẹ́ èyí tí a bu ọlá fún, gẹ́gẹ́ bí ó ti rí ní ọ̀dọ̀ yín."'), '2Thess$3:7': ('ṣe', 'ni', '2Thess$3:7 Nítorí ẹ̀yin náà mọ̀ bí ó ṣe yẹ kí ẹ máa farawé wa. Àwa kì í ṣe ìmẹ́lẹ́ nígbà tí a wà ní ọ̀dọ̀ yín.'), '1Tim$1:8': (None, None, '1Tim$1:8 "Ṣùgbọ́n àwa mọ̀ pé òfin dára, bí ènìyàn bá lò ó dáradára."'), '1Tim$1:18': ('ṣe', 'ni', '1Tim$1:18 "Àṣẹ yìí ni mo pa fún ọ, Tímótíù ọmọ mi, gẹ́gẹ́ bí ìsọtẹ́lẹ̀ wọ̀nyí tó ó ti ṣáájú nípa rẹ̀, pé nípaṣẹ̀ wọ́n kí ìwọ lè máa ja ìjà rere;"'), '1Tim$2:5': (None, 'eniyan', '1Tim$2:5 "Nítorí Ọlọ́run kan ní ń bẹ, onílàjà kan pẹ̀lú láàrin Ọlọ́run àti ènìyàn, àní Kírísítì Jésù ọkùnrin náà."'), '1Tim$2:7': ('otitọ', None, '1Tim$2:7 "Nítorí èyí tí a yàn mi ṣe oníwàásù, Àpósítélì—òtítọ́ ni èmi ń sọ, èmi kò ṣèké olùkọ́ àwọn aláìkọlà nínú ìgbàgbọ́ àti òtítọ́."'), '1Tim$3:2': (None, None, '1Tim$3:2 "Ǹjẹ́ Bíṣọ́ọ̀bù yẹ kí ó jẹ́ aláìlẹ́gàn, ọkọ aya kan, olùsọ̀ràn, aláìrékọjá, oníwà yíyẹ, olùfẹ́ àlejò ṣíṣe, ẹni tí ó lè ṣe olùkọ́."'), '1Tim$3:13': ('dara', 'daradara', '1Tim$3:13 "Nítorí àwọn tí ó lo ipò díákónì dáradára ra ipò rere fún ara wọn, àti ìgboyà púpọ̀ nínú ìgbàgbọ́ tí ń bẹ nínú Kírísítì Jésù."'), '1Tim$4:10': ('gbagbọ', None, '1Tim$4:10 "Nítorí fún èyí ni àwa ń ṣe làálàá tí a sì ń jìjàkadì, nítorí àwá ní ìrèti nínú Ọlọ́run alààyè, ẹni tí í ṣe Olùgbàlà gbogbo ènìyàn, pẹ̀lúpẹ̀lú ti àwọn ti ó gbàgbọ́."'), '1Tim$4:14': ('fi fun', 'ebun', '1Tim$4:14 "Má ṣe àìnání ẹ̀bùn tí ń bẹ lára rẹ, èyí tí a fi fún ọ nípa ìsọtẹ́lẹ̀ pẹ̀lú ìgbọ́wọ́lé àwọn alàgbà."'), '1Tim$5:4': ('jẹ ki', 'ni', '1Tim$5:4 "Ṣùgbọ́n bí opó kan bá ni ọmọ tàbí ọmọ-ọmọ, jẹ́ kí wọn kọ́kọ́ kọ́ bí a ti ń ṣe ìtọ́jú ilé àwọn tìkárawọn, kí wọn sì san oore àwọn òbí wọn padà; nítorí pé èyí ni ó ṣe ìtẹ́wọ́gbà níwájú Ọlọ́run."'), '1Tim$5:10': (None, 'iṣẹ', '1Tim$5:10 "Ẹni ti a jẹ́rì rẹ̀ fún iṣẹ́ rere; bí ẹni ti ó ti tọ́ ọmọ dàgbà, ti ó ń ṣe ìtọ́jú àlejò, tí ó sì ń wẹ ẹṣẹ̀ àwọn ènìyàn mímọ́, tí ó ti ran àwọn olupọ́njú lọ́wọ́, tí ó sì ń lépa iṣẹ́ rere gbogbo."'), '1Tim$5:16': (None, 'ni', '1Tim$5:16 "Bí ọkùnrin tàbí obìnrin kan tí ó gbàgbọ́ bá ní àwọn opó, kí ó máa ràn wọ́n lọ́wọ́, kí a má sì di ẹrù lé ìjọ, kí wọn lè máa ran àwọn ti í ṣe opó nítòótọ́ lọ́wọ́."'), '1Tim$5:22': (None, None, '1Tim$5:22 "Má ṣe fi ìkánjú gbe ọwọ́ lé ẹnikẹ́ni, bẹ́ẹ̀ ni kí ó má sì ṣe jẹ́ alábàápín nínú ẹ̀ṣẹ̀ àwọn ẹlòmíràn: pa ara rẹ mọ́ ní ìwà funfun."'), '1Tim$5:24': (None, 'tẹle', '1Tim$5:24 "Ẹ̀sẹ̀ àwọn ẹlòmíràn a máa hàn gbangba, a máa lọ ṣáájú sí ìdájọ́; tí àwọn ẹlòmíràn pẹ̀lú a sì máa tẹ̀lé wọn."'), '1Tim$6:2': (None, 'ni', '1Tim$6:2 "Àwọn ti ó ní ọ̀gá onígbàgbọ́, kí wọn má ṣe ṣàìbọ̀wọ̀ fún wọn nítorí tí wọn jẹ́ arákùnrin; ṣùgbọ́n kí wọn túbọ̀ máa sìn wọ́n, nítorí àwọn tí í ṣe alábàápín iṣẹ́ rere wọn jẹ́ onígbàgbọ́ àti olùfẹ́. Nǹkan wọ̀nyí ni kí ó máa kọ́ ni, kí o sì máa fi gbani níyànjú."'), '1Tim$6:3': (None, None, '1Tim$6:3 "Bí ẹnikẹ́ni bá ń kọ́ni ní ẹ̀kọ́ mìíràn, ti kò sì gba ọ̀rọ̀ ti ó yè kooro, ti Jésù Kírísítì Olúwa wa, àti ẹ̀kọ́ ti ó bá ìwà-bí-Ọlọ́run mu,"'), '1Tim$6:8': (None, None, '1Tim$6:8 "Bí a sì ni oúnjẹ àti aṣọ, ìwọ̀nyí yóò tẹ́ wa lọ́rùn."'), '1Tim$6:13': ('jẹri', None, '1Tim$6:13 " Mo paṣẹ fún ọ níwájú Ọlọ́run, tí ń sọ ohun gbogbo di ààyè, àti níwájú Jésù Kírísítì, ẹni tí ó jẹ́rìí níwájú Pọ́ńtíù Pílátù,"'), '1Tim$6:17': ('wá', 'ṣe', '1Tim$6:17 "Kìlọ̀ fún àwọn tí ó lọ́rọ̀ ní ayé ìṣinṣinyìí kí wọn ma ṣe gbéraga, bẹ́ẹ̀ ni kí wọn má ṣe gbẹ́kẹ̀lé ọ̀rọ̀ àìdánilójú, bí kò se lé Ọlọ́run alààyè, tí ń fi ohun gbogbo fún wa lọ́pọ̀lọpọ̀ láti lò;"'), '1Tim$6:20': (None, None, '1Tim$6:20 "Tìmótíù, máa sọ ohun tí a fi sí ìtọ́jú rẹ, yà kúrò nínú ọ̀rọ̀ asán àti ìjiyàn ohun tí a ń fi èké pè ni ìmọ̀;"'), '2Tim$1:12': ('ni', 'jiya', '2Tim$1:12 "Nítorí ìdí èyí ní èmi ṣe ń jìyà wọ̀nyí pẹ̀lú. Ṣùgbọ́n ojú kò tì mí: nítorí èmi mọ ẹni tí èmi gbàgbọ́, ó sì dá mi lójú pé, òun lè pa ohun ti mo fi lé e lọ́wọ́ mọ́ títí di ọjọ́ náà."'), '2Tim$2:11': ('ni', None, '2Tim$2:11 "Òtítọ́ ni ọ̀rọ̀ náà: Bi àwa bá bá a kú, àwa yóò yè pẹ̀lú rẹ̀."'), '2Tim$2:21': (None, None, '2Tim$2:21 "Bí ẹnìkẹni bá wẹ ara rẹ̀ mọ́ kúrò nínú ìwọ̀nyí, òun yóò jẹ́ ohun-èlò sí ọlá, tí a yà sọ́tọ̀, tí ó sì yẹ fún ìlò baálé, tí a sì ti pèsè sílẹ̀ fún iṣẹ́ rere gbogbo."'), '2Tim$3:2': (None, None, '2Tim$3:2 "Nitori àwọn ènìyàn yóò jẹ́ olùfẹ́ ti ara wọn, olùfẹ́ owó, afọ́nnu, agbéraga, asọ̀rọ̀-búburú, aṣàìgbọ́ràn sí òbí, aláìlọ́pẹ́, aláìmọ́."'), '2Tim$3:16': (None, None, '2Tim$3:16 "Gbogbo ìwé-Mímọ́ ni ó ní ìmísí Ọlọ́run tí ó sì ní èrè fún ẹ̀kọ́, fún ìbániwí, fún ìtọ́ni, fún ìkọ́ni tí ó wà nínú òdodo."'), '2Tim$4:3': (None, None, '2Tim$4:3 "Nítorí pé ìgbà yóò dé, tí wọn kì yóò lè gba ẹ̀kọ́ tí ó yè kooro, ṣùgbọ́n bí wọ́n ti jẹ́ ẹni tí etí ń rin, wọn ó lọ kó oluko jọ fún ara wọn nínú ìfẹ́kùfẹ́ ara wọn."'), '2Tim$4:5': ('ṣe', 'iṣẹ', '2Tim$4:5 "Ṣùgbọ́n máa faradà ìpọ́nju, ṣe iṣẹ́ ẹ̀fáńjẹ́lísítì, ṣe iṣẹ́ rẹ láṣepé."'), '2Tim$4:7': (None, None, '2Tim$4:7 "Èmi ti ja ìjà rere, èmi tí parí iré-ìje mi, èmi ti pa ìgbàgbọ́ mọ́;"'), '2Tim$4:16': (None, 'eniyan', '2Tim$4:16 "Ní àkọ́kọ́ jẹ́ ẹjọ́ mi, kò sí ẹni tí ó ba mi gba ẹjọ́ rò ṣùgbọ́n gbogbo ènìyàn ni o kọ̀ mi sílẹ̀: Àdúrà mi ni kí a má ṣe ká à sí wọn lọ́rùn."'), 'Titus$1:5': ('ní', None, 'Titus$1:5 Ìdí tí mo fi fi ọ́ sílẹ̀ ní Kírétè ni pé kí o lè ṣe àsepé àwọn iṣẹ́ tó sẹ́kù. Mo sì ń rọ̀ ọ́ kí o yan àwọn alàgbà ní ìlú kọ̀ọ̀kan gẹ́gẹ́ bí mo ṣe darí rẹ̀.'), 'Titus$1:7': (None, None, 'Titus$1:7 "Alámójútó jẹ́ ẹni tí a gbé iṣẹ́ Ọlọ́run lé lọ́wọ́, nítorí náà, kò gbọdọ̀ ní àbùkù kankan tàbí agbéraga, oníjà, kò gbọdọ̀ jẹ́ ọ̀mùtí tàbí alágídí tàbí olójúkòkòrò."'), 'Titus$2:9': (None, None, 'Titus$2:9 "Kọ́ àwọn ẹrú láti se ìgbọ́ran sí àwọn olówó wọn nínú ohun gbogbo, láti máa gbìyànjú láti tẹ́ wọn lọ́rùn, wọn kò gbọdọ̀ gbó olówó wọn lẹ́nu,"'), 'Titus$2:11': (None, None, 'Titus$2:11 Nítorí oore-ọ̀fẹ́ Ọlọ́run tó mú ìgbàlà wà ti fara hàn fún gbogbo ènìyàn.'), 'Titus$3:1': (None, 'eniyan', 'Titus$3:1 "Rán àwọn ènìyàn náà létí láti máa tẹríba fún ìjọba àti àwọn aláṣẹ. Kí wọn ṣe ìgbọ́ràn nígbà gbogbo, kí wọn sì múra fún iṣẹ́ rere gbogbo."'), 'Titus$3:4': (None, 'ni', 'Titus$3:4 "Ṣùgbọ́n nígbà tí inú rere àti ìfẹ́ Ọlọ́run Olùgbàlà wa farahàn,"'), 'Phlm$1:10': ('ni', None, 'Phlm$1:10 " Èmí bẹ̀ ọ́ nítorí ọmọ mi Ónísímu, ẹni tí ó di ọmọ nígbà tí mo wà nínú ìdè."'), 'Phlm$1:18': ('ni', None, 'Phlm$1:18 "Bí ó bá ti ṣe ọ́ ní ibi kan tàbí jẹ ọ́ ní gbésè ohun kan, kà á sí mi lọ́rùn."'), 'Phlm$1:20': (None, 'ni', 'Phlm$1:20 "Èmi ń fẹ́, arákùnrin, pé kí èmi kí ó lè ni àǹfààní kan láti ọ̀dọ̀ rẹ nínú Olúwa; fi ayọ̀ rẹ kún ọkàn mi nínú Jésù."'), 'Heb$1:7': ('ina', 'ina', 'Heb$1:7 " Àti nípa ti àwọn ańgẹ́lì, ó wí pé; “Ẹni tí ó dá àwọn ańgẹ́lì rẹ̀ ní ẹ̀mi, àwọn ìránṣẹ́ rẹ̀ ní ọwọ́ ìná.”"'), 'Heb$1:9': ('ororo', None, 'Heb$1:9 "Ìwọ fẹ́ òdodo, bẹ́ẹ̀ ni ìwọ kórìíra iṣẹ́ búburú; nítorí èyí ni Ọlọ́run, àní Ọlọ́run rẹ ṣe fi òróró ayọ̀ yàn ọ tí ó gbé ọ ju àwọn ẹgbẹ́ rẹ lọ.”"'), 'Heb$2:1': (None, 'ni', 'Heb$2:1 "Nítorí náà, ó yẹ kí àwa máa fi iyè sí àwọn nǹkan wọ̀n-ọn-nì gidigidi tí àwa ti gbọ́, kí a má baà gbá wa lọ kúrò nínú wọn nígbà kan."'), 'Heb$2:14': ('ní', None, 'Heb$2:14 "Ǹjẹ́ ni ìwọ̀n bí àwọn ọmọ tí ṣe alábàápín ará àti ẹ̀jẹ̀, bẹ́ẹ̀ gẹ́gẹ́ ni òun pẹ̀lú sì ṣe alábàápín nínú ohun kan náà; kí ó lè ti ipa ikú rẹ̀ pa ẹni tí ó ní agbára ikú run, èyí ni Èṣù."'), 'Heb$3:5': ('ile', None, 'Heb$3:5 " Mósè nítòótọ́ sì ṣe olóòótọ́ nínú gbogbo iṣẹ́ rẹ̀ nínú ilé Ọlọ́run, bí ìránṣẹ́, fún ẹ̀rí ohun tí a ó sọ̀rọ̀ wọ̀n ní ìgbà ìkẹyìn."'), 'Heb$3:7': ('ri', 'gbo', 'Heb$3:7 " Nítorí náà gẹ́gẹ́ bi Ẹ̀mi Mímọ́ tí wí: “Lònì-ín bí ẹ̀yín bá gbọ́ ohùn rẹ̀,"'), 'Heb$3:14': ('ṣe', None, 'Heb$3:14 "Nítorí àwá di alábàápín pẹ̀lú Kírísítì, bí àwá bá di ìpilẹ̀ṣẹ̀ ìgbẹ́kẹ̀lé wa mú ṣinṣin títí dé òpin;"'), 'Heb$4:6': (None, 'gbo', 'Heb$4:6 "Nítorí náà bí ó tí jẹ́ pé, ó ku kí àwọn kan wọ inú rẹ̀, àti àwọn tí a tí wàásù ìyìn rere náà fún ní ìṣáajú kò wọ inú rẹ̀ nítorí àìgbọ́ràn:"'), 'Heb$4:8': (None, 'ni', 'Heb$4:8 "Nítorí, ìbá ṣe pé Jóṣúà tí fún wọn ní ìsinmi, òun kì bá tí sọ̀rọ̀ nípa ọjọ́ ìsinmi mìíràn lẹ̀yìn náà,"'), 'Heb$4:12': (None, None, 'Heb$4:12 "Nítorí ọ̀rọ̀ Ọlọ́run yè, ó sì ní agbára, ó sì mu ju idàkídà olójú-méjì lọ, ó sì ń gunni àní títí dé pínpín ọkàn àti ẹ̀mí níyà, àti ní oríkèé àti ọ̀rá inú egungun, òun sì ni olùmọ̀ èrò inú àti ète ọkàn."'), 'Heb$4:14': ('jẹ ki', 'ni', 'Heb$4:14 "Ǹjẹ́ bí a ti ní olórí àlùfáà ńlá kan, tí ó ti la àwọn ọ̀run kọjá lọ, Jésù ọmọ Ọlọ́run, ẹ jẹ́ kí a di ìjẹ́wọ́ wa mú ṣinṣin."'), 'Heb$4:16': ('wa', 'gba', 'Heb$4:16 "Nítorí náà, ẹ jẹ́ kí a wá si ibi ìtẹ́ oore-ọ̀fẹ́ pẹ̀lú ìgboyà, kí a lè rí àánú gbà, kí a sì rí oore-ọ̀fẹ́ láti máa ranni lọ́wọ́ ní àkókò tí ó wọ̀."'), 'Heb$5:1': ('ya', 'ni', 'Heb$5:1 "Nítorí olórí àlùfáà kọ̀ọ̀kan tí a yàn nínú àwọn ènìyàn, ní a fi jẹ nítorí iṣẹ́ ìsìn àwọn ènìyàn sí Ọlọ́run láti máa mú ẹ̀bùn àti ẹbọ wá nítorí ẹ̀ṣẹ̀."'), 'Heb$5:5': ('ṣe', 'ni', 'Heb$5:5 " Bẹ́ẹ̀ ni Kírísítì pẹ̀lú kò si ṣe ara rẹ̀ lógo láti jẹ́ olórí àlùfáà; bí kò ṣe ẹni tí o wí fún ún pé, “Ìwọ ni ọmọ mi, lónìn-ín ni mo bi ọ.”"'), 'Heb$5:14': (None, None, 'Heb$5:14 "Ṣùgbọ́n oúnjẹ líle wà fún àwọn tí ó dàgbà, àwọn ẹni nípa ìrírí, tí wọn ń lo ọgbọ́n wọn láti fi ìyàtọ̀ sáàrin rere àti búburú."'), 'Heb$6:4': ('ṣe', None, 'Heb$6:4 "Nítorí pé, kò ṣe é ṣe fún àwọn tí a ti là lójú lẹ́ẹ̀kan, tí wọ́n sì ti tọ́ ẹ̀bùn ọ̀run wò, tí wọn sì ti di alábàápín Ẹ̀mí Mímọ́,"'), 'Heb$6:11': (None, 'ireti', 'Heb$6:11 "Àwá sì fẹ́ kí olúkúlùkù yín máa fi irú àìsinmi kan náà hàn, fún ẹ̀kún ìdánilójú ìrètí títí dé òpin:"'), 'Heb$6:17': (None, 'ileri', 'Heb$6:17 "Nínú èyí tí Ọlọ́run, ẹni tí ń fẹ́ gidgidi láti fi àìlèyípadà ète rẹ̀ hàn fún àwọn ajogún ìlérí náà, ó fi ìbúra sáàrin wọn."'), 'Heb$7:1': (None, None, 'Heb$7:1 " Nítorí Melikisédékì yìí, ọba Sálẹ́mù, àlùfáà Ọlọ́run ọ̀gá ògo, ẹni tí ó pàdé Ábúráhámù bí ó ti ń padà bọ̀ láti ibi pípa àwọn ọba, tí ó sì súre fún un."'), 'Heb$7:5': ('ní', 'gba', 'Heb$7:5 "Àti nítòótọ́ àwọn tí ó jẹ́ ọmọ Léfì, tí o gba oyè àlùfáà, wọ́n ní àṣẹ láti máa gba ìdámẹ́wàá lọ́wọ́ àwọn ènìyàn gẹ́gẹ́ bí òfin, èyí yìí, lọ́wọ́ àwọn arákùnrin wọn, bí ó tilẹ̀ ti jẹ́ pé, wọn ti inú Ábúráhámù jáde."'), 'Heb$7:19': (None, None, 'Heb$7:19 "(Nítorí òfin kò mú ohunkóhun pé), a sì mú ìrètí tí ó dára jù wá nípa èyí tí àwa ń súnmọ́ Ọlọ́run."'), 'Heb$7:20': (None, None, 'Heb$7:20 Níwọ̀n bí ó sì ti ṣe pé kì í ṣe ní àìbúra ni.'), 'Heb$7:26': (None, None, 'Heb$7:26 "Nítorí pé irú olórí àlùfáà bẹ́ẹ̀ ni o yẹ wá, mímọ́, àìlẹ́gàn, àìléèérí, tí a yà sọ́tọ̀ kúrò láàrin àwọn ẹlẹ́ṣẹ̀, tí a sì gbéga ju àwọn ọ̀run lọ;"'), 'Heb$8:8': ('mu', None, 'Heb$8:8 " Nítorí tí o rí àbùkù lára wọn, ó wí pé, “Kíyèsí i ọjọ́ ń bọ̀, ni Olúwa wí, ti èmi o bá ilé Ísírẹ́lì àti ilé Júdà dá májẹ̀mu títún."'), 'Heb$9:7': ('nikan', 'nikan', 'Heb$9:7 " Ṣùgbọ́n sínú èkejì ni olórí àlùfáà nìkan máa ń lọ lẹ́kan ṣoṣo lọ́dún, fún ara rẹ̀, àti fún ìsìnà àwọn ènìyàn."'), 'Heb$9:11': (None, None, 'Heb$9:11 "Ṣùgbọ́n nígbà tí Kírísítì dé bí Olórí àlùfáà àwọn ohun rere tí ń bọ̀, nípaṣẹ̀ àgọ́ tí o tóbi ti ó sì pé ju ti ìṣáájú, (èyí tí a kò fi ọwọ́ dá, èyí yìí ni, tí kì í ṣe ti ẹ̀dá yìí),"'), 'Heb$9:15': (None, 'ileri', 'Heb$9:15 "Àti nítorí èyí ni ó ṣe jẹ́ alárinà májẹ̀mú tuntun pé bí ikú ti ń bẹ fún ìdáǹdè àwọn ìrékọjá ti o tí wà lábẹ́ májẹ̀mú ìṣáájú, kí àwọn tí a ti pè lè rí ilèrí ogún àìnípẹ̀kun gbà."'), 'Heb$9:17': ('agbara', None, 'Heb$9:17 Nítorí ìwé ogún ní agbára lẹ̀yìn ìgbà tí ènìyàn bá kú: Nítorí kò ní agbára rárá nígbà tí ẹni tí o ṣè e bá ń bẹ láàyè.'), 'Heb$9:24': ('ṣe', 'ni', 'Heb$9:24 "Nítorí Kírísítì kò wọ ibi mímọ́ tí a fi ọwọ́ ṣe lọ tí i ṣe àpẹẹrẹ ti òtítọ́; ṣùgbọ́n ó lọ sí ọ̀run páàpáà, nísinsin yìí láti farahàn ní iwájú Ọlọ́run fún wa:"'), 'Heb$9:27': ('wo', None, 'Heb$9:27 "Níwọ̀n bí a sì ti fi lélẹ̀ fún gbogbo ènìyàn láti kú lẹ̀ẹ̀kanṣoṣo, ṣùgbọ́n lẹ̀yìn èyí ìdájọ́:"'), 'Heb$10:1': (None, 'ṣe', 'Heb$10:1 "Nítorí òfin, bí o ti ní òjìjì àwọn ohun rere ti ń bẹ̀ tí kì í ṣe àwòrán tóòtọ́ fún rere nǹkan náà, kò lè fi ẹbọ kan náà tí wọn ń rú nígbàgbogbo lọdọ́ọ̀dún mu àwọn tí ń wá sibẹ̀ di pípé."'), 'Heb$10:7': (None, 'ṣe', 'Heb$10:7 "Nígbà náà ni mo wí pé, ‘Kíyèsí i (nínú ìwé kíká ni a gbé kọọ́ nípa ti èmi) mo dé láti ṣe ìfẹ́ rẹ, Ọlọ́run.’ ”"'), 'Heb$10:8': (None, 'ṣe', 'Heb$10:8 "Nígbà tí o wí ni ìṣáajú pé, “Ìwọ kò fẹ́ ẹbọ àti ọ̀rẹ àti ẹbọ sísun, àti ẹbọ fún ẹ̀ṣẹ̀, bẹ́ẹ̀ ni ìwọ kò ni inú dídùn si wọn” (àwọn èyí tí a ń rú gẹ́gẹ́ bí òfin)."'), 'Heb$10:11': ('ní', 'gba', 'Heb$10:11 "Àti olukulùkù àlùfáà sì ń dúró lójoojúmọ́ láti ṣiṣẹ́ ìsìn, ó sì ń ṣe ẹbọ kan náà nígbàkúgbà, tí kò lè mú ẹ̀ṣẹ̀ kúrò láé:"'), 'Heb$10:15': (None, None, 'Heb$10:15 "Ẹ̀mí mímọ́ sì ń jẹri fún wa pẹ̀lú: Nítorí lẹ̀yìn tí ó wí pé,"'), 'Heb$10:23': ('wo', None, 'Heb$10:23 Ẹ jẹ́ kí a di ìjẹ́wọ́ ìreti wa mu ṣinṣin ni àìsiyèméjì; (nítorí pé olóòtọ́ ní ẹni tí o ṣe ìlérí).'), 'Heb$10:28': (None, None, 'Heb$10:28 " Ẹnikẹ́ni tí ó ba gan òfin Móṣè, ó kú láìsí àánú nípa ẹ̀rí ẹni méjì tàbí mẹ́ta:"'), 'Heb$10:34': (None, 'ni', 'Heb$10:34 "Nítorí ẹ̀yin bá àwọn tí ó wà nínú ìdè kẹ́dùn, ẹ sì fi ayọ̀ gba ìkólọ ẹrù yin, nítorí ẹ̀yin mọ nínú ara yin pé, ẹ ni ọrọ̀ tí ó wà títí, tí ó sì dára jù bẹ́ẹ̀ lọ."'), 'Heb$10:36': ('ni', 'gba', 'Heb$10:36 "Nítorí ẹ̀yin kò le ṣe aláìní suuru, nítorí ìgbà tí ẹ̀yin bá ti ṣe ìfẹ́ Ọlọ́run tan kí ẹ̀yin le gba ìlérí náà."'), 'Heb$11:3': ('ri', None, 'Heb$11:3 Nípa ìgbàgbọ́ ni a mọ̀ pé a ti dá ayé nípa ọ̀rọ̀ Ọlọ́run; nítorí náà kì í ṣe ohun tí o hàn ni a fi dá ohun tí a ń ri.'), 'Heb$11:5': ('ri', None, 'Heb$11:5 " Nípa ìgbàgbọ́ni a sì Énókù nípò padà kí o má ṣe rí ikú; a kò sí rí i, nítorí ti Ọlọ́run ṣi i nípò padà: nítorí ṣaáju ìsìpo padà rẹ̀, a jẹ́ri yìí si i pé o wu Ọlọ́run."'), 'Heb$11:9': ('ilẹ', None, 'Heb$11:9 "Nípa ìgbàgbọ́ ní o ṣe àtìpó ní ilẹ̀ ìlérí, bí ẹni pé ni ilẹ̀ àjèjì, o ń gbé inú àgọ́, pẹ̀lú Ísáákì àti Jákọ́bù, àwọn ajogún ìlérí kan náà pẹ̀lú rẹ̀:"'), 'Heb$11:24': ('ní', None, 'Heb$11:24 " Nípa ìgbàgbọ́ ni Móṣè, nígbà tí o dàgbà, ó kọ̀ ki a máa pé òun ni ọmọ ọmọ-bìnrin Fáráò;"'), 'Heb$11:35': ('gba', 'gba', 'Heb$11:35 " Àwọn obìnrin ri òkú wọn gbà nípa àjíǹde: a sì da àwọn ẹlòmíràn lóró, wọ́n kọ̀ láti gba ìdásílẹ̀; kí wọn ba lè rí àjíǹde tí o dára jù gbà;"'), 'Heb$11:39': (None, None, 'Heb$11:39 "Gbogbo àwọn wọ̀nyí tí a jẹ́ri rere sí nípa ìgbàgbọ́, wọn kò sì rí ìlérí náà gbà:"'), 'Heb$12:1': (None, 'ije', 'Heb$12:1 "Nítorí náà bí a ti fi ikúukùu àwọsánmà tí o kún fún àwọn ẹlẹ́ri tó báyìí yí wa ká, ẹ jẹ́ kí a pa ohun ìdíwọ́ gbogbo tí si apákan, àti ẹ̀ṣẹ̀ tí o rọrùn láti di mọ́ wa, kí a sì máa fi sùúrù súré ìje tí a gbé ka iwájú wa,"'), 'Heb$12:9': ('fun', 'ni', 'Heb$12:9 "Pẹ̀lúpẹ̀lú àwa ni baba wa nípa ti ara tí o ń tọ́ wa, àwa sì ń bu ọlá fún wọn: kò ha yẹ kí a kúkú tẹríbà fún Baba àwọn ẹ̀mí, kí a sì yè?"'), 'Heb$12:14': (None, 'eniyan', 'Heb$12:14 "Ẹ máa lépa àlàáfíà pẹ̀lú ènìyàn gbogbo, àti ìwà mímọ́, láìsí èyí yìí kò sí ẹni tí yóò rí Olúwa:"'), 'Heb$12:18': (None, None, 'Heb$12:18 " Nítorí ẹ̀yin kò wá òkè tí a lè fi ọwọ́ kàn, àti ti iná ti ń jó, àti ti ìṣúdudu àti òkùnkùn, àti ìjì."'), 'Heb$12:28': ('jẹ ki', 'ni', 'Heb$12:28 "Nítorí náà bí àwa tí ń gbà ilẹ̀ ọba ti a kò lè mì, ẹ jẹ́ kí a ni oore-ọfẹ́ nípa èyí ti a fi lè máa sin Ọlọ́run ni ìtẹ́tẹ́wọ́gbà pẹ̀lú ọ̀wọ̀ àti ìbẹ̀rù rẹ̀."'), 'Heb$13:15': (None, None, 'Heb$13:15 " Ǹjẹ́ nípasẹ̀ rẹ̀, ẹ jẹ́ kí a máa rú ẹbọ ìyìn si Ọlọ́run nígbà gbogbo, èyí yìí ni èso ètè wa, tí ń jẹ́wọ́ orúkọ rẹ̀."'), 'Heb$13:16': (None, None, 'Heb$13:16 Ṣùgbọ́n ẹ má ṣe gbàgbé láti máa ṣoore àti láti máa pín funni nítorí irú ẹbọ wọ̀nyí ni inú Ọlọ́run dùn sí jọjọ'), 'Heb$13:17': ('ni', 'fun', 'Heb$13:17 "Ẹ máa gbọ́ ti àwọn tí ń ṣe olórí yín, kí ẹ sì máa tẹríbà fún wọ́n: Nítorí wọn ń ṣọ́ ẹ̀sọ́ nítorí ọkàn yín, bí àwọn ti yóò ṣe ìsírò, kí wọn lè fi ayọ̀ ṣe èyí, kì í ṣe pẹ̀lú ìbànújẹ, nítorí èyí yìí yóò jẹ àìlérè fún yín."'), 'Heb$13:19': (None, None, 'Heb$13:19 "Ṣùgbọ́n èmi ń bẹ̀ yín gidigidi sí i láti máa ṣe èyí, kí a ba lè tètè fi mi fún yín padà."'), 'Heb$13:20': ('mu', None, 'Heb$13:20 " Ǹjẹ́ Ọlọ́run àlàáfíà, ẹni tí o tún mu olùsọ́-àgùntàn ńlá ti àwọn àgùntàn, ti inú òkú wá, nípa ẹ̀jẹ̀ májẹ̀mu ayérayé, àní Olúwa wa Jésù."'), 'Heb$13:22': ('ni', None, 'Heb$13:22 "Èmi sì ń bẹ̀ yín ará, ẹ gbà ọ̀rọ̀ ìyànjú mi; nítorí ìwé kukuru ni mo kọ sí yín."'), 'Jas$1:4': ('ni', 'jẹ ki', 'Jas$1:4 "Ṣùgbọ́n ẹ jẹ́ kí sùúrù kí ó ṣiṣẹ́ àṣepé, kí ẹ̀yin kí ó lè jẹ́ pípé àti aláìlábùkù tí kò se aláìní ohunkóhun."'), 'Jas$3:3': (None, None, 'Jas$3:3 "Bí a bá sì fi ìjánu bọ ẹṣin lẹ́nu, kí wọn kí ó le gbọ́ tiwa, gbogbo ara wọn ni àwa sì ń tì kiri pẹ̀lú."'), 'Jas$3:7': ('eniyan', None, 'Jas$3:7 "Nítorí olúkúlùkù ẹ̀dá ẹranko, àti ti ẹyẹ, àti ti ejò, àti ti ohun tí ó ń bẹ ní òkun, ni à ń tù lójú, tí a sì ti tù lójú láti ọwọ́ ẹ̀dá ènìyàn wá."'), 'Jas$3:17': (None, None, 'Jas$3:17 "Ṣùgbọ́n ọgbọ́n tí ó ti òkè wá jẹ́ mímọ́ ní àkọ́kọ́, ti àlàáfíà, àti ti ìrẹ̀lẹ̀, kì í sì í ṣòro láti bẹ̀, ó kún fún àánú àti fún èso rere, kì í ṣe ojúsáájú, a sì máa sọ òtítọ́."'), 'Jas$4:13': ('lọ', None, 'Jas$4:13 "Ẹ wá nísinsìn yìí, ẹ̀yin tí ó ń wí pé, “Lónìí tàbí lọ́la àwa ó lọ sí ìlú báyìí, a ó sì ṣe ọdún kan níbẹ̀, a ó sì ṣòwò, a ó sì jèrè.”"'), 'Jas$5:3': ('jẹ', None, 'Jas$5:3 "Góòlù òun sílífà yín díbàjẹ́; ìbàjẹ́ wọn ni yóò sì ṣe ẹlẹ́rìí sí yín, tí yóò sì jẹ ẹran ara yín bí iná. Ẹ̀yin tí kó ìsúra jọ de ọjọ́ ìkẹyìn."'), 'Jas$5:10': (None, 'ni', 'Jas$5:10 "Ará mi, ẹ fi àwọn wòlíì tí ó ti ń sọ̀rọ̀ ní orúkọ Olúwa ṣe àpẹẹrẹ ìyà jíjẹ, àti sùúrù."'), '1Pet$1:6': (None, 'wo', '1Pet$1:6 "Ẹ yọ̀ nínú èyí púpọ̀, bí ó tilẹ̀ jẹ́ pe nísinsin yìí fún ìgbà díẹ̀, níwọ̀nbí ó ti yẹ, a ti fi ọ̀pọ̀lọpọ̀ ìdánwò bá yín nínú jẹ́:"'), '1Pet$1:10': ('wá', 'tẹle', '1Pet$1:10 "Ní ti ìgbàlà yìí, àwọn wòlíì tí wọ́n sọ àsọtẹ́lẹ̀ nípa oore-ọ̀fẹ́ tí ó mú tọ̀ yín wá, wádìí jinlẹ̀ jinlẹ̀ pẹ̀lú ìbìkítà tí ó ga jùlọ."'), '1Pet$1:17': (None, None, '1Pet$1:17 "Níwọ̀n bí ẹ̀yin ti ń képe Baba, ẹni tí ń ṣe ìdájọ́ gẹ́gẹ́ bí iṣẹ́ olúkúlùkù láìṣe ojúsáájú, ẹ máa lo ìgbà àtipó yin ni ìbẹ̀rù."'), '1Pet$1:22': ('bi', 'ni', '1Pet$1:22 "Níwọ̀n bí ẹ̀yin ti wẹ ọkàn yin mọ́ nípa ìgbọ́ràn yín sí òtítọ́ sí ìfẹ́ ará ti kò ní ẹ̀tàn, ẹ fẹ́ ọmọnikejì yín gidigidi láti ọkàn wá."'), '1Pet$2:11': (None, None, '1Pet$2:11 "Olùfẹ́ ọ̀wọ́n, mo bẹ̀ yín, bí àlejò àti èrò, láti fà sẹ́hìn kúrò nínú ìfẹ́kúfẹ̀ẹ́ ara, tí ń ba ọkàn jagun;"'), '1Pet$2:21': (None, None, '1Pet$2:21 "Sí èyí ni a pè yín: nítorí Kírísítì pẹ̀lú jìyà fún-un yin, ó fi àpẹẹrẹ sílẹ̀ fún-un yín, kí ẹ̀yin lè máa tọ ipaṣẹ̀ rẹ̀:"'), '1Pet$3:3': (None, None, '1Pet$3:3 "Kí ọ̀sọ́ yín má ṣe jẹ́ ọ̀sọ́ òde, tí irun dídì, àti wúrà lílò, tàbí ti aṣọ wíwọ̀;"'), '1Pet$3:8': (None, 'ife', '1Pet$3:8 "Lákòótan, kí gbogbo yín jẹ́ onínú kan, ẹ máa bá ara yín kẹ́dùn, ẹ ní ìfẹ́ ará, ẹ máa ṣe ìyọ́nú, ẹ ni ẹ̀mí ìrẹ̀lẹ̀"'), '1Pet$3:14': (None, 'jiya', '1Pet$3:14 " Ṣùgbọ́n bí ẹ̀yin bá jìyà nítorí òdodo, àlàáfíà ni: ẹ má ṣe bẹ̀rù wọn, kí ẹ má sì ṣe kọminú;"'), '1Pet$3:17': (None, None, '1Pet$3:17 "Nítorí ó sàn, bí ó bá jẹ́ ìfẹ́ Ọlọ́run, kí ẹ jìyà fún ṣíṣe rere ju fún ṣiṣé búburú lọ."'), '1Pet$3:21': (None, None, '1Pet$3:21 "Àpẹẹrẹ èyí ti ń gbà yín là nísinsìnyìí pẹ̀lú, àní ìtẹ̀bọmi, kì í ṣe wíwẹ́ èérí ti ara nù, bí kò ṣe ìdáhùn ẹ̀rí ọkàn rere si Ọlọ́run, nípa àjíǹde Jésù Kírísítì."'), '1Pet$4:3': (None, None, '1Pet$4:3 "Nítorí ìgbà tí ó ti kọjá ti tó fún ṣíṣe ìfẹ́ àwọn aláìkọlà, rìnrìn nínú ìwà wọ̀bìà, ìfẹ́kúfẹ̀ẹ́, ọtí àmupara, ìréde òru, kíkó ẹ̀gbẹ́ ọ̀mùtí, àti ìbọ̀rìṣà tí í ṣe ohun ìríra."'), '1Pet$4:6': (None, None, '1Pet$4:6 "Nítorí èyí ní a ṣá ṣe wàásù ìyìn rere fún àwọn òkú, kí a lè ṣe ìdájọ́ wọn gẹ́gẹ́ bí ènìyàn nípa ti ara, ṣùgbọ́n kí wọ́n lè wà láàyè si Ọlọ́run nípa tí Ẹ̀mí."'), '1Pet$5:10': ('jiya', 'ni', '1Pet$5:10 "Ọlọ́run oore-ọ̀fẹ́ gbogbo, tí ó ti pè yín sínú ògo rẹ̀ tí kò nípẹ̀kun nínú Kírísítì Jésù, nígbà tí ẹ̀yin bá ti jìyà díẹ̀, òun tìkararẹ̀, yóò sì ṣe yín ní àṣepé, yóò fi ẹsẹ̀ yín múlẹ̀, yóò fún yín lágbára, yóò fi ìdí yín kalẹ̀."'), '2Pet$1:10': ('ṣe', None, '2Pet$1:10 "Nítorí náà, ará ẹ túbọ̀ máa ṣe àìsinmi láti sọ ìpè àti yíyàn yín di dájúdájú: nítorí bí ẹ̀yin bá ń ṣe nǹkan wọ̀nyí, ẹ̀yin kì yóò subú."'), '2Pet$1:16': ('ṣe', 'ni', '2Pet$1:16 "Nítorí kì í ṣe bí ẹni tí ó ń tọ ìtàn asán lẹ́yìn tí a fí ọgbọ́nkọ́gbọ́n là sílẹ̀, nígbà tí àwa sọ fún yín ní ti agbára àti wíwá Jésù Kírísítì Olúwa, ṣùgbọ́n ẹlẹ́rìí Ọlá-ńlá Rẹ̀ ni àwa jẹ́."'), '2Pet$1:19': (None, 'ni', '2Pet$1:19 "Àwa sì ní ìfẹsẹ̀múlẹ̀ ọ̀rọ̀ àwọn wòlìí dunjúdunjú síi, èyí tí ó yẹ kí ẹ kíyèsí gẹ́gẹ́ bí ìmọ́lẹ̀ tó ń mọ́lẹ̀ níbi tí òkùnkùn gbé wà, títí ilẹ̀ yóò fi mọ́, tí ìràwọ́ òwúrọ̀ yóò sì yọ lọ́kàn yín."'), '2Pet$1:21': (None, 'eniyan', '2Pet$1:21 Nítorí àsọtẹ́lẹ̀ kan kò ti ipá ìfẹ́ ènìyàn wá rí; ṣùgbọ́n àwọn ènìyàn ń sọ̀rọ̀ láti ọ̀dọ̀ Ọlọ́run bí a ti ń darí wọn láti ọwọ́ Ẹ̀mí Mímọ̀ wá.'), '2Pet$2:2': ('tẹle', None, '2Pet$2:2 "Ọ̀pọ̀lọpọ̀ ni yóò si máa tẹ̀lé ọ̀nà ìtìjú wọn, tí wọn yóò sì mú kí ọ̀nà òtítọ́ di ìsọ̀rọ̀-òdì-sí."'), '2Pet$2:4': (None, None, '2Pet$2:4 "Nítorí pé bi Ọlọ́run kò bá dá àwọn ańgẹ́lì si nígbà tí wọn ṣẹ̀, ṣùgbọ́n ti ó sọ wọ́n sí ìsàlẹ̀ ọ̀gbun tí ó sì fi wọ́n sínú ọ̀gbun òkùnkùn biribiri nínú ìfipamọ́ títí dé ìdájọ́."'), '2Pet$2:18': ('wà', None, '2Pet$2:18 "Nítorí ìgbà tí wọ́n bá ń sọ̀rọ̀ ìhàlẹ̀ asán, nínú ìfẹ́kúfẹ̀ẹ́ ara, wọn a máa tan àwọn tí wọ́n fẹ́rẹ̀ má tí ì bọ́ tán kúrò lọ́wọ́ àwọn tí wọ́n wà nínú ìsìnà."'), '2Pet$2:21': (None, 'ni', '2Pet$2:21 "Nítorí ìbá sàn fún wọn, kí wọ́n má mọ́ ọ̀nà òdodo, jù pé lẹ́yìn tí wọ́n mọ̀ ọ́n tán, kí wọ́n yípadà kúrò nínú òfin mímọ́ tí a fi fún wọn."'), '2Pet$3:5': (None, None, '2Pet$3:5 " Nítorí èyí ni wọ́n mọ̀ọ́mọ̀ ṣe àìfẹ́ẹ́ mọ̀ pé nípa ọ̀rọ̀ Ọlọ́run ni àwọn ọ̀run ti wà láti ìgbà àtijọ́ àti tí ilẹ̀ yọrí jáde nínú omi, tí ó sì dúró nínú omi."'), '2Pet$3:9': (None, None, '2Pet$3:9 "Olúwa kò fi ìlérí rẹ̀ jáfara, bí àwọn ẹlòmíràn ti ka ìjáfara sí; ṣùgbọ́n ó ń mú sùúrù fún yín nítorí kò fẹ́ kí ẹnikẹ́ni ṣègbé, bí kò ṣe kí gbogbo ènìyàn wá sí ìrònúpìwàdà."'), '2Pet$3:11': (None, 'yo', '2Pet$3:11 "Ǹjẹ́ bí gbogbo nǹkan wọ̀nyí yóò ti yọ́ bẹ́ẹ̀, irú ènìyàn wo ni ẹ̀yin ìbá jẹ́ nínú ìwà-mímọ́ gbogbo àti ìwà-bí-Ọlọ́run."'), '2Pet$3:14': (None, 'wo', '2Pet$3:14 "Nítorí náà, olùfẹ́, bí ẹ̀yin ti ń retí irú nǹkan wọ̀nyí, ẹ múra gírí, kí a lè bá yín ní àlàáfíà ní àìlábàwọ́n, àti ní àìlábùkù lójú rẹ̀."'), '1John$1:10': ('ṣe', 'ni', '1John$1:10 " Bí àwa bá wí pé àwa kò dẹ́sẹ̀, àwa mú un ní èké, ọ̀rọ̀ rẹ̀ kò sì sí nínú wa."'), '1John$2:5': ('mọ', 'ife', '1John$2:5 " Ṣùgbọ́n ẹnikẹ́ni tí ó bá ń pa òfin rẹ̀ mọ́, lára rẹ̀ ni a gbé mú ìfẹ́ Ọlọ́run pé nítòótọ́. Nípa èyí ni àwa mọ̀ pé àwa ń wà nínú rẹ̀."'), '1John$2:19': ('ṣe', None, '1John$2:19 "Wọn ti ọ̀dọ̀ wá jáde, ṣùgbọ́n wọn kì í ṣe ara wa. Nítorí bí wọ́n bá jẹ́ ara wa, wọn ìbá bá wa dúró: ṣùgbọ́n jíjáde lọ wọn fihàn pé gbogbo wọn kì í ṣe ara wa."'), '1John$3:1': ('mọ', 'ife', '1John$3:1 " Ẹ wo irú ìfẹ́ tí Baba fi fẹ́ wa, tí a fi ń pè wá ní Ọmọ Ọlọ́run; bẹ́ẹ̀ ni a sáà jẹ́. Nítorí èyí ni ayé kò ṣe mọ̀ wá, nítorí tí kò mọ̀ ọ́n."'), '1John$3:14': ('mọ', 'ife', '1John$3:14 " Àwa mọ̀ pé àwa tí rékọjá láti inú ikú sínú ìyè, nítorí tí àwa fẹ́ràn àwọn ará. Ẹni tí kò ba ni ìfẹ́ ó ń gbé inú ikú."'), '1John$4:16': ('mọ', 'ife', '1John$4:16 "Báyìí ni àwa mọ̀, tí a sì gba ìfẹ́ tí Ọlọ́run ní sí wa gbọ́. Ìfẹ́ ni Ọlọ́run; ẹni tí ó bá sì ń gbé inú ìfẹ́ ó ń gbé inú Ọlọ́run, àti Ọlọ́run nínú rẹ̀."'), '1John$4:17': (None, 'ni', '1John$4:17 " Nínú èyí ni a mú ìfẹ́ tí ó wà nínú wa pé, kí àwa bà á lè ni ìgboyà ní ọjọ́ ìdájọ́: nítorí pé bí òun tí rí, bẹ́ẹ̀ ni àwa sì rí ni ayé yìí."'), '1John$5:2': ('mọ', None, '1John$5:2 "Nípa èyí ni àwa mọ̀ pé àwa fẹ́ràn àwọn ọmọ Ọlọ́run, nígbà tí a bá fẹ́ràn Ọlọ́run, tí a sì ń pa òfin rẹ̀ mọ́."'), '2John$1:8': (None, 'gba', '2John$1:8 "Ẹ kíyèsára yín, kí ẹ má ba à sọ iṣẹ́ tí ẹ tí ṣe nù, ṣùgbọ́n kí ẹ̀yin lè rí èrè kíkún gbà."'), '3John$1:5': (None, None, '3John$1:5 "Olùfẹ́, ìwọ ń ṣe olóòótọ́ nínú iṣẹ́ tí ìwọ ń ṣe fún àwọn tí ń ṣe ará, àti pàápàá fún àwọn tí ń ṣe àlejò."'), '3John$1:11': ('ni', None, '3John$1:11 "Olùfẹ́, má ṣe ṣe àfarawé ohun tí í ṣe ibi bí kò ṣe ohun tí ṣe rere. Ẹni tí ó bá ń ṣe rere ti Ọlọ́run ni; ẹni tí ó ba ń ṣe búburú kò rí Ọlọ́run."'), 'Jude$1:12': (None, 'àse', 'Jude$1:12 "Àwọn wọ̀nyí ní ó jẹ́ àbàwọ́n nínú àṣè ìfẹ́ yín, nígbà tí wọ́n ń bá yín jẹ àsè, àwọn olùsọ́ àgùntàn ti ń bọ́ ara wọn láìbẹ̀rù. Wọ́n jẹ́ ìkùùkuu láìrọ òjò, tí a ń ti ọwọ́ afẹ́fẹ́ gbá kiri; àwọn igi aláìléso ní àkókò èso, wọ́n kú lẹ́ẹ̀méjì, a sì fà wọn tú ti-gbòǹgbò-ti-gbòǹgbò."'), 'Jude$1:16': (None, None, 'Jude$1:16 "Àwọn ènìyàn wọ̀nyí ní àwọn ti ń kùn, àwọn aláròyé, ti ń rìn nípa ìfẹ́kúfẹ̀ẹ́ ara wọn; wọn ń gbéraga nipa ara wọn, wọ́n sì ń ṣáta àwọn ènìyàn mìíràn fún èrè ara wọn."'), 'Jude$1:20': (None, 'olufẹ', 'Jude$1:20 "Ṣùgbọ́n ẹ̀yin, olùfẹ́, ti ẹ ń gbé ara yín ró lórí ìgbàgbọ́ yín tí ó mọ́ jùlọ, ti ẹ ń gbàdúrà nínú Ẹ̀mí Mímọ́."'), 'Jude$1:22': (None, None, 'Jude$1:22 Ẹ máa yí àwọn tí ń ṣe iyèméjì lọ́kàn padà:'), 'Rev$1:14': ('ina', 'ina', 'Rev$1:14 "Orí rẹ̀ àti irun rẹ̀ funfun bí ẹ̀gbọ̀n òwú, ó funfun bí Sínóò; ojú rẹ̀ sì dàbí, ọwọ́ iná;"'), 'Rev$2:5': ('wá', 'ṣe', 'Rev$2:5 "Rántí ibi tí ìwọ ti gbé subú! Ronúpìwàdà, kí ó sì ṣe iṣẹ́ ìsáájú; bí kò sì ṣe bẹ́ẹ̀, èmi ó sì tọ̀ ọ́ wá, èmi ó sì sí ọ̀pá fìtílà rẹ̀ kúrò ní ipò rẹ̀, bí kò ṣe bí ìwọ bá ronúpìwàdà."'), 'Rev$2:10': ('ni', 'jiya', 'Rev$2:10 " Máṣe bẹ̀rù ohunkóhun tí ìwọ ń bọ wá jìyà rẹ̀. Kíyèsí i, èṣù yóò gbé nínú yín jù sínú túúbú, kí a lè dán yin wò; ẹ̀yin ó sì ní ìpọ́njú ní ọjọ́ mẹ́wàá: ìwọ sa se olóòtọ́ dé ojú ikú, èmi ó sì fi adé ìyè fún ọ"'), 'Rev$2:14': (None, 'ni', 'Rev$2:14 " Ṣùgbọ́n mo ni nǹkan díẹ̀ wí sí ọ; nítorí tí ìwọ ní àwọn kan tí o di ẹ̀kọ́ Báláámù mú nibẹ̀, ẹni tí ó kọ́ Bálákì láti mú ohun ìkọ̀sẹ̀ wá ṣíwájú àwọn ọmọ Ísírẹ́lì, láti máa jẹ ohun tí a pa rúbọ sí òrìṣà, àti láti máa ṣe àgbèrè."'), 'Rev$2:18': ('ina', 'ina', 'Rev$2:18 " “Àti sí Ańgẹ́lì ìjọ ní Tíátírà kọ̀wé: Nǹkan wọ̀nyí ni Ọmọ Ọlọ́run wí, ẹni tí ojú rẹ̀ dàbí ọ̀wọ́ iná, tí ẹṣẹ̀ rẹ̀ sì dàbí idẹ dáradára:"'), 'Rev$2:25': (None, 'ni', 'Rev$2:25 "Ṣùgbọ́n èyí tí ẹ̀yin ní, ẹ di i mú ṣinṣin títí èmi ó fi dé."'), 'Rev$2:26': (None, 'fun', 'Rev$2:26 " Ẹni tí ó bá sì ṣẹ́gun, àti tí o sì ṣe ìfẹ́ mi títí dé òpin, èmi o fún un láṣẹ lórí àwọn orílẹ̀-èdè:"'), 'Rev$3:9': ('wá', 'ṣe', 'Rev$3:9 " Kíyèsí i, èmi ó mú àwọn ti sínágógù Sàtánì, àwọn tí wọ́n ń wí pé Júù ni àwọn, tí wọn kì í sì ṣe bẹ́ẹ̀, ṣùgbọ́n tí wọn ń ṣèké; kíyèsí i, èmi ó mú kí wọn wá wólẹ̀ níwájú ẹṣẹ̀ rẹ̀, kí wọn sì mọ̀ pé èmi tí fẹ́ ọ."'), 'Rev$3:10': (None, 'ayé', 'Rev$3:10 "Nítorí tí ìwọ tí pa ọ̀rọ̀ sùúrù mi mọ́, èmi pẹ̀lú yóò pa ọ́ mọ́ kúrò nínú wákàtí ìdánwò, tí ń bọ̀ wá dé bá gbogbo ayé, láti dán àwọn tí ń gbé orí ilẹ̀ ayé wò."'), 'Rev$3:11': ('gba', None, 'Rev$3:11 "Kíyèsí i, èmi ń bọ̀ kánkán: di èyí ti ìwọ ní mú ṣinṣin, kí ẹnikẹ́ni má ṣe gba adé rẹ."'), 'Rev$3:12': ('ṣe', 'orukọ', 'Rev$3:12 " Ẹni tí ó ba ṣẹ́gun, òun ni èmi ó fi ṣe ọ̀wọ́n nínú tẹ́ḿpìlì Ọlọ́run mi, òun kì yóò sì jáde kúrò níbẹ̀ mọ́: èmi ó sì kọ orúkọ Ọlọ́run mi sí i lára, àti orúkọ ìlú Ọlọ́run mi, tí i ṣe Jerúsálémù tuntun, tí ó ń ti ọrun sọ̀kalẹ̀ láti ọ̀dọ̀ Ọlọ́run mi wá: àti orúkọ tuntun ti èmi tikarámì."'), 'Rev$3:16': (None, None, 'Rev$3:16 "Ǹjẹ nítorí tí ìwọ lọ wọ́ọ́rọ́, tí o kò si gbóná, bẹ́ẹ̀ ni tí o kò tutù, èmi yóò pọ̀ ọ́ jáde kúrò ni ẹnu mi."'), 'Rev$3:17': (None, 'talaka', 'Rev$3:17 " Nítorí tí ìwọ wí pé, Èmi ní ọrọ̀, èmi sì ń pọ̀ sí i ni ọrọ̀, èmi kò si ṣe aláìní ohunkóhun; tí ìwọ kò sì mọ̀ pé, òsì ni ìwọ, ẹni-ikáànù, tálákà, afọ́jú, àti ẹni-ìhòhò:"'), 'Rev$5:9': ('ṣe', 'gba', 'Rev$5:9 " Wọn sì ń kọ orin tuntun kan, wí pé: “Ìwọ ní o yẹ láti gba ìwé náà, àti láti ṣí èdìdì rẹ̀: nítorí tí a tí pa ọ, ìwọ sì tí fi ẹ̀jẹ̀ rẹ̀ ṣe ìràpadà ènìyàn sí Ọlọ́run láti inú ẹ̀yà gbogbo, àti èdè gbogbo, àti inú ènìyàn gbogbo, àti orílẹ̀-èdè gbogbo wá:"'), 'Rev$6:1': ('ri', 'wo', 'Rev$6:1 "Èmi sì rí i nígbà tí Ọ̀dọ́-Àgùntàn náà sí ọkàn nínú èdìdì wọ̀nyí, mo sì gbọ́ ọ̀kan nínú àwọn ẹ̀dá alààyè mẹ́rin náà ń wí bí sísán àrá pé, “Wá, wò ó!”"'), 'Rev$6:3': ('gbo', 'wo', 'Rev$6:3 "Nígbà tí ó sì sí èdìdì kéjì, mo gbọ́ ohùn ẹ̀dá alààyè wí pé, “Wá, wò ó!”"'), 'Rev$6:5': ('gbo', 'wo', 'Rev$6:5 "Nígbà tí ó sì di èdìdì kẹ́tà, mo gbọ́ ohùn ẹ̀dá alààyè kẹta wí pé, Wá, wò ó. Mo sì wò ó, sì kíyèsí i, ẹṣin dúdú kan; ẹni tí ó jókòó lórí ni ìwọ̀n aláwẹ́ méjì ní ọwọ́ rẹ̀."'), 'Rev$6:6': ('gbo', 'wo', 'Rev$6:6 " Mo sì gbọ́ bí ẹni pé ohùn kan ní àárin àwọn ẹ̀dá alààyè mẹ́rẹ̀ẹ̀rin nì ti ń wí pé, òsùwọ̀n àlìkámà kan fún owó idẹ kan, àti òṣùwọ̀n ọkà-bálì mẹ́ta fún owó idẹ kan, sì kíyèsí i, kí ó má sì ṣe pa òróró àti ọtí wáìnì lára."'), 'Rev$6:7': ('gbo', 'wo', 'Rev$6:7 "Nígbà tí ó sì ṣí èdìdì kẹ́rin, mo gbọ́ ohùn ẹ̀dá alààyè kan wí pé, Wá wò ó."'), 'Rev$6:15': (None, None, 'Rev$6:15 " Àwọn ọba ayé àti àwọn ọlọ́lá àti àwọn olórí ogun, àti àwọn ọlọ́rọ̀ àti àwọn alágbára, àti olókúlùkù ẹrú, àti olúkúlùkù òmìnira, sì fi ara wọn pamọ́ nínú ihò ilẹ̀, àti nínú àpáta orí òkè;"'), 'Rev$7:2': (None, 'ni', 'Rev$7:2 "Mo sì rí ańgẹ́lì mìíràn tí ó ń ti ìhà ìlà-òòrùn gòkè wá, ti òun ti èdìdì Ọlọ́run alààyè lọ́wọ́: ó sì kígbe ní ohùn rara sí àwọn ańgẹ́lì mẹ́rin náà tí a fifún un láti pa ayé, àti òkun, lára,"'), 'Rev$7:9': ('ọpọlọpọ', 'eniyan', 'Rev$7:9 "Lẹ́yìn náà, mo ri, sì kíyèsí i, ọ̀pọ̀lọpọ̀ ènìyàn tí ẹnikẹ́ni kò lè kà, láti inú orílẹ̀-èdè gbogbo, àti ẹ̀yà, àti ènìyàn, àti láti inú èdè gbogbo wá, wọn dúró níwájú ìtẹ́, àti níwájú Ọ̀dọ́-Àgùntàn náà, a wọ̀ wọ́n ni aṣọ funfun, imọ̀ ọ̀pẹ si ń bẹ ni ọwọ́ wọn."'), 'Rev$7:11': (None, None, 'Rev$7:11 "Gbogbo àwọn ańgẹ́lì sì dúró yí ìtẹ́ náà ká, àti yí àwọn àgbà àti àwọn ẹ̀dá alààyè mẹ́rin náà ká, wọn wólẹ̀ wọn si dojúbolẹ̀ níwájú ìtẹ́ náà wọ́n sì sin Ọlọ́run."'), 'Rev$7:16': (None, 'oungbe', 'Rev$7:16 " Ebi kì yóò pa wọn mọ́, bẹ́ẹ̀ ni òungbẹ kì yóò gbẹ́ wọ́n mọ́; bẹ́ẹ̀ ni oòrùn kì yóò pa wọn tàbí oorukóoru kan."'), 'Rev$8:3': ('mimo', None, 'Rev$8:3 " Ańgẹ́lì mìíràn sì wá, ó sì dúró ti pẹpẹ, ó ní àwo tùràrí wúrà kan a sì fi tùràrí púpọ̀ fún un, láti máa fí kún àdúrà gbogbo àwọn ènìyàn mímọ́ lórí pẹpẹ, pẹpẹ wúrà tí ń bẹ níwájú ìtẹ́."'), 'Rev$8:4': ('mimo', 'angẹli', 'Rev$8:4 Àti èéfín tùràrí náà pẹ̀lú àdúrà àwọn ènìyàn-mímọ́ sì gòkè lọ ṣíwájú Ọlọ́run láti ọwọ́ ańgẹ́lì náà wá.'), 'Rev$8:10': (None, None, 'Rev$8:10 " Ańgẹ́lì kẹ́ta sì fún ìpè tirẹ̀, ìràwọ̀ ńlá kan tí ń jó bí fìtílà sì bọ́ láti ọ̀run wá, ó sì bọ́ sórí ìdámẹ̀ta àwọn odò ṣíṣàn, àti sórí àwọn orísun omi;"'), 'Rev$8:13': ('ohun', 'ohun', 'Rev$8:13 "Mo sì wò, mo sì gbọ́ idí kan tí ń fò ni àárin ọ̀run, ó ń wí lóhùn rara pé, “Ègbé, ègbé, fún àwọn tí ń gbé orí ilẹ̀ ayé nítorí ohun ìpè ìyókù tí àwọn ańgẹ́lì mẹ́ta tí ń bọ̀ wá yóò fún!”"'), 'Rev$9:3': (None, None, 'Rev$9:3 Àwọn eṣú sì jáde ti inú èéfín náà wá sórí ilẹ̀: a sì fi agbára fún wọn gẹ́gẹ́ bí agbára àkéekèe ilẹ̀.'), 'Rev$9:13': ('iwo', None, 'Rev$9:13 " Ańgẹ́lì kẹ́fà si fún, mo sì gbọ́ ohùn kan láti ibi ìwo mẹ́rin pẹpẹ wúrà wá, tí ń bẹ níwájú Ọlọ́run."'), 'Rev$9:20': ('wo', 'gbo', 'Rev$9:20 " Ṣùgbọ́n àwọn ènìyàn ìyókù tí a kò sì ti ipa ìyọnu wọ̀nyí pa, kò sì ronúpìwàdà iṣẹ́ ọwọ́ wọn, kí wọn má ṣe fàdákà, àti ti idẹ, àti òkúta, àti ti igi mọ́: àwọn tí kò lè ríran, tàbí kí wọn gbọ́ràn, tàbí kí wọn rìn:"'), 'Rev$10:5': (None, None, 'Rev$10:5 " Ańgẹ́lì náà tí mo rí tí ó dúró lórí òkun àti lórí ilẹ̀, sì gbé ọwọ́ rẹ̀ sí òkè ọ̀run:"'), 'Rev$11:2': ('fi fun', None, 'Rev$11:2 " Sì fi àgbàlá tí ń bẹ lóde tẹ́ḿpìlì sílẹ̀, má si ṣe wọ̀n ọ́n; nítorí tí a fi fún àwọn aláìkọlà: ìlú mímọ́ náà ni wọn ó sì tẹ̀ mọ́lẹ̀ ní oṣù méjì lé lógójì."'), 'Rev$11:7': (None, 'ṣe', 'Rev$11:7 " Nígbà tí wọn bá sì ti parí ẹ̀rí wọn, ẹranko tí o ń tí inú ọ̀gbun gòkè wá yóò bá wọn jagun, yóò sì ṣẹ́gun wọn, yóò sì pa wọ́n."'), 'Rev$11:11': (None, 'ẹmi', 'Rev$11:11 " Àti lẹ́yìn ijọ́ mẹ́ta àti ààbọ̀ náà, ẹ̀mí iyè láti ọ̀dọ̀ Ọlọ́run wá wọ inú wọn, wọn sì dìde dúró ni ẹṣẹ̀ wọn; ẹ̀rù ńlá sì ba àwọn tí o rí wọn."'), 'Rev$11:16': (None, 'wá', 'Rev$11:16 "Àwọn àgbà mẹ́rìnlélógún náà tí wọn jókòó níwájú Ọlọ́run lórí ìtẹ́ wọn, dojúbolẹ̀, wọn sì sin Ọlọ́run,"'), 'Rev$12:1': (None, None, 'Rev$12:1 "Àmì ńlá kan sì hàn ni ọ̀run; obìnrin kan tí a fi òòrùn wọ̀ ní aṣọ, òṣùpá sì ń bẹ lábẹ́ ẹsẹ̀ rẹ̀, adé oníràwọ̀ méjìlá sì ń bẹ lórí rẹ̀:"'), 'Rev$12:4': (None, None, 'Rev$12:4 " Ìrù rẹ̀ sì wọ́ ìdá mẹ́ta àwọn ìràwọ̀, ó sì jù wọ́n sí ilẹ̀ ayé, dírágónì náà sì dúró níwájú obìnrin náà tí ó fẹ́ bímọ, pé nígbà tí o bá bí i, kí òun lè pa ọmọ rẹ̀ jẹ."'), 'Rev$12:9': (None, 'ayé', 'Rev$12:9 " A sì lé dírágónì ńlá náà jáde, ejò láéláé ni, tí a ń pè ni Èṣù, àti Sàtanì, tí ń tan gbogbo ayé jẹ: a sì lé e jù sí ilẹ̀ ayé, a sì lé àwọn ańgẹ́lì rẹ̀ jáde pẹ̀lú rẹ̀."'), 'Rev$12:17': ('ni', None, 'Rev$12:17 "Dírágónì náà sì bínú gidigidi sí obìnrin náà, ó sì lọ bá àwọn irú ọmọ rẹ̀ ìyókù jagun, tí wọ́n ń pa òfin Ọlọ́run mọ̀, tí wọn sì di ẹ̀rí Jésù mú, Ó sì dúró lórí ìyanrìn òkun."'), 'Rev$13:7': (None, 'ṣe', 'Rev$13:7 " A sì fún-un láti máa bá àwọn ènìyàn-mímọ́ jagun, àti láti ṣẹ́gun wọn: a sì fi agbára fún un lórí gbogbo ẹ̀yà, àti ènìyàn, àti èdè, àti orílẹ̀."'), 'Rev$13:8': (None, 'ayé', 'Rev$13:8 "Gbogbo àwọn tí ń gbé orí ilẹ̀ ayé yóò sì máa sìn ín, olúkúlúkù ẹni tí a kò kọ orúkọ rẹ̀, sínú ìwé-ìyè Ọ̀dọ́-Àgùntàn tí a tí pa láti ìpilẹ̀ṣẹ̀ ayé."'), 'Rev$13:9': (None, 'ni', 'Rev$13:9 Bí ẹnikẹ́ni bá létí kí o gbọ́.'), 'Rev$13:13': ('ní', 'ṣe', 'Rev$13:13 "Ó sì ń ṣe ohun ìyanu ńlá, àní tí ó fi ń mú iná sọ̀kalẹ̀ láti ọ̀run wá sì ilẹ̀ ayé níwájú àwọn ènìyàn."'), 'Rev$13:15': (None, None, 'Rev$13:15 " A sì fi fún un, láti fi ẹ̀mí fún àwòrán ẹranko náà kí ó máa sọ̀rọ̀ kí ó sì mu kí a pa gbogbo àwọn tí kò foríbalẹ̀ fún àwòrán ẹranko náà."'), 'Rev$13:16': ('ní', 'gba', 'Rev$13:16 "Ó sì mu gbogbo wọn, àti kékeré àti ńlá, ọlọ́rọ̀ àti talákà, olómìnira àti ẹrú, láti gba àmì kan sí ọwọ́ ọ̀tún wọn, tàbí ní iwájú orí wọn;"'), 'Rev$14:2': ('gbo', None, 'Rev$14:2 "Mo sì gbọ́ ohùn kan láti ọ̀run wá, bí aríwo omi púpọ̀, àti bí sísán àrá ńlá: mo sì gbọ́ àwọn oníháàpù, ń lù haàpù wọn."'), 'Rev$14:6': (None, 'fun', 'Rev$14:6 "Mo sì rí ańgẹ́lì mìíràn ń fò ní àárin méjì ọ̀run, pẹ̀lú Ìyìn rere àìnípẹ̀kun láti wàásù fún àwọn tí ń gbé orí ilẹ̀ ayé, àti fún gbogbo orílẹ̀, àti ẹ̀yà, àti èdè, àti ènìyàn."'), 'Rev$14:9': ('ni', 'gba', 'Rev$14:9 "Ańgẹ́lì mìíràn, ẹ̀kẹta, sì tẹ̀lé wọn, ó ń wí ni ohùn rara pé, “Bí ẹnikẹ́ni bá ń fóríbalẹ̀ fún ẹranko náà àti àwòrán rẹ̀, tí ó sì gba àmì sí iwájú orí rẹ̀ tàbí sí ọwọ́ rẹ̀."'), 'Rev$14:13': ('ni', None, 'Rev$14:13 "Mo sì gbọ́ ohùn kan láti ọ̀run wá ń wí fún mi pé, “Kọ̀wé rẹ̀: Alábùkún fún ni àwọn òkú tí o kú nínú Olúwa láti ìhín lọ.” Alábùkún ni wọ́n nítòótọ́, bẹ́ẹ̀ ni, Ẹ̀mí wí, “Nítorí tí wọn yóò sìnmí kúrò nínú làálàá wọn, nítorí iṣẹ́ wọn ń tọ̀ wọn lẹ́yìn.”"'), 'Rev$15:2': ('ri', 'aworan', 'Rev$15:2 "Mo sì rí bí ẹni pé, òkun dígí tí o dàpọ̀ pẹ̀lú iná: àwọn tí ó sì dúró lórí òkun dígí yìí jẹ́ àwọn ti wọ́n sẹ́gun ẹranko náà, àti àwòrán rẹ̀, àti àmì rẹ̀ àti nọ́ḿba orúkọ rẹ̀, wọn ní Haàpù Ọlọ́run."'), 'Rev$15:5': ('agọ', 'tẹmpili', 'Rev$15:5 " Lẹ́yìn náà mo sì wo, sì kíyèsí i, a sí tẹ́ḿpìlì àgọ́ ẹ̀rí ní ọ̀run sílẹ̀;"'), 'Rev$16:6': (None, 'ni', 'Rev$16:6 " Nítorí tí wọ́n ti ta ẹ̀jẹ̀ àwọn ènìyàn mímọ́ àti ti àwọn wòlíì sílẹ̀, ìwọ sì fi ẹ̀jẹ̀ fún wọn mu; èyí yìí ní ó yẹ wọ́n.”"'), 'Rev$16:14': (None, 'ayé', 'Rev$16:14 "Nítorí ẹ̀mí èṣù ni wọ́n, tí ń ṣe iṣẹ́ ìyanu, àwọn tí ń jáde lọ sọ́dọ̀ àwọn ọba gbogbo ilẹ̀ ayé, láti gbá wọn jọ sí ogun ọjọ́ ńlá Ọlọ́run Olúdàmarè."'), 'Rev$17:1': (None, 'ni', 'Rev$17:1 " Ọ̀kan nínú àwọn ańgẹ́lì méje náà tí ó ní ìgò méje wọ̀n-ọn-nì sì wá, ó sì ba mi sọ̀rọ̀ wí pé, “Wá níhìn-ín; èmi ó sì fi ìdájọ́ àgbèrè ńlá ní tí ó jókòó lórí omi púpọ̀ han ọ:"'), 'Rev$17:8': (None, None, 'Rev$17:8 " Ẹranko tí ìwọ ri nì, o ti wà, kò sì sí mọ́: Yóò sì ti inú ọ̀gbun gòkè wá, yóò sì lọ sínú ìwé iye láti ìpilẹ̀ṣẹ̀ ayé, nígbà tí wọn ń wò ẹranko tí o ti wà, tí kò sì sí mọ́, tí ó sì ń bọ̀ wá."'), 'Rev$17:10': ('ṣubu', None, 'Rev$17:10 "Ọba méje sì ní wọn: àwọn márùn-ún ṣubú, ọ̀kan ń bẹ, ọ̀kan ìyókù kò sì tí ì dé; nígbà tí ó bá sì dé, yóò dúró fún ìgbà kúkurú."'), 'Rev$17:12': ('ni', 'gba', 'Rev$17:12 " “Ìwo mẹ́wáà tí ìwọ sì rí ni ọba mẹ́wàá ni wọn, tí wọn kò ì ti gba ìjọba; ṣùgbọ́n wọn gba ọlá bí ọba pẹ̀lú ẹranko náà fún wákàtí kan."'), 'Rev$17:13': ('ni', 'fun', 'Rev$17:13 "Àwọn wọ̀nyí ní inú kan, wọ́n ò sì fi agbára àti ọlá wọn fún ẹranko náà."'), 'Rev$18:11': (None, None, 'Rev$18:11 " “Àwọn oníṣòwò ayé sì ń sọkún, wọn sì ń sọ̀fọ̀ lórí rẹ̀, nítorí pé ẹnikẹ́ni kò rà ọjà wọn mọ́:"'), 'Rev$18:21': ('da', None, 'Rev$18:21 " Ańgẹ́lì alágbára kan sì gbé òkúta kan sókè, ó dàbí ọlọ ńlá, ó sì jù ú sínú òkun, wí pé: “Báyìí ní a ó fi agbára ńlá bíi Bábílónì ìlú ńlá ni wó, a kì yóò sì rí i mọ́ láé."'), 'Rev$19:7': (None, None, 'Rev$19:7 " Ẹ jẹ́ kí a yọ̀, kí inú wa kí ó sì dùn gidigidi, kí a sì fi ògo fún un. Nítorí pé ìgbéyàwó Ọ̀dọ́-Àgùntàn dé, aya rẹ̀ sì ti múra tán."'), 'Rev$19:8': ('mimọ', 'funfun', 'Rev$19:8 Òun ni a sì fi fún pé kí ó wọ aṣọ ọ̀gbọ̀ wíwẹ́ tí ó funfun gbòò.” (Nítorí pé aṣọ ọ̀gbọ̀ nì dúró fún iṣẹ́ òdodo àwọn ènìyàn mímọ́.)'), 'Rev$19:12': ('ina', 'ina', 'Rev$19:12 " Ojú rẹ̀ dàbí ọ̀wọ́ iná, àti ní orí rẹ̀ ni adé púpọ̀ wà; ó sì ní orúkọ kan tí a kọ, tí ẹnikẹ́ni kò mọ́, bí kò ṣe òun tìkárarẹ̀."'), 'Rev$19:14': ('mimọ', 'funfun', 'Rev$19:14 "Àwọn ogun tí ń bẹ ní ọrùn tí a wọ̀ ní aṣọ ọ̀gbọ̀ wíwẹ́, funfun àti mímọ́, sì ń tọ̀ ọ́ lẹ̀yìn lórí ẹ̀sin funfun."'), 'Rev$20:2': ('dè', None, 'Rev$20:2 "O sì di Dírágónì náà mú, ejò àtijọ́ nì, tí í ṣe èṣù, àti Sàtánì, ó sì dè é ní ẹgbẹ̀rún ọdún."'), 'Rev$20:4': ('ri', None, 'Rev$20:4 " Mo sì rí àwọn ìtẹ́, wọ́n sì jókòó lórí wọn, a sì fi ìdájọ́ fún wọn: mo sì rí ọkàn àwọn tí a ti bẹ́ lórí nítorí ẹ̀rí Jésù, àti nítorí ọ̀rọ̀ Ọlọ́run, àti àwọn tí kò sì foríbalẹ̀ fún ẹranko náà, àti fún àwòrán rẹ̀, tàbí tí kò sì gbà àmì rẹ̀ ní iwájú wọn àti ní ọwọ́ wọn; wọ́n sì wà láàyè, wọ́n sì jọba pẹ̀lú Kírísítì ní ẹgbẹ̀rún ọdún."'), 'Rev$20:7': (None, None, 'Rev$20:7 "Nígbà tí ẹgbẹ̀rún ọdún náà bá sì pé, a ó tú Sàtánì sílẹ̀ kúrò nínú túbú rẹ̀."'), 'Rev$20:9': (None, None, 'Rev$20:9 " Wọ́n sì gòkè lọ la ibú ayé já, wọ́n sì yí ibùdó àwọn ènìyàn mímọ́ ká àti ìlú àyànfẹ́ náà: iná sì ti ọ̀run sọ̀kalẹ̀ wá, ó sì jó wọn run."'), 'Rev$21:10': (None, None, 'Rev$21:10 " Ó sì mu mi lọ nínú Ẹ̀mí si òkè ńlá kan tí o sì ga, ó sì fí ìlú náà hàn mi, Jerúsálémù mímọ́, tí ń ti ọ̀run sọ̀kalẹ̀ wá láti ọ̀dọ̀ Ọlọ́run,"'), 'Rev$22:13': ('opin', None, 'Rev$22:13 " Èmi ni Álífà àti Òmégà, ẹni ìṣáájú àti ẹni ìkẹyìn, ìpilẹ̀ṣẹ̀ àti òpin."')}


for dataset, dest in ((yoruba_ant, 'kjv-yor-test/yo-ant-sentences.txt'), (yoruba_syn, 'kjv-yor-test/yo-syn-sentences.txt')):
    with open(dest, 'w', encoding='utf8') as f:
        for key, value in dataset.items():
            word1, word2, sentence = value
            if word1 is not None and word2 is not None:
                f.write('\t'.join((word1, word2, sentence)) + '\n')